INSERT INTO `tbl_ayat` (`id`, `surah`, `nomor`, `ar`, `tr`, `idn`) VALUES
	(1, 1, 1, 'بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ', 'bismi <strong>al</strong>l<u>aa</u>hi <strong>al</strong>rra<u>h</u>m<u>aa</u>ni <strong>al</strong>rra<u>h</u>iim<strong>i</strong>', 'Dengan nama Allah Yang Maha Pengasih, Maha Penyayang.'),
	(2, 1, 2, 'اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ', 'al<u>h</u>amdu lill<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Segala puji bagi Allah, Tuhan seluruh alam,'),
	(3, 1, 3, 'الرَّحْمٰنِ الرَّحِيْمِۙ', 'a<strong>l</strong>rra<u>h</u>m<u>aa</u>ni <strong>al</strong>rra<u>h</u>iim<strong>i</strong>', 'Yang Maha Pengasih, Maha Penyayang,'),
	(4, 1, 4, 'مٰلِكِ يَوْمِ الدِّيْنِۗ', 'm<u>aa</u>liki yawmi <strong>al</strong>ddiin<strong>i</strong>', 'Pemilik hari pembalasan.'),
	(5, 1, 5, 'اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَعِيْنُۗ', 'iyy<u>aa</u>ka na\'budu wa-iyy<u>aa</u>ka nasta\'iin<strong>u</strong>', 'Hanya kepada Engkaulah kami menyembah dan hanya kepada Engkaulah kami mohon pertolongan.'),
	(6, 1, 6, 'اِهْدِنَا الصِّرَاطَ الْمُسْتَقِيْمَ ۙ', 'ihdin<u>aa</u> <strong>al</strong><u>shsh</u>ir<u>aath</u>a <strong>a</strong>lmustaqiim<strong>a</strong>', 'Tunjukilah kami jalan yang lurus,'),
	(7, 1, 7, 'صِرَاطَ الَّذِيْنَ اَنْعَمْتَ عَلَيْهِمْ ەۙ غَيْرِ الْمَغْضُوْبِ عَلَيْهِمْ وَلَا الضَّاۤلِّيْنَ ࣖ', '<u>sh</u>ir<u>aath</u>a <strong>al</strong>la<u>dz</u>iina an\'amta \'alayhim ghayri <strong>a</strong>lmagh<u>dh</u>uubi \'alayhim wal<u>aa</u> <strong>al</strong><u>dhdhaa</u>lliin<strong>a</strong>', '(yaitu) jalan orang-orang yang telah Engkau beri nikmat kepadanya; bukan (jalan) mereka yang dimurkai, dan bukan (pula jalan) mereka yang sesat.'),
	(8, 2, 1, 'الۤمّۤ ۚ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(9, 2, 2, 'ذٰلِكَ الْكِتٰبُ لَا رَيْبَ ۛ فِيْهِ ۛ هُدًى لِّلْمُتَّقِيْنَۙ', '<u>dzaa</u>lika <strong>a</strong>lkit<u>aa</u>bu l<u>aa</u> rayba fiihi hudan lilmuttaqiin<strong>a</strong>', 'Kitab (Al-Qur\'an) ini tidak ada keraguan padanya; petunjuk bagi mereka yang bertakwa,'),
	(10, 2, 3, 'الَّذِيْنَ يُؤْمِنُوْنَ بِالْغَيْبِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۙ', '<strong>al</strong>la<u>dz</u>iina yu/minuuna bi<strong>a</strong>lghaybi wayuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun<strong>a</strong>', '(yaitu) mereka yang beriman kepada yang gaib, melaksanakan salat, dan menginfakkan sebagian rezeki yang Kami berikan kepada mereka,'),
	(11, 2, 4, 'وَالَّذِيْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ ۚ وَبِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ', 'wa<strong>a</strong>lla<u>dz</u>iina yu/minuuna bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika wabi<strong>a</strong>l-<u>aa</u>khirati hum yuuqinuun<strong>a</strong>', 'dan mereka yang beriman kepada (Al-Qur\'an) yang diturunkan kepadamu (Muhammad) dan (kitab-kitab) yang telah diturunkan sebelum engkau, dan mereka yakin akan adanya akhirat.'),
	(12, 2, 5, 'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ ۙ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'ul<u>aa</u>-ika \'al<u>aa</u> hudan min rabbihim waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Merekalah yang mendapat petunjuk dari Tuhannya, dan mereka itulah orang-orang yang beruntung.'),
	(13, 2, 6, 'اِنَّ الَّذِيْنَ كَفَرُوْا سَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu saw<u>aa</u>un \'alayhim a-an<u>dz</u>artahum am lam tun<u>dz</u>irhum l<u>aa</u> yu/minuun<strong>a</strong>', 'Sesungguhnya orang-orang kafir, sama saja bagi mereka, engkau (Muhammad) beri peringatan atau tidak engkau beri peringatan, mereka tidak akan beriman.'),
	(14, 2, 7, 'خَتَمَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَعَلٰى سَمْعِهِمْ ۗ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌ وَّلَهُمْ عَذَابٌ عَظِيْمٌ ࣖ ', 'khatama <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> quluubihim wa\'al<u>aa</u> sam\'ihim wa\'al<u>aa</u> ab<u>shaa</u>rihim ghisy<u>aa</u>watun walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Allah telah mengunci hati dan pendengaran mereka, penglihatan mereka telah tertutup, dan mereka akan mendapat azab yang berat.'),
	(15, 2, 8, 'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِنِيْنَۘ', 'wamina <strong>al</strong>nn<u>aa</u>si man yaquulu <u>aa</u>mann<u>aa</u> bi ill<u>aa</u>hi wa billyaumil akhiri wam<u>aa</u> hum bimu/miniin<strong>a</strong>', 'Dan di antara manusia ada yang berkata, “Kami beriman kepada Allah dan hari akhir,” padahal sesungguhnya mereka itu bukanlah orang-orang yang beriman.'),
	(16, 2, 9, 'يُخٰدِعُوْنَ اللّٰهَ وَالَّذِيْنَ اٰمَنُوْا ۚ وَمَا يَخْدَعُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَۗ', 'yukh<u>aa</u>di\'uuna <strong>al</strong>l<u>aa</u>ha wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wam<u>aa</u> yakhda\'uuna ill<u>aa</u> anfusahum wam<u>aa</u> yasy\'uruun<strong>a</strong>', 'Mereka menipu Allah dan orang-orang yang beriman, padahal mereka hanyalah menipu diri sendiri tanpa mereka sadari.'),
	(17, 2, 10, 'فِيْ قُلُوْبِهِمْ مَّرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضًاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ۢ ەۙ بِمَا كَانُوْا يَكْذِبُوْنَ', 'fii quluubihim mara<u>dh</u>un faz<u>aa</u>dahumu <strong>al</strong>l<u>aa</u>hu mara<u>dh</u>an walahum \'a<u>dzaa</u>bun <strong>a</strong>liimun bim<u>aa</u> k<u>aa</u>nuu yak<u>dz</u>ibuun<strong>a</strong>', 'Dalam hati mereka ada penyakit, lalu Allah menambah penyakitnya itu; dan mereka mendapat azab yang pedih, karena mereka berdusta.'),
	(18, 2, 11, 'وَاِذَا قِيْلَ لَهُمْ لَا تُفْسِدُوْا فِى الْاَرْضِۙ قَالُوْٓا اِنَّمَا نَحْنُ مُصْلِحُوْنَ', 'wa-i<u>dzaa</u> qiila lahum l<u>aa</u> tufsiduu fii <strong>a</strong>l-ar<u>dh</u>i q<u>aa</u>luu innam<u>aa</u> na<u>h</u>nu mu<u>sh</u>li<u>h</u>uun<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Janganlah berbuat kerusakan di bumi!” Mereka menjawab, “Sesungguhnya kami justru orang-orang yang melakukan perbaikan.”'),
	(19, 2, 12, 'اَلَآ اِنَّهُمْ هُمُ الْمُفْسِدُوْنَ وَلٰكِنْ لَّا يَشْعُرُوْنَ', 'al<u>aa</u> innahum humu <strong>a</strong>lmufsiduuna wal<u>aa</u>kin l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Ingatlah, sesungguhnya merekalah yang berbuat kerusakan, tetapi mereka tidak menyadari.'),
	(20, 2, 13, 'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا كَمَآ اٰمَنَ النَّاسُ قَالُوْٓا اَنُؤْمِنُ كَمَآ اٰمَنَ السُّفَهَاۤءُ ۗ اَلَآ اِنَّهُمْ هُمُ السُّفَهَاۤءُ وَلٰكِنْ لَّا يَعْلَمُوْنَ ', 'wa-i<u>dzaa</u> qiila lahum <u>aa</u>minuu kam<u>aa</u> <u>aa</u>mana <strong>al</strong>nn<u>aa</u>su q<u>aa</u>luu anu/minu kam<u>aa</u> <u>aa</u>mana <strong>al</strong>ssufah<u>aa</u>u <strong>a</strong>l<u>aa</u> innahum humu <strong>al</strong>ssufah<u>aa</u>u wal<u>aa</u>kin l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Berimanlah kamu sebagaimana orang lain telah beriman!” Mereka menjawab, “Apakah kami akan beriman seperti orang-orang yang kurang akal itu beriman?” Ingatlah, sesungguhnya mereka itulah orang-orang yang kurang akal, tetapi mereka tidak tahu.'),
	(21, 2, 14, 'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّا ۚ وَاِذَا خَلَوْا اِلٰى شَيٰطِيْنِهِمْ ۙ قَالُوْٓا اِنَّا مَعَكُمْ  ۙاِنَّمَا نَحْنُ مُسْتَهْزِءُوْنَ', 'wa-i<u>dzaa</u> laquu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa-i<u>dzaa</u> khalaw il<u>aa</u> syay<u>aath</u>iinihim q<u>aa</u>luu inn<u>aa</u> ma\'akum innam<u>aa</u> na<u>h</u>nu mustahzi-uun<strong>a</strong>', 'Dan apabila mereka berjumpa dengan orang yang beriman, mereka berkata, “Kami telah beriman.” Tetapi apabila mereka kembali kepada setan-setan (para pemimpin) mereka, mereka berkata, “Sesungguhnya kami bersama kamu, kami hanya berolok-olok.”'),
	(22, 2, 15, ' اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ', '<strong>al</strong>l<u>aa</u>hu yastahzi-u bihim wayamudduhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun<strong>a</strong>', 'Allah akan memperolok-olokkan mereka dan membiarkan mereka terombang-ambing dalam kesesatan.'),
	(23, 2, 16, 'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِّجَارَتُهُمْ وَمَا كَانُوْا مُهْتَدِيْنَ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina isytarawuu <strong>al</strong><u>dhdh</u>al<u>aa</u>lata bi<strong>a</strong>lhud<u>aa</u> fam<u>aa</u> rabi<u>h</u>at tij<u>aa</u>ratuhum wam<u>aa</u> k<u>aa</u>nuu muhtadiin<strong>a</strong>', 'Mereka itulah yang membeli kesesatan dengan petunjuk. Maka perdagangan mereka itu tidak beruntung dan mereka tidak mendapat petunjuk.'),
	(24, 2, 17, 'مَثَلُهُمْ كَمَثَلِ الَّذِى اسْتَوْقَدَ نَارًا ۚ فَلَمَّآ اَضَاۤءَتْ مَا حَوْلَهٗ ذَهَبَ اللّٰهُ بِنُوْرِهِمْ وَتَرَكَهُمْ فِيْ ظُلُمٰتٍ لَّا يُبْصِرُوْنَ', 'matsaluhum kamatsali <strong>al</strong>la<u>dz</u>ii istawqada n<u>aa</u>ran falamm<u>aa</u> a<u>dhaa</u>-at m<u>aa</u> <u>h</u>awlahu <u>dz</u>ahaba <strong>al</strong>l<u>aa</u>hu binuurihim watarakahum fii <u>zh</u>ulum<u>aa</u>tin l<u>aa</u> yub<u>sh</u>iruun<strong>a</strong>', 'Perumpamaan mereka seperti orang-orang yang menyalakan api, setelah menerangi sekelilingnya, Allah melenyapkan cahaya (yang menyinari) mereka dan membiarkan mereka dalam kegelapan, tidak dapat melihat.'),
	(25, 2, 18, 'صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُوْنَۙ', '<u>sh</u>ummun bukmun \'umyun fahum l<u>aa</u> yarji\'uun<strong>a</strong>', 'Mereka tuli, bisu dan buta, sehingga mereka tidak dapat kembali.'),
	(26, 2, 19, 'اَوْ كَصَيِّبٍ مِّنَ السَّمَاۤءِ فِيْهِ ظُلُمٰتٌ وَّرَعْدٌ وَّبَرْقٌۚ يَجْعَلُوْنَ اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۗ وَاللّٰهُ مُحِيْطٌۢ بِالْكٰفِرِيْنَ', 'aw ka<u>sh</u>ayyibin mina <strong>al</strong>ssam<u>aa</u>-i fiihi <u>zh</u>ulum<u>aa</u>tun wara\'dun wabarqun yaj\'aluuna a<u>shaa</u>bi\'ahum fii <u>aatsaa</u>nihim mina <strong>al</strong><u>shsh</u>aw<u>aa</u>\'iqi <u>h</u>a<u>dz</u>ara <strong>a</strong>lmawti wa<strong>al</strong>l<u>aa</u>hu mu<u>h</u>ii<u>th</u>un bi<strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Atau seperti (orang yang ditimpa) hujan lebat dari langit, yang disertai kegelapan, petir dan kilat. Mereka menyumbat telinga dengan jari-jarinya, (menghindari) suara petir itu karena takut mati. Allah meliputi orang-orang yang kafir.'),
	(27, 2, 20, 'يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْ ۗ كُلَّمَآ اَضَاۤءَ لَهُمْ مَّشَوْا فِيْهِ ۙ وَاِذَآ اَظْلَمَ عَلَيْهِمْ قَامُوْا ۗوَلَوْ شَاۤءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ', 'yak<u>aa</u>du <strong>a</strong>lbarqu yakh<u>th</u>afu ab<u>shaa</u>rahum kullam<u>aa</u> a<u>dhaa</u>-a lahum masyaw fiihi wa-i<u>dzaa</u> a<u>zh</u>lama \'alayhim q<u>aa</u>muu walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu la<u>dz</u>ahaba bisam\'ihim wa-ab<u>shaa</u>rihim inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Hampir saja kilat itu menyambar penglihatan mereka. Setiap kali (kilat itu) menyinari, mereka berjalan di bawah (sinar) itu, dan apabila gelap menerpa mereka, mereka berhenti. Sekiranya Allah menghendaki, niscaya Dia hilangkan pendengaran dan penglihatan mereka. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(28, 2, 21, ' يٰٓاَيُّهَا النَّاسُ اعْبُدُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ وَالَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su u\'buduu rabbakumu <strong>al</strong>la<u>dz</u>ii khalaqakum wa<strong>a</strong>lla<u>dz</u>iina min qablikum la\'allakum tattaquun<strong>a</strong>', 'Wahai manusia! Sembahlah Tuhanmu yang telah menciptakan kamu dan orang-orang yang sebelum kamu, agar kamu bertakwa.'),
	(29, 2, 22, ' الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ فِرَاشًا وَّالسَّمَاۤءَ بِنَاۤءً  ۖوَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚ فَلَا تَجْعَلُوْا لِلّٰهِ اَنْدَادًا وَّاَنْتُمْ تَعْلَمُوْنَ ', '<strong>al</strong>la<u>dz</u>ii ja\'ala lakumu <strong>a</strong>l-ar<u>dh</u>a fir<u>aa</u>syan wa<strong>al</strong>ssam<u>aa</u>-a bin<u>aa</u>-an wa-anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-akhraja bihi mina <strong>al</strong>tstsamar<u>aa</u>ti rizqan lakum fal<u>aa</u> taj\'aluu lill<u>aa</u>hi and<u>aa</u>dan wa-antum ta\'lamuun<strong>a</strong>', '(Dialah) yang menjadikan bumi sebagai hamparan bagimu dan langit sebagai atap, dan Dialah yang menurunkan air (hujan) dari langit, lalu Dia hasilkan dengan (hujan) itu buah-buahan sebagai rezeki untukmu. Karena itu janganlah kamu mengadakan tandingan-tandingan bagi Allah, padahal kamu mengetahui.'),
	(30, 2, 23, 'وَاِنْ كُنْتُمْ فِيْ رَيْبٍ مِّمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوْا بِسُوْرَةٍ مِّنْ مِّثْلِهٖ ۖ وَادْعُوْا شُهَدَاۤءَكُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'wa-in kuntum fii raybin mimm<u>aa</u> nazzaln<u>aa</u> \'al<u>aa</u> \'abdin<u>aa</u> fa/tuu bisuuratin min mitslihi wa<strong>u</strong>d\'uu syuhad<u>aa</u>-akum min duuni <strong>al</strong>l<u>aa</u>hi in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan jika kamu meragukan (Al-Qur\'an) yang Kami turunkan kepada hamba Kami (Muhammad), maka buatlah satu surah semisal dengannya dan ajaklah penolong-penolongmu selain Allah, jika kamu orang-orang yang benar.'),
	(31, 2, 24, 'فَاِنْ لَّمْ تَفْعَلُوْا وَلَنْ تَفْعَلُوْا فَاتَّقُوا النَّارَ الَّتِيْ وَقُوْدُهَا النَّاسُ وَالْحِجَارَةُ ۖ اُعِدَّتْ لِلْكٰفِرِيْنَ', 'fa-in lam taf\'aluu walan taf\'aluu fa<strong>i</strong>ttaquu <strong>al</strong>nn<u>aa</u>ra <strong>al</strong>latii waquuduh<u>aa</u> <strong>al</strong>nn<u>aa</u>su wa<strong>a</strong>l<u>h</u>ij<u>aa</u>ratu u\'iddat lilk<u>aa</u>firiin<strong>a</strong>', 'Jika kamu tidak mampu membuatnya, dan (pasti) tidak akan mampu, maka takutlah kamu akan api neraka yang bahan bakarnya manusia dan batu, yang disediakan bagi orang-orang kafir.'),
	(32, 2, 25, 'وَبَشِّرِ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اَنَّ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗ كُلَّمَا رُزِقُوْا مِنْهَا مِنْ ثَمَرَةٍ رِّزْقًا ۙ قَالُوْا هٰذَا الَّذِيْ رُزِقْنَا مِنْ قَبْلُ  وَاُتُوْا بِهٖ مُتَشَابِهًا ۗوَلَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ  وَّهُمْ فِيْهَا خٰلِدُوْنَ', 'wabasysyiri <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti anna lahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kullam<u>aa</u> ruziquu minh<u>aa</u> min tsamaratin rizqan q<u>aa</u>luu h<u>aadzaa</u> <strong>al</strong>la<u>dz</u>ii ruziqn<u>aa</u> min qablu wautuu bihi mutasy<u>aa</u>bihan walahum fiih<u>aa</u> azw<u>aa</u>jun mu<u>th</u>ahharatun wahum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Dan sampaikanlah kabar gembira kepada orang-orang yang beriman dan berbuat kebajikan, bahwa untuk mereka (disediakan) surga-surga yang mengalir di bawahnya sungai-sungai. Setiap kali mereka diberi rezeki buah-buahan dari surga, mereka berkata, “Inilah rezeki yang diberikan kepada kami dahulu.” Mereka telah diberi (buah-buahan) yang serupa. Dan di sana mereka (memperoleh) pasangan-pasangan yang suci. Mereka kekal di dalamnya.'),
	(33, 2, 26, '۞ اِنَّ اللّٰهَ لَا يَسْتَحْيٖٓ اَنْ يَّضْرِبَ مَثَلًا مَّا بَعُوْضَةً فَمَا فَوْقَهَا ۗ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۚ وَاَمَّا الَّذِيْنَ كَفَرُوْا فَيَقُوْلُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًا ۘ', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yasta<u>h</u>yii an ya<u>dh</u>riba matsalan m<u>aa</u> ba\'uu<u>dh</u>atan fam<u>aa</u> fawqah<u>aa</u> fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu faya\'lamuuna annahu <strong>a</strong>l<u>h</u>aqqu min rabbihim wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu fayaquuluuna m<u>aatsaa</u> ar<u>aa</u>da <strong>al</strong>l<u>aa</u>hu bih<u>aadzaa</u> matsalan yu<u>dh</u>illu bihi katsiiran wayahdii bihi katsiiran wam<u>aa</u> yu<u>dh</u>illu bihi ill<u>aa</u> <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Sesungguhnya Allah tidak segan membuat perumpamaan seekor nyamuk atau yang lebih kecil dari itu. Adapun orang-orang yang beriman, mereka tahu bahwa itu kebenaran dari Tuhan. Tetapi mereka yang kafir berkata, “Apa maksud Allah dengan perumpamaan ini?” Dengan (perumpamaan) itu banyak orang yang dibiarkan-Nya sesat, dan dengan itu banyak (pula) orang yang diberi-Nya petunjuk. Tetapi tidak ada yang Dia sesatkan dengan (perumpamaan) itu selain orang-orang fasik,'),
	(34, 2, 27, 'الَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْۢ بَعْدِ مِيْثَاقِهٖۖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۗ  اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ', '<strong>al</strong>la<u>dz</u>iina yanqu<u>dh</u>uuna \'ahda <strong>al</strong>l<u>aa</u>hi min ba\'di miits<u>aa</u>qihi wayaq<u>th</u>a\'uuna m<u>aa</u> amara <strong>al</strong>l<u>aa</u>hu bihi an yuu<u>sh</u>ala wayufsiduuna fii <strong>a</strong>l-ar<u>dh</u>i ul<u>aa</u>-ika humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', '(yaitu) orang-orang yang melanggar perjanjian Allah setelah (perjanjian) itu diteguhkan, dan memutuskan apa yang diperintahkan Allah untuk disambungkan dan berbuat kerusakan di bumi. Mereka itulah orang-orang yang rugi.'),
	(35, 2, 28, 'كَيْفَ تَكْفُرُوْنَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتًا فَاَحْيَاكُمْۚ  ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ ', 'kayfa takfuruuna biall<u>aa</u>hi wakuntum amw<u>aa</u>tan fa-a<u>h</u>y<u>aa</u>kum tsumma yumiitukum tsumma yu<u>h</u>yiikum tsumma ilayhi turja\'uun<strong>a</strong>', 'Bagaimana kamu ingkar kepada Allah, padahal kamu (tadinya) mati, lalu Dia menghidupkan kamu, kemudian Dia mematikan kamu lalu Dia menghidupkan kamu kembali. Kemudian kepada-Nyalah kamu dikembalikan.'),
	(36, 2, 29, 'هُوَ الَّذِيْ خَلَقَ لَكُمْ مَّا فِى الْاَرْضِ جَمِيْعًا ثُمَّ اسْتَوٰٓى اِلَى السَّمَاۤءِ فَسَوّٰىهُنَّ سَبْعَ سَمٰوٰتٍ ۗ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ', 'huwa <strong>al</strong>la<u>dz</u>ii khalaqa lakum m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an tsumma istaw<u>aa</u> il<u>aa</u> <strong>al</strong>ssam<u>aa</u>-i fasaww<u>aa</u>hunna sab\'a sam<u>aa</u>w<u>aa</u>tin wahuwa bikulli syay-in \'aliim<strong>un</strong>', 'Dialah (Allah) yang menciptakan segala apa yang ada di bumi untukmu kemudian Dia menuju ke langit, lalu Dia menyempurnakannya menjadi tujuh langit. Dan Dia Maha Mengetahui segala sesuatu.'),
	(37, 2, 30, 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ ِانِّيْ جَاعِلٌ فِى الْاَرْضِ خَلِيْفَةً ۗ قَالُوْٓا اَتَجْعَلُ فِيْهَا مَنْ يُّفْسِدُ فِيْهَا وَيَسْفِكُ الدِّمَاۤءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۗ قَالَ اِنِّيْٓ اَعْلَمُ مَا لَا تَعْلَمُوْنَ', 'wa-i<u>dz</u> q<u>aa</u>la rabbuka lilmal<u>aa</u>-ikati innii j<u>aa</u>\'ilun fii <strong>a</strong>l-ar<u>dh</u>i khaliifatan q<u>aa</u>luu ataj\'alu fiih<u>aa</u> man yufsidu fiih<u>aa</u> wayasfiku <strong>al</strong>ddim<u>aa</u>-a wana<u>h</u>nu nusabbi<u>h</u>u bi<u>h</u>amdika wanuqaddisu laka q<u>aa</u>la innii a\'lamu m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan (ingatlah) ketika Tuhanmu berfirman kepada para malaikat, “Aku hendak menjadikan khalifah di bumi.” Mereka berkata, “Apakah Engkau hendak menjadikan orang yang merusak dan menumpahkan darah di sana, sedangkan kami bertasbih memuji-Mu dan menyucikan nama-Mu?” Dia berfirman, “Sungguh, Aku mengetahui apa yang tidak kamu ketahui.”'),
	(38, 2, 31, 'وَعَلَّمَ اٰدَمَ الْاَسْمَاۤءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰۤىِٕكَةِ فَقَالَ اَنْۢبِـُٔوْنِيْ بِاَسْمَاۤءِ هٰٓؤُلَاۤءِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wa\'allama <u>aa</u>dama <strong>a</strong>l-asm<u>aa</u>-a kullah<u>aa</u> tsumma \'ara<u>dh</u>ahum \'al<u>aa</u> <strong>a</strong>lmal<u>aa</u>-ikati faq<u>aa</u>la anbi-uunii bi-asm<u>aa</u>-i h<u>aa</u>ul<u>aa</u>-i in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan Dia ajarkan kepada Adam nama-nama (benda) semuanya, kemudian Dia perlihatkan kepada para malaikat, seraya berfirman, “Sebutkan kepada-Ku nama semua (benda) ini, jika kamu yang benar!”'),
	(39, 2, 32, 'قَالُوْا سُبْحٰنَكَ لَا عِلْمَ لَنَآ اِلَّا مَا عَلَّمْتَنَا ۗاِنَّكَ اَنْتَ الْعَلِيْمُ الْحَكِيْمُ ', 'q<u>aa</u>luu sub<u>haa</u>naka l<u>aa</u> \'ilma lan<u>aa</u> ill<u>aa</u> m<u>aa</u> \'allamtan<u>aa</u> innaka anta <strong>a</strong>l\'aliimu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Mereka menjawab, “Mahasuci Engkau, tidak ada yang kami  ketahui selain apa yang telah Engkau ajarkan kepada kami. Sungguh, Engkaulah Yang Maha Mengetahui, Mahabijaksana.”'),
	(40, 2, 33, 'قَالَ يٰٓاٰدَمُ اَنْۢبِئْهُمْ بِاَسْمَاۤىِٕهِمْ ۚ فَلَمَّآ اَنْۢبَاَهُمْ بِاَسْمَاۤىِٕهِمْۙ قَالَ اَلَمْ اَقُلْ لَّكُمْ اِنِّيْٓ اَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۙ وَاَعْلَمُ مَا تُبْدُوْنَ وَمَا كُنْتُمْ تَكْتُمُوْنَ ', 'q<u>aa</u>la y<u>aa</u> <u>aa</u>damu anbi/hum bi-asm<u>aa</u>-ihim falamm<u>aa</u> anba-ahum bi-asm<u>aa</u>-ihim q<u>aa</u>la alam aqul lakum innii a\'lamu ghayba <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa-a\'lamu m<u>aa</u> tubduuna wam<u>aa</u> kuntum taktumuun<strong>a</strong>', 'Dia (Allah) berfirman, “Wahai Adam! Beritahukanlah kepada mereka nama-nama itu!” Setelah dia (Adam) menyebutkan nama-namanya, Dia berfirman, “Bukankah telah Aku katakan kepadamu, bahwa Aku mengetahui rahasia langit dan bumi, dan Aku mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan?”'),
	(41, 2, 34, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ  اَبٰى وَاسْتَكْبَرَۖ وَكَانَ مِنَ الْكٰفِرِيْنَ', 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa ab<u>aa</u> wa<strong>i</strong>stakbara wak<u>aa</u>na mina <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Maka mereka pun sujud kecuali Iblis. Ia menolak dan menyombongkan diri, dan ia termasuk golongan yang kafir.'),
	(42, 2, 35, 'وَقُلْنَا يٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ', 'waquln<u>aa</u> y<u>aa</u> <u>aa</u>damu uskun anta wazawjuka <strong>a</strong>ljannata wakul<u>aa</u> minh<u>aa</u> raghadan <u>h</u>aytsu syi/tum<u>aa</u> wal<u>aa</u> taqrab<u>aa</u> h<u>aadz</u>ihi <strong>al</strong>sysyajarata fatakuun<u>aa</u> mina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan Kami berfirman, “Wahai Adam! Tinggallah engkau dan istrimu di dalam surga, dan makanlah dengan nikmat (berbagai makanan) yang ada di sana sesukamu. (Tetapi) janganlah kamu dekati pohon ini, nanti kamu termasuk orang-orang yang zalim!”'),
	(43, 2, 36, 'فَاَزَلَّهُمَا الشَّيْطٰنُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا فِيْهِ ۖ وَقُلْنَا اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚ وَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ', 'fa-azallahum<u>aa</u> <strong>al</strong>sysyay<u>th</u><u>aa</u>nu \'anh<u>aa</u> fa-akhrajahum<u>aa</u> mimm<u>aa</u> k<u>aa</u>n<u>aa</u> fiihi waquln<u>aa</u> ihbi<u>th</u>uu ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun walakum fii <strong>a</strong>l-ar<u>dh</u>i mustaqarrun wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Lalu setan memperdayakan keduanya dari surga sehingga keduanya dikeluarkan dari (segala kenikmatan) ketika keduanya di sana (surga). Dan Kami berfirman, “Turunlah kamu! Sebagian kamu menjadi musuh bagi yang lain. Dan bagi kamu ada tempat tinggal dan kesenangan di bumi sampai waktu yang ditentukan.”'),
	(44, 2, 37, 'فَتَلَقّٰٓى اٰدَمُ مِنْ رَّبِّهٖ كَلِمٰتٍ فَتَابَ عَلَيْهِ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ ', 'fatalaqq<u>aa</u> <u>aa</u>damu min rabbihi kalim<u>aa</u>tin fat<u>aa</u>ba \'alayhi innahu huwa <strong>al</strong>ttaww<u>aa</u>bu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Kemudian Adam menerima beberapa kalimat dari Tuhannya, lalu Dia pun menerima tobatnya. Sungguh, Allah Maha Penerima tobat, Maha Penyayang.'),
	(45, 2, 38, 'قُلْنَا اهْبِطُوْا مِنْهَا جَمِيْعًا ۚ فَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'quln<u>aa</u> ihbi<u>th</u>uu minh<u>aa</u> jamii\'an fa-imm<u>aa</u> ya/tiyannakum minnii hudan faman tabi\'a hud<u>aa</u>ya fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Kami berfirman, “Turunlah kamu semua dari surga! Kemudian jika benar-benar datang petunjuk-Ku kepadamu, maka barangsiapa mengikuti petunjuk-Ku, tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.”'),
	(46, 2, 39, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu penghuni neraka. Mereka kekal di dalamnya.'),
	(47, 2, 40, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوْا بِعَهْدِيْٓ اُوْفِ بِعَهْدِكُمْۚ وَاِيَّايَ فَارْهَبُوْنِ', 'y<u>aa</u> banii isr<u>aa</u>-iila u<u>dz</u>kuruu ni\'matiya <strong>al</strong>latii an\'amtu \'alaykum wa-awfuu bi\'ahdii uufi bi\'ahdikum wa-iyy<u>aa</u>ya fa<strong>i</strong>rhabuun<strong>i</strong>', 'Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu. Dan penuhilah janjimu kepada-Ku, niscaya Aku penuhi janji-Ku kepadamu, dan takutlah kepada-Ku saja.'),
	(48, 2, 41, 'وَاٰمِنُوْا بِمَآ اَنْزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُوْنُوْٓا اَوَّلَ كَافِرٍۢ بِهٖ ۖ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا  ۖوَّاِيَّايَ فَاتَّقُوْنِ ', 'wa<u>aa</u>minuu bim<u>aa</u> anzaltu mu<u>sh</u>addiqan lim<u>aa</u> ma\'akum wal<u>aa</u> takuunuu awwala k<u>aa</u>firin bihi wal<u>aa</u> tasytaruu bi-<u>aa</u>y<u>aa</u>tii tsamanan qaliilan wa-iyy<u>aa</u>ya fa<strong>i</strong>ttaquun<strong>i</strong>', 'Dan berimanlah kamu kepada apa (Al-Qur\'an) yang telah Aku turunkan yang membenarkan apa (Taurat) yang ada pada kamu, dan janganlah kamu menjadi orang yang pertama kafir kepadanya. Janganlah kamu jual ayat-ayat-Ku dengan harga murah, dan bertakwalah hanya kepada-Ku.'),
	(49, 2, 42, 'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ ', 'wal<u>aa</u> talbisuu <strong>a</strong>l<u>h</u>aqqa bi<strong>a</strong>lb<u>aath</u>ili wataktumuu <strong>a</strong>l<u>h</u>aqqa wa-antum ta\'lamuun<strong>a</strong>', 'Dan janganlah kamu campuradukkan kebenaran dengan kebatilan dan (janganlah) kamu sembunyikan kebenaran, sedangkan kamu mengetahuinya.'),
	(50, 2, 43, 'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوْا مَعَ الرَّاكِعِيْنَ', 'wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu <strong>al</strong>zzak<u>aa</u>ta wa<strong>i</strong>rka\'uu ma\'a <strong>al</strong>rr<u>aa</u>ki\'iin<strong>a</strong>', 'Dan laksanakanlah salat, tunaikanlah zakat, dan rukuklah beserta orang yang rukuk.'),
	(51, 2, 44, '۞ اَتَأْمُرُوْنَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُوْنَ الْكِتٰبَ ۗ اَفَلَا تَعْقِلُوْنَ ', 'ata/muruuna <strong>al</strong>nn<u>aa</u>sa bi<strong>a</strong>lbirri watansawna anfusakum wa-antum tatluuna <strong>a</strong>lkit<u>aa</u>ba afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Mengapa kamu menyuruh orang lain (mengerjakan) kebajikan, sedangkan kamu melupakan dirimu sendiri, padahal kamu membaca Kitab (Taurat)? Tidakkah kamu mengerti?'),
	(52, 2, 45, 'وَاسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ وَاِنَّهَا لَكَبِيْرَةٌ اِلَّا عَلَى الْخٰشِعِيْنَۙ', 'wa<strong>i</strong>sta\'iinuu bi<strong>al</strong><u>shsh</u>abri wa<strong>al</strong><u>shsh</u>al<u>aa</u>ti wa-innah<u>aa</u> lakabiiratun ill<u>aa</u> \'al<u>aa</u> <strong>a</strong>lkh<u>aa</u>syi\'iin<strong>a</strong>', 'Dan mohonlah pertolongan (kepada Allah) dengan sabar dan salat. Dan (salat) itu sungguh berat, kecuali bagi orang-orang yang khusyuk,'),
	(53, 2, 46, 'الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رٰجِعُوْنَ ࣖ', '<strong>al</strong>la<u>dz</u>iina ya<u>zh</u>unnuuna annahum mul<u>aa</u>quu rabbihim wa-annahum ilayhi r<u>aa</u>ji\'uun<strong>a</strong>', '(yaitu) mereka yang yakin, bahwa mereka akan menemui Tuhannya, dan bahwa mereka akan kembali kepada-Nya.'),
	(54, 2, 47, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ', 'y<u>aa</u> banii isr<u>aa</u>-iila u<u>dz</u>kuruu ni\'matiya <strong>al</strong>latii an\'amtu \'alaykum wa-annii fa<u>dhdh</u>altukum \'al<u>aa</u> <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu, dan Aku telah melebihkan kamu dari semua umat yang lain di alam ini (pada masa itu).'),
	(55, 2, 48, 'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَّلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَّلَا هُمْ يُنْصَرُوْنَ ', 'wa<strong>i</strong>ttaquu yawman l<u>aa</u> tajzii nafsun \'an nafsin syay-an wal<u>aa</u> yuqbalu minh<u>aa</u> syaf<u>aa</u>\'atun wal<u>aa</u> yu/kha<u>dz</u>u minh<u>aa</u> \'adlun wal<u>aa</u> hum yun<u>sh</u>aruun<strong>a</strong>', 'Dan takutlah kamu pada hari, (ketika) tidak seorang pun dapat membela orang lain sedikit pun. Sedangkan syafaat dan tebusan apa pun darinya tidak diterima dan mereka tidak akan ditolong.'),
	(56, 2, 49, 'وَاِذْ نَجَّيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ يُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ', 'wa-i<u>dz</u> najjayn<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a <strong>a</strong>l\'a<u>dzaa</u>bi yu<u>dz</u>abbi<u>h</u>uuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim<strong>un</strong>', 'Dan (ingatlah) ketika Kami menyelamatkan kamu dari (Fir‘aun dan) pengikut-pengikut Fir‘aun. Mereka menimpakan siksaan yang sangat berat kepadamu. Mereka menyembelih anak-anak laki-lakimu dan membiarkan hidup anak-anak perempuanmu. Dan pada yang demikian itu merupakan cobaan yang besar dari Tuhanmu. '),
	(57, 2, 50, 'وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنٰكُمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُوْنَ ', 'wa-i<u>dz</u> faraqn<u>aa</u> bikumu <strong>a</strong>lba<u>h</u>ra fa-anjayn<u>aa</u>kum wa-aghraqn<u>aa</u> <u>aa</u>la fir\'awna wa-antum tan<u>zh</u>uruun<strong>a</strong>', 'Dan (ingatlah) ketika Kami membelah laut untukmu, sehingga kamu dapat Kami selamatkan dan Kami tenggelamkan (Fir‘aun dan) pengikut-pengikut Fir‘aun, sedang kamu menyaksikan.'),
	(58, 2, 51, 'وَاِذْ وٰعَدْنَا مُوْسٰىٓ اَرْبَعِيْنَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ', 'wa-i<u>dz</u> w<u>aa</u>\'adn<u>aa</u> muus<u>aa</u> arba\'iina laylatan tsumma ittakha<u>dz</u>tumu <strong>a</strong>l\'ijla min ba\'dihi wa-antum <u>zhaa</u>limuun<strong>a</strong>', 'Dan (ingatlah) ketika Kami menjanjikan kepada Musa empat puluh malam. Kemudian kamu (Bani Israil) menjadikan (patung) anak sapi (sebagai sesembahan) setelah (kepergian)nya, dan kamu (menjadi) orang yang zalim.'),
	(59, 2, 52, 'ثُمَّ عَفَوْنَا عَنْكُمْ مِّنْۢ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُوْنَ', 'tsumma \'afawn<u>aa</u> \'ankum min ba\'di <u>dzaa</u>lika la\'allakum tasykuruun<strong>a</strong>', 'Kemudian Kami memaafkan kamu setelah itu, agar kamu bersyukur.'),
	(60, 2, 53, 'وَاِذْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُوْنَ', 'wa-i<u>dz</u> <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>lfurq<u>aa</u>na la\'allakum tahtaduun<strong>a</strong>', 'Dan (ingatlah), ketika Kami memberikan kepada Musa Kitab dan Furqan, agar kamu memperoleh petunjuk. '),
	(61, 2, 54, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوْبُوْٓا اِلٰى بَارِىِٕكُمْ فَاقْتُلُوْٓا اَنْفُسَكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ عِنْدَ بَارِىِٕكُمْۗ فَتَابَ عَلَيْكُمْ ۗ اِنَّهٗ هُوَ التَّوَّابُ الر', 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi y<u>aa</u> qawmi innakum <u>zh</u>alamtum anfusakum bi<strong>i</strong>ttikh<u>aadz</u>ikumu <strong>a</strong>l\'ijla fatuubuu il<u>aa</u> b<u>aa</u>ri-ikum fa<strong>u</strong>qtuluu anfusakum <u>dzaa</u>likum khayrun lakum \'inda b<u>aa</u>ri-ikum fat<u>aa</u>ba \'alaykum innahu huwa <strong>al</strong>ttaww<u>aa</u>bu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Wahai kaumku! Kamu benar-benar telah menzalimi dirimu sendiri dengan menjadikan (patung) anak sapi (sebagai sesembahan), karena itu bertobatlah kepada Penciptamu dan bunuhlah dirimu. Itu lebih baik bagimu di sisi Penciptamu. Dia akan menerima tobatmu. Sungguh, Dialah Yang Maha Penerima tobat, Maha Penyayang.'),
	(62, 2, 55, 'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نُّؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصّٰعِقَةُ وَاَنْتُمْ تَنْظُرُوْنَ', 'wa-i<u>dz</u> qultum y<u>aa</u> muus<u>aa</u> lan nu/mina laka <u>h</u>att<u>aa</u> nar<u>aa</u> <strong>al</strong>l<u>aa</u>ha jahratan fa-akha<u>dz</u>atkumu <strong>al</strong><u>shshaa</u>\'iqatu wa-antum tan<u>zh</u>uruun<strong>a</strong>', 'Dan (ingatlah) ketika kamu berkata, “Wahai Musa! Kami tidak akan beriman kepadamu sebelum kami melihat Allah dengan jelas,” maka halilintar menyambarmu, sedang kamu menyaksikan.'),
	(63, 2, 56, 'ثُمَّ بَعَثْنٰكُمْ مِّنْۢ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ', 'tsumma ba\'atsn<u>aa</u>kum min ba\'di mawtikum la\'allakum tasykuruun<strong>a</strong>', 'Kemudian, Kami membangkitkan kamu setelah kamu mati, agar kamu bersyukur.'),
	(64, 2, 57, 'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ ۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ', 'wa<u>zh</u>allaln<u>aa</u> \'alaykumu <strong>a</strong>lgham<u>aa</u>ma wa-anzaln<u>aa</u> \'alaykumu <strong>a</strong>lmanna wa<strong>al</strong>ssalw<u>aa</u> kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wam<u>aa</u> <u>zh</u>alamuun<u>aa</u> wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Dan Kami menaungi kamu dengan awan, dan Kami menurunkan kepadamu mann dan salwa. Makanlah (makanan) yang baik-baik dari rezeki yang telah Kami berikan kepadamu. Mereka tidak menzalimi Kami, tetapi justru merekalah yang menzalimi diri sendiri.'),
	(65, 2, 58, 'وَاِذْ قُلْنَا ادْخُلُوْا هٰذِهِ الْقَرْيَةَ فَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَّادْخُلُوا الْبَابَ سُجَّدًا وَّقُوْلُوْا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطٰيٰكُمْ ۗ وَسَنَزِيْدُ الْمُحْسِنِيْنَ ', 'wa-i<u>dz</u> quln<u>aa</u> udkhuluu h<u>aadz</u>ihi <strong>a</strong>lqaryata fakuluu minh<u>aa</u> <u>h</u>aytsu syi/tum raghadan wa<strong>u</strong>dkhuluu <strong>a</strong>lb<u>aa</u>ba sujjadan waquuluu <u>h</u>i<u>ththh</u>atun naghfir lakum kha<u>th</u><u>aa</u>y<u>aa</u>kum wasanaziidu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan (ingatlah) ketika Kami berfirman, “Masuklah ke negeri ini (Baitulmaqdis), maka makanlah dengan nikmat (berbagai makanan) yang ada di sana sesukamu. Dan masukilah pintu gerbangnya sambil membungkuk, dan katakanlah, “Bebaskanlah kami (dari dosa-dosa kami),” niscaya Kami ampuni kesalahan-kesalahanmu. Dan Kami akan menambah (karunia) bagi orang-orang yang berbuat kebaikan.”'),
	(66, 2, 59, 'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذِيْنَ ظَلَمُوْا رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ ࣖ', 'fabaddala <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu qawlan ghayra <strong>al</strong>la<u>dz</u>ii qiila lahum fa-anzaln<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu rijzan mina <strong>al</strong>ssam<u>aa</u>-i bim<u>aa</u> k<u>aa</u>nuu yafsuquun<strong>a</strong>', 'Lalu orang-orang yang zalim mengganti perintah dengan (perintah lain) yang tidak diperintahkan kepada mereka. Maka Kami turunkan malapetaka dari langit kepada orang-orang yang zalim itu, karena mereka (selalu) berbuat fasik.'),
	(67, 2, 60, '۞ وَاِذِ اسْتَسْقٰى مُوْسٰى لِقَوْمِهٖ فَقُلْنَا اضْرِبْ بِّعَصَاكَ الْحَجَرَۗ  فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْ ۗ كُلُوْا وَاشْرَبُوْا مِنْ رِّزْقِ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ', 'wa-i<u>dz</u>i istasq<u>aa</u> muus<u>aa</u> liqawmihi faquln<u>aa</u> i<u>dh</u>rib bi\'a<u>shaa</u>ka <strong>a</strong>l<u>h</u>ajara fa<strong>i</strong>nfajarat minhu itsnat<u>aa</u> \'asyrata \'aynan qad \'alima kullu un<u>aa</u>sin masyrabahum kuluu wa<strong>i</strong>syrabuu min rizqi <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> ta\'tsaw fii <strong>a</strong>l-ar<u>dh</u>i mufsidiin<strong>a</strong>', 'Dan (ingatlah) ketika Musa memohon air untuk kaumnya, lalu Kami berfirman, “Pukullah batu itu dengan tongkatmu!” Maka memancarlah daripadanya dua belas mata air. Setiap suku telah mengetahui tempat minumnya (masing-masing). Makan dan minumlah dari rezeki (yang diberikan) Allah, dan janganlah kamu melakukan kejahatan di bumi dengan berbuat kerusakan.'),
	(68, 2, 61, 'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نَّصْبِرَ عَلٰى طَعَامٍ وَّاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْۢبِتُ الْاَرْضُ مِنْۢ بَقْلِهَا وَقِثَّاۤىِٕهَا وَفُوْمِهَا وَعَدَسِهَا وَبَصَلِهَا ۗ قَالَ اَتَسْتَبْدِلُوْنَ الَّذِيْ هُوَ اَدْنٰى بِالَّذِيْ', 'wa-i<u>dz</u> qultum y<u>aa</u> muus<u>aa</u> lan na<u>sh</u>bira \'al<u>aa</u> <u>th</u>a\'<u>aa</u>min w<u>aah</u>idin fa<strong>u</strong>d\'u lan<u>aa</u> rabbaka yukhrij lan<u>aa</u> mimm<u>aa</u> tunbitu <strong>a</strong>l-ar<u>dh</u>u min baqlih<u>aa</u> waqitsts<u>aa</u>-ih<u>aa</u> wafuumih<u>aa</u> wa\'adasih<u>aa</u> waba<u>sh</u>alih<u>aa</u> q<u>aa</u>la atastabdiluuna <strong>al</strong>la<u>dz</u>ii huwa adn<u>aa</u> bi<strong>a</strong>lla<u>dz</u>ii huwa khayrun ihbi<u>th</u>uu mi<u>sh</u>ran fa-inna lakum m<u>aa</u> sa-altum wa<u>dh</u>uribat \'alayhimu <strong>al</strong><u>dzdz</u>illatu wa<strong>a</strong>lmaskanatu wab<u>aa</u>uu bigha<u>dh</u>abin mina <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>lika bi-annahum k<u>aa</u>nuu yakfuruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wayaqtuluuna <strong>al</strong>nnabiyyiina bighayri <strong>a</strong>l<u>h</u>aqqi <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun<strong>a</strong>', 'Dan (ingatlah), ketika kamu berkata, “Wahai Musa! Kami tidak tahan hanya (makan) dengan satu macam makanan saja, maka mohonkanlah kepada Tuhanmu untuk kami, agar Dia memberi kami apa yang ditumbuhkan bumi, seperti: sayur-mayur, mentimun, bawang putih, kacang adas dan bawang merah.” Dia (Musa) menjawab, “Apakah kamu meminta sesuatu yang buruk sebagai ganti dari sesuatu yang baik? Pergilah ke suatu kota, pasti kamu akan memperoleh apa yang kamu minta.” Kemudian mereka ditimpa kenistaan dan kemiskinan, dan mereka (kembali) mendapat kemurkaan dari Allah. Hal itu (terjadi) karena mereka mengingkari ayat-ayat Allah dan membunuh para nabi tanpa hak (alasan yang benar). Yang demikian itu karena mereka durhaka dan melampaui batas.'),
	(69, 2, 62, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالنَّصٰرٰى وَالصَّابِــِٕيْنَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>duu wa<strong>al</strong>nna<u>shaa</u>r<u>aa</u> wa<strong>al</strong><u>shshaa</u>bi-iina man <u>aa</u>mana biall<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa\'amila <u>shaa</u>li<u>h</u>an falahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, orang-orang Nasrani dan orang-orang sabi\'in, siapa saja (di antara mereka) yang beriman kepada Allah dan hari akhir, dan melakukan kebajikan, mereka mendapat pahala dari Tuhannya, tidak ada rasa takut pada mereka, dan mereka tidak bersedih hati.'),
	(70, 2, 63, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ', 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qakum warafa\'n<u>aa</u> fawqakumu <strong>al</strong><u>ththh</u>uura khu<u>dz</u>uu m<u>aa</u> <u>aa</u>tayn<u>aa</u>kum biquwwatin wa<strong>u</strong><u>dz</u>kuruu m<u>aa</u> fiihi la\'allakum tattaquun<strong>a</strong>', 'Dan (ingatlah) ketika Kami mengambil janji kamu dan Kami angkat gunung (Sinai) di atasmu (seraya berfirman), “Pegang teguhlah apa yang telah Kami berikan kepadamu dan ingatlah apa yang ada di dalamnya, agar kamu bertakwa.”'),
	(71, 2, 64, 'ثُمَّ تَوَلَّيْتُمْ مِّنْۢ بَعْدِ ذٰلِكَ  فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَكُنْتُمْ مِّنَ الْخٰسِرِيْنَ', 'tsumma tawallaytum min ba\'di <u>dzaa</u>lika falawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu lakuntum mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Kemudian setelah itu kamu berpaling. Maka sekiranya bukan karena karunia Allah dan rahmat-Nya kepadamu, pasti kamu termasuk orang yang rugi.'),
	(72, 2, 65, 'وَلَقَدْ عَلِمْتُمُ الَّذِيْنَ اعْتَدَوْا مِنْكُمْ فِى السَّبْتِ فَقُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ', 'walaqad \'alimtumu <strong>al</strong>la<u>dz</u>iina i\'tadaw minkum fii <strong>al</strong>ssabti faquln<u>aa</u> lahum kuunuu qiradatan kh<u>aa</u>si-iin<strong>a</strong>', 'Dan sungguh, kamu telah mengetahui orang-orang yang melakukan pelanggaran di antara kamu pada hari Sabat,  lalu Kami katakan kepada mereka, “Jadilah kamu kera yang hina!” '),
	(73, 2, 66, '  فَجَعَلْنٰهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِّلْمُتَّقِيْنَ', 'faja\'aln<u>aa</u>h<u>aa</u> nak<u>aa</u>lan lim<u>aa</u> bayna yadayh<u>aa</u> wam<u>aa</u> khalfah<u>aa</u> wamaw\'i<u>zh</u>atan lilmuttaqiin<strong>a</strong>', 'Maka Kami jadikan (yang demikian) itu peringatan bagi orang-orang pada masa itu dan bagi mereka yang datang kemudian, serta menjadi pelajaran bagi orang-orang yang bertakwa.'),
	(74, 2, 67, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوْا بَقَرَةً ۗ قَالُوْٓا اَتَتَّخِذُنَا هُزُوًا ۗ قَالَ اَعُوْذُ بِاللّٰهِ اَنْ اَكُوْنَ مِنَ الْجٰهِلِيْنَ ', 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi inna <strong>al</strong>l<u>aa</u>ha ya/murukum an ta<u>dz</u>ba<u>h</u>uu baqaratan q<u>aa</u>luu atattakhi<u>dz</u>un<u>aa</u> huzuwan q<u>aa</u>la a\'uu<u>dz</u>u biall<u>aa</u>hi an akuuna mina <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Allah memerintahkan kamu agar menyembelih seekor sapi betina.” Mereka bertanya, “Apakah engkau akan menjadikan kami sebagai ejekan?” Dia (Musa) menjawab, “Aku berlindung kepada Allah agar tidak termasuk orang-orang yang bodoh.”'),
	(75, 2, 68, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَ ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا فَارِضٌ وَّلَا بِكْرٌۗ عَوَانٌۢ بَيْنَ ذٰلِكَ ۗ فَافْعَلُوْا مَا تُؤْمَرُوْنَ ', 'q<u>aa</u>luu ud\'u lan<u>aa</u> rabbaka yubayyin lan<u>aa</u> m<u>aa</u> hiya q<u>aa</u>la innahu yaquulu innah<u>aa</u> baqaratun l<u>aa</u> f<u>aa</u>ri<u>dh</u>un wal<u>aa</u> bikrun \'aw<u>aa</u>nun bayna <u>dzaa</u>lika fa<strong>i</strong>f\'aluu m<u>aa</u> tu/maruun<strong>a</strong>', 'Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami tentang (sapi betina) itu.” Dia (Musa) menjawab, “Dia (Allah) berfirman, bahwa sapi betina itu tidak tua dan tidak muda, (tetapi) pertengahan antara itu. Maka kerjakanlah apa yang diperintahkan kepadamu.”'),
	(76, 2, 69, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا لَوْنُهَا ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ صَفْرَاۤءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النّٰظِرِيْنَ', 'q<u>aa</u>luu ud\'u lan<u>aa</u> rabbaka yubayyin lan<u>aa</u> m<u>aa</u> lawnuh<u>aa</u> q<u>aa</u>la innahu yaquulu innah<u>aa</u> baqaratun <u>sh</u>afr<u>aa</u>u f<u>aa</u>qi\'un lawnuh<u>aa</u> tasurru <strong>al</strong>nn<u>aazh</u>iriin<strong>a</strong>', 'Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami apa warnanya.” Dia (Musa) menjawab, “Dia (Allah) berfirman, bahwa (sapi) itu adalah sapi betina yang kuning tua warnanya, yang menyenangkan orang-orang yang memandang(nya).”'),
	(77, 2, 70, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشٰبَهَ عَلَيْنَاۗ وَاِنَّآ اِنْ شَاۤءَ اللّٰهُ لَمُهْتَدُوْنَ', 'q<u>aa</u>luu ud\'u lan<u>aa</u> rabbaka yubayyin lan<u>aa</u> m<u>aa</u> hiya inna <strong>a</strong>lbaqara tasy<u>aa</u>baha \'alayn<u>aa</u> wa-inn<u>aa</u> in sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu lamuhtaduun<strong>a</strong>', 'Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami tentang (sapi betina) itu. (Karena) sesungguhnya sapi itu belum jelas bagi kami, dan jika Allah menghendaki, niscaya kami mendapat petunjuk.”'),
	(78, 2, 71, 'قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا ذَلُوْلٌ تُثِيْرُ الْاَرْضَ وَلَا تَسْقِى الْحَرْثَۚ مُسَلَّمَةٌ لَّاشِيَةَ فِيْهَا ۗ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّ  فَذَبَحُوْهَا وَمَا كَادُوْا يَفْعَلُوْنَ ࣖ', 'q<u>aa</u>la innahu yaquulu innah<u>aa</u> baqaratun l<u>aa</u> <u>dz</u>aluulun tutsiiru <strong>a</strong>l-ar<u>dh</u>a wal<u>aa</u> tasqii <strong>a</strong>l<u>h</u>artsa musallamatun l<u>aa</u> syiyata fiih<u>aa</u> q<u>aa</u>luu <strong>a</strong>l-<u>aa</u>na ji/ta bi<strong>a</strong>l<u>h</u>aqqi fa<u>dz</u>aba<u>h</u>uuh<u>aa</u> wam<u>aa</u> k<u>aa</u>duu yaf\'aluun<strong>a</strong>', 'Dia (Musa) menjawab, “Dia (Allah) berfirman, (sapi) itu adalah sapi betina yang belum pernah dipakai untuk membajak tanah dan tidak (pula) untuk mengairi tanaman, sehat, dan tanpa belang.” Mereka berkata, “Sekarang barulah engkau menerangkan (hal) yang sebenarnya.” Lalu mereka menyembelihnya, dan nyaris mereka tidak melaksanakan (perintah) itu.'),
	(79, 2, 72, 'وَاِذْ قَتَلْتُمْ نَفْسًا فَادّٰرَءْتُمْ فِيْهَا ۗ وَاللّٰهُ مُخْرِجٌ مَّا كُنْتُمْ تَكْتُمُوْنَ ۚ', 'wa-i<u>dz</u> qataltum nafsan fa<strong>i</strong>dd<u>aa</u>ra/tum fiih<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu mukhrijun m<u>aa </u>kuntum taktumuun<strong>a</strong>', 'Dan (ingatlah) ketika kamu membunuh seseorang, lalu kamu tuduh-menuduh tentang itu. Tetapi Allah menyingkapkan apa yang kamu sembunyikan.'),
	(80, 2, 73, 'فَقُلْنَا اضْرِبُوْهُ بِبَعْضِهَاۗ  كَذٰلِكَ يُحْيِ اللّٰهُ الْمَوْتٰى وَيُرِيْكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ', 'faquln<u>aa</u> i<u>dh</u>ribuuhu biba\'<u>dh</u>ih<u>aa</u> ka<u>dzaa</u>lika yu<u>h</u>yii <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmawt<u>aa</u> wayuriikum <u>aa</u>y<u>aa</u>tihi la\'allakum ta\'qiluun<strong>a</strong>', 'Lalu Kami berfirman, “Pukullah (mayat) itu dengan bagian dari (sapi) itu!” Demikianlah Allah menghidupkan (orang) yang telah mati, dan Dia memperlihatkan kepadamu tanda-tanda (kekuasaan-Nya) agar kamu mengerti.'),
	(81, 2, 74, 'ثُمَّ قَسَتْ قُلُوْبُكُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةً ۗ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهٰرُ ۗ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاۤءُ  ۗوَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِن', 'tsumma qasat quluubukum min ba\'di <u>dzaa</u>lika fahiya ka<strong>a</strong>l<u>h</u>ij<u>aa</u>rati aw asyaddu qaswatan wa-inna mina <strong>a</strong>l<u>h</u>ij<u>aa</u>rati lam<u>aa</u> yatafajjaru minhu <strong>a</strong>l-anh<u>aa</u>ru wa-inna minh<u>aa</u> lam<u>aa</u> yasysyaqqaqu fayakhruju minhu <strong>a</strong>lm<u>aa</u>u wa-inna minh<u>aa</u> lam<u>aa</u> yahbi<u>th</u>u min khasyyati <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Kemudian setelah itu hatimu menjadi keras, sehingga (hatimu) seperti batu, bahkan lebih keras. Padahal dari batu-batu itu pasti ada sungai-sungai yang (airnya) memancar daripadanya. Ada pula yang terbelah lalu keluarlah mata air daripadanya. Dan ada pula yang meluncur jatuh karena takut kepada Allah. Dan Allah tidaklah lengah terhadap apa yang kamu kerjakan.'),
	(82, 2, 75, '۞ اَفَتَطْمَعُوْنَ اَنْ يُّؤْمِنُوْا لَكُمْ وَقَدْ كَانَ فَرِيْقٌ مِّنْهُمْ يَسْمَعُوْنَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُوْنَهٗ مِنْۢ بَعْدِ مَا عَقَلُوْهُ وَهُمْ يَعْلَمُوْنَ ', 'afata<u>th</u>ma\'uuna an yu/minuu lakum waqad k<u>aa</u>na fariiqun minhum yasma\'uuna kal<u>aa</u>ma <strong>al</strong>l<u>aa</u>hi tsumma yu<u>h</u>arrifuunahu min ba\'di m<u>aa</u> \'aqaluuhu wahum ya\'lamuun<strong>a</strong>', 'Maka apakah kamu (Muslimin) sangat mengharapkan mereka akan percaya kepadamu, sedangkan segolongan dari mereka mendengar firman Allah, lalu mereka mengubahnya setelah memahaminya, padahal mereka mengetahuinya?'),
	(83, 2, 76, 'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُوْٓا اَتُحَدِّثُوْنَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَاۤجُّوْكُمْ بِهٖ عِنْدَ رَبِّكُمْ ۗ اَفَلَا تَعْقِلُوْنَ', 'wa-i<u>dzaa</u> laquu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa-i<u>dzaa</u> khal<u>aa</u> ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in q<u>aa</u>luu atu<u>h</u>additsuunahum bim<u>aa</u> fata<u>h</u>a <strong>al</strong>l<u>aa</u>hu \'alaykum liyu<u>haa</u>jjuukum bihi \'inda rabbikum afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Dan apabila mereka berjumpa dengan orang-orang yang beriman, mereka berkata, “Kami telah beriman.” Tetapi apabila kembali kepada sesamanya, mereka bertanya, “Apakah akan kamu ceritakan kepada mereka apa yang telah diterangkan Allah kepadamu, sehingga mereka dapat menyanggah kamu di hadapan Tuhanmu? Tidakkah kamu mengerti?” '),
	(84, 2, 77, 'اَوَلَا يَعْلَمُوْنَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ', 'awa l<u>aa</u> ya\'lamuuna anna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> yusirruuna wam<u>aa</u> yu\'linuun<strong>a</strong>', 'Dan tidakkah mereka tahu bahwa Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka nyatakan?'),
	(85, 2, 78, ' وَمِنْهُمْ اُمِّيُّوْنَ لَا يَعْلَمُوْنَ الْكِتٰبَ اِلَّآ اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّوْنَ', 'waminhum ummiyyuuna l<u>aa</u> ya\'lamuuna <strong>a</strong>lkit<u>aa</u>ba ill<u>aa</u> am<u>aa</u>niyya wa-in hum ill<u>aa</u> ya<u>zh</u>unnuun<strong>a</strong>', 'Dan di antara mereka ada yang buta huruf, tidak memahami Kitab (Taurat), kecuali hanya berangan-angan dan mereka hanya menduga-duga.'),
	(86, 2, 79, 'فَوَيْلٌ لِّلَّذِيْنَ يَكْتُبُوْنَ الْكِتٰبَ بِاَيْدِيْهِمْ ثُمَّ يَقُوْلُوْنَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوْا بِهٖ ثَمَنًا قَلِيْلًا ۗفَوَيْلٌ لَّهُمْ مِّمَّا كَتَبَتْ اَيْدِيْهِمْ وَوَيْلٌ لَّهُمْ مِّمَّا يَكْسِبُوْنَ ', 'fawaylun lilla<u>dz</u>iina yaktubuuna <strong>a</strong>lkit<u>aa</u>ba bi-aydiihim tsumma yaquuluuna h<u>aadzaa</u> min \'indi <strong>al</strong>l<u>aa</u>hi liyasytaruu bihi tsamanan qaliilan fawaylun lahum mimm<u>aa</u> katabat aydiihim wawaylun lahum mimm<u>aa</u> yaksibuun<strong>a</strong>', 'Maka celakalah orang-orang yang menulis kitab dengan tangan mereka (sendiri), kemudian berkata, “Ini dari Allah,” (dengan maksud) untuk menjualnya dengan harga murah. Maka celakalah mereka, karena tulisan tangan mereka, dan celakalah mereka karena apa yang mereka perbuat.'),
	(87, 2, 80, 'وَقَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدَةً ۗ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْدًا فَلَنْ يُّخْلِفَ اللّٰهُ عَهْدَهٗٓ اَمْ تَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ', 'waq<u>aa</u>luu lan tamassan<u>aa</u> <strong>al</strong>nn<u>aa</u>ru ill<u>aa</u> ayy<u>aa</u>man ma\'duudatan qul attakha<u>dz</u>tum \'inda <strong>al</strong>l<u>aa</u>hi \'ahdan falan yukhlifa <strong>al</strong>l<u>aa</u>hu \'ahdahu am taquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan mereka berkata, “Neraka tidak akan menyentuh kami, kecuali beberapa hari saja.” Katakanlah, “Sudahkah kamu menerima janji dari Allah, sehingga Allah tidak akan mengingkari janji-Nya, ataukah kamu mengatakan tentang Allah, sesuatu yang tidak kamu ketahui?”'),
	(88, 2, 81, 'بَلٰى مَنْ كَسَبَ سَيِّئَةً وَّاَحَاطَتْ بِهٖ خَطِيْۤـَٔتُهٗ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ', 'bal<u>aa</u> man kasaba sayyi-atan wa-a<u>hath</u>at bihi kha<u>th</u>ii-atuhu faul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Bukan demikian! Barangsiapa berbuat keburukan, dan dosanya telah menenggelamkannya, maka mereka itu penghuni neraka. Mereka kekal di dalamnya.'),
	(89, 2, 82, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Dan orang-orang yang beriman dan mengerjakan kebajikan, mereka itu penghuni surga. Mereka kekal di dalamnya.'),
	(90, 2, 83, 'وَاِذْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ لَا تَعْبُدُوْنَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَانًا وَّذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَقُوْلُوْا لِلنَّاسِ حُسْنًا وَّاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۗ  ثُمَّ تَوَلَّيْتُمْ', 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qa banii isr<u>aa</u>-iila l<u>aa</u> ta\'buduuna ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha wabi<strong>a</strong>lw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan wa<u>dz</u>ii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiini waquuluu li<strong>l</strong>nn<u>aa</u>si <u>h</u>usnan wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu <strong>al</strong>zzak<u>aa</u>ta tsumma tawallaytum ill<u>aa</u> qaliilan minkum wa-antum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan (ingatlah) ketika Kami mengambil janji dari Bani Israil, “Janganlah kamu menyembah selain Allah, dan berbuat-baiklah kepada kedua orang tua, kerabat, anak-anak yatim, dan orang-orang miskin. Dan bertuturkatalah yang baik kepada manusia, laksanakanlah salat dan tunaikanlah zakat.” Tetapi kemudian kamu berpaling (mengingkari), kecuali sebagian kecil dari kamu, dan kamu (masih menjadi) pembangkang.'),
	(91, 2, 84, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ لَا تَسْفِكُوْنَ دِمَاۤءَكُمْ وَلَا تُخْرِجُوْنَ اَنْفُسَكُمْ مِّنْ دِيَارِكُمْ ۖ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُوْنَ ', 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qakum l<u>aa</u> tasfikuuna dim<u>aa</u>-akum wal<u>aa</u> tukhrijuuna anfusakum min diy<u>aa</u>rikum tsumma aqrartum wa-antum tasyhaduun<strong>a</strong>', 'Dan (ingatlah) ketika Kami mengambil janji kamu, “Janganlah kamu menumpahkan darahmu (membunuh orang), dan mengusir dirimu (saudara sebangsamu) dari kampung halamanmu.” Kemudian kamu berikrar dan bersaksi.'),
	(92, 2, 85, 'ثُمَّ اَنْتُمْ هٰٓؤُلَاۤءِ تَقْتُلُوْنَ اَنْفُسَكُمْ وَتُخْرِجُوْنَ فَرِيْقًا مِّنْكُمْ مِّنْ دِيَارِهِمْۖ تَظٰهَرُوْنَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۗ وَاِنْ يَّأْتُوْكُمْ اُسٰرٰى تُفٰدُوْهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْ ۗ اَفَتُؤْمِ', 'tsumma antum h<u>aa</u>ul<u>aa</u>-i taqtuluuna anfusakum watukhrijuuna fariiqan minkum min diy<u>aa</u>rihim ta<u>zhaa</u>haruuna \'alayhim bi<strong>a</strong>l-itsmi wa<strong>a</strong>l\'udw<u>aa</u>ni wa-in ya/tuukum us<u>aa</u>r<u>aa</u> tuf<u>aa</u>duuhum wahuwa mu<u>h</u>arramun \'alaykum ikhr<u>aa</u>juhum afatu/minuuna biba\'<u>dh</u>i <strong>a</strong>lkit<u>aa</u>bi watakfuruuna biba\'<u>dh</u>in fam<u>aa</u> jaz<u>aa</u>u man yaf\'alu <u>dzaa</u>lika minkum ill<u>aa</u> khizyun fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wayawma <strong>a</strong>lqiy<u>aa</u>mati yuradduuna il<u>aa</u> asyaddi <strong>a</strong>l\'a<u>dzaa</u>bi wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Kemudian kamu (Bani Israil) membunuh dirimu (sesamamu), dan mengusir segolongan dari kamu dari kampung halamannya. Kamu saling membantu (menghadapi) mereka dalam kejahatan dan permusuhan. Dan jika mereka datang kepadamu sebagai tawanan, kamu tebus mereka, padahal kamu dilarang mengusir mereka. Apakah kamu beriman kepada sebagian Kitab (Taurat) dan ingkar kepada sebagian (yang lain)? Maka tidak ada balasan (yang pantas) bagi orang yang berbuat demikian di antara kamu selain kenistaan dalam kehidupan dunia, dan pada hari Kiamat mereka dikembalikan kepada azab yang paling berat. Dan Allah tidak lengah terhadap apa yang kamu kerjakan.'),
	(93, 2, 86, ' اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُوْنَ ࣖ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina isytarawuu <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> bi<strong>a</strong>l-<u>aa</u>khirati fal<u>aa</u> yukhaffafu \'anhumu <strong>a</strong>l\'a<u>dzaa</u>bu wal<u>aa</u> hum yun<u>sh</u>aruun<strong>a</strong>', 'Mereka itulah orang-orang yang membeli kehidupan dunia dengan (kehidupan) akhirat. Maka tidak akan diringankan azabnya dan mereka tidak akan ditolong.'),
	(94, 2, 87, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَقَفَّيْنَا مِنْۢ بَعْدِهٖ بِالرُّسُلِ ۖ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ  اَفَكُلَّمَا جَاۤءَكُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰىٓ اَنْفُسُكُمُ اسْتَكْبَرْتُمْ ۚ فَفَرِيْق', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba waqaffayn<u>aa</u> min ba\'dihi bi<strong>al</strong>rrusuli wa<u>aa</u>tayn<u>aa</u> \'iis<u>aa</u> ibna maryama <strong>a</strong>lbayyin<u>aa</u>ti wa-ayyadn<u>aa</u>hu biruu<u>h</u>i <strong>a</strong>lqudusi afakullam<u>aa</u> j<u>aa</u>-akum rasuulun bim<u>aa</u> l<u>aa</u> tahw<u>aa</u> anfusukumu istakbartum fafariiqan ka<u>dzdz</u>abtum wafariiqan taqtuluun<strong>a</strong>', 'Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa, dan Kami susulkan setelahnya dengan rasul-rasul, dan Kami telah berikan kepada Isa putra Maryam bukti-bukti kebenaran serta Kami perkuat dia dengan Rohulkudus (Jibril). Mengapa setiap rasul yang datang kepadamu (membawa) sesuatu (pelajaran) yang tidak kamu inginkan, kamu menyombongkan diri, lalu sebagian kamu dustakan dan sebagian kamu bunuh?'),
	(95, 2, 88, 'وَقَالُوْا قُلُوْبُنَا غُلْفٌ ۗ بَلْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَلِيْلًا مَّا يُؤْمِنُوْنَ ', 'waq<u>aa</u>luu quluubun<u>aa</u> ghulfun bal la\'anahumu <strong>al</strong>l<u>aa</u>hu bikufrihim faqaliilan m<u>aa</u> yu/minuun<strong>a</strong>', 'Dan mereka berkata, “Hati kami tertutup.” Tidak! Allah telah melaknat mereka itu karena keingkaran mereka, tetapi sedikit sekali mereka yang beriman.'),
	(96, 2, 89, 'وَلَمَّا جَاۤءَهُمْ كِتٰبٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْۙ وَكَانُوْا مِنْ قَبْلُ يَسْتَفْتِحُوْنَ عَلَى الَّذِيْنَ كَفَرُوْاۚ فَلَمَّا جَاۤءَهُمْ مَّا عَرَفُوْا كَفَرُوْا بِهٖ ۖ فَلَعْنَةُ اللّٰهِ عَلَى الْكٰفِرِيْنَ ', 'walamm<u>aa</u> j<u>aa</u>-ahum kit<u>aa</u>bun min \'indi <strong>al</strong>l<u>aa</u>hi mu<u>sh</u>addiqun lim<u>aa</u> ma\'ahum wak<u>aa</u>nuu min qablu yastafti<u>h</u>uuna \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu falamm<u>aa</u> j<u>aa</u>-ahum m<u>aa</u> \'arafuu kafaruu bihi fala\'natu <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan setelah sampai kepada mereka Kitab (Al-Qur\'an) dari Allah yang membenarkan apa yang ada pada mereka sedangkan sebelumnya mereka memohon kemenangan atas orang-orang kafir, ternyata setelah sampai kepada mereka apa yang telah mereka ketahui itu, mereka mengingkarinya. Maka laknat Allah bagi orang-orang yang ingkar.'),
	(97, 2, 90, '  بِئْسَمَا اشْتَرَوْا بِهٖٓ اَنْفُسَهُمْ اَنْ يَّكْفُرُوْا بِمَآ اَنْزَلَ اللّٰهُ بَغْيًا اَنْ يُّنَزِّلَ اللّٰهُ مِنْ فَضْلِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۚ فَبَاۤءُوْ بِغَضَبٍ عَلٰى غَضَبٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌ', 'bi/sam<u>aa</u> isytaraw bihi anfusahum an yakfuruu bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu baghyan an yunazzila <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi \'al<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi fab<u>aa</u>uu bigha<u>dh</u>abin \'al<u>aa</u> gha<u>dh</u>abin walilk<u>aa</u>firiina \'a<u>dzaa</u>bun muhiin<strong>un</strong>', 'Sangatlah buruk (perbuatan) mereka menjual dirinya, dengan mengingkari apa yang diturunkan Allah, karena dengki bahwa Allah menurunkan karunia-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Karena itulah mereka menanggung kemurkaan demi kemurkaan. Dan kepada orang-orang kafir (ditimpakan) azab yang menghinakan.'),
	(98, 2, 91, 'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا بِمَآ اَنْزَلَ اللّٰهُ قَالُوْا نُؤْمِنُ بِمَآ اُنْزِلَ عَلَيْنَا وَيَكْفُرُوْنَ بِمَا وَرَاۤءَهٗ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُوْنَ اَنْۢبِيَاۤءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُّؤْ', 'wa-i<u>dzaa</u> qiila lahum <u>aa</u>minuu bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu q<u>aa</u>luu nu/minu bim<u>aa</u> unzila \'alayn<u>aa</u> wayakfuruuna bim<u>aa</u> war<u>aa</u>-ahu wahuwa <strong>a</strong>l<u>h</u>aqqu mu<u>sh</u>addiqan lim<u>aa</u> ma\'ahum qul falima taqtuluuna anbiy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hi min qablu in kuntum mu/miniin<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Berimanlah kepada apa yang diturunkan Allah (Al-Qur\'an),” mereka menjawab, “Kami beriman kepada apa yang diturunkan kepada kami.” Dan mereka ingkar kepada apa yang setelahnya, padahal (Al-Qur\'an) itu adalah yang hak yang membenarkan apa yang ada pada mereka. Katakanlah (Muhammad), “Mengapa kamu dahulu membunuh nabi-nabi Allah jika kamu orang-orang beriman?”'),
	(99, 2, 92, '۞ وَلَقَدْ جَاۤءَكُمْ مُّوْسٰى بِالْبَيِّنٰتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ', 'walaqad j<u>aa</u>-akum muus<u>aa</u> bi<strong>a</strong>lbayyin<u>aa</u>ti tsumma ittakha<u>dz</u>tumu <strong>a</strong>l\'ijla min ba\'dihi wa-antum <u>zhaa</u>limuun<strong>a</strong>', 'Dan sungguh, Musa telah datang kepadamu dengan bukti-bukti kebenaran, kemudian kamu mengambil (patung) anak sapi (sebagai sesembahan) setelah (kepergian)nya, dan kamu (menjadi) orang-orang zalim.'),
	(100, 2, 93, 'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاسْمَعُوْا ۗ قَالُوْا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوْا فِيْ قُلُوْبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۗ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهٖٓ اِيْمَانُكُمْ اِ', 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qakum warafa\'n<u>aa</u> fawqakumu <strong>al</strong><u>ththh</u>uura khu<u>dz</u>uu m<u>aa</u> <u>aa</u>tayn<u>aa</u>kum biquwwatin wa<strong>i</strong>sma\'uu q<u>aa</u>luu sami\'n<u>aa</u> wa\'a<u>sh</u>ayn<u>aa</u> wausyribuu fii quluubihimu <strong>a</strong>l\'ijla bikufrihim qul bi/sam<u>aa</u> ya/murukum bihi iim<u>aa</u>nukum in kuntum mu/miniin<strong>a</strong>', 'Dan (ingatlah) ketika Kami mengambil janji kamu dan Kami angkat gunung (Sinai) di atasmu (seraya berfirman), “Pegang teguhlah apa yang Kami berikan kepadamu dan dengarkanlah!” Mereka menjawab, “Kami mendengarkan tetapi kami tidak menaati.” Dan diresapkanlah ke dalam hati mereka itu (kecintaan menyembah patung) anak sapi karena kekafiran mereka. Katakanlah, “Sangat buruk apa yang diperintahkan oleh kepercayaanmu kepadamu jika kamu orang-orang beriman!”'),
	(101, 2, 94, ' قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِّنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'qul in k<u>aa</u>nat lakumu <strong>al</strong>dd<u>aa</u>ru <strong>a</strong>l-<u>aa</u>khiratu \'inda <strong>al</strong>l<u>aa</u>hi kh<u>aa</u>li<u>sh</u>atan min duuni <strong>al</strong>nn<u>aa</u>si fatamannawuu <strong>a</strong>lmawta in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Katakanlah (Muhammad), “Jika negeri akhirat di sisi Allah, khusus untukmu saja bukan untuk orang lain, maka mintalah kematian jika kamu orang yang benar.”'),
	(102, 2, 95, 'وَلَنْ يَّتَمَنَّوْهُ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢ بِالظّٰلِمِيْنَ ', 'walan yatamannawhu abadan bim<u>aa</u> qaddamat aydiihim wa<strong>al</strong>l<u>aa</u>hu \'aliimun bia<strong>l</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Tetapi mereka tidak akan menginginkan kematian itu sama sekali, karena dosa-dosa yang telah dilakukan tangan-tangan mereka. Dan Allah Maha Mengetahui orang-orang zalim.'),
	(103, 2, 96, 'وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍ ۛوَمِنَ الَّذِيْنَ اَشْرَكُوْا ۛيَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِهٖ مِنَ الْعَذَابِ اَنْ يُّعَمَّرَۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ ࣖ', 'walatajidannahum a<u>h</u>ra<u>sh</u>a <strong>al</strong>nn<u>aa</u>si \'al<u>aa</u> <u>h</u>ay<u>aa</u>tin wamina <strong>al</strong>la<u>dz</u>iina asyrakuu yawaddu a<u>h</u>aduhum law yu\'ammaru <strong>a</strong>lfa sanatin wam<u>aa</u> huwa bimuza<u>h</u>zi<u>h</u>ihi mina <strong>a</strong>l\'a<u>dzaa</u>bi an yu\'ammara wa<strong>al</strong>l<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ya\'maluun<strong>a</strong>', 'Dan sungguh, engkau (Muhammad) akan mendapati mereka (orang-orang Yahudi), manusia yang paling tamak akan kehidupan (dunia), bahkan (lebih tamak) dari orang-orang musyrik. Masing-masing dari mereka, ingin diberi umur seribu tahun, padahal umur panjang itu tidak akan menjauhkan mereka dari azab. Dan Allah Maha Melihat apa yang mereka kerjakan.'),
	(104, 2, 97, ' قُلْ مَنْ كَانَ عَدُوًّا لِّجِبْرِيْلَ فَاِنَّهٗ نَزَّلَهٗ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ', 'qul man k<u>aa</u>na \'aduwwan lijibriila fa-innahu nazzalahu \'al<u>aa</u> qalbika bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi wahudan wabusyr<u>aa</u> lilmu/miniin<strong>a</strong>', 'Katakanlah (Muhammad), “Barangsiapa menjadi musuh Jibril, maka (ketahuilah) bahwa dialah yang telah menurunkan (Al-Qur\'an) ke dalam hatimu dengan izin Allah, membenarkan apa (kitab-kitab) yang terdahulu, dan menjadi petunjuk serta berita gembira bagi orang-orang beriman.”'),
	(105, 2, 98, 'مَنْ كَانَ عَدُوًّا لِّلّٰهِ وَمَلٰۤىِٕكَتِهٖ وَرُسُلِهٖ وَجِبْرِيْلَ وَمِيْكٰىلَ فَاِنَّ اللّٰهَ عَدُوٌّ لِّلْكٰفِرِيْنَ', 'man k<u>aa</u>na \'aduwwan lill<u>aa</u>hi wamal<u>aa</u>-ikatihi warusulihi wajibriila wamiik<u>aa</u>la fa-inna <strong>al</strong>l<u>aa</u>ha \'aduwwun lilk<u>aa</u>firiin<strong>a</strong>', 'Barangsiapa menjadi musuh Allah, malaikat-malaikat-Nya, rasul-rasul-Nya, Jibril dan Mikail, maka sesungguhnya Allah musuh bagi orang-orang kafir.'),
	(106, 2, 99, 'وَلَقَدْ اَنْزَلْنَآ اِلَيْكَ اٰيٰتٍۢ بَيِّنٰتٍۚ وَمَا يَكْفُرُ بِهَآ اِلَّا الْفٰسِقُوْنَ', 'walaqad anzaln<u>aa</u> ilayka <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin wam<u>aa</u> yakfuru bih<u>aa</u> ill<u>aa</u> <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Dan sungguh, Kami telah menurunkan ayat-ayat yang jelas kepadamu (Muhammad), dan tidaklah ada yang mengingkarinya selain orang-orang fasik.'),
	(107, 2, 100, 'اَوَكُلَّمَا عٰهَدُوْا عَهْدًا نَّبَذَهٗ فَرِيْقٌ مِّنْهُمْ ۗ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُوْنَ', 'awa kullam<u>aa</u> \'<u>aa</u>haduu \'ahdan naba<u>dz</u>ahu fariiqun minhum bal aktsaruhum l<u>aa</u> yu/minuun<strong>a</strong>', 'Dan mengapa setiap kali mereka mengikat janji, sekelompok mereka melanggarnya? Sedangkan sebagian besar mereka tidak beriman.'),
	(108, 2, 101, ' وَلَمَّا جَاۤءَهُمْ رَسُوْلٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيْقٌ مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَۙ  كِتٰبَ اللّٰهِ وَرَاۤءَ ظُهُوْرِهِمْ كَاَنَّهُمْ لَا يَعْلَمُوْنَۖ', 'walammaa jaa-ahum rasuulun min \'indi allaahi mushaddiqun limaa ma\'ahum nabadza fariiqun mina alladziina uutuu alkitaaba kitaaba allaahi waraa-a zhuhuurihim ka-annahum laa ya\'lamuuna', 'Dan setelah datang kepada mereka seorang Rasul (Muhammad) dari Allah yang membenarkan apa yang ada pada mereka, sebagian dari orang-orang yang diberi Kitab (Taurat) melemparkan Kitab Allah itu ke belakang (punggung), seakan-akan mereka tidak tahu.'),
	(109, 2, 102, 'وَاتَّبَعُوْا مَا تَتْلُوا الشَّيٰطِيْنُ عَلٰى مُلْكِ سُلَيْمٰنَ ۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيٰطِيْنَ كَفَرُوْا يُعَلِّمُوْنَ النَّاسَ السِّحْرَ وَمَآ اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوْتَ وَمَارُوْتَ ۗ وَمَا يُعَلِّمٰنِ مِنْ اَحَ', 'waittaba\'uu maa tatluu alsysyayaathiinu \'alaa mulki sulaymaana wamaa kafara sulaymaanu walaakinna alsysyayaathiina kafaruu yu\'allimuuna alnnaasa alssihra wamaa unzila \'alaa almalakayni bibaabila haaruuta wamaaruuta wamaa yu\'allimaani min ahadin hattaa yaquulaa innamaa nahnu fitnatun falaa takfur fayata\'allamuuna minhumaa maa yufarriquuna bihi bayna almar-i wazawjihi wamaa hum bidaarriina bihi min ahadin illaa bi-idzni allaahi wayata\'allamuuna maa yadhurruhum walaa yanfa\'uhum walaqad \'alimuu lamani isytaraahu maa lahu fii al-aakhirati min khalaaqin walabi/sa maa syaraw bihi anfusahum law kaanu ya\'lamuuna', 'Dan mereka mengikuti apa yang dibaca oleh setan-setan pada masa kerajaan Sulaiman. Sulaiman itu tidak kafir tetapi setan-setan itulah yang kafir, mereka mengajarkan sihir kepada manusia dan apa yang diturunkan kepada dua malaikat di negeri Babilonia yaitu Harut dan Marut. Padahal keduanya tidak mengajarkan sesuatu kepada seseorang sebelum mengatakan, “Sesungguhnya kami hanyalah cobaan (bagimu), sebab itu janganlah kafir.” Maka mereka mempelajari dari keduanya (malaikat itu) apa yang (dapat) memisahkan antara seorang (suami) dengan istrinya. Mereka tidak akan dapat mencelakakan seseorang dengan sihirnya kecuali dengan izin Allah. Mereka mempelajari sesuatu yang mencelakakan, dan tidak memberi manfaat kepada mereka. Dan sungguh, mereka sudah tahu, barangsiapa membeli (menggunakan sihir) itu, niscaya tidak akan mendapat keuntungan di akhirat. Dan sungguh, sangatlah buruk perbuatan mereka yang menjual dirinya dengan sihir, sekiranya mereka tahu.'),
	(110, 2, 103, 'وَلَوْ اَنَّهُمْ اٰمَنُوْا وَاتَّقَوْا لَمَثُوْبَةٌ مِّنْ عِنْدِ اللّٰهِ خَيْرٌ ۗ  لَوْ كَانُوْا يَعْلَمُوْنَ ࣖ', 'walaw annahum aamanuu waittaqaw lamatsuubatun min \'indi allaahi khayrun law kaanuu ya\'lamuuna', 'Dan jika mereka beriman dan bertakwa, pahala dari Allah pasti lebih baik, sekiranya mereka tahu.'),
	(111, 2, 104, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقُوْلُوْا رَاعِنَا وَقُوْلُوا انْظُرْنَا وَاسْمَعُوْا  وَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ', 'yaa ayyuhaa alladziina aamanuu laa taquuluu raa\'inaa waquuluu unzhurnaa waisma\'uu walilkaafiriina \'adzaabun aliimun', 'Wahai orang-orang yang beriman! Janganlah kamu katakan, raa\'inaa, tetapi katakanlah, “Unzhurnaa” dan dengarkanlah. Dan orang-orang kafir akan mendapat azab yang pedih.'),
	(112, 2, 105, 'مَا يَوَدُّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَلَا الْمُشْرِكِيْنَ اَنْ يُّنَزَّلَ عَلَيْكُمْ مِّنْ خَيْرٍ مِّنْ رَّبِّكُمْ ۗ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ', 'maa yawaddu alladziina kafaruu min ahli alkitaabi walaa almusyrikiina an yunazzala \'alaykum min khayrin min rabbikum waallaahu yakhtashshu birahmatihi man yasyaau waallaahu dzuu alfadhli al\'azhiimi', 'Orang-orang yang kafir dari Ahli Kitab dan orang-orang musyrik tidak  menginginkan diturunkannya kepadamu suatu kebaikan dari Tuhanmu. Tetapi secara khusus Allah memberikan rahmat-Nya kepada orang yang Dia kehendaki. Dan Allah pemilik karunia yang besar.'),
	(113, 2, 106, '۞ مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِّنْهَآ اَوْ مِثْلِهَا ۗ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'maa nansakh min aayatin aw nunsihaa na/ti bikhayrin minhaa aw mitslihaa alam ta\'lam anna allaaha \'alaa kulli syay-in qadiirun', 'Ayat yang Kami batalkan atau Kami hilangkan dari ingatan, pasti Kami ganti dengan yang lebih baik atau yang sebanding dengannya. Tidakkah kamu tahu bahwa Allah Mahakuasa atas segala sesuatu?'),
	(114, 2, 107, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ', 'alam ta\'lam anna allaaha lahu mulku alssamaawaati waal-ardhi wamaa lakum min duuni allaahi min waliyyin walaa nashiirin', 'Tidakkah kamu tahu bahwa Allah memiliki kerajaan langit dan bumi? Dan tidak ada bagimu pelindung dan penolong selain Allah.'),
	(115, 2, 108, 'اَمْ تُرِيْدُوْنَ اَنْ تَسْـَٔلُوْا رَسُوْلَكُمْ كَمَا سُىِٕلَ مُوْسٰى مِنْ قَبْلُ ۗوَمَنْ يَّتَبَدَّلِ الْكُفْرَ بِالْاِيْمَانِ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ', 'am turiiduuna an tas-aluu rasuulakum kamaa su-ila muusaa min qablu waman yatabaddali alkufra bial-iimaani faqad dhalla sawaa-a alssabiili', 'Ataukah kamu hendak meminta kepada Rasulmu (Muhammad) seperti halnya Musa (pernah) diminta (Bani Israil) dahulu? Barangsiapa mengganti iman dengan kekafiran, maka sungguh, dia telah tersesat dari jalan yang lurus.'),
	(116, 2, 109, 'وَدَّ كَثِيْرٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يَرُدُّوْنَكُمْ مِّنْۢ بَعْدِ اِيْمَانِكُمْ كُفَّارًاۚ حَسَدًا مِّنْ عِنْدِ اَنْفُسِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۚ فَاعْفُوْا وَاصْفَحُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖ ۗ اِنَّ اللّٰهَ عَ', 'wadda katsiirun min ahli alkitaabi law yarudduunakum min ba\'di iimaanikum kuffaaran hasadan min \'indi anfusihim min ba\'di maa tabayyana lahumu alhaqqu fau\'fuu waishfahuu hattaa ya/tiya allaahu bi-amrihi inna allaaha \'alaa kulli syay-in qadiirun', 'Banyak di antara Ahli Kitab menginginkan sekiranya mereka dapat mengembalikan kamu setelah kamu beriman, menjadi kafir kembali, karena rasa dengki dalam diri mereka, setelah kebenaran jelas bagi mereka. Maka maafkanlah dan berlapangdadalah, sampai Allah memberikan perintah-Nya. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(117, 2, 110, ' وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ ۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ', 'wa-aqiimuu alshshalaata waaatuu alzzakaata wamaa tuqaddimuu li-anfusikum min khayrin tajiduuhu \'inda allaahi inna allaaha bimaa ta\'maluuna bashiirun', 'Dan laksanakanlah salat dan tunaikanlah zakat. Dan segala kebaikan yang kamu kerjakan untuk dirimu, kamu akan mendapatkannya (pahala) di sisi Allah. Sungguh, Allah Maha Melihat apa yang kamu kerjakan.'),
	(118, 2, 111, 'وَقَالُوْا لَنْ يَّدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوْدًا اَوْ نَصٰرٰى ۗ تِلْكَ اَمَانِيُّهُمْ ۗ قُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'waqaaluu lan yadkhula aljannata illaa man kaana huudan aw nashaaraa tilka amaaniyyuhum qul haatuu burhaanakum in kuntum shaadiqiina', 'Dan mereka (Yahudi dan Nasrani) berkata, “Tidak akan masuk surga kecuali orang Yahudi atau Nasrani.” Itu (hanya) angan-angan mereka. Katakanlah, “Tunjukkan bukti kebenaranmu jika kamu orang yang benar.”'),
	(119, 2, 112, 'بَلٰى مَنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهٗٓ اَجْرُهٗ عِنْدَ رَبِّهٖۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ࣖ', 'balaa man aslama wajhahu lillaahi wahuwa muhsinun falahu ajruhu \'inda rabbihi walaa khawfun \'alayhim walaa hum yahzanuuna', 'Tidak! Barangsiapa menyerahkan diri sepenuhnya kepada Allah, dan dia berbuat baik, dia mendapat pahala di sisi Tuhannya dan tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(120, 2, 113, 'وَقَالَتِ الْيَهُوْدُ لَيْسَتِ النَّصٰرٰى عَلٰى شَيْءٍۖ وَّقَالَتِ النَّصٰرٰى لَيْسَتِ الْيَهُوْدُ عَلٰى شَيْءٍۙ وَّهُمْ يَتْلُوْنَ الْكِتٰبَۗ  كَذٰلِكَ قَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ مِثْلَ قَوْلِهِمْ ۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ', 'waqaalati alyahuudu laysati alnnashaaraa \'alaa syay-in waqaalati alnnashaaraa laysati alyahuudu \'alaa syay-in wahum yatluuna alkitaaba kadzaalika qaala alladziina laa ya\'lamuuna mitsla qawlihim faallaahu yahkumu baynahum yawma alqiyaamati fiimaa kaanuu fiihi yakhtalifuuna', 'Dan orang Yahudi berkata, “Orang Nasrani itu tidak memiliki sesuatu (pegangan),” dan orang-orang Nasrani (juga) berkata, “Orang-orang Yahudi tidak memiliki sesuatu (pegangan),” padahal mereka membaca Kitab. Demikian pula orang-orang yang tidak berilmu, berkata seperti ucapan mereka itu. Maka Allah akan mengadili mereka pada hari Kiamat, tentang apa yang mereka perselisihkan.'),
	(121, 2, 114, 'وَمَنْ اَظْلَمُ مِمَّنْ مَّنَعَ مَسٰجِدَ اللّٰهِ اَنْ يُّذْكَرَ فِيْهَا اسْمُهٗ وَسَعٰى فِيْ خَرَابِهَاۗ اُولٰۤىِٕكَ مَا كَانَ لَهُمْ اَنْ يَّدْخُلُوْهَآ اِلَّا خَاۤىِٕفِيْنَ ەۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ وَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ', 'waman azhlamu mimman mana\'a masaajida allaahi an yudzkara fiihaa ismuhu wasa\'aa fii kharaabihaa ulaa-ika maa kaana lahum an yadkhuluuhaa illaa khaa-ifiina lahum fii alddunyaa khizyun walahum fii al-aakhirati \'adzaabun \'azhiimun', 'Dan siapakah yang lebih zalim daripada orang yang melarang di dalam masjid-masjid Allah untuk menyebut nama-Nya, dan berusaha merobohkannya? Mereka itu tidak pantas memasukinya kecuali dengan rasa takut (kepada Allah). Mereka mendapat kehinaan di dunia dan di akhirat mendapat azab yang berat.'),
	(122, 2, 115, 'وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ  فَاَيْنَمَا تُوَلُّوْا فَثَمَّ وَجْهُ اللّٰهِ ۗ اِنَّ اللّٰهَ وَاسِعٌ عَلِيْمٌ ', 'walillaahi almasyriqu waalmaghribu fa-aynamaa tuwalluu fatsamma wajhu allaahi inna allaaha waasi\'un \'aliimun', 'Dan milik Allah timur dan barat. Kemanapun kamu menghadap di sanalah wajah Allah. Sungguh, Allah Mahaluas, Maha Mengetahui.'),
	(123, 2, 116, 'وَقَالُوا اتَّخَذَ اللّٰهُ وَلَدًا ۙسُبْحٰنَهٗ ۗ بَلْ لَّهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  كُلٌّ لَّهٗ قَانِتُوْنَ ', 'waqaaluu itakhadza allaahu waladan subhaanahu bal lahu maa fii alssamaawaati waal-ardhi kullun lahu qaanituuna', 'Dan mereka berkata, “Allah mempunyai anak.” Mahasuci Allah, bahkan milik-Nyalah apa yang di langit dan di bumi. Semua tunduk kepada-Nya.'),
	(124, 2, 117, 'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ', 'badii\'u alssamaawaati waal-ardhi wa-idzaa qadaa amran fa-innamaa yaquulu lahu kun fayakuunu', '(Allah) pencipta langit dan bumi. Apabila Dia hendak menetapkan sesuatu, Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu.'),
	(125, 2, 118, 'وَقَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْتِيْنَآ اٰيَةٌ  ۗ كَذٰلِكَ قَالَ الَّذِيْنَ مِنْ قَبْلِهِمْ مِّثْلَ قَوْلِهِمْ ۗ تَشَابَهَتْ قُلُوْبُهُمْ ۗ قَدْ بَيَّنَّا الْاٰيٰتِ لِقَوْمٍ يُّوْقِنُوْنَ', 'waqaala alladziina laa ya\'lamuuna lawlaa yukallimunaa allaahu aw ta/tiinaa aayatun kadzaalika qaala alladziina min qablihim mitsla qawlihim tasyaabahat quluubuhum qad bayyannaa al-aayaati liqawmin yuuqinuuna', 'Dan orang-orang yang tidak mengetahui berkata, “Mengapa Allah tidak berbicara dengan kita atau datang tanda-tanda (kekuasaan-Nya) kepada kita?” Demikian pula orang-orang yang sebelum mereka telah berkata seperti ucapan mereka itu. Hati mereka serupa. Sesungguhnya telah Kami jelaskan tanda-tanda (kekuasaan Kami) kepada orang-orang yang yakin.'),
	(126, 2, 119, 'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًاۙ وَّلَا تُسْـَٔلُ عَنْ اَصْحٰبِ الْجَحِيْمِ', 'innaa arsalnaaka bialhaqqi basyiiran wanadziiran walaa tus-alu \'an ash-haabi aljahiimi', 'Sungguh, Kami telah mengutusmu (Muhammad) dengan kebenaran, sebagai pembawa berita gembira dan pemberi peringatan. Dan engkau tidak akan diminta (pertanggungjawaban) tentang penghuni-penghuni neraka.'),
	(127, 2, 120, 'وَلَنْ تَرْضٰى عَنْكَ الْيَهُوْدُ وَلَا النَّصٰرٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰى ۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَ الَّذِيْ جَاۤءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ', 'walan tardaa \'anka alyahuudu walaa alnnashaaraa hattaa tattabi\'a millatahum qul inna hudaa allaahi huwa alhudaa wala-ini ittaba\'ta ahwaa-ahum ba\'da alladzii jaa-aka mina al\'ilmi maa laka mina allaahi min waliyyin walaa nashiirin', 'Dan orang-orang Yahudi dan Nasrani tidak akan rela kepadamu (Muhammad) sebelum engkau mengikuti agama mereka. Katakanlah, “Sesungguhnya petunjuk Allah itulah petunjuk (yang sebenarnya).” Dan jika engkau mengikuti keinginan mereka setelah ilmu (kebenaran) sampai kepadamu, tidak akan ada bagimu pelindung dan penolong dari Allah.'),
	(128, 2, 121, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَتْلُوْنَهٗ حَقَّ تِلَاوَتِهٖۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗ وَمَنْ يَّكْفُرْ بِهٖ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ࣖ', 'alladziina aataynaahumu alkitaaba yatluunahu haqqa tilaawatihi ulaa-ika yu/minuuna bihi waman yakfur bihi faulaa-ika humu alkhaasiruuna', 'Orang-orang yang telah Kami beri Kitab, mereka membacanya sebagaimana mestinya, mereka itulah yang beriman kepadanya. Dan barangsiapa ingkar kepadanya, mereka itulah orang-orang yang rugi.'),
	(129, 2, 122, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ ', 'yaa banii israa-iila udzkuruu ni\'matiya allatii an\'amtu \'alaykum wa-annii fadhdhaltukum \'alaa al\'aalamiina', 'Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu dan Aku telah melebihkan kamu dari semua umat yang lain di alam ini (pada masa itu).'),
	(130, 2, 123, 'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا عَدْلٌ وَّلَا تَنْفَعُهَا شَفَاعَةٌ وَّلَا هُمْ يُنْصَرُوْنَ ', 'waittaquu yawman laa tajzii nafsun \'an nafsin syay-an walaa yuqbalu minhaa \'adlun walaa tanfa\'uhaa syafaa\'atun walaa hum yunsharuuna', 'Dan takutlah kamu pada hari, (ketika) tidak seorang pun dapat menggantikan (membela) orang lain sedikit pun, tebusan tidak diterima, bantuan tidak berguna baginya, dan mereka tidak akan ditolong.'),
	(131, 2, 124, '۞ وَاِذِ ابْتَلٰٓى اِبْرٰهٖمَ رَبُّهٗ بِكَلِمٰتٍ فَاَتَمَّهُنَّ ۗ قَالَ اِنِّيْ جَاعِلُكَ لِلنَّاسِ اِمَامًا ۗ قَالَ وَمِنْ ذُرِّيَّتِيْ ۗ قَالَ لَا يَنَالُ عَهْدِى الظّٰلِمِيْنَ', 'wa-idzi ibtalaa ibraahiima rabbuhu bikalimaatin fa-atammahunna qaala innii jaa\'iluka lilnnaasi imaaman qaala wamin dzurriyyatii qaala laa yanaalu \'ahdii alzhzhaalimiina', 'Dan (ingatlah), ketika Ibrahim diuji Tuhannya dengan beberapa kalimat, lalu dia melaksanakannya dengan sempurna. Dia (Allah) berfirman, “Sesungguhnya Aku menjadikan engkau sebagai pemimpin bagi seluruh manusia.” Dia (Ibrahim) berkata, “Dan (juga) dari anak cucuku?” Allah berfirman, “(Benar, tetapi) janji-Ku tidak berlaku bagi orang-orang zalim.”   '),
	(132, 2, 125, 'وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِّلنَّاسِ وَاَمْنًاۗ وَاتَّخِذُوْا مِنْ مَّقَامِ اِبْرٰهٖمَ مُصَلًّىۗ  وَعَهِدْنَآ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْعٰكِفِيْنَ وَالرُّكَّعِ السُّجُوْدِ ', 'wa-idz ja\'alnaa albayta matsaabatan lilnnaasi wa-amnan waittakhidzuu min maqaami ibraahiima mushallan wa\'ahidnaa ilaa ibraahiima wa-ismaa\'iila an thahhiraa baytiya lilththaa-ifiina waal\'aakifiina waalrrukka\'i alssujuudi', 'Dan (ingatlah), ketika Kami menjadikan rumah (Ka’bah) tempat berkumpul dan tempat yang aman bagi manusia. Dan jadikanlah maqam Ibrahim  itu tempat salat. Dan telah Kami perintahkan kepada Ibrahim dan Ismail, “Bersihkanlah rumah-Ku untuk orang-orang yang tawaf, orang yang iktikaf, orang yang rukuk dan orang yang sujud!”'),
	(133, 2, 126, 'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اجْعَلْ هٰذَا بَلَدًا اٰمِنًا وَّارْزُقْ اَهْلَهٗ مِنَ الثَّمَرٰتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهٗ قَلِيْلًا ثُمَّ اَضْطَرُّهٗٓ اِلٰى عَذَابِ النَّارِ ۗ وَبِئْسَ الْمَصِ', 'wa-idz qaala ibraahiimu rabbi ij\'al haadzaa baladan aaminan waurzuq ahlahu mina altstsamaraati man aamana minhum biallaahi waalyawmi al-aakhiri qaala waman kafara faumatti\'uhu qaliilan tsumma adtharruhu ilaa \'adzaabi alnnaari wabi/sa almashiiru', 'Dan (ingatlah) ketika Ibrahim berdoa, “Ya Tuhanku, jadikanlah (negeri Mekah) ini negeri yang aman dan berilah rezeki berupa buah-buahan kepada penduduknya, yaitu di antara mereka yang beriman kepada Allah dan hari kemudian,” Dia (Allah) berfirman, “Dan kepada orang yang kafir akan Aku beri kesenangan sementara, kemudian akan Aku paksa dia ke dalam azab neraka dan itulah seburuk-buruk tempat kembali.”'),
	(134, 2, 127, 'وَاِذْ يَرْفَعُ اِبْرٰهٖمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰعِيْلُۗ رَبَّنَا تَقَبَّلْ مِنَّا ۗ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ ', 'wa-idz yarfa\'u ibraahiimu alqawaa\'ida mina albayti wa-ismaa\'iilu rabbanaa taqabbal minnaa innaka anta alssamii\'u al\'aliimu', 'Dan (ingatlah) ketika Ibrahim meninggikan pondasi Baitullah bersama Ismail, (seraya berdoa), “Ya Tuhan kami, terimalah (amal) dari kami. Sungguh, Engkaulah Yang Maha Mendengar, Maha Mengetahui.'),
	(135, 2, 128, 'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَآ اُمَّةً مُّسْلِمَةً لَّكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّحِيْمُ ', 'rabbanaa waij\'alnaa muslimayni laka wamin dzurriyyatinaa ummatan muslimatan laka wa-arinaa manaasikanaa watub \'alaynaa innaka anta alttawwaabu alrrahiimu', 'Ya Tuhan kami, jadikanlah kami orang yang berserah diri kepada-Mu, dan anak cucu kami (juga) umat yang berserah diri kepada-Mu dan tunjukkanlah kepada kami cara-cara melakukan ibadah (haji) kami, dan terimalah tobat kami. Sungguh, Engkaulah Yang Maha Penerima tobat, Maha Penyayang.'),
	(136, 2, 129, 'رَبَّنَا وَابْعَثْ فِيْهِمْ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِكَ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُزَكِّيْهِمْ ۗ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ࣖ', 'rabbanaa waib\'ats fiihim rasuulan minhum yatluu \'alayhim aayaatika wayu\'allimuhumu alkitaaba waalhikmata wayuzakkiihim innaka anta al\'aziizu alhakiimu', 'Ya Tuhan kami, utuslah di tengah mereka seorang rasul dari kalangan mereka sendiri, yang akan membacakan kepada mereka ayat-ayat-Mu dan mengajarkan Kitab dan Hikmah kepada mereka, dan menyucikan mereka. Sungguh, Engkaulah Yang Mahaperkasa, Mahabijaksana.”'),
	(137, 2, 130, 'وَمَنْ يَّرْغَبُ عَنْ مِّلَّةِ اِبْرٰهٖمَ اِلَّا مَنْ سَفِهَ نَفْسَهٗ ۗوَلَقَدِ اصْطَفَيْنٰهُ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ', 'waman yarghabu \'an millati ibraahiima illaa man safiha nafsahu walaqadi isthafaynaahu fii alddunyaa wa-innahu fii al-aakhirati lamina alshshaalihiina', 'Dan orang yang membenci agama Ibrahim, hanyalah orang yang memperbodoh dirinya sendiri. Dan sungguh, Kami telah memilihnya (Ibrahim) di dunia ini. Dan sesungguhnya di akhirat dia termasuk orang-orang saleh.'),
	(138, 2, 131, 'اِذْ قَالَ لَهٗ رَبُّهٗٓ اَسْلِمْۙ  قَالَ اَسْلَمْتُ لِرَبِّ الْعٰلَمِيْنَ', 'idz qaala lahu rabbuhu aslim qaala aslamtu lirabbi al\'aalamiina', '(Ingatlah) ketika Tuhan berfirman kepadanya (Ibrahim), “Berserahdirilah!” Dia menjawab, “Aku berserah diri kepada Tuhan seluruh alam.”'),
	(139, 2, 132, 'وَوَصّٰى بِهَآ اِبْرٰهٖمُ بَنِيْهِ وَيَعْقُوْبُۗ يٰبَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدِّيْنَ فَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ۗ', 'wawashshaa bihaa ibraahiimu baniihi waya\'quubu yaa baniyya inna allaaha isthafaa lakumu alddiina falaa tamuutunna illaa wa-antum muslimuuna', 'Dan Ibrahim mewasiatkan (ucapan) itu kepada anak-anaknya, demikian pula Yakub. “Wahai anak-anakku! Sesungguhnya Allah telah memilih agama ini untukmu, maka janganlah kamu mati kecuali dalam keadaan Muslim.”'),
	(140, 2, 133, 'اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ حَضَرَ يَعْقُوْبَ الْمَوْتُۙ اِذْ قَالَ لِبَنِيْهِ مَا تَعْبُدُوْنَ مِنْۢ بَعْدِيْۗ قَالُوْا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَاۤىِٕكَ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ اِلٰهًا وَّاحِدًاۚ وَنَحْنُ لَهٗ مُسْلِمُوْنَ ', 'am kuntum syuhadaa-a idz hadhara ya\'quuba almawtu idz qaala libaniihi maa ta\'buduuna min ba\'dii qaaluu na\'budu ilaahaka wa-ilaaha aabaa-ika ibraahiima wa-ismaa\'iila wa-ishaaqa ilaahan waahidan wanahnu lahu muslimuuna', 'Apakah kamu menjadi saksi saat maut akan menjemput Yakub, ketika dia berkata kepada anak-anaknya, “Apa yang kamu sembah sepeninggalku?” Mereka menjawab, “Kami akan menyembah Tuhanmu dan Tuhan nenek moyangmu yaitu Ibrahim, Ismail dan Ishak, (yaitu) Tuhan Yang Maha Esa dan kami (hanya) berserah diri kepada-Nya.”'),
	(141, 2, 134, 'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ', 'tilka ummatun qad khalat lahaa maa kasabat walakum maa kasabtum walaa tus-aluuna \'ammaa kaanu ya\'maluuna', 'Itulah umat yang telah lalu. Baginya apa yang telah mereka usahakan dan bagimu apa yang telah kamu usahakan. Dan kamu tidak akan diminta (pertanggungjawaban) tentang apa yang dahulu mereka kerjakan.'),
	(142, 2, 135, 'وَقَالُوْا كُوْنُوْا هُوْدًا اَوْ نَصٰرٰى تَهْتَدُوْا ۗ قُلْ بَلْ مِلَّةَ اِبْرٰهٖمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ', 'waqaaluu kuunuu huudan aw nashaaraa tahtaduu qul bal millata ibraahiima haniifan wamaa kaana mina almusyrikiina', 'Dan mereka berkata, “Jadilah kamu (penganut) Yahudi atau Nasrani, niscaya kamu mendapat petunjuk.” Katakanlah, “(Tidak!) Tetapi (kami mengikuti) agama Ibrahim yang lurus dan dia tidak termasuk golongan orang yang mempersekutukan Tuhan.”'),
	(143, 2, 136, 'قُوْلُوْٓا اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَمَآ اُوْتِيَ النَّبِيُّوْنَ مِنْ رَّبِّهِمْۚ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ ', 'quuluu aamannaa biallaahi wamaa unzila ilaynaa wamaa unzila ilaa ibraahiima wa-ismaa\'iila wa-ishaaqa waya\'quuba waal-asbaathi wamaa uutiya muusaa wa\'iisaa wamaa uutiya alnnabiyyuuna min rabbihim laa nufarriqu bayna ahadin minhum wanahnu lahu muslimuuna', 'Katakanlah, “Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami, dan kepada apa yang diturunkan kepada Ibrahim, Ismail, Ishak, Yakub dan anak cucunya, dan kepada apa yang diberikan kepada Musa dan Isa serta kepada apa yang diberikan kepada nabi-nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorang pun di antara mereka, dan kami berserah diri kepada-Nya.”'),
	(144, 2, 137, 'فَاِنْ اٰمَنُوْا بِمِثْلِ مَآ اٰمَنْتُمْ بِهٖ فَقَدِ اهْتَدَوْا ۚوَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ فِيْ شِقَاقٍۚ فَسَيَكْفِيْكَهُمُ اللّٰهُ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ ۗ ', 'fa-in aamanuu bimitsli maa aamantum bihi faqadi ihtadaw wa-in tawallaw fa-innamaa hum fii syiqaaqin fasayakfiikahumu allaahu wahuwa alssamii\'u al\'aliimu', 'Maka jika mereka telah beriman sebagaimana yang kamu imani, sungguh, mereka telah mendapat petunjuk. Tetapi jika mereka berpaling, sesungguhnya mereka berada dalam permusuhan (denganmu), maka Allah mencukupkan engkau (Muhammad) terhadap mereka (dengan pertolongan-Nya). Dan Dia Maha Mendengar, Maha Mengetahui.'),
	(145, 2, 138, 'صِبْغَةَ اللّٰهِ ۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةً ۖ وَّنَحْنُ لَهٗ عٰبِدُوْنَ ', 'shibghata allaahi waman ahsanu mina allaahi shibghatan wanahnu lahu \'aabiduuna', 'Sibgah Allah.” Siapa yang lebih baik sibgah-nya daripada Allah? Dan kepada-Nya kami menyembah.'),
	(146, 2, 139, 'قُلْ اَتُحَاۤجُّوْنَنَا فِى اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهٗ مُخْلِصُوْنَ ۙ', 'qul atuhaajjuunanaa fii allaahi wahuwa rabbunaa warabbukum walanaa a\'maalunaa walakum a\'maalukum wanahnu lahu mukhlishuuna', 'Katakanlah (Muhammad), “Apakah kamu hendak berdebat dengan kami tentang Allah, padahal Dia adalah Tuhan kami dan Tuhan kamu. Bagi kami amalan kami, bagi kamu amalan kamu, dan hanya kepada-Nya kami dengan tulus mengabdikan diri.'),
	(147, 2, 140, 'اَمْ تَقُوْلُوْنَ اِنَّ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطَ كَانُوْا هُوْدًا اَوْ نَصٰرٰى ۗ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُ ۗ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهٗ مِنَ اللّٰهِ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّ', 'am taquuluuna inna ibraahiima wa-ismaa\'iila wa-ishaaqa waya\'quuba waal-asbaatha kaanuu huudan aw nashaaraa qul a-antum a\'lamu ami allaahu waman azhlamu mimman katama syahaadatan \'indahu mina allaahi wamaa allaahu bighaafilin \'ammaa ta\'maluuna', 'Ataukah kamu (orang-orang Yahudi dan Nasrani) berkata bahwa Ibrahim, Ismail, Ishak, Yakub dan anak cucunya adalah penganut Yahudi atau Nasrani? Katakanlah, “Kamukah yang lebih tahu atau Allah, dan siapakah yang lebih zalim daripada orang yang menyembunyikan kesaksian dari Allah yang ada padanya?” Allah tidak lengah terhadap apa yang kamu kerjakan.'),
	(148, 2, 141, 'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ ࣖ   ۔', 'tilka ummatun qad khalat lah<u>aa</u> m<u>aa</u> kasabat walakum m<u>aa</u> kasabtum wal<u>aa</u> tus-aluuna \'amm<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Itulah umat yang telah lalu. Baginya apa yang telah mereka usahakan dan bagimu apa yang telah kamu usahakan. Dan kamu tidak akan diminta (pertanggungjawaban) tentang apa yang dahulu mereka kerjakan.'),
	(149, 2, 142, '۞ سَيَقُوْلُ السُّفَهَاۤءُ مِنَ النَّاسِ مَا وَلّٰىهُمْ عَنْ قِبْلَتِهِمُ الَّتِيْ كَانُوْا عَلَيْهَا ۗ قُلْ لِّلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۗ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'sayaquulu <strong>al</strong>ssufah<u>aa</u>u mina <strong>al</strong>nn<u>aa</u>si m<u>aa</u> wall<u>aa</u>hum \'an qiblatihimu <strong>al</strong>latii k<u>aa</u>nuu \'alayh<u>aa</u> qul lill<u>aa</u>hi <strong>a</strong>lmasyriqu wa<strong>a</strong>lmaghribu yahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Orang-orang yang kurang akal di antara manusia akan berkata, “Apakah yang memalingkan mereka (Muslim) dari kiblat yang dahulu mereka (berkiblat) kepadanya?” Katakanlah (Muhammad), “Milik Allah-lah timur dan barat; Dia memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.”'),
	(150, 2, 143, 'وَكَذٰلِكَ جَعَلْنٰكُمْ اُمَّةً وَّسَطًا لِّتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِ وَيَكُوْنَ الرَّسُوْلُ عَلَيْكُمْ شَهِيْدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِيْ كُنْتَ عَلَيْهَآ اِلَّا لِنَعْلَمَ مَنْ يَّتَّبِعُ الرَّسُوْلَ مِمَّنْ يَّنْقَلِبُ عَلٰى ', 'waka<u>dzaa</u>lika ja\'aln<u>aa</u>kum ummatan wasa<u>th</u>an litakuunuu syuhad<u>aa</u>-a \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si wayakuuna <strong>al</strong>rrasuulu \'alaykum syahiidan wam<u>aa</u> ja\'aln<u>aa</u> <strong>a</strong>lqiblata <strong>al</strong>latii kunta \'alayh<u>aa</u> ill<u>aa</u> lina\'lama man yattabi\'u <strong>al</strong>rrasuula mimman yanqalibu \'al<u>aa</u> \'aqibayhi wa-in k<u>aa</u>nat lakabiiratan ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina had<u>aa</u> <strong>al</strong>l<u>aa</u>hu wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liyu<u>dh</u>ii\'a iim<u>aa</u>nakum inna <strong>al</strong>l<u>aa</u>ha bi<strong>al</strong>nn<u>aa</u>si larauufun ra<u>h</u>iim<strong>un</strong>', 'Dan demikian pula Kami telah menjadikan kamu (umat Islam) ”umat pertengahan” agar kamu menjadi saksi atas (perbuatan) manusia dan agar Rasul (Muhammad) menjadi saksi atas (perbuatan) kamu. Kami tidak menjadikan kiblat yang (dahulu) kamu (berkiblat) kepadanya melainkan agar Kami mengetahui siapa yang mengikuti Rasul dan siapa yang berbalik ke belakang. Sungguh, (pemindahan kiblat) itu sangat berat, kecuali bagi orang yang telah diberi petunjuk oleh Allah. Dan Allah tidak akan menyia-nyiakan imanmu. Sungguh, Allah Maha Pengasih, Maha Penyayang kepada manusia.'),
	(151, 2, 144, 'قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِى السَّمَاۤءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰىهَا ۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۗ وَاِنَّ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَيَعْلَمُوْنَ اَنَّهُ ', 'qad nar<u>aa</u> taqalluba wajhika fii <strong>al</strong>ssam<u>aa</u>-i falanuwalliyannaka qiblatan tar<u>daa</u>h<u>aa</u> fawalli wajhaka sya<u>th</u>ra <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa<u>h</u>aytsu m<u>aa</u> kuntum fawalluu wujuuhakum sya<u>th</u>rahu wa-inna <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba laya\'lamuuna annahu <strong>a</strong>l<u>h</u>aqqu min rabbihim wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ya\'maluun<strong>a</strong>', 'Kami melihat wajahmu (Muhammad) sering menengadah ke langit, maka akan Kami palingkan engkau ke kiblat yang engkau senangi. Maka hadapkanlah wajahmu ke arah Masjidilharam. Dan di mana saja engkau berada, hadapkanlah wajahmu ke arah itu. Dan sesungguhnya orang-orang yang diberi Kitab (Taurat dan Injil) tahu, bahwa (pemindahan kiblat) itu adalah kebenaran dari Tuhan mereka. Dan Allah tidak lengah terhadap apa yang mereka kerjakan.'),
	(152, 2, 145, 'وَلَىِٕنْ اَتَيْتَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ بِكُلِّ اٰيَةٍ مَّا تَبِعُوْا قِبْلَتَكَ ۚ وَمَآ اَنْتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ مِّنْۢ بَعْدِ مَاجَاۤءَكَ مِنَ الْعِلْمِ ۙ ا', 'wala-in atayta <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba bikulli <u>aa</u>yatin m<u>aa</u> tabi\'uu qiblataka wam<u>aa</u> anta bit<u>aa</u>bi\'in qiblatahum wam<u>aa</u> ba\'<u>dh</u>uhum bit<u>aa</u>bi\'in qiblata ba\'<u>dh</u>in wala-ini ittaba\'ta ahw<u>aa</u>-ahum min ba\'di m<u>aa</u> j<u>aa</u>-aka mina <strong>a</strong>l\'ilmi innaka i<u>dz</u>an lamina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan walaupun engkau (Muhammad) memberikan semua ayat (keterangan) kepada orang-orang yang diberi Kitab itu, mereka tidak akan mengikuti kiblatmu, dan engkau pun tidak akan  mengikuti kiblat mereka. Sebagian mereka tidak akan mengikuti kiblat sebagian yang lain. Dan jika engkau mengikuti keinginan mereka setelah sampai ilmu kepadamu, niscaya eng-kau termasuk orang-orang zalim.'),
	(153, 2, 146, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْ ۗ وَاِنَّ فَرِيْقًا مِّنْهُمْ لَيَكْتُمُوْنَ الْحَقَّ وَهُمْ يَعْلَمُوْنَ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba ya\'rifuunahu kam<u>aa</u> ya\'rifuuna abn<u>aa</u>-ahum wa-inna fariiqan minhum layaktumuuna <strong>a</strong>l<u>h</u>aqqa wahum ya\'lamuun<strong>a</strong>', 'Orang-orang yang telah Kami beri Kitab (Taurat dan Injil) mengenalnya (Muhammad) seperti mereka mengenal anak-anak mereka sendiri. Sesungguhnya sebagian mereka pasti menyembunyikan kebenaran, padahal mereka mengetahui(nya).'),
	(154, 2, 147, 'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ ࣖ', 'al<u>h</u>aqqu min rabbika fal<u>aa</u> takuunanna mina <strong>a</strong>lmumtariin<strong>a</strong>', 'Kebenaran itu dari Tuhanmu, maka janganlah sekali-kali engkau (Muhammad) termasuk orang-orang yang ragu.'),
	(155, 2, 148, 'وَلِكُلٍّ وِّجْهَةٌ هُوَ مُوَلِّيْهَا فَاسْتَبِقُوا الْخَيْرٰتِۗ اَيْنَ مَا تَكُوْنُوْا يَأْتِ بِكُمُ اللّٰهُ جَمِيْعًا ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'walikullin wijhatun huwa muwalliih<u>aa</u> fa<strong>i</strong>stabiquu <strong>a</strong>lkhayr<u>aa</u>ti aynam<u>aa</u> takuunuu ya/ti bikumu <strong>al</strong>l<u>aa</u>hu jamii\'an inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan setiap umat mempunyai kiblat yang dia menghadap kepadanya. Maka berlomba-lombalah kamu dalam kebaikan. Di mana saja kamu berada, pasti Allah akan mengumpulkan kamu semuanya. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(156, 2, 149, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَاِنَّهٗ لَلْحَقُّ مِنْ رَّبِّكَ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ', 'wamin <u>h</u>aytsu kharajta fawalli wajhaka sya<u>th</u>ra <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa-innahu lal<u>h</u>aqqu min rabbika wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan dari manapun engkau (Muhammad) keluar, hadapkanlah wajahmu ke arah Masjidilharam, sesungguhnya itu benar-benar ketentuan dari Tuhanmu. Allah tidak lengah terhadap apa yang kamu kerjakan.'),
	(157, 2, 150, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۙ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِيْ وَلِاُتِم', 'wamin <u>h</u>aytsu kharajta fawalli wajhaka sya<u>th</u>ra <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa<u>h</u>aytsu m<u>aa</u> kuntum fawalluu wujuuhakum sya<u>th</u>rahu li-all<u>aa</u> yakuuna li<strong>l</strong>nn<u>aa</u>si \'alaykum <u>h</u>ujjatun ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu minhum fal<u>aa</u> takhsyawhum wa<strong>i</strong>khsyawnii wali-utimma ni\'matii \'alaykum wala\'allakum tahtaduun<strong>a</strong>', 'Dan dari manapun engkau (Muhammad) keluar, maka hadapkanlah wajahmu ke arah Masjidilharam. Dan di mana saja kamu berada, maka hadapkanlah wajahmu ke arah itu, agar tidak ada alasan bagi manusia (untuk menentangmu), kecuali orang-orang yang zalim di antara mereka. Janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, agar Aku sempurnakan nikmat-Ku kepadamu, dan agar kamu mendapat petunjuk.'),
	(158, 2, 151, 'كَمَآ اَرْسَلْنَا فِيْكُمْ رَسُوْلًا مِّنْكُمْ يَتْلُوْا عَلَيْكُمْ اٰيٰتِنَا وَيُزَكِّيْكُمْ وَيُعَلِّمُكُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَۗ', 'kam<u>aa</u> arsaln<u>aa</u> fiikum rasuulan minkum yatluu \'alaykum <u>aa</u>y<u>aa</u>tin<u>aa</u> wayuzakkiikum wayu\'allimukumu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wayu\'allimukum m<u>aa</u> lam takuunuu ta\'lamuun<strong>a</strong>', 'Sebagaimana Kami telah mengutus kepadamu seorang Rasul (Muhammad) dari (kalangan) kamu yang membacakan ayat-ayat Kami, menyucikan kamu, dan mengajarkan kepadamu Kitab (Al-Qur\'an) dan Hikmah (Sunnah), serta mengajarkan apa yang belum kamu ketahui.'),
	(159, 2, 152, 'فَاذْكُرُوْنِيْٓ اَذْكُرْكُمْ وَاشْكُرُوْا لِيْ وَلَا تَكْفُرُوْنِ ࣖ ', 'fa<strong>u</strong><u>dz</u>kuruunii a<u>dz</u>kurkum wa<strong>u</strong>sykuruu lii wal<u>aa</u> takfuruun<strong>i</strong>', 'Maka ingatlah kepada-Ku, Aku pun akan ingat kepadamu. Bersyukurlah kepada-Ku, dan janganlah kamu ingkar kepada-Ku.'),
	(160, 2, 153, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ista\'iinuu bi<strong>al</strong><u>shsh</u>abri wa<strong>al</strong><u>shsh</u>al<u>aa</u>ti inna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Wahai orang-orang yang beriman! Mohonlah pertolongan (kepada Allah) dengan sabar dan salat. Sungguh, Allah beserta orang-orang yang sabar.'),
	(161, 2, 154, 'وَلَا تَقُوْلُوْا لِمَنْ يُّقْتَلُ فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتٌ ۗ بَلْ اَحْيَاۤءٌ وَّلٰكِنْ لَّا تَشْعُرُوْنَ', 'wal<u>aa</u> taquuluu liman yuqtalu fii sabiili <strong>al</strong>l<u>aa</u>hi amw<u>aa</u>tun bal a<u>h</u>y<u>aa</u>un wal<u>aa</u>kin l<u>aa</u> tasy\'uruun<strong>a</strong>', 'Dan janganlah kamu mengatakan orang-orang yang terbunuh di jalan Allah (mereka) telah mati. Sebenarnya (mereka) hidup, tetapi kamu tidak menyadarinya.'),
	(162, 2, 155, 'وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِّنَ الْخَوْفِ وَالْجُوْعِ وَنَقْصٍ مِّنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرٰتِۗ وَبَشِّرِ الصّٰبِرِيْنَ', 'walanabluwannakum bisyay-in mina <strong>a</strong>lkhawfi wa<strong>a</strong>ljuu\'i wanaq<u>sh</u>in mina <strong>a</strong>l-amw<u>aa</u>li wa<strong>a</strong>l-anfusi wa<strong>al</strong>tstsamar<u>aa</u>ti wabasysyiri <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Dan Kami pasti akan menguji kamu dengan sedikit ketakutan, kelaparan, kekurangan harta, jiwa, dan buah-buahan. Dan sampaikanlah kabar gembira kepada orang-orang yang sabar,'),
	(163, 2, 156, 'اَلَّذِيْنَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۗ قَالُوْٓا اِنَّا لِلّٰهِ وَاِنَّآ اِلَيْهِ رٰجِعُوْنَۗ', '<strong>al</strong>la<u>dz</u>iina i<u>dzaa</u> a<u>shaa</u>bat-hum mu<u>sh</u>iibatun q<u>aa</u>luu inn<u>aa</u> lill<u>aa</u>hi wa-inn<u>aa</u> ilayhi r<u>aa</u>ji\'uun<strong>a</strong>', '(yaitu) orang-orang yang apabila ditimpa musibah, mereka berkata “Inna lillahi wa inna ilaihi raji‘un” (sesungguhnya kami milik Allah dan kepada-Nyalah kami kembali).'),
	(164, 2, 157, 'اُولٰۤىِٕكَ عَلَيْهِمْ صَلَوٰتٌ مِّنْ رَّبِّهِمْ وَرَحْمَةٌ  ۗوَاُولٰۤىِٕكَ هُمُ الْمُهْتَدُوْنَ', 'ul<u>aa</u>-ika \'alayhim <u>sh</u>alaw<u>aa</u>tun min rabbihim wara<u>h</u>matun waul<u>aa</u>-ika humu <strong>a</strong>lmuhtaduun<strong>a</strong>', 'Mereka itulah yang memperoleh ampunan dan rahmat dari Tuhannya, dan mereka itulah orang-orang yang mendapat petunjuk.'),
	(165, 2, 158, '۞ اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَاۤىِٕرِ اللّٰهِ ۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَّطَّوَّفَ بِهِمَا ۗ وَمَنْ تَطَوَّعَ خَيْرًاۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَلِيْمٌ ', 'inna <strong>al</strong><u>shsh</u>af<u>aa</u> wa<strong>a</strong>lmarwata min sya\'<u>aa</u>-iri <strong>al</strong>l<u>aa</u>hi faman <u>h</u>ajja <strong>a</strong>lbayta awi i\'tamara fal<u>aa</u> jun<u>aah</u>a \'alayhi an ya<u>ththh</u>awwafa bihim<u>aa</u> waman ta<u>th</u>awwa\'a khayran fa-inna <strong>al</strong>l<u>aa</u>ha sy<u>aa</u>kirun \'aliim<strong>un</strong>', 'Sesungguhnya Safa dan Marwah merupakan sebagian syi‘ar (agama) Allah. Maka barangsiapa beribadah haji ke Baitullah atau berumrah, tidak ada dosa baginya mengerjakan sa‘i antara keduanya. Dan barangsiapa dengan kerelaan hati mengerjakan kebajikan, maka Allah Maha Mensyukuri, Maha Mengetahui.'),
	(166, 2, 159, 'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلْنَا مِنَ الْبَيِّنٰتِ وَالْهُدٰى مِنْۢ بَعْدِ مَا بَيَّنّٰهُ لِلنَّاسِ فِى الْكِتٰبِۙ اُولٰۤىِٕكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللّٰعِنُوْنَۙ', 'inna <strong>al</strong>la<u>dz</u>iina yaktumuuna m<u>aa</u> anzaln<u>aa</u> mina <strong>a</strong>lbayyin<u>aa</u>ti wa<strong>a</strong>lhud<u>aa</u> min ba\'di m<u>aa</u> bayyann<u>aa</u>hu li<strong>l</strong>nn<u>aa</u>si fii <strong>a</strong>lkit<u>aa</u>bi ul<u>aa</u>-ika yal\'anuhumu <strong>al</strong>l<u>aa</u>hu wayal\'anuhumu <strong>al</strong>l<u>aa</u>\'inuun<strong>a</strong>', 'Sungguh, orang-orang yang menyembunyikan apa yang telah Kami turunkan berupa keterangan-keterangan dan petunjuk, setelah Kami jelaskan kepada manusia dalam Kitab (Al-Qur\'an), mereka itulah yang dilaknat Allah dan dilaknat (pula) oleh mereka yang melaknat,'),
	(167, 2, 160, 'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَبَيَّنُوْا فَاُولٰۤىِٕكَ اَتُوْبُ عَلَيْهِمْ ۚ وَاَنَا التَّوَّابُ الرَّحِيْمُ ', '<p>ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina t<u>aa</u>buu wa-a<u>sh</u>la<u>h</u>uu wabayyanuu faul<u>aa</u>-ika atuubu \'alayhim wa-an<u>aa</u> <strong>al</strong>ttaww<u>aa</u>bu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'kecuali mereka yang telah bertobat, mengadakan perbaikan dan menjelaskan(nya), mereka itulah yang Aku terima tobatnya dan Akulah Yang Maha Penerima tobat, Maha Penyayang.'),
	(168, 2, 161, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ اُولٰۤىِٕكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu wam<u>aa</u>tuu wahum kuff<u>aa</u>run ul<u>aa</u>-ika \'alayhim la\'natu <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmal<u>aa</u>-ikati wa<strong>al</strong>nn<u>aa</u>si ajma\'iin<strong>a</strong>', 'Sungguh, orang-orang yang kafir dan mati dalam keadaan kafir, mereka itu mendapat laknat Allah, para malaikat dan manusia seluruhnya,'),
	(169, 2, 162, 'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَ', 'kh<u>aa</u>lidiina fiih<u>aa</u> l<u>aa</u> yukhaffafu \'anhumu <strong>a</strong>l\'a<u>dzaa</u>bu wal<u>aa</u> hum yun<u>zh</u>aruun<strong>a</strong>', 'mereka kekal di dalamnya (laknat), tidak akan diringankan azabnya, dan mereka tidak diberi penangguhan.'),
	(170, 2, 163, 'وَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ  لَآاِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّحِيْمُ ࣖ', 'wa-il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa <strong>al</strong>rra<u>h</u>m<u>aa</u>nu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan Tuhan kamu adalah Tuhan Yang Maha Esa, tidak ada tuhan selain Dia, Yang Maha Pengasih, Maha Penyayang.'),
	(171, 2, 164, 'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِيْ تَجْرِيْ فِى الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ مَّاۤءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيْهَ', 'inna fii khalqi <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>i</strong>khtil<u>aa</u>fi <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri wa<strong>a</strong>lfulki <strong>al</strong>latii tajrii fii <strong>a</strong>lba<u>h</u>ri bim<u>aa</u> yanfa\'u <strong>al</strong>nn<u>aa</u>sa wam<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu mina <strong>al</strong>ssam<u>aa</u>-i min m<u>aa</u>-in fa-a<u>h</u>y<u>aa</u> bihi <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> wabatstsa fiih<u>aa</u> min kulli d<u>aa</u>bbatin wata<u>sh</u>riifi <strong>al</strong>rriy<u>aah</u>i wa<strong>al</strong>ssa<u>haa</u>bi <strong>a</strong>lmusakhkhari bayna <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun<strong>a</strong>', 'Sesungguhnya pada penciptaan langit dan bumi, pergantian malam dan siang, kapal yang berlayar di laut dengan (muatan) yang bermanfaat bagi manusia, apa yang diturunkan Allah dari langit berupa air, lalu dengan itu dihidupkan-Nya bumi setelah mati (kering), dan Dia tebarkan di dalamnya bermacam-macam binatang, dan perkisaran angin dan awan yang dikendalikan antara langit dan bumi, (semua itu) sungguh, merupakan tanda-tanda (kebesaran Allah) bagi orang-orang yang mengerti.'),
	(172, 2, 165, 'وَمِنَ النَّاسِ مَنْ يَّتَّخِذُ مِنْ دُوْنِ اللّٰهِ اَنْدَادًا يُّحِبُّوْنَهُمْ كَحُبِّ اللّٰهِ ۗ وَالَّذِيْنَ اٰمَنُوْٓا اَشَدُّ حُبًّا لِّلّٰهِ ۙوَلَوْ يَرَى الَّذِيْنَ ظَلَمُوْٓا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَمِيْعًا ۙوَّاَنَّ ال', 'wamina <strong>al</strong>nn<u>aa</u>si man yattakhi<u>dz</u>u min duuni <strong>al</strong>l<u>aa</u>hi and<u>aa</u>dan yu<u>h</u>ibbuunahum ka<u>h</u>ubbi <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu asyaddu <u>h</u>ubban lill<u>aa</u>hi walaw yar<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu i<u>dz</u> yarawna <strong>a</strong>l\'a<u>dzaa</u>ba anna <strong>a</strong>lquwwata lill<u>aa</u>hi jamii\'an wa-anna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'a<u>dzaa</u>b<strong>i</strong>', 'Dan di antara manusia ada orang yang menyembah tuhan selain Allah sebagai tandingan, yang mereka cintai seperti mencintai Allah. Adapun orang-orang yang beriman sangat besar cintanya kepada Allah. Sekiranya orang-orang yang berbuat zalim itu melihat, ketika mereka melihat azab (pada hari Kiamat), bahwa kekuatan itu semuanya milik Allah dan bahwa Allah sangat berat azab-Nya (niscaya mereka menyesal).'),
	(173, 2, 166, 'اِذْ تَبَرَّاَ الَّذِيْنَ اتُّبِعُوْا مِنَ الَّذِيْنَ اتَّبَعُوْا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ', 'i<u>dz</u> tabarra-a <strong>al</strong>la<u>dz</u>iina ittubi\'uu mina <strong>al</strong>la<u>dz</u>iina ittaba\'uu wara-awuu <strong>a</strong>l\'a<u>dzaa</u>ba wataqa<u>ththh</u>a\'at bihimu <strong>a</strong>l-asb<u>aa</u>b<strong>u</strong>', '(Yaitu) ketika orang-orang yang diikuti berlepas tangan dari orang-orang yang mengikuti, dan mereka melihat azab, dan (ketika) segala hubungan antara mereka terputus.'),
	(174, 2, 167, 'وَقَالَ الَّذِيْنَ اتَّبَعُوْا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ ۗ كَمَا تَبَرَّءُوْا مِنَّا ۗ كَذٰلِكَ يُرِيْهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرٰتٍ عَلَيْهِمْ ۗ وَمَا هُمْ بِخَارِجِيْنَ مِنَ النَّارِ ࣖ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina ittaba\'uu law anna lan<u>aa</u> karratan fanatabarra-a minhum kam<u>aa</u> tabarrauu minn<u>aa</u> ka<u>dzaa</u>lika yuriihimu <strong>al</strong>l<u>aa</u>hu a\'m<u>aa</u>lahum <u>h</u>asar<u>aa</u>tin \'alayhim wam<u>aa</u> hum bikh<u>aa</u>rijiina mina <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Dan orang-orang yang mengikuti berkata, “Sekiranya kami mendapat kesempatan (kembali ke dunia), tentu kami akan berlepas tangan dari mereka, sebagaimana mereka berlepas tangan dari kami.” Demikianlah Allah memperlihatkan kepada mereka amal per-buatan mereka yang menjadi penyesalan mereka. Dan mereka tidak akan keluar dari api neraka.'),
	(175, 2, 168, 'يٰٓاَيُّهَا النَّاسُ كُلُوْا مِمَّا فِى الْاَرْضِ حَلٰلًا طَيِّبًا ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su kuluu mimm<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wal<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti <strong>al</strong>sysyay<u>th</u><u>aa</u>ni innahu lakum \'aduwwun mubiin<strong>un</strong>', 'Wahai manusia! Makanlah dari (makanan) yang halal dan baik yang terdapat di bumi, dan janganlah kamu mengikuti langkah-langkah setan. Sungguh, setan itu musuh yang nyata bagimu.'),
	(176, 2, 169, 'اِنَّمَا يَأْمُرُكُمْ بِالسُّوْۤءِ وَالْفَحْشَاۤءِ وَاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'innam<u>aa</u> ya/murukum bi<strong>al</strong>ssuu-i wa<strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i wa-an taquuluu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Sesungguhnya (setan) itu hanya menyuruh kamu agar berbuat jahat dan keji, dan mengatakan apa yang tidak kamu ketahui tentang Allah.'),
	(177, 2, 170, 'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَآ اَلْفَيْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْقِلُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ', 'wa-i<u>dzaa</u> qiila lahumu ittabi\'uu m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu q<u>aa</u>luu bal nattabi\'u m<u>aa</u> <strong>a</strong>lfayn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na <u>aa</u>b<u>aa</u>uhum l<u>aa</u> ya\'qiluuna syay-an wal<u>aa</u> yahtaduun<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Ikutilah apa yang telah diturunkan Allah.” Mereka menjawab, “(Tidak!) Kami mengikuti apa yang kami dapati pada nenek moyang kami (melakukannya).” Padahal, nenek moyang mereka itu tidak mengetahui apa pun, dan tidak mendapat petunjuk.'),
	(178, 2, 171, 'وَمَثَلُ الَّذِيْنَ كَفَرُوْا كَمَثَلِ الَّذِيْ يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَاۤءً وَّنِدَاۤءً ۗ صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُوْنَ ', 'wamatsalu <strong>al</strong>la<u>dz</u>iina kafaruu kamatsali <strong>al</strong>la<u>dz</u>ii yan\'iqu bim<u>aa</u> l<u>aa</u> yasma\'u ill<u>aa</u> du\'<u>aa</u>-an wanid<u>aa</u>-an <u>sh</u>ummun bukmun \'umyun fahum l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Dan perumpamaan bagi (penyeru) orang yang kafir adalah seperti (penggembala) yang meneriaki (binatang) yang tidak mendengar selain panggilan dan teriakan. (Mereka) tuli, bisu dan buta, maka mereka tidak mengerti.'),
	(179, 2, 172, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ وَاشْكُرُوْا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wa<strong>u</strong>sykuruu lill<u>aa</u>hi in kuntum iyy<u>aa</u>hu ta\'buduun<strong>a</strong>', 'Wahai orang-orang yang beriman! Makanlah dari rezeki yang baik yang Kami berikan kepada kamu dan bersyukurlah kepada Allah, jika kamu hanya menyembah kepada-Nya.'),
	(180, 2, 173, 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ بِهٖ لِغَيْرِ اللّٰهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'innam<u>aa</u> <u>h</u>arrama \'alaykumu <strong>a</strong>lmaytata wa<strong>al</strong>ddama wala<u>h</u>ma <strong>a</strong>lkhinziiri wam<u>aa</u> uhilla bihi lighayri <strong>al</strong>l<u>aa</u>hi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fal<u>aa</u> itsma \'alayhi inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Sesungguhnya Dia hanya mengharamkan atasmu bangkai, darah, daging babi, dan (daging) hewan yang disembelih dengan (menyebut nama) selain Allah. Tetapi barangsiapa terpaksa (memakannya), bukan karena menginginkannya dan tidak (pula) melampaui batas, maka tidak ada dosa baginya. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(181, 2, 174, 'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلَ اللّٰهُ مِنَ الْكِتٰبِ وَيَشْتَرُوْنَ بِهٖ ثَمَنًا قَلِيْلًاۙ اُولٰۤىِٕكَ مَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۚوَلَهُمْ عَذَابٌ ا', 'inna <strong>al</strong>la<u>dz</u>iina yaktumuuna m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu mina <strong>a</strong>lkit<u>aa</u>bi wayasytaruuna bihi tsamanan qaliilan ul<u>aa</u>-ika m<u>aa</u> ya/kuluuna fii bu<u>th</u>uunihim ill<u>aa</u> <strong>al</strong>nn<u>aa</u>ra wal<u>aa</u> yukallimuhumu <strong>al</strong>l<u>aa</u>hu yawma <strong>a</strong>lqiy<u>aa</u>mati wal<u>aa</u> yuzakkiihim walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sungguh, orang-orang yang menyembunyikan apa yang telah diturunkan Allah, yaitu Kitab, dan menjualnya dengan harga murah, mereka hanya menelan api neraka ke dalam perutnya, dan Allah tidak akan menyapa mereka pada hari Kiamat, dan tidak akan menyucikan mereka. Mereka akan mendapat azab yang sangat pedih.'),
	(182, 2, 175, 'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَآ اَصْبَرَهُمْ عَلَى النَّارِ ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina isytarawuu <strong>al</strong><u>dhdh</u>al<u>aa</u>lata bi<strong>a</strong>lhud<u>aa</u> wa<strong>a</strong>l\'a<u>dzaa</u>ba bi<strong>a</strong>lmaghfirati fam<u>aa</u> a<u>sh</u>barahum \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Mereka itulah yang membeli kesesatan dengan petunjuk dan azab dengan ampunan. Maka alangkah beraninya mereka menentang api neraka!'),
	(183, 2, 176, 'ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتٰبَ بِالْحَقِّ ۗ وَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِى الْكِتٰبِ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ࣖ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha nazzala <strong>a</strong>lkit<u>aa</u>ba bi<strong>a</strong>l<u>h</u>aqqi wa-inna <strong>al</strong>la<u>dz</u>iina ikhtalafuu fii <strong>a</strong>lkit<u>aa</u>bi lafii syiq<u>aa</u>qin ba\'iid<strong>in</strong>', 'Yang demikian itu karena Allah telah menurunkan Kitab (Al-Qur\'an) dengan (membawa) kebenaran, dan sesungguhnya orang-orang yang berselisih paham tentang (kebenaran) Kitab itu, mereka dalam perpecahan yang jauh.'),
	(184, 2, 177, '۞ لَيْسَ الْبِرَّاَنْ تُوَلُّوْا وُجُوْهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰۤىِٕكَةِ وَالْكِتٰبِ وَالنَّبِيّٖنَ ۚ وَاٰتَى الْمَالَ عَلٰى حُبِّهٖ ذَوِى الْقُرْبٰى وَالْيَتٰمٰى وَالْ', 'laysa <strong>a</strong>lbirra an tuwalluu wujuuhakum qibala <strong>a</strong>lmasyriqi wa<strong>a</strong>lmaghribi wal<u>aa</u>kinna <strong>a</strong>lbirra man <u>aa</u>mana bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa<strong>a</strong>lmal<u>aa</u>-ikati wa<strong>a</strong>lkit<u>aa</u>bi wa<strong>al</strong>nnabiyyiina wa<u>aa</u>t<u>aa</u> <strong>a</strong>lm<u>aa</u>la \'al<u>aa</u> <u>h</u>ubbihi <u>dz</u>awii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiina wa<strong>i</strong>bna <strong>a</strong>lssabiili wa<strong>al</strong>ss<u>aa</u>-iliina wafii <strong>al</strong>rriq<u>aa</u>bi wa-aq<u>aa</u>ma <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>t<u>aa</u> <strong>al</strong>zzak<u>aa</u>ta wa<strong>a</strong>lmuufuuna bi\'ahdihim i<u>dzaa</u> \'<u>aa</u>haduu wa<strong>al</strong><u>shshaa</u>biriina fii <strong>a</strong>lba/s<u>aa</u>-i wa<strong>al</strong><u>dhdh</u>arr<u>aa</u>-i wa<u>h</u>iina <strong>a</strong>lba/si ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina <u>sh</u>adaquu waul<u>aa</u>-ika humu <strong>a</strong>lmuttaquun<strong>a</strong>', 'Kebajikan itu bukanlah menghadapkan wajahmu ke arah timur dan ke barat, tetapi kebajikan itu ialah (kebajikan) orang yang beriman kepada Allah, hari akhir, malaikat-malaikat, kitab-kitab, dan nabi-nabi dan memberikan harta yang dicintainya kepada kerabat, anak yatim, orang-orang miskin, orang-orang yang dalam perjalanan (musafir), peminta-minta, dan untuk memerdekakan hamba sahaya, yang melaksanakan salat dan menunaikan zakat, orang-orang yang menepati janji apabila berjanji, dan orang yang sabar dalam kemelaratan, penderitaan dan pada masa peperangan. Mereka itulah orang-orang yang benar, dan mereka itulah orang-orang yang bertakwa.'),
	(185, 2, 178, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِى الْقَتْلٰىۗ  اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۗ  فَمَنْ عُفِيَ لَهٗ مِنْ اَخِيْهِ شَيْءٌ فَاتِّبَاعٌ ۢبِالْمَعْرُوْفِ وَاَدَاۤءٌ اِلَيْهِ بِاِحْسَانٍ ۗ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu kutiba \'alaykumu <strong>a</strong>lqi<u>sas</u>u fii <strong>a</strong>lqatl<u>aa</u> <strong>a</strong>l<u>h</u>urru bi<strong>a</strong>l<u>h</u>urri wa<strong>a</strong>l\'abdu bi<strong>a</strong>l\'abdi wa<strong>a</strong>lunts<u>aa</u> bi<strong>a</strong>lunts<u>aa</u> faman \'ufiya lahu min akhiihi syay-un fa<strong>i</strong>ttib<u>aa</u>\'un bi<strong>a</strong>lma\'ruufi wa-ad<u>aa</u>un ilayhi bi-i<u>h</u>s<u>aa</u>nin <u>dzaa</u>lika takhfiifun min rabbikum wara<u>h</u>matun famani i\'tad<u>aa</u> ba\'da <u>dzaa</u>lika falahu \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Wahai orang-orang yang beriman! Diwajibkan atas kamu (melaksanakan) qisas berkenaan dengan orang yang dibunuh. Orang merdeka dengan orang merdeka, hamba sahaya dengan hamba sahaya, perempuan dengan perempuan. Tetapi barangsiapa memperoleh maaf dari saudaranya, hendaklah dia mengikutinya dengan baik, dan membayar diat (tebusan) kepadanya dengan baik (pula). Yang demikian itu adalah keringanan dan rahmat dari Tuhanmu. Barangsiapa melampaui batas setelah itu, maka ia akan mendapat azab yang sangat pedih.'),
	(186, 2, 179, 'وَلَكُمْ فِى الْقِصَاصِ حَيٰوةٌ يّٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُوْنَ ', 'walakum fii <strong>a</strong>lqi<u>sas</u>i <u>h</u>ay<u>aa</u>tun y<u>aa</u> ulii <strong>a</strong>l-alb<u>aa</u>bi la\'allakum tattaquun<strong>a</strong>', 'Dan dalam qisas itu ada (jaminan) kehidupan bagimu, wahai orang-orang yang berakal, agar kamu bertakwa.'),
	(187, 2, 180, 'كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْرًا ۖ ۨالْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ بِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُتَّقِيْنَ ۗ  ', 'kutiba \'alaykum i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adakumu <strong>a</strong>lmawtu in taraka khayran <strong>a</strong>lwa<u>sh</u>iyyatu lilw<u>aa</u>lidayni wa<strong>a</strong>l-aqrabiina bi<strong>a</strong>lma\'ruufi <u>h</u>aqqan \'al<u>aa</u> <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Diwajibkan atas kamu, apabila maut hendak menjemput seseorang di antara kamu, jika dia meninggalkan harta, berwasiat untuk kedua orang tua dan karib kerabat dengan cara yang baik, (sebagai) kewajiban bagi orang-orang yang bertakwa.'),
	(188, 2, 181, 'فَمَنْۢ بَدَّلَهٗ بَعْدَمَا سَمِعَهٗ فَاِنَّمَآ اِثْمُهٗ عَلَى الَّذِيْنَ يُبَدِّلُوْنَهٗ ۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ۗ  ', 'faman baddalahu ba\'da m<u>aa</u> sami\'ahu fa-innam<u>aa</u> itsmuhu \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina yubaddiluunahu inna <strong>al</strong>l<u>aa</u>ha samii\'un \'aliim<strong>un</strong>', 'Barangsiapa mengubahnya (wasiat itu), setelah mendengarnya, maka sesungguhnya dosanya hanya bagi orang yang mengubahnya. Sungguh, Allah Maha Mendengar, Maha Mengetahui.'),
	(189, 2, 182, 'فَمَنْ خَافَ مِنْ مُّوْصٍ جَنَفًا اَوْ اِثْمًا فَاَصْلَحَ بَيْنَهُمْ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ ', 'faman kh<u>aa</u>fa min muu<u>sh</u>in janafan aw itsman fa-a<u>sh</u>la<u>h</u>a baynahum fal<u>aa</u> itsma \'alayhi inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Tetapi barangsiapa khawatir bahwa pemberi wasiat (berlaku) berat sebelah atau berbuat salah, lalu dia mendamaikan antara mereka, maka dia tidak berdosa. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(190, 2, 183, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu kutiba \'alaykumu <strong>al</strong><u>shsh</u>iy<u>aa</u>mu kam<u>aa</u> kutiba \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina min qablikum la\'allakum tattaquun<strong>a</strong>', 'Wahai orang-orang yang beriman! Diwajibkan atas kamu berpuasa sebagaimana diwajibkan atas orang sebelum kamu agar kamu bertakwa,'),
	(191, 2, 184, 'اَيَّامًا مَّعْدُوْدٰتٍۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ وَعَلَى الَّذِيْنَ يُطِيْقُوْنَهٗ فِدْيَةٌ طَعَامُ مِسْكِيْنٍۗ  فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهٗ ۗ وَاَنْ تَصُوْمُوْا خَيْرٌ لَّكُمْ ', 'ayy<u>aa</u>man ma\'duud<u>aa</u>tin faman k<u>aa</u>na minkum marii<u>dh</u>an aw \'al<u>aa</u> safarin fa\'iddatun min ayy<u>aa</u>min ukhara wa\'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina yu<u>th</u>iiquunahu fidyatun <u>th</u>a\'<u>aa</u>mu miskiinin faman ta<u>th</u>awwa\'a khayran fahuwa khayrun lahu wa-an ta<u>sh</u>uumuu khayrun lakum in kuntum ta\'lamuun<strong>a</strong>', '(Yaitu) beberapa hari tertentu. Maka barangsiapa di antara kamu sakit atau dalam perjalanan (lalu tidak berpuasa), maka (wajib mengganti) sebanyak hari (yang dia tidak berpuasa itu) pada hari-hari yang lain. Dan bagi orang yang berat menjalankannya, wajib membayar fidyah, yaitu memberi makan seorang miskin. Tetapi barangsiapa dengan kerelaan hati mengerjakan kebajikan, maka itu lebih baik baginya, dan puasamu itu lebih baik bagimu jika kamu mengetahui.'),
	(192, 2, 185, 'شَهْرُ رَمَضَانَ الَّذِيْٓ اُنْزِلَ فِيْهِ الْقُرْاٰنُ هُدًى لِّلنَّاسِ وَبَيِّنٰتٍ مِّنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ ۗ وَمَنْ كَانَ مَرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ يُرِيْدُ اللّٰهُ ', 'syahru rama<u>daa</u>na <strong>al</strong>la<u>dz</u>ii unzila fiihi <strong>a</strong>lqur-<u>aa</u>nu hudan li<strong>l</strong>nn<u>aa</u>si wabayyin<u>aa</u>tin mina <strong>a</strong>lhud<u>aa</u> wa<strong>a</strong>lfurq<u>aa</u>ni faman syahida minkumu <strong>al</strong>sysyahra falya<u>sh</u>umhu waman k<u>aa</u>na marii<u>dh</u>an aw \'al<u>aa</u> safarin fa\'iddatun min ayy<u>aa</u>min ukhara yuriidu <strong>al</strong>l<u>aa</u>hu bikumu <strong>a</strong>lyusra wal<u>aa</u> yuriidu bikumu <strong>a</strong>l\'usra walitukmiluu <strong>a</strong>l\'iddata walitukabbiruu <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> m<u>aa</u> had<u>aa</u>kum wala\'allakum tasykuruun<strong>a</strong>', 'Bulan Ramadan adalah (bulan) yang di dalamnya diturunkan Al-Qur\'an, sebagai petunjuk bagi manusia dan penjelasan-penjelasan mengenai petunjuk itu dan pembeda (antara yang benar dan yang batil). Karena itu, barangsiapa di antara kamu ada di bulan itu, maka berpuasalah. Dan barangsiapa sakit atau dalam perjalanan (dia tidak berpuasa), maka (wajib menggantinya), sebanyak hari yang ditinggalkannya itu, pada hari-hari yang lain. Allah menghendaki kemudahan bagimu, dan tidak menghendaki kesukaran bagimu. Hendaklah kamu mencukupkan bilangannya dan mengagungkan Allah atas petunjuk-Nya yang diberikan kepadamu, agar kamu bersyukur.'),
	(193, 2, 186, 'وَاِذَا سَاَلَكَ عِبَادِيْ عَنِّيْ فَاِنِّيْ قَرِيْبٌ ۗ اُجِيْبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَجِيْبُوْا لِيْ وَلْيُؤْمِنُوْا بِيْ لَعَلَّهُمْ يَرْشُدُوْنَ ', 'wa-i<u>dzaa</u> sa-alaka \'ib<u>aa</u>dii \'annii fa-innii qariibun ujiibu da\'wata <strong>al</strong>dd<u>aa</u>\'i i<u>dzaa</u> da\'<u>aa</u>ni falyastajiibuu lii walyu/minuu bii la\'allahum yarsyuduun<strong>a</strong>', 'Dan apabila hamba-hamba-Ku bertanya kepadamu (Muhammad) tentang Aku, maka sesungguhnya Aku dekat. Aku Kabulkan permohonan orang yang berdoa apabila dia berdoa kepada-Ku. Hendaklah mereka itu memenuhi (perintah)-Ku dan beriman kepada-Ku, agar mereka memperoleh kebenaran.'),
	(194, 2, 187, 'اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَاۤىِٕكُمْ ۗ هُنَّ لِبَاسٌ لَّكُمْ وَاَنْتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُوْنَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ ۚ فَالْـٰٔنَ بَاشِرُوْهُنَّ وَابْتَغُوْ', 'u<u>h</u>illa lakum laylata <strong>al</strong><u>shsh</u>iy<u>aa</u>mi <strong>al</strong>rrafatsu il<u>aa</u> nis<u>aa</u>-ikum hunna lib<u>aa</u>sun lakum wa-antum lib<u>aa</u>sun lahunna \'alima <strong>al</strong>l<u>aa</u>hu annakum kuntum takht<u>aa</u>nuuna anfusakum fat<u>aa</u>ba \'alaykum wa\'af<u>aa</u> \'ankum fa<strong>a</strong>l-<u>aa</u>na b<u>aa</u>syiruuhunna wa<strong>i</strong>btaghuu m<u>aa</u> kataba <strong>al</strong>l<u>aa</u>hu lakum wakuluu wa<strong>i</strong>syrabuu <u>h</u>att<u>aa</u> yatabayyana lakumu <strong>a</strong>lkhay<u>th</u>u <strong>a</strong>l-abya<u>dh</u>u mina <strong>a</strong>lkhay<u>th</u>i <strong>a</strong>l-aswadi mina <strong>a</strong>lfajri tsumma atimmuu <strong>al</strong><u>shsh</u>iy<u>aa</u>ma il<u>aa</u> <strong>al</strong>layli wal<u>aa</u> tub<u>aa</u>syiruuhunna wa-antum \'<u>aa</u>kifuuna fii <strong>a</strong>lmas<u>aa</u>jidi tilka <u>h</u>uduudu <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> taqrabuuh<u>aa</u> ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu <u>aa</u>y<u>aa</u>tihi li<strong>l</strong>nn<u>aa</u>si la\'allahum yattaquun<strong>a</strong>', 'Dihalalkan bagimu pada malam hari puasa bercampur dengan istrimu. Mereka adalah pakaian bagimu, dan kamu adalah pakaian bagi mereka. Allah mengetahui bahwa kamu tidak dapat menahan dirimu sendiri, tetapi Dia menerima tobatmu dan memaafkan kamu. Maka sekarang campurilah mereka dan carilah apa yang telah ditetapkan Allah bagimu. Makan dan minumlah hingga jelas bagimu (perbedaan) antara benang putih dan benang hitam, yaitu fajar. Kemudian sempurnakanlah puasa sampai (datang) malam. Tetapi jangan kamu campuri mereka, ketika kamu beriktikaf dalam masjid. Itulah ketentuan Allah, maka janganlah kamu mendekatinya. Demikianlah Allah menerangkan ayat-ayat-Nya kepada manusia, agar mereka bertakwa.'),
	(195, 2, 188, 'وَلَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوْا بِهَآ اِلَى الْحُكَّامِ لِتَأْكُلُوْا فَرِيْقًا مِّنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُوْنَ ࣖ', 'wal<u>aa</u> ta/kuluu amw<u>aa</u>lakum baynakum bi<strong>a</strong>lb<u>aath</u>ili watudluu bih<u>aa</u> il<u>aa</u> <strong>a</strong>l<u>h</u>ukk<u>aa</u>mi lita/kuluu fariiqan min amw<u>aa</u>li <strong>al</strong>nn<u>aa</u>si bi<strong>a</strong>l-itsmi wa-antum ta\'lamuun<strong>a</strong>', 'Dan janganlah kamu makan harta di antara kamu dengan jalan yang batil, dan (janganlah) kamu menyuap dengan harta itu kepada para hakim, dengan maksud agar kamu dapat memakan sebagian harta orang lain itu dengan jalan dosa, padahal kamu mengetahui.'),
	(196, 2, 189, '۞ يَسـَٔلُوْنَكَ عَنِ الْاَهِلَّةِ ۗ قُلْ هِيَ مَوَاقِيْتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوْتَ مِنْ ظُهُوْرِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوْتَ مِنْ اَبْوَابِهَا ۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْل', 'yas-aluunaka \'ani <strong>a</strong>l-ahillati qul hiya maw<u>aa</u>qiitu li<strong>l</strong>nn<u>aa</u>si wa<strong>a</strong>l<u>h</u>ajji walaysa <strong>a</strong>lbirru bi-an ta/tuu <strong>a</strong>lbuyuuta min <u>zh</u>uhuurih<u>aa</u> wal<u>aa</u>kinna <strong>a</strong>lbirra mani ittaq<u>aa</u> wa/tuu <strong>a</strong>lbuyuuta min abw<u>aa</u>bih<u>aa</u> wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Mereka bertanya kepadamu (Muhammad) tentang bulan sabit. Katakanlah, “Itu adalah (penunjuk) waktu bagi manusia dan (ibadah) haji.” Dan bukanlah suatu kebajikan memasuki rumah dari atasnya, tetapi kebajikan adalah (kebajikan) orang yang bertakwa. Masukilah rumah-rumah dari pintu-pintunya, dan bertakwalah kepada Allah agar kamu beruntung.'),
	(197, 2, 190, 'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يُقَاتِلُوْنَكُمْ وَلَا تَعْتَدُوْا ۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ', 'waq<u>aa</u>tiluu fii sabiili <strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>iina yuq<u>aa</u>tiluunakum wal<u>aa</u> ta\'taduu inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmu\'tadiin<strong>a</strong>', 'Dan perangilah di jalan Allah orang-orang yang memerangi kamu, tetapi jangan melampaui batas. Sungguh, Allah tidak menyukai orang-orang yang melampaui batas.'),
	(198, 2, 191, 'وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ وَاَخْرِجُوْهُمْ مِّنْ حَيْثُ اَخْرَجُوْكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوْهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقٰتِلُوْكُمْ فِيْهِۚ فَاِنْ قٰتَلُوْكُمْ فَاقْتُلُوْهُمْۗ  كَذٰلِكَ جَ', 'wa<strong>u</strong>qtuluuhum <u>h</u>aytsu tsaqiftumuuhum wa-akhrijuuhum min <u>h</u>aytsu akhrajuukum wa<strong>a</strong>lfitnatu asyaddu mina <strong>a</strong>lqatli wal<u>aa</u> tuq<u>aa</u>tiluuhum \'inda <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi <u>h</u>att<u>aa</u> yuq<u>aa</u>tiluukum fiihi fa-in q<u>aa</u>taluukum fa<strong>u</strong>qtuluuhum ka<u>dzaa</u>lika jaz<u>aa</u>u <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan bunuhlah mereka di mana kamu temui mereka, dan usirlah mereka dari mana mereka telah mengusir kamu. Dan fitnah itu lebih kejam daripada pembunuhan. Dan janganlah kamu perangi mereka di Masjidilharam, kecuali jika mereka memerangi kamu di tempat itu. Jika mereka memerangi kamu, maka perangilah mereka. Demikianlah balasan bagi orang kafir.'),
	(199, 2, 192, 'فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'fa-ini intahaw fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Tetapi jika mereka berhenti, maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(200, 2, 193, 'وَقٰتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ لِلّٰهِ ۗ فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظّٰلِمِيْنَ', 'waq<u>aa</u>tiluuhum <u>h</u>att<u>aa</u> l<u>aa</u> takuuna fitnatun wayakuuna <strong>al</strong>ddiinu lill<u>aa</u>hi fa-ini intahaw fal<u>aa</u> \'udw<u>aa</u>na ill<u>aa</u> \'al<u>aa</u> <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan perangilah mereka itu sampai tidak ada lagi fitnah, dan agama hanya bagi Allah semata. Jika mereka berhenti, maka tidak ada (lagi) permusuhan, kecuali terhadap orang-orang zalim.'),
	(201, 2, 194, 'اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمٰتُ قِصَاصٌۗ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوْا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْ ۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ', 'a<strong>l</strong>sysyahru <strong>a</strong>l<u>h</u>ar<u>aa</u>mu bi<strong>al</strong>sysyahri <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa<strong>a</strong>l<u>h</u>urum<u>aa</u>tu qi<u>sas</u>un famani i\'tad<u>aa</u> \'alaykum fa<strong>i</strong>\'taduu \'alayhi bimitsli m<u>aa</u> i\'tad<u>aa</u> \'alaykum wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Bulan haram dengan bulan haram, dan (terhadap) sesuatu yang dihormati berlaku (hukum) qisas. Oleh sebab itu barangsiapa menyerang kamu, maka seranglah dia setimpal dengan serangannya terhadap kamu. Bertakwalah kepada Allah dan ketahuilah bahwa Allah beserta orang-orang yang bertakwa.'),
	(202, 2, 195, 'وَاَنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلَا تُلْقُوْا بِاَيْدِيْكُمْ اِلَى التَّهْلُكَةِ ۛ وَاَحْسِنُوْا ۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ ', 'wa-anfiquu fii sabiili <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> tulquu bi-aydiikum il<u>aa</u> <strong>al</strong>ttahlukati wa-a<u>h</u>sinuu inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan infakkanlah (hartamu) di jalan Allah, dan janganlah kamu jatuhkan (diri sendiri) ke dalam kebinasaan dengan tangan sendiri, dan berbuatbaiklah. Sungguh, Allah menyukai orang-orang yang berbuat baik.'),
	(203, 2, 196, 'وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِ ۗ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوْا رُءُوْسَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهٗ ۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ بِهٖٓ اَذًى مِّنْ رَّأْسِهٖ فَفِدْيَةٌ مِّنْ صِيَ', 'wa-atimmuu <strong>a</strong>l<u>h</u>ajja wa<strong>a</strong>l\'umrata lill<u>aa</u>hi fa-in u<u>hs</u>irtum fam<u>aa</u> istaysara mina <strong>a</strong>lhadyi wal<u>aa</u> ta<u>h</u>liquu ruuusakum <u>h</u>att<u>aa</u> yablugha <strong>a</strong>lhadyu ma<u>h</u>illahu faman k<u>aa</u>na minkum marii<u>dh</u>an aw bihi a<u>dz</u>an min ra/sihi fafidyatun min <u>sh</u>iy<u>aa</u>min aw <u>sh</u>adaqatin aw nusukin fa-i<u>dzaa</u> amintum faman tamatta\'a bi<strong>a</strong>l\'umrati il<u>aa</u> <strong>a</strong>l<u>h</u>ajji fam<u>aa</u> istaysara mina <strong>a</strong>lhadyi faman lam yajid fa<u>sh</u>iy<u>aa</u>mu tsal<u>aa</u>tsati ayy<u>aa</u>min fii <strong>a</strong>l<u>h</u>ajji wasab\'atin i<u>dzaa</u> raja\'tum tilka \'asyaratun k<u>aa</u>milatun <u>dzaa</u>lika liman lam yakun ahluhu <u>had</u>irii <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Dan sempurnakanlah ibadah haji dan umrah karena Allah. Tetapi jika kamu terkepung (oleh musuh), maka (sembelihlah) hadyu yang mudah didapat, dan jangan kamu mencukur kepalamu, sebelum hadyu sampai di tempat penyembelihannya. Jika ada di antara kamu yang sakit atau ada gangguan di kepalanya (lalu dia bercukur), maka dia wajib berfidyah, yaitu berpuasa, bersedekah atau berkurban. Apabila kamu dalam keadaan aman, maka barangsiapa mengerjakan umrah sebelum haji, dia (wajib menyembelih) hadyu yang mudah didapat. Tetapi jika dia tidak mendapatkannya, maka dia (wajib) berpuasa tiga hari dalam (musim) haji dan tujuh (hari) setelah kamu kembali. Itu seluruhnya sepuluh (hari). Demikian itu, bagi orang yang keluarganya tidak ada (tinggal) di sekitar Masjidilharam. Bertakwalah kepada Allah dan ketahuilah bahwa Allah sangat keras hukuman-Nya.'),
	(204, 2, 197, 'اَلْحَجُّ اَشْهُرٌ مَّعْلُوْمٰتٌ ۚ فَمَنْ فَرَضَ فِيْهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوْقَ وَلَا جِدَالَ فِى الْحَجِّ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ يَّعْلَمْهُ اللّٰهُ ۗ وَتَزَوَّدُوْا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۖ وَاتَّقُوْنِ يٰٓاُولِى الْا', 'al<u>h</u>ajju asyhurun ma\'luum<u>aa</u>tun faman fara<u>dh</u>a fiihinna <strong>a</strong>l<u>h</u>ajja fal<u>aa</u> rafatsa wal<u>aa</u> fusuuqa wal<u>aa</u> jid<u>aa</u>la fii <strong>a</strong>l<u>h</u>ajji wam<u>aa</u> taf\'aluu min khayrin ya\'lamhu <strong>al</strong>l<u>aa</u>hu watazawwaduu fa-inna khayra <strong>al</strong>zz<u>aa</u>di <strong>al</strong>ttaqw<u>aa</u> wa<strong>i</strong>ttaquuni y<u>aa</u> ulii <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', '(Musim) haji itu (pada) bulan-bulan yang telah dimaklumi. Barangsiapa mengerjakan (ibadah) haji dalam (bulan-bulan) itu, maka janganlah dia berkata jorok (rafats), berbuat maksiat dan bertengkar dalam (melakukan ibadah) haji. Segala yang baik yang kamu kerjakan, Allah mengetahuinya. Bawalah bekal, karena sesungguhnya sebaik-baik bekal adalah takwa. Dan bertakwalah kepada-Ku wahai orang-orang yang mempunyai akal sehat!'),
	(205, 2, 198, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ ۗ فَاِذَآ اَفَضْتُمْ مِّنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوْهُ كَمَا هَدٰىكُمْ ۚ وَاِنْ كُنْتُمْ مِّنْ قَبْلِهٖ لَمِنَ الضَّاۤلِّيْنَ ', 'laysa \'alaykum jun<u>aah</u>un an tabtaghuu fa<u>dh</u>lan min rabbikum fa-i<u>dzaa</u> afa<u>dh</u>tum min \'araf<u>aa</u>tin fa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha \'inda <strong>a</strong>lmasy\'ari <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa<strong>u</strong><u>dz</u>kuruuhu kam<u>aa</u> had<u>aa</u>kum wa-in kuntum min qablihi lamina <strong>al</strong><u>dhdhaa</u>lliin<strong>a</strong>', 'Bukanlah suatu dosa bagimu mencari karunia dari Tuhanmu. Maka apabila kamu bertolak dari Arafah, berzikirlah kepada Allah di Masy’arilharam. Dan berzikirlah kepada-Nya sebagaimana Dia telah memberi petunjuk kepadamu, sekalipun sebelumnya kamu benar-benar termasuk orang yang tidak tahu.'),
	(206, 2, 199, 'ثُمَّ اَفِيْضُوْا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'tsumma afii<u>dh</u>uu min <u>h</u>aytsu af<u>aad</u>a <strong>al</strong>nn<u>aa</u>su wa<strong>i</strong>staghfiruu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Kemudian bertolaklah kamu dari tempat orang banyak bertolak (Arafah) dan mohonlah ampunan kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(207, 2, 200, 'فَاِذَا قَضَيْتُمْ مَّنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَاۤءَكُمْ اَوْ اَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ', 'fa-i<u>dzaa</u> qa<u>dh</u>aytum man<u>aa</u>sikakum fa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha ka<u>dz</u>ikrikum <u>aa</u>b<u>aa</u>-akum aw asyadda <u>dz</u>ikran famina <strong>al</strong>nn<u>aa</u>si man yaquulu rabban<u>aa</u> <u>aa</u>tin<u>aa</u> fii <strong>al</strong>dduny<u>aa</u> wam<u>aa</u> lahu fii <strong>a</strong>l-<u>aa</u>khirati min khal<u>aa</u>q<strong>in</strong>', 'Apabila kamu telah menyelesaikan ibadah haji, maka berzikirlah kepada Allah, sebagaimana kamu menyebut-nyebut nenek moyang kamu, bahkan berzikirlah lebih dari itu. Maka di antara manusia ada yang berdoa, “Ya Tuhan kami, berilah kami (kebaikan) di dunia,” dan di akhirat dia tidak memperoleh bagian apa pun.'),
	(208, 2, 201, 'وَمِنْهُمْ مَّنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ حَسَنَةً وَّقِنَا عَذَابَ النَّارِ ', 'waminhum man yaquulu rabban<u>aa</u> <u>aa</u>tin<u>aa</u> fii <strong>al</strong>dduny<u>aa</u> <u>h</u>asanatan wafii <strong>a</strong>l-<u>aa</u>khirati <u>h</u>asanatan waqin<u>aa</u> \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Dan di antara mereka ada yang berdoa, “Ya Tuhan kami, berilah kami kebaikan di dunia dan kebaikan di akhirat, dan lindungilah kami dari azab neraka.”'),
	(209, 2, 202, 'اُولٰۤىِٕكَ لَهُمْ نَصِيْبٌ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ', 'ul<u>aa</u>-ika lahum na<u>sh</u>iibun mimm<u>aa</u> kasabuu wa<strong>al</strong>l<u>aa</u>hu sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Mereka itulah yang memperoleh bagian dari apa yang telah mereka kerjakan, dan Allah Mahacepat perhitungan-Nya.'),
	(210, 2, 203, '۞ وَاذْكُرُوا اللّٰهَ فِيْٓ اَيَّامٍ مَّعْدُوْدٰتٍ ۗ فَمَنْ تَعَجَّلَ فِيْ يَوْمَيْنِ فَلَآ اِثْمَ عَلَيْهِ ۚوَمَنْ تَاَخَّرَ فَلَآ اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ اِلَيْهِ تُحْشَرُوْنَ ', 'wa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha fii ayy<u>aa</u>min ma\'duud<u>aa</u>tin faman ta\'ajjala fii yawmayni fal<u>aa</u> itsma \'alayhi waman ta-akhkhara fal<u>aa</u> itsma \'alayhi limani ittaq<u>aa</u> wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu annakum ilayhi tu<u>h</u>syaruun<strong>a</strong>', 'Dan berzikirlah kepada Allah pada hari yang telah ditentukan jumlahnya. Barangsiapa mempercepat (meninggalkan Mina) setelah dua hari, maka tidak ada dosa baginya. Dan barangsiapa mengakhirkannya tidak ada dosa (pula) baginya, (yakni) bagi orang yang bertakwa. Dan bertakwalah kepada Allah, dan ketahuilah bahwa kamu akan dikumpulkan-Nya.'),
	(211, 2, 204, 'وَمِنَ النَّاسِ مَنْ يُّعْجِبُكَ قَوْلُهٗ فِى الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا فِيْ قَلْبِهٖ ۙ وَهُوَ اَلَدُّ الْخِصَامِ ', 'wamina <strong>al</strong>nn<u>aa</u>si man yu\'jibuka qawluhu fii al<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wayusyhidu <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> m<u>aa</u> fii qalbihi wahuwa aladdu alkhi<u>shaa</u>m<strong>i</strong>', 'Dan di antara manusia ada yang pembicaraannya tentang kehidupan dunia mengagumkan engkau (Muhammad), dan dia bersaksi kepada Allah mengenai isi hatinya, padahal dia adalah penentang yang paling keras.'),
	(212, 2, 205, 'وَاِذَا تَوَلّٰى سَعٰى فِى الْاَرْضِ لِيُفْسِدَ فِيْهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَ اللّٰهُ لَا يُحِبُّ الْفَسَادَ ', 'wa-i<u>dzaa</u> tawall<u>aa</u> sa\'<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i liyufsida fiih<u>aa</u> wayuhlika <strong>a</strong>l<u>h</u>artsa wa<strong>al</strong>nnasla wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lfas<u>aa</u>d<strong>a</strong>', 'Dan apabila dia berpaling (dari engkau), dia berusaha untuk berbuat kerusakan di bumi, serta merusak tanam-tanaman dan ternak, sedang Allah tidak menyukai kerusakan.'),
	(213, 2, 206, 'وَاِذَا قِيْلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهٗ جَهَنَّمُ ۗ وَلَبِئْسَ الْمِهَادُ ', 'wa-i<u>dzaa</u> qiila lahu ittaqi <strong>al</strong>l<u>aa</u>ha akha<u>dz</u>at-hu <strong>a</strong>l\'izzatu bi<strong>a</strong>l-itsmi fa<u>h</u>asbuhu jahannamu walabi/sa <strong>a</strong>lmih<u>aa</u>d<strong>u</strong>', 'Dan apabila dikatakan kepadanya, “Bertakwalah kepada Allah,” bangkitlah kesombongannya untuk berbuat dosa. Maka pantaslah baginya neraka Jahanam, dan sungguh (Jahanam itu) tempat tinggal yang terburuk.'),
	(214, 2, 207, 'وَمِنَ النَّاسِ مَنْ يَّشْرِيْ نَفْسَهُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ ', 'wamina <strong>al</strong>nn<u>aa</u>si man yasyrii nafsahu ibtigh<u>aa</u>-a mar<u>daa</u>ti <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu rauufun bi<strong>a</strong>l\'ib<u>aa</u>d<strong>i</strong>', 'Dan di antara manusia ada orang yang mengorbankan dirinya untuk mencari keridaan Allah. Dan Allah Maha Penyantun kepada hamba-hamba-Nya.'),
	(215, 2, 208, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ادْخُلُوْا فِى السِّلْمِ كَاۤفَّةً  ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu udkhuluu fii <strong>al</strong>ssilmi k<u>aa</u>ffatan wal<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti <strong>al</strong>sysyay<u>th</u><u>aa</u>ni innahu lakum \'aduwwun mubiin<strong>un</strong>', 'Wahai orang-orang yang beriman! Masuklah ke dalam Islam secara keseluruhan, dan janganlah kamu ikuti langkah-langkah setan. Sungguh, ia musuh yang nyata bagimu.'),
	(216, 2, 209, 'فَاِنْ زَلَلْتُمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْكُمُ الْبَيِّنٰتُ فَاعْلَمُوْٓا اَنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ', 'fa-in zalaltum min ba\'di m<u>aa</u> j<u>aa</u>-atkumu <strong>a</strong>lbayyin<u>aa</u>tu fa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Tetapi jika kamu tergelincir setelah bukti-bukti yang nyata sampai kepadamu, ketahuilah bahwa Allah Maha-perkasa, Mahabijaksana.'),
	(217, 2, 210, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ يَّأْتِيَهُمُ اللّٰهُ فِيْ ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلٰۤىِٕكَةُ وَقُضِيَ الْاَمْرُ ۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ', 'hal yan<u>zh</u>uruuna ill<u>aa</u> an ya/tiyahumu <strong>al</strong>l<u>aa</u>hu fii <u>zh</u>ulalin mina <strong>a</strong>lgham<u>aa</u>mi wa<strong>a</strong>lmal<u>aa</u>-ikatu waqu<u>dh</u>iya <strong>a</strong>l-amru wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi turja\'u <strong>a</strong>l-umuur<strong>u</strong>', 'Tidak ada yang mereka tunggu-tunggu kecuali datangnya (azab) Allah bersama malaikat dalam naungan awan, sedangkan perkara (mereka) telah diputuskan. Dan kepada Allah-lah segala perkara dikembalikan.'),
	(218, 2, 211, 'سَلْ بَنِيْٓ اِسْرَاۤءِيْلَ كَمْ اٰتَيْنٰهُمْ مِّنْ اٰيَةٍ ۢ بَيِّنَةٍ ۗ وَمَنْ يُّبَدِّلْ نِعْمَةَ اللّٰهِ مِنْۢ بَعْدِ مَا جَاۤءَتْهُ فَاِنَّ اللّٰهَ  شَدِيْدُ الْعِقَابِ ', 'sal banii isr<u>aa</u>-iila kam <u>aa</u>tayn<u>aa</u>hum min <u>aa</u>yatin bayyinatin waman yubaddil ni\'mata <strong>al</strong>l<u>aa</u>hi min ba\'di m<u>aa</u> j<u>aa</u>-at-hu fa-inna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Tanyakanlah kepada Bani Israil, berapa banyak bukti nyata yang telah Kami berikan kepada mereka. Barangsiapa menukar nikmat Allah setelah (nikmat itu) datang kepadanya, maka sungguh, Allah sangat keras hukuman-Nya.'),
	(219, 2, 212, 'زُيِّنَ لِلَّذِيْنَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُوْنَ مِنَ الَّذِيْنَ اٰمَنُوْا ۘ وَالَّذِيْنَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِ ۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ', 'zuyyina lilla<u>dz</u>iina kafaruu <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> wayaskharuuna mina <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa<strong>a</strong>lla<u>dz</u>iina ittaqaw fawqahum yawma <strong>a</strong>lqiy<u>aa</u>mati wa<strong>al</strong>l<u>aa</u>hu yarzuqu man yasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b<strong>in</strong>', 'Kehidupan dunia dijadikan terasa indah dalam pandangan orang-orang yang kafir, dan mereka menghina orang-orang yang beriman. Padahal orang-orang yang bertakwa itu berada di atas mereka pada hari Kiamat. Dan Allah memberi rezeki kepada orang yang Dia kehendaki tanpa perhitungan.'),
	(220, 2, 213, 'كَانَ النَّاسُ اُمَّةً وَّاحِدَةً ۗ فَبَعَثَ اللّٰهُ النَّبِيّٖنَ مُبَشِّرِيْنَ وَمُنْذِرِيْنَ ۖ وَاَنْزَلَ مَعَهُمُ الْكِتٰبَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيْمَا اخْتَلَفُوْا فِيْهِ ۗ وَمَا اخْتَلَفَ فِيْهِ اِلَّا الَّذِيْنَ اُوْتُوْهُ مِنْۢ بَ', 'k<u>aa</u>na <strong>al</strong>nn<u>aa</u>su ummatan w<u>aah</u>idatan faba\'atsa <strong>al</strong>l<u>aa</u>hu <strong>al</strong>nnabiyyiina mubasysyiriina wamun<u>dz</u>iriina wa-anzala ma\'ahumu <strong>a</strong>lkit<u>aa</u>ba bi<strong>a</strong>l<u>h</u>aqqi liya<u>h</u>kuma bayna <strong>al</strong>nn<u>aa</u>si fiim<u>aa</u> ikhtalafuu fiihi wam<u>aa</u> ikhtalafa fiihi ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuuhu min ba\'di m<u>aa</u> j<u>aa</u>-at-humu <strong>a</strong>lbayyin<u>aa</u>tu baghyan baynahum fahad<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu lim<u>aa</u> ikhtalafuu fiihi mina <strong>a</strong>l<u>h</u>aqqi bi-i<u>dz</u>nihi wa<strong>al</strong>l<u>aa</u>hu yahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Manusia itu (dahulunya) satu umat. Lalu Allah mengutus para nabi (untuk) menyampaikan kabar gembira dan peringatan. Dan diturunkan-Nya bersama mereka Kitab yang mengandung kebenaran, untuk memberi keputusan di antara manusia tentang perkara yang mereka perselisihkan. Dan yang berselisih hanyalah orang-orang yang telah diberi (Kitab), setelah bukti-bukti yang nyata sampai kepada mereka, karena kedengkian di antara mereka sendiri. Maka dengan kehendak-Nya, Allah memberi petunjuk kepada mereka yang beriman tentang kebenaran yang mereka perselisihkan. Allah memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.'),
	(221, 2, 214, 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَّثَلُ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ ۗ مَسَّتْهُمُ الْبَأْسَاۤءُ وَالضَّرَّاۤءُ وَزُلْزِلُوْا حَتّٰى يَقُوْلَ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ مَتٰى نَصْرُ اللّٰهِ ۗ اَلَآ اِنّ', 'am <u>h</u>asibtum an tadkhuluu <strong>a</strong>ljannata walamm<u>aa</u> ya/tikum matsalu <strong>al</strong>la<u>dz</u>iina khalaw min qablikum massat-humu <strong>a</strong>lba/s<u>aa</u>u wa<strong>al</strong><u>dhdh</u>arr<u>aa</u>u wazulziluu <u>h</u>att<u>aa</u> yaquula <strong>al</strong>rrasuulu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu mat<u>aa</u> na<u>sh</u>ru <strong>al</strong>l<u>aa</u>hi <strong>a</strong>l<u>aa</u> inna na<u>sh</u>ra <strong>al</strong>l<u>aa</u>hi qariib<strong>un</strong>', 'Ataukah kamu mengira bahwa kamu akan masuk surga, padahal belum datang kepadamu (cobaan) seperti (yang dialami) orang-orang terdahulu sebelum kamu. Mereka ditimpa kemelaratan, penderitaan dan diguncang (dengan berbagai cobaan), sehingga Rasul dan orang-orang yang beriman bersamanya berkata, “Kapankah datang pertolongan Allah?” Ingatlah, sesungguhnya pertolongan Allah itu dekat.'),
	(222, 2, 215, 'يَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ۗ قُلْ مَآ اَنْفَقْتُمْ مِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ', 'yas-aluunaka m<u>aatsaa</u> yunfiquuna qul m<u>aa</u> anfaqtum min khayrin falilw<u>aa</u>lidayni wa<strong>a</strong>l-aqrabiina wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiini wa<strong>i</strong>bni <strong>al</strong>ssabiili wam<u>aa</u> taf\'aluu min khayrin fa-inna <strong>al</strong>l<u>aa</u>ha bihi \'aliim<strong>un</strong>', 'Mereka bertanya kepadamu (Muhammad) tentang apa yang harus mereka infakkan. Katakanlah, “Harta apa saja yang kamu infakkan, hendaknya diperuntukkan bagi kedua orang tua, kerabat, anak yatim, orang miskin dan orang yang dalam perjalanan.” Dan kebaikan apa saja yang kamu kerjakan, maka sesungguhnya Allah Maha Mengetahui.'),
	(223, 2, 216, 'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّهُوَ خَيْرٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تُحِبُّوْا شَيْـًٔا وَّهُوَ شَرٌّ لَّكُمْ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ࣖ', 'kutiba \'alaykumu <strong>a</strong>lqit<u>aa</u>lu wahuwa kurhun lakum wa\'as<u>aa</u> an takrahuu syay-an wahuwa khayrun lakum wa\'as<u>aa</u> an tu<u>h</u>ibbuu syay-an wahuwa syarrun lakum wa<strong>al</strong>l<u>aa</u>hu ya\'lamu wa-antum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Diwajibkan atas kamu berperang, padahal itu tidak menyenangkan bagimu. Tetapi boleh jadi kamu tidak menyenangi sesuatu, padahal itu baik bagimu, dan boleh jadi kamu menyukai sesuatu, padahal itu tidak baik bagimu. Allah mengetahui, sedang kamu tidak mengetahui.'),
	(224, 2, 217, 'يَسْـَٔلُوْنَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيْهِۗ  قُلْ قِتَالٌ فِيْهِ كَبِيْرٌ ۗ وَصَدٌّ عَنْ سَبِيْلِ اللّٰهِ وَكُفْرٌۢ بِهٖ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِهٖ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِ ۚ وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِ ۗ و', 'yas-aluunaka \'ani <strong>al</strong>sysyahri <strong>a</strong>l<u>h</u>ar<u>aa</u>mi qit<u>aa</u>lin fiihi qul qit<u>aa</u>lun fiihi kabiirun wa<u>sh</u>addun \'an sabiili <strong>al</strong>l<u>aa</u>hi wakufrun bihi wa<strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wa-ikhr<u>aa</u>ju ahlihi minhu akbaru \'inda <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lfitnatu akbaru mina <strong>a</strong>lqatli wal<u>aa</u> yaz<u>aa</u>luuna yuq<u>aa</u>tiluunakum <u>h</u>att<u>aa</u> yarudduukum \'an diinikum ini ista<u>th</u><u>aa</u>\'uu waman yartadid minkum \'an diinihi fayamut wahuwa k<u>aa</u>firun faul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Mereka bertanya kepadamu (Muhammad) tentang berperang pada bulan haram. Katakanlah, “Berperang dalam bulan itu adalah (dosa) besar. Tetapi menghalangi (orang) dari jalan Allah, ingkar kepada-Nya, (menghalangi orang masuk) Masjidilharam, dan mengusir penduduk dari sekitarnya, lebih besar (dosanya) dalam pandangan Allah. Sedangkan fitnah lebih kejam daripada pembunuhan. Mereka tidak akan berhenti memerangi kamu sampai kamu murtad (keluar) dari agamamu, jika mereka sanggup. Barangsiapa murtad di antara kamu dari agamanya, lalu dia mati dalam kekafiran, maka mereka itu sia-sia amalnya di dunia dan di akhirat, dan mereka itulah penghuni neraka, mereka kekal di dalamnya.”'),
	(225, 2, 218, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ ۙ اُولٰۤىِٕكَ يَرْجُوْنَ رَحْمَتَ اللّٰهِ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>jaruu waj<u>aa</u>haduu fii sabiili <strong>al</strong>l<u>aa</u>hi ul<u>aa</u>-ika yarjuuna ra<u>h</u>mata <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Sesungguhnya orang-orang yang beriman, dan orang-orang yang berhijrah dan berjihad di jalan Allah, mereka itulah yang mengharapkan rahmat Allah. Allah Maha Pengampun, Maha Penyayang.'),
	(226, 2, 219, '۞ يَسْـَٔلُوْنَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۗ قُلْ فِيْهِمَآ اِثْمٌ كَبِيْرٌ وَّمَنَافِعُ لِلنَّاسِۖ وَاِثْمُهُمَآ اَكْبَرُ مِنْ نَّفْعِهِمَاۗ وَيَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ەۗ قُلِ الْعَفْوَۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلّ', 'yas-aluunaka \'ani <strong>a</strong>lkhamri wa<strong>a</strong>lmaysiri qul fiihim<u>aa</u> itsmun kabiirun waman<u>aa</u>fi\'u li<strong>l</strong>nn<u>aa</u>si wa-itsmuhum<u>aa</u> akbaru min naf\'ihim<u>aa</u> wayas-aluunaka m<u>aatsaa</u> yunfiquuna quli <strong>a</strong>l\'afwa ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti la\'allakum tatafakkaruun<strong>a</strong>', 'Mereka menanyakan kepadamu (Muhammad) tentang khamar dan judi. Katakanlah, “Pada keduanya terdapat dosa besar dan beberapa manfaat bagi manusia. Tetapi dosanya lebih besar daripada manfaatnya.” Dan mereka menanyakan kepadamu (tentang) apa yang (harus) mereka infakkan. Katakanlah, “Kelebihan (dari apa yang diperlukan).” Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu agar kamu memikirkan,'),
	(227, 2, 220, 'فِى الدُّنْيَا وَالْاٰخِرَةِ ۗ وَيَسْـَٔلُوْنَكَ عَنِ الْيَتٰمٰىۗ قُلْ اِصْلَاحٌ لَّهُمْ خَيْرٌ ۗ وَاِنْ تُخَالِطُوْهُمْ فَاِخْوَانُكُمْ ۗ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَعْنَتَكُمْ اِنَّ اللّٰهَ عَزِيْزٌ حَكِيْ', 'fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wayas-aluunaka \'ani <strong>a</strong>lyat<u>aa</u>m<u>aa</u> qul i<u>sh</u>l<u>aah</u>un lahum khayrun wa-in tukh<u>aa</u>li<u>th</u>uuhum fa-ikhw<u>aa</u>nukum wa<strong>al</strong>l<u>aa</u>hu ya\'lamu <strong>a</strong>lmufsida mina <strong>a</strong>lmu<u>sh</u>li<u>h</u>i walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu la-a\'natakum inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Tentang dunia dan akhirat. Mereka menanyakan kepadamu (Muhammad) tentang anak-anak yatim. Katakanlah, “Memperbaiki keadaan mereka adalah baik!” Dan jika kamu mempergauli mereka, maka mereka adalah saudara-saudaramu. Allah mengetahui orang yang berbuat kerusakan dan yang berbuat kebaikan. Dan jika Allah menghendaki, niscaya Dia datangkan kesulitan kepadamu. Sungguh, Allah Mahaperkasa, Mahabijaksana.'),
	(228, 2, 221, 'وَلَا تَنْكِحُوا الْمُشْرِكٰتِ حَتّٰى يُؤْمِنَّ ۗ وَلَاَمَةٌ مُّؤْمِنَةٌ خَيْرٌ مِّنْ مُّشْرِكَةٍ وَّلَوْ اَعْجَبَتْكُمْ ۚ وَلَا تُنْكِحُوا الْمُشْرِكِيْنَ حَتّٰى يُؤْمِنُوْا ۗ وَلَعَبْدٌ مُّؤْمِنٌ خَيْرٌ مِّنْ مُّشْرِكٍ وَّلَوْ اَعْجَبَكُمْ ۗ اُولٰۤىِٕكَ', 'wal<u>aa</u> tanki<u>h</u>uu <strong>a</strong>lmusyrik<u>aa</u>ti <u>h</u>att<u>aa</u> yu/minna wala-amatun mu/minatun khayrun min musyrikatin walaw a\'jabatkum wal<u>aa</u> tunki<u>h</u>uu <strong>a</strong>lmusyrikiina <u>h</u>att<u>aa</u> yu/minuu wala\'abdun mu/minun khayrun min musyrikin walaw a\'jabakum ul<u>aa</u>-ika yad\'uuna il<u>aa</u> <strong>al</strong>nn<u>aa</u>ri wa<strong>al</strong>l<u>aa</u>hu yad\'uu il<u>aa</u> <strong>a</strong>ljannati wa<strong>a</strong>lmaghfirati bi-i<u>dz</u>nihi wayubayyinu <u>aa</u>y<u>aa</u>tihi li<strong>l</strong>nn<u>aa</u>si la\'allahum yata<u>dz</u>akkaruun<strong>a</strong>', 'Dan janganlah kamu nikahi perempuan musyrik, sebelum mereka beriman. Sungguh, hamba sahaya perempuan yang beriman lebih baik daripada perempuan musyrik meskipun dia menarik hatimu. Dan janganlah kamu nikahkan orang (laki-laki) musyrik (dengan perempuan yang beriman) sebelum mereka beriman. Sungguh, hamba sahaya laki-laki yang beriman lebih baik daripada laki-laki musyrik meskipun dia menarik hatimu. Mereka mengajak ke neraka, sedangkan Allah mengajak ke surga dan ampunan dengan izin-Nya. (Allah) menerangkan ayat-ayat-Nya kepada manusia agar mereka mengambil pelajaran.'),
	(229, 2, 222, 'وَيَسْـَٔلُوْنَكَ عَنِ الْمَحِيْضِ ۗ  قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَاۤءَ فِى الْمَحِيْضِۙ وَلَا تَقْرَبُوْهُنَّ حَتّٰى يَطْهُرْنَ ۚ فَاِذَا تَطَهَّرْنَ فَأْتُوْهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُ ۗ اِنَّ اللّٰهَ يُحِبُّ التَّوَّابِيْنَ وَيُحِبُّ ', 'wayas-aluunaka \'ani <strong>a</strong>lma<u>h</u>ii<u>dh</u>i qul huwa a<u>dz</u>an fa<strong>i</strong>\'taziluu <strong>al</strong>nnis<u>aa</u>-a fii <strong>a</strong>lma<u>h</u>ii<u>dh</u>i wal<u>aa</u> taqrabuuhunna <u>h</u>att<u>aa</u> ya<u>th</u>hurna fa-i<u>dzaa</u> ta<u>th</u>ahharna fa/tuuhunna min <u>h</u>aytsu amarakumu <strong>al</strong>l<u>aa</u>hu inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>al</strong>ttaww<u>aa</u>biina wayu<u>h</u>ibbu <strong>a</strong>lmuta<u>th</u>ahhiriin<strong>a</strong>', 'Dan mereka menanyakan kepadamu (Muhammad) tentang haid. Katakanlah, “Itu adalah sesuatu yang kotor.” Karena itu jauhilah istri pada waktu haid; dan jangan kamu dekati mereka sebelum mereka suci. Apabila mereka telah suci, campurilah mereka sesuai dengan (ketentuan) yang diperintahkan Allah kepadamu. Sungguh, Allah menyukai orang yang tobat dan menyukai orang yang menyucikan diri.'),
	(230, 2, 223, 'نِسَاۤؤُكُمْ حَرْثٌ لَّكُمْ ۖ فَأْتُوْا حَرْثَكُمْ اَنّٰى شِئْتُمْ ۖ وَقَدِّمُوْا لِاَنْفُسِكُمْ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ مُّلٰقُوْهُ ۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ', 'nis<u>aa</u>ukum <u>h</u>artsun lakum fa/tuu <u>h</u>artsakum ann<u>aa</u> syi/tum waqaddimuu li-anfusikum wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu annakum mul<u>aa</u>quuhu wabasysyiri <strong>a</strong>lmu/miniin<strong>a</strong>', 'Istri-istrimu adalah ladang bagimu, maka datangilah ladangmu itu kapan saja dan dengan cara yang kamu sukai. Dan utamakanlah (yang baik) untuk dirimu. Bertakwalah kepada Allah dan ketahuilah bahwa kamu (kelak) akan menemui-Nya. Dan sampaikanlah kabar gembira kepada orang yang beriman.'),
	(231, 2, 224, 'وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِّاَيْمَانِكُمْ اَنْ تَبَرُّوْا وَتَتَّقُوْا وَتُصْلِحُوْا بَيْنَ النَّاسِۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ', 'wal<u>aa</u> taj\'aluu <strong>al</strong>l<u>aa</u>ha \'ur<u>dh</u>atan li-aym<u>aa</u>nikum an tabarruu watattaquu watu<u>sh</u>li<u>h</u>uu bayna <strong>al</strong>nn<u>aa</u>si wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Dan janganlah kamu jadikan (nama) Allah dalam sumpahmu sebagai penghalang untuk berbuat kebajikan, bertakwa dan menciptakan kedamaian di antara manusia. Allah Maha Mendengar, Maha Mengetahui.'),
	(232, 2, 225, 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوْبُكُمْ ۗ وَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ', 'l<u>aa</u> yu-<u>aa</u>khi<u>dz</u>ukumu <strong>al</strong>l<u>aa</u>hu bi<strong>a</strong>llaghwi fii aym<u>aa</u>nikum wal<u>aa</u>kin yu-<u>aa</u>khi<u>dz</u>ukum bim<u>aa</u> kasabat quluubukum wa<strong>al</strong>l<u>aa</u>hu ghafuurun <u>h</u>aliim<strong>un</strong>', 'Allah tidak menghukum kamu karena sumpahmu yang tidak kamu sengaja, tetapi Dia menghukum kamu karena niat yang terkandung dalam hatimu. Allah Maha Pengampun, Maha Penyantun.'),
	(233, 2, 226, 'لِلَّذِيْنَ يُؤْلُوْنَ مِنْ نِّسَاۤىِٕهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَاۤءُوْ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'lilla<u>dz</u>iina yu/luuna min nis<u>aa</u>-ihim tarabbu<u>sh</u>u arba\'ati asyhurin fa-in f<u>aa</u>uu fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Bagi orang yang meng-ila\' istrinya harus menunggu empat bulan. Kemudian jika mereka kembali (kepada istrinya), maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(234, 2, 227, 'وَاِنْ عَزَمُوا الطَّلَاقَ فَاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ', 'wa-in \'azamuu <strong>al</strong><u>ththh</u>al<u>aa</u>qa fa-inna <strong>al</strong>l<u>aa</u>ha samii\'un \'aliim<strong>un</strong>', 'Dan jika mereka berketetapan hati hendak menceraikan, maka sungguh, Allah Maha Mendengar, Maha Mengetahui.'),
	(235, 2, 228, 'وَالْمُطَلَّقٰتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُوْۤءٍۗ وَلَا يَحِلُّ لَهُنَّ اَنْ يَّكْتُمْنَ مَا خَلَقَ اللّٰهُ فِيْٓ اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ وَبُعُوْلَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ فِيْ ذٰلِكَ اِنْ', 'wa<strong>a</strong>lmu<u>th</u>allaq<u>aa</u>tu yatarabba<u>sh</u>na bi-anfusihinna tsal<u>aa</u>tsata quruu-in wal<u>aa</u> ya<u>h</u>illu lahunna an yaktumna m<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu fii ar<u>haa</u>mihinna in kunna yu/minna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wabu\'uulatuhunna a<u>h</u>aqqu biraddihinna fii <u>dzaa</u>lika in ar<u>aa</u>duu i<u>sh</u>l<u>aah</u>an walahunna mitslu <strong>al</strong>la<u>dz</u>ii \'alayhinna bi<strong>a</strong>lma\'ruufi wali<strong>l</strong>rrij<u>aa</u>li \'alayhinna darajatun wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan para istri yang diceraikan (wajib) menahan diri mereka (menunggu) tiga kali quru\'. Tidak boleh bagi mereka menyembunyikan apa yang diciptakan Allah  dalam  rahim  mereka,  jika mereka beriman kepada Allah dan hari akhir. Dan para suami mereka lebih berhak kembali kepada mereka dalam (masa) itu, jika mereka menghendaki perbaikan. Dan mereka (para perempuan) mempunyai hak seimbang dengan kewajibannya menurut cara yang patut. Tetapi para suami mempunyai kelebihan di atas mereka. Allah Mahaperkasa, Mahabijaksana.'),
	(236, 2, 229, 'اَلطَّلَاقُ مَرَّتٰنِ ۖ فَاِمْسَاكٌۢ بِمَعْرُوْفٍ اَوْ تَسْرِيْحٌۢ بِاِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوْا مِمَّآ اٰتَيْتُمُوْهُنَّ شَيْـًٔا اِلَّآ اَنْ يَّخَافَآ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۗ فَاِنْ خِفْتُمْ اَلَّا يُقِيْمَا حُدُوْدَ', 'a<strong>l</strong><u>ththh</u>al<u>aa</u>qu marrat<u>aa</u>ni fa-ims<u>aa</u>kun bima\'ruufin aw tasrii<u>h</u>un bi-i<u>h</u>s<u>aa</u>nin wal<u>aa</u> ya<u>h</u>illu lakum an ta/khu<u>dz</u>uu mimm<u>aa</u> <u>aa</u>taytumuuhunna syay-an ill<u>aa</u> an yakh<u>aa</u>f<u>aa</u> <strong>al</strong>l<u>aa</u> yuqiim<u>aa</u> <u>h</u>uduuda <strong>al</strong>l<u>aa</u>hi fa-in khiftum <strong>al</strong>l<u>aa</u> yuqiim<u>aa</u> <u>h</u>uduuda <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> jun<u>aah</u>a \'alayhim<u>aa</u> fiim<u>aa</u> iftadat bihi tilka <u>h</u>uduudu <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> ta\'taduuh<u>aa</u> waman yata\'adda <u>h</u>uduuda <strong>al</strong>l<u>aa</u>hi faul<u>aa</u>-ika humu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Talak (yang dapat dirujuk) itu dua kali. (Setelah itu suami dapat) menahan dengan baik, atau melepaskan dengan baik. Tidak halal bagi kamu mengambil kembali sesuatu yang telah kamu berikan kepada mereka, kecuali keduanya (suami dan istri) khawatir tidak mampu menjalankan hukum-hukum Allah. Jika kamu (wali) khawatir bahwa keduanya tidak mampu menjalankan hukum-hukum Allah, maka keduanya tidak berdosa atas bayaran yang (harus) diberikan (oleh istri) untuk menebus dirinya. Itulah hukum-hukum Allah, maka janganlah kamu melanggarnya. Barangsiapa melanggar hukum-hukum Allah, mereka itulah orang-orang zalim.'),
	(237, 2, 230, 'فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهٗ مِنْۢ بَعْدُ حَتّٰى تَنْكِحَ زَوْجًا غَيْرَهٗ ۗ فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يَّتَرَاجَعَآ اِنْ ظَنَّآ اَنْ يُّقِيْمَا حُدُوْدَ اللّٰهِ ۗ وَتِلْكَ حُدُوْدُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَّعْلَمُ', 'fa-in <u>th</u>allaqah<u>aa</u> fal<u>aa</u> ta<u>h</u>illu lahu min ba\'du <u>h</u>att<u>aa</u> tanki<u>h</u>a zawjan ghayrahu fa-in <u>th</u>allaqah<u>aa</u> fal<u>aa</u> jun<u>aah</u>a \'alayhim<u>aa</u> an yatar<u>aa</u>ja\'<u>aa</u> in <u>zh</u>ann<u>aa</u> an yuqiim<u>aa</u> <u>h</u>uduuda <strong>al</strong>l<u>aa</u>hi watilka <u>h</u>uduudu <strong>al</strong>l<u>aa</u>hi yubayyinuh<u>aa</u> liqawmin ya\'lamuun<strong>a</strong>', 'Kemudian jika dia menceraikannya (setelah talak yang kedua), maka perempuan itu tidak halal lagi baginya sebelum dia menikah dengan suami yang lain. Kemudian jika suami yang lain itu menceraikannya, maka tidak ada dosa bagi keduanya (suami pertama dan bekas istri) untuk menikah kembali jika keduanya berpendapat akan dapat menjalankan hukum-hukum Allah. Itulah ketentuan-ketentuan Allah yang diterangkan-Nya kepada orang-orang yang berpengetahuan.'),
	(238, 2, 231, 'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ سَرِّحُوْهُنَّ بِمَعْرُوْفٍۗ وَلَا تُمْسِكُوْهُنَّ ضِرَارًا لِّتَعْتَدُوْا ۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ وَلَا تَتَّخِذُوْٓا اٰيٰتِ اللّٰهِ هُز', 'wa-i<u>dzaa</u> <u>th</u>allaqtumu <strong>al</strong>nnis<u>aa</u>-a fabalaghna ajalahunna fa-amsikuuhunna bima\'ruufin aw sarri<u>h</u>uuhunna bima\'ruufin wal<u>aa</u> tumsikuuhunna <u>dh</u>ir<u>aa</u>ran lita\'taduu waman yaf\'al <u>dzaa</u>lika faqad <u>zh</u>alama nafsahu wal<u>aa</u> tattakhi<u>dz</u>uu <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi huzuwan wa<strong>u</strong><u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum wam<u>aa</u> anzala \'alaykum mina <strong>a</strong>lkit<u>aa</u>bi wa<strong>a</strong>l<u>h</u>ikmati ya\'i<u>zh</u>ukum bihi wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha bikulli syay-in \'aliim<strong>un</strong>', 'Dan apabila kamu menceraikan istri-istri (kamu), lalu sampai (akhir) idahnya, maka tahanlah mereka dengan cara yang baik, atau ceraikanlah mereka dengan cara yang baik (pula). Dan janganlah kamu tahan mereka dengan maksud jahat untuk menzalimi mereka. Barangsiapa melakukan demikian, maka dia telah menzalimi dirinya sendiri. Dan janganlah kamu jadikan ayat-ayat Allah sebagai bahan ejekan. Ingatlah nikmat Allah kepada kamu, dan apa yang telah diturunkan Allah kepada kamu yaitu Kitab (Al-Qur\'an) dan Hikmah (Sunnah), untuk memberi pengajaran kepadamu. Dan bertakwalah kepada Allah dan ketahuilah bahwa Allah Maha Mengetahui segala sesuatu.'),
	(239, 2, 232, 'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوْهُنَّ اَنْ يَّنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوْفِ ۗ ذٰلِكَ يُوْعَظُ بِهٖ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ۗ ذٰلِكُمْ اَزْكٰى', 'wa-i<u>dzaa</u> <u>th</u>allaqtumu <strong>al</strong>nnis<u>aa</u>-a fabalaghna ajalahunna fal<u>aa</u> ta\'<u>dh</u>uluuhunna an yanki<u>h</u>na azw<u>aa</u>jahunna i<u>dzaa</u> tar<u>aad</u>aw baynahum bi<strong>a</strong>lma\'ruufi <u>dzaa</u>lika yuu\'a<u>zh</u>u bihi man k<u>aa</u>na minkum yu/minu bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri <u>dzaa</u>likum azk<u>aa</u> lakum wa-a<u>th</u>haru wa<strong>al</strong>l<u>aa</u>hu ya\'lamu wa-antum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan apabila kamu menceraikan istri-istri (kamu), lalu sampai idahnya, maka jangan kamu halangi mereka menikah (lagi) dengan calon suaminya, apabila telah terjalin kecocokan di antara mereka dengan cara yang baik. Itulah yang dinasihatkan kepada orang-orang di antara kamu yang beriman kepada Allah dan hari akhir. Itu lebih suci bagimu dan lebih bersih. Dan Allah mengetahui, sedangkan kamu tidak mengetahui.'),
	(240, 2, 233, '۞ وَالْوَالِدٰتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُّتِمَّ الرَّضَاعَةَ ۗ وَعَلَى الْمَوْلُوْدِ لَهٗ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوْفِۗ  لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَا ۚ لَا تُضَاۤرَّ وَالِدَةٌ ۢبِوَلَد', 'wa<strong>a</strong>lw<u>aa</u>lid<u>aa</u>tu yur<u>dh</u>i\'na awl<u>aa</u>dahunna <u>h</u>awlayni k<u>aa</u>milayni liman ar<u>aa</u>da an yutimma <strong>al</strong>rra<u>daa</u>\'ata wa\'al<u>aa</u> <strong>a</strong>lmawluudi lahu rizquhunna wakiswatuhunna bi<strong>a</strong>lma\'ruufi l<u>aa</u> tukallafu nafsun ill<u>aa</u> wus\'ah<u>aa</u> l<u>aa</u> tu<u>daa</u>rra w<u>aa</u>lidatun biwaladih<u>aa</u> wal<u>aa</u> mawluudun lahu biwaladihi wa\'al<u>aa</u> <strong>a</strong>lw<u>aa</u>ritsi mitslu <u>dzaa</u>lika fa-in ar<u>aa</u>d<u>aa</u> fi<u>shaa</u>lan \'an tar<u>aad</u>in minhum<u>aa</u> watasy<u>aa</u>wurin fal<u>aa</u> jun<u>aah</u>a \'alayhim<u>aa</u> wa-in aradtum an tastar<u>dh</u>i\'uu awl<u>aa</u>dakum fal<u>aa</u> jun<u>aah</u>a \'alaykum i<u>dzaa</u> sallamtum m<u>aa</u> <u>aa</u>taytum bi<strong>a</strong>lma\'ruufi wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Dan ibu-ibu hendaklah menyusui anak-anaknya selama dua tahun penuh, bagi yang ingin menyusui secara sempurna. Dan kewajiban ayah menanggung nafkah dan pakaian mereka dengan cara yang patut. Seseorang tidak dibebani lebih dari kesanggupannya. Janganlah seorang ibu menderita karena anaknya dan jangan pula seorang ayah (menderita) karena anaknya. Ahli waris pun (berkewajiban) seperti itu pula. Apabila keduanya ingin menyapih dengan persetujuan dan permusyawaratan antara keduanya, maka tidak ada dosa atas keduanya. Dan jika kamu ingin menyusukan anakmu kepada orang lain, maka tidak ada dosa bagimu memberikan pembayaran dengan cara yang patut. Bertakwalah kepada Allah dan ketahuilah bahwa Allah Maha Melihat apa yang kamu kerjakan.'),
	(241, 2, 234, 'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًا يَّتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَّعَشْرًا ۚ فاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ بِالْمَعْرُوْفِۗ وَاللّٰهُ بِمَا تَعْمَلُ', 'wa<strong>a</strong>lla<u>dz</u>iina yutawaffawna minkum waya<u>dz</u>aruuna azw<u>aa</u>jan yatarabba<u>sh</u>na bi-anfusihinna arba\'ata asyhurin wa\'asyran fa-i<u>dzaa</u> balaghna ajalahunna fal<u>aa</u> jun<u>aah</u>a \'alaykum fiim<u>aa</u> fa\'alna fii anfusihinna bi<strong>a</strong>lma\'ruufi wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir<strong>un</strong>', 'Dan orang-orang yang mati di antara kamu serta meninggalkan istri-istri hendaklah mereka (istri-istri) menunggu empat bulan sepuluh hari. Kemudian apabila telah sampai (akhir) idah mereka, maka tidak ada dosa bagimu mengenai apa yang mereka lakukan terhadap diri mereka menurut cara yang patut. Dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
	(242, 2, 235, 'وَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا عَرَّضْتُمْ بِهٖ مِنْ خِطْبَةِ النِّسَاۤءِ اَوْ اَكْنَنْتُمْ فِيْٓ اَنْفُسِكُمْ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُوْنَهُنَّ وَلٰكِنْ لَّا تُوَاعِدُوْهُنَّ سِرًّا اِلَّآ اَنْ تَقُوْلُوْا قَوْلًا مَّعْرُوْفًا ەۗ وَلَا تَ', 'wal<u>aa</u> jun<u>aah</u>a \'alaykum fiim<u>aa</u> \'arra<u>dh</u>tum bihi min khi<u>th</u>bati <strong>al</strong>nnis<u>aa</u>-i aw aknantum fii anfusikum \'alima <strong>al</strong>l<u>aa</u>hu annakum sata<u>dz</u>kuruunahunna wal<u>aa</u>kin l<u>aa</u> tuw<u>aa</u>\'iduuhunna sirran ill<u>aa</u> an taquuluu qawlan ma\'ruufan wal<u>aa</u> ta\'zimuu \'uqdata <strong>al</strong>nnik<u>aah</u>i <u>h</u>att<u>aa</u> yablugha <strong>a</strong>lkit<u>aa</u>bu ajalahu wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> fii anfusikum fa<strong>i</strong><u>hts</u>aruuhu wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ghafuurun <u>h</u>aliim<strong>un</strong>', 'Dan tidak ada dosa bagimu meminang perempuan-perempuan itu dengan sindiran atau kamu sembunyikan (keinginanmu) dalam hati. Allah mengetahui bahwa kamu akan menyebut-nyebut kepada mereka. Tetapi janganlah kamu membuat perjanjian (untuk menikah) dengan mereka secara rahasia, kecuali sekedar mengucapkan kata-kata yang baik. Dan janganlah kamu menetapkan akad nikah, sebelum habis masa idahnya. Ketahuilah bahwa Allah mengetahui apa yang ada dalam hatimu, maka takutlah kepada-Nya. Dan ketahuilah bahwa Allah Maha Pengampun, Maha Penyantun.'),
	(243, 2, 236, 'لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَاۤءَ مَا لَمْ تَمَسُّوْهُنَّ اَوْ تَفْرِضُوْا لَهُنَّ فَرِيْضَةً ۖ وَّمَتِّعُوْهُنَّ عَلَى الْمُوْسِعِ قَدَرُهٗ وَعَلَى الْمُقْتِرِ قَدَرُهٗ ۚ مَتَاعًا ۢبِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُحْسِنِيْنَ ', 'l<u>aa</u> jun<u>aah</u>a \'alaykum in <u>th</u>allaqtumu <strong>al</strong>nnis<u>aa</u>-a m<u>aa</u> lam tamassuuhunna aw tafri<u>dh</u>uu lahunna farii<u>dh</u>atan wamatti\'uuhunna \'al<u>aa</u> <strong>a</strong>lmuusi\'i qadaruhu wa\'al<u>aa</u> <strong>a</strong>lmuqtiri qadaruhu mat<u>aa</u>\'an bi<strong>a</strong>lma\'ruufi <u>h</u>aqqan \'al<u>aa</u> <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Tidak ada dosa bagimu, jika kamu menceraikan istri-istri kamu yang belum kamu sentuh (campuri) atau belum kamu tentukan maharnya. Dan hendaklah kamu beri mereka mut‘ah, bagi yang mampu menurut kemampuannya dan bagi yang tidak mampu menurut kesanggupannya, yaitu pemberian dengan cara yang patut, yang merupakan kewajiban bagi orang-orang yang berbuat kebaikan.'),
	(244, 2, 237, 'وَاِنْ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيْضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّآ اَنْ يَّعْفُوْنَ اَوْ يَعْفُوَا الَّذِيْ بِيَدِهٖ عُقْدَةُ النِّكَاحِ ۗ وَاَنْ تَعْفُوْٓا اَقْرَبُ لِلتَّقْوٰىۗ وَلَا تَنْسَوُا ', 'wa-in <u>th</u>allaqtumuuhunna min qabli an tamassuuhunna waqad fara<u>dh</u>tum lahunna farii<u>dh</u>atan fani<u>sh</u>fu m<u>aa</u> fara<u>dh</u>tum ill<u>aa</u> an ya\'fuuna aw ya\'fuwa <strong>al</strong>la<u>dz</u>ii biyadihi \'uqdatu <strong>al</strong>nnik<u>aah</u>i wa-an ta\'fuu aqrabu li<strong>l</strong>ttaqw<u>aa</u> wal<u>aa</u> tansawuu <strong>a</strong>lfa<u>dh</u>la baynakum inna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un </strong>', 'Dan jika kamu menceraikan mereka sebelum kamu sentuh (campuri), padahal kamu sudah menentukan Maharnya, maka (bayarlah) seperdua dari yang telah kamu tentukan, kecuali jika mereka (membebaskan) atau dibebaskan oleh orang yang akad nikah ada di tangannya. Pembebasan itu lebih dekat kepada takwa. Dan janganlah kamu lupa kebaikan di antara kamu. Sungguh, Allah Maha Melihat apa yang kamu kerjakan.'),
	(245, 2, 238, 'حَافِظُوْا عَلَى الصَّلَوٰتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُوْمُوْا لِلّٰهِ قٰنِتِيْنَ ', '<u>haa</u>fi<u>zh</u>uu \'al<u>aa</u> <strong>al</strong><u>shsh</u>alaw<u>aa</u>ti wa<strong>al</strong><u>shsh</u>al<u>aa</u>ti <strong>a</strong>lwus<u>th</u><u>aa</u> waquumuu lill<u>aa</u>hi q<u>aa</u>nitiin<strong>a</strong>', 'Peliharalah semua salat dan salat wustha. Dan laksanakanlah (salat) karena Allah dengan khusyuk.'),
	(246, 2, 239, 'فَاِنْ خِفْتُمْ فَرِجَالًا اَوْ رُكْبَانًا ۚ فَاِذَآ اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَ ', 'fa-in khiftum farij<u>aa</u>lan aw rukb<u>aa</u>nan fa-i<u>dzaa</u> amintum fa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha kam<u>aa</u> \'allamakum m<u>aa</u> lam takuunuu ta\'lamuun<strong>a</strong>', 'Jika kamu takut (ada bahaya), salatlah sambil berjalan kaki atau berkendaraan. Kemudian apabila telah aman, maka ingatlah Allah (salatlah), sebagaimana Dia telah mengajarkan kepadamu apa yang tidak kamu ketahui.'),
	(247, 2, 240, 'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًاۖ وَّصِيَّةً لِّاَزْوَاجِهِمْ مَّتَاعًا اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍ ۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِيْ مَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ مِنْ مَّعْرُوْفٍۗ وَاللّٰهُ عَزِيْزٌ حَكِي', 'wa<strong>a</strong>lla<u>dz</u>iina yutawaffawna minkum waya<u>dz</u>aruuna azw<u>aa</u>jan wa<u>sh</u>iyyatan li-azw<u>aa</u>jihim mat<u>aa</u>\'an il<u>aa</u> <strong>a</strong>l<u>h</u>awli ghayra ikhr<u>aa</u>jin fa-in kharajna fal<u>aa</u> jun<u>aah</u>a \'alaykum fii m<u>aa</u> fa\'alna fii anfusihinna min ma\'ruufin wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan orang-orang yang akan mati di antara kamu dan meninggalkan istri-istri, hendaklah membuat wasiat untuk istri-istrinya, (yaitu) nafkah sampai setahun tanpa mengeluarkannya (dari rumah). Tetapi jika mereka keluar (sendiri), maka tidak ada dosa bagimu (mengenai apa) yang mereka lakukan terhadap diri mereka sendiri dalam hal-hal yang baik. Allah Mahaperkasa, Mahabijaksana.'),
	(248, 2, 241, 'وَلِلْمُطَلَّقٰتِ مَتَاعٌ ۢبِالْمَعْرُوْفِۗ حَقًّا عَلَى الْمُتَّقِيْنَ ', 'walilmu<u>th</u>allaq<u>aa</u>ti mat<u>aa</u>\'un bi<strong>a</strong>lma\'ruufi <u>h</u>aqqan \'al<u>aa</u> <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Dan bagi perempuan-perempuan yang diceraikan hendaklah diberi mut‘ah menurut cara yang patut, sebagai suatu kewajiban bagi orang yang bertakwa.'),
	(249, 2, 242, 'كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ ࣖ', 'ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi la\'allakum ta\'qiluun<strong>a</strong>', 'Demikianlah Allah menerangkan kepadamu ayat-ayat-Nya agar kamu mengerti.'),
	(250, 2, 243, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ وَهُمْ اُلُوْفٌ حَذَرَ الْمَوْتِۖ  فَقَالَ لَهُمُ اللّٰهُ مُوْتُوْا ۗ ثُمَّ اَحْيَاهُمْ ۗ اِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina kharajuu min diy<u>aa</u>rihim wahum uluufun <u>h</u>a<u>dz</u>ara <strong>a</strong>lmawti faq<u>aa</u>la lahumu <strong>al</strong>l<u>aa</u>hu muutuu tsumma a<u>h</u>y<u>aa</u>hum inna <strong>al</strong>l<u>aa</u>ha la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> yasykuruun<strong>a</strong>', 'Tidakkah kamu memperhatikan orang-orang yang keluar dari kampung halamannya, sedang jumlahnya ribuan karena takut mati? Lalu Allah berfirman kepada mereka, “Matilah kamu!” Kemudian Allah menghidupkan mereka. Sesungguhnya Allah memberikan karunia kepada manusia, tetapi kebanyakan manusia tidak bersyukur.'),
	(251, 2, 244, 'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ وَاعْلَمُوْٓا اَنَّ اللّٰهَ  سَمِيْعٌ عَلِيْمٌ ', 'waq<u>aa</u>tiluu fii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha samii\'un \'aliim<strong>un</strong>', 'Dan berperanglah kamu di jalan Allah, dan ketahuilah bahwa Allah Maha Mendengar, Maha Mengetahui.'),
	(252, 2, 245, 'مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗٓ اَضْعَافًا كَثِيْرَةً ۗوَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُوْنَ', 'man <u>dzaa</u> <strong>al</strong>la<u>dz</u>ii yuqri<u>dh</u>u <strong>al</strong>l<u>aa</u>ha qar<u>dh</u>an <u>h</u>asanan fayu<u>daa</u>\'ifahu lahu a<u>dh</u>\'<u>aa</u>fan katsiiratan wa<strong>al</strong>l<u>aa</u>hu yaqbi<u>dh</u>u wayabsu<u>th</u>u wa-ilayhi turja\'uun<strong>a</strong>', 'Barangsiapa meminjami Allah dengan pinjaman yang baik maka Allah melipatgandakan ganti kepadanya dengan banyak. Allah menahan dan melapangkan (rezeki) dan kepada-Nyalah kamu dikembalikan.'),
	(253, 2, 246, 'اَلَمْ تَرَ اِلَى الْمَلَاِ مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ مِنْۢ بَعْدِ مُوْسٰىۘ اِذْ قَالُوْا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۗ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُوْا ۗ قَالُوْا ', 'alam tara il<u>aa</u> <strong>a</strong>lmala-i min banii isr<u>aa</u>-iila min ba\'di muus<u>aa</u> i<u>dz</u> q<u>aa</u>luu linabiyyin lahumu ib\'ats lan<u>aa</u> malikan nuq<u>aa</u>til fii sabiili <strong>al</strong>l<u>aa</u>hi q<u>aa</u>la hal \'asaytum in kutiba \'alaykumu <strong>a</strong>lqit<u>aa</u>lu <strong>al</strong>l<u>aa</u> tuq<u>aa</u>tiluu q<u>aa</u>luu wam<u>aa</u> lan<u>aa</u> <strong>al</strong>l<u>aa</u> nuq<u>aa</u>tila fii sabiili <strong>al</strong>l<u>aa</u>hi waqad ukhrijn<u>aa</u> min diy<u>aa</u>rin<u>aa</u> wa-abn<u>aa</u>-in<u>aa</u> falamm<u>aa</u> kutiba \'alayhimu <strong>a</strong>lqit<u>aa</u>lu tawallaw ill<u>aa</u> qaliilan minhum wa<strong>al</strong>l<u>aa</u>hu \'aliimun bi<strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Tidakkah kamu perhatikan para pemuka Bani Israil setelah Musa wafat, ketika mereka berkata kepada seorang nabi mereka, “Angkatlah seorang raja untuk kami, niscaya kami berperang di jalan Allah.” Nabi mereka menjawab, “Jangan-jangan jika diwajibkan atasmu berperang, kamu tidak akan berperang juga?” Mereka menjawab, “Mengapa kami tidak akan berperang di jalan Allah, sedangkan kami telah diusir dari kampung halaman kami dan (dipisahkan dari) anak-anak kami?” Tetapi ketika perang itu diwajibkan atas mereka, mereka berpaling, kecuali sebagian kecil dari mereka. Dan Allah Maha Mengetahui orang-orang yang zalim.'),
	(254, 2, 247, 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوْتَ مَلِكًا ۗ قَالُوْٓا اَنّٰى يَكُوْنُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِّنَ الْمَالِۗ قَالَ اِنَّ اللّٰهَ اصْطَفٰىهُ عَلَيْكُمْ وَزَادَهٗ', 'waq<u>aa</u>la lahum nabiyyuhum inna <strong>al</strong>l<u>aa</u>ha qad ba\'atsa lakum <u>th</u><u>aa</u>luuta malikan q<u>aa</u>luu ann<u>aa</u> yakuunu lahu <strong>a</strong>lmulku \'alayn<u>aa</u> wana<u>h</u>nu a<u>h</u>aqqu bi<strong>a</strong>lmulki minhu walam yu/ta sa\'atan mina <strong>a</strong>lm<u>aa</u>li q<u>aa</u>la inna <strong>al</strong>l<u>aa</u>ha i<u>sth</u>af<u>aa</u>hu \'alaykum waz<u>aa</u>dahu bas<u>th</u>atan fii <strong>a</strong>l\'ilmi wa<strong>a</strong>ljismi wa<strong>al</strong>l<u>aa</u>hu yu/tii mulkahu man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'un \'aliim<strong>un</strong>', 'Dan nabi mereka berkata kepada mereka, “Sesungguhnya Allah telah mengangkat Talut menjadi rajamu.” Mereka menjawab, “Bagaimana Talut memperoleh kerajaan atas kami, sedangkan kami lebih berhak atas kerajaan itu daripadanya, dan dia tidak diberi kekayaan yang banyak?” (Nabi) menjawab, “Allah telah memilihnya (menjadi raja) kamu dan memberikan kelebihan ilmu dan fisik.” Allah memberikan kerajaan-Nya kepada siapa yang Dia kehendaki, dan Allah Mahaluas, Maha Mengetahui.'),
	(255, 2, 248, 'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِهٖٓ اَنْ يَّأْتِيَكُمُ التَّابُوْتُ فِيْهِ سَكِيْنَةٌ مِّنْ رَّبِّكُمْ وَبَقِيَّةٌ مِّمَّا تَرَكَ اٰلُ مُوْسٰى وَاٰلُ هٰرُوْنَ تَحْمِلُهُ الْمَلٰۤىِٕكَةُ ۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤ', 'waq<u>aa</u>la lahum nabiyyuhum inna <u>aa</u>yata mulkihi an ya/tiyakumu <strong>al</strong>tt<u>aa</u>buutu fiihi sakiinatun min rabbikum wabaqiyyatun mimm<u>aa</u> taraka <u>aa</u>lu muus<u>aa</u> wa<u>aa</u>lu h<u>aa</u>ruuna ta<u>h</u>miluhu <strong>a</strong>lmal<u>aa</u>-ikatu inna fii <u>dzaa</u>lika la<u>aa</u>yatan lakum in kuntum mu/miniin<strong>a</strong>', 'Dan nabi mereka berkata kepada mereka, “Sesungguhnya tanda kerajaannya ialah datangnya Tabut kepadamu, yang di dalamnya terdapat ketenangan dari Tuhanmu dan sisa peninggalan keluarga Musa dan keluarga Harun, yang dibawa oleh malaikat. Sungguh, pada yang demikian itu terdapat tanda (kebesaran Allah) bagimu, jika kamu orang beriman.'),
	(256, 2, 249, 'فَلَمَّا فَصَلَ طَالُوْتُ بِالْجُنُوْدِ قَالَ اِنَّ اللّٰهَ مُبْتَلِيْكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّيْۚ وَمَنْ لَّمْ يَطْعَمْهُ فَاِنَّهٗ مِنِّيْٓ اِلَّا مَنِ اغْتَرَفَ غُرْفَةً ۢبِيَدِهٖ ۚ فَشَرِبُوْا مِنْهُ اِلَّا قَلِيْلًا مِّنْهُمْ', 'falamm<u>aa</u> fa<u>sh</u>ala <u>th</u><u>aa</u>luutu bi<strong>a</strong>ljunuudi q<u>aa</u>la inna <strong>al</strong>l<u>aa</u>ha mubtaliikum binaharin faman syariba minhu falaysa minnii waman lam ya<u>th</u>\'amhu fa-innahu minnii ill<u>aa</u> mani ightarafa ghurfatan biyadihi fasyaribuu minhu ill<u>aa</u> qaliilan minhum falamm<u>aa</u> j<u>aa</u>wazahu huwa wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu q<u>aa</u>luu l<u>aa</u> <u>th</u><u>aa</u>qata lan<u>aa</u> <strong>a</strong>lyawma bij<u>aa</u>luuta wajunuudihi q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina ya<u>zh</u>unnuuna annahum mul<u>aa</u>quu <strong>al</strong>l<u>aa</u>hi kam min fi-atin qaliilatin ghalabat fi-atan katsiiratan bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu ma\'a <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Maka ketika Talut membawa bala tentaranya, dia berkata, “Allah akan menguji kamu dengan sebuah sungai. Maka barangsiapa meminum (airnya), dia bukanlah pengikutku. Dan barangsiapa tidak meminumnya, maka dia adalah pengikutku kecuali menciduk seciduk dengan tangan.” Tetapi mereka meminumnya kecuali sebagian kecil di antara mereka. Ketika dia (Talut) dan orang-orang yang beriman bersamanya menyeberangi sungai itu, mereka berkata, “Kami tidak kuat lagi pada hari ini melawan Jalut dan bala tentaranya.” Mereka yang meyakini bahwa mereka akan menemui Allah berkata, “Betapa banyak kelompok kecil mengalahkan kelompok besar dengan izin Allah.” Dan Allah beserta orang-orang yang sabar.'),
	(257, 2, 250, 'وَلَمَّا بَرَزُوْا لِجَالُوْتَ وَجُنُوْدِهٖ قَالُوْا رَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ۗ ', 'walamm<u>aa</u> barazuu lij<u>aa</u>luuta wajunuudihi q<u>aa</u>luu rabban<u>aa</u> afrigh \'alayn<u>aa</u> <u>sh</u>abran watsabbit aqd<u>aa</u>man<u>aa</u> wa<strong>u</strong>n<u>sh</u>urn<u>aa</u> \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan ketika mereka maju melawan Jalut dan tentaranya, mereka berdoa, “Ya Tuhan kami, limpahkanlah kesabaran kepada kami, kukuhkanlah langkah kami dan tolonglah kami menghadapi orang-orang kafir.”'),
	(258, 2, 251, 'فَهَزَمُوْهُمْ بِاِذْنِ اللّٰهِ  ۗوَقَتَلَ دَاوٗدُ جَالُوْتَ وَاٰتٰىهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهٗ مِمَّا يَشَاۤءُ ۗ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوْ فَضْلٍ عَلَى الْعٰلَمِي', 'fahazamuuhum bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi waqatala d<u>aa</u>wuudu j<u>aa</u>luuta wa<u>aa</u>t<u>aa</u>hu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmulka wa<strong>a</strong>l<u>h</u>ikmata wa\'allamahu mimm<u>aa</u> yasy<u>aa</u>u walawl<u>aa</u> daf\'u <strong>al</strong>l<u>aa</u>hi <strong>al</strong>nn<u>aa</u>sa ba\'<u>dh</u>ahum biba\'<u>dh</u>in lafasadati <strong>a</strong>l-ar<u>dh</u>u wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha <u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Maka mereka mengalahkannya dengan izin Allah, dan Dawud membunuh Jalut. Kemudian Allah memberinya (Dawud) kerajaan, dan hikmah, dan mengajarinya apa yang Dia kehendaki. Dan kalau Allah tidak melindungi sebagian manusia dengan sebagian yang lain, niscaya rusaklah bumi ini. Tetapi Allah mempunyai karunia (yang dilimpahkan-Nya) atas seluruh alam.'),
	(259, 2, 252, 'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَاِنَّكَ لَمِنَ الْمُرْسَلِيْنَ  ۔', 'tilka <u>aa</u>y<u>aa</u>tu <strong>al</strong>l<u>aa</u>hi natluuh<u>aa</u> \'alayka bi<strong>a</strong>l<u>h</u>aqqi wa-innaka lamina <strong>a</strong>lmursaliin<strong>a</strong>', 'Itulah ayat-ayat Allah, Kami bacakan kepadamu dengan benar dan engkau (Muhammad) adalah benar-benar seorang rasul.'),
	(260, 2, 253, 'تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۘ مِنْهُمْ مَّنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجٰتٍۗ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ وَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلَ الَّذِيْنَ مِنْۢ بَعْدِ', 'tilka <strong>al</strong>rrusulu fa<u>dhdh</u>aln<u>aa</u> ba\'<u>dh</u>ahum \'al<u>aa</u> ba\'<u>dh</u>in minhum man kallama <strong>al</strong>l<u>aa</u>hu warafa\'a ba\'<u>dh</u>ahum daraj<u>aa</u>tin wa<u>aa</u>tayn<u>aa</u> \'iis<u>aa</u> ibna maryama <strong>a</strong>lbayyin<u>aa</u>ti wa-ayyadn<u>aa</u>hu biruu<u>h</u>i <strong>a</strong>lqudusi walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> iqtatala <strong>al</strong>la<u>dz</u>iina min ba\'dihim min ba\'di m<u>aa</u> j<u>aa</u>-at-humu <strong>a</strong>lbayyin<u>aa</u>tu wal<u>aa</u>kini ikhtalafuu faminhum man <u>aa</u>mana waminhum man kafara walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> iqtataluu wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yaf\'alu m<u>aa</u> yuriid<strong>u</strong>', 'Rasul-rasul itu Kami lebihkan sebagian mereka dari sebagian yang lain. Di antara mereka ada yang (langsung) Allah berfirman dengannya dan sebagian lagi ada yang ditinggikan-Nya beberapa derajat. Dan Kami beri Isa putra Maryam beberapa mukjizat dan Kami perkuat dia dengan Rohulkudus. Kalau Allah menghendaki, niscaya orang-orang setelah mereka tidak akan berbunuh-bunuhan, setelah bukti-bukti sampai kepada mereka. Tetapi mereka berselisih, maka ada di antara mereka yang beriman dan ada (pula) yang kafir. Kalau Allah menghendaki, tidaklah mereka berbunuh-bunuhan. Tetapi Allah berbuat menurut kehendak-Nya.'),
	(261, 2, 254, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِمَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خُلَّةٌ وَّلَا شَفَاعَةٌ  ۗوَالْكٰفِرُوْنَ هُمُ الظّٰلِمُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu anfiquu mimm<u>aa</u> razaqn<u>aa</u>kum min qabli an ya/tiya yawmun l<u>aa</u> bay\'un fiihi wal<u>aa</u> khullatun wal<u>aa</u> syaf<u>aa</u>\'atun wa<strong>a</strong>lk<u>aa</u>firuuna humu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Wahai orang-orang yang beriman! Infakkanlah sebagian dari rezeki yang telah Kami berikan kepadamu sebelum datang hari ketika tidak ada lagi jual beli, tidak ada lagi persahabatan dan tidak ada lagi syafaat. Orang-orang kafir itulah orang yang zalim.'),
	(262, 2, 255, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّوْمُ ەۚ لَا تَأْخُذُهٗ سِنَةٌ وَّلَا نَوْمٌۗ  لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَنْ ذَا الَّذِيْ يَشْفَعُ عِنْدَهٗٓ اِلَّا بِاِذْنِهٖۗ يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۚ وَلَ', '<strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa <strong>a</strong>l<u>h</u>ayyu <strong>a</strong>lqayyuumu l<u>aa</u> ta/khu<u>dz</u>uhu sinatun wal<u>aa</u> nawmun lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i man <u>dzaa</u> <strong>al</strong>la<u>dz</u>ii yasyfa\'u \'indahu ill<u>aa</u> bi-i<u>dz</u>nihi ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wal<u>aa</u> yu<u>h</u>ii<u>th</u>uuna bisyay-in min \'ilmihi ill<u>aa</u> bim<u>aa</u> sy<u>aa</u>-a wasi\'a kursiyyuhu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wal<u>aa</u> yauuduhu <u>h</u>if<u>zh</u>uhum<u>aa</u> wahuwa <strong>a</strong>l\'aliyyu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Allah, tidak ada tuhan selain Dia. Yang Mahahidup, Yang terus menerus mengurus (makhluk-Nya), tidak mengantuk dan tidak tidur. Milik-Nya apa yang ada di langit dan apa yang ada di bumi. Tidak ada yang dapat memberi syafaat di sisi-Nya tanpa izin-Nya. Dia mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka, dan mereka tidak mengetahui sesuatu apa pun tentang ilmu-Nya melainkan apa yang Dia kehendaki. Kursi-Nya meliputi langit dan bumi. Dan Dia tidak merasa berat memelihara keduanya, dan Dia Mahatinggi, Mahabesar.'),
	(263, 2, 256, 'لَآ اِكْرَاهَ فِى الدِّيْنِۗ  قَدْ تَّبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَنْ يَّكْفُرْ بِالطَّاغُوْتِ وَيُؤْمِنْۢ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰى لَا انْفِصَامَ لَهَا ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ', 'l<u>aa</u> ikr<u>aa</u>ha fii <strong>al</strong>ddiini qad tabayyana <strong>al</strong>rrusydu mina <strong>a</strong>lghayyi faman yakfur bi<strong>al</strong><u>ththaa</u>ghuuti wayu/min bi<strong>al</strong>l<u>aa</u>hi faqadi istamsaka bi<strong>a</strong>l\'urwati <strong>a</strong>lwutsq<u>aa</u> l<u>aa</u> infi<u>shaa</u>ma lah<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Tidak ada paksaan dalam (menganut) agama (Islam), sesungguhnya telah jelas (perbedaan) antara jalan yang benar dengan jalan yang sesat. Barang siapa ingkar kepada Tagut dan beriman kepada Allah, maka sungguh, dia telah berpegang (teguh) pada tali yang sangat kuat yang tidak akan putus. Allah Maha Mendengar, Maha Mengetahui.'),
	(264, 2, 257, 'اَللّٰهُ وَلِيُّ الَّذِيْنَ اٰمَنُوْا يُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَالَّذِيْنَ كَفَرُوْٓا اَوْلِيَاۤؤُهُمُ الطَّاغُوْتُ يُخْرِجُوْنَهُمْ مِّنَ النُّوْرِ اِلَى الظُّلُمٰتِۗ  اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ', '<strong>al</strong>l<u>aa</u>hu waliyyu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu yukhrijuhum mina <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> <strong>al</strong>nnuuri wa<strong>a</strong>lla<u>dz</u>iina kafaruu awliy<u>aa</u>uhumu <strong>al</strong><u>ththaa</u>ghuutu yukhrijuunahum mina <strong>al</strong>nnuuri il<u>aa</u> <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Allah pelindung orang yang beriman. Dia mengeluarkan mereka dari kegelapan kepada cahaya (iman). Dan orang-orang yang kafir, pelindung-pelindungnya adalah setan, yang mengeluarkan mereka dari cahaya kepada kegelapan. Mereka adalah penghuni neraka. Mereka kekal di dalamnya.'),
	(265, 2, 258, 'اَلَمْ تَرَ اِلَى الَّذِيْ حَاۤجَّ اِبْرٰهٖمَ فِيْ رَبِّهٖٓ اَنْ اٰتٰىهُ اللّٰهُ الْمُلْكَ ۘ اِذْ قَالَ اِبْرٰهٖمُ رَبِّيَ الَّذِيْ يُحْيٖ وَيُمِيْتُۙ قَالَ اَنَا۠ اُحْيٖ وَاُمِيْتُ ۗ قَالَ اِبْرٰهٖمُ فَاِنَّ اللّٰهَ يَأْتِيْ بِالشَّمْسِ مِنَ الْمَشْرِقِ ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>ii <u>haa</u>jja ibr<u>aa</u>hiima fii rabbihi an <u>aa</u>t<u>aa</u>hu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmulka i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbiya <strong>al</strong>la<u>dz</u>ii yu<u>h</u>yii wayumiitu q<u>aa</u>la an<u>aa</u> u<u>h</u>yii waumiitu q<u>aa</u>la ibr<u>aa</u>hiimu fa-inna <strong>al</strong>l<u>aa</u>ha ya/tii bi<strong>al</strong>sysyamsi mina <strong>a</strong>lmasyriqi fa/ti bih<u>aa</u> mina <strong>a</strong>lmaghribi fabuhita <strong>al</strong>la<u>dz</u>ii kafara wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Tidakkah kamu memperhatikan orang yang mendebat Ibrahim mengenai Tuhannya, karena Allah telah memberinya kerajaan (kekuasaan). Ketika Ibrahim berkata, “Tuhanku ialah Yang menghidupkan dan mematikan,” dia berkata, “Aku pun dapat menghidupkan dan mematikan.” Ibrahim berkata, “Allah menerbitkan matahari dari timur, maka terbitkanlah ia dari barat.” Maka bingunglah orang yang kafir itu. Allah tidak memberi petunjuk kepada orang-orang zalim.'),
	(266, 2, 259, 'اَوْ كَالَّذِيْ مَرَّ عَلٰى قَرْيَةٍ وَّهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۚ قَالَ اَنّٰى يُحْيٖ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَا ۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهٗ ۗ قَالَ كَمْ لَبِثْتَ ۗ قَالَ لَبِثْتُ يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالَ بَلْ ', 'aw ka<strong>a</strong>lla<u>dz</u>ii marra \'al<u>aa</u> qaryatin wahiya kh<u>aa</u>wiyatun \'al<u>aa</u> \'uruusyih<u>aa</u> q<u>aa</u>la ann<u>aa</u> yu<u>h</u>yii h<u>aadz</u>ihi <strong>al</strong>l<u>aa</u>hu ba\'da mawtih<u>aa</u> fa-am<u>aa</u>tahu <strong>al</strong>l<u>aa</u>hu mi-ata \'<u>aa</u>min tsumma ba\'atsahu q<u>aa</u>la kam labitsta q<u>aa</u>la labitstu yawman aw ba\'<u>dh</u>a yawmin q<u>aa</u>la bal labitsta mi-ata \'<u>aa</u>min fa<strong>u</strong>n<u>zh</u>ur il<u>aa</u> <u>th</u>a\'<u>aa</u>mika wasyar<u>aa</u>bika lam yatasannah wa<strong>u</strong>n<u>zh</u>ur il<u>aa</u> <u>h</u>im<u>aa</u>rika walinaj\'alaka <u>aa</u>yatan li<strong>l</strong>nn<u>aa</u>si wa<strong>u</strong>n<u>zh</u>ur il<u>aa</u> <strong>a</strong>l\'i<u>zhaa</u>mi kayfa nunsyizuh<u>aa</u> tsumma naksuuh<u>aa</u> la<u>h</u>man falamm<u>aa</u> tabayyana lahu q<u>aa</u>la a\'lamu anna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Atau seperti orang yang melewati suatu negeri yang (bangunan-bangunannya) telah roboh hingga menutupi (reruntuhan) atap-atapnya, dia berkata, “Bagaimana Allah menghidupkan kembali (negeri) ini setelah hancur?” Lalu Allah mematikannya (orang itu) selama seratus tahun, kemudian membangkitkannya (menghidupkannya) kembali. Dan (Allah) bertanya, “Berapa lama engkau tinggal (di sini)?” Dia (orang itu) menjawab, “Aku tinggal (di sini) sehari atau setengah hari.” Allah berfirman, “Tidak! Engkau telah tinggal seratus tahun. Lihatlah makanan dan minumanmu yang belum berubah, tetapi lihatlah keledaimu (yang telah menjadi tulang belulang). Dan agar Kami jadikan engkau tanda kekuasaan Kami bagi manusia. Lihatlah tulang belulang (keledai itu), bagaimana Kami menyusunnya kembali, kemudian Kami membalutnya dengan daging.” Maka ketika telah nyata baginya, dia pun berkata, “Saya mengetahui bahwa Allah Mahakuasa atas segala sesuatu.”'),
	(267, 2, 260, 'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اَرِنِيْ كَيْفَ تُحْيِ الْمَوْتٰىۗ قَالَ اَوَلَمْ تُؤْمِنْ ۗقَالَ بَلٰى وَلٰكِنْ لِّيَطْمَىِٕنَّ قَلْبِيْ ۗقَالَ فَخُذْ اَرْبَعَةً مِّنَ الطَّيْرِفَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِّنْهُنَّ جُزْءًا ثُمَّ ', 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbi arinii kayfa tu<u>h</u>yii <strong>a</strong>lmawt<u>aa</u> q<u>aa</u>la awa lam tu/min q<u>aa</u>la bal<u>aa</u> wal<u>aa</u>kin liya<u>th</u>ma-inna qalbii q<u>aa</u>la fakhu<u>dz</u> arba\'atan mina <strong>al</strong><u>ththh</u>ayri fa<u>sh</u>urhunna ilayka tsumma ij\'al \'al<u>aa</u> kulli jabalin minhunna juz-an tsumma ud\'uhunna ya/tiinaka sa\'yan wa<strong>i</strong>\'lam anna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbi arinii kayfa tu<u>h</u>yii <strong>a</strong>lmawt<u>aa</u> q<u>aa</u>la awa lam tu/min q<u>aa</u>la bal<u>aa</u> wal<u>aa</u>kin liya<u>th</u>ma-inna qalbii q<u>aa</u>la fakhu<u>dz</u> arba\'atan mina <strong>al</strong><u>ththh</u>ayri fa<u>sh</u>urhunna ilayka tsumma ij\'al \'al<u>aa</u> kulli jabalin minhunna juz-an tsumma ud\'uhunna ya/tiinaka sa\'yan wa<strong>i</strong>\'lam anna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan (ingatlah) ketika Ibrahim berkata, “Ya Tuhanku, perlihatkanlah kepadaku bagaimana Engkau menghidupkan orang mati.” Allah berfirman, “Belum percayakah engkau?” Dia (Ibrahim) menjawab, “Aku percaya, tetapi agar hatiku tenang (mantap).” Dia (Allah) berfirman, “Kalau begitu ambillah empat ekor burung, lalu cincanglah olehmu kemudian letakkan di atas masing-masing bukit satu bagian, kemudian panggillah mereka, niscaya mereka datang kepadamu dengan segera.” Ketahuilah bahwa Allah Mahaperkasa, Mahabijaksana.'),
	(268, 2, 261, 'مَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْۢبَتَتْ سَبْعَ سَنَابِلَ فِيْ كُلِّ سُنْۢبُلَةٍ مِّائَةُ حَبَّةٍ ۗ وَاللّٰهُ يُضٰعِفُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ', 'matsalu <strong>al</strong>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum fii sabiili <strong>al</strong>l<u>aa</u>hi kamatsali <u>h</u>abbatin anbatat sab\'a san<u>aa</u>bila fii kulli sunbulatin mi-atu <u>h</u>abbatin wa<strong>al</strong>l<u>aa</u>hu yu<u>daa</u>\'ifu liman yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'un \'aliim<strong>un</strong>', 'Perumpamaan orang yang menginfakkan hartanya di jalan Allah seperti sebutir biji yang menumbuhkan tujuh tangkai, pada setiap tangkai ada seratus biji. Allah melipatgandakan bagi siapa yang Dia kehendaki, dan Allah Mahaluas, Maha Mengetahui.'),
	(269, 2, 262, 'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ ثُمَّ لَا يُتْبِعُوْنَ مَآ اَنْفَقُوْا مَنًّا وَّلَآ اَذًىۙ  لَّهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', '<strong>al</strong>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum fii sabiili <strong>al</strong>l<u>aa</u>hi tsumma l<u>aa</u> yutbi\'uuna m<u>aa</u> anfaquu mannan wal<u>aa</u> a<u>dz</u>an lahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Orang yang menginfakkan hartanya di jalan Allah, kemudian tidak mengiringi apa yang dia infakkan itu dengan menyebut-nyebutnya dan menyakiti (perasaan penerima), mereka memperoleh pahala di sisi Tuhan mereka. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(270, 2, 263, '۞ قَوْلٌ مَّعْرُوْفٌ وَّمَغْفِرَةٌ خَيْرٌ مِّنْ صَدَقَةٍ يَّتْبَعُهَآ اَذًى ۗ وَاللّٰهُ غَنِيٌّ حَلِيْمٌ', 'qawlun ma\'ruufun wamaghfiratun khayrun min <u>sh</u>adaqatin yatba\'uh<u>aa</u> a<u>dz</u>an wa<strong>al</strong>l<u>aa</u>hu ghaniyyun <u>h</u>aliim<strong>un</strong>', 'Perkataan yang baik dan pemberian maaf lebih baik daripada sedekah yang diiringi tindakan yang menyakiti. Allah Mahakaya, Maha Penyantun.'),
	(271, 2, 264, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُبْطِلُوْا صَدَقٰتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ  كَالَّذِيْ يُنْفِقُ مَالَهٗ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ  فَمَثَلُهٗ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهٗ وَابِلٌ فَتَرَك', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tub<u>th</u>iluu <u>sh</u>adaq<u>aa</u>tikum bi<strong>a</strong>lmanni wa<strong>a</strong>l-a<u>dzaa</u> ka<strong>a</strong>lla<u>dz</u>ii yunfiqu m<u>aa</u>lahu ri-<u>aa</u>-a <strong>al</strong>nn<u>aa</u>si wal<u>aa</u> yu/minu bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri famatsaluhu kamatsali <u>sh</u>afw<u>aa</u>nin \'alayhi tur<u>aa</u>bun fa-a<u>shaa</u>bahu w<u>aa</u>bilun fatarakahu <u>sh</u>aldan l<u>aa</u> yaqdiruuna \'al<u>aa</u> syay-in mimm<u>aa</u> kasabuu wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu merusak sedekahmu dengan menyebut-nyebutnya dan menyakiti (perasaan penerima), seperti orang yang menginfakkan hartanya karena ria (pamer) kepada manusia dan dia tidak beriman kepada Allah dan hari akhir. Perumpamaannya (orang itu) seperti batu yang licin yang di atasnya ada debu, kemudian batu itu ditimpa hujan lebat, maka tinggallah batu itu licin lagi. Mereka tidak memperoleh sesuatu apa pun dari apa yang mereka kerjakan. Dan Allah tidak memberi petunjuk kepada orang-orang kafir.'),
	(272, 2, 265, 'وَمَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ وَتَثْبِيْتًا مِّنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍۢ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ  ۗوَاللّٰهُ بِمَا تَعْمَلُو', 'wamatsalu <strong>al</strong>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahumu ibtigh<u>aa</u>-a mar<u>daa</u>ti <strong>al</strong>l<u>aa</u>hi watatsbiitan minanfusihim kamatsali jannatin birabwatin a<u>shaa</u>bah<u>aa</u> w<u>aa</u>bilun fa<u>aa</u>tat ukulah<u>aa</u> <u>dh</u>i\'fayni fa-in lam yu<u>sh</u>ibh<u>aa</u> w<u>aa</u>bilun fa<u>th</u>allun wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Dan perumpamaan orang yang menginfakkan hartanya untuk mencari rida Allah dan untuk memperteguh jiwa mereka, seperti sebuah kebun yang terletak di dataran tinggi yang disiram oleh hujan lebat, maka kebun itu menghasilkan buah-buahan dua kali lipat. Jika hujan lebat tidak menyiraminya, maka embun (pun memadai). Allah Maha Melihat apa yang kamu kerjakan.'),
	(273, 2, 266, 'اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُوْنَ لَهٗ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ  لَهٗ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهٗ ذُرِّيَّةٌ ضُعَفَاۤءُۚ فَاَصَابَهَآ اِعْصَارٌ فِيْهِ نَارٌ فَاحْتَرَقَتْ ', 'ayawaddu a<u>h</u>adukum an takuuna lahu jannatun min nakhiilin wa-a\'n<u>aa</u>bin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru lahu fiih<u>aa</u> min kulli <strong>al</strong>tstsamar<u>aa</u>ti wa-a<u>shaa</u>bahu <strong>a</strong>lkibaru walahu <u>dz</u>urriyyatun <u>dh</u>u\'af<u>aa</u>u fa-a<u>shaa</u>bah<u>aa</u> i\'<u>shaa</u>run fiihi n<u>aa</u>run fa<strong>i</strong><u>h</u>taraqat ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>tila\'allakum tatafakkaruun<strong>a</strong>', 'Adakah salah seorang di antara kamu yang ingin memiliki kebun kurma dan anggur yang mengalir di bawahnya sungai-sungai, di sana dia memiliki segala macam buah-buahan, kemudian datanglah masa tuanya sedang dia memiliki keturunan yang masih kecil-kecil. Lalu kebun itu ditiup angin keras yang mengandung api, sehingga terbakar. Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu agar kamu memikirkannya.'),
	(274, 2, 267, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِنْ طَيِّبٰتِ مَا كَسَبْتُمْ وَمِمَّآ اَخْرَجْنَا لَكُمْ مِّنَ الْاَرْضِ ۗ وَلَا تَيَمَّمُوا الْخَبِيْثَ مِنْهُ تُنْفِقُوْنَ وَلَسْتُمْ بِاٰخِذِيْهِ اِلَّآ اَنْ تُغْمِضُوْا فِيْهِ ۗ وَاعْلَمُوْٓا اَنَّ اللّ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu anfiquu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> kasabtum wamimm<u>aa</u> akhrajn<u>aa</u> lakum mina <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> tayammamuu <strong>a</strong>lkhabiitsa minhu tunfiquuna walastum bi-<u>aa</u>khi<u>dz</u>iihi ill<u>aa</u> an tughmi<u>dh</u>uu fiihi wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ghaniyyun <u>h</u>amiid<strong>un</strong>', 'Wahai orang-orang yang beriman! Infakkanlah sebagian dari hasil usahamu yang baik-baik dan sebagian dari apa yang Kami keluarkan dari bumi untukmu. Janganlah kamu memilih yang buruk untuk kamu keluarkan, padahal kamu sendiri tidak mau mengambilnya melainkan dengan memicingkan mata (enggan) terhadapnya. Dan ketahuilah bahwa Allah Mahakaya, Maha Terpuji.'),
	(275, 2, 268, 'اَلشَّيْطٰنُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاۤءِ ۚ وَاللّٰهُ يَعِدُكُمْ مَّغْفِرَةً مِّنْهُ وَفَضْلًا ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۖ ', 'a<strong>l</strong>sysyay<u>th</u><u>aa</u>nu ya\'idukumu <strong>a</strong>lfaqra waya/murukum bi<strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i wa<strong>al</strong>l<u>aa</u>hu ya\'idukum maghfiratan minhu wafa<u>dh</u>lan wa<strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'un \'aliim<strong>un</strong>', 'Setan menjanjikan (menakut-nakuti) kemiskinan kepadamu dan menyuruh kamu berbuat keji (kikir), sedangkan Allah menjanjikan ampunan dan karunia-Nya kepadamu. Dan Allah Mahaluas, Maha Mengetahui.'),
	(276, 2, 269, 'يُّؤْتِى الْحِكْمَةَ مَنْ يَّشَاۤءُ ۚ وَمَنْ يُّؤْتَ الْحِكْمَةَ فَقَدْ اُوْتِيَ خَيْرًا كَثِيْرًا ۗ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ', 'yu/tii <strong>a</strong>l<u>h</u>ikmata man yasy<u>aa</u>u waman yu/ta <strong>a</strong>l<u>h</u>ikmata faqad uutiya khayran katsiiran wam<u>aa</u> ya<u>dzdz</u>akkaru ill<u>aa</u> uluu <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', 'Dia memberikan hikmah kepada siapa yang Dia kehendaki. Barangsiapa diberi hikmah, sesungguhnya dia telah diberi kebaikan yang banyak. Dan tidak ada yang dapat mengambil pelajaran kecuali orang-orang yang mempunyai akal sehat.'),
	(277, 2, 270, 'وَمَآ اَنْفَقْتُمْ مِّنْ نَّفَقَةٍ اَوْ نَذَرْتُمْ مِّنْ نَّذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ', 'wam<u>aa</u> anfaqtum min nafaqatin aw na<u>dz</u>artum min na<u>dz</u>rin fa-inna <strong>al</strong>l<u>aa</u>ha ya\'lamuhu wam<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina min an<u>shaa</u>r<strong>in</strong>', 'Dan apa pun infak yang kamu berikan atau nazar yang kamu janjikan, maka sungguh, Allah mengetahuinya. Dan bagi orang zalim tidak ada seorang penolong pun'),
	(278, 2, 271, 'اِنْ تُبْدُوا الصَّدَقٰتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوْهَا وَتُؤْتُوْهَا الْفُقَرَاۤءَ فَهُوَ خَيْرٌ لَّكُمْ ۗ وَيُكَفِّرُ عَنْكُمْ مِّنْ سَيِّاٰتِكُمْ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'in tubduu <strong>al</strong><u>shsh</u>adaq<u>aa</u>ti fani\'imm<u>aa</u> hiya wa-in tukhfuuh<u>aa</u> watu/tuuh<u>aa</u> <strong>a</strong>lfuqar<u>aa</u>-a fahuwa khayrun lakum wayukaffiru \'ankum min sayyi-<u>aa</u>tikum wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir<strong>un</strong>', 'Jika kamu menampakkan sedekah-sedekahmu, maka itu baik. Dan jika kamu menyembunyikannya dan memberikannya kepada orang-orang fakir, maka itu lebih baik bagimu dan Allah akan menghapus sebagian kesalahan-kesalahanmu. Dan Allah Mahateliti apa yang kamu kerjakan.'),
	(279, 2, 272, '۞ لَيْسَ عَلَيْكَ هُدٰىهُمْ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْ ۗوَمَا تُنْفِقُوْنَ اِلَّا ابْتِغَاۤءَ وَجْهِ اللّٰهِ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ يُّوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ', 'laysa \'alayka hud<u>aa</u>hum wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yahdii man yasy<u>aa</u>u wam<u>aa</u> tunfiquu min khayrin fali-anfusikum wam<u>aa</u> tunfiquuna ill<u>aa</u> ibtigh<u>aa</u>-a wajhi <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> tunfiquu min khayrin yuwaffa ilaykum wa-antum l<u>aa</u> tu<u>zh</u>lamuun<strong>a</strong>', 'Bukanlah kewajibanmu (Muhammad) menjadikan mereka mendapat petunjuk, tetapi Allah-lah yang memberi petunjuk kepada siapa yang Dia kehendaki. Apa pun harta yang kamu infakkan, maka (kebaikannya) untuk dirimu sendiri. Dan janganlah kamu berinfak melainkan karena mencari rida Allah. Dan apa pun harta yang kamu infakkan, niscaya kamu akan diberi (pahala) secara penuh dan kamu tidak akan dizalimi (dirugikan).'),
	(280, 2, 273, 'لِلْفُقَرَاۤءِ الَّذِيْنَ اُحْصِرُوْا فِيْ سَبِيْلِ اللّٰهِ لَا يَسْتَطِيْعُوْنَ ضَرْبًا فِى الْاَرْضِۖ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَاۤءَ مِنَ التَّعَفُّفِۚ  تَعْرِفُهُمْ بِسِيْمٰهُمْۚ  لَا يَسْـَٔلُوْنَ النَّاسَ اِلْحَافًا ۗوَمَا تُنْفِقُوْا مِنْ خَيْ', 'lilfuqar<u>aa</u>-i <strong>al</strong>la<u>dz</u>iina u<u>hs</u>iruu fii sabiili <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yasta<u>th</u>ii\'uuna <u>dh</u>arban fii <strong>a</strong>l-ar<u>dh</u>i ya<u>h</u>sabuhumu <strong>a</strong>lj<u>aa</u>hilu aghniy<u>aa</u>-a mina <strong>al</strong>tta\'affufi ta\'rifuhum bisiim<u>aa</u>hum l<u>aa</u> yas-aluuna <strong>al</strong>nn<u>aa</u>sa il<u>haa</u>fan wam<u>aa</u> tunfiquu min khayrin fa-inna <strong>al</strong>l<u>aa</u>ha bihi \'aliim<strong>un</strong>', '(Apa yang kamu infakkan) adalah untuk orang-orang fakir yang terhalang (usahanya karena jihad) di jalan Allah, sehingga dia yang tidak dapat berusaha di bumi; (orang lain) yang tidak tahu, menyangka bahwa mereka adalah orang-orang kaya karena mereka menjaga diri (dari meminta-minta). Engkau (Muhammad) mengenal mereka dari ciri-cirinya, mereka tidak meminta secara paksa kepada orang lain. Apa pun harta yang baik yang kamu infakkan, sungguh, Allah Maha Mengetahui.'),
	(281, 2, 274, 'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِرًّا وَّعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ  وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', '<strong>al</strong>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum bi<strong>a</strong>llayli wa<strong>al</strong>nnah<u>aa</u>ri sirran wa\'al<u>aa</u>niyatan falahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Orang-orang yang menginfakkan hartanya malam dan siang hari (secara) sembunyi-sembunyi maupun terang-terangan, mereka mendapat pahala di sisi Tuhannya. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(282, 2, 275, 'اَلَّذِيْنَ يَأْكُلُوْنَ الرِّبٰوا لَا يَقُوْمُوْنَ اِلَّا كَمَا يَقُوْمُ الَّذِيْ يَتَخَبَّطُهُ الشَّيْطٰنُ مِنَ الْمَسِّۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْٓا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۘ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۗ  فَمَنْ جَاۤءَهٗ', '<strong>al</strong>la<u>dz</u>iina ya/kuluuna <strong>al</strong>rrib<u>aa</u> l<u>aa</u> yaquumuuna ill<u>aa</u> kam<u>aa</u> yaquumu <strong>al</strong>la<u>dz</u>ii yatakhabba<u>th</u>uhu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu mina <strong>a</strong>lmassi <u>dzaa</u>lika bi-annahum q<u>aa</u>luu innam<u>aa</u> <strong>a</strong>lbay\'u mitslu <strong>al</strong>rrib<u>aa</u> wa-a<u>h</u>alla <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lbay\'a wa<u>h</u>arrama <strong>al</strong>rrib<u>aa</u> faman j<u>aa</u>-ahu maw\'i<u>zh</u>atun min rabbihi fa<strong>i</strong>ntah<u>aa</u> falahu m<u>aa</u> salafa wa-amruhu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi waman \'<u>aa</u>da faul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Orang-orang yang memakan riba tidak dapat berdiri melainkan seperti berdirinya orang yang kemasukan setan karena gila. Yang demikian itu karena mereka berkata bahwa jual beli sama dengan riba. Padahal Allah telah menghalalkan jual beli dan mengharamkan riba. Barangsiapa mendapat peringatan dari Tuhannya, lalu dia berhenti, maka apa yang telah diperolehnya dahulu menjadi miliknya dan urusannya (terserah) kepada Allah. Barangsiapa mengulangi, maka mereka itu penghuni neraka, mereka kekal di dalamnya.'),
	(283, 2, 276, 'يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِى الصَّدَقٰتِ ۗ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَثِيْمٍ', 'yam<u>h</u>aqu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>rrib<u>aa</u> wayurbii <strong>al</strong><u>shsh</u>adaq<u>aa</u>ti wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu kulla kaff<u>aa</u>rin atsiim<strong>in</strong>', 'Allah memusnahkan riba dan menyuburkan sedekah. Allah tidak menyukai setiap orang yang tetap dalam kekafiran dan bergelimang dosa.'),
	(284, 2, 277, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu <strong>al</strong>zzak<u>aa</u>ta lahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Sungguh, orang-orang yang beriman, mengerjakan kebajikan, melaksanakan salat dan menunaikan zakat, mereka mendapat pahala di sisi Tuhannya. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(285, 2, 278, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوْا مَا بَقِيَ مِنَ الرِّبٰوٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ittaquu <strong>al</strong>l<u>aa</u>ha wa<u>dz</u>aruu m<u>aa</u> baqiya mina <strong>al</strong>rrib<u>aa</u> in kuntum mu/miniin<strong>a</strong>', 'Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan tinggalkan sisa riba (yang belum dipungut) jika kamu orang beriman.'),
	(286, 2, 279, 'فَاِنْ لَّمْ تَفْعَلُوْا فَأْذَنُوْا بِحَرْبٍ مِّنَ اللّٰهِ وَرَسُوْلِهٖۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُءُوْسُ اَمْوَالِكُمْۚ  لَا تَظْلِمُوْنَ وَلَا تُظْلَمُوْنَ', 'fa-in lam taf\'aluu fa/<u>dz</u>anuu bi<u>h</u>arbin mina <strong>al</strong>l<u>aa</u>hi warasuulihi wa-in tubtum falakum ruuusu amw<u>aa</u>likum l<u>aa</u> ta<u>zh</u>limuuna wal<u>aa</u> tu<u>zh</u>lamuun<strong>a</strong>', 'Jika kamu tidak melaksanakannya, maka umumkanlah perang dari Allah dan Rasul-Nya. Tetapi jika kamu bertobat, maka kamu berhak atas pokok hartamu. Kamu tidak berbuat zalim (merugikan) dan tidak dizalimi (dirugikan).'),
	(287, 2, 280, 'وَاِنْ كَانَ ذُوْ عُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍ ۗ وَاَنْ تَصَدَّقُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ', 'wa-in k<u>aa</u>na <u>dz</u>uu \'usratin fana<u>zh</u>iratun il<u>aa</u> maysaratin wa-an ta<u>sh</u>addaquu khayrun lakum in kuntum ta\'lamuun<strong>a</strong>', 'Dan jika (orang berutang itu) dalam kesulitan, maka berilah tenggang waktu sampai dia memperoleh kelapangan. Dan jika kamu menyedekahkan, itu lebih baik bagimu, jika kamu mengetahui.'),
	(288, 2, 281, 'وَاتَّقُوْا يَوْمًا تُرْجَعُوْنَ فِيْهِ اِلَى اللّٰهِ ۗثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ ࣖ', 'wa<strong>i</strong>ttaquu yawman turja\'uuna fiihi il<u>aa</u> <strong>al</strong>l<u>aa</u>hi tsumma tuwaff<u>aa</u> kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Dan takutlah pada hari (ketika) kamu semua dikembalikan kepada Allah. Kemudian setiap orang diberi balasan yang sempurna sesuai dengan apa yang telah dilakukannya, dan mereka tidak dizalimi (dirugikan).'),
	(289, 2, 282, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُّسَمًّى فَاكْتُبُوْهُۗ وَلْيَكْتُبْ بَّيْنَكُمْ كَاتِبٌۢ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَّكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذِيْ عَلَيْهِ ا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> tad<u>aa</u>yantum bidaynin il<u>aa</u> ajalin musamman fa<strong>u</strong>ktubuuhu walyaktub baynakum k<u>aa</u>tibun bi<strong>a</strong>l\'adli wal<u>aa</u> ya/ba k<u>aa</u>tibun an yaktuba kam<u>aa</u> \'allamahu <strong>al</strong>l<u>aa</u>hu falyaktub walyumlili <strong>al</strong>la<u>dz</u>ii \'alayhi <strong>a</strong>l<u>h</u>aqqu walyattaqi <strong>al</strong>l<u>aa</u>ha rabbahu wal<u>aa</u> yabkhas minhu syay-an fa-in k<u>aa</u>na <strong>al</strong>la<u>dz</u>ii \'alayhi <strong>a</strong>l<u>h</u>aqqu safiihan aw <u>dh</u>a\'iifan aw l<u>aa</u> yasta<u>th</u>ii\'u an yumilla huwa falyumlil waliyyuhu bi<strong>a</strong>l\'adli wa<strong>i</strong>stasyhiduu syahiidayni min rij<u>aa</u>likum fa-in lam yakuun<u>aa</u> rajulayni farajulun wa<strong>i</strong>mra-at<u>aa</u>ni mimman tar<u>dh</u>awna mina <strong>al</strong>sysyuhad<u>aa</u>-i an ta<u>dh</u>illa i<u>h</u>d<u>aa</u>hum<u>aa</u> fatu<u>dz</u>akkira i<u>h</u>d<u>aa</u>hum<u>aa</u> <strong>a</strong>l-ukhr<u>aa</u> wal<u>aa</u> ya/ba <strong>al</strong>sysyuhad<u>aa</u>u i<u>dzaa</u> m<u>aa</u> du\'uu wal<u>aa</u> tas-amuu an taktubuuhu <u>sh</u>aghiiran aw kabiiran il<u>aa</u> ajalihi <u>dzaa</u>likum aqsa<u>th</u>u \'inda <strong>al</strong>l<u>aa</u>hi wa-aqwamu li<strong>l</strong>sysyah<u>aa</u>dati wa-adn<u>aa</u> <strong>al</strong>l<u>aa</u> tart<u>aa</u>buu ill<u>aa</u> an takuuna tij<u>aa</u>ratan <u>had</u>iratan tudiiruunah<u>aa</u> baynakum falaysa \'alaykum jun<u>aah</u>un <strong>al</strong>l<u>aa</u> taktubuuh<u>aa</u> wa-asyhiduu i<u>dzaa</u> tab<u>aa</u>ya\'tum wal<u>aa</u> yu<u>daa</u>rra k<u>aa</u>tibun wal<u>aa</u> syahiidun wa-in taf\'aluu fa-innahu fusuuqun bikum wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wayu\'allimukumu <strong>al</strong>l<u>aa</u>hu wa<strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<strong>un</strong>', 'Wahai orang-orang yang beriman! Apabila kamu melakukan utang piutang untuk waktu yang ditentukan, hendaklah kamu menuliskannya. Dan hendaklah seorang penulis di antara kamu menuliskannya dengan benar. Janganlah penulis menolak untuk menuliskannya sebagaimana Allah telah mengajarkan kepadanya, maka hendaklah dia menuliskan. Dan hendaklah orang yang berutang itu mendiktekan, dan hendaklah dia bertakwa kepada Allah, Tuhannya, dan janganlah dia mengurangi sedikit pun daripadanya. Jika yang berutang itu orang yang kurang akalnya atau lemah (keadaannya), atau tidak mampu mendiktekan sendiri, maka hendaklah walinya mendiktekannya dengan benar. Dan persaksikanlah dengan dua orang saksi laki-laki di antara kamu. Jika tidak ada (saksi) dua orang laki-laki, maka (boleh) seorang laki-laki dan dua orang perempuan di antara orang-orang yang kamu sukai dari para saksi (yang ada), agar jika yang seorang lupa, maka yang seorang lagi mengingatkannya. Dan janganlah saksi-saksi itu menolak apabila dipanggil. Dan janganlah kamu bosan menuliskannya, untuk batas waktunya baik (utang itu) kecil maupun besar. Yang demikian itu, lebih adil di sisi Allah, lebih dapat menguatkan kesaksian, dan lebih mendekatkan kamu kepada ketidakraguan, kecuali jika hal itu merupakan perdagangan tunai yang kamu jalankan di antara kamu, maka tidak ada dosa bagi kamu jika kamu tidak menuliskannya. Dan ambillah saksi apabila kamu berjual beli, dan janganlah penulis dipersulit dan begitu juga saksi. Jika kamu lakukan (yang demikian), maka sungguh, hal itu suatu kefasikan pada kamu. Dan bertakwalah kepada Allah, Allah memberikan pengajaran kepadamu, dan Allah Maha Mengetahui segala sesuatu.'),
	(290, 2, 283, '۞ وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَّلَمْ تَجِدُوْا كَاتِبًا فَرِهٰنٌ مَّقْبُوْضَةٌ  ۗفَاِنْ اَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِى اؤْتُمِنَ اَمَانَتَهٗ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَۗ وَمَنْ يَّكْتُمْهَا فَاِنَّهٗٓ اٰثِم', 'wa-in kuntum \'al<u>aa</u> safarin walam tajiduu k<u>aa</u>tiban farih<u>aa</u>nun maqbuu<u>dh</u>atun fa-in amina ba\'<u>dh</u>ukum ba\'<u>dh</u>an falyu-addi <strong>al</strong>la<u>dz</u>ii<u> </u>i/tumina am<u>aa</u>natahu walyattaqi <strong>al</strong>l<u>aa</u>ha rabbahu wal<u>aa</u> taktumuu <strong>al</strong>sysyah<u>aa</u>data waman yaktumh<u>aa</u> fa-innahu <u>aa</u>tsimun qalbuhu wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna \'aliim<strong>un</strong>', 'Dan jika kamu dalam perjalanan sedang kamu tidak mendapatkan seorang penulis, maka hendaklah ada barang jaminan yang dipegang. Tetapi, jika sebagian kamu mempercayai sebagian yang lain, hendaklah yang dipercayai itu menunaikan amanatnya (utangnya) dan hendaklah dia bertakwa kepada Allah, Tuhannya. Dan janganlah kamu menyembunyikan kesaksian, karena barangsiapa menyembunyikannya, sungguh, hatinya kotor (berdosa). Allah Maha Mengetahui apa yang kamu kerjakan.'),
	(291, 2, 284, ' لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗ وَاِنْ تُبْدُوْا مَا فِيْٓ اَنْفُسِكُمْ اَوْ تُخْفُوْهُ يُحَاسِبْكُمْ بِهِ اللّٰهُ ۗ فَيَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa-in tubduu m<u>aa</u> fii anfusikum aw tukhfuuhu yu<u>haa</u>sibkum bihi <strong>al</strong>l<u>aa</u>hu fayaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Jika kamu nyatakan apa yang ada di dalam hatimu atau kamu sembunyikan, niscaya Allah memperhitungkannya (tentang perbuatan itu) bagimu. Dia mengampuni siapa yang Dia kehendaki dan mengazab siapa yang Dia kehendaki. Allah Mahakuasa atas segala sesuatu.'),
	(292, 2, 285, 'اٰمَنَ الرَّسُوْلُ بِمَآ اُنْزِلَ اِلَيْهِ مِنْ رَّبِّهٖ وَالْمُؤْمِنُوْنَۗ  كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖۗ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْ رُّسُلِهٖ ۗ وَقَالُوْا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَصِيْرُ', '<u>aa</u>mana <strong>al</strong>rrasuulu bim<u>aa</u> unzila ilayhi min rabbihi wa<strong>a</strong>lmu/minuuna kullun <u>aa</u>mana bi<strong>al</strong>l<u>aa</u>hi wamal<u>aa</u>-ikatihi wakutubihi warusulihi l<u>aa</u> nufarriqu bayna a<u>h</u>adin min rusulihi waq<u>aa</u>luu sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> ghufr<u>aa</u>naka rabban<u>aa</u> wa-ilayka <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Rasul (Muhammad) beriman kepada apa yang diturunkan kepadanya (Al-Qur\'an) dari Tuhannya, demikian pula orang-orang yang beriman. Semua beriman kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya dan rasul-rasul-Nya. (Mereka berkata), “Kami tidak membeda-bedakan seorang pun dari rasul-rasul-Nya.” Dan mereka berkata, “Kami dengar dan kami taat. Ampunilah kami Ya Tuhan kami, dan kepada-Mu tempat (kami) kembali.”'),
	(293, 2, 286, 'لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا وُسْعَهَا ۗ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَآ اِنْ نَّسِيْنَآ اَوْ اَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَآ اِصْرًا كَمَا حَمَلْتَهٗ عَلَى الَّذِيْنَ مِنْ قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهٖۚ وَاعْفُ عَنَّاۗ وَاغْفِرْ لَنَاۗ وَارْحَمْنَا ۗ اَنْتَ مَوْلٰىنَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ࣖ', 'l<u>aa</u> yukallifu <strong>al</strong>l<u>aa</u>hu nafsan ill<u>aa</u> wus\'ah<u>aa</u> lah<u>aa</u> m<u>aa</u> kasabat wa\'alayh<u>aa</u> m<u>aa</u> iktasabat rabban<u>aa</u> l<u>aa</u> tu-<u>aa</u>khi<u>dz</u>n<u>aa</u> in nasiin<u>aa</u> aw akh<u>th</u>a/n<u>aa</u> rabban<u>aa</u> wal<u>aa</u> ta<u>h</u>mil \'alayn<u>aa</u> i<u>sh</u>ran kam<u>aa</u> <u>h</u>amaltahu \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina min qablin<u>aa</u> rabban<u>aa</u> wal<u>aa</u> tu<u>h</u>ammiln<u>aa</u> m<u>aa</u> l<u>aa</u> <u>th</u><u>aa</u>qata lan<u>aa</u> bihi wa<strong>u</strong>\'fu \'ann<u>aa</u> wa<strong>i</strong>ghfir lan<u>aa</u> wa<strong>i</strong>r<u>h</u>amn<u>aa</u> anta mawl<u>aa</u>n<u>aa</u> fa<strong>u</strong>n<u>sh</u>urn<u>aa</u> \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Allah tidak membebani seseorang melainkan sesuai dengan kesanggupannya. Dia mendapat (pahala) dari (kebajikan) yang dikerjakannya dan dia mendapat (siksa) dari (kejahatan) yang diperbuatnya. (Mereka berdoa), “Ya Tuhan kami, janganlah Engkau hukum kami jika kami lupa atau kami melakukan kesalahan. Ya Tuhan kami, janganlah Engkau bebani kami dengan beban yang berat sebagaimana Engkau bebankan kepada orang-orang sebelum kami. Ya Tuhan kami, janganlah Engkau pikulkan kepada kami apa yang tidak sanggup kami memikulnya. Maafkanlah kami, ampunilah kami, dan rahmatilah kami. Engkaulah pelindung kami, maka tolonglah kami menghadapi orang-orang kafir.”'),
	(294, 3, 1, 'الۤمّۤ ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(295, 3, 2, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ الْحَيُّ الْقَيُّوْمُۗ', '<strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa <strong>a</strong>l<u>h</u>ayyu <strong>a</strong>lqayyuum<strong>u</strong>', 'Allah, tidak ada tuhan selain Dia. Yang Mahahidup, Yang terus-menerus mengurus (makhluk-Nya).'),
	(296, 3, 3, 'نَزَّلَ عَلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰىةَ وَالْاِنْجِيْلَۙ', 'nazzala \'alayka <strong>a</strong>lkit<u>aa</u>ba bi<strong>a</strong>l<u>h</u>aqqi mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi wa-anzala <strong>al</strong>ttawr<u>aa</u>ta wa<strong>a</strong>l-injiil<strong>a</strong>', 'Dia menurunkan Kitab (Al-Qur\'an) kepadamu (Muhammad) yang mengandung kebenaran, membenarkan (kitab-kitab) sebelumnya, dan menurunkan Taurat dan Injil,'),
	(297, 3, 4, 'مِنْ قَبْلُ هُدًى لِّلنَّاسِ وَاَنْزَلَ الْفُرْقَانَ ەۗ اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍۗ', 'min qablu hudan li<strong>l</strong>nn<u>aa</u>si wa-anzala <strong>a</strong>lfurq<u>aa</u>na inna <strong>al</strong>la<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi lahum \'a<u>dzaa</u>bun syadiidun wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>dz</u>uu intiq<u>aa</u>m<strong>in</strong>', 'sebelumnya, sebagai petunjuk bagi manusia, dan Dia menurunkan Al-Furqan. Sungguh, orang-orang yang ingkar terhadap ayat-ayat Allah akan memperoleh azab yang berat. Allah Mahaperkasa lagi mempunyai hukuman.'),
	(298, 3, 5, 'اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yakhf<u>aa</u> \'alayhi syay-un fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>/-<strong>i </strong>', 'Bagi Allah tidak ada sesuatu pun yang tersembunyi di bumi dan di langit.'),
	(299, 3, 6, 'هُوَ الَّذِيْ يُصَوِّرُكُمْ فِى الْاَرْحَامِ كَيْفَ يَشَاۤءُ ۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ', 'huwa <strong>al</strong>la<u>dz</u>ii yu<u>sh</u>awwirukum fii <strong>a</strong>l-ar<u>haa</u>mi kayfa yasy<u>aa</u>u l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Dialah yang membentuk kamu dalam rahim menurut yang Dia kehendaki. Tidak ada tuhan selain Dia. Yang Mahaperkasa, Mahabijaksana.'),
	(300, 3, 7, 'هُوَ الَّذِيْٓ اَنْزَلَ عَلَيْكَ الْكِتٰبَ مِنْهُ اٰيٰتٌ مُّحْكَمٰتٌ هُنَّ اُمُّ الْكِتٰبِ وَاُخَرُ مُتَشٰبِهٰتٌ ۗ فَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ زَيْغٌ فَيَتَّبِعُوْنَ مَا تَشَابَهَ مِنْهُ ابْتِغَاۤءَ الْفِتْنَةِ وَابْتِغَاۤءَ تَأْوِيْلِهٖۚ وَمَا ', 'huwa <strong>al</strong>la<u>dz</u>ii anzala \'alayka <strong>a</strong>lkit<u>aa</u>ba minhu <u>aa</u>y<u>aa</u>tun mu<u>h</u>kam<u>aa</u>tun hunna ummu <strong>a</strong>lkit<u>aa</u>bi waukharu mutasy<u>aa</u>bih<u>aa</u>tun fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina fii quluubihim zayghun fayattabi\'uuna m<u>aa</u> tasy<u>aa</u>baha minhu ibtigh<u>aa</u>-a <strong>a</strong>lfitnati wa<strong>i</strong>btigh<u>aa</u>-a ta/wiilihi wam<u>aa</u> ya\'lamu ta/wiilahu ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu wa<strong>al</strong>rr<u>aa</u>sikhuuna fii <strong>a</strong>l\'ilmi yaquuluuna <u>aa</u>mann<u>aa</u> bihi kullun min \'indi rabbin<u>aa</u> wam<u>aa</u> ya<u>dzdz</u>akkaru ill<u>aa</u> uluu <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', 'Dialah yang menurunkan Kitab (Al-Qur\'an) kepadamu (Muhammad). Di antaranya ada ayat-ayat yang muhkamat, itulah pokok-pokok Kitab (Al-Qur\'an) dan yang lain mutasyabihat. Adapun orang-orang yang dalam hatinya condong pada kesesatan, mereka mengikuti yang mutasyabihat untuk mencari-cari fitnah dan untuk mencari-cari takwilnya, padahal tidak ada yang mengetahui takwilnya kecuali Allah. Dan orang-orang yang ilmunya mendalam berkata, “Kami beriman kepadanya (Al-Qur\'an), semuanya dari sisi Tuhan kami.” Tidak ada yang dapat mengambil pelajaran kecuali orang yang berakal.'),
	(301, 3, 8, 'رَبَّنَا لَا تُزِغْ قُلُوْبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَّدُنْكَ رَحْمَةً ۚاِنَّكَ اَنْتَ الْوَهَّابُ ', 'rabban<u>aa</u> l<u>aa</u> tuzigh quluuban<u>aa</u> ba\'da i<u>dz</u> hadaytan<u>aa</u> wahab lan<u>aa</u> min ladunka ra<u>h</u>matan innaka anta <strong>a</strong>lwahh<u>aa</u>b<strong>u</strong>', '(Mereka berdoa), “Ya Tuhan kami, janganlah Engkau condongkan hati kami kepada kesesatan setelah Engkau berikan petunjuk kepada kami, dan karuniakanlah kepada kami rahmat dari sisi-Mu, sesungguhnya Engkau Maha Pemberi.”'),
	(302, 3, 9, 'رَبَّنَآ اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَّا رَيْبَ فِيْهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ ࣖ', 'rabban<u>aa</u> innaka j<u>aa</u>mi\'u <strong>al</strong>nn<u>aa</u>si liyawmin l<u>aa</u> rayba fiihi inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yukhlifu <strong>a</strong>lmii\'<u>aa</u>d<strong>a</strong>', '”Ya Tuhan kami, Engkaulah yang mengumpulkan manusia pada hari yang tidak ada keraguan padanya.” Sungguh, Allah tidak menyalahi janji.'),
	(303, 3, 10, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗوَاُولٰۤىِٕكَ هُمْ وَقُوْدُ النَّارِۗ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu lan tughniya \'anhum amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum mina <strong>al</strong>l<u>aa</u>hi syay-an waul<u>aa</u>-ika hum waquudu <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Sesungguhnya orang-orang yang kafir, bagi mereka tidak akan berguna sedikit pun harta benda dan anak-anak mereka terhadap (azab) Allah. Dan mereka itu (menjadi) bahan bakar api neraka.'),
	(304, 3, 11, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَذَّبُوْا بِاٰيٰتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗ وَاللّٰهُ شَدِيْدُ الْعِقَابِ', 'kada/bi <u>aa</u>li fir\'awna wa<strong>a</strong>lla<u>dz</u>iina min qablihim ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fa-akha<u>dz</u>ahumu <strong>al</strong>l<u>aa</u>hu bi<u>dz</u>unuubihim wa<strong>al</strong>l<u>aa</u>hu syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', '(Keadaan mereka) seperti keadaan pengikut Fir’aun dan orang-orang yang sebelum mereka. Mereka mendustakan ayat-ayat Kami, maka Allah menyiksa mereka disebabkan dosa-dosanya. Allah sangat berat hukuman-Nya.'),
	(305, 3, 12, 'قُلْ لِّلَّذِيْنَ كَفَرُوْا سَتُغْلَبُوْنَ وَتُحْشَرُوْنَ اِلٰى جَهَنَّمَ ۗ وَبِئْسَ الْمِهَادُ', 'qul lilla<u>dz</u>iina kafaruu satughlabuuna watu<u>h</u>syaruuna il<u>aa</u> jahannama wabi/sa <strong>a</strong>lmih<u>aa</u>d<strong>u</strong>', 'Katakanlah (Muhammad) kepada orang-orang yang kafir, “Kamu (pasti) akan dikalahkan dan digiring ke dalam neraka Jahanam. Dan itulah seburuk-buruk tempat tinggal.”'),
	(306, 3, 13, 'قَدْ كَانَ لَكُمْ اٰيَةٌ فِيْ فِئَتَيْنِ الْتَقَتَا ۗفِئَةٌ تُقَاتِلُ فِيْ سَبِيْلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَّرَوْنَهُمْ مِّثْلَيْهِمْ رَأْيَ الْعَيْنِ ۗوَاللّٰهُ يُؤَيِّدُ بِنَصْرِهٖ مَنْ يَّشَاۤءُ ۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَا', 'qad k<u>aa</u>na lakum <u>aa</u>yatun fii fi-atayni iltaqat<u>aa</u> fi-atun tuq<u>aa</u>tilu fii sabiili <strong>al</strong>l<u>aa</u>hi waukhr<u>aa</u> k<u>aa</u>firatun yarawnahum mitslayhim ra/ya <strong>a</strong>l\'ayni wa<strong>al</strong>l<u>aa</u>hu yu-ayyidu bina<u>sh</u>rihi man yasy<u>aa</u>u inna fii <u>dzaa</u>lika la\'ibratan li-ulii <strong>a</strong>l-ab<u>shaa</u>r<strong>i</strong>', 'Sungguh, telah ada tanda bagi kamu pada dua golongan yang berhadap-hadapan. Satu golongan berperang di jalan Allah dan yang lain (golongan) kafir yang melihat dengan mata kepala, bahwa mereka (golongan Muslim) dua kali lipat mereka. Allah menguatkan dengan pertolongan-Nya bagi siapa yang Dia kehendaki. Sungguh, pada yang demikian itu terdapat pelajaran bagi orang-orang yang mempunyai penglihatan (mata hati).'),
	(307, 3, 14, 'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوٰتِ مِنَ النِّسَاۤءِ وَالْبَنِيْنَ وَالْقَنَاطِيْرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِ ۗ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَاللّٰهُ عِنْدَهٗ حُسْنُ الْمَاٰ', 'zuyyina li<strong>l</strong>nn<u>aa</u>si <u>h</u>ubbu <strong>al</strong>sysyahaw<u>aa</u>ti mina <strong>al</strong>nnis<u>aa</u>-i wa<strong>a</strong>lbaniina wa<strong>a</strong>lqan<u>aath</u>iiri <strong>a</strong>lmuqan<u>th</u>arati mina <strong>al</strong><u>dzdz</u>ahabi wa<strong>a</strong>lfi<u>dhdh</u>ati wa<strong>a</strong>lkhayli <strong>a</strong>lmusawwamati wa<strong>a</strong>l-an\'<u>aa</u>mi wa<strong>a</strong>l<u>h</u>artsi <u>dzaa</u>lika mat<u>aa</u>\'u <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu \'indahu <u>h</u>usnu <strong>a</strong>lma<u>aa</u>b<strong>i</strong>', 'Dijadikan terasa indah dalam pandangan manusia cinta terhadap apa yang diinginkan, berupa perempuan-perempuan, anak-anak, harta benda yang bertumpuk dalam bentuk emas dan perak, kuda pilihan, hewan ternak dan sawah ladang. Itulah kesenangan hidup di dunia, dan di sisi Allah-lah tempat kembali yang baik.'),
	(308, 3, 15, '۞ قُلْ اَؤُنَبِّئُكُمْ بِخَيْرٍ مِّنْ ذٰلِكُمْ ۗ لِلَّذِيْنَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَاَزْوَاجٌ مُّطَهَّرَةٌ وَّرِضْوَانٌ مِّنَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِۚ', 'qul a-unabbi-ukum bikhayrin min <u>dzaa</u>likum lilla<u>dz</u>iina ittaqaw \'inda rabbihim jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa-azw<u>aa</u>jun mu<u>th</u>ahharatun wari<u>dh</u>w<u>aa</u>nun mina <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu ba<u>sh</u>iirun bi<strong>a</strong>l\'ib<u>aa</u>d<strong>i</strong>', 'Katakanlah, “Maukah aku kabarkan kepadamu apa yang lebih baik dari yang demikian itu?” Bagi orang-orang yang bertakwa (tersedia) di sisi Tuhan mereka surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya, dan pasangan-pasangan yang suci, serta rida Allah. Dan Allah Maha Melihat hamba-hamba-Nya.'),
	(309, 3, 16, 'اَلَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اِنَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَقِنَا عَذَابَ النَّارِۚ', '<strong>al</strong>la<u>dz</u>iina yaquuluuna rabban<u>aa</u> innan<u>aa</u> <u>aa</u>mann<u>aa</u> fa<strong>i</strong>ghfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> waqin<u>aa</u> \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', '(Yaitu) orang-orang yang berdoa, “Ya Tuhan kami, kami benar-benar beriman, maka ampunilah dosa-dosa kami dan lindungilah kami dari azab neraka.”'),
	(310, 3, 17, 'اَلصّٰبِرِيْنَ وَالصّٰدِقِيْنَ وَالْقٰنِتِيْنَ وَالْمُنْفِقِيْنَ وَالْمُسْتَغْفِرِيْنَ بِالْاَسْحَارِ ', 'a<strong>l</strong><u>shshaa</u>biriina wa<strong>al</strong><u>shshaa</u>diqiina wa<strong>a</strong>lq<u>aa</u>nitiina wa<strong>a</strong>lmunfiqiina wa<strong>a</strong>lmustaghfiriina bi<strong>a</strong>l-as<u>haa</u>r<strong>i</strong>', '(Juga) orang yang sabar, orang yang benar, orang yang taat, orang yang menginfakkan hartanya, dan orang yang memohon ampunan pada waktu sebelum fajar.'),
	(311, 3, 18, 'شَهِدَ اللّٰهُ اَنَّهٗ لَآ اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰۤىِٕكَةُ وَاُولُوا الْعِلْمِ قَاۤىِٕمًاۢ بِالْقِسْطِۗ  لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ', 'syahida <strong>al</strong>l<u>aa</u>hu annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wa<strong>a</strong>lmal<u>aa</u>-ikatu wauluu <strong>a</strong>l\'ilmi q<u>aa</u>-iman bi<strong>a</strong>lqis<u>th</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Allah menyatakan bahwa tidak ada tuhan selain Dia; (demikian pula) para malaikat dan orang berilmu yang menegakkan keadilan, tidak ada tuhan selain Dia, Yang Mahaperkasa, Maha-bijaksana.'),
	(312, 3, 19, 'اِنَّ الدِّيْنَ عِنْدَ اللّٰهِ الْاِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗوَمَنْ يَّكْفُرْ بِاٰيٰتِ اللّٰهِ فَاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ', 'inna <strong>al</strong>ddiina \'inda <strong>al</strong>l<u>aa</u>hi <strong>a</strong>l-isl<u>aa</u>mu wam<u>aa</u> ikhtalafa <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba ill<u>aa</u> min ba\'di m<u>aa</u> j<u>aa</u>-ahumu <strong>a</strong>l\'ilmu baghyan baynahum waman yakfur bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi fa-inna <strong>al</strong>l<u>aa</u>ha sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Sesungguhnya agama di sisi Allah ialah Islam. Tidaklah berselisih orang-orang yang telah diberi Kitab kecuali setelah mereka memperoleh ilmu, karena kedengkian di antara mereka. Barangsiapa ingkar terhadap ayat-ayat Allah, maka sungguh, Allah sangat cepat perhitungan-Nya.'),
	(313, 3, 20, 'فَاِنْ حَاۤجُّوْكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّبَعَنِ ۗوَقُلْ لِّلَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْاُمِّيّٖنَ ءَاَسْلَمْتُمْ ۗ فَاِنْ اَسْلَمُوْا فَقَدِ اهْتَدَوْا ۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ ۗ وَاللّٰهُ بَصِيْرٌۢ ب', 'fa-in <u>haa</u>jjuuka faqul aslamtu wajhiya lill<u>aa</u>hi wamani ittaba\'ani waqul lilla<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<strong>-</strong>ummiyyiina a-aslamtum fa-in aslamuu faqadi ihtadaw wa-in tawallaw fa-innam<u>aa</u> \'alayka <strong>a</strong>lbal<u>aa</u>ghu wa<strong>al</strong>l<u>aa</u>hu ba<u>sh</u>iirun bi<strong>a</strong>l\'ib<u>aa</u>d<strong>i</strong>', 'Kemudian jika mereka membantah engkau (Muhammad) katakanlah, “Aku berserah diri kepada Allah dan (demikian pula) orang-orang yang mengikutiku.” Dan katakanlah kepada orang-orang yang telah diberi Kitab dan kepada orang-orang buta huruf, ”Sudahkah kamu masuk Islam?” Jika mereka masuk Islam, berarti mereka telah mendapat petunjuk, tetapi jika mereka berpaling, maka kewajibanmu hanyalah menyampaikan. Dan Allah Maha Melihat hamba-hamba-Nya.'),
	(314, 3, 21, 'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِحَقٍّۖ وَّيَقْتُلُوْنَ الَّذِيْنَ يَأْمُرُوْنَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍ', 'inna <strong>al</strong>la<u>dz</u>iina yakfuruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wayaqtuluuna <strong>al</strong>nnabiyyiina bighayri <u>h</u>aqqin wayaqtuluuna <strong>al</strong>la<u>dz</u>iina ya/muruuna bi<strong>a</strong>lqis<u>th</u>i mina <strong>al</strong>nn<u>aa</u>si fabasysyirhum bi\'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Sesungguhnya orang-orang yang mengingkari ayat-ayat Allah dan membunuh para nabi tanpa hak (alasan yang benar) dan membunuh orang-orang yang menyuruh manusia berbuat adil, sampaikanlah kepada mereka kabar gembira yaitu azab yang pedih.'),
	(315, 3, 22, 'اُولٰۤىِٕكَ الَّذِيْنَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wam<u>aa</u> lahum min n<u>aas</u>iriin<strong>a</strong>', 'Mereka itulah orang-orang yang sia-sia pekerjaannya di dunia dan di akhirat, dan mereka tidak memperoleh penolong.'),
	(316, 3, 23, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُدْعَوْنَ اِلٰى كِتٰبِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ وَهُمْ مُّعْرِضُوْنَ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu na<u>sh</u>iiban mina <strong>a</strong>lkit<u>aa</u>bi yud\'awna il<u>aa</u> kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi liya<u>h</u>kuma baynahum tsumma yatawall<u>aa</u> fariiqun minhum wahum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Tidakkah engkau memperhatikan orang-orang yang telah diberi bagian Kitab (Taurat)? Mereka diajak (berpegang) pada Kitab Allah untuk memutuskan (perkara) di antara mereka. Kemudian sebagian dari mereka berpaling seraya menolak (kebenaran).'),
	(317, 3, 24, 'ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدٰتٍ ۖ وَّغَرَّهُمْ فِيْ دِيْنِهِمْ مَّا كَانُوْا يَفْتَرُوْنَ', '<u>dzaa</u>lika bi-annahum q<u>aa</u>luu lan tamassan<u>aa</u> <strong>al</strong>nn<u>aa</u>ru ill<u>aa</u> ayy<u>aa</u>man ma\'duud<u>aa</u>tin wagharrahum fii diinihim m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Hal itu adalah karena mereka berkata, “Api neraka tidak akan menyentuh kami kecuali beberapa hari saja.” Mereka teperdaya dalam agama mereka oleh apa yang mereka ada-adakan.'),
	(318, 3, 25, 'فَكَيْفَ اِذَا جَمَعْنٰهُمْ لِيَوْمٍ لَّا رَيْبَ فِيْهِۗ وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'fakayfa i<u>dzaa</u> jama\'n<u>aa</u>hum liyawmin l<u>aa</u> rayba fiihi wawuffiyat kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Bagaimana jika (nanti) mereka Kami kumpulkan pada hari (Kiamat) yang tidak diragukan terjadinya dan kepada setiap jiwa diberi balasan yang sempurna sesuai dengan apa yang telah dikerjakannya dan mereka tidak dizalimi (dirugikan)?'),
	(319, 3, 26, 'قُلِ اللهم مٰلِكَ الْمُلْكِ تُؤْتِى الْمُلْكَ مَنْ تَشَاۤءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاۤءُۖ وَتُعِزُّ مَنْ تَشَاۤءُ وَتُذِلُّ مَنْ تَشَاۤءُ ۗ بِيَدِكَ الْخَيْرُ ۗ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'quli <strong>al</strong>l<u>aa</u>humma m<u>aa</u>lika <strong>a</strong>lmulki tu/tii <strong>a</strong>lmulka man tasy<u>aa</u>u watanzi\'u <strong>a</strong>lmulka mimman tasy<u>aa</u>u watu\'izzu man tasy<u>aa</u>u watu<u>dz</u>illu man tasy<u>aa</u>u biyadika <strong>a</strong>lkhayru innaka \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Katakanlah (Muhammad), “Wahai Tuhan pemilik kekuasaan, Engkau berikan kekuasaan kepada siapa pun yang Engkau kehendaki, dan Engkau cabut kekuasaan dari siapa pun yang Engkau kehendaki. Engkau muliakan siapa pun yang Engkau kehendaki dan Engkau hinakan siapa pun yang Engkau kehendaki. Di tangan Engkaulah segala kebajikan. Sungguh, Engkau Mahakuasa atas segala sesuatu.'),
	(320, 3, 27, 'تُوْلِجُ الَّيْلَ فِى النَّهَارِ وَتُوْلِجُ النَّهَارَ فِى الَّيْلِ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَتَرْزُقُ مَنْ تَشَاۤءُ بِغَيْرِ حِسَابٍ', 'tuuliju <strong>al</strong>layla fii <strong>al</strong>nnah<u>aa</u>ri watuuliju <strong>al</strong>nnah<u>aa</u>ra fii <strong>al</strong>layli watukhriju <strong>a</strong>l<u>h</u>ayya mina <strong>a</strong>lmayyiti watukhriju <strong>a</strong>lmayyita mina <strong>a</strong>l<u>h</u>ayyi watarzuqu man tasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b<strong>in</strong>', 'Engkau masukkan malam ke dalam siang dan Engkau masukkan siang ke dalam malam. Dan Engkau keluarkan yang hidup dari yang mati, dan Engkau keluarkan yang mati dari yang hidup. Dan Engkau berikan rezeki kepada siapa yang Engkau kehendaki tanpa perhitungan.”'),
	(321, 3, 28, 'لَا يَتَّخِذِ الْمُؤْمِنُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ فِيْ شَيْءٍ اِلَّآ اَنْ تَتَّقُوْا مِنْهُمْ تُقٰىةً ۗ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗ وَاِلَى اللّٰهِ الْمَصِيْرُ', 'l<u>aa</u> yattakhi<u>dz</u>i <strong>a</strong>lmu/minuuna <strong>a</strong>lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni <strong>a</strong>lmu/miniina waman yaf\'al <u>dzaa</u>lika falaysa mina <strong>al</strong>l<u>aa</u>hi fii syay-in ill<u>aa</u> an tattaquu minhum tuq<u>aa</u>tan wayu<u>h</u>a<u>dzdz</u>irukumu <strong>al</strong>l<u>aa</u>hu nafsahu wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Janganlah orang-orang beriman menjadikan orang kafir sebagai pemimpin, melainkan orang-orang beriman. Barang siapa berbuat demikian, niscaya dia tidak akan memperoleh apa pun dari Allah, kecuali karena (siasat) menjaga diri dari sesuatu yang kamu takuti dari mereka. Dan Allah memperingatkan kamu akan diri (siksa)-Nya, dan hanya kepada Allah tempat kembali.'),
	(322, 3, 29, 'قُلْ اِنْ تُخْفُوْا مَا فِيْ صُدُوْرِكُمْ اَوْ تُبْدُوْهُ يَعْلَمْهُ اللّٰهُ ۗوَيَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ  عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'qul in tukhfuu m<u>aa</u> fii <u>sh</u>uduurikum aw tubduuhu ya\'lamhu <strong>al</strong>l<u>aa</u>hu waya\'lamu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Katakanlah, “Jika kamu sembunyikan apa yang ada dalam hatimu atau kamu nyatakan, Allah pasti mengetahuinya.” Dia mengetahui apa yang ada di langit dan apa yang ada di bumi. Allah Mahakuasa atas segala sesuatu.'),
	(323, 3, 30, 'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَّا عَمِلَتْ مِنْ خَيْرٍ مُّحْضَرًا  ۛوَمَا عَمِلَتْ مِنْ سُوْۤءٍ ۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهٗٓ اَمَدًاۢ بَعِيْدًا ۗوَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ ࣖ', 'yawma tajidu kullu nafsin m<u>aa</u> \'amilat min khayrin mu<u>hd</u>aran wam<u>aa</u> \'amilat min suu-in tawaddu law anna baynah<u>aa</u> wabaynahu amadan ba\'iidan wayu<u>h</u>a<u>dzdz</u>irukumu <strong>al</strong>l<u>aa</u>hu nafsahu wa<strong>al</strong>l<u>aa</u>hu rauufun bi<strong>a</strong>l\'ib<u>aa</u>d<strong>i</strong>', '(Ingatlah) pada hari (ketika) setiap jiwa mendapatkan (balasan) atas kebajikan yang telah dikerjakan dihadapkan kepadanya, (begitu juga balasan) atas kejahatan yang telah dia kerjakan. Dia berharap sekiranya ada jarak yang jauh antara dia dengan (hari) itu. Dan Allah memperingatkan kamu akan diri (siksa)-Nya. Allah Maha Penyayang terhadap hamba-hamba-Nya.'),
	(324, 3, 31, 'قُلْ اِنْ كُنْتُمْ تُحِبُّوْنَ اللّٰهَ فَاتَّبِعُوْنِيْ يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'qul in kuntum tu<u>h</u>ibbuuna <strong>al</strong>l<u>aa</u>ha fa<strong>i</strong>ttabi\'uunii yu<u>h</u>bibkumu <strong>al</strong>l<u>aa</u>hu wayaghfir lakum <u>dz</u>unuubakum wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Katakanlah (Muhammad), “Jika kamu mencintai Allah, ikutilah aku, niscaya Allah mencintaimu dan mengampuni dosa-dosamu.” Allah Maha Pengampun, Maha Penyayang.'),
	(325, 3, 32, 'قُلْ اَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ ۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكٰفِرِيْنَ', 'qul a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha wa<strong>al</strong>rrasuula fa-in tawallaw fa-inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Katakanlah (Muhammad), “Taatilah Allah dan Rasul. Jika kamu berpaling, ketahuilah bahwa Allah tidak menyukai orang-orang kafir.”'),
	(326, 3, 33, '۞ اِنَّ اللّٰهَ اصْطَفٰىٓ اٰدَمَ وَنُوْحًا وَّاٰلَ اِبْرٰهِيْمَ وَاٰلَ عِمْرَانَ عَلَى الْعٰلَمِيْنَۙ', 'inna <strong>al</strong>l<u>aa</u>ha i<u>sth</u>af<u>aa</u> <u>aa</u>dama wanuu<u>h</u>an wa<u>aa</u>la ibr<u>aa</u>hiima wa<u>aa</u>la \'imr<u>aa</u>na \'al<u>aa</u> <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Sesungguhnya Allah telah memilih Adam, Nuh, keluarga Ibrahim dan keluarga Imran melebihi segala umat (pada masa masing-masing),'),
	(327, 3, 34, 'ذُرِّيَّةً ۢ بَعْضُهَا مِنْۢ بَعْضٍۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۚ', '<u>dz</u>urriyyatan ba\'<u>dh</u>uh<u>aa</u> min ba\'<u>dh</u>in wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', '(sebagai) satu keturunan, sebagiannya adalah (keturunan) dari sebagian yang lain. Allah Maha Mendengar, Maha Mengetahui.'),
	(328, 3, 35, 'اِذْ قَالَتِ امْرَاَتُ عِمْرَانَ رَبِّ اِنِّيْ نَذَرْتُ لَكَ مَا فِيْ بَطْنِيْ مُحَرَّرًا فَتَقَبَّلْ مِنِّيْ ۚ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ', 'i<u>dz</u> q<u>aa</u>lati imra-atu \'imr<u>aa</u>na rabbi innii na<u>dz</u>artu laka m<u>aa</u> fii ba<u>th</u>nii mu<u>h</u>arraran fataqabbal minnii innaka anta <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', '(Ingatlah), ketika istri Imran berkata, “Ya Tuhanku, sesungguhnya aku bernazar kepada-Mu, apa (janin) yang dalam kandunganku (kelak) menjadi hamba yang mengabdi (kepada-Mu), maka terimalah (nazar itu) dariku. Sungguh, Engkaulah Yang Maha Mendengar, Maha Mengetahui.”'),
	(329, 3, 36, 'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنِّيْ وَضَعْتُهَآ اُنْثٰىۗ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۗ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰى ۚ وَاِنِّيْ سَمَّيْتُهَا مَرْيَمَ وَاِنِّيْٓ اُعِيْذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطٰنِ الرَّجِيْمِ', 'falamm<u>aa</u> wa<u>dh</u>a\'at-h<u>aa</u> q<u>aa</u>lat rabbi innii wa<u>dh</u>a\'tuh<u>aa</u> unts<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> wa<u>dh</u>a\'at walaysa <strong>al</strong><u>dzdz</u>akaru ka<strong>a</strong>lunts<u>aa</u> wa-innii sammaytuh<u>aa</u> maryama wa-innii u\'ii<u>dz</u>uh<u>aa</u> bika wa<u>dz</u>urriyyatah<u>aa</u> mina <strong>al</strong>sysyay<u>th</u><u>aa</u>ni <strong>al</strong>rrajiim<strong>i</strong>', 'Maka ketika melahirkannya, dia berkata, “Ya Tuhanku, aku telah melahirkan anak perempuan.” Padahal Allah lebih tahu apa yang dia lahirkan, dan laki-laki tidak sama dengan perempuan. ”Dan aku memberinya nama Maryam, dan aku mohon perlindungan-Mu untuknya dan anak cucunya dari (gangguan) setan yang terkutuk.”'),
	(330, 3, 37, 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُوْلٍ حَسَنٍ وَّاَنْۢبَتَهَا نَبَاتًا حَسَنًاۖ وَّكَفَّلَهَا زَكَرِيَّا ۗ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقًا ۚ قَالَ يٰمَرْيَمُ اَنّٰى لَكِ هٰذَا ۗ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِ ۗ اِ', 'fataqabbalah<u>aa</u> rabbuh<u>aa</u> biqabuulin <u>h</u>asanin wa-anbatah<u>aa</u> nab<u>aa</u>tan <u>h</u>asanan wakaffalah<u>aa</u> zakariyy<u>aa</u> kullam<u>aa</u> dakhala \'alayh<u>aa</u> zakariyy<u>aa</u> <strong>a</strong>lmi<u>h</u>r<u>aa</u>ba wajada \'indah<u>aa</u> rizqan q<u>aa</u>la y<u>aa</u> maryamu ann<u>aa</u> laki h<u>aadzaa</u> q<u>aa</u>lat huwa min \'indi <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha yarzuqu man yasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b<strong>in</strong>', 'Maka Dia (Allah) menerimanya dengan penerimaan yang baik, membesarkannya dengan pertumbuhan yang baik dan menyerahkan pemeliharaannya kepada Zakaria. Setiap kali Zakaria masuk menemuinya di mihrab (kamar khusus ibadah), dia dapati makanan di sisinya. Dia berkata, “Wahai Maryam! Dari mana ini engkau peroleh?” Dia (Maryam) menjawab, “Itu dari Allah.” Sesungguhnya Allah memberi rezeki kepada siapa yang Dia kehendaki tanpa perhitungan.'),
	(331, 3, 38, 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهٗ ۚ قَالَ رَبِّ هَبْ لِيْ مِنْ لَّدُنْكَ ذُرِّيَّةً طَيِّبَةً ۚ اِنَّكَ سَمِيْعُ الدُّعَاۤءِ', 'hun<u>aa</u>lika da\'<u>aa</u> zakariyy<u>aa</u> rabbahu q<u>aa</u>la rabbi hab lii min ladunka <u>dz</u>urriyyatan <u>th</u>ayyibatan innaka samii\'u <strong>al</strong>ddu\'<u>aa</u>/-<strong>i</strong>', 'Di sanalah Zakaria berdoa kepada Tuhannya. Dia berkata, “Ya Tuhanku, berilah aku keturunan yang baik dari sisi-Mu, sesungguhnya Engkau Maha Mendengar doa.”'),
	(332, 3, 39, 'فَنَادَتْهُ الْمَلٰۤىِٕكَةُ وَهُوَ قَاۤىِٕمٌ يُّصَلِّيْ فِى الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقًاۢ بِكَلِمَةٍ مِّنَ اللّٰهِ وَسَيِّدًا وَّحَصُوْرًا وَّنَبِيًّا مِّنَ الصّٰلِحِيْنَ', 'fan<u>aa</u>dat-hu <strong>a</strong>lmal<u>aa</u>-ikatu wahuwa q<u>aa</u>-imun yu<u>sh</u>allii fii <strong>a</strong>lmi<u>h</u>r<u>aa</u>bi anna <strong>al</strong>l<u>aa</u>ha yubasysyiruka biya<u>h</u>y<u>aa</u> mu<u>sh</u>addiqan bikalimatin mina <strong>al</strong>l<u>aa</u>hi wasayyidan wa<u>h</u>a<u>sh</u>uuran wanabiyyan mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Kemudian para malaikat memanggilnya, ketika dia berdiri melaksanakan salat di mihrab, “Allah menyampaikan kabar gembira kepadamu dengan (kelahiran) Yahya, yang membenarkan sebuah kalimat (firman) dari Allah, panutan, berkemampuan menahan diri (dari hawa nafsu) dan seorang nabi di antara orang-orang saleh.”'),
	(333, 3, 40, 'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَتِيْ عَاقِرٌ ۗ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَاۤءُ', 'q<u>aa</u>la rabbi ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun waqad balaghaniya <strong>a</strong>lkibaru wa<strong>i</strong>mra-atii \'<u>aa</u>qirun q<u>aa</u>la ka<u>dzaa</u>lika <strong>al</strong>l<u>aa</u>hu yaf\'alu m<u>aa</u> yasy<u>aa</u>/<strong>u</strong>', 'Dia (Zakaria) berkata, “Ya Tuhanku, bagaimana aku bisa mendapat anak sedang aku sudah sangat tua dan istriku pun mandul?” Dia (Allah) berfirman, “Demikianlah, Allah berbuat apa yang Dia kehendaki.”'),
	(334, 3, 41, 'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزًا ۗ وَاذْكُرْ رَّبَّكَ كَثِيْرًا وَّسَبِّحْ بِالْعَشِيِّ وَالْاِبْكَارِ ࣖ', 'q<u>aa</u>la rabbi ij\'al lii <u>aa</u>yatan q<u>aa</u>la <u>aa</u>yatuka <strong>al</strong>l<u>aa</u> tukallima <strong>al</strong>nn<u>aa</u>sa tsal<u>aa</u>tsata ayy<u>aa</u>min ill<u>aa</u> ramzan wa<strong>u</strong><u>dz</u>kur rabbaka katsiiran wasabbi<u>h</u> bi<strong>a</strong>l\'asyiyyi wa<strong>a</strong>l-ibk<u>aa</u>r<strong>i</strong>', 'Dia (Zakaria) berkata, “Ya Tuhanku, berilah aku suatu tanda.” Allah berfirman, “Tanda bagimu, adalah bahwa engkau tidak berbicara dengan manusia selama tiga hari, kecuali dengan isyarat. Dan sebutlah (nama) Tuhanmu banyak-banyak, dan bertasbihlah (memuji-Nya) pada waktu petang dan pagi hari.”'),
	(335, 3, 42, 'وَاِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰىكِ وَطَهَّرَكِ وَاصْطَفٰىكِ عَلٰى نِسَاۤءِ الْعٰلَمِيْنَ', 'wa-i<u>dz</u> q<u>aa</u>lati <strong>a</strong>lmal<u>aa</u>-ikatu y<u>aa</u> maryamu inna <strong>al</strong>l<u>aa</u>ha i<u>sth</u>af<u>aa</u>ki wa<u>th</u>ahharaki wa<strong>i</strong><u>sth</u>af<u>aa</u>ki \'al<u>aa</u> nis<u>aa</u>-i <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan (ingatlah) ketika para malaikat berkata, “Wahai Maryam! Sesungguhnya Allah telah memilihmu, menyucikanmu, dan melebihkanmu di atas segala perempuan di seluruh alam (pada masa itu).'),
	(336, 3, 43, 'يٰمَرْيَمُ اقْنُتِيْ لِرَبِّكِ وَاسْجُدِيْ وَارْكَعِيْ مَعَ الرَّاكِعِيْنَ', 'y<u>aa</u> maryamu uqnutii lirabbiki wa<strong>u</strong>sjudii wa<strong>i</strong>rka\'ii ma\'a <strong>al</strong>rr<u>aa</u>ki\'iin<strong>a</strong>', 'Wahai Maryam! Taatilah Tuhanmu, sujud dan rukuklah bersama orang-orang yang rukuk.”'),
	(337, 3, 44, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَ ۗوَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُوْنَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُوْنَ', '<u>dzaa</u>lika min anb<u>aa</u>-i <strong>a</strong>lghaybi nuu<u>h</u>iihi ilayka wam<u>aa</u> kunta ladayhim i<u>dz</u> yulquuna aql<u>aa</u>mahum ayyuhum yakfulu maryama wam<u>aa</u> kunta ladayhim i<u>dz</u> yakhta<u>sh</u>imuun<strong>a</strong>', 'Itulah sebagian dari berita-berita gaib yang Kami wahyukan kepadamu (Muhammad), padahal engkau tidak bersama mereka ketika mereka melemparkan pena mereka (untuk mengundi) siapa di antara mereka yang akan memelihara Maryam. Dan engkau pun tidak bersama mereka ketika mereka bertengkar.'),
	(338, 3, 45, 'اِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِّنْهُۖ اسْمُهُ الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ وَجِيْهًا فِى الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّبِيْنَۙ', 'i<u>dz</u> q<u>aa</u>lati <strong>a</strong>lmal<u>aa</u>-ikatu y<u>aa</u> maryamu inna <strong>al</strong>l<u>aa</u>ha yubasysyiruki bikalimatin minhu ismuhu <strong>a</strong>lmasii<u>h</u>u \'iis<u>aa</u> ibnu maryama wajiihan fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wamina <strong>a</strong>lmuqarrabiin<strong>a</strong>', '(Ingatlah), ketika para malaikat berkata, “Wahai Maryam! Sesungguhnya Allah menyampaikan kabar gembira kepadamu tentang sebuah kalimat (fir-man) dari-Nya (yaitu seorang putra), namanya Al-Masih Isa putra Maryam, seorang terkemuka di dunia dan di akhirat, dan termasuk orang-orang yang didekatkan (kepada Allah),'),
	(339, 3, 46, 'وَيُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا وَّمِنَ الصّٰلِحِيْنَ', 'wayukallimu <strong>al</strong>nn<u>aa</u>sa fii <strong>a</strong>lmahdi wakahlan wamina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'dan dia berbicara dengan manusia (sewaktu) dalam buaian dan ketika sudah dewasa, dan dia termasuk di antara orang-orang saleh.”'),
	(340, 3, 47, 'قَالَتْ رَبِّ اَنّٰى يَكُوْنُ لِيْ وَلَدٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ ۗ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَاۤءُ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ', 'q<u>aa</u>lat rabbi ann<u>aa</u> yakuunu lii waladun walam yamsasnii basyarun q<u>aa</u>la ka<u>dzaa</u>liki <strong>al</strong>l<u>aa</u>hu yakhluqu m<u>aa</u> yasy<u>aa</u>u i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun<strong>u</strong>', 'Dia (Maryam) berkata, “Ya Tuhanku, bagaimana mungkin aku akan mempunyai anak, padahal tidak ada seorang laki-laki pun yang menyentuhku?” Dia (Allah) berfirman, “Demikianlah Allah menciptakan apa yang Dia kehendaki. Apabila Dia hendak menetapkan sesuatu, Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu.'),
	(341, 3, 48, 'وَيُعَلِّمُهُ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَۚ', 'wayu\'allimuhu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wa<strong>al</strong>ttawr<u>aa</u>ta wa<strong>a</strong>l-injiil<strong>a</strong>', 'Dan Dia (Allah) mengajarkan kepadanya (Isa) Kitab, Hikmah, Taurat, dan Injil.'),
	(342, 3, 49, 'وَرَسُوْلًا اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ اَنِّيْ قَدْ جِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْ ۙاَنِّيْٓ اَخْلُقُ لَكُمْ مِّنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ فِيْهِ فَيَكُوْنُ طَيْرًاۢ بِاِذْنِ اللّٰهِ ۚوَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُ', 'warasuulan il<u>aa</u> banii isr<u>aa</u>-iila annii qad ji/tukum bi-<u>aa</u>yatin min rabbikum annii akhluqu lakum mina <strong>al</strong><u>ththh</u>iini kahay-ati <strong>al</strong><u>ththh</u>ayri fa-anfukhu fiihi fayakuunu <u>th</u>ayran bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi waubri-u <strong>a</strong>l-akmaha wa<strong>a</strong>l-abra<u>sh</u>a wau<u>h</u>yii <strong>a</strong>lmawt<u>aa</u> bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi wa-unabbi-ukum bim<u>aa</u> ta/kuluuna wam<u>aa</u> taddakhiruuna fii buyuutikum inna fii <u>dzaa</u>lika la<u>aa</u>yatan lakum in kuntum mu/miniin<strong>a</strong>', 'Dan sebagai Rasul kepada Bani Israil (dia berkata), “Aku telah datang kepada kamu dengan sebuah tanda (mukjizat) dari Tuhanmu, yaitu aku membuatkan bagimu (sesuatu) dari tanah berbentuk seperti burung, lalu aku meniupnya, maka ia menjadi seekor burung dengan izin Allah. Dan aku menyembuhkan orang yang buta sejak dari lahir dan orang yang berpenyakit kusta. Dan aku menghidupkan orang mati dengan izin Allah, dan aku beritahukan kepadamu apa yang kamu makan dan apa yang kamu simpan di rumahmu. Sesungguhnya pada yang demikian itu terdapat suatu tanda (kebenaran kerasulanku) bagimu, jika kamu orang beriman.'),
	(343, 3, 50, 'وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذِيْ حُرِّمَ عَلَيْكُمْ  وَجِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْۗ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ', 'wamu<u>sh</u>addiqan lim<u>aa</u> bayna yadayya mina <strong>al</strong>ttawr<u>aa</u>ti wali-u<u>h</u>illa lakum ba\'<u>dh</u>a <strong>al</strong>la<u>dz</u>ii <u>h</u>urrima \'alaykum waji/tukum bi-<u>aa</u>yatin min rabbikum fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'Dan sebagai seorang yang membenarkan Taurat yang datang sebelumku, dan agar aku menghalalkan bagi kamu sebagian dari yang telah diharamkan untukmu. Dan aku datang kepadamu membawa suatu tanda (mukjizat) dari Tuhanmu. Karena itu, bertakwalah kepada Allah dan taatlah kepadaku.'),
	(344, 3, 51, 'اِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ', 'inna <strong>al</strong>l<u>aa</u>ha rabbii warabbukum fa<strong>u</strong>\'buduuhu h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un mustaqiim<strong>un</strong>', 'Sesungguhnya Allah itu Tuhanku dan Tuhanmu, karena itu sembahlah Dia. Inilah jalan yang lurus.”'),
	(345, 3, 52, '۞ فَلَمَّآ اَحَسَّ عِيْسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗ قَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ ۚ اٰمَنَّا بِاللّٰهِ ۚ وَاشْهَدْ بِاَنَّا مُسْلِمُوْنَ', 'falamm<u>aa</u> a<u>h</u>assa \'iis<u>aa</u> minhumu <strong>a</strong>lkufra q<u>aa</u>la man an<u>shaa</u>rii il<u>aa</u> <strong>al</strong>l<u>aa</u>hi q<u>aa</u>la <strong>a</strong>l<u>h</u>aw<u>aa</u>riyyuuna na<u>h</u>nu an<u>shaa</u>ru <strong>al</strong>l<u>aa</u>hi <u>aa</u>mann<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wa<strong>i</strong>syhad bi-ann<u>aa</u> muslimuun<strong>a</strong>', 'Maka ketika Isa merasakan keingkaran mereka (Bani Israil), dia berkata, “Siapakah yang akan menjadi penolong untuk (menegakkan agama) Allah?” Para Hawariyyun (sahabat setianya) menjawab, “Kamilah penolong (agama) Allah. Kami beriman kepada Allah, dan saksikanlah, bahwa kami adalah orang-orang Muslim.'),
	(346, 3, 53, 'رَبَّنَآ اٰمَنَّا بِمَآ اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُوْلَ فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ', 'rabban<u>aa</u> <u>aa</u>mann<u>aa</u> bim<u>aa</u> anzalta wa<strong>i</strong>ttaba\'n<u>aa</u> <strong>al</strong>rrasuula fa<strong>u</strong>ktubn<u>aa</u> ma\'a <strong>al</strong>sysy<u>aa</u>hidiin<strong>a</strong>', 'Ya Tuhan kami, kami telah beriman kepada apa yang Engkau turunkan dan kami telah mengikuti Rasul, karena itu tetapkanlah kami bersama golongan orang yang memberikan kesaksian.”'),
	(347, 3, 54, 'وَمَكَرُوْا وَمَكَرَ اللّٰهُ  ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ ࣖ', 'wamakaruu wamakara <strong>al</strong>l<u>aa</u>hu wa<strong>al</strong>l<u>aa</u>hu khayru <strong>a</strong>lm<u>aa</u>kir', 'Dan mereka (orang-orang kafir) membuat tipu daya, maka Allah pun membalas tipu daya. Dan Allah sebaik-baik pembalas tipu daya.'),
	(348, 3, 55, 'اِذْ قَالَ اللّٰهُ يٰعِيْسٰٓى اِنِّيْ مُتَوَفِّيْكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِيْنَ كَفَرُوْا وَجَاعِلُ الَّذِيْنَ اتَّبَعُوْكَ فَوْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى يَوْمِ الْقِيٰمَةِ ۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ فِيْ', 'i<u>dz</u> q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> innii mutawaffiika war<u>aa</u>fi\'uka ilayya wamu<u>th</u>ahhiruka mina <strong>al</strong>la<u>dz</u>iina kafaruu waj<u>aa</u>\'ilu <strong>al</strong>la<u>dz</u>iina ittaba\'uuka fawqa <strong>al</strong>la<u>dz</u>iina kafaruu il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati tsumma ilayya marji\'ukum fa-a<u>h</u>kumu baynakum fiim<u>aa</u> kuntum fiihi takhtalifuun<strong>a</strong>', '(Ingatlah), ketika Allah berfirman, “Wahai Isa! Aku mengambilmu dan mengangkatmu kepada-Ku, serta menyucikanmu dari orang-orang yang kafir, dan menjadikan orang-orang yang mengikutimu di atas orang-orang yang kafir hingga hari Kiamat. Kemudian kepada-Ku engkau kembali, lalu Aku beri keputusan tentang apa yang kamu perselisihkan.”'),
	(349, 3, 56, 'فَاَمَّا الَّذِيْنَ كَفَرُوْا فَاُعَذِّبُهُمْ عَذَابًا شَدِيْدًا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ', 'fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu fau\'a<u>dzdz</u>ibuhum \'a<u>dzaa</u>ban syadiidan fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wam<u>aa</u> lahum min n<u>aas</u>iriin<strong>a</strong>', 'Maka adapun orang-orang yang kafir, maka akan Aku azab mereka dengan azab yang sangat keras di dunia dan di akhirat, sedang mereka tidak memperoleh penolong.'),
	(350, 3, 57, 'وَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ ۗ وَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti fayuwaffiihim ujuurahum wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan adapun orang yang beriman dan melakukan kebajikan, maka Dia akan memberikan pahala kepada mereka dengan sempurna. Dan Allah tidak menyukai orang zalim.'),
	(351, 3, 58, 'ذٰلِكَ نَتْلُوْهُ عَلَيْكَ مِنَ الْاٰيٰتِ وَالذِّكْرِ الْحَكِيْمِ ', '<u>dzaa</u>lika natluuhu \'alayka mina <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti wa<strong>al</strong><u>dzdz</u>ikri <strong>a</strong>l<u>h</u>akiim<strong>i</strong>', 'Demikianlah Kami bacakan kepadamu (Muhammad) sebagian ayat-ayat dan peringatan yang penuh hikmah.'),
	(352, 3, 59, 'اِنَّ مَثَلَ عِيْسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَ ۗ خَلَقَهٗ مِنْ تُرَابٍ ثُمَّ قَالَ لَهٗ كُنْ فَيَكُوْنُ', 'inna matsala \'iis<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi kamatsali <u>aa</u>dama khalaqahu min tur<u>aa</u>bin tsumma q<u>aa</u>la lahu kun fayakuun<strong>u</strong>', 'Sesungguhnya perumpamaan (penciptaan) Isa bagi Allah, seperti (penciptaan) Adam. Dia menciptakannya dari tanah, kemudian Dia berkata kepadanya, “Jadilah!”  Maka jadilah sesuatu itu.'),
	(353, 3, 60, 'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُنْ مِّنَ الْمُمْتَرِيْنَ', 'al<u>h</u>aqqu min rabbika fal<u>aa</u> takun mina <strong>a</strong>lmumtariin<strong>a</strong>', 'Kebenaran itu dari Tuhanmu, karena itu janganlah engkau (Muhammad) termasuk orang-orang yang ragu.'),
	(354, 3, 61, 'فَمَنْ حَاۤجَّكَ فِيْهِ مِنْۢ بَعْدِ مَا جَاۤءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَاۤءَنَا وَاَبْنَاۤءَكُمْ وَنِسَاۤءَنَا وَنِسَاۤءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْۗ  ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَّعْنَتَ اللّٰهِ عَلَى الْكٰذِبِيْنَ', 'faman <u>haa</u>jjaka fiihi min ba\'di m<u>aa</u> j<u>aa</u>-aka mina <strong>a</strong>l\'ilmi faqul ta\'<u>aa</u>law nad\'u abn<u>aa</u>-an<u>aa</u> wa-abn<u>aa</u>-akum wanis<u>aa</u>-an<u>aa</u> wanis<u>aa</u>-akum wa-anfusan<u>aa</u> wa-anfusakum tsumma nabtahil fanaj\'al la\'nata <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Siapa yang membantahmu dalam hal ini setelah engkau memperoleh ilmu, katakanlah (Muhammad), “Marilah kita panggil anak-anak kami dan anak-anak kamu, istri-istri kami dan istri-istrimu, kami sendiri dan kamu juga, kemudian marilah kita bermubahalah agar laknat Allah ditimpakan kepada orang-orang yang dusta.”'),
	(355, 3, 62, 'اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ ۗوَاِنَّ اللّٰهَ لَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'inna h<u>aadzaa</u> lahuwa <strong>a</strong>lqa<u>sh</u>a<u>sh</u>u <strong>a</strong>l<u>h</u>aqqu wam<u>aa</u> min il<u>aa</u>hin ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu wa-inna <strong>al</strong>l<u>aa</u>ha lahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Sungguh, ini adalah kisah yang benar. Tidak ada tuhan selain Allah, dan sungguh, Allah Mahaperkasa, Mahabijaksana.'),
	(356, 3, 63, 'فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَلِيْمٌ ۢبِالْمُفْسِدِيْنَ ࣖ ', 'fa-in tawallaw fa-inna <strong>al</strong>l<u>aa</u>ha \'aliimun bi<strong>a</strong>lmufsidiin<strong>a</strong>', 'Kemudian jika mereka berpaling, maka (ketahuilah) bahwa Allah Maha Mengetahui orang-orang yang berbuat kerusakan.'),
	(357, 3, 64, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَاۤءٍۢ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِهٖ شَيْـًٔا وَّلَا يَتَّخِذَ بَعْضُنَا بَعْضًا اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَقُوْلُوا اشْهَدُوْا بِ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi ta\'<u>aa</u>law il<u>aa</u> kalimatin saw<u>aa</u>-in baynan<u>aa</u> wabaynakum <strong>al</strong>l<u>aa</u> na\'buda ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> nusyrika bihi syay-an wal<u>aa</u> yattakhi<u>dz</u>a ba\'<u>dh</u>un<u>aa</u> ba\'<u>dh</u>an arb<u>aa</u>ban min duuni <strong>al</strong>l<u>aa</u>hi fa-in tawallaw faquuluu isyhaduu bi-ann<u>aa</u> muslimuun<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai Ahli Kitab! Marilah (kita) menuju kepada satu kalimat (pegangan) yang sama antara kami dan kamu, bahwa kita tidak menyembah selain Allah dan kita tidak mempersekutukan-Nya dengan sesuatu pun, dan bahwa kita tidak menjadikan satu sama lain tuhan-tuhan selain Allah. Jika mereka berpaling maka katakanlah (kepada mereka), “Saksikanlah, bahwa kami adalah orang Muslim.”'),
	(358, 3, 65, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تُحَاۤجُّوْنَ فِيْٓ اِبْرٰهِيْمَ وَمَآ اُنْزِلَتِ التَّوْرٰىةُ وَالْاِنْجِيْلُ اِلَّا مِنْۢ بَعْدِهٖۗ  اَفَلَا تَعْقِلُوْنَ', 'yaa ahla alkitaabi lima tuhaajjuuna fii ibraahiima wamaa unzilati alttawraatu waal-injiilu illaa min ba\'dihi afalaa ta\'qiluuna', 'Wahai Ahli Kitab! Mengapa kamu berbantah-bantahan tentang Ibrahim, padahal Taurat dan Injil diturunkan setelah dia (Ibrahim)? Apakah kamu tidak mengerti?'),
	(359, 3, 66, 'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ حَاجَجْتُمْ فِيْمَا لَكُمْ بِهٖ عِلْمٌ فَلِمَ تُحَاۤجُّوْنَ فِيْمَا لَيْسَ لَكُمْ بِهٖ عِلْمٌ ۗ وَاللّٰهُ يَعْلَمُ واَنْتُمْ لَا تَعْلَمُوْنَ', 'h<u>aa</u> antum h<u>aa</u>ul<u>aa</u>-i <u>haa</u>jajtum fiim<u>aa</u> lakum bihi \'ilmun falima tu<u>haa</u>jjuuna fiim<u>aa</u> laysa lakum bihi \'ilmun wa<strong>al</strong>l<u>aa</u>hu ya\'lamu wa-antum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Begitulah kamu! Kamu berbantah-bantahan tentang apa yang kamu ketahui, tetapi mengapa kamu berbantah-bantahan juga tentang apa yang tidak kamu ketahui? Allah mengetahui sedang kamu tidak mengetahui.'),
	(360, 3, 67, 'مَاكَانَ اِبْرٰهِيْمُ يَهُوْدِيًّا وَّلَا نَصْرَانِيًّا وَّلٰكِنْ كَانَ حَنِيْفًا مُّسْلِمًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'maa kaana ibraahiimu yahuudiyyan walaa nashraaniyyan walaakin kaana haniifan musliman wamaa kaana mina almusyrikiina', 'Ibrahim bukanlah seorang Yahudi dan bukan (pula) seorang Nasrani, tetapi dia adalah seorang yang lurus, Muslim dan dia tidaklah termasuk orang-orang musyrik.'),
	(361, 3, 68, 'اِنَّ اَوْلَى النَّاسِ بِاِبْرٰهِيْمَ لَلَّذِيْنَ اتَّبَعُوْهُ وَهٰذَا النَّبِيُّ وَالَّذِيْنَ اٰمَنُوْا ۗ وَاللّٰهُ وَلِيُّ الْمُؤْمِنِيْنَ', 'inna awl<u>aa</u> <strong>al</strong>nn<u>aa</u>si bi-ibr<u>aa</u>hiima lalla<u>dz</u>iina ittaba\'uuhu wah<u>aadzaa</u> <strong>al</strong>nnabiyyu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa<strong>al</strong>l<u>aa</u>hu waliyyu <strong>a</strong>lmu/miniin<strong>a</strong>', 'Orang yang paling dekat kepada Ibrahim ialah orang yang mengikutinya, dan Nabi ini (Muhammad), dan orang yang beriman. Allah adalah pelindung orang-orang yang beriman.'),
	(362, 3, 69, 'وَدَّتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يُضِلُّوْنَكُمْۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ', 'waddat <u>th</u><u>aa</u>-ifatun min ahli <strong>a</strong>lkit<u>aa</u>bi law yu<u>dh</u>illuunakum wam<u>aa</u> yu<u>dh</u>illuuna ill<u>aa</u> anfusahum wam<u>aa</u> yasy\'uruun<strong>a</strong>', 'Segolongan Ahli Kitab ingin menyesatkan kamu. Padahal (sesungguhnya), mereka tidak menyesatkan melainkan diri mereka sendiri, tetapi mereka tidak menyadari.'),
	(363, 3, 70, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُوْنَ', 'y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi lima takfuruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wa-antum tasyhaduun<strong>a</strong>', 'Wahai Ahli Kitab! Mengapa kamu mengingkari ayat-ayat Allah, padahal kamu mengetahui (kebenarannya)?'),
	(364, 3, 71, 'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَلْبِسُوْنَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوْنَ الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ ࣖ', 'y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi lima talbisuuna <strong>a</strong>l<u>h</u>aqqa bi<strong>a</strong>lb<u>aath</u>ili wataktumuuna <strong>a</strong>l<u>h</u>aqqa wa-antum ta\'lamuun<strong>a</strong>', 'Wahai Ahli Kitab! Mengapa kamu mencampuradukkan kebenaran dengan kebatilan, dan kamu menyembunyikan kebenaran, padahal kamu mengetahui?'),
	(365, 3, 72, 'وَقَالَتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ اٰمِنُوْا بِالَّذِيْٓ اُنْزِلَ عَلَى الَّذِيْنَ اٰمَنُوْا وَجْهَ النَّهَارِ وَاكْفُرُوْٓا اٰخِرَهٗ لَعَلَّهُمْ يَرْجِعُوْنَۚ', 'waq<u>aa</u>lat <u>th</u><u>aa</u>-ifatun min ahli <strong>a</strong>lkit<u>aa</u>bi <u>aa</u>minuu bi<strong>a</strong>lla<u>dz</u>ii unzila \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wajha <strong>al</strong>nnah<u>aa</u>ri wa<strong>u</strong>kfuruu <u>aa</u>khirahu la\'allahum yarji\'uun<strong>a</strong>', 'Dan segolongan Ahli Kitab berkata (kepada sesamanya), “Berimanlah kamu kepada apa yang diturunkan kepada orang-orang beriman pada awal siang dan ingkarilah di akhirnya, agar mereka kembali (kepada kekafiran).'),
	(366, 3, 73, 'وَلَا تُؤْمِنُوْٓا اِلَّا لِمَنْ تَبِعَ دِيْنَكُمْ ۗ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِ ۙ اَنْ يُّؤْتٰىٓ اَحَدٌ مِّثْلَ مَآ اُوْتِيْتُمْ اَوْ يُحَاۤجُّوْكُمْ عِنْدَ رَبِّكُمْ ۗ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِ ۚ يُؤْتِيْهِ مَنْ يَّشَاۤءُ  ۗوَاللّٰهُ وَ', 'walaa tu/minuu illaa liman tabi\'a diinakum qul inna alhudaa hudaa allaahi an yu/taa ahadun mitsla maa uutiitum aw yuhaajjuukum \'inda rabbikum qul inna alfadhla biyadi allaahi yu/tiihi man yasyaau waallaahu waasi\'un \'aliimun', 'Dan janganlah kamu percaya selain kepada orang yang mengikuti agamamu.” Katakanlah (Muhammad), “Sesungguhnya petunjuk itu hanyalah petunjuk Allah. (Janganlah kamu percaya) bahwa seseorang akan diberi seperti apa yang diberikan kepada kamu, atau bahwa mereka akan menyanggah kamu di hadapan Tuhanmu.” Katakanlah (Muhammad), “Sesungguhnya karunia itu di tangan Allah, Dia memberikannya kepada siapa yang Dia kehendaki. Allah Mahaluas, Maha Mengetahui.”'),
	(367, 3, 74, 'يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ  ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'yakhta<u>shsh</u>u bira<u>h</u>matihi man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu <u>dz</u>uu <strong>a</strong>lfa<u>dh</u>li <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Dia menentukan rahmat-Nya kepada siapa yang Dia kehendaki. Dan Allah memiliki karunia yang besar.'),
	(368, 3, 75, '۞ وَمِنْ اَهْلِ الْكِتٰبِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُّؤَدِّهٖٓ اِلَيْكَۚ وَمِنْهُمْ مَّنْ اِنْ تَأْمَنْهُ بِدِيْنَارٍ لَّا يُؤَدِّهٖٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَاۤىِٕمًا ۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَيْسَ عَلَيْنَا فِى الْاُمِّيّٖنَ س', 'wamin ahli <strong>a</strong>lkit<u>aa</u>bi man in ta/manhu biqin<u>th</u><u>aa</u>rin yu-addihi ilayka waminhum man in ta/manhu bidiin<u>aa</u>rin l<u>aa</u> yu-addihi ilayka ill<u>aa</u> m<u>aa</u> dumta \'alayhi q<u>aa</u>-iman <u>dzaa</u>lika bi-annahum q<u>aa</u>luu laysa \'alayn<u>aa</u> fii <strong>a</strong>l-ummiyyiina sabiilun wayaquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba wahum ya\'lamuun<strong>a</strong>', 'Dan di antara Ahli Kitab ada yang jika engkau percayakan kepadanya harta yang banyak, niscaya dia mengembalikannya kepadamu. Tetapi ada (pula) di antara mereka yang jika engkau percayakan kepadanya satu dinar, dia tidak mengembalikannya kepadamu, kecuali jika engkau selalu menagihnya. Yang demikian itu disebabkan mereka berkata, “Tidak ada dosa bagi kami terhadap orang-orang buta huruf.” Mereka mengatakan hal yang dusta terhadap Allah, padahal mereka mengetahui.'),
	(369, 3, 76, 'بَلٰى مَنْ اَوْفٰى بِعَهْدِهٖ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ', 'bal<u>aa</u> man awf<u>aa</u> bi\'ahdihi wa<strong>i</strong>ttaq<u>aa</u> fa-inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Sebenarnya barangsiapa menepati janji dan bertakwa, maka sungguh, Allah mencintai orang-orang yang bertakwa.'),
	(370, 3, 77, 'اِنَّ الَّذِيْنَ يَشْتَرُوْنَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَنًا قَلِيْلًا اُولٰۤىِٕكَ لَا خَلَاقَ لَهُمْ فِى الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ', 'inna <strong>al</strong>la<u>dz</u>iina yasytaruuna bi\'ahdi <strong>al</strong>l<u>aa</u>hi wa-aym<u>aa</u>nihim tsamanan qaliilan ul<u>aa</u>-ika l<u>aa</u> khal<u>aa</u>qa lahum fii <strong>a</strong>l-<u>aa</u>khirati wal<u>aa</u> yukallimuhumu <strong>al</strong>l<u>aa</u>hu wal<u>aa</u> yan<u>zh</u>uru ilayhim yawma <strong>a</strong>lqiy<u>aa</u>mati wal<u>aa</u> yuzakkiihim walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sesungguhnya orang-orang yang memperjualbelikan janji Allah dan sumpah-sumpah mereka dengan harga murah, mereka itu tidak memperoleh bagian di akhirat, Allah tidak akan menyapa mereka, tidak akan memperhatikan mereka pada hari Kiamat, dan tidak akan menyucikan mereka. Bagi mereka azab yang pedih.'),
	(371, 3, 78, 'وَاِنَّ مِنْهُمْ لَفَرِيْقًا يَّلْوٗنَ اَلْسِنَتَهُمْ بِالْكِتٰبِ لِتَحْسَبُوْهُ مِنَ الْكِتٰبِ وَمَا هُوَ مِنَ الْكِتٰبِۚ وَيَقُوْلُوْنَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِ ۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ', 'wa-inna minhum lafariiqan yalwuuna <strong>a</strong>lsinatahum bi<strong>a</strong>lkit<u>aa</u>bi lita<u>h</u>sabuuhu mina <strong>a</strong>lkit<u>aa</u>bi wam<u>aa</u> huwa mina <strong>a</strong>lkit<u>aa</u>bi wayaquuluuna huwa min \'indi <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> huwa min \'indi <strong>al</strong>l<u>aa</u>hi wayaquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba wahum ya\'lamuun<strong>a</strong>', 'Dan sungguh, di antara mereka niscaya ada segolongan yang memutarbalikkan lidahnya membaca Kitab, agar kamu menyangka (yang mereka baca) itu sebagian dari Kitab, padahal itu bukan dari Kitab dan mereka berkata, “Itu dari Allah,” padahal itu bukan dari Allah. Mereka mengatakan hal yang dusta terhadap Allah, padahal mereka mengetahui.'),
	(372, 3, 79, 'مَا كَانَ لِبَشَرٍ اَنْ يُّؤْتِيَهُ اللّٰهُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُوْلَ لِلنَّاسِ كُوْنُوْا عِبَادًا لِّيْ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ كُوْنُوْا رَبَّانِيّٖنَ بِمَا كُنْتُمْ تُعَلِّمُوْنَ الْكِتٰبَ وَبِمَا كُنْتُمْ تَدْرُسُوْنَ ۙ', 'm<u>aa</u> k<u>aa</u>na libasyarin an yu/tiyahu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ukma wa<strong>al</strong>nnubuwwata tsumma yaquula li<strong>l</strong>nn<u>aa</u>si kuunuu \'ib<u>aa</u>dan lii min duuni <strong>al</strong>l<u>aa</u>hi wal<u>aa</u>kin kuunuu rabb<u>aa</u>niyyiina bim<u>aa</u> kuntum tu\'allimuuna <strong>a</strong>lkit<u>aa</u>ba wabim<u>aa</u> kuntum tadrusuun<strong>a</strong>', 'Tidak mungkin bagi seseorang yang telah diberi kitab oleh Allah, serta hikmah dan kenabian, kemudian dia berkata kepada manusia, “Jadilah kamu penyembahku, bukan penyembah Allah,” tetapi (dia berkata), “Jadilah kamu pengabdi-pengabdi Allah, karena kamu mengajarkan kitab dan karena kamu mempelajarinya!”'),
	(373, 3, 80, 'وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰۤىِٕكَةَ وَالنَّبِيّٖنَ اَرْبَابًا ۗ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُّسْلِمُوْنَ ࣖ', 'wal<u>aa</u> ya/murakum an tattakhi<u>dz</u>uu <strong>a</strong>lmal<u>aa</u>-ikata wa<strong>al</strong>nnabiyyiina arb<u>aa</u>ban aya/murukum bi<strong>a</strong>lkufri ba\'da i<u>dz</u> antum muslimuun<strong>a</strong>', 'dan tidak (mungkin pula baginya) menyuruh kamu menjadikan para malaikat dan para nabi sebagai Tuhan. Apakah (patut) dia menyuruh kamu menjadi kafir setelah kamu menjadi Muslim?'),
	(374, 3, 81, 'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ النَّبِيّٖنَ لَمَآ اٰتَيْتُكُمْ مِّنْ كِتٰبٍ وَّحِكْمَةٍ ثُمَّ جَاۤءَكُمْ رَسُوْلٌ مُّصَدِّقٌ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهٖ وَلَتَنْصُرُنَّهٗ ۗ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْرِيْ ۗ قَالُوْٓا اَق', 'wa-idz akhadza allaahu miitsaaqa alnnabiyyiina lamaa aataytukum min kitaabin wahikmatin tsumma jaa-akum rasuulun mushaddiqun limaa ma\'akum latu/minunna bihi walatanshurunnahu qaala a-aqrartum wa-akhadztum \'alaa dzaalikum ishrii qaaluu aqrarnaa qaala faisyhaduu wa-anaa ma\'akum mina alsysyaahidiina', 'Dan (ingatlah), ketika Allah mengambil perjanjian dari para nabi, “Manakala Aku memberikan kitab dan hikmah kepadamu lalu datang kepada kamu seorang Rasul yang membenarkan apa yang ada pada kamu, niscaya kamu akan sungguh-sungguh beriman kepadanya dan menolongnya.” Allah berfirman, “Apakah kamu setuju dan menerima perjanjian dengan-Ku atas yang demikian itu?” Mereka menjawab, “Kami setuju.” Allah berfirman,  ”Kalau begitu bersaksilah kamu (para nabi) dan Aku menjadi saksi bersama kamu.”'),
	(375, 3, 82, 'فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'faman tawall<u>aa</u> ba\'da <u>dzaa</u>lika faul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Maka barangsiapa berpaling setelah itu, maka mereka itulah orang yang fasik.'),
	(376, 3, 83, 'اَفَغَيْرَ دِيْنِ اللّٰهِ يَبْغُوْنَ وَلَهٗ ٓ اَسْلَمَ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّاِلَيْهِ يُرْجَعُوْنَ', 'afaghayra diini <strong>al</strong>l<u>aa</u>hi yabghuuna walahu aslama man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <u>th</u>aw\'an wakarhan wa-ilayhi yurja\'uun<strong>a</strong>afaghayra diini <strong>al</strong>l<u>aa</u>hi yabghuuna walahu aslama man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <u>th</u>aw\'an wakarhan wa-ilayhi yurja\'uun<strong>a</strong>', 'Maka mengapa mereka mencari agama yang lain selain agama Allah, padahal apa yang di langit dan di bumi berserah diri kepada-Nya, (baik) dengan suka maupun terpaksa, dan hanya kepada-Nya mereka dikembalikan?'),
	(377, 3, 84, 'قُلْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ عَلَيْنَا وَمَآ اُنْزِلَ عَلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَالنَّبِيُّوْنَ مِنْ رَّبِّهِمْۖ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْن', 'qul <u>aa</u>mann<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wam<u>aa</u> unzila \'alayn<u>aa</u> wam<u>aa</u> unzila \'al<u>aa</u> ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa waya\'quuba wa<strong>a</strong>l-asb<u>aath</u>i wam<u>aa</u> uutiya muus<u>aa</u> wa\'iis<u>aa</u> wa<strong>al</strong>nnabiyyuuna min rabbihim l<u>aa</u> nufarriqu bayna a<u>h</u>adin minhum wana<u>h</u>nu lahu muslimuun<strong>a</strong>', 'Katakanlah (Muhammad), “Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami dan yang diturunkan kepada Ibrahim, Ismail, Ishak, Yakub, dan anak cucunya, dan apa yang diberikan kepada Musa, Isa dan para nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorang pun di antara mereka dan hanya kepada-Nya kami berserah diri.”'),
	(378, 3, 85, 'وَمَنْ يَّبْتَغِ غَيْرَ الْاِسْلَامِ دِيْنًا فَلَنْ يُّقْبَلَ مِنْهُۚ وَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ', 'waman yabtaghi ghayra <strong>a</strong>l-isl<u>aa</u>mi diinan falan yuqbala minhu wahuwa fii <strong>a</strong>l-<u>aa</u>khirati mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Dan barangsiapa mencari agama selain Islam, dia tidak akan diterima, dan di akhirat dia termasuk orang yang rugi.'),
	(379, 3, 86, 'كَيْفَ يَهْدِى اللّٰهُ قَوْمًا كَفَرُوْا بَعْدَ اِيْمَانِهِمْ وَشَهِدُوْٓا اَنَّ الرَّسُوْلَ حَقٌّ وَّجَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'kayfa yahdii <strong>al</strong>l<u>aa</u>hu qawman kafaruu ba\'da iim<u>aa</u>nihim wasyahiduu anna <strong>al</strong>rrasuula <u>h</u>aqqun waj<u>aa</u>-ahumu <strong>a</strong>lbayyin<u>aa</u>tu wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Bagaimana Allah akan memberi petunjuk kepada suatu kaum yang kafir setelah mereka beriman, serta mengakui bahwa Rasul (Muhammad) itu benar-benar (rasul), dan bukti-bukti yang jelas telah sampai kepada mereka? Allah tidak memberi petunjuk kepada orang zalim.'),
	(380, 3, 87, 'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ', 'ul<u>aa</u>-ika jaz<u>aa</u>uhum anna \'alayhim la\'nata <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmal<u>aa</u>-ikati wa<strong>al</strong>nn<u>aa</u>si ajma\'iin<strong>a</strong>', 'Mereka itu, balasannya ialah ditimpa laknat Allah, para malaikat, dan manusia seluruhnya,'),
	(381, 3, 88, 'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَۙ', 'kh<u>aa</u>lidiina fiih<u>aa</u> l<u>aa</u> yukhaffafu \'anhumu <strong>a</strong>l\'a<u>dzaa</u>bu wal<u>aa</u> hum yun<u>zh</u>aruun<strong>a</strong>', 'mereka kekal di dalamnya, tidak akan diringankan azabnya, dan mereka tidak diberi penangguhan,'),
	(382, 3, 89, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۗ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina t<u>aa</u>buu min ba\'di <u>dzaa</u>lika wa-a<u>sh</u>la<u>h</u>uu fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'kecuali orang-orang yang bertobat setelah itu, dan melakukan perbaikan, maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(383, 3, 90, 'اِنَّ الَّذِيْنَ كَفَرُوْا بَعْدَ اِيْمَانِهِمْ ثُمَّ ازْدَادُوْا كُفْرًا لَّنْ تُقْبَلَ تَوْبَتُهُمْ ۚ وَاُولٰۤىِٕكَ هُمُ الضَّاۤلُّوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu ba\'da iim<u>aa</u>nihim tsumma izd<u>aa</u>duu kufran lan tuqbala tawbatuhum waul<u>aa</u>-ika humu <strong>al</strong><u>dhdhaa</u>lluun<strong>a</strong>', 'Sungguh, orang-orang yang kafir setelah beriman, kemudian bertambah kekafirannya, tidak akan diterima tobatnya, dan mereka itulah orang-orang yang sesat.'),
	(384, 3, 91, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يُّقْبَلَ مِنْ اَحَدِهِمْ مِّلْءُ الْاَرْضِ ذَهَبًا وَّلَوِ افْتَدٰى بِهٖۗ  اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ وَّمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ࣖ   ۔', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu wam<u>aa</u>tuu wahum kuff<u>aa</u>run falan yuqbala min a<u>h</u>adihim milu <strong>a</strong>l-ar<u>dh</u>i <u>dz</u>ahaban walawi iftad<u>aa</u> bihi ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun <strong>a</strong>liimun wam<u>aa</u> lahum min n<u>aas</u>iriin<strong>a</strong>', 'Sungguh, orang-orang yang kafir dan mati dalam kekafiran, tidak akan diterima (tebusan) dari seseorang di antara mereka sekalipun (berupa) emas sepenuh bumi, sekiranya dia hendak menebus diri dengannya. Mereka itulah orang-orang yang mendapat azab yang pedih dan tidak memperoleh penolong.'),
	(385, 3, 92, 'لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوْا مِمَّا تُحِبُّوْنَ ۗوَمَا تُنْفِقُوْا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ', 'lan tan<u>aa</u>luu <strong>a</strong>lbirra <u>h</u>att<u>aa</u> tunfiquu mimm<u>aa</u> tu<u>h</u>ibbuuna wam<u>aa</u> tunfiquu min syay-in fa-inna <strong>al</strong>l<u>aa</u>ha bihi \'aliim<strong>un</strong>', 'Kamu tidak akan memperoleh kebajikan, sebelum kamu menginfakkan sebagian harta yang kamu cintai. Dan apa pun yang kamu infakkan, tentang hal itu sungguh, Allah Maha Mengetahui.'),
	(386, 3, 93, '۞ كُلُّ الطَّعَامِ كَانَ حِلًّا لِّبَنِيْٓ اِسْرَاۤءِيْلَ اِلَّا مَا حَرَّمَ اِسْرَاۤءِيْلُ عَلٰى نَفْسِهٖ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰىةُ ۗ قُلْ فَأْتُوْا بِالتَّوْرٰىةِ فَاتْلُوْهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'kullu <strong>al</strong><u>ththh</u>a\'<u>aa</u>mi k<u>aa</u>na <u>h</u>illan libanii isr<u>aa</u>-iila ill<u>aa</u> m<u>aa</u> <u>h</u>arrama isr<u>aa</u>-iilu \'al<u>aa</u> nafsihi min qabli an tunazzala <strong>al</strong>ttawr<u>aa</u>tu qul fa/tuu bi<strong>al</strong>ttawr<u>aa</u>ti fa<strong>u</strong>tluuh<u>aa</u> in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Semua makanan itu halal bagi Bani Israil, kecuali makanan yang diharamkan oleh Israil (Yakub) atas dirinya sebelum Taurat diturunkan. Katakanlah (Muhammad), “Maka bawalah Taurat lalu bacalah, jika kamu orang-orang yang benar.”'),
	(387, 3, 94, 'فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْۢ بَعْدِ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ', 'famani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba min ba\'di <u>dzaa</u>lika faul<u>aa</u>-ika humu <strong>al</strong><u>zhaa</u>limuun<strong>a</strong>', 'Maka barangsiapa mengada-adakan kebohongan terhadap Allah setelah itu, maka mereka itulah orang-orang zalim.'),
	(388, 3, 95, 'قُلْ صَدَقَ اللّٰهُ ۗ فَاتَّبِعُوْا مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'qul <u>sh</u>adaqa <strong>al</strong>l<u>aa</u>hu fa<strong>i</strong>ttabi\'uu millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Katakanlah (Muhammad), “Benarlah (segala yang difirmankan) Allah.” Maka ikutilah agama Ibrahim yang lurus, dan dia tidaklah termasuk orang musyrik.'),
	(389, 3, 96, 'اِنَّ اَوَّلَ بَيْتٍ وُّضِعَ لِلنَّاسِ لَلَّذِيْ بِبَكَّةَ مُبٰرَكًا وَّهُدًى لِّلْعٰلَمِيْنَۚ', 'inna awwala baytin wu<u>dh</u>i\'a li<strong>l</strong>nn<u>aa</u>si lalla<u>dz</u>ii bibakkata mub<u>aa</u>rakan wahudan lil\'<u>aa</u>lamiin<strong>a</strong>', 'Sesungguhnya rumah (ibadah) pertama yang dibangun untuk manusia, ialah (Baitullah) yang di Bakkah (Mekah) yang diberkahi dan menjadi petunjuk bagi seluruh alam.'),
	(390, 3, 97, 'فِيْهِ اٰيٰتٌۢ بَيِّنٰتٌ مَّقَامُ اِبْرٰهِيْمَ ەۚ وَمَنْ دَخَلَهٗ كَانَ اٰمِنًا ۗ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَبِيْلًا ۗ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعٰلَمِيْنَ', 'fiihi <u>aa</u>y<u>aa</u>tun bayyin<u>aa</u>tun maq<u>aa</u>mu ibr<u>aa</u>hiima waman dakhalahu k<u>aa</u>na <u>aa</u>minan walill<u>aa</u>hi \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si <u>h</u>ijju <strong>a</strong>lbayti mani ista<u>th</u><u>aa</u>\'a ilayhi sabiilan waman kafara fa-inna <strong>al</strong>l<u>aa</u>ha ghaniyyun \'ani <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Di sana terdapat tanda-tanda yang jelas, (di antaranya) maqam Ibrahim. Barangsiapa memasukinya (Baitullah) amanlah dia. Dan (di antara) kewajiban manusia terhadap Allah adalah melaksanakan ibadah haji ke Baitullah, yaitu bagi orang-orang yang mampu mengadakan perjalanan ke sana. Barangsiapa mengingkari (kewajiban) haji, maka ketahuilah bahwa Allah Mahakaya (tidak memerlukan sesuatu) dari seluruh alam.'),
	(391, 3, 98, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاللّٰهُ شَهِيْدٌ عَلٰى مَا تَعْمَلُوْنَ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi lima takfuruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu syahiidun \'al<u>aa</u> m<u>aa</u> ta\'maluun<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai Ahli Kitab! Mengapa kamu mengingkari ayat-ayat Allah, padahal Allah Maha Menyaksikan apa yang kamu kerjakan?”'),
	(392, 3, 99, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُوْنَهَا عِوَجًا وَّاَنْتُمْ شُهَدَاۤءُ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi lima ta<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi man <u>aa</u>mana tabghuunah<u>aa</u> \'iwajan wa-antum syuhad<u>aa</u>u wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai Ahli Kitab! Mengapa kamu menghalang-halangi orang-orang yang beriman dari jalan Allah, kamu menghendakinya (jalan Allah) bengkok, padahal kamu menyaksikan?” Dan Allah tidak lengah terhadap apa yang kamu kerjakan.'),
	(393, 3, 100, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوْا فَرِيْقًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ يَرُدُّوْكُمْ بَعْدَ اِيْمَانِكُمْ كٰفِرِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu in tu<u>th</u>ii\'uu fariiqan mina <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba yarudduukum ba\'da iim<u>aa</u>nikum k<u>aa</u>firiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Jika kamu mengikuti sebagian dari orang yang diberi Kitab, niscaya mereka akan mengembalikan kamu menjadi orang kafir setelah beriman.'),
	(394, 3, 101, 'وَكَيْفَ تَكْفُرُوْنَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيٰتُ اللّٰهِ وَفِيْكُمْ رَسُوْلُهٗ ۗ وَمَنْ يَّعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ࣖ', 'wakayfa takfuruuna wa-antum tutl<u>aa</u> \'alaykum <u>aa</u>y<u>aa</u>tu <strong>al</strong>l<u>aa</u>hi wafiikum rasuuluhu waman ya\'ta<u>sh</u>im bi<strong>al</strong>l<u>aa</u>hi faqad hudiya il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dan bagaimana kamu (sampai) menjadi kafir, padahal ayat-ayat Allah dibacakan kepada kamu, dan Rasul-Nya (Muhammad) pun berada di tengah-tengah kamu? Barangsiapa berpegang teguh kepada (agama) Allah, maka sungguh, dia diberi petunjuk kepada jalan yang lurus.'),
	(395, 3, 102, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقٰىتِهٖ وَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ittaquu <strong>al</strong>l<u>aa</u>ha <u>h</u>aqqa tuq<u>aa</u>tihi wal<u>aa</u> tamuutunna ill<u>aa</u> wa-antum muslimuun<strong>a</strong>', 'Wahai orang-orang yang beriman! Bertakwalah kepada Allah sebenar-benar takwa kepada-Nya dan janganlah kamu mati kecuali dalam keadaan Muslim.'),
	(396, 3, 103, 'وَاعْتَصِمُوْا بِحَبْلِ اللّٰهِ جَمِيْعًا وَّلَا تَفَرَّقُوْا ۖوَاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ كُنْتُمْ اَعْدَاۤءً فَاَلَّفَ بَيْنَ قُلُوْبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِهٖٓ اِخْوَانًاۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِّنَ النَّارِ فَاَنْقَ', 'wa<strong>i</strong>\'ta<u>sh</u>imuu bi<u>h</u>abli <strong>al</strong>l<u>aa</u>hi jamii\'an wal<u>aa</u> tafarraquu wa<strong>u</strong><u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum i<u>dz</u> kuntum a\'d<u>aa</u>-an fa-allafa bayna quluubikum fa-a<u>sh</u>ba<u>h</u>tum bini\'matihi ikhw<u>aa</u>nan wakuntum \'al<u>aa</u> syaf<u>aa</u> <u>h</u>ufratin mina <strong>al</strong>nn<u>aa</u>ri fa-anqa<u>dz</u>akum minh<u>aa</u> ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi la\'allakum tahtaduun<strong>a</strong>', 'Dan berpegangteguhlah kamu semuanya pada tali (agama) Allah, dan janganlah kamu bercerai berai, dan ingatlah nikmat Allah kepadamu ketika kamu dahulu (masa jahiliah) bermusuhan, lalu Allah mempersatukan hatimu, sehingga dengan karunia-Nya kamu menjadi bersaudara, sedangkan (ketika itu) kamu berada di tepi jurang neraka, lalu Allah menyelamatkan kamu dari sana. Demikianlah, Allah menerangkan ayat-ayat-Nya kepadamu agar kamu mendapat petunjuk.'),
	(397, 3, 104, 'وَلْتَكُنْ مِّنْكُمْ اُمَّةٌ يَّدْعُوْنَ اِلَى الْخَيْرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ ۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ', 'waltakun minkum ummatun yad\'uuna il<u>aa</u> <strong>a</strong>lkhayri waya/muruuna bi<strong>a</strong>lma\'ruufi wayanhawna \'ani <strong>a</strong>lmunkari waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Dan hendaklah di antara kamu ada segolongan orang yang menyeru kepada kebajikan, menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar. Dan mereka itulah orang-orang yang beruntung.'),
	(398, 3, 105, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ تَفَرَّقُوْا وَاخْتَلَفُوْا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ عَظِيْمٌ ۙ', 'wal<u>aa</u> takuunuu ka<strong>a</strong>lla<u>dz</u>iina tafarraquu wa<strong>i</strong>khtalafuu min ba\'di m<u>aa</u> j<u>aa</u>-ahumu <strong>a</strong>lbayyin<u>aa</u>tu waul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Dan janganlah kamu menjadi seperti orang-orang yang bercerai berai dan berselisih setelah sampai kepada mereka keterangan yang jelas. Dan Mereka itulah orang-orang yang mendapat azab yang berat,'),
	(399, 3, 106, 'يَّوْمَ تَبْيَضُّ وُجُوْهٌ وَّتَسْوَدُّ وُجُوْهٌ ۚ فَاَمَّا الَّذِيْنَ اسْوَدَّتْ وُجُوْهُهُمْۗ  اَ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'yawma tabya<u>dhdh</u>u wujuuhun wataswaddu wujuuhun fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina iswaddat wujuuhuhum akafartum ba\'da iim<u>aa</u>nikum fa<u>dz</u>uuquu <strong>a</strong>l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun<strong>a</strong>', 'pada hari itu ada wajah yang putih berseri, dan ada pula wajah yang hitam muram. Adapun orang-orang yang berwajah hitam muram (kepada mereka dikatakan), “Mengapa kamu kafir setelah beriman? Karena itu rasakanlah azab disebabkan kekafiranmu itu.”'),
	(400, 3, 107, 'وَاَمَّا الَّذِيْنَ ابْيَضَّتْ وُجُوْهُهُمْ فَفِيْ رَحْمَةِ اللّٰهِ ۗ هُمْ فِيْهَا خٰلِدُوْنَ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina ibya<u>dhdh</u>at wujuuhuhum fafii ra<u>h</u>mati <strong>al</strong>l<u>aa</u>hi hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Dan adapun orang-orang yang berwajah putih berseri, mereka berada dalam rahmat Allah (surga); mereka kekal di dalamnya.'),
	(401, 3, 108, 'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعٰلَمِيْنَ', 'tilka <u>aa</u>y<u>aa</u>tu <strong>al</strong>l<u>aa</u>hi natluuh<u>aa</u> \'alayka bi<strong>a</strong>l<u>h</u>aqqi wam<u>aa</u> <strong>al</strong>l<u>aa</u>hu yuriidu <u>zh</u>ulman lil\'<u>aa</u>lamiin<strong>a</strong>', 'Itulah ayat-ayat Allah yang Kami bacakan kepada kamu dengan benar, dan Allah tidaklah berkehendak menzalimi (siapa pun) di seluruh alam.'),
	(402, 3, 109, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ', 'walill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi turja\'u <strong>a</strong>l-umuur<strong>u</strong>', 'Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan hanya kepada Allah segala urusan dikembalikan.'),
	(403, 3, 110, 'كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُوْنَ بِالْمَعْرُوْفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُوْنَ بِاللّٰهِ ۗ وَلَوْ اٰمَنَ اَهْلُ الْكِتٰبِ لَكَانَ خَيْرًا لَّهُمْ ۗ مِنْهُمُ الْمُؤْمِنُوْنَ وَاَكْثَرُهُمُ الْفٰسِقُوْنَ ', 'kuntum khayra ummatin ukhrijat li<strong>l</strong>nn<u>aa</u>si ta/muruuna bi<strong>a</strong>lma\'ruufi watanhawna \'ani <strong>a</strong>lmunkari watu/minuuna bi<strong>al</strong>l<u>aa</u>hi walaw <u>aa</u>mana ahlu <strong>a</strong>lkit<u>aa</u>bi lak<u>aa</u>na khayran lahum minhumu <strong>a</strong>lmu/minuuna wa-aktsaruhumu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Kamu (umat Islam) adalah umat terbaik yang dilahirkan untuk manusia, (karena kamu) menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar, dan beriman kepada Allah. Sekiranya Ahli Kitab beriman, tentulah itu lebih baik bagi mereka. Di antara mereka ada yang beriman, namun kebanyakan mereka adalah orang-orang fasik.'),
	(404, 3, 111, 'لَنْ يَّضُرُّوْكُمْ اِلَّآ اَذًىۗ  وَاِنْ يُّقَاتِلُوْكُمْ يُوَلُّوْكُمُ الْاَدْبَارَۗ  ثُمَّ لَا يُنْصَرُوْنَ', 'lan ya<u>dh</u>urruukum ill<u>aa</u> a<u>dz</u>an wa-in yuq<u>aa</u>tiluukum yuwalluukumu <strong>a</strong>l-adb<u>aa</u>ra tsumma l<u>aa</u> yun<u>sh</u>aruun<strong>a</strong>', 'Mereka tidak akan membahayakan kamu, kecuali gangguan-gangguan kecil saja, dan jika mereka memerangi kamu, niscaya mereka mundur berbalik ke belakang (kalah). Selanjutnya mereka tidak mendapat pertolongan.'),
	(405, 3, 112, 'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُوْٓا اِلَّا بِحَبْلٍ مِّنَ اللّٰهِ وَحَبْلٍ مِّنَ النَّاسِ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ الْ', '<u>dh</u>uribat \'alayhimu <strong>al</strong><u>dzdz</u>illatu ayna m<u>aa</u> tsuqifuu ill<u>aa</u> bi<u>h</u>ablin mina <strong>al</strong>l<u>aa</u>hi wa<u>h</u>ablin mina <strong>al</strong>nn<u>aa</u>si wab<u>aa</u>uu bigha<u>dh</u>abin mina <strong>al</strong>l<u>aa</u>hi wa<u>dh</u>uribat \'alayhimu <strong>a</strong>lmaskanatu <u>dzaa</u>lika bi-annahum k<u>aa</u>nuu yakfuruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wayaqtuluuna <strong>a</strong>l-anbiy<u>aa</u>-a bighayri <u>h</u>aqqin <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun<strong>a</strong>', 'Mereka diliputi kehinaan di mana saja mereka berada, kecuali jika mereka (berpegang) pada tali (agama) Allah dan tali (perjanjian) dengan manusia. Mereka mendapat murka dari Allah dan (selalu) diliputi kesengsaraan. Yang demikian itu karena mereka mengingkari ayat-ayat Allah dan membunuh para nabi, tanpa hak (alasan yang benar). Yang demikian itu karena mereka durhaka dan melampaui batas.'),
	(406, 3, 113, '۞ لَيْسُوْا سَوَاۤءً ۗ مِنْ اَهْلِ الْكِتٰبِ اُمَّةٌ قَاۤىِٕمَةٌ يَّتْلُوْنَ اٰيٰتِ اللّٰهِ اٰنَاۤءَ الَّيْلِ وَهُمْ يَسْجُدُوْنَ ', 'laysuu saw<u>aa</u>-an min ahli <strong>a</strong>lkit<u>aa</u>bi ummatun q<u>aa</u>-imatun yatluuna <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi <u>aa</u>n<u>aa</u>-a <strong>al</strong>layli wahum yasjuduun<strong>a</strong>', 'Mereka itu tidak (seluruhnya) sama. Di antara Ahli Kitab ada golongan yang jujur, mereka membaca ayat-ayat Allah pada malam hari, dan mereka (juga) bersujud (salat).'),
	(407, 3, 114, 'يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُوْنَ فِى الْخَيْرٰتِۗ وَاُولٰۤىِٕكَ مِنَ الصّٰلِحِيْنَ ', 'yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri waya/muruuna bi<strong>a</strong>lma\'ruufi wayanhawna \'ani <strong>a</strong>lmunkari wayus<u>aa</u>ri\'uuna fii <strong>a</strong>lkhayr<u>aa</u>ti waul<u>aa</u>-ika mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Mereka beriman kepada Allah dan hari akhir, menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar dan bersegera (mengerjakan) berbagai kebajikan. Mereka termasuk orang-orang saleh.'),
	(408, 3, 115, 'وَمَا يَفْعَلُوْا مِنْ خَيْرٍ فَلَنْ يُّكْفَرُوْهُ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِالْمُتَّقِيْنَ', 'wam<u>aa</u> yaf\'aluu min khayrin falan yukfaruuhu wa<strong>al</strong>l<u>aa</u>hu \'aliimun bi<strong>a</strong>lmuttaqiin<strong>a</strong>', 'Dan kebajikan apa pun yang mereka kerjakan, tidak ada yang mengingkarinya. Dan Allah Maha Mengetahui orang-orang yang bertakwa.'),
	(409, 3, 116, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu lan tughniya \'anhum amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum mina <strong>al</strong>l<u>aa</u>hi syay-an waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Sesungguhnya orang-orang kafir, baik harta maupun anak-anak mereka, sedikit pun tidak dapat menolak azab Allah. Mereka itu penghuni neraka, (dan) mereka kekal di dalamnya.'),
	(410, 3, 117, 'مَثَلُ مَا يُنْفِقُوْنَ فِيْ هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ رِيْحٍ فِيْهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوْٓا اَنْفُسَهُمْ فَاَهْلَكَتْهُ ۗ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُوْنَ ', 'matsalu m<u>aa</u> yunfiquuna fii h<u>aadz</u>ihi <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> kamatsali rii<u>h</u>in fiih<u>aa</u> <u>sh</u>irrun a<u>shaa</u>bat <u>h</u>artsa qawmin <u>zh</u>alamuu anfusahum fa-ahlakat-hu wam<u>aa</u> <u>zh</u>alamahumu <strong>al</strong>l<u>aa</u>hu wal<u>aa</u>kin anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Perumpamaan harta yang mereka infakkan di dalam kehidupan dunia ini, ibarat angin yang mengandung hawa sangat dingin, yang menimpa tanaman (milik) suatu kaum yang menzalimi diri sendiri, lalu angin itu merusaknya. Allah tidak menzalimi mereka, tetapi mereka yang menzalimi diri sendiri.'),
	(411, 3, 118, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا بِطَانَةً مِّنْ دُوْنِكُمْ لَا يَأْلُوْنَكُمْ خَبَالًاۗ وَدُّوْا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَاۤءُ مِنْ اَفْوَاهِهِمْۖ وَمَا تُخْفِيْ صُدُوْرُهُمْ اَكْبَرُ ۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ  اِنْ كُ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu bi<u>th</u><u>aa</u>natan min duunikum l<u>aa</u> ya/luunakum khab<u>aa</u>lan wadduu m<u>aa</u> \'anittum qad badati <strong>a</strong>lbagh<u>daa</u>u min afw<u>aa</u>hihim wam<u>aa</u> tukhfii <u>sh</u>uduuruhum akbaru qad bayyann<u>aa</u> lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti in kuntum ta\'qiluun<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu menjadikan teman orang-orang yang di luar kalanganmu (seagama) sebagai teman kepercayaanmu, (karena) mereka tidak henti-hentinya menyusahkan kamu. Mereka mengharapkan kehancuranmu. Sungguh, telah nyata kebencian dari mulut mereka, dan apa yang tersembunyi di hati mereka lebih jahat. Sungguh, telah Kami terangkan kepadamu ayat-ayat (Kami), jika kamu mengerti.'),
	(412, 3, 119, 'هٰٓاَنْتُمْ اُولَاۤءِ تُحِبُّوْنَهُمْ وَلَا يُحِبُّوْنَكُمْ وَتُؤْمِنُوْنَ بِالْكِتٰبِ كُلِّهٖۚ وَاِذَا لَقُوْكُمْ قَالُوْٓا اٰمَنَّاۖ وَاِذَا خَلَوْا عَضُّوْا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِ ۗ قُلْ مُوْتُوْا بِغَيْظِكُمْ ۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢ', 'h<u>aa</u> antum ul<u>aa</u>-i tu<u>h</u>ibbuunahum wal<u>aa</u> yu<u>h</u>ibbuunakum watu/minuuna bi<strong>a</strong>lkit<u>aa</u>bi kullihi wa-i<u>dzaa</u> laquukum q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa-i<u>dzaa</u> khalaw \'a<u>dhdh</u>uu \'alaykumu <strong>a</strong>l-an<u>aa</u>mila mina <strong>a</strong>lghay<u>zh</u>i qul muutuu bighay<u>zh</u>ikum inna <strong>al</strong>l<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Beginilah kamu! Kamu menyukai mereka, padahal mereka tidak menyukaimu, dan kamu beriman kepada semua kitab. Apabila mereka berjumpa kamu, mereka berkata, “Kami beriman,” dan apabila mereka menyendiri, mereka menggigit ujung jari karena marah dan benci kepadamu. Katakanlah, “Matilah kamu karena kemarahanmu itu!” Sungguh, Allah Maha Mengetahui segala isi hati.'),
	(413, 3, 120, 'اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۖ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَّفْرَحُوْا بِهَا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـًٔا ۗ اِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ مُحِيْطٌ ࣖ', 'in tamsaskum <u>h</u>asanatun tasu/hum wa-in tu<u>sh</u>ibkum sayyi-atun yafra<u>h</u>uu bih<u>aa</u> wa-in ta<u>sh</u>biruu watattaquu l<u>aa</u> ya<u>dh</u>urrukum kayduhum syay-an inna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u><strong>un</strong>', 'Jika kamu memperoleh kebaikan, (niscaya) mereka bersedih hati, tetapi jika kamu tertimpa bencana, mereka bergembira karenanya. Jika kamu bersabar dan bertakwa, tipu daya mereka tidak akan menyusahkan kamu sedikit pun. Sungguh, Allah Maha Meliputi segala apa yang mereka kerjakan.'),
	(414, 3, 121, 'وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِنِيْنَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۙ', 'wa-i<u>dz</u> ghadawta min ahlika tubawwi-u <strong>a</strong>lmu/miniina maq<u>aa</u>\'ida lilqit<u>aa</u>li wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Dan (ingatlah), ketika engkau (Muhammad) berangkat pada pagi hari meninggalkan keluargamu untuk mengatur orang-orang beriman pada pos-pos pertempuran. Allah Maha Mendengar, Maha Mengetahui.'),
	(415, 3, 122, 'اِذْ هَمَّتْ طَّۤاىِٕفَتٰنِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَا ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ', 'i<u>dz</u> hammat <u>th</u><u>aa</u>-ifat<u>aa</u>ni minkum an tafsyal<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu waliyyuhum<u>aa</u> wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmu/minuun<strong>a</strong>', 'Ketika dua golongan dari pihak kamu ingin (mundur) karena takut, padahal Allah adalah penolong mereka. Karena itu, hendaklah kepada Allah saja orang-orang mukmin bertawakal.'),
	(416, 3, 123, 'وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَّاَنْتُمْ اَذِلَّةٌ  ۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُوْنَ ', 'walaqad na<u>sh</u>arakumu <strong>al</strong>l<u>aa</u>hu bibadrin wa-antum a<u>dz</u>illatun fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha la\'allakum tasykuruun<strong>a</strong>', 'Dan sungguh, Allah telah menolong kamu dalam perang Badar, padahal kamu dalam keadaan lemah. Karena itu bertakwalah kepada Allah, agar kamu mensyukuri-Nya.'),
	(417, 3, 124, 'اِذْ تَقُوْلُ لِلْمُؤْمِنِيْنَ اَلَنْ يَّكْفِيَكُمْ اَنْ يُّمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُنْزَلِيْنَۗ', 'i<u>dz</u> taquulu lilmu/miniina alan yakfiyakum an yumiddakum rabbukum bitsal<u>aa</u>tsati <u>aa</u>l<u>aa</u>fin mina almal<u>aa</u>-ikati munzaliin<strong>a</strong>', '(Ingatlah), ketika engkau (Muhammad) mengatakan kepada orang-orang beriman, “Apakah tidak cukup bagimu bahwa Allah membantu kamu dengan tiga ribu malaikat yang diturunkan (dari langit)?”'),
	(418, 3, 125, 'بَلٰٓى ۙاِنْ تَصْبِرُوْا وَتَتَّقُوْا وَيَأْتُوْكُمْ مِّنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُسَوِّمِيْنَ ', 'bal<u>aa</u> in ta<u>sh</u>biruu watattaquu waya/tuukum min fawrihim h<u>aadzaa</u> yumdidkum rabbukum bikhamsati <u>aa</u>l<u>aa</u>fin mina <strong>a</strong>lmal<u>aa</u>-ikati musawwimiin<strong>a</strong>', '“Ya” (cukup). Jika kamu bersabar dan bertakwa ketika mereka datang menyerang kamu dengan tiba-tiba, niscaya Allah menolongmu dengan lima ribu malaikat yang memakai tanda.'),
	(419, 3, 126, 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَىِٕنَّ قُلُوْبُكُمْ بِهٖ ۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِۙ', 'wam<u>aa</u> ja\'alahu <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> busyr<u>aa</u> lakum walita<u>th</u>ma-inna quluubukum bihi wam<u>aa</u> <strong>al</strong>nna<u>sh</u>ru ill<u>aa</u> min \'indi <strong>al</strong>l<u>aa</u>hi <strong>a</strong>l\'aziizi <strong>a</strong>l<u>h</u>akiim<strong>i</strong>', 'Dan Allah tidak menjadikannya (pemberian bala-bantuan itu) melainkan sebagai kabar gembira bagi (kemenangan)mu, dan agar hatimu tenang karenanya. Dan tidak ada kemenangan itu, selain dari Allah Yang Mahaperkasa, Mahabijaksana.'),
	(420, 3, 127, 'لِيَقْطَعَ طَرَفًا مِّنَ الَّذِيْنَ كَفَرُوْٓا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوْا خَاۤىِٕبِيْنَ', 'liyaq<u>th</u>a\'a <u>th</u>arafan mina <strong>al</strong>la<u>dz</u>iina kafaruu aw yakbitahum fayanqalibuu kh<u>aa</u>-ibiin<strong>a</strong>', '(Allah menolong kamu dalam perang Badar dan memberi bantuan) adalah untuk membinasakan segolongan orang kafir, atau untuk menjadikan mereka hina, sehingga mereka kembali tanpa memperoleh apa pun.'),
	(421, 3, 128, 'لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوْبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظٰلِمُوْنَ ', 'laysa laka mina <strong>a</strong>l-amri syay-un aw yatuuba \'alayhim aw yu\'a<u>dzdz</u>ibahum fa-innahum <u>zhaa</u>limuun<strong>a</strong>', 'Itu bukan menjadi urusanmu (Muhammad) apakah Allah menerima tobat mereka, atau mengazabnya, karena sesungguhnya mereka orang-orang zalim.'),
	(422, 3, 129, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ࣖ', 'walill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i yaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Dia mengampuni siapa yang Dia kehendaki, dan mengazab siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang.'),
	(423, 3, 130, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوا الرِّبٰوٓا اَضْعَافًا مُّضٰعَفَةً ۖوَّاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَۚ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ta/kuluu <strong>al</strong>rrib<u>aa</u> a<u>dh</u>\'<u>aa</u>fan mu<u>daa</u>\'afatan wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu memakan riba dengan berlipat ganda dan bertakwalah kepada Allah agar kamu beruntung.'),
	(424, 3, 131, 'وَاتَّقُوا النَّارَ الَّتِيْٓ اُعِدَّتْ لِلْكٰفِرِيْنَ ۚ ', 'wa<strong>i</strong>ttaquu <strong>al</strong>nn<u>aa</u>ra <strong>al</strong>latii u\'iddat lilk<u>aa</u>firiin<strong>a</strong>', 'Dan peliharalah dirimu dari api neraka, yang disediakan bagi orang kafir.'),
	(425, 3, 132, 'وَاَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَۚ ', 'wa-a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha wa<strong>al</strong>rrasuula la\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dan taatlah kepada Allah dan Rasul (Muhammad), agar kamu diberi rahmat.'),
	(426, 3, 133, '۞ وَسَارِعُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوٰتُ وَالْاَرْضُۙ  اُعِدَّتْ لِلْمُتَّقِيْنَۙ', 'was<u>aa</u>ri\'uu il<u>aa</u> maghfiratin min rabbikum wajannatin \'ar<u>dh</u>uh<u>aa</u> <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu wa<strong>a</strong>l-ar<u>dh</u>u u\'iddat lilmuttaqiin<strong>a</strong>', 'Dan bersegeralah kamu mencari ampunan dari Tuhanmu dan mendapatkan surga yang luasnya seluas langit dan bumi yang disediakan bagi orang-orang yang bertakwa,'),
	(427, 3, 134, 'الَّذِيْنَ يُنْفِقُوْنَ فِى السَّرَّۤاءِ وَالضَّرَّۤاءِ وَالْكَاظِمِيْنَ الْغَيْظَ وَالْعَافِيْنَ عَنِ النَّاسِۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَۚ ', '<strong>al</strong>la<u>dz</u>iina yunfiquuna fii <strong>al</strong>ssarr<u>aa</u>-i wa<strong>al</strong><u>dhdh</u>arr<u>aa</u>-i wa<strong>a</strong>lk<u>aats</u>imiina <strong>a</strong>lghay<u>zh</u>a wa<strong>a</strong>l\'<u>aa</u>fiina \'ani <strong>al</strong>nn<u>aa</u>si wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>ibbu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', '(yaitu) orang yang berinfak, baik di waktu lapang maupun sempit, dan orang-orang yang menahan amarahnya dan memaafkan (kesalahan) orang lain. Dan Allah mencintai orang yang berbuat kebaikan,'),
	(428, 3, 135, 'وَالَّذِيْنَ اِذَا فَعَلُوْا فَاحِشَةً اَوْ ظَلَمُوْٓا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوْا لِذُنُوْبِهِمْۗ وَمَنْ يَّغْفِرُ الذُّنُوْبَ اِلَّا اللّٰهُ ۗ وَلَمْ يُصِرُّوْا عَلٰى مَا فَعَلُوْا وَهُمْ يَعْلَمُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina i<u>dzaa</u> fa\'aluu f<u>aah</u>isyatan aw <u>zh</u>alamuu anfusahum <u>dz</u>akaruu <strong>al</strong>l<u>aa</u>ha fa<strong>i</strong>staghfaruu li<u>dz</u>unuubihim waman yaghfiru <strong>al</strong><u>dzdz</u>unuuba ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu walam yu<u>sh</u>irruu \'al<u>aa</u> m<u>aa</u> fa\'aluu wahum ya\'lamuun<strong>a</strong>', 'dan (juga) orang-orang yang apabila mengerjakan perbuatan keji atau menzalimi diri sendiri, (segera) mengingat Allah, lalu memohon ampunan atas dosa-dosanya, dan siapa (lagi) yang dapat mengampuni dosa-dosa selain Allah? Dan mereka tidak meneruskan perbuatan dosa itu, sedang mereka mengetahui.'),
	(429, 3, 136, 'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ مَّغْفِرَةٌ مِّنْ رَّبِّهِمْ وَجَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَنِعْمَ اَجْرُ الْعٰمِلِيْنَۗ', 'ul<u>aa</u>-ika jaz<u>aa</u>uhum maghfiratun min rabbihim wajann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wani\'ma ajru <strong>a</strong>l\'<u>aa</u>miliin<strong>a</strong>', 'Balasan bagi mereka ialah ampunan dari Tuhan mereka dan surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan (itulah) sebaik-baik pahala bagi orang-orang yang beramal.'),
	(430, 3, 137, 'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ', 'qad khalat min qablikum sunanun fasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fa<strong>u</strong>n<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmuka<u>dzdz</u>ibiin<strong>a</strong>', 'Sungguh, telah berlalu sebelum kamu sunnah-sunnah (Allah), karena itu berjalanlah kamu ke (segenap penjuru) bumi dan perhatikanlah bagai-mana kesudahan orang yang mendustakan (rasul-rasul).    '),
	(431, 3, 138, 'هٰذَا بَيَانٌ لِّلنَّاسِ وَهُدًى وَّمَوْعِظَةٌ لِّلْمُتَّقِيْنَ ', 'h<u>aadzaa</u> bay<u>aa</u>nun li<strong>l</strong>nn<u>aa</u>si wahudan wamaw\'i<u>zh</u>atun lilmuttaqiin<strong>a</strong>', 'Inilah (Al-Qur\'an) suatu keterangan yang jelas untuk semua manusia, dan menjadi petunjuk serta pelajaran bagi orang-orang yang bertakwa.'),
	(432, 3, 139, 'وَلَا تَهِنُوْا وَلَا تَحْزَنُوْا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ', 'wal<u>aa</u> tahinuu wal<u>aa</u> ta<u>h</u>zanuu wa-antumu <strong>a</strong>l-a\'lawna in kuntum mu/miniin<strong>a</strong>', 'Dan janganlah kamu (merasa) lemah, dan jangan (pula) bersedih hati, sebab kamu paling tinggi (derajatnya), jika kamu orang beriman.'),
	(433, 3, 140, 'اِنْ يَّمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِّثْلُهٗ ۗوَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَتَّخِذَ مِنْكُمْ شُهَدَاۤءَ ۗوَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَۙ', 'in yamsaskum qar<u>h</u>un faqad massa <strong>a</strong>lqawma qar<u>h</u>un mitsluhu watilka <strong>a</strong>l-ayy<u>aa</u>mu nud<u>aa</u>wiluh<u>aa</u> bayna <strong>al</strong>nn<u>aa</u>si waliya\'lama <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wayattakhi<u>dz</u>a minkum syuhad<u>aa</u>-a wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>in yamsaskum qar<u>h</u>un faqad massa <strong>a</strong>lqawma qar<u>h</u>un mitsluhu watilka <strong>a</strong>l-ayy<u>aa</u>mu nud<u>aa</u>wiluh<u>aa</u> bayna <strong>al</strong>nn<u>aa</u>si waliya\'lama <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wayattakhi<u>dz</u>a minkum syuhad<u>aa</u>-a wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Jika kamu (pada Perang Uhud) mendapat luka, maka mereka pun (pada Perang Badar) mendapat luka yang serupa. Dan masa (kejayaan dan kehancuran) itu, Kami pergilirkan di antara manusia (agar mereka mendapat pelajaran), dan agar Allah membedakan orang-orang yang beriman (dengan orang-orang kafir) dan agar sebagian kamu dijadikan-Nya (gugur sebagai) syuhada. Dan Allah tidak menyukai orang-orang zalim,'),
	(434, 3, 141, 'وَلِيُمَحِّصَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَمْحَقَ الْكٰفِرِيْنَ', 'waliyuma<u>hh</u>i<u>sh</u>a <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wayam<u>h</u>aqa <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'dan agar Allah membersihkan orang-orang yang beriman (dari dosa mereka) dan membinasakan orang-orang kafir.'),
	(435, 3, 142, 'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَيَعْلَمَ الصّٰبِرِيْنَ ', 'am <u>h</u>asibtum an tadkhuluu <strong>a</strong>ljannata walamm<u>aa</u> ya\'lami <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina j<u>aa</u>haduu minkum waya\'lama <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum nyata bagi Allah orang-orang yang berjihad di antara kamu, dan belum nyata orang-orang yang sabar.'),
	(436, 3, 143, 'وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوْهُ وَاَنْتُمْ تَنْظُرُوْنَ ࣖ', 'walaqad kuntum tamannawna <strong>a</strong>lmawta min qabli an talqawhu faqad ra-aytumuuhu wa-antum tan<u>zh</u>uruun<strong>a</strong>', 'Dan kamu benar-benar mengharapkan mati (syahid) sebelum kamu menghadapinya; maka (sekarang) kamu sungguh, telah melihatnya dan kamu menyaksikannya.'),
	(437, 3, 144, 'وَمَا مُحَمَّدٌ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ ۗ اَفَا۟ىِٕنْ مَّاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْ ۗ وَمَنْ يَّنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَّضُرَّ اللّٰهَ شَيْـًٔا  ۗوَسَيَجْزِى اللّٰهُ الشّٰكِرِيْنَ ', 'wam<u>aa</u> mu<u>h</u>ammadun ill<u>aa</u> rasuulun qad khalat min qablihi <strong>al</strong>rrusulu afa-in m<u>aa</u>ta aw qutila inqalabtum \'al<u>aa</u> a\'q<u>aa</u>bikum waman yanqalib \'al<u>aa</u> \'aqibayhi falan ya<u>dh</u>urra <strong>al</strong>l<u>aa</u>ha syay-an wasayajzii <strong>al</strong>l<u>aa</u>hu <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Dan Muhammad hanyalah seorang Rasul; sebelumnya telah berlalu beberapa rasul. Apakah jika dia wafat atau dibunuh kamu berbalik ke belakang (murtad)? Barangsiapa berbalik ke belakang, maka ia tidak akan merugikan Allah sedikit pun. Allah akan memberi balasan kepada orang yang bersyukur.'),
	(438, 3, 145, 'وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوْتَ اِلَّا بِاِذْنِ اللّٰهِ كِتٰبًا مُّؤَجَّلًا ۗ وَمَنْ يُّرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۚ وَمَنْ يُّرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِهٖ مِنْهَا ۗ وَسَنَجْزِى الشّٰكِرِيْنَ ', 'wam<u>aa</u> k<u>aa</u>na linafsin an tamuuta ill<u>aa</u> bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi kit<u>aa</u>ban mu-ajjalan waman yurid tsaw<u>aa</u>ba <strong>al</strong>dduny<u>aa</u> nu/tihi minh<u>aa</u> waman yurid tsaw<u>aa</u>ba <strong>a</strong>l-<u>aa</u>khirati nu/tihi minh<u>aa</u> wasanajzii <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Dan setiap yang bernyawa tidak akan mati kecuali dengan izin Allah, sebagai ketetapan yang telah ditentukan waktunya. Barangsiapa menghendaki pahala dunia, niscaya Kami berikan kepadanya pahala (dunia) itu, dan barangsiapa menghendaki pahala akhirat, Kami berikan (pula) kepadanya pahala (akhirat) itu, dan Kami akan memberi balasan kepada orang-orang yang bersyukur.'),
	(439, 3, 146, 'وَكَاَيِّنْ مِّنْ نَّبِيٍّ قَاتَلَۙ مَعَهٗ رِبِّيُّوْنَ كَثِيْرٌۚ فَمَا وَهَنُوْا لِمَآ اَصَابَهُمْ فِيْ سَبِيْلِ اللّٰهِ وَمَا ضَعُفُوْا وَمَا اسْتَكَانُوْا ۗ وَاللّٰهُ يُحِبُّ الصّٰبِرِيْنَ ', 'waka-ayyin min nabiyyin q<u>aa</u>tala ma\'ahu ribbiyyuuna katsiirun fam<u>aa</u> wahanuu lim<u>aa</u> a<u>shaa</u>bahum fii sabiili <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> <u>dh</u>a\'ufuu wam<u>aa</u> istak<u>aa</u>nuu wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>ibbu <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Dan betapa banyak nabi yang berperang didampingi sejumlah besar dari pengikut(nya) yang bertakwa. Mereka tidak (menjadi) lemah karena bencana yang menimpanya di jalan Allah, tidak patah semangat dan tidak (pula) menyerah (kepada musuh). Dan Allah mencintai orang-orang yang sabar.'),
	(440, 3, 147, 'وَمَا كَانَ قَوْلَهُمْ اِلَّآ اَنْ قَالُوْا رَبَّنَا اغْفِرْ لَنَا ذُنُوْبَنَا وَاِسْرَافَنَا فِيْٓ اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ', 'wam<u>aa</u> k<u>aa</u>na qawlahum ill<u>aa</u> an q<u>aa</u>luu rabban<u>aa</u> ighfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> wa-isr<u>aa</u>fan<u>aa</u> fii amrin<u>aa</u> watsabbit aqd<u>aa</u>man<u>aa</u> wa<strong>u</strong>n<u>sh</u>urn<u>aa</u> \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan tidak lain ucapan mereka hanyalah doa, “Ya Tuhan kami, ampunilah dosa-dosa kami dan tindakan-tindakan kami yang berlebihan (dalam) urusan kami dan tetapkanlah pendirian kami, dan tolonglah kami terhadap orang-orang kafir.”'),
	(441, 3, 148, 'فَاٰتٰىهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِ ۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ࣖ ', 'fa<u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu tsaw<u>aa</u>ba <strong>al</strong>dduny<u>aa</u> wa<u>h</u>usna tsaw<u>aa</u>bi <strong>a</strong>l-<u>aa</u>khirati wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>ibbu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Maka Allah memberi mereka pahala di dunia dan pahala yang baik di akhirat. Dan Allah mencintai orang-orang yang berbuat kebaikan.'),
	(442, 3, 149, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوا الَّذِيْنَ كَفَرُوْا يَرُدُّوْكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu in tu<u>th</u>ii\'uu <strong>al</strong>la<u>dz</u>iina kafaruu yarudduukum \'al<u>aa</u> a\'q<u>aa</u>bikum fatanqalibuu kh<u>aa</u>siriin<strong>a</strong>', 'Wahai orang-orang yang beriman! Jika kamu menaati orang-orang yang kafir, niscaya mereka akan mengembalikan kamu ke belakang (murtad), maka kamu akan kembali menjadi orang yang rugi.'),
	(443, 3, 150, 'بَلِ اللّٰهُ مَوْلٰىكُمْ ۚ وَهُوَ خَيْرُ النّٰصِرِيْنَ ', 'bali <strong>al</strong>l<u>aa</u>hu mawl<u>aa</u>kum wahuwa khayru <strong>al</strong>nn<u>aas</u>iriin<strong>a</strong>', 'Tetapi hanya Allah-lah pelindungmu, dan Dia penolong yang terbaik.'),
	(444, 3, 151, 'سَنُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا ۚ وَمَأْوٰىهُمُ النَّارُ ۗ وَبِئْسَ مَثْوَى الظّٰلِمِيْنَ ', 'sanulqii fii quluubi <strong>al</strong>la<u>dz</u>iina kafaruu <strong>al</strong>rru\'ba bim<u>aa</u> asyrakuu bi<strong>al</strong>l<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wama/w<u>aa</u>humu <strong>al</strong>nn<u>aa</u>ru wabi/sa matsw<u>aa</u> <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Akan Kami masukkan rasa takut ke dalam hati orang-orang kafir, karena mereka  mempersekutukan  Allah dengan sesuatu yang Allah tidak menurunkan  keterangan  tentang itu. Dan tempat kembali mereka ialah neraka. Dan (itulah) seburuk-buruk tempat tinggal (bagi) orang-orang zalim.'),
	(445, 3, 152, 'وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهٗٓ اِذْ تَحُسُّوْنَهُمْ بِاِذْنِهٖ ۚ حَتّٰىٓ اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِى الْاَمْرِ وَعَصَيْتُمْ مِّنْۢ بَعْدِ مَآ اَرٰىكُمْ مَّا تُحِبُّوْنَ ۗ مِنْكُمْ مَّنْ يُّرِيْدُ الدُّنْيَا وَمِنْكُمْ مَّنْ يُّرِيْدُ الْا', 'walaqad <u>sh</u>adaqakumu <strong>al</strong>l<u>aa</u>hu wa\'dahu i<u>dz</u> ta<u>h</u>ussuunahum bi-i<u>dz</u>nihi <u>h</u>att<u>aa</u> i<u>dzaa</u> fasyiltum watan<u>aa</u>za\'tum fii <strong>a</strong>l-amri wa\'a<u>sh</u>aytum min ba\'di m<u>aa</u> ar<u>aa</u>kum m<u>aa</u> tu<u>h</u>ibbuuna minkum man yuriidu <strong>al</strong>dduny<u>aa</u> waminkum man yuriidu <strong>a</strong>l-<u>aa</u>khirata tsumma <u>sh</u>arafakum \'anhum liyabtaliyakum walaqad \'af<u>aa</u> \'ankum wa<strong>al</strong>l<u>aa</u>hu <u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan sungguh, Allah telah memenuhi janji-Nya kepadamu, ketika kamu membunuh mereka dengan izin-Nya sampai pada saat kamu lemah dan berselisih dalam urusan itu dan mengabaikan perintah Rasul setelah Allah memperlihatkan kepadamu apa yang kamu sukai. Di antara kamu ada orang yang menghendaki dunia dan di antara kamu ada (pula) orang yang menghendaki akhirat. Kemudian Allah memalingkan kamu dari mereka untuk mengujimu, tetapi Dia benar-benar telah memaafkan kamu. Dan Allah mempunyai karunia (yang diberikan) kepada orang-orang mukmin.'),
	(446, 3, 153, '۞ اِذْ تُصْعِدُوْنَ وَلَا تَلْوٗنَ عَلٰٓى اَحَدٍ وَّالرَّسُوْلُ يَدْعُوْكُمْ فِيْٓ اُخْرٰىكُمْ فَاَثَابَكُمْ غَمًّا ۢبِغَمٍّ لِّكَيْلَا تَحْزَنُوْا عَلٰى مَا فَاتَكُمْ وَلَا مَآ اَصَابَكُمْ ۗ وَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ', 'i<u>dz</u> tu<u>sh</u>\'iduuna wal<u>aa</u> talwuuna \'al<u>aa</u> a<u>h</u>adin wa<strong>al</strong>rrasuulu yad\'uukum fii ukhr<u>aa</u>kum fa-ats<u>aa</u>bakum ghamman bighammin likay l<u>aa</u> ta<u>h</u>zanuu \'al<u>aa</u> m<u>aa</u> f<u>aa</u>takum wal<u>aa</u> m<u>aa</u> a<u>shaa</u>bakum wa<strong>al</strong>l<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun<strong>a</strong>', '(Ingatlah) ketika kamu lari dan tidak menoleh kepada siapa pun, sedang Rasul (Muhammad) yang berada di antara (kawan-kawan)mu yang lain memanggil kamu (kelompok yang lari), karena itu Allah menimpakan kepadamu kesedihan demi kesedihan, agar kamu tidak bersedih hati (lagi) terhadap apa yang luput dari kamu dan terhadap apa yang menimpamu. Dan Allah Mahateliti apa yang kamu kerjakan.'),
	(447, 3, 154, 'ثُمَّ اَنْزَلَ عَلَيْكُمْ مِّنْۢ بَعْدِ الْغَمِّ اَمَنَةً نُّعَاسًا يَّغْشٰى طَۤاىِٕفَةً مِّنْكُمْ ۙ وَطَۤاىِٕفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّوْنَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۗ يَقُوْلُوْنَ هَلْ لَّنَا مِنَ الْاَمْرِ مِنْ ش', 'tsumma anzala \'alaykum min ba\'di <strong>a</strong>lghammi amanatan nu\'<u>aa</u>san yaghsy<u>aa</u> <u>th</u><u>aa</u>-ifatan minkum wa<u>th</u><u>aa</u>-ifatun qad ahammat-hum anfusuhum ya<u>zh</u>unnuuna bi<strong>al</strong>l<u>aa</u>hi ghayra <strong>a</strong>l<u>h</u>aqqi <u>zh</u>anna <strong>a</strong>lj<u>aa</u>hiliyyati yaquuluuna hal lan<u>aa</u> mina <strong>a</strong>l-amri min syay-in qul inna <strong>a</strong>l-amra kullahu lill<u>aa</u>hi yukhfuuna fii anfusihim m<u>aa</u> l<u>aa</u> yubduuna laka yaquuluuna law k<u>aa</u>na lan<u>aa</u> mina <strong>a</strong>l-amri syay-un m<u>aa</u> qutiln<u>aa</u> h<u>aa</u>hun<u>aa</u> qul law kuntum fii buyuutikum labaraza <strong>al</strong>la<u>dz</u>iina kutiba \'alayhimu <strong>a</strong>lqatlu il<u>aa</u> ma<u>daa</u>ji\'ihim waliyabtaliya <strong>al</strong>l<u>aa</u>hu m<u>aa</u> fii <u>sh</u>uduurikum waliyuma<u>hh</u>i<u>sh</u>a m<u>aa</u> fii quluubikum wa<strong>al</strong>l<u>aa</u>hu \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Kemudian setelah kamu ditimpa kesedihan, Dia menurunkan rasa aman kepadamu (berupa) kantuk yang meliputi segolongan dari kamu, sedangkan segolongan lagi telah dicemaskan oleh diri mereka sendiri; mereka menyangka yang tidak benar terhadap Allah seperti sangkaan jahiliah. Mereka berkata, “Adakah sesuatu yang dapat kita perbuat dalam urusan ini?” Katakanlah (Muhammad), “Sesungguhnya segala urusan itu di tangan Allah.” Mereka menyembunyikan dalam hatinya apa yang tidak mereka terangkan kepadamu. Mereka berkata, “Sekiranya ada sesuatu yang dapat kita perbuat dalam urusan ini, niscaya kita tidak akan dibunuh (dikalahkan) di sini.” Katakanlah (Muhammad), “Meskipun kamu ada di rumahmu, niscaya orang-orang yang telah ditetapkan akan mati terbunuh itu keluar (juga) ke tempat mereka terbunuh.” Allah (berbuat demikian) untuk menguji apa yang ada dalam dadamu dan untuk membersihkan apa yang ada dalam hatimu. Dan Allah Maha Mengetahui isi hati.'),
	(448, 3, 155, 'اِنَّ الَّذِيْنَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطٰنُ بِبَعْضِ مَا كَسَبُوْا ۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْ ۗ اِنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ ࣖ', 'inna <strong>al</strong>la<u>dz</u>iina tawallaw minkum yawma iltaq<u>aa</u> <strong>a</strong>ljam\'<u>aa</u>ni innam<u>aa</u> istazallahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu biba\'<u>dh</u>i m<u>aa</u> kasabuu walaqad \'af<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'anhum inna <strong>al</strong>l<u>aa</u>ha ghafuurun <u>h</u>aliim<strong>un</strong>', 'Sesungguhnya orang-orang yang berpaling di antara kamu ketika terjadi pertemuan (pertempuran) antara dua pasukan itu, sesungguhnya mereka digelincirkan oleh setan, disebabkan sebagian kesalahan (dosa) yang telah mereka perbuat (pada masa lampau), tetapi Allah benar-benar telah memaafkan mereka. Sungguh, Allah Maha Pengampun, Maha Penyantun.'),
	(449, 3, 156, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ كَفَرُوْا وَقَالُوْا لِاِخْوَانِهِمْ اِذَا ضَرَبُوْا فِى الْاَرْضِ اَوْ كَانُوْا غُزًّى لَّوْ كَانُوْا عِنْدَنَا مَا مَاتُوْا وَمَا قُتِلُوْاۚ  لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً فِيْ قُلُوْبِه', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> takuunuu ka<strong>a</strong>lla<u>dz</u>iina kafaruu waq<u>aa</u>luu li-ikhw<u>aa</u>nihim i<u>dzaa</u> <u>dh</u>arabuu fii <strong>a</strong>l-ar<u>dh</u>i aw k<u>aa</u>nuu ghuzzan law k<u>aa</u>nuu \'indan<u>aa</u> m<u>aa</u> m<u>aa</u>tuu wam<u>aa</u> qutiluu liyaj\'ala <strong>al</strong>l<u>aa</u>hu <u>dzaa</u>lika <u>h</u>asratan fii quluubihim wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>yii wayumiitu wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu seperti orang-orang kafir yang mengatakan kepada saudara-saudaranya apabila mereka mengadakan perjalanan di bumi atau berperang, “Sekiranya mereka tetap bersama kita, tentulah mereka tidak mati dan tidak terbunuh.” (Dengan perkataan) yang demikian itu, karena Allah hendak menimbulkan rasa penyesalan di hati mereka. Allah menghidupkan dan mematikan, dan Allah Maha Melihat apa yang kamu kerjakan.'),
	(450, 3, 157, 'وَلَىِٕنْ قُتِلْتُمْ فِيْ سَبِيْلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِّنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِّمَّا يَجْمَعُوْنَ ', 'wala-in qutiltum fii sabiili <strong>al</strong>l<u>aa</u>hi aw muttum lamaghfiratun mina <strong>al</strong>l<u>aa</u>hi wara<u>h</u>matun khayrun mimm<u>aa</u> yajma\'uun<strong>a</strong>', 'Dan sungguh, sekiranya kamu gugur di jalan Allah atau mati, sungguh, pastilah ampunan Allah dan rahmat-Nya lebih baik (bagimu) daripada apa (harta rampasan) yang mereka kumpulkan.'),
	(451, 3, 158, 'وَلَىِٕنْ مُّتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُوْنَ', 'wala-in muttum aw qutiltum la-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi tu<u>h</u>syaruun<strong>a</strong>', 'Dan sungguh, sekiranya kamu mati atau gugur, pastilah kepada Allah kamu dikumpulkan.'),
	(452, 3, 159, 'فَبِمَا رَحْمَةٍ مِّنَ اللّٰهِ لِنْتَ لَهُمْ ۚ وَلَوْ كُنْتَ فَظًّا غَلِيْظَ الْقَلْبِ لَانْفَضُّوْا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِى الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِ ۗ اِنَّ اللّٰهَ يُحِبُّ الْم', 'fabim<u>aa</u> ra<u>h</u>matin mina <strong>al</strong>l<u>aa</u>hi linta lahum walaw kunta fa<u>zhzh</u>an ghalii<u>zh</u>a <strong>a</strong>lqalbi la<strong>i</strong>nfa<u>dhdh</u>uu min <u>h</u>awlika fa<strong>u</strong>\'fu \'anhum wa<strong>i</strong>staghfir lahum wasy<u>aa</u>wirhum fii <strong>a</strong>l-amri fa-i<u>dzaa</u> \'azamta fatawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmutawakkiliin<strong>a</strong>', 'Maka berkat rahmat Allah engkau (Muhammad) berlaku lemah lembut terhadap mereka. Sekiranya engkau bersikap keras dan berhati kasar, tentulah mereka menjauhkan diri dari sekitarmu. Karena itu maafkanlah mereka dan mohonkanlah ampunan untuk mereka, dan bermusyawarahlah dengan mereka dalam urusan itu. Kemudian, apabila engkau telah membulatkan tekad, maka bertawakallah kepada Allah. Sungguh, Allah mencintai orang yang bertawakal.'),
	(453, 3, 160, 'اِنْ يَّنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْ ۚ وَاِنْ يَّخْذُلْكُمْ فَمَنْ ذَا الَّذِيْ يَنْصُرُكُمْ مِّنْۢ بَعْدِهٖ ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ', 'in yan<u>sh</u>urkumu <strong>al</strong>l<u>aa</u>hu fal<u>aa</u> gh<u>aa</u>liba lakum wa-in yakh<u>dz</u>ulkum faman <u>dzaa</u> <strong>al</strong>la<u>dz</u>ii yan<u>sh</u>urukum min ba\'dihi wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmu/minuun<strong>a</strong>', 'Jika Allah menolong kamu, maka tidak ada yang dapat mengalahkanmu, tetapi jika Allah membiarkan kamu (tidak memberi pertolongan), maka siapa yang dapat menolongmu setelah itu? Karena itu, hendaklah kepada Allah saja orang-orang mukmin bertawakal.'),
	(454, 3, 161, 'وَمَا كَانَ لِنَبِيٍّ اَنْ يَّغُلَّ  ۗوَمَنْ يَّغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِ ۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ', 'wam<u>aa</u> k<u>aa</u>na linabiyyin an yaghulla waman yaghlul ya/ti bim<u>aa</u> ghalla yawma <strong>a</strong>lqiy<u>aa</u>mati tsumma tuwaff<u>aa</u> kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>v', 'Dan tidak mungkin seorang nabi berkhianat (dalam urusan harta rampasan perang). Barangsiapa berkhianat, niscaya pada hari Kiamat dia akan datang membawa apa yang dikhianatkannya itu. Kemudian setiap orang akan diberi balasan yang sempurna sesuai dengan apa yang dilakukannya, dan mereka tidak dizalimi.'),
	(455, 3, 162, 'اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْۢ بَاۤءَ بِسَخَطٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ ', 'afamani ittaba\'a ri<u>dh</u>w<u>aa</u>na <strong>al</strong>l<u>aa</u>hi kaman b<u>aa</u>-a bisakha<u>th</u>in mina <strong>al</strong>l<u>aa</u>hi wama/w<u>aa</u>hu jahannamu wabi/sa <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Maka adakah orang yang mengikuti keridaan Allah sama dengan orang yang kembali membawa kemurkaan dari Allah dan tempatnya di neraka Jahanam? Itulah seburuk-buruk tempat kembali.'),
	(456, 3, 163, 'هُمْ دَرَجٰتٌ عِنْدَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌ ۢبِمَا يَعْمَلُوْنَ ', 'hum daraj<u>aa</u>tun \'inda <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ya\'maluun<strong>a</strong>', '(Kedudukan) mereka itu bertingkat-tingkat di sisi Allah, dan Allah Maha Melihat apa yang mereka kerjakan.'),
	(457, 3, 164, 'لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ اِذْ بَعَثَ فِيْهِمْ رَسُوْلًا مِّنْ اَنْفُسِهِمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَۚ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍ ', 'aqad manna <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> <strong>a</strong>lmu/miniina i<u>dz</u> ba\'atsa fiihim rasuulan min anfusihim yatluu \'alayhim <u>aa</u>y<u>aa</u>tihi wayuzakkiihim wayu\'allimuhumu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wa-in k<u>aa</u>nuu min qablu lafii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Sungguh, Allah telah memberi karunia kepada orang-orang beriman ketika (Allah) mengutus seorang Rasul (Muhammad) di tengah-tengah mereka dari kalangan mereka sendiri, yang membacakan kepada mereka ayat-ayat-Nya, menyucikan (jiwa) mereka, dan mengajarkan kepada mereka Kitab (Al-Qur\'an) dan Hikmah (Sunnah), meskipun sebelumnya, mereka benar-benar dalam kesesatan yang nyata.'),
	(458, 3, 165, 'اَوَلَمَّآ اَصَابَتْكُمْ مُّصِيْبَةٌ قَدْ اَصَبْتُمْ مِّثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَا ۗ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'awa lamm<u>aa</u> a<u>shaa</u>batkum mu<u>sh</u>iibatun qad a<u>sh</u>abtum mitslayh<u>aa</u> qultum ann<u>aa</u> h<u>aadzaa</u> qul huwa min \'indi anfusikum inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan mengapa kamu (heran) ketika ditimpa musibah (kekalahan pada Perang Uhud), padahal kamu telah menimpakan musibah dua kali lipat (kepada musuh-musuhmu pada Perang Badar) kamu berkata, “Dari mana datangnya (kekalahan) ini?” Katakanlah, “Itu dari (kesalahan) dirimu sendiri.” Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(459, 3, 166, 'وَمَآ اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِنِيْنَۙ', 'wam<u>aa</u> a<u>shaa</u>bakum yawma iltaq<u>aa</u> <strong>a</strong>ljam\'<u>aa</u>ni fabi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi waliya\'lama <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan apa yang menimpa kamu ketika terjadi pertemuan (pertempuran) antara dua pasukan itu adalah dengan izin Allah, dan agar Allah menguji siapa orang (yang benar-benar) beriman.'),
	(460, 3, 167, 'وَلِيَعْلَمَ الَّذِيْنَ نَافَقُوْا ۖوَقِيْلَ لَهُمْ تَعَالَوْا قَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَوِ ادْفَعُوْا ۗ قَالُوْا لَوْ نَعْلَمُ قِتَالًا لَّاتَّبَعْنٰكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَىِٕذٍ اَقْرَبُ مِنْهُمْ لِلْاِيْمَانِ ۚ يَقُوْلُوْنَ بِاَفْوَاهِهِم', 'waliya\'lama <strong>al</strong>la<u>dz</u>iina n<u>aa</u>faquu waqiila lahum ta\'<u>aa</u>law q<u>aa</u>tiluu fii sabiili <strong>al</strong>l<u>aa</u>hi awi idfa\'uu q<u>aa</u>luu law na\'lamu qit<u>aa</u>lan la<strong>i</strong>ttaba\'n<u>aa</u>kum hum lilkufri yawma-i<u>dz</u>in aqrabu minhum lil-iim<u>aa</u>ni yaquuluuna bi-afw<u>aa</u>hihim m<u>aa</u> laysa fii quluubihim wa<strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> yaktumuun<strong>a</strong>', 'Dan untuk menguji orang-orang yang munafik, kepada mereka dikatakan, “Marilah berperang di jalan Allah atau pertahankanlah (dirimu).” Mereka berkata, “Sekiranya kami mengetahui (bagaimana cara) berperang, tentulah kami mengikuti kamu.” Mereka pada hari itu lebih dekat kepada kekafiran dari pada keimanan. Mereka mengatakan dengan mulutnya apa yang tidak sesuai dengan isi hatinya. Dan Allah lebih mengetahui apa yang mereka sembunyikan.'),
	(461, 3, 168, 'اَلَّذِيْنَ قَالُوْا لِاِخْوَانِهِمْ وَقَعَدُوْا لَوْ اَطَاعُوْنَا مَا قُتِلُوْا ۗ قُلْ فَادْرَءُوْا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ', '<strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu li-ikhw<u>aa</u>nihim waqa\'aduu law a<u>th</u><u>aa</u>\'uun<u>aa</u> m<u>aa</u> qutiluu qul fa<strong>i</strong>drauu \'an anfusikumu <strong>a</strong>lmawta in kuntum <u>shaa</u>diqiin<strong>a</strong>', '(Mereka itu adalah) orang-orang yang berkata kepada saudara-saudaranya dan mereka tidak turut pergi berperang, “Sekiranya mereka mengikuti kita, tentulah mereka tidak terbunuh.” Katakanlah, “Cegahlah kematian itu dari dirimu, jika kamu orang yang benar.”'),
	(462, 3, 169, 'وَلَا تَحْسَبَنَّ الَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتًا ۗ بَلْ اَحْيَاۤءٌ عِنْدَ رَبِّهِمْ يُرْزَقُوْنَۙ', 'wal<u>aa</u> ta<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina qutiluu fii sabiili <strong>al</strong>l<u>aa</u>hi amw<u>aa</u>tan bal a<u>h</u>y<u>aa</u>un \'inda rabbihim yurzaquun<strong>a</strong>', 'Dan jangan sekali-kali kamu mengira bahwa orang-orang yang gugur di jalan Allah itu mati; sebenarnya mereka itu hidup di sisi Tuhannya mendapat rezeki,'),
	(463, 3, 170, 'فَرِحِيْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۙ وَيَسْتَبْشِرُوْنَ بِالَّذِيْنَ لَمْ يَلْحَقُوْا بِهِمْ مِّنْ خَلْفِهِمْ ۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۘ', 'fari<u>h</u>iina bim<u>aa</u> <u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi wayastabsyiruuna bi<strong>a</strong>lla<u>dz</u>iina lam yal<u>h</u>aquu bihim min khalfihim <strong>al</strong>l<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Mereka bergembira dengan karunia yang diberikan Allah kepadanya, dan bergirang hati terhadap orang yang masih tinggal di belakang yang belum menyusul mereka, bahwa tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(464, 3, 171, '۞ يَسْتَبْشِرُوْنَ بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍۗ وَاَنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُؤْمِنِيْنَ ࣖ ', 'yastabsyiruuna bini\'matin mina <strong>al</strong>l<u>aa</u>hi wafa<u>dh</u>lin wa-anna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra <strong>a</strong>lmu/miniin<strong>a</strong>', 'Mereka bergirang hati dengan nikmat dan karunia dari Allah. Dan sungguh, Allah tidak menyia-nyiakan pahala orang-orang yang beriman,'),
	(465, 3, 172, 'اَلَّذِيْنَ اسْتَجَابُوْا لِلّٰهِ وَالرَّسُوْلِ مِنْۢ بَعْدِ مَآ اَصَابَهُمُ الْقَرْحُ ۖ لِلَّذِيْنَ اَحْسَنُوْا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظِيْمٌۚ', '<strong>al</strong>la<u>dz</u>iina istaj<u>aa</u>buu lill<u>aa</u>hi wa<strong>al</strong>rrasuuli min ba\'di m<u>aa</u> a<u>shaa</u>bahumu <strong>a</strong>lqar<u>h</u>u lilla<u>dz</u>iina a<u>h</u>sanuu minhum wa<strong>i</strong>ttaqaw ajrun \'a<u>zh</u>iim<strong>un</strong>', '(yaitu) orang-orang yang menaati (perintah) Allah dan Rasul setelah mereka mendapat luka (dalam Perang Uhud). Orang-orang yang berbuat kebajikan dan bertakwa di antara mereka mendapat pahala yang besar.'),
	(466, 3, 173, 'اَلَّذِيْنَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوْا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ اِيْمَانًاۖ وَّقَالُوْا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَكِيْلُ ', '<strong>al</strong>la<u>dz</u>iina q<u>aa</u>la lahumu <strong>al</strong>nn<u>aa</u>su inna <strong>al</strong>nn<u>aa</u>sa qad jama\'uu lakum fa<strong>i</strong>khsyawhum faz<u>aa</u>dahum iim<u>aa</u>nan waq<u>aa</u>luu <u>h</u>asbun<u>aa</u> <strong>al</strong>l<u>aa</u>hu wani\'ma <strong>a</strong>lwakiil<strong>u</strong>', '(Yaitu) orang-orang (yang menaati Allah dan Rasul) yang ketika ada orang-orang mengatakan kepadanya, “Orang-orang (Quraisy) telah mengumpulkan pasukan untuk menyerang kamu, karena itu takutlah kepada mereka,” ternyata (ucapan) itu menambah (kuat) iman mereka dan mereka menjawab, “Cukuplah Allah (menjadi penolong) bagi kami dan Dia sebaik-baik pelindung.”'),
	(467, 3, 174, 'فَانْقَلَبُوْا بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍ لَّمْ يَمْسَسْهُمْ سُوْۤءٌۙ وَّاتَّبَعُوْا رِضْوَانَ اللّٰهِ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَظِيْمٍ ', 'fa<strong>i</strong>nqalabuu bini\'matin mina <strong>al</strong>l<u>aa</u>hi wafa<u>dh</u>lin lam yamsas-hum suu-un wa<strong>i</strong>ttaba\'uu ri<u>dh</u>w<u>aa</u>na <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu <u>dz</u>uu fa<u>dh</u>lin \'a<u>zh</u>iim<strong>in</strong>', 'Maka mereka kembali dengan nikmat dan karunia (yang besar) dari Allah, mereka tidak ditimpa suatu bencana dan mereka mengikuti keridaan Allah. Allah mempunyai karunia yang besar.'),
	(468, 3, 175, 'اِنَّمَا ذٰلِكُمُ الشَّيْطٰنُ يُخَوِّفُ اَوْلِيَاۤءَهٗۖ فَلَا تَخَافُوْهُمْ وَخَافُوْنِ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ', 'innam<u>aa</u> <u>dzaa</u>likumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu yukhawwifu awliy<u>aa</u>-ahu fal<u>aa</u> takh<u>aa</u>fuuhum wakh<u>aa</u>fuuni in kuntum mu/miniin', 'Sesungguhnya mereka hanyalah setan yang menakut-nakuti (kamu) dengan teman-teman setianya, karena itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, jika kamu orang-orang beriman.'),
	(469, 3, 176, 'وَلَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِۚ اِنَّهُمْ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔا ۗ يُرِيْدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِى الْاٰخِرَةِ  وَلَهُمْ عَذَابٌ عَظِيْمٌۚ ', 'wal<u>aa</u> ya<u>h</u>zunka <strong>al</strong>la<u>dz</u>iina yus<u>aa</u>ri\'uuna fii <strong>a</strong>lkufri innahum lan ya<u>dh</u>urruu <strong>al</strong>l<u>aa</u>ha syay-an yuriidu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>l<u>aa</u> yaj\'ala lahum <u>h</u>a<u>zhzh</u>an fii <strong>a</strong>l-<u>aa</u>khirati walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Dan janganlah engkau (Muhammad) dirisaukan oleh orang-orang yang dengan mudah kembali menjadi kafir; sesungguhnya sedikit pun mereka tidak merugikan Allah. Allah tidak akan memberi bagian (pahala) kepada mereka di akhirat, dan mereka akan mendapat azab yang besar.'),
	(470, 3, 177, 'اِنَّ الَّذِيْنَ اشْتَرَوُا الْكُفْرَ بِالْاِيْمَانِ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ', 'inna <strong>al</strong>la<u>dz</u>iina isytarawuu <strong>a</strong>lkufra bi<strong>a</strong>l-iim<u>aa</u>ni lan ya<u>dh</u>urruu <strong>al</strong>l<u>aa</u>ha syay-an walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sesungguhnya orang-orang yang membeli kekafiran dengan iman, sedikit pun tidak merugikan Allah; dan mereka akan mendapat azab yang pedih.'),
	(471, 3, 178, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْٓا اَنَّمَا نُمْلِيْ لَهُمْ خَيْرٌ لِّاَنْفُسِهِمْ ۗ اِنَّمَا نُمْلِيْ لَهُمْ لِيَزْدَادُوْٓا اِثْمًا ۚ وَلَهُمْ عَذَابٌ مُّهِيْنٌ ', 'wal<u>aa</u> ya<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina kafaruu annam<u>aa</u> numlii lahum khayrun li-anfusihim innam<u>aa</u> numlii lahum liyazd<u>aa</u>duu itsman walahum \'a<u>dzaa</u>bun muhiin<strong>un</strong>', 'Dan jangan sekali-kali orang-orang kafir itu mengira bahwa tenggang waktu yang Kami berikan kepada mereka lebih baik baginya. Sesungguhnya tenggang waktu yang Kami berikan kepada mereka hanyalah agar dosa mereka semakin bertambah; dan mereka akan mendapat azab yang menghinakan.'),
	(472, 3, 179, 'مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِنِيْنَ عَلٰى مَآ اَنْتُمْ عَلَيْهِ حَتّٰى يَمِيْزَ الْخَبِيْثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَبِيْ مِنْ رُّسُلِهٖ مَنْ يَّشَاۤءُ ۖ فَاٰمِنُوْا بِاللّٰهِ وَر', 'm<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liya<u>dz</u>ara <strong>a</strong>lmu/miniina \'al<u>aa</u> m<u>aa</u> antum \'alayhi <u>h</u>att<u>aa</u> yamiiza <strong>a</strong>lkhabiitsa mina <strong>al</strong><u>ththh</u>ayyibi wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liyu<u>th</u>li\'akum \'al<u>aa</u> <strong>a</strong>lghaybi wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yajtabii min rusulihi man yasy<u>aa</u>u fa<u>aa</u>minuu bi<strong>al</strong>l<u>aa</u>hi warusulihi wa-in tu/minuu watattaquu falakum ajrun \'a<u>zh</u>iim<strong>un</strong>', 'Allah tidak akan membiarkan orang-orang yang beriman sebagaimana dalam keadaan kamu sekarang ini, sehingga Dia membedakan yang buruk dari yang baik. Allah tidak akan memperlihatkan kepadamu hal-hal yang gaib, tetapi Allah memilih siapa yang Dia kehendaki di antara rasul-rasul-Nya. Karena itu, berimanlah kepada Allah dan rasul-rasul-Nya. Jika kamu beriman dan bertakwa, maka kamu akan mendapat pahala yang besar.'),
	(473, 3, 180, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ يَبْخَلُوْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖ هُوَ خَيْرًا لَّهُمْ ۗ بَلْ هُوَ شَرٌّ لَّهُمْ ۗ سَيُطَوَّقُوْنَ مَا بَخِلُوْا بِهٖ يَوْمَ الْقِيٰمَةِ ۗ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بِمَا تَعْمَلُ', 'wal<u>aa</u> ya<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina yabkhaluuna bim<u>aa</u> <u>aa</u>t<u>aa</u>hummu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi huwa khayran lahum bal huwa syarrun lahum sayu<u>th</u>awwaquuna m<u>aa</u> bakhiluu bihi yawma <strong>a</strong>lqiy<u>aa</u>mati walill<u>aa</u>hi miir<u>aa</u>tsu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir<strong>un</strong>', 'Dan jangan sekali-kali orang-orang yang kikir dengan apa yang diberikan Allah kepada mereka dari karunia-Nya mengira bahwa (kikir) itu baik bagi mereka, padahal (kikir) itu buruk bagi mereka. Apa (harta) yang mereka kikirkan itu akan dikalungkan (di lehernya) pada hari Kiamat. Milik Allah-lah warisan (apa yang ada) di langit dan di bumi. Allah Mahateliti apa yang kamu kerjakan.'),
	(474, 3, 181, 'لَقَدْ سَمِعَ اللّٰهُ قَوْلَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ فَقِيْرٌ وَّنَحْنُ اَغْنِيَاۤءُ ۘ سَنَكْتُبُ مَا قَالُوْا وَقَتْلَهُمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّۙ وَّنَقُوْلُ ذُوْقُوْا عَذَابَ الْحَرِيْقِ ', 'laqad sami\'a <strong>al</strong>l<u>aa</u>hu qawla <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha faqiirun wana<u>h</u>nu aghniy<u>aa</u>un sanaktubu m<u>aa</u> q<u>aa</u>luu waqatlahumu <strong>a</strong>l-anbiy<u>aa</u>-a bighayri <u>h</u>aqqin wanaquulu <u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>a</strong>l<u>h</u>ariiq<strong>i</strong>', 'Sungguh, Allah telah mendengar perkataan orang-orang (Yahudi) yang mengatakan, “Sesungguhnya Allah itu miskin dan kami kaya.” Kami akan mencatat perkataan mereka dan perbuatan mereka membunuh nabi-nabi tanpa hak (alasan yang benar), dan Kami akan mengatakan (kepada mereka), “Rasakanlah olehmu azab yang membakar!”'),
	(475, 3, 182, 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۚ ', '<u>dzaa</u>lika bim<u>aa</u> qaddamat aydiikum wa-anna <strong>al</strong>l<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid<strong>i</strong>', 'Demikian itu disebabkan oleh perbuatan tanganmu sendiri, dan sesungguhnya Allah tidak menzalimi hamba-hamba-Nya.'),
	(476, 3, 183, 'اَلَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَآ اَلَّا نُؤْمِنَ لِرَسُوْلٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاۤءَكُمْ رُسُلٌ مِّنْ قَبْلِيْ بِالْبَيِّنٰتِ وَبِالَّذِيْ قُلْتُمْ فَلِمَ قَتَلْتُمُوْهُمْ اِنْ كُنْتُمْ صٰد', '<strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha \'ahida ilayn<u>aa</u> <strong>al</strong>l<u>aa</u> nu/mina lirasuulin <u>h</u>att<u>aa</u> ya/tiyan<u>aa</u> biqurb<u>aa</u>nin ta/kuluhu <strong>al</strong>nn<u>aa</u>ru qul qad j<u>aa</u>-akum rusulun min qablii bi<strong>a</strong>lbayyin<u>aa</u>ti wabi<strong>a</strong>lla<u>dz</u>ii qultum falima qataltumuuhum in kutum <u>shaa</u>diqiin<strong>a</strong>', '(Yaitu) orang-orang (Yahudi) yang mengatakan, “Sesungguhnya Allah telah memerintahkan kepada kami, agar kami tidak beriman kepada seorang rasul, sebelum dia mendatangkan kepada kami kurban yang dimakan api.” Katakanlah (Muhammad), “Sungguh, beberapa orang rasul sebelumku telah datang kepadamu, (dengan) membawa bukti-bukti yang nyata dan membawa apa yang kamu sebutkan, tetapi mengapa kamu membunuhnya jika kamu orang-orang yang benar.”'),
	(477, 3, 184, 'فَاِنْ كَذَّبُوْكَ فَقَدْ كُذِّبَ رُسُلٌ مِّنْ قَبْلِكَ جَاۤءُوْ بِالْبَيِّنٰتِ وَالزُّبُرِ وَالْكِتٰبِ الْمُنِيْرِ', 'fa-in ka<u>dzdz</u>abuuka faqad ku<u>dzdz</u>iba rusulun min qablika j<u>aa</u>uu bi<strong>a</strong>lbayyin<u>aa</u>ti wa<strong>al</strong>zzuburi wa<strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>lmuniir<strong>i</strong>', 'Maka jika mereka mendustakan engkau (Muhammad), maka (ketahuilah) rasul-rasul sebelum engkau pun telah didustakan (pula), mereka membawa mukjizat-mukjizat yang nyata, Zubur dan Kitab yang memberi penjelasan yang sempurna.'),
	(478, 3, 185, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَاِنَّمَا تُوَفَّوْنَ اُجُوْرَكُمْ يَوْمَ الْقِيٰمَةِ ۗ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ ', 'kullu nafsin <u>dzaa</u>-iqatu <strong>a</strong>lmawti wa-innam<u>aa</u> tuwaffawna ujuurakum yawma <strong>a</strong>lqiy<u>aa</u>mati faman zu<u>h</u>zi<u>h</u>a \'ani <strong>al</strong>nn<u>aa</u>ri waudkhila <strong>a</strong>ljannata faqad f<u>aa</u>za wam<u>aa</u> <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> ill<u>aa</u> mat<u>aa</u>\'u <strong>a</strong>lghuruur<strong>i</strong>', 'Setiap yang bernyawa akan merasakan mati. Dan hanya pada hari Kiamat sajalah diberikan dengan sempurna balasanmu. Barangsiapa dijauhkan dari neraka dan dimasukkan ke dalam surga, sungguh, dia memperoleh kemenangan. Kehidupan dunia hanyalah kesenangan yang memperdaya.'),
	(479, 3, 186, '۞ لَتُبْلَوُنَّ فِيْٓ اَمْوَالِكُمْ وَاَنْفُسِكُمْۗ وَلَتَسْمَعُنَّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَمِنَ الَّذِيْنَ اَشْرَكُوْٓا اَذًى كَثِيْرًا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ ', 'latublawunna fii amw<u>aa</u>likum wa-anfusikum walatasma\'unna mina <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba min qablikum wamina <strong>al</strong>la<u>dz</u>iina asyrakuu a<u>dz</u>an katsiiran wa-in ta<u>sh</u>biruu watattaquu fa-inna <u>dzaa</u>lika min \'azmi <strong>a</strong>l-umuur<strong>i</strong>', 'Kamu pasti akan diuji dengan hartamu dan dirimu. Dan pasti kamu akan mendengar banyak hal yang sangat menyakitkan hati dari orang-orang yang diberi Kitab sebelum kamu dan dari orang-orang musyrik. Jika kamu bersabar dan bertakwa, maka sesungguhnya yang demikian itu termasuk urusan yang (patut) diutamakan.'),
	(480, 3, 187, 'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَتُبَيِّنُنَّهٗ لِلنَّاسِ وَلَا تَكْتُمُوْنَهٗۖ فَنَبَذُوْهُ وَرَاۤءَ ظُهُوْرِهِمْ وَاشْتَرَوْا بِهٖ ثَمَنًا قَلِيْلًا ۗ فَبِئْسَ مَا يَشْتَرُوْنَ ', 'wa-i<u>dz</u> akha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu miits<u>aa</u>qa <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba latubayyinunnahu li<strong>l</strong>nn<u>aa</u>si wal<u>aa</u> taktumuunahu fanaba<u>dz</u>uuhu war<u>aa</u>-a <u>zh</u>uhuurihim wa<strong>i</strong>sytaraw bihi tsamanan qaliilan fabi/sa m<u>aa</u> yasytaruun<strong>a</strong>', 'Dan (ingatlah), ketika Allah mengambil janji dari orang-orang yang telah diberi Kitab (yaitu), “Hendaklah kamu benar-benar menerangkannya (isi Kitab itu) kepada manusia, dan janganlah kamu menyembunyikannya,” lalu mereka melemparkan (janji itu) ke belakang punggung mereka dan menjualnya dengan harga murah. Maka itu seburuk-buruk jual-beli yang mereka lakukan.'),
	(481, 3, 188, 'لَا تَحْسَبَنَّ الَّذِيْنَ يَفْرَحُوْنَ بِمَآ اَتَوْا وَّيُحِبُّوْنَ اَنْ يُّحْمَدُوْا بِمَا لَمْ يَفْعَلُوْا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِّنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'l<u>aa</u> ta<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina yafra<u>h</u>uuna bim<u>aa</u> ataw wayu<u>h</u>ibbuuna an yu<u>h</u>maduu bim<u>aa</u> lam yaf\'aluu fal<u>aa</u> ta<u>h</u>sabannahum bimaf<u>aa</u>zatin mina <strong>a</strong>l\'a<u>dzaa</u>bi walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Jangan sekali-kali kamu mengira bahwa orang yang gembira dengan apa yang telah mereka kerjakan dan mereka suka dipuji atas perbuatan yang tidak mereka lakukan, jangan sekali-kali kamu mengira bahwa mereka akan lolos dari azab. Mereka akan mendapat azab yang pedih.'),
	(482, 3, 189, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ ', 'walill<u>aa</u>hi mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan milik Allah-lah kerajaan langit dan bumi; dan Allah Mahakuasa atas segala sesuatu.'),
	(483, 3, 190, 'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيٰتٍ لِّاُولِى الْاَلْبَابِۙ', 'inna fii khalqi <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>i</strong>khtil<u>aa</u>fi <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri la<u>aa</u>y<u>aa</u>tin li-ulii <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', 'Sesungguhnya dalam penciptaan langit dan bumi, dan pergantian malam dan siang terdapat tanda-tanda (kebesaran Allah) bagi orang yang berakal,'),
	(484, 3, 191, 'الَّذِيْنَ يَذْكُرُوْنَ اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِهِمْ وَيَتَفَكَّرُوْنَ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلًاۚ سُبْحٰنَكَ فَقِنَا عَذَابَ النَّارِ ', '<strong>al</strong>la<u>dz</u>iina ya<u>dz</u>kuruuna <strong>al</strong>l<u>aa</u>ha qiy<u>aa</u>man waqu\'uudan wa\'al<u>aa</u> junuubihim wayatafakkaruuna fii khalqi <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i rabban<u>aa</u> m<u>aa</u> khalaqta h<u>aadzaa</u> b<u>aath</u>ilan sub<u>haa</u>naka faqin<u>aa</u> \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', '(yaitu) orang-orang yang mengingat Allah sambil berdiri, duduk atau dalam keadaan berbaring, dan mereka memikirkan tentang penciptaan langit dan bumi (seraya berkata), “Ya Tuhan kami, tidaklah Engkau menciptakan semua ini sia-sia; Mahasuci Engkau, lindungilah kami dari azab neraka.'),
	(485, 3, 192, 'رَبَّنَآ اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ', 'rabban<u>aa</u> innaka man tudkhili <strong>al</strong>nn<u>aa</u>ra faqad akhzaytahu wam<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina min an<u>shaa</u>r<strong>in</strong>', 'Ya Tuhan kami, sesungguhnya orang yang Engkau masukkan ke dalam neraka, maka sungguh, Engkau telah menghinakannya, dan tidak ada seorang penolong pun bagi orang yang zalim.'),
	(486, 3, 193, 'رَبَّنَآ اِنَّنَا سَمِعْنَا مُنَادِيًا يُّنَادِيْ لِلْاِيْمَانِ اَنْ اٰمِنُوْا بِرَبِّكُمْ فَاٰمَنَّا ۖرَبَّنَا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَكَفِّرْ عَنَّا سَيِّاٰتِنَا وَتَوَفَّنَا مَعَ الْاَبْرَارِۚ ', 'rabban<u>aa</u> innan<u>aa</u> sami\'n<u>aa</u> mun<u>aa</u>diyan yun<u>aa</u>dii lil-iim<u>aa</u>ni an <u>aa</u>minuu birabbikum fa<u>aa</u>mann<u>aa</u> rabban<u>aa</u> fa<strong>i</strong>ghfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> wakaffir \'ann<u>aa</u> sayyi-<u>aa</u>tin<u>aa</u> watawaffan<u>aa</u> ma\'a <strong>a</strong>l-abr<u>aa</u>r<strong>i</strong>', 'Ya Tuhan kami, sesungguhnya kami mendengar orang yang menyeru kepada iman, (yaitu), “Berimanlah kamu kepada Tuhanmu,” maka kami pun beriman. Ya Tuhan kami, ampunilah dosa-dosa kami dan hapuskanlah kesalahan-kesalahan kami, dan matikanlah kami beserta orang-orang yang berbakti.'),
	(487, 3, 194, 'رَبَّنَا وَاٰتِنَا مَا وَعَدْتَّنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِ ۗ اِنَّكَ لَا تُخْلِفُ الْمِيْعَادَ ', 'rabban<u>aa</u> wa<u>aa</u>tin<u>aa</u> m<u>aa</u> wa\'adtan<u>aa</u> \'al<u>aa</u> rusulika wal<u>aa</u> tukhzin<u>aa</u> yawma <strong>a</strong>lqiy<u>aa</u>mati innaka l<u>aa</u> tukhlifu <strong>a</strong>lmii\'<u>aa</u>d<strong>a</strong>', 'Ya Tuhan kami, berilah kami apa yang telah Engkau janjikan kepada kami melalui rasul-rasul-Mu. Dan janganlah Engkau hinakan kami pada hari Kiamat. Sungguh, Engkau tidak pernah mengingkari janji.”'),
	(488, 3, 195, 'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنِّيْ لَآ اُضِيْعُ عَمَلَ عَامِلٍ مِّنْكُمْ مِّنْ ذَكَرٍ اَوْ اُنْثٰى ۚ بَعْضُكُمْ مِّنْۢ بَعْضٍ ۚ فَالَّذِيْنَ هَاجَرُوْا وَاُخْرِجُوْا مِنْ دِيَارِهِمْ وَاُوْذُوْا فِيْ سَبِيْلِيْ وَقٰتَلُوْا وَقُتِلُوْا لَاُكَفِّرَنَّ ع', 'fa<strong>i</strong>staj<u>aa</u>ba lahum rabbuhum annii l<u>aa</u> u<u>dh</u>ii\'u \'amala \'<u>aa</u>milin minkum min <u>dz</u>akarin aw unts<u>aa</u> ba\'<u>dh</u>ukum min ba\'<u>dh</u>in fa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>jaruu waukhrijuu min diy<u>aa</u>rihim wauu<u>dz</u>uu fii sabiilii waq<u>aa</u>taluu waqutiluu laukaffiranna \'anhum sayyi-<u>aa</u>tihim walaudkhilannahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru tsaw<u>aa</u>ban min \'indi <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu \'indahu <u>h</u>usnu <strong>al</strong>tstsaw<u>aa</u>b<strong>i</strong>', 'Maka Tuhan mereka memperkenankan permohonannya (dengan berfirman), “Sesungguhnya Aku tidak menyia-nyiakan amal orang yang beramal di antara kamu, baik laki-laki maupun perempuan, (karena) sebagian kamu adalah (keturunan) dari sebagian yang lain. Maka orang yang berhijrah, yang diusir dari kampung halamannya, yang disakiti pada jalan-Ku, yang berperang dan yang terbunuh, pasti akan Aku hapus kesalahan mereka dan pasti Aku masukkan mereka ke dalam surga-surga yang mengalir di bawahnya sungai-sungai, sebagai pahala dari Allah. Dan di sisi Allah ada pahala yang baik.”'),
	(489, 3, 196, 'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِيْنَ كَفَرُوْا فِى الْبِلَادِۗ', 'l<u>aa</u> yaghurrannaka taqallubu <strong>al</strong>la<u>dz</u>iina kafaruu fii <strong>a</strong>lbil<u>aa</u>d<strong>i</strong>', 'Jangan sekali-kali kamu teperdaya oleh kegiatan orang-orang kafir (yang bergerak) di seluruh negeri.'),
	(490, 3, 197, 'مَتَاعٌ قَلِيْلٌ  ۗ ثُمَّ مَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ ', 'mat<u>aa</u>\'un qaliilun tsumma ma/w<u>aa</u>hum jahannamu wabi/sa <strong>a</strong>lmih<u>aa</u>d<strong>u</strong>', 'Itu hanyalah kesenangan sementara, kemudian tempat kembali mereka ialah neraka Jahanam. (Jahanam) itu seburuk-buruk tempat tinggal.'),
	(491, 3, 198, 'لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا نُزُلًا مِّنْ عِنْدِ اللّٰهِ ۗ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِّلْاَبْرَارِ', 'l<u>aa</u>kini <strong>al</strong>la<u>dz</u>iina ittaqaw rabbahum lahum jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> nuzulan min \'indi <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi khayrun lil-abr<u>aa</u>r<strong>i</strong>', 'Tetapi orang-orang yang bertakwa kepada Tuhannya, mereka akan mendapat surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya sebagai karunia dari Allah. Dan apa yang di sisi Allah lebih baik bagi orang-orang yang berbakti.'),
	(492, 3, 199, 'وَاِنَّ مِنْ اَهْلِ الْكِتٰبِ لَمَنْ يُّؤْمِنُ بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْكُمْ وَمَآ اُنْزِلَ اِلَيْهِمْ خٰشِعِيْنَ لِلّٰهِ ۙ لَا يَشْتَرُوْنَ بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا ۗ اُولٰۤىِٕكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْ ۗ اِنَّ اللّٰهَ سَرِيْ', 'wa-inna min ahli <strong>a</strong>lkit<u>aa</u>bi laman yu/minu bi<strong>al</strong>l<u>aa</u>hi wam<u>aa</u> unzila ilaykum wam<u>aa</u> unzila ilayhim kh<u>aa</u>syi\'iina lill<u>aa</u>hi l<u>aa</u> yasytaruuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi tsamanan qaliilan ul<u>aa</u>-ika lahum ajruhum \'inda rabbihim inna <strong>al</strong>l<u>aa</u>ha sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Dan sesungguhnya di antara Ahli Kitab ada yang beriman kepada Allah, dan kepada apa yang diturunkan kepada kamu, dan yang diturunkan kepada mereka, karena mereka berendah hati kepada Allah, dan mereka tidak memperjualbelikan ayat-ayat Allah dengan harga murah. Mereka memperoleh pahala di sisi Tuhannya. Sungguh, Allah sangat cepat perhitungan-Nya.'),
	(493, 3, 200, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اصْبِرُوْا وَصَابِرُوْا وَرَابِطُوْاۗ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ ࣖ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>sh</u>biruu wa<u>shaa</u>biruu war<u>aa</u>bi<u>th</u>uu wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Bersabarlah kamu dan kuatkanlah kesabaranmu dan tetaplah bersiap-siaga (di perbatasan negerimu) dan bertakwalah kepada Allah agar kamu beruntung.                    '),
	(494, 4, 1, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيْرًا وَّنِسَاۤءً ۚ وَاتَّقُوا اللّٰهَ الَّذِيْ تَسَاۤءَلُوْنَ بِهٖ وَالْاَرْحَامَ ۗ اِنَّ اللّٰهَ كَانَ عَلَي', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su ittaquu rabbakumu <strong>al</strong>la<u>dz</u>ii khalaqakum min nafsin w<u>aah</u>idatin wakhalaqa minh<u>aa</u> zawjah<u>aa</u> wabatstsa minhum<u>aa</u> rij<u>aa</u>lan katsiiran wanis<u>aa</u>-an wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha <strong>al</strong>la<u>dz</u>ii tas<u>aa</u>-aluuna bihi wa<strong>a</strong>l-ar<u>haa</u>ma inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'alaykum raqiib<strong>an</strong>', 'Wahai manusia! Bertakwalah kepada Tuhanmu yang telah menciptakan kamu dari diri yang satu (Adam), dan (Allah) menciptakan pasangannya (Hawa) dari (diri)-nya; dan dari keduanya Allah memperkembangbiakkan laki-laki dan perempuan yang banyak. Bertakwalah kepada Allah yang dengan nama-Nya kamu saling meminta, dan (peliharalah) hubungan kekeluargaan. Sesungguhnya Allah selalu menjaga dan mengawasimu.'),
	(495, 4, 2, 'وَاٰتُوا الْيَتٰمٰىٓ اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَبِيْثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوْٓا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْ ۗ اِنَّهٗ كَانَ حُوْبًا كَبِيْرًا', 'wa<u>aa</u>tuu <strong>a</strong>lyat<u>aa</u>m<u>aa</u> amw<u>aa</u>lahum wal<u>aa</u> tatabaddaluu <strong>a</strong>lkhabiitsa bi<strong>al</strong><u>ththh</u>ayyibi wal<u>aa</u> ta/kuluu amw<u>aa</u>lahum il<u>aa</u> amw<u>aa</u>likum innahu k<u>aa</u>na <u>h</u>uuban kabiir<u>aa</u><strong>n</strong>', 'Dan berikanlah kepada anak-anak yatim (yang sudah dewasa) harta mereka, janganlah kamu menukar yang baik dengan yang buruk, dan janganlah kamu makan harta mereka bersama hartamu. Sungguh, (tindakan menukar dan memakan) itu adalah dosa yang besar.'),
	(496, 4, 3, 'وَاِنْ خِفْتُمْ اَلَّا تُقْسِطُوْا فِى الْيَتٰمٰى فَانْكِحُوْا مَا طَابَ لَكُمْ مِّنَ النِّسَاۤءِ مَثْنٰى وَثُلٰثَ وَرُبٰعَ ۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوْا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْ ۗ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُوْلُوْاۗ', 'wa-in khiftum <strong>al</strong>l<u>aa</u> tuqsi<u>th</u>uu fii <strong>a</strong>lyat<u>aa</u>m<u>aa</u> fa<strong>i</strong>nki<u>h</u>uu m<u>aa</u> <u>th</u><u>aa</u>ba lakum mina <strong>al</strong>nnis<u>aa</u>-i matsn<u>aa</u> watsul<u>aa</u>tsa warub<u>aa</u>\'a fa-in khiftum <strong>al</strong>l<u>aa</u> ta\'diluu faw<u>aah</u>idatan aw m<u>aa</u> malakat aym<u>aa</u>nukum <u>dzaa</u>lika adn<u>aa</u> <strong>al</strong>l<u>aa</u> ta\'uuluu', 'Dan jika kamu khawatir tidak akan mampu berlaku adil terhadap (hak-hak) perempuan yatim (bilamana kamu menikahinya), maka nikahilah perempuan (lain) yang kamu senangi: dua, tiga atau empat. Tetapi jika kamu khawatir tidak akan mampu berlaku adil, maka (nikahilah) seorang saja, atau hamba sahaya perempuan yang kamu miliki. Yang demikian itu lebih dekat agar kamu tidak berbuat zalim.'),
	(497, 4, 4, 'وَاٰتُوا النِّسَاۤءَ صَدُقٰتِهِنَّ نِحْلَةً ۗ فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِّنْهُ نَفْسًا فَكُلُوْهُ هَنِيْۤـًٔا مَّرِيْۤـًٔا ', 'wa<u>aa</u>tuu <strong>al</strong>nnis<u>aa</u>-a <u>sh</u>aduq<u>aa</u>tihinna ni<u>h</u>latan fa-in <u>th</u>ibna lakum \'an syay-in minhu nafsan fakuluuhu hanii-an marii-<u>aa</u><strong>n</strong>', 'Dan berikanlah maskawin (mahar) kepada perempuan (yang kamu nikahi) sebagai pemberian yang penuh kerelaan. Kemudian, jika mereka menyerahkan kepada kamu sebagian dari (maskawin) itu dengan senang hati, maka terimalah dan nikmatilah pemberian itu dengan senang hati.'),
	(498, 4, 5, 'وَلَا تُؤْتُوا السُّفَهَاۤءَ اَمْوَالَكُمُ الَّتِيْ جَعَلَ اللّٰهُ لَكُمْ قِيٰمًا وَّارْزُقُوْهُمْ فِيْهَا وَاكْسُوْهُمْ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا', 'wal<u>aa</u> tu/tuu <strong>al</strong>ssufah<u>aa</u>-a amw<u>aa</u>lakumu <strong>al</strong>latii ja\'ala <strong>al</strong>l<u>aa</u>hu lakum qiy<u>aa</u>man wa<strong>u</strong>rzuquuhum fiih<u>aa</u> wa<strong>u</strong>ksuuhum waquuluu lahum qawlan ma\'ruuf<u>aa</u><strong>n</strong>', 'Dan janganlah kamu serahkan kepada orang yang belum sempurna akalnya, harta (mereka yang ada dalam kekuasaan) kamu yang dijadikan Allah sebagai pokok kehidupan. Berilah mereka belanja dan pakaian (dari hasil harta itu) dan ucapkanlah kepada mereka perkataan yang baik.'),
	(499, 4, 6, 'وَابْتَلُوا الْيَتٰمٰى حَتّٰىٓ اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِّنْهُمْ رُشْدًا فَادْفَعُوْٓا اِلَيْهِمْ اَمْوَالَهُمْ ۚ وَلَا تَأْكُلُوْهَآ اِسْرَافًا وَّبِدَارًا اَنْ يَّكْبَرُوْا ۗ وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۚ وَمَنْ كَانَ فَ', 'wa<strong>i</strong>btaluu <strong>a</strong>lyat<u>aa</u>m<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> balaghuu <strong>al</strong>nnik<u>aah</u>a fa-in <u>aa</u>nastum minhum rusydan fa<strong>i</strong>dfa\'uu ilayhim amw<u>aa</u>lahum wal<u>aa</u> ta/kuluuh<u>aa</u> isr<u>aa</u>fan wabid<u>aa</u>ran an yakbaruu waman k<u>aa</u>na ghaniyyan falyasta\'fif waman k<u>aa</u>na faqiiran falya/kul bi<strong>a</strong>lma\'ruufi fa-i<u>dzaa</u> dafa\'tum ilayhim amw<u>aa</u>lahum fa-asyhiduu \'alayhim wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi <u>h</u>asiib<u>aa</u><strong>n</strong>', 'Dan ujilah anak-anak yatim itu sampai mereka cukup umur untuk menikah. Kemudian jika menurut pendapatmu mereka telah cerdas (pandai memelihara harta), maka serahkanlah kepada mereka hartanya. Dan janganlah kamu memakannya (harta anak yatim) melebihi batas kepatutan dan (janganlah kamu) tergesa-gesa (menyerahkannya) sebelum mereka dewasa. Barangsiapa (di antara pemelihara itu) mampu, maka hendaklah dia menahan diri (dari memakan harta anak yatim itu) dan barangsiapa miskin, maka bolehlah dia makan harta itu menurut cara yang patut. Kemudian, apabila kamu menyerahkan harta itu kepada mereka, maka hendaklah kamu adakan saksi-saksi. Dan cukuplah Allah sebagai pengawas.'),
	(500, 4, 7, 'لِلرِّجَالِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَۖ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَ ۗ نَصِيْبًا مَّفْرُوْضًا', 'li<strong>l</strong>rrij<u>aa</u>li na<u>sh</u>iibun mimm<u>aa</u> taraka <strong>a</strong>lw<u>aa</u>lid<u>aa</u>ni wa<strong>a</strong>l-aqrabuuna wali<strong>l</strong>nnis<u>aa</u>-i na<u>sh</u>iibun mimm<u>aa</u> taraka <strong>a</strong>lw<u>aa</u>lid<u>aa</u>ni wa<strong>a</strong>l-aqrabuuna mimm<u>aa</u> qalla minhu aw katsura na<u>sh</u>iiban mafruu<u>daa</u><strong>n</strong>', 'Bagi laki-laki ada hak bagian dari harta peninggalan kedua orang tua dan kerabatnya, dan bagi perempuan ada hak bagian (pula) dari harta peninggalan kedua orang tua dan kerabatnya, baik sedikit atau banyak menurut bagian yang telah ditetapkan.'),
	(501, 4, 8, 'وَاِذَا حَضَرَ الْقِسْمَةَ اُولُوا الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنُ فَارْزُقُوْهُمْ مِّنْهُ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا', 'wa-i<u>dzaa</u> <u>h</u>a<u>dh</u>ara <strong>a</strong>lqismata uluu <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiinu fa<strong>u</strong>rzuquuhum minhu waquuluu lahum qawlan ma\'ruuf<u>aa</u><strong>n</strong>', 'Dan apabila sewaktu pembagian itu hadir beberapa kerabat, anak-anak yatim dan orang-orang miskin, maka berilah mereka dari harta itu (sekedarnya) dan ucapkanlah kepada mereka perkataan yang baik.'),
	(502, 4, 9, 'وَلْيَخْشَ الَّذِيْنَ لَوْ تَرَكُوْا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعٰفًا خَافُوْا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُوْلُوْا قَوْلًا سَدِيْدًا', 'walyakhsya <strong>al</strong>la<u>dz</u>iina law tarakuu min khalfihim <u>dz</u>urriyyatan <u>dh</u>i\'<u>aa</u>fan kh<u>aa</u>fuu \'alayhim falyattaquu <strong>al</strong>l<u>aa</u>ha walyaquuluu qawlan sadiid<u>aa</u><strong>n</strong>', 'Dan hendaklah takut (kepada Allah) orang-orang yang sekiranya mereka meninggalkan keturunan yang lemah di belakang mereka yang mereka khawatir terhadap (kesejahteraan)nya. Oleh sebab itu, hendaklah mereka bertakwa kepada Allah, dan hendaklah mereka berbicara dengan tutur kata yang benar.'),
	(503, 4, 10, 'اِنَّ الَّذِيْنَ يَأْكُلُوْنَ اَمْوَالَ الْيَتٰمٰى ظُلْمًا اِنَّمَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ نَارًا ۗ وَسَيَصْلَوْنَ سَعِيْرًا ࣖ ', 'inna <strong>al</strong>la<u>dz</u>iina ya/kuluuna amw<u>aa</u>la <strong>a</strong>lyat<u>aa</u>m<u>aa</u> <u>zh</u>ulman innam<u>aa</u> ya/kuluuna fii bu<u>th</u>uunihim n<u>aa</u>ran wasaya<u>sh</u>lawna sa\'iir<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang memakan harta anak yatim secara zalim, sebenarnya mereka itu menelan api dalam perutnya dan mereka akan masuk ke dalam api yang menyala-nyala (neraka).'),
	(504, 4, 11, 'يُوْصِيْكُمُ اللّٰهُ فِيْٓ اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِ ۚ فَاِنْ كُنَّ نِسَاۤءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۗ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ مِم', 'yuu<u>sh</u>iikumu <strong>al</strong>l<u>aa</u>hu fii awl<u>aa</u>dikum li<strong>l</strong><u>dzdz</u>akari mitslu <u>h</u>a<u>zhzh</u>i <strong>a</strong>luntsayayni fa-in kunna nis<u>aa</u>-an fawqa itsnatayni falahunna tsuluts<u>aa</u> m<u>aa</u> taraka wa-in k<u>aa</u>nat w<u>aah</u>idatan falah<u>aa</u> <strong>al</strong>nni<u>sh</u>fu wali-abawayhi likulli w<u>aah</u>idin minhum<u>aa</u> <strong>al</strong>ssudusu mimm<u>aa</u> taraka in k<u>aa</u>na lahu waladun fa-in lam yakun lahu waladun wawaritsahu abaw<u>aa</u>hu fali-ummihi <strong>al</strong>tstsulutsu fa-in k<u>aa</u>na lahu ikhwatun fali-ummihi <strong>al</strong>ssudusu min ba\'di wa<u>sh</u>iyyatin yuu<u>sh</u>ii bih<u>aa</u> aw daynin <u>aa</u>b<u>aa</u>ukum wa-abn<u>aa</u>ukum l<u>aa</u> tadruuna ayyuhum aqrabu lakum naf\'an farii<u>dh</u>atan mina <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Allah mensyariatkan (mewajibkan) kepadamu tentang (pembagian warisan untuk) anak-anakmu, (yaitu) bagian seorang anak laki-laki sama dengan bagian dua orang anak perempuan. Dan jika anak itu semuanya perempuan yang jumlahnya lebih dari dua, maka bagian mereka dua pertiga dari harta yang ditinggalkan. Jika dia (anak perempuan) itu seorang saja, maka dia memperoleh setengah (harta yang ditinggalkan). Dan untuk kedua ibu-bapak, bagian masing-masing seperenam dari harta yang ditinggalkan, jika dia (yang meninggal) mempunyai anak. Jika dia (yang meninggal) tidak mempunyai anak dan dia diwarisi oleh kedua ibu-bapaknya (saja), maka ibunya mendapat sepertiga. Jika dia (yang meninggal) mempunyai beberapa saudara, maka ibunya mendapat seperenam. (Pembagian-pembagian tersebut di atas) setelah (dipenuhi) wasiat yang dibuatnya atau (dan setelah dibayar) utangnya. (Tentang) orang tuamu dan anak-anakmu, kamu tidak mengetahui siapa di antara mereka yang lebih banyak manfaatnya bagimu. Ini adalah ketetapan Allah. Sungguh, Allah Maha Mengetahui, Mahabijaksana.'),
	(505, 4, 12, '۞ وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَّمْ يَكُنْ لَّهُنَّ وَلَدٌ ۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْنَ بِهَآ اَوْ دَيْنٍ ۗ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَّمْ يَكُنْ ل', 'walakum ni<u>sh</u>fu m<u>aa</u> taraka azw<u>aa</u>jukum in lam yakun lahunna waladun fa-in k<u>aa</u>na lahunna waladun falakumu <strong>al</strong>rrubu\'u mimm<u>aa</u> tarakna min ba\'di wa<u>sh</u>iyyatin yuu<u>sh</u>iina bih<u>aa</u> aw daynin walahunna <strong>al</strong>rrubu\'u mimm<u>aa</u> taraktum in lam yakun lakum waladun fa-in k<u>aa</u>na lakum waladun falahunna <strong>al</strong>tstsumunu mimm<u>aa</u> taraktum min ba\'di wa<u>sh</u>iyyatin tuu<u>sh</u>uuna bih<u>aa</u> aw daynin wa-in k<u>aa</u>na rajulun yuuratsu kal<u>aa</u>latan awi imra-atun walahu akhun aw ukhtun falikulli w<u>aah</u>idin minhum<u>aa</u> <strong>al</strong>ssudusu fa-in k<u>aa</u>nuu aktsara min <u>dzaa</u>lika fahum syurak<u>aa</u>u fii <strong>al</strong>tstsulutsi min ba\'di wa<u>sh</u>iyyatin yuu<u>shaa</u> bih<u>aa</u> aw daynin ghayra mu<u>daa</u>rrin wa<u>sh</u>iyyatan mina <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>aliim<strong>un</strong>', 'Dan bagianmu (suami-suami) adalah seperdua dari harta yang ditinggalkan oleh istri-istrimu, jika mereka tidak mempunyai anak. Jika mereka (istri-istrimu) itu mempunyai anak, maka kamu mendapat seperempat dari harta yang ditinggalkannya setelah (dipenuhi) wasiat yang mereka buat atau (dan setelah dibayar) utangnya. Para istri memperoleh seperempat harta yang kamu tinggalkan jika kamu tidak mempunyai anak. Jika kamu mempunyai anak, maka para istri memperoleh seperdelapan dari harta yang kamu tinggalkan (setelah dipenuhi) wasiat yang kamu buat atau (dan setelah dibayar) utang-utangmu. Jika seseorang meninggal, baik laki-laki maupun perempuan yang tidak meninggalkan ayah dan tidak meninggalkan anak, tetapi mempunyai seorang saudara laki-laki (seibu) atau seorang saudara perempuan (seibu), maka bagi masing-masing dari kedua jenis saudara itu seperenam harta.  Tetapi  jika  saudara-saudara seibu itu lebih dari seorang, maka mereka bersama-sama dalam bagian yang sepertiga itu, setelah (dipenuhi wasiat) yang dibuatnya atau (dan setelah dibayar) utangnya dengan tidak menyusahkan (kepada ahli waris). Demikianlah ketentuan Allah. Allah Maha Mengetahui, Maha Penyantun.'),
	(506, 4, 13, 'تِلْكَ حُدُوْدُ اللّٰهِ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَذٰلِكَ الْفَوْزُ الْعَظِيْمُ ', 'tilka <u>h</u>uduudu <strong>al</strong>l<u>aa</u>hi waman yu<u>th</u>i\'i <strong>al</strong>l<u>aa</u>ha warasuulahu yudkhilhu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Itulah batas-batas (hukum) Allah. Barangsiapa taat kepada Allah dan Rasul-Nya, Dia akan memasukkannya ke dalam surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan itulah kemenangan yang agung.'),
	(507, 4, 14, 'وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ وَيَتَعَدَّ حُدُوْدَهٗ يُدْخِلْهُ نَارًا خَالِدًا فِيْهَاۖ وَلَهٗ عَذَابٌ مُّهِيْنٌ ࣖ', 'waman ya\'<u>sh</u>i <strong>al</strong>l<u>aa</u>ha warasuulahu wayata\'adda <u>h</u>uduudahu yudkhilhu n<u>aa</u>ran kh<u>aa</u>lidan fiih<u>aa</u> walahu \'a<u>dzaa</u>bun muhiin<strong>un</strong>', 'Dan barangsiapa mendurhakai Allah dan Rasul-Nya dan melanggar batas-batas hukum-Nya, niscaya Allah memasukkannya ke dalam api neraka, dia kekal di dalamnya dan dia akan mendapat azab yang menghinakan.'),
	(508, 4, 15, 'وَالّٰتِيْ يَأْتِيْنَ الْفَاحِشَةَ مِنْ نِّسَاۤىِٕكُمْ فَاسْتَشْهِدُوْا عَلَيْهِنَّ اَرْبَعَةً مِّنْكُمْ ۚ فَاِنْ شَهِدُوْا فَاَمْسِكُوْهُنَّ فِى الْبُيُوْتِ حَتّٰى يَتَوَفّٰىهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَبِيْلًا ', 'wa<strong>a</strong>ll<u>aa</u>tii ya/tiina <strong>a</strong>lf<u>aah</u>isyata min nis<u>aa</u>-ikum fa<strong>i</strong>stasyhiduu \'alayhinna arba\'atan minkum fa-in syahiduu fa-amsikuuhunna fii <strong>a</strong>lbuyuuti <u>h</u>att<u>aa</u> yatawaff<u>aa</u>hunna <strong>a</strong>lmawtu aw yaj\'ala <strong>al</strong>l<u>aa</u>hu lahunna sabiil<u>aa</u><strong>n</strong>wa<strong>a</strong>lla<u>dzaa</u>ni ya/tiy<u>aa</u>nih<u>aa</u> minkum fa<u>aadz</u>uuhum<u>aa</u> fa-in t<u>aa</u>b<u>aa</u> wa-a<u>sh</u>la<u>haa</u> fa-a\'ri<u>dh</u>uu \'anhum<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na taww<u>aa</u>ban ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dan para perempuan yang melakukan perbuatan keji di antara perempuan-perempuan kamu, hendaklah terhadap mereka ada empat orang saksi di antara kamu (yang menyaksikannya). Apabila mereka telah memberi kesaksian, maka kurunglah mereka (perempuan itu) dalam rumah sampai mereka menemui ajalnya, atau sampai Allah memberi jalan (yang lain) kepadanya.'),
	(509, 4, 16, 'وَالَّذٰنِ يَأْتِيٰنِهَا مِنْكُمْ فَاٰذُوْهُمَا ۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوْا عَنْهُمَا ۗ اِنَّ اللّٰهَ كَانَ تَوَّابًا رَّحِيْمًا ', 'wa<strong>a</strong>lla<u>dzaa</u>ni ya/tiy<u>aa</u>nih<u>aa</u> minkum fa<u>aadz</u>uuhum<u>aa</u> fa-in t<u>aa</u>b<u>aa</u> wa-a<u>sh</u>la<u>haa</u> fa-a\'ri<u>dh</u>uu \'anhum<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na taww<u>aa</u>ban ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dan terhadap dua orang yang melakukan perbuatan keji di antara kamu, maka berilah hukuman kepada keduanya. Jika keduanya tobat dan memperbaiki diri, maka biarkanlah mereka. Sungguh, Allah Maha Penerima tobat, Maha Penyayang.'),
	(510, 4, 17, 'اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذِيْنَ يَعْمَلُوْنَ السُّوْۤءَ بِجَهَالَةٍ ثُمَّ يَتُوْبُوْنَ مِنْ قَرِيْبٍ فَاُولٰۤىِٕكَ يَتُوْبُ اللّٰهُ عَلَيْهِمْ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ', 'innam<u>aa</u> <strong>al</strong>ttawbatu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi lilla<u>dz</u>iina ya\'maluuna <strong>al</strong>ssuu-a bijah<u>aa</u>latin tsumma yatuubuuna min qariibin faul<u>aa</u>-ika yatuubu <strong>al</strong>l<u>aa</u>hu \'alayhim wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Sesungguhnya bertobat kepada Allah itu hanya (pantas) bagi mereka yang melakukan kejahatan karena tidak mengerti, kemudian segera bertobat. Tobat mereka itulah yang diterima Allah. Allah Maha Mengetahui, Mahabijaksana.'),
	(511, 4, 18, 'وَلَيْسَتِ التَّوْبَةُ لِلَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِۚ حَتّٰىٓ اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنِّيْ تُبْتُ الْـٰٔنَ وَلَا الَّذِيْنَ يَمُوْتُوْنَ وَهُمْ كُفَّارٌ ۗ اُولٰۤىِٕكَ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا ', 'walaysati <strong>al</strong>ttawbatu lilla<u>dz</u>iina ya\'maluuna <strong>al</strong>ssayyi-<u>aa</u>ti <u>h</u>att<u>aa</u> i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adahumu <strong>a</strong>lmawtu q<u>aa</u>la innii tubtu <strong>a</strong>l-<u>aa</u>na wal<u>aa</u> <strong>al</strong>la<u>dz</u>iina yamuutuuna wahum kuff<u>aa</u>run ul<u>aa</u>-ika a\'tadn<u>aa</u> lahum \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'Dan tobat itu tidaklah (diterima Allah) dari mereka yang melakukan kejahatan hingga apabila datang ajal kepada seseorang di antara mereka, (barulah) dia mengatakan, “Saya benar-benar bertobat sekarang.” Dan tidak (pula diterima tobat) dari orang-orang yang meninggal sedang mereka di dalam kekafiran. Bagi orang-orang itu telah Kami sediakan azab yang pedih.'),
	(512, 4, 19, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَاۤءَ كَرْهًا ۗ وَلَا تَعْضُلُوْهُنَّ لِتَذْهَبُوْا بِبَعْضِ مَآ اٰتَيْتُمُوْهُنَّ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوْهُنَّ بِالْمَعْرُوْفِ ۚ فَاِنْ كَرِه', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ya<u>h</u>illu lakum an taritsuu <strong>al</strong>nnis<u>aa</u>-a karhan wal<u>aa</u> ta\'<u>dh</u>uluuhunna lita<u>dz</u>habuu biba\'<u>dh</u>i m<u>aa</u> <u>aa</u>taytumuuhunna ill<u>aa</u> an ya/tiina bif<u>aah</u>isyatin mubayyinatin wa\'<u>aa</u>syiruuhunna bi<strong>a</strong>lma\'ruufi fa-in karihtumuuhunna fa\'as<u>aa</u> an takrahuu syay-an wayaj\'ala <strong>al</strong>l<u>aa</u>hu fiihi khayran katsiir<u>aa</u><strong>n</strong>', 'Wahai orang-orang beriman! Tidak halal bagi kamu mewarisi perempuan dengan jalan paksa dan janganlah kamu menyusahkan mereka karena hendak mengambil kembali sebagian dari apa yang telah kamu berikan kepadanya, kecuali apabila mereka melakukan perbuatan keji yang nyata. Dan bergaullah dengan mereka menurut cara yang patut. Jika kamu tidak menyukai mereka, (maka bersabarlah) karena boleh jadi kamu tidak menyukai sesuatu, padahal Allah menjadikan kebaikan yang banyak padanya.'),
	(513, 4, 20, 'وَاِنْ اَرَدْتُّمُ اسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍۙ وَّاٰتَيْتُمْ اِحْدٰىهُنَّ قِنْطَارًا فَلَا تَأْخُذُوْا مِنْهُ شَيْـًٔا ۗ اَتَأْخُذُوْنَهٗ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ', 'wa-in aradtumu istibd<u>aa</u>la zawjin mak<u>aa</u>na zawjin wa<u>aa</u>taytum i<u>h</u>d<u>aa</u>hunna qin<u>th</u><u>aa</u>ran fal<u>aa</u> ta/khu<u>dz</u>uu minhu syay-an ata/khu<u>dz</u>uunahu buht<u>aa</u>nan wa-itsman mubiin<u>aa</u><strong>n</strong>', 'Dan jika kamu ingin mengganti istrimu dengan istri yang lain, sedang kamu telah memberikan kepada seorang di antara mereka harta yang banyak, maka janganlah kamu mengambil kembali sedikit pun darinya. Apakah kamu akan mengambilnya kembali dengan jalan tuduhan yang dusta dan dengan (menanggung) dosa yang nyata?'),
	(514, 4, 21, 'وَكَيْفَ تَأْخُذُوْنَهٗ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَّاَخَذْنَ مِنْكُمْ مِّيْثَاقًا غَلِيْظًا ', 'wakayfa ta/khu<u>dz</u>uunahu waqad af<u>daa</u> ba\'<u>dh</u>ukum il<u>aa</u> ba\'<u>dh</u>in wa-akha<u>dz</u>na minkum miits<u>aa</u>qan ghalii<u>zhaa</u><strong>n</strong>', 'Dan bagaimana kamu akan mengambilnya kembali, padahal kamu telah bergaul satu sama lain (sebagai suami-istri). Dan mereka (istri-istrimu) telah mengambil perjanjian yang kuat (ikatan pernikahan) dari kamu.'),
	(515, 4, 22, 'وَلَا تَنْكِحُوْا مَا نَكَحَ اٰبَاۤؤُكُمْ مِّنَ النِّسَاۤءِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّهٗ كَانَ فَاحِشَةً وَّمَقْتًاۗ وَسَاۤءَ سَبِيْلًا ࣖ ', 'wal<u>aa</u> tanki<u>h</u>uu m<u>aa</u> naka<u>h</u>a <u>aa</u>b<u>aa</u>ukum mina <strong>al</strong>nnis<u>aa</u>-i ill<u>aa</u> m<u>aa</u> qad salafa innahu k<u>aa</u>na f<u>aah</u>isyatan wamaqtan was<u>aa</u>-a sabiil<u>aa</u><strong>n</strong>', 'Dan janganlah kamu menikahi perempuan-perempuan yang telah dinikahi oleh ayahmu, kecuali (kejadian pada masa) yang telah lampau. Sungguh, perbuatan itu sangat keji dan dibenci (oleh Allah) dan seburuk-buruk jalan (yang ditempuh).'),
	(516, 4, 23, 'حُرِّمَتْ عَلَيْكُمْ اُمَّهٰتُكُمْ وَبَنٰتُكُمْ وَاَخَوٰتُكُمْ وَعَمّٰتُكُمْ وَخٰلٰتُكُمْ وَبَنٰتُ الْاَخِ وَبَنٰتُ الْاُخْتِ وَاُمَّهٰتُكُمُ الّٰتِيْٓ اَرْضَعْنَكُمْ وَاَخَوٰتُكُمْ مِّنَ الرَّضَاعَةِ وَاُمَّهٰتُ نِسَاۤىِٕكُمْ وَرَبَاۤىِٕبُكُمُ الّٰتِيْ ف', '<u>h</u>urrimat \'alaykum ummah<u>aa</u>tukum waban<u>aa</u>tukum wa-akhaw<u>aa</u>tukum wa\'amm<u>aa</u>tukum wakh<u>aa</u>l<u>aa</u>tukum waban<u>aa</u>tu <strong>a</strong>l-akhi waban<u>aa</u>tu <strong>a</strong>l-ukhti waummah<u>aa</u>tukumu <strong>al</strong>l<u>aa</u>tii ar<u>dh</u>a\'nakum wa-akhaw<u>aa</u>tukum mina <strong>al</strong>rra<u>daa</u>\'ati waummah<u>aa</u>tu nis<u>aa</u>-ikum warab<u>aa</u>-ibukumu <strong>al</strong>l<u>aa</u>tii fii <u>h</u>ujuurikum min nis<u>aa</u>-ikumu <strong>al</strong>l<u>aa</u>tii dakhaltum bihinna fa-in lam takuunuu dakhaltum bihinna fal<u>aa</u> jun<u>aah</u>a \'alaykum wa<u>h</u>al<u>aa</u>-ilu abn<u>aa</u>-ikumu <strong>al</strong>la<u>dz</u>iina min a<u>sh</u>l<u>aa</u>bikum wa-an tajma\'uu bayna <strong>a</strong>l-ukhtayni ill<u>aa</u> m<u>aa</u> qad salafa inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Diharamkan atas kamu (menikahi) ibu-ibumu, anak-anakmu yang perempuan, saudara-saudaramu yang perempuan, saudara-saudara ayahmu yang perempuan, saudara-saudara ibumu yang perempuan, anak-anak perempuan dari saudara-saudaramu yang laki-laki, anak-anak perempuan dari saudara-saudaramu yang perempuan, ibu-ibumu yang menyusui kamu, saudara-saudara perempuanmu sesusuan, ibu-ibu istrimu (mertua), anak-anak perempuan dari istrimu (anak tiri) yang dalam pemeliharaanmu dari istri yang telah kamu campuri, tetapi jika kamu belum campur dengan istrimu itu (dan sudah kamu ceraikan), maka tidak berdosa kamu (menikahinya), (dan diharamkan bagimu) istri-istri anak kandungmu (menantu), dan (diharamkan) mengumpulkan (dalam pernikahan) dua perempuan yang bersaudara, kecuali yang telah terjadi pada masa lampau. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(517, 4, 24, '۞ وَالْمُحْصَنٰتُ مِنَ النِّسَاۤءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْ ۚ  كِتٰبَ اللّٰهِ عَلَيْكُمْ ۚ وَاُحِلَّ لَكُمْ مَّا وَرَاۤءَ ذٰلِكُمْ اَنْ تَبْتَغُوْا بِاَمْوَالِكُمْ مُّحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ ۗ فَمَا اسْتَمْتَعْتُمْ بِهٖ مِنْهُنَّ فَاٰتُوْ', 'wa<strong>a</strong>lmu<u>hs</u>an<u>aa</u>tu mina <strong>al</strong>nnis<u>aa</u>-i ill<u>aa</u> m<u>aa</u> malakat aym<u>aa</u>nukum kit<u>aa</u>ba <strong>al</strong>l<u>aa</u>hi \'alaykum wau<u>h</u>illa lakum m<u>aa</u> war<u>aa</u>-a <u>dzaa</u>likum an tabtaghuu bi-amw<u>aa</u>likum mu<u>hs</u>iniina ghayra mus<u>aa</u>fi<u>h</u>iina fam<u>aa</u> istamta\'tum bihi minhunna fa<u>aa</u>tuuhunna ujuurahunna farii<u>dh</u>atan wal<u>aa</u> jun<u>aah</u>a \'alaykum fiim<u>aa</u> tar<u>aad</u>aytum bihi min ba\'di <strong>a</strong>lfarii<u>dh</u>ati inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Dan (diharamkan juga kamu menikahi) perempuan yang bersuami, kecuali hamba sahaya perempuan (tawanan perang) yang kamu miliki sebagai ketetapan Allah atas kamu. Dan dihalalkan bagimu selain (perempuan-perempuan) yang demikian itu jika kamu berusaha dengan hartamu untuk menikahinya bukan untuk berzina. Maka karena kenikmatan yang telah kamu dapatkan dari mereka, berikanlah maskawinnya kepada mereka sebagai suatu kewajiban. Tetapi tidak mengapa jika ternyata di antara kamu telah saling merelakannya, setelah ditetapkan. Sungguh, Allah Maha Mengetahui, Mahabijaksana.'),
	(518, 4, 25, 'وَمَنْ لَّمْ يَسْتَطِعْ مِنْكُمْ طَوْلًا اَنْ يَّنْكِحَ الْمُحْصَنٰتِ الْمُؤْمِنٰتِ فَمِنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ فَتَيٰتِكُمُ الْمُؤْمِنٰتِۗ وَاللّٰهُ اَعْلَمُ بِاِيْمَانِكُمْ ۗ بَعْضُكُمْ مِّنْۢ بَعْضٍۚ فَانْكِحُوْهُنَّ بِاِذْنِ اَهْلِهِنَّ و', 'waman lam yasta<u>th</u>i\' minkum <u>th</u>awlan an yanki<u>h</u>a <strong>a</strong>lmu<u>hs</u>an<u>aa</u>ti <strong>a</strong>lmu/min<u>aa</u>ti famin m<u>aa</u> malakat aym<u>aa</u>nukum min fatay<u>aa</u>tikumu <strong>a</strong>lmu/min<u>aa</u>ti wa<strong>al</strong>l<u>aa</u>hu a\'lamu bi-iim<u>aa</u>nikum ba\'<u>dh</u>ukum min ba\'<u>dh</u>in fa<strong>i</strong>nki<u>h</u>uuhunna bi-i<u>dz</u>ni ahlihinna wa<u>aa</u>tuuhunna ujuurahunna bi<strong>a</strong>lma\'ruufi mu<u>hs</u>an<u>aa</u>tin ghayra mas<u>aa</u>fi<u>haa</u>tin wal<u>aa</u> muttakhi<u>dzaa</u>ti akhd<u>aa</u>nin fa-i<u>dzaa</u> u<u>hs</u>inna fa-in atayna bif<u>aah</u>isyatin fa\'alayhinna ni<u>sh</u>fu m<u>aa</u> \'al<u>aa</u> <strong>a</strong>lmu<u>hs</u>an<u>aa</u>ti mina <strong>a</strong>l\'a<u>dzaa</u>bi <u>dzaa</u>lika liman khasyiya <strong>a</strong>l\'anata minkum wa-an ta<u>sh</u>biruu khayrun lakum wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan barangsiapa di antara kamu tidak mempunyai biaya untuk menikahi perempuan merdeka yang beriman, maka (dihalalkan menikahi perempuan) yang beriman dari hamba sahaya yang kamu miliki. Allah mengetahui keimananmu. Sebagian dari kamu adalah dari sebagian yang lain (sama-sama keturunan Adam-Hawa), karena itu nikahilah mereka dengan izin tuannya dan berilah mereka maskawin yang pantas, karena mereka adalah perempuan-perempuan yang memelihara diri, bukan pezina dan bukan (pula) perempuan yang mengambil laki-laki lain sebagai piaraannya. Apabila mereka telah berumah tangga (bersuami), tetapi melakukan perbuatan keji (zina), maka (hukuman) bagi mereka setengah dari apa (hukuman) perempuan-perempuan merdeka (yang tidak bersuami). (Kebolehan menikahi hamba sahaya) itu, adalah bagi orang-orang yang takut terhadap kesulitan dalam menjaga diri (dari perbuatan zina). Tetapi jika kamu bersabar, itu lebih baik bagimu. Allah Maha Pengampun, Maha Penyayang.'),
	(519, 4, 26, 'يُرِيْدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِيْنَ مِنْ قَبْلِكُمْ وَيَتُوْبَ عَلَيْكُمْ ۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ', 'yuriidu <strong>al</strong>l<u>aa</u>hu liyubayyina lakum wayahdiyakum sunana <strong>al</strong>la<u>dz</u>iina min qablikum wayatuuba \'alaykum wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Allah hendak menerangkan (syariat-Nya) kepadamu, dan menunjukkan jalan-jalan (kehidupan) orang yang sebelum kamu (para nabi dan orang-orang saleh) dan Dia menerima tobatmu. Allah Maha Mengetahui, Mahabijaksana.'),
	(520, 4, 27, 'وَاللّٰهُ يُرِيْدُ اَنْ يَّتُوْبَ عَلَيْكُمْ ۗ وَيُرِيْدُ الَّذِيْنَ يَتَّبِعُوْنَ الشَّهَوٰتِ اَنْ تَمِيْلُوْا مَيْلًا عَظِيْمًا', 'wa<strong>al</strong>l<u>aa</u>hu yuriidu an yatuuba \'alaykum wayuriidu <strong>al</strong>la<u>dz</u>iina yattabi\'uuna <strong>al</strong>sysyahaw<u>aa</u>ti an tamiiluu maylan \'a<u>zh</u>iim<strong>an</strong>v', 'Dan Allah hendak menerima tobatmu, sedang orang-orang yang mengikuti keinginannya menghendaki agar kamu berpaling sejauh-jauhnya (dari kebenaran).'),
	(521, 4, 28, 'يُرِيْدُ اللّٰهُ اَنْ يُّخَفِّفَ عَنْكُمْ ۚ وَخُلِقَ الْاِنْسَانُ ضَعِيْفًا ', 'yuriidu <strong>al</strong>l<u>aa</u>hu an yukhaffifa \'ankum wakhuliqa <strong>a</strong>l-ins<u>aa</u>nu <u>dh</u>a\'iif<u>aa</u><strong>n</strong>', 'Allah hendak memberikan keringanan kepadamu, karena manusia diciptakan (bersifat) lemah.'),
	(522, 4, 29, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّآ اَنْ تَكُوْنَ تِجَارَةً عَنْ تَرَاضٍ مِّنْكُمْ ۗ وَلَا تَقْتُلُوْٓا اَنْفُسَكُمْ ۗ اِنَّ اللّٰهَ كَانَ بِكُمْ رَحِيْمًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ta/kuluu amw<u>aa</u>lakum baynakum bi<strong>a</strong>lb<u>aath</u>ili ill<u>aa</u> an takuuna tij<u>aa</u>ratan \'an tar<u>aad</u>in minkum wal<u>aa</u> taqtuluu anfusakum inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bikum ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu saling memakan harta sesamamu dengan jalan yang batil (tidak benar), kecuali dalam perdagangan yang berlaku atas dasar suka sama suka di antara kamu. Dan janganlah kamu membunuh dirimu. Sungguh, Allah Maha Penyayang kepadamu.'),
	(523, 4, 30, 'وَمَنْ يَّفْعَلْ ذٰلِكَ عُدْوَانًا وَّظُلْمًا فَسَوْفَ نُصْلِيْهِ نَارًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا ', 'waman yaf\'al <u>dzaa</u>lika \'udw<u>aa</u>nan wa<u>zh</u>ulman fasawfa nu<u>sh</u>liihi n<u>aa</u>ran wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<u>aa</u><strong>n</strong>', 'Dan barangsiapa berbuat demikian dengan cara melanggar hukum dan zalim, akan Kami masukkan dia ke dalam neraka. Yang demikian itu mudah bagi Allah.'),
	(524, 4, 31, 'اِنْ تَجْتَنِبُوْا كَبَاۤىِٕرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَنُدْخِلْكُمْ مُّدْخَلًا كَرِيْمًا ', 'in tajtanibuu kab<u>aa</u>-ira m<u>aa</u> tunhawna \'anhu nukaffir \'ankum sayyi-<u>aa</u>tikum wanudkhilkum mudkhalan kariim<u>aa</u><strong>n</strong>', 'Jika kamu menjauhi dosa-dosa besar di antara dosa-dosa yang dilarang mengerjakannya, niscaya Kami hapus kesalahan-kesalahanmu dan akan Kami masukkan kamu ke tempat yang mulia (surga).'),
	(525, 4, 32, 'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِهٖ بَعْضَكُمْ عَلٰى بَعْضٍ ۗ لِلرِّجَالِ نَصِيْبٌ مِّمَّا اكْتَسَبُوْا ۗ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا اكْتَسَبْنَ ۗوَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِهٖ ۗ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ', 'wal<u>aa</u> tatamannaw m<u>aa</u> fa<u>dhdh</u>ala <strong>al</strong>l<u>aa</u>hu bihi ba\'<u>dh</u>akum \'al<u>aa</u> ba\'<u>dh</u>in li<strong>l</strong>rrij<u>aa</u>li na<u>sh</u>iibun mimm<u>aa</u> iktasabuu wali<strong>l</strong>nnis<u>aa</u>-i na<u>sh</u>iibun mimm<u>aa</u> iktasabna wa<strong>i</strong>s-aluu <strong>al</strong>l<u>aa</u>ha min fa<u>dh</u>lihi inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bikulli syay-in \'aliim<u>aa</u><strong>n</strong>', 'Dan janganlah kamu iri hati terhadap karunia yang telah dilebihkan Allah kepada sebagian kamu atas sebagian yang lain. (Karena) bagi laki-laki ada bagian dari apa yang mereka usahakan, dan bagi perempuan (pun) ada bagian dari apa yang mereka usahakan. Mohonlah kepada Allah sebagian dari karunia-Nya. Sungguh, Allah Maha Mengetahui segala sesuatu.'),
	(526, 4, 33, 'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ ۗ وَالَّذِيْنَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوْهُمْ نَصِيْبَهُمْ ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا ࣖ ', 'walikullin ja\'aln<u>aa</u> maw<u>aa</u>liya mimm<u>aa</u> taraka <strong>a</strong>lw<u>aa</u>lid<u>aa</u>ni wa<strong>a</strong>l-aqrabuuna wa<strong>a</strong>lla<u>dz</u>iina \'aqadat aym<u>aa</u>nukum fa<u>aa</u>tuuhum na<u>sh</u>iibahum inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'al<u>aa</u> kulli syay-in syahiid<u>aa</u><strong>n</strong>', 'Dan untuk masing-masing (laki-laki dan perempuan) Kami telah menetapkan para ahli waris atas apa yang ditinggalkan oleh kedua orang tuanya dan karib kerabatnya. Dan orang-orang yang kamu telah bersumpah setia dengan mereka, maka berikanlah kepada mereka bagiannya. Sungguh, Allah Maha Menyaksikan segala sesuatu.'),
	(527, 4, 34, 'اَلرِّجَالُ قَوَّامُوْنَ عَلَى النِّسَاۤءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَّبِمَآ اَنْفَقُوْا مِنْ اَمْوَالِهِمْ ۗ فَالصّٰلِحٰتُ قٰنِتٰتٌ حٰفِظٰتٌ لِّلْغَيْبِ بِمَا حَفِظَ اللّٰهُ ۗوَالّٰتِيْ تَخَافُوْنَ نُشُوْزَهُنَّ فَعِظُوْهُنَّ وَاهْج', 'a<strong>l</strong>rrij<u>aa</u>lu qaww<u>aa</u>muuna \'al<u>aa</u> <strong>al</strong>nnis<u>aa</u>-i bim<u>aa</u> fa<u>dhdh</u>ala <strong>al</strong>l<u>aa</u>hu ba\'<u>dh</u>ahum \'al<u>aa</u> ba\'<u>dh</u>in wabim<u>aa</u> anfaquu min amw<u>aa</u>lihim fa<strong>al</strong><u>shshaa</u>li<u>haa</u>tu q<u>aa</u>nit<u>aa</u>tun <u>haa</u>fi<u>zhaa</u>tun lilghaybi bim<u>aa</u> <u>h</u>afi<u>zh</u>a <strong>al</strong>l<u>aa</u>hu wa<strong>a</strong>ll<u>aa</u>tii takh<u>aa</u>fuuna nusyuuzahunna fa\'i<u>zh</u>uuhunna wa<strong>u</strong>hjuruuhunna fii <strong>a</strong>lma<u>daa</u>ji\'i wa<strong>i</strong><u>dh</u>ribuuhunna fa-in a<u>th</u>a\'nakum fal<u>aa</u> tabghuu \'alayhinna sabiilan inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aliyyan kabiir<u>aa</u><strong>n</strong>v', 'Laki-laki (suami) itu pelindung bagi perempuan (istri), karena Allah telah melebihkan sebagian mereka (laki-laki) atas sebagian yang lain (perempuan), dan karena mereka (laki-laki) telah memberikan nafkah dari hartanya. Maka perempuan-perempuan yang saleh adalah mereka yang taat (kepada Allah) dan menjaga diri ketika (suaminya) tidak ada, karena Allah telah menjaga (mereka). Perempuan-perempuan yang kamu khawatirkan akan nusyuz, hendaklah kamu beri nasihat kepada mereka, tinggalkanlah mereka di tempat tidur (pisah ranjang), dan (kalau perlu) pukullah mereka. Tetapi jika mereka menaatimu, maka janganlah kamu mencari-cari alasan untuk menyusahkannya. Sungguh, Allah Mahatinggi, Mahabesar.'),
	(528, 4, 35, 'وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوْا حَكَمًا مِّنْ اَهْلِهٖ وَحَكَمًا مِّنْ اَهْلِهَا ۚ اِنْ يُّرِيْدَآ اِصْلَاحًا يُّوَفِّقِ اللّٰهُ بَيْنَهُمَا ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا خَبِيْرًا ', 'wa-in khiftum syiq<u>aa</u>qa baynihim<u>aa</u> fa<strong>i</strong>b\'atsuu <u>h</u>akaman min ahlihi wa<u>h</u>akaman min ahlih<u>aa</u> in yuriid<u>aa</u> i<u>sh</u>l<u>aah</u>an yuwaffiqi <strong>al</strong>l<u>aa</u>hu baynahum<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aliiman khabiir<u>aa</u><strong>n</strong>v', 'Dan jika kamu khawatir terjadi persengketaan antara keduanya, maka kirimlah seorang juru damai dari keluarga laki-laki dan seorang juru damai dari keluarga perempuan. Jika keduanya (juru damai itu) bermaksud mengadakan perbaikan, niscaya Allah memberi taufik kepada suami-istri itu. Sungguh, Allah Mahateliti, Maha Mengenal.'),
	(529, 4, 36, '۞ وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًا وَّبِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَالْجَارِ ذِى الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْۢبِ وَابْنِ السَّبِيْلِۙ وَمَا مَلَكَتْ اَيْمَانُكُم', 'wa<strong>u</strong>\'buduu <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> tusyrikuu bihi syay-an wabi<strong>a</strong>lw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan wabi<u>dz</u>ii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiini wa<strong>a</strong>lj<u>aa</u>ri <u>dz</u>ii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lj<u>aa</u>ri <strong>a</strong>ljunubi wa<strong>al</strong><u>ssah</u>ibi bi<strong>a</strong>ljanbi wa<strong>i</strong>bni <strong>al</strong>ssabiili wam<u>aa</u> malakat aym<u>aa</u>nukum inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu man k<u>aa</u>na mukht<u>aa</u>lan fakhuur<u>aa</u><strong>n</strong><strong>al</strong>la<u>dz</u>iina yabkhaluuna waya/muruuna <strong>al</strong>nn<u>aa</u>sa bi<strong>a</strong>lbukhli wayaktumuuna m<u>aa</u> <u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi wa-a\'tadn<u>aa</u> lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u><strong>n</strong>', 'Dan sembahlah Allah dan janganlah kamu mempersekutukan-Nya dengan sesuatu apa pun. Dan berbuat-baiklah kepada kedua orang tua, karib-kerabat, anak-anak yatim, orang-orang miskin, tetangga dekat dan tetangga jauh, teman sejawat, ibnu sabil dan hamba sahaya yang kamu miliki. Sungguh, Allah tidak menyukai orang yang sombong dan membanggakan diri,'),
	(530, 4, 37, ' ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُوْنَ مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًاۚ', '<strong>al</strong>la<u>dz</u>iina yabkhaluuna waya/muruuna <strong>al</strong>nn<u>aa</u>sa bi<strong>a</strong>lbukhli wayaktumuuna m<u>aa</u> <u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi wa-a\'tadn<u>aa</u> lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u><strong>n</strong>', '(yaitu) orang yang kikir, dan menyuruh orang lain berbuat kikir, dan menyembunyikan karunia yang telah diberikan Allah kepadanya. Kami telah menyediakan untuk orang-orang kafir azab yang menghinakan.'),
	(531, 4, 38, 'وَالَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ ۗ وَمَنْ يَّكُنِ الشَّيْطٰنُ لَهٗ قَرِيْنًا فَسَاۤءَ قَرِيْنًا ', 'wa<strong>a</strong>lla<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum ri-<u>aa</u>-a <strong>al</strong>nn<u>aa</u>si wal<u>aa</u> yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wal<u>aa</u> bi<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri waman yakuni <strong>al</strong>sysyay<u>th</u><u>aa</u>nu lahu qariinan fas<u>aa</u>-a qariin<u>aa</u><strong>n</strong>', 'Dan (juga) orang-orang yang menginfakkan hartanya karena ria dan kepada orang lain (ingin dilihat dan dipuji), dan orang-orang yang tidak beriman kepada Allah dan kepada hari kemudian. Barangsiapa menjadikan setan sebagai temannya, maka (ketahuilah) dia (setan itu) adalah teman yang sangat jahat.'),
	(532, 4, 39, 'وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوْا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوْا مِمَّا رَزَقَهُمُ اللّٰهُ ۗوَكَانَ اللّٰهُ بِهِمْ عَلِيْمًا ', 'wam<u>aatsaa</u> \'alayhim law <u>aa</u>manuu bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa-anfaquu mimm<u>aa</u> razaqahumu <strong>al</strong>l<u>aa</u>hu wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu bihim \'aliim<u>aa</u><strong>n</strong>inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> ya<u>zh</u>limu mitsq<u>aa</u>la <u>dz</u>arratin wa-in taku <u>h</u>asanatan yu<u>daa</u>\'ifh<u>aa</u> wayu/ti min ladunhu ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Dan apa (keberatan) bagi mereka jika mereka beriman kepada Allah dan hari kemudian dan menginfakkan sebagian rezeki yang telah diberikan Allah kepadanya? Dan Allah Maha Mengetahui keadaan mereka.'),
	(533, 4, 40, 'اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۚوَاِنْ تَكُ حَسَنَةً يُّضٰعِفْهَا وَيُؤْتِ مِنْ لَّدُنْهُ اَجْرًا عَظِيْمًا', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> ya<u>zh</u>limu mitsq<u>aa</u>la <u>dz</u>arratin wa-in taku <u>h</u>asanatan yu<u>daa</u>\'ifh<u>aa</u> wayu/ti min ladunhu ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Sungguh, Allah tidak akan menzalimi seseorang walaupun sebesar dzarrah, dan jika ada kebajikan (sekecil dzarrah), niscaya Allah akan melipatgandakannya dan memberikan pahala yang besar dari sisi-Nya.'),
	(534, 4, 41, 'فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍۢ بِشَهِيْدٍ وَّجِئْنَا بِكَ عَلٰى هٰٓؤُلَاۤءِ شَهِيْدًاۗ', 'fakayfa i<u>dzaa</u> ji/n<u>aa</u> min kulli ummatin bisyahiidin waji/n<u>aa</u> bika \'al<u>aa</u> h<u>aa</u>ul<u>aa</u>-i syahiid<u>aa</u><strong>n</strong>', 'Dan bagaimanakah (keadaan orang kafir nanti), jika Kami mendatangkan seorang saksi (Rasul) dari setiap umat dan Kami mendatangkan engkau (Muhammad) sebagai saksi atas mereka.'),
	(535, 4, 42, 'يَوْمَىِٕذٍ يَّوَدُّ الَّذِيْنَ كَفَرُوْا وَعَصَوُا الرَّسُوْلَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۗ وَلَا يَكْتُمُوْنَ اللّٰهَ حَدِيْثًا ࣖ', 'yawma-i<u>dz</u>in yawaddu <strong>al</strong>la<u>dz</u>iina kafaruu wa\'a<u>sh</u>awuu <strong>al</strong>rrasuula law tusaww<u>aa</u> bihimu <strong>a</strong>l-ar<u>dh</u>u wal<u>aa</u> yaktumuuna <strong>al</strong>l<u>aa</u>ha <u>h</u>adiits<u>aa</u><strong>n</strong>', 'Pada hari itu, orang yang kafir dan orang yang mendurhakai Rasul (Muhammad), berharap sekiranya mereka diratakan dengan tanah (dikubur atau hancur luluh menjadi tanah), padahal mereka tidak dapat menyembunyikan sesuatu kejadian apa pun dari Allah.'),
	(536, 4, 43, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوْا مَا تَقُوْلُوْنَ وَلَا جُنُبًا اِلَّا عَابِرِيْ سَبِيْلٍ حَتّٰى تَغْتَسِلُوْا ۗوَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> taqrabuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa-antum suk<u>aa</u>r<u>aa</u> <u>h</u>att<u>aa</u> ta\'lamuu m<u>aa</u> taquuluuna wal<u>aa</u> junuban ill<u>aa</u> \'<u>aa</u>birii sabiilin <u>h</u>att<u>aa</u> taghtasiluu wa-in kuntum mar<u>daa</u> aw \'al<u>aa</u> safarin aw j<u>aa</u>-a a<u>h</u>adun minkum mina <strong>a</strong>lgh<u>aa</u>-i<u>th</u>i aw l<u>aa</u>mastumu <strong>al</strong>nnis<u>aa</u>-a falam tajiduu m<u>aa</u>-an fatayammamuu <u>sh</u>a\'iidan <u>th</u>ayyiban fa<strong>i</strong>msa<u>h</u>uu biwujuuhikum wa-aydiikum inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'afuwwan ghafuur<u>aa</u><strong>n</strong>', 'Wahai orang yang beriman! Janganlah kamu mendekati salat ketika kamu dalam keadaan mabuk, sampai kamu sadar apa yang kamu ucapkan, dan jangan pula (kamu hampiri masjid ketika kamu) dalam keadaan junub kecuali sekedar melewati jalan saja, sebelum kamu mandi (mandi junub). Adapun jika kamu sakit atau sedang dalam perjalanan atau sehabis buang air atau kamu telah menyentuh perempuan, sedangkan kamu tidak mendapat air, maka bertayamumlah kamu dengan debu yang baik (suci); usaplah wajahmu dan tanganmu dengan (debu) itu. Sungguh, Allah Maha Pemaaf, Maha Pengampun.'),
	(537, 4, 44, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يَشْتَرُوْنَ الضَّلٰلَةَ وَيُرِيْدُوْنَ اَنْ تَضِلُّوا السَّبِيْلَۗ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu na<u>sh</u>iiban mina <strong>a</strong>lkit<u>aa</u>bi yasytaruuna <strong>al</strong><u>dhdh</u>al<u>aa</u>lata wayuriiduuna an ta<u>dh</u>illuu <strong>al</strong>ssabiil<strong>a</strong>', 'Tidakkah kamu memperhatikan orang yang telah diberi bagian Kitab (Taurat)? Mereka membeli kesesatan dan mereka menghendaki agar kamu tersesat (menyimpang) dari jalan (yang benar).'),
	(538, 4, 45, 'وَاللّٰهُ اَعْلَمُ بِاَعْدَاۤىِٕكُمْ ۗوَكَفٰى بِاللّٰهِ وَلِيًّا ۙوَّكَفٰى بِاللّٰهِ نَصِيْرًا ', 'wa<strong>al</strong>l<u>aa</u>hu a\'lamu bi-a\'d<u>aa</u>-ikum wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi waliyyan wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan Allah lebih mengetahui tentang musuh-musuhmu. Cukuplah Allah menjadi pelindung dan cukuplah Allah menjadi penolong (bagimu).'),
	(539, 4, 46, 'مِنَ الَّذِيْنَ هَادُوْا يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖ وَيَقُوْلُوْنَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَّرَاعِنَا لَيًّاۢ بِاَلْسِنَتِهِمْ وَطَعْنًا فِى الدِّيْنِۗ وَلَوْ اَنَّهُمْ قَالُوْا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْ', 'mina <strong>al</strong>la<u>dz</u>iina h<u>aa</u>duu yu<u>h</u>arrifuuna <strong>a</strong>lkalima \'an maw<u>aad</u>i\'ihi wayaquuluuna sami\'n<u>aa</u> wa\'a<u>sh</u>ayn<u>aa</u> wa<strong>i</strong>sma\' ghayra musma\'in war<u>aa</u>\'in<u>aa</u> layyan bi-alsinatihim wa<u>th</u>a\'nan fii <strong>al</strong>ddiini walaw annahum q<u>aa</u>luu sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> wa<strong>i</strong>sma\' wa<strong>u</strong>n<u>zh</u>urn<u>aa</u> lak<u>aa</u>na khayran lahum wa-aqwama wal<u>aa</u>kin la\'anahumu <strong>al</strong>l<u>aa</u>hu bikufrihim fal<u>aa</u> yu/minuuna ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', '(Yaitu) di antara orang Yahudi, yang mengubah perkataan dari tempat-tempatnya. Dan mereka berkata, “Kami mendengar, tetapi kami tidak mau menurutinya.” Dan (mereka mengatakan pula), “Dengarlah,” sedang (engkau Muhammad sebenarnya) tidak mendengar apa pun. Dan (mereka mengatakan), “Raa‘ina” dengan memutar-balikkan lidahnya dan mencela agama. Sekiranya mereka mengatakan, “Kami mendengar dan patuh, dan dengarlah, dan perhatikanlah kami,” tentulah itu lebih baik bagi mereka dan lebih tepat, tetapi Allah melaknat mereka, karena kekafiran mereka. Mereka tidak beriman kecuali sedikit sekali.'),
	(540, 4, 47, 'يٰٓاَيُّهَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ اٰمِنُوْا بِمَا نَزَّلْنَا مُصَدِّقًا لِّمَا مَعَكُمْ مِّنْ قَبْلِ اَنْ نَّطْمِسَ وُجُوْهًا فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَآ اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّآ اَصْحٰبَ السَّبْتِ ۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْل', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba <u>aa</u>minuu bim<u>aa</u> nazzaln<u>aa</u> mu<u>sh</u>addiqan lim<u>aa</u> ma\'akum min qabli an na<u>th</u>misa wujuuhan fanaruddah<u>aa</u> \'al<u>aa</u> adb<u>aa</u>rih<u>aa</u> aw nal\'anahum kam<u>aa</u> la\'ann<u>aa</u> a<u>sh</u>-<u>haa</u>ba <strong>al</strong>ssabti wak<u>aa</u>na amru <strong>al</strong>l<u>aa</u>hi maf\'uul<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang telah diberi Kitab! Berimanlah kamu kepada apa yang telah Kami turunkan (Al-Qur\'an) yang membenarkan Kitab yang ada pada kamu, sebelum Kami mengubah wajah-wajah(mu), lalu Kami putar ke belakang atau Kami laknat mereka sebagaimana Kami melaknat orang-orang (yang berbuat maksiat) pada hari Sabat (Sabtu). Dan ketetapan Allah pasti berlaku.'),
	(541, 4, 48, 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۚ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْمًا عَظِيْمًا ', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yaghfiru an yusyraka bihi wayaghfiru m<u>aa</u> duuna <u>dzaa</u>lika liman yasy<u>aa</u>u waman yusyrik bi<strong>al</strong>l<u>aa</u>hi faqadi iftar<u>aa</u> itsman \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Sesungguhnya Allah tidak akan mengampuni (dosa) karena mempersekutukan-Nya (syirik), dan Dia mengampuni apa (dosa) yang selain (syirik) itu bagi siapa yang Dia kehendaki. Barangsiapa mempersekutukan Allah, maka sungguh, dia telah berbuat dosa yang besar.'),
	(542, 4, 49, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ يُزَكُّوْنَ اَنْفُسَهُمْ ۗ بَلِ اللّٰهُ يُزَكِّيْ مَنْ يَّشَاۤءُ وَلَا يُظْلَمُوْنَ فَتِيْلًا ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina yuzakkuuna anfusahum bali <strong>al</strong>l<u>aa</u>hu yuzakkii man yasy<u>aa</u>u wal<u>aa</u> yu<u>zh</u>lamuuna fatiil<u>aa</u><strong>n</strong>', 'Tidakkah engkau memperhatikan orang-orang yang menganggap dirinya suci (orang Yahudi dan Nasrani)? Sebenarnya Allah menyucikan siapa yang Dia kehendaki dan mereka tidak dizalimi sedikit pun.'),
	(543, 4, 50, 'اُنْظُرْ كَيْفَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ  وَكَفٰى بِهٖٓ اِثْمًا مُّبِيْنًا ࣖ ', 'un<u>zh</u>ur kayfa yaftaruuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba wakaf<u>aa</u> bihi itsman mubiin<u>aa</u><strong>n</strong>', 'Perhatikanlah, betapa mereka mengada-adakan dusta terhadap Allah! Dan cukuplah perbuatan itu menjadi dosa yang nyata (bagi mereka).'),
	(544, 4, 51, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُؤْمِنُوْنَ بِالْجِبْتِ وَالطَّاغُوْتِ وَيَقُوْلُوْنَ لِلَّذِيْنَ كَفَرُوْا هٰٓؤُلَاۤءِ اَهْدٰى مِنَ الَّذِيْنَ اٰمَنُوْا سَبِيْلًا ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu na<u>sh</u>iiban mina <strong>a</strong>lkit<u>aa</u>bi yu/minuuna bi<strong>a</strong>ljibti wa<strong>al</strong><u>ththaa</u>ghuuti wayaquuluuna lilla<u>dz</u>iina kafaruu h<u>aa</u>ul<u>aa</u>-i ahd<u>aa</u> mina <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu sabiil<u>aa</u><strong>n</strong>', 'Tidakkah kamu memperhatikan orang-orang yang diberi bagian dari Kitab (Taurat)? Mereka percaya kepada Jibt dan thaghut, dan mengatakan kepada orang-orang kafir (musyrik Mekah), bahwa mereka itu lebih benar jalannya daripada orang-orang yang beriman.'),
	(545, 4, 52, 'اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ ۗوَمَنْ يَّلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ نَصِيْرًا ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina la\'anahumu <strong>al</strong>l<u>aa</u>hu waman yal\'ani <strong>al</strong>l<u>aa</u>hu falan tajida lahu na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Mereka itulah orang-orang yang dilaknat Allah. Dan barangsiapa dilaknat Allah, niscaya engkau tidak akan mendapatkan penolong baginya.'),
	(546, 4, 53, 'اَمْ لَهُمْ نَصِيْبٌ مِّنَ الْمُلْكِ فَاِذًا لَّا يُؤْتُوْنَ النَّاسَ نَقِيْرًاۙ', 'am lahum na<u>sh</u>iibun mina <strong>a</strong>lmulki fa-i<u>dz</u>an l<u>aa</u> yu/tuuna <strong>al</strong>nn<u>aa</u>sa naqiir<u>aa</u><strong>n</strong>', 'Ataukah mereka mempunyai bagian dari kerajaan (kekuasaan), meskipun mereka tidak akan memberikan sedikit pun  (kebajikan) kepada manusia,'),
	(547, 4, 54, 'اَمْ يَحْسُدُوْنَ النَّاسَ عَلٰى مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۚ فَقَدْ اٰتَيْنَآ اٰلَ اِبْرٰهِيْمَ الْكِتٰبَ وَالْحِكْمَةَ وَاٰتَيْنٰهُمْ مُّلْكًا عَظِيْمًا ', 'am ya<u>h</u>suduuna <strong>al</strong>nn<u>aa</u>sa \'al<u>aa</u> m<u>aa</u> <u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi faqad <u>aa</u>tayn<u>aa</u> <u>aa</u>la ibr<u>aa</u>hiima <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wa<u>aa</u>tayn<u>aa</u>hum mulkan \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'ataukah mereka dengki kepada manusia (Muhammad) karena karunia yang telah diberikan Allah kepadanya? Sungguh, Kami telah memberikan Kitab dan Hikmah kepada keluarga Ibrahim, dan Kami telah memberikan kepada mereka kerajaan (kekuasaan) yang besar.'),
	(548, 4, 55, 'فَمِنْهُمْ مَّنْ اٰمَنَ بِهٖ وَمِنْهُمْ مَّنْ صَدَّ عَنْهُ ۗ وَكَفٰى بِجَهَنَّمَ سَعِيْرًا', 'faminhum man <u>aa</u>mana bihi waminhum man <u>sh</u>adda \'anhu wakaf<u>aa</u> bijahannama sa\'iir<u>aa</u><strong>n</strong>', 'Maka di antara mereka (yang dengki itu), ada yang beriman kepadanya dan ada pula yang menghalangi (manusia beriman) kepadanya. Cukuplah (bagi mereka) neraka Jahanam yang menyala-nyala apinya.'),
	(549, 4, 56, 'اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا سَوْفَ نُصْلِيْهِمْ نَارًاۗ كُلَّمَا نَضِجَتْ جُلُوْدُهُمْ بَدَّلْنٰهُمْ جُلُوْدًا غَيْرَهَا لِيَذُوْقُوا الْعَذَابَۗ اِنَّ اللّٰهَ كَانَ عَزِيْزًا حَكِيْمًا ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> sawfa nu<u>sh</u>liihim n<u>aa</u>ran kullam<u>aa</u> na<u>dh</u>ijat juluuduhum baddaln<u>aa</u>hum juluudan ghayrah<u>aa</u> liya<u>dz</u>uuquu <strong>a</strong>l\'a<u>dzaa</u>ba inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aziizan <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Sungguh, orang-orang yang kafir kepada ayat-ayat Kami, kelak akan Kami masukkan ke dalam neraka. Setiap kali kulit mereka hangus, Kami ganti dengan kulit yang lain, agar mereka merasakan azab. Sungguh, Allah Maha-perkasa, Mahabijaksana.'),
	(550, 4, 57, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ  لَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ ۙ وَّنُدْخِلُهُمْ ظِلًّا ظَلِيْلًا ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti sanudkhiluhum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan lahum fiih<u>aa</u> azw<u>aa</u>jun mu<u>th</u>ahharatun wanudkhiluhum <u>zh</u>illan <u>zh</u>aliil<u>aa</u><strong>n</strong>', 'Adapun orang-orang yang beriman dan mengerjakan kebajikan, kelak akan Kami masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Di sana mereka mempunyai pasangan-pasangan yang suci, dan Kami masukkan mereka ke tempat yang teduh lagi nyaman.'),
	(551, 4, 58, '۞ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمٰنٰتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوْا بِالْعَدْلِ ۗ اِنَّ اللّٰهَ نِعِمَّا يَعِظُكُمْ بِهٖ ۗ اِنَّ اللّٰهَ كَانَ سَمِيْعًاۢ بَصِيْرًا', 'inna <strong>al</strong>l<u>aa</u>ha ya/murukum an tu-adduu <strong>a</strong>l-am<u>aa</u>n<u>aa</u>ti il<u>aa</u> ahlih<u>aa</u> wa-i<u>dzaa</u> <u>h</u>akamtum bayna <strong>al</strong>nn<u>aa</u>si an ta<u>h</u>kumuu bi<strong>a</strong>l\'adli inna <strong>al</strong>l<u>aa</u>ha ni\'imm<u>aa</u> ya\'i<u>zh</u>ukum bihi inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na samii\'an ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Sungguh, Allah menyuruhmu menyampaikan amanat kepada yang berhak menerimanya, dan apabila kamu menetapkan hukum di antara manusia hendaknya kamu menetapkannya dengan adil. Sungguh, Allah sebaik-baik yang memberi pengajaran kepadamu. Sungguh, Allah Maha Mendengar, Maha Melihat.'),
	(552, 4, 59, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاُولِى الْاَمْرِ مِنْكُمْۚ  فَاِنْ تَنَازَعْتُمْ فِيْ شَيْءٍ فَرُدُّوْهُ اِلَى اللّٰهِ وَالرَّسُوْلِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ ذٰلِكَ خَيْرٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uu <strong>al</strong>rrasuula waulii <strong>a</strong>l-amri minkum fa-in tan<u>aa</u>za\'tum fii syay-in farudduuhu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>rrasuuli in kuntum tu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri <u>dzaa</u>lika khayrun wa-a<u>h</u>sanu ta/wiil<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Taatilah Allah dan taatilah Rasul (Muhammad), dan Ulil Amri (pemegang kekuasaan) di antara kamu. Kemudian, jika kamu berbeda pendapat tentang sesuatu, maka kembalikanlah kepada Allah (Al-Qur\'an) dan Rasul (sunnahnya), jika kamu beriman kepada Allah dan hari kemudian. Yang demikian itu lebih utama (bagimu) dan lebih baik akibatnya.'),
	(553, 4, 60, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ يَزْعُمُوْنَ اَنَّهُمْ اٰمَنُوْا بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ يُرِيْدُوْنَ اَنْ يَّتَحَاكَمُوْٓا اِلَى الطَّاغُوْتِ وَقَدْ اُمِرُوْٓا اَنْ يَّكْفُرُوْا بِهٖ ۗوَيُرِيْدُ الشَّيْطٰنُ اَنْ يُّضِلَّهُمْ ض', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina yaz\'umuuna annahum <u>aa</u>manuu bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika yuriiduuna an yata<u>haa</u>kamuu il<u>aa</u> <strong>al</strong><u>ththaa</u>ghuuti waqad umiruu an yakfuruu bihi wayuriidu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu an yu<u>dh</u>illahum <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u><strong>n</strong>', 'Tidakkah engkau (Muhammad) memperhatikan orang-orang yang mengaku bahwa mereka telah beriman kepada apa yang diturunkan kepadamu dan kepada apa yang diturunkan sebelummu? Tetapi mereka masih menginginkan ketetapan hukum kepada thaghut, padahal mereka telah diperintahkan untuk mengingkari thaghut itu. Dan setan bermaksud menyesatkan mereka (dengan) kesesatan yang sejauh-jauhnya.'),
	(554, 4, 61, 'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ رَاَيْتَ الْمُنٰفِقِيْنَ يَصُدُّوْنَ عَنْكَ صُدُوْدًاۚ', 'wa-i<u>dzaa</u> qiila lahum ta\'<u>aa</u>law il<u>aa</u> m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu wa-il<u>aa</u> <strong>al</strong>rrasuuli ra-ayta <strong>a</strong>lmun<u>aa</u>fiqiina ya<u>sh</u>udduuna \'anka <u>sh</u>uduud<u>aa</u><strong>n</strong>', 'Dan apabila dikatakan kepada mereka, “Marilah (patuh) kepada apa yang telah diturunkan Allah dan (patuh) kepada Rasul,” (niscaya) engkau (Muhammad) melihat orang munafik menghalangi dengan keras darimu.'),
	(555, 4, 62, 'فَكَيْفَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ ثُمَّ جَاۤءُوْكَ يَحْلِفُوْنَ بِاللّٰهِ ۖاِنْ اَرَدْنَآ اِلَّآ اِحْسَانًا وَّتَوْفِيْقًا ', 'fakayfa i<u>dzaa</u> a<u>shaa</u>bat-hum mu<u>sh</u>iibatun bim<u>aa</u> qaddamat aydiihim tsumma j<u>aa</u>uuka ya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi in aradn<u>aa</u> ill<u>aa</u> i<u>h</u>s<u>aa</u>nan watawfiiq<u>aa</u><strong>n</strong>', 'Maka bagaimana halnya apabila (kelak) musibah menimpa mereka (orang munafik) disebabkan perbuatan tangannya sendiri, kemudian mereka datang kepadamu (Muhammad) sambil bersumpah, “Demi Allah, kami sekali-kali tidak menghendaki selain kebaikan dan kedamaian.”'),
	(556, 4, 63, 'اُولٰۤىِٕكَ الَّذِيْنَ يَعْلَمُ اللّٰهُ مَا فِيْ قُلُوْبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَّهُمْ فِيْٓ  اَنْفُسِهِمْ قَوْلًا ۢ بَلِيْغًا ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina ya\'lamu <strong>al</strong>l<u>aa</u>hu m<u>aa</u> fii quluubihim fa-a\'ri<u>dh</u> \'anhum wa\'i<u>zh</u>hum waqul lahum fii anfusihim qawlan baliigh<u>aa</u><strong>n</strong>', 'Mereka itu adalah orang-orang yang (sesungguhnya) Allah mengetahui apa yang ada di dalam hatinya. Karena itu berpalinglah kamu dari mereka, dan berilah mereka nasihat, dan katakanlah kepada mereka perkataan yang membekas pada jiwanya.'),
	(557, 4, 64, 'وَمَآ  اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِ ۗوَلَوْ اَنَّهُمْ اِذْ ظَّلَمُوْٓا اَنْفُسَهُمْ جَاۤءُوْكَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُوْلُ لَوَجَدُوا اللّٰهَ تَوَّابًا رَّحِيْمًا ', 'wam<u>aa</u> arsaln<u>aa</u> min rasuulin ill<u>aa</u> liyu<u>th</u><u>aa</u>\'a bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi walaw annahum i<u>dz</u> <u>zh</u>alamuu anfusahum j<u>aa</u>uuka fa<strong>i</strong>staghfaruu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>staghfara lahumu <strong>al</strong>rrasuulu lawajaduu <strong>al</strong>l<u>aa</u>ha taww<u>aa</u>ban ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dan Kami tidak mengutus seorang rasul melainkan untuk ditaati dengan izin Allah. Dan sungguh, sekiranya mereka setelah menzalimi dirinya datang kepadamu (Muhammad), lalu memohon ampunan kepada Allah, dan Rasul pun memohonkan ampunan untuk mereka, niscaya mereka mendapati Allah Maha Penerima tobat, Maha Penyayang.'),
	(558, 4, 65, 'فَلَا وَرَبِّكَ لَا يُؤْمِنُوْنَ حَتّٰى يُحَكِّمُوْكَ فِيْمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوْا فِيْٓ اَنْفُسِهِمْ حَرَجًا مِّمَّا قَضَيْتَ وَيُسَلِّمُوْا تَسْلِيْمًا ', 'fal<u>aa</u> warabbika l<u>aa</u> yu/minuuna <u>h</u>att<u>aa</u> yu<u>h</u>akkimuuka fiim<u>aa</u> syajara baynahum tsumma l<u>aa</u> yajiduu fii anfusihim <u>h</u>arajan mimm<u>aa</u> qa<u>dh</u>ayta wayusallimuu tasliim<u>aa</u><strong>n</strong>', 'Maka demi Tuhanmu, mereka tidak beriman sebelum mereka menjadikan engkau (Muhammad) sebagai hakim dalam perkara yang mereka perselisihkan, (sehingga) kemudian tidak ada rasa keberatan dalam hati mereka terhadap putusan yang engkau berikan, dan mereka menerima dengan sepenuhnya.'),
	(559, 4, 66, 'وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُوْٓا اَنْفُسَكُمْ اَوِ اخْرُجُوْا مِنْ دِيَارِكُمْ مَّا فَعَلُوْهُ اِلَّا قَلِيْلٌ مِّنْهُمْ ۗوَلَوْ اَنَّهُمْ فَعَلُوْا مَا يُوْعَظُوْنَ بِهٖ لَكَانَ خَيْرًا لَّهُمْ وَاَشَدَّ تَثْبِيْتًاۙ', 'walaw ann<u>aa</u> katabn<u>aa</u> \'alayhim ani uqtuluu anfusakum awi ukhrujuu min diy<u>aa</u>rikum m<u>aa</u> fa\'aluuhu ill<u>aa</u> qaliilun minhum walaw annahum fa\'aluu m<u>aa</u> yuu\'a<u>zh</u>uuna bihi lak<u>aa</u>na khayran lahum wa-asyadda tatsbiit<u>aa</u><strong>n</strong>', 'Dan sekalipun telah Kami perintahkan kepada mereka, “Bunuhlah dirimu atau keluarlah kamu dari kampung halamanmu,” ternyata mereka tidak akan melakukannya, kecuali sebagian kecil dari mereka. Dan sekiranya mereka benar-benar melaksanakan perintah yang diberikan, niscaya itu lebih baik bagi mereka dan lebih menguatkan (iman mereka),'),
	(560, 4, 67, 'وَّاِذًا لَّاٰ تَيْنٰهُمْ مِّنْ لَّدُنَّآ اَجْرًا عَظِيْمًاۙ', 'wa-i<u>dz</u>an la<u>aa</u>tayn<u>aa</u>hum min ladunn<u>aa</u> ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'dan dengan demikian, pasti Kami berikan kepada mereka pahala yang besar dari sisi Kami,'),
	(561, 4, 68, 'وَّلَهَدَيْنٰهُمْ صِرَاطًا مُّسْتَقِيْمًا ', 'walahadayn<u>aa</u>hum <u>sh</u>ir<u>aath</u>an mustaqiim<u>aa</u><strong>n</strong>', 'dan pasti Kami tunjukkan kepada mereka  jalan yang lurus.'),
	(562, 4, 69, 'وَمَنْ يُّطِعِ اللّٰهَ وَالرَّسُوْلَ فَاُولٰۤىِٕكَ مَعَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ وَالصِّدِّيْقِيْنَ وَالشُّهَدَاۤءِ وَالصّٰلِحِيْنَ ۚ وَحَسُنَ اُولٰۤىِٕكَ رَفِيْقًا ', 'waman yu<u>th</u>i\'i <strong>al</strong>l<u>aa</u>ha wa<strong>al</strong>rrasuula faul<u>aa</u>-ika ma\'a <strong>al</strong>la<u>dz</u>iina an\'ama <strong>al</strong>l<u>aa</u>hu \'alayhim mina <strong>al</strong>nnabiyyiina wa<strong>al</strong><u>shsh</u>iddiiqiina wa<strong>al</strong>sysyuhad<u>aa</u>-i wa<strong>al</strong><u>shshaa</u>li<u>h</u>iina wa<u>h</u>asuna ul<u>aa</u>-ika rafiiq<u>aa</u><strong>n</strong>', 'Dan barangsiapa menaati Allah dan Rasul (Muhammad), maka mereka itu akan bersama-sama dengan orang yang diberikan nikmat oleh Allah, (yaitu) para nabi, para pencinta kebenaran, orang-orang yang mati syahid dan orang-orang saleh. Mereka itulah teman yang sebaik-baiknya.'),
	(563, 4, 70, 'ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِ ۗوَكَفٰى بِاللّٰهِ عَلِيْمًا ࣖ', '<u>dzaa</u>lika <strong>a</strong>lfa<u>dh</u>lu mina <strong>al</strong>l<u>aa</u>hi wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi \'aliim<u>aa</u><strong>n</strong>', 'Yang demikian itu adalah karunia dari Allah, dan cukuplah Allah yang Maha Mengetahui.'),
	(564, 4, 71, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا خُذُوْا حِذْرَكُمْ فَانْفِرُوْا ثُبَاتٍ اَوِ انْفِرُوْا جَمِيْعًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu khu<u>dz</u>uu <u>h</u>i<u>dz</u>rakum fa<strong>i</strong>nfiruu tsub<u>aa</u>tin awi infiruu jamii\'<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Bersiapsiagalah kamu, dan majulah (ke medan pertempuran) secara berkelompok, atau majulah bersama-sama (serentak).'),
	(565, 4, 72, 'وَاِنَّ مِنْكُمْ لَمَنْ لَّيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُّصِيْبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَّعَهُمْ شَهِيْدًا ', 'wa-inna minkum laman layuba<u>ththh</u>i-anna fa-in a<u>shaa</u>batkum mu<u>sh</u>iibatun q<u>aa</u>la qad an\'ama <strong>al</strong>l<u>aa</u>hu \'alayya i<u>dz</u> lam akun ma\'ahum syahiid<u>aa</u><strong>n</strong>wala-in a<u>shaa</u>bakum fa<u>dh</u>lun mina <strong>al</strong>l<u>aa</u>hi layaquulanna ka-an lam takun baynakum wabaynahu mawaddatun y<u>aa</u> laytanii kuntu ma\'ahum fa-afuuza fawzan \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Dan sesungguhnya di antara kamu pasti ada orang yang sangat enggan (ke medan pertempuran). Lalu jika kamu ditimpa musibah dia berkata, “Sungguh, Allah telah memberikan nikmat kepadaku karena aku tidak ikut berperang bersama mereka.”'),
	(566, 4, 73, 'وَلَىِٕنْ اَصَابَكُمْ فَضْلٌ مِّنَ اللّٰهِ لَيَقُوْلَنَّ كَاَنْ لَّمْ تَكُنْۢ بَيْنَكُمْ وَبَيْنَهٗ مَوَدَّةٌ يّٰلَيْتَنِيْ كُنْتُ مَعَهُمْ فَاَفُوْزَ فَوْزًا عَظِيْمًا ', 'wala-in a<u>shaa</u>bakum fa<u>dh</u>lun mina <strong>al</strong>l<u>aa</u>hi layaquulanna ka-an lam takun baynakum wabaynahu mawaddatun y<u>aa</u> laytanii kuntu ma\'ahum fa-afuuza fawzan \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Dan sungguh, jika kamu mendapat karunia (kemenangan) dari Allah, tentulah dia mengatakan seakan-akan belum pernah ada hubungan kasih sayang antara kamu dengan dia, “Wahai, sekiranya aku bersama mereka, tentu aku akan memperoleh kemenangan yang agung (pula).”'),
	(567, 4, 74, '۞ فَلْيُقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يَشْرُوْنَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۗ وَمَنْ يُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ', 'falyuq<u>aa</u>til fii sabiili <strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>iina yasyruuna <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> bi<strong>a</strong>l-<u>aa</u>khirati waman yuq<u>aa</u>til fii sabiili <strong>al</strong>l<u>aa</u>hi fayuqtal aw yaghlib fasawfa nu/tiihi ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Karena itu, hendaklah orang-orang yang menjual kehidupan dunia untuk (kehidupan) akhirat berperang di jalan Allah. Dan barangsiapa berperang di jalan Allah, lalu gugur atau memperoleh kemenangan maka akan Kami berikan pahala yang besar kepadanya.'),
	(568, 4, 75, 'وَمَا لَكُمْ لَا تُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَالْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ الَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ وَلِيًّاۚ و', 'wam<u>aa</u> lakum l<u>aa</u> tuq<u>aa</u>tiluuna fii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmusta<u>dh</u>\'afiina mina <strong>al</strong>rrij<u>aa</u>li wa<strong>al</strong>nnis<u>aa</u>-i wa<strong>a</strong>lwild<u>aa</u>ni <strong>al</strong>la<u>dz</u>iina yaquuluuna rabban<u>aa</u> akhrijn<u>aa</u> min h<u>aadz</u>ihi <strong>a</strong>lqaryati <strong>al</strong><u>zhzhaa</u>limi ahluh<u>aa</u> wa<strong>i</strong>j\'al lan<u>aa</u> min ladunka waliyyan wa<strong>i</strong>j\'al lan<u>aa</u> min ladunka na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan mengapa kamu tidak mau berperang di jalan Allah dan (membela) orang yang lemah, baik laki-laki, perempuan maupun anak-anak yang berdoa, “Ya Tuhan kami,  keluarkanlah kami dari negeri ini (Mekah) yang penduduknya zalim. Berilah kami pelindung dari sisi-Mu, dan berilah kami penolong dari sisi-Mu.”'),
	(569, 4, 76, 'اَلَّذِيْنَ اٰمَنُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۚ وَالَّذِيْنَ كَفَرُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ الطَّاغُوْتِ فَقَاتِلُوْٓا  اَوْلِيَاۤءَ الشَّيْطٰنِ ۚ اِنَّ كَيْدَ الشَّيْطٰنِ كَانَ ضَعِيْفًا ۚ ࣖ ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu yuq<u>aa</u>tiluuna fii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lla<u>dz</u>iina kafaruu yuq<u>aa</u>tiluuna fii sabiili <strong>al</strong><u>ththaa</u>ghuuti faq<u>aa</u>tiluu awliy<u>aa</u>-a <strong>al</strong>sysyay<u>th</u><u>aa</u>ni inna kayda <strong>al</strong>sysyay<u>th</u><u>aa</u>ni k<u>aa</u>na <u>dh</u>a\'iif<u>aa</u><strong>n</strong>', 'Orang-orang yang beriman berperang di jalan Allah, dan orang-orang yang kafir berperang di jalan thaghut, maka perangilah kawan-kawan setan itu, (karena) sesungguhnya tipu daya setan itu lemah.'),
	(570, 4, 77, 'اَلَمْ تَرَ اِلَى الَّذِيْنَ قِيْلَ لَهُمْ كُفُّوْٓا اَيْدِيَكُمْ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَرِيْقٌ مِّنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةً ۚ وَقَالُوْا رَبَّنَ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina qiila lahum kuffuu aydiyakum wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu <strong>al</strong>zzak<u>aa</u>ta falamm<u>aa</u> kutiba \'alayhimu <strong>a</strong>lqit<u>aa</u>lu i<u>dzaa</u> fariiqun minhum yakhsyawna <strong>al</strong>nn<u>aa</u>sa kakhasyyati <strong>al</strong>l<u>aa</u>hi aw asyadda khasyyatan waq<u>aa</u>luu rabban<u>aa</u> lima katabta \'alayn<u>aa</u> <strong>a</strong>lqit<u>aa</u>la lawl<u>aa</u> akhkhartan<u>aa</u> il<u>aa</u> ajalin qariibin qul mat<u>aa</u>\'u <strong>al</strong>dduny<u>aa</u> qaliilun wa<strong>a</strong>l-<u>aa</u>khiratu khayrun limani ittaq<u>aa</u> wal<u>aa</u> tu<u>zh</u>lamuuna fatiil<u>aa</u><strong>n</strong>', 'Tidakkah engkau memperhatikan orang-orang yang dikatakan kepada mereka, ”Tahanlah tanganmu (dari berperang), laksanakanlah salat dan tunaikanlah zakat!” Ketika mereka diwajibkan berperang, tiba-tiba sebagian mereka (golongan munafik) takut kepada manusia (musuh), seperti takutnya kepada Allah, bahkan lebih takut (dari itu). Mereka berkata, “Ya Tuhan kami, mengapa Engkau wajibkan berperang kepada kami? Mengapa tidak Engkau tunda (kewajiban berperang) kepada kami beberapa waktu lagi?”  Katakanlah, “Kesenangan di dunia ini hanya sedikit dan akhirat itu lebih baik bagi orang-orang yang bertakwa (mendapat pahala turut berperang) dan kamu tidak akan dizalimi sedikit pun.”'),
	(571, 4, 78, 'اَيْنَمَا تَكُوْنُوْا يُدْرِكْكُّمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِيْ بُرُوْجٍ مُّشَيَّدَةٍ ۗ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِ اللّٰهِ ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِكَ ۗ قُلْ كُلٌّ مِّنْ عِنْدِ اللّٰهِ', 'aynam<u>aa</u> takuunuu yudrikkumu <strong>a</strong>lmawtu walaw kuntum fii buruujin musyayyadatin wa-in tu<u>sh</u>ibhum <u>h</u>asanatun yaquuluu h<u>aadz</u>ihi min \'indi <strong>al</strong>l<u>aa</u>hi wa-in tu<u>sh</u>ibhum sayyi-atun yaquuluu h<u>aadz</u>ihi min \'indika qul kullun min \'indi <strong>al</strong>l<u>aa</u>hi fam<u>aa</u>li h<u>aa</u>ul<u>aa</u>-i <strong>a</strong>lqawmi l<u>aa</u> yak<u>aa</u>duuna yafqahuuna <u>h</u>adiits<u>aa</u><strong>n</strong>', 'Di manapun kamu berada, kematian akan mendapatkan kamu, kendatipun kamu berada di dalam benteng yang tinggi dan kukuh. Jika mereka memperoleh kebaikan, mereka mengatakan, “Ini dari sisi Allah,” dan jika mereka ditimpa suatu keburukan, mereka mengatakan, “Ini dari engkau (Muham-mad).” Katakanlah, “Semuanya (datang) dari sisi Allah.” Maka mengapa orang-orang itu (orang-orang munafik) hampir-hampir tidak memahami pembicaraan (sedikit pun)?”'),
	(572, 4, 79, 'مَآ اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِ ۖ وَمَآ اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَّفْسِكَ ۗ وَاَرْسَلْنٰكَ لِلنَّاسِ رَسُوْلًا  ۗ وَكَفٰى بِاللّٰهِ شَهِيْدًا ', 'm<u>aa</u> a<u>shaa</u>baka min <u>h</u>asanatin famina <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> a<u>shaa</u>baka min sayyi-atin famin nafsika wa-arsaln<u>aa</u>ka li<strong>l</strong>nn<u>aa</u>si rasuulan wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi syahiidan', 'Kebajikan apa pun yang kamu peroleh, adalah dari sisi Allah, dan keburukan apa pun yang menimpamu, itu dari (kesalahan) dirimu sendiri. Kami mengutusmu (Muhammad) menjadi Rasul kepada (seluruh) manusia. Dan cukuplah Allah yang menjadi saksi.'),
	(573, 4, 80, 'مَنْ يُّطِعِ الرَّسُوْلَ فَقَدْ اَطَاعَ اللّٰهَ ۚ وَمَنْ تَوَلّٰى فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗ ', 'man yu<u>th</u>i\'i <strong>al</strong>rrasuula faqad a<u>th</u><u>aa</u>\'a <strong>al</strong>l<u>aa</u>ha waman tawall<u>aa</u> fam<u>aa</u> arsaln<u>aa</u>ka \'alayhim <u>h</u>afii<u>zhaa</u><strong>n</strong>', 'Barangsiapa menaati Rasul (Muhammad), maka sesungguhnya dia telah menaati Allah. Dan barangsiapa berpaling (dari ketaatan itu), maka (ketahuilah) Kami tidak mengutusmu (Muhammad) untuk menjadi pemelihara mereka.'),
	(574, 4, 81, 'وَيَقُوْلُوْنَ طَاعَةٌ  ۖ فَاِذَا بَرَزُوْا مِنْ عِنْدِكَ بَيَّتَ طَاۤىِٕفَةٌ مِّنْهُمْ غَيْرَ الَّذِيْ تَقُوْلُ ۗ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُوْنَ ۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا ', 'wayaquuluuna <u>th</u><u>aa</u>\'atun fa-i<u>dzaa</u> barazuu min \'indika bayyata <u>th</u><u>aa</u>-ifatun minhum ghayra <strong>al</strong>la<u>dz</u>ii taquulu wa<strong>al</strong>l<u>aa</u>hu yaktubu m<u>aa</u> yubayyituuna fa-a\'ri<u>dh</u> \'anhum watawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wakiil<u>aa</u><strong>n</strong>', 'Dan mereka (orang-orang munafik) mengatakan, “(Kewajiban kami hanyalah) taat.” Tetapi, apabila mereka telah pergi dari sisimu (Muhammad), sebagian dari mereka mengatur siasat di malam hari (mengambil keputusan) lain dari yang telah mereka katakan tadi. Allah mencatat siasat yang mereka atur di malam hari itu, maka berpalinglah dari mereka dan bertawakallah kepada Allah. Cukuplah Allah yang menjadi pelindung.'),
	(575, 4, 82, 'اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ ۗ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوْا فِيْهِ اخْتِلَافًا كَثِيْرًا ', 'afal<u>aa</u> yatadabbaruuna <strong>a</strong>lqur-<u>aa</u>na walaw k<u>aa</u>na min \'indi ghayri <strong>al</strong>l<u>aa</u>hi lawajaduu fiihi ikhtil<u>aa</u>fan katsiir<u>aa</u><strong>n</strong>', 'Maka tidakkah mereka menghayati (mendalami) Al-Qur\'an? Sekiranya (Al-Qur\'an) itu bukan dari Allah, pastilah mereka menemukan banyak hal yang bertentangan di dalamnya.'),
	(576, 4, 83, 'وَاِذَا جَاۤءَهُمْ اَمْرٌ مِّنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوْا بِهٖ ۗ وَلَوْ رَدُّوْهُ اِلَى الرَّسُوْلِ وَاِلٰٓى اُولِى الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِيْنَ يَسْتَنْۢبِطُوْنَهٗ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَاتَّبَ', 'wa-i<u>dzaa</u> j<u>aa</u>-ahum amrun mina <strong>a</strong>l-amni awi <strong>a</strong>lkhawfi a<u>dzaa</u>\'uu bihi walaw radduuhu il<u>aa</u> <strong>al</strong>rrasuuli wa-il<u>aa</u> ulii <strong>a</strong>l-amri minhum la\'alimahu <strong>al</strong>la<u>dz</u>iina yastanbi<u>th</u>uunahu minhum walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu la<strong>i</strong>ttaba\'tumu <strong>al</strong>sysyay<u>th</u><u>aa</u>na ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Dan apabila sampai kepada mereka suatu berita tentang keamanan ataupun ketakutan, mereka (langsung) menyiarkannya. (Padahal) apabila mereka menyerahkannya kepada Rasul dan Ulil Amri di antara mereka, tentulah orang-orang yang ingin mengetahui kebenarannya (akan dapat) mengetahuinya (secara resmi) dari mereka (Rasul dan Ulil Amri). Sekiranya bukan karena karunia dan rahmat Allah kepadamu, tentulah kamu mengikuti setan, kecuali sebagian kecil saja (di antara kamu).'),
	(577, 4, 84, 'فَقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِنِيْنَ ۚ عَسَى اللّٰهُ اَنْ يَّكُفَّ بَأْسَ الَّذِيْنَ كَفَرُوْا ۗوَاللّٰهُ اَشَدُّ بَأْسًا وَّاَشَدُّ تَنْكِيْلًا ', 'faq<u>aa</u>til fii sabiili <strong>al</strong>l<u>aa</u>hi l<u>aa</u> tukallafu ill<u>aa</u> nafsaka wa<u>h</u>arri<u>dh</u>i <strong>a</strong>lmu/miniina \'as<u>aa</u> <strong>al</strong>l<u>aa</u>hu an yakuffa ba/sa <strong>al</strong>la<u>dz</u>iina kafaruu wa<strong>al</strong>l<u>aa</u>hu asyaddu ba/san wa-asyaddu tankiil<u>aa</u><strong>n</strong>', 'Maka berperanglah engkau (Muhammad) di jalan Allah, engkau tidaklah dibebani melainkan atas dirimu sendiri. Kobarkanlah (semangat) orang-orang beriman (untuk berperang). Mudah-mudahan Allah menolak (mematahkan) serangan orang-orang yang kafir itu. Allah sangat besar kekuatan(-Nya) dan sangat keras siksaan(-Nya).'),
	(578, 4, 85, 'مَنْ يَّشْفَعْ شَفَاعَةً حَسَنَةً يَّكُنْ لَّهٗ نَصِيْبٌ مِّنْهَا ۚ وَمَنْ يَّشْفَعْ شَفَاعَةً سَيِّئَةً يَّكُنْ لَّهٗ كِفْلٌ مِّنْهَا ۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقِيْتًا ', 'man yasyfa\' syaf<u>aa</u>\'atan <u>h</u>asanatan yakun lahu na<u>sh</u>iibun minh<u>aa</u> waman yasyfa\' syaf<u>aa</u>\'atan sayyi-atan yakun lahu kiflun minh<u>aa</u> wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in muqiit<u>aa</u><strong>n</strong>', 'Barangsiapa memberi pertolongan dengan pertolongan yang baik, niscaya dia akan memperoleh bagian dari (pahala)nya. Dan barangsiapa memberi pertolongan dengan pertolongan yang buruk, niscaya dia akan memikul bagian dari (dosa)nya. Allah Mahakuasa atas segala sesuatu.'),
	(579, 4, 86, 'وَاِذَا حُيِّيْتُمْ بِتَحِيَّةٍ فَحَيُّوْا بِاَحْسَنَ مِنْهَآ اَوْ رُدُّوْهَا ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَسِيْبًا', 'wa-i<u>dzaa</u> <u>h</u>uyyiitum bita<u>h</u>iyyatin fa<u>h</u>ayyuu bi-a<u>h</u>sana minh<u>aa</u> aw rudduuh<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'al<u>aa</u> kulli syay-in <u>h</u>asiib<u>aa</u><strong>n</strong>', 'Dan apabila kamu dihormati dengan suatu (salam) penghormatan, maka balaslah penghormatan itu dengan yang lebih baik, atau balaslah (penghormatan itu, yang sepadan) dengannya. Sungguh, Allah memperhitungkan segala sesuatu.'),
	(580, 4, 87, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ  لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِ ۗ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَدِيْثًا ࣖ', '<strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa layajma\'annakum il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati l<u>aa</u> rayba fiihi waman a<u>sh</u>daqu mina <strong>al</strong>l<u>aa</u>hi <u>h</u>adiits<u>aa</u><strong>n</strong>', 'Allah, tidak ada tuhan selain Dia. Dia pasti akan mengumpulkan kamu pada hari Kiamat yang tidak diragukan terjadinya. Siapakah yang lebih benar perkataan(nya) daripada Allah?'),
	(581, 4, 88, '۞ فَمَا لَكُمْ فِى الْمُنٰفِقِيْنَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُوْا ۗ اَتُرِيْدُوْنَ اَنْ تَهْدُوْا مَنْ اَضَلَّ اللّٰهُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ', 'fam<u>aa</u> lakum fii <strong>a</strong>lmun<u>aa</u>fiqiina fi-atayni wa<strong>al</strong>l<u>aa</u>hu arkasahum bim<u>aa</u> kasabuu aturiiduuna an tahduu man a<u>dh</u>alla <strong>al</strong>l<u>aa</u>hu waman yu<u>dh</u>lili <strong>al</strong>l<u>aa</u>hu falan tajida lahu sabiil<u>aa</u><strong>n</strong>', 'Maka mengapa kamu (terpecah) menjadi dua golongan dalam (menghadapi) orang-orang munafik, padahal Allah telah mengembalikan mereka (kepada kekafiran), disebabkan usaha mereka sendiri? Apakah kamu bermaksud memberi petunjuk kepada orang yang telah dibiarkan sesat oleh Allah? Barangsiapa dibiarkan sesat oleh Allah, kamu tidak akan mendapatkan jalan (untuk memberi petunjuk) baginya.'),
	(582, 4, 89, 'وَدُّوْا لَوْ تَكْفُرُوْنَ كَمَا كَفَرُوْا فَتَكُوْنُوْنَ سَوَاۤءً فَلَا تَتَّخِذُوْا مِنْهُمْ اَوْلِيَاۤءَ حَتّٰى يُهَاجِرُوْا فِيْ سَبِيْلِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ وَجَدْتُّمُوْهُمْ ۖ وَلَا تَتَّخِذُوْا مِنْهُمْ و', 'wadduu law takfuruuna kam<u>aa</u> kafaruu fatakuunuuna saw<u>aa</u>-an fal<u>aa</u> tattakhi<u>dz</u>uu minhum awliy<u>aa</u>-a <u>h</u>att<u>aa</u> yuh<u>aa</u>jiruu fii sabiili <strong>al</strong>l<u>aa</u>hi fa-in tawallaw fakhu<u>dz</u>uuhum wa<strong>u</strong>qtuluuhum <u>h</u>aytsu wajadtumuuhum wal<u>aa</u> tattakhi<u>dz</u>uu minhum waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Mereka ingin agar kamu menjadi kafir sebagaimana mereka telah menjadi kafir, sehingga kamu menjadi sama (dengan mereka). Janganlah kamu jadikan dari antara mereka sebagai teman-teman(mu), sebelum mereka berpindah pada jalan Allah. Apabila mereka berpaling, maka tawanlah mereka dan bunuhlah mereka di mana pun mereka kamu temukan, dan janganlah kamu jadikan seorang pun di antara mereka sebagai teman setia dan penolong,'),
	(583, 4, 90, 'اِلَّا الَّذِيْنَ يَصِلُوْنَ اِلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ اَوْ جَاۤءُوْكُمْ حَصِرَتْ صُدُوْرُهُمْ اَنْ يُّقَاتِلُوْكُمْ اَوْ يُقَاتِلُوْا قَوْمَهُمْ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوْكُمْ ۚ فَاِنِ اعْتَزَلُو', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina ya<u>sh</u>iluuna il<u>aa</u> qawmin baynakum wabaynahum miits<u>aa</u>qun aw j<u>aa</u>uukum <u>h</u>a<u>sh</u>irat <u>sh</u>uduuruhum an yuq<u>aa</u>tiluukum aw yuq<u>aa</u>tiluu qawmahum walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu lasalla<u>th</u>ahum \'alaykum falaq<u>aa</u>taluukum fa-ini i\'tazaluukum falam yuq<u>aa</u>tiluukum wa-alqaw ilaykumu <strong>al</strong>ssalama fam<u>aa</u> ja\'ala <strong>al</strong>l<u>aa</u>hu lakum \'alayhim sabiil<u>aa</u><strong>n</strong>', 'kecuali orang-orang yang meminta perlindungan kepada suatu kaum, yang antara kamu dan kaum itu telah ada perjanjian (damai) atau orang yang datang kepadamu sedang hati mereka merasa keberatan untuk memerangi kamu atau memerangi kaumnya. Sekiranya Allah menghendaki, niscaya diberikan-Nya kekuasaan kepada mereka (dalam) menghadapi kamu, maka pastilah mereka memerangimu. Tetapi jika mereka membiarkan kamu, dan tidak memerangimu serta menawarkan perdamaian kepadamu (menyerah), maka Allah tidak memberi jalan bagimu (untuk menawan dan membunuh) mereka.'),
	(584, 4, 91, 'سَتَجِدُوْنَ اٰخَرِيْنَ يُرِيْدُوْنَ اَنْ يَّأْمَنُوْكُمْ وَيَأْمَنُوْا قَوْمَهُمْ ۗ كُلَّ مَا رُدُّوْٓا اِلَى الْفِتْنَةِ اُرْكِسُوْا فِيْهَا ۚ فَاِنْ لَّمْ يَعْتَزِلُوْكُمْ وَيُلْقُوْٓا اِلَيْكُمُ السَّلَمَ وَيَكُفُّوْٓا اَيْدِيَهُمْ فَخُذُوْهُمْ وَاقْت', 'satajiduuna <u>aa</u>khariina yuriiduuna an ya/manuukum waya/manuu qawmahum kulla m<u>aa</u> rudduu il<u>aa</u> <strong>a</strong>lfitnati urkisuu fiih<u>aa</u> fa-in lam ya\'taziluukum wayulquu ilaykumu <strong>al</strong>ssalama wayakuffuu aydiyahum fakhu<u>dz</u>uuhum wa<strong>u</strong>qtuluuhum <u>h</u>aytsu tsaqiftumuuhum waul<u>aa</u>-ikum ja\'aln<u>aa</u> lakum \'alayhim sul<u>th</u><u>aa</u>nan mubiin<u>aa</u><strong>n</strong>', 'Kelak akan kamu dapati (golongan-golongan) yang lain, yang menginginkan agar mereka hidup aman bersamamu dan aman (pula) bersama kaumnya. Setiap kali mereka diajak kembali kepada fitnah (syirik), mereka pun terjun ke dalamnya. Karena itu jika mereka tidak membiarkan kamu dan tidak mau menawarkan perdamaian kepadamu, serta tidak menahan tangan mereka (dari memerangimu), maka tawanlah mereka dan bunuhlah mereka di mana saja kamu temui, dan merekalah orang yang Kami berikan kepadamu alasan yang nyata (untuk memerangi, menawan dan membunuh) mereka.'),
	(585, 4, 92, 'وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَّقْتُلَ مُؤْمِنًا اِلَّا خَطَـًٔا ۚ وَمَنْ قَتَلَ مُؤْمِنًا خَطَـًٔا فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ وَّدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى  اَهْلِهٖٓ  اِلَّآ  اَنْ يَّصَّدَّقُوْا ۗ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَّكُمْ وَهُوَ م', 'wam<u>aa</u> k<u>aa</u>na limu/minin an yaqtula mu/minan ill<u>aa</u> kha<u>th</u>a-an waman qatala mu/minan kha<u>th</u>a-an fata<u>h</u>riiru raqabatin mu/minatin wadiyatun musallamatun il<u>aa</u> ahlihi ill<u>aa</u> an ya<u>shsh</u>addaquu fa-in k<u>aa</u>na min qawmin \'aduwwin lakum wahuwa mu/minun fata<u>h</u>riiru raqabatin mu/minatin wa-in k<u>aa</u>na min qawmin baynakum wabaynahum miits<u>aa</u>qun fadiyatun musallamatun il<u>aa</u> ahlihi wata<u>h</u>riiru raqabatin mu/minatin faman lam yajid fa<u>sh</u>iy<u>aa</u>mu syahrayni mutat<u>aa</u>bi\'ayni tawbatan mina <strong>al</strong>l<u>aa</u>hi wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Dan tidak patut bagi seorang yang beriman membunuh seorang yang beriman (yang lain), kecuali karena tersalah (tidak sengaja). Barangsiapa membunuh seorang yang beriman karena tersalah (hendaklah) dia memerdekakan seorang hamba sahaya yang beriman serta (membayar) tebusan yang diserahkan kepada keluarganya (si terbunuh itu), kecuali jika mereka (keluarga si terbunuh) membebaskan pembayaran. Jika dia (si terbunuh) dari kaum yang memusuhimu, padahal dia orang beriman, maka (hendaklah si pembunuh) memerdekakan hamba sahaya yang beriman. Dan jika dia (si terbunuh) dari kaum (kafir) yang ada perjanjian (damai) antara mereka dengan kamu, maka (hendaklah si pembunuh) membayar tebusan yang diserahkan kepada keluarganya (si terbunuh) serta memerdekakan hamba sahaya yang beriman. Barangsiapa tidak mendapatkan (hamba sahaya), maka hendaklah dia (si pembunuh) berpuasa dua bulan berturut-turut sebagai tobat kepada Allah. Dan Allah Maha Mengetahui, Mahabijaksana.'),
	(586, 4, 93, 'وَمَنْ يَّقْتُلْ مُؤْمِنًا مُّتَعَمِّدًا فَجَزَاۤؤُهٗ جَهَنَّمُ خَالِدًا فِيْهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهٗ وَاَعَدَّ لَهٗ عَذَابًا عَظِيْمًا ', 'waman yaqtul mu/minan muta\'ammidan fajaz<u>aa</u>uhu jahannamu kh<u>aa</u>lidan fiih<u>aa</u> wagha<u>dh</u>iba <strong>al</strong>l<u>aa</u>hu \'alayhi wala\'anahu wa-a\'adda lahu \'a<u>dzaa</u>ban \'a<u>zh</u>iim<u>aa</u><strong>n </strong>', 'Dan barangsiapa membunuh seorang yang beriman dengan sengaja, maka balasannya ialah neraka Jahanam, dia kekal di dalamnya. Allah murka kepadanya, dan melaknatnya serta menyediakan azab yang besar baginya.'),
	(587, 4, 94, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا ضَرَبْتُمْ فِيْ سَبِيْلِ اللّٰهِ فَتَبَيَّنُوْا وَلَا تَقُوْلُوْا لِمَنْ اَلْقٰىٓ اِلَيْكُمُ السَّلٰمَ لَسْتَ مُؤْمِنًاۚ تَبْتَغُوْنَ عَرَضَ الْحَيٰوةِ الدُّنْيَا ۖفَعِنْدَ اللّٰهِ مَغَانِمُ كَثِيْرَةٌ ۗ كَذٰلِكَ كُ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> <u>dh</u>arabtum fii sabiili <strong>al</strong>l<u>aa</u>hi fatabayyanuu wal<u>aa</u> taquuluu liman <strong>a</strong>lq<u>aa</u> ilaykumu <strong>al</strong>ssal<u>aa</u>ma lasta mu/minan tabtaghuuna \'ara<u>dh</u>a <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> fa\'inda <strong>al</strong>l<u>aa</u>hi magh<u>aa</u>nimu katsiiratun ka<u>dzaa</u>lika kuntum min qablu famanna <strong>al</strong>l<u>aa</u>hu \'alaykum fatabayyanuu inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Apabila kamu pergi (berperang) di jalan Allah, maka telitilah (carilah keterangan) dan janganlah kamu mengatakan kepada orang yang mengucapkan ”salam” kepadamu, ”Kamu bukan seorang yang beriman,” (lalu kamu membunuhnya), dengan maksud mencari harta benda kehidupan dunia, padahal di sisi Allah ada harta yang banyak. Begitu jugalah keadaan kamu dahulu, lalu Allah memberikan nikmat-Nya kepadamu, maka telitilah. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan.'),
	(588, 4, 95, 'لَا يَسْتَوِى الْقَاعِدُوْنَ مِنَ الْمُؤْمِنِيْنَ غَيْرُ اُولِى الضَّرَرِ وَالْمُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ  فَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقٰعِدِيْنَ دَرَجَةً ۗ وَكُلًّا وَّع', 'l<u>aa</u> yastawii <strong>a</strong>lq<u>aa</u>\'iduuna mina <strong>a</strong>lmu/miniina ghayru ulii <strong>al</strong><u>dhdh</u>arari wa<strong>a</strong>lmuj<u>aa</u>hiduuna fii sabiili <strong>al</strong>l<u>aa</u>hi bi-amw<u>aa</u>lihim wa-anfusihim fa<u>dhdh</u>ala <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmuj<u>aa</u>hidiina bi-amw<u>aa</u>lihim wa-anfusihim \'al<u>aa</u> <strong>a</strong>lq<u>aa</u>\'idiina darajatan wakullan wa\'ada <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l<u>h</u>usn<u>aa</u> wafa<u>dhdh</u>ala <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmuj<u>aa</u>hidiina \'al<u>aa</u> <strong>a</strong>lq<u>aa</u>\'idiina ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Tidaklah sama antara orang beriman yang duduk (yang tidak turut berperang) tanpa mempunyai uzur (halangan) dengan orang yang berjihad di jalan Allah dengan harta dan jiwanya. Allah melebihkan derajat orang-orang yang berjihad dengan harta dan jiwanya atas orang-orang yang duduk (tidak ikut berperang tanpa halangan). Kepada masing-masing, Allah menjanjikan (pahala) yang baik (surga) dan Allah melebihkan orang-orang yang berjihad atas orang yang duduk dengan pahala yang besar,'),
	(589, 4, 96, 'دَرَجٰتٍ مِّنْهُ وَمَغْفِرَةً وَّرَحْمَةً ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ', 'daraj<u>aa</u>tin minhu wamaghfiratan wara<u>h</u>matan wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong></strong>', '(yaitu) beberapa derajat daripada-Nya, serta ampunan dan rahmat. Allah Ma-ha Pengampun, Maha Penyayang.'),
	(590, 4, 97, 'اِنَّ الَّذِيْنَ تَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ قَالُوْا فِيْمَ كُنْتُمْ ۗ قَالُوْا كُنَّا مُسْتَضْعَفِيْنَ فِى الْاَرْضِۗ  قَالُوْٓا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوْا فِيْهَا ۗ فَاُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُ', 'inna <strong>al</strong>la<u>dz</u>iina tawaff<u>aa</u>humu almal<u>aa</u>-ikatu <u>zhaa</u>limii anfusihim q<u>aa</u>luu fiima kuntum q<u>aa</u>luu kunn<u>aa</u> musta<u>dh</u>\'afiina fii al-ar<u>dh</u>i q<u>aa</u>luu alam takun ar<u>dh</u>u <strong>al</strong>l<u>aa</u>hi w<u>aa</u>si\'atan fatuh<u>aa</u>jiruu fiih<u>aa</u> faul<u>aa</u>-ika ma/w<u>aa</u>hum jahannamu was<u>aa</u>-at ma<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang dicabut nyawanya oleh malaikat dalam keadaan menzalimi sendiri, mereka (para malaikat) bertanya, “Bagaimana kamu ini?” Mereka menjawab, “Kami orang-orang yang tertindas di bumi (Mekah).” Mereka (para malaikat) bertanya, “Bukankah bumi Allah itu luas, sehingga kamu dapat berhijrah (berpindah-pindah) di bumi itu?” Maka orang-orang itu tempatnya di neraka Jahanam, dan (Jahanam) itu seburuk-buruk tempat kembali,'),
	(591, 4, 98, 'اِلَّا الْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ لَا يَسْتَطِيْعُوْنَ حِيْلَةً وَّلَا يَهْتَدُوْنَ سَبِيْلًاۙ  ', 'ill<u>aa</u> <strong>a</strong>lmusta<u>dh</u>\'afiina mina <strong>al</strong>rrij<u>aa</u>li wa<strong>al</strong>nnis<u>aa</u>-i wa<strong>a</strong>lwild<u>aa</u>ni l<u>aa</u> yasta<u>th</u>ii\'uuna <u>h</u>iilatan wal<u>aa</u> yahtaduuna sabiil<u>aa</u><strong>n</strong>', 'kecuali mereka yang tertindas baik laki-laki atau perempuan dan anak-anak yang tidak berdaya dan tidak mengetahui jalan (untuk berhijrah),'),
	(592, 4, 99, 'فَاُولٰۤىِٕكَ عَسَى اللّٰهُ اَنْ يَّعْفُوَ عَنْهُمْ ۗ وَكَانَ اللّٰهُ عَفُوًّا غَفُوْرًا ', 'faul<u>aa</u>-ika \'as<u>aa</u> <strong>al</strong>l<u>aa</u>hu an ya\'fuwa \'anhum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'afuwwan ghafuur<u>aa</u><strong>n</strong>', 'maka mereka itu, mudah-mudahan Allah memaafkannya. Allah Maha Pemaaf, Maha Pengampun.'),
	(593, 4, 100, '۞ وَمَنْ يُّهَاجِرْ فِيْ سَبِيْلِ اللّٰهِ يَجِدْ فِى الْاَرْضِ مُرَاغَمًا كَثِيْرًا وَّسَعَةً ۗوَمَنْ يَّخْرُجْ مِنْۢ بَيْتِهٖ مُهَاجِرًا اِلَى اللّٰهِ وَرَسُوْلِهٖ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهٗ عَلَى اللّٰهِ ۗوَكَانَ اللّٰهُ غَفُوْرًا', 'waman yuh<u>aa</u>jir fii sabiili <strong>al</strong>l<u>aa</u>hi yajid fii <strong>a</strong>l-ar<u>dh</u>i mur<u>aa</u>ghaman katsiiran wasa\'atan waman yakhruj min baytihi muh<u>aa</u>jiran il<u>aa</u> <strong>al</strong>l<u>aa</u>hi warasuulihi tsumma yudrik-hu <strong>a</strong>lmawtu faqad waqa\'a ajruhu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dan barangsiapa berhijrah di jalan Allah, niscaya mereka akan mendapatkan di bumi ini tempat hijrah yang luas dan (rezeki) yang banyak. Barangsiapa keluar dari rumahnya dengan maksud berhijrah karena Allah dan Rasul-Nya, kemudian kematian menimpanya (sebelum sampai ke tempat yang dituju), maka sungguh, pahalanya telah ditetapkan di sisi Allah. Dan Allah Maha Pengampun, Maha Penyayang.'),
	(594, 4, 101, 'وَاِذَا ضَرَبْتُمْ فِى الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوْا مِنَ الصَّلٰوةِ ۖ اِنْ خِفْتُمْ اَنْ يَّفْتِنَكُمُ الَّذِيْنَ كَفَرُوْاۗ اِنَّ الْكٰفِرِيْنَ كَانُوْا لَكُمْ عَدُوًّا مُّبِيْنًا ', 'wa-i<u>dzaa</u> <u>dh</u>arabtum fii <strong>a</strong>l-ar<u>dh</u>i falaysa \'alaykum jun<u>aah</u>un an taq<u>sh</u>uruu mina <strong>al</strong><u>shsh</u>al<u>aa</u>ti in khiftum an yaftinakumu <strong>al</strong>la<u>dz</u>iina kafaruu inna <strong>a</strong>lk<u>aa</u>firiina k<u>aa</u>nuu lakum \'aduwwan mubiin<u>aa</u><strong>n</strong>', 'Dan apabila kamu bepergian di bumi, maka tidaklah berdosa kamu meng-qasar salat, jika kamu takut diserang orang kafir. Sesungguhnya orang kafir itu adalah musuh yang nyata bagimu.'),
	(595, 4, 102, 'وَاِذَا كُنْتَ فِيْهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَاۤىِٕفَةٌ مِّنْهُمْ مَّعَكَ وَلْيَأْخُذُوْٓا اَسْلِحَتَهُمْ ۗ فَاِذَا سَجَدُوْا فَلْيَكُوْنُوْا مِنْ وَّرَاۤىِٕكُمْۖ  وَلْتَأْتِ طَاۤىِٕفَةٌ اُخْرٰى لَمْ يُصَلُّوْا فَلْيُصَلُّوْا مَعَكَ وَ', 'wa-i<u>dzaa</u> kunta fiihim fa-aqamta lahumu <strong>al</strong><u>shsh</u>al<u>aa</u>ta faltaqum <u>th</u><u>aa</u>-ifatun minhum ma\'aka walya/khu<u>dz</u>uu asli<u>h</u>atahum fa-i<u>dzaa</u> sajaduu falyakuunuu min war<u>aa</u>-ikum walta/ti <u>th</u><u>aa</u>-ifatun ukhr<u>aa</u> lam yu<u>sh</u>alluu falyu<u>sh</u>alluu ma\'aka walya/khu<u>dz</u>uu <u>h</u>i<u>dz</u>rahum wa-asli<u>h</u>atahum wadda <strong>al</strong>la<u>dz</u>iina kafaruu law taghfuluuna \'an asli<u>h</u>atikum wa-amti\'atikum fayamiiluuna \'alaykum maylatan w<u>aah</u>idatan wal<u>aa</u> jun<u>aah</u>a \'alaykum in k<u>aa</u>na bikum a<u>dz</u>an min ma<u>th</u>arin aw kuntum mar<u>daa</u> an ta<u>dh</u>a\'uu asli<u>h</u>atakum wakhu<u>dz</u>uu <u>h</u>i<u>dz</u>rakum inna <strong>al</strong>l<u>aa</u>ha a\'adda lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u><strong>n</strong>', 'Dan apabila engkau (Muhammad) berada di tengah-tengah mereka (sahabatmu) lalu engkau hendak melaksanakan salat bersama-sama mereka, maka hendaklah segolongan dari mereka berdiri (salat) besertamu dan menyandang senjata mereka, kemudian apabila mereka (yang salat besertamu) sujud (telah menyempurnakan satu rakaat), maka hendaklah mereka pindah dari belakangmu (untuk menghadapi musuh) dan hendaklah datang golongan yang lain yang belum salat, lalu mereka salat denganmu, dan hendaklah mereka bersiap siaga dan menyandang senjata mereka. Orang-orang kafir ingin agar kamu lengah terhadap senjatamu dan harta bendamu, lalu mereka menyerbu kamu sekaligus. Dan tidak mengapa kamu meletakkan senjata-senjatamu, jika kamu mendapat suatu kesusahan karena hujan atau karena kamu sakit, dan bersiap siagalah kamu. Sungguh, Allah telah menyediakan azab yang menghinakan bagi orang-orang kafir itu.'),
	(596, 4, 103, 'فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِكُمْ ۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَقِيْمُوا الصَّلٰوةَ ۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِنِيْنَ كِتٰبًا مَّوْقُوْتًا ', 'fa-i<u>dzaa</u> qa<u>dh</u>aytumu <strong>al</strong><u>shsh</u>al<u>aa</u>ta fa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha qiy<u>aa</u>man waqu\'uudan wa\'al<u>aa</u> junuubikum fa-i<u>dzaa</u> i<u>th</u>ma/nantum fa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta inna <strong>al</strong><u>shsh</u>al<u>aa</u>ta k<u>aa</u>nat \'al<u>aa</u> <strong>a</strong>lmu/miniina kit<u>aa</u>ban mawquut<u>aa</u><strong>n</strong>', 'Selanjutnya, apabila kamu telah menyelesaikan salat(mu), ingatlah Allah ketika kamu berdiri, pada waktu duduk dan ketika berbaring. Kemudian, apabila kamu telah merasa aman, maka laksanakanlah salat itu (sebagaimana biasa). Sungguh, salat itu adalah kewajiban yang ditentukan waktunya atas orang-orang yang beriman.'),
	(597, 4, 104, 'وَلَا تَهِنُوْا فِى ابْتِغَاۤءِ الْقَوْمِ ۗ اِنْ تَكُوْنُوْا تَأْلَمُوْنَ فَاِنَّهُمْ يَأْلَمُوْنَ كَمَا تَأْلَمُوْنَ ۚوَتَرْجُوْنَ مِنَ اللّٰهِ مَا لَا يَرْجُوْنَ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ࣖ ', 'wal<u>aa</u> tahinuu fii ibtigh<u>aa</u>-i <strong>a</strong>lqawmi in takuunuu ta/lamuuna fa-innahum ya/lamuuna kam<u>aa</u> ta/lamuuna watarjuuna mina <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yarjuuna wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Dan janganlah kamu berhati lemah dalam mengejar mereka (musuhmu). Jika kamu menderita kesakitan, maka ketahuilah mereka pun menderita kesakitan (pula), sebagaimana kamu rasakan, sedang kamu masih dapat mengharapkan dari Allah apa yang tidak dapat mereka harapkan. Allah Maha Mengetahui, Mahabijaksana.'),
	(598, 4, 105, 'اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَآ اَرٰىكَ اللّٰهُ ۗوَلَا تَكُنْ لِّلْخَاۤىِٕنِيْنَ خَصِيْمًا ۙ', 'inn<u>aa</u> anzaln<u>aa</u> ilayka <strong>a</strong>lkit<u>aa</u>ba bi<strong>a</strong>l<u>h</u>aqqi lita<u>h</u>kuma bayna <strong>al</strong>nn<u>aa</u>si bim<u>aa</u> ar<u>aa</u>ka <strong>al</strong>l<u>aa</u>hu wal<u>aa</u> takun lilkh<u>aa</u>-iniina kha<u>sh</u>iim<u>aa</u><strong>n</strong>', 'Sungguh, Kami telah menurunkan Kitab (Al-Qur\'an) kepadamu (Muhammad) membawa kebenaran, agar engkau mengadili antara manusia dengan apa yang telah diajarkan Allah kepadamu, dan janganlah engkau menjadi penentang (orang yang tidak bersalah), karena (membela) orang yang berkhianat,'),
	(599, 4, 106, 'وَّاسْتَغْفِرِ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ', 'wa<strong>i</strong>staghfiri <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'dan mohonkanlah ampunan kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(600, 4, 107, 'وَلَا تُجَادِلْ عَنِ الَّذِيْنَ يَخْتَانُوْنَ اَنْفُسَهُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّانًا اَثِيْمًاۙ', 'wal<u>aa</u> tuj<u>aa</u>dil \'ani <strong>al</strong>la<u>dz</u>iina yakht<u>aa</u>nuuna anfusahum inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu man k<u>aa</u>na khaww<u>aa</u>nan atsiim<u>aa</u><strong>n</strong>', 'Dan janganlah kamu berdebat untuk (membela) orang-orang yang mengkhianati dirinya. Sungguh, Allah tidak menyukai orang-orang yang selalu berkhianat dan bergelimang dosa,'),
	(601, 4, 108, 'يَّسْتَخْفُوْنَ مِنَ النَّاسِ وَلَا يَسْتَخْفُوْنَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُوْنَ مَا لَا يَرْضٰى مِنَ الْقَوْلِ ۗ وَكَانَ اللّٰهُ بِمَا يَعْمَلُوْنَ مُحِيْطًا ', 'yastakhfuuna mina <strong>al</strong>nn<u>aa</u>si wal<u>aa</u> yastakhfuuna mina <strong>al</strong>l<u>aa</u>hi wahuwa ma\'ahum i<u>dz</u> yubayyituuna m<u>aa</u> l<u>aa</u> yar<u>daa</u> mina <strong>a</strong>lqawli wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u><u>aa</u><strong>n</strong>', 'mereka dapat bersembunyi dari manusia, tetapi mereka tidak dapat bersembunyi dari Allah, karena Allah beserta mereka, ketika pada suatu malam mereka menetapkan keputusan rahasia yang tidak diridai-Nya. Dan Allah Ma-ha Meliputi terhadap apa yang mereka kerjakan.'),
	(602, 4, 109, 'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ جَادَلْتُمْ عَنْهُمْ فِى الْحَيٰوةِ الدُّنْيَاۗ فَمَنْ يُّجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَّنْ يَّكُوْنُ عَلَيْهِمْ وَكِيْلًا ', 'h<u>aa</u>-antum h<u>aa</u>ul<u>aa</u>-i j<u>aa</u>daltum \'anhum fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> faman yuj<u>aa</u>dilu <strong>al</strong>l<u>aa</u>ha \'anhum yawma <strong>a</strong>lqiy<u>aa</u>mati am man yakuunu \'alayhim wakiil<u>aa</u><strong>n</strong>', 'Itulah kamu! Kamu berdebat untuk (membela) mereka dalam kehidupan dunia ini, tetapi siapa yang akan menentang Allah untuk (membela) mereka pada hari Kiamat? Atau siapakah yang menjadi pelindung mereka (terhadap azab Allah)?'),
	(603, 4, 110, 'وَمَنْ يَّعْمَلْ سُوْۤءًا اَوْ يَظْلِمْ نَفْسَهٗ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوْرًا رَّحِيْمًا ', 'waman ya\'mal suu-an aw ya<u>zh</u>lim nafsahu tsumma yastaghfiri <strong>al</strong>l<u>aa</u>ha yajidi <strong>al</strong>l<u>aa</u>ha ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>waman ya\'mal suu-an aw ya<u>zh</u>lim nafsahu tsumma yastaghfiri <strong>al</strong>l<u>aa</u>ha yajidi <strong>al</strong>l<u>aa</u>ha ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>v', 'Dan barangsiapa berbuat kejahatan dan menganiaya dirinya, kemudian dia memohon ampunan kepada Allah, niscaya dia akan mendapatkan Allah Maha Pengampun, Maha Penyayang.'),
	(604, 4, 111, 'وَمَنْ يَّكْسِبْ اِثْمًا فَاِنَّمَا يَكْسِبُهٗ عَلٰى نَفْسِهٖ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ', 'waman yaksib itsman fa-innam<u>aa</u> yaksibuhu \'al<u>aa</u> nafsihi wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Dan barangsiapa berbuat dosa, maka sesungguhnya dia mengerjakannya untuk (kesulitan) dirinya sendiri. Dan Allah Maha Mengetahui, Mahabijak-sana.'),
	(605, 4, 112, 'وَمَنْ يَّكْسِبْ خَطِيْۤـَٔةً اَوْ اِثْمًا ثُمَّ يَرْمِ بِهٖ بَرِيْۤـًٔا فَقَدِ احْتَمَلَ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ࣖ ', 'waman yaksib kha<u>th</u>ii-atan aw itsman tsumma yarmi bihi barii-an faqadi i<u>h</u>tamala buht<u>aa</u>nan wa-itsman mubiin<u>aa</u><strong>n</strong>', 'Dan barangsiapa berbuat kesalahan atau dosa, kemudian dia tuduhkan kepada orang yang tidak bersalah, maka sungguh, dia telah memikul suatu kebohongan dan dosa yang nyata.'),
	(606, 4, 113, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهٗ لَهَمَّتْ طَّاۤىِٕفَةٌ مِّنْهُمْ اَنْ يُّضِلُّوْكَۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَضُرُّوْنَكَ مِنْ شَيْءٍ ۗ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتٰبَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَع', 'walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alayka wara<u>h</u>matuhu lahammat <u>th</u><u>aa</u>-ifatun minhum an yu<u>dh</u>illuuka wam<u>aa</u> yu<u>dh</u>illuuna ill<u>aa</u> anfusahum wam<u>aa</u> ya<u>dh</u>urruunaka min syay-in wa-anzala <strong>al</strong>l<u>aa</u>hu \'alayka <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wa\'allamaka m<u>aa</u> lam takun ta\'lamu wak<u>aa</u>na fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alayka \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Dan kalau bukan karena karunia Allah dan rahmat-Nya kepadamu (Muhammad), tentulah segolongan dari mereka berkeinginan keras untuk menyesatkanmu. Tetapi mereka hanya menyesatkan dirinya sendiri, dan tidak membahayakanmu sedikit pun. Dan (juga karena) Allah telah menurunkan Kitab (Al-Qur\'an) dan Hikmah (Sunnah) kepadamu, dan telah mengajarkan kepadamu apa yang belum engkau ketahui. Karunia Allah yang dilimpahkan kepadamu itu sangat besar.'),
	(607, 4, 114, '۞ لَا خَيْرَ فِيْ كَثِيْرٍ مِّنْ نَّجْوٰىهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوْفٍ اَوْ اِصْلَاحٍۢ بَيْنَ النَّاسِۗ وَمَنْ يَّفْعَلْ ذٰلِكَ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ', 'l<u>aa</u> khayra fii katsiirin min najw<u>aa</u>hum ill<u>aa</u> man amara bi<u>sh</u>adaqatin aw ma\'ruufin aw i<u>sh</u>l<u>aah</u>in bayna <strong>al</strong>nn<u>aa</u>si waman yaf\'al <u>dzaa</u>lika ibtigh<u>aa</u>-a mar<u>daa</u>ti <strong>al</strong>l<u>aa</u>hi fasawfa nu/tiihi ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Tidak ada kebaikan dari banyak pembicaraan rahasia mereka, kecuali pembicaraan rahasia dari orang yang menyuruh (orang) bersedekah, atau berbuat kebaikan, atau mengadakan perdamaian di antara manusia. Barangsiapa berbuat demikian karena mencari keridaan Allah, maka kelak Kami akan memberinya pahala yang besar.'),
	(608, 4, 115, 'وَمَنْ يُّشَاقِقِ الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِعْ غَيْرَ سَبِيْلِ الْمُؤْمِنِيْنَ نُوَلِّهٖ مَا تَوَلّٰى وَنُصْلِهٖ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا ࣖ ', 'waman yusy<u>aa</u>qiqi <strong>al</strong>rrasuula min ba\'di m<u>aa</u> tabayyana lahu <strong>a</strong>lhud<u>aa</u> wayattabi\' ghayra sabiili <strong>a</strong>lmu/miniina nuwallihi m<u>aa</u> tawall<u>aa</u> wanu<u>sh</u>lihi jahannama was<u>aa</u>-at ma<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan barangsiapa menentang Rasul (Muhammad) setelah jelas kebenaran baginya, dan mengikuti jalan yang bukan jalan orang-orang mukmin, Kami biarkan dia dalam kesesatan yang telah dilakukannya itu dan akan Kami masukkan dia ke dalam neraka Jahanam, dan itu seburuk-buruk tempat kembali.'),
	(609, 4, 116, 'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا ', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yaghfiru an yusyraka bihi wayaghfiru m<u>aa</u> duuna <u>dzaa</u>lika liman yasy<u>aa</u>u waman yusyrik bi<strong>al</strong>l<u>aa</u>hi faqad <u>dh</u>alla <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u><strong>n</strong>', 'Allah tidak akan mengampuni dosa syirik (mempersekutukan Allah dengan sesuatu), dan Dia mengampuni dosa selain itu bagi siapa yang Dia kehendaki. Dan barangsiapa mempersekutukan (sesuatu) dengan Allah, maka sungguh, dia telah tersesat jauh sekali.'),
	(610, 4, 117, 'اِنْ يَّدْعُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اِنَاثًاۚ وَاِنْ يَّدْعُوْنَ اِلَّا شَيْطٰنًا مَّرِيْدًاۙ', 'in yad\'uuna min duunihi ill<u>aa</u> in<u>aa</u>tsan wa-in yad\'uuna ill<u>aa</u> syay<u>th</u><u>aa</u>nan mariid<u>aa</u><strong>n</strong>', 'Yang mereka sembah selain Allah itu tidak lain hanyalah (berhala), dan mereka tidak lain hanyalah menyembah setan yang durhaka,'),
	(611, 4, 118, 'لَّعَنَهُ اللّٰهُ ۘ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيْبًا مَّفْرُوْضًاۙ', 'la\'anahu <strong>al</strong>l<u>aa</u>hu waq<u>aa</u>la la-attakhi<u>dz</u>anna min \'ib<u>aa</u>dika na<u>sh</u>iiban mafruu<u>daa</u><strong>n</strong>', 'yang dilaknati Allah, dan (setan) itu mengatakan, “Aku pasti akan mengambil bagian tertentu dari hamba-hamba-Mu,'),
	(612, 4, 119, 'وَّلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِ ۚ وَمَنْ يَّتَّخِذِ الشَّيْطٰنَ وَلِيًّا مِّنْ دُوْنِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَانًا مُّبِيْنًا ', 'walau<u>dh</u>illannahum walaumanniyannahum wala<u>aa</u>murannahum falayubattikunna <u>aatsaa</u>na <strong>a</strong>l-an\'<u>aa</u>mi wala<u>aa</u>murannahum falayughayyirunna khalqa <strong>al</strong>l<u>aa</u>hi waman yattakhi<u>dz</u>i <strong>al</strong>sysyay<u>th</u><u>aa</u>na waliyyan min duuni <strong>al</strong>l<u>aa</u>hi faqad khasira khusr<u>aa</u>nan mubiin<u>aa</u><strong>n</strong>', 'dan pasti kusesatkan mereka, dan akan kubangkitkan angan-angan kosong pada mereka dan akan kusuruh mereka memotong telinga-telinga binatang ternak, (lalu mereka benar-benar memotongnya), dan akan aku suruh mereka mengubah ciptaan Allah, (lalu mereka benar-benar mengubahnya).” Barangsiapa menjadikan setan sebagai pelindung selain Allah, maka sungguh, dia menderita kerugian yang nyata.'),
	(613, 4, 120, 'يَعِدُهُمْ وَيُمَنِّيْهِمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا ', 'ya\'iduhum wayumanniihim wam<u>aa</u> ya\'iduhumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu ill<u>aa</u> ghuruur<u>aa</u><strong>n</strong>', '(Setan itu) memberikan janji-janji kepada mereka dan membangkitkan angan-angan kosong pada mereka, padahal setan itu hanya menjanjikan tipuan belaka kepada mereka.'),
	(614, 4, 121, 'اُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُۖ وَلَا يَجِدُوْنَ عَنْهَا مَحِيْصًا ', 'ul<u>aa</u>-ika ma/w<u>aa</u>hum jahannamu wal<u>aa</u> yajiduuna \'anh<u>aa</u> ma<u>h</u>ii<u>shaa</u><strong>n</strong>', 'Mereka (yang tertipu) itu tempatnya di neraka Jahanam dan mereka tidak akan mendapat tempat (lain untuk) lari darinya.'),
	(615, 4, 122, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ وَعْدَ اللّٰهِ حَقًّا ۗوَمَنْ اَصْدَقُ مِنَ اللّٰهِ قِيْلًا ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti sanudkhiluhum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqan waman a<u>sh</u>daqu mina <strong>al</strong>l<u>aa</u>hi qiil<u>aa</u><strong>n</strong>', 'Dan orang yang beriman dan mengerjakan amal kebajikan, kelak akan Kami masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Dan janji Allah itu benar. Siapakah yang lebih benar perkataannya daripada Allah?'),
	(616, 4, 123, 'لَيْسَ بِاَمَانِيِّكُمْ وَلَآ اَمَانِيِّ اَهْلِ الْكِتٰبِ ۗ مَنْ يَّعْمَلْ سُوْۤءًا يُّجْزَ بِهٖۙ وَلَا يَجِدْ لَهٗ مِنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا ', 'laysa bi-am<u>aa</u>niyyikum wal<u>aa</u> am<u>aa</u>niyyi ahli <strong>a</strong>lkit<u>aa</u>bi man ya\'mal suu-an yujza bihi wal<u>aa</u> yajid lahu min duuni <strong>al</strong>l<u>aa</u>hi waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', '(Pahala dari Allah) itu bukanlah angan-anganmu dan bukan (pula) angan-angan Ahli Kitab. Barangsiapa mengerjakan kejahatan, niscaya akan dibalas sesuai dengan kejahatan itu, dan dia tidak akan mendapatkan pelindung dan penolong selain Allah.'),
	(617, 4, 124, 'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ نَقِيْرًا ', 'waman ya\'mal mina <strong>al</strong><u>shshaa</u>li<u>haa</u>ti min <u>dz</u>akarin aw unts<u>aa</u> wahuwa mu/minun faul<u>aa</u>-ika yadkhuluuna <strong>a</strong>ljannata wal<u>aa</u> yu<u>zh</u>lamuuna naqiir<u>aa</u><strong>n</strong>', 'Dan barangsiapa mengerjakan amal kebajikan, baik laki-laki maupun perempuan sedang dia beriman, maka mereka itu akan masuk ke dalam surga dan mereka tidak dizalimi sedikit pun.'),
	(618, 4, 125, 'وَمَنْ اَحْسَنُ دِيْنًا مِّمَّنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ وَّاتَّبَعَ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَاتَّخَذَ اللّٰهُ اِبْرٰهِيْمَ خَلِيْلًا ', 'waman a<u>h</u>sanu diinan mimman aslama wajhahu lill<u>aa</u>hi wahuwa mu<u>h</u>sinun wa<strong>i</strong>ttaba\'a millata ibr<u>aa</u>hiima <u>h</u>aniifan wa<strong>i</strong>ttakha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu ibr<u>aa</u>hiima khaliil<u>aa</u><strong>n</strong>', 'Dan siapakah yang lebih baik agamanya daripada orang yang dengan ikhlas berserah diri kepada Allah, sedang dia mengerjakan kebaikan, dan mengikuti agama Ibrahim yang lurus? Dan Allah telah memilih Ibrahim menjadi kesayangan(-Nya).'),
	(619, 4, 126, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُّحِيْطًا ࣖ ', 'walill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu bikulli syay-in mu<u>h</u>ii<u>th</u><u>aa</u><strong>n</strong>', 'Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan (pengetahuan) Allah meliputi segala sesuatu.'),
	(620, 4, 127, 'وَيَسْتَفْتُوْنَكَ فِى النِّسَاۤءِۗ  قُلِ اللّٰهُ يُفْتِيْكُمْ فِيْهِنَّ ۙوَمَا يُتْلٰى عَلَيْكُمْ فِى الْكِتٰبِ فِيْ يَتٰمَى النِّسَاۤءِ الّٰتِيْ لَا تُؤْتُوْنَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُوْنَ اَنْ تَنْكِحُوْهُنَّ وَالْمُسْتَضْعَفِيْنَ مِنَ الْوِل', 'wayastaftuunaka fii <strong>al</strong>nnis<u>aa</u>-i quli <strong>al</strong>l<u>aa</u>hu yuftiikum fiihinna wam<u>aa</u> yutl<u>aa</u> \'alaykum fii <strong>a</strong>lkit<u>aa</u>bi fii yat<u>aa</u>m<u>aa</u> <strong>al</strong>nnis<u>aa</u>-i <strong>al</strong>l<u>aa</u>tii l<u>aa</u> tu/tuunahunna m<u>aa</u> kutiba lahunna watarghabuuna an tanki<u>h</u>uuhunna wa<strong>a</strong>lmusta<u>dh</u>\'afiina mina <strong>a</strong>lwild<u>aa</u>ni wa-an taquumuu lilyat<u>aa</u>m<u>aa</u> bi<strong>a</strong>lqis<u>th</u>i wam<u>aa</u> taf\'aluu min khayrin fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bihi \'aliim<u>aa</u><strong>n</strong>', 'Dan mereka meminta fatwa kepadamu tentang perempuan. Katakanlah, “Allah memberi fatwa kepadamu tentang mereka, dan apa yang dibacakan kepadamu dalam Al-Qur\'an (juga memfatwakan) tentang para perempuan yatim yang tidak kamu berikan sesuatu (maskawin) yang ditetapkan untuk mereka, sedang kamu ingin menikahi mereka dan (tentang) anak-anak yang masih dipandang lemah. Dan (Allah menyuruh kamu) agar mengurus anak-anak yatim secara adil. Dan kebajikan apa pun yang kamu kerjakan, sesungguhnya Allah Maha Mengetahui.”'),
	(621, 4, 128, 'وَاِنِ امْرَاَةٌ خَافَتْ مِنْۢ بَعْلِهَا نُشُوْزًا اَوْ اِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يُّصْلِحَا بَيْنَهُمَا صُلْحًا ۗوَالصُّلْحُ خَيْرٌ ۗوَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۗ وَاِنْ تُحْسِنُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُو', 'wa-ini imra-atun kh<u>aa</u>fat min ba\'lih<u>aa</u> nusyuuzan aw i\'r<u>aad</u>an fal<u>aa</u> jun<u>aah</u>a \'alayhim<u>aa</u> an yu<u>sh</u>li<u>haa</u> baynahum<u>aa</u> <u>sh</u>ul<u>h</u>an wa<strong>al</strong><u>shsh</u>ul<u>h</u>u khayrun wau<u>hd</u>irati <strong>a</strong>l-anfusu <strong>al</strong>sysyu<u>hh</u>a wa-in tu<u>h</u>sinuu watattaquu fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u><strong>n</strong>', 'Dan jika seorang perempuan khawatir suaminya akan nusyuz atau bersikap tidak acuh, maka keduanya dapat mengadakan perdamaian yang sebenarnya, dan perdamaian itu lebih baik (bagi mereka) walaupun manusia itu menurut tabiatnya kikir. Dan jika kamu memperbaiki (pergaulan dengan istrimu) dan memelihara dirimu (dari nusyuz dan sikap acuh tak acuh), maka sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan.'),
	(622, 4, 129, 'وَلَنْ تَسْتَطِيْعُوْٓا اَنْ تَعْدِلُوْا بَيْنَ النِّسَاۤءِ وَلَوْ حَرَصْتُمْ فَلَا تَمِيْلُوْا كُلَّ الْمَيْلِ فَتَذَرُوْهَا كَالْمُعَلَّقَةِ ۗوَاِنْ تُصْلِحُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا', 'walan tasta<u>th</u>ii\'uu an ta\'diluu bayna <strong>al</strong>nnis<u>aa</u>-i walaw <u>h</u>ara<u>sh</u>tum fal<u>aa</u> tamiiluu kulla <strong>a</strong>lmayli fata<u>dz</u>aruuh<u>aa</u> ka<strong>a</strong>lmu\'allaqati wa-in tu<u>sh</u>li<u>h</u>uu watattaquu fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dan kamu tidak akan dapat berlaku adil di antara istri-istri(mu), walaupun kamu sangat ingin berbuat demikian, karena itu janganlah kamu terlalu cenderung (kepada yang kamu cintai), sehingga kamu biarkan yang lain terkatung-katung. Dan jika kamu mengadakan perbaikan dan memelihara diri (dari kecurangan), maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(623, 4, 130, 'وَاِنْ يَّتَفَرَّقَا يُغْنِ اللّٰهُ كُلًّا مِّنْ سَعَتِهٖۗ وَكَانَ اللّٰهُ وَاسِعًا حَكِيْمًا ', 'wa-in yatafarraq<u>aa</u> yughni <strong>al</strong>l<u>aa</u>hu kullan min sa\'atihi wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'an <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Dan jika keduanya bercerai, maka Allah akan memberi kecukupan kepada masing-masing dari karunia-Nya. Dan Allah Mahaluas (karunia-Nya), Mahabijaksana.'),
	(624, 4, 131, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَلَقَدْ وَصَّيْنَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ غَنِيًّا ح', 'walill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i walaqad wa<u>shsh</u>ayn<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba min qablikum wa-iyy<u>aa</u>kum ani ittaquu <strong>al</strong>l<u>aa</u>ha wa-in takfuruu fa-inna lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghaniyyan <u>h</u>amiid<u>aa</u><strong>n</strong>', 'Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan sungguh, Kami telah memerintahkan kepada orang yang diberi kitab suci sebelum kamu dan (juga) kepadamu agar bertakwa kepada Allah. Tetapi jika kamu ingkar, maka (ketahuilah), milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan Allah Mahakaya, Maha Terpuji.'),
	(625, 4, 132, 'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ', 'walill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wakiil<u>aa</u><strong>n</strong>', 'Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Cukuplah Allah sebagai pemeliharanya.'),
	(626, 4, 133, 'اِنْ يَّشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَرِيْنَۗ  وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَدِيْرًا ', 'in yasya/ yu<u>dz</u>hibkum ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su waya/ti bi-<u>aa</u>khariina wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> <u>dzaa</u>lika qadiir<u>aa</u><strong>n</strong>', 'Kalau Allah menghendaki, niscaya dimusnahkan-Nya kamu semua wahai manusia! Kemudian Dia datangkan (umat) yang lain (sebagai penggantimu). Dan Allah Mahakuasa berbuat demikian.'),
	(627, 4, 134, 'مَنْ كَانَ يُرِيْدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِ ۗوَكَانَ اللّٰهُ سَمِيْعًاۢ بَصِيْرًا ࣖ ', 'man k<u>aa</u>na yuriidu tsaw<u>aa</u>ba <strong>al</strong>dduny<u>aa</u> fa\'inda <strong>al</strong>l<u>aa</u>hi tsaw<u>aa</u>bu <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu samii\'an ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Barangsiapa menghendaki pahala di dunia maka ketahuilah bahwa di sisi Allah ada pahala dunia dan akhirat. Dan Allah Maha Mendengar, Maha Melihat.'),
	(628, 4, 135, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ بِالْقِسْطِ شُهَدَاۤءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَبِيْنَ ۚ اِنْ يَّكُنْ غَنِيًّا اَوْ فَقِيْرًا فَاللّٰهُ اَوْلٰى بِهِمَاۗ فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu kuunuu qaww<u>aa</u>miina bi<strong>a</strong>lqis<u>th</u>i syuhad<u>aa</u>-a lill<u>aa</u>hi walaw \'al<u>aa</u> anfusikum awi <strong>a</strong>lw<u>aa</u>lidayni wa<strong>a</strong>l-aqrabiina in yakun ghaniyyan aw faqiiran fa<strong>al</strong>l<u>aa</u>hu awl<u>aa</u> bihim<u>aa</u> fal<u>aa</u> tattabi\'uu <strong>a</strong>lhaw<u>aa</u> an ta\'diluu wa-in talwuu aw tu\'ri<u>dh</u>uu fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Jadilah kamu penegak keadilan, menjadi saksi karena Allah, walaupun terhadap dirimu sendiri atau terhadap ibu bapak dan kaum kerabatmu. Jika dia (yang terdakwa) kaya ataupun miskin, maka Allah lebih tahu kemaslahatan (kebaikannya). Maka janganlah kamu mengikuti hawa nafsu karena ingin menyimpang dari kebenaran. Dan jika kamu memutarbalikkan (kata-kata) atau enggan menjadi saksi, maka ketahuilah Allah Mahateliti terhadap segala apa yang kamu kerjakan.'),
	(629, 4, 136, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْ نَزَّلَ عَلٰى رَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْٓ اَنْزَلَ مِنْ قَبْلُ ۗوَمَنْ يَّكْفُرْ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَل', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu <u>aa</u>minuu bi<strong>al</strong>l<u>aa</u>hi warasuulihi wa<strong>a</strong>lkit<u>aa</u>bi <strong>al</strong>la<u>dz</u>ii nazzala \'al<u>aa</u> rasuulihi wa<strong>a</strong>lkit<u>aa</u>bi <strong>al</strong>la<u>dz</u>ii anzala min qablu waman yakfur bi<strong>al</strong>l<u>aa</u>hi wamal<u>aa</u>-ikatihi wakutubihi warusulihi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri faqad <u>dh</u>alla <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Tetaplah beriman kepada Allah dan Rasul-Nya (Muhammad) dan kepada Kitab (Al-Qur\'an) yang diturunkan kepada Rasul-Nya, serta kitab yang diturunkan sebelumnya. Barangsiapa ingkar kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya, rasul-rasul-Nya, dan hari kemudian, maka sungguh, orang itu telah tersesat sangat jauh.'),
	(630, 4, 137, 'اِنَّ الَّذِيْنَ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ ازْدَادُوْا كُفْرًا لَّمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيْلًاۗ  ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu tsumma kafaruu tsumma <u>aa</u>manuu tsumma kafaruu tsumma izd<u>aa</u>duu kufran lam yakuni <strong>al</strong>l<u>aa</u>hu liyaghfira lahum wal<u>aa</u> liyahdiyahum sabiil<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang beriman lalu kafir, kemudian beriman (lagi), kemudian kafir lagi, lalu bertambah kekafirannya, maka Allah tidak akan mengampuni mereka, dan tidak (pula) menunjukkan kepada mereka jalan (yang lurus).'),
	(631, 4, 138, 'بَشِّرِ الْمُنٰفِقِيْنَ بِاَنَّ لَهُمْ عَذَابًا اَلِيْمًاۙ', 'basysyiri <strong>a</strong>lmun<u>aa</u>fiqiina bi-anna lahum \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'Kabarkanlah kepada orang-orang munafik bahwa mereka akan mendapat siksaan yang pedih,'),
	(632, 4, 139, ' ۨالَّذِيْنَ يَتَّخِذُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۗ اَيَبْتَغُوْنَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ', '<strong>al</strong>la<u>dz</u>iina yattakhi<u>dz</u>uuna <strong>a</strong>lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni <strong>a</strong>lmu/miniina ayabtaghuuna \'indahumu <strong>a</strong>l\'izzata fa-inna <strong>a</strong>l\'izzata lill<u>aa</u>hi jamii\'<u>aa</u><strong>n</strong>', '(yaitu) orang-orang yang menjadikan orang-orang kafir sebagai pemimpin dengan meninggalkan orang-orang mukmin. Apakah mereka mencari kekuatan di sisi orang kafir itu? Ketahuilah bahwa semua kekuatan itu milik Allah.'),
	(633, 4, 140, 'وَقَدْ نَزَّلَ عَلَيْكُمْ فِى الْكِتٰبِ اَنْ اِذَا سَمِعْتُمْ اٰيٰتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوْا مَعَهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖٓ  ۖ اِنَّكُمْ اِذًا مِّثْلُهُمْ ۗ اِنَّ اللّٰهَ جَامِعُ الْمُنٰفِقِيْنَ و', 'waqad nazzala \'alaykum fii <strong>a</strong>lkit<u>aa</u>bi an i<u>dzaa</u> sami\'tum <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi yukfaru bih<u>aa</u> wayustahzau bih<u>aa</u> fal<u>aa</u> taq\'uduu ma\'ahum <u>h</u>att<u>aa</u> yakhuu<u>dh</u>uu fii <u>h</u>adiitsin ghayrihi innakum i<u>dz</u>an mitsluhum inna <strong>al</strong>l<u>aa</u>ha j<u>aa</u>mi\'u <strong>a</strong>lmun<u>aa</u>fiqiina wa<strong>a</strong>lk<u>aa</u>firiina fii jahannama jamii\'<u>aa</u><strong>n</strong>', 'Dan sungguh, Allah telah menurunkan (ketentuan) bagimu di dalam Kitab (Al-Qur\'an) bahwa apabila kamu mendengar ayat-ayat Allah diingkari dan diperolok-olokkan (oleh orang-orang kafir), maka janganlah kamu duduk bersama mereka, sebelum mereka memasuki pembicaraan yang lain. Karena (kalau tetap duduk dengan mereka), tentulah kamu serupa dengan mereka. Sungguh, Allah akan mengumpulkan semua orang-orang munafik dan orang-orang kafir di neraka Jahanam,'),
	(634, 4, 141, ' ۨالَّذِيْنَ يَتَرَبَّصُوْنَ بِكُمْۗ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِّنَ اللّٰهِ قَالُوْٓا اَلَمْ نَكُنْ مَّعَكُمْ ۖ وَاِنْ كَانَ لِلْكٰفِرِيْنَ نَصِيْبٌ قَالُوْٓا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِّنَ الْمُؤْمِنِيْنَ ۗ فَاللّٰهُ يَحْكُمُ بَيْن', '<strong>al</strong>la<u>dz</u>iina yatarabba<u>sh</u>uuna bikum fa-in k<u>aa</u>na lakum fat<u>h</u>un mina <strong>al</strong>l<u>aa</u>hi q<u>aa</u>luu alam nakun ma\'akum wa-in k<u>aa</u>na lilk<u>aa</u>firiina na<u>sh</u>iibun q<u>aa</u>luu alam nasta<u>h</u>wi<u>dz</u> \'alaykum wanamna\'kum mina almu/miniina fa<strong>al</strong>l<u>aa</u>hu ya<u>h</u>kumu baynakum yawma alqiy<u>aa</u>mati walan yaj\'ala <strong>al</strong>l<u>aa</u>hu lilk<u>aa</u>firiina \'al<u>aa</u> almu/miniina sabiil<u>aa</u><strong>n</strong>', '(yaitu) orang yang menunggu-nunggu (peristiwa) yang akan terjadi pada dirimu. Apabila kamu mendapat kemenangan dari Allah mereka berkata, “Bukankah kami (turut berperang) bersama kamu?” Dan jika orang kafir mendapat bagian, mereka berkata, “Bukankah kami turut memenangkanmu, dan membela kamu dari orang mukmin?” Maka Allah akan memberi keputusan di antara kamu pada hari Kiamat. Allah tidak akan memberi jalan kepada orang kafir untuk mengalahkan orang-orang beriman.'),
	(635, 4, 142, 'اِنَّ الْمُنٰفِقِيْنَ يُخٰدِعُوْنَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُوْٓا اِلَى الصَّلٰوةِ قَامُوْا كُسَالٰىۙ يُرَاۤءُوْنَ النَّاسَ وَلَا يَذْكُرُوْنَ اللّٰهَ اِلَّا قَلِيْلًاۖ  ', 'inna <strong>a</strong>lmun<u>aa</u>fiqiina yukh<u>aa</u>di\'uuna <strong>al</strong>l<u>aa</u>ha wahuwa kh<u>aa</u>di\'uhum wa-i<u>dzaa</u> q<u>aa</u>muu il<u>aa</u> <strong>al</strong><u>shsh</u>al<u>aa</u>ti q<u>aa</u>muu kus<u>aa</u>l<u>aa</u> yur<u>aa</u>uuna <strong>al</strong>nn<u>aa</u>sa wal<u>aa</u> ya<u>dz</u>kuruuna <strong>al</strong>l<u>aa</u>ha ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Sesungguhnya orang munafik itu hendak menipu Allah, tetapi Allah-lah yang menipu mereka. Apabila mereka berdiri untuk salat, mereka lakukan dengan malas. Mereka bermaksud ria (ingin dipuji) di hadapan manusia. Dan mereka tidak mengingat Allah kecuali sedikit sekali.'),
	(636, 4, 143, 'مُّذَبْذَبِيْنَ بَيْنَ ذٰلِكَۖ  لَآ اِلٰى هٰٓؤُلَاۤءِ وَلَآ اِلٰى هٰٓؤُلَاۤءِ ۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ', 'mu<u>dz</u>ab<u>dz</u>abiina bayna <u>dzaa</u>lika l<u>aa</u> il<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wal<u>aa</u> il<u>aa</u> h<u>aa</u>ul<u>aa</u>-i waman yu<u>dh</u>lili <strong>al</strong>l<u>aa</u>hu falan tajida lahu sabiil<u>aa</u><strong>n</strong>', 'Mereka dalam keadaan ragu antara yang demikian (iman atau kafir), tidak termasuk kepada golongan ini (orang beriman) dan tidak (pula) kepada golongan itu (orang kafir). Barangsiapa dibiarkan sesat oleh Allah, maka kamu tidak akan mendapatkan jalan (untuk memberi petunjuk) baginya.'),
	(637, 4, 144, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۚ اَتُرِيْدُوْنَ اَنْ تَجْعَلُوْا لِلّٰهِ عَلَيْكُمْ سُلْطٰنًا مُّبِيْنًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu <strong>a</strong>lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni <strong>a</strong>lmu/miniina aturiiduuna an taj\'aluu lill<u>aa</u>hi \'alaykum sul<u>th</u><u>aa</u>nan mubiin<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu menjadikan orang-orang kafir sebagai pemimpin selain dari orang-orang mukmin. Apakah kamu ingin memberi alasan yang jelas bagi Allah (untuk menghukummu)?'),
	(638, 4, 145, 'اِنَّ الْمُنٰفِقِيْنَ فِى الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَصِيْرًاۙ', 'inna <strong>a</strong>lmun<u>aa</u>fiqiina fii <strong>al</strong>ddarki <strong>a</strong>l-asfali mina <strong>al</strong>nn<u>aa</u>ri walan tajida lahum na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Sungguh, orang-orang munafik itu (ditempatkan) pada tingkatan yang paling bawah dari neraka. Dan kamu tidak akan mendapat seorang penolong pun bagi mereka.'),
	(639, 4, 146, 'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَاعْتَصَمُوْا بِاللّٰهِ وَاَخْلَصُوْا دِيْنَهُمْ لِلّٰهِ فَاُولٰۤىِٕكَ مَعَ الْمُؤْمِنِيْنَۗ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِنِيْنَ اَجْرًا عَظِيْمًا ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina t<u>aa</u>buu wa-a<u>sh</u>la<u>h</u>uu wa<strong>i</strong>\'ta<u>sh</u>amuu bi<strong>al</strong>l<u>aa</u>hi wa-akhla<u>sh</u>uu diinahum lill<u>aa</u>hi faul<u>aa</u>-ika ma\'a <strong>a</strong>lmu/miniina wasawfa yu/ti <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmu/miniina ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Kecuali orang-orang yang bertobat dan memperbaiki diri dan berpegang teguh pada (agama) Allah dan dengan tulus ikhlas (menjalankan) agama mereka karena Allah. Maka mereka itu bersama-sama orang-orang yang beriman dan kelak Allah akan memberikan pahala yang besar kepada orang-orang yang beriman.   '),
	(640, 4, 147, 'مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۗ وَكَانَ اللّٰهُ شَاكِرًا عَلِيْمًا   ۔', 'm<u>aa</u> yaf\'alu <strong>al</strong>l<u>aa</u>hu bi\'a<u>dzaa</u>bikum in syakartum wa<u>aa</u>mantum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu sy<u>aa</u>kiran \'aliim<u>aa</u><strong>n</strong>', 'Allah tidak akan menyiksamu, jika kamu bersyukur dan beriman. Dan Allah Maha Mensyukuri, Maha Mengetahui.'),
	(641, 4, 148, '۞ لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّوْۤءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَ ۗ وَكَانَ اللّٰهُ سَمِيْعًا عَلِيْمًا ', 'l<u>aa</u> yu<u>h</u>ibbu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>ljahra bi<strong>al</strong>ssuu-i mina <strong>a</strong>lqawli ill<u>aa</u> man <u>zh</u>ulima wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu samii\'an \'aliim<u>aa</u><strong>n</strong>', 'Allah tidak menyukai perkataan buruk, (yang diucapkan) secara terus terang kecuali oleh orang yang dizalimi. Dan Allah Maha Mendengar, Maha Mengetahui.'),
	(642, 4, 149, 'اِنْ تُبْدُوْا خَيْرًا اَوْ تُخْفُوْهُ اَوْ تَعْفُوْا عَنْ سُوْۤءٍ فَاِنَّ اللّٰهَ كَانَ عَفُوًّا قَدِيْرًا', 'in tubduu khayran aw tukhfuuhu aw ta\'fuu \'an suu-in fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'afuwwan qadiir<u>aa</u><strong>n</strong>', 'Jika kamu menyatakan sesuatu kebajikan, menyembunyikannya atau memaafkan suatu kesalahan (orang lain), maka sungguh, Allah Maha Pemaaf, Mahakuasa.'),
	(643, 4, 150, 'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاللّٰهِ وَرُسُلِهٖ وَيُرِيْدُوْنَ اَنْ يُّفَرِّقُوْا بَيْنَ اللّٰهِ وَرُسُلِهٖ وَيَقُوْلُوْنَ نُؤْمِنُ بِبَعْضٍ وَّنَكْفُرُ بِبَعْضٍۙ وَّيُرِيْدُوْنَ اَنْ يَّتَّخِذُوْا بَيْنَ ذٰلِكَ سَبِيْلًاۙ ', 'inna <strong>al</strong>la<u>dz</u>iina yakfuruuna bi<strong>al</strong>l<u>aa</u>hi warusulihi wayuriiduuna an yufarriquu bayna <strong>al</strong>l<u>aa</u>hi warusulihi wayaquuluuna nu/minu biba\'<u>dh</u>in wanakfuru biba\'<u>dh</u>in wayuriiduuna an yattakhi<u>dz</u>uu bayna <u>dzaa</u>lika sabiil<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang ingkar kepada Allah dan rasul-rasul-Nya, dan bermaksud membeda-bedakan antara (keimanan kepada) Allah dan rasul-rasul-Nya, dengan mengatakan, “Kami beriman kepada sebagian dan kami mengingkari sebagian (yang lain),” serta bermaksud mengambil jalan tengah (iman atau kafir),'),
	(644, 4, 151, 'اُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ حَقًّا ۚوَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا', 'ul<u>aa</u>-ika humu <strong>a</strong>lk<u>aa</u>firuuna <u>h</u>aqqan wa-a\'tadn<u>aa</u> lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u><strong>n</strong>', 'merekalah orang-orang kafir yang sebenarnya. Dan Kami sediakan untuk orang-orang kafir itu azab yang menghinakan.'),
	(645, 4, 152, 'وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖ وَلَمْ يُفَرِّقُوْا بَيْنَ اَحَدٍ مِّنْهُمْ اُولٰۤىِٕكَ سَوْفَ يُؤْتِيْهِمْ اُجُوْرَهُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu bi<strong>al</strong>l<u>aa</u>hi warusulihi walam yufarriquu bayna a<u>h</u>adin minhum ul<u>aa</u>-ika sawfa yu/tiihim ujuurahum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Adapun orang-orang yang beriman kepada Allah dan rasul-rasul-Nya dan tidak membeda-bedakan di antara mereka (para rasul), kelak Allah akan memberikan pahala kepada mereka. Allah Maha Pengampun, Maha Penyayang.'),
	(646, 4, 153, 'يَسْـَٔلُكَ اَهْلُ الْكِتٰبِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتٰبًا مِّنَ السَّمَاۤءِ فَقَدْ سَاَلُوْا مُوْسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُوْٓا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ  ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْۢ بَعْدِ مَا جَا', 'yas-aluka ahlu <strong>a</strong>lkit<u>aa</u>bi an tunazzila \'alayhim kit<u>aa</u>ban mina <strong>al</strong>ssam<u>aa</u>-i faqad sa-aluu muus<u>aa</u> akbara min <u>dzaa</u>lika faq<u>aa</u>luu arin<u>aa</u> <strong>al</strong>l<u>aa</u>ha jahratan fa-akha<u>dz</u>at-humu <strong>al</strong><u>shshaa</u>\'iqatu bi<u>zh</u>ulmihim tsumma ittakha<u>dz</u>uu <strong>a</strong>l\'ijla min ba\'di m<u>aa</u> j<u>aa</u>-at-humu <strong>a</strong>lbayyin<u>aa</u>tu fa\'afawn<u>aa</u> \'an <u>dzaa</u>lika wa<u>aa</u>tayn<u>aa</u> muus<u>aa</u> sul<u>th</u><u>aa</u>nan mubiin<u>aa</u><strong>n</strong>', '(Orang-orang) Ahli Kitab meminta kepadamu (Muhammad) agar engkau menurunkan sebuah kitab dari langit kepada mereka. Sesungguhnya mereka telah meminta kepada Musa yang lebih besar dari itu. Mereka berkata, “Perlihatkanlah Allah kepada kami secara nyata.” Maka mereka disambar petir karena kezalimannya. Kemudian mereka menyembah anak sapi, setelah mereka melihat bukti-bukti yang nyata, namun demikian Kami maafkan mereka, dan telah Kami berikan kepada Musa kekuasaan yang nyata.'),
	(647, 4, 154, 'وَرَفَعْنَا فَوْقَهُمُ الطُّوْرَ بِمِيْثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَّقُلْنَا لَهُمْ لَا تَعْدُوْا فِى السَّبْتِ وَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًا', 'warafa\'n<u>aa</u> fawqahumu <strong>al</strong><u>ththh</u>uura bimiits<u>aa</u>qihim waquln<u>aa</u> lahumu udkhuluu <strong>a</strong>lb<u>aa</u>ba sujjadan waquln<u>aa</u> lahum l<u>aa</u> ta\'duu fii <strong>al</strong>ssabti wa-akha<u>dz</u>n<u>aa</u> minhum miits<u>aa</u>qan ghalii<u>zhaa</u><strong>n</strong>', 'Dan Kami angkat gunung (Sinai) di atas mereka untuk (menguatkan) perjanjian mereka. Dan Kami perintahkan kepada mereka, “Masukilah pintu gerbang (Baitulmaqdis) itu sambil bersujud,” dan Kami perintahkan (pula) kepada mereka, “Janganlah kamu melanggar peraturan mengenai hari Sabat.” Dan Kami telah mengambil dari mereka perjanjian yang kukuh.'),
	(648, 4, 155, 'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ وَكُفْرِهِمْ بِاٰيٰتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّ وَّقَوْلِهِمْ قُلُوْبُنَا غُلْفٌ ۗ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًاۖ ', 'fabim<u>aa</u> naq<u>dh</u>ihim miits<u>aa</u>qahum wakufrihim bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi waqatlihimu <strong>a</strong>l-anbiy<u>aa</u>-a bighayri <u>h</u>aqqin waqawlihim quluubun<u>aa</u> ghulfun bal <u>th</u>aba\'a <strong>al</strong>l<u>aa</u>hu \'alayh<u>aa</u> bikufrihim fal<u>aa</u> yu/minuuna ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Maka (Kami hukum mereka), karena mereka melanggar perjanjian itu, karena kekafiran mereka terhadap keterangan-keterangan Allah, dan karena mereka telah membunuh nabi-nabi tanpa hak (alasan yang benar) dan karena mereka mengatakan, “Hati kami tertutup.” Sebenarnya Allah telah mengunci hati mereka karena kekafirannya, karena itu hanya sebagian kecil dari mereka yang beriman,'),
	(649, 4, 156, 'وَّبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَانًا عَظِيْمًاۙ ', 'wabikufrihim waqawlihim \'al<u>aa</u> maryama buht<u>aa</u>nan \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'dan (Kami hukum juga) karena kekafiran mereka (terhadap Isa), dan tuduhan mereka yang sangat keji terhadap Maryam,'),
	(650, 4, 157, 'وَّقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَسِيْحَ عِيْسَى ابْنَ مَرْيَمَ رَسُوْلَ اللّٰهِۚ وَمَا قَتَلُوْهُ وَمَا صَلَبُوْهُ وَلٰكِنْ شُبِّهَ لَهُمْ ۗوَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِيْهِ لَفِيْ شَكٍّ مِّنْهُ ۗمَا لَهُمْ بِهٖ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّن', 'waqawlihim inn<u>aa</u> qataln<u>aa</u> <strong>a</strong>lmasii<u>h</u>a \'iis<u>aa</u> ibna maryama rasuula <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> qataluuhu wam<u>aa</u> <u>sh</u>alabuuhu wal<u>aa</u>kin syubbiha lahum wa-inna <strong>al</strong>la<u>dz</u>iina ikhtalafuu fiihi lafii syakkin minhu m<u>aa</u> lahum bihi min \'ilmin ill<u>aa</u> ittib<u>aa</u>\'a <strong>al</strong><u>zhzh</u>anni wam<u>aa</u> qataluuhu yaqiin<u>aa</u><strong>n</strong>', 'dan (Kami hukum juga) karena ucapan mereka, “Sesungguhnya kami telah membunuh Al-Masih, Isa putra Maryam, Rasul Allah,” padahal mereka tidak membunuhnya dan tidak (pula) menyalibnya, tetapi (yang mereka bunuh adalah) orang yang diserupakan dengan Isa. Sesungguhnya mereka yang berselisih pendapat tentang (pembunuhan) Isa, selalu dalam keragu-raguan tentang yang dibunuh itu. Mereka benar-benar tidak tahu (siapa sebenarnya yang dibunuh itu), melainkan mengikuti persangkaan belaka, jadi mereka tidak yakin telah membunuhnya.'),
	(651, 4, 158, 'بَلْ رَّفَعَهُ اللّٰهُ اِلَيْهِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'bal rafa\'ahu <strong>al</strong>l<u>aa</u>hu ilayhi wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aziizan <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Tetapi Allah telah mengangkat Isa ke hadirat-Nya. Allah Mahaperkasa, Mahabijaksana.'),
	(652, 4, 159, 'وَاِنْ مِّنْ اَهْلِ الْكِتٰبِ اِلَّا لَيُؤْمِنَنَّ بِهٖ قَبْلَ مَوْتِهٖ ۚوَيَوْمَ الْقِيٰمَةِ يَكُوْنُ عَلَيْهِمْ شَهِيْدًاۚ ', 'wa-in min ahli <strong>a</strong>lkit<u>aa</u>bi ill<u>aa</u> layu/minanna bihi qabla mawtihi wayawma <strong>a</strong>lqiy<u>aa</u>mati yakuunu \'alayhim syahiid<u>aa</u><strong>n</strong>', 'Tidak ada seorang pun di antara Ahli Kitab yang tidak beriman kepadanya (Isa) menjelang kematiannya. Dan pada hari Kiamat dia (Isa) akan menjadi saksi mereka.'),
	(653, 4, 160, 'فَبِظُلْمٍ مِّنَ الَّذِيْنَ هَادُوْا حَرَّمْنَا عَلَيْهِمْ طَيِّبٰتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيْلِ اللّٰهِ كَثِيْرًاۙ ', 'fabi<u>zh</u>ulmin mina <strong>al</strong>la<u>dz</u>iina h<u>aa</u>duu <u>h</u>arramn<u>aa</u> \'alayhim <u>th</u>ayyib<u>aa</u>tin u<u>h</u>illat lahum wabi<u>sh</u>addihim \'an sabiili <strong>al</strong>l<u>aa</u>hi katsiir<u>aa</u><strong>n</strong>', 'Karena kezaliman orang-orang Yahudi, Kami haramkan bagi mereka makanan yang baik-baik yang (dahulu) pernah dihalalkan; dan karena mereka sering menghalangi (orang lain) dari jalan Allah,'),
	(654, 4, 161, 'وَّاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوْا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِ ۗوَاَعْتَدْنَا لِلْكٰفِرِيْنَ مِنْهُمْ عَذَابًا اَلِيْمًا', 'wa-akh<u>dz</u>ihimu <strong>al</strong>rrib<u>aa</u> waqad nuhuu \'anhu wa-aklihim amw<u>aa</u>la <strong>al</strong>nn<u>aa</u>si bi<strong>a</strong>lb<u>aath</u>ili wa-a\'tadn<u>aa</u> lilk<u>aa</u>firiina minhum \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'dan karena mereka menjalankan riba, padahal sungguh mereka telah dilarang darinya, dan karena mereka memakan harta orang dengan cara tidak sah (batil). Dan Kami sediakan untuk orang-orang kafir di antara mereka azab yang pedih.'),
	(655, 4, 162, 'لٰكِنِ الرَّاسِخُوْنَ فِى الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُوْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيْمِيْنَ الصَّلٰوةَ وَالْمُؤْتُوْنَ الزَّكٰوةَ وَالْمُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ اُولٰۤىِٕكَ سَنُؤ', 'l<u>aa</u>kini <strong>al</strong>rr<u>aa</u>sikhuuna fii <strong>a</strong>l\'ilmi minhum wa<strong>a</strong>lmu/minuuna yu/minuuna bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika wa<strong>a</strong>lmuqiimiina <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<strong>a</strong>lmu/tuuna <strong>al</strong>zzak<u>aa</u>ta wa<strong>a</strong>lmu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri ul<u>aa</u>-ika sanu/tiihim ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Tetapi orang-orang yang ilmunya mendalam di antara mereka, dan orang-orang yang beriman, mereka beriman kepada (Al-Qur\'an) yang diturunkan kepadamu (Muhammad), dan kepada (kitab-kitab) yang diturunkan sebelummu, begitu pula mereka yang melaksanakan salat dan menunaikan zakat dan beriman kepada Allah dan hari kemudian. Kepada mereka akan Kami berikan pahala yang besar.'),
	(656, 4, 163, '۞ اِنَّآ اَوْحَيْنَآ اِلَيْكَ كَمَآ اَوْحَيْنَآ اِلٰى نُوْحٍ وَّالنَّبِيّٖنَ مِنْۢ بَعْدِهٖۚ وَاَوْحَيْنَآ اِلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَعِيْسٰى وَاَيُّوْبَ وَيُوْنُسَ وَهٰرُوْنَ وَسُلَيْمٰنَ ۚوَاٰتَيْنَا', 'inn<u>aa</u> aw<u>h</u>ayn<u>aa</u> ilayka kam<u>aa</u> aw<u>h</u>ayn<u>aa</u> il<u>aa</u> nuu<u>h</u>in wa<strong>al</strong>nnabiyyiina min ba\'dihi wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa waya\'quuba wa<strong>a</strong>l-asb<u>aath</u>i wa\'iis<u>aa</u> wa-ayyuuba wayuunusa wah<u>aa</u>ruuna wasulaym<u>aa</u>na wa<u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda zabuur<u>aa</u><strong>n</strong>', 'Sesungguhnya Kami mewahyukan kepadamu (Muhammad) sebagaimana Kami telah mewahyukan kepada Nuh dan nabi-nabi setelahnya, dan Kami telah mewahyukan (pula) kepada Ibrahim, Ismail, Ishak, Yakub dan anak cucunya; Isa, Ayyub, Yunus, Harun dan Sulaiman. Dan Kami telah memberikan Kitab Zabur kepada Dawud.'),
	(657, 4, 164, 'وَرُسُلًا قَدْ قَصَصْنٰهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلًا لَّمْ نَقْصُصْهُمْ عَلَيْكَ ۗوَكَلَّمَ اللّٰهُ مُوْسٰى تَكْلِيْمًاۚ ', 'warusulan qad qa<u>sh</u>a<u>sh</u>n<u>aa</u>hum \'alayka min qablu warusulan lam naq<u>sh</u>u<u>sh</u>hum \'alayka wakallama <strong>al</strong>l<u>aa</u>hu muus<u>aa</u> takliim<u>aa</u><strong>n</strong>', 'Dan ada beberapa rasul yang telah Kami kisahkan mereka kepadamu sebelumnya dan ada beberapa rasul (la-in) yang tidak Kami kisahkan mereka kepadamu. Dan kepada Musa, Allah berfirman langsung.'),
	(658, 4, 165, 'رُسُلًا مُّبَشِّرِيْنَ وَمُنْذِرِيْنَ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ ۢ بَعْدَ الرُّسُلِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا', 'rusulan mubasysyiriina wamun<u>dz</u>iriina li-all<u>aa</u> yakuuna li<strong>l</strong>nn<u>aa</u>si \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <u>h</u>ujjatun ba\'da <strong>al</strong>rrusuli wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aziizan <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Rasul-rasul itu adalah sebagai pembawa berita gembira dan pemberi peringatan, agar tidak ada alasan bagi manusia untuk membantah Allah setelah rasul-rasul itu diutus. Allah Mahaperkasa, Mahabijaksana.'),
	(659, 4, 166, 'لٰكِنِ اللّٰهُ يَشْهَدُ بِمَآ اَنْزَلَ اِلَيْكَ اَنْزَلَهٗ بِعِلْمِهٖ ۚوَالْمَلٰۤىِٕكَةُ يَشْهَدُوْنَ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًاۗ ', 'l<u>aa</u>kini <strong>al</strong>l<u>aa</u>hu yasyhadu bim<u>aa</u> anzala ilayka anzalahu bi\'ilmihi wa<strong>a</strong>lmal<u>aa</u>-ikatu yasyhaduuna wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi syahiid<u>aa</u><strong>n</strong>', 'Tetapi Allah menjadi saksi atas (Al-Qur\'an) yang diturunkan-Nya kepadamu (Muhammad). Dia menurunkannya dengan ilmu-Nya, dan para malaikat pun menyaksikan. Dan cukuplah Allah yang menjadi saksi.'),
	(660, 4, 167, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ قَدْ ضَلُّوْا ضَلٰلًا ۢ بَعِيْدًا', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiili <strong>al</strong>l<u>aa</u>hi qad <u>dh</u>alluu <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang kafir dan menghalang-halangi (orang lain) dari jalan Allah, benar-benar telah sesat sejauh-jauhnya.  '),
	(661, 4, 168, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَظَلَمُوْا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيْقًاۙ ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu wa<u>zh</u>alamuu lam yakuni <strong>al</strong>l<u>aa</u>hu liyaghfira lahum wal<u>aa</u> liyahdiyahum <u>th</u>ariiq<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang kafir dan melakukan kezaliman, Allah tidak akan mengampuni mereka, dan tidak (pula) akan menunjukkan kepada mereka jalan (yang lurus),   '),
	(662, 4, 169, 'اِلَّا طَرِيْقَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا', 'ill<u>aa</u> <u>th</u>ariiqa jahannama kh<u>aa</u>lidiina fiih<u>aa</u> abadan wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<u>aa</u><strong>n</strong>', 'kecuali jalan ke neraka Jahanam; mereka kekal di dalamnya selama-lamanya. Dan hal itu (sangat) mudah bagi Allah.'),
	(663, 4, 170, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الرَّسُوْلُ بِالْحَقِّ مِنْ رَّبِّكُمْ فَاٰمِنُوْا خَيْرًا لَّكُمْ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su qad j<u>aa</u>-akumu <strong>al</strong>rrasuulu bi<strong>a</strong>l<u>h</u>aqqi min rabbikum fa<u>aa</u>minuu khayran lakum wa-in takfuruu fa-inna lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Wahai manusia! Sungguh, telah datang Rasul (Muhammad) kepadamu dengan (membawa) kebenaran dari Tuhanmu, maka berimanlah (kepadanya), itu lebih baik bagimu. Dan jika kamu kafir, (itu tidak merugikan Allah sedikit pun) karena sesungguhnya milik Allah-lah apa yang di langit dan di bumi. Allah Maha Mengetahui, Mahabijaksana.'),
	(664, 4, 171, 'يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ وَلَا تَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّۗ اِنَّمَا الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ رَسُوْلُ اللّٰهِ وَكَلِمَتُهٗ ۚ اَلْقٰهَآ اِلٰى مَرْيَمَ وَرُوْحٌ مِّنْهُ ۖفَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖۗ وَ', 'y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi l<u>aa</u> taghluu fii diinikum wal<u>aa</u> taquuluu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> <strong>a</strong>l<u>h</u>aqqa innam<u>aa</u> <strong>a</strong>lmasii<u>h</u>u \'iis<u>aa</u> ibnu maryama rasuulu <strong>al</strong>l<u>aa</u>hi wakalimatuhu <strong>a</strong>lq<u>aa</u>h<u>aa</u> il<u>aa</u> maryama waruu<u>h</u>un minhu fa<u>aa</u>minuu bi<strong>al</strong>l<u>aa</u>hi warusulihi wal<u>aa</u> taquuluu tsal<u>aa</u>tsatun intahuu khayran lakum innam<u>aa</u> <strong>al</strong>l<u>aa</u>hu il<u>aa</u>hun w<u>aah</u>idun sub<u>haa</u>nahu an yakuuna lahu waladun lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wakiil<u>aa</u><strong>n</strong>', 'Wahai Ahli Kitab! Janganlah kamu melampaui batas dalam agamamu, dan janganlah kamu mengatakan terhadap Allah kecuali yang benar. Sungguh, Al-Masih Isa putra Maryam itu adalah utusan Allah dan (yang diciptakan dengan) kalimat-Nya yang disampaikan-Nya kepada Maryam, dan (dengan tiupan) roh dari-Nya. Maka berimanlah kepada Allah dan rasul-rasul-Nya dan janganlah kamu mengatakan, “(Tuhan itu) tiga,” berhentilah (dari ucapan itu). (Itu) lebih baik bagimu. Sesungguhnya Allah Tuhan Yang Maha Esa, Mahasuci Dia dari (anggapan) mempunyai anak. Milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Dan cukuplah Allah sebagai pelindung.'),
	(665, 4, 172, 'لَنْ يَّسْتَنْكِفَ الْمَسِيْحُ اَنْ يَّكُوْنَ عَبْدًا لِّلّٰهِ وَلَا الْمَلٰۤىِٕكَةُ الْمُقَرَّبُوْنَۗ وَمَنْ يَّسْتَنْكِفْ عَنْ عِبَادَتِهٖ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَمِيْعًا', 'lan yastankifa <strong>a</strong>lmasii<u>h</u>u an yakuuna \'abdan lill<u>aa</u>hi wal<u>aa</u> <strong>a</strong>lmal<u>aa</u>-ikatu <strong>a</strong>lmuqarrabuuna waman yastankif \'an \'ib<u>aa</u>datihi wayastakbir fasaya<u>h</u>syuruhum ilayhi jamii\'<u>aa</u><strong>n</strong>', 'Al-Masih sama sekali tidak enggan menjadi hamba Allah, dan begitu pula para malaikat yang terdekat (kepada Allah). Dan barangsiapa enggan menyembah-Nya dan menyombongkan diri, maka Allah akan mengumpulkan mereka semua kepada-Nya.'),
	(666, 4, 173, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖۚ وَاَمَّا الَّذِيْنَ اسْتَنْكَفُوْا وَاسْتَكْبَرُوْا فَيُعَذِّبُهُمْ عَذَابًا اَلِيْمًاۙ وَّلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا', 'fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti fayuwaffiihim ujuurahum wayaziiduhum min fa<u>dh</u>lihi wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina istankafuu wa<strong>i</strong>stakbaruu fayu\'a<u>dzdz</u>ibuhum \'a<u>dzaa</u>ban <strong>a</strong>liiman wal<u>aa</u> yajiduuna lahum min duuni <strong>al</strong>l<u>aa</u>hi waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Adapun orang-orang yang beriman dan mengerjakan kebajikan, Allah akan menyempurnakan pahala bagi mereka dan menambah sebagian dari karunia-Nya. Sedangkan orang-orang yang enggan (menyembah Allah) dan menyombongkan diri, maka Allah akan mengazab mereka dengan azab yang pedih. Dan mereka tidak akan mendapatkan pelindung dan penolong selain Allah.'),
	(667, 4, 174, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمْ بُرْهَانٌ مِّنْ رَّبِّكُمْ وَاَنْزَلْنَآ اِلَيْكُمْ نُوْرًا مُّبِيْنًا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su qad j<u>aa</u>-akum burh<u>aa</u>nun min rabbikum wa-anzaln<u>aa</u> ilaykum nuuran mubiin<u>aa</u><strong>n</strong>', 'Wahai manusia! Sesungguhnya telah sampai kepadamu bukti kebenaran dari Tuhanmu, (Muhammad dengan mukjizatnya) dan telah Kami turunkan kepadamu cahaya yang terang benderang (Al-Qur\'an).'),
	(668, 4, 175, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَاعْتَصَمُوْا بِهٖ فَسَيُدْخِلُهُمْ فِيْ رَحْمَةٍ مِّنْهُ وَفَضْلٍۙ وَّيَهْدِيْهِمْ اِلَيْهِ صِرَاطًا مُّسْتَقِيْمًاۗ ', 'fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu bi<strong>al</strong>l<u>aa</u>hi wa<strong>i</strong>\'ta<u>sh</u>amuu bihi fasayudkhiluhum fii ra<u>h</u>matin minhu wafa<u>dh</u>lin wayahdiihim ilayhi <u>sh</u>ir<u>aath</u>an mustaqiim<u>aa</u><strong>n</strong>', 'Adapun orang-orang yang beriman kepada Allah dan berpegang teguh kepada (agama)-Nya, maka Allah akan memasukkan mereka ke dalam rahmat dan karunia dari-Nya (surga), dan menunjukkan mereka jalan yang lurus kepada-Nya.'),
	(669, 4, 176, 'يَسْتَفْتُوْنَكَۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِى الْكَلٰلَةِ ۗاِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهٗ وَلَدٌ وَّلَهٗٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ اِنْ لَّمْ يَكُنْ لَّهَا وَلَدٌ ۚ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثٰنِ مِمَّا تَ', 'yastaftuunaka quli <strong>al</strong>l<u>aa</u>hu yuftiikum fii <strong>a</strong>lkal<u>aa</u>lati ini imruun halaka laysa lahu waladun walahu ukhtun falah<u>aa</u> ni<u>sh</u>fu m<u>aa</u> taraka wahuwa yaritsuh<u>aa</u> in lam yakun lah<u>aa</u> waladun fa-in k<u>aa</u>nat<u>aa</u> itsnatayni falahum<u>aa</u> <strong>al</strong>tstsuluts<u>aa</u>ni mimm<u>aa</u> taraka wa-in k<u>aa</u>nuu ikhwatan rij<u>aa</u>lan wanis<u>aa</u>-an fali<strong>l</strong><u>dzdz</u>akari mitslu <u>h</u>a<u>zhzh</u>i <strong>a</strong>luntsayayni yubayyinu <strong>al</strong>l<u>aa</u>hu lakum an ta<u>dh</u>illuu wa<strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<strong>un</strong>yastaftuunaka quli <strong>al</strong>l<u>aa</u>hu yuftiikum fii <strong>a</strong>lkal<u>aa</u>lati ini imruun halaka laysa lahu waladun walahu ukhtun falah<u>aa</u> ni<u>sh</u>fu m<u>aa</u> taraka wahuwa yaritsuh<u>aa</u> in lam yakun lah<u>aa</u> waladun fa-in k<u>aa</u>nat<u>aa</u> itsnatayni falahum<u>aa</u> <strong>al</strong>tstsuluts<u>aa</u>ni mimm<u>aa</u> taraka wa-in k<u>aa</u>nuu ikhwatan rij<u>aa</u>lan wanis<u>aa</u>-an fali<strong>l</strong><u>dzdz</u>akari mitslu <u>h</u>a<u>zhzh</u>i <strong>a</strong>luntsayayni yubayyinu <strong>al</strong>l<u>aa</u>hu lakum an ta<u>dh</u>illuu wa<strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<strong>un</strong>', 'Mereka meminta fatwa kepadamu (tentang kalalah). Katakanlah, “Allah memberi fatwa kepadamu tentang kalalah (yaitu), jika seseorang mati dan dia tidak mempunyai anak tetapi mempunyai saudara perempuan, maka bagiannya (saudara perempuannya itu) seperdua dari harta yang ditinggalkannya, dan saudaranya yang laki-laki mewarisi (seluruh harta saudara perempuan), jika dia tidak mempunyai anak. Tetapi jika saudara perempuan itu dua orang, maka bagi keduanya dua pertiga dari harta yang ditinggalkan. Dan jika mereka (ahli waris itu terdiri dari) saudara-saudara laki-laki dan perempuan, maka bagian seorang saudara laki-laki sama dengan bagian dua saudara perempuan. Allah menerangkan (hukum ini) kepadamu, agar kamu tidak sesat. Allah Maha Mengetahui segala sesuatu.”'),
	(670, 5, 1, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَوْفُوْا بِالْعُقُوْدِۗ  اُحِلَّتْ لَكُمْ بَهِيْمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّى الصَّيْدِ وَاَنْتُمْ حُرُمٌۗ  اِنَّ اللّٰهَ يَحْكُمُ مَا يُرِيْدُ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu awfuu bi<strong>a</strong>l\'uquudi u<u>h</u>illat lakum bahiimatu <strong>a</strong>l-an\'<u>aa</u>mi ill<u>aa</u> m<u>aa</u> yutl<u>aa</u> \'alaykum ghayra mu<u>h</u>illii <strong>al</strong><u>shsh</u>aydi wa-antum <u>h</u>urumun inna <strong>al</strong>l<u>aa</u>ha ya<u>h</u>kumu m<u>aa</u> yuriid<strong>u</strong>', 'Wahai orang-orang yang beriman! Penuhilah janji-janji. Hewan ternak dihalalkan bagimu, kecuali yang akan disebutkan kepadamu, dengan tidak menghalalkan berburu ketika kamu sedang berihram (haji atau umrah). Sesungguhnya Allah menetapkan hukum sesuai dengan yang Dia kehendaki.'),
	(671, 5, 2, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحِلُّوْا شَعَاۤىِٕرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَاۤىِٕدَ وَلَآ اٰۤمِّيْنَ الْبَيْتَ الْحَرَامَ يَبْتَغُوْنَ فَضْلًا مِّنْ رَّبِّهِمْ وَرِضْوَانًا ۗوَاِذَا حَلَلْتُمْ فَاصْطَادُوْا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tu<u>h</u>illuu sya\'<u>aa</u>-ira <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> <strong>al</strong>sysyahra <strong>a</strong>l<u>h</u>ar<u>aa</u>ma wal<u>aa</u> <strong>a</strong>lhadya wal<u>aa</u> <strong>a</strong>lqal<u>aa</u>-ida wal<u>aa</u> <u>aa</u>mmiina <strong>a</strong>lbayta <strong>a</strong>l<u>h</u>ar<u>aa</u>ma yabtaghuuna fa<u>dh</u>lan min rabbihim wari<u>dh</u>w<u>aa</u>nan wa-i<u>dzaa</u> <u>h</u>alaltum fa<strong>i</strong><u>staa</u>duu wal<u>aa</u> yajrimannakum syana<u>aa</u>nu qawmin an <u>sh</u>adduukum \'ani <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi an ta\'taduu wata\'<u>aa</u>wanuu \'al<u>aa</u> <strong>a</strong>lbirri wa<strong>al</strong>ttaqw<u>aa</u> wal<u>aa</u> ta\'<u>aa</u>wanuu \'al<u>aa</u> <strong>a</strong>l-itsmi wa<strong>a</strong>l\'udw<u>aa</u>ni wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu melanggar syiar-syiar kesucian Allah, dan jangan (melanggar kehormatan) bulan-bulan haram, jangan (mengganggu) hadyu (hewan-hewan kurban) dan qala\'id (hewan-hewan kurban yang diberi tanda), dan jangan (pula) mengganggu orang-orang yang mengunjungi Baitulharam; mereka mencari karunia dan keridaan Tuhannya. Tetapi apabila kamu telah menyelesaikan ihram, maka bolehlah kamu berburu. Jangan sampai kebencian(mu) kepada suatu kaum karena mereka menghalang-halangimu dari Masjidilharam, mendorongmu berbuat melampaui batas (kepada mereka). Dan tolong-menolonglah kamu dalam (mengerjakan) kebajikan dan takwa, dan jangan tolong-menolong dalam berbuat dosa dan permusuhan. Bertakwalah kepada Allah, sungguh, Allah sangat berat siksaan-Nya.'),
	(672, 5, 3, 'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖ وَالْمُنْخَنِقَةُ وَالْمَوْقُوْذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيْحَةُ وَمَآ اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْۗ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَ', '<u>h</u>urrimat \'alaykumu <strong>a</strong>lmaytatu wa<strong>al</strong>ddamu wala<u>h</u>mu <strong>a</strong>lkhinziiri wam<u>aa</u> uhilla lighayri <strong>al</strong>l<u>aa</u>hi bihi wa<strong>a</strong>lmunkhaniqatu wa<strong>a</strong>lmawquu<u>dz</u>atu wa<strong>a</strong>lmutaraddiyatu wa<strong>al</strong>nna<u>th</u>ii<u>h</u>atu wam<u>aa</u> akala <strong>al</strong>ssabu\'u ill<u>aa</u> m<u>aa ts</u>akkaytum wam<u>aa</u> <u>dz</u>ubi<u>h</u>a \'al<u>aa</u> <strong>al</strong>nnu<u>sh</u>ubi wa-an tastaqsimuu bi<strong>a</strong>l-azl<u>aa</u>mi <u>dzaa</u>likum fisqun <strong>a</strong>lyawma ya-isa <strong>al</strong>la<u>dz</u>iina kafaruu min diinikum fal<u>aa</u> takhsyawhum wa<strong>i</strong>khsyawni <strong>a</strong>lyawma akmaltu lakum diinakum wa-atmamtu \'alaykum ni\'matii wara<u>dh</u>iitu lakumu <strong>a</strong>l-isl<u>aa</u>ma diinan famani i<u>dth</u>urra fii makhma<u>sh</u>atin ghayra mutaj<u>aa</u>nifin li-itsmin fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Diharamkan bagimu (memakan) bangkai, darah, daging babi, dan (daging) hewan yang disembelih bukan atas (nama) Allah, yang tercekik, yang dipukul, yang jatuh, yang ditanduk, dan yang diterkam binatang buas, kecuali yang sempat kamu sembelih. Dan (diharamkan pula) yang disembelih untuk berhala. Dan (diharamkan pula) mengundi nasib dengan azlam (anak panah), (karena) itu suatu perbuatan fasik. Pada hari ini orang-orang kafir telah putus asa untuk (mengalahkan) agamamu, sebab itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku. Pada hari ini telah Aku sempurnakan agamamu untukmu, dan telah Aku cukupkan nikmat-Ku bagimu, dan telah Aku ridai Islam sebagai agamamu. Tetapi barangsiapa terpaksa karena lapar, bukan karena ingin berbuat dosa, maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(673, 5, 4, 'يَسْـَٔلُوْنَكَ مَاذَآ اُحِلَّ لَهُمْۗ  قُلْ اُحِلَّ لَكُمُ الطَّيِّبٰتُۙ وَمَا عَلَّمْتُمْ مِّنَ الْجَوَارِحِ مُكَلِّبِيْنَ تُعَلِّمُوْنَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُ  فَكُلُوْا مِمَّآ اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِ ۖوَاتَّ', 'yas-aluunaka m<u>aatsaa</u> u<u>h</u>illa lahum qul u<u>h</u>illa lakumu <strong>al</strong><u>ththh</u>ayyib<u>aa</u>tu wam<u>aa</u> \'allamtum mina <strong>a</strong>ljaw<u>aa</u>ri<u>h</u>i mukallibiina tu\'allimuunahunna mimm<u>aa</u> \'allamakumu <strong>al</strong>l<u>aa</u>hu fakuluu mimm<u>aa</u> amsakna \'alaykum wa<strong>u</strong><u>dz</u>kuruu isma <strong>al</strong>l<u>aa</u>hi \'alayhi wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Mereka bertanya kepadamu (Muhammad), “Apakah yang dihalalkan bagi mereka?” Katakanlah, ”Yang dihalalkan bagimu (adalah makanan) yang baik-baik dan (buruan yang ditangkap) oleh binatang pemburu yang telah kamu latih untuk berburu, yang kamu latih menurut apa yang telah diajarkan Allah kepadamu. Maka makanlah apa yang ditangkapnya untukmu, dan sebutlah nama Allah (waktu melepasnya). Dan bertakwalah kepada Allah, sungguh, Allah sangat cepat perhitungan-Nya.”'),
	(674, 5, 5, 'اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبٰتُۗ وَطَعَامُ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حِلٌّ لَّكُمْ ۖوَطَعَامُكُمْ حِلٌّ لَّهُمْ ۖوَالْمُحْصَنٰتُ مِنَ الْمُؤْمِنٰتِ وَالْمُحْصَنٰتُ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُ', 'alyawma u<u>h</u>illa lakumu <strong>al</strong><u>ththh</u>ayyib<u>aa</u>tu wa<u>th</u>a\'<u>aa</u>mu <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba <u>h</u>illun lakum wa<u>th</u>a\'<u>aa</u>mukum <u>h</u>illun lahum wa<strong>a</strong>lmu<u>hs</u>an<u>aa</u>tu mina <strong>a</strong>lmu/min<u>aa</u>ti wa<strong>a</strong>lmu<u>hs</u>an<u>aa</u>tu mina <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba min qablikum i<u>dzaa</u> <u>aa</u>taytumuuhunna ujuurahunna mu<u>hs</u>iniina ghayra mus<u>aa</u>fi<u>h</u>iina wal<u>aa</u> muttakhi<u>dz</u>ii akhd<u>aa</u>nin waman yakfur bi<strong>a</strong>l-iim<u>aa</u>ni faqad <u>h</u>abi<u>th</u>a \'amaluhu wahuwa fii <strong>a</strong>l-<u>aa</u>khirati mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Pada hari ini dihalalkan bagimu segala yang baik-baik. Makanan (sembelihan) Ahli Kitab itu halal bagimu, dan makananmu halal bagi mereka. Dan (dihalalkan bagimu menikahi) perempuan-perempuan yang menjaga kehormatan di antara perempuan-perempuan yang beriman dan perempuan-perempuan yang menjaga kehormatan di antara orang-orang yang diberi kitab sebelum kamu, apabila kamu membayar maskawin mereka untuk menikahinya, tidak dengan maksud berzina dan bukan untuk menjadikan perempuan piaraan. Barangsiapa kafir setelah beriman, maka sungguh, sia-sia amal mereka, dan di akhirat dia termasuk orang-orang yang rugi.'),
	(675, 5, 6, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوْا وُجُوْهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوْا بِرُءُوْسِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۗ وَاِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوْاۗ وَاِنْ كُنْتُمْ مَّرْضٰٓى', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> qumtum il<u>aa</u> <strong>al</strong><u>shsh</u>al<u>aa</u>ti fa<strong>i</strong>ghsiluu wujuuhakum wa-aydiyakum il<u>aa</u> <strong>a</strong>lmar<u>aa</u>fiqi wa<strong>i</strong>msa<u>h</u>uu biruuusikum wa-arjulakum il<u>aa</u> <strong>a</strong>lka\'bayni wa-in kuntum junuban fa<strong>i</strong><u>ththh</u>ahharuu wa-in kuntum mar<u>daa</u> aw \'al<u>aa</u> safarin aw j<u>aa</u>-a a<u>h</u>adun minkum mina <strong>a</strong>lgh<u>aa</u>-i<u>th</u>i aw l<u>aa</u>mastumu <strong>al</strong>nnis<u>aa</u>-a falam tajiduu m<u>aa</u>-an fatayammamuu <u>sh</u>a\'iidan <u>th</u>ayyiban fa<strong>i</strong>msa<u>h</u>uu biwujuuhikum wa-aydiikum minhu m<u>aa</u> yuriidu <strong>al</strong>l<u>aa</u>hu liyaj\'ala \'alaykum min <u>h</u>arajin wal<u>aa</u>kin yuriidu liyu<u>th</u>ahhirakum waliyutimma ni\'matahu \'alaykum la\'allakum tasykuruun<strong>a</strong>', 'Wahai orang-orang yang beriman! Apabila kamu hendak melaksanakan salat, maka basuhlah wajahmu dan tanganmu sampai ke siku, dan sapulah kepalamu dan (basuh) kedua kakimu sampai ke kedua mata kaki. Jika kamu junub, maka mandilah. Dan jika kamu sakit atau dalam perjalanan atau kembali dari tempat buang air (kakus) atau menyentuh perempuan, maka jika kamu tidak memperoleh air, maka bertayamumlah dengan debu yang baik (suci); usaplah wajahmu dan tanganmu dengan (debu) itu. Allah tidak ingin menyulitkan kamu, tetapi Dia hendak membersihkan kamu dan menyempurnakan nikmat-Nya bagimu, agar kamu bersyukur.'),
	(676, 5, 7, 'وَاذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَمِيْثَاقَهُ الَّذِيْ وَاثَقَكُمْ بِهٖٓ ۙاِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَا ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ', 'wa<strong>u</strong><u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum wamiits<u>aa</u>qahu <strong>al</strong>la<u>dz</u>ii w<u>aa</u>tsaqakum bihi i<u>dz</u> qultum sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Dan ingatlah akan karunia Allah kepadamu dan perjanjian-Nya yang telah diikatkan kepadamu, ketika kamu mengatakan, “Kami mendengar dan kami menaati.” Dan bertakwalah kepada Allah, sungguh, Allah Maha Mengetahui segala isi hati.'),
	(677, 5, 8, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ لِلّٰهِ شُهَدَاۤءَ بِالْقِسْطِۖ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُوْا ۗاِعْدِلُوْاۗ هُوَ اَقْرَبُ لِلتَّقْوٰىۖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu kuunuu qaww<u>aa</u>miina lill<u>aa</u>hi syuhad<u>aa</u>-a bi<strong>a</strong>lqis<u>th</u>i wal<u>aa</u> yajrimannakum syana<u>aa</u>nu qawmin \'al<u>aa</u> <strong>al</strong>l<u>aa</u> ta\'diluu i\'diluu huwa aqrabu li<strong>l</strong>ttaqw<u>aa</u> wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha khabiirun bim<u>aa</u> ta\'maluun<strong>a</strong>', 'Wahai orang-orang yang beriman! Jadilah kamu sebagai penegak keadilan karena Allah, (ketika) menjadi saksi dengan adil. Dan janganlah kebencianmu terhadap suatu kaum mendorong kamu untuk berlaku tidak adil. Berlaku adillah. Karena (adil) itu lebih dekat kepada takwa. Dan bertakwalah kepada Allah, sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan.'),
	(678, 5, 9, 'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۙ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ', 'wa\'ada <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lahum maghfiratun wa-ajrun \'a<u>zh</u>iim<strong>un</strong>', 'Allah telah menjanjikan kepada orang-orang yang beriman dan beramal saleh, (bahwa) mereka akan mendapat ampunan dan pahala yang besar.'),
	(679, 5, 10, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>lja<u>h</u>iim<strong>i</strong>', 'Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni neraka.'),
	(680, 5, 11, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَّبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ࣖ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu u<u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum i<u>dz</u> hamma qawmun an yabsu<u>th</u>uu ilaykum aydiyahum fakaffa aydiyahum \'ankum wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmu/minuun<strong>a</strong>', 'Wahai orang-orang yang beriman! Ingatlah nikmat Allah (yang diberikan) kepadamu, ketika suatu kaum bermaksud hendak menyerangmu dengan tangannya, lalu Allah menahan tangan mereka dari kamu. Dan bertakwalah kepada Allah, dan hanya kepada Allah-lah hendaknya orang-orang beriman itu bertawakal.'),
	(681, 5, 12, '۞ وَلَقَدْ اَخَذَ اللّٰهُ مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيْبًاۗ وَقَالَ اللّٰهُ اِنِّيْ مَعَكُمْ ۗ لَىِٕنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُلِيْ وَعَزَّرْتُمُوْهُمْ وَاَقْرَضْتُمُ ', 'walaqad akha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu miits<u>aa</u>qa banii isr<u>aa</u>-iila waba\'atsn<u>aa</u> minhumu itsnay \'asyara naqiiban waq<u>aa</u>la <strong>al</strong>l<u>aa</u>hu innii ma\'akum la-in aqamtumu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>taytumu <strong>al</strong>zzak<u>aa</u>ta wa<u>aa</u>mantum birusulii wa\'azzartumuuhum wa-aqra<u>dh</u>tumu <strong>al</strong>l<u>aa</u>ha qar<u>dh</u>an <u>h</u>asanan laukaffiranna \'ankum sayyi-<u>aa</u>tikum walaudkhilannakum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru faman kafara ba\'da <u>dzaa</u>lika minkum faqad <u>dh</u>alla saw<u>aa</u>-a <strong>al</strong>ssabiil<strong>i</strong>', 'Dan sungguh, Allah telah mengambil perjanjian dari Bani Israil dan Kami telah mengangkat dua belas orang pemimpin di antara mereka. Dan Allah berfirman, “Aku bersamamu.” Sungguh, jika kamu melaksanakan salat dan menunaikan zakat serta beriman kepada rasul-rasul-Ku dan kamu bantu mereka dan kamu pinjamkan kepada Allah pinjaman yang baik, pasti akan Aku hapus kesalahan-kesalahanmu, dan pasti akan Aku masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai. Tetapi barangsiapa kafir di antaramu setelah itu, maka sesungguhnya dia telah tersesat dari jalan yang lurus.”'),
	(682, 5, 13, 'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ لَعَنّٰهُمْ وَجَعَلْنَا قُلُوْبَهُمْ قٰسِيَةً ۚ يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖۙ وَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَاۤىِٕنَةٍ مِّنْهُمْ اِلَّا قَلِيْلًا مِّنْهُمْ ۖ فَاعْفُ', 'fabim<u>aa</u> naq<u>dh</u>ihim miits<u>aa</u>qahum la\'ann<u>aa</u>hum waja\'aln<u>aa</u> quluubahum q<u>aa</u>siyatan yu<u>h</u>arrifuuna <strong>a</strong>lkalima \'an maw<u>aad</u>i\'ihi wanasuu <u>h</u>a<u>zhzh</u>an mimm<u>aa</u> <u>dz</u>ukkiruu bihi wal<u>aa</u> taz<u>aa</u>lu ta<u>ththh</u>ali\'u \'al<u>aa</u> kh<u>aa</u>-inatin minhum ill<u>aa</u> qaliilan minhum fa<strong>u</strong>\'fu \'anhum wa<strong>i</strong><u>sh</u>fa<u>h</u> inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', '(Tetapi) karena mereka melanggar janjinya, maka Kami melaknat mereka, dan Kami jadikan hati mereka keras membatu. Mereka suka mengubah firman (Allah) dari tempatnya, dan mereka (sengaja) melupakan sebagian pesan yang telah diperingatkan kepada mereka. Engkau (Muhammad) senantiasa akan melihat pengkhianatan dari mereka kecuali sekelompok kecil di antara mereka (yang tidak berkhianat), maka maafkanlah mereka dan biarkan mereka. Sungguh, Allah menyukai orang-orang yang berbuat baik.'),
	(683, 5, 14, 'وَمِنَ الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰٓى اَخَذْنَا مِيْثَاقَهُمْ فَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِ ۗ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوْا يَصْنَعُوْنَ', 'wamina <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inn<u>aa</u> na<u>shaa</u>r<u>aa</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qahum fanasuu <u>h</u>a<u>zhzh</u>an mimm<u>aa</u> <u>dz</u>ukkiruu bihi fa-aghrayn<u>aa</u> baynahumu <strong>a</strong>l\'ad<u>aa</u>wata wa<strong>a</strong>lbagh<u>dhaa</u>-a il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati wasawfa yunabbi-uhumu <strong>al</strong>l<u>aa</u>hu bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun<strong>a</strong>', 'Dan di antara orang-orang yang mengatakan, “Kami ini orang Nasrani,” Kami telah mengambil perjanjian mereka, tetapi mereka (sengaja) melupakan sebagian pesan yang telah diperingatkan kepada mereka, maka Kami timbulkan permusuhan dan kebencian di antara mereka hingga hari Kiamat. Dan kelak Allah akan memberitakan kepada mereka apa yang telah mereka kerjakan.'),
	(684, 5, 15, 'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ كَثِيْرًا مِّمَّا كُنْتُمْ تُخْفُوْنَ مِنَ الْكِتٰبِ وَيَعْفُوْا عَنْ كَثِيْرٍەۗ قَدْ جَاۤءَكُمْ مِّنَ اللّٰهِ نُوْرٌ وَّكِتٰبٌ مُّبِيْنٌۙ', 'y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi qad j<u>aa</u>-akum rasuulun<u>aa</u> yubayyinu lakum katsiiran mimm<u>aa</u> kuntum tukhfuuna mina <strong>a</strong>lkit<u>aa</u>bi waya\'fuu \'an katsiirin qad j<u>aa</u>-akum mina <strong>al</strong>l<u>aa</u>hi nuurun wakit<u>aa</u>bun mubiin<strong>un</strong>', 'Wahai Ahli Kitab! Sungguh, Rasul Kami telah datang kepadamu, menjelaskan kepadamu banyak hal dari (isi) kitab yang kamu sembunyikan, dan banyak (pula) yang dibiarkannya. Sungguh, telah datang kepadamu cahaya dari Allah, dan Kitab yang menjelaskan.'),
	(685, 5, 16, 'يَّهْدِيْ بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهٗ سُبُلَ السَّلٰمِ وَيُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِ بِاِذْنِهٖ وَيَهْدِيْهِمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'yahdii bihi <strong>al</strong>l<u>aa</u>hu mani ittaba\'a ri<u>dh</u>w<u>aa</u>nahu subula <strong>al</strong>ssal<u>aa</u>mi wayukhrijuhum mina <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> <strong>al</strong>nnuuri bi-i<u>dz</u>nihi wayahdiihim il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dengan Kitab itulah Allah memberi petunjuk kepada orang yang mengikuti keridaan-Nya ke jalan keselamatan, dan (dengan Kitab itu pula) Allah mengeluarkan orang itu dari gelap gulita kepada cahaya dengan izin-Nya, dan menunjukkan ke jalan yang lurus.'),
	(686, 5, 17, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَۗ  قُلْ فَمَنْ يَّمْلِكُ مِنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ اَنْ يُّهْلِكَ الْمَسِيْحَ ابْنَ مَرْيَمَ وَاُمَّهٗ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۗوَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَ', 'laqad kafara <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>lmasii<u>h</u>u ibnu maryama qul faman yamliku mina <strong>al</strong>l<u>aa</u>hi syay-an in ar<u>aa</u>da an yuhlika <strong>a</strong>lmasii<u>h</u>a ibna maryama waummahu waman fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an walill<u>aa</u>hi mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> yakhluqu m<u>aa</u> yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Sungguh, telah kafir orang yang berkata, “Sesungguhnya Allah itu dialah Al-Masih putra Maryam.” Katakanlah (Muhammad), “Siapakah yang dapat menghalang-halangi kehendak Allah, jika Dia hendak membinasakan Al-Masih putra Maryam beserta ibunya dan seluruh (manusia) yang berada di bumi?” Dan milik Allah-lah kerajaan langit dan bumi dan apa yang ada di antara keduanya. Dia menciptakan apa yang Dia Kehendaki. Dan Allah Mahakuasa atas segala sesuatu.'),
	(687, 5, 18, 'وَقَالَتِ الْيَهُوْدُ وَالنَّصٰرٰى نَحْنُ اَبْنٰۤؤُ اللّٰهِ وَاَحِبَّاۤؤُهٗ ۗ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوْبِكُمْ ۗ بَلْ اَنْتُمْ بَشَرٌ مِّمَّنْ خَلَقَۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُۗ وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ و', 'waq<u>aa</u>lati <strong>a</strong>lyahuudu wa<strong>al</strong>nna<u>shaa</u>r<u>aa</u> na<u>h</u>nu abn<u>aa</u>u <strong>al</strong>l<u>aa</u>hi wa-a<u>h</u>ibb<u>aa</u>uhu qul falima yu\'a<u>dzdz</u>ibukum bi<u>dz</u>unuubikum bal antum basyarun mimman khalaqa yaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u walill<u>aa</u>hi mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> wa-ilayhi <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Orang Yahudi dan Nasrani berkata, “Kami adalah anak-anak Allah dan kekasih-kekasih-Nya.” Katakanlah, “Mengapa Allah menyiksa kamu karena dosa-dosamu? Tidak, kamu adalah manusia (biasa) di antara orang-orang yang Dia ciptakan. Dia mengampuni siapa yang Dia kehendaki dan menyiksa siapa yang Dia kehendaki. Dan milik Allah seluruh kerajaan langit dan bumi serta apa yang ada di antara keduanya. Dan kepada-Nya semua akan kembali.”'),
	(688, 5, 19, 'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِّنَ الرُّسُلِ اَنْ تَقُوْلُوْا مَا جَاۤءَنَا مِنْۢ بَشِيْرٍ وَّلَا نَذِيْرٍۗ فَقَدْ جَاۤءَكُمْ بَشِيْرٌ وَّنَذِيْرٌ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ', 'y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi qad j<u>aa</u>-akum rasuulun<u>aa</u> yubayyinu lakum \'al<u>aa</u> fatratin mina <strong>al</strong>rrusuli an taquuluu m<u>aa</u> j<u>aa</u>-an<u>aa</u> min basyiirin wal<u>aa</u> na<u>dz</u>iirin faqad j<u>aa</u>-akum basyiirun wana<u>dz</u>iirun wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Wahai Ahli Kitab! Sungguh, Rasul Kami telah datang kepadamu, menjelaskan (syariat Kami) kepadamu ketika terputus (pengiriman) rasul-rasul, agar kamu tidak mengatakan, “Tidak ada yang datang kepada kami baik seorang pembawa berita gembira maupun seorang pemberi peringatan.” Sungguh, telah datang kepadamu pembawa berita gembira dan pemberi peringatan. Dan Allah Mahakuasa atas segala sesuatu.'),
	(689, 5, 20, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ فِيْكُمْ اَنْۢبِيَاۤءَ وَجَعَلَكُمْ مُّلُوْكًاۙ وَّاٰتٰىكُمْ مَّا لَمْ يُؤْتِ اَحَدًا مِّنَ الْعٰلَمِيْنَ', 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi y<u>aa</u> qawmi u<u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum i<u>dz</u> ja\'ala fiikum anbiy<u>aa</u>-a waja\'alakum muluukan wa<u>aa</u>t<u>aa</u>kum m<u>aa</u> lam yu/ti a<u>h</u>adan mina <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Wahai kaumku! Ingatlah akan nikmat Allah kepadamu ketika Dia mengangkat nabi-nabi di antaramu, dan menjadikan kamu sebagai orang-orang merdeka, dan memberikan kepada kamu apa yang belum pernah diberikan kepada seorang pun di antara umat yang lain.”'),
	(690, 5, 21, 'يٰقَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّتِيْ كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوْا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ', 'y<u>aa</u> qawmi udkhuluu <strong>a</strong>l-ar<u>dh</u>a <strong>a</strong>lmuqaddasata <strong>al</strong>latii kataba <strong>al</strong>l<u>aa</u>hu lakum wal<u>aa</u> tartadduu \'al<u>aa</u> adb<u>aa</u>rikum fatanqalibuu kh<u>aa</u>siriin<strong>a</strong>', 'Wahai kaumku! Masuklah ke tanah suci (Palestina) yang telah ditentukan Allah bagimu, dan janganlah kamu berbalik ke belakang (karena takut kepada musuh), nanti kamu menjadi orang yang rugi.'),
	(691, 5, 22, 'قَالُوْا يٰمُوْسٰٓى اِنَّ فِيْهَا قَوْمًا جَبَّارِيْنَۖ وَاِنَّا لَنْ نَّدْخُلَهَا حَتّٰى يَخْرُجُوْا مِنْهَاۚ فَاِنْ يَّخْرُجُوْا مِنْهَا فَاِنَّا دٰخِلُوْنَ', 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> inna fiih<u>aa</u> qawman jabb<u>aa</u>riina wa-inn<u>aa</u> lan nadkhulah<u>aa</u> <u>h</u>att<u>aa</u> yakhrujuu minh<u>aa</u> fa-in yakhrujuu minh<u>aa</u> fa-inn<u>aa</u> d<u>aa</u>khiluun<strong>a</strong>', 'Mereka berkata, “Wahai Musa! Sesungguhnya di dalam negeri itu ada orang-orang yang sangat kuat dan kejam, kami tidak akan memasukinya sebelum mereka keluar darinya. Jika mereka keluar dari sana, niscaya kami akan masuk.”'),
	(692, 5, 23, 'قَالَ رَجُلَانِ مِنَ الَّذِيْنَ يَخَافُوْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوْا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوْهُ فَاِنَّكُمْ غٰلِبُوْنَ ەۙ وَعَلَى اللّٰهِ فَتَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'q<u>aa</u>la rajul<u>aa</u>ni mina <strong>al</strong>la<u>dz</u>iina yakh<u>aa</u>fuuna an\'ama <strong>al</strong>l<u>aa</u>hu \'alayhim<u>aa</u> udkhuluu \'alayhimu <strong>a</strong>lb<u>aa</u>ba fa-i<u>dzaa</u> dakhaltumuuhu fa-innakum gh<u>aa</u>libuuna wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi fatawakkaluu in kuntum mu/miniin<strong>a</strong>', 'Berkatalah dua orang laki-laki di antara mereka yang bertakwa, yang telah diberi nikmat oleh Allah, “Serbulah mereka melalui pintu gerbang (negeri) itu. Jika kamu memasukinya niscaya kamu akan menang. Dan bertawakallah kamu hanya kepada Allah, jika kamu orang-orang beriman.”'),
	(693, 5, 24, 'قَالُوْا يٰمُوْسٰٓى اِنَّا لَنْ نَّدْخُلَهَآ اَبَدًا مَّا دَامُوْا فِيْهَا ۖفَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَآ اِنَّا هٰهُنَا قٰعِدُوْنَ', 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> inn<u>aa</u> lan nadkhulah<u>aa</u> abadan m<u>aa</u> d<u>aa</u>muu fiih<u>aa</u> fa-i<u>dz</u>hab anta warabbuka faq<u>aa</u>til<u>aa</u> inn<u>aa</u> h<u>aa</u>hun<u>aa</u> q<u>aa</u>\'iduun<strong>a</strong>', 'Mereka berkata, “Wahai Musa! Sampai kapan pun kami tidak akan memasukinya selama mereka masih ada di dalamnya, karena itu pergilah engkau bersama Tuhanmu, dan berperanglah kamu berdua. Biarlah kami tetap (menanti) di sini saja.”'),
	(694, 5, 25, 'قَالَ رَبِّ اِنِّيْ لَآ اَمْلِكُ اِلَّا نَفْسِيْ وَاَخِيْ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفٰسِقِيْنَ', 'q<u>aa</u>la rabbi innii l<u>aa</u> amliku ill<u>aa</u> nafsii wa-akhii fa<strong>u</strong>fruq baynan<u>aa</u> wabayna <strong>a</strong>lqawmi <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Dia (Musa) berkata, “Ya Tuhanku, aku hanya menguasai diriku sendiri dan saudaraku. Sebab itu pisahkanlah antara kami dengan orang-orang yang fasik itu.”'),
	(695, 5, 26, 'قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَعِيْنَ سَنَةً  ۚيَتِيْهُوْنَ فِى الْاَرْضِۗ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفٰسِقِيْنَ ࣖ', 'q<u>aa</u>la fa-innah<u>aa</u> mu<u>h</u>arramatun \'alayhim arba\'iina sanatan yatiihuuna fii <strong>a</strong>l-ar<u>dh</u>i fal<u>aa</u> ta/sa \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', '(Allah) berfirman, “(Jika demikian), maka (negeri) itu terlarang buat mereka selama empat puluh tahun, (selama itu) mereka akan mengembara kebingungan di bumi. Maka janganlah eng-kau (Musa) bersedih hati (memikirkan nasib) orang-orang yang fasik itu.”'),
	(696, 5, 27, '۞ وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۘ اِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۗ قَالَ لَاَقْتُلَنَّكَ ۗ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّقِيْنَ', 'wa<strong>u</strong>tlu \'alayhim naba-a ibnay <u>aa</u>dama bi<strong>a</strong>l<u>h</u>aqqi i<u>dz</u> qarrab<u>aa</u> qurb<u>aa</u>nan fatuqubbila min a<u>h</u>adihim<u>aa</u> walam yutaqabbal mina <strong>a</strong>l-<u>aa</u>khari q<u>aa</u>la la-aqtulannaka q<u>aa</u>la innam<u>aa</u> yataqabbalu <strong>al</strong>l<u>aa</u>hu mina <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Dan ceritakanlah (Muhammad) yang sebenarnya kepada mereka tentang kisah kedua putra Adam, ketika keduanya mempersembahkan kurban, maka (kurban) salah seorang dari mereka berdua (Habil) diterima dan dari yang lain (Qabil) tidak diterima. Dia (Qabil) berkata, “Sungguh, aku pasti membunuhmu!” Dia (Habil) berkata, “Sesungguhnya Allah hanya menerima (amal) dari orang yang bertakwa.”'),
	(697, 5, 28, 'لَىِٕنْۢ بَسَطْتَّ اِلَيَّ يَدَكَ لِتَقْتُلَنِيْ مَآ اَنَا۠ بِبَاسِطٍ يَّدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ', 'la-in basa<u>th</u>ta ilayya yadaka litaqtulanii m<u>aa</u> an<u>aa</u> bib<u>aa</u>si<u>th</u>in yadiya ilayka li-aqtulaka innii akh<u>aa</u>fu <strong>al</strong>l<u>aa</u>ha rabba <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', '”Sungguh, jika engkau (Qabil) menggerakkan tanganmu kepadaku untuk membunuhku, aku tidak akan menggerakkan tanganku kepadamu untuk membunuhmu. Aku takut kepada Allah, Tuhan seluruh alam.”'),
	(698, 5, 29, 'اِنِّيْٓ اُرِيْدُ اَنْ تَبُوْۤاَ بِاِثْمِيْ وَاِثْمِكَ فَتَكُوْنَ مِنْ اَصْحٰبِ النَّارِۚ وَذٰلِكَ جَزَاۤؤُا الظّٰلِمِيْنَۚ ', 'innii uriidu an tabuu-a bi-itsmii wa-itsmika fatakuuna min a<u>sh</u>-<u>haa</u>bi <strong>al</strong>nn<u>aa</u>ri wa<u>dzaa</u>lika jaz<u>aa</u>u <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', '”Sesungguhnya aku ingin agar engkau kembali dengan (membawa) dosa (membunuh)ku dan dosamu sendiri, maka engkau akan menjadi penghuni neraka; dan itulah balasan bagi orang yang zalim.”'),
	(699, 5, 30, 'فَطَوَّعَتْ لَهٗ نَفْسُهٗ قَتْلَ اَخِيْهِ فَقَتَلَهٗ فَاَصْبَحَ مِنَ الْخٰسِرِيْنَ ', 'fa<u>th</u>awwa\'at lahu nafsuhu qatla akhiihi faqatalahu fa-a<u>sh</u>ba<u>h</u>a mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Maka nafsu (Qabil) mendorongnya untuk membunuh saudaranya, kemudian dia pun (benar-benar) membunuhnya, maka jadilah dia termasuk orang yang rugi.'),
	(700, 5, 31, 'فَبَعَثَ اللّٰهُ غُرَابًا يَّبْحَثُ فِى الْاَرْضِ لِيُرِيَهٗ كَيْفَ يُوَارِيْ سَوْءَةَ اَخِيْهِ ۗ قَالَ يٰوَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُوْنَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْءَةَ اَخِيْۚ  فَاَصْبَحَ مِنَ النّٰدِمِيْنَ ۛ', 'faba\'atsa <strong>al</strong>l<u>aa</u>hu ghur<u>aa</u>ban yab<u>h</u>atsu fii <strong>a</strong>l-ar<u>dh</u>i liyuriyahu kayfa yuw<u>aa</u>rii saw-ata akhiihi q<u>aa</u>la y<u>aa</u> waylat<u>aa</u> a\'ajaztu an akuuna mitsla h<u>aadzaa</u> <strong>a</strong>lghur<u>aa</u>bi fauw<u>aa</u>riya saw-ata akhii fa-a<u>sh</u>ba<u>h</u>a mina <strong>al</strong>nn<u>aa</u>dimiin<strong>a</strong>', 'Kemudian Allah mengutus seekor burung gagak menggali tanah untuk diperlihatkan kepadanya (Qabil). Bagaimana dia seharusnya menguburkan mayat saudaranya. Qabil berkata, “Oh, celaka aku! Mengapa aku tidak mampu berbuat seperti burung gagak ini, sehingga aku dapat menguburkan mayat saudaraku ini?” Maka jadilah dia termasuk orang yang menyesal.'),
	(701, 5, 32, 'مِنْ اَجْلِ ذٰلِكَ ۛ كَتَبْنَا عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَنَّهٗ مَنْ قَتَلَ نَفْسًاۢ بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِى الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَمِيْعًاۗ وَمَنْ اَحْيَاهَا فَكَاَنَّمَآ اَحْيَا النَّاسَ جَمِيْعًا ۗوَلَقَدْ جَاۤءَتْهُمْ ر', 'min ajli <u>dzaa</u>lika katabn<u>aa</u> \'al<u>aa</u> banii isr<u>aa</u>-iila annahu man qatala nafsan bighayri nafsin aw fas<u>aa</u>din fii <strong>a</strong>l-ar<u>dh</u>i faka-annam<u>aa</u> qatala <strong>al</strong>nn<u>aa</u>sa jamii\'an waman a<u>h</u>y<u>aa</u>h<u>aa</u> faka-annam<u>aa</u> a<u>h</u>y<u>aa</u> <strong>al</strong>nn<u>aa</u>sa jamii\'an walaqad j<u>aa</u>-at-hum rusulun<u>aa</u> bi<strong>a</strong>lbayyin<u>aa</u>ti tsumma inna katsiiran minhum ba\'da <u>dzaa</u>lika fii <strong>a</strong>l-ar<u>dh</u>i lamusrifuun<strong>a</strong>', 'Oleh karena itu Kami tetapkan (suatu hukum) bagi Bani Israil, bahwa barangsiapa membunuh seseorang, bukan karena orang itu membunuh orang lain, atau bukan karena berbuat kerusakan di bumi, maka seakan-akan dia telah membunuh semua manusia. Barangsiapa memelihara kehidupan seorang manusia, maka seakan-akan dia telah memelihara kehidupan semua manusia. Sesungguhnya Rasul Kami telah datang kepada mereka dengan (membawa) keterangan-keterangan yang jelas. Tetapi kemudian banyak di antara mereka setelah itu melampaui batas di bumi.'),
	(702, 5, 33, 'اِنَّمَا جَزٰۤؤُا الَّذِيْنَ يُحَارِبُوْنَ اللّٰهَ وَرَسُوْلَهٗ وَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًا اَنْ يُّقَتَّلُوْٓا اَوْ يُصَلَّبُوْٓا اَوْ تُقَطَّعَ اَيْدِيْهِمْ وَاَرْجُلُهُمْ مِّنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۗ ذٰلِكَ لَهُمْ خِزْيٌ فِى الد', 'innam<u>aa</u> jaz<u>aa</u>u <strong>al</strong>la<u>dz</u>iina yu<u>haa</u>ribuuna <strong>al</strong>l<u>aa</u>ha warasuulahu wayas\'awna fii <strong>a</strong>l-ar<u>dh</u>i fas<u>aa</u>dan an yuqattaluu aw yu<u>sh</u>allabuu aw tuqa<u>ththh</u>a\'a aydiihim wa-arjuluhum min khil<u>aa</u>fin aw yunfaw mina <strong>a</strong>l-ar<u>dh</u>i <u>dzaa</u>lika lahum khizyun fii <strong>al</strong>dduny<u>aa</u> walahum fii <strong>a</strong>l-<u>aa</u>khirati \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Hukuman bagi orang-orang yang memerangi Allah dan Rasul-Nya dan membuat kerusakan di bumi hanyalah dibunuh atau disalib, atau dipotong tangan dan kaki mereka secara silang, atau diasingkan dari tempat kediamannya. Yang demikian itu kehinaan bagi mereka di dunia, dan di akhirat mereka mendapat azab yang besar.'),
	(703, 5, 34, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْ قَبْلِ اَنْ تَقْدِرُوْا عَلَيْهِمْۚ فَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina t<u>aa</u>buu min qabli an taqdiruu \'alayhim fa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Kecuali orang-orang yang bertobat sebelum kamu dapat menguasai mereka; maka ketahuilah, bahwa Allah Maha Pengampun, Maha Penyayang.'),
	(704, 5, 35, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُوْٓا اِلَيْهِ الْوَسِيْلَةَ وَجَاهِدُوْا فِيْ سَبِيْلِهٖ لَعَلَّكُمْ تُفْلِحُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ittaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>btaghuu ilayhi <strong>a</strong>lwasiilata waj<u>aa</u>hiduu fii sabiilihi la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan carilah wasilah (jalan) untuk mendekatkan diri kepada-Nya, dan berjihadlah (berjuanglah) di jalan-Nya, agar kamu beruntung.'),
	(705, 5, 36, 'اِنَّ الَّذِيْنَ كَفَرُوْا لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لِيَفْتَدُوْا بِهٖ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْ ۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu law anna lahum m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an wamitslahu ma\'ahu liyaftaduu bihi min \'a<u>dzaa</u>bi yawmi <strong>a</strong>lqiy<u>aa</u>mati m<u>aa</u> tuqubbila minhum walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sesungguhnya orang-orang yang kafir, seandainya mereka memiliki segala apa yang ada di bumi dan ditambah dengan sebanyak itu (lagi) untuk menebus diri mereka dari azab pada hari Kiamat, niscaya semua (tebusan) itu tidak akan diterima dari mereka. Mereka (tetap) mendapat azab yang pedih.'),
	(706, 5, 37, 'يُرِيْدُوْنَ اَنْ يَّخْرُجُوْا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِيْنَ مِنْهَا ۖوَلَهُمْ عَذَابٌ مُّقِيمٌ', 'yuriiduuna an yakhrujuu mina <strong>al</strong>nn<u>aa</u>ri wam<u>aa</u> hum bikh<u>aa</u>rijiina minh<u>aa</u> walahum \'a<u>dzaa</u>bun muqiim<strong>un</strong>', 'Mereka ingin keluar dari neraka, tetapi tidak akan dapat keluar dari sana. Dan mereka mendapat azab yang kekal.'),
	(707, 5, 38, 'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوْٓا اَيْدِيَهُمَا جَزَاۤءًۢ بِمَا كَسَبَا نَكَالًا مِّنَ اللّٰهِ ۗوَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ', 'wa<strong>al</strong>ss<u>aa</u>riqu wa<strong>al</strong>ss<u>aa</u>riqatu fa<strong>i</strong>q<u>th</u>a\'uu aydiyahum<u>aa</u> jaz<u>aa</u>-an bim<u>aa</u> kasab<u>aa</u> nak<u>aa</u>lan mina <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>h</u>akiim<strong>un</strong>', 'Adapun orang laki-laki maupun perempuan yang mencuri, potonglah tangan keduanya (sebagai) balasan atas perbuatan yang mereka lakukan dan sebagai siksaan dari Allah. Dan Allah Mahaperkasa, Mahabijaksana.'),
	(708, 5, 39, 'فَمَنْ تَابَ مِنْۢ بَعْدِ ظُلْمِهٖ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوْبُ عَلَيْهِ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'faman t<u>aa</u>ba min ba\'di <u>zh</u>ulmihi wa-a<u>sh</u>la<u>h</u>a fa-inna <strong>al</strong>l<u>aa</u>ha yatuubu \'alayhi inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Tetapi barangsiapa bertobat setelah melakukan kejahatan itu dan memperbaiki diri, maka sesungguhnya Allah menerima tobatnya. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(709, 5, 40, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَغْفِرُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'alam ta\'lam anna <strong>al</strong>l<u>aa</u>ha lahu mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i yu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wayaghfiru liman yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Tidakkah kamu tahu, bahwa Allah memiliki seluruh kerajaan langit dan bumi, Dia menyiksa siapa yang Dia kehendaki dan mengampuni siapa yang Dia kehendaki. Allah Mahakuasa atas segala sesuatu.'),
	(710, 5, 41, '۞ يٰٓاَيُّهَا الرَّسُوْلُ لَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِ مِنَ الَّذِيْنَ قَالُوْٓا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوْبُهُمْ ۛ وَمِنَ الَّذِيْنَ هَادُوْا ۛ سَمّٰعُوْنَ لِلْكَذِبِ سَمّٰعُوْنَ لِقَوْمٍ اٰخَرِيْنَۙ لَمْ يَأ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>rrasuulu l<u>aa</u> ya<u>h</u>zunka <strong>al</strong>la<u>dz</u>iina yus<u>aa</u>ri\'uuna fii <strong>a</strong>lkufri mina <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu <u>aa</u>mann<u>aa</u> bi-afw<u>aa</u>hihim walam tu/min quluubuhum wamina <strong>al</strong>la<u>dz</u>iina h<u>aa</u>duu samm<u>aa</u>\'uuna lilka<u>dz</u>ibi samm<u>aa</u>\'uuna liqawmin <u>aa</u>khariina lam ya/tuuka yu<u>h</u>arrifuuna <strong>a</strong>lkalima min ba\'di maw<u>aad</u>i\'ihi yaquuluuna in uutiitum h<u>aadzaa</u> fakhu<u>dz</u>uuhu wa-in lam tu/tawhu fa<strong>i</strong><u>hts</u>aruu waman yuridi <strong>al</strong>l<u>aa</u>hu fitnatahu falan tamlika lahu mina <strong>al</strong>l<u>aa</u>hi syay-an ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina lam yuridi <strong>al</strong>l<u>aa</u>hu an yu<u>th</u>ahhira quluubahum lahum fii <strong>al</strong>dduny<u>aa</u> khizyun walahum fii <strong>a</strong>l-<u>aa</u>khirati \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Wahai Rasul (Muhammad)! Janganlah engkau disedihkan karena mereka berlomba-lomba dalam kekafirannya. Yaitu orang-orang (munafik) yang mengatakan dengan mulut mereka, “Kami telah beriman,” padahal hati mereka belum beriman; dan juga orang-orang Yahudi yang sangat suka mendengar (berita-berita) bohong dan sangat suka mendengar (perkataan-perkataan) orang lain yang belum pernah datang kepadamu. Mereka mengubah kata-kata (Taurat) dari makna yang sebenarnya. Mereka mengatakan, “Jika ini yang diberikan kepadamu (yang sudah diubah) terimalah, dan jika kamu diberi yang bukan ini, maka hati-hatilah.” Barangsiapa dikehendaki Allah untuk dibiarkan sesat, sedikit pun engkau tidak akan mampu menolak sesuatu pun dari Allah (untuk menolongnya). Mereka itu adalah orang-orang yang sudah tidak dikehendaki Allah untuk menyucikan hati mereka. Di dunia mereka mendapat kehinaan dan di akhirat akan mendapat azab yang besar.'),
	(711, 5, 42, 'سَمّٰعُوْنَ لِلْكَذِبِ اَكّٰلُوْنَ لِلسُّحْتِۗ فَاِنْ جَاۤءُوْكَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْ ۚوَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَّضُرُّوْكَ شَيْـًٔا ۗ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ', 'samm<u>aa</u>\'uuna lilka<u>dz</u>ibi akk<u>aa</u>luuna li<strong>l</strong>ssu<u>h</u>ti fa-in j<u>aa</u>uuka fa<strong>u</strong><u>h</u>kum baynahum aw a\'ri<u>dh</u> \'anhum wa-in tu\'ri<u>dh</u> \'anhum falan ya<u>dh</u>urruuka syay-an wa-in <u>h</u>akamta fa<strong>u</strong><u>h</u>kum baynahum bi<strong>a</strong>lqis<u>th</u>i inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmuqsi<u>th</u>iin<strong>a</strong>', 'Mereka sangat suka mendengar berita bohong, banyak memakan (makanan) yang haram. Jika mereka (orang Yahudi) datang kepadamu (Muhammad untuk meminta putusan), maka berilah putusan di antara mereka atau berpalinglah dari mereka, dan jika engkau berpaling dari mereka maka mereka tidak akan membahayakanmu sedikit pun. Tetapi jika engkau memutuskan (perkara mereka), maka putuskanlah dengan adil. Sesungguhnya Allah menyukai orang-orang yang adil.'),
	(712, 5, 43, 'وَكَيْفَ يُحَكِّمُوْنَكَ وَعِنْدَهُمُ التَّوْرٰىةُ فِيْهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْۢ بَعْدِ ذٰلِكَ ۗوَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ ࣖ ', 'wakayfa yu<u>h</u>akkimuunaka wa\'indahumu <strong>al</strong>ttawr<u>aa</u>tu fiih<u>aa</u> <u>h</u>ukmu <strong>al</strong>l<u>aa</u>hi tsumma yatawallawna min ba\'di <u>dzaa</u>lika wam<u>aa</u> ul<u>aa</u>-ika bi<strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan bagaimana mereka akan mengangkatmu menjadi hakim mereka, padahal mereka mempunyai Taurat yang di dalamnya (ada) hukum Allah, nanti mereka berpaling (dari putusanmu) setelah itu? Sungguh, mereka bukan orang-orang yang beriman.'),
	(713, 5, 44, 'اِنَّآ اَنْزَلْنَا التَّوْرٰىةَ فِيْهَا هُدًى وَّنُوْرٌۚ يَحْكُمُ بِهَا النَّبِيُّوْنَ الَّذِيْنَ اَسْلَمُوْا لِلَّذِيْنَ هَادُوْا وَالرَّبَّانِيُّوْنَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوْا مِنْ كِتٰبِ اللّٰهِ وَكَانُوْا عَلَيْهِ شُهَدَاۤءَۚ فَلَا تَخْشَوُ', 'inn<u>aa</u> anzaln<u>aa</u> <strong>al</strong>ttawr<u>aa</u>ta fiih<u>aa</u> hudan wanuurun ya<u>h</u>kumu bih<u>aa</u> <strong>al</strong>nnabiyyuuna <strong>al</strong>la<u>dz</u>iina aslamuu lilla<u>dz</u>iina h<u>aa</u>duu wa<strong>al</strong>rrabb<u>aa</u>niyyuuna wa<strong>a</strong>l-a<u>h</u>b<u>aa</u>ru bim<u>aa</u> istu<u>h</u>fi<u>zh</u>uu min kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi wak<u>aa</u>nuu \'alayhi syuhad<u>aa</u>-a fal<u>aa</u> takhsyawuu <strong>al</strong>nn<u>aa</u>sa wa<strong>i</strong>khsyawni wal<u>aa</u> tasytaruu bi-<u>aa</u>y<u>aa</u>tii tsamanan qaliilan waman lam ya<u>h</u>kum bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu faul<u>aa</u>-ika humu <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Sungguh, Kami yang menurunkan Kitab Taurat; di dalamnya (ada) petunjuk dan cahaya. Yang dengan Kitab itu para nabi yang berserah diri kepada Allah memberi putusan atas perkara orang Yahudi, demikian juga para ulama dan pendeta-pendeta mereka, sebab mereka diperintahkan memelihara kitab-kitab Allah dan mereka menjadi saksi terhadapnya. Karena itu janganlah kamu takut kepada manusia, (tetapi) takutlah kepada-Ku. Dan janganlah kamu jual ayat-ayat-Ku dengan harga murah. Barangsiapa tidak memutuskan dengan apa yang diturunkan Allah, maka mereka itulah orang-orang kafir.'),
	(714, 5, 45, 'وَكَتَبْنَا عَلَيْهِمْ فِيْهَآ اَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوْحَ قِصَاصٌۗ فَمَنْ تَصَدَّقَ بِهٖ فَهُوَ كَفَّارَةٌ لَّهٗ  ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ ', 'wakatabn<u>aa</u> \'alayhim fiih<u>aa</u> anna <strong>al</strong>nnafsa bi<strong>al</strong>nnafsi wa<strong>a</strong>l\'ayna bi<strong>a</strong>l\'ayni wa<strong>a</strong>l-anfa bi<strong>a</strong>l-anfi wa<strong>a</strong>lu<u>dz</u>una bi<strong>a</strong>lu<u>dz</u>uni wa<strong>al</strong>ssinna bi<strong>al</strong>ssinni wa<strong>a</strong>ljuruu<u>h</u>a qi<u>sas</u>un faman ta<u>sh</u>addaqa bihi fahuwa kaff<u>aa</u>ratun lahu waman lam ya<u>h</u>kum bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu faul<u>aa</u>-ika humu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Kami telah menetapkan bagi mereka di dalamnya (Taurat) bahwa nyawa (dibalas) dengan nyawa, mata dengan mata, hidung dengan hidung, telinga dengan telinga, gigi dengan gigi, dan luka-luka (pun) ada qisas-nya (balasan yang sama). Barangsiapa melepaskan (hak qisas)nya, maka itu (menjadi) penebus dosa baginya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itulah orang-orang zalim.'),
	(715, 5, 46, 'وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِعِيْسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ ۖواٰتَيْنٰهُ الْاِنْجِيْلَ فِيْهِ هُدًى وَّنُوْرٌۙ وَّمُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ وَهُدًى وَّمَوْعِظَةً لِّلْمُتَّقِيْنَۗ ', 'waqaffayn<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim bi\'iis<u>aa</u> ibni maryama mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina <strong>al</strong>ttawr<u>aa</u>ti wa<u>aa</u>tayn<u>aa</u>hu <strong>a</strong>l-injiila fiihi hudan wanuurun wamu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina <strong>al</strong>ttawr<u>aa</u>ti wahudan wamaw\'i<u>zh</u>atan lilmuttaqiin<strong>a</strong>', 'Dan Kami teruskan jejak mereka dengan mengutus Isa putra Maryam, membenarkan Kitab yang sebelumnya, yaitu Taurat. Dan Kami menurunkan Injil kepadanya, di dalamnya terdapat petunjuk dan cahaya, dan membenarkan Kitab yang sebelumnya yaitu Taurat, dan sebagai petunjuk serta pengajaran untuk orang-orang yang bertakwa.'),
	(716, 5, 47, 'وَلْيَحْكُمْ اَهْلُ الْاِنْجِيْلِ بِمَآ اَنْزَلَ اللّٰهُ فِيْهِۗ وَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ', 'walya<u>h</u>kum ahlu <strong>a</strong>l-injiili bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu fiihi waman lam ya<u>h</u>kum bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu faul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Dan hendaklah pengikut Injil memutuskan perkara menurut apa yang diturunkan Allah di dalamnya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itulah orang-orang fasik.'),
	(717, 5, 48, 'وَاَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ الْكِتٰبِ وَمُهَيْمِنًا عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ عَمَّا جَاۤءَكَ مِنَ الْحَقِّۗ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْ', 'wa-anzaln<u>aa</u> ilayka <strong>a</strong>lkit<u>aa</u>ba bi<strong>a</strong>l<u>h</u>aqqi mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina <strong>a</strong>lkit<u>aa</u>bi wamuhayminan \'alayhi fa<strong>u</strong><u>h</u>kum baynahum bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu wal<u>aa</u> tattabi\' ahw<u>aa</u>-ahum \'amm<u>aa</u> j<u>aa</u>-aka mina <strong>a</strong>l<u>h</u>aqqi likullin ja\'aln<u>aa</u> minkum syir\'atan waminh<u>aa</u>jan walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu laja\'alakum ummatan w<u>aah</u>idatan wal<u>aa</u>kin liyabluwakum fiim<u>aa</u> <u>aa</u>t<u>aa</u>kum fa<strong>i</strong>stabiquu <strong>a</strong>lkhayr<u>aa</u>ti il<u>aa</u> <strong>al</strong>l<u>aa</u>hi marji\'ukum jamii\'an fayunabbi-ukum bim<u>aa</u> kuntum fiihi takhtalifuun<strong>a</strong>', 'Dan Kami telah menurunkan Kitab (Al-Qur\'an) kepadamu (Muhammad) dengan membawa kebenaran, yang membenarkan kitab-kitab yang diturunkan sebelumnya dan menjaganya, maka putuskanlah perkara mereka menurut apa yang diturunkan Allah dan janganlah  engkau mengikuti keinginan mereka dengan meninggalkan kebenaran yang telah datang kepadamu. Untuk setiap umat di antara kamu, Kami berikan aturan dan jalan yang terang. Kalau Allah menghendaki, niscaya kamu dijadikan-Nya satu umat (saja), tetapi Allah hendak menguji kamu terhadap karunia yang telah diberikan-Nya kepadamu, maka berlomba-lombalah berbuat kebajikan. Hanya kepada Allah kamu semua kembali, lalu diberitahukan-Nya kepadamu terhadap apa yang dahulu kamu perselisihkan,'),
	(718, 5, 49, 'وَاَنِ احْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ وَاحْذَرْهُمْ اَنْ يَّفْتِنُوْكَ عَنْۢ بَعْضِ مَآ اَنْزَلَ اللّٰهُ اِلَيْكَۗ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّصِيْبَهُمْ بِبَعْضِ ذُنُوْبِهِمْ ۗ', 'wa-ani u<u>h</u>kum baynahum bim<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu wal<u>aa</u> tattabi\' ahw<u>aa</u>-ahum wa<strong>i</strong><u>hts</u>arhum an yaftinuuka \'an ba\'<u>dh</u>i m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu ilayka fa-in tawallaw fa<strong>i</strong>\'lam annam<u>aa</u> yuriidu <strong>al</strong>l<u>aa</u>hu an yu<u>sh</u>iibahum biba\'<u>dh</u>i <u>dz</u>unuubihim wa-inna katsiiran mina <strong>al</strong>nn<u>aa</u>si laf<u>aa</u>siquun<strong>a</strong>', 'dan hendaklah engkau memutuskan perkara di antara mereka menurut apa yang diturunkan Allah, dan janganlah engkau mengikuti keinginan mereka. Dan waspadalah terhadap mereka, jangan sampai mereka memperdayakan engkau terhadap sebagian apa yang telah diturunkan Allah kepadamu. Jika mereka berpaling (dari hukum yang telah diturunkan Allah), maka ketahuilah bahwa sesungguhnya Allah berkehendak menimpakan musibah kepada mereka disebabkan sebagian dosa-dosa mereka. Dan sungguh, kebanyakan manusia adalah orang-orang yang fasik.'),
	(719, 5, 50, 'اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُوْنَۗ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْمًا لِّقَوْمٍ يُّوْقِنُوْنَ ࣖ ', 'afa<u>h</u>ukma <strong>a</strong>lj<u>aa</u>hiliyyati yabghuuna waman a<u>h</u>sanu mina <strong>al</strong>l<u>aa</u>hu <u>h</u>ukman liqawmin yuuqinuun<strong>a</strong>', 'Apakah hukum Jahiliah yang mereka kehendaki? (Hukum) siapakah yang lebih baik daripada (hukum) Allah bagi orang-orang yang meyakini (agamanya)?'),
	(720, 5, 51, '۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْيَهُوْدَ وَالنَّصٰرٰٓى اَوْلِيَاۤءَ ۘ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاِنَّهٗ مِنْهُمْ ۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu <strong>a</strong>lyahuuda wa<strong>al</strong>nna<u>shaa</u>r<u>aa</u> awliy<u>aa</u>-a ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in waman yatawallahum minkum fa-innahu minhum inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu menjadikan orang Yahudi dan Nasrani sebagai teman setia(mu); mereka satu sama lain saling melindungi. Barangsiapa di antara kamu yang menjadikan mereka teman setia, maka sesungguhnya dia termasuk golongan mereka. Sungguh, Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
	(721, 5, 52, 'فَتَرَى الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يُّسَارِعُوْنَ فِيْهِمْ يَقُوْلُوْنَ نَخْشٰٓى اَنْ تُصِيْبَنَا دَاۤىِٕرَةٌ  ۗفَعَسَى اللّٰهُ اَنْ يَّأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِّنْ عِنْدِهٖ فَيُصْبِحُوْا عَلٰى مَآ اَسَرُّوْا فِيْٓ اَنْفُسِهِمْ نٰدِمِيْ', 'fatar<u>aa</u> <strong>al</strong>la<u>dz</u>iina fii quluubihim mara<u>dh</u>un yus<u>aa</u>ri\'uuna fiihim yaquuluuna nakhsy<u>aa</u> an tu<u>sh</u>iiban<u>aa</u> d<u>aa</u>-iratun fa\'as<u>aa</u> <strong>al</strong>l<u>aa</u>hu an ya/tiya bi<strong>a</strong>lfat<u>h</u>i aw amrin min \'indihi fayu<u>sh</u>bi<u>h</u>uu \'al<u>aa</u> m<u>aa</u> asarruu fii anfusihim n<u>aa</u>dimiin<strong>a</strong>', 'Maka kamu akan melihat orang-orang yang hatinya berpenyakit segera mendekati mereka (Yahudi dan Nasrani), seraya berkata, “Kami takut akan mendapat bencana.” Mudah-mudahan Allah akan mendatangkan kemenangan (kepada Rasul-Nya), atau suatu keputusan dari sisi-Nya, sehingga mereka menjadi menyesal terhadap apa yang mereka rahasiakan dalam diri mereka.'),
	(722, 5, 53, 'وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْٓا اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۗ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوْا خٰسِرِيْنَ', 'wayaquulu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ah<u>aa</u>ul<u>aa</u>-i <strong>al</strong>la<u>dz</u>iina aqsamuu bi<strong>al</strong>l<u>aa</u>hi jahda aym<u>aa</u>nihim innahum lama\'akum <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fa-a<u>sh</u>ba<u>h</u>uu kh<u>aa</u>siriin<strong>a</strong>', 'Dan orang-orang yang beriman akan berkata, “Inikah orang yang bersumpah secara sungguh-sungguh dengan (nama) Allah, bahwa mereka benar-benar beserta kamu?” Segala amal mereka menjadi sia-sia, sehingga mereka menjadi orang yang rugi.'),
	(723, 5, 54, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَنْ يَّرْتَدَّ مِنْكُمْ عَنْ دِيْنِهٖ فَسَوْفَ يَأْتِى اللّٰهُ بِقَوْمٍ يُّحِبُّهُمْ وَيُحِبُّوْنَهٗٓ  ۙاَذِلَّةٍ عَلَى الْمُؤْمِنِيْنَ اَعِزَّةٍ عَلَى الْكٰفِرِيْنَۖ  يُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَخَافُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu man yartadda minkum \'an diinihi fasawfa ya/tii <strong>al</strong>l<u>aa</u>hu biqawmin yu<u>h</u>ibbuhum wayu<u>h</u>ibbuunahu a<u>dz</u>illatin \'al<u>aa</u> <strong>a</strong>lmu/miniina a\'izzatin \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiina yuj<u>aa</u>hiduuna fii sabiili <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> yakh<u>aa</u>fuuna lawmata l<u>aa</u>-imin <u>dzaa</u>lika fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'un \'aliim<strong>un</strong>', 'Wahai orang-orang yang beriman! Barangsiapa di antara kamu yang murtad (keluar) dari agamanya, maka kelak Allah akan mendatangkan suatu kaum, Dia mencintai mereka dan mereka pun mencintai-Nya, dan bersikap lemah lembut terhadap orang-orang yang beriman, tetapi bersikap keras terhadap orang-orang kafir, yang berjihad di jalan Allah, dan yang tidak takut kepada celaan orang yang suka mencela. Itulah karunia Allah yang diberikan-Nya kepada siapa yang Dia kehendaki. Dan Allah Mahaluas (pemberian-Nya), Maha Mengetahui.'),
	(724, 5, 55, 'اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُوْلُهٗ وَالَّذِيْنَ اٰمَنُوا الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ رَاكِعُوْنَ', 'innam<u>aa</u> waliyyukumu <strong>al</strong>l<u>aa</u>hu warasuuluhu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu <strong>al</strong>la<u>dz</u>iina yuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wayu/tuuna <strong>al</strong>zzak<u>aa</u>ta wahum r<u>aa</u>ki\'uun<strong>a</strong>', 'Sesungguhnya penolongmu hanyalah Allah, Rasul-Nya, dan orang-orang yang beriman, yang melaksanakan salat dan menunaikan zakat, seraya tunduk (kepada Allah).'),
	(725, 5, 56, 'وَمَنْ يَّتَوَلَّ اللّٰهَ وَرَسُوْلَهٗ وَالَّذِيْنَ اٰمَنُوْا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغٰلِبُوْنَ ࣖ ', 'waman yatawalla <strong>al</strong>l<u>aa</u>ha warasuulahu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu fa-inna <u>h</u>izba <strong>al</strong>l<u>aa</u>hi humu <strong>a</strong>lgh<u>aa</u>libuun<strong>a</strong>', 'Dan barangsiapa menjadikan Allah, Rasul-Nya dan orang-orang yang beriman sebagai penolongnya, maka sungguh, pengikut (agama) Allah itulah yang menang.'),
	(726, 5, 57, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الَّذِيْنَ اتَّخَذُوْا دِيْنَكُمْ هُزُوًا وَّلَعِبًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَاۤءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu <strong>al</strong>la<u>dz</u>iina ittakha<u>dz</u>uu diinakum huzuwan wala\'iban mina <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba min qablikum wa<strong>a</strong>lkuff<u>aa</u>ra awliy<u>aa</u>-a wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha in kuntum mu/miniin<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu menjadikan pemimpinmu orang-orang yang membuat agamamu jadi bahan ejekan dan permainan, (yaitu) di antara orang-orang yang telah diberi kitab sebelummu dan orang-orang kafir (orang musyrik). Dan bertakwalah kepada Allah jika kamu orang-orang beriman.'),
	(727, 5, 58, 'وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوْهَا هُزُوًا وَّلَعِبًا ۗذٰلِكَ بِاَ نَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَ', 'wa-i<u>dzaa</u> n<u>aa</u>daytum il<u>aa</u> <strong>al</strong><u>shsh</u>al<u>aa</u>ti ittakha<u>dz</u>uuh<u>aa</u> huzuwan wala\'iban <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Dan apabila kamu menyeru (mereka) untuk (melaksanakan) salat, mereka menjadikannya bahan ejekan dan permainan. Yang demikian itu adalah karena mereka orang-orang yang tidak mengerti.'),
	(728, 5, 59, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ هَلْ تَنْقِمُوْنَ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فٰسِقُوْنَ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi hal tanqimuuna minn<u>aa</u> ill<u>aa</u> an <u>aa</u>mann<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wam<u>aa</u> unzila ilayn<u>aa</u> wam<u>aa</u> unzila min qablu wa-anna aktsarakum f<u>aa</u>siquun<strong>a</strong>', 'Katakanlah, “Wahai Ahli Kitab! Apakah kamu memandang kami salah, hanya karena kami beriman kepada Allah, kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan sebelumnya? Sungguh, kebanyakan dari kamu adalah orang-orang yang fasik.”'),
	(729, 5, 60, 'قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكَ مَثُوْبَةً عِنْدَ اللّٰهِ ۗمَنْ لَّعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيْرَ وَعَبَدَ الطَّاغُوْتَۗ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ عَنْ سَوَاۤءِ السَّبِيْلِ', 'qul hal unabbi-ukum bisyarrin min <u>dzaa</u>lika matsuubatan \'inda <strong>al</strong>l<u>aa</u>hi man la\'anahu <strong>al</strong>l<u>aa</u>hu wagha<u>dh</u>iba \'alayhi waja\'ala minhumu <strong>a</strong>lqiradata wa<strong>a</strong>lkhan<u>aa</u>ziira wa\'abada <strong>al</strong><u>ththaa</u>ghuuti ul<u>aa</u>-ika syarrun mak<u>aa</u>nan wa-a<u>dh</u>allu \'an saw<u>aa</u>-i <strong>al</strong>ssabiil<strong>i</strong>', 'Katakanlah (Muhammad), “Apakah akan aku beritakan kepadamu tentang orang yang lebih buruk pembalasannya dari (orang fasik) di sisi Allah? Yaitu, orang yang dilaknat dan dimurkai Allah, di antara mereka (ada) yang dijadikan kera dan babi dan (orang yang) menyembah Thaghut.” Mereka itu lebih buruk tempatnya dan lebih tersesat dari jalan yang lurus.'),
	(730, 5, 61, 'وَاِذَا جَاۤءُوْكُمْ قَالُوْٓا اٰمَنَّا وَقَدْ دَّخَلُوْا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوْا بِهٖ ۗوَاللّٰهُ اَعْلَمُ بِمَا كَانُوْا يَكْتُمُوْنَ', 'wa-i<u>dzaa</u> j<u>aa</u>uukum q<u>aa</u>luu <u>aa</u>mann<u>aa</u> waqad dakhaluu bi<strong>a</strong>lkufri wahum qad kharajuu bihi wa<strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> k<u>aa</u>nuu yaktumuun<strong>a</strong>', 'Dan apabila mereka (Yahudi atau munafik) datang kepadamu, mereka mengatakan, “Kami telah beriman,” padahal mereka datang kepadamu dengan kekafiran dan mereka pergi pun demikian; dan Allah lebih mengetahui apa yang mereka sembunyikan.'),
	(731, 5, 62, 'وَتَرٰى كَثِيْرًا مِّنْهُمْ يُسَارِعُوْنَ فِى الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۗ  لَبِئْسَ مَا كَانُوْا يَعْمَلُوْنَ', 'watar<u>aa</u> katsiiran minhum yus<u>aa</u>ri\'uuna fii <strong>a</strong>l-itsmi wa<strong>a</strong>l\'udw<u>aa</u>ni wa-aklihimu <strong>al</strong>ssu<u>h</u>ta labi/sa m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan kamu akan melihat banyak di antara mereka (orang Yahudi) berlomba dalam berbuat dosa, permusuhan dan memakan yang haram. Sungguh, sangat buruk apa yang mereka perbuat.'),
	(732, 5, 63, 'لَوْلَا يَنْهٰىهُمُ الرَّبَّانِيُّوْنَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۗ  لَبِئْسَ مَا كَانُوْا يَصْنَعُوْنَ', 'lawl<u>aa</u> yanh<u>aa</u>humu <strong>al</strong>rrabb<u>aa</u>niyyuuna wa<strong>a</strong>l-a<u>h</u>b<u>aa</u>ru \'an qawlihimu <strong>a</strong>l-itsma wa-aklihimu <strong>al</strong>ssu<u>h</u>ta labi/sa m<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun<strong>a</strong>', 'Mengapa para ulama dan para pendeta mereka tidak melarang mereka mengucapkan perkataan bohong dan memakan yang haram? Sungguh, sangat buruk apa yang mereka perbuat.'),
	(733, 5, 64, 'وَقَالَتِ الْيَهُوْدُ يَدُ اللّٰهِ مَغْلُوْلَةٌ  ۗغُلَّتْ اَيْدِيْهِمْ وَلُعِنُوْا بِمَا قَالُوْا ۘ بَلْ يَدٰهُ مَبْسُوْطَتٰنِۙ يُنْفِقُ كَيْفَ يَشَاۤءُۗ وَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۗ وَاَ', 'waq<u>aa</u>lati <strong>a</strong>lyahuudu yadu <strong>al</strong>l<u>aa</u>hi maghluulatun ghullat aydiihim walu\'inuu bim<u>aa</u> q<u>aa</u>luu bal yad<u>aa</u>hu mabsuu<u>th</u>at<u>aa</u>ni yunfiqu kayfa yasy<u>aa</u>u walayaziidanna katsiiran minhum m<u>aa</u> unzila ilayka min rabbika <u>th</u>ughy<u>aa</u>nan wakufran wa-alqayn<u>aa</u> baynahumu <strong>a</strong>l\'ad<u>aa</u>wata wa<strong>a</strong>lbagh<u>dhaa</u>-a il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati kullam<u>aa</u> awqaduu n<u>aa</u>ran lil<u>h</u>arbi a<u>th</u>fa-ah<u>aa</u> <strong>al</strong>l<u>aa</u>hu wayas\'awna fii <strong>a</strong>l-ar<u>dh</u>i fas<u>aa</u>dan wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan orang-orang Yahudi berkata, “Tangan Allah terbelenggu.” Sebenarnya tangan merekalah yang dibelenggu dan merekalah yang dilaknat disebabkan apa yang telah mereka katakan itu, padahal kedua tangan Allah terbuka; Dia memberi rezeki sebagaimana Dia kehendaki. Dan (Al-Qur\'an) yang diturunkan kepadamu dari Tuhanmu itu pasti akan menambah kedurhakaan dan kekafiran bagi kebanyakan mereka. Dan Kami timbulkan permusuhan dan kebencian di antara mereka sampai hari Kiamat. Setiap mereka menyalakan api peperangan, Allah memadamkannya. Dan mereka berusaha (menimbulkan) kerusakan di bumi. Dan Allah tidak menyukai orang-orang yang berbuat kerusakan.'),
	(734, 5, 65, 'وَلَوْ اَنَّ اَهْلَ الْكِتٰبِ اٰمَنُوْا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّاٰتِهِمْ وَلَاَدْخَلْنٰهُمْ جَنّٰتِ النَّعِيْمِ', 'walaw anna ahla <strong>a</strong>lkit<u>aa</u>bi <u>aa</u>manuu wa<strong>i</strong>ttaqaw lakaffarn<u>aa</u> \'anhum sayyi-<u>aa</u>tihim wala-adkhaln<u>aa</u>hum jann<u>aa</u>ti <strong>al</strong>nna\'iim<strong>i</strong>', 'Dan sekiranya Ahli Kitab itu beriman dan bertakwa, niscaya Kami hapus kesalahan-kesalahan mereka, dan mereka tentu Kami masukkan ke dalam surga-surga yang penuh kenikmatan.'),
	(735, 5, 66, 'وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْهِمْ مِّنْ رَّبِّهِمْ لَاَكَلُوْا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۗ مِنْهُمْ اُمَّةٌ مُّقْتَصِدَةٌ  ۗ وَكَثِيْرٌ مِّنْهُمْ سَاۤءَ مَا يَعْمَلُوْنَ ࣖ ', 'walaw annahum aq<u>aa</u>muu <strong>al</strong>ttawr<u>aa</u>ta wa<strong>a</strong>l-injiila wam<u>aa</u> unzila ilayhim min rabbihim la-akaluu min fawqihim wamin ta<u>h</u>ti arjulihim minhum ummatun muqta<u>sh</u>idatun wakatsiirun minhum s<u>aa</u>-a m<u>aa</u> ya\'maluun<strong>a</strong>', 'Dan sekiranya mereka sungguh-sungguh menjalankan (hukum) Taurat, Injil dan (Al-Qur\'an) yang diturunkan kepada mereka dari Tuhannya, niscaya mereka akan mendapat makanan dari atas mereka dan dari bawah kaki mereka. Di antara mereka ada sekelompok yang jujur dan taat. Dan banyak di antara mereka sangat buruk apa yang mereka kerjakan.'),
	(736, 5, 67, '۞ يٰٓاَيُّهَا الرَّسُوْلُ بَلِّغْ مَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ ۗوَاِنْ لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسٰلَتَهٗ ۗوَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>rrasuulu balligh m<u>aa</u> unzila ilayka min rabbika wa-in lam taf\'al fam<u>aa</u> ballaghta ris<u>aa</u>latahu wa<strong>al</strong>l<u>aa</u>hu ya\'<u>sh</u>imuka mina <strong>al</strong>nn<u>aa</u>si inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Wahai Rasul! Sampaikanlah apa yang diturunkan Tuhanmu kepadamu. Jika tidak engkau lakukan (apa yang diperintahkan itu) berarti engkau tidak menyampaikan amanat-Nya. Dan Allah memelihara engkau dari (gangguan) manusia. Sungguh, Allah tidak memberi petunjuk kepada orang-orang kafir.'),
	(737, 5, 68, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُقِيْمُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ ۗوَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۚ فَلَا تَأْسَ عَلَى ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi lastum \'al<u>aa</u> syay-in <u>h</u>att<u>aa</u> tuqiimuu <strong>al</strong>ttawr<u>aa</u>ta wa<strong>a</strong>l-injiila wam<u>aa</u> unzila ilaykum min rabbikum walayaziidanna katsiiran minhum m<u>aa</u> unzila ilayka min rabbika <u>th</u>ughy<u>aa</u>nan wakufran fal<u>aa</u> ta/sa \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai Ahli Kitab! Kamu tidak dipandang beragama sedikit pun hingga kamu menegakkan ajaran-ajaran Taurat, Injil dan  (Al-Qur\'an) yang diturunkan Tuhanmu kepadamu.” Dan apa yang diturunkan Tuhanmu kepadamu pasti akan membuat banyak di antara mereka lebih durhaka dan lebih ingkar, maka janganlah engkau berputus asa terhadap orang-orang kafir itu.'),
	(738, 5, 69, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـُٔوْنَ وَالنَّصٰرٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>duu wa<strong>al</strong><u>shshaa</u>bi-uuna wa<strong>al</strong>nna<u>shaa</u>r<u>aa</u> man <u>aa</u>mana bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa\'amila <u>shaa</u>li<u>h</u>an fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, shabiin dan orang-orang Nasrani, barangsiapa beriman kepada Allah, kepada hari kemudian, dan berbuat kebajikan, maka tidak ada rasa khawatir padanya dan mereka tidak bersedih hati.'),
	(739, 5, 70, 'لَقَدْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ وَاَرْسَلْنَآ اِلَيْهِمْ رُسُلًا ۗ كُلَّمَا جَاۤءَهُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَرِيْقًا كَذَّبُوْا وَفَرِيْقًا يَّقْتُلُوْنَ', 'laqad akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qa banii isr<u>aa</u>-iila wa-arsaln<u>aa</u> ilayhim rusulan kullam<u>aa</u> j<u>aa</u>-ahum rasuulun bim<u>aa</u> l<u>aa</u> tahw<u>aa</u> anfusuhum fariiqan ka<u>dzdz</u>abuu wafariiqan yaqtuluun<strong>a</strong>', 'Sesungguhnya Kami telah mengambil perjanjian dari Bani Israil, dan telah Kami utus kepada mereka rasul-rasul. Tetapi setiap rasul datang kepada mereka dengan membawa apa yang tidak sesuai dengan keinginan mereka, (maka) sebagian (dari rasul itu) mereka dustakan dan sebagian yang lain mereka bunuh.'),
	(740, 5, 71, 'وَحَسِبُوْٓا اَلَّا تَكُوْنَ فِتْنَةٌ فَعَمُوْا وَصَمُّوْا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوْا وَصَمُّوْا كَثِيْرٌ مِّنْهُمْۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ', 'wa<u>h</u>asibuu <strong>al</strong>l<u>aa</u> takuuna fitnatun fa\'amuu wa<u>sh</u>ammuu tsumma t<u>aa</u>ba <strong>al</strong>l<u>aa</u>hu \'alayhim tsumma \'amuu wa<u>sh</u>ammuu katsiirun minhum wa<strong>al</strong>l<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ya\'maluun<strong>a</strong>', 'Dan mereka mengira bahwa tidak akan terjadi bencana apa pun (terhadap mereka dengan membunuh nabi-nabi itu), karena itu mereka menjadi buta dan tuli, kemudian Allah menerima tobat mereka, lalu banyak di antara mereka buta dan tuli. Dan Allah Maha Melihat apa yang mereka kerjakan.'),
	(741, 5, 72, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَ ۗوَقَالَ الْمَسِيْحُ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۗاِنَّهٗ مَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰىهُ ا', 'laqad kafara <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>lmasii<u>h</u>u ibnu maryama waq<u>aa</u>la <strong>a</strong>lmasii<u>h</u>u y<u>aa</u> banii isr<u>aa</u>-iila u\'buduu <strong>al</strong>l<u>aa</u>ha rabbii warabbakum innahu man yusyrik bi<strong>al</strong>l<u>aa</u>hi faqad <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu \'alayhi <strong>a</strong>ljannata wama/w<u>aa</u>hu <strong>al</strong>nn<u>aa</u>ru wam<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina min an<u>shaa</u>r<strong>in</strong>', 'Sungguh, telah kafir orang-orang yang berkata, “Sesungguhnya Allah itu dialah Al-Masih putra Maryam.” Padahal Al-Masih (sendiri) berkata, “Wahai Bani Israil! Sembahlah Allah, Tuhanku dan Tuhanmu.” Sesungguhnya barangsiapa mempersekutukan (sesuatu dengan) Allah, maka sungguh, Allah mengharamkan surga baginya, dan tempatnya ialah neraka. Dan tidak ada seorang penolong pun bagi orang-orang zalim itu.'),
	(742, 5, 73, 'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍ ۘ وَمَا مِنْ اِلٰهٍ اِلَّآ اِلٰهٌ وَّاحِدٌ  ۗوَاِنْ لَّمْ يَنْتَهُوْا عَمَّا يَقُوْلُوْنَ لَيَمَسَّنَّ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ', 'laqad kafara <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha ts<u>aa</u>litsu tsal<u>aa</u>tsatin wam<u>aa</u> min il<u>aa</u>hin ill<u>aa</u> il<u>aa</u>hun w<u>aah</u>idun wa-in lam yantahuu \'amm<u>aa</u> yaquuluuna layamassanna <strong>al</strong>la<u>dz</u>iina kafaruu minhum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sungguh, telah kafir orang-orang yang mengatakan, bahwa Allah adalah salah satu dari yang tiga, padahal tidak ada tuhan (yang berhak disembah) selain Tuhan Yang Esa. Jika mereka tidak berhenti dari apa yang mereka katakan, pasti orang-orang yang kafir di antara mereka akan ditimpa azab yang pedih.'),
	(743, 5, 74, 'اَفَلَا يَتُوْبُوْنَ اِلَى اللّٰهِ وَيَسْتَغْفِرُوْنَهٗۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'afal<u>aa</u> yatuubuuna il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wayastaghfiruunahu wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Mengapa mereka tidak bertobat kepada Allah dan memohon ampunan kepada-Nya? Allah Maha Pengampun, Maha Penyayang.'),
	(744, 5, 75, 'مَا الْمَسِيْحُ ابْنُ مَرْيَمَ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۗ وَاُمُّهٗ صِدِّيْقَةٌ  ۗ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيٰتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُوْنَ', 'm<u>aa</u> <strong>a</strong>lmasii<u>h</u>u ibnu maryama ill<u>aa</u> rasuulun qad khalat min qablihi <strong>al</strong>rrusulu waummuhu <u>sh</u>iddiiqatun k<u>aa</u>n<u>aa</u> ya/kul<u>aa</u>ni <strong>al</strong><u>ththh</u>a\'<u>aa</u>ma un<u>zh</u>ur kayfa nubayyinu lahumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti tsumma un<u>zh</u>ur ann<u>aa</u> yu/fakuun<strong>a</strong>', 'Al-Masih putra Maryam hanyalah seorang Rasul. Sebelumnya pun sudah berlalu beberapa rasul. Dan ibunya seorang yang berpegang teguh pada kebenaran. Keduanya biasa memakan makanan. Perhatikanlah bagaimana Kami menjelaskan ayat-ayat (tanda-tanda kekuasaan) kepada mereka (Ahli Kitab), kemudian perhatikanlah bagaimana mereka dipalingkan (oleh keinginan mereka).'),
	(745, 5, 76, 'قُلْ اَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَّلَا نَفْعًا ۗوَاللّٰهُ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'qul ata\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yamliku lakum <u>dh</u>arran wal<u>aa</u> naf\'an wa<strong>al</strong>l<u>aa</u>hu huwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Katakanlah (Muhammad), “Mengapa kamu menyembah yang selain Allah, sesuatu yang tidak dapat menimbulkan bencana kepadamu dan tidak (pula) memberi manfaat?” Dan Allah Maha Mendengar, Maha Mengetahui.'),
	(746, 5, 77, 'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوْٓا اَهْوَاۤءَ قَوْمٍ قَدْ ضَلُّوْا مِنْ قَبْلُ وَاَضَلُّوْا كَثِيْرًا وَّضَلُّوْا عَنْ سَوَاۤءِ السَّبِيْلِ ࣖ ', 'qul y<u>aa</u> ahla <strong>a</strong>lkit<u>aa</u>bi l<u>aa</u> taghluu fii diinikum ghayra <strong>a</strong>l<u>h</u>aqqi wal<u>aa </u>tattabi\'uu ahw<u>aa</u>-a qawmin qad <u>dh</u>alluu min qablu wa-a<u>dh</u>alluu katsiiran wa<u>dh</u>alluu \'an saw<u>aa</u>-i <strong>al</strong>ssabiil<strong>i</strong>', 'Katakanlah (Muhammad), “Wahai Ahli Kitab! Janganlah kamu berlebih-lebihan dengan cara yang tidak benar dalam agamamu. Dan janganlah kamu mengikuti keinginan orang-orang yang telah tersesat dahulu dan (telah) menyesatkan banyak (manusia), dan mereka sendiri tersesat dari jalan yang lurus.”'),
	(747, 5, 78, 'لُعِنَ الَّذِيْنَ كَفَرُوْا مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى لِسَانِ دَاوٗدَ وَعِيْسَى ابْنِ مَرْيَمَ ۗذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ', 'lu\'ina <strong>al</strong>la<u>dz</u>iina kafaruu min banii isr<u>aa</u>-iila \'al<u>aa</u> lis<u>aa</u>ni d<u>aa</u>wuuda wa\'iis<u>aa</u> ibni maryama <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun<strong>a</strong>', 'Orang-orang kafir dari Bani Israil telah dilaknat melalui lisan (ucapan) Dawud dan Isa putra Maryam. Yang demikian itu karena mereka durhaka dan selalu melampaui batas.'),
	(748, 5, 79, 'كَانُوْا لَا يَتَنَاهَوْنَ عَنْ مُّنْكَرٍ فَعَلُوْهُۗ  لَبِئْسَ مَا كَانُوْا يَفْعَلُوْنَ', 'k<u>aa</u>nuu l<u>aa</u> yatan<u>aa</u>hawna \'an munkarin fa\'aluuhu labi/sa m<u>aa</u> k<u>aa</u>nuu yaf\'aluun<strong>a</strong>', 'Mereka tidak saling mencegah perbuatan mungkar yang selalu mereka perbuat. Sungguh, sangat buruk apa yang mereka perbuat.'),
	(749, 5, 80, 'تَرٰى كَثِيْرًا مِّنْهُمْ يَتَوَلَّوْنَ الَّذِيْنَ كَفَرُوْا ۗ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِى الْعَذَابِ هُمْ خٰلِدُوْنَ', 'tar<u>aa</u> katsiiran minhum yatawallawna <strong>al</strong>la<u>dz</u>iina kafaruu labi/sa m<u>aa </u>qaddamat lahum anfusuhum an sakhi<u>th</u>a <strong>al</strong>l<u>aa</u>hu \'alayhim wafii <strong>a</strong>l\'a<u>dzaa</u>bi hum kh<u>aa</u>liduun<strong>a</strong>', 'Kamu melihat banyak di antara mereka tolong-menolong dengan orang-orang kafir (musyrik). Sungguh, sangat buruk apa yang mereka lakukan untuk diri mereka sendiri, yaitu kemurkaan Allah, dan mereka akan kekal dalam azab.'),
	(750, 5, 81, 'وَلَوْ كَانُوْا يُؤْمِنُوْنَ بِاللّٰهِ وَالنَّبِيِّ وَمَآ اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوْهُمْ اَوْلِيَاۤءَ وَلٰكِنَّ كَثِيْرًا مِّنْهُمْ فٰسِقُوْنَ', 'walaw k<u>aa</u>nuu yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>nnabiyyi wam<u>aa</u> unzila ilayhi m<u>aa</u> ittakha<u>dz</u>uuhum awliy<u>aa</u>-a wal<u>aa</u>kinna katsiiran minhum f<u>aa</u>siquun<strong>a</strong>', 'Dan sekiranya mereka beriman kepada Allah, kepada Nabi (Muhammad) dan kepada apa yang diturunkan kepadanya, niscaya mereka tidak akan menjadikan orang musyrik itu sebagai teman setia. Tetapi banyak di antara mereka orang-orang yang fasik.'),
	(751, 5, 82, '۞ لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِّلَّذِيْنَ اٰمَنُوا الْيَهُوْدَ وَالَّذِيْنَ اَشْرَكُوْاۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَّوَدَّةً لِّلَّذِيْنَ اٰمَنُوا الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰىۗ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسِّيْسِيْنَ وَرُهْبَانًا وَّاَن', 'latajidanna asyadda <strong>al</strong>nn<u>aa</u>si \'ad<u>aa</u>watan lilla<u>dz</u>iina <u>aa</u>manuu <strong>a</strong>lyahuuda wa<strong>a</strong>lla<u>dz</u>iina asyrakuu walatajidanna aqrabahum mawaddatan lilla<u>dz</u>iina <u>aa</u>manuu <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu inn<u>aa</u> na<u>shaa</u>r<u>aa</u> <u>dzaa</u>lika bi-anna minhum qissiisiina waruhb<u>aa</u>nan wa-annahum l<u>aa</u> yastakbiruun<strong>a</strong>', 'Pasti akan kamu dapati orang yang paling keras permusuhannya terhadap orang-orang yang beriman, yaitu orang-orang Yahudi dan orang-orang musyrik. Dan pasti akan kamu dapati orang yang paling dekat persahabatannya dengan orang-orang yang beriman ialah orang-orang yang berkata, “Sesungguhnya kami adalah orang Nasrani.” Yang demikian itu karena di antara mereka terdapat para pendeta dan para rahib, (juga) karena mereka tidak menyombongkan diri.'),
	(752, 5, 83, 'وَاِذَا سَمِعُوْا مَآ اُنْزِلَ اِلَى الرَّسُوْلِ تَرٰٓى اَعْيُنَهُمْ تَفِيْضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوْا مِنَ الْحَقِّۚ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ ', 'wa-i<u>dzaa</u> sami\'uu m<u>aa</u> unzila il<u>aa</u> <strong>al</strong>rrasuuli tar<u>aa</u> a\'yunahum tafii<u>dh</u>u mina <strong>al</strong>ddam\'i mimm<u>aa</u> \'arafuu mina <strong>a</strong>l<u>h</u>aqqi yaquuluuna rabban<u>aa</u> <u>aa</u>mann<u>aa</u> fa<strong>u</strong>ktubn<u>aa</u> ma\'a <strong>al</strong>sysy<u>aa</u>hidiin<strong>a</strong>', 'Dan apabila mereka mendengarkan apa (Al-Qur\'an) yang diturunkan kepada Rasul (Muhammad), kamu lihat mata mereka mencucurkan air mata disebabkan kebenaran yang telah mereka ketahui (dari kitab-kitab mereka sendiri), seraya berkata, “Ya Tuhan, kami telah beriman, maka catatlah kami bersama orang-orang yang menjadi saksi (atas kebenaran Al-Qur\'an dan kenabian Muhammad).'),
	(753, 5, 84, 'وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَاۤءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُّدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصّٰلِحِيْنَ', 'wam<u>aa</u> lan<u>aa</u> l<u>aa</u> nu/minu bi<strong>al</strong>l<u>aa</u>hi wam<u>aa</u> j<u>aa</u>-an<u>aa</u> mina <strong>a</strong>l<u>h</u>aqqi wana<u>th</u>ma\'u an yudkhilan<u>aa</u> rabbun<u>aa</u> ma\'a <strong>a</strong>lqawmi <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan mengapa kami tidak akan beriman kepada Allah dan kepada kebenaran yang datang kepada kami, padahal kami sangat ingin agar Tuhan kami memasukkan kami ke dalam golongan orang-orang saleh?”'),
	(754, 5, 85, 'فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوْا جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ وَذٰلِكَ جَزَاۤءُ الْمُحْسِنِيْنَ ', 'fa-ats<u>aa</u>bahumu <strong>al</strong>l<u>aa</u>hu bim<u>aa</u> q<u>aa</u>luu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika jaz<u>aa</u>u <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Maka Allah memberi pahala kepada mereka atas perkataan yang telah mereka ucapkan, (yaitu) surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan itulah balasan (bagi) orang-orang yang berbuat kebaikan.'),
	(755, 5, 86, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>lja<u>h</u>iim<strong>i</strong>', 'Dan orang-orang yang kafir serta mendustakan ayat-ayat Kami, mereka itulah penghuni neraka.'),
	(756, 5, 87, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحَرِّمُوْا طَيِّبٰتِ مَآ اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُوْا ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tu<u>h</u>arrimuu <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> a<u>h</u>alla <strong>al</strong>l<u>aa</u>hu lakum wal<u>aa</u> ta\'taduu inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmu\'tadiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu mengharamkan apa yang baik yang telah dihalalkan Allah kepadamu, dan janganlah kamu melampaui batas. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.'),
	(757, 5, 88, 'وَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًا ۖوَّاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ ', 'wakuluu mimm<u>aa</u> razaqakumu <strong>al</strong>l<u>aa</u>hu <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha <strong>al</strong>la<u>dz</u>ii antum bihi mu/minuun<strong>a</strong>', 'Dan makanlah dari apa yang telah diberikan Allah kepadamu sebagai rezeki yang halal dan baik, dan bertakwalah kepada Allah yang kamu beriman kepada-Nya.'),
	(758, 5, 89, 'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ  فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا عَقَّدْتُّمُ الْاَيْمَانَۚ فَكَفَّارَتُهٗٓ اِطْعَامُ عَشَرَةِ مَسٰكِيْنَ مِنْ اَوْسَطِ مَا تُطْعِمُوْنَ اَهْلِيْكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْرِيْرُ رَقَبَةٍ ۗفَمَن', 'l<u>aa</u> yu-<u>aa</u>khi<u>dz</u>ukumu <strong>al</strong>l<u>aa</u>hu bi<strong>a</strong>llaghwi fii aym<u>aa</u>nikum wal<u>aa</u>kin yu-<u>aa</u>khi<u>dz</u>ukum bim<u>aa</u> \'aqqadtumu <strong>a</strong>l-aym<u>aa</u>na fakaff<u>aa</u>ratuhu i<u>th</u>\'<u>aa</u>mu \'asyarati mas<u>aa</u>kiina min awsa<u>th</u>i m<u>aa</u> tu<u>th</u>\'imuuna ahliikum aw kiswatuhum aw ta<u>h</u>riiru raqabatin faman lam yajid fa<u>sh</u>iy<u>aa</u>mu tsal<u>aa</u>tsati ayy<u>aa</u>min <u>dzaa</u>lika kaff<u>aa</u>ratu aym<u>aa</u>nikum i<u>dzaa</u> <u>h</u>alaftum wa<strong>i</strong><u>h</u>fa<u>zh</u>uu aym<u>aa</u>nakum ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi la\'allakum tasykuruun<strong>a</strong>', 'Allah tidak menghukum kamu disebabkan sumpah-sumpahmu yang tidak disengaja (untuk bersumpah), tetapi Dia menghukum kamu disebabkan sumpah-sumpah yang kamu sengaja, maka kafaratnya (denda pelanggaran sumpah) ialah memberi makan sepuluh orang miskin, yaitu dari makanan yang biasa kamu berikan kepada keluargamu, atau memberi mereka pakaian atau memerdekakan seorang hamba sahaya. Barangsiapa tidak mampu melakukannya, maka (kafaratnya) berpuasalah tiga hari. Itulah kafarat sumpah-sumpahmu apabila kamu bersumpah. Dan jagalah sumpahmu. Demikianlah Allah menerangkan hukum-hukum-Nya kepadamu agar kamu bersyukur (kepada-Nya).'),
	(759, 5, 90, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِّنْ عَمَلِ الشَّيْطٰنِ فَاجْتَنِبُوْهُ لَعَلَّكُمْ تُفْلِحُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu innam<u>aa</u> <strong>a</strong>lkhamru wa<strong>a</strong>lmaysiru wa<strong>a</strong>l-an<u>shaa</u>bu wa<strong>a</strong>l-azl<u>aa</u>mu rijsun min \'amali <strong>al</strong>sysyay<u>th</u><u>aa</u>ni fa<strong>i</strong>jtanibuuhu la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Sesungguhnya minuman keras, berjudi, (berkurban untuk) berhala, dan mengundi nasib dengan anak panah, adalah perbuatan keji dan termasuk perbuatan setan. Maka jauhilah (perbuatan-perbuatan) itu agar kamu beruntung.'),
	(760, 5, 91, 'اِنَّمَا يُرِيْدُ الشَّيْطٰنُ اَنْ يُّوْقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ فِى الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِ فَهَلْ اَنْتُمْ مُّنْتَهُوْنَ ', 'innam<u>aa</u> yuriidu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu an yuuqi\'a baynakumu <strong>a</strong>l\'ad<u>aa</u>wata wa<strong>a</strong>lbagh<u>dhaa</u>-a fii <strong>a</strong>lkhamri wa<strong>a</strong>lmaysiri waya<u>sh</u>uddakum \'an <u>dz</u>ikri <strong>al</strong>l<u>aa</u>hi wa\'ani <strong>al</strong><u>shsh</u>al<u>aa</u>ti fahal antum muntahuun<strong>a</strong>', 'Dengan minuman keras dan judi itu, setan hanyalah bermaksud menimbulkan permusuhan dan kebencian di antara kamu, dan menghalang-halangi kamu dari mengingat Allah dan melaksanakan salat, maka tidakkah kamu mau berhenti?'),
	(761, 5, 92, 'وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاحْذَرُوْا ۚفَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ ', 'wa-a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uu <strong>al</strong>rrasuula wa<strong>i</strong><u>hts</u>aruu fa-in tawallaytum fa<strong>i</strong>\'lamuu annam<u>aa</u> \'al<u>aa</u> rasuulin<u>aa</u> <strong>a</strong>lbal<u>aa</u>ghu <strong>a</strong>lmubiin<strong>u</strong>', 'Dan taatlah kamu kepada Allah dan taatlah kamu kepada Rasul serta berhati-hatilah. Jika kamu berpaling, maka ketahuilah bahwa kewajiban Rasul Kami hanyalah menyampaikan (amanat) dengan jelas.'),
	(762, 5, 93, 'لَيْسَ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جُنَاحٌ فِيْمَا طَعِمُوْٓا اِذَا مَا اتَّقَوْا وَّاٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ ثُمَّ اتَّقَوْا وَّاٰمَنُوْا ثُمَّ اتَّقَوْا وَّاَحْسَنُوْا ۗوَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ࣖ ', 'laysa \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti jun<u>aah</u>un fiim<u>aa</u> <u>th</u>a\'imuu i<u>dzaa</u> m<u>aa</u> ittaqaw wa<u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti tsumma ittaqaw wa<u>aa</u>manuu tsumma ittaqaw wa-a<u>h</u>sanuu wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>ibbu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Tidak berdosa bagi orang-orang yang beriman dan mengerjakan kebajikan tentang apa yang mereka makan (dahulu), apabila mereka bertakwa dan beriman, serta mengerjakan kebajikan, kemudian mereka tetap bertakwa dan beriman, selanjutnya mereka (tetap juga) bertakwa dan berbuat kebajikan. Dan Allah menyukai orang-orang yang berbuat kebajikan.'),
	(763, 5, 94, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِّنَ الصَّيْدِ تَنَالُهٗٓ اَيْدِيْكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَّخَافُهٗ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu layabluwannakumu <strong>al</strong>l<u>aa</u>hu bisyay-in mina <strong>al</strong><u>shsh</u>aydi tan<u>aa</u>luhu aydiikum warim<u>aah</u>ukum liya\'lama <strong>al</strong>l<u>aa</u>hu man yakh<u>aa</u>fuhu bi<strong>a</strong>lghaybi famani i\'tad<u>aa</u> ba\'da <u>dzaa</u>lika falahu \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Wahai orang-orang yang beriman! Allah pasti akan menguji kamu dengan hewan buruan yang dengan mudah kamu peroleh dengan tangan dan tombakmu agar Allah mengetahui siapa yang takut kepada-Nya, meskipun dia tidak melihat-Nya. Barangsiapa melampaui batas setelah itu, maka dia akan mendapat azab yang pedih.'),
	(764, 5, 95, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌ ۗوَمَنْ قَتَلَهٗ مِنْكُمْ مُّتَعَمِّدًا فَجَزَۤاءٌ مِّثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهٖ ذَوَا عَدْلٍ مِّنْكُمْ هَدْيًاۢ بٰلِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسٰك', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> taqtuluu <strong>al</strong><u>shsh</u>ayda wa-antum <u>h</u>urumun waman qatalahu minkum muta\'ammidan fajaz<u>aa</u>un mitslu m<u>aa</u> qatala mina <strong>al</strong>nna\'ami ya<u>h</u>kumu bihi <u>dz</u>aw<u>aa</u> \'adlin minkum hadyan b<u>aa</u>ligha <strong>a</strong>lka\'bati aw kaff<u>aa</u>ratun <u>th</u>a\'<u>aa</u>mu mas<u>aa</u>kiina aw \'adlu <u>dzaa</u>lika <u>sh</u>iy<u>aa</u>man liya<u>dz</u>uuqa wab<u>aa</u>la amrihi \'af<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'amm<u>aa</u> salafa waman \'<u>aa</u>da fayantaqimu <strong>al</strong>l<u>aa</u>hu minhu wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>dz</u>uu intiq<u>aa</u>m<strong>in</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu membunuh hewan buruan, ketika kamu sedang ihram (haji atau umrah). Barangsiapa di antara kamu membunuhnya dengan sengaja, maka dendanya ialah mengganti dengan hewan ternak yang sepadan dengan buruan yang dibunuhnya, menurut putusan dua orang yang adil di antara kamu sebagai hadyu yang dibawa ke Ka‘bah, atau kafarat (membayar tebusan dengan) memberi makan kepada orang-orang miskin, atau berpuasa, seimbang dengan makanan yang dikeluarkan itu, agar dia merasakan akibat buruk dari perbuatannya.  Allah  telah memaafkan apa yang telah lalu. Dan barangsiapa kembali mengerjakannya, niscaya Allah akan menyiksanya. Dan Allah Mahaperkasa, memiliki (kekuasaan untuk) menyiksa.'),
	(765, 5, 96, 'اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهٗ مَتَاعًا لَّكُمْ وَلِلسَّيَّارَةِ ۚوَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗوَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ', 'u<u>h</u>illa lakum <u>sh</u>aydu <strong>a</strong>lba<u>h</u>ri wa<u>th</u>a\'<u>aa</u>muhu mat<u>aa</u>\'an lakum wali<strong>l</strong>ssayy<u>aa</u>rati wa<u>h</u>urrima \'alaykum <u>sh</u>aydu <strong>a</strong>lbarri m<u>aa</u> dumtum <u>h</u>uruman wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha <strong>al</strong>la<u>dz</u>ii ilayhi tu<u>h</u>syaruun<strong>a</strong>', 'Dihalalkan bagimu hewan buruan laut dan makanan (yang berasal) dari laut sebagai makanan yang lezat bagimu, dan bagi orang-orang yang dalam perjalanan; dan diharamkan atasmu (menangkap) hewan darat, selama kamu sedang ihram. Dan bertakwalah kepada Allah yang kepada-Nya kamu akan dikumpulkan (kembali).'),
	(766, 5, 97, '۞ جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيٰمًا لِّلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَاۤىِٕدَ ۗذٰلِكَ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۙ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ', 'ja\'ala <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lka\'bata <strong>a</strong>lbayta <strong>a</strong>l<u>h</u>ar<u>aa</u>ma qiy<u>aa</u>man li<strong>l</strong>nn<u>aa</u>si wa<strong>al</strong>sysyahra <strong>a</strong>l<u>h</u>ar<u>aa</u>ma wa<strong>a</strong>lhadya wa<strong>a</strong>lqal<u>aa</u>-ida <u>dzaa</u>lika lita\'lamuu anna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa-anna <strong>al</strong>l<u>aa</u>ha bikulli syay-in \'aliim<strong>un</strong>', 'Allah telah menjadikan Ka‘bah rumah suci tempat manusia berkumpul. Demikian pula bulan haram, hadyu dan qala\'id. Yang demikian itu agar kamu mengetahui, bahwa Allah mengetahui apa yang ada di langit dan apa yang ada di bumi, dan bahwa Allah Maha Mengetahui segala sesuatu.'),
	(767, 5, 98, 'اِعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۙ وَاَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌۗ ', 'i\'lamuu anna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>bi wa-anna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Ketahuilah, bahwa Allah sangat keras siksaan-Nya dan bahwa Allah Maha Pengampun, Maha Penyayang.'),
	(768, 5, 99, 'مَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ ۗوَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ ', 'm<u>aa</u> \'al<u>aa</u> <strong>al</strong>rrasuuli ill<u>aa</u> <strong>a</strong>lbal<u>aa</u>ghu wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> tubduuna wam<u>aa</u> taktumuun<strong>a</strong>', 'Kewajiban Rasul tidak lain hanyalah menyampaikan (amanat Allah), dan Allah mengetahui apa yang kamu tampakkan dan apa yang kamu sembunyikan.'),
	(769, 5, 100, 'قُلْ لَّا يَسْتَوِى الْخَبِيْثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَثْرَةُ الْخَبِيْثِۚ فَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُوْنَ ࣖ', 'qul l<u>aa</u> yastawii <strong>a</strong>lkhabiitsu wa<strong>al</strong><u>ththh</u>ayyibu walaw a\'jabaka katsratu <strong>a</strong>lkhabiitsi fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha y<u>aa</u> ulii <strong>a</strong>l-alb<u>aa</u>bi la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Katakanlah (Muhammad), “Tidaklah sama yang buruk dengan yang baik, meskipun banyaknya keburukan itu menarik hatimu, maka bertakwalah kepada Allah wahai orang-orang yang mempunyai akal sehat, agar kamu beruntung.”'),
	(770, 5, 101, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَسْـَٔلُوْا عَنْ اَشْيَاۤءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ ۚوَاِنْ تَسْـَٔلُوْا عَنْهَا حِيْنَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْ ۗعَفَا اللّٰهُ عَنْهَا ۗوَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tas-aluu \'an asyy<u>aa</u>-a in tubda lakum tasu/kum wa-in tas-aluu \'anh<u>aa</u> <u>h</u>iina yunazzalu <strong>a</strong>lqur-<u>aa</u>nu tubda lakum \'af<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'anh<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu ghafuurun <u>h</u>aliim<strong>un</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu menanyakan (kepada Nabimu) hal-hal yang jika diterangkan kepadamu (justru) menyusahkan kamu. Jika kamu menanyakannya ketika Al-Qur\'an sedang diturunkan, (niscaya) akan diterangkan kepadamu. Allah telah memaafkan (kamu) tentang hal itu. Dan Allah Maha Pengampun, Maha Penyantun.'),
	(771, 5, 102, 'قَدْ سَاَلَهَا قَوْمٌ مِّنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوْا بِهَا كٰفِرِيْنَ ', 'qad sa-alah<u>aa</u> qawmun min qablikum tsumma a<u>sh</u>ba<u>h</u>uu bih<u>aa</u> k<u>aa</u>firiin<strong>a</strong>', 'Sesungguhnya sebelum kamu telah ada segolongan manusia yang menanyakan hal-hal serupa itu (kepada nabi mereka), kemudian mereka menjadi kafir.'),
	(772, 5, 103, 'مَا جَعَلَ اللّٰهُ مِنْۢ بَحِيْرَةٍ وَّلَا سَاۤىِٕبَةٍ وَّلَا وَصِيْلَةٍ وَّلَا حَامٍ ۙوَّلٰكِنَّ الَّذِيْنَ كَفَرُوْا يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَاَكْثَرُهُمْ لَا يَعْقِلُوْنَ ', 'm<u>aa</u> ja\'ala <strong>al</strong>l<u>aa</u>hu min ba<u>h</u>iiratin wal<u>aa</u> s<u>aa</u>-ibatin wal<u>aa</u> wa<u>sh</u>iilatin wal<u>aa</u> <u>haa</u>min wal<u>aa</u>kinna <strong>al</strong>la<u>dz</u>iina kafaruu yaftaruuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba wa-aktsaruhum l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Allah tidak pernah mensyariatkan adanya Bahirah, Sa\'ibah, Wasilah dan haam. Tetapi orang-orang kafir membuat-buat kedustaan terhadap Allah, dan kebanyakan mereka tidak mengerti.'),
	(773, 5, 104, 'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ قَالُوْا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْلَمُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ ', 'wa-i<u>dzaa</u> qiila lahum ta\'<u>aa</u>law il<u>aa</u> m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu wa-il<u>aa</u> <strong>al</strong>rrasuuli q<u>aa</u>luu <u>h</u>asbun<u>aa</u> m<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na <u>aa</u>b<u>aa</u>uhum l<u>aa</u> ya\'lamuuna syay-an wal<u>aa</u> yahtaduun<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Marilah (mengikuti) apa yang diturunkan Allah dan (mengikuti) Rasul.” Mereka menjawab, “Cukuplah bagi kami apa yang kami dapati nenek moyang kami (mengerjakannya).” Apakah (mereka akan mengikuti) juga nenek moyang mereka walaupun nenek moyang mereka itu tidak mengetahui apa-apa dan tidak (pula) mendapat petunjuk?'),
	(774, 5, 105, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا عَلَيْكُمْ اَنْفُسَكُمْ ۚ لَا يَضُرُّكُمْ مَّنْ ضَلَّ اِذَا اهْتَدَيْتُمْ ۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu \'alaykum anfusakum l<u>aa</u> ya<u>dh</u>urrukum man <u>dh</u>alla i<u>dz</u>a ihtadaytum il<u>aa</u> <strong>al</strong>l<u>aa</u>hi marji\'ukum jamii\'an fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Wahai orang-orang yang beriman! Jagalah dirimu; (karena) orang yang sesat itu tidak akan membahayakanmu apabila kamu telah mendapat petunjuk. Hanya kepada Allah kamu semua akan kembali, kemudian Dia akan menerangkan kepadamu apa yang telah kamu kerjakan.'),
	(775, 5, 106, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ حِيْنَ الْوَصِيَّةِ اثْنٰنِ ذَوَا عَدْلٍ مِّنْكُمْ اَوْ اٰخَرٰنِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِى الْاَرْضِ فَاَصَابَتْكُمْ مُّصِيْبَةُ الْمَوْتِۗ تَحْبِسُ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu syah<u>aa</u>datu baynikum i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adakumu <strong>a</strong>lmawtu <u>h</u>iina <strong>a</strong>lwa<u>sh</u>iyyati itsn<u>aa</u>ni <u>dz</u>aw<u>aa</u> \'adlin minkum aw <u>aa</u>khar<u>aa</u>ni min ghayrikum in antum <u>dh</u>arabtum fii <strong>a</strong>l-ar<u>dh</u>i fa-a<u>shaa</u>batkum mu<u>sh</u>iibatu <strong>a</strong>lmawti ta<u>h</u>bisuunahum<u>aa</u> min ba\'di <strong>al</strong><u>shsh</u>al<u>aa</u>ti fayuqsim<u>aa</u>ni bi<strong>al</strong>l<u>aa</u>hi ini irtabtum l<u>aa</u> nasytarii bihi tsamanan walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> wal<u>aa</u> naktumu syah<u>aa</u>data <strong>al</strong>l<u>aa</u>hi inn<u>aa</u> i<u>dz</u>an lamina <strong>a</strong>l-<u>aa</u>tsimiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Apabila salah seorang (di antara) kamu menghadapi kematian, sedang dia akan berwasiat, maka hendaklah (wasiat itu) disaksikan oleh dua orang yang adil di antara kamu, atau dua orang yang berlainan (agama) dengan kamu. Jika kamu dalam perjalanan di bumi lalu kamu ditimpa bahaya kematian, hendaklah kamu tahan kedua saksi itu setelah salat, agar keduanya bersumpah dengan nama Allah jika kamu ragu-ragu, “Demi Allah kami tidak akan mengambil keuntungan dengan sumpah ini, walaupun dia karib kerabat, dan kami tidak menyembunyikan kesaksian Allah; sesungguhnya jika demikian tentu kami termasuk orang-orang yang berdosa.”'),
	(776, 5, 107, 'فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّآ اِثْمًا فَاٰخَرٰنِ يَقُوْمٰنِ مَقَامَهُمَا مِنَ الَّذِيْنَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيٰنِ فَيُقْسِمٰنِ بِاللّٰهِ لَشَهَادَتُنَآ اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَآ ۖاِنَّآ اِذًا لَّمِنَ الظّٰ', 'fa-in \'utsira \'al<u>aa</u> annahum<u>aa</u> ista<u>h</u>aqq<u>aa</u> itsman fa<u>aa</u>khara<u>n</u>i yaquum<u>aa</u>ni maq<u>aa</u>mahum<u>aa</u> mina <strong>al</strong>la<u>dz</u>iina ista<u>h</u>aqqa \'alayhimu <strong>a</strong>l-awlay<u>aa</u>ni fayuqsim<u>aa</u>ni bi<strong>al</strong>l<u>aa</u>hi lasyah<u>aa</u>datun<u>aa</u> a<u>h</u>aqqu min syah<u>aa</u>datihim<u>aa</u> wam<u>aa</u> i\'tadayn<u>aa</u> inn<u>aa</u> i<u>dz</u>an lamina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Jika terbukti kedua saksi itu berbuat dosa, maka dua orang yang lain menggantikan kedudukannya, yaitu di antara ahli waris yang berhak dan lebih dekat kepada orang yang mati, lalu keduanya bersumpah dengan nama Allah, “Sungguh, kesaksian kami lebih layak diterima daripada kesaksian kedua saksi itu, dan kami tidak melanggar batas. Sesungguhnya jika kami berbuat demikian tentu kami termasuk orang-orang zalim.”'),
	(777, 5, 108, 'ذٰلِكَ اَدْنٰٓى اَنْ يَّأْتُوْا بِالشَّهَادَةِ عَلٰى وَجْهِهَآ اَوْ يَخَافُوْٓا اَنْ تُرَدَّ اَيْمَانٌۢ بَعْدَ اَيْمَانِهِمْۗ وَاتَّقُوا اللّٰهَ وَاسْمَعُوْا ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ࣖ', '<u>dzaa</u>lika adn<u>aa</u> an ya/tuu bi<strong>al</strong>sysyah<u>aa</u>dati \'al<u>aa</u> wajhih<u>aa</u> aw yakh<u>aa</u>fuu an turadda aym<u>aa</u>nun ba\'da aym<u>aa</u>nihim wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>sma\'uu wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Dengan cara itu mereka lebih patut memberikan kesaksiannya menurut yang sebenarnya, dan mereka merasa takut akan dikembalikan sumpahnya (kepada ahli waris) setelah mereka bersumpah. Bertakwalah kepada Allah dan dengarkanlah (perintah-Nya). Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik.'),
	(778, 5, 109, '۞ يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُوْلُ مَاذَٓا اُجِبْتُمْ ۗ قَالُوْا لَا عِلْمَ لَنَا ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ', 'yawma yajma\'u <strong>al</strong>l<u>aa</u>hu <strong>al</strong>rrusula fayaquulu m<u>aatsaa</u> ujibtum q<u>aa</u>luu l<u>aa</u> \'ilma lan<u>aa</u> innaka anta \'all<u>aa</u>mu <strong>a</strong>lghuyuub<strong>i</strong>', '(Ingatlah) pada hari ketika Allah mengumpulkan para rasul, lalu Dia bertanya (kepada mereka), “Apa jawaban (kaummu) terhadap (seruan)mu?” Mereka (para rasul) menjawab, “Kami tidak tahu (tentang itu). Sesungguhnya Engkaulah Yang Maha Mengetahui segala yang gaib.”'),
	(779, 5, 110, 'اِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِيْ عَلَيْكَ وَعَلٰى وَالِدَتِكَ ۘاِذْ اَيَّدْتُّكَ بِرُوْحِ الْقُدُسِۗ  تُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا ۚوَاِذْ عَلَّمْتُكَ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَ ۚوَاِ', 'i<u>dz</u> q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> ibna maryama u<u>dz</u>kur ni\'matii \'alayka wa\'al<u>aa</u> w<u>aa</u>lidatika i<u>dz</u> ayyadtuka biruu<u>h</u>i <strong>a</strong>lqudusi tukallimu <strong>al</strong>nn<u>aa</u>sa fii <strong>a</strong>lmahdi wakahlan wa-i<u>dz</u> \'allamtuka <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ikmata wa<strong>al</strong>ttawr<u>aa</u>ta wa<strong>a</strong>l-injiila wa-i<u>dz</u> takhluqu mina <strong>al</strong><u>ththh</u>iini kahay-ati <strong>al</strong><u>ththh</u>ayri bi-i<u>dz</u>nii fatanfukhu fiih<u>aa</u> fatakuunu <u>th</u>ayran bi-i<u>dz</u>nii watubri-u <strong>a</strong>l-akmaha wa<strong>a</strong>l-abra<u>sh</u>a bi-i<u>dz</u>nii wa-i<u>dz</u> tukhriju <strong>a</strong>lmawt<u>aa</u> bi-i<u>dz</u>nii wa-i<u>dz</u> kafaftu banii isr<u>aa</u>-iila \'anka i<u>dz</u> ji/tahum bi<strong>a</strong>lbayyin<u>aa</u>ti faq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu minhum in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin<strong>un</strong>', 'Dan ingatlah ketika Allah berfirman, “Wahai Isa putra Maryam! Ingatlah nikmat-Ku kepadamu dan kepada ibumu sewaktu Aku menguatkanmu dengan Rohulkudus. Engkau dapat berbicara dengan manusia pada waktu masih dalam buaian dan setelah dewasa. Dan ingatlah ketika Aku mengajarkan menulis kepadamu, (juga) Hikmah, Taurat dan Injil. Dan ingatlah ketika engkau membentuk dari tanah berupa burung dengan seizin-Ku, kemudian engkau meniupnya, lalu menjadi seekor burung (yang sebenarnya) dengan seizin-Ku. Dan ingatlah ketika engkau menyembuhkan orang yang buta sejak lahir dan orang yang berpenyakit kusta dengan seizin-Ku. Dan ingatlah ketika engkau mengeluarkan orang mati (dari kubur menjadi hidup) dengan seizin-Ku. Dan ingatlah ketika Aku menghalangi Bani Israil (dari keinginan mereka membunuhmu) di kala waktu engkau mengemukakan kepada mereka keterangan-keterangan yang nyata, lalu orang-orang kafir di antara mereka berkata, “Ini tidak lain hanyalah sihir yang nyata.”'),
	(780, 5, 111, 'وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّٖنَ اَنْ اٰمِنُوْا بِيْ وَبِرَسُوْلِيْ ۚ قَالُوْٓا اٰمَنَّا وَاشْهَدْ بِاَنَّنَا مُسْلِمُوْنَ ', 'wa-i<u>dz</u> aw<u>h</u>aytu il<u>aa</u> <strong>a</strong>l<u>h</u>aw<u>aa</u>riyyiina an <u>aa</u>minuu bii wabirasuulii q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa<strong>i</strong>syhad bi-annan<u>aa</u> muslimuun<strong>a</strong>', 'Dan (ingatlah), ketika Aku ilhamkan kepada pengikut-pengikut Isa yang setia, “Berimanlah kamu kepada-Ku dan kepada Rasul-Ku.” Mereka menjawab, “Kami telah beriman, dan saksikanlah (wahai Rasul) bahwa kami adalah orang-orang yang berserah diri (Muslim).”'),
	(781, 5, 112, 'اِذْ قَالَ الْحَوَارِيُّوْنَ يٰعِيْسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيْعُ رَبُّكَ اَنْ يُّنَزِّلَ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ ۗقَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ', 'i<u>dz</u> q<u>aa</u>la <strong>a</strong>l<u>h</u>aw<u>aa</u>riyyuuna y<u>aa</u> \'iis<u>aa</u> ibna maryama hal yasta<u>th</u>ii\'u rabbuka an yunazzila \'alayn<u>aa</u> m<u>aa</u>-idatan mina <strong>al</strong>ssam<u>aa</u>-i q<u>aa</u>la ittaquu <strong>al</strong>l<u>aa</u>ha in kuntum mu/miniin<strong>a</strong>', '(Ingatlah), ketika pengikut-pengikut Isa yang setia berkata, “Wahai Isa putra Maryam! Bersediakah Tuhanmu menurunkan hidangan dari langit kepada kami?” Isa menjawab, “Bertakwalah kepada Allah jika kamu orang-orang beriman.”'),
	(782, 5, 113, 'قَالُوْا نُرِيْدُ اَنْ نَّأْكُلَ مِنْهَا وَتَطْمَىِٕنَّ قُلُوْبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُوْنَ عَلَيْهَا مِنَ الشّٰهِدِيْنَ ', 'q<u>aa</u>luu nuriidu an na/kula minh<u>aa</u> wata<u>th</u>ma-inna quluubun<u>aa</u> wana\'lama an qad <u>sh</u>adaqtan<u>aa</u> wanakuuna \'alayh<u>aa</u> mina <strong>al</strong>sysy<u>aa</u>hidiin<strong>a</strong>', 'Mereka berkata, “Kami ingin memakan hidangan itu agar tenteram hati kami dan agar kami yakin bahwa engkau telah berkata benar kepada kami, dan kami menjadi orang-orang yang menyaksikan (hidangan itu).”'),
	(783, 5, 114, 'قَالَ عِيْسَى ابْنُ مَرْيَمَ اللهم رَبَّنَآ اَنْزِلْ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ تَكُوْنُ لَنَا عِيْدًا لِّاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِّنْكَ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرّٰزِقِيْنَ', 'q<u>aa</u>la \'iis<u>aa</u> ibnu maryama <strong>al</strong>l<u>aa</u>humma rabban<u>aa</u> anzil \'alayn<u>aa</u> m<u>aa</u>-idatan mina <strong>al</strong>ssam<u>aa</u>-i takuunu lan<u>aa</u> \'iidan li-awwalin<u>aa</u> wa<u>aa</u>khirin<u>aa</u> wa<u>aa</u>yatan minka wa<strong>u</strong>rzuqn<u>aa</u> wa-anta khayru <strong>al</strong>rr<u>aa</u>ziqiin<strong>a</strong>', ' Isa putra Maryam berdoa, “Ya Tuhan kami, turunkanlah kepada kami hidangan dari langit (yang hari turunnya) akan menjadi hari raya bagi kami, yaitu bagi orang-orang yang sekarang bersama kami maupun yang datang setelah kami, dan menjadi tanda bagi kekuasaan Engkau; berilah kami rezeki, dan Engkaulah sebaik-baik pemberi rezeki.”'),
	(784, 5, 115, 'قَالَ اللّٰهُ اِنِّيْ مُنَزِّلُهَا عَلَيْكُمْ ۚ فَمَنْ يَّكْفُرْ بَعْدُ مِنْكُمْ فَاِنِّيْٓ اُعَذِّبُهٗ عَذَابًا لَّآ اُعَذِّبُهٗٓ اَحَدًا مِّنَ الْعٰلَمِيْنَ ࣖ ', 'q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu innii munazziluh<u>aa</u> \'alaykum faman yakfur ba\'du minkum fa-innii u\'a<u>dzdz</u>ibuhu \'a<u>dzaa</u>ban l<u>aa</u> u\'a<u>dzdz</u>ibuhu a<u>h</u>adan mina <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Allah berfirman, “Sungguh, Aku akan menurunkan hidangan itu kepadamu, tetapi barangsiapa kafir di antaramu setelah (turun hidangan) itu, maka sungguh, Aku akan mengazabnya dengan azab yang tidak pernah Aku timpakan kepada seorang pun di antara umat manusia (seluruh alam).”'),
	(785, 5, 116, 'وَاِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُوْنِيْ وَاُمِّيَ اِلٰهَيْنِ مِنْ دُوْنِ اللّٰهِ ۗقَالَ سُبْحٰنَكَ مَا يَكُوْنُ لِيْٓ اَنْ اَقُوْلَ مَا لَيْسَ لِيْ بِحَقٍّ ۗاِنْ كُنْتُ قُلْتُهٗ فَقَدْ عَلِمْتَهٗ ۗتَعْلَمُ مَ', 'wa-i<u>dz</u> q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> ibna maryama a-anta qulta li<strong>l</strong>nn<u>aa</u>si ittakhi<u>dz</u>uunii waummiya il<u>aa</u>hayni min duuni <strong>al</strong>l<u>aa</u>hi q<u>aa</u>la sub<u>haa</u>naka m<u>aa</u> yakuunu lii an aquula m<u>aa</u> laysa lii bi<u>h</u>aqqin in kuntu qultuhu faqad \'alimtahu ta\'lamu m<u>aa</u> fii nafsii wal<u>aa</u> a\'lamu m<u>aa</u> fii nafsika innaka anta \'all<u>aa</u>mu <strong>a</strong>lghuyuub<strong>i</strong>', 'Dan (ingatlah) ketika Allah berfirman, “Wahai Isa putra Maryam! Engkaukah yang mengatakan kepada orang-orang, jadikanlah aku dan ibuku sebagai dua tuhan selain Allah?” (Isa) menjawab, “Mahasuci Engkau, tidak patut bagiku mengatakan apa yang bukan hakku. Jika aku pernah mengatakannya tentulah Engkau telah mengetahuinya. Engkau mengetahui apa yang ada pada diriku dan aku tidak mengetahui apa yang ada pada-Mu. Sungguh, Engkaulah Yang Maha Mengetahui segala yang gaib.”'),
	(786, 5, 117, 'مَا قُلْتُ لَهُمْ اِلَّا مَآ اَمَرْتَنِيْ بِهٖٓ اَنِ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۚوَكُنْتُ عَلَيْهِمْ شَهِيْدًا مَّا دُمْتُ فِيْهِمْ ۚ فَلَمَّا تَوَفَّيْتَنِيْ كُنْتَ اَنْتَ الرَّقِيْبَ عَلَيْهِمْ ۗوَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ', 'm<u>aa</u> qultu lahum ill<u>aa</u> m<u>aa</u> amartanii bihi ani u\'buduu <strong>al</strong>l<u>aa</u>ha rabbii warabbakum wakuntu \'alayhim syahiidan m<u>aa</u> dumtu fiihim falamm<u>aa</u> tawaffaytanii kunta anta <strong>al</strong>rraqiiba \'alayhim wa-anta \'al<u>aa</u> kulli syay-in syahiid<strong>un</strong>', 'Aku tidak pernah mengatakan kepada mereka kecuali apa yang Engkau perintahkan kepadaku (yaitu), “Sembahlah Allah, Tuhanku dan Tuhanmu,” dan aku menjadi saksi terhadap mereka, selama aku berada di tengah-tengah mereka. Maka setelah Engkau mewafatkan aku, Engkaulah yang mengawasi mereka. Dan Engkaulah Yang Maha Menyaksikan atas segala sesuatu.'),
	(787, 5, 118, 'اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَ ۚوَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ', 'in tu\'a<u>dzdz</u>ibhum fa-innahum \'ib<u>aa</u>duka wa-in taghfir lahum fa-innaka anta <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Jika Engkau menyiksa mereka, maka sesungguhnya mereka adalah hamba-hamba-Mu, dan jika Engkau mengampuni mereka, sesungguhnya Engkaulah Yang Mahaperkasa, Mahabijaksana.”'),
	(788, 5, 119, 'قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصّٰدِقِيْنَ صِدْقُهُمْ ۗ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ ', 'q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu h<u>aadzaa</u> yawmu yanfa\'u <strong>al</strong><u>shshaa</u>diqiina <u>sh</u>idquhum lahum jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan ra<u>dh</u>iya <strong>al</strong>l<u>aa</u>hu \'anhum wara<u>dh</u>uu \'anhu <u>dzaa</u>lika <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Allah berfirman, “Inilah saat orang yang benar memperoleh manfaat dari kebenarannya. Mereka memperoleh surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Allah rida kepada mereka dan mereka pun rida kepada-Nya. Itulah kemenangan yang agung.”'),
	(789, 5, 120, 'لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا فِيْهِنَّ ۗوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ ', 'lill<u>aa</u>hi mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> fiihinna wahuwa \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Milik Allah kerajaan langit dan bumi dan apa yang ada di dalamnya; dan Dia Mahakuasa atas segala sesuatu.'),
	(790, 6, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمٰتِ وَالنُّوْرَ ەۗ ثُمَّ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ يَعْدِلُوْنَ', 'al<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a waja\'ala <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti wa<strong>al</strong>nnuura tsumma <strong>al</strong>la<u>dz</u>iina kafaruu birabbihim ya\'diluun<strong>a</strong>', 'Segala puji bagi Allah yang telah menciptakan langit dan bumi, dan menjadikan gelap dan terang, namun demikian orang-orang kafir masih mempersekutukan Tuhan mereka dengan sesuatu.'),
	(791, 6, 2, 'هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ طِيْنٍ ثُمَّ قَضٰٓى اَجَلًا ۗوَاَجَلٌ مُّسَمًّى عِنْدَهٗ ثُمَّ اَنْتُمْ تَمْتَرُوْنَ ', 'huwa <strong>al</strong>la<u>dz</u>ii khalaqakum min <u>th</u>iinin tsumma qa<u>daa</u> ajalan wa-ajalun musamman \'indahu tsumma antum tamtaruun<strong>a</strong>', 'Dialah yang menciptakan kamu dari tanah, kemudian Dia menetapkan ajal (kematianmu), dan batas waktu tertentu yang hanya diketahui oleh-Nya. Namun demikian kamu masih meragukannya.'),
	(792, 6, 3, 'وَهُوَ اللّٰهُ فِى السَّمٰوٰتِ وَفِى الْاَرْضِۗ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُوْنَ ', 'wahuwa <strong>al</strong>l<u>aa</u>hu fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wafii <strong>a</strong>l-ar<u>dh</u>i ya\'lamu sirrakum wajahrakum waya\'lamu m<u>aa</u> taksibuun<strong>a</strong>', 'Dan Dialah Allah (yang disembah), di langit maupun di bumi; Dia mengetahui apa yang kamu rahasiakan dan apa yang kamu nyatakan dan mengetahui (pula) apa yang kamu kerjakan.'),
	(793, 6, 4, 'وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ ', 'wam<u>aa</u> ta/tiihim min <u>aa</u>yatin min <u>aa</u>y<u>aa</u>ti rabbihim ill<u>aa</u> k<u>aa</u>nuu \'anh<u>aa</u> mu\'ri<u>dh</u>iin<strong>a</strong>', 'Dan setiap ayat dari ayat-ayat Tuhan yang sampai kepada mereka (orang kafir), semuanya selalu diingkarinya.'),
	(794, 6, 5, 'فَقَدْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْۗ فَسَوْفَ يَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ', 'faqad ka<u>dzdz</u>abuu bi<strong>a</strong>l<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum fasawfa ya/tiihim anb<u>aa</u>u m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Sungguh, mereka telah mendustakan kebenaran (Al-Qur\'an) ketika sampai kepada mereka, maka kelak akan sampai kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan.'),
	(795, 6, 6, 'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ مَّكَّنّٰهُمْ فِى الْاَرْضِ مَا لَمْ نُمَكِّنْ لَّكُمْ وَاَرْسَلْنَا السَّمَاۤءَ عَلَيْهِمْ مِّدْرَارًا ۖوَّجَعَلْنَا الْاَنْهٰرَ تَجْرِيْ مِنْ تَحْتِهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَنْش', 'alam yaraw kam ahlakn<u>aa</u> min qablihim min qarnin makkann<u>aa</u>hum fii <strong>a</strong>l-ar<u>dh</u>i m<u>aa</u> lam numakkin lakum wa-arsaln<u>aa</u> <strong>al</strong>ssam<u>aa</u>-a \'alayhim midr<u>aa</u>ran waja\'aln<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ra tajrii min ta<u>h</u>tihim fa-ahlakn<u>aa</u>hum bi<u>dz</u>unuubihim wa-ansya/n<u>aa</u> min ba\'dihim qarnan <u>aa</u>khariin<strong>a</strong>', 'Tidakkah mereka memperhatikan berapa banyak generasi sebelum mereka yang telah Kami binasakan, padahal (generasi itu) telah Kami teguhkan kedudukannya di bumi, yaitu keteguhan yang belum pernah Kami berikan kepadamu. Kami curahkan hujan yang lebat untuk mereka dan Kami jadikan sungai-sungai mengalir di bawah mereka, kemudian Kami binasakan mereka karena dosa-dosa mereka sendiri, dan Kami ciptakan generasi yang lain setelah generasi mereka.'),
	(796, 6, 7, 'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتٰبًا فِيْ قِرْطَاسٍ فَلَمَسُوْهُ بِاَيْدِيْهِمْ لَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ', 'walaw nazzaln<u>aa</u> \'alayka kit<u>aa</u>ban fii qir<u>th</u><u>aa</u>sin falamasuuhu bi-aydiihim laq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin<strong>un</strong>', 'Dan sekiranya Kami turunkan kepadamu (Muhammad) tulisan di atas kertas, sehingga mereka dapat memegangnya dengan tangan mereka sendiri, niscaya orang-orang kafir itu akan berkata, “Ini tidak lain hanyalah sihir yang nyata.”'),
	(797, 6, 8, 'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ مَلَكٌ ۗوَلَوْ اَنْزَلْنَا مَلَكًا لَّقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُوْنَ ', 'waq<u>aa</u>luu lawl<u>aa</u> unzila \'alayhi malakun walaw anzaln<u>aa</u> malakan laqu<u>dh</u>iya <strong>a</strong>l-amru tsumma l<u>aa</u> yun<u>zh</u>aruun<strong>a</strong>', 'Dan mereka berkata, “Mengapa tidak diturunkan malaikat kepadanya (Muhammad)?” Jika Kami turunkan malaikat (kepadanya), tentu selesailah urusan itu, tetapi mereka tidak diberi penangguhan (sedikit pun).'),
	(798, 6, 9, 'وَلَوْ جَعَلْنٰهُ مَلَكًا لَّجَعَلْنٰهُ رَجُلًا وَّلَلَبَسْنَا عَلَيْهِمْ مَّا يَلْبِسُوْنَ ', 'walaw ja\'aln<u>aa</u>hu malakan laja\'aln<u>aa</u>hu rajulan walalabasn<u>aa</u> \'alayhim m<u>aa</u> yalbisuun<strong>a</strong>', 'Dan sekiranya rasul itu Kami jadikan (dari) malaikat, pastilah Kami jadikan dia (berwujud) laki-laki, dan (dengan demikian) pasti Kami akan menjadikan mereka tetap ragu sebagaimana kini mereka ragu.'),
	(799, 6, 10, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ', 'walaqadi istuhzi-a birusulin min qablika fa<u>haa</u>qa bi<strong>a</strong>lla<u>dz</u>iina sakhiruu minhum m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Dan sungguh, beberapa rasul sebelum engkau (Muhammad) telah diperolok-olokkan, sehingga turunlah azab kepada orang-orang yang mencemoohkan itu sebagai balasan olok-olokan mereka.'),
	(800, 6, 11, 'قُلْ سِيْرُوْا فِى الْاَرْضِ ثُمَّ انْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ', 'qul siiruu fii <strong>a</strong>l-ar<u>dh</u>i tsumma un<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmuka<u>dzdz</u>ibiin<strong>a</strong>', 'Katakanlah (Muhammad), “Jelajahilah bumi, kemudian perhatikanlah bagai-mana kesudahan orang-orang yang mendustakan itu.”'),
	(801, 6, 12, 'قُلْ لِّمَنْ مَّا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  قُلْ لِّلّٰهِ ۗ كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَ ۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِۗ  اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ ', 'qul liman m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i qul lill<u>aa</u>hi kataba \'al<u>aa</u> nafsihi <strong>al</strong>rra<u>h</u>mata layajma\'annakum il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati l<u>aa</u> rayba fiihi <strong>al</strong>la<u>dz</u>iina khasiruu anfusahum fahum l<u>aa</u> yu/minuun<strong>a</strong>', 'Katakanlah (Muhammad), “Milik siapakah apa yang di langit dan di bumi?” Katakanlah, “Milik Allah.” Dia telah menetapkan (sifat) kasih sayang pada diri-Nya. Dia sungguh akan mengumpulkan kamu pada hari Kiamat yang tidak diragukan lagi. Orang-orang yang merugikan dirinya, mereka itu tidak beriman.'),
	(802, 6, 13, '۞ وَلَهٗ مَا سَكَنَ فِى الَّيْلِ وَالنَّهَارِ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ', 'walahu m<u>aa</u> sakana fii <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri wahuwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Dan milik-Nyalah segala apa yang ada pada malam dan siang hari. Dan Dialah Yang Maha Mendengar, Maha Mengetahui.'),
	(803, 6, 14, 'قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِيًّا فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَكُوْنَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ', 'qul aghayra <strong>al</strong>l<u>aa</u>hi attakhi<u>dz</u>u waliyyan f<u>aath</u>iri <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wahuwa yu<u>th</u>\'imu wal<u>aa</u> yu<u>th</u>\'amu qul innii umirtu an akuuna awwala man aslama wal<u>aa</u> takuunanna mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Katakanlah (Muhammad), “Apakah aku akan menjadikan pelindung selain Allah yang menjadikan langit dan bumi, padahal Dia memberi makan dan tidak diberi makan?” Katakanlah, “Sesungguhnya aku diperintahkan agar aku menjadi orang yang pertama berserah diri (kepada Allah), dan jangan sekali-kali kamu masuk golongan orang-orang musyrik.”'),
	(804, 6, 15, 'قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ ', 'qul innii akh<u>aa</u>fu in \'a<u>sh</u>aytu rabbii \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Katakanlah (Muhammad), “Aku benar-benar takut akan azab hari yang besar (hari Kiamat), jika aku mendurhakai Tuhanku.”'),
	(805, 6, 16, 'مَنْ يُّصْرَفْ عَنْهُ يَوْمَىِٕذٍ فَقَدْ رَحِمَهٗ ۗوَذٰلِكَ الْفَوْزُ الْمُبِيْنُ', 'man yu<u>sh</u>raf \'anhu yawma-i<u>dz</u>in faqad ra<u>h</u>imahu wa<u>dzaa</u>lika <strong>a</strong>lfawzu <strong>a</strong>lmubiin<strong>u</strong>', 'Barangsiapa dijauhkan dari azab atas dirinya pada hari itu, maka sungguh, Allah telah memberikan rahmat kepadanya. Dan itulah kemenangan yang nyata.'),
	(806, 6, 17, 'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗٓ اِلَّا هُوَ ۗوَاِنْ يَّمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa-in yamsaska <strong>al</strong>l<u>aa</u>hu bi<u>dh</u>urrin fal<u>aa</u> k<u>aa</u>syifa lahu ill<u>aa</u> huwa wa-in yamsaska bikhayrin fahuwa \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan jika Allah menimpakan suatu bencana kepadamu, tidak ada yang dapat menghilangkannya selain Dia. Dan jika Dia mendatangkan kebaikan kepadamu, maka Dia Mahakuasa atas segala sesuatu.'),
	(807, 6, 18, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ', 'wahuwa <strong>a</strong>lq<u>aa</u>hiru fawqa \'ib<u>aa</u>dihi wahuwa <strong>a</strong>l<u>h</u>akiimu <strong>a</strong>lkhabiir<strong>u</strong>', 'Dan Dialah yang berkuasa atas hamba-hamba-Nya. Dan Dia Mahabijaksana, Maha Mengetahui.'),
	(808, 6, 19, 'قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةً ۗ قُلِ اللّٰهُ ۗشَهِيْدٌۢ بَيْنِيْ وَبَيْنَكُمْ ۗوَاُوْحِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِهٖ وَمَنْۢ بَلَغَ ۗ اَىِٕنَّكُمْ لَتَشْهَدُوْنَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۗ  قُلْ لَّآ اَشْهَدُ ۚ قُلْ اِ', 'qul ayyu syay-in akbaru syah<u>aa</u>datan quli <strong>al</strong>l<u>aa</u>hu syahiidun baynii wabaynakum wauu<u>h</u>iya ilayya h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>nu li-un<u>dz</u>irakum bihi waman balagha a-innakum latasyhaduuna anna ma\'a <strong>al</strong>l<u>aa</u>hi <u>aa</u>lihatan ukhr<u>aa</u> qul l<u>aa</u> asyhadu qul innam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun wa-innanii barii-un mimm<u>aa</u> tusyrikuun<strong>a</strong>', 'Katakanlah (Muhammad), “Siapakah yang lebih kuat kesaksiannya?” Katakanlah, “Allah, Dia menjadi saksi antara aku dan kamu. Al-Qur\'an ini diwahyukan kepadaku agar dengan itu aku memberi peringatan kepadamu dan kepada orang yang sampai (Al-Qur\'an kepadanya). Dapatkah kamu benar-benar bersaksi bahwa ada tuhan-tuhan lain bersama Allah?” Katakanlah, “Aku tidak dapat bersaksi.” Katakanlah, “Sesungguhnya hanya Dialah Tuhan Yang Maha Esa dan aku berlepas diri dari apa yang kamu persekutukan (dengan Allah).”'),
	(809, 6, 20, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْۘ  اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ ࣖ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba ya\'rifuunahu kam<u>aa</u> ya\'rifuuna abn<u>aa</u>-ahum <strong>al</strong>la<u>dz</u>iina khasiruu anfusahum fahum l<u>aa</u> yu/minuun<strong>a</strong>', 'Orang-orang yang telah Kami berikan Kitab kepadanya, mereka mengenalnya (Muhammad) seperti mereka mengenal anak-anaknya sendiri. Orang-orang yang merugikan dirinya, mereka itu tidak beriman (kepada Allah).'),
	(810, 6, 21, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi innahu l<u>aa</u> yufli<u>h</u>u <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Dan siapakah yang lebih zalim daripada orang yang mengada-adakan suatu kebohongan terhadap Allah, atau yang mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang zalim itu tidak beruntung.'),
	(811, 6, 22, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْٓا اَيْنَ شُرَكَاۤؤُكُمُ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ', 'wayawma na<u>h</u>syuruhum jamii\'an tsumma naquulu lilla<u>dz</u>iina asyrakuu ayna syurak<u>aa</u>ukumu <strong>al</strong>la<u>dz</u>iina kuntum taz\'umuun<strong>a</strong>', 'Dan (ingatlah), pada hari ketika Kami mengumpulkan mereka semua kemudian Kami berfirman kepada orang-orang yang menyekutukan Allah, “Di manakah sembahan-sembahanmu yang dahulu kamu sangka (sekutu-sekutu Kami)?”'),
	(812, 6, 23, 'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّآ اَنْ قَالُوْا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِكِيْنَ ', 'tsumma lam takun fitnatuhum ill<u>aa</u> an q<u>aa</u>luu wa<strong>al</strong>l<u>aa</u>hi rabbin<u>aa</u> m<u>aa</u> kunn<u>aa</u> musyrikiin<strong>a</strong>', 'Kemudian tidaklah ada jawaban bohong mereka, kecuali mengatakan, “Demi Allah, ya Tuhan kami, tidaklah kami mempersekutukan Allah.”'),
	(813, 6, 24, 'اُنْظُرْ كَيْفَ كَذَبُوْا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ', 'tsumma lam takun fitnatuhum ill<u>aa</u> an q<u>aa</u>luu wa<strong>al</strong>l<u>aa</u>hi rabbin<u>aa</u> m<u>aa</u> kunn<u>aa</u> musyrikiin<strong>a</strong>', 'Lihatlah, bagaimana mereka berbohong terhadap diri mereka sendiri. Dan sesembahan yang mereka ada-adakan dahulu akan hilang dari mereka.'),
	(814, 6, 25, 'وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَ ۚوَجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًا ۗوَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَا ۗحَتّٰٓى اِذَا جَاۤءُوْكَ يُجَادِلُوْنَكَ يَقُوْلُ الَّذِيْنَ كَفَرُوْٓا اِ', 'waminhum man yastami\'u ilayka waja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-in yaraw kulla <u>aa</u>yatin l<u>aa</u> yu/minuu bih<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uuka yuj<u>aa</u>diluunaka yaquulu <strong>al</strong>la<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dan di antara mereka ada yang mendengarkan bacaanmu (Muhammad), dan Kami telah menjadikan hati mereka tertutup (sehingga mereka tidak) memahaminya, dan telinganya tersumbat. Dan kalaupun mereka melihat segala tanda (kebenaran), mereka tetap tidak mau beriman kepadanya. Sehingga apabila mereka datang kepadamu untuk membantahmu, orang-orang kafir itu berkata, “Ini (Al-Qur\'an) tidak lain hanyalah dongengan orang-orang terdahulu.”'),
	(815, 6, 26, 'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُ ۚوَاِنْ يُّهْلِكُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ ', 'wahum yanhawna \'anhu wayan-awna \'anhu wa-in yuhlikuuna ill<u>aa</u> anfusahum wam<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan mereka melarang (orang lain) mendengarkan (Al-Qur\'an) dan mereka sendiri menjauhkan diri daripadanya, dan mereka hanyalah membinasakan diri mereka sendiri, sedang mereka tidak menyadari.'),
	(816, 6, 27, 'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلَى النَّارِ فَقَالُوْا يٰلَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيٰتِ رَبِّنَا وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ', 'walaw tar<u>aa</u> i<u>dz</u> wuqifuu \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>ri faq<u>aa</u>luu y<u>aa</u> laytan<u>aa</u> nuraddu wal<u>aa</u> nuka<u>dzdz</u>iba bi-<u>aa</u>y<u>aa</u>ti rabbin<u>aa</u> wanakuuna mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan seandainya engkau (Muhammad) melihat ketika mereka dihadapkan ke neraka, mereka berkata, “Seandainya kami dikembalikan (ke dunia), tentu kami tidak akan mendustakan ayat-ayat Tuhan kami, serta menjadi orang-orang yang beriman.”'),
	(817, 6, 28, 'بَلْ بَدَا لَهُمْ مَّا كَانُوْا يُخْفُوْنَ مِنْ قَبْلُ ۗوَلَوْ رُدُّوْا لَعَادُوْا لِمَا نُهُوْا عَنْهُ وَاِنَّهُمْ لَكٰذِبُوْنَ ', 'bal bad<u>aa</u> lahum m<u>aa</u> k<u>aa</u>nuu yukhfuuna min qablu walaw rudduu la\'<u>aa</u>duu lim<u>aa</u> nuhuu \'anhu wa-innahum lak<u>aadz</u>ibuun<strong>a</strong>', 'Tetapi (sebenarnya) bagi mereka telah nyata kejahatan yang mereka sembunyikan dahulu. Seandainya mereka dikembalikan ke dunia, tentu mereka akan mengulang kembali apa yang telah dilarang mengerjakannya. Mereka itu sungguh pendusta.'),
	(818, 6, 29, 'وَقَالُوْٓا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ', 'waq<u>aa</u>luu in hiya ill<u>aa</u> <u>h</u>ay<u>aa</u>tun<u>aa</u> <strong>al</strong>dduny<u>aa</u> wam<u>aa</u> na<u>h</u>nu bimab\'uutsiin<strong>a</strong>', 'Dan tentu mereka akan mengatakan (pula), “Hidup hanyalah di dunia ini, dan kita tidak akan dibangkitkan.”'),
	(819, 6, 30, 'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلٰى رَبِّهِمْ ۗ قَالَ اَلَيْسَ هٰذَا بِالْحَقِّ ۗقَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ࣖ', 'walaw tar<u>aa</u> i<u>dz</u> wuqifuu \'al<u>aa</u> rabbihim q<u>aa</u>la alaysa h<u>aadzaa</u> bi<strong>a</strong>l<u>h</u>aqqi q<u>aa</u>luu bal<u>aa</u> warabbin<u>aa</u> q<u>aa</u>la fa<u>dz</u>uuquu al\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun<strong>a</strong>', 'Dan seandainya engkau (Muhammad) melihat ketika mereka dihadapkan kepada Tuhannya (tentulah engkau melihat peristiwa yang mengharukan). Dia berfirman, “Bukankah (kebangkitan) ini benar?” Mereka menjawab, “Sungguh benar, demi Tuhan kami.” Dia berfirman, “Rasakanlah azab ini, karena dahulu kamu mengingkarinya.”'),
	(820, 6, 31, 'قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ ۗحَتّٰٓى اِذَا جَاۤءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوْا يٰحَسْرَتَنَا عَلٰى مَا فَرَّطْنَا فِيْهَاۙ وَهُمْ يَحْمِلُوْنَ اَوْزَارَهُمْ عَلٰى ظُهُوْرِهِمْۗ  اَلَا سَاۤءَ مَا يَزِرُوْنَ ', 'qad khasira <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu biliq<u>aa</u>-i <strong>al</strong>l<u>aa</u>hi <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at-humu <strong>al</strong>ss<u>aa</u>\'atu baghtatan q<u>aa</u>luu y<u>aa</u> <u>h</u>asratan<u>aa</u> \'al<u>aa</u> m<u>aa</u> farra<u>th</u>n<u>aa</u> fiih<u>aa</u> wahum ya<u>h</u>miluuna awz<u>aa</u>rahum \'al<u>aa</u> <u>zh</u>uhuurihim <strong>a</strong>l<u>aa</u> s<u>aa</u>-a m<u>aa</u> yaziruun<strong>a</strong>', 'Sungguh rugi orang-orang yang mendustakan pertemuan dengan Allah; sehingga apabila Kiamat datang kepada mereka secara tiba-tiba, mereka berkata, “Alangkah besarnya penyesalan kami terhadap kelalaian kami tentang Kiamat itu,” sambil mereka memikul dosa-dosa di atas punggungnya. Alangkah buruknya apa yang mereka pikul itu.'),
	(821, 6, 32, 'وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا لَعِبٌ وَّلَهْوٌ ۗوَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ ', 'wam<u>aa</u> <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> ill<u>aa</u> la\'ibun walahwun wala<strong>l</strong>dd<u>aa</u>ru <strong>a</strong>l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina yattaquuna afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Dan kehidupan dunia ini hanyalah permainan dan senda gurau. Sedangkan negeri akhirat itu, sungguh lebih baik bagi orang-orang yang bertakwa. Tidakkah kamu mengerti?'),
	(822, 6, 33, 'قَدْ نَعْلَمُ اِنَّهٗ لَيَحْزُنُكَ الَّذِيْ يَقُوْلُوْنَ فَاِنَّهُمْ لَا يُكَذِّبُوْنَكَ وَلٰكِنَّ الظّٰلِمِيْنَ بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ ', 'qad na\'lamu innahu laya<u>h</u>zunuka <strong>al</strong>la<u>dz</u>ii yaquuluuna fa-innahum l<u>aa</u> yuka<u>dzdz</u>ibuunaka wal<u>aa</u>kinna <strong>al</strong><u>zhzhaa</u>limiina bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi yaj<u>h</u>aduun<strong>a</strong>', 'Sungguh, Kami mengetahui bahwa apa yang mereka katakan itu menyedihkan hatimu (Muhammad), (janganlah bersedih hati) karena sebenarnya mereka bukan mendustakan engkau, tetapi orang yang zalim itu mengingkari ayat-ayat Allah.'),
	(823, 6, 34, 'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَ فَصَبَرُوْا عَلٰى مَا كُذِّبُوْا وَاُوْذُوْا حَتّٰٓى اَتٰىهُمْ نَصْرُنَا ۚوَلَا مُبَدِّلَ لِكَلِمٰتِ اللّٰهِ ۚوَلَقَدْ جَاۤءَكَ مِنْ نَّبَإِ۟ى الْمُرْسَلِيْنَ ', 'walaqad ku<u>dzdz</u>ibat rusulun min qablika fa<u>sh</u>abaruu \'al<u>aa</u> m<u>aa</u> ku<u>dzdz</u>ibuu wauu<u>dz</u>uu <u>h</u>att<u>aa</u> at<u>aa</u>hum na<u>sh</u>run<u>aa</u> wal<u>aa</u> mubaddila likalim<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi walaqad j<u>aa</u>-aka min naba-i <strong>a</strong>lmursaliin<strong>a</strong>', 'Dan sesungguhnya rasul-rasul sebelum engkau pun telah didustakan, tetapi mereka sabar terhadap pendustaan dan penganiayaan (yang dilakukan) terhadap mereka, sampai datang pertolongan Kami kepada mereka. Dan tidak ada yang dapat mengubah kalimat-kalimat (ketetapan) Allah. Dan sungguh, telah datang kepadamu sebagian dari berita rasul-rasul itu.'),
	(824, 6, 35, 'وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقًا فِى الْاَرْضِ اَوْ سُلَّمًا فِى السَّمَاۤءِ فَتَأْتِيَهُمْ بِاٰيَةٍ ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُوْنَنَّ مِنَ الْجٰهِلِيْنَ ', 'wa-in k<u>aa</u>na kabura \'alayka i\'r<u>aad</u>uhum fa-ini ista<u>th</u>a\'ta an tabtaghiya nafaqan fii <strong>a</strong>l-ar<u>dh</u>i aw sullaman fii <strong>al</strong>ssam<u>aa</u>-i fata/tiyahum bi-<u>aa</u>yatin walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu lajama\'ahum \'al<u>aa</u> <strong>a</strong>lhud<u>aa</u> fal<u>aa</u> takuunanna mina <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Dan jika keberpalingan mereka terasa berat bagimu (Muhammad), maka sekiranya engkau dapat membuat lubang di bumi atau tangga ke langit lalu engkau dapat mendatangkan mukjizat kepada mereka, (maka buatlah). Dan sekiranya Allah menghendaki, tentu Dia jadikan mereka semua mengikuti petunjuk, sebab itu janganlah sekali-kali engkau termasuk orang-orang yang bodoh.'),
	(825, 6, 36, '۞ اِنَّمَا يَسْتَجِيْبُ الَّذِيْنَ يَسْمَعُوْنَ ۗوَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُوْنَ ', 'innam<u>aa</u> yastajiibu <strong>al</strong>la<u>dz</u>iina yasma\'uuna wa<strong>a</strong>lmawt<u>aa</u> yab\'atsuhumu <strong>al</strong>l<u>aa</u>hu tsumma ilayhi yurja\'uun<strong>a</strong>', 'Hanya orang-orang yang mendengar sajalah yang mematuhi (seruan Allah), dan orang-orang yang mati, kelak akan dibangkitkan oleh Allah, kemudian kepada-Nya mereka dikembalikan.'),
	(826, 6, 37, 'وَقَالُوْا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُّنَزِّلَ اٰيَةً وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'waq<u>aa</u>luu lawl<u>aa</u> nuzzila \'alayhi <u>aa</u>yatun min rabbihi qul inna <strong>al</strong>l<u>aa</u>ha q<u>aa</u>dirun \'al<u>aa</u> an yunazzila <u>aa</u>yatan wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan mereka (orang-orang musyrik) berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu mukjizat dari Tuhannya?” Katakanlah, “Sesungguhnya Allah berkuasa menurunkan suatu mukjizat, tetapi kebanyakan mereka tidak mengetahui.”'),
	(827, 6, 38, 'وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ وَلَا طٰۤىِٕرٍ يَّطِيْرُ بِجَنَاحَيْهِ اِلَّآ اُمَمٌ اَمْثَالُكُمْ ۗمَا فَرَّطْنَا فِى الْكِتٰبِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُوْنَ ', 'wam<u>aa</u> min d<u>aa</u>bbatin fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> <u>th</u><u>aa</u>-irin ya<u>th</u>iiru bijan<u>aah</u>ayhi ill<u>aa</u> umamun amts<u>aa</u>lukum m<u>aa</u> farra<u>th</u>n<u>aa</u> fii <strong>a</strong>lkit<u>aa</u>bi min syay-in tsumma il<u>aa</u> rabbihim yu<u>h</u>syaruun<strong>a</strong>', 'Dan tidak ada seekor binatang pun yang ada di bumi dan burung-burung yang terbang dengan kedua sayapnya, melainkan semuanya merupakan umat-umat (juga) seperti kamu. Tidak ada sesuatu pun yang Kami luputkan di dalam Kitab, kemudian kepada Tuhan mereka dikumpulkan.'),
	(828, 6, 39, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا صُمٌّ وَّبُكْمٌ فِى الظُّلُمٰتِۗ مَنْ يَّشَاِ اللّٰهُ يُضْلِلْهُ وَمَنْ يَّشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'wa<strong>a</strong>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> <u>sh</u>ummun wabukmun fii <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti man yasya-i <strong>al</strong>l<u>aa</u>hu yu<u>dh</u>lilhu waman yasya/ yaj\'alhu \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dan orang-orang yang mendustakan ayat-ayat Kami adalah tuli, bisu dan berada dalam gelap gulita. Barangsiapa dikehendaki Allah (dalam kesesatan), niscaya disesatkan-Nya. Dan barangsiapa dikehendaki Allah (untuk diberi petunjuk), niscaya Dia menjadikannya berada di atas jalan yang lurus.'),
	(829, 6, 40, 'قُلْ اَرَءَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُوْنَۚ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'qul ara-aytakum in at<u>aa</u>kum \'a<u>dzaa</u>bu <strong>al</strong>l<u>aa</u>hi aw atatkumu <strong>al</strong>ss<u>aa</u>\'atu aghayra <strong>al</strong>l<u>aa</u>hi tad\'uuna in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Katakanlah (Muhammad), “Terangkanlah kepadaku jika siksaan Allah sampai kepadamu, atau hari Kiamat sampai kepadamu, apakah kamu akan menyeru (tuhan) selain Allah, jika kamu orang yang benar!”'),
	(830, 6, 41, 'بَلْ اِيَّاهُ تَدْعُوْنَ فَيَكْشِفُ مَا تَدْعُوْنَ اِلَيْهِ اِنْ شَاۤءَ وَتَنْسَوْنَ مَا تُشْرِكُوْنَ ࣖ', 'bal iyy<u>aa</u>hu tad\'uuna fayaksyifu m<u>aa</u> tad\'uuna ilayhi in sy<u>aa</u>-a watansawna m<u>aa</u> tusyrikuun<strong>a</strong>', '(Tidak), hanya kepada-Nya kamu minta tolong. Jika Dia menghendaki, Dia hilangkan apa (bahaya) yang kamu mohonkan kepada-Nya, dan kamu tinggalkan apa yang kamu persekutukan (dengan Allah).'),
	(831, 6, 42, 'وَلَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَاَخَذْنٰهُمْ بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَتَضَرَّعُوْنَ ', 'walaqad arsaln<u>aa</u> il<u>aa</u> umamin min qablika fa-akha<u>dz</u>n<u>aa</u>hum bi<strong>a</strong>lba/s<u>aa</u>-i wa<strong>al</strong><u>dhdh</u>arr<u>aa</u>-i la\'allahum yata<u>dh</u>arra\'uun<strong>a</strong>', 'Dan sungguh, Kami telah mengutus (para rasul) kepada umat-umat sebelum engkau, kemudian Kami siksa mereka dengan (menimpakan) kemelaratan dan kesengsaraan, agar mereka memohon (kepada Allah) dengan kerendahan hati.'),
	(832, 6, 43, 'فَلَوْلَآ اِذْ جَاۤءَهُمْ بَأْسُنَا تَضَرَّعُوْا وَلٰكِنْ قَسَتْ قُلُوْبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ مَا كَانُوْا يَعْمَلُوْنَ ', 'falawl<u>aa</u> i<u>dz</u> j<u>aa</u>-ahum ba/sun<u>aa</u> ta<u>dh</u>arra\'uu wal<u>aa</u>kin qasat quluubuhum wazayyana lahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Tetapi mengapa mereka tidak memohon (kepada Allah) dengan kerendahan hati ketika siksaan Kami datang menimpa mereka? Bahkan hati mereka telah menjadi keras dan setan pun menjadikan terasa indah bagi mereka apa yang selalu mereka kerjakan.'),
	(833, 6, 44, 'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۗ حَتّٰٓى اِذَا فَرِحُوْا بِمَآ اُوْتُوْٓا اَخَذْنٰهُمْ بَغْتَةً فَاِذَا هُمْ مُّبْلِسُوْنَ', 'falamm<u>aa</u> nasuu m<u>aa</u> <u>dz</u>ukkiruu bihi fata<u>h</u>n<u>aa</u> \'alayhim abw<u>aa</u>ba kulli syay-in <u>h</u>att<u>aa</u> i<u>dzaa</u> fari<u>h</u>uu bim<u>aa</u> uutuu akha<u>dz</u>n<u>aa</u>hum baghtatan fa-i<u>dzaa</u> hum mublisuun<strong>a</strong>', 'Maka ketika mereka melupakan peringatan yang telah diberikan kepada mereka, Kami pun membukakan semua pintu (kesenangan) untuk mereka. Sehingga ketika mereka bergembira dengan apa yang telah diberikan kepada mereka, Kami siksa mereka secara tiba-tiba, maka ketika itu mereka terdiam putus asa.'),
	(834, 6, 45, 'فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِيْنَ ظَلَمُوْاۗ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ', 'faqu<u>th</u>i\'a d<u>aa</u>biru <strong>a</strong>lqawmi <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu wa<strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Maka orang-orang yang zalim itu dimusnahkan sampai ke akar-akarnya. Dan segala puji bagi Allah, Tuhan seluruh alam.'),
	(835, 6, 46, 'قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوْبِكُمْ مَّنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِهٖۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ ثُمَّ هُمْ يَصْدِفُوْنَ ', 'qul ara-aytum in akha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu sam\'akum wa-ab<u>shaa</u>rakum wakhatama \'al<u>aa</u> quluubikum man il<u>aa</u>hun ghayru <strong>al</strong>l<u>aa</u>hi ya/tiikum bihi un<u>zh</u>ur kayfa nu<u>sh</u>arrifu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti tsumma hum ya<u>sh</u>difuun<strong>a</strong>', 'Katakanlah (Muhammad), “Terangkanlah kepadaku jika Allah mencabut pendengaran dan penglihatan serta menutup hatimu, siapakah tuhan selain Allah yang kuasa mengembalikannya kepadamu?” Perhatikanlah, bagaimana Kami menjelaskan berulang-ulang (kepada mereka) tanda-tanda kekuasaan (Kami), tetapi mereka tetap berpaling.'),
	(836, 6, 47, 'قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظّٰلِمُوْنَ ', 'qul ara-aytakum in at<u>aa</u>kum \'a<u>dzaa</u>bu <strong>al</strong>l<u>aa</u>hi baghtatan aw jahratan hal yuhlaku ill<u>aa</u> <strong>a</strong>lqawmu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Katakanlah (Muhammad), “Terangkanlah kepadaku jika siksaan Allah sampai kepadamu secara tiba-tiba atau terang-terangan, maka adakah yang dibinasakan (Allah) selain orang-orang yang zalim?”'),
	(837, 6, 48, 'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ', 'wam<u>aa</u> nursilu <strong>a</strong>lmursaliina ill<u>aa</u> mubasysyiriina wamun<u>dz</u>iriina faman <u>aa</u>mana wa-a<u>sh</u>la<u>h</u>a fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Para rasul yang Kami utus itu adalah untuk memberi kabar gembira dan memberi peringatan. Barangsiapa beriman dan mengadakan perbaikan, maka tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(838, 6, 49, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوْا يَفْسُقُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yamassuhumu <strong>a</strong>l\'a<u>dzaa</u>bu bim<u>aa</u> k<u>aa</u>nuu yafsuquun<strong>a</strong>', 'Dan orang-orang yang mendustakan ayat-ayat Kami akan ditimpa azab karena mereka selalu berbuat fasik (berbuat dosa).'),
	(839, 6, 50, 'قُلْ لَّآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ لَكُمْ اِنِّيْ مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّۗ  قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُۗ اَفَلَا تَتَفَكَّرُوْنَ ࣖ', 'qul l<u>aa</u> aquulu lakum \'indii khaz<u>aa</u>-inu <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> a\'lamu <strong>a</strong>lghayba wal<u>aa</u> aquulu lakum innii malakun in attabi\'u ill<u>aa</u> m<u>aa</u> yuu<u>haa</u> ilayya qul hal yastawii <strong>a</strong>l-a\'m<u>aa</u> wa<strong>a</strong>lba<u>sh</u>iiru afal<u>aa</u> tatafakkaruun<strong>a</strong>', 'Katakanlah (Muhammad), “Aku tidak mengatakan kepadamu, bahwa perbendaharaan Allah ada padaku, dan aku tidak mengetahui yang gaib dan aku tidak (pula) mengatakan kepadamu bahwa aku malaikat. Aku hanya mengikuti apa yang diwahyukan kepadaku.” Katakanlah, “Apakah sama orang yang buta dengan orang yang melihat? Apakah kamu tidak memikirkan(nya)?”'),
	(840, 6, 51, 'وَاَنْذِرْ بِهِ الَّذِيْنَ يَخَافُوْنَ اَنْ يُّحْشَرُوْٓا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِّنْ دُوْنِهٖ وَلِيٌّ وَّلَا شَفِيْعٌ لَّعَلَّهُمْ يَتَّقُوْنَ', 'wa-an<u>dz</u>ir bihi <strong>al</strong>la<u>dz</u>iina yakh<u>aa</u>fuuna an yu<u>h</u>syaruu il<u>aa</u> rabbihim laysa lahum min duunihi waliyyun wal<u>aa</u> syafii\'un la\'allahum yattaquun<strong>a</strong>', 'Peringatkanlah dengannya (Al-Qur\'an) itu kepada orang yang takut akan dikumpulkan menghadap Tuhannya (pada hari Kiamat), tidak ada bagi mereka pelindung dan pemberi syafaat (pertolongan) selain Allah, agar mereka bertakwa.'),
	(841, 6, 52, 'وَلَا تَطْرُدِ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ ۗمَا عَلَيْكَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِّنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُوْنَ مِنَ الظّٰلِمِيْنَ ', 'wal<u>aa</u> ta<u>th</u>rudi <strong>al</strong>la<u>dz</u>iina yad\'uuna rabbahum bi<strong>a</strong>lghad<u>aa</u>ti wa<strong>a</strong>l\'asyiyyi yuriiduuna wajhahu m<u>aa</u> \'alayka min <u>h</u>is<u>aa</u>bihim min syay-in wam<u>aa</u> min <u>h</u>is<u>aa</u>bika \'alayhim min syay-in fata<u>th</u>rudahum fatakuuna mina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Janganlah engkau mengusir orang-orang yang menyeru Tuhannya pada pagi dan petang hari, mereka mengharapkan keridaan-Nya. Engkau tidak memikul tanggung jawab sedikit pun terhadap perbuatan mereka dan mereka tidak memikul tanggung jawab sedikit pun terhadap perbuatanmu, yang menyebabkan engkau (berhak) mengusir mereka, sehingga engkau termasuk orang-orang yang zalim.'),
	(842, 6, 53, 'وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِّيَقُوْلُوْٓا اَهٰٓؤُلَاۤءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِّنْۢ بَيْنِنَاۗ  اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشّٰكِرِيْنَ ', 'waka<u>dzaa</u>lika fatann<u>aa</u> ba\'<u>dh</u>ahum biba\'<u>dh</u>in liyaquuluu ah<u>aa</u>ul<u>aa</u>-i manna <strong>al</strong>l<u>aa</u>hu \'alayhim min baynin<u>aa</u> alaysa <strong>al</strong>l<u>aa</u>hu bi-a\'lama bi<strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Demikianlah Kami telah menguji sebagian mereka (orang yang kaya) dengan sebagian yang lain (orang yang miskin), agar mereka (orang yang kaya itu) berkata, “Orang-orang semacam inikah di antara kita yang diberi anugerah oleh Allah?” (Allah berfirman), “Tidakkah Allah lebih mengetahui tentang mereka yang bersyukur (kepada-Nya)?”'),
	(843, 6, 54, 'وَاِذَا جَاۤءَكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاٰيٰتِنَا فَقُلْ سَلٰمٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهٗ مَنْ عَمِلَ مِنْكُمْ سُوْۤءًاۢ بِجَهَالَةٍ ثُمَّ تَابَ مِنْۢ بَعْدِهٖ وَاَصْلَحَ فَاَنَّهٗ غَفُوْرٌ رَّحِيْمٌ  ', 'wa-i<u>dzaa</u> j<u>aa</u>-aka <strong>al</strong>la<u>dz</u>iina yu/minuuna bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> faqul sal<u>aa</u>mun \'alaykum kataba rabbukum \'al<u>aa</u> nafsihi <strong>al</strong>rra<u>h</u>mata annahu man \'amila minkum suu-an bijah<u>aa</u>latin tsumma t<u>aa</u>ba min ba\'dihi wa-a<u>sh</u>la<u>h</u>a fa-annahu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan apabila orang-orang yang beriman kepada ayat-ayat Kami datang kepadamu, maka katakanlah, “Salamun ‘alaikum  (selamat sejahtera untuk kamu).” Tuhanmu telah menetapkan sifat kasih sayang pada diri-Nya, (yaitu) barang-siapa berbuat kejahatan di antara kamu karena kebodohan, kemudian dia bertobat setelah itu dan memperbaiki diri, maka Dia Maha Pengampun, Maha Penyayang.'),
	(844, 6, 55, 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلِتَسْتَبِيْنَ سَبِيْلُ الْمُجْرِمِيْنَ ࣖ', 'waka<u>dzaa</u>lika nufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti walitastabiina sabiilu <strong>a</strong>lmujrimiin<strong>a</strong>', 'Dan demikianlah Kami terangkan ayat-ayat Al-Qur\'an, (agar terlihat jelas jalan orang-orang yang saleh) dan agar terlihat jelas (pula) jalan orang-orang yang berdosa.'),
	(845, 6, 56, 'قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗ قُلْ لَّآ اَتَّبِعُ اَهْوَاۤءَكُمْۙ قَدْ ضَلَلْتُ اِذًا وَّمَآ اَنَا۠ مِنَ الْمُهْتَدِيْنَ ', 'qul innii nuhiitu an a\'buda <strong>al</strong>la<u>dz</u>iina tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi qul l<u>aa</u> attabi\'u ahw<u>aa</u>-akum qad <u>dh</u>alaltu i<u>dz</u>an wam<u>aa</u> an<u>aa</u> mina <strong>a</strong>lmuhtadiin<strong>a</strong>', 'Katakanlah (Muhammad), “Aku dilarang menyembah tuhan-tuhan yang kamu sembah selain Allah.” Katakanlah, “Aku tidak akan mengikuti keinginanmu. Jika berbuat demikian, sungguh tersesatlah aku, dan aku tidak termasuk orang yang mendapat petunjuk.”'),
	(846, 6, 57, 'قُلْ اِنِّيْ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَكَذَّبْتُمْ بِهٖۗ مَا عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗيَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِلِيْنَ', 'qul innii \'al<u>aa</u> bayyinatin min rabbii waka<u>dzdz</u>abtum bihi m<u>aa</u> \'indii m<u>aa</u> tasta\'jiluuna bihi ini <strong>a</strong>l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi yaqu<u>shsh</u>u <strong>a</strong>l<u>h</u>aqqa wahuwa khayru <strong>a</strong>lf<u>aas</u>iliin<strong>a</strong>', 'Katakanlah (Muhammad), “Aku (berada) di atas keterangan yang nyata (Al-Qur\'an) dari Tuhanku sedang kamu mendustakannya. Bukanlah kewenanganku (untuk menurunkan azab) yang kamu tuntut untuk disegerakan kedatangannya. Menetapkan (hukum itu) hanyalah hak Allah. Dia menerangkan kebenaran dan Dia pemberi keputusan yang terbaik.”'),
	(847, 6, 58, 'قُلْ لَّوْ اَنَّ عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖ لَقُضِيَ الْاَمْرُ بَيْنِيْ وَبَيْنَكُمْ ۗوَاللّٰهُ اَعْلَمُ بِالظّٰلِمِيْنَ ', 'qul law anna \'indii m<u>aa</u> tasta\'jiluuna bihi laqu<u>dh</u>iya <strong>a</strong>l-amru baynii wabaynakum wa<strong>al</strong>l<u>aa</u>hu a\'lamu bi<strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Katakanlah (Muhammad), “Seandainya ada padaku apa (azab) yang kamu minta agar disegerakan kedatangannya, tentu selesailah segala perkara antara aku dan kamu.” Dan Allah lebih mengetahui tentang orang-orang yang zalim.'),
	(848, 6, 59, '۞ وَعِنْدَهٗ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَآ اِلَّا هُوَۗ وَيَعْلَمُ مَا فِى الْبَرِّ وَالْبَحْرِۗ وَمَا تَسْقُطُ مِنْ وَّرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِيْ ظُلُمٰتِ الْاَرْضِ وَلَا رَطْبٍ وَّلَا يَابِسٍ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ', 'wa\'indahu maf<u>aa</u>ti<u>h</u>u <strong>a</strong>lghaybi l<u>aa</u> ya\'lamuh<u>aa</u> ill<u>aa</u> huwa waya\'lamu m<u>aa</u> fii <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri wam<u>aa</u> tasqu<u>th</u>u min waraqatin ill<u>aa</u> ya\'lamuh<u>aa</u> wal<u>aa</u> <u>h</u>abbatin fii <u>zh</u>ulum<u>aa</u>ti <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> ra<u>th</u>bin wal<u>aa</u> y<u>aa</u>bisin ill<u>aa</u> fii kit<u>aa</u>bin mubiin<strong>in</strong>', 'Dan kunci-kunci semua yang gaib ada pada-Nya; tidak ada yang mengetahui selain Dia. Dia mengetahui apa yang ada di darat dan di laut. Tidak ada sehelai daun pun yang gugur yang tidak diketahui-Nya. Tidak ada sebutir biji pun dalam kegelapan bumi dan tidak pula sesuatu yang basah atau yang kering, yang tidak tertulis dalam Kitab yang nyata (Lauh Mahfuzh).'),
	(849, 6, 60, 'وَهُوَ الَّذِيْ يَتَوَفّٰىكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيْهِ لِيُقْضٰٓى اَجَلٌ مُّسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ࣖ', 'wahuwa <strong>al</strong>la<u>dz</u>ii yatawaff<u>aa</u>kum bi<strong>a</strong>llayli waya\'lamu m<u>aa</u> jara<u>h</u>tum bi<strong>al</strong>nnah<u>aa</u>ri tsumma yab\'atsukum fiihi liyuq<u>daa</u> ajalun musamman tsumma ilayhi marji\'ukum tsumma yunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan Dialah yang menidurkan kamu pada malam hari dan Dia mengetahui apa yang kamu kerjakan pada siang hari. Kemudian Dia membangunkan kamu pada siang hari untuk disempurnakan umurmu yang telah ditetapkan. Kemudian kepada-Nya tempat kamu kembali, lalu Dia memberitahukan kepadamu apa yang telah kamu kerjakan.'),
	(850, 6, 61, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً ۗحَتّٰٓى اِذَا جَاۤءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُوْنَ ', 'wahuwa <strong>a</strong>lq<u>aa</u>hiru fawqa \'ib<u>aa</u>dihi wayursilu \'alaykum <u>h</u>afa<u>zh</u>atan <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a a<u>h</u>adakumu <strong>a</strong>lmawtu tawaffat-hu rusulun<u>aa</u> wahum l<u>aa</u> yufarri<u>th</u>uun<strong>a</strong>', 'Dan Dialah Penguasa mutlak atas semua hamba-Nya, dan diutus-Nya kepadamu malaikat-malaikat penjaga, sehingga apabila kematian datang kepada salah seorang di antara kamu, malaikat-malaikat Kami mencabut nyawanya, dan mereka tidak melalaikan tugasnya.'),
	(851, 6, 62, 'ثُمَّ رُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّۗ  اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِبِيْنَ ', 'tsumma rudduu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi mawl<u>aa</u>humu <strong>a</strong>l<u>h</u>aqqi <strong>a</strong>l<u>aa</u> lahu <strong>a</strong>l<u>h</u>ukmu wahuwa asra\'u <strong>a</strong>l<u>haa</u>sibiin<strong>a</strong>', 'Kemudian mereka (hamba-hamba Allah) dikembalikan kepada Allah, penguasa mereka yang sebenarnya. Ketahuilah bahwa segala hukum (pada hari itu) ada pada-Nya. Dan Dialah pembuat perhitungan yang paling cepat.'),
	(852, 6, 63, 'قُلْ مَنْ يُّنَجِّيْكُمْ مِّنْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ تَدْعُوْنَهٗ تَضَرُّعًا وَّخُفْيَةً ۚ لَىِٕنْ اَنْجٰىنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ ', 'qul man yunajjiikum min <u>zh</u>ulum<u>aa</u>ti <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri tad\'uunahu ta<u>dh</u>arru\'an wakhufyatan la-in anj<u>aa</u>n<u>aa</u> min h<u>aadz</u>ihi lanakuunanna mina <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Katakanlah (Muhammad), “Siapakah yang dapat menyelamatkan kamu dari bencana di darat dan di laut, ketika kamu berdoa kepada-Nya dengan rendah hati dan dengan suara yang lembut?” (Dengan mengatakan), “Sekiranya Dia menyelamatkan kami dari (bencana) ini, tentulah kami menjadi orang-orang yang bersyukur.”'),
	(853, 6, 64, 'قُلِ اللّٰهُ يُنَجِّيْكُمْ مِّنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُوْنَ ', 'quli <strong>al</strong>l<u>aa</u>hu yunajjiikum minh<u>aa</u> wamin kulli karbin tsumma antum tusyrikuun<strong>a</strong>', 'Katakanlah (Muhammad), “Allah yang menyelamatkan kamu dari bencana itu dan dari segala macam kesusahan, namun kemudian kamu (kembali) mempersekutukan-Nya.”'),
	(854, 6, 65, 'قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَّبْعَثَ عَلَيْكُمْ عَذَابًا مِّنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعًا وَّيُذِيْقَ بَعْضَكُمْ بَأْسَ بَعْضٍۗ  اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ لَعَلَّهُمْ يَفْقَهُوْنَ ', 'qul huwa <strong>a</strong>lq<u>aa</u>diru \'al<u>aa</u> an yab\'atsa \'alaykum \'a<u>dzaa</u>ban min fawqikum aw min ta<u>h</u>ti arjulikum aw yalbisakum syiya\'an wayu<u>dz</u>iiqa ba\'<u>dh</u>akum ba/sa ba\'<u>dh</u>in un<u>zh</u>ur kayfa nu<u>sh</u>arrifu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti la\'allahum yafqahuun<strong>a</strong>', 'Katakanlah (Muhammad), “Dialah yang berkuasa mengirimkan azab kepadamu, dari atas atau dari bawah kakimu atau Dia mencampurkan kamu dalam golongan-golongan (yang saling bertentangan) dan merasakan kepada sebagian kamu keganasan sebagian yang lain.” Perhatikanlah, bagaimana Kami menjelaskan berulang-ulang tanda-tanda (kekuasaan Kami) agar mereka memahami(nya).'),
	(855, 6, 66, 'وَكَذَّبَ بِهٖ قَوْمُكَ وَهُوَ الْحَقُّۗ قُلْ لَّسْتُ عَلَيْكُمْ بِوَكِيْلٍ ۗ', 'waka<u>dzdz</u>aba bihi qawmuka wahuwa <strong>a</strong>l<u>h</u>aqqu qul lastu \'alaykum biwakiil<strong>in</strong>', 'Dan kaummu mendustakannya (azab) padahal (azab) itu benar adanya. Katakanlah (Muhammad), “Aku ini bukanlah penanggung jawab kamu.”'),
	(856, 6, 67, 'لِكُلِّ نَبَاٍ مُّسْتَقَرٌّ وَّسَوْفَ تَعْلَمُوْنَ ', 'likulli naba-in mustaqarrun wasawfa ta\'lamuun<strong>a</strong>', 'Setiap berita (yang dibawa oleh rasul) ada (waktu) terjadinya dan kelak kamu akan mengetahui.'),
	(857, 6, 68, 'وَاِذَا رَاَيْتَ الَّذِيْنَ يَخُوْضُوْنَ فِيْٓ اٰيٰتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖۗ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطٰنُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظّٰلِمِيْنَ ', 'wa-i<u>dzaa</u> ra-ayta <strong>al</strong>la<u>dz</u>iina yakhuu<u>dh</u>uuna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> fa-a\'ri<u>dh</u> \'anhum <u>h</u>att<u>aa</u> yakhuu<u>dh</u>uu fii <u>h</u>adiitsin ghayrihi wa-imm<u>aa</u> yunsiyannaka <strong>al</strong>sysyay<u>th</u><u>aa</u>nu fal<u>aa</u> taq\'ud ba\'da <strong>al</strong><u>dzdz</u>ikr<u>aa</u> ma\'a <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Apabila engkau (Muhammad) melihat orang-orang memperolok-olokkan ayat-ayat Kami, maka tinggalkanlah mereka hingga mereka beralih ke pembicaraan lain. Dan jika setan benar-benar menjadikan engkau lupa (akan larangan ini), setelah ingat kembali janganlah engkau duduk bersama orang-orang yang zalim.  '),
	(858, 6, 69, 'وَمَا عَلَى الَّذِيْنَ يَتَّقُوْنَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُوْنَ', 'wam<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina yattaquuna min <u>h</u>is<u>aa</u>bihim min syay-in wal<u>aa</u>kin <u>dz</u>ikr<u>aa</u> la\'allahum yattaquun<strong>a</strong>', 'Orang-orang yang bertakwa tidak ada tanggung jawab sedikit pun atas (dosa-dosa) mereka; tetapi (berkewajiban) mengingatkan agar mereka (juga) bertakwa.'),
	(859, 6, 70, 'وَذَرِ الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَعِبًا وَّلَهْوًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِهٖٓ اَنْ تُبْسَلَ نَفْسٌۢ بِمَا كَسَبَتْۖ  لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ وَلِيٌّ وَّلَا شَفِيْعٌ ۚوَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَّا يُؤْخَذْ مِ', 'wa<u>dz</u>ari <strong>al</strong>la<u>dz</u>iina ittakha<u>dz</u>uu diinahum la\'iban walahwan wagharrat-humu <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> wa<u>dz</u>akkir bihi an tubsala nafsun bim<u>aa</u> kasabat laysa lah<u>aa</u> min duuni <strong>al</strong>l<u>aa</u>hi waliyyun wal<u>aa</u> syafii\'un wa-in ta\'dil kulla \'adlin l<u>aa</u> yu/kha<u>dz</u> minh<u>aa</u> ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina ubsiluu bim<u>aa</u> kasabuu lahum syar<u>aa</u>bun min <u>h</u>amiimin wa\'a<u>dzaa</u>bun <strong>a</strong>liimun bim<u>aa</u> k<u>aa</u>nuu yakfuruun<strong>a</strong>', 'Tinggalkanlah orang-orang yang menjadikan agamanya sebagai permainan dan senda gurau, dan mereka telah tertipu oleh kehidupan dunia. Peringatkanlah (mereka) dengan Al-Qur\'an agar setiap orang tidak terjerumus (ke dalam neraka), karena perbuatannya sendiri. Tidak ada baginya pelindung dan pemberi syafaat (pertolongan) selain Allah. Dan jika dia hendak menebus dengan segala macam tebusan apa pun, niscaya tidak akan diterima. Mereka itulah orang-orang yang dijerumuskan (ke dalam neraka), karena perbuatan mereka sendiri. Mereka mendapat minuman dari air yang mendidih dan azab yang pedih karena kekafiran mereka dahulu.'),
	(860, 6, 71, 'قُلْ اَنَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰىنَا اللّٰهُ كَالَّذِى اسْتَهْوَتْهُ الشَّيٰطِيْنُ فِى الْاَرْضِ حَيْرَانَ لَهٗٓ اَصْحٰبٌ يَّدْعُوْنَهٗٓ اِلَى الْهُدَى ائْتِنَا ۗ قُلْ', 'qul anad\'uu min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'un<u>aa</u> wal<u>aa</u> ya<u>dh</u>urrun<u>aa</u> wanuraddu \'al<u>aa</u> a\'q<u>aa</u>bin<u>aa</u> ba\'da i<u>dz</u> had<u>aa</u>n<u>aa</u> <strong>al</strong>l<u>aa</u>hu ka<strong>a</strong>lla<u>dz</u>ii<u> </u>istahwat-hu <strong>al</strong>sysyay<u>aath</u>iinu fii <strong>a</strong>l-ar<u>dh</u>i <u>h</u>ayr<u>aa</u>na lahu a<u>sh</u>-<u>haa</u>bun yad\'uunahu il<u>aa</u> <strong>a</strong>lhud<u>aa</u> i/tin<u>aa</u> qul inna hud<u>aa</u> <strong>al</strong>l<u>aa</u>hi huwa <strong>a</strong>lhud<u>aa</u> waumirn<u>aa</u> linuslima lirabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Katakanlah (Muhammad), “Apakah kita akan memohon kepada sesuatu selain Allah, yang tidak dapat memberi manfaat dan tidak (pula) mendatangkan mudarat kepada kita, dan (apakah) kita akan dikembalikan ke belakang, setelah Allah memberi petunjuk kepada kita, seperti orang yang telah disesatkan oleh setan di bumi, dalam keadaan kebingungan.” Kawan-kawannya mengajaknya ke jalan yang lurus (dengan mengatakan), “Ikutilah kami.” Katakanlah, “Sesungguhnya petunjuk Allah itulah petunjuk (yang sebenarnya); dan kita diperintahkan agar berserah diri kepada Tuhan seluruh alam,'),
	(861, 6, 72, 'وَاَنْ اَقِيْمُوا الصَّلٰوةَ وَاتَّقُوْهُۗ وَهُوَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ', 'wa-an aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<strong>i</strong>ttaquuhu wahuwa <strong>al</strong>la<u>dz</u>ii ilayhi tu<u>h</u>syaruun<strong>a</strong>', 'dan agar melaksanakan salat serta bertakwa kepada-Nya.” Dan Dialah Tuhan yang kepada-Nya kamu semua akan dihimpun.'),
	(862, 6, 73, 'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ وَيَوْمَ يَقُوْلُ كُنْ فَيَكُوْنُۚ قَوْلُهُ الْحَقُّۗ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِى الصُّوْرِۗ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a bi<strong>a</strong>l<u>h</u>aqqi wayawma yaquulu kun fayakuunu qawluhu <strong>a</strong>l<u>h</u>aqqu walahu <strong>a</strong>lmulku yawma yunfakhu fii <strong>al</strong><u>shsh</u>uuri \'<u>aa</u>limu <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati wahuwa <strong>a</strong>l<u>h</u>akiimu <strong>a</strong>lkhabiir<strong>u</strong>', 'Dialah yang menciptakan langit dan bumi dengan hak (benar), ketika Dia berkata, “Jadilah!” Maka jadilah sesuatu itu. Firman-Nya adalah benar, dan milik-Nyalah segala kekuasaan pada waktu sangkakala ditiup. Dia mengetahui yang gaib dan yang nyata. Dialah Yang Mahabijaksana, Mahateliti.'),
	(863, 6, 74, '۞ وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ اٰزَرَ اَتَتَّخِذُ اَصْنَامًا اٰلِهَةً ۚاِنِّيْٓ اَرٰىكَ وَقَوْمَكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu li-abiihi <u>aa</u>zara atattakhi<u>dz</u>u a<u>sh</u>n<u>aa</u>man <u>aa</u>lihatan innii ar<u>aa</u>ka waqawmaka fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Dan (ingatlah) ketika Ibrahim berkata kepada ayahnya Azar, ”Pantaskah engkau menjadikan berhala-berhala itu sebagai tuhan? Sesungguhnya aku melihat engkau dan kaummu dalam kesesatan yang nyata.”'),
	(864, 6, 75, 'وَكَذٰلِكَ نُرِيْٓ اِبْرٰهِيْمَ مَلَكُوْتَ السَّمٰوٰتِ وَالْاَرْضِ وَلِيَكُوْنَ مِنَ الْمُوْقِنِيْنَ ', 'waka<u>dzaa</u>lika nurii ibr<u>aa</u>hiima malakuuta <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i waliyakuuna mina <strong>a</strong>lmuuqiniin<strong>a</strong>', 'Dan demikianlah Kami memperlihatkan kepada Ibrahim kekuasaan (Kami yang terdapat) di langit dan di bumi, dan agar dia termasuk orang-orang yang yakin.'),
	(865, 6, 76, 'فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَبًا ۗقَالَ هٰذَا رَبِّيْۚ فَلَمَّآ اَفَلَ قَالَ لَآ اُحِبُّ الْاٰفِلِيْنَ', 'falamm<u>aa</u> janna \'alayhi <strong>al</strong>laylu ra<u>aa</u> kawkaban q<u>aa</u>la h<u>aadzaa</u> rabbii falamm<u>aa</u> afala q<u>aa</u>la l<u>aa</u> u<u>h</u>ibbu <strong>a</strong>l-<u>aa</u>filiin<strong>a</strong>', 'Ketika malam telah menjadi gelap, dia (Ibrahim) melihat sebuah bintang  (lalu) dia berkata, “Inilah Tuhanku.” Maka ketika bintang itu terbenam dia berkata, “Aku tidak suka kepada yang terbenam.”'),
	(866, 6, 77, 'فَلَمَّا رَاَ الْقَمَرَ بَازِغًا قَالَ هٰذَا رَبِّيْ ۚفَلَمَّآ اَفَلَ قَالَ لَىِٕنْ لَّمْ يَهْدِنِيْ رَبِّيْ لَاَكُوْنَنَّ مِنَ الْقَوْمِ الضَّاۤلِّيْنَ ', 'falamm<u>aa</u> ra<u>aa</u> <strong>a</strong>lqamara b<u>aa</u>zighan q<u>aa</u>la h<u>aadzaa</u> rabbii falamm<u>aa</u> afala q<u>aa</u>la la-in lam yahdinii rabbii la-akuunanna mina <strong>a</strong>lqawmi <strong>al</strong><u>dhdhaa</u>lliin<strong>a</strong>', 'Lalu ketika dia melihat bulan terbit dia berkata, “Inilah Tuhanku.” Tetapi ketika bulan itu terbenam dia berkata, “Sungguh, jika Tuhanku tidak memberi petunjuk kepadaku, pastilah aku termasuk orang-orang yang sesat.”'),
	(867, 6, 78, 'فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبِّيْ هٰذَآ اَكْبَرُۚ فَلَمَّآ اَفَلَتْ قَالَ يٰقَوْمِ اِنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ ', 'falamm<u>aa</u> ra<u>aa</u> <strong>al</strong>sysyamsa b<u>aa</u>zighatan q<u>aa</u>la h<u>aadzaa</u> rabbii h<u>aadzaa</u> akbaru falamm<u>aa</u> afalat q<u>aa</u>la y<u>aa</u> qawmi innii barii-un mimm<u>aa</u> tusyrikuun<strong>a</strong>', 'Kemudian ketika dia melihat matahari terbit, dia berkata, “Inilah Tuhanku, ini lebih besar.” Tetapi ketika matahari terbenam, dia berkata, “Wahai kaumku! Sungguh, aku berlepas diri dari apa yang kamu persekutukan.”'),
	(868, 6, 79, 'اِنِّيْ وَجَّهْتُ وَجْهِيَ لِلَّذِيْ فَطَرَ السَّمٰوٰتِ وَالْاَرْضَ حَنِيْفًا وَّمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَۚ ', 'innii wajjahtu wajhiya lilla<u>dz</u>ii fa<u>th</u>ara <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a <u>h</u>aniifan wam<u>aa</u> an<u>aa</u> mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Aku hadapkan wajahku kepada (Allah) yang menciptakan langit dan bumi dengan penuh kepasrahan (mengikuti) agama yang benar, dan aku bukanlah termasuk orang-orang musyrik.'),
	(869, 6, 80, 'وَحَاۤجَّهٗ قَوْمُهٗ ۗقَالَ اَتُحَاۤجُّوْۤنِّيْ فِى اللّٰهِ وَقَدْ هَدٰىنِۗ وَلَآ اَخَافُ مَا تُشْرِكُوْنَ بِهٖٓ اِلَّآ اَنْ يَّشَاۤءَ رَبِّيْ شَيْـًٔا ۗوَسِعَ رَبِّيْ كُلَّ شَيْءٍ عِلْمًا ۗ اَفَلَا تَتَذَكَّرُوْنَ ', 'wa<u>haa</u>jjahu qawmuhu q<u>aa</u>la atu<u>haa</u>jjuunnii fii <strong>al</strong>l<u>aa</u>hi waqad had<u>aa</u>ni wal<u>aa</u> akh<u>aa</u>fu m<u>aa</u> tusyrikuuna bihi ill<u>aa</u> an yasy<u>aa</u>-a rabbii syay-an wasi\'a rabbii kulla syay-in \'ilman afal<u>aa</u> tata<u>dz</u>akkaruun<strong>a</strong>', 'Dan kaumnya membantahnya. Dia (Ibrahim) berkata, “Apakah kamu hendak membantahku tentang Allah, padahal Dia benar-benar telah memberi petunjuk kepadaku? Aku tidak takut kepada (malapetaka dari) apa yang kamu persekutukan dengan Allah, kecuali Tuhanku menghendaki sesuatu. Ilmu Tuhanku meliputi segala sesuatu. Tidakkah kamu dapat mengambil pelajaran?'),
	(870, 6, 81, 'وَكَيْفَ اَخَافُ مَآ اَشْرَكْتُمْ وَلَا تَخَافُوْنَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ عَلَيْكُمْ سُلْطٰنًا ۗفَاَيُّ الْفَرِيْقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُوْنَۘ ', 'wakayfa akh<u>aa</u>fu m<u>aa</u> asyraktum wal<u>aa</u> takh<u>aa</u>fuuna annakum asyraktum bi<strong>al</strong>l<u>aa</u>hi m<u>aa</u> lam yunazzil bihi \'alaykum sul<u>th</u><u>aa</u>nan fa-ayyu <strong>a</strong>lfariiqayni a<u>h</u>aqqu bi<strong>a</strong>l-amni in kuntum ta\'lamuun<strong>a</strong>', 'Bagaimana aku takut kepada apa yang kamu persekutukan (dengan Allah), padahal kamu tidak takut dengan apa yang Allah sendiri tidak menurunkan keterangan kepadamu untuk mempersekutukan-Nya. Manakah dari kedua golongan itu yang lebih berhak mendapat keamanan (dari malapetaka), jika kamu mengetahui?”'),
	(871, 6, 82, 'اَلَّذِيْنَ اٰمَنُوْا وَلَمْ يَلْبِسُوْٓا اِيْمَانَهُمْ بِظُلْمٍ اُولٰۤىِٕكَ لَهُمُ الْاَمْنُ وَهُمْ مُّهْتَدُوْنَ ࣖ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu walam yalbisuu iim<u>aa</u>nahum bi<u>zh</u>ulmin ul<u>aa</u>-ika lahumu <strong>a</strong>l-amnu wahum muhtaduun<strong>a</strong>', 'Orang-orang yang beriman dan tidak mencampuradukkan iman mereka dengan syirik, mereka itulah orang-orang yang mendapat rasa aman dan mereka mendapat petunjuk.'),
	(872, 6, 83, 'وَتِلْكَ حُجَّتُنَآ اٰتَيْنٰهَآ اِبْرٰهِيْمَ عَلٰى قَوْمِهٖۗ نَرْفَعُ دَرَجٰتٍ مَّنْ نَّشَاۤءُۗ اِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ ', 'watilka <u>h</u>ujjatun<u>aa</u> <u>aa</u>tayn<u>aa</u>h<u>aa</u> ibr<u>aa</u>hiima \'al<u>aa</u> qawmihi narfa\'u daraj<u>aa</u>tin man nasy<u>aa</u>u inna rabbaka <u>h</u>akiimun \'aliim<strong>un</strong>', 'Dan itulah keterangan Kami yang Kami berikan kepada Ibrahim untuk menghadapi kaumnya. Kami tinggikan derajat siapa yang Kami kehendaki. Sesungguhnya Tuhanmu Mahabijaksana, Maha Mengetahui.'),
	(873, 6, 84, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ  كُلًّا هَدَيْنَا وَنُوْحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهٖ دَاوٗدَ وَسُلَيْمٰنَ وَاَيُّوْبَ وَيُوْسُفَ وَمُوْسٰى وَهٰرُوْنَ ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَۙ ', 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba kullan hadayn<u>aa</u> wanuu<u>h</u>an hadayn<u>aa</u> min qablu wamin <u>dz</u>urriyyatihi d<u>aa</u>wuuda wasulaym<u>aa</u>na wa-ayyuuba wayuusufa wamuus<u>aa</u> wah<u>aa</u>ruuna waka<u>dzaa</u>lika najzii <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan Kami telah menganugerahkan Ishak dan Yakub kepadanya. Kepada masing-masing telah Kami beri petunjuk; dan sebelum itu Kami telah memberi petunjuk kepada Nuh, dan kepada sebagian dari keturunannya (Ibrahim) yaitu Dawud, Sulaiman, Ayyub, Yusuf, Musa, dan Harun. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik,'),
	(874, 6, 85, 'وَزَكَرِيَّا وَيَحْيٰى وَعِيْسٰى وَاِلْيَاسَۗ  كُلٌّ مِّنَ الصّٰلِحِيْنَۙ ', 'wazakariyy<u>aa</u> waya<u>h</u>y<u>aa</u> wa\'iis<u>aa</u> wa<strong>i</strong>ly<u>aa</u>sa kullun mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'dan Zakaria, Yahya, Isa, dan Ilyas. Semuanya termasuk orang-orang yang saleh,'),
	(875, 6, 86, 'وَاِسْمٰعِيْلَ وَالْيَسَعَ وَيُوْنُسَ وَلُوْطًاۗ وَكُلًّا فَضَّلْنَا عَلَى الْعٰلَمِيْنَۙ ', 'wa-ism<u>aa</u>\'iila wa<strong>i</strong>lyasa\'a wayuunusa waluu<u>th</u>an wakullan fa<u>dhdh</u>aln<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'dan Ismail, Alyasa‘, Yunus, dan Lut. Masing-masing Kami lebihkan (derajatnya) di atas umat lain (pada masanya),'),
	(876, 6, 87, 'وَمِنْ اٰبَاۤىِٕهِمْ وَذُرِّيّٰتِهِمْ وَاِخْوَانِهِمْ ۚوَاجْتَبَيْنٰهُمْ وَهَدَيْنٰهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'wamin <u>aa</u>b<u>aa</u>-ihim wa<u>dz</u>urriyy<u>aa</u>tihim wa-ikhw<u>aa</u>nihim wa<strong>i</strong>jtabayn<u>aa</u>hum wahadayn<u>aa</u>hum il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', '(dan Kami lebihkan pula derajat) sebagian dari nenek moyang mereka, keturunan mereka dan saudara-saudara mereka. Kami telah memilih mereka (menjadi nabi dan rasul) dan mereka Kami beri petunjuk ke jalan yang lurus.'),
	(877, 6, 88, 'ذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَلَوْ اَشْرَكُوْا لَحَبِطَ عَنْهُمْ مَّا كَانُوْا يَعْمَلُوْنَ ', '<u>dzaa</u>lika hud<u>aa</u> <strong>al</strong>l<u>aa</u>hi yahdii bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi walaw asyrakuu la<u>h</u>abi<u>th</u>a \'anhum m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Itulah petunjuk Allah, dengan itu Dia memberi petunjuk kepada siapa saja di antara hamba-hamba-Nya yang Dia kehendaki. Sekiranya mereka mempersekutukan Allah, pasti lenyaplah amalan yang telah mereka kerjakan.'),
	(878, 6, 89, 'اُولٰۤىِٕكَ الَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚفَاِنْ يَّكْفُرْ بِهَا هٰٓؤُلَاۤءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَّيْسُوْا بِهَا بِكٰفِرِيْنَ ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba wa<strong>a</strong>l<u>h</u>ukma wa<strong>al</strong>nnubuwwata fa-in yakfur bih<u>aa</u> h<u>aa</u>ul<u>aa</u>-i faqad wakkaln<u>aa</u> bih<u>aa</u> qawman laysuu bih<u>aa</u> bik<u>aa</u>firiin<strong>a</strong>', 'Mereka itulah orang-orang yang telah Kami berikan kitab, hikmah dan kenabian. Jika orang-orang (Quraisy) itu mengingkarinya, maka Kami akan menyerahkannya kepada kaum yang tidak mengingkarinya.'),
	(879, 6, 90, 'اُولٰۤىِٕكَ الَّذِيْنَ هَدَى اللّٰهُ فَبِهُدٰىهُمُ اقْتَدِهْۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًاۗ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعٰلَمِيْنَ ࣖ ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina had<u>aa</u> <strong>al</strong>l<u>aa</u>hu fabihud<u>aa</u>humu iqtadih qul l<u>aa</u> as-alukum \'alayhi ajran in huwa ill<u>aa</u> <u>dz</u>ikr<u>aa</u> lil\'<u>aa</u>lamiin<strong>a</strong>', 'Mereka itulah (para nabi) yang telah diberi petunjuk oleh Allah, maka ikutilah petunjuk mereka. Katakanlah (Muhammad), “Aku tidak meminta imbalan kepadamu dalam menyampaikan (Al-Qur\'an).” Al-Qur\'an itu tidak lain hanyalah peringatan untuk (segala umat) seluruh alam.   '),
	(880, 6, 91, 'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖٓ اِذْ قَالُوْا مَآ اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِّنْ شَيْءٍۗ  قُلْ مَنْ اَنْزَلَ الْكِتٰبَ الَّذِيْ جَاۤءَ بِهٖ مُوْسٰى نُوْرًا وَّهُدًى لِّلنَّاسِ تَجْعَلُوْنَهٗ قَرَاطِيْسَ تُبْدُوْنَهَا وَتُخْفُوْنَ كَثِيْرًاۚ و', 'wam<u>aa</u> qadaruu <strong>al</strong>l<u>aa</u>ha <u>h</u>aqqa qadrihi i<u>dz</u> q<u>aa</u>luu m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> basyarin min syay-in qul man anzala <strong>a</strong>lkit<u>aa</u>ba <strong>al</strong>la<u>dz</u>ii j<u>aa</u>-a bihi muus<u>aa</u> nuuran wahudan li<strong>l</strong>nn<u>aa</u>si taj\'aluunahu qar<u>aath</u>iisa tubduunah<u>aa</u> watukhfuuna katsiiran wa\'ullimtum m<u>aa</u> lam ta\'lamuu antum wal<u>aa</u> <u>aa</u>b<u>aa</u>ukum quli <strong>al</strong>l<u>aa</u>hu tsumma <u>dz</u>arhum fii khaw<u>dh</u>ihim yal\'abuun<strong>a</strong>', 'Mereka tidak mengagungkan Allah sebagaimana mestinya ketika mereka berkata, “Allah tidak menurunkan sesuatu pun kepada manusia.” Katakanlah (Muhammad), “Siapakah yang menurunkan Kitab (Taurat) yang dibawa Musa sebagai cahaya dan petunjuk bagi manusia, kamu jadikan Kitab itu lembaran-lembaran kertas yang bercerai-berai, kamu memperlihatkan (sebagiannya) dan banyak yang kamu sembunyikan, padahal telah diajarkan kepadamu apa yang tidak diketahui, baik olehmu maupun oleh nenek moyangmu.” Katakanlah, “Allah-lah (yang menurunkannya),” kemudian (setelah itu), biarkanlah mereka bermain-main dalam kesesatannya.'),
	(881, 6, 92, 'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ مُّصَدِّقُ الَّذِيْ بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۗ وَالَّذِيْنَ يُؤْمِنُوْنَ بِالْاٰخِرَةِ يُؤْمِنُوْنَ بِهٖ وَهُمْ عَلٰى صَلَاتِهِمْ يُحٰفِظُوْنَ ', 'wah<u>aadzaa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu mub<u>aa</u>rakun mu<u>sh</u>addiqu <strong>al</strong>la<u>dz</u>ii bayna yadayhi walitun<u>dz</u>ira umma <strong>a</strong>lqur<u>aa</u> waman <u>h</u>awlah<u>aa</u> wa<strong>a</strong>lla<u>dz</u>iina yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati yu/minuuna bihi wahum \'al<u>aa</u> <u>sh</u>al<u>aa</u>tihim yu<u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'Dan ini (Al-Qur\'an), Kitab yang telah Kami turunkan dengan penuh berkah; membenarkan kitab-kitab yang (diturunkan) sebelumnya dan agar engkau memberi peringatan kepada (penduduk) Ummul Qura (Mekah) dan orang-orang yang ada di sekitarnya. Orang-orang yang beriman kepada (kehidupan) akhirat tentu beriman kepadanya                    (Al-Qur\'an), dan mereka selalu memelihara salatnya.'),
	(882, 6, 93, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ قَالَ اُوْحِيَ اِلَيَّ وَلَمْ يُوْحَ اِلَيْهِ شَيْءٌ وَّمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَآ اَنْزَلَ اللّٰهُ ۗوَلَوْ تَرٰٓى اِذِ الظّٰلِمُوْنَ فِيْ غَمَرٰتِ الْمَوْتِ وَالْمَلٰۤىِٕكَةُ بَاسِطُوْٓا ', 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban aw q<u>aa</u>la uu<u>h</u>iya ilayya walam yuu<u>h</u>a ilayhi syay-un waman q<u>aa</u>la saunzilu mitsla m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu walaw tar<u>aa</u> i<u>dz</u>i <strong>al</strong><u>zhzhaa</u>limuuna fii ghamar<u>aa</u>ti <strong>a</strong>lmawti wa<strong>a</strong>lmal<u>aa</u>-ikatu b<u>aa</u>si<u>th</u>uu aydiihim akhrijuu anfusakumu <strong>a</strong>lyawma tujzawna \'a<u>dzaa</u>ba <strong>a</strong>lhuuni bim<u>aa</u> kuntum taquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ghayra <strong>a</strong>l<u>h</u>aqqi wakuntum \'an <u>aa</u>y<u>aa</u>tihi tastakbiruun<strong>a</strong>', 'Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan dusta terhadap Allah atau yang berkata, “Telah diwahyukan kepadaku,” padahal tidak diwahyukan sesuatu pun kepadanya, dan orang yang berkata, “Aku akan menurunkan seperti apa yang diturunkan Allah.” (Alangkah ngerinya) sekiranya engkau melihat pada waktu orang-orang zalim (berada) dalam kesakitan sakratul maut, sedang para malaikat memukul dengan tangannya, (sambil berkata), “Keluarkanlah nyawamu.” Pada hari ini kamu akan dibalas dengan azab yang sangat menghinakan, karena kamu mengatakan terhadap Allah (perkataan) yang tidak benar dan (karena) kamu menyombongkan diri terhadap ayat-ayat-Nya.'),
	(883, 6, 94, 'وَلَقَدْ جِئْتُمُوْنَا فُرَادٰى كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍ وَّتَرَكْتُمْ مَّا خَوَّلْنٰكُمْ وَرَاۤءَ ظُهُوْرِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَاۤءَكُمُ الَّذِيْنَ زَعَمْتُمْ اَنَّهُمْ فِيْكُمْ شُرَكٰۤؤُا ۗ لَقَدْ تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْك', 'walaqad ji/tumuun<u>aa</u> fur<u>aa</u>d<u>aa</u> kam<u>aa</u> khalaqn<u>aa</u>kum awwala marratin wataraktum m<u>aa</u> khawwaln<u>aa</u>kum war<u>aa</u>-a <u>zh</u>uhuurikum wam<u>aa</u> nar<u>aa</u> ma\'akum syufa\'<u>aa</u>-akumu <strong>al</strong>la<u>dz</u>iina za\'amtum annahum fiikum syurak<u>aa</u>u laqad taqa<u>ththh</u>a\'a baynakum wa<u>dh</u>alla \'ankum m<u>aa</u> kuntum taz\'umuun<strong>a</strong>', 'Dan kamu benar-benar datang sendiri-sendiri kepada Kami sebagaimana Kami ciptakan kamu pada mulanya, dan apa yang telah Kami karuniakan kepadamu, kamu tinggalkan di belakangmu (di dunia). Kami tidak melihat pemberi syafaat (pertolongan) besertamu yang kamu anggap bahwa mereka itu sekutu-sekutu (bagi Allah). Sungguh, telah terputuslah (semua pertalian) antara kamu dan telah lenyap dari kamu apa yang dahulu kamu sangka (sebagai sekutu Allah).'),
	(884, 6, 95, '۞ اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۗ  يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۗذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُوْنَ ', 'inna <strong>al</strong>l<u>aa</u>ha f<u>aa</u>liqu <strong>a</strong>l<u>h</u>abbi wa<strong>al</strong>nnaw<u>aa</u> yukhriju <strong>a</strong>l<u>h</u>ayya mina <strong>a</strong>lmayyiti wamukhriju <strong>a</strong>lmayyiti mina <strong>a</strong>l<u>h</u>ayyi <u>dzaa</u>likumu <strong>al</strong>l<u>aa</u>hu fa-ann<u>aa</u> tu/fakuun<strong>a</strong>', 'Sungguh, Allah yang menumbuhkan butir (padi-padian) dan biji (kurma). Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup. Itulah (kekuasaan) Allah, maka mengapa kamu masih berpaling?'),
	(885, 6, 96, 'فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَنًا وَّالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ ', 'f<u>aa</u>liqu <strong>a</strong>l-i<u>sh</u>b<u>aah</u>i waja\'ala <strong>al</strong>layla sakanan wa<strong>al</strong>sysyamsa wa<strong>a</strong>lqamara <u>h</u>usb<u>aa</u>nan <u>dzaa</u>lika taqdiiru <strong>a</strong>l\'aziizi <strong>a</strong>l\'aliim<strong>i</strong>', 'Dia menyingsingkan pagi dan menjadikan malam untuk beristirahat, dan (menjadikan) matahari dan bulan untuk perhitungan. Itulah ketetapan Allah Yang Mahaperkasa, Maha Mengetahui.'),
	(886, 6, 97, 'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ النُّجُوْمَ لِتَهْتَدُوْا بِهَا فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ja\'ala lakumu <strong>al</strong>nnujuuma litahtaduu bih<u>aa</u> fii <u>zh</u>ulum<u>aa</u>ti <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri qad fa<u>shsh</u>aln<u>aa</u> <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun<strong>a</strong>', 'Dan Dialah yang menjadikan bintang-bintang bagimu, agar kamu menjadikannya petunjuk dalam kegelapan di darat dan di laut. Kami telah menjelaskan tanda-tanda (kekuasaan Kami) kepada orang-orang yang mengetahui.'),
	(887, 6, 98, 'وَهُوَ الَّذِيْٓ اَنْشَاَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ فَمُسْتَقَرٌّ وَّمُسْتَوْدَعٌ  ۗقَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّفْقَهُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ansya-akum min nafsin w<u>aah</u>idatin famustaqarrun wamustawda\'un qad fa<u>shsh</u>aln<u>aa</u> <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin yafqahuun<strong>a</strong>', 'Dan Dialah yang menciptakan kamu dari diri yang satu (Adam), maka (bagimu) ada tempat menetap dan tempat simpanan. Sesungguhnya telah Kami jelaskan tanda-tanda (kebesaran Kami) kepada orang-orang yang mengetahui.'),
	(888, 6, 99, 'وَهُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِرًا نُّخْرِجُ مِنْهُ حَبًّا مُّتَرَاكِبًاۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَّجَنّٰتٍ مِّنْ اَعْنَابٍ وَّالزَّيْتُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi nab<u>aa</u>ta kulli syay-in fa-akhrajn<u>aa</u> minhu kha<u>dh</u>iran nukhriju minhu <u>h</u>abban mutar<u>aa</u>kiban wamina <strong>al</strong>nnakhli min <u>th</u>al\'ih<u>aa</u> qinw<u>aa</u>nun d<u>aa</u>niyatun wajann<u>aa</u>tin min a\'n<u>aa</u>bin wa<strong>al</strong>zzaytuuna wa<strong>al</strong>rrumm<u>aa</u>na musytabihan waghayra mutasy<u>aa</u>bihin un<u>zh</u>uruu il<u>aa</u> tsamarihi i<u>dzaa</u> atsmara wayan\'ihi inna fii <u>dzaa</u>likum la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun<strong>a</strong>', 'Dan Dialah yang menurunkan air dari langit, lalu Kami tumbuhkan dengan air itu segala macam tumbuh-tumbuhan, maka Kami keluarkan dari tumbuh-tumbuhan itu tanaman yang menghijau, Kami keluarkan dari tanaman yang menghijau itu butir yang banyak; dan dari mayang kurma, mengurai tangkai-tangkai yang menjulai, dan kebun-kebun anggur, dan (Kami keluarkan pula) zaitun dan delima yang serupa dan yang tidak serupa. Perhatikanlah buahnya pada waktu berbuah, dan menjadi masak. Sungguh, pada yang demikian itu ada tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.'),
	(889, 6, 100, 'وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوْا لَهٗ بَنِيْنَ وَبَنٰتٍۢ بِغَيْرِ عِلْمٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَصِفُوْنَ ࣖ ', 'waja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a <strong>a</strong>ljinna wakhalaqahum wakharaquu lahu baniina waban<u>aa</u>tin bighayri \'ilmin sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> ya<u>sh</u>ifuun<strong>a</strong>', 'Dan mereka (orang-orang musyrik) menjadikan jin sekutu-sekutu Allah, padahal Dia yang menciptakannya (jin-jin itu), dan mereka berbohong (dengan mengatakan), “Allah mempunyai anak laki-laki dan anak perempuan,” tanpa (dasar) pengetahuan. Mahasuci Allah dan Mahatinggi dari sifat-sifat yang mereka gambarkan.'),
	(890, 6, 101, 'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ اَنّٰى يَكُوْنُ لَهٗ وَلَدٌ وَّلَمْ تَكُنْ لَّهٗ صَاحِبَةٌ  ۗوَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ  ', 'badii\'u <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i ann<u>aa</u> yakuunu lahu waladun walam takun lahu <u>shaah</u>ibatun wakhalaqa kulla syay-in wahuwa bikulli syay-in \'aliim<strong>un</strong>', 'Dia (Allah) pencipta langit dan bumi. Bagaimana (mungkin) Dia mempunyai anak padahal Dia tidak mempunyai istri. Dia menciptakan segala sesuatu; dan Dia mengetahui segala sesuatu.'),
	(891, 6, 102, 'ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ  لَآ اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوْهُ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ  ', '<u>dzaa</u>likumu <strong>al</strong>l<u>aa</u>hu rabbukum l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa kh<u>aa</u>liqu kulli syay-in fa<strong>u</strong>\'buduuhu wahuwa \'al<u>aa</u> kulli syay-in wakiil<strong>un</strong>', 'Itulah Allah, Tuhan kamu; tidak ada tuhan selain Dia; pencipta segala sesuatu, maka sembahlah Dia; Dialah pemelihara segala sesuatu.'),
	(892, 6, 103, 'لَا تُدْرِكُهُ الْاَبْصَارُ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ  ', 'l<u>aa</u> tudrikuhu <strong>a</strong>l-ab<u>shaa</u>ru wahuwa yudriku <strong>a</strong>l-ab<u>shaa</u>ra wahuwa <strong>al</strong>la<u>th</u>iifu <strong>a</strong>lkhabiir<strong>u</strong>', 'Dia tidak dapat dicapai oleh penglihatan mata, sedang Dia dapat melihat segala penglihatan itu, dan Dialah Yang Mahahalus, Mahateliti.'),
	(893, 6, 104, 'قَدْ جَاۤءَكُمْ بَصَاۤىِٕرُ مِنْ رَّبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِهٖۚ وَمَنْ عَمِيَ فَعَلَيْهَاۗ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ  ', 'qad j<u>aa</u>-akum ba<u>shaa</u>-iru min rabbikum faman ab<u>sh</u>ara falinafsihi waman \'amiya fa\'alayh<u>aa</u> wam<u>aa</u> an<u>aa</u> \'alaykum bi<u>h</u>afii<u>zh</u><strong>in</strong>', 'Sungguh, bukti-bukti yang nyata telah datang dari Tuhanmu. Barangsiapa melihat (kebenaran itu), maka (manfaatnya) bagi dirinya sendiri; dan barangsiapa buta (tidak melihat kebenaran itu), maka dialah yang rugi. Dan aku (Muhammad) bukanlah penjaga-(mu).'),
	(894, 6, 105, 'وَكَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ وَلِيَقُوْلُوْا دَرَسْتَ وَلِنُبَيِّنَهٗ لِقَوْمٍ يَّعْلَمُوْنَ  ', 'waka<u>dzaa</u>lika nu<u>sh</u>arrifu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti waliyaquuluu darasta walinubayyinahu liqawmin ya\'lamuun<strong>a</strong>', 'Dan demikianlah Kami menjelaskan berulang-ulang ayat-ayat Kami agar orang-orang musyrik mengatakan, “Engkau telah mempelajari ayat-ayat itu (dari Ahli Kitab),” dan agar Kami menjelaskan Al-Qur\'an itu kepada orang-orang yang mengetahui.'),
	(895, 6, 106, 'اِتَّبِعْ مَآ اُوْحِيَ اِلَيْكَ مِنْ رَّبِّكَۚ  لَآ اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ  ', 'ittabi\' m<u>aa</u> uu<u>h</u>iya ilayka min rabbika l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wa-a\'ri<u>dh</u> \'ani <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Ikutilah apa yang telah diwahyukan Tuhanmu kepadamu (Muhammad); tidak ada tuhan selain Dia; dan berpalinglah dari orang-orang musyrik.'),
	(896, 6, 107, 'وَلَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكُوْاۗ وَمَا جَعَلْنٰكَ عَلَيْهِمْ حَفِيْظًاۚ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ', 'walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> asyrakuu wam<u>aa</u> ja\'aln<u>aa</u>ka \'alayhim <u>h</u>afii<u>zh</u>an wam<u>aa</u> anta \'alayhim biwakiil<strong>in</strong>', 'Dan sekiranya Allah menghendaki, niscaya mereka tidak mempersekutukan(-Nya). Dan Kami tidak menjadikan engkau penjaga mereka; dan engkau bukan pula pemelihara mereka.'),
	(897, 6, 108, 'وَلَا تَسُبُّوا الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْوًاۢ بِغَيْرِ عِلْمٍۗ  كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْۖ ثُمَّ اِلٰى رَبِّهِمْ مَّرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ', 'wal<u>aa</u> tasubbuu <strong>al</strong>la<u>dz</u>iina yad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi fayasubbuu <strong>al</strong>l<u>aa</u>ha \'adwan bighayri \'ilmin ka<u>dzaa</u>lika zayyann<u>aa</u> likulli ummatin \'amalahum tsumma il<u>aa</u> rabbihim marji\'uhum fayunabbi-uhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan janganlah kamu memaki sesembahan yang mereka sembah selain Allah, karena mereka nanti akan memaki Allah dengan melampaui batas tanpa dasar pengetahuan. Demikianlah, Kami jadikan setiap umat menganggap baik pekerjaan mereka. Kemudian kepada Tuhan tempat kembali mereka, lalu Dia akan memberitahukan kepada mereka apa yang telah mereka kerjakan.'),
	(898, 6, 109, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَتْهُمْ اٰيَةٌ لَّيُؤْمِنُنَّ بِهَاۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْ اَنَّهَآ اِذَا جَاۤءَتْ لَا يُؤْمِنُوْنَ ', 'wa-aqsamuu bi<strong>al</strong>l<u>aa</u>hi jahda aym<u>aa</u>nihim la-in j<u>aa</u>-at-hum <u>aa</u>yatun layu/minunna bih<u>aa</u> qul innam<u>aa</u> <strong>a</strong>l-<u>aa</u>y<u>aa</u>tu \'inda <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> yusy\'irukum annah<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at l<u>aa</u> yu/minuun<strong>a</strong>', 'Dan mereka bersumpah dengan nama Allah dengan segala kesungguhan, bahwa jika datang suatu mukjizat kepada mereka, pastilah mereka akan beriman kepadanya. Katakanlah, “Mukjizat-mukjizat itu hanya ada pada sisi Allah.” Dan tahukah kamu, bahwa apabila mukjizat (ayat-ayat) datang, mereka tidak juga akan beriman.'),
	(899, 6, 110, 'وَنُقَلِّبُ اَفْـِٕدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوْا بِهٖٓ اَوَّلَ مَرَّةٍ وَّنَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ࣖ   ۔ ', 'wanuqallibu af-idatahum wa-ab<u>shaa</u>rahum kam<u>aa</u> lam yu/minuu bihi awwala marratin wana<u>dz</u>aruhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun<strong>a</strong>', 'Dan (begitu pula) Kami memalingkan hati dan penglihatan mereka seperti pertama kali mereka tidak beriman kepadanya (Al-Qur\'an), dan Kami biarkan mereka bingung dalam kesesatan.'),
	(900, 6, 111, '۞ وَلَوْ اَنَّنَا نَزَّلْنَآ اِلَيْهِمُ الْمَلٰۤىِٕكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَّا كَانُوْا لِيُؤْمِنُوْٓا اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُوْنَ ', 'walaw annan<u>aa</u> nazzaln<u>aa</u> ilayhimu <strong>a</strong>lmal<u>aa</u>-ikata wakallamahumu <strong>a</strong>lmawt<u>aa</u> wa<u>h</u>asyarn<u>aa</u> \'alayhim kulla syay-in qubulan m<u>aa</u> k<u>aa</u>nuu liyu/minuu ill<u>aa</u> an yasy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu wal<u>aa</u>kinna aktsarahum yajhaluun<strong>a</strong>', 'Dan sekalipun Kami benar-benar menurunkan malaikat kepada mereka, dan orang yang telah mati berbicara dengan mereka dan Kami kumpulkan (pula) di hadapan mereka segala sesuatu (yang mereka inginkan), mereka tidak juga akan beriman, kecuali jika Allah menghendaki. Tapi kebanyakan mereka tidak mengetahui (arti kebenaran).'),
	(901, 6, 112, 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيٰطِيْنَ الْاِنْسِ وَالْجِنِّ يُوْحِيْ بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوْرًا ۗوَلَوْ شَاۤءَ رَبُّكَ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ  ', 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> likulli nabiyyin \'aduwwan syay<u>aath</u>iina <strong>a</strong>l-insi wa<strong>a</strong>ljinni yuu<u>h</u>ii ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in zukhrufa <strong>a</strong>lqawli ghuruuran walaw sy<u>aa</u>-a rabbuka m<u>aa</u> fa\'aluuhu fa<u>dz</u>arhum wam<u>aa</u> yaftaruun<strong>a</strong>', 'Dan demikianlah untuk setiap nabi Kami menjadikan musuh yang terdiri dari setan-setan manusia dan jin, sebagian mereka membisikkan kepada sebagian yang lain perkataan yang indah sebagai tipuan. Dan kalau Tuhanmu menghendaki, niscaya mereka tidak akan melakukannya, maka biarkanlah mereka bersama apa (kebohongan) yang mereka ada-adakan.'),
	(902, 6, 113, 'وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٕدَةُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوْا مَا هُمْ مُّقْتَرِفُوْنَ ', 'walita<u>sh</u>gh<u>aa</u> ilayhi af-idatu <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati waliyar<u>dh</u>awhu waliyaqtarifuu m<u>aa</u> hum muqtarifuun<strong>a</strong>', 'Dan agar hati kecil orang-orang yang tidak beriman kepada akhirat, tertarik kepada bisikan itu, dan menyenanginya, dan agar mereka melakukan apa yang biasa mereka lakukan.'),
	(903, 6, 114, 'اَفَغَيْرَ اللّٰهِ اَبْتَغِيْ حَكَمًا وَّهُوَ الَّذِيْٓ اَنْزَلَ اِلَيْكُمُ الْكِتٰبَ مُفَصَّلًا ۗوَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْلَمُوْنَ اَنَّهٗ مُنَزَّلٌ مِّنْ رَّبِّكَ بِالْحَقِّ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ ', 'afaghayra <strong>al</strong>l<u>aa</u>hi abtaghii <u>h</u>akaman wahuwa <strong>al</strong>la<u>dz</u>ii anzala ilaykumu <strong>a</strong>lkit<u>aa</u>ba mufa<u>shsh</u>alan wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba ya\'lamuuna annahu munazzalun min rabbika bi<strong>a</strong>l<u>h</u>aqqi fal<u>aa</u> takuunanna mina <strong>a</strong>lmumtariin<strong>a</strong>', 'Pantaskah aku mencari hakim selain Allah, padahal Dialah yang menurunkan Kitab (Al-Qur\'an) kepadamu secara rinci? Orang-orang yang telah Kami beri kitab mengetahui benar bahwa (Al-Qur\'an) itu diturunkan dari Tuhanmu dengan benar. Maka janganlah kamu termasuk orang-orang yang ragu.'),
	(904, 6, 115, 'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَّعَدْلًاۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'watammat kalimatu rabbika <u>sh</u>idqan wa\'adlan l<u>aa</u> mubaddila likalim<u>aa</u>tihi wahuwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Dan telah sempurna firman Tuhanmu (Al-Qur\'an) dengan benar dan adil. Tidak ada yang dapat mengubah firman-Nya. Dan Dia Maha Mendengar, Maha Mengetahui.'),
	(905, 6, 116, 'وَاِنْ تُطِعْ اَكْثَرَ مَنْ فِى الْاَرْضِ يُضِلُّوْكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ', 'wa-in tu<u>th</u>i\' aktsara man fii <strong>a</strong>l-ar<u>dh</u>i yu<u>dh</u>illuuka \'an sabiili <strong>al</strong>l<u>aa</u>hi in yattabi\'uuna ill<u>aa</u> <strong>al</strong><u>zhzh</u>anna wa-in hum ill<u>aa</u> yakhru<u>sh</u>uun<strong>a</strong>', 'Dan jika kamu mengikuti kebanyakan orang di bumi ini, niscaya mereka akan menyesatkanmu dari jalan Allah. Yang mereka ikuti hanya persangkaan belaka dan mereka hanyalah membuat kebohongan.'),
	(906, 6, 117, 'اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَّضِلُّ عَنْ سَبِيْلِهٖۚ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ', 'inna rabbaka huwa a\'lamu man ya<u>dh</u>illu \'an sabiilihi wahuwa a\'lamu bi<strong>a</strong>lmuhtadiin<strong>a</strong>', 'Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui siapa yang tersesat dari jalan-Nya, dan Dia lebih mengetahui orang-orang yang mendapat petunjuk.'),
	(907, 6, 118, 'فَكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيٰتِهٖ مُؤْمِنِيْنَ ', 'fakuluu mimm<u>aa</u> <u>dz</u>ukira ismu <strong>al</strong>l<u>aa</u>hi \'alayhi in kuntum bi-<u>aa</u>y<u>aa</u>tihi mu/miniin<strong>a</strong>', 'Maka makanlah dari apa (daging hewan) yang (ketika disembelih) disebut nama Allah, jika kamu beriman kepada ayat-ayat-Nya.'),
	(908, 6, 119, 'وَمَا لَكُمْ اَلَّا تَأْكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَّا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِ ۗوَاِنَّ كَثِيرًا لَّيُضِلُّوْنَ بِاَهْوَاۤىِٕهِمْ بِغَيْرِ عِلْمٍ ۗاِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْت', 'wam<u>aa</u> lakum <strong>al</strong>l<u>aa</u> ta/kuluu mimm<u>aa</u> <u>dz</u>ukira ismu <strong>al</strong>l<u>aa</u>hi \'alayhi waqad fa<u>shsh</u>ala lakum m<u>aa</u> <u>h</u>arrama \'alaykum ill<u>aa</u> m<u>aa</u> i<u>dth</u>urirtum ilayhi wa-inna katsiiran layu<u>dh</u>illuuna bi-ahw<u>aa</u>-ihim bighayri \'ilmin inna rabbaka huwa a\'lamu bi<strong>a</strong>lmu\'tadiin<strong>a</strong>', 'Dan mengapa kamu tidak mau memakan dari apa (daging hewan) yang (ketika disembelih) disebut nama Allah, padahal Allah telah menjelaskan kepadamu apa yang diharamkan-Nya kepadamu, kecuali jika kamu dalam keadaan terpaksa. Dan sungguh, banyak yang menyesatkan orang dengan keinginannya tanpa dasar pengetahuan. Tuhanmu lebih mengetahui orang-orang yang melampaui batas.'),
	(909, 6, 120, 'وَذَرُوْا ظَاهِرَ الْاِثْمِ وَبَاطِنَهٗ ۗاِنَّ الَّذِيْنَ يَكْسِبُوْنَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوْا يَقْتَرِفُوْنَ ', 'wa<u>dz</u>aruu <u>zhaa</u>hira <strong>a</strong>l-itsmi wab<u>aath</u>inahu inna <strong>al</strong>la<u>dz</u>iina yaksibuuna <strong>a</strong>l-itsma sayujzawna bim<u>aa</u> k<u>aa</u>nuu yaqtarifuun<strong>a</strong>', 'Dan tinggalkanlah dosa yang terlihat ataupun yang tersembunyi. Sungguh, orang-orang yang mengerjakan (perbuatan) dosa kelak akan diberi balasan sesuai dengan apa yang mereka kerjakan.'),
	(910, 6, 121, 'وَلَا تَأْكُلُوْا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهٗ لَفِسْقٌۗ وَاِنَّ الشَّيٰطِيْنَ لَيُوْحُوْنَ اِلٰٓى اَوْلِيَاۤىِٕهِمْ  لِيُجَادِلُوْكُمْ ۚوَاِنْ اَطَعْتُمُوْهُمْ اِنَّكُمْ لَمُشْرِكُوْنَ ࣖ ', 'wal<u>aa</u> ta/kuluu mimm<u>aa</u> lam yu<u>dz</u>kari ismu <strong>al</strong>l<u>aa</u>hi \'alayhi wa-innahu lafisqun wa-inna <strong>al</strong>sysyay<u>aath</u>iina layuu<u>h</u>uuna il<u>aa</u> awliy<u>aa</u>-ihim liyuj<u>aa</u>diluukum wa-in a<u>th</u>a\'tumuuhum innakum lamusyrikuun<strong>a</strong>', 'Dan janganlah kamu memakan dari apa (daging hewan) yang (ketika disembelih) tidak disebut nama Allah, perbuatan itu benar-benar suatu kefasikan. Sesungguhnya setan-setan akan membisikkan kepada kawan-kawannya agar mereka membantah kamu. Dan jika kamu menuruti mereka, tentu kamu telah menjadi orang musyrik.'),
	(911, 6, 122, 'اَوَمَنْ كَانَ مَيْتًا فَاَحْيَيْنٰهُ وَجَعَلْنَا لَهٗ نُوْرًا يَّمْشِيْ بِهٖ فِى النَّاسِ كَمَنْ مَّثَلُهٗ فِى الظُّلُمٰتِ لَيْسَ بِخَارِجٍ مِّنْهَاۗ  كَذٰلِكَ زُيِّنَ لِلْكٰفِرِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ', 'awa man k<u>aa</u>na maytan fa-a<u>h</u>yayn<u>aa</u>hu waja\'aln<u>aa</u> lahu nuuran yamsyii bihi fii <strong>al</strong>nn<u>aa</u>si kaman matsaluhu fii <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti laysa bikh<u>aa</u>rijin minh<u>aa</u> ka<u>dzaa</u>lika zuyyina lilk<u>aa</u>firiina m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan apakah orang yang sudah mati lalu Kami hidupkan dan Kami beri dia cahaya yang membuatnya dapat berjalan di tengah-tengah orang banyak, sama dengan orang yang berada dalam kegelapan, sehingga dia tidak dapat keluar dari sana? Demikianlah dijadikan terasa indah bagi orang-orang kafir terhadap apa yang mereka kerjakan.'),
	(912, 6, 123, 'وَكَذٰلِكَ جَعَلْنَا فِيْ كُلِّ قَرْيَةٍ اَكٰبِرَ مُجْرِمِيْهَا لِيَمْكُرُوْا فِيْهَاۗ وَمَا يَمْكُرُوْنَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُوْنَ ', 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> fii kulli qaryatin ak<u>aa</u>bira mujrimiih<u>aa</u> liyamkuruu fiih<u>aa</u> wam<u>aa</u> yamkuruuna ill<u>aa</u> bi-anfusihim wam<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan demikianlah pada setiap negeri Kami jadikan pembesar-pembesar yang jahat agar melakukan tipu daya di negeri itu. Tapi mereka hanya menipu diri sendiri tanpa menyadarinya.'),
	(913, 6, 124, 'وَاِذَا جَاۤءَتْهُمْ اٰيَةٌ قَالُوْا لَنْ نُّؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَآ اُوْتِيَ رُسُلُ اللّٰهِ ۘ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسٰلَتَهٗۗ سَيُصِيْبُ الَّذِيْنَ اَجْرَمُوْا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَدِيْدٌۢ بِمَا كَانُوْا يَمْكُرُوْنَ', 'wa-i<u>dzaa</u> j<u>aa</u>-at-hum <u>aa</u>yatun q<u>aa</u>luu lan nu/mina <u>h</u>att<u>aa</u> nu/t<u>aa</u> mitsla m<u>aa</u> uutiya rusulu <strong>al</strong>l<u>aa</u>hi <strong>al</strong>l<u>aa</u>hu a\'lamu <u>h</u>aytsu yaj\'alu ris<u>aa</u>latahu sayu<u>sh</u>iibu <strong>al</strong>la<u>dz</u>iina ajramuu <u>sh</u>agh<u>aa</u>run \'inda <strong>al</strong>l<u>aa</u>hi wa\'a<u>dzaa</u>bun syadiidun bim<u>aa</u> k<u>aa</u>nuu yamkuruun<strong>a</strong>', 'Dan apabila datang suatu ayat kepada mereka, mereka berkata, “Kami tidak akan percaya (beriman) sebelum diberikan kepada kami seperti apa yang diberikan kepada rasul-rasul Allah.” Allah lebih mengetahui di mana Dia menempatkan tugas kerasulan-Nya. Orang-orang yang berdosa, nanti akan ditimpa kehinaan di sisi Allah dan azab yang keras karena tipu daya yang mereka lakukan.'),
	(914, 6, 125, 'فَمَنْ يُّرِدِ اللّٰهُ اَنْ يَّهْدِيَهٗ يَشْرَحْ صَدْرَهٗ لِلْاِسْلَامِۚ وَمَنْ يُّرِدْ اَنْ يُّضِلَّهٗ يَجْعَلْ صَدْرَهٗ ضَيِّقًا حَرَجًا كَاَنَّمَا يَصَّعَّدُ فِى السَّمَاۤءِۗ   كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يُؤْمِنُوْنَ ', 'faman yuridi <strong>al</strong>l<u>aa</u>hu an yahdiyahu yasyra<u>h</u> <u>sh</u>adrahu lil-isl<u>aa</u>mi waman yurid an yu<u>dh</u>illahu yaj\'al <u>sh</u>adrahu <u>dh</u>ayyiqan <u>h</u>arajan ka-annam<u>aa</u> ya<u>shsh</u>a\'\'adu fii <strong>al</strong>ssam<u>aa</u>-i ka<u>dzaa</u>lika yaj\'alu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>rrijsa \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuun<strong>a</strong>', 'Barangsiapa dikehendaki Allah akan mendapat hidayah (petunjuk), Dia akan membukakan dadanya untuk (menerima) Islam. Dan barangsiapa dikehendaki-Nya menjadi sesat, Dia jadikan dadanya sempit dan sesak, seakan-akan dia (sedang) mendaki ke langit. Demikianlah Allah menimpakan siksa kepada orang-orang yang tidak beriman.'),
	(915, 6, 126, 'وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيْمًاۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّذَّكَّرُوْنَ ', 'wah<u>aadzaa</u> <u>sh</u>ir<u>aath</u>u rabbika mustaqiiman qad fa<u>shsh</u>aln<u>aa</u> <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya<u>dzdz</u>akkaruun<strong>a</strong>', 'Dan inilah jalan Tuhanmu yang lurus. Kami telah menjelaskan ayat-ayat (Kami) kepada orang-orang yang menerima peringatan.'),
	(916, 6, 127, '۞ لَهُمْ دَارُ السَّلٰمِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ', 'lahum d<u>aa</u>ru <strong>al</strong>ssal<u>aa</u>mi \'inda rabbihim wahuwa waliyyuhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Bagi mereka (disediakan) tempat yang damai (surga) di sisi Tuhannya. Dan Dialah pelindung mereka karena amal kebajikan yang mereka kerjakan.'),
	(917, 6, 128, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًاۚ يٰمَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِّنَ الْاِنْسِ ۚوَقَالَ اَوْلِيَاۤؤُهُمْ مِّنَ الْاِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَّبَلَغْنَآ اَجَلَنَا الَّذِيْٓ اَجَّلْتَ لَنَا ۗقَالَ النَّارُ مَثْوٰىكُمْ خٰلِد', 'wayawma ya<u>h</u>syuruhum jamii\'an y<u>aa</u> ma\'syara <strong>a</strong>ljinni qadi istaktsartum mina <strong>a</strong>l-insi waq<u>aa</u>la awliy<u>aa</u>uhum mina <strong>a</strong>l-insi rabban<u>aa</u> istamta\'a ba\'<u>dh</u>un<u>aa</u> biba\'<u>dh</u>in wabalaghn<u>aa</u> ajalan<u>aa</u> <strong>al</strong>la<u>dz</u>ii ajjalta lan<u>aa</u> q<u>aa</u>la <strong>al</strong>nn<u>aa</u>ru matsw<u>aa</u>kum kh<u>aa</u>lidiina fiih<u>aa</u> ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu inna rabbaka <u>h</u>akiimun \'aliim<strong>un</strong>', 'Dan (ingatlah) pada hari ketika Dia mengumpulkan mereka semua (dan Allah berfirman), “Wahai golongan jin! Kamu telah banyak (menyesatkan) manusia.” Dan kawan-kawan mereka dari golongan manusia berkata, “Ya Tuhan, kami telah saling mendapatkan kesenangan dan sekarang waktu yang telah Engkau tentukan buat kami telah datang.”  Allah berfirman, “Nerakalah tempat kamu selama-lamanya, kecuali jika Allah menghendaki lain.” Sungguh, Tuhanmu Mahabijaksana, Maha Mengetahui.'),
	(918, 6, 129, 'وَكَذٰلِكَ نُوَلِّيْ بَعْضَ الظّٰلِمِيْنَ بَعْضًاۢ بِمَا كَانُوْا يَكْسِبُوْنَ ࣖ ', 'waka<u>dzaa</u>lika nuwallii ba\'<u>dh</u>a <strong>al</strong><u>zhzhaa</u>limiina ba\'<u>dh</u>an bim<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'Dan demikianlah Kami jadikan sebagian orang-orang zalim berteman dengan sesamanya, sesuai dengan apa yang mereka kerjakan.'),
	(919, 6, 130, 'يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۗ قَالُوْا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنّ', 'y<u>aa</u> ma\'syara aljinni wa<strong>a</strong>l-insi alam ya/tikum rusulun minkum yaqu<u>shsh</u>uuna \'alaykum <u>aa</u>y<u>aa</u>tii wayun<u>dz</u>iruunakum liq<u>aa</u>-a yawmikum h<u>aadzaa</u> q<u>aa</u>luu syahidn<u>aa</u> \'al<u>aa</u> anfusin<u>aa</u> wagharrat-humu al<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> wasyahiduu \'al<u>aa</u> anfusihim annahum k<u>aa</u>nuu k<u>aa</u>firiin<strong>a</strong>', 'Wahai golongan jin dan manusia! Bukankah sudah datang kepadamu rasul-rasul dari kalanganmu sendiri, mereka menyampaikan ayat-ayat-Ku kepadamu dan memperingatkanmu tentang pertemuan pada hari ini? Mereka menjawab, “(Ya), kami menjadi saksi atas diri kami sendiri.” Tetapi mereka tertipu oleh kehidupan dunia dan mereka telah menjadi saksi atas diri mereka sendiri, bahwa mereka adalah orang-orang kafir.'),
	(920, 6, 131, 'ذٰلِكَ اَنْ لَّمْ يَكُنْ رَّبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا غٰفِلُوْنَ ', '<u>dzaa</u>lika an lam yakun rabbuka muhlika <strong>a</strong>lqur<u>aa</u> bi<u>zh</u>ulmin wa-ahluh<u>aa</u> gh<u>aa</u>filuun<strong>a</strong>', 'Demikianlah (para rasul diutus) karena Tuhanmu tidak akan membinasakan suatu negeri secara zalim, sedang penduduknya dalam keadaan lengah (belum tahu).'),
	(921, 6, 132, 'وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ ', 'walikullin daraj<u>aa</u>tun mimm<u>aa</u> \'amiluu wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ya\'maluun<strong>a</strong>', 'Dan masing-masing orang ada tingkatannya, (sesuai) dengan apa yang mereka kerjakan. Dan Tuhanmu tidak lengah terhadap apa yang mereka kerjakan.'),
	(922, 6, 133, 'وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۗاِنْ يَّشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْۢ  بَعْدِكُمْ مَّا يَشَاۤءُ كَمَآ اَنْشَاَكُمْ مِّنْ ذُرِّيَّةِ قَوْمٍ اٰخَرِيْنَ ', 'warabbuka <strong>a</strong>lghaniyyu <u>dz</u>uu <strong>al</strong>rra<u>h</u>mati in yasya/ yu<u>dz</u>hibkum wayastakhlif min ba\'dikum m<u>aa</u> yasy<u>aa</u>u kam<u>aa</u> ansya-akum min <u>dz</u>urriyyati qawmin <u>aa</u>khariin<strong>a</strong>', 'Dan Tuhanmu Mahakaya, penuh rahmat. Jika Dia menghendaki, Dia akan memusnahkan kamu dan setelah kamu (musnah) akan Dia ganti dengan yang Dia kehendaki, sebagaimana Dia menjadikan kamu dari keturunan golongan lain.'),
	(923, 6, 134, 'اِنَّ مَا تُوْعَدُوْنَ لَاٰتٍۙ وَّمَآ اَنْتُمْ بِمُعْجِزِيْنَ ', 'inna m<u>aa</u> tuu\'aduuna la<u>aa</u>tin wam<u>aa</u> antum bimu\'jiziin<strong>a</strong>', 'Sesungguhnya apa pun yang dijanjikan kepadamu pasti datang dan kamu tidak mampu menolaknya.'),
	(924, 6, 135, 'قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌۚ فَسَوْفَ تَعْلَمُوْنَۙ مَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ', 'qul y<u>aa</u> qawmi i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum innii \'<u>aa</u>milun fasawfa ta\'lamuuna man takuunu lahu \'<u>aa</u>qibatu <strong>al</strong>dd<u>aa</u>ri innahu l<u>aa</u> yufli<u>h</u>u <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai kaumku! Berbuatlah menurut kedudukanmu, aku pun berbuat (demikian). Kelak kamu akan mengetahui, siapa yang akan memperoleh tempat (terbaik) di akhirat (nanti). Sesungguhnya orang-orang yang zalim itu tidak akan beruntung.'),
	(925, 6, 136, 'وَجَعَلُوْا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَصِيْبًا فَقَالُوْا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَاۤىِٕنَاۚ فَمَا كَانَ لِشُرَكَاۤىِٕهِمْ فَلَا يَصِلُ اِلَى اللّٰهِ ۚوَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَاۤىِٕهِمْۗ سَ', 'waja\'aluu lill<u>aa</u>hi mimm<u>aa</u> <u>dz</u>ara-a mina <strong>a</strong>l<u>h</u>artsi wa<strong>a</strong>l-an\'<u>aa</u>mi na<u>sh</u>iiban faq<u>aa</u>luu h<u>aadzaa</u> lill<u>aa</u>hi biza\'mihim wah<u>aadzaa</u> lisyurak<u>aa</u>-in<u>aa</u> fam<u>aa</u> k<u>aa</u>na lisyurak<u>aa</u>-ihim fal<u>aa</u> ya<u>sh</u>ilu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> k<u>aa</u>na lill<u>aa</u>hi fahuwa ya<u>sh</u>ilu il<u>aa</u> syurak<u>aa</u>-ihim s<u>aa</u>-a m<u>aa</u> ya<u>h</u>kumuun<strong>a</strong>', 'Dan mereka menyediakan sebagian hasil tanaman dan hewan (bagian) untuk Allah sambil berkata menurut persangkaan mereka, “Ini untuk Allah dan yang ini untuk berhala-berhala kami.” Bagian yang untuk berhala-berhala mereka tidak akan sampai kepada Allah, dan bagian yang untuk Allah akan sampai kepada berhala-berhala mereka. Sangat buruk ketetapan mereka itu.'),
	(926, 6, 137, 'وَكَذٰلِكَ زَيَّنَ لِكَثِيْرٍ مِّنَ الْمُشْرِكِيْنَ قَتْلَ اَوْلَادِهِمْ شُرَكَاۤؤُهُمْ لِيُرْدُوْهُمْ وَلِيَلْبِسُوْا عَلَيْهِمْ دِيْنَهُمْۗ وَلَوْ شَاۤءَ اللّٰهُ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ ', 'waka<u>dzaa</u>lika zayyana likatsiirin mina <strong>a</strong>lmusyrikiina qatla awl<u>aa</u>dihim syurak<u>aa</u>uhum liyurduuhum waliyalbisuu \'alayhim diinahum walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> fa\'aluuhu fa<u>dz</u>arhum wam<u>aa</u> yaftaruun<strong>a</strong>', 'Dan demikianlah berhala-berhala mereka (setan) menjadikan terasa indah bagi banyak orang-orang musyrik membunuh anak-anak mereka, untuk membinasakan mereka dan mengacaukan agama mereka sendiri. Dan kalau Allah menghendaki, niscaya mereka tidak akan mengerjakannya. Biarkanlah mereka bersama apa (kebohongan) yang mereka ada-adakan.'),
	(927, 6, 138, 'وَقَالُوْا هٰذِهٖٓ اَنْعَامٌ وَّحَرْثٌ حِجْرٌ لَّا يَطْعَمُهَآ اِلَّا مَنْ نَّشَاۤءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُوْرُهَا وَاَنْعَامٌ لَّا يَذْكُرُوْنَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَاۤءً عَلَيْهِۗ سَيَجْزِيْهِمْ بِمَا كَانُوْا يَفْتَرُوْنَ ', 'waq<u>aa</u>luu h<u>aadz</u>ihi an\'<u>aa</u>mun wa<u>h</u>artsun <u>h</u>ijrun l<u>aa</u> ya<u>th</u>\'amuh<u>aa</u> ill<u>aa</u> man nasy<u>aa</u>u biza\'mihim wa-an\'<u>aa</u>mun <u>h</u>urrimat <u>zh</u>uhuuruh<u>aa</u> wa-an\'<u>aa</u>mun l<u>aa</u> ya<u>dz</u>kuruuna isma <strong>al</strong>l<u>aa</u>hi \'alayh<u>aa</u> iftir<u>aa</u>-an', 'Dan mereka berkata (menurut anggapan mereka), “Inilah hewan ternak dan hasil bumi yang dilarang, tidak boleh dimakan, kecuali oleh orang yang kami kehendaki.” Dan ada pula hewan yang diharamkan (tidak boleh) ditunggangi, dan ada hewan ternak yang (ketika disembelih) boleh tidak menyebut nama Allah, itu sebagai kebohongan terhadap Allah. Kelak Allah akan membalas semua yang mereka ada-adakan.'),
	(928, 6, 139, 'وَقَالُوْا مَا فِيْ بُطُوْنِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِّذُكُوْرِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَّكُنْ مَّيْتَةً فَهُمْ فِيْهِ شُرَكَاۤءُ ۗسَيَجْزِيْهِمْ وَصْفَهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ', 'waq<u>aa</u>luu m<u>aa</u> fii bu<u>th</u>uuni h<u>aadz</u>ihi <strong>a</strong>l-an\'<u>aa</u>mi kh<u>aa</u>li<u>sh</u>atun li<u>dz</u>ukuurin<u>aa</u> wamu<u>h</u>arramun \'al<u>aa</u> azw<u>aa</u>jin<u>aa</u> wa-in yakun maytatan fahum fiihi syurak<u>aa</u>u sayajziihim wa<u>sh</u>fahum innahu <u>h</u>akiimun \'aliim<strong>un</strong>', 'Dan mereka berkata (pula), “Apa yang ada di dalam perut hewan ternak ini khusus untuk kaum laki-laki kami, haram bagi istri-istri kami.” Dan jika yang dalam perut itu (dilahirkan) mati, maka semua boleh (memakannya). Kelak Allah akan membalas atas ketetapan mereka. Sesungguhnya Allah Mahabijaksana, Maha Mengetahui.'),
	(929, 6, 140, 'قَدْ خَسِرَ الَّذِيْنَ قَتَلُوْٓا اَوْلَادَهُمْ سَفَهًاۢ بِغَيْرِ عِلْمٍ وَّحَرَّمُوْا مَا رَزَقَهُمُ اللّٰهُ افْتِرَاۤءً عَلَى اللّٰهِ ۗقَدْ ضَلُّوْا وَمَا كَانُوْا مُهْتَدِيْنَ ࣖ ', 'qad khasira <strong>al</strong>la<u>dz</u>iina qataluu awl<u>aa</u>dahum safahan bighayri \'ilmin wa<u>h</u>arramuu m<u>aa</u> razaqahumu <strong>al</strong>l<u>aa</u>hu iftir<u>aa</u>-an \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi qad <u>dh</u>alluu wam<u>aa</u> k<u>aa</u>nuu muhtadiin<strong>a</strong>', 'Sungguh rugi mereka yang membunuh anak-anaknya karena kebodohan tanpa pengetahuan, dan mengharamkan rezeki yang dikaruniakan Allah kepada mereka dengan semata-mata membuat-buat kebohongan terhadap Allah. Sungguh, mereka telah sesat dan tidak mendapat petunjuk.'),
	(930, 6, 141, '۞ وَهُوَ الَّذِيْٓ اَنْشَاَ جَنّٰتٍ مَّعْرُوْشٰتٍ وَّغَيْرَ مَعْرُوْشٰتٍ وَّالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا اُكُلُهٗ وَالزَّيْتُوْنَ وَالرُّمَّانَ مُتَشَابِهًا وَّغَيْرَ مُتَشَابِهٍۗ  كُلُوْا مِنْ ثَمَرِهٖٓ اِذَآ اَثْمَرَ وَاٰتُوْا حَقَّهٗ يَوْمَ حَصَا', 'wahuwa <strong>al</strong>la<u>dz</u>ii ansya-a jann<u>aa</u>tin ma\'ruusy<u>aa</u>tin waghayra ma\'ruusy<u>aa</u>tin wa<strong>al</strong>nnakhla wa<strong>al</strong>zzar\'a mukhtalifan ukuluhu wa<strong>al</strong>zzaytuuna wa<strong>al</strong>rrumm<u>aa</u>na mutasy<u>aa</u>bihan waghayra mutasy<u>aa</u>bihin kuluu min tsamarihi i<u>dzaa</u> atsmara wa<u>aa</u>tuu <u>h</u>aqqahu yawma <u>h</u>a<u>shaa</u>dihi wal<u>aa</u> tusrifuu innahu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmusrifiin<strong>a</strong>', 'Dan Dialah yang menjadikan tanaman-tanaman yang merambat dan yang tidak merambat, pohon kurma, tanaman yang beraneka ragam rasanya, zaitun dan delima yang serupa (bentuk dan warnanya) dan tidak serupa (rasanya). Makanlah buahnya apabila ia berbuah dan berikanlah haknya (zakatnya) pada waktu memetik hasilnya, tapi janganlah berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang-orang yang berlebihan,'),
	(931, 6, 142, 'وَمِنَ الْاَنْعَامِ حَمُوْلَةً وَّفَرْشًا ۗ كُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌۙ', 'wamina <strong>a</strong>l-an\'<u>aa</u>mi <u>h</u>amuulatan wafarsyan kuluu mimm<u>aa</u> razaqakumu <strong>al</strong>l<u>aa</u>hu wal<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti <strong>al</strong>sysyay<u>th</u><u>aa</u>ni innahu lakum \'aduwwun mubiin<strong>un</strong>', 'dan di antara hewan-hewan ternak itu ada yang dijadikan pengangkut beban dan ada (pula) yang untuk disembelih. Makanlah rezeki yang diberikan Allah kepadamu, dan janganlah kamu mengikuti langkah-langkah setan. Sesungguhnya setan itu musuh yang nyata bagimu,'),
	(932, 6, 143, 'ثَمٰنِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ نَبِّئُوْنِيْ بِعِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'tsam<u>aa</u>niyata azw<u>aa</u>jin mina <strong>al</strong><u>dhdh</u>a/ni itsnayni wamina <strong>a</strong>lma\'zi itsnayni qul <u>aa</u><strong>l</strong><u>dzdz</u>akarayni <u>h</u>arrama ami <strong>a</strong>luntsayayni amm<u>aa</u> isytamalat \'alayhi ar<u>haa</u>mu <strong>a</strong>luntsayayni nabbi-uunii bi\'ilmin in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'ada delapan hewan ternak yang berpasangan (empat pasang); sepasang domba dan sepasang kambing. Katakanlah, “Apakah yang diharamkan Allah dua yang jantan atau dua yang betina atau yang ada dalam kandungan kedua betinanya? Terangkanlah kepadaku berdasar pengetahuan jika kamu orang yang benar.”'),
	(933, 6, 144, 'وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ وَصّٰىكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَل', 'wamina <strong>a</strong>l-ibili itsnayni wamina <strong>a</strong>lbaqari itsnayni qul <u>aa</u><strong>l</strong><u>dzdz</u>akarayni <u>h</u>arrama ami <strong>a</strong>luntsayayni amm<u>aa</u> isytamalat \'alayhi ar<u>haa</u>mu <strong>a</strong>luntsayayni am kuntum syuhad<u>aa</u>-a i<u>dz</u> wa<u>shshaa</u>kumu <strong>al</strong>l<u>aa</u>hu bih<u>aadzaa</u> faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban liyu<u>dh</u>illa <strong>al</strong>nn<u>aa</u>sa bighayri \'ilmin inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan dari unta sepasang dan dari sapi sepasang. Katakanlah, “Apakah yang diharamkan dua yang jantan atau dua yang betina, atau yang ada dalam kandungan kedua betinanya? Apakah kamu menjadi saksi ketika Allah menetapkan ini bagimu? Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kebohongan terhadap Allah untuk menyesatkan orang-orang tanpa pengetahuan?” Sesungguhnya Allah tidak akan memberi petunjuk kepada orang-orang yang zalim.'),
	(934, 6, 145, 'قُلْ لَّآ اَجِدُ فِيْ مَآ اُوْحِيَ اِلَيَّ مُحَرَّمًا عَلٰى طَاعِمٍ يَّطْعَمُهٗٓ اِلَّآ اَنْ يَّكُوْنَ مَيْتَةً اَوْ دَمًا مَّسْفُوْحًا اَوْ لَحْمَ خِنْزِيْرٍ فَاِنَّهٗ رِجْسٌ اَوْ فِسْقًا اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّ', 'qul l<u>aa</u> ajidu fiim<u>aa</u> uu<u>h</u>iya ilayya mu<u>h</u>arraman \'al<u>aa</u> <u>th</u><u>aa</u>\'imin ya<u>th</u>\'amuhu ill<u>aa</u> an yakuuna maytatan aw daman masfuu<u>h</u>an aw la<u>h</u>ma khinziirin fa-innahu rijsun aw fisqan uhilla lighayri <strong>al</strong>l<u>aa</u>hi bihi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fa-inna rabbaka ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Katakanlah, “Tidak kudapati di dalam apa yang diwahyukan kepadaku, sesuatu yang diharamkan memakannya bagi yang ingin memakannya, kecuali daging hewan yang mati (bangkai), darah yang mengalir, daging babi – karena semua itu kotor – atau hewan yang disembelih bukan atas (nama) Allah. Tetapi barangsiapa terpaksa bukan karena menginginkan dan tidak melebihi (batas darurat) maka sungguh, Tuhanmu Maha Pengampun, Maha Penyayang.'),
	(935, 6, 146, 'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا كُلَّ ذِيْ ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُوْمَهُمَآ اِلَّا مَا حَمَلَتْ ظُهُوْرُهُمَآ اَوِ الْحَوَايَآ اَوْ مَا اخْتَلَطَ بِعَظْمٍۗ ذٰلِكَ جَزَيْنٰهُمْ بِبَغْيِهِمْۚ وَاِنَّا لَصٰدِ', 'wa\'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina h<u>aa</u>duu <u>h</u>arramn<u>aa</u> kulla <u>dz</u>ii <u>zh</u>ufurin wamina <strong>a</strong>lbaqari wa<strong>a</strong>lghanami <u>h</u>arramn<u>aa</u> \'alayhim syu<u>h</u>uumahum<u>aa</u> ill<u>aa</u> m<u>aa</u> <u>h</u>amalat <u>zh</u>uhuuruhum<u>aa</u> awi <strong>a</strong>l<u>h</u>aw<u>aa</u>y<u>aa</u> aw m<u>aa</u> ikhtala<u>th</u>a bi\'a<u>zh</u>min <u>dzaa</u>lika jazayn<u>aa</u>hum bibaghyihim wa-inn<u>aa</u> la<u>shaa</u>diquun<strong>a</strong>', 'Dan kepada orang-orang Yahudi, Kami haramkan semua (hewan) yang berkuku, dan Kami haramkan kepada mereka lemak sapi dan domba, kecuali yang melekat di punggungnya, atau yang dalam isi perutnya, atau yang bercampur dengan tulang. Demikianlah Kami menghukum mereka karena kedurhakaannya. Dan sungguh, Kami  Mahabenar.'),
	(936, 6, 147, 'فَاِنْ كَذَّبُوْكَ فَقُلْ رَّبُّكُمْ ذُوْ رَحْمَةٍ وَّاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهٗ عَنِ الْقَوْمِ الْمُجْرِمِيْنَ ', 'fa-in ka<u>dzdz</u>abuuka faqul rabbukum <u>dz</u>uu ra<u>h</u>matin w<u>aa</u>si\'atin wal<u>aa</u> yuraddu ba/suhu \'ani <strong>a</strong>lqawmi <strong>a</strong>lmujrimiin<strong>a</strong>', 'Maka jika mereka mendustakan kamu, katakanlah, “Tuhanmu mempunyai rahmat yang luas, dan siksa-Nya kepada orang-orang yang berdosa tidak dapat dielakkan.”'),
	(937, 6, 148, 'سَيَقُوْلُ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكْنَا وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۗ  كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوْا بَأْسَنَاۗ قُلْ هَلْ عِنْدَكُمْ مِّنْ عِلْمٍ فَتُخْرِجُوْهُ لَنَاۗ اِنْ تَ', 'sayaquulu <strong>al</strong>la<u>dz</u>iina asyrakuu law sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> asyrakn<u>aa</u> wal<u>aa</u> <u>aa</u>b<u>aa</u>un<u>aa</u> wal<u>aa</u> <u>h</u>arramn<u>aa</u> min syay-in ka<u>dzaa</u>lika ka<u>dzdz</u>aba <strong>al</strong>la<u>dz</u>iina min qablihim <u>h</u>att<u>aa</u> <u>dzaa</u>quu ba/san<u>aa</u> qul hal \'indakum min \'ilmin fatukhrijuuhu lan<u>aa</u> in tattabi\'uuna ill<u>aa</u> <strong>al</strong><u>zhzh</u>anna wa-in antum ill<u>aa</u> takhru<u>sh</u>uun<strong>a</strong>', 'Orang-orang musyrik akan berkata, “Jika Allah menghendaki, tentu kami tidak akan mempersekutukan-Nya, begitu pula nenek moyang kami, dan kami tidak akan mengharamkan apa pun.” Demikian pula orang-orang sebelum mereka yang telah mendustakan (para rasul) sampai mereka merasakan azab Kami. Katakanlah (Muhammad), “Apakah kamu mempunyai pengetahuan yang dapat kamu kemukakan kepada kami? Yang kamu ikuti hanya persangkaan belaka, dan kamu hanya mengira.”'),
	(938, 6, 149, 'قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ', 'qul falill<u>aa</u>hi <strong>a</strong>l<u>h</u>ujjatu <strong>a</strong>lb<u>aa</u>lighatu falaw sy<u>aa</u>-a lahad<u>aa</u>kum ajma\'iin<strong>a</strong>', 'Katakanlah (Muhammad), “Alasan yang kuat hanya pada Allah. Maka kalau Dia menghendaki, niscaya kamu semua mendapat petunjuk.”'),
	(939, 6, 150, 'قُلْ هَلُمَّ شُهَدَاۤءَكُمُ الَّذِيْنَ يَشْهَدُوْنَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوْا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُ', 'qul halumma syuhad<u>aa</u>-akumu <strong>al</strong>la<u>dz</u>iina yasyhaduuna anna <strong>al</strong>l<u>aa</u>ha <u>h</u>arrama h<u>aadzaa</u> fa-in syahiduu fal<u>aa</u> tasyhad ma\'ahum wal<u>aa</u> tattabi\' ahw<u>aa</u>-a <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wa<strong>a</strong>lla<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati wahum birabbihim ya\'diluun<strong>a</strong>', 'Katakanlah (Muhammad), “Bawalah saksi-saksimu yang dapat membuktikan bahwa Allah mengharamkan ini.” Jika mereka memberikan kesaksian, engkau jangan (ikut pula) memberikan kesaksian bersama mereka. Jangan engkau ikuti keinginan orang-orang yang mendustakan ayat-ayat Kami dan orang-orang yang tidak beriman kepada akhirat, dan mereka mempersekutukan Tuhan.'),
	(940, 6, 151, '۞ قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًاۚ وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ مِّنْ اِمْلَاقٍۗ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْ ۚوَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا', 'qul ta\'<u>aa</u>law atlu m<u>aa</u> <u>h</u>arrama rabbukum \'alaykum <strong>al</strong>l<u>aa</u> tusyrikuu bihi syay-an wabi<strong>a</strong>lw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan wal<u>aa</u> taqtuluu awl<u>aa</u>dakum min iml<u>aa</u>qin na<u>h</u>nu narzuqukum wa-iyy<u>aa</u>hum wal<u>aa</u> taqrabuu <strong>a</strong>lfaw<u>aah</u>isya m<u>aa</u> <u>zh</u>ahara minh<u>aa</u> wam<u>aa</u> ba<u>th</u>ana wal<u>aa</u> taqtuluu <strong>al</strong>nnafsa <strong>al</strong>latii <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum ta\'qiluun<strong>a</strong>', 'Katakanlah (Muhammad), “Marilah aku bacakan apa yang diharamkan Tuhan kepadamu. Jangan mempersekutukan-Nya dengan apa pun, berbuat baik kepada ibu bapak, janganlah membunuh anak-anakmu karena miskin. Kamilah yang memberi rezeki kepadamu dan kepada mereka; janganlah kamu mendekati perbuatan yang keji, baik yang terlihat ataupun yang tersembunyi, janganlah kamu membunuh orang yang diharamkan Allah kecuali dengan alasan yang benar. Demikianlah Dia memerintahkan kepadamu agar kamu mengerti.'),
	(941, 6, 152, 'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗ ۚوَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ بِالْقِسْطِۚ  لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۚ وَاِذَا قُلْتُمْ فَاعْدِلُوْا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ الل', 'wal<u>aa</u> taqrabuu m<u>aa</u>la <strong>a</strong>lyatiimi ill<u>aa</u> bi<strong>a</strong>llatii hiya a<u>h</u>sanu <u>h</u>att<u>aa</u> yablugha asyuddahu wa-awfuu <strong>a</strong>lkayla wa<strong>a</strong>lmiiz<u>aa</u>na bi<strong>a</strong>lqis<u>th</u>i l<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> wa-i<u>dzaa</u> qultum fa<strong>i</strong>\'diluu walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> wabi\'ahdi <strong>al</strong>l<u>aa</u>hi awfuu <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum ta<u>dz</u>akkaruun<strong>a</strong>', 'Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih bermanfaat, sampai dia mencapai (usia) dewasa. Dan sempurnakanlah takaran dan timbangan dengan adil. Kami tidak membebani seseorang melainkan menurut kesanggupannya. Apabila kamu berbicara, bicaralah sejujurnya, sekalipun dia kerabat(mu) dan penuhilah janji Allah. Demikianlah Dia memerintahkan kepadamu agar kamu ingat.”'),
	(942, 6, 153, 'وَاَنَّ هٰذَا صِرَاطِيْ مُسْتَقِيْمًا فَاتَّبِعُوْهُ ۚوَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيْلِهٖ ۗذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَتَّقُوْنَ ', 'wa-anna h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>ii mustaqiiman fa<strong>i</strong>ttabi\'uuhu wal<u>aa</u> tattabi\'uu <strong>al</strong>ssubula fatafarraqa bikum \'an sabiilihi <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum tattaquun<strong>a</strong>', 'Dan sungguh, inilah jalan-Ku yang lurus. Maka ikutilah! Jangan kamu ikuti jalan-jalan (yang lain) yang akan mencerai-beraikan kamu dari jalan-Nya. Demikianlah Dia memerintahkan kepadamu agar kamu bertakwa.'),
	(943, 6, 154, 'ثُمَّ اٰتَيْنَا مُوْسَى الْكِتٰبَ تَمَامًا عَلَى الَّذِيْٓ اَحْسَنَ وَتَفْصِيْلًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لَّعَلَّهُمْ بِلِقَاۤءِ رَبِّهِمْ يُؤْمِنُوْنَ ࣖ ', 'tsumma <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba tam<u>aa</u>man \'al<u>aa</u> <strong>al</strong>la<u>dz</u>ii a<u>h</u>sana wataf<u>sh</u>iilan likulli syay-in wahudan wara<u>h</u>matan la\'allahum biliq<u>aa</u>-i rabbihim yu/minuun<strong>a</strong>', 'Kemudian Kami telah memberikan kepada Musa Kitab (Taurat) untuk menyempurnakan (nikmat Kami) kepada orang yang berbuat kebaikan, untuk menjelaskan segala sesuatu, dan sebagai petunjuk dan rahmat, agar mereka beriman akan adanya pertemuan dengan Tuhannya.'),
	(944, 6, 155, 'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ فَاتَّبِعُوْهُ وَاتَّقُوْا لَعَلَّكُمْ تُرْحَمُوْنَۙ ', 'wah<u>aadzaa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu mub<u>aa</u>rakun fa<strong>i</strong>ttabi\'uuhu wa<strong>i</strong>ttaquu la\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dan ini adalah Kitab (Al-Qur\'an) yang Kami turunkan dengan penuh berkah. Ikutilah, dan bertakwalah agar kamu mendapat rahmat,'),
	(945, 6, 156, 'اَنْ تَقُوْلُوْٓا اِنَّمَآ اُنْزِلَ الْكِتٰبُ عَلٰى طَاۤىِٕفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغٰفِلِيْنَۙ ', 'an taquuluu innam<u>aa</u> unzila <strong>a</strong>lkit<u>aa</u>bu \'al<u>aa</u> <u>th</u><u>aa</u>-ifatayni min qablin<u>aa</u> wa-in kunn<u>aa</u> \'an dir<u>aa</u>satihim lagh<u>aa</u>filiin<strong>a</strong>', '(Kami turunkan Al-Qur\'an itu) agar kamu (tidak) mengatakan, “Kitab itu hanya diturunkan kepada dua golongan sebelum kami (Yahudi dan Nasrani) dan sungguh, kami tidak memperhatikan apa yang mereka baca,”'),
	(946, 6, 157, 'اَوْ تَقُوْلُوْا لَوْ اَنَّآ اُنْزِلَ عَلَيْنَا الْكِتٰبُ لَكُنَّآ اَهْدٰى مِنْهُمْۚ فَقَدْ جَاۤءَكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ ۚفَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيٰتِ اللّٰهِ وَصَدَفَ عَنْهَا ۗسَنَجْزِى الَّذِيْنَ يَصْدِفُوْنَ ع', 'aw taquuluu law ann<u>aa</u> unzila \'alayn<u>aa</u> <strong>a</strong>lkit<u>aa</u>bu lakunn<u>aa</u> ahd<u>aa</u> minhum faqad j<u>aa</u>-akum bayyinatun min rabbikum wahudan wara<u>h</u>matun faman a<u>zh</u>lamu mimman ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wa<u>sh</u>adafa \'anh<u>aa</u> sanajzii <strong>al</strong>la<u>dz</u>iina ya<u>sh</u>difuuna \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> suu-a <strong>a</strong>l\'a<u>dzaa</u>bi bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>difuun<strong>a</strong>', 'atau agar kamu (tidak) mengatakan, “Jikalau Kitab itu diturunkan kepada kami, tentulah kami lebih mendapat petunjuk daripada mereka.” Sungguh, telah datang kepadamu penjelasan yang nyata, petunjuk dan rahmat dari Tuhanmu. Siapakah yang lebih zalim daripada orang yang mendustakan ayat-ayat Allah dan berpaling daripadanya? Kelak, Kami akan memberi balasan kepada orang-orang yang berpaling dari ayat-ayat Kami dengan azab yang keras, karena mereka selalu berpaling.'),
	(947, 6, 158, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيٰتِ رَبِّكَ ۗيَوْمَ يَأْتِيْ بَعْضُ اٰيٰتِ رَبِّكَ لَا يَنْفَعُ نَفْسًا اِيْمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ فِيْٓ اِيْمَانِهَ', 'hal yan<u>zh</u>uruuna ill<u>aa</u> an ta/tiyahumu <strong>a</strong>lmal<u>aa</u>-ikatu aw ya/tiya rabbuka aw ya/tiya ba\'<u>dh</u>u <u>aa</u>y<u>aa</u>ti rabbika yawma ya/tii ba\'<u>dh</u>u <u>aa</u>y<u>aa</u>ti rabbika l<u>aa</u> yanfa\'u nafsan iim<u>aa</u>nuh<u>aa</u> lam takun <u>aa</u>manat min qablu aw kasabat fii iim<u>aa</u>nih<u>aa</u> khayran quli inta<u>zh</u>iruu inn<u>aa</u> munta<u>zh</u>iruun<strong>a</strong>', 'Yang mereka nanti-nantikan hanyalah kedatangan malaikat kepada mereka, atau kedatangan Tuhanmu, atau sebagian tanda-tanda dari Tuhanmu. Pada hari datangnya sebagian tanda-tanda Tuhanmu tidak berguna lagi iman seseorang yang belum beriman sebelum itu, atau (belum) berusaha berbuat kebajikan dengan imannya itu. Katakanlah, “Tunggulah! Kami pun menunggu.”'),
	(948, 6, 159, 'اِنَّ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا لَّسْتَ مِنْهُمْ فِيْ شَيْءٍۗ اِنَّمَآ اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوْا يَفْعَلُوْنَ ', 'inna <strong>al</strong>la<u>dz</u>iina farraquu diinahum wak<u>aa</u>nuu syiya\'an lasta minhum fii syay-in innam<u>aa</u> amruhum il<u>aa</u> <strong>al</strong>l<u>aa</u>hi tsumma yunabbi-uhum bim<u>aa</u> k<u>aa</u>nuu yaf\'aluun<strong>a</strong>', 'Sesungguhnya orang-orang yang memecah belah agamanya dan mereka menjadi (terpecah) dalam golongan-golongan, sedikit pun bukan tanggung jawabmu (Muhammad) atas mereka. Sesungguhnya urusan mereka (terserah) kepada Allah. Kemudian Dia akan memberitahukan kepada mereka apa yang telah mereka perbuat.'),
	(949, 6, 160, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ عَشْرُ اَمْثَالِهَا ۚوَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُوْنَ', 'man j<u>aa</u>-a bi<strong>a</strong>l<u>h</u>asanati falahu \'asyru amts<u>aa</u>lih<u>aa</u> waman j<u>aa</u>-a bi<strong>al</strong>ssayyi-<u>aa</u>ti fal<u>aa</u> yujz<u>aa</u> ill<u>aa</u> mitslah<u>aa</u> wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Barangsiapa berbuat kebaikan mendapat balasan sepuluh kali lipat amalnya. Dan barangsiapa berbuat kejahatan dibalas seimbang dengan kejahatannya. Mereka sedikit pun tidak dirugikan (dizalimi).'),
	(950, 6, 161, 'قُلْ اِنَّنِيْ هَدٰىنِيْ رَبِّيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ەۚ دِيْنًا قِيَمًا مِّلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۚ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ', 'qul innanii had<u>aa</u>nii rabbii il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiimin diinan qiyaman millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Katakanlah (Muhammad), “Sesungguhnya Tuhanku telah memberiku petunjuk ke jalan yang lurus, agama yang benar, agama Ibrahim yang lurus. Dia (Ibrahim) tidak termasuk orang-orang musyrik.”'),
	(951, 6, 162, 'قُلْ اِنَّ صَلَاتِيْ وَنُسُكِيْ وَمَحْيَايَ وَمَمَاتِيْ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ', 'qul inna <u>sh</u>al<u>aa</u>tii wanusukii wama<u>h</u>y<u>aa</u>ya wamam<u>aa</u>tii lill<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Katakanlah (Muhammad), “Sesungguhnya salatku, ibadahku, hidupku dan matiku hanyalah untuk Allah, Tuhan seluruh alam,'),
	(952, 6, 163, 'لَا شَرِيْكَ لَهٗ ۚوَبِذٰلِكَ اُمِرْتُ وَاَنَا۠ اَوَّلُ الْمُسْلِمِيْنَ ', 'l<u>aa</u> syariika lahu wabi<u>dzaa</u>lika umirtu wa-an<u>aa</u> awwalu <strong>a</strong>lmuslimiin<strong>a</strong>', 'tidak ada sekutu bagi-Nya; dan demikianlah yang diperintahkan kepadaku dan aku adalah orang yang pertama-tama berserah diri (muslim).”'),
	(953, 6, 164, 'قُلْ اَغَيْرَ اللّٰهِ اَبْغِيْ رَبًّا وَّهُوَ رَبُّ كُلِّ شَيْءٍۗ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ', 'qul aghayra <strong>al</strong>l<u>aa</u>hi abghii rabban wahuwa rabbu kulli syay-in wal<u>aa</u> taksibu kullu nafsin ill<u>aa</u> \'alayh<u>aa</u> wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> tsumma il<u>aa</u> rabbikum marji\'ukum fayunabbi-ukum bim<u>aa</u> kuntum fiihi takhtalifuun<strong>a</strong>', 'Katakanlah (Muhammad), “Apakah (patut) aku mencari tuhan selain Allah, padahal Dialah Tuhan bagi segala sesuatu. Setiap perbuatan dosa seseorang, dirinya sendiri yang bertanggung jawab. Dan seseorang tidak akan memikul beban dosa orang lain. Kemudian kepada Tuhanmulah kamu kembali, dan akan diberitahukan-Nya kepadamu apa yang dahulu kamu perselisihkan.”'),
	(954, 6, 165, 'وَهُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْۗ اِنَّ رَبَّكَ سَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ ࣖ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ja\'alakum khal<u>aa</u>-ifa <strong>a</strong>l-ar<u>dh</u>i warafa\'a ba\'<u>dh</u>akum fawqa ba\'<u>dh</u>in daraj<u>aa</u>tin liyabluwakum fii m<u>aa</u> <u>aa</u>t<u>aa</u>kum inna rabbaka sarii\'u <strong>a</strong>l\'iq<u>aa</u>bi wa-innahu laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan Dialah yang menjadikan kamu sebagai khalifah-khalifah di bumi dan Dia mengangkat (derajat) sebagian kamu di atas yang lain, untuk mengujimu atas (karunia) yang diberikan-Nya kepadamu. Sesungguhnya Tuhanmu sangat cepat memberi hukuman dan sungguh, Dia Maha Pengampun, Maha Penyayang.'),
	(955, 7, 1, 'الۤمّۤصۤ ۚ ', 'alif-l<u>aa</u>m-miim-<u>shaa</u>d', 'Alif Lam Mim shad.'),
	(956, 7, 2, 'كِتٰبٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ فِيْ صَدْرِكَ حَرَجٌ مِّنْهُ لِتُنْذِرَ بِهٖ وَذِكْرٰى لِلْمُؤْمِنِيْنَ ', 'kit<u>aa</u>bun unzila ilayka fal<u>aa</u> yakun fii <u>sh</u>adrika <u>h</u>arajun minhu litun<u>dz</u>ira bihi wa<u>dz</u>ikr<u>aa</u> lilmu/miniin<strong>a</strong>', '(Inilah) Kitab yang diturunkan kepadamu (Muhammad); maka janganlah engkau sesak dada karenanya, agar engkau memberi peringatan dengan (Kitab) itu dan menjadi pelajaran bagi orang yang beriman.'),
	(957, 7, 3, 'اِتَّبِعُوْا مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ وَلَا تَتَّبِعُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۗ قَلِيْلًا مَّا تَذَكَّرُوْنَ ', 'ittabi\'uu m<u>aa</u> unzila ilaykum min rabbikum wal<u>aa</u> tattabi\'uu min duunihi awliy<u>aa</u>-a qaliilan m<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Ikutilah apa yang diturunkan kepadamu dari Tuhanmu, dan janganlah kamu ikuti selain Dia sebagai pemimpin. Sedikit sekali kamu mengambil pelajaran.'),
	(958, 7, 4, 'وَكَمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا فَجَاۤءَهَا بَأْسُنَا بَيَاتًا اَوْ هُمْ قَاۤىِٕلُوْنَ', 'wakam min qaryatin ahlakn<u>aa</u>h<u>aa</u> faj<u>aa</u>-ah<u>aa</u> ba/sun<u>aa</u> bay<u>aa</u>tan aw hum q<u>aa</u>-iluun<strong>a</strong>', 'Betapa banyak negeri yang telah Kami binasakan, siksaan Kami datang (menimpa penduduk)nya pada malam hari, atau pada saat mereka beristirahat pada siang hari.'),
	(959, 7, 5, 'فَمَا كَانَ دَعْوٰىهُمْ اِذْ جَاۤءَهُمْ بَأْسُنَآ اِلَّآ اَنْ قَالُوْٓا اِنَّا كُنَّا ظٰلِمِيْنَ ', 'fam<u>aa</u> k<u>aa</u>na da\'w<u>aa</u>hum i<u>dz</u> j<u>aa</u>-ahum ba/sun<u>aa</u> ill<u>aa</u> an q<u>aa</u>luu inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin<strong>a</strong>', 'Maka ketika siksaan Kami datang menimpa mereka, keluhan mereka tidak lain, hanya mengucap, “Sesungguhnya kami adalah orang-orang yang zalim.”'),
	(960, 7, 6, 'فَلَنَسْـَٔلَنَّ الَّذِيْنَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَلِيْنَۙ', 'falanas-alanna <strong>al</strong>la<u>dz</u>iina ursila ilayhim walanas-alanna <strong>a</strong>lmursaliin<strong>a</strong>', 'Maka pasti akan Kami tanyakan kepada umat yang telah mendapat seruan (dari rasul-rasul) dan Kami akan tanyai (pula) para rasul,'),
	(961, 7, 7, 'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَّمَا كُنَّا غَاۤىِٕبِيْنَ ', 'falanaqu<u>shsh</u>anna \'alayhim bi\'ilmin wam<u>aa</u> kunn<u>aa</u> gh<u>aa</u>-ibiin<strong>a</strong>', 'dan pasti akan Kami beritakan kepada mereka dengan ilmu (Kami) dan Kami  tidak jauh (dari mereka).'),
	(962, 7, 8, 'وَالْوَزْنُ يَوْمَىِٕذِ ِۨالْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ', 'wa<strong>a</strong>lwaznu yawma-i<u>dz</u>ini <strong>a</strong>l<u>h</u>aqqu faman tsaqulat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Timbangan pada hari itu (menjadi ukuran) kebenaran. Maka barangsiapa berat timbangan (kebaikan)nya, mereka itulah orang yang beruntung,'),
	(963, 7, 9, 'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ بِمَا كَانُوْا بِاٰيٰتِنَا يَظْلِمُوْنَ ', 'waman khaffat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina khasiruu anfusahum bim<u>aa</u> k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ya<u>zh</u>limuun<strong>a</strong>', 'dan barangsiapa ringan timbangan (kebaikan)nya, maka mereka itulah orang yang telah merugikan dirinya sendiri, karena mereka  mengingkari ayat-ayat Kami.'),
	(964, 7, 10, 'وَلَقَدْ مَكَّنّٰكُمْ فِى الْاَرْضِ وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ࣖ ', 'walaqad makkann<u>aa</u>kum fii <strong>a</strong>l-ar<u>dh</u>i waja\'aln<u>aa</u> lakum fiih<u>aa</u> ma\'<u>aa</u>yisya qaliilan m<u>aa</u> tasykuruun<strong>a</strong>', 'Dan sungguh, Kami telah menempatkan kamu di bumi dan di sana Kami sediakan (sumber) penghidupan untukmu. (Tetapi) sedikit sekali kamu bersyukur.'),
	(965, 7, 11, 'وَلَقَدْ خَلَقْنٰكُمْ ثُمَّ صَوَّرْنٰكُمْ ثُمَّ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ  لَمْ يَكُنْ مِّنَ السّٰجِدِيْنَ', 'walaqad khalaqn<u>aa</u>kum tsumma <u>sh</u>awwarn<u>aa</u>kum tsumma quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa lam yakun mina <strong>al</strong>ss<u>aa</u>jidiin<strong>a</strong>', 'Dan sungguh, Kami telah menciptakan kamu, kemudian membentuk (tubuh)mu, kemudian Kami berfirman kepada para malaikat, “Bersujudlah kamu kepada Adam,” maka mereka pun sujud kecuali Iblis. Ia (Iblis) tidak termasuk mereka yang bersujud.'),
	(966, 7, 12, 'قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَ ۗقَالَ اَنَا۠ خَيْرٌ مِّنْهُۚ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ ', 'q<u>aa</u>la m<u>aa</u> mana\'aka <strong>al</strong>l<u>aa</u> tasjuda i<u>dz</u> amartuka q<u>aa</u>la an<u>aa</u> khayrun minhu khalaqtanii min n<u>aa</u>rin wakhalaqtahu min <u>th</u>iin<strong>in</strong>', '(Allah) berfirman, “Apakah yang menghalangimu (sehingga) kamu tidak bersujud (kepada Adam) ketika Aku menyuruhmu?” (Iblis) menjawab, “Aku lebih baik daripada dia. Engkau ciptakan aku dari api, sedangkan dia Engkau ciptakan dari tanah.”'),
	(967, 7, 13, 'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُوْنُ لَكَ اَنْ تَتَكَبَّرَ فِيْهَا فَاخْرُجْ اِنَّكَ مِنَ الصّٰغِرِيْنَ ', 'q<u>aa</u>la fa<strong>i</strong>hbi<u>th</u> minh<u>aa</u> fam<u>aa</u> yakuunu laka an tatakabbara fiih<u>aa</u> fa<strong>u</strong>khruj innaka mina <strong>al</strong><u>shshaa</u>ghiriin<strong>a</strong>', '(Allah) berfirman, “Maka turunlah kamu darinya (surga); karena kamu tidak sepatutnya menyombongkan diri di dalamnya. Keluarlah! Sesungguhnya kamu termasuk makhluk yang hina.”'),
	(968, 7, 14, 'قَالَ اَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ', 'q<u>aa</u>la an<u>zh</u>irnii il<u>aa</u> yawmi yub\'atsuun<strong>a</strong>', '(Iblis) menjawab, “Berilah aku penangguhan waktu, sampai hari mereka dibangkitkan.”'),
	(969, 7, 15, 'قَالَ اِنَّكَ مِنَ الْمُنْظَرِيْنَ ', 'q<u>aa</u>la innaka mina <strong>a</strong>lmun<u>zh</u>ariin<strong>a</strong>', '(Allah) berfirman, “Benar, kamu termasuk yang diberi penangguhan waktu.”'),
	(970, 7, 16, 'قَالَ فَبِمَآ اَغْوَيْتَنِيْ لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيْمَۙ', 'q<u>aa</u>la fabim<u>aa</u> aghwaytanii la-aq\'udanna lahum <u>sh</u>ir<u>aath</u>aka <strong>a</strong>lmustaqiim<strong>a</strong>', '(Iblis) menjawab, “Karena Engkau telah menyesatkan aku, pasti aku akan selalu menghalangi mereka dari jalan-Mu yang lurus,'),
	(971, 7, 17, 'ثُمَّ لَاٰتِيَنَّهُمْ مِّنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَاۤىِٕلِهِمْۗ وَلَا تَجِدُ اَكْثَرَهُمْ شٰكِرِيْنَ', 'tsumma la<u>aa</u>tiyannahum min bayni aydiihim wamin khalfihim wa\'an aym<u>aa</u>nihim wa\'an syam<u>aa</u>-ilihim wal<u>aa</u> tajidu aktsarahum sy<u>aa</u>kiriin<strong>a</strong>', 'kemudian pasti aku akan mendatangi mereka dari depan, dari belakang, dari kanan dan dari kiri mereka. Dan Engkau tidak akan mendapati kebanyakan mereka bersyukur.”'),
	(972, 7, 18, 'قَالَ اخْرُجْ مِنْهَا مَذْءُوْمًا مَّدْحُوْرًا ۗ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَعِيْنَ', 'q<u>aa</u>la ukhruj minh<u>aa</u> ma<u>dz</u>uuman mad<u>h</u>uuran laman tabi\'aka minhum la-amla-anna jahannama minkum ajma\'iin<strong>a</strong>', '(Allah) berfirman, “Keluarlah kamu dari sana (surga) dalam keadaan terhina dan terusir! Sesungguhnya barangsiapa di antara mereka ada yang mengikutimu, pasti akan Aku isi neraka Jahanam dengan kamu semua.”'),
	(973, 7, 19, 'وَيٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ', 'way<u>aa</u> <u>aa</u>damu uskun anta wazawjuka <strong>a</strong>ljannata fakul<u>aa</u> min <u>h</u>aytsu syi/tum<u>aa</u> wal<u>aa</u> taqrab<u>aa</u> h<u>aadz</u>ihi <strong>al</strong>sysyajarata fatakuun<u>aa</u> mina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan (Allah berfirman), “Wahai Adam! Tinggallah engkau bersama istrimu dalam surga dan makanlah apa saja yang kamu berdua sukai. Tetapi janganlah kamu berdua dekati pohon yang satu ini. (Apabila didekati) kamu berdua termasuk orang-orang yang zalim.”'),
	(974, 7, 20, 'فَوَسْوَسَ لَهُمَا الشَّيْطٰنُ لِيُبْدِيَ لَهُمَا مَا وٗرِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰىكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ ِالَّآ اَنْ تَكُوْنَا مَلَكَيْنِ اَوْ تَكُوْنَا مِنَ الْخٰلِدِيْنَ ', 'fawaswasa lahum<u>aa</u> <strong>al</strong>sysyay<u>th</u><u>aa</u>nu liyubdiya lahum<u>aa</u> m<u>aa</u> wuuriya \'anhum<u>aa</u> min saw-<u>aa</u>tihim<u>aa</u> waq<u>aa</u>la m<u>aa</u> nah<u>aa</u>kum<u>aa</u> rabbukum<u>aa</u> \'an h<u>aadz</u>ihi <strong>al</strong>sysyajarati ill<u>aa</u> an takuun<u>aa</u> malakayni aw takuun<u>aa</u> mina <strong>a</strong>lkh<u>aa</u>lidiin<strong>a</strong>', 'Kemudian setan membisikkan pikiran jahat kepada mereka agar menampakkan aurat mereka (yang selama ini) tertutup. Dan (setan) berkata, “Tuhanmu hanya melarang kamu berdua mendekati pohon ini, agar kamu berdua tidak menjadi malaikat atau tidak menjadi orang yang kekal (dalam surga).”'),
	(975, 7, 21, 'وَقَاسَمَهُمَآ اِنِّيْ لَكُمَا لَمِنَ النّٰصِحِيْنَۙ', 'waq<u>aa</u>samahum<u>aa</u> innii lakum<u>aa</u> lamina <strong>al</strong>nn<u>aas</u>i<u>h</u>iin<strong>a</strong>', 'Dan dia (setan) bersumpah kepada keduanya, “Sesungguhnya aku ini benar-benar termasuk para penasihatmu,”'),
	(976, 7, 22, 'فَدَلّٰىهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۗ وَنَادٰىهُمَا رَبُّهُمَآ اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَّكُمَآ اِنَّ الشَّيْطٰنَ لَكُمَ', 'fadall<u>aa</u>hum<u>aa</u> bighuruurin falamm<u>aa</u> <u>dzaa</u>q<u>aa</u> <strong>al</strong>sysyajarata badat lahum<u>aa</u> saw-<u>aa</u>tuhum<u>aa</u> wa<u>th</u>afiq<u>aa</u> yakh<u>sh</u>if<u>aa</u>ni \'alayhim<u>aa</u> min waraqi aljannati wan<u>aa</u>d<u>aa</u>hum<u>aa</u> rabbuhum<u>aa</u> alam anhakum<u>aa</u> \'an tilkum<u>aa</u> <strong>al</strong>sysyajarati wa-aqul lakum<u>aa</u> inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na lakum<u>aa</u> \'aduwwun mubiin<strong>un</strong>', 'dia (setan) membujuk mereka dengan tipu daya. Ketika mereka mencicipi (buah) pohon itu, tampaklah oleh mereka auratnya, maka mulailah mereka menutupinya dengan daun-daun surga. Tuhan menyeru mereka, “Bukankah Aku telah melarang kamu dari pohon itu dan Aku telah mengatakan bahwa sesungguhnya setan adalah musuh yang nyata bagi kamu berdua?”'),
	(977, 7, 23, 'قَالَا رَبَّنَا ظَلَمْنَآ اَنْفُسَنَا وَاِنْ لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ ', 'q<u>aa</u>l<u>aa</u> rabban<u>aa</u> <u>zh</u>alamn<u>aa</u> anfusan<u>aa</u> wa-in lam taghfir lan<u>aa</u> watar<u>h</u>amn<u>aa </u>lanakuunanna mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Keduanya berkata, “Ya Tuhan kami, kami telah menzalimi diri kami sendiri. Jika Engkau tidak mengampuni kami dan memberi rahmat kepada kami, niscaya kami termasuk orang-orang yang rugi.”'),
	(978, 7, 24, 'قَالَ اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚوَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ ', 'q<u>aa</u>la ihbi<u>th</u>uu ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun walakum fii <strong>a</strong>l-ar<u>dh</u>i mustaqarrun wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin<strong>in</strong>', '(Allah) berfirman, “Turunlah kamu! Kamu akan saling bermusuhan satu sama lain. Bumi adalah tempat kediaman dan kesenanganmu sampai waktu yang telah ditentukan.”'),
	(979, 7, 25, 'قَالَ فِيْهَا تَحْيَوْنَ وَفِيْهَا تَمُوْتُوْنَ وَمِنْهَا تُخْرَجُوْنَ ࣖ ', 'q<u>aa</u>la fiih<u>aa</u> ta<u>h</u>yawna wafiih<u>aa</u> tamuutuuna waminh<u>aa</u> tukhrajuun<strong>a</strong>', '(Allah) berfirman, “Di sana kamu hidup, di sana kamu mati, dan dari sana (pula) kamu akan dibangkitkan.”'),
	(980, 7, 26, 'يَا بَنِيْٓ اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُّوَارِيْ سَوْاٰتِكُمْ وَرِيْشًاۗ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُوْنَ ', 'y<u>aa</u> banii <u>aa</u>dama qad anzaln<u>aa</u> \'alaykum lib<u>aa</u>san yuw<u>aa</u>rii saw-<u>aa</u>tikum wariisyan walib<u>aa</u>su <strong>al</strong>ttaqw<u>aa</u> <u>dzaa</u>lika khayrun <u>dzaa</u>lika min <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi la\'allahum ya<u>dzdz</u>akkaruun<strong>a</strong>', 'Wahai anak cucu Adam! Sesungguhnya Kami telah menyediakan pakaian untuk menutupi auratmu dan untuk perhiasan bagimu. Tetapi pakaian takwa, itulah yang lebih baik. Demikianlah sebagian tanda-tanda kekuasaan Allah, mudah-mudahan mereka ingat.'),
	(981, 7, 27, 'يٰبَنِيْٓ اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطٰنُ كَمَآ اَخْرَجَ اَبَوَيْكُمْ مِّنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَا ۗاِنَّهٗ يَرٰىكُمْ هُوَ وَقَبِيْلُهٗ مِنْ حَيْثُ لَا تَرَوْنَهُمْۗ اِنَّا جَعَلْنَا الشَّيٰطِيْنَ اَ', 'y<u>aa</u> banii <u>aa</u>dama l<u>aa</u> yaftinannakumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu kam<u>aa</u> akhraja abawaykum mina <strong>a</strong>ljannati yanzi\'u \'anhum<u>aa</u> lib<u>aa</u>sahum<u>aa</u> liyuriyahum<u>aa</u> saw-<u>aa</u>tihim<u>aa</u> innahu yar<u>aa</u>kum huwa waqabiiluhu min <u>h</u>aytsu l<u>aa</u> tarawnahum inn<u>aa</u> ja\'aln<u>aa</u> <strong>al</strong>sysyay<u>aath</u>iina awliy<u>aa</u>-a lilla<u>dz</u>iina l<u>aa</u> yu/minuun<strong>a</strong>', 'Wahai anak cucu Adam! Janganlah sampai kamu tertipu oleh setan sebagaimana halnya dia (setan) telah mengeluarkan ibu bapakmu dari surga, dengan menanggalkan pakaian keduanya untuk memperlihatkan aurat keduanya. Sesungguhnya dia dan pengikutnya dapat melihat kamu dari suatu tempat yang kamu tidak bisa melihat mereka. Sesungguhnya Kami telah menjadikan setan-setan itu pemimpin bagi orang-orang yang tidak beriman.'),
	(982, 7, 28, 'وَاِذَا فَعَلُوْا فَاحِشَةً قَالُوْا وَجَدْنَا عَلَيْهَآ اٰبَاۤءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۗ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَاۤءِۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ', 'wa-i<u>dzaa</u> fa\'aluu f<u>aah</u>isyatan q<u>aa</u>luu wajadn<u>aa</u> \'alayh<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu amaran<u>aa</u> bih<u>aa</u> qul inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> ya/muru bi<strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i ataquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan apabila mereka melakukan perbuatan keji, mereka berkata, “Kami mendapati nenek moyang kami melakukan yang demikian, dan Allah menyuruh kami mengerjakannya.” Katakanlah, “Sesungguhnya Allah tidak pernah menyuruh berbuat keji. Mengapa kamu membicarakan tentang Allah apa yang tidak kamu ketahui?”'),
	(983, 7, 29, 'قُلْ اَمَرَ رَبِّيْ بِالْقِسْطِۗ وَاَقِيْمُوْا وُجُوْهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۗ كَمَا بَدَاَكُمْ تَعُوْدُوْنَۗ', 'qul amara rabbii bi<strong>a</strong>lqis<u>th</u>i wa-aqiimuu wujuuhakum \'inda kulli masjidin wa<strong>u</strong>d\'uuhu mukhli<u>sh</u>iina lahu <strong>al</strong>ddiina kam<u>aa</u> bada-akum ta\'uuduun<strong>a</strong>', 'Katakanlah, “Tuhanku menyuruhku berlaku adil. Hadapkanlah wajahmu (kepada Allah) pada setiap salat, dan sembahlah Dia dengan mengikhlaskan ibadah semata-mata hanya kepada-Nya. Kamu akan dikembalikan kepada-Nya sebagaimana kamu diciptakan semula.'),
	(984, 7, 30, 'فَرِيْقًا هَدٰى وَفَرِيْقًا حَقَّ عَلَيْهِمُ الضَّلٰلَةُ  ۗاِنَّهُمُ اتَّخَذُوا الشَّيٰطِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ اللّٰهِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ ', 'fariiqan had<u>aa</u> wafariiqan <u>h</u>aqqa \'alayhimu <strong>al</strong><u>dhdh</u>al<u>aa</u>latu innahumu ittakha<u>dz</u>uu <strong>al</strong>sysyay<u>aath</u>iina awliy<u>aa</u>-a min duuni <strong>al</strong>l<u>aa</u>hi waya<u>h</u>sabuuna annahum muhtaduun<strong>a</strong>', 'Sebagian diberi-Nya petunjuk dan sebagian lagi sepantasnya menjadi sesat. Mereka menjadikan setan-setan sebagai pelindung selain Allah. Mereka mengira bahwa mereka mendapat petunjuk.'),
	(985, 7, 31, '۞ يٰبَنِيْٓ اٰدَمَ خُذُوْا زِيْنَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّكُلُوْا وَاشْرَبُوْا وَلَا تُسْرِفُوْاۚ اِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَ ࣖ ', 'y<u>aa</u> banii <u>aa</u>dama khu<u>dz</u>uu ziinatakum \'inda kulli masjidin wakuluu wa<strong>i</strong>syrabuu wal<u>aa</u> tusrifuu innahu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmusrifiin<strong>a</strong>', 'Wahai anak cucu Adam! Pakailah pakaianmu yang bagus pada setiap (memasuki) masjid, makan dan minumlah, tetapi jangan berlebihan. Sungguh, Allah tidak menyukai orang yang berlebih-lebihan.'),
	(986, 7, 32, 'قُلْ مَنْ حَرَّمَ زِيْنَةَ اللّٰهِ الَّتِيْٓ اَخْرَجَ لِعِبَادِهٖ وَالطَّيِّبٰتِ مِنَ الرِّزْقِۗ قُلْ هِيَ لِلَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَّوْمَ الْقِيٰمَةِۗ  كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ', 'qul man <u>h</u>arrama ziinata <strong>al</strong>l<u>aa</u>hi <strong>al</strong>latii akhraja li\'ib<u>aa</u>dihi wa<strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti mina <strong>al</strong>rrizqi qul hiya lilla<u>dz</u>iina <u>aa</u>manuu fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> kh<u>aa</u>li<u>sh</u>atan yawma <strong>a</strong>lqiy<u>aa</u>mati ka<u>dzaa</u>lika nufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun<strong>a</strong>', 'Katakanlah (Muhammad), “Siapakah yang mengharamkan perhiasan dari Allah yang telah disediakan untuk hamba-hamba-Nya dan rezeki yang baik-baik? Katakanlah, “Semua itu untuk orang-orang yang beriman dalam kehidupan dunia, dan khusus (untuk mereka saja) pada hari Kiamat.” Demikianlah Kami menjelaskan ayat-ayat itu untuk orang-orang yang mengetahui.'),
	(987, 7, 33, 'قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ', 'qul innam<u>aa</u> <u>h</u>arrama rabbiya <strong>a</strong>lfaw<u>aah</u>isya m<u>aa</u> <u>zh</u>ahara minh<u>aa</u> wam<u>aa</u> ba<u>th</u>ana wa<strong>a</strong>l-itsma wa<strong>a</strong>lbaghya bighayri <strong>a</strong>l<u>h</u>aqqi wa-an tusyrikuu bi<strong>al</strong>l<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wa-an taquuluu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Katakanlah (Muhammad), “Tuhanku hanya mengharamkan segala perbuatan keji yang terlihat dan yang tersembunyi, perbuatan dosa, perbuatan zalim tanpa alasan yang benar, dan (mengharamkan) kamu mempersekutukan Allah dengan sesuatu, sedangkan Dia tidak menurunkan alasan untuk itu, dan (mengharamkan) kamu membicarakan tentang Allah apa yang tidak kamu ketahui.”'),
	(988, 7, 34, 'وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ', 'walikulli ummatin ajalun fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum l<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun<strong>a</strong>', 'Dan setiap umat mempunyai ajal (batas waktu). Apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun.'),
	(989, 7, 35, 'يٰبَنِيْٓ اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ', 'y<u>aa</u> banii <u>aa</u>dama imm<u>aa</u> ya/tiyannakum rusulun minkum yaqu<u>shsh</u>uuna \'alaykum <u>aa</u>y<u>aa</u>tii famani ittaq<u>aa</u> wa-a<u>sh</u>la<u>h</u>a fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Wahai anak cucu Adam! Jika datang kepadamu rasul-rasul dari kalanganmu sendiri, yang menceritakan ayat-ayat-Ku kepadamu, maka barangsiapa bertakwa dan mengadakan perbaikan, maka tidak ada rasa takut pada mereka, dan mereka tidak bersedih hati.'),
	(990, 7, 36, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wa<strong>i</strong>stakbaruu \'anh<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Tetapi orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, mereka itulah penghuni neraka; mereka kekal di dalamnya.'),
	(991, 7, 37, 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اُولٰۤىِٕكَ يَنَالُهُمْ نَصِيْبُهُمْ مِّنَ الْكِتٰبِۗ حَتّٰٓى اِذَا جَاۤءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ  قَالُوْٓا اَيْنَ مَا كُنْتُمْ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗقَ', 'faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi ul<u>aa</u>-ika yan<u>aa</u>luhum na<u>sh</u>iibuhum mina <strong>a</strong>lkit<u>aa</u>bi <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at-hum rusulun<u>aa</u> yatawaffawnahum q<u>aa</u>luu ayna m<u>aa</u> kuntum tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi q<u>aa</u>luu <u>dh</u>alluu \'ann<u>aa</u> wasyahiduu \'al<u>aa</u> anfusihim annahum k<u>aa</u>nuu k<u>aa</u>firiin<strong>a</strong>', 'Siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah atau yang mendustakan ayat-ayat-Nya? Mereka itu akan memperoleh bagian yang telah ditentukan dalam Kitab sampai datang para utusan (malaikat) Kami kepada mereka untuk mencabut nyawanya. Mereka (para malaikat) berkata, “Manakah sembahan yang biasa kamu sembah selain Allah?” Mereka (orang musyrik) menjawab, “Semuanya telah lenyap dari kami.” Dan mereka memberikan kesaksian terhadap diri mereka sendiri bahwa mereka adalah orang-orang kafir.'),
	(992, 7, 38, 'قَالَ ادْخُلُوْا فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِّنَ الْجِنِّ وَالْاِنْسِ فِى النَّارِۙ كُلَّمَا دَخَلَتْ اُمَّةٌ لَّعَنَتْ اُخْتَهَا ۗحَتّٰٓى اِذَا ادَّارَكُوْا فِيْهَا جَمِيْعًا ۙقَالَتْ اُخْرٰىهُمْ لِاُوْلٰىهُمْ رَبَّنَا هٰٓؤُلَاۤءِ اَضَلُّو', 'q<u>aa</u>la udkhuluu fii umamin qad khalat min qablikum mina <strong>a</strong>ljinni wa<strong>a</strong>l-insi fii <strong>al</strong>nn<u>aa</u>ri kullam<u>aa</u> dakhalat ummatun la\'anat ukhtah<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> idd<u>aa</u>rakuu fiih<u>aa</u> jamii\'an q<u>aa</u>lat ukhr<u>aa</u>hum li-uul<u>aa</u>hum rabban<u>aa</u> h<u>aa</u>ul<u>aa</u>-i a<u>dh</u>alluun<u>aa</u> fa<u>aa</u>tihim \'a<u>dzaa</u>ban <u>dh</u>i\'fan mina <strong>al</strong>nn<u>aa</u>ri q<u>aa</u>la likullin <u>dh</u>i\'fun wal<u>aa</u>kin l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Allah berfirman, “Masuklah kamu ke dalam api neraka bersama golongan jin dan manusia yang telah lebih dahulu dari kamu. Setiap kali suatu umat masuk, dia melaknat saudaranya, sehingga apabila mereka telah masuk semuanya, berkatalah orang yang (masuk) belakangan (kepada) orang yang (masuk) terlebih dahulu, “Ya Tuhan kami, mereka telah menyesatkan kami. Datangkanlah siksaan api neraka yang berlipat ganda kepada mereka” Allah berfirman, “Masing-masing mendapatkan (siksaan) yang berlipat ganda, tapi kamu tidak mengetahui.”'),
	(993, 7, 39, 'وَقَالَتْ اُوْلٰىهُمْ لِاُخْرٰىهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُوْنَ ࣖ', 'waq<u>aa</u>lat uul<u>aa</u>hum li-ukhr<u>aa</u>hum fam<u>aa</u> k<u>aa</u>na lakum \'alayn<u>aa</u> min fa<u>dh</u>lin fa<u>dz</u>uuquu <strong>a</strong>l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum taksibuun<strong>a</strong>', 'Dan orang yang (masuk) terlebih dahulu berkata kepada yang (masuk) belakangan, “Kamu tidak mempunyai kelebihan sedikit pun atas kami. Maka rasakanlah azab itu karena perbuatan yang telah kamu lakukan.”'),
	(994, 7, 40, 'اِنَّ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَاۤءِ وَلَا يَدْخُلُوْنَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ فِيْ سَمِّ الْخِيَاطِ ۗ وَكَذٰلِكَ نَجْزِى الْمُجْرِمِيْنَ ', 'inna <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wa<strong>i</strong>stakbaruu \'anh<u>aa</u> l<u>aa</u> tufatta<u>h</u>u lahum abw<u>aa</u>bu <strong>al</strong>ssam<u>aa</u>-i wal<u>aa</u> yadkhuluuna <strong>a</strong>ljannata <u>h</u>att<u>aa</u> yalija <strong>a</strong>ljamalu fii sammi <strong>a</strong>lkhiy<u>aath</u>i waka<u>dzaa</u>lika najzii <strong>a</strong>lmujrimiin<strong>a</strong>', 'Sesungguhnya orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, tidak akan dibukakan pintu-pintu langit bagi mereka, dan mereka tidak akan masuk surga, sebelum unta masuk ke dalam lubang jarum. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat jahat.'),
	(995, 7, 41, 'لَهُمْ مِّنْ جَهَنَّمَ مِهَادٌ وَّمِنْ فَوْقِهِمْ غَوَاشٍۗ وَكَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ ', 'lahum min jahannama mih<u>aa</u>dun wamin fawqihim ghaw<u>aa</u>syin waka<u>dzaa</u>lika najzii <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Bagi mereka tikar tidur dari api neraka dan di atas mereka ada selimut (api neraka). Demikianlah Kami memberi balasan kepada orang-orang yang zalim.'),
	(996, 7, 42, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti l<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Dan orang-orang yang beriman serta mengerjakan kebajikan, Kami tidak akan membebani seseorang melainkan menurut kesanggupannya. Mereka itulah penghuni surga; mereka kekal di dalamnya,'),
	(997, 7, 43, 'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ تَجْرِيْ مِنْ تَحْتِهِمُ الْانْهٰرُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ هَدٰىنَا لِهٰذَاۗ وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَآ اَنْ هَدٰىنَا اللّٰهُ ۚ لَقَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۗ وَنُوْدُوْٓا ا', 'wanaza\'n<u>aa</u> m<u>aa</u> fii <u>sh</u>uduurihim min ghillin tajrii min ta<u>h</u>tihimu <strong>a</strong>l-anh<u>aa</u>ru waq<u>aa</u>luu <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii had<u>aa</u>n<u>aa</u> lih<u>aadzaa</u> wam<u>aa</u> kunn<u>aa</u> linahtadiya lawl<u>aa</u> an had<u>aa</u>n<u>aa</u> <strong>al</strong>l<u>aa</u>hu laqad j<u>aa</u>-at rusulu rabbin<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wanuuduu an tilkumu <strong>a</strong>ljannatu uuritstumuuh<u>aa</u> bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'dan Kami mencabut rasa dendam dari dalam dada mereka, di bawahnya mengalir sungai-sungai. Mereka berkata, “Segala puji bagi Allah yang telah menunjukkan kami ke (surga) ini. Kami tidak akan mendapat petunjuk sekiranya Allah tidak menunjukkan kami. Sesungguhnya rasul-rasul Tuhan kami telah datang membawa kebenaran.” Diserukan kepada mereka, “Itulah surga yang telah diwariskan kepadamu, karena apa yang telah kamu kerjakan.”'),
	(998, 7, 44, 'وَنَادٰٓى اَصْحٰبُ الْجَنَّةِ اَصْحٰبَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُّمْ مَّا وَعَدَ رَبُّكُمْ حَقًّا ۗقَالُوْا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌۢ بَيْنَهُمْ اَنْ لَّعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ', 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati a<u>sh</u>-<u>haa</u>ba <strong>al</strong>nn<u>aa</u>ri an qad wajadn<u>aa</u> m<u>aa</u> wa\'adan<u>aa</u> rabbun<u>aa</u> <u>h</u>aqqan fahal wajadtum m<u>aa</u> wa\'ada rabbukum <u>h</u>aqqan q<u>aa</u>luu na\'am fa-a<u>dzdz</u>ana mu-a<u>dzdz</u>inun baynahum an la\'natu <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan para penghuni surga menyeru penghuni-penghuni neraka, “Sungguh, kami telah memperoleh apa yang dijanjikan Tuhan kepada kami itu benar. Apakah kamu telah memperoleh apa yang dijanjikan Tuhan kepadamu itu benar?” Mereka menjawab, “Benar.” Kemudian penyeru (malaikat) mengumumkan di antara mereka, “Laknat Allah bagi orang-orang zalim,'),
	(999, 7, 45, 'اَلَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۚ وَهُمْ بِالْاٰخِرَةِ كٰفِرُوْنَۘ', '<strong>al</strong>la<u>dz</u>iina ya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan wahum bi<strong>a</strong>l-<u>aa</u>khirati k<u>aa</u>firuun<strong>a</strong>', '(yaitu) orang-orang yang menghalang-halangi (orang lain) dari jalan Allah dan ingin membelokkannya. Mereka itulah yang mengingkari kehidupan akhirat.”'),
	(1000, 7, 46, 'وَبَيْنَهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَّعْرِفُوْنَ كُلًّا ۢ بِسِيْمٰىهُمْۚ وَنَادَوْا اَصْحٰبَ الْجَنَّةِ اَنْ سَلٰمٌ عَلَيْكُمْۗ  لَمْ يَدْخُلُوْهَا وَهُمْ يَطْمَعُوْنَ ', 'wabaynahum<u>aa</u> <u>h</u>ij<u>aa</u>bun wa\'al<u>aa</u> <strong>a</strong>l-a\'r<u>aa</u>fi rij<u>aa</u>lun ya\'rifuuna kullan bisiim<u>aa</u>hum wan<u>aa</u>daw a<u>sh</u>-<u>haa</u>ba <strong>a</strong>ljannati an sal<u>aa</u>mun \'alaykum lam yadkhuluuh<u>aa</u> wahum ya<u>th</u>ma\'uun<strong>a</strong>', 'Dan di antara keduanya (penghuni surga dan neraka) ada tabir dan di atas A‘raf (tempat yang tertinggi) ada orang-orang yang saling mengenal, masing-masing dengan tanda-tandanya. Mereka menyeru penghuni surga, “Salamun ‘alaikum” (salam sejahtera bagimu). Mereka belum dapat masuk, tetapi mereka ingin segera (masuk).'),
	(1001, 7, 47, '۞ وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَاۤءَ اَصْحٰبِ النَّارِۙ قَالُوْا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظّٰلِمِيْنَ ࣖ', 'wa-i<u>dzaa</u> <u>sh</u>urifat ab<u>shaa</u>ruhum tilq<u>aa</u>-a a<u>sh</u>-<u>haa</u>bi <strong>al</strong>nn<u>aa</u>ri q<u>aa</u>luu rabban<u>aa</u> l<u>aa</u> taj\'aln<u>aa</u> ma\'a <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan apabila pandangan mereka dialihkan ke arah penghuni neraka, mereka berkata, “Ya Tuhan kami, janganlah Engkau tempatkan kami bersama-sama orang-orang zalim itu.”'),
	(1002, 7, 48, 'وَنَادٰٓى اَصْحٰبُ الْاَعْرَافِ رِجَالًا يَّعْرِفُوْنَهُمْ بِسِيْمٰىهُمْ قَالُوْا مَآ اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُوْنَ ', 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu <strong>a</strong>l-a\'r<u>aa</u>fi rij<u>aa</u>lan ya\'rifuunahum bisiim<u>aa</u>hum q<u>aa</u>luu m<u>aa</u> aghn<u>aa</u> \'ankum jam\'ukum wam<u>aa</u> kuntum tastakbiruun<strong>a</strong>', 'Dan orang-orang di atas A‘raf (tempat yang tertinggi) menyeru orang-orang yang mereka kenal dengan tanda-tandanya sambil berkata, “Harta yang kamu kumpulkan dan apa yang kamu sombongkan, (ternyata) tidak ada manfaatnya buat kamu.'),
	(1003, 7, 49, 'اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۗ  اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَآ اَنْتُمْ تَحْزَنُوْنَ ', 'ah<u>aa</u>ul<u>aa</u>-i <strong>al</strong>la<u>dz</u>iina aqsamtum l<u>aa</u> yan<u>aa</u>luhumu <strong>al</strong>l<u>aa</u>hu bira<u>h</u>matin udkhuluu <strong>a</strong>ljannata l<u>aa</u> khawfun \'alaykum wal<u>aa</u> antum ta<u>h</u>zanuun<strong>a</strong>', 'Itukah orang-orang yang kamu telah bersumpah, bahwa mereka tidak akan mendapat rahmat Allah?” (Allah berfirman), “Masuklah kamu ke dalam surga! Tidak ada rasa takut padamu dan kamu tidak pula akan bersedih hati.”'),
	(1004, 7, 50, 'وَنَادٰٓى اَصْحٰبُ النَّارِ اَصْحٰبَ الْجَنَّةِ اَنْ اَفِيْضُوْا عَلَيْنَا مِنَ الْمَاۤءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُ ۗقَالُوْٓا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكٰفِرِيْنَۙ ', 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu <strong>al</strong>n<u>aa</u>ri a<u>sh</u>-<u>haa</u>ba <strong>a</strong>ljannati an afii<u>dh</u>uu \'alayn<u>aa</u> mina <strong>a</strong>lm<u>aa</u>-i aw mimm<u>aa</u> razaqakumu <strong>al</strong>l<u>aa</u>hu q<u>aa</u>luu inna <strong>al</strong>l<u>aa</u>ha <u>h</u>arramahum<u>aa</u> \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Para penghuni neraka menyeru para penghuni surga, “Tuangkanlah (sedikit) air kepada kami atau rezeki apa saja yang telah dikaruniakan Allah kepadamu.” Mereka menjawab, “Sungguh, Allah telah mengharamkan keduanya bagi orang-orang kafir,”'),
	(1005, 7, 51, 'الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَهْوًا وَّلَعِبًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰىهُمْ كَمَا نَسُوْا لِقَاۤءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ ', '<strong>al</strong>la<u>dz</u>iina ittakha<u>dz</u>uu diinahum lahwan wala\'iban wagharrat-humu <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> fa<strong>a</strong>lyawma nans<u>aa</u>hum kam<u>aa</u> nasuu liq<u>aa</u>-a yawmihim h<u>aadzaa</u> wam<u>aa</u> k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yaj<u>h</u>aduun<strong>a</strong>', '(yaitu) orang-orang yang menjadikan agamanya sebagai permainan dan senda gurau, dan mereka telah tertipu oleh kehidupan dunia. Maka pada hari ini (Kiamat), Kami melupakan mereka sebagaimana mereka dahulu melupakan pertemuan hari ini, dan karena mereka mengingkari ayat-ayat Kami.'),
	(1006, 7, 52, 'وَلَقَدْ جِئْنٰهُمْ بِكِتٰبٍ فَصَّلْنٰهُ عَلٰى عِلْمٍ هُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ', 'walaqad ji/n<u>aa</u>hum bikit<u>aa</u>bin fa<u>shsh</u>aln<u>aa</u>hu \'al<u>aa</u> \'ilmin hudan wara<u>h</u>matan liqawmin yu/minuun<strong>a</strong>', 'Sungguh, Kami telah mendatangkan Kitab (Al-Qur\'an) kepada mereka, yang Kami jelaskan atas dasar pengetahuan, sebagai petunjuk dan rahmat bagi orang-orang yang beriman.'),
	(1007, 7, 53, 'هَلْ يَنْظُرُوْنَ اِلَّا تَأْوِيْلَهٗۗ يَوْمَ يَأْتِيْ تَأْوِيْلُهٗ يَقُوْلُ الَّذِيْنَ نَسُوْهُ مِنْ قَبْلُ قَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَّنَا مِنْ شُفَعَاۤءَ فَيَشْفَعُوْا لَنَآ اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِيْ كُنَّا نَعْمَ', 'hal yan<u>zh</u>uruuna ill<u>aa</u> ta/wiilahu yawma ya/tii ta/wiiluhu yaquulu <strong>al</strong>la<u>dz</u>iina nasuuhu min qablu qad j<u>aa</u>-at rusulu rabbin<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi fahal lan<u>aa</u> min syufa\'<u>aa</u>-a fayasyfa\'uu lan<u>aa</u> aw nuraddu fana\'mala ghayra <strong>al</strong>la<u>dz</u>ii kunn<u>aa</u> na\'malu qad khasiruu anfusahum wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Tidakkah mereka hanya menanti-nanti bukti kebenaran (Al-Qur\'an) itu. Pada hari bukti kebenaran itu tiba, orang-orang yang sebelum itu mengabaikannya berkata, “Sungguh, rasul-rasul Tuhan kami telah datang membawa kebenaran. Maka adakah pemberi syafaat bagi kami yang akan memberikan pertolongan kepada kami atau agar kami dikembalikan (ke dunia) sehingga kami akan beramal tidak seperti perbuatan yang pernah kami lakukan dahulu?” Mereka sebenarnya telah merugikan dirinya sendiri dan apa yang mereka ada-adakan dahulu telah hilang lenyap dari mereka.'),
	(1008, 7, 54, 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ يُغْشِى الَّيْلَ النَّهَارَ يَطْلُبُهٗ حَثِيْثًاۙ وَّالشَّمْسَ وَالْقَمَرَ وَالنُّجُوْمَ مُسَخَّرٰتٍۢ بِاَمْرِهٖٓ  ۙاَلَا لَهُ الْخَلْقُ ', 'inna rabbakumu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'arsyi yughsyii <strong>al</strong>layla <strong>al</strong>nnah<u>aa</u>ra ya<u>th</u>lubuhu <u>h</u>atsiitsan wa<strong>al</strong>sysyamsa wa<strong>a</strong>lqamara wa<strong>al</strong>nnujuuma musakhkhar<u>aa</u>tin bi-amrihi <strong>a</strong>l<u>aa</u> lahu <strong>a</strong>lkhalqu wa<strong>a</strong>l-amru tab<u>aa</u>raka <strong>al</strong>l<u>aa</u>hu rabbu <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Sungguh, Tuhanmu (adalah) Allah yang menciptakan langit dan bumi dalam enam masa, lalu Dia bersemayam di atas ‘Arsy. Dia menutupkan malam kepada siang yang mengikutinya dengan cepat. (Dia ciptakan) matahari, bulan dan bintang-bintang tunduk kepada perintah-Nya. Ingatlah! Segala penciptaan dan urusan menjadi hak-Nya. Mahasuci Allah, Tuhan seluruh alam.'),
	(1009, 7, 55, 'اُدْعُوْا رَبَّكُمْ تَضَرُّعًا وَّخُفْيَةً ۗاِنَّهٗ لَا يُحِبُّ الْمُعْتَدِيْنَۚ ', 'ud\'uu rabbakum ta<u>dh</u>arru\'an wakhufyatan innahu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmu\'tadiin<strong>a</strong>', 'Berdoalah kepada Tuhanmu dengan rendah hati dan suara yang lembut. Sungguh, Dia tidak menyukai orang-orang yang melampaui batas.');
INSERT INTO `tbl_ayat` (`id`, `surah`, `nomor`, `ar`, `tr`, `idn`) VALUES
	(1010, 7, 56, 'وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوْهُ خَوْفًا وَّطَمَعًاۗ اِنَّ رَحْمَتَ اللّٰهِ قَرِيْبٌ مِّنَ الْمُحْسِنِيْنَ ', 'wal<u>aa</u> tufsiduu fii <strong>a</strong>l-ar<u>dh</u>i ba\'da i<u>sh</u>l<u>aah</u>ih<u>aa</u> wa<strong>u</strong>d\'uuhu khawfan wa<u>th</u>ama\'an inna ra<u>h</u>mata <strong>al</strong>l<u>aa</u>hi qariibun mina <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan janganlah kamu berbuat kerusakan di bumi setelah (diciptakan) dengan baik. Berdoalah kepada-Nya dengan rasa takut dan penuh harap. Sesungguhnya rahmat Allah sangat dekat kepada orang yang berbuat kebaikan.'),
	(1011, 7, 57, 'وَهُوَ الَّذِيْ يُرْسِلُ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ حَتّٰٓى اِذَآ اَقَلَّتْ سَحَابًا ثِقَالًا سُقْنٰهُ لِبَلَدٍ مَّيِّتٍ فَاَنْزَلْنَا بِهِ الْمَاۤءَ فَاَخْرَجْنَا بِهٖ مِنْ كُلِّ الثَّمَرٰتِۗ  كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ ت', 'wahuwa <strong>al</strong>la<u>dz</u>ii yursilu <strong>al</strong>rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi <u>h</u>att<u>aa</u> i<u>dzaa</u> aqallat sa<u>haa</u>ban tsiq<u>aa</u>lan suqn<u>aa</u>hu libaladin mayyitin fa-anzaln<u>aa</u> bihi <strong>a</strong>lm<u>aa</u>-a fa-akhrajn<u>aa</u> bihi min kulli <strong>al</strong>tstsamar<u>aa</u>ti ka<u>dzaa</u>lika nukhriju <strong>a</strong>lmawt<u>aa</u> la\'allakum ta<u>dz</u>akkaruun<strong>a</strong>', 'Dialah yang meniupkan angin sebagai pembawa kabar gembira, mendahului kedatangan rahmat-Nya (hujan), sehingga apabila angin itu membawa awan mendung, Kami halau ke suatu daerah yang tandus, lalu Kami turunkan hujan di daerah itu. Kemudian Kami tumbuhkan dengan hujan itu berbagai macam buah-buahan. Seperti itulah Kami membangkitkan orang yang telah mati, mudah-mudahan kamu mengambil pelajaran.'),
	(1012, 7, 58, 'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهٗ بِاِذْنِ رَبِّهٖۚ وَالَّذِيْ خَبُثَ لَا يَخْرُجُ اِلَّا نَكِدًاۗ  كَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ لِقَوْمٍ يَّشْكُرُوْنَ ࣖ ', 'wa<strong>a</strong>lbaladu <strong>al</strong><u>ththh</u>ayyibu yakhruju nab<u>aa</u>tuhu bi-i<u>dz</u>ni rabbihi wa<strong>a</strong>lla<u>dz</u>ii khabutsa l<u>aa</u> yakhruju ill<u>aa</u> nakidan ka<u>dzaa</u>lika nu<u>sh</u>arrifu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin yasykuruun<strong>a</strong>', 'Dan tanah yang baik, tanaman-tanamannya tumbuh subur dengan izin Tuhan; dan tanah yang buruk, tanaman-tanamannya yang tumbuh merana. Demikianlah Kami menjelaskan berulang-ulang tanda-tanda (kebesaran Kami) bagi orang-orang yang bersyukur.'),
	(1013, 7, 59, 'لَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ', 'laqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi faq<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Sungguh, Kami benar-benar telah mengutus Nuh kepada kaumnya, lalu dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya aku takut kamu akan ditimpa azab pada hari yang dahsyat (kiamat).'),
	(1014, 7, 60, 'قَالَ الْمَلَاُ مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'q<u>aa</u>la <strong>a</strong>lmalau min qawmihi inn<u>aa</u> lanar<u>aa</u>ka fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Pemuka-pemuka kaumnya berkata, “Sesungguhnya kami memandang kamu benar-benar berada dalam kesesatan yang nyata.”'),
	(1015, 7, 61, 'قَالَ يٰقَوْمِ لَيْسَ بِيْ ضَلٰلَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ', 'q<u>aa</u>la y<u>aa</u> qawmi laysa bii <u>dh</u>al<u>aa</u>latun wal<u>aa</u>kinnii rasuulun min rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dia (Nuh) menjawab, “Wahai kaumku! Aku tidak sesat; tetapi aku ini seorang Rasul dari Tuhan seluruh alam.'),
	(1016, 7, 62, 'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ ', 'uballighukum ris<u>aa</u>l<u>aa</u>ti rabbii wa-an<u>sh</u>a<u>h</u>u lakum wa-a\'lamu mina <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Aku menyampaikan kepadamu amanat Tuhanku, memberi nasihat kepadamu, dan aku mengetahui dari Allah apa yang tidak kamu ketahui.”'),
	(1017, 7, 63, 'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوْا وَلَعَلَّكُمْ تُرْحَمُوْنَ ', 'awa \'ajibtum an j<u>aa</u>-akum <u>dz</u>ikrun min rabbikum \'al<u>aa</u> rajulin minkum liyun<u>dz</u>irakum walitattaquu wala\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dan herankah kamu bahwa ada peringatan yang datang dari Tuhanmu melalui seorang laki-laki dari kalanganmu sendiri, untuk memberi peringatan kepadamu dan agar kamu bertakwa, sehingga kamu mendapat rahmat?'),
	(1018, 7, 64, 'فَكَذَّبُوْهُ فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ فِى الْفُلْكِ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمًا عَمِيْنَ ࣖ ', 'faka<u>dzdz</u>abuuhu fa-anjayn<u>aa</u>hu wa<strong>a</strong>lla<u>dz</u>iina ma\'ahu fii <strong>a</strong>lfulki wa-aghraqn<u>aa</u> <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> innahum k<u>aa</u>nuu qawman \'amiin<strong>a</strong>', 'Maka mereka mendustakannya (Nuh). Lalu Kami selamatkan dia dan orang-orang yang bersamanya di dalam kapal. Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang buta (mata hatinya).'),
	(1019, 7, 65, '۞ وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ', 'wa-il<u>aa</u> \'<u>aa</u>din akh<u>aa</u>hum huudan q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun<strong>a</strong>', 'Dan kepada kaum ‘Ad (Kami utus) Hud, saudara mereka. Dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Maka mengapa kamu tidak bertakwa?”'),
	(1020, 7, 66, 'قَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ سَفَاهَةٍ وَّاِنَّا لَنَظُنُّكَ مِنَ الْكٰذِبِيْنَ ', 'q<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina kafaruu min qawmihi inn<u>aa</u> lanar<u>aa</u>ka fii saf<u>aa</u>hatin wa-inn<u>aa</u> lana<u>zh</u>unnuka mina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Pemuka-pemuka orang-orang yang kafir dari kaumnya berkata, “Sesungguhnya kami memandang kamu benar-benar kurang waras dan kami kira kamu termasuk orang-orang yang berdusta.”'),
	(1021, 7, 67, 'قَالَ يٰقَوْمِ لَيْسَ بِيْ سَفَاهَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ', 'q<u>aa</u>la y<u>aa</u> qawmi laysa bii saf<u>aa</u>hatun wal<u>aa</u>kinnii rasuulun min rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dia (Hud) menjawab, “Wahai kaumku! Bukan aku kurang waras, tetapi aku ini adalah Rasul dari Tuhan seluruh alam.'),
	(1022, 7, 68, 'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنَا۠ لَكُمْ نَاصِحٌ اَمِيْنٌ ', 'uballighukum ris<u>aa</u>l<u>aa</u>ti rabbii wa-an<u>aa</u> lakum n<u>aas</u>i<u>h</u>un amiin<strong>un</strong>', 'Aku menyampaikan kepadamu amanat Tuhanku dan pemberi nasihat yang terpercaya kepada kamu.'),
	(1023, 7, 69, 'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْۗ وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ قَوْمِ نُوْحٍ وَّزَادَكُمْ فِى الْخَلْقِ بَصْۣطَةً ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُوْنَ', 'awa \'ajibtum an j<u>aa</u>-akum <u>dz</u>ikrun min rabbikum \'al<u>aa</u> rajulin minkum liyun<u>dz</u>irakum wa<strong>u</strong><u>dz</u>kuruu i<u>dz</u> ja\'alakum khulaf<u>aa</u>-a min ba\'di qawmi nuu<u>h</u>in waz<u>aa</u>dakum fii <strong>a</strong>lkhalqi bas<u>th</u>atan fa<strong>u</strong><u>dz</u>kuruu <u>aa</u>l<u>aa</u>-a <strong>al</strong>l<u>aa</u>hi la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Dan herankah kamu bahwa ada peringatan yang datang dari Tuhanmu melalui seorang laki-laki dari kalanganmu sendiri, untuk memberi peringatan kepadamu? Ingatlah ketika Dia menjadikan kamu sebagai khalifah-khalifah setelah kaum Nuh, dan Dia lebihkan kamu dalam kekuatan tubuh dan perawakan. Maka ingatlah akan nikmat-nikmat Allah agar kamu beruntung. ”'),
	(1024, 7, 70, 'قَالُوْٓا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهٗ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَاۤؤُنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ', 'q<u>aa</u>luu aji/tan<u>aa</u> lina\'buda <strong>al</strong>l<u>aa</u>ha wa<u>h</u>dahu wana<u>dz</u>ara m<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> fa/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Mereka berkata, “Apakah kedatanganmu kepada kami, agar kami hanya menyembah Allah saja dan meninggalkan apa yang biasa disembah oleh nenek moyang kami? Maka buktikanlah ancamanmu kepada kami, jika kamu benar!”'),
	(1025, 7, 71, 'قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِّنْ رَّبِّكُمْ رِجْسٌ وَّغَضَبٌۗ  اَتُجَادِلُوْنَنِيْ فِيْٓ اَسْمَاۤءٍ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ', 'q<u>aa</u>la qad waqa\'a \'alaykum min rabbikum rijsun wagha<u>dh</u>abun atuj<u>aa</u>diluunanii fii asm<u>aa</u>-in sammaytumuuh<u>aa</u> antum wa<u>aa</u>b<u>aa</u>ukum m<u>aa</u> nazzala <strong>al</strong>l<u>aa</u>hu bih<u>aa</u> min sul<u>th</u><u>aa</u>nin fa<strong>i</strong>nta<u>zh</u>iruu innii ma\'akum mina <strong>a</strong>lmunta<u>zh</u>iriin<strong>a</strong>', 'Dia (Hud) menjawab, “Sungguh, kebencian dan kemurkaan dari Tuhan akan menimpa kamu. Apakah kamu hendak berbantah denganku tentang nama-nama (berhala) yang kamu dan nenek moyangmu buat sendiri, padahal Allah tidak menurunkan keterangan untuk itu? Jika demikian, tunggulah! Sesungguhnya aku pun bersamamu termasuk yang menunggu.”'),
	(1026, 7, 72, 'فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ بِرَحْمَةٍ مِّنَّا وَقَطَعْنَا دَابِرَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَمَا كَانُوْا مُؤْمِنِيْنَ ࣖ ', 'fa-anjayn<u>aa</u>hu wa<strong>a</strong>lla<u>dz</u>iina ma\'ahu bira<u>h</u>matin minn<u>aa</u> waqa<u>th</u>a\'n<u>aa</u> d<u>aa</u>bira <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wam<u>aa</u> k<u>aa</u>nuu mu/miniin<strong>a</strong>', 'Maka Kami selamatkan dia (Hud) dan orang-orang yang bersamanya dengan rahmat Kami dan Kami musnahkan sampai ke akar-akarnya orang-orang yang mendustakan ayat-ayat Kami. Mereka bukanlah orang-orang beriman.'),
	(1027, 7, 73, 'وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًاۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْۗ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤء', 'wa-il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu qad j<u>aa</u>-atkum bayyinatun min rabbikum h<u>aadz</u>ihi n<u>aa</u>qatu <strong>al</strong>l<u>aa</u>hi lakum <u>aa</u>yatan fa<u>dz</u>aruuh<u>aa</u> ta/kul fii ar<u>dh</u>i <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> tamassuuh<u>aa</u> bisuu-in faya/khu<u>dz</u>akum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan kepada kaum Samud (Kami utus) saudara mereka Saleh. Dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Ini (seekor) unta betina dari Allah sebagai tanda untukmu. Biarkanlah ia makan di bumi Allah, janganlah disakiti, nanti akibatnya kamu akan mendapatkan siksaan yang pedih.”'),
	(1028, 7, 74, 'وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ عَادٍ وَّبَوَّاَكُمْ فِى الْاَرْضِ تَتَّخِذُوْنَ مِنْ سُهُوْلِهَا قُصُوْرًا وَّتَنْحِتُوْنَ الْجِبَالَ بُيُوْتًا ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ', 'wa<strong>u</strong><u>dz</u>kuruu i<u>dz</u> ja\'alakum khulaf<u>aa</u>-a min ba\'di \'<u>aa</u>din wabawwa-akum fii <strong>a</strong>l-ar<u>dh</u>i tattakhi<u>dz</u>uuna min suhuulih<u>aa</u> qu<u>sh</u>uuran watan<u>h</u>ituuna <strong>a</strong>ljib<u>aa</u>la buyuutan fa<strong>u</strong><u>dz</u>kuruu <u>aa</u>l<u>aa</u>-a <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> ta\'tsaw fii <strong>a</strong>l-ar<u>dh</u>i mufsidiin<strong>a</strong>', 'Dan ingatlah ketika Dia menjadikan kamu khalifah-khalifah setelah kaum ‘Ad dan menempatkan kamu di bumi. Di tempat yang datar kamu dirikan istana-istana dan di bukit-bukit kamu pahat menjadi rumah-rumah. Maka ingatlah nikmat-nikmat Allah dan janganlah kamu membuat kerusakan di bumi.'),
	(1029, 7, 75, 'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لِلَّذِيْنَ اسْتُضْعِفُوْا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُوْنَ اَنَّ صٰلِحًا مُّرْسَلٌ مِّنْ رَّبِّهٖۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلَ بِهٖ مُؤْمِنُوْنَ ', 'q<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina istakbaruu min qawmihi lilla<u>dz</u>iina istu<u>dh</u>\'ifuu liman <u>aa</u>mana minhum ata\'lamuuna anna <u>shaa</u>li<u>h</u>an mursalun min rabbihi q<u>aa</u>luu inn<u>aa</u> bim<u>aa</u> ursila bihi mu/minuun<strong>a</strong>', 'Pemuka-pemuka yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah, yaitu orang-orang yang telah beriman di antara kaumnya, “Tahukah kamu bahwa Saleh adalah seorang rasul dari Tuhannya?” Mereka menjawab, “Sesungguhnya kami percaya kepada apa yang disampaikannya.”'),
	(1030, 7, 76, 'قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا بِالَّذِيْٓ اٰمَنْتُمْ بِهٖ كٰفِرُوْنَ', 'q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina istakbaruu inn<u>aa</u> bi<strong>a</strong>lla<u>dz</u>ii <u>aa</u>mantum bihi k<u>aa</u>firuun<strong>a</strong>', 'Orang-orang yang menyombongkan diri berkata, “Sesungguhnya kami mengingkari apa yang kamu percayai.”'),
	(1031, 7, 77, 'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوْا يٰصٰلِحُ ائْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الْمُرْسَلِيْنَ ', 'fa\'aqaruu <strong>al</strong>nn<u>aa</u>qata wa\'ataw \'an amri rabbihim waq<u>aa</u>luu y<u>aa</u> <u>shaa</u>li<u>h</u>u i/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <strong>a</strong>lmursaliin<strong>a</strong>', 'Kemudian mereka sembelih unta betina itu, dan berlaku angkuh terhadap perintah Tuhannya. Mereka berkata, “Wahai Saleh! Buktikanlah ancaman kamu kepada kami, jika benar engkau salah seorang rasul.”'),
	(1032, 7, 78, 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ', 'fa-akha<u>dz</u>at-humu <strong>al</strong>rrajfatu fa-a<u>sh</u>ba<u>h</u>uu fii d<u>aa</u>rihim j<u>aa</u>tsimiin<strong>a</strong>', 'Lalu datanglah gempa menimpa mereka, dan mereka pun mati bergelimpangan di dalam reruntuhan rumah mereka.'),
	(1033, 7, 79, 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبِّيْ وَنَصَحْتُ لَكُمْ وَلٰكِنْ لَّا تُحِبُّوْنَ النّٰصِحِيْنَ ', 'fatawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> qawmi laqad ablaghtukum ris<u>aa</u>lata rabbii wana<u>sh</u>a<u>h</u>tu lakum wal<u>aa</u>kin l<u>aa</u> tu<u>h</u>ibbuuna <strong>al</strong>nn<u>aas</u>i<u>h</u>iin<strong>a</strong>', 'Kemudian dia (Saleh) pergi meninggalkan mereka sambil berkata, “Wahai kaumku! Sungguh, aku telah menyampaikan amanat Tuhanku kepadamu dan aku telah menasihati kamu. Tetapi kamu tidak menyukai orang yang memberi nasihat.”'),
	(1034, 7, 80, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ', 'waluu<u>th</u>an i<u>dz</u> q<u>aa</u>la liqawmihi ata/tuuna <strong>a</strong>lf<u>aah</u>isyata m<u>aa</u> sabaqakum bih<u>aa</u> min a<u>h</u>adin mina <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan (Kami juga telah mengutus) Lut, ketika dia berkata kepada kaumnya, “Mengapa kamu melakukan perbuatan keji, yang belum pernah dilakukan oleh seorang pun sebelum kamu (di dunia ini).'),
	(1035, 7, 81, 'اِنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ ', 'innakum lata/tuuna <strong>al</strong>rrij<u>aa</u>la syahwatan min duuni <strong>al</strong>nnis<u>aa</u>-i bal antum qawmun musrifuun<strong>a</strong>', 'Sungguh, kamu telah melampiaskan syahwatmu kepada sesama lelaki bukan kepada perempuan. Kamu benar-benar kaum yang melampaui batas.”'),
	(1036, 7, 82, 'وَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْهُمْ مِّنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ ', 'wam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu akhrijuuhum min qaryatikum innahum un<u>aa</u>sun yata<u>th</u>ahharuun<strong>a</strong>', 'Dan jawaban kaumnya tidak lain hanya berkata, “Usirlah mereka (Lut dan pengikutnya) dari negerimu ini, mereka adalah orang yang menganggap dirinya suci.”'),
	(1037, 7, 83, 'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ', 'fa-anjayn<u>aa</u>hu wa-ahlahu ill<u>aa</u> imra-atahu k<u>aa</u>nat mina <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'Kemudian Kami selamatkan dia dan pengikutnya, kecuali istrinya. Dia (istrinya) termasuk orang-orang yang tertinggal.'),
	(1038, 7, 84, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ࣖ ', 'wa-am<u>th</u>arn<u>aa</u> \'alayhim ma<u>th</u>aran fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmujrimiin<strong>a</strong>', 'Dan Kami hujani mereka dengan hujan (batu). Maka perhatikanlah bagaimana kesudahan orang yang berbuat dosa itu.'),
	(1039, 7, 85, 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تُفْسِدُوْا فِى الْاَر', 'wa-il<u>aa</u> madyana akh<u>aa</u>hum syu\'ayban q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu qad j<u>aa</u>-atkum bayyinatun min rabbikum fa-awfuu <strong>a</strong>lkayla wa<strong>a</strong>lmiiz<u>aa</u>na wal<u>aa</u> tabkhasuu <strong>al</strong>nn<u>aa</u>sa asyy<u>aa</u>-ahum wal<u>aa</u> tufsiduu fii <strong>a</strong>l-ar<u>dh</u>i ba\'da i<u>sh</u>l<u>aah</u>ih<u>aa</u> <u>dzaa</u>likum khayrun lakum in kuntum mu/miniin<strong>a</strong>', 'Dan kepada penduduk Madyan, Kami (utus) Syuaib, saudara mereka sendiri. Dia berkata, “Wahai kaumku! Sembahlah Allah. Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Sempurnakanlah takaran dan timbangan, dan jangan kamu merugikan orang sedikit pun. Janganlah kamu berbuat kerusakan di bumi setelah (diciptakan) dengan baik. Itulah yang lebih baik bagimu jika kamu orang beriman.”'),
	(1040, 7, 86, 'وَلَا تَقْعُدُوْا بِكُلِّ صِرَاطٍ تُوْعِدُوْنَ وَتَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ بِهٖ وَتَبْغُوْنَهَا عِوَجًاۚ وَاذْكُرُوْٓا اِذْ كُنْتُمْ قَلِيْلًا فَكَثَّرَكُمْۖ وَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ', 'wal<u>aa</u> taq\'uduu bikulli <u>sh</u>ir<u>aath</u>in tuu\'iduuna wata<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi man <u>aa</u>mana bihi watabghuunah<u>aa</u> \'iwajan wa<strong>u</strong><u>dz</u>kuruu i<u>dz</u> kuntum qaliilan fakatstsarakum wa<strong>u</strong>n<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan janganlah kamu duduk di setiap jalan dengan menakut-nakuti dan menghalang-halangi orang-orang yang beriman dari jalan Allah dan ingin membelokkannya. Ingatlah ketika kamu dahulunya sedikit, lalu Allah memperbanyak jumlah kamu. Dan perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan.'),
	(1041, 7, 87, 'وَاِنْ كَانَ طَاۤىِٕفَةٌ مِّنْكُمْ اٰمَنُوْا بِالَّذِيْٓ اُرْسِلْتُ بِهٖ وَطَاۤىِٕفَةٌ لَّمْ يُؤْمِنُوْا فَاصْبِرُوْا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ  ۔', 'wa-in k<u>aa</u>na <u>th</u><u>aa</u>-ifatun minkum <u>aa</u>manuu bi<strong>a</strong>lla<u>dz</u>ii ursiltu bihi wa<u>th</u><u>aa</u>-ifatun lam yu/minuu fa<strong>i</strong><u>sh</u>biruu <u>h</u>att<u>aa</u> ya<u>h</u>kuma <strong>al</strong>l<u>aa</u>hu baynan<u>aa</u> wahuwa khayru <strong>a</strong>l<u>haa</u>kimiin<strong>a</strong>', 'Jika ada segolongan di antara kamu yang beriman kepada (ajaran) yang aku diutus menyampaikannya, dan ada (pula) segolongan yang tidak beriman, maka bersabarlah sampai Allah menetapkan keputusan di antara kita. Dialah hakim yang terbaik.'),
	(1042, 7, 88, 'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لَنُخْرِجَنَّكَ يٰشُعَيْبُ وَالَّذِيْنَ اٰمَنُوْا مَعَكَ مِنْ قَرْيَتِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ قَالَ اَوَلَوْ كُنَّا كَارِهِيْنَ', 'q<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina istakbaruu min qawmihi lanukhrijannaka y<u>aa</u> syu\'aybu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'aka min qaryatin<u>aa</u> aw lata\'uudunna fii millatin<u>aa</u> q<u>aa</u>la awa law kunn<u>aa</u> k<u>aa</u>rihiin<strong>a</strong>', 'Pemuka-pemuka yang menyombongkan diri dari kaum Syuaib berkata, “Wahai Syuaib! Pasti kami usir engkau bersama orang-orang yang beriman dari negeri kami, kecuali engkau kembali kepada agama kami.”Syuaib berkata, “Apakah (kamu akan mengusir kami), kendatipun kami tidak suka?'),
	(1043, 7, 89, 'قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِبًا اِنْ عُدْنَا فِيْ مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰىنَا اللّٰهُ مِنْهَاۗ وَمَا يَكُوْنُ لَنَآ اَنْ نَّعُوْدَ فِيْهَآ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّنَاۗ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًاۗ عَلَى اللّٰهِ تَوَكَّ', 'qadi iftarayn<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban in \'udn<u>aa</u> fii millatikum ba\'da i<u>dz</u> najj<u>aa</u>n<u>aa</u> <strong>al</strong>l<u>aa</u>hu minh<u>aa</u> wam<u>aa</u> yakuunu lan<u>aa</u> an na\'uuda fiih<u>aa</u> ill<u>aa</u> an yasy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu rabbun<u>aa</u> wasi\'a rabbun<u>aa</u> kulla syay-in \'ilman \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi tawakkaln<u>aa</u> rabban<u>aa</u> ifta<u>h</u> baynan<u>aa</u> wabayna qawmin<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wa-anta khayru <strong>a</strong>lf<u>aa</u>ti<u>h</u>iin<strong>a</strong>', 'Sungguh, kami telah mengada-adakan kebohongan yang besar terhadap Allah, jika kami kembali kepada agamamu, setelah Allah melepaskan kami darinya. Dan tidaklah pantas kami kembali kepadanya, kecuali jika Allah, Tuhan kami menghendaki. Pengetahuan Tuhan kami meliputi segala sesuatu. Hanya kepada Allah kami bertawakal. Ya Tuhan kami, berilah keputusan antara kami dan kaum kami dengan hak (adil). Engkaulah pemberi keputusan terbaik.”'),
	(1044, 7, 90, 'وَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ لَىِٕنِ اتَّبَعْتُمْ شُعَيْبًا ِانَّكُمْ اِذًا لَّخٰسِرُوْنَ', 'waq<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina kafaruu min qawmihi la-ini ittaba\'tum syu\'ayban innakum i<u>dz</u>an lakh<u>aa</u>siruun<strong>a</strong>', 'Dan pemuka-pemuka dari kaumnya (Syuaib) yang kafir berkata (kepada sesamanya), “Sesungguhnya jika kamu mengikuti Syuaib, tentu kamu menjadi orang-orang yang rugi.”'),
	(1045, 7, 91, 'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَۙ', 'fa-akha<u>dz</u>at-humu <strong>al</strong>rrajfatu fa-a<u>sh</u>ba<u>h</u>uu fii d<u>aa</u>rihim j<u>aa</u>tsimiin<strong>a</strong>', 'Lalu datanglah gempa menimpa mereka, dan mereka pun mati bergelimpangan di dalam reruntuhan rumah mereka.'),
	(1046, 7, 92, 'الَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَاَنْ لَّمْ يَغْنَوْا فِيْهَاۚ  اَلَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَانُوْا هُمُ الْخٰسِرِيْنَ', '<strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu syu\'ayban ka-an lam yaghnaw fiih<u>aa</u> <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu syu\'ayban k<u>aa</u>nuu humu <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Orang-orang yang mendustakan Syuaib seakan-akan mereka belum pernah tinggal di (negeri) itu. Mereka yang mendustakan Syuaib, itulah orang-orang yang rugi.'),
	(1047, 7, 93, 'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسٰلٰتِ رَبِّيْ وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كٰفِرِيْنَ ࣖ ', 'fatawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> qawmi laqad ablaghtukum ris<u>aa</u>l<u>aa</u>ti rabbii wana<u>sh</u>a<u>h</u>tu lakum fakayfa <u>aa</u>s<u>aa</u> \'al<u>aa</u> qawmin k<u>aa</u>firiin<strong>a</strong>', 'Maka Syuaib meninggalkan mereka seraya berkata, “Wahai kaumku! Sungguh, aku telah menyampaikan amanat Tuhanku kepadamu dan aku telah menasihati kamu. Maka bagaimana aku akan bersedih hati terhadap orang-orang kafir?”'),
	(1048, 7, 94, 'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّبِيٍّ اِلَّآ اَخَذْنَآ اَهْلَهَا بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَضَّرَّعُوْنَ', 'wam<u>aa</u> arsaln<u>aa</u> fii qaryatin min nabiyyin ill<u>aa</u> akha<u>dz</u>n<u>aa</u> ahlah<u>aa</u> bi<strong>a</strong>lba/s<u>aa</u>-i wa<strong>al</strong><u>dhdh</u>arr<u>aa</u>-i la\'allahum ya<u>dhdh</u>arra\'uun<strong>a</strong>', 'Dan Kami tidak mengutus seorang nabi pun kepada sesuatu negeri, (lalu penduduknya mendustakan nabi itu), melainkan Kami timpakan kepada penduduknya kesempitan dan penderitaan agar mereka (tunduk dengan) merendahkan diri.'),
	(1049, 7, 95, 'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَّقَالُوْا قَدْ مَسَّ اٰبَاۤءَنَا الضَّرَّاۤءُ وَالسَّرَّاۤءُ فَاَخَذْنٰهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ', 'tsumma baddaln<u>aa</u> mak<u>aa</u>na <strong>al</strong>ssayyi-ati <strong>a</strong>l<u>h</u>asanata <u>h</u>att<u>aa</u> \'afaw waq<u>aa</u>luu qad massa <u>aa</u>b<u>aa</u>-an<u>aa</u> <strong>al</strong><u>dhdh</u>arr<u>aa</u>u wa<strong>al</strong>ssarr<u>aa</u>u fa-akha<u>dz</u>n<u>aa</u>hum baghtatan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Kemudian Kami ganti penderitaan itu dengan kesenangan (sehingga keturunan dan harta mereka) bertambah banyak, lalu mereka berkata, “Sungguh, nenek moyang kami telah merasakan penderitaan dan kesenangan,” maka Kami timpakan siksaan atas mereka dengan tiba-tiba tanpa mereka sadari.'),
	(1050, 7, 96, 'وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوْا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكٰتٍ مِّنَ السَّمَاۤءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوْا فَاَخَذْنٰهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ', 'walaw anna ahla <strong>a</strong>lqur<u>aa</u> <u>aa</u>manuu wa<strong>i</strong>ttaqaw lafata<u>h</u>n<u>aa</u> \'alayhim barak<u>aa</u>tin mina <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u>kin ka<u>dzdz</u>abuu fa-akha<u>dz</u>n<u>aa</u>hum bim<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'Dan sekiranya penduduk negeri beriman dan bertakwa, pasti Kami akan melimpahkan kepada mereka berkah dari langit dan bumi, tetapi ternyata mereka mendustakan (ayat-ayat Kami), maka Kami siksa mereka sesuai dengan apa yang telah mereka kerjakan.'),
	(1051, 7, 97, 'اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَّهُمْ نَاۤىِٕمُوْنَۗ', 'afa-amina ahlu <strong>a</strong>lqur<u>aa</u> an ya/tiyahum ba/sun<u>aa</u> bay<u>aa</u>tan wahum n<u>aa</u>-imuun<strong>a</strong>', 'Maka apakah penduduk negeri itu merasa aman dari siksaan Kami yang datang malam hari ketika mereka sedang tidur?'),
	(1052, 7, 98, 'اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا ضُحًى وَّهُمْ يَلْعَبُوْنَ', 'awa amina ahlu <strong>a</strong>lqur<u>aa</u> an ya/tiyahum ba/sun<u>aa</u> <u>dh</u>u<u>h</u>an wahum yal\'abuun<strong>a</strong>', 'Atau apakah penduduk negeri itu merasa aman dari siksaan Kami yang datang pada pagi hari ketika mereka sedang bermain?'),
	(1053, 7, 99, 'اَفَاَمِنُوْا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخٰسِرُوْنَ ࣖ ', 'afa-aminuu makra <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> ya/manu makra <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> <strong>a</strong>lqawmu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', 'Atau apakah mereka merasa aman dari siksaan Allah (yang tidak terduga-duga)? Tidak ada yang merasa aman dari siksaan Allah selain orang-orang yang rugi.'),
	(1054, 7, 100, 'اَوَلَمْ يَهْدِ لِلَّذِيْنَ يَرِثُوْنَ الْاَرْضَ مِنْۢ بَعْدِ اَهْلِهَآ اَنْ لَّوْ نَشَاۤءُ اَصَبْنٰهُمْ بِذُنُوْبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَسْمَعُوْنَ', 'awa lam yahdi lilla<u>dz</u>iina yaritsuuna <strong>a</strong>l-ar<u>dh</u>a min ba\'di ahlih<u>aa</u> an law nasy<u>aa</u>u a<u>sh</u>abn<u>aa</u>hum bi<u>dz</u>unuubihim wana<u>th</u>ba\'u \'al<u>aa</u> quluubihim fahum l<u>aa</u> yasma\'uun<strong>a</strong>', 'Atau apakah belum jelas bagi orang-orang yang mewarisi suatu negeri setelah (lenyap) penduduknya? Bahwa kalau Kami menghendaki pasti Kami siksa mereka karena dosa-dosanya; dan Kami mengunci hati mereka sehingga mereka tidak dapat mendengar (pelajaran).'),
	(1055, 7, 101, 'تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤىِٕهَاۚ وَلَقَدْ جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا مِنْ قَبْلُۗ  كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الْكٰفِرِيْنَ  ', 'tilka <strong>a</strong>lqur<u>aa</u> naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-ih<u>aa</u> walaqad j<u>aa</u>-at-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nuu liyu/minuu bim<u>aa</u> ka<u>dzdz</u>abuu min qablu ka<u>dzaa</u>lika ya<u>th</u>ba\'u <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> quluubi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Itulah negeri-negeri (yang telah Kami binasakan) itu, Kami ceritakan sebagian kisahnya kepadamu. Rasul-rasul mereka benar-benar telah datang kepada mereka dengan membawa bukti-bukti yang nyata. Tetapi mereka tidak beriman (juga) kepada apa yang telah mereka dustakan sebelumnya. Demikianlah Allah mengunci hati orang-orang kafir.'),
	(1056, 7, 102, 'وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِّنْ عَهْدٍۚ وَاِنْ وَّجَدْنَآ اَكْثَرَهُمْ لَفٰسِقِيْنَ', 'wam<u>aa</u> wajadn<u>aa</u> li-aktsarihim min \'ahdin wa-in wajadn<u>aa</u> aktsarahum laf<u>aa</u>siqiin<strong>a</strong>', 'Dan Kami tidak mendapati kebanyakan mereka memenuhi janji. Sebaliknya yang Kami dapati kebanyakan mereka adalah orang-orang yang benar-benar fasik.'),
	(1057, 7, 103, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَظَلَمُوْا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ  ', 'tsumma ba\'atsn<u>aa</u> min ba\'dihim muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> il<u>aa</u> fir\'awna wamala-ihi fa<u>zh</u>alamuu bih<u>aa</u> fa<strong>u</strong>nu<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmufsidiin<strong>a</strong>', 'Setelah mereka, kemudian Kami utus Musa dengan membawa bukti-bukti Kami kepada Fir‘aun dan pemuka-pemuka kaumnya, lalu mereka mengingkari bukti-bukti itu. Maka perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan.'),
	(1058, 7, 104, 'وَقَالَ مُوْسٰى يٰفِرْعَوْنُ اِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَۙ', 'waq<u>aa</u>la muus<u>aa</u> y<u>aa</u> fir\'awnu innii rasuulun min rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan Musa berkata, “Wahai Fir‘aun! Sungguh, aku adalah seorang utusan dari Tuhan seluruh alam,'),
	(1059, 7, 105, 'حَقِيْقٌ عَلٰٓى اَنْ لَّآ اَقُوْلَ عَلَى اللّٰهِ اِلَّا الْحَقَّۗ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِّنْ رَّبِّكُمْ فَاَرْسِلْ مَعِيَ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', '<u>h</u>aqiiqun \'al<u>aa</u> an l<u>aa</u> aquula \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> <strong>a</strong>l<u>h</u>aqqa qad ji/tukum bibayyinatin min rabbikum fa-arsil ma\'iya banii isr<u>aa</u>-iil<strong>a</strong>', 'aku wajib mengatakan yang sebenarnya tentang Allah. Sungguh, aku datang kepadamu dengan membawa bukti yang nyata dari Tuhanmu, maka lepaskanlah Bani Israil (pergi) bersamaku.”'),
	(1060, 7, 106, 'قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'q<u>aa</u>la in kunta ji/ta bi-<u>aa</u>yatin fa/ti bih<u>aa</u> in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Dia (Fir‘aun) menjawab, “Jika benar engkau membawa sesuatu bukti, maka tunjukkanlah, kalau kamu termasuk orang-orang yang benar.”'),
	(1061, 7, 107, 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۖ  ', 'fa-alq<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya tsu\'b<u>aa</u>nun mubiin<strong>un</strong>', 'Lalu (Musa) melemparkan tongkatnya, tiba-tiba tongkat itu menjadi ular besar yang sebenarnya.'),
	(1062, 7, 108, 'وَّنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ ࣖ', 'wanaza\'a yadahu fa-i<u>dzaa</u> hiya bay<u>daa</u>u li<strong>l</strong>nn<u>aats</u>iriin<strong>a</strong>', 'Dan dia mengeluarkan tangannya, tiba-tiba tangan itu menjadi putih (bercahaya) bagi orang-orang yang melihatnya.'),
	(1063, 7, 109, 'قَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌۙ  ', 'q<u>aa</u>la <strong>a</strong>lmalau min qawmi fir\'awna inna h<u>aadzaa</u> las<u>aah</u>irun \'aliim<strong>un</strong>', 'Pemuka-pemuka kaum Fir‘aun berkata, “Orang ini benar-benar pesihir yang pandai,'),
	(1064, 7, 110, 'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ ۚ فَمَاذَا تَأْمُرُوْنَ', 'yuriidu an yukhrijakum min ar<u>dh</u>ikum fam<u>aatsaa</u> ta/muruun<strong>a</strong>', 'yang hendak mengusir kamu dari negerimu.” (Fir‘aun berkata), “Maka apa saran kamu?”'),
	(1065, 7, 111, 'قَالُوْآ اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَۙ ', 'q<u>aa</u>luu arjih wa-akh<u>aa</u>hu wa-arsil fii <strong>a</strong>lmad<u>aa</u>-ini <u>haa</u>syiriin<strong>a</strong>', '(Pemuka-pemuka) itu menjawab, “Tahanlah (untuk sementara) dia dan saudaranya dan utuslah ke kota-kota beberapa orang untuk mengumpulkan (para pesihir),'),
	(1066, 7, 112, 'يَأْتُوْكَ بِكُلِّ سٰحِرٍ عَلِيْمٍ', 'ya/tuuka bikulli s<u>aah</u>irin \'aliim<strong>in</strong>', 'agar mereka membawa semua pesihir yang pandai kepadamu.”'),
	(1067, 7, 113, 'وَجَاۤءَ السَّحَرَةُ فِرْعَوْنَ قَالُوْٓا اِنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ', 'waj<u>aa</u>-a <strong>al</strong>ssa<u>h</u>aratu fir\'awna q<u>aa</u>luu inna lan<u>aa</u> la-ajran in kunn<u>aa</u> na<u>h</u>nu <strong>a</strong>lgh<u>aa</u>libiin<strong>a</strong>', 'Dan para pesihir datang kepada Fir‘aun. Mereka berkata, “(Apakah) kami akan mendapat imbalan, jika kami menang?”'),
	(1068, 7, 114, 'قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّبِيْنَ', 'q<u>aa</u>la na\'am wa-innakum lamina <strong>a</strong>lmuqarrabiin<strong>a</strong>', 'Dia (Fir‘aun) menjawab, “Ya, bahkan kamu pasti termasuk orang-orang yang dekat (kepadaku).”'),
	(1069, 7, 115, 'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ نَحْنُ الْمُلْقِيْنَ', 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> imm<u>aa</u> an tulqiya wa-imm<u>aa</u> an nakuuna na<u>h</u>nu <strong>a</strong>lmulqiin<strong>a</strong>', 'Mereka (para pesihir) berkata, “Wahai Musa! Engkaukah yang akan melemparkan lebih dahulu, atau kami yang melemparkan?”'),
	(1070, 7, 116, 'قَالَ اَلْقُوْاۚ فَلَمَّآ اَلْقَوْا سَحَرُوْٓا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوْهُمْ وَجَاۤءُوْ بِسِحْرٍ عَظِيْمٍ', 'q<u>aa</u>la <strong>a</strong>lquu falamm<u>aa</u> <strong>a</strong>lqaw sa<u>h</u>aruu a\'yuna <strong>al</strong>nn<u>aa</u>si wa<strong>i</strong>starhabuuhum waj<u>aa</u>uu bisi<u>h</u>rin \'a<u>zh</u>iim<strong>in</strong>', 'Dia (Musa) menjawab, “Lemparkanlah (lebih dahulu)!” Maka setelah mereka melemparkan, mereka menyihir mata orang banyak dan menjadikan orang banyak itu takut, karena mereka memperlihatkan sihir yang hebat (menakjubkan).'),
	(1071, 7, 117, '۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَۚ ', 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> an <strong>a</strong>lqi \'a<u>shaa</u>ka fa-i<u>dzaa</u> hiya talqafu m<u>aa</u> ya/fikuun<strong>a</strong>', 'Dan Kami wahyukan kepada Musa, “Lemparkanlah tongkatmu!” Maka tiba-tiba ia menelan (habis) segala kepalsuan mereka.'),
	(1072, 7, 118, 'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوْا يَعْمَلُوْنَۚ ', 'fawaqa\'a <strong>a</strong>l<u>h</u>aqqu waba<u>th</u>ala m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Maka terbuktilah kebenaran, dan segala yang mereka kerjakan jadi sia-sia.'),
	(1073, 7, 119, 'فَغُلِبُوْا هُنَالِكَ وَانْقَلَبُوْا صٰغِرِيْنَۚ ', 'faghulibuu hun<u>aa</u>lika wa<strong>i</strong>nqalabuu <u>shaa</u>ghiriin<strong>a</strong>', 'Maka mereka dikalahkan di tempat itu dan jadilah mereka orang-orang yang hina.'),
	(1074, 7, 120, 'وَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَۙ ', 'waulqiya <strong>al</strong>ssa<u>h</u>aratu s<u>aa</u>jidiin<strong>a</strong>', 'Dan para pesihir itu serta merta menjatuhkan diri dengan bersujud.'),
	(1075, 7, 121, 'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَۙ ', 'q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Mereka berkata, “Kami beriman kepada Tuhan seluruh alam,'),
	(1076, 7, 122, 'رَبِّ مُوْسٰى وَهٰرُوْنَ', 'rabbi muus<u>aa</u> wah<u>aa</u>ruun<strong>a</strong>', '(yaitu) Tuhannya Musa dan Harun.”'),
	(1077, 7, 123, 'قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِهٖ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَّكَرْتُمُوْهُ فِى الْمَدِيْنَةِ لِتُخْرِجُوْا مِنْهَآ اَهْلَهَاۚ فَسَوْفَ تَعْلَمُوْنَ', 'q<u>aa</u>la fir\'awnu <u>aa</u>mantum bihi qabla an <u>aadz</u>ana lakum inna h<u>aadzaa</u> lamakrun makartumuuhu fii <strong>a</strong>lmadiinati litukhrijuu minh<u>aa</u> ahlah<u>aa</u> fasawfa ta\'lamuun<strong>a</strong>', 'Fir‘aun berkata, “Mengapa kamu beriman kepadanya sebelum aku memberi izin kepadamu? Sesungguhnya ini benar-benar tipu muslihat yang telah kamu rencanakan di kota ini, untuk mengusir penduduknya. Kelak kamu akan mengetahui (akibat perbuatanmu ini).'),
	(1078, 7, 124, 'لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَ', 'lauqa<u>th</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin tsumma lau<u>sh</u>allibannakum ajma\'iin<strong>a</strong>', 'Pasti akan aku potong tangan dan kakimu dengan bersilang (tangan kanan dan kaki kiri atau sebaliknya), kemudian aku akan menyalib kamu semua.”'),
	(1079, 7, 125, 'قَالُوْٓا اِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَۙ ', 'q<u>aa</u>luu inn<u>aa</u> il<u>aa</u> rabbin<u>aa</u> munqalibuun<strong>a</strong>', 'Mereka (para pesihir) menjawab, “Sesungguhnya kami akan kembali kepada Tuhan kami.'),
	(1080, 7, 126, 'وَمَا تَنْقِمُ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاٰيٰتِ رَبِّنَا لَمَّا جَاۤءَتْنَا ۗرَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّتَوَفَّنَا مُسْلِمِيْنَ ࣖ ', 'wam<u>aa</u> tanqimu minn<u>aa</u> ill<u>aa</u> an <u>aa</u>mann<u>aa</u> bi-<u>aa</u>y<u>aa</u>ti rabbin<u>aa</u> lamm<u>aa</u> j<u>aa</u>-atn<u>aa</u> rabban<u>aa</u> afrigh \'alayn<u>aa</u> <u>sh</u>abran watawaffan<u>aa</u> muslimiin<strong>a</strong>', 'Dan engkau tidak melakukan balas dendam kepada kami, melainkan karena kami beriman kepada ayat-ayat Tuhan kami ketika ayat-ayat itu datang kepada kami.” (Mereka berdoa), “Ya Tuhan kami, limpahkanlah kesabaran kepada kami dan matikanlah kami dalam keadaan muslim (berserah diri kepada-Mu).”'),
	(1081, 7, 127, 'وَقَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوْسٰى وَقَوْمَهٗ لِيُفْسِدُوْا فِى الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۗ قَالَ سَنُقَتِّلُ اَبْنَاۤءَهُمْ وَنَسْتَحْيٖ نِسَاۤءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُوْنَ', 'waq<u>aa</u>la <strong>a</strong>lmalau min qawmi fir\'awna ata<u>dz</u>aru muus<u>aa</u> waqawmahu liyufsiduu fii <strong>a</strong>l-ar<u>dh</u>i waya<u>dz</u>araka wa<u>aa</u>lihataka q<u>aa</u>la sanuqattilu abn<u>aa</u>-ahum wanasta<u>h</u>yii nis<u>aa</u>-ahum wa-inn<u>aa</u> fawqahum q<u>aa</u>hiruun<strong>a</strong>', 'Dan para pemuka dari kaum Fir‘aun berkata, “Apakah engkau akan membiarkan Musa dan kaumnya untuk berbuat kerusakan di negeri ini (Mesir) dan meninggalkanmu dan tuhan-tuhanmu?” (Fir‘aun) menjawab, “Akan kita bunuh anak-anak laki-laki mereka dan kita biarkan hidup anak-anak perempuan mereka dan sesungguhnya kita berkuasa penuh atas mereka.”                   '),
	(1082, 7, 128, 'قَالَ مُوْسٰى لِقَوْمِهِ اسْتَعِيْنُوْا بِاللّٰهِ وَاصْبِرُوْاۚ اِنَّ الْاَرْضَ لِلّٰهِ ۗيُوْرِثُهَا مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَالْعَاقِبَةُ لِلْمُتَّقِيْنَ', 'q<u>aa</u>la muus<u>aa</u> liqawmihi ista\'iinuu bi<strong>al</strong>l<u>aa</u>hi wa<strong>i</strong><u>sh</u>biruu inna <strong>a</strong>l-ar<u>dh</u>a lill<u>aa</u>hi yuuritsuh<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wa<strong>a</strong>l\'<u>aa</u>qibatu lilmuttaqiin<strong>a</strong>', 'Musa berkata kepada kaumnya, “Mohonlah pertolongan kepada Allah dan bersabarlah. Sesungguhnya bumi (ini) milik Allah; diwariskan-Nya kepada siapa saja yang Dia kehendaki di antara hamba-hamba-Nya. Dan kesudahan (yang baik) adalah bagi orang-orang yang bertakwa.”'),
	(1083, 7, 129, 'قَالُوْٓا اُوْذِيْنَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْۢ بَعْدِ مَا جِئْتَنَا ۗقَالَ عَسٰى رَبُّكُمْ اَنْ يُّهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِى الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُوْنَ ࣖ ', 'q<u>aa</u>luu uu<u>dz</u>iin<u>aa</u> min qabli an ta/tiyan<u>aa</u> wamin ba\'di m<u>aa</u> ji/tan<u>aa</u> q<u>aa</u>la \'as<u>aa</u> rabbukum an yuhlika \'aduwwakum wayastakhlifakum fii <strong>a</strong>l-ar<u>dh</u>i fayan<u>zh</u>ura kayfa ta\'maluun<strong>a</strong>', 'Mereka (kaum Musa) berkata,  ”Kami telah ditindas (oleh Fir‘aun) sebelum engkau datang kepada kami dan setelah engkau datang.” (Musa) menjawab, “Mudah-mudahan Tuhanmu membinasakan musuhmu dan menjadikan kamu khalifah di bumi; maka Dia akan melihat bagaimana perbuatanmu.”'),
	(1084, 7, 130, 'وَلَقَدْ اَخَذْنَآ اٰلَ فِرْعَوْنَ بِالسِّنِيْنَ وَنَقْصٍ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَذَّكَّرُوْنَ', 'walaqad akha<u>dz</u>n<u>aa</u> <u>aa</u>la fir\'awna bi<strong>al</strong>ssiniina wanaq<u>sh</u>in mina <strong>al</strong>tstsamar<u>aa</u>ti la\'allahum ya<u>dzdz</u>akkaruun<strong>a</strong>', 'Dan sungguh, Kami telah menghukum Fir‘aun dan kaumnya dengan (mendatangkan musim kemarau) bertahun-tahun dan kekurangan buah-buahan, agar mereka mengambil pelajaran.'),
	(1085, 7, 131, 'فَاِذَا جَاۤءَتْهُمُ الْحَسَنَةُ قَالُوْا لَنَا هٰذِهٖ ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّطَّيَّرُوْا بِمُوْسٰى وَمَنْ مَّعَهٗۗ  اَلَآ اِنَّمَا طٰۤىِٕرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'fa-i<u>dzaa</u> j<u>aa</u>-at-humu <strong>a</strong>l<u>h</u>asanatu q<u>aa</u>luu lan<u>aa</u> h<u>aadz</u>ihi wa-in tu<u>sh</u>ibhum sayyi-atun ya<u>ththh</u>ayyaruu bimuus<u>aa</u> waman ma\'ahu <strong>a</strong>l<u>aa</u> innam<u>aa</u> <u>th</u><u>aa</u>-iruhum \'inda <strong>al</strong>l<u>aa</u>hi wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Kemudian apabila kebaikan (kemakmuran) datang kepada mereka, mereka berkata, “Ini adalah karena (usaha) kami.” Dan jika mereka ditimpa kesusahan, mereka lemparkan sebab kesialan itu kepada Musa dan pengikutnya. Ketahuilah, sesungguhnya nasib mereka di tangan Allah, namun kebanyakan mereka tidak mengetahui.'),
	(1086, 7, 132, 'وَقَالُوْا مَهْمَا تَأْتِنَا بِهٖ مِنْ اٰيَةٍ لِّتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ', 'waq<u>aa</u>luu mahm<u>aa</u> ta/tin<u>aa</u> bihi min <u>aa</u>yatin litas<u>h</u>aran<u>aa</u> bih<u>aa</u> fam<u>aa</u> na<u>h</u>nu laka bimu/miniin<strong>a</strong>', 'Dan mereka berkata (kepada Musa), “Bukti apa pun yang engkau bawa kepada kami untuk menyihir kami, kami tidak akan beriman kepadamu.”'),
	(1087, 7, 133, 'فَاَرْسَلْنَا عَلَيْهِمُ الطُّوْفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيٰتٍ مُّفَصَّلٰتٍۗ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ', 'fa-arsaln<u>aa</u> \'alayhimu <strong>al</strong><u>ththh</u>uuf<u>aa</u>na wa<strong>a</strong>ljar<u>aa</u>da wa<strong>a</strong>lqummala wa<strong>al</strong><u>dhdh</u>af<u>aa</u>di\'a wa<strong>al</strong>ddama <u>aa</u>y<u>aa</u>tin mufa<u>shsh</u>al<u>aa</u>tin fa<strong>i</strong>stakbaruu wak<u>aa</u>nuu qawman mujrimiin<strong>a</strong>', 'Maka Kami kirimkan kepada mereka topan, belalang, kutu, katak dan darah (air minum berubah menjadi darah) sebagai bukti-bukti yang jelas, tetapi mereka tetap menyombongkan diri dan mereka adalah kaum yang berdosa.'),
	(1088, 7, 134, 'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوْا يٰمُوْسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ  لَىِٕنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِيْٓ اِسْرَاۤءِيْلَ ۚ', 'walamm<u>aa</u> waqa\'a \'alayhimu <strong>al</strong>rrijzu q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> ud\'u lan<u>aa</u> rabbaka bim<u>aa</u> \'ahida \'indaka la-in kasyafta \'ann<u>aa</u> <strong>al</strong>rrijza lanu/minanna laka walanursilanna ma\'aka banii isr<u>aa</u>-iil<strong>a</strong>', 'Dan ketika mereka ditimpa azab (yang telah diterangkan itu) mereka pun berkata, “Wahai Musa! Mohonkanlah untuk kami kepada Tuhanmu sesuai dengan janji-Nya kepadamu. Jika engkau dapat menghilangkan azab itu dari kami, niscaya kami akan beriman kepadamu dan pasti akan kami biarkan Bani Israil pergi bersamamu.”'),
	(1089, 7, 135, 'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوْهُ اِذَا هُمْ يَنْكُثُوْنَ', 'falamm<u>aa</u> kasyafn<u>aa</u> \'anhumu <strong>al</strong>rrijza il<u>aa</u> ajalin hum b<u>aa</u>lighuuhu i<u>dzaa</u> hum yankutsuun<strong>a</strong>', 'Tetapi setelah Kami hilangkan azab itu dari mereka hingga batas waktu yang harus mereka penuhi ternyata mereka ingkar janji.'),
	(1090, 7, 136, 'فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ فِى الْيَمِّ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ ', 'fa<strong>i</strong>ntaqamn<u>aa</u> minhum fa-aghraqn<u>aa</u>hum fii <strong>a</strong>lyammi bi-annahum ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wak<u>aa</u>nuu \'anh<u>aa</u> gh<u>aa</u>filiin<strong>a</strong>', 'Maka Kami hukum sebagian di antara mereka, lalu Kami tenggelamkan mereka di laut karena mereka telah mendustakan ayat-ayat Kami dan melalaikan ayat-ayat Kami.'),
	(1091, 7, 137, 'وَاَوْرَثْنَا الْقَوْمَ الَّذِيْنَ كَانُوْا يُسْتَضْعَفُوْنَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَۙ بِمَا صَبَرُوْاۗ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَق', 'wa-awratsn<u>aa</u> <strong>a</strong>lqawma <strong>al</strong>la<u>dz</u>iina k<u>aa</u>nuu yusta<u>dh</u>\'afuuna masy<u>aa</u>riqa <strong>a</strong>l-ar<u>dh</u>i wamagh<u>aa</u>ribah<u>aa</u> <strong>al</strong>latii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> watammat kalimatu rabbika <strong>a</strong>l<u>h</u>usn<u>aa</u> \'al<u>aa</u> banii isr<u>aa</u>-iila bim<u>aa</u> <u>sh</u>abaruu wadammarn<u>aa</u> m<u>aa</u> k<u>aa</u>na ya<u>sh</u>na\'u fir\'awnu waqawmuhu wam<u>aa</u> k<u>aa</u>nuu ya\'risyuun<strong>a</strong>', 'Dan Kami wariskan kepada kaum yang tertindas itu, bumi bagian timur dan bagian baratnya yang telah Kami berkahi. Dan telah sempurnalah firman Tuhanmu yang baik itu (sebagai janji) untuk Bani Israil disebabkan kesabaran mereka. Dan Kami hancurkan apa yang telah dibuat Fir‘aun dan kaumnya dan apa yang telah mereka bangun.'),
	(1092, 7, 138, 'وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَّعْكُفُوْنَ عَلٰٓى اَصْنَامٍ لَّهُمْ ۚقَالُوْا يٰمُوْسَى اجْعَلْ لَّنَآ اِلٰهًا كَمَا لَهُمْ اٰلِهَةٌ  ۗقَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُوْنَ', 'waj<u>aa</u>wazn<u>aa</u> bibanii isr<u>aa</u>-iila <strong>a</strong>lba<u>h</u>ra fa-ataw \'al<u>aa</u> qawmin ya\'kufuuna \'al<u>aa</u> a<u>sh</u>n<u>aa</u>min lahum q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> ij\'al lan<u>aa</u> il<u>aa</u>han kam<u>aa</u> lahum <u>aa</u>lihatun q<u>aa</u>la innakum qawmun tajhaluun<strong>a</strong>', 'Dan Kami selamatkan Bani Israil menyeberangi laut itu (bagian utara dari Laut Merah). Ketika mereka sampai kepada suatu kaum yang tetap menyembah berhala, mereka (Bani Israil) berkata, “Wahai Musa! Buatlah untuk kami sebuah tuhan (berhala) sebagaimana mereka mempunyai beberapa tuhan (berhala).” (Musa) menjawab, “Sungguh, kamu orang-orang yang bodoh.”'),
	(1093, 7, 139, 'اِنَّ هٰٓؤُلَاۤءِ مُتَبَّرٌ مَّا هُمْ فِيْهِ وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ', 'inna h<u>aa</u>ul<u>aa</u>-i mutabbarun m<u>aa</u> hum fiihi wab<u>aath</u>ilun m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Sesungguhnya mereka akan dihancurkan (oleh kepercayaan) yang dianutnya dan akan sia-sia apa yang telah mereka kerjakan.'),
	(1094, 7, 140, 'قَالَ اَغَيْرَ اللّٰهِ اَبْغِيْكُمْ اِلٰهًا وَّهُوَ فَضَّلَكُمْ عَلَى الْعٰلَمِيْنَ', 'q<u>aa</u>la aghayra <strong>al</strong>l<u>aa</u>hi abghiikum il<u>aa</u>han wahuwa fa<u>dhdh</u>alakum \'al<u>aa</u> <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dia (Musa) berkata, “Pantaskah aku mencari tuhan untukmu selain Allah, padahal Dia yang telah melebihkan kamu atas segala umat (pada masa itu).”'),
	(1095, 7, 141, 'وَاِذْ اَنْجَيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِۚ يُقَتِّلُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ࣖ ', 'wa-i<u>dz</u> anjayn<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a <strong>a</strong>l\'a<u>dzaa</u>bi yuqattiluuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim<strong>un</strong>', 'Dan (ingatlah wahai Bani Israil) ketika Kami menyelamatkan kamu dari (Fir‘aun) dan kaumnya, yang menyiksa kamu dengan siksaan yang sangat berat, mereka membunuh anak-anak laki-lakimu dan membiarkan hidup anak-anak perempuanmu. Dan pada yang demikian itu merupakan cobaan yang besar dari Tuhanmu.'),
	(1096, 7, 142, '۞ وَوٰعَدْنَا مُوْسٰى ثَلٰثِيْنَ لَيْلَةً وَّاَتْمَمْنٰهَا بِعَشْرٍ فَتَمَّ مِيْقَاتُ رَبِّهٖٓ اَرْبَعِيْنَ لَيْلَةً ۚوَقَالَ مُوْسٰى لِاَخِيْهِ هٰرُوْنَ اخْلُفْنِيْ فِيْ قَوْمِيْ وَاَصْلِحْ وَلَا تَتَّبِعْ سَبِيْلَ الْمُفْسِدِيْنَ', 'waw<u>aa</u>\'adn<u>aa</u> muus<u>aa</u> tsal<u>aa</u>tsiina laylatan wa-atmamn<u>aa</u>h<u>aa</u> bi\'asyrin fatamma miiq<u>aa</u>tu rabbihi arba\'iina laylatan waq<u>aa</u>la muus<u>aa</u> li-akhiihi h<u>aa</u>ruuna ukhlufnii fii qawmii wa-a<u>sh</u>li<u>h</u> wal<u>aa</u> tattabi\' sabiila <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan Kami telah menjanjikan kepada Musa (memberikan Taurat) tiga puluh malam, dan Kami sempurnakan jumlah malam itu dengan sepuluh (malam lagi), maka sempurnalah waktu yang telah ditentukan Tuhannya empat puluh malam. Dan Musa berkata kepada saudaranya (yaitu) Harun, “Gantikanlah aku dalam (memimpin) kaumku, dan perbaikilah (dirimu dan kaummu),  dan janganlah engkau mengikuti  jalan orang-orang  yang berbuat kerusakan.”'),
	(1097, 7, 143, 'وَلَمَّا جَاۤءَ مُوْسٰى لِمِيْقَاتِنَا وَكَلَّمَهٗ رَبُّهٗۙ قَالَ رَبِّ اَرِنِيْٓ اَنْظُرْ اِلَيْكَۗ قَالَ لَنْ تَرٰىنِيْ وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهٗ فَسَوْفَ تَرٰىنِيْۚ فَلَمَّا تَجَلّٰى رَبُّهٗ لِلْجَبَلِ جَعَلَهٗ دَكًّا', 'walamm<u>aa</u> j<u>aa</u>-a muus<u>aa</u> limiiq<u>aa</u>tin<u>aa</u> wakallamahu rabbuhu q<u>aa</u>la rabbi arinii an<u>zh</u>ur ilayka q<u>aa</u>la lan tar<u>aa</u>nii wal<u>aa</u>kini un<u>zh</u>ur il<u>aa</u> <strong>a</strong>ljabali fa-ini istaqarra mak<u>aa</u>nahu fasawfa tar<u>aa</u>nii falamm<u>aa</u> tajall<u>aa</u> rabbuhu liljabali ja\'alahu dakkan wakharra muus<u>aa</u> <u>sh</u>a\'iqan falamm<u>aa</u> af<u>aa</u>qa q<u>aa</u>la sub<u>haa</u>naka tubtu ilayka wa-an<u>aa</u> awwalu <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan ketika Musa datang untuk (munajat) pada waktu yang telah Kami tentukan dan Tuhan telah berfirman (langsung) kepadanya, (Musa) berkata, “Ya Tuhanku, tampakkanlah (diri-Mu) kepadaku agar aku dapat melihat Engkau.” (Allah) berfirman, “Engkau tidak akan (sanggup) melihat-Ku, namun lihatlah ke gunung itu, jika ia tetap di tempatnya (sebagai sediakala) niscaya engkau dapat melihat-Ku.” Maka ketika Tuhannya menampakkan (keagungan-Nya) kepada gunung itu, gunung itu hancur luluh dan Musa pun jatuh pingsan. Setelah Musa sadar, dia berkata, “Mahasuci Engkau, aku bertobat kepada Engkau dan aku adalah orang yang pertama-tama beriman.”'),
	(1098, 7, 144, 'قَالَ يٰمُوْسٰٓى اِنِّى اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسٰلٰتِيْ وَبِكَلَامِيْ ۖفَخُذْ مَآ اٰتَيْتُكَ وَكُنْ مِّنَ الشّٰكِرِيْنَ', 'q<u>aa</u>la y<u>aa</u> muus<u>aa</u> innii i<u>sth</u>afaytuka \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si biris<u>aa</u>l<u>aa</u>tii wabikal<u>aa</u>mii fakhu<u>dz</u> m<u>aa</u> <u>aa</u>taytuka wakun mina <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', '(Allah) berfirman, “Wahai Musa! Sesungguhnya Aku memilih (melebihkan) engkau dari manusia yang lain (pada masamu) untuk membawa risalah-Ku dan firman-Ku, sebab itu berpegang-teguhlah kepada apa yang Aku berikan kepadamu dan hendaklah engkau termasuk orang-orang yang bersyukur.”'),
	(1099, 7, 145, 'وَكَتَبْنَا لَهٗ فِى الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَّوْعِظَةً وَّتَفْصِيْلًا لِّكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَّأْمُرْ قَوْمَكَ يَأْخُذُوْا بِاَحْسَنِهَا ۗسَاُورِيْكُمْ دَارَ الْفٰسِقِيْنَ', 'wakatabn<u>aa</u> lahu fii <strong>a</strong>l-alw<u>aah</u>i min kulli syay-in maw\'i<u>zh</u>atan wataf<u>sh</u>iilan likulli syay-in fakhu<u>dz</u>h<u>aa</u> biquwwatin wa/mur qawmaka ya/khu<u>dz</u>uu bi-a<u>h</u>sanih<u>aa</u> sauriikum d<u>aa</u>ra <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Dan telah Kami tuliskan untuk Musa pada lauh-lauh (Taurat) segala sesuatu sebagai pelajaran dan penjelasan untuk segala hal; maka (Kami berfirman), “Berpegangteguhlah kepadanya dan suruhlah kaummu berpegang kepadanya dengan sebaik-baiknya, Aku akan memperlihatkan kepadamu negeri orang-orang fasik.”   '),
	(1100, 7, 146, 'سَاَصْرِفُ عَنْ اٰيٰتِيَ الَّذِيْنَ يَتَكَبَّرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ وَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَاۚ وَاِنْ يَّرَوْا سَبِيْلَ الرُّشْدِ لَا يَتَّخِذُوْهُ سَبِيْلًاۚ وَاِنْ يَّرَوْا سَبِيْلَ الْغَيِّ يَتَّخِذُوْهُ سَبِي', 'sa-a<u>sh</u>rifu \'an <u>aa</u>y<u>aa</u>tiya <strong>al</strong>la<u>dz</u>iina yatakabbaruuna fii <strong>a</strong>l-ar<u>dh</u>i bighayri <strong>a</strong>l<u>h</u>aqqi wa-in yaraw kulla <u>aa</u>yatin l<u>aa</u> yu/minuu bih<u>aa</u> wa-in yaraw sabiila <strong>al</strong>rrusydi l<u>aa</u> yattakhi<u>dz</u>uuhu sabiilan wa-in yaraw sabiila <strong>a</strong>lghayyi yattakhi<u>dz</u>uuhu sabiilan <u>dzaa</u>lika bi-annahum ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wak<u>aa</u>nuu \'anh<u>aa</u> gh<u>aa</u>filiin<strong>a</strong>', 'Akan Aku palingkan dari tanda-tanda (kekuasaan-Ku) orang-orang yang menyombongkan diri di bumi tanpa alasan yang benar. Kalaupun mereka melihat setiap tanda (kekuasaan-Ku) mereka tetap tidak akan beriman kepadanya. Dan jika mereka melihat jalan yang membawa kepada petunjuk, mereka tidak (akan) menempuhnya, tetapi jika mereka melihat jalan kesesatan, mereka menempuhnya. Yang demikian adalah karena mereka mendustakan ayat-ayat Kami dan mereka selalu lengah terhadapnya.'),
	(1101, 7, 147, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waliq<u>aa</u>-i <strong>a</strong>l-<u>aa</u>khirati <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum hal yujzawna ill<u>aa</u> m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan orang-orang yang mendustakan tanda-tanda (kekuasaan) Kami dan (mendustakan) adanya pertemuan akhirat, sia-sialah amal mereka. Mereka diberi balasan sesuai dengan apa yang telah mereka kerjakan.'),
	(1102, 7, 148, 'وَاتَّخَذَ قَوْمُ مُوْسٰى مِنْۢ بَعْدِهٖ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌۗ  اَلَمْ يَرَوْا اَنَّهٗ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيْهِمْ سَبِيْلًاۘ اِتَّخَذُوْهُ وَكَانُوْا ظٰلِمِيْنَ', 'wa<strong>i</strong>ttakha<u>dz</u>a qawmu muus<u>aa</u> min ba\'dihi min <u>h</u>uliyyihim \'ijlan jasadan lahu khuw<u>aa</u>run alam yaraw annahu l<u>aa</u> yukallimuhum wal<u>aa </u>yahdiihim sabiilan ittakha<u>dz</u>uuhu wak<u>aa</u>nuu <u>zhaa</u>limiin<strong>a</strong>', 'Dan kaum Musa, setelah kepergian (Musa ke Gunung Sinai) mereka membuat patung anak sapi yang bertubuh dan dapat melenguh (bersuara) dari perhiasan (emas). Apakah mereka tidak mengetahui bahwa (patung) anak sapi itu tidak dapat berbicara dengan mereka dan tidak dapat (pula) menunjukkan jalan kepada mereka? Mereka menjadikannya (sebagai sembahan). Mereka adalah orang-orang yang zalim.'),
	(1103, 7, 149, 'وَلَمَّا سُقِطَ فِيْٓ اَيْدِيْهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّوْاۙ قَالُوْا لَىِٕنْ لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ', 'walamm<u>aa</u> suqi<u>th</u>a fii aydiihim wara-aw annahum qad <u>dh</u>alluu q<u>aa</u>luu la-in lam yar<u>h</u>amn<u>aa</u> rabbun<u>aa</u> wayaghfir lan<u>aa</u> lanakuunanna mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Dan setelah mereka menyesali perbuatannya dan mengetahui bahwa telah sesat, mereka pun berkata, “Sungguh, jika Tuhan kami tidak memberi rahmat kepada kami dan tidak mengampuni kami, pastilah kami menjadi orang-orang yang rugi.”'),
	(1104, 7, 150, 'وَلَمَّا رَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًاۙ قَالَ بِئْسَمَا خَلَفْتُمُوْنِيْ مِنْۢ بَعْدِيْۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخِيْهِ يَجُرُّهٗٓ اِلَيْهِ ۗقَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُ', 'walamm<u>aa</u> raja\'a muus<u>aa</u> il<u>aa</u> qawmihi gha<u>dh</u>b<u>aa</u>na asifan q<u>aa</u>la bi/sam<u>aa</u> khalaftumuunii min ba\'dii a\'ajiltum amra rabbikum wa-alq<u>aa </u>al-alw<u>aah</u>a wa-akha<u>dz</u>a bira/si akhiihi yajurruhu ilayhi q<u>aa</u>la ibna umma inna <strong>a</strong>lqawma ista<u>dh</u>\'afuunii wak<u>aa</u>duu yaqtuluunanii fal<u>aa</u> tusymit biya <strong>a</strong>l-a\'d<u>aa</u>-a wal<u>aa</u> taj\'alnii ma\'a <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan ketika Musa telah kembali kepada kaumnya, dengan marah dan sedih hati dia berkata, “Alangkah buruknya perbuatan yang kamu kerjakan selama kepergianku! Apakah kamu hendak mendahului janji Tuhanmu?” Musa pun melemparkan lauh-lauh (Taurat) itu dan memegang kepala saudaranya (Harun) sambil menarik ke arahnya. (Harun) berkata, “Wahai anak ibuku! Kaum ini telah menganggapku lemah dan hampir saja mereka membunuhku, sebab itu janganlah engkau menjadikan musuh-musuh menyoraki melihat kemalanganku, dan janganlah engkau jadikan aku sebagai orang-orang yang zalim.”'),
	(1105, 7, 151, 'قَالَ رَبِّ اغْفِرْ لِيْ وَلِاَخِيْ وَاَدْخِلْنَا فِيْ رَحْمَتِكَ ۖوَاَنْتَ اَرْحَمُ الرّٰحِمِيْنَ ࣖ ', 'q<u>aa</u>la rabbi ighfir lii wali-akhii wa-adkhiln<u>aa</u> fii ra<u>h</u>matika wa-anta ar<u>h</u>amu <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Dia (Musa) berdoa, “Ya Tuhanku, ampunilah aku dan saudaraku dan masukkanlah kami ke dalam rahmat Engkau, dan Engkau adalah Maha Penyayang dari semua penyayang.”'),
	(1106, 7, 152, 'اِنَّ الَّذِيْنَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِّنْ رَّبِّهِمْ وَذِلَّةٌ فِى الْحَيٰوةِ الدُّنْيَاۗ وَكَذٰلِكَ نَجْزِى الْمُفْتَرِيْنَ', 'inna <strong>al</strong>la<u>dz</u>iina ittakha<u>dz</u>uu <strong>a</strong>l\'ijla sayan<u>aa</u>luhum gha<u>dh</u>abun min rabbihim wa<u>dz</u>illatun fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> waka<u>dzaa</u>lika najzii <strong>a</strong>lmuftariin<strong>a</strong>', 'Sesungguhnya orang-orang yang menjadikan (patung) anak sapi (sebagai sembahannya), kelak akan menerima kemurkaan dari Tuhan mereka dan kehinaan dalam kehidupan di dunia. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat kebohongan.'),
	(1107, 7, 153, 'وَالَّذِيْنَ عَمِلُوا السَّيِّاٰتِ ثُمَّ تَابُوْا مِنْۢ بَعْدِهَا وَاٰمَنُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ', 'wa<strong>a</strong>lla<u>dz</u>iina \'amiluu <strong>al</strong>ssayyi-<u>aa</u>ti tsumma t<u>aa</u>buu min ba\'dih<u>aa</u> wa<u>aa</u>manuu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan orang-orang yang telah mengerjakan kejahatan, kemudian bertobat dan beriman, niscaya setelah itu Tuhanmu Maha Pengampun, Maha Penyayang.'),
	(1108, 7, 154, 'وَلَمَّا سَكَتَ عَنْ مُّوْسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۖ وَفِيْ نُسْخَتِهَا هُدًى وَّرَحْمَةٌ لِّلَّذِيْنَ هُمْ لِرَبِّهِمْ يَرْهَبُوْنَ', 'walamm<u>aa</u> sakata \'an muus<u>aa</u> <strong>a</strong>lgha<u>dh</u>abu akha<u>dz</u>a <strong>a</strong>l-alw<u>aah</u>a wafii nuskhatih<u>aa</u> hudan wara<u>h</u>matun lilla<u>dz</u>iina hum lirabbihim yarhabuun<strong>a</strong>', 'Dan setelah amarah Musa mereda, diambilnya (kembali) lauh-lauh (Taurat) itu; di dalam tulisannya terdapat petunjuk dan rahmat bagi orang-orang yang takut kepada Tuhannya.'),
	(1109, 7, 155, 'وَاخْتَارَ مُوْسٰى قَوْمَهٗ سَبْعِيْنَ رَجُلًا لِّمِيْقَاتِنَا ۚفَلَمَّآ اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِّنْ قَبْلُ وَاِيَّايَۗ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاۤءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۗ تُضِلُّ بِهَا ', 'wa<strong>i</strong>kht<u>aa</u>ra muus<u>aa</u> qawmahu sab\'iina rajulan limiiq<u>aa</u>tin<u>aa</u> falamm<u>aa</u> akha<u>dz</u>at-humu <strong>al</strong>rrajfatu q<u>aa</u>la rabbi law syi/ta ahlaktahum min qablu wa-iyy<u>aa</u>ya atuhlikun<u>aa</u> bim<u>aa</u> fa\'ala <strong>al</strong>ssufah<u>aa</u>u minn<u>aa</u> in hiya ill<u>aa</u> fitnatuka tu<u>dh</u>illu bih<u>aa</u> man tasy<u>aa</u>u watahdii man tasy<u>aa</u>u anta waliyyun<u>aa</u> fa<strong>i</strong>ghfir lan<u>aa</u> wa<strong>i</strong>r<u>h</u>amn<u>aa</u> wa-anta khayru <strong>a</strong>lgh<u>aa</u>firiin<strong>a</strong>', 'Dan Musa memilih tujuh puluh orang dari kaumnya untuk (memohon tobat kepada Kami) pada waktu yang telah Kami tentukan. Ketika mereka ditimpa gempa bumi, Musa berkata, “Ya Tuhanku, jika Engkau kehendaki, tentulah Engkau binasakan mereka dan aku sebelum ini. Apakah Engkau akan membinasakan kami karena perbuatan orang-orang yang kurang berakal di antara kami? Itu hanyalah cobaan dari-Mu, Engkau sesatkan dengan cobaan itu siapa yang Engkau kehendaki dan Engkau beri petunjuk kepada siapa yang Engkau kehendaki. Engkaulah pemimpin kami, maka ampunilah kami dan berilah kami rahmat. Engkaulah pemberi ampun yang terbaik.”'),
	(1110, 7, 156, '۞ وَاكْتُبْ لَنَا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ اِنَّا هُدْنَآ اِلَيْكَۗ قَالَ عَذَابِيْٓ اُصِيْبُ بِهٖ مَنْ اَشَاۤءُۚ وَرَحْمَتِيْ وَسِعَتْ كُلَّ شَيْءٍۗ فَسَاَكْتُبُهَا لِلَّذِيْنَ يَتَّقُوْنَ وَيُؤْتُوْنَ الزَّكٰوةَ وَالَّذِيْنَ ه', 'wa<strong>u</strong>ktub lan<u>aa</u> fii h<u>aadz</u>ihi <strong>al</strong>dduny<u>aa</u> <u>h</u>asanatan wafii <strong>a</strong>l-<u>aa</u>khirati inn<u>aa</u> hudn<u>aa</u> ilayka q<u>aa</u>la \'a<u>dzaa</u>bii u<u>sh</u>iibu bihi man asy<u>aa</u>u wara<u>h</u>matii wasi\'at kulla syay-in fasa-aktubuh<u>aa</u> lilla<u>dz</u>iina yattaquuna wayu/tuuna <strong>al</strong>zzak<u>aa</u>ta wa<strong>a</strong>lla<u>dz</u>iina hum bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yu/minuun<strong>a</strong>', 'Dan tetapkanlah untuk kami kebaikan di dunia ini dan di akhirat. Sungguh, kami kembali (bertobat) kepada Engkau. (Allah) berfirman, “Siksa-Ku akan Aku timpakan kepada siapa yang Aku kehendaki dan rahmat-Ku meliputi segala sesuatu. Maka akan Aku tetapkan rahmat-Ku bagi orang-orang yang bertakwa, yang menunaikan zakat dan orang-orang yang beriman kepada ayat-ayat Kami.”'),
	(1111, 7, 157, 'اَلَّذِيْنَ يَتَّبِعُوْنَ الرَّسُوْلَ النَّبِيَّ الْاُمِّيَّ الَّذِيْ يَجِدُوْنَهٗ مَكْتُوْبًا عِنْدَهُمْ فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ يَأْمُرُهُمْ بِالْمَعْرُوْفِ وَيَنْهٰىهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبٰتِ وَيُحَرِّمُ عَلَيْهِمُ الْ', '<strong>al</strong>la<u>dz</u>iina yattabi\'uuna <strong>al</strong>rrasuula <strong>al</strong>nnabiyya <strong>a</strong>l-ummiyya <strong>al</strong>la<u>dz</u>ii yajiduunahu maktuuban \'indahum fii <strong>al</strong>ttawr<u>aa</u>ti wa<strong>a</strong>l-injiili ya/muruhum bi<strong>a</strong>lma\'ruufi wayanh<u>aa</u>hum \'ani <strong>a</strong>lmunkari wayu<u>h</u>illu lahumu <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti wayu<u>h</u>arrimu \'alayhimu <strong>a</strong>lkhab<u>aa</u>-itsa waya<u>dh</u>a\'u \'anhum i<u>sh</u>rahum wa<strong>a</strong>l-aghl<u>aa</u>la <strong>al</strong>latii k<u>aa</u>nat \'alayhim fa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu bihi wa\'azzaruuhu wana<u>sh</u>aruuhu wa<strong>i</strong>ttaba\'uu <strong>al</strong>nnuura <strong>al</strong>la<u>dz</u>ii unzila ma\'ahu ul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', '(Yaitu) orang-orang yang mengikuti Rasul, Nabi yang ummi (tidak bisa baca tulis) yang (namanya) mereka dapati tertulis di dalam Taurat dan Injil yang ada pada mereka, yang menyuruh mereka berbuat yang makruf dan mencegah dari yang mungkar, dan yang menghalalkan segala yang baik bagi mereka dan mengharamkan segala yang buruk bagi mereka, dan membebaskan beban-beban dan belenggu-belenggu yang ada pada mereka. Adapun orang-orang yang beriman kepadanya, memuliakannya, menolongnya dan mengikuti cahaya yang terang yang diturunkan kepadanya (Al-Qur\'an), mereka itulah orang-orang beruntung.'),
	(1112, 7, 158, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ جَمِيْعًا ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ  لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُۖ فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهِ النَّبِيِّ الْاُمِّيِّ الَّذِيْ يُؤْمِنُ بِاللّٰهِ وَكَلِ', 'qul y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su innii rasuulu <strong>al</strong>l<u>aa</u>hi ilaykum jamii\'an <strong>al</strong>la<u>dz</u>ii lahu mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa yu<u>h</u>yii wayumiitu fa<u>aa</u>minuu bi<strong>al</strong>l<u>aa</u>hi warasuulihi <strong>al</strong>nnabiyyi <strong>a</strong>l-ummiyyi <strong>al</strong>la<u>dz</u>ii yu/minu bi<strong>al</strong>l<u>aa</u>hi wakalim<u>aa</u>tihi wa<strong>i</strong>ttabi\'uuhu la\'allakum tahtaduun<strong>a</strong>', 'Katakanlah (Muhammad), “Wahai manusia! Sesungguhnya aku ini utusan Allah bagi kamu semua, Yang memiliki kerajaan langit dan bumi; tidak ada tuhan (yang berhak disembah) selain Dia, Yang menghidupkan dan mematikan, maka berimanlah kamu kepada Allah dan Rasul-Nya, (yaitu) Nabi yang ummi yang beriman kepada Allah dan kepada kalimat-kalimat-Nya (kitab-kitab-Nya). Ikutilah dia, agar kamu mendapat petunjuk.”          '),
	(1113, 7, 159, 'وَمِنْ قَوْمِ مُوْسٰٓى اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ', 'wamin qawmi muus<u>aa</u> ummatun yahduuna bi<strong>a</strong>l<u>h</u>aqqi wabihi ya\'diluun<strong>a</strong>', 'Dan di antara kaum Musa itu terdapat suatu umat yang memberi petunjuk (kepada manusia) dengan (dasar) kebenaran dan dengan itu (pula) mereka berlaku adil menjalankan keadilan.'),
	(1114, 7, 160, 'وَقَطَّعْنٰهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطًا اُمَمًاۗ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اِذِ اسْتَسْقٰىهُ قَوْمُهٗٓ اَنِ اضْرِبْ بِّعَصَاكَ الْحَجَرَۚ فَانْۢبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًاۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْۗ وَظَلَّلْنَا عَلَ', 'waqa<u>ththh</u>a\'n<u>aa</u>humu itsnatay \'asyrata asb<u>aath</u>an umaman wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> i<u>dz</u>i istasq<u>aa</u>hu qawmuhu ani i<u>dh</u>rib bi\'a<u>shaa</u>ka <strong>a</strong>l<u>h</u>ajara fa<strong>i</strong>nbajasat minhu itsnat<u>aa</u> \'asyrata \'aynan qad \'alima kullu un<u>aa</u>sin masyrabahum wa<u>zh</u>allaln<u>aa</u> \'alayhimu <strong>a</strong>lgham<u>aa</u>ma wa-anzaln<u>aa</u> \'alayhimu <strong>a</strong>lmanna wa<strong>al</strong>ssalw<u>aa</u> kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wam<u>aa</u> <u>zh</u>alamuun<u>aa</u> wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Dan Kami membagi mereka menjadi dua belas suku yang masing-masing berjumlah besar, dan Kami wahyukan kepada Musa ketika kaumnya meminta air kepadanya, “Pukullah batu itu dengan tongkatmu!” Maka memancarlah dari (batu) itu dua belas mata air. Setiap suku telah mengetahui tempat minumnya masing-masing. Dan Kami naungi mereka dengan awan dan Kami turunkan kepada mereka mann dan salwa. (Kami berfirman), “Makanlah yang baik-baik dari rezeki yang telah Kami berikan kepadamu.” Mereka tidak menzalimi Kami, tetapi merekalah yang selalu menzalimi  dirinya sendiri.'),
	(1115, 7, 161, 'وَاِذْ قِيْلَ لَهُمُ اسْكُنُوْا هٰذِهِ الْقَرْيَةَ وَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ وَقُوْلُوْا حِطَّةٌ وَّادْخُلُوا الْبَابَ سُجَّدًا نَّغْفِرْ لَكُمْ خَطِيْۤـٰٔتِكُمْۗ سَنَزِيْدُ الْمُحْسِنِيْنَ', 'wa-i<u>dz</u> qiila lahumu uskunuu h<u>aadz</u>ihi <strong>a</strong>lqaryata wakuluu minh<u>aa</u> <u>h</u>aytsu syi/tum waquuluu <u>h</u>i<u>ththh</u>atun wa<strong>u</strong>dkhuluu <strong>a</strong>lb<u>aa</u>ba sujjadan naghfir lakum kha<u>th</u>ii-<u>aa</u>tikum sanaziidu <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan (ingatlah), ketika dikatakan kepada mereka (Bani Israil), “Diamlah di negeri ini (Baitulmaqdis) dan makanlah dari (hasil bumi)nya di mana saja kamu kehendaki.” Dan katakanlah, “Bebaskanlah kami dari dosa kami dan masukilah pintu gerbangnya sambil membungkuk, niscaya Kami ampuni kesalahan-kesalahanmu.” Kelak akan Kami tambah (pahala) kepada orang-orang yang berbuat baik.'),
	(1116, 7, 162, 'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا مِنْهُمْ قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَظْلِمُوْنَ ࣖ ', 'fabaddala <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu minhum qawlan ghayra <strong>al</strong>la<u>dz</u>ii qiila lahum fa-arsaln<u>aa</u> \'alayhim rijzan mina <strong>al</strong>ssam<u>aa</u>-i bim<u>aa</u> k<u>aa</u>nuu ya<u>zh</u>limuun<strong>a</strong>', 'Maka orang-orang yang zalim di antara mereka mengganti (perkataan itu) dengan perkataan yang tidak dikatakan kepada mereka, maka Kami timpakan kepada mereka azab dari langit disebabkan kezaliman mereka.'),
	(1117, 7, 163, 'وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّتِيْ كَانَتْ حَاضِرَةَ الْبَحْرِۘ اِذْ يَعْدُوْنَ فِى السَّبْتِ اِذْ تَأْتِيْهِمْ حِيْتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَّيَوْمَ لَا يَسْبِتُوْنَۙ لَا تَأْتِيْهِمْ ۛ كَذٰلِكَ ۛنَبْلُوْهُمْ بِمَا كَانُوْا يَفْسُقُوْنَ', 'wa<strong>i</strong>s-alhum \'ani <strong>a</strong>lqaryati <strong>al</strong>latii k<u>aa</u>nat <u>had</u>irata <strong>a</strong>lba<u>h</u>ri i<u>dz</u> ya\'duuna fii <strong>al</strong>ssabti i<u>dz</u> ta/tiihim <u>h</u>iit<u>aa</u>nuhum yawma sabtihim syurra\'an wayawma l<u>aa</u> yasbituuna l<u>aa</u> ta/tiihim ka<u>dzaa</u>lika nabluuhum bim<u>aa</u> k<u>aa</u>nuu yafsuquun<strong>a</strong>', 'Dan tanyakanlah kepada Bani Israil tentang negeri yang terletak di dekat laut ketika mereka melanggar aturan pada hari Sabtu, (yaitu) ketika datang kepada mereka ikan-ikan (yang berada di sekitar) mereka terapung-apung di permukaan air, padahal pada hari-hari yang bukan Sabat ikan-ikan itu tidak datang kepada mereka. Demikianlah Kami menguji mereka disebabkan mereka berlaku fasik.'),
	(1118, 7, 164, 'وَاِذْ قَالَتْ اُمَّةٌ مِّنْهُمْ لِمَ تَعِظُوْنَ قَوْمًاۙ ۨاللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيْدًاۗ قَالُوْا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُوْنَ', 'wa-i<u>dz</u> q<u>aa</u>lat ummatun minhum lima ta\'i<u>zh</u>uuna qawman <strong>al</strong>l<u>aa</u>hu muhlikuhum aw mu\'a<u>dzdz</u>ibuhum \'a<u>dzaa</u>ban syadiidan q<u>aa</u>luu ma\'<u>dz</u>iratan il<u>aa</u> rabbikum wala\'allahum yattaquun<strong>a</strong>', 'Dan (ingatlah) ketika suatu umat di antara mereka berkata, “Mengapa kamu menasihati kaum yang akan dibinasakan atau diazab Allah dengan azab yang sangat keras?” Mereka menjawab, “Agar kami mempunyai alasan (lepas tanggung jawab) kepada Tuhanmu, dan agar mereka bertakwa.”'),
	(1119, 7, 165, 'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖٓ اَنْجَيْنَا الَّذِيْنَ يَنْهَوْنَ عَنِ السُّوْۤءِ وَاَخَذْنَا الَّذِيْنَ ظَلَمُوْا بِعَذَابٍۢ بَـِٔيْسٍۢ بِمَا كَانُوْا يَفْسُقُوْنَ', 'falamm<u>aa</u> nasuu m<u>aa</u> <u>dz</u>ukkiruu bihi anjayn<u>aa</u> <strong>al</strong>la<u>dz</u>iina yanhawna \'ani <strong>al</strong>ssuu-i wa-akha<u>dz</u>n<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu bi\'a<u>dzaa</u>bin ba-iisin bim<u>aa</u> k<u>aa</u>nuu yafsuquun<strong>a</strong>', 'Maka setelah mereka melupakan apa yang diperingatkan kepada mereka, Kami selamatkan orang-orang yang melarang orang berbuat jahat dan Kami timpakan kepada orang-orang yang zalim siksaan yang keras, disebabkan mereka selalu berbuat fasik.'),
	(1120, 7, 166, 'فَلَمَّا عَتَوْا عَنْ مَّا نُهُوْا عَنْهُ قُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ', 'falamm<u>aa</u> \'ataw \'an m<u>aa</u> nuhuu \'anhu quln<u>aa</u> lahum kuunuu qiradatan kh<u>aa</u>si-iin<strong>a</strong>', 'Maka setelah mereka bersikap sombong terhadap segala apa yang dilarang. Kami katakan kepada mereka, “Jadilah kamu kera yang hina.”'),
	(1121, 7, 167, 'وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَّسُوْمُهُمْ سُوْۤءَ الْعَذَابِۗ اِنَّ رَبَّكَ لَسَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ', 'wa-i<u>dz</u> ta-a<u>dzdz</u>ana rabbuka layab\'atsanna \'alayhim il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati man yasuumuhum suu-a <strong>a</strong>l\'a<u>dzaa</u>bi inna rabbaka lasarii\'u <strong>a</strong>l\'iq<u>aa</u>bi wa-innahu laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan (ingatlah), ketika Tuhanmu memberitahukan, bahwa sungguh, Dia akan mengirim orang-orang yang akan menimpakan azab yang seburuk-buruknya kepada mereka (orang Yahudi) sampai hari Kiamat. Sesungguhnya Tuhanmu sangat cepat siksa-Nya, dan sesungguhnya Dia Maha Pengampun, Maha Penyayang.'),
	(1122, 7, 168, 'وَقَطَّعْنٰهُمْ فِى الْاَرْضِ اُمَمًاۚ مِنْهُمُ الصّٰلِحُوْنَ وَمِنْهُمْ دُوْنَ ذٰلِكَ ۖوَبَلَوْنٰهُمْ بِالْحَسَنٰتِ وَالسَّيِّاٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'waqa<u>ththh</u>a\'n<u>aa</u>hum fii <strong>a</strong>l-ar<u>dh</u>i umaman minhumu <strong>al</strong><u>shshaa</u>li<u>h</u>uuna waminhum duuna <u>dzaa</u>lika wabalawn<u>aa</u>hum bi<strong>a</strong>l<u>h</u>asan<u>aa</u>ti wa<strong>al</strong>ssayyi-<u>aa</u>ti la\'allahum yarji\'uun<strong>a</strong>', 'Dan Kami pecahkan mereka di dunia ini menjadi beberapa golongan; di antaranya ada orang-orang yang saleh dan ada yang tidak demikian. Dan Kami uji mereka dengan (nikmat) yang baik-baik dan (bencana) yang buruk-buruk, agar mereka kembali (kepada kebenaran).'),
	(1123, 7, 169, 'فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ وَّرِثُوا الْكِتٰبَ يَأْخُذُوْنَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُوْلُوْنَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَّأْتِهِمْ عَرَضٌ مِّثْلُهٗ يَأْخُذُوْهُۗ  اَلَمْ يُؤْخَذْ عَلَيْهِمْ مِّيْثَاقُ الْكِتٰبِ اَنْ لَّا يَقُوْلُوْا عَلَى اللّٰ', 'fakhalafa min ba\'dihim khalfun waritsuu alkit<u>aa</u>ba ya/khu<u>dz</u>uuna \'ara<u>dh</u>a h<u>aadzaa</u> al-adn<u>aa</u> wayaquuluuna sayughfaru lan<u>aa</u> wa-in ya/tihim \'ara<u>dh</u>un mitsluhu ya/khu<u>dz</u>uuhu alam yu/kha<u>dz</u> \'alayhim miits<u>aa</u>qu alkit<u>aa</u>bi an l<u>aa</u> yaquuluu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> al<u>h</u>aqqa wadarasuu m<u>aa</u> fiihi wa<strong>al</strong>dd<u>aa</u>ru al-<u>aa</u>khiratu khayrun lilla<u>dz</u>iina yattaquuna afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Maka setelah mereka, datanglah generasi (yang jahat) yang mewarisi Taurat, yang mengambil harta benda dunia yang rendah ini. Lalu mereka berkata, “Kami akan diberi ampun.” Dan kelak jika harta benda dunia datang kepada mereka sebanyak itu (pula), niscaya mereka akan mengambilnya (juga). Bukankah mereka sudah terikat perjanjian dalam Kitab (Taurat) bahwa mereka tidak akan mengatakan terhadap Allah, kecuali yang benar, padahal mereka telah mempelajari apa yang tersebut di dalamnya? Negeri akhirat itu lebih baik bagi mereka yang bertakwa. Maka tidakkah kamu mengerti?'),
	(1124, 7, 170, 'وَالَّذِيْنَ يُمَسِّكُوْنَ بِالْكِتٰبِ وَاَقَامُوا الصَّلٰوةَۗ اِنَّا لَا نُضِيْعُ اَجْرَ الْمُصْلِحِيْنَ', 'wa<strong>a</strong>lla<u>dz</u>iina yumassikuuna bi<strong>a</strong>lkit<u>aa</u>bi wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta inn<u>aa</u> l<u>aa</u> nu<u>dh</u>ii\'u ajra <strong>a</strong>lmu<u>sh</u>li<u>h</u>iin<strong>a</strong>', 'Dan orang-orang yang berpegang teguh pada Kitab (Taurat) serta melaksanakan salat, (akan diberi pahala). Sungguh, Kami tidak akan menghilangkan pahala orang-orang saleh.'),
	(1125, 7, 171, '۞ وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهٗ ظُلَّةٌ وَّظَنُّوْٓا اَنَّهٗ وَاقِعٌۢ بِهِمْۚ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ ࣖ ', 'wa-i<u>dz</u> nataqn<u>aa</u> <strong>a</strong>ljabala fawqahum ka-annahu <u>zh</u>ullatun wa<u>zh</u>annuu annahu w<u>aa</u>qi\'un bihim khu<u>dz</u>uu m<u>aa</u> <u>aa</u>tayn<u>aa</u>kum biquwwatin wa<strong>u</strong><u>dz</u>kuruu m<u>aa</u> fiihi la\'allakum tattaquun<strong>a</strong>', 'Dan (ingatlah) ketika Kami mengangkat gunung ke atas mereka, seakan-akan (gunung) itu naungan awan dan mereka yakin bahwa (gunung) itu akan jatuh menimpa mereka. (Dan Kami firmankan kepada mereka), “Peganglah dengan teguh apa yang telah Kami berikan kepadamu, serta ingatlah selalu (amalkanlah) apa yang tersebut di dalamnya agar kamu menjadi orang-orang bertakwa.”'),
	(1126, 7, 172, 'وَاِذْ اَخَذَ رَبُّكَ مِنْۢ بَنِيْٓ اٰدَمَ مِنْ ظُهُوْرِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ  اَلَسْتُ بِرَبِّكُمْۗ قَالُوْا بَلٰىۛ شَهِدْنَا ۛاَنْ تَقُوْلُوْا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غٰفِلِيْنَۙ ', 'wa-i<u>dz</u> akha<u>dz</u>a rabbuka min banii <u>aa</u>dama min <u>zh</u>uhuurihim <u>dz</u>urriyyatahum wa-asyhadahum \'al<u>aa</u> anfusihim alastu birabbikum q<u>aa</u>luu bal<u>aa</u> syahidn<u>aa</u> an taquuluu yawma alqiy<u>aa</u>mati inn<u>aa</u> kunn<u>aa</u> \'an h<u>aadzaa</u> gh<u>aa</u>filiin<strong>a</strong>', 'Dan (ingatlah) ketika Tuhanmu mengeluarkan dari sulbi (tulang belakang) anak cucu Adam keturunan mereka dan Allah mengambil kesaksian terhadap roh mereka (seraya berfirman), “Bukankah Aku ini Tuhanmu?” Mereka menjawab, “Betul (Engkau Tuhan kami), kami bersaksi.” (Kami lakukan yang demikian itu) agar di hari Kiamat kamu tidak mengatakan, “Sesungguhnya ketika itu kami lengah terhadap ini.”'),
	(1127, 7, 173, 'اَوْ تَقُوْلُوْٓا اِنَّمَآ اَشْرَكَ اٰبَاۤؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِّنْۢ بَعْدِهِمْۚ  اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُوْنَ', 'aw taquuluu innam<u>aa</u> asyraka <u>aa</u>b<u>aa</u>un<u>aa</u> min qablu wakunn<u>aa</u> <u>dz</u>urriyyatan min ba\'dihim afatuhlikun<u>aa</u> bim<u>aa</u> fa\'ala <strong>a</strong>lmub<u>th</u>iluun<strong>a</strong>', 'Atau agar kamu tidak mengatakan, “Sesungguhnya nenek moyang kami telah mempersekutukan Tuhan sejak dahulu, sedang kami adalah keturunan yang (datang) setelah mereka. Maka apakah Engkau akan membinasakan kami karena perbuatan orang-orang (dahulu) yang sesat?”'),
	(1128, 7, 174, 'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلَعَلَّهُمْ يَرْجِعُوْنَ', 'waka<u>dzaa</u>lika nufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti wala\'allahum yarji\'uun<strong>a</strong>', 'Dan demikianlah Kami menjelaskan ayat-ayat itu, agar mereka kembali (kepada kebenaran).'),
	(1129, 7, 175, 'وَاتْلُ عَلَيْهِمْ نَبَاَ الَّذِيْٓ اٰتَيْنٰهُ اٰيٰتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطٰنُ فَكَانَ مِنَ الْغٰوِيْنَ', 'wa<strong>u</strong>tlu \'alayhim naba-a <strong>al</strong>la<u>dz</u>ii <u>aa</u>tayn<u>aa</u>hu <u>aa</u>y<u>aa</u>tin<u>aa</u> fa<strong>i</strong>nsalakha minh<u>aa</u> fa-atba\'ahu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu fak<u>aa</u>na mina <strong>a</strong>lgh<u>aa</u>wiin<strong>a</strong>', 'Dan bacakanlah (Muhammad) kepada mereka, berita orang yang telah Kami berikan ayat-ayat Kami kepadanya, kemudian dia melepaskan diri dari ayat-ayat itu, lalu dia diikuti oleh setan (sampai dia tergoda), maka jadilah dia termasuk orang yang sesat.'),
	(1130, 7, 176, 'وَلَوْ شِئْنَا لَرَفَعْنٰهُ بِهَا وَلٰكِنَّهٗٓ اَخْلَدَ اِلَى الْاَرْضِ وَاتَّبَعَ هَوٰىهُۚ فَمَثَلُهٗ كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۗ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاقْصُصِ الْقَ', 'walaw syi/n<u>aa</u> larafa\'n<u>aa</u>hu bih<u>aa</u> wal<u>aa</u>kinnahu akhlada il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i wa<strong>i</strong>ttaba\'a haw<u>aa</u>hu famatsaluhu kamatsali <strong>a</strong>lkalbi in ta<u>h</u>mil \'alayhi yalhats aw tatruk-hu yalhats <u>dzaa</u>lika matsalu <strong>a</strong>lqawmi <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fa<strong>u</strong>q<u>sh</u>u<u>sh</u>i <strong>a</strong>lqa<u>sh</u>a<u>sh</u>a la\'allahum yatafakkaruun<strong>a</strong>', 'Dan sekiranya Kami menghendaki niscaya Kami tinggikan (derajat)nya dengan (ayat-ayat) itu, tetapi dia cenderung kepada dunia dan mengikuti keinginannya (yang rendah), maka perumpamaannya seperti anjing, jika kamu menghalaunya dijulurkan lidahnya dan jika kamu membiarkannya dia menjulurkan lidahnya (juga). Demikianlah perumpamaan orang-orang yang mendustakan ayat-ayat Kami. Maka ceritakanlah kisah-kisah itu agar mereka berpikir.'),
	(1131, 7, 177, 'سَاۤءَ مَثَلًا ۨالْقَوْمُ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاَنْفُسَهُمْ كَانُوْا يَظْلِمُوْنَ', 's<u>aa</u>-a matsalan <strong>a</strong>lqawmu <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wa-anfusahum k<u>aa</u>nuu ya<u>zh</u>limuun<strong>a</strong>', 'Sangat buruk perumpamaan orang-orang yang mendustakan ayat-ayat Kami; mereka menzalimi diri sendiri.'),
	(1132, 7, 178, 'مَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِيْۚ وَمَنْ يُّضْلِلْ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'man yahdi <strong>al</strong>l<u>aa</u>hu fahuwa <strong>a</strong>lmuhtadii waman yu<u>dh</u>lil faul<u>aa</u>-ika humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', 'Barangsiapa diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa disesatkan Allah, maka merekalah orang-orang yang rugi.'),
	(1133, 7, 179, 'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيْرًا مِّنَ الْجِنِّ وَالْاِنْسِۖ  لَهُمْ قُلُوْبٌ لَّا يَفْقَهُوْنَ بِهَاۖ وَلَهُمْ اَعْيُنٌ لَّا يُبْصِرُوْنَ بِهَاۖ وَلَهُمْ اٰذَانٌ لَّا يَسْمَعُوْنَ بِهَاۗ اُولٰۤىِٕكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ  ۗ اُولٰۤىِٕكَ', 'walaqad <u>dz</u>ara/n<u>aa</u> lijahannama katsiiran mina <strong>a</strong>ljinni wa<strong>a</strong>l-insi lahum quluubun l<u>aa</u> yafqahuuna bih<u>aa</u> walahum a\'yunun l<u>aa</u> yub<u>sh</u>iruuna bih<u>aa</u> walahum <u>aatsaa</u>nun l<u>aa</u> yasma\'uuna bih<u>aa</u> ul<u>aa</u>-ika ka<strong>a</strong>l-an\'<u>aa</u>mi bal hum a<u>dh</u>allu ul<u>aa</u>-ika humu <strong>a</strong>lgh<u>aa</u>filuun<strong>a</strong>', 'Dan sungguh, akan Kami isi neraka Jahanam banyak dari kalangan jin dan manusia. Mereka memiliki hati, tetapi tidak dipergunakannya untuk memahami (ayat-ayat Allah) dan mereka memiliki mata (tetapi) tidak dipergunakannya untuk melihat (tanda-tanda kekuasaan Allah), dan mereka mempunyai telinga (tetapi) tidak dipergunakannya untuk mendengarkan (ayat-ayat Allah). Mereka seperti hewan ternak, bahkan lebih sesat lagi. Mereka itulah orang-orang yang lengah.'),
	(1134, 7, 180, 'وَلِلّٰهِ الْاَسْمَاۤءُ الْحُسْنٰى فَادْعُوْهُ بِهَاۖ وَذَرُوا الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اَسْمَاۤىِٕهٖۗ سَيُجْزَوْنَ مَا كَانُوْا يَعْمَلُوْنَ ۖ', 'walill<u>aa</u>hi <strong>a</strong>l-asm<u>aa</u>u <strong>a</strong>l<u>h</u>usn<u>aa</u> fa<strong>u</strong>d\'uuhu bih<u>aa</u> wa<u>dz</u>aruu <strong>al</strong>la<u>dz</u>iina yul<u>h</u>iduuna fii asm<u>aa</u>-ihi sayujzawna m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan Allah memiliki Asma\'ul-husna (nama-nama yang terbaik), maka bermohonlah kepada-Nya dengan menyebutnya Asma\'ul-husna itu dan tinggalkanlah orang-orang yang menyalahartikan nama-nama-Nya. Mereka kelak akan mendapat balasan terhadap apa yang telah mereka kerjakan.'),
	(1135, 7, 181, 'وَمِمَّنْ خَلَقْنَآ اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ ࣖ ', 'wamimman khalaqn<u>aa</u> ummatun yahduuna bi<strong>a</strong>l<u>h</u>aqqi wabihi ya\'diluun<strong>a</strong>', 'Dan di antara orang-orang yang telah Kami ciptakan ada umat yang memberi petunjuk dengan (dasar) kebenaran, dan dengan itu (pula) mereka berlaku adil.'),
	(1136, 7, 182, 'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَ', 'wa<strong>a</strong>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> sanastadrijuhum min <u>h</u>aytsu l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan orang-orang yang mendustakan ayat-ayat Kami, akan Kami biarkan mereka berangsur-angsur (ke arah kebinasaan), dengan cara yang tidak mereka ketahui.'),
	(1137, 7, 183, 'وَاُمْلِيْ لَهُمْ ۗاِنَّ كَيْدِيْ مَتِيْنٌ', 'waumlii lahum inna kaydii matiin<strong>un</strong>', 'Dan Aku akan memberikan tenggang waktu kepada mereka. Sungguh, rencana-Ku sangat teguh.'),
	(1138, 7, 184, 'اَوَلَمْ يَتَفَكَّرُوْا مَا بِصَاحِبِهِمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ مُّبِيْنٌ', 'awa lam yatafakkaruu m<u>aa</u> bi<u>shaah</u>ibihim min jinnatin in huwa ill<u>aa</u> na<u>dz</u>iirun mubiin<strong>un</strong>', 'Dan apakah mereka tidak merenungkan bahwa teman mereka (Muhammad) tidak gila. Dia (Muhammad) tidak lain hanyalah seorang pemberi peringatan yang jelas.'),
	(1139, 7, 185, 'اَوَلَمْ يَنْظُرُوْا فِيْ مَلَكُوْتِ السَّمٰوٰتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ وَّاَنْ عَسٰٓى اَنْ يَّكُوْنَ قَدِ اقْتَرَبَ اَجَلُهُمْۖ فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ', 'awalam yan<u>zh</u>uruu fii malakuuti <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu min syay-in wa-an \'as<u>aa</u> an yakuuna qadi iqtaraba ajaluhum fabi-ayyi <u>h</u>adiitsin ba\'dahu yu/minuun<strong>a</strong>', 'Dan apakah mereka tidak memperhatikan kerajaan langit dan bumi dan segala apa yang diciptakan Allah, dan kemungkinan telah dekatnya waktu (kebinasaan) mereka? Lalu berita mana lagi setelah ini yang akan mereka percayai?'),
	(1140, 7, 186, 'مَنْ يُّضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهٗ  ۖوَيَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'man yu<u>dh</u>lili <strong>al</strong>l<u>aa</u>hu fal<u>aa</u> h<u>aa</u>diya lahu waya<u>dz</u>aruhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun<strong>a</strong>', 'Barangsiapa dibiarkan sesat oleh Allah, maka tidak ada yang mampu memberi petunjuk. Allah membiarkannya terombang-ambing dalam kesesatan.'),
	(1141, 7, 187, 'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبِّيْۚ  لَا يُجَلِّيْهَا لِوَقْتِهَآ اِلَّا هُوَۘ ثَقُلَتْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ  لَا تَأْتِيْكُمْ اِلَّا بَغْتَةً ۗيَسْـَٔلُوْنَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۗ قُ', 'yas-aluunaka \'ani <strong>al</strong>ss<u>aa</u>\'ati ayy<u>aa</u>na murs<u>aa</u>h<u>aa</u> qul innam<u>aa</u> \'ilmuh<u>aa</u> \'inda rabbii l<u>aa</u> yujalliih<u>aa</u> liwaqtih<u>aa</u> ill<u>aa</u> huwa tsaqulat fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i l<u>aa</u> ta/tiikum ill<u>aa</u> baghtatan yas-aluunaka ka-annaka <u>h</u>afiyyun \'anh<u>aa</u> qul innam<u>aa</u> \'ilmuh<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Mereka menanyakan kepadamu (Muhammad) tentang Kiamat, “Kapan terjadi?” Katakanlah, “Sesungguhnya pengetahuan tentang Kiamat itu ada pada Tuhanku; tidak ada (seorang pun) yang dapat menjelaskan waktu terjadinya selain Dia. (Kiamat) itu sangat berat (huru-haranya bagi makhluk) yang di langit dan di bumi, tidak akan datang kepadamu kecuali secara tiba-tiba.” Mereka bertanya kepadamu seakan-akan engkau mengetahuinya. Katakanlah (Muhammad), “Sesungguhnya pengetahuan tentang (hari Kiamat) ada pada Allah, tetapi kebanyakan manusia tidak mengetahui.”'),
	(1142, 7, 188, 'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ نَفْعًا وَّلَا ضَرًّا اِلَّا مَا شَاۤءَ اللّٰهُ ۗوَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۛ وَمَا مَسَّنِيَ السُّوْۤءُ ۛاِنْ اَنَا۠ اِلَّا نَذِيْرٌ وَّبَشِيْرٌ لِّقَوْمٍ يُّؤْمِنُوْنَ ࣖ ', 'qul l<u>aa</u> amliku linafsii naf\'an wal<u>aa</u> <u>dh</u>arran ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu walaw kuntu a\'lamu <strong>a</strong>lghayba la<strong>i</strong>staktsartu mina <strong>a</strong>lkhayri wam<u>aa</u> massaniya <strong>al</strong>ssuu-u in an<u>aa</u> ill<u>aa</u> na<u>dz</u>iirun wabasyiirun liqawmin yu/minuun<strong>a</strong>', 'Katakanlah (Muhammad), “Aku tidak kuasa mendatangkan manfaat maupun menolak mudarat bagi diriku kecuali apa yang dikehendaki Allah. Sekiranya aku mengetahui yang gaib, niscaya aku membuat kebajikan sebanyak-banyaknya dan tidak akan ditimpa bahaya. Aku hanyalah pemberi peringatan, dan pembawa berita gembira bagi orang-orang yang beriman.”'),
	(1143, 7, 189, '۞ هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰىهَا حَمَلَتْ حَمْلًا خَفِيْفًا فَمَرَّتْ بِهٖ ۚفَلَمَّآ اَثْقَلَتْ دَّعَوَا اللّٰهَ رَبَّهُمَا لَىِٕنْ اٰتَيْتَنَا صَالِحًا لَّنَكُوْنَ', 'huwa <strong>al</strong>la<u>dz</u>ii khalaqakum min nafsin w<u>aah</u>idatin waja\'ala minh<u>aa</u> zawjah<u>aa</u> liyaskuna ilayh<u>aa</u> falamm<u>aa</u> taghasysy<u>aa</u>h<u>aa</u> <u>h</u>amalat <u>h</u>amlan khafiifan famarrat bihi falamm<u>aa</u> atsqalat da\'aw<u>aa</u> <strong>al</strong>l<u>aa</u>ha rabbahum<u>aa</u> la-in <u>aa</u>taytan<u>aa</u> <u>shaa</u>li<u>h</u>an lanakuunanna mina <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Dialah yang menciptakan kamu dari jiwa yang satu (Adam) dan daripadanya Dia menciptakan pasangannya, agar dia merasa senang kepadanya. Maka setelah dicampurinya, (istrinya) mengandung kandungan yang ringan, dan teruslah dia merasa ringan (beberapa waktu). Kemudian ketika dia merasa berat, keduanya (suami istri) bermohon kepada Allah, Tuhan Mereka (seraya berkata), “Jika Engkau memberi kami anak yang saleh, tentulah kami akan selalu bersyukur.”'),
	(1144, 7, 190, 'فَلَمَّآ اٰتٰىهُمَا صَالِحًا جَعَلَا لَهٗ شُرَكَاۤءَ فِيْمَآ اٰتٰىهُمَا ۚفَتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ', 'falamm<u>aa</u> <u>aa</u>t<u>aa</u>hum<u>aa</u> <u>shaa</u>li<u>h</u>an ja\'al<u>aa</u> lahu syurak<u>aa</u>-a fiim<u>aa</u> <u>aa</u>t<u>aa</u>hum<u>aa</u> fata\'<u>aa</u>l<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Maka setelah Dia memberi keduanya seorang anak yang saleh, mereka menjadikan sekutu bagi Allah terhadap anak yang telah dianugerahkan-Nya itu. Maka Mahatinggi Allah dari apa yang mereka persekutukan.'),
	(1145, 7, 191, 'اَيُشْرِكُوْنَ مَا لَا يَخْلُقُ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۖ ', 'ayusyrikuuna m<u>aa</u> l<u>aa</u> yakhluqu syay-an wahum yukhlaquun<strong>a</strong>', 'Mengapa mereka mempersekutukan (Allah dengan) sesuatu (berhala) yang tidak dapat menciptakan sesuatu apa pun? Padahal (berhala) itu sendiri diciptakan.'),
	(1146, 7, 192, 'وَلَا يَسْتَطِيْعُوْنَ لَهُمْ نَصْرًا وَّلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ', 'wal<u>aa</u> yasta<u>th</u>ii\'uuna lahum na<u>sh</u>ran wal<u>aa</u> anfusahum yan<u>sh</u>uruun<strong>a</strong>', 'Dan (berhala) itu tidak dapat memberikan pertolongan kepada penyembahnya, dan kepada dirinya sendiri pun mereka tidak dapat memberi pertolongan.'),
	(1147, 7, 193, 'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوْكُمْۗ سَوَۤاءٌ عَلَيْكُمْ اَدَعَوْتُمُوْهُمْ اَمْ اَنْتُمْ صَامِتُوْنَ', 'wa-in tad\'uuhum il<u>aa</u> <strong>a</strong>lhud<u>aa</u> l<u>aa</u> yattabi\'uukum saw<u>aa</u>un \'alaykum ada\'awtumuuhum am antum <u>shaa</u>mituun<strong>a</strong>', 'Dan jika kamu (wahai orang-orang musyrik) menyerunya (berhala-berhala) untuk memberi petunjuk kepadamu, tidaklah berhala-berhala itu dapat memperkenankan seruanmu; sama saja (hasilnya) buat kamu menyeru mereka atau berdiam diri.'),
	(1148, 7, 194, 'اِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوْهُمْ فَلْيَسْتَجِيْبُوْا لَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'inna <strong>al</strong>la<u>dz</u>iina tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi \'ib<u>aa</u>dun amts<u>aa</u>lukum fa<strong>u</strong>d\'uuhum falyastajiibuu lakum in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Sesungguhnya mereka (berhala-berhala) yang kamu seru selain Allah adalah makhluk (yang lemah) yang serupa juga dengan kamu. Maka serulah mereka lalu biarkanlah mereka memperkenankan permintaanmu, jika kamu orang yang benar.'),
	(1149, 7, 195, 'اَلَهُمْ اَرْجُلٌ يَّمْشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَيْدٍ يَّبْطِشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَعْيُنٌ يُّبْصِرُوْنَ بِهَآ ۖ اَمْ لَهُمْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۗ قُلِ ادْعُوْا شُرَكَاۤءَكُمْ ثُمَّ كِيْدُوْنِ فَلَا تُنْظِرُوْنِ ', 'alahum arjulun yamsyuuna bih<u>aa</u> am lahum aydin yab<u>th</u>isyuuna bih<u>aa</u> am lahum a\'yunun yub<u>sh</u>iruuna bih<u>aa</u> am lahum <u>aatsaa</u>nun yasma\'uuna bih<u>aa</u> quli ud\'uu syurak<u>aa</u>-akum tsumma kiiduuni fal<u>aa</u> tun<u>zh</u>iruun<strong>i</strong>', 'Apakah mereka (berhala-berhala) mempunyai kaki untuk berjalan, atau mempunyai tangan untuk memegang dengan keras, atau mempunyai mata untuk melihat, atau mempunyai telinga untuk mendengar? Katakanlah (Muhammad), “Panggillah (berhala-berhalamu) yang kamu anggap sekutu Allah, kemudian lakukanlah tipu daya (untuk mencelakakan)ku, dan jangan kamu tunda lagi.'),
	(1150, 7, 196, 'اِنَّ وَلِيِّ َۧ اللّٰهُ الَّذِيْ نَزَّلَ الْكِتٰبَۖ وَهُوَ يَتَوَلَّى الصّٰلِحِيْنَ', 'inna waliyyiya <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii nazzala <strong>a</strong>lkit<u>aa</u>ba wahuwa yatawall<u>aa</u> <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Sesungguhnya pelindungku adalah Allah yang telah menurunkan Kitab (Al-Qur\'an). Dia melindungi orang-orang saleh.'),
	(1151, 7, 197, 'وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَطِيْعُوْنَ نَصْرَكُمْ وَلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ', 'wa<strong>a</strong>lla<u>dz</u>iina tad\'uuna min duunihi l<u>aa</u> yasta<u>th</u>ii\'uuna na<u>sh</u>rakum wal<u>aa</u> anfusahum yan<u>sh</u>uruun<strong>a</strong>', 'Dan berhala-berhala yang kamu seru selain Allah tidaklah sanggup menolongmu, bahkan tidak dapat menolong dirinya sendiri.”'),
	(1152, 7, 198, 'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَسْمَعُوْاۗ وَتَرٰىهُمْ يَنْظُرُوْنَ اِلَيْكَ وَهُمْ لَا يُبْصِرُوْنَ', 'wa-in tad\'uuhum il<u>aa</u> <strong>a</strong>lhud<u>aa</u> l<u>aa</u> yasma\'uu watar<u>aa</u>hum yan<u>zh</u>uruuna ilayka wahum l<u>aa</u> yub<u>sh</u>iruun<strong>a</strong>', 'Dan jika kamu menyeru mereka (berhala-berhala) untuk memberi petunjuk, mereka tidak dapat mendengarnya. Dan kamu lihat mereka memandangmu padahal mereka tidak melihat.'),
	(1153, 7, 199, 'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِلِيْنَ', 'khu<u>dz</u>i <strong>a</strong>l\'afwa wa/mur bi<strong>a</strong>l\'urfi wa-a\'ri<u>dh</u> \'ani <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Jadilah pemaaf dan suruhlah orang mengerjakan yang makruf, serta jangan pedulikan orang-orang yang bodoh.'),
	(1154, 7, 200, 'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ سَمِيْعٌ عَلِيْمٌ', 'wa-imm<u>aa</u> yanzaghannaka mina <strong>al</strong>sysyay<u>th</u><u>aa</u>ni nazghun fa<strong>i</strong>sta\'i<u>dz</u> bi<strong>al</strong>l<u>aa</u>hi innahu samii\'un \'aliim<strong>un</strong>', 'Dan jika setan datang menggodamu, maka berlindunglah kepada Allah. Sungguh, Dia Maha Mendengar, Maha Mengetahui.'),
	(1155, 7, 201, 'اِنَّ الَّذِيْنَ اتَّقَوْا اِذَا مَسَّهُمْ طٰۤىِٕفٌ مِّنَ الشَّيْطٰنِ تَذَكَّرُوْا فَاِذَا هُمْ مُّبْصِرُوْنَۚ ', 'inna <strong>al</strong>la<u>dz</u>iina ittaqaw i<u>dzaa</u> massahum <u>th</u><u>aa</u>-ifun mina <strong>al</strong>sysyay<u>th</u><u>aa</u>ni ta<u>dz</u>akkaruu fa-i<u>dzaa</u> hum mub<u>sh</u>iruun<strong>a</strong>', 'Sesungguhnya orang-orang yang bertakwa apabila mereka dibayang-bayangi pikiran jahat (berbuat dosa) dari setan, mereka pun segera ingat kepada Allah, maka ketika itu juga mereka melihat (kesalahan-kesalahannya).'),
	(1156, 7, 202, 'وَاِخْوَانُهُمْ يَمُدُّوْنَهُمْ فِى الْغَيِّ ثُمَّ لَا يُقْصِرُوْنَ', 'wa-ikhw<u>aa</u>nuhum yamudduunahum fii <strong>a</strong>lghayyi tsumma l<u>aa</u> yuq<u>sh</u>iruun<strong>a</strong>', 'Dan teman-teman mereka (orang kafir dan fasik) membantu setan-setan dalam menyesatkan dan mereka tidak henti-hentinya (menyesatkan).'),
	(1157, 7, 203, 'وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوْا لَوْلَا اجْتَبَيْتَهَاۗ قُلْ اِنَّمَآ اَتَّبِعُ مَا يُوْحٰٓى اِلَيَّ مِنْ رَّبِّيْۗ هٰذَا بَصَاۤىِٕرُ مِنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'wa-i<u>dzaa</u> lam ta/tihim bi-<u>aa</u>yatin q<u>aa</u>luu lawl<u>aa</u> ijtabaytah<u>aa</u> qul innam<u>aa</u> attabi\'u m<u>aa</u> yuu<u>haa</u> ilayya min rabbii h<u>aadzaa</u> ba<u>shaa</u>-iru min rabbikum wahudan wara<u>h</u>matun liqawmin yu/minuun<strong>a</strong>', 'Dan apabila engkau (Muhammad) tidak membacakan suatu ayat kepada mereka, mereka berkata, “Mengapa tidak engkau buat sendiri ayat itu?” Katakanlah (Muhammad), “Sesungguhnya aku hanya mengikuti apa yang diwahyukan Tuhanku kepadaku. (Al-Qur\'an) ini adalah bukti-bukti yang nyata dari Tuhanmu, petunjuk dan rahmat bagi orang-orang yang beriman.”'),
	(1158, 7, 204, 'وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوْا لَهٗ وَاَنْصِتُوْا لَعَلَّكُمْ تُرْحَمُوْنَ', 'wa-i<u>dzaa</u> quri-a <strong>a</strong>lqur-<u>aa</u>nu fa<strong>i</strong>stami\'uu lahu wa-an<u>sh</u>ituu la\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dan apabila dibacakan Al-Qur\'an, maka dengarkanlah dan diamlah, agar kamu mendapat rahmat.'),
	(1159, 7, 205, 'وَاذْكُرْ رَّبَّكَ فِيْ نَفْسِكَ تَضَرُّعًا وَّخِيْفَةً وَّدُوْنَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِّنَ الْغٰفِلِيْنَ', 'wa<strong>u</strong><u>dz</u>kur rabbaka fii nafsika ta<u>dh</u>arru\'an wakhiifatan waduuna <strong>a</strong>ljahri mina <strong>a</strong>lqawli bi<strong>a</strong>lghuduwwi wa<strong>a</strong>l-<u>aasaa</u>li wal<u>aa</u> takun mina <strong>a</strong>lgh<u>aa</u>filiin<strong>a</strong>', 'Dan ingatlah Tuhanmu dalam hatimu dengan rendah hati dan rasa takut, dan dengan tidak mengeraskan suara, pada waktu pagi dan petang, dan janganlah kamu termasuk orang-orang yang lengah.'),
	(1160, 7, 206, 'اِنَّ الَّذِيْنَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَيُسَبِّحُوْنَهٗ وَلَهٗ يَسْجُدُوْنَ ࣖ ۩', 'inna <strong>al</strong>la<u>dz</u>iina \'inda rabbika l<u>aa</u> yastakbiruuna \'an \'ib<u>aa</u>datihi wayusabbi<u>h</u>uunahu walahu yasjuduun<strong>a</strong>', 'Sesungguhnya orang-orang yang ada di sisi Tuhanmu tidak merasa enggan untuk menyembah Allah dan mereka menyucikan-Nya dan hanya kepada-Nya mereka bersujud.'),
	(1161, 8, 1, 'يَسْـَٔلُوْنَكَ عَنِ الْاَنْفَالِۗ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُوْلِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوْا ذَاتَ بَيْنِكُمْ ۖوَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗٓ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ', 'yas-aluunaka \'ani <strong>a</strong>l-anf<u>aa</u>li quli <strong>a</strong>l-anf<u>aa</u>lu lill<u>aa</u>hi wa<strong>al</strong>rrasuuli fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>sh</u>li<u>h</u>uu <u>dzaa</u>ta baynikum wa-a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha warasuulahu in kuntum mu/miniin<strong>a</strong>', 'Mereka menanyakan kepadamu (Muhammad) tentang (pembagian) harta rampasan perang. Katakanlah, “Harta rampasan perang itu milik Allah dan Rasul (menurut ketentuan Allah dan Rasul-Nya), maka bertakwalah kepada Allah dan perbaikilah hubungan di antara sesamamu, dan taatlah kepada Allah dan Rasul-Nya jika kamu orang-orang yang beriman.”'),
	(1162, 8, 2, 'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيٰتُهٗ زَادَتْهُمْ اِيْمَانًا وَّعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۙ', 'innam<u>aa</u> <strong>a</strong>lmu/minuuna <strong>al</strong>la<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukira <strong>al</strong>l<u>aa</u>hu wajilat quluubuhum wa-i<u>dzaa</u> tuliyat \'alayhim <u>aa</u>y<u>aa</u>tuhu z<u>aa</u>dat-hum iim<u>aa</u>nan wa\'al<u>aa</u> rabbihim yatawakkaluun<strong>a</strong>', 'Sesungguhnya orang-orang yang beriman adalah mereka yang apabila disebut nama Allah gemetar hatinya, dan apabila dibacakan ayat-ayat-Nya kepada mereka, bertambah (kuat) imannya dan hanya kepada Tuhan mereka bertawakal,'),
	(1163, 8, 3, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَۗ ', '<strong>al</strong>la<u>dz</u>iina yuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun<strong>a</strong>', '(Yaitu) orang-orang yang melaksanakan salat dan yang menginfakkan sebagian dari rezeki yang Kami berikan kepada mereka.'),
	(1164, 8, 4, 'اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ  لَهُمْ دَرَجٰتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌۚ ', 'ul<u>aa</u>-ika humu <strong>a</strong>lmu/minuuna <u>h</u>aqqan lahum daraj<u>aa</u>tun \'inda rabbihim wamaghfiratun warizqun kariim<strong>un</strong>', 'Mereka itulah orang-orang yang benar-benar beriman. Mereka akan memperoleh derajat (tinggi) di sisi Tuhannya dan ampunan serta rezeki (nikmat) yang mulia.'),
	(1165, 8, 5, 'كَمَآ اَخْرَجَكَ رَبُّكَ مِنْۢ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ لَكٰرِهُوْنَ', 'kam<u>aa</u> akhrajaka rabbuka min baytika bi<strong>a</strong>l<u>h</u>aqqi wa-inna fariiqan mina <strong>a</strong>lmu/miniina lak<u>aa</u>rihuun<strong>a</strong>', 'Sebagaimana Tuhanmu menyuruhmu pergi dari rumahmu dengan kebenaran, meskipun sesungguhnya sebagian dari orang-orang yang beriman itu tidak menyukainya,'),
	(1166, 8, 6, 'يُجَادِلُوْنَكَ فِى الْحَقِّ بَعْدَمَا تَبَيَّنَ كَاَنَّمَا يُسَاقُوْنَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُوْنَ ۗ ', 'yuj<u>aa</u>diluunaka fii <strong>a</strong>l<u>h</u>aqqi ba\'da m<u>aa</u> tabayyana ka-annam<u>aa</u> yus<u>aa</u>quuna il<u>aa</u> <strong>a</strong>lmawti wahum yan<u>zh</u>uruun<strong>a</strong>', 'mereka membantahmu (Muhammad) tentang kebenaran setelah nyata (bahwa mereka pasti menang), seakan-akan mereka dihalau kepada kematian, sedang mereka melihat (sebab kematian itu).'),
	(1167, 8, 7, 'وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّاۤىِٕفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّوْنَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُوْنُ لَكُمْ وَيُرِيْدُ اللّٰهُ اَنْ يُّحِقَّ الْحَقَّ بِكَلِمٰتِهٖ وَيَقْطَعَ دَابِرَ الْكٰفِرِيْنَۙ ', 'wa-i<u>dz</u> ya\'idukumu <strong>al</strong>l<u>aa</u>hu i<u>h</u>d<u>aa</u> <strong>al</strong><u>ththaa</u>-ifatayni annah<u>aa</u> lakum watawadduuna anna ghayra <u>dzaa</u>ti <strong>al</strong>sysyawkati takuunu lakum wayuriidu <strong>al</strong>l<u>aa</u>hu an yu<u>h</u>iqqa <strong>a</strong>l<u>h</u>aqqa bikalim<u>aa</u>tihi wayaq<u>th</u>a\'a d<u>aa</u>bira <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan (ingatlah) ketika Allah menjanjikan kepadamu bahwa salah satu dari dua golongan (yang kamu hadapi) adalah untukmu, sedang kamu menginginkan bahwa yang tidak mempunyai kekuatan senjatalah untukmu. Tetapi Allah hendak membenarkan yang benar dengan ayat-ayat-Nya dan memusnahkan orang-orang kafir sampai ke akar-akarnya,'),
	(1168, 8, 8, 'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُوْنَۚ ', 'liyu<u>h</u>iqqa <strong>a</strong>l<u>h</u>aqqa wayub<u>th</u>ila <strong>a</strong>lb<u>aath</u>ila walaw kariha <strong>a</strong>lmujrimuun<strong>a</strong>', 'agar Allah memperkuat yang hak (Islam) dan menghilangkan yang batil (syirik) walaupun orang-orang yang berdosa (musyrik) itu tidak menyukainya.'),
	(1169, 8, 9, 'اِذْ تَسْتَغِيْثُوْنَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنِّيْ مُمِدُّكُمْ بِاَلْفٍ مِّنَ الْمَلٰۤىِٕكَةِ مُرْدِفِيْنَ', 'i<u>dz</u> tastaghiitsuuna rabbakum fa<strong>i</strong>staj<u>aa</u>ba lakum annii mumiddukum bi-alfin mina <strong>a</strong>lmal<u>aa</u>-ikati murdifiin<strong>a</strong>', '(Ingatlah), ketika kamu memohon pertolongan kepada Tuhanmu, lalu diperkenankan-Nya bagimu, “Sungguh, Aku akan mendatangkan bala bantuan kepadamu dengan seribu malaikat yang datang berturut-turut.”'),
	(1170, 8, 10, 'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَىِٕنَّ بِهٖ قُلُوْبُكُمْۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ࣖ ', 'wam<u>aa</u> ja\'alahu <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> busyr<u>aa</u> walita<u>th</u>ma-inna bihi quluubukum wam<u>aa</u> <strong>al</strong>nna<u>sh</u>ru ill<u>aa</u> min \'indi <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan tidaklah Allah menjadikannya melainkan sebagai kabar gembira agar hatimu menjadi tenteram karenanya. Dan kemenangan itu hanyalah dari sisi Allah. Sungguh, Allah Mahaperkasa, Mahabijaksana.'),
	(1171, 8, 11, 'اِذْ يُغَشِّيْكُمُ النُّعَاسَ اَمَنَةً مِّنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِّنَ السَّمَاۤءِ مَاۤءً لِّيُطَهِّرَكُمْ بِهٖ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطٰنِ وَلِيَرْبِطَ عَلٰى قُلُوْبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۗ ', 'i<u>dz</u> yughasysyiikumu <strong>al</strong>nnu\'<u>aa</u>sa amanatan minhu wayunazzilu \'alaykum mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an liyu<u>th</u>ahhirakum bihi wayu<u>dz</u>hiba \'ankum rijza <strong>al</strong>sysyay<u>th</u><u>aa</u>ni waliyarbi<u>th</u>a \'al<u>aa</u> quluubikum wayutsabbita bihi <strong>a</strong>l-aqd<u>aa</u>m<strong>a</strong>', '(Ingatlah), ketika Allah membuat kamu mengantuk untuk memberi ketenteraman dari-Nya, dan Allah menurunkan air (hujan) dari langit kepadamu untuk menyucikan kamu dengan (hujan) itu dan menghilangkan gangguan-gangguan setan dari dirimu dan untuk menguatkan hatimu serta memperteguh telapak kakimu (teguh pendirian).'),
	(1172, 8, 12, 'اِذْ يُوْحِيْ رَبُّكَ اِلَى الْمَلٰۤىِٕكَةِ اَنِّيْ مَعَكُمْ فَثَبِّتُوا الَّذِيْنَ اٰمَنُوْاۗ سَاُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ فَاضْرِبُوْا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوْا مِنْهُمْ كُلَّ بَنَانٍۗ ', 'i<u>dz</u> yuu<u>h</u>ii rabbuka il<u>aa</u> <strong>a</strong>lmal<u>aa</u>-ikati annii ma\'akum fatsabbituu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu saulqii fii quluubi <strong>al</strong>la<u>dz</u>iina kafaruu <strong>al</strong>rru\'ba fa<strong>i</strong><u>dh</u>ribuu fawqa <strong>a</strong>l-a\'n<u>aa</u>qi wa<strong>i</strong><u>dh</u>ribuu minhum kulla ban<u>aa</u>n<strong>in</strong>', '(Ingatlah), ketika Tuhanmu mewahyukan kepada para malaikat, “Sesungguhnya Aku bersama kamu, maka teguhkanlah (pendirian) orang-orang yang telah beriman.” Kelak akan Aku berikan rasa ketakutan ke dalam hati orang-orang kafir, maka pukullah di atas leher mereka dan pukullah tiap-tiap ujung jari mereka.'),
	(1173, 8, 13, 'ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗۚ وَمَنْ يُّشَاقِقِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', '<u>dzaa</u>lika bi-annahum sy<u>aa</u>qquu <strong>al</strong>l<u>aa</u>ha warasuulahu waman yusy<u>aa</u>qiqi <strong>al</strong>l<u>aa</u>ha warasuulahu fa-inna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', '(Ketentuan) yang demikian itu adalah karena sesungguhnya mereka menentang Allah dan Rasul-Nya; dan barangsiapa menentang Allah dan Rasul-Nya, sungguh, Allah sangat keras siksa-Nya.'),
	(1174, 8, 14, 'ذٰلِكُمْ فَذُوْقُوْهُ وَاَنَّ لِلْكٰفِرِيْنَ عَذَابَ النَّارِ', '<u>dzaa</u>likum fa<u>dz</u>uuquuhu wa-anna lilk<u>aa</u>firiina \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Demikianlah (hukuman dunia yang ditimpakan atasmu), maka rasakanlah hukuman itu. Sesungguhnya bagi orang-orang kafir ada (lagi) azab neraka.'),
	(1175, 8, 15, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا زَحْفًا فَلَا تُوَلُّوْهُمُ الْاَدْبَارَۚ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> laqiitumu <strong>al</strong>la<u>dz</u>iina kafaruu za<u>h</u>fan fal<u>aa</u> tuwalluuhumu <strong>a</strong>l-adb<u>aa</u>r<strong>a</strong>', 'Wahai orang yang beriman! Apabila kamu bertemu dengan orang-orang kafir yang akan menyerangmu, maka janganlah kamu berbalik membelakangi mereka (mundur).'),
	(1176, 8, 16, 'وَمَنْ يُّوَلِّهِمْ يَوْمَىِٕذٍ دُبُرَهٗٓ اِلَّا مُتَحَرِّفًا لِّقِتَالٍ اَوْ مُتَحَيِّزًا اِلٰى فِئَةٍ فَقَدْ بَاۤءَ بِغَضَبٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ', 'waman yuwallihim yawma-i<u>dz</u>in duburahu ill<u>aa</u> muta<u>h</u>arrifan liqit<u>aa</u>lin aw muta<u>h</u>ayyizan il<u>aa</u> fi-atin faqad b<u>aa</u>-a bigha<u>dh</u>abin mina <strong>al</strong>l<u>aa</u>hi wama/w<u>aa</u>hu jahannamu wabi/sa <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan barangsiapa mundur pada waktu itu, kecuali berbelok untuk (siasat) perang atau hendak menggabungkan diri dengan pasukan yang lain, maka sungguh, orang itu kembali dengan membawa kemurkaan dari Allah, dan tempatnya ialah neraka Jahanam, seburuk-buruk tempat kembali.'),
	(1177, 8, 17, 'فَلَمْ تَقْتُلُوْهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِنِيْنَ مِنْهُ بَلَاۤءً حَسَنًاۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ', 'falam taqtuluuhum wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha qatalahum wam<u>aa</u> ramayta i<u>dz</u> ramayta wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha ram<u>aa</u> waliyubliya <strong>a</strong>lmu/miniina minhu bal<u>aa</u>-an <u>h</u>asanan inna <strong>al</strong>l<u>aa</u>ha samii\'un \'aliim<strong>un</strong>', 'Maka (sebenarnya) bukan kamu yang membunuh mereka, melainkan Allah yang membunuh mereka, dan bukan engkau yang melempar ketika engkau melempar, tetapi Allah yang melempar. (Allah berbuat demikian untuk membinasakan mereka) dan untuk memberi kemenangan kepada orang-orang mukmin, dengan kemenangan yang baik. Sungguh, Allah Maha Mendengar, Maha Mengetahui.'),
	(1178, 8, 18, 'ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوْهِنُ كَيْدِ الْكٰفِرِيْنَ', '<u>dzaa</u>likum wa-anna <strong>al</strong>l<u>aa</u>ha muuhinu kaydi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Demikianlah (karunia Allah yang dilimpahkan kepadamu), dan sungguh, Allah melemahkan tipu daya orang-orang kafir.'),
	(1179, 8, 19, 'اِنْ تَسْتَفْتِحُوْا فَقَدْ جَاۤءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوْا فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَعُوْدُوْا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـًٔا وَّلَوْ كَثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِنِيْنَ ࣖ ', 'in tastafti<u>h</u>uu faqad j<u>aa</u>-akumu <strong>a</strong>lfat<u>h</u>u wa-in tantahuu fahuwa khayrun lakum wa-in ta\'uuduu na\'ud walan tughniya \'ankum fi-atukum syay-an walaw katsurat wa-anna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>a</strong>lmu/miniin<strong>a</strong>', 'Jika kamu meminta keputusan, maka sesungguhnya keputusan telah datang kepadamu; dan jika kamu berhenti (memusuhi Rasul), maka itulah yang lebih baik bagimu; dan jika kamu kembali, niscaya Kami kembali (memberi pertolongan); dan pasukanmu tidak akan dapat menolak sesuatu bahaya sedikit pun darimu, biarpun dia jumlahnya (pasukan) banyak. Sungguh, Allah beserta orang-orang beriman.'),
	(1180, 8, 20, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha warasuulahu wal<u>aa</u> tawallaw \'anhu wa-antum tasma\'uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Taatlah kepada Allah dan Rasul-Nya, dan janganlah kamu berpaling dari-Nya, padahal kamu mendengar (perintah-perintah-Nya),'),
	(1181, 8, 21, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ قَالُوْا سَمِعْنَا وَهُمْ لَا يَسْمَعُوْنَۚ ', 'wal<u>aa</u> takuunuu ka<strong>a</strong>lla<u>dz</u>iina q<u>aa</u>luu sami\'n<u>aa</u> wahum l<u>aa</u> yasma\'uun<strong>a</strong>', 'dan janganlah kamu menjadi seperti orang-orang (munafik) yang berkata, “Kami mendengarkan,” padahal mereka tidak mendengarkan (karena hati mereka mengingkarinya).'),
	(1182, 8, 22, '۞ اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذِيْنَ لَا يَعْقِلُوْنَ', 'inna syarra <strong>al</strong>ddaw<u>aa</u>bbi \'inda <strong>al</strong>l<u>aa</u>hi <strong>al</strong><u>shsh</u>ummu <strong>a</strong>lbukmu <strong>al</strong>la<u>dz</u>iina l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Sesungguhnya makhluk bergerak yang bernyawa yang paling buruk dalam pandangan Allah ialah mereka yang tuli dan bisu (tidak mendengar dan memahami kebenaran) yaitu orang-orang yang tidak mengerti.'),
	(1183, 8, 23, 'وَلَوْ عَلِمَ اللّٰهُ فِيْهِمْ خَيْرًا لَّاَسْمَعَهُمْۗ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ', 'walaw \'alima <strong>al</strong>l<u>aa</u>hu fiihim khayran la-asma\'ahum walaw asma\'ahum latawallaw wahum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan sekiranya Allah mengetahui ada kebaikan pada mereka, tentu Dia jadikan mereka dapat mendengar. Dan jika Allah menjadikan mereka dapat mendengar, niscaya mereka berpaling, sedang mereka memalingkan diri.'),
	(1184, 8, 24, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَجِيْبُوْا لِلّٰهِ وَلِلرَّسُوْلِ اِذَا دَعَاكُمْ لِمَا يُحْيِيْكُمْۚ وَاعْلَمُوْٓا اَنَّ اللّٰهَ يَحُوْلُ بَيْنَ الْمَرْءِ وَقَلْبِهٖ وَاَنَّهٗٓ اِلَيْهِ تُحْشَرُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu istajiibuu lill<u>aa</u>hi wali<strong>l</strong>rrasuuli i<u>dzaa</u> da\'<u>aa</u>kum lim<u>aa</u> yu<u>h</u>yiikum wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ya<u>h</u>uulu bayna <strong>a</strong>lmar-i waqalbihi wa-annahu ilayhi tu<u>h</u>syaruun<strong>a</strong>', 'Wahai orang-orang yang beriman! Penuhilah seruan Allah dan Rasul, apabila dia menyerumu kepada sesuatu yang memberi kehidupan kepadamu, dan ketahuilah bahwa sesungguhnya Allah membatasi antara manusia dan hatinya dan sesungguhnya kepada-Nyalah kamu akan dikumpulkan.'),
	(1185, 8, 25, 'وَاتَّقُوْا فِتْنَةً لَّا تُصِيْبَنَّ الَّذِيْنَ ظَلَمُوْا مِنْكُمْ خَاۤصَّةً ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ', 'wa<strong>i</strong>ttaquu fitnatan l<u>aa</u> tu<u>sh</u>iibanna <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu minkum kh<u>aass</u>atan wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Dan peliharalah dirimu dari siksaan yang tidak hanya menimpa orang-orang yang zalim saja di antara kamu. Ketahuilah bahwa Allah sangat keras siksa-Nya.'),
	(1186, 8, 26, 'وَاذْكُرُوْٓا اِذْ اَنْتُمْ قَلِيْلٌ مُّسْتَضْعَفُوْنَ فِى الْاَرْضِ تَخَافُوْنَ اَنْ يَّتَخَطَّفَكُمُ النَّاسُ فَاٰوٰىكُمْ وَاَيَّدَكُمْ بِنَصْرِهٖ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ لَعَلَّكُمْ تَشْكُرُوْنَ', 'wa<strong>u</strong><u>dz</u>kuruu i<u>dz</u> antum qaliilun musta<u>dh</u>\'afuuna fii <strong>a</strong>l-ar<u>dh</u>i takh<u>aa</u>fuuna an yatakha<u>ththh</u>afakumu <strong>al</strong>nn<u>aa</u>su fa<u>aa</u>w<u>aa</u>kum wa-ayyadakum bina<u>sh</u>rihi warazaqakum mina <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti la\'allakum tasykuruun<strong>a</strong>', 'Dan ingatlah ketika kamu (para Muhajirin) masih (berjumlah) sedikit, lagi tertindas di bumi (Mekah), dan kamu takut orang-orang (Mekah) akan menculik kamu, maka Dia memberi kamu tempat menetap (Madinah) dan dijadikan-Nya kamu kuat dengan pertolongan-Nya dan diberi-Nya kamu rezeki yang baik agar kamu bersyukur.'),
	(1187, 8, 27, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَخُوْنُوا اللّٰهَ وَالرَّسُوْلَ وَتَخُوْنُوْٓا اَمٰنٰتِكُمْ وَاَنْتُمْ تَعْلَمُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> takhuunuu <strong>al</strong>l<u>aa</u>ha wa<strong>al</strong>rrasuula watakhuunuu am<u>aa</u>n<u>aa</u>tikum wa-antum ta\'lamuun<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu mengkhianati Allah dan Rasul dan (juga) janganlah kamu mengkhianati amanat yang dipercayakan kepadamu, sedang kamu mengetahui.'),
	(1188, 8, 28, 'وَاعْلَمُوْٓا اَنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ  ۙوَّاَنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ࣖ', 'wa<strong>i</strong>\'lamuu annam<u>aa</u> amw<u>aa</u>lukum wa-awl<u>aa</u>dukum fitnatun wa-anna <strong>al</strong>l<u>aa</u>ha \'indahu ajrun \'a<u>zh</u>iim<strong>un</strong>', 'Dan ketahuilah bahwa hartamu dan anak-anakmu itu hanyalah sebagai cobaan dan sesungguhnya di sisi Allah ada pahala yang besar.'),
	(1189, 8, 29, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَّكُمْ فُرْقَانًا وَّيُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>m<u>aa</u>nuu in tattaquu <strong>al</strong>l<u>aa</u>ha yaj\'al lakum furq<u>aa</u>nan wayukaffir \'ankum sayyi-<u>aa</u>tikum wayaghfir lakum wa<strong>al</strong>l<u>aa</u>hu <u>dz</u>uu <strong>a</strong>lfa<u>dh</u>li <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Wahai orang-orang yang beriman! Jika kamu bertakwa kepada Allah, niscaya Dia akan memberikan furqan (kemampuan membedakan antara yang hak dan batil) kepadamu dan menghapus segala kesalahanmu dan mengampuni (dosa-dosa)mu. Allah memiliki karunia yang besar.'),
	(1190, 8, 30, 'وَاِذْ يَمْكُرُ بِكَ الَّذِيْنَ كَفَرُوْا لِيُثْبِتُوْكَ اَوْ يَقْتُلُوْكَ اَوْ يُخْرِجُوْكَۗ وَيَمْكُرُوْنَ وَيَمْكُرُ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ', 'wa-i<u>dz</u> yamkuru bika <strong>al</strong>la<u>dz</u>iina kafaruu liyutsbituuka aw yaqtuluuka aw yukhrijuuka wayamkuruuna wayamkuru <strong>al</strong>l<u>aa</u>hu wa<strong>al</strong>l<u>aa</u>hu khayru <strong>a</strong>lm<u>aa</u>kiriin<strong>a</strong>', 'Dan (ingatlah), ketika orang-orang kafir (Quraisy) memikirkan tipu daya terhadapmu (Muhammad) untuk menangkap dan memenjarakanmu atau membunuhmu, atau mengusirmu. Mereka membuat tipu daya dan Allah menggagalkan tipu daya itu. Allah adalah sebaik-baik pembalas tipu daya.'),
	(1191, 8, 31, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا قَالُوْا قَدْ سَمِعْنَا لَوْ نَشَاۤءُ لَقُلْنَا مِثْلَ هٰذَآ  ۙاِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> q<u>aa</u>luu qad sami\'n<u>aa</u> law nasy<u>aa</u>u laquln<u>aa</u> mitsla h<u>aadzaa</u> in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dan apabila ayat-ayat Kami dibacakan kepada mereka, mereka berkata, “Sesungguhnya kami telah mendengar (ayat-ayat seperti ini), jika kami menghendaki niscaya kami dapat membacakan yang seperti ini. (Al-Qur\'an) ini tidak lain hanyalah dongeng orang-orang terdahulu.”'),
	(1192, 8, 32, 'وَاِذْ قَالُوا اللهم  اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِّنَ السَّمَاۤءِ اَوِ ائْتِنَا بِعَذَابٍ اَلِيْمٍ', 'wa-i<u>dz</u> q<u>aa</u>luu <strong>al</strong>l<u>aa</u>humma in k<u>aa</u>na h<u>aadzaa</u> huwa <strong>a</strong>l<u>h</u>aqqa min \'indika fa-am<u>th</u>ir \'alayn<u>aa</u> <u>h</u>ij<u>aa</u>ratan mina <strong>al</strong>ssam<u>aa</u>-i awi i/tin<u>aa</u> bi\'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Dan (ingatlah), ketika mereka (orang-orang musyrik) berkata, “Ya Allah, jika (Al-Qur\'an) ini benar (wahyu) dari Engkau, maka hujanilah kami dengan batu dari langit, atau datangkanlah kepada kami azab yang pedih.”'),
	(1193, 8, 33, 'وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ فِيْهِمْۚ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُوْنَ ', 'wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liyu\'a<u>dzdz</u>ibahum wa-anta fiihim wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu mu\'a<u>dzdz</u>ibahum wahum yastaghfiruun<strong>a</strong>', 'Tetapi Allah tidak akan menghukum mereka, selama engkau (Muhammad) berada di antara mereka. Dan tidaklah (pula) Allah akan menghukum mereka, sedang mereka (masih) memohon ampunan.'),
	(1194, 8, 34, 'وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّوْنَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوْٓا اَوْلِيَاۤءَهٗۗ اِنْ اَوْلِيَاۤؤُهٗٓ اِلَّا الْمُتَّقُوْنَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'wam<u>aa</u> lahum <strong>al</strong>l<u>aa</u> yu\'a<u>dzdz</u>ibahumu <strong>al</strong>l<u>aa</u>hu wahum ya<u>sh</u>udduuna \'ani <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi wam<u>aa</u> k<u>aa</u>nuu awliy<u>aa</u>-ahu in awliy<u>aa</u>uhu ill<u>aa</u> <strong>a</strong>lmuttaquuna wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan mengapa Allah tidak menghukum mereka padahal mereka menghalang-halangi (orang) untuk (mendatangi) Masjidilharam dan mereka bukanlah orang-orang yang berhak menguasainya? Orang yang berhak menguasai(nya), hanyalah orang-orang yang bertakwa, tetapi kebanyakan mereka tidak mengetahui.'),
	(1195, 8, 35, 'وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَاۤءً وَّتَصْدِيَةًۗ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ', 'wam<u>aa</u> k<u>aa</u>na <u>sh</u>al<u>aa</u>tuhum \'inda <strong>a</strong>lbayti ill<u>aa</u> muk<u>aa</u>-an wata<u>sh</u>diyatan fa<u>dz</u>uuquu <strong>a</strong>l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun<strong>a</strong>', 'Dan salat mereka di sekitar Baitullah itu, tidak lain hanyalah siulan dan tepuk tangan. Maka rasakanlah azab disebabkan kekafiranmu itu.'),
	(1196, 8, 36, 'اِنَّ الَّذِيْنَ كَفَرُوْا يُنْفِقُوْنَ اَمْوَالَهُمْ لِيَصُدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗفَسَيُنْفِقُوْنَهَا ثُمَّ تَكُوْنُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُوْنَ ەۗ وَالَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ يُحْشَرُوْنَۙ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu yunfiquuna amw<u>aa</u>lahum liya<u>sh</u>udduu \'an sabiili <strong>al</strong>l<u>aa</u>hi fasayunfiquunah<u>aa</u> tsumma takuunu \'alayhim <u>h</u>asratan tsumma yughlabuuna wa<strong>a</strong>lla<u>dz</u>iina kafaruu il<u>aa</u> jahannama yu<u>h</u>syaruun<strong>a</strong>', 'Sesungguhnya orang-orang yang kafir itu, menginfakkan harta mereka untuk menghalang-halangi (orang) dari jalan Allah. Mereka akan (terus) menginfakkan harta itu, kemudian mereka akan menyesal sendiri, dan akhirnya mereka akan dikalahkan. Ke dalam neraka Jahanamlah orang-orang kafir itu akan dikumpulkan,'),
	(1197, 8, 37, 'لِيَمِيْزَ اللّٰهُ الْخَبِيْثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيْثَ بَعْضَهٗ عَلٰى بَعْضٍ فَيَرْكُمَهٗ جَمِيْعًا فَيَجْعَلَهٗ فِيْ جَهَنَّمَۗ  اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ࣖ ', 'liyamiiza <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lkhabiitsa mina <strong>al</strong><u>ththh</u>ayyibi wayaj\'ala <strong>a</strong>lkhabiitsa ba\'<u>dh</u>ahu \'al<u>aa</u> ba\'<u>dh</u>in fayarkumahu jamii\'an fayaj\'alahu fii jahannama ul<u>aa</u>-ika humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', 'agar Allah memisahkan (golongan) yang buruk dari yang baik dan menjadikan (golongan) yang buruk itu sebagiannya di atas yang lain, lalu kesemuanya ditumpukkan-Nya, dan dimasukkan-Nya ke dalam neraka Jahanam. Mereka itulah orang-orang yang rugi.'),
	(1198, 8, 38, 'قُلْ لِّلَّذِيْنَ كَفَرُوْٓا اِنْ يَّنْتَهُوْا يُغْفَرْ لَهُمْ مَّا قَدْ سَلَفَۚ وَاِنْ يَّعُوْدُوْا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّلِيْنَ', 'qul lilla<u>dz</u>iina kafaruu in yantahuu yughfar lahum m<u>aa</u> qad salafa wa-in ya\'uuduu faqad ma<u>dh</u>at sunnatu <strong>a</strong>l-awwaliin<strong>a</strong>', 'Katakanlah kepada orang-orang yang kafir itu (Abu Sufyan dan kawan-kawannya), “Jika mereka berhenti (dari kekafirannya), niscaya Allah akan mengampuni dosa-dosa mereka yang telah lalu; dan jika mereka kembali lagi (memerangi Nabi) sungguh, berlaku (kepada mereka) sunnah (Allah terhadap) orang-orang dahulu (dibinasakan).”'),
	(1199, 8, 39, 'وَقَاتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ كُلُّهٗ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ بَصِيْرٌ', 'waq<u>aa</u>tiluuhum <u>h</u>att<u>aa</u> l<u>aa</u> takuuna fitnatun wayakuuna <strong>al</strong>ddiinu kulluhu lill<u>aa</u>hi fa-ini intahaw fa-inna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> ya\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Dan perangilah mereka itu sampai tidak ada lagi fitnah, dan agama hanya bagi Allah semata. Jika mereka berhenti (dari kekafiran), maka sesungguhnya Allah Maha Melihat apa yang mereka kerjakan.'),
	(1200, 8, 40, 'وَاِنْ تَوَلَّوْا فَاعْلَمُوْٓا اَنَّ اللّٰهَ مَوْلٰىكُمْ ۗنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ  ۔', 'wa-in tawallaw fa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha mawl<u>aa</u>kum ni\'ma <strong>a</strong>lmawl<u>aa</u> wani\'ma <strong>al</strong>nna<u>sh</u>iir<strong>u</strong>', 'Dan jika mereka berpaling, maka ketahuilah bahwa sesungguhnya Allah pelindungmu. Dia adalah sebaik-baik pelindung dan sebaik-baik penolong.'),
	(1201, 8, 41, '۞ وَاعْلَمُوْٓا اَنَّمَا غَنِمْتُمْ مِّنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهٗ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَآ اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ ال', 'wa<strong>i</strong>\'lamuu annam<u>aa</u> ghanimtum min syay-in fa-anna lill<u>aa</u>hi khumusahu wali<strong>l</strong>rrasuuli wali<u>dz</u>ii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lyat<u>aa</u>m<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiini wa<strong>i</strong>bni <strong>al</strong>ssabiili in kuntum <u>aa</u>mantum bi<strong>al</strong>l<u>aa</u>hi wam<u>aa</u> anzaln<u>aa</u> \'al<u>aa</u> \'abdin<u>aa</u> yawma <strong>a</strong>lfurq<u>aa</u>ni yawma iltaq<u>aa</u> <strong>a</strong>ljam\'<u>aa</u>ni wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan ketahuilah, sesungguhnya segala yang kamu peroleh sebagai rampasan perang, maka seperlima untuk Allah, Rasul, kerabat Rasul, anak yatim, orang miskin dan ibnu sabil, (demikian) jika kamu beriman kepada Allah dan kepada apa yang Kami turunkan kepada hamba Kami (Muhammad) di hari Furqan, yaitu pada hari bertemunya dua pasukan. Allah Mahakuasa atas segala sesuatu.'),
	(1202, 8, 42, 'اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۗ وَلَوْ تَوَاعَدْتُّمْ لَاخْتَلَفْتُمْ فِى الْمِيْعٰدِۙ وَلٰكِنْ لِّيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ەۙ لِّيَهْلِكَ مَنْ هَلَكَ عَنْۢ بَيِّنَةٍ ', 'i<u>dz</u> antum bi<strong>a</strong>l\'udwati <strong>al</strong>dduny<u>aa</u> wahum bi<strong>a</strong>l\'udwati <strong>a</strong>lqu<u>sh</u>w<u>aa</u> wa<strong>al</strong>rrakbu asfala minkum walaw taw<u>aa</u>\'adtum la<strong>i</strong>khtalaftum fii <strong>a</strong>lmii\'<u>aa</u>di wal<u>aa</u>kin liyaq<u>dh</u>iya <strong>al</strong>l<u>aa</u>hu amran k<u>aa</u>na maf\'uulan liyahlika man halaka \'an bayyinatin waya<u>h</u>y<u>aa</u> man <u>h</u>ayya \'an bayyinatin wa-inna <strong>al</strong>l<u>aa</u>ha lasamii\'un \'aliim<strong>un</strong>', '(Yaitu) ketika kamu berada di pinggir lembah yang dekat dan mereka berada di pinggir lembah yang jauh sedang kafilah itu berada lebih rendah dari kamu. Sekiranya kamu mengadakan persetujuan (untuk menentukan hari pertempuran), niscaya kamu berbeda pendapat dalam menentukan (hari pertempuran itu), tetapi Allah berkehendak melaksanakan suatu urusan yang harus dilaksanakan, yaitu agar orang yang binasa itu binasa dengan bukti yang nyata dan agar orang yang hidup itu hidup dengan bukti yang nyata. Sungguh, Allah Maha Mendengar, Maha Mengetahui.'),
	(1203, 8, 43, 'اِذْ يُرِيْكَهُمُ اللّٰهُ فِيْ مَنَامِكَ قَلِيْلًاۗ وَلَوْ اَرٰىكَهُمْ كَثِيْرًا لَّفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِى الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۗ اِنَّهٗ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ ', 'i<u>dz</u> yuriikahumu <strong>al</strong>l<u>aa</u>hu fii man<u>aa</u>mika qaliilan walaw ar<u>aa</u>kahum katsiiran lafasyiltum walatan<u>aa</u>za\'tum fii <strong>a</strong>l-amri wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha sallama innahu \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', '(Ingatlah) ketika Allah memperlihatkan mereka di dalam mimpimu (berjumlah) sedikit. Dan sekiranya Allah memperlihatkan mereka (berjumlah) banyak tentu kamu menjadi gentar dan tentu kamu akan berbantah-bantahan dalam urusan itu, tetapi Allah telah menyelamatkan kamu. Sungguh, Allah Maha Mengetahui apa yang ada dalam hatimu.'),
	(1204, 8, 44, 'وَاِذْ يُرِيْكُمُوْهُمْ اِذِ الْتَقَيْتُمْ فِيْٓ اَعْيُنِكُمْ قَلِيْلًا وَّيُقَلِّلُكُمْ فِيْٓ اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ ', 'wa-i<u>dz</u> yuriikumuuhum i<u>dz</u>i iltaqaytum fii a\'yunikum qaliilan wayuqallilukum fii a\'yunihim liyaq<u>dh</u>iya <strong>al</strong>l<u>aa</u>hu amran k<u>aa</u>na maf\'uulan wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi turja\'u <strong>a</strong>l-umuur<strong>u</strong>', 'Dan ketika Allah memperlihatkan mereka kepadamu, ketika kamu berjumpa dengan mereka berjumlah sedikit menurut penglihatan matamu dan kamu diperlihatkan-Nya berjumlah sedikit menurut penglihatan mereka, itu karena Allah berkehendak melaksanakan suatu urusan yang harus dilaksanakan. Hanya kepada Allah segala urusan dikembalikan.'),
	(1205, 8, 45, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمْ فِئَةً فَاثْبُتُوْا وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَۚ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> laqiitum fi-atan fa<strong>u</strong>tsbutuu wa<strong>u</strong><u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha katsiiran la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Apabila kamu bertemu pasukan (musuh), maka berteguh hatilah dan sebutlah (nama) Allah banyak-banyak (berzikir dan berdoa) agar kamu beruntung.'),
	(1206, 8, 46, 'وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَنَازَعُوْا فَتَفْشَلُوْا وَتَذْهَبَ رِيْحُكُمْ وَاصْبِرُوْاۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَۚ ', 'wa-a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha warasuulahu wal<u>aa</u> tan<u>aa</u>za\'uu fatafsyaluu wata<u>dz</u>haba rii<u>h</u>ukum wa<strong>i</strong><u>sh</u>biruu inna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Dan taatilah Allah dan Rasul-Nya dan janganlah kamu berselisih, yang menyebabkan kamu menjadi gentar dan kekuatanmu hilang dan bersabarlah. Sungguh, Allah beserta orang-orang sabar.'),
	(1207, 8, 47, 'وَلَا تَكُوْنُوْا كَالَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ بَطَرًا وَّرِئَاۤءَ النَّاسِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَاللّٰهُ بِمَايَعْمَلُوْنَ مُحِيْطٌ ', 'wal<u>aa</u> takuunuu ka<strong>a</strong>lla<u>dz</u>iina kharajuu min diy<u>aa</u>rihim ba<u>th</u>aran wari-<u>aa</u>-a <strong>al</strong>nn<u>aa</u>si waya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u><strong>un</strong>', 'Dan janganlah kamu seperti orang-orang yang keluar dari kampung halamannya dengan rasa angkuh dan ingin dipuji orang (ria) serta menghalang-halangi (orang) dari jalan Allah. Allah meliputi segala yang mereka kerjakan.'),
	(1208, 8, 48, 'وَاِذْ زَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنِّيْ جَارٌ لَّكُمْۚ فَلَمَّا تَرَاۤءَتِ الْفِئَتٰنِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكُمْ اِنِّيْٓ اَرٰى مَا لَا تَرَوْنَ اِنِّيْ', 'wa-i<u>dz</u> zayyana lahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum waq<u>aa</u>la l<u>aa</u> gh<u>aa</u>liba lakumu <strong>a</strong>lyawma mina <strong>al</strong>nn<u>aa</u>si wa-innii j<u>aa</u>run lakum falamm<u>aa</u> tar<u>aa</u>-ati <strong>a</strong>lfi-at<u>aa</u>ni naka<u>sh</u>a \'al<u>aa</u> \'aqibayhi waq<u>aa</u>la innii barii-un minkum innii ar<u>aa</u> m<u>aa</u> l<u>aa</u> tarawna innii akh<u>aa</u>fu <strong>al</strong>l<u>aa</u>ha wa<strong>al</strong>l<u>aa</u>hu syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Dan (ingatlah) ketika setan menjadikan terasa indah bagi mereka perbuatan (dosa) mereka dan mengatakan, “Tidak ada (orang) yang dapat mengalahkan kamu pada hari ini, dan sungguh, aku adalah penolongmu.” Maka ketika kedua pasukan itu telah saling melihat (berhadapan), setan balik ke belakang seraya berkata, “Sesungguhnya aku berlepas diri dari kamu; aku dapat melihat apa yang kamu tidak dapat melihat; sesungguhnya aku takut kepada Allah.” Allah sangat keras siksa-Nya.'),
	(1209, 8, 49, 'اِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ غَرَّ هٰٓؤُلَاۤءِ دِيْنُهُمْۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ', 'i<u>dz</u> yaquulu <strong>a</strong>lmun<u>aa</u>fiquuna wa<strong>a</strong>lla<u>dz</u>iina fii quluubihim mara<u>dh</u>un gharra h<u>aa</u>ul<u>aa</u>-i diinuhum waman yatawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi fa-inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', '(Ingatlah), ketika orang-orang munafik dan orang-orang yang ada penyakit di dalam hatinya berkata, “Mereka itu (orang mukmin) ditipu agamanya.” (Allah berfirman), “Barangsiapa bertawakal kepada Allah, ketahuilah bahwa Allah Mahaperkasa, Mahabijaksana.”'),
	(1210, 8, 50, 'وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذِيْنَ كَفَرُوا الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْۚ وَذُوْقُوْا عَذَابَ الْحَرِيْقِ', 'walaw tar<u>aa</u> i<u>dz</u> yatawaff<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu <strong>a</strong>lmal<u>aa</u>-ikatu ya<u>dh</u>ribuuna wujuuhahum wa-adb<u>aa</u>rahum wa<u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>a</strong>l<u>h</u>ariiq<strong>i</strong>', 'Dan sekiranya kamu melihat ketika para malaikat mencabut nyawa orang-orang yang kafir sambil memukul wajah dan punggung mereka (dan berkata), “Rasakanlah olehmu siksa neraka yang membakar.”'),
	(1211, 8, 51, 'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۙ', '<u>dzaa</u>lika bim<u>aa</u> qaddamat aydiikum wa-anna <strong>al</strong>l<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid<strong>i</strong>', 'Demikian itu disebabkan oleh perbuatan tanganmu sendiri. Dan sesungguhnya Allah tidak menzalimi hamba-hamba-Nya,'),
	(1212, 8, 52, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَفَرُوْا بِاٰيٰتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْۗ اِنَّ اللّٰهَ قَوِيٌّ شَدِيْدُ الْعِقَابِ ', 'kada/bi <u>aa</u>li fir\'awna wa<strong>a</strong>lla<u>dz</u>iina min qablihim kafaruu bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi fa-akha<u>dz</u>ahumu <strong>al</strong>l<u>aa</u>hu bi<u>dz</u>unuubihim inna <strong>al</strong>l<u>aa</u>ha qawiyyun syadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', '(Keadaan mereka) serupa dengan keadaan pengikut Fir‘aun dan orang-orang yang sebelum mereka. Mereka mengingkari ayat-ayat Allah, maka Allah menyiksa mereka disebabkan dosa-dosanya. Sungguh, Allah Mahakuat lagi sangat keras siksa-Nya.'),
	(1213, 8, 53, 'ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّرًا نِّعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌۙ ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha lam yaku mughayyiran ni\'matan an\'amah<u>aa</u> \'al<u>aa</u> qawmin <u>h</u>att<u>aa</u> yughayyiruu m<u>aa</u> bi-anfusihim wa-anna <strong>al</strong>l<u>aa</u>ha samii\'un \'aliim<strong>un</strong>', 'Yang demikian itu karena sesungguhnya Allah tidak akan mengubah suatu nikmat yang telah diberikan-Nya kepada suatu kaum, hingga kaum itu mengubah apa yang ada pada diri mereka sendiri. Sungguh, Allah Maha Mendengar, Maha Mengetahui,'),
	(1214, 8, 54, 'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۚ  كَذَّبُوْا بِاٰيٰتِ رَبِّهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوْا ظٰلِمِيْنَ ', 'kada/bi <u>aa</u>li fir\'awna wa<strong>a</strong>lla<u>dz</u>iina min qablihim ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>ti rabbihim fa-ahlakn<u>aa</u>hum bi<u>dz</u>unuubihim wa-aghraqn<u>aa</u> <u>aa</u>la fir\'awna wakullun k<u>aa</u>nuu <u>zhaa</u>limiin<strong>a</strong>', '(Keadaan mereka) serupa dengan keadaan pengikut Fir‘aun dan orang-orang yang sebelum mereka. Mereka mendustakan ayat-ayat Tuhannya, maka Kami membinasakan mereka disebabkan oleh dosa-dosanya dan Kami tenggelamkan Fir‘aun dan pengikut-pengikutnya; karena mereka adalah orang-orang yang zalim.'),
	(1215, 8, 55, 'اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الَّذِيْنَ كَفَرُوْا فَهُمْ لَا يُؤْمِنُوْنَۖ ', 'inna syarra <strong>al</strong>ddaw<u>aa</u>bbi \'inda <strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>iina kafaruu fahum l<u>aa</u> yu/minuun<strong>a</strong>', 'Sesungguhnya makhluk bergerak yang bernyawa yang paling buruk dalam pandangan Allah ialah orang-orang kafir, karena mereka tidak beriman.'),
	(1216, 8, 56, 'الَّذِيْنَ عَاهَدْتَّ مِنْهُمْ ثُمَّ يَنْقُضُوْنَ عَهْدَهُمْ فِيْ كُلِّ مَرَّةٍ وَّهُمْ لَا يَتَّقُوْنَ ', '<strong>al</strong>la<u>dz</u>iina \'<u>aa</u>hadta minhum tsumma yanqu<u>dh</u>uuna \'ahdahum fii kulli marratin wahum l<u>aa</u> yattaquun<strong>a</strong>', '(Yaitu) orang-orang yang terikat perjanjian dengan kamu, kemudian setiap kali berjanji mereka mengkhianati janjinya, sedang mereka tidak takut (kepada Allah).'),
	(1217, 8, 57, 'فَاِمَّا تَثْقَفَنَّهُمْ فِى الْحَرْبِ فَشَرِّدْ بِهِمْ مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُوْنَ ', 'fa-imm<u>aa</u> tatsqafannahum fii <strong>a</strong>l<u>h</u>arbi fasyarrid bihim man khalfahum la\'allahum ya<u>dzdz</u>akkaruun<strong>a</strong>', 'Maka jika engkau (Muhammad) mengungguli mereka dalam peperangan, maka cerai-beraikanlah orang-orang yang di belakang mereka dengan (menumpas) mereka, agar mereka mengambil pelajaran.'),
	(1218, 8, 58, 'وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْۢبِذْ اِلَيْهِمْ عَلٰى سَوَاۤءٍۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَاۤىِٕنِيْنَ ࣖ ', 'wa-imm<u>aa</u> takh<u>aa</u>fanna min qawmin khiy<u>aa</u>natan fa<strong>i</strong>nbi<u>dz</u> ilayhim \'al<u>aa</u> sawa-in inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lkh<u>aa</u>-iniin<strong>a</strong>', 'Dan jika engkau (Muhammad) khawatir akan (terjadinya) pengkhianatan dari suatu golongan, maka kembalikanlah perjanjian itu kepada mereka dengan cara yang jujur. Sungguh, Allah tidak menyukai orang yang berkhianat.'),
	(1219, 8, 59, 'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا سَبَقُوْاۗ اِنَّهُمْ لَا يُعْجِزُوْنَ ', 'wal<u>aa</u> ya<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina kafaruu sabaquu innahum l<u>aa</u> yu\'jizuun<strong>a</strong>', 'Dan janganlah orang-orang kafir mengira, bahwa mereka akan dapat lolos (dari kekuasaan Allah). Sungguh, mereka tidak dapat melemahkan (Allah).'),
	(1220, 8, 60, 'وَاَعِدُّوْا لَهُمْ مَّا اسْتَطَعْتُمْ مِّنْ قُوَّةٍ وَّمِنْ رِّبَاطِ الْخَيْلِ تُرْهِبُوْنَ بِهٖ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَرِيْنَ مِنْ دُوْنِهِمْۚ  لَا تَعْلَمُوْنَهُمْۚ  اَللّٰهُ يَعْلَمُهُمْۗ وَمَا تُنْفِقُوْا مِنْ شَيْءٍ فِيْ سَبِيْلِ اللّٰه', 'wa-a\'idduu lahum m<u>aa</u> ista<u>th</u>a\'tum min quwwatin wamin rib<u>aath</u>i <strong>a</strong>lkhayli turhibuuna bihi \'aduwwa <strong>al</strong>l<u>aa</u>hi wa\'aduwwakum wa<u>aa</u>khariina min duunihim l<u>aa</u> ta\'lamuunahumu <strong>al</strong>l<u>aa</u>hu ya\'lamuhum wam<u>aa</u> tunfiquu min syay-in fii sabiili <strong>al</strong>l<u>aa</u>hi yuwaffa ilaykum wa-antum l<u>aa</u> tu<u>zh</u>lamuun<strong>a</strong>', 'Dan persiapkanlah dengan segala kemampuan untuk menghadapi mereka dengan kekuatan yang kamu miliki dan dari pasukan berkuda yang dapat menggentarkan musuh Allah, musuhmu dan orang-orang selain mereka yang kamu tidak mengetahuinya; tetapi Allah mengetahuinya. Apa saja yang kamu infakkan di jalan Allah niscaya akan dibalas dengan cukup kepadamu dan kamu tidak akan dizalimi (dirugikan).'),
	(1221, 8, 61, '۞ وَاِنْ جَنَحُوْا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ', 'wa-in jana<u>h</u>uu li<strong>l</strong>ssalmi fa<strong>i</strong>jna<u>h</u> lah<u>aa</u> watawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi innahu huwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Tetapi jika mereka condong kepada perdamaian, maka terimalah dan bertawakallah kepada Allah. Sungguh, Dia Maha Mendengar, Maha Mengetahui.'),
	(1222, 8, 62, 'وَاِنْ يُّرِيْدُوْٓا اَنْ يَّخْدَعُوْكَ فَاِنَّ حَسْبَكَ اللّٰهُ ۗهُوَ الَّذِيْٓ اَيَّدَكَ بِنَصْرِهٖ وَبِالْمُؤْمِنِيْنَۙ ', 'wa-in yuriiduu an yakhda\'uuka fa-inna <u>h</u>asbaka <strong>al</strong>l<u>aa</u>hu huwa <strong>al</strong>la<u>dz</u>ii ayyadaka bina<u>sh</u>rihi wabi<strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan jika mereka hendak menipumu, maka sesungguhnya cukuplah Allah (menjadi pelindung) bagimu. Dialah yang memberikan kekuatan kepadamu dengan pertolongan-Nya dan dengan (dukungan) orang-orang mukmin,'),
	(1223, 8, 63, 'وَاَلَّفَ بَيْنَ قُلُوْبِهِمْۗ  لَوْاَنْفَقْتَ مَا فِى الْاَرْضِ جَمِيْعًا مَّآ اَلَّفْتَ بَيْنَ قُلُوْبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۗ اِنَّهٗ عَزِيْزٌ حَكِيْمٌ ', 'wa-allafa bayna quluubihim law anfaqta m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an m<u>aa</u> <strong>al</strong>lafta bayna quluubihim wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha <strong>al</strong>lafa baynahum innahu \'aziizun <u>h</u>akiim<strong>un</strong>', 'dan Dia (Allah) yang mempersatukan hati mereka (orang yang beriman). Walaupun kamu menginfakkan semua (kekayaan) yang berada di bumi, niscaya kamu tidak dapat mempersatukan hati mereka, tetapi Allah telah mempersatukan hati mereka. Sungguh, Dia Mahaperkasa, Mahabijaksana.'),
	(1224, 8, 64, 'يٰٓاَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ࣖ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu <u>h</u>asbuka <strong>al</strong>l<u>aa</u>hu wamani ittaba\'aka mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Wahai Nabi (Muhammad)! Cukuplah Allah (menjadi pelindung) bagimu dan bagi orang-orang mukmin yang mengikutimu.'),
	(1225, 8, 65, 'يٰٓاَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِيْنَ عَلَى الْقِتَالِۗ اِنْ يَّكُنْ مِّنْكُمْ عِشْرُوْنَ صَابِرُوْنَ يَغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ يَّغْلِبُوْٓا اَلْفًا مِّنَ الَّذِيْنَ كَفَرُوْا بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu <u>h</u>arri<u>dh</u>i <strong>a</strong>lmu/miniina \'al<u>aa</u> <strong>a</strong>lqit<u>aa</u>li in yakun minkum \'isyruuna <u>shaa</u>biruuna yaghlibuu mi-atayni wa-in yakun minkum mi-atun yaghlibuu <strong>a</strong>lfan mina <strong>al</strong>la<u>dz</u>iina kafaruu bi-annahum qawmun l<u>aa</u> yafqahuun<strong>a</strong>', 'Wahai Nabi (Muhammad)! Kobarkanlah semangat para mukmin untuk berperang. Jika ada dua puluh orang yang sabar di antara kamu, niscaya mereka dapat mengalahkan dua ratus orang musuh. Dan jika ada seratus orang (yang sabar) di antara kamu, niscaya mereka dapat mengalahkan seribu orang kafir, karena orang-orang kafir itu adalah kaum yang tidak mengerti.'),
	(1226, 8, 66, 'اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ فِيْكُمْ ضَعْفًاۗ فَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ صَابِرَةٌ يَّغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ اَلْفٌ يَّغْلِبُوْٓا اَلْفَيْنِ بِاِذْنِ اللّٰهِ ۗوَاللّٰهُ مَعَ الصّٰبِرِيْنَ ', 'al-<u>aa</u>na khaffafa <strong>al</strong>l<u>aa</u>hu \'ankum wa\'alima anna fiikum <u>dh</u>a\'fan fa-in yakun minkum mi-atun <u>shaa</u>biratun yaghlibuu mi-atayni wa-in yakun minkum <strong>a</strong>lfun yaghlibuu <strong>a</strong>lfayni bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu ma\'a <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', 'Sekarang Allah telah meringankan kamu karena Dia mengetahui bahwa ada kelemahan padamu. Maka jika di antara kamu ada seratus orang yang sabar, niscaya mereka dapat mengalahkan dua ratus (orang musuh); dan jika di antara kamu ada seribu orang (yang sabar), niscaya mereka dapat mengalahkan dua ribu orang dengan seizin Allah. Allah beserta orang-orang yang sabar.'),
	(1227, 8, 67, 'مَاكَانَ لِنَبِيٍّ اَنْ يَّكُوْنَ لَهٗٓ اَسْرٰى حَتّٰى يُثْخِنَ فِى الْاَرْضِۗ تُرِيْدُوْنَ عَرَضَ الدُّنْيَاۖ وَاللّٰهُ يُرِيْدُ الْاٰخِرَةَۗ وَاللّٰهُ عَزِيْزٌحَكِيْمٌ ', 'm<u>aa</u> k<u>aa</u>na linabiyyin an yakuuna lahu asr<u>aa</u> <u>h</u>att<u>aa</u> yutskhina fii <strong>a</strong>l-ar<u>dh</u>i turiiduuna \'ara<u>dh</u>a <strong>al</strong>dduny<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu yuriidu <strong>a</strong>l-<u>aa</u>khirata wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>h</u>akiim<strong>un</strong>', 'Tidaklah pantas, bagi seorang nabi mempunyai tawanan sebelum dia dapat melumpuhkan musuhnya di bumi. Kamu menghendaki harta benda duniawi sedangkan Allah menghendaki (pahala) akhirat (untukmu). Allah Mahaperkasa, Mahabijaksana.'),
	(1228, 8, 68, 'لَوْلَاكِتٰبٌ مِّنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ فِيْمَآ اَخَذْتُمْ عَذَابٌ عَظِيْمٌ ', 'lawl<u>aa</u> kit<u>aa</u>bun mina <strong>al</strong>l<u>aa</u>hi sabaqa lamassakum fiim<u>aa</u> akha<u>dz</u>tum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Sekiranya tidak ada ketetapan terdahulu dari Allah, niscaya kamu ditimpa siksaan yang besar karena (tebusan) yang kamu ambil.'),
	(1229, 8, 69, 'فَكُلُوْا مِمَّاغَنِمْتُمْ حَلٰلًا طَيِّبًاۖ وَّاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'fakuluu mimm<u>aa</u> ghanimtum <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Maka makanlah dari sebagian rampasan perang yang telah kamu peroleh itu, sebagai makanan yang halal lagi baik, dan bertakwalah kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(1230, 8, 70, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّمَنْ فِيْٓ اَيْدِيْكُمْ مِّنَ الْاَسْرٰٓىۙ اِنْ يَّعْلَمِ اللّٰهُ فِيْ قُلُوْبِكُمْ خَيْرًا يُّؤْتِكُمْ خَيْرًا مِّمَّآ اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ࣖ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu qul liman fii aydiikum mina <strong>a</strong>l-asr<u>aa</u> in ya\'lami <strong>al</strong>l<u>aa</u>hu fii quluubikum khayran yu/tikum khayran mimm<u>aa</u> ukhi<u>dz</u>a minkum wayaghfir lakum wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Wahai Nabi (Muhammad)! Katakanlah kepada para tawanan perang yang ada di tanganmu, “Jika Allah mengetahui ada kebaikan di dalam hatimu, niscaya Dia akan memberikan yang lebih baik dari apa yang telah diambil darimu dan Dia akan mengampuni kamu.” Allah Maha Pengampun, Maha Penyayang.'),
	(1231, 8, 71, 'وَاِنْ يُّرِيْدُوْا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wa-in yuriiduu khiy<u>aa</u>nataka faqad kh<u>aa</u>nuu <strong>al</strong>l<u>aa</u>ha min qablu fa-amkana minhum wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Tetapi jika mereka (tawanan itu) hendak mengkhianatimu (Muhammad) maka sesungguhnya sebelum itu pun mereka telah berkhianat kepada Allah, maka Dia memberikan kekuasaan kepadamu atas mereka. Allah Maha Mengetahui, Mahabijaksana.'),
	(1232, 8, 72, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَالَّذِيْنَ اٰمَنُوْا وَلَمْ يُهَاجِرُوْا مَا لَكُمْ مِّنْ وَّلَايَتِ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu bi-amw<u>aa</u>lihim wa-anfusihim fii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>waw wana<u>sh</u>aruu ul<u>aa</u>-ika ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu walam yuh<u>aa</u>jiruu m<u>aa</u> lakum min wal<u>aa</u>yatihim min syay-in <u>h</u>att<u>aa</u> yuh<u>aa</u>jiruu wa-ini istan<u>sh</u>aruukum fii <strong>al</strong>ddiini fa\'alaykumu <strong>al</strong>nna<u>sh</u>ru ill<u>aa</u> \'al<u>aa</u> qawmin baynakum wabaynahum miits<u>aa</u>qun wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Sesungguhnya orang-orang yang beriman dan berhijrah serta berjihad dengan harta dan jiwanya pada jalan Allah dan orang-orang yang memberikan tempat kediaman dan memberi pertolongan (kepada Muhajirin), mereka itu satu sama lain saling melindungi. Dan (terhadap) orang-orang yang beriman tetapi belum berhijrah, maka tidak ada kewajiban sedikit pun bagimu melindungi  mereka, sampai mereka berhijrah. (Tetapi) jika mereka meminta pertolongan kepadamu dalam (urusan pembelaan) agama, maka kamu wajib memberikan pertolongan kecuali terhadap kaum yang telah terikat perjanjian antara kamu dengan mereka. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
	(1233, 8, 73, 'وَالَّذِيْنَ كَفَرُوْا بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ اِلَّا تَفْعَلُوْهُ تَكُنْ فِتْنَةٌ فِى الْاَرْضِ وَفَسَادٌ كَبِيْرٌۗ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in ill<u>aa</u> taf\'aluuhu takun fitnatun fii <strong>a</strong>l-ar<u>dh</u>i wafas<u>aa</u>dun kabiir<strong>un</strong>', 'Dan orang-orang yang kafir, sebagian mereka melindungi sebagian yang lain. Jika kamu tidak melaksanakan apa yang telah diperintahkan Allah (saling melindungi), niscaya akan terjadi kekacauan di bumi dan kerusakan yang besar.'),
	(1234, 8, 74, 'وَالَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ  لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu fii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>waw wana<u>sh</u>aruu ul<u>aa</u>-ika humu <strong>a</strong>lmu/minuuna <u>h</u>aqqan lahum maghfiratun warizqun kariim<strong>un</strong>', 'Dan orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah, dan orang-orang yang memberi tempat kediaman dan memberi pertolongan (kepada orang Muhajirin), mereka itulah orang yang benar-benar beriman. Mereka memperoleh ampunan dan rezeki (nikmat) yang mulia.'),
	(1235, 8, 75, 'وَالَّذِيْنَ اٰمَنُوْا مِنْۢ بَعْدُ وَهَاجَرُوْا وَجَاهَدُوْا مَعَكُمْ فَاُولٰۤىِٕكَ مِنْكُمْۗ وَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu min ba\'du wah<u>aa</u>jaruu waj<u>aa</u>haduu ma\'akum faul<u>aa</u>-ika minkum wauluu <strong>a</strong>l-ar<u>haa</u>mi ba\'<u>dh</u>uhum awl<u>aa</u> biba\'<u>dh</u>in fii kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha bikulli syay-in \'aliim<strong>un</strong>', 'Dan orang-orang yang beriman setelah itu, kemudian berhijrah dan berjihad bersamamu maka mereka termasuk golonganmu. Orang-orang yang mempunyai hubungan kerabat itu sebagiannya lebih berhak terhadap sesamanya (daripada yang bukan kerabat) menurut Kitab Allah. Sungguh, Allah Maha Mengetahui segala sesuatu.'),
	(1236, 9, 1, 'بَرَاۤءَةٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَۗ', 'bar<u>aa</u>-atun mina <strong>al</strong>l<u>aa</u>hi warasuulihi il<u>aa</u> <strong>al</strong>la<u>dz</u>iina \'<u>aa</u>hadtum mina <strong>a</strong>lmusyrikiin<strong>a</strong>', '(Inilah pernyataan) pemutusan hubungan dari Allah dan Rasul-Nya kepada orang-orang musyrik yang kamu telah mengadakan perjanjian (dengan mereka).'),
	(1237, 9, 2, 'فَسِيْحُوْا فِى الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَّاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۙوَاَنَّ اللّٰهَ مُخْزِى الْكٰفِرِيْنَ ', 'fasii<u>h</u>uu fii <strong>a</strong>l-ar<u>dh</u>i arba\'ata asyhurin wa<strong>i</strong>\'lamuu annakum ghayru mu\'jizii <strong>al</strong>l<u>aa</u>hi wa-anna <strong>al</strong>l<u>aa</u>ha mukhzii <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Maka berjalanlah kamu (kaum musyrikin) di bumi selama empat bulan dan ketahuilah bahwa kamu tidak dapat melemahkan Allah, dan sesungguhnya Allah menghinakan orang-orang kafir.'),
	(1238, 9, 3, 'وَاَذَانٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَرِيْۤءٌ مِّنَ الْمُشْرِكِيْنَ ەۙ وَرَسُوْلُهٗ ۗفَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۗو', 'wa-a<u>dzaa</u>nun mina <strong>al</strong>l<u>aa</u>hi warasuulihi il<u>aa</u> <strong>al</strong>nn<u>aa</u>si yawma <strong>a</strong>l<u>h</u>ajji <strong>a</strong>l-akbari anna <strong>al</strong>l<u>aa</u>ha barii-un mina <strong>a</strong>lmusyrikiina warasuuluhu fa-in tubtum fahuwa khayrun lakum wa-in tawallaytum fa<strong>i</strong>\'lamuu annakum ghayru mu\'jizii <strong>al</strong>l<u>aa</u>hi wabasysyiri <strong>al</strong>la<u>dz</u>iina kafaruu bi\'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Dan satu maklumat (pemberitahuan) dari Allah dan Rasul-Nya kepada umat manusia pada hari haji akbar, bahwa sesungguhnya Allah dan Rasul-Nya berlepas diri dari orang-orang musyrik. Kemudian jika kamu (kaum musyrikin) bertobat, maka itu lebih baik bagimu; dan jika kamu berpaling, maka ketahuilah bahwa kamu tidak dapat melemahkan Allah. Dan berilah kabar gembira kepada orang-orang kafir (bahwa mereka akan mendapat) azab yang pedih,'),
	(1239, 9, 4, 'اِلَّا الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَ ثُمَّ لَمْ يَنْقُصُوْكُمْ شَيْـًٔا وَّلَمْ يُظَاهِرُوْا عَلَيْكُمْ اَحَدًا فَاَتِمُّوْٓا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina \'<u>aa</u>hadtum mina <strong>a</strong>lmusyrikiina tsumma lam yanqu<u>sh</u>uukum syay-an walam yu<u>zhaa</u>hiruu \'alaykum a<u>h</u>adan fa-atimmuu ilayhim \'ahdahum il<u>aa</u> muddatihim inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmuttaqiin<strong>a</strong>', 'kecuali orang-orang musyrik yang telah mengadakan perjanjian dengan kamu dan mereka sedikit pun tidak mengurangi (isi perjanjian) dan tidak (pula) mereka membantu seorang pun yang memusuhi kamu, maka terhadap mereka itu penuhilah janjinya sampai batas waktunya. Sungguh, Allah menyukai orang-orang yang bertakwa.'),
	(1240, 9, 5, 'فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِيْنَ حَيْثُ وَجَدْتُّمُوْهُمْ وَخُذُوْهُمْ وَاحْصُرُوْهُمْ وَاقْعُدُوْا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوْا سَبِيْلَهُمْۗ اِنَّ اللّٰه', 'fa-i<u>dzaa</u> insalakha <strong>a</strong>l-asyhuru <strong>a</strong>l<u>h</u>urumu fa<strong>u</strong>qtuluu <strong>a</strong>lmusyrikiina <u>h</u>aytsu wajadtumuuhum wakhu<u>dz</u>uuhum wa<strong>u</strong><u>hs</u>uruuhum wa<strong>u</strong>q\'uduu lahum kulla mar<u>sh</u>adin fa-in t<u>aa</u>buu wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu <strong>al</strong>zzak<u>aa</u>ta fakhalluu sabiilahum inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Apabila telah habis bulan-bulan haram, maka perangilah orang-orang musyrik di mana saja kamu temui, tangkaplah dan kepunglah mereka, dan awasilah di tempat pengintaian. Jika mereka bertobat dan melaksanakan salat serta menunaikan zakat, maka berilah kebebasan kepada mereka. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(1241, 9, 6, 'وَاِنْ اَحَدٌ مِّنَ الْمُشْرِكِيْنَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلٰمَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهٗ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْلَمُوْنَ ࣖ', 'wa-in a<u>h</u>adun mina <strong>a</strong>lmusyrikiina istaj<u>aa</u>raka fa-ajirhu <u>h</u>att<u>aa</u> yasma\'a kal<u>aa</u>ma <strong>al</strong>l<u>aa</u>hi tsumma ablighhu ma/manahu <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan jika di antara kaum musyrikin ada yang meminta perlindungan kepadamu, maka lindungilah agar dia dapat mendengar firman Allah, kemudian antarkanlah dia ke tempat yang aman baginya. (Demikian) itu karena sesungguhnya mereka kaum yang tidak mengetahui.'),
	(1242, 9, 7, 'كَيْفَ يَكُوْنُ لِلْمُشْرِكِيْنَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُوْلِهٖٓ اِلَّا الَّذِيْنَ عَاهَدْتُّمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوْا لَكُمْ فَاسْتَقِيْمُوْا لَهُمْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ', 'kayfa yakuunu lilmusyrikiina \'ahdun \'inda <strong>al</strong>l<u>aa</u>hi wa\'inda rasuulihi ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina \'<u>aa</u>hadtum \'inda <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi fam<u>aa</u> istaq<u>aa</u>muu lakum fa<strong>i</strong>staqiimuu lahum inna <strong>al</strong>l<u>aa</u>ha yu<u>h</u>ibbu <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Bagaimana mungkin ada perjanjian (aman) di sisi Allah dan Rasul-Nya dengan orang-orang musyrik, kecuali dengan orang-orang yang kamu telah mengadakan perjanjian (dengan mereka) di dekat Masjidilharam (Hudaibiyah), maka selama mereka berlaku jujur terhadapmu, hendaklah kamu berlaku jujur (pula) terhadap mereka. Sungguh, Allah menyukai orang-orang yang bertakwa.'),
	(1243, 9, 8, 'كَيْفَ وَاِنْ يَّظْهَرُوْا عَلَيْكُمْ لَا يَرْقُبُوْا فِيْكُمْ اِلًّا وَّلَا ذِمَّةً ۗيُرْضُوْنَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوْبُهُمْۚ وَاَكْثَرُهُمْ فٰسِقُوْنَۚ', 'kayfa wa-in ya<u>zh</u>haruu \'alaykum l<u>aa</u> yarqubuu fiikum illan wal<u>aa</u> <u>dz</u>immatan yur<u>dh</u>uunakum bi-afw<u>aa</u>hihim wata/b<u>aa</u> quluubuhum wa-aktsaruhum f<u>aa</u>siquun<strong>a</strong>', 'Bagaimana mungkin (ada perjanjian demikian), padahal jika mereka memperoleh kemenangan atas kamu, mereka tidak memelihara hubungan kekerabatan denganmu dan tidak (pula mengindahkan) perjanjian. Mereka menyenangkan hatimu dengan mulutnya, sedang hatinya menolak. Kebanyakan mereka adalah orang-orang fasik (tidak menepati janji).'),
	(1244, 9, 9, 'اِشْتَرَوْا بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا فَصَدُّوْا عَنْ سَبِيْلِهٖۗ اِنَّهُمْ سَاۤءَ مَاكَانُوْا يَعْمَلُوْنَ ', 'isytaraw bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi tsamanan qaliilan fa<u>sh</u>adduu \'an sabiilihi innahum s<u>aa</u>-a m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Mereka memperjualbelikan ayat-ayat Allah dengan harga murah, lalu mereka menghalang-halangi (orang) dari jalan Allah. Sungguh, betapa buruknya apa yang mereka kerjakan.'),
	(1245, 9, 10, 'لَا يَرْقُبُوْنَ فِيْ مُؤْمِنٍ اِلًّا وَّلَا ذِمَّةً ۗوَاُولٰۤىِٕكَ هُمُ الْمُعْتَدُوْنَ ', 'l<u>aa</u> yarqubuuna fii mu/minin illan wal<u>aa</u> <u>dz</u>immatan waul<u>aa</u>-ika humu <strong>a</strong>lmu\'taduun<strong>a</strong>', 'Mereka tidak memelihara (hubungan) kekerabatan dengan orang mukmin dan tidak (pula mengindahkan) perjanjian. Dan mereka itulah orang-orang yang melampaui batas.'),
	(1246, 9, 11, 'فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِى الدِّيْنِ ۗوَنُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ', 'fa-in t<u>aa</u>buu wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu <strong>al</strong>zzak<u>aa</u>ta fa-ikhw<u>aa</u>nukum fii <strong>al</strong>ddiini wanufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun<strong>a</strong>', 'Dan jika mereka bertobat, melaksanakan salat dan menunaikan zakat, maka (berarti mereka itu) adalah saudara-saudaramu seagama. Kami menjelaskan ayat-ayat itu bagi orang-orang yang mengetahui.'),
	(1247, 9, 12, 'وَاِنْ نَّكَثُوْٓا اَيْمَانَهُمْ مِّنْۢ بَعْدِ عَهْدِهِمْ وَطَعَنُوْا فِيْ دِيْنِكُمْ فَقَاتِلُوْٓا اَىِٕمَّةَ الْكُفْرِۙ اِنَّهُمْ لَآ اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُوْنَ ', 'wa-in nakatsuu aym<u>aa</u>nahum min ba\'di \'ahdihim wa<u>th</u>a\'anuu fii diinikum faq<u>aa</u>tiluu a-immata <strong>a</strong>lkufri innahum l<u>aa</u> aym<u>aa</u>na lahum la\'allahum yantahuun<strong>a</strong>', 'Dan jika mereka melanggar sumpah setelah ada perjanjian, dan mencerca agamamu, maka perangilah pemimpin-pemimpin kafir itu. Sesungguhnya mereka adalah orang-orang yang tidak dapat dipegang janjinya, mudah-mudahan mereka berhenti.'),
	(1248, 9, 13, 'اَلَا تُقَاتِلُوْنَ قَوْمًا نَّكَثُوْٓا اَيْمَانَهُمْ وَهَمُّوْا بِاِخْرَاجِ الرَّسُوْلِ وَهُمْ بَدَءُوْكُمْ اَوَّلَ مَرَّةٍۗ  اَتَخْشَوْنَهُمْ ۚفَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ', 'al<u>aa</u> tuq<u>aa</u>tiluuna qawman nakatsuu aym<u>aa</u>nahum wahammuu bi-ikhr<u>aa</u>ji <strong>al</strong>rrasuuli wahum badauukum awwala marratin atakhsyawnahum fa<strong>al</strong>l<u>aa</u>hu a<u>h</u>aqqu an takhsyawhu in kuntum mu/miniin<strong>a</strong>', 'Mengapa kamu tidak memerangi orang-orang yang melanggar sumpah (janjinya), dan telah merencanakan untuk mengusir Rasul, dan mereka yang pertama kali memerangi kamu? Apakah kamu takut kepada mereka, padahal Allah-lah yang lebih berhak untuk kamu takuti, jika kamu orang-orang beriman.'),
	(1249, 9, 14, 'قَاتِلُوْهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْدِيْكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُوْرَ قَوْمٍ مُّؤْمِنِيْنَۙ', 'q<u>aa</u>tiluuhum yu\'a<u>dzdz</u>ibhumu <strong>al</strong>l<u>aa</u>hu bi-aydiikum wayukhzihim wayan<u>sh</u>urkum \'alayhim wayasyfi <u>sh</u>uduura qawmin mu/miniin<strong>a</strong>', 'Perangilah mereka, niscaya Allah akan menyiksa mereka dengan (perantaraan) tanganmu dan Dia akan menghina mereka dan menolongmu (dengan kemenangan) atas mereka, serta melegakan hati orang-orang yang beriman,'),
	(1250, 9, 15, 'وَيُذْهِبْ غَيْظَ قُلُوْبِهِمْۗ وَيَتُوْبُ اللّٰهُ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ', 'wayu<u>dz</u>hib ghay<u>zh</u>a quluubihim wayatuubu <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'dan Dia menghilangkan kemarahan hati mereka (orang mukmin). Dan Allah menerima tobat orang yang Dia kehendaki. Allah Maha Mengetahui, Mahabijaksana.'),
	(1251, 9, 16, 'اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوْا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَلَمْ يَتَّخِذُوْا مِنْ دُوْنِ اللّٰهِ وَلَا رَسُوْلِهٖ وَلَا الْمُؤْمِنِيْنَ وَلِيْجَةً ۗوَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ ࣖ', 'am <u>h</u>asibtum an tutrakuu walamm<u>aa</u> ya\'lami <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina j<u>aa</u>haduu minkum walam yattakhi<u>dz</u>uu min duuni <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> rasuulihi wal<u>aa</u> <strong>a</strong>lmu/miniina waliijatan wa<strong>al</strong>l<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun<strong>a</strong>', 'Apakah kamu mengira bahwa kamu akan dibiarkan (begitu saja), padahal Allah belum mengetahui orang-orang yang berjihad di antara kamu dan tidak mengambil teman yang setia selain Allah, Rasul-Nya dan orang-orang yang beriman. Allah Mahateliti terhadap apa yang kamu kerjakan.'),
	(1252, 9, 17, 'مَا كَانَ لِلْمُشْرِكِيْنَ اَنْ يَّعْمُرُوْا مَسٰجِدَ اللّٰهِ شٰهِدِيْنَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْۚ وَ فِى النَّارِ هُمْ خٰلِدُوْنَ ', 'm<u>aa</u> k<u>aa</u>na lilmusyrikiina an ya\'muruu mas<u>aa</u>jida <strong>al</strong>l<u>aa</u>hi sy<u>aa</u>hidiina \'al<u>aa</u> anfusihim bi<strong>a</strong>lkufri ul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum wafii <strong>al</strong>nn<u>aa</u>ri hum kh<u>aa</u>liduun<strong>a</strong>', 'Tidaklah pantas orang-orang musyrik memakmurkan masjid Allah, padahal mereka mengakui bahwa mereka sendiri kafir. Mereka itu sia-sia amalnya, dan mereka kekal di dalam neraka.'),
	(1253, 9, 18, 'اِنَّمَا يَعْمُرُ مَسٰجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ ۗفَعَسٰٓى اُولٰۤىِٕكَ اَنْ يَّكُوْنُوْا مِنَ الْمُهْتَدِيْنَ ', 'innam<u>aa</u> ya\'muru mas<u>aa</u>jida <strong>al</strong>l<u>aa</u>hi man <u>aa</u>mana bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa-aq<u>aa</u>ma <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>t<u>aa</u> <strong>al</strong>zzak<u>aa</u>ta walam yakhsya ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha fa\'as<u>aa</u> ul<u>aa</u>-ika an yakuunuu mina <strong>a</strong>lmuhtadiin<strong>a</strong>', 'Sesungguhnya yang memakmurkan masjid Allah hanyalah orang-orang yang beriman kepada Allah dan hari kemudian, serta (tetap) melaksanakan salat, menunaikan zakat dan tidak takut (kepada apa pun) kecuali kepada Allah. Maka mudah-mudahan mereka termasuk orang-orang yang mendapat petunjuk.'),
	(1254, 9, 19, '۞ اَجَعَلْتُمْ سِقَايَةَ الْحَاۤجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ فِيْ سَبِيْلِ اللّٰهِ ۗ لَا يَسْتَوٗنَ عِنْدَ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۘ', 'aja\'altum siq<u>aa</u>yata <strong>a</strong>l<u>haa</u>jji wa\'im<u>aa</u>rata <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi kaman <u>aa</u>mana bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri waj<u>aa</u>hada fii sabiili <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yastawuuna \'inda <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Apakah (orang-orang) yang memberi minuman kepada orang-orang yang mengerjakan haji dan mengurus Masjidilharam, kamu samakan dengan orang yang beriman kepada Allah dan hari kemudian serta berjihad di jalan Allah? Mereka tidak sama di sisi Allah. Allah tidak memberikan petunjuk kepada orang-orang zalim.'),
	(1255, 9, 20, 'اَلَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِ ۗوَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu fii sabiili <strong>al</strong>l<u>aa</u>hi bi-amw<u>aa</u>lihim wa-anfusihim a\'<u>zh</u>amu darajatan \'inda <strong>al</strong>l<u>aa</u>hi waul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>-izuun<strong>a</strong>', 'Orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah, dengan harta dan jiwa mereka, adalah lebih tinggi derajatnya di sisi Allah. Mereka itulah orang-orang yang memperoleh kemenangan.'),
	(1256, 9, 21, 'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِّنْهُ وَرِضْوَانٍ وَّجَنّٰتٍ لَّهُمْ فِيْهَا نَعِيْمٌ مُّقِيْمٌۙ', 'yubasysyiruhum rabbuhum bira<u>h</u>matin minhu wari<u>dh</u>w<u>aa</u>nin wajann<u>aa</u>tin lahum fiih<u>aa</u> na\'iimun muqiim<strong>un</strong>', 'Tuhan menggembirakan mereka dengan memberikan rahmat, keridaan dan surga, mereka memperoleh kesenangan yang kekal di dalamnya,'),
	(1257, 9, 22, 'خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗاِنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ', 'kh<u>aa</u>lidiina fiih<u>aa</u> abadan inna <strong>al</strong>l<u>aa</u>ha \'indahu ajrun \'a<u>zh</u>iim<strong>un</strong>', 'mereka kekal di dalamnya selama-lamanya. Sungguh, di sisi Allah terdapat pahala yang besar.'),
	(1258, 9, 23, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْٓا اٰبَاۤءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَاۤءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْاِيْمَانِۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu <u>aa</u>b<u>aa</u>-akum wa-ikhw<u>aa</u>nakum awliy<u>aa</u>-a ini ista<u>h</u>abbuu <strong>a</strong>lkufra \'al<u>aa</u> <strong>a</strong>l-iim<u>aa</u>ni waman yatawallahum minkum faul<u>aa</u>-ika humu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu jadikan bapak-bapakmu dan saudara-saudaramu sebagai pelindung, jika mereka lebih menyukai kekafiran daripada keimanan. Barangsiapa di antara kamu yang menjadikan mereka pelindung, maka mereka itulah orang-orang yang zalim.'),
	(1259, 9, 24, 'قُلْ اِنْ كَانَ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَشِيْرَتُكُمْ وَاَمْوَالُ ِۨاقْتَرَفْتُمُوْهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسٰكِنُ تَرْضَوْنَهَآ اَحَبَّ اِلَيْكُمْ مِّنَ اللّٰهِ وَرَسُوْلِهٖ وَجِهَادٍ فِيْ سَبِيْ', 'qul in k<u>aa</u>na <u>aa</u>b<u>aa</u>ukum wa-abn<u>aa</u>ukum wa-ikhw<u>aa</u>nukum wa-azw<u>aa</u>jukum wa\'asyiiratukum wa-amw<u>aa</u>lun iqtaraftumuuh<u>aa</u> watij<u>aa</u>ratun takhsyawna kas<u>aa</u>dah<u>aa</u> wamas<u>aa</u>kinu tar<u>dh</u>awnah<u>aa</u> a<u>h</u>abba ilaykum mina <strong>al</strong>l<u>aa</u>hi warasuulihi wajih<u>aa</u>din fii sabiilihi fatarabba<u>sh</u>uu <u>h</u>att<u>aa</u> ya/tiya <strong>al</strong>l<u>aa</u>hu bi-amrihi wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Katakanlah, “Jika bapak-bapakmu, anak-anakmu, saudara-saudaramu, istri-istrimu, keluargamu, harta kekayaan yang kamu usahakan, perdagangan yang kamu khawatirkan kerugiannya, dan rumah-rumah tempat tinggal yang kamu sukai, lebih kamu cintai dari pada Allah dan Rasul-Nya serta berjihad di jalan-Nya, maka tunggulah sampai Allah memberikan keputusan-Nya.” Dan Allah tidak memberi petunjuk kepada orang-orang fasik.'),
	(1260, 9, 25, 'لَقَدْ نَصَرَكُمُ اللّٰهُ فِيْ مَوَاطِنَ كَثِيْرَةٍۙ وَّيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـًٔا وَّضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُّدْبِرِيْنَۚ', 'laqad na<u>sh</u>arakumu <strong>al</strong>l<u>aa</u>hu fii maw<u>aath</u>ina katsiiratin wayawma <u>h</u>unaynin i<u>dz</u> a\'jabatkum katsratukum falam tughni \'ankum syay-an wa<u>daa</u>qat \'alaykumu <strong>a</strong>l-ar<u>dh</u>u bim<u>aa</u> ra<u>h</u>ubat tsumma wallaytum mudbiriin<strong>a</strong>', 'Sungguh, Allah telah menolong kamu (mukminin) di banyak medan perang, dan (ingatlah) Perang Hunain, ketika jumlahmu yang besar itu membanggakan kamu, tetapi (jumlah yang banyak itu) sama sekali tidak berguna bagimu, dan bumi yang luas itu terasa sempit bagimu, kemudian kamu berbalik ke belakang dan lari tunggang-langgang.'),
	(1261, 9, 26, 'ثُمَّ اَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَنْزَلَ جُنُوْدًا لَّمْ تَرَوْهَا وَعَذَّبَ الَّذِيْنَ كَفَرُوْاۗ وَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ ', 'tsumma anzala <strong>al</strong>l<u>aa</u>hu sakiinatahu \'al<u>aa</u> rasuulihi wa\'al<u>aa</u> <strong>a</strong>lmu/miniina wa-anzala junuudan lam tarawh<u>aa</u> wa\'a<u>dzdz</u>aba <strong>al</strong>la<u>dz</u>iina kafaruu wa<u>dzaa</u>lika jaz<u>aa</u>u <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Kemudian Allah menurunkan ketenangan kepada Rasul-Nya dan kepada orang-orang yang beriman, dan Dia menurunkan bala tentara (para malaikat) yang tidak terlihat olehmu, dan Dia menimpakan azab kepada orang-orang kafir. Itulah balasan bagi orang-orang kafir.'),
	(1262, 9, 27, 'ثُمَّ يَتُوْبُ اللّٰهُ مِنْۢ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ', 'tsumma yatuubu <strong>al</strong>l<u>aa</u>hu min ba\'di <u>dzaa</u>lika \'al<u>aa</u> man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Setelah itu Allah menerima tobat orang yang Dia kehendaki. Allah Maha Pengampun, Maha Penyayang.'),
	(1263, 9, 28, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْمُشْرِكُوْنَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَا ۚوَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيْكُمُ اللّٰهُ مِنْ فَضْلِهٖٓ اِنْ شَاۤءَۗ اِنَّ اللّٰهَ عَلِيْمٌ حَكِيْمٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu innam<u>aa</u> <strong>a</strong>lmusyrikuuna najasun fal<u>aa</u> yaqrabuu <strong>a</strong>lmasjida <strong>a</strong>l<u>h</u>ar<u>aa</u>ma ba\'da \'<u>aa</u>mihim h<u>aadzaa</u> wa-in khiftum \'aylatan fasawfa yughniikumu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi in sy<u>aa</u>-a inna <strong>al</strong>l<u>aa</u>ha \'aliimun <u>h</u>akiim<strong>un</strong>', 'Wahai orang-orang yang beriman! Sesungguhnya orang-orang musyrik itu najis (kotor jiwa), karena itu janganlah mereka mendekati Masjidilharam setelah tahun ini. Dan jika kamu khawatir menjadi miskin (karena orang kafir tidak datang), maka Allah nanti akan memberikan kekayaan kepadamu dari karunia-Nya, jika Dia menghendaki. Sesungguhnya Allah Maha Mengetahui, Mahabijaksana.'),
	(1264, 9, 29, 'قَاتِلُوا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُوْنَ مَا حَرَّمَ اللّٰهُ وَرَسُوْلُهٗ وَلَا يَدِيْنُوْنَ دِيْنَ الْحَقِّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَّدٍ وَّهُمْ صَاغِرُوْ', 'q<u>aa</u>tiluu <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wal<u>aa</u> bi<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wal<u>aa</u> yu<u>h</u>arrimuuna m<u>aa</u> <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu warasuuluhu wal<u>aa</u> yadiinuuna diina <strong>a</strong>l<u>h</u>aqqi mina <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>lkit<u>aa</u>ba <u>h</u>att<u>aa</u> yu\'<u>th</u>uu <strong>a</strong>ljizyata \'an yadin wahum <u>shaa</u>ghiruun<strong>a</strong>', 'Perangilah orang-orang yang tidak beriman kepada Allah dan hari kemudian, mereka yang tidak mengharamkan apa yang telah diharamkan Allah dan Rasul-Nya dan mereka yang tidak beragama dengan agama yang benar (agama Allah), (yaitu orang-orang) yang telah diberikan Kitab, hingga mereka membayar jizyah (pajak) dengan patuh sedang mereka dalam keadaan tunduk.'),
	(1265, 9, 30, 'وَقَالَتِ الْيَهُوْدُ عُزَيْرُ ِۨابْنُ اللّٰهِ وَقَالَتِ النَّصٰرَى الْمَسِيْحُ ابْنُ اللّٰهِ ۗذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِـُٔوْنَ قَوْلَ الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۗقَاتَلَهُمُ اللّٰهُ ۚ اَنّٰى يُؤْفَكُوْنَ ', 'waq<u>aa</u>lati <strong>a</strong>lyahuudu \'uzayrun ibnu <strong>al</strong>l<u>aa</u>hi waq<u>aa</u>lati <strong>al</strong>nna<u>shaa</u>r<u>aa</u> <strong>a</strong>lmasii<u>h</u>u ibnu <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>lika qawluhum bi-afw<u>aa</u>hihim yu<u>daa</u>hi-uuna qawla <strong>al</strong>la<u>dz</u>iina kafaruu min qablu q<u>aa</u>talahumu <strong>al</strong>l<u>aa</u>hu ann<u>aa</u> yu/fakuun<strong>a</strong>', 'Dan orang-orang Yahudi berkata, “Uzair putra Allah,” dan orang-orang Nasrani  berkata, “Al-Masih putra Allah.” Itulah ucapan yang keluar dari mulut mereka. Mereka meniru ucapan orang-orang kafir yang terdahulu. Allah melaknat mereka; bagaimana mereka sampai  berpaling?'),
	(1266, 9, 31, 'اِتَّخَذُوْٓا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ وَالْمَسِيْحَ ابْنَ مَرْيَمَۚ وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوْٓا اِلٰهًا وَّاحِدًاۚ  لَآ اِلٰهَ اِلَّا هُوَۗ سُبْحٰنَهٗ عَمَّا يُشْرِكُوْنَ ', 'ittakha<u>dz</u>uu a<u>h</u>b<u>aa</u>rahum waruhb<u>aa</u>nahum arb<u>aa</u>ban min duuni <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmasii<u>h</u>a ibna maryama wam<u>aa</u> umiruu ill<u>aa</u> liya\'buduu il<u>aa</u>han w<u>aah</u>idan l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa sub<u>haa</u>nahu \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Mereka menjadikan orang-orang alim (Yahudi), dan rahib-rahibnya (Nasrani) sebagai tuhan selain Allah, dan (juga) Al-Masih putra Maryam; padahal mereka hanya disuruh menyembah Tuhan Yang Maha Esa; tidak ada tuhan selain Dia. Mahasuci Dia dari apa yang mereka persekutukan.'),
	(1267, 9, 32, 'يُرِيْدُوْنَ اَنْ يُّطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّآ اَنْ يُّتِمَّ نُوْرَهٗ وَلَوْ كَرِهَ الْكٰفِرُوْنَ ', 'yuriiduuna an yu<u>th</u>fi-uu nuura <strong>al</strong>l<u>aa</u>hi bi-afw<u>aa</u>hihim waya/b<u>aa</u> <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> an yutimma nuurahu walaw kariha <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Mereka hendak memadamkan cahaya (agama) Allah dengan mulut (ucapan-ucapan) mereka, tetapi Allah menolaknya, malah berkehendak menyempurnakan cahaya-Nya, walaupun orang-orang kafir itu tidak menyukai.'),
	(1268, 9, 33, 'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ ', 'huwa <strong>al</strong>la<u>dz</u>ii arsala rasuulahu bi<strong>a</strong>lhud<u>aa</u> wadiini <strong>a</strong>l<u>h</u>aqqi liyu<u>zh</u>hirahu \'al<u>aa</u> <strong>al</strong>ddiini kullihi walaw kariha <strong>a</strong>lmusyrikuun<strong>a</strong>', 'Dialah yang telah mengutus Rasul-Nya dengan petunjuk (Al-Qur\'an) dan agama yang benar untuk diunggulkan atas segala agama, walaupun orang-orang musyrik tidak menyukai.'),
	(1269, 9, 34, '۞ يٰٓاَيُّهَا الَّذِينَ اٰمَنُوْٓا اِنَّ كَثِيْرًا مِّنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُوْنَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَالَّذِيْنَ يَكْنِزُوْنَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُوْنَهَا فِيْ سَبِيْلِ ا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu inna katsiiran mina <strong>a</strong>l-a<u>h</u>b<u>aa</u>ri wa<strong>al</strong>rruhb<u>aa</u>ni laya/kuluuna amw<u>aa</u>la <strong>al</strong>nn<u>aa</u>si bi<strong>a</strong>lb<u>aath</u>ili waya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lla<u>dz</u>iina yaknizuuna <strong>al</strong><u>dzdz</u>ahaba wa<strong>a</strong>lfi<u>dhdh</u>ata wal<u>aa</u> yunfiquunah<u>aa</u> fii sabiili <strong>al</strong>l<u>aa</u>hi fabasysyirhum bi\'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Wahai orang-orang yang beriman! Sesungguhnya banyak dari orang-orang alim dan rahib-rahib mereka benar-benar memakan harta orang dengan jalan yang batil, dan (mereka) menghalang-halangi (manusia) dari jalan Allah. Dan orang-orang yang menyimpan emas dan perak dan tidak menginfakkannya di jalan Allah, maka berikanlah kabar gembira kepada mereka, (bahwa mereka akan mendapat) azab yang pedih.'),
	(1270, 9, 35, 'يَّوْمَ يُحْمٰى عَلَيْهَا فِيْ نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوْبُهُمْ وَظُهُوْرُهُمْۗ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوْقُوْا مَا كُنْتُمْ تَكْنِزُوْنَ ', 'yawma yu<u>h</u>m<u>aa</u> \'alayh<u>aa</u> fii n<u>aa</u>ri jahannama fatukw<u>aa</u> bih<u>aa</u> jib<u>aa</u>huhum wajunuubuhum wa<u>zh</u>uhuuruhum h<u>aadzaa</u> m<u>aa</u> kanaztum li-anfusikum fa<u>dz</u>uuquu m<u>aa</u> kuntum taknizuun<strong>a</strong>', '(Ingatlah) pada hari ketika emas dan perak dipanaskan dalam neraka Jahanam, lalu dengan itu disetrika dahi, lambung dan punggung mereka (seraya dikatakan) kepada mereka, “Inilah harta bendamu yang kamu simpan untuk dirimu sendiri, maka rasakanlah (akibat dari) apa yang kamu simpan itu.”'),
	(1271, 9, 36, 'اِنَّ عِدَّةَ الشُّهُوْرِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْرًا فِيْ كِتٰبِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ مِنْهَآ اَرْبَعَةٌ حُرُمٌ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ ەۙ فَلَا تَظْلِمُوْا فِيْهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِكِيْنَ كَاۤفّ', 'inna \'iddata <strong>al</strong>sysyuhuuri \'inda <strong>al</strong>l<u>aa</u>hi itsn<u>aa</u> \'asyara syahran fii kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi yawma khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a minh<u>aa</u> arba\'atun <u>h</u>urumun <u>dzaa</u>lika <strong>al</strong>ddiinu <strong>a</strong>lqayyimu fal<u>aa</u> ta<u>zh</u>limuu fiihinna anfusakum waq<u>aa</u>tiluu <strong>a</strong>lmusyrikiina k<u>aa</u>ffatan kam<u>aa</u> yuq<u>aa</u>tiluunakum k<u>aa</u>ffatan wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Sesungguhnya jumlah bulan menurut Allah ialah dua belas bulan, (sebagaimana) dalam ketetapan Allah pada waktu Dia menciptakan langit dan bumi, di antaranya ada empat bulan haram. Itulah (ketetapan) agama yang lurus, maka janganlah kamu menzalimi dirimu dalam (bulan yang empat) itu, dan perangilah kaum musyrikin semuanya sebagaimana mereka pun memerangi kamu semuanya. Dan ketahuilah bahwa Allah beserta orang-orang yang takwa.'),
	(1272, 9, 37, 'اِنَّمَا النَّسِيْۤءُ زِيَادَةٌ فِى الْكُفْرِ يُضَلُّ بِهِ الَّذِيْنَ كَفَرُوْا يُحِلُّوْنَهٗ عَامًا وَّيُحَرِّمُوْنَهٗ عَامًا لِّيُوَاطِـُٔوْا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوْا مَا حَرَّمَ اللّٰهُ ۗزُيِّنَ لَهُمْ سُوْۤءُ اَعْمَالِهِمْۗ وَاللّٰهُ ل', 'innam<u>aa</u> <strong>al</strong>nnasii-u ziy<u>aa</u>datun fii <strong>a</strong>lkufri yu<u>dh</u>allu bihi <strong>al</strong>la<u>dz</u>iina kafaruu yu<u>h</u>illuunahu \'<u>aa</u>man wayu<u>h</u>arrimuunahu \'<u>aa</u>man liyuw<u>aath</u>i-uu \'iddata m<u>aa</u> <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu fayu<u>h</u>illuu m<u>aa</u> <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu zuyyina lahum suu-u a\'m<u>aa</u>lihim wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Sesungguhnya pengunduran (bulan haram) itu hanya menambah kekafiran. Orang-orang kafir disesatkan dengan (pengunduran) itu, mereka menghalalkannya suatu tahun dan mengharamkannya pada suatu tahun yang lain, agar mereka dapat menyesuaikan dengan bilangan yang diharamkan Allah, sekaligus mereka menghalalkan apa yang diharamkan Allah. (Setan) dijadikan terasa indah bagi mereka perbuatan-perbuatan buruk mereka. Dan Allah tidak memberi petunjuk kepada orang-orang yang kafir.'),
	(1273, 9, 38, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَا لَكُمْ اِذَا قِيْلَ لَكُمُ انْفِرُوْا فِيْ سَبِيْلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۗ اَرَضِيْتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا قَلِيْلٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu m<u>aa</u> lakum i<u>dzaa</u> qiila lakumu infiruu fii sabiili <strong>al</strong>l<u>aa</u>hi itsts<u>aa</u>qaltum il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i ara<u>dh</u>iitum bi<strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> mina <strong>a</strong>l-<u>aa</u>khirati fam<u>aa</u> mat<u>aa</u>\'u <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> fii <strong>a</strong>l-<u>aa</u>khirati ill<u>aa</u> qaliil<strong>un</strong>', 'Wahai orang-orang yang beriman! Mengapa apabila dikatakan kepada kamu, “Berangkatlah (untuk berperang) di jalan Allah,” kamu merasa berat dan ingin tinggal di tempatmu? Apakah kamu lebih menyenangi kehidupan di dunia daripada kehidupan di akhirat? Padahal kenikmatan hidup di dunia ini (dibandingkan dengan kehidupan) di akhirat hanyalah sedikit.'),
	(1274, 9, 39, 'اِلَّا تَنْفِرُوْا يُعَذِّبْكُمْ عَذَابًا اَلِيمًاۙ وَّيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوْهُ شَيْـًٔاۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'ill<u>aa</u> tanfiruu yu\'a<u>dzdz</u>ibkum \'a<u>dzaa</u>ban <strong>a</strong>liiman wayastabdil qawman ghayrakum wal<u>aa</u> ta<u>dh</u>urruuhu syay-an wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Jika kamu tidak berangkat (untuk berperang), niscaya Allah akan menghukum kamu dengan azab yang pedih dan menggantikan kamu dengan kaum yang lain, dan kamu tidak akan merugikan-Nya sedikit pun. Dan Allah Mahakuasa atas segala sesuatu.'),
	(1275, 9, 40, 'اِلَّا تَنْصُرُوْهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذِيْنَ كَفَرُوْا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِى الْغَارِ اِذْ يَقُوْلُ لِصَاحِبِهٖ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلَيْهِ وَاَيَّدَهٗ بِجُنُوْدٍ لَّمْ ', 'ill<u>aa</u> tan<u>sh</u>uruuhu faqad na<u>sh</u>arahu <strong>al</strong>l<u>aa</u>hu i<u>dz</u> akhrajahu <strong>al</strong>la<u>dz</u>iina kafaruu ts<u>aa</u>niya itsnayni i<u>dz</u> hum<u>aa</u> fii <strong>a</strong>lgh<u>aa</u>ri i<u>dz</u> yaquulu li<u>shaah</u>ibihi l<u>aa</u> ta<u>h</u>zan inna <strong>al</strong>l<u>aa</u>ha ma\'an<u>aa</u> fa-anzala <strong>al</strong>l<u>aa</u>hu sakiinatahu \'alayhi wa-ayyadahu bijunuudin lam tarawh<u>aa</u> waja\'ala kalimata <strong>al</strong>la<u>dz</u>iina kafaruu <strong>al</strong>ssufl<u>aa</u> wakalimatu <strong>al</strong>l<u>aa</u>hi hiya <strong>a</strong>l\'uly<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu \'aziizun <u>h</u>akiim<strong>un</strong>', 'Jika kamu tidak menolongnya (Muhammad), sesungguhnya Allah telah menolongnya (yaitu) ketika orang-orang kafir mengusirnya (dari Mekah); sedang dia salah seorang dari dua orang ketika keduanya berada dalam gua, ketika itu dia berkata kepada sahabatnya, “Jangan engkau bersedih, sesungguhnya Allah bersama kita.” Maka Allah menurunkan ketenangan kepadanya (Muhammad) dan membantu dengan bala tentara (malaikat-malaikat) yang tidak terlihat olehmu, dan Dia menjadikan seruan orang-orang kafir itu rendah. Dan firman Allah itulah yang tinggi. Allah Mahaperkasa, Mahabijaksana.'),
	(1276, 9, 41, 'اِنْفِرُوْا خِفَافًا وَّثِقَالًا وَّجَاهِدُوْا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ فِيْ سَبِيْلِ اللّٰهِ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ', 'infiruu khif<u>aa</u>fan watsiq<u>aa</u>lan waj<u>aa</u>hiduu bi-amw<u>aa</u>likum wa-anfusikum fii sabiili <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>likum khayrun lakum in kuntum ta\'lamuun<strong>a</strong>', 'Berangkatlah kamu baik dengan rasa ringan maupun dengan rasa berat, dan berjihadlah dengan harta dan jiwamu di jalan Allah. Yang demikian itu adalah lebih baik bagimu jika kamu mengetahui.'),
	(1277, 9, 42, 'لَوْ كَانَ عَرَضًا قَرِيْبًا وَّسَفَرًا قَاصِدًا لَّاتَّبَعُوْكَ وَلٰكِنْۢ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۗ وَسَيَحْلِفُوْنَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُوْنَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكٰذِبُوْنَ ࣖ ', 'law k<u>aa</u>na \'ara<u>dh</u>an qariiban wasafaran q<u>aas</u>idan la<strong>i</strong>ttaba\'uuka wal<u>aa</u>kin ba\'udat \'alayhimu <strong>al</strong>sysyuqqatu wasaya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi lawi ista<u>th</u>a\'n<u>aa</u> lakharajn<u>aa</u> ma\'akum yuhlikuuna anfusahum wa<strong>al</strong>l<u>aa</u>hu ya\'lamu innahum lak<u>aadz</u>ibuun<strong>a</strong>', 'Sekiranya (yang kamu serukan kepada mereka) ada keuntungan yang mudah diperoleh dan perjalanan yang tidak seberapa jauh, niscaya mereka mengikutimu, tetapi tempat yang dituju itu terasa sangat jauh bagi mereka. Mereka akan bersumpah dengan (nama) Allah, “Jikalau kami sanggup niscaya kami berangkat bersamamu.” Mereka membinasakan diri sendiri dan Allah mengetahui bahwa mereka benar-benar orang-orang yang berdusta.'),
	(1278, 9, 43, 'عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذِيْنَ صَدَقُوْا وَتَعْلَمَ الْكٰذِبِيْنَ ', '\'af<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'anka lima a<u>dz</u>inta lahum <u>h</u>att<u>aa</u> yatabayyana laka <strong>al</strong>la<u>dz</u>iina <u>sh</u>adaquu wata\'lama <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Allah memaafkanmu (Muhammad). Mengapa engkau memberi izin kepada mereka (untuk tidak pergi berperang), sebelum jelas bagimu orang-orang yang benar-benar (berhalangan) dan sebelum engkau mengetahui orang-orang yang berdusta?'),
	(1279, 9, 44, 'لَا يَسْتَأْذِنُكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالْمُتَّقِيْنَ ', 'l<u>aa</u> yasta/<u>dz</u>inuka <strong>al</strong>la<u>dz</u>iina yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri an yuj<u>aa</u>hiduu bi-amw<u>aa</u>lihim wa-anfusihim wa<strong>al</strong>l<u>aa</u>hu \'aliimun bi<strong>a</strong>lmuttaqiin<strong>a</strong>', 'Orang-orang yang beriman kepada Allah dan hari kemudian, tidak akan meminta izin (tidak ikut) kepadamu untuk berjihad dengan harta dan jiwa mereka. Allah mengetahui orang-orang yang bertakwa.'),
	(1280, 9, 45, 'اِنَّمَا يَسْتَأْذِنُكَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوْبُهُمْ فَهُمْ فِيْ رَيْبِهِمْ يَتَرَدَّدُوْنَ ', 'innam<u>aa</u> yasta/<u>dz</u>inuka <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wa<strong>i</strong>rt<u>aa</u>bat quluubuhum fahum fii raybihim yataraddaduun<strong>a</strong>', 'Sesungguhnya yang akan meminta izin kepadamu (Muhammad), hanyalah orang-orang yang tidak beriman kepada Allah dan hari kemudian, dan hati mereka ragu, karena itu mereka selalu bimbang  dalam keraguan.'),
	(1281, 9, 46, '۞ وَلَوْ اَرَادُوا الْخُرُوْجَ لَاَعَدُّوْا لَهٗ عُدَّةً وَّلٰكِنْ كَرِهَ اللّٰهُ انْۢبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيْلَ اقْعُدُوْا مَعَ الْقٰعِدِيْنَ ', 'walaw ar<u>aa</u>duu <strong>a</strong>lkhuruuja la-a\'adduu lahu \'uddatan wal<u>aa</u>kin kariha <strong>al</strong>l<u>aa</u>hu inbi\'<u>aa</u>tsahum fatsabba<u>th</u>ahum waqiila uq\'uduu ma\'a <strong>a</strong>lq<u>aa</u>\'idiin<strong>a</strong>', 'Dan jika mereka mau berangkat, niscaya mereka menyiapkan persiapan untuk keberangkatan itu, tetapi Allah tidak menyukai keberangkatan mereka, maka Dia melemahkan keinginan mereka, dan dikatakan (kepada mereka), “Tinggallah kamu bersama orang-orang yang tinggal itu.”'),
	(1282, 9, 47, 'لَوْ خَرَجُوْا فِيْكُمْ مَّا زَادُوْكُمْ اِلَّا خَبَالًا وَّلَاَوْضَعُوْا خِلٰلَكُمْ يَبْغُوْنَكُمُ الْفِتْنَةَۚ وَفِيْكُمْ سَمّٰعُوْنَ لَهُمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ ', 'law kharajuu fiikum m<u>aa</u> z<u>aa</u>duukum ill<u>aa</u> khab<u>aa</u>lan wala-aw<u>dh</u>a\'uu khil<u>aa</u>lakum yabghuunakumu <strong>a</strong>lfitnata wafiikum samm<u>aa</u>\'uuna lahum wa<strong>al</strong>l<u>aa</u>hu \'aliimun bi<strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Jika (mereka berangkat bersamamu), niscaya mereka tidak akan menambah (kekuatan)mu, malah hanya akan membuat kekacauan, dan mereka tentu bergegas maju ke depan di celah-celah barisanmu untuk mengadakan kekacauan (di barisanmu); sedang di antara kamu ada orang-orang yang sangat suka mendengarkan (perkataan) mereka. Allah mengetahui orang-orang yang zalim.'),
	(1283, 9, 48, 'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوْا لَكَ الْاُمُوْرَ حَتّٰى جَاۤءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كٰرِهُوْنَ ', 'laqadi ibtaghawuu <strong>a</strong>lfitnata min qablu waqallabuu laka <strong>a</strong>l-umuura <u>h</u>att<u>aa</u> j<u>aa</u>-a <strong>a</strong>l<u>h</u>aqqu wa<u>zh</u>ahara amru <strong>al</strong>l<u>aa</u>hi wahum k<u>aa</u>rihuun<strong>a</strong>', 'Sungguh, sebelum itu mereka memang sudah berusaha membuat kekacauan dan mengatur berbagai macam tipu daya bagimu (memutarbalikkan persoalan), hingga datanglah kebenaran (pertolongan Allah), dan menanglah urusan (agama) Allah, padahal mereka tidak menyukainya.'),
	(1284, 9, 49, 'وَمِنْهُمْ مَّنْ يَّقُوْلُ ائْذَنْ لِّيْ وَلَا تَفْتِنِّيْۗ  اَلَا فِى الْفِتْنَةِ سَقَطُوْاۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَ ', 'waminhum man yaquulu i/<u>dz</u>an lii wal<u>aa</u> taftinnii <strong>a</strong>l<u>aa</u> fii <strong>a</strong>lfitnati saqa<u>th</u>uu wa-inna jahannama lamu<u>h</u>ii<u>th</u>atun bi<strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan di antara mereka ada orang yang berkata, “Berilah aku izin (tidak pergi berperang) dan janganlah engkau (Muhammad) menjadikan aku terjerumus ke dalam fitnah.” Ketahuilah, bahwa mereka telah terjerumus ke dalam fitnah. Dan sungguh, Jahanam meliputi orang-orang yang kafir.'),
	(1285, 9, 50, 'اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُصِيْبَةٌ يَّقُوْلُوْا قَدْ اَخَذْنَآ اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَّهُمْ فَرِحُوْنَ ', 'in tu<u>sh</u>ibka <u>h</u>asanatun tasu/hum wa-in tu<u>sh</u>ibka mu<u>sh</u>iibatun yaquuluu qad akha<u>dz</u>n<u>aa</u> amran<u>aa</u> min qablu wayatawallaw wahum fari<u>h</u>uun<strong>a</strong>', 'Jika engkau (Muhammad) mendapat kebaikan, mereka tidak senang; tetapi jika engkau ditimpa bencana, mereka berkata, “Sungguh, sejak semula kami telah berhati-hati (tidak pergi berperang),” dan mereka berpaling dengan (perasaan) gembira.'),
	(1286, 9, 51, 'قُلْ لَّنْ يُّصِيْبَنَآ اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰىنَا وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ', 'qul lan yu<u>sh</u>iiban<u>aa</u> ill<u>aa</u> m<u>aa</u> kataba <strong>al</strong>l<u>aa</u>hu lan<u>aa</u> huwa mawl<u>aa</u>n<u>aa</u> wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmu/minuun<strong>a</strong>', 'Katakanlah (Muhammad), “Tidak akan menimpa kami melainkan apa yang telah ditetapkan Allah bagi kami. Dialah pelindung kami, dan hanya kepada Allah bertawakallah orang-orang yang beriman.”'),
	(1287, 9, 52, 'قُلْ هَلْ تَرَبَّصُوْنَ بِنَآ اِلَّآ اِحْدَى الْحُسْنَيَيْنِۗ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُّصِيْبَكُمُ اللّٰهُ بِعَذَابٍ مِّنْ عِنْدِهٖٓ اَوْ بِاَيْدِيْنَاۖ فَتَرَبَّصُوْٓا اِنَّا مَعَكُمْ مُّتَرَبِّصُوْنَ ', 'qul hal tarabba<u>sh</u>uuna bin<u>aa</u> ill<u>aa</u> i<u>h</u>d<u>aa</u> <strong>a</strong>l<u>h</u>usnayayni wana<u>h</u>nu natarabba<u>sh</u>u bikum an yu<u>sh</u>iibakumu <strong>al</strong>l<u>aa</u>hu bi\'a<u>dzaa</u>bin min \'indihi aw bi-aydiin<u>aa</u> fatarabba<u>sh</u>uu inn<u>aa</u> ma\'akum mutarabbi<u>sh</u>uun<strong>a</strong>', 'Katakanlah (Muhammad), “Tidak ada yang kamu tunggu-tunggu bagi kami, kecuali salah satu dari dua kebaikan (menang atau mati syahid). Dan kami menunggu-nunggu bagi kamu bahwa Allah akan menimpakan azab kepadamu dari sisi-Nya, atau (azab) melalui tangan kami. Maka tunggulah, sesungguhnya kami menunggu (pula) bersamamu.”'),
	(1288, 9, 53, 'قُلْ اَنْفِقُوْا طَوْعًا اَوْ كَرْهًا لَّنْ يُّتَقَبَّلَ مِنْكُمْ ۗاِنَّكُمْ كُنْتُمْ قَوْمًا فٰسِقِيْنَ ', 'qul anfiquu <u>th</u>aw\'an aw karhan lan yutaqabbala minkum innakum kuntum qawman f<u>aa</u>siqiin<strong>a</strong>', 'Katakanlah (Muhammad), “Infakkanlah hartamu baik dengan sukarela maupun dengan terpaksa, namun (infakmu) tidak akan diterima. Sesungguhnya kamu adalah orang-orang yang fasik.”'),
	(1289, 9, 54, 'وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقٰتُهُمْ اِلَّآ اَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَبِرَسُوْلِهٖ وَلَا يَأْتُوْنَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُوْنَ اِلَّا وَهُمْ كٰرِهُوْنَ ', 'wam<u>aa</u> mana\'ahum an tuqbala minhum nafaq<u>aa</u>tuhum ill<u>aa</u> annahum kafaruu bi<strong>al</strong>l<u>aa</u>hi wabirasuulihi wal<u>aa</u> ya/tuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta ill<u>aa</u> wahum kus<u>aa</u>l<u>aa</u> wal<u>aa</u> yunfiquuna ill<u>aa</u> wahum k<u>aa</u>rihuun<strong>a</strong>', 'Dan yang menghalang-halangi infak mereka untuk diterima adalah karena mereka kafir (ingkar) kepada Allah dan Rasul-Nya dan mereka tidak melaksanakan salat, melainkan dengan malas dan tidak (pula) menginfakkan (harta) mereka, melainkan dengan rasa enggan (terpaksa).'),
	(1290, 9, 55, 'فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِى الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ', 'fal<u>aa</u> tu\'jibka amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum innam<u>aa</u> yuriidu <strong>al</strong>l<u>aa</u>hu liyu\'a<u>dzdz</u>ibahum bih<u>aa</u> fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> watazhaqa anfusuhum wahum k<u>aa</u>firuun<strong>a</strong>', 'Maka janganlah harta dan anak-anak mereka membuatmu kagum. Sesungguhnya maksud Allah dengan itu adalah untuk menyiksa mereka dalam kehidupan dunia dan kelak akan mati dalam keadaan kafir.'),
	(1291, 9, 56, 'وَيَحْلِفُوْنَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۗ وَمَا هُمْ مِّنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَّفْرَقُوْنَ ', 'waya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi innahum laminkum wam<u>aa</u> hum minkum wal<u>aa</u>kinnahum qawmun yafraquun<strong>a</strong>', 'Dan mereka (orang-orang munafik) bersumpah dengan (nama) Allah, bahwa sesungguhnya mereka termasuk golonganmu; namun mereka bukanlah dari golonganmu, tetapi mereka orang-orang yang sangat takut (kepadamu).'),
	(1292, 9, 57, 'لَوْ يَجِدُوْنَ مَلْجَاً اَوْ مَغٰرٰتٍ اَوْ مُدَّخَلًا لَّوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُوْنَ ', 'law yajiduuna malja-an aw magh<u>aa</u>r<u>aa</u>tin aw muddakhalan lawallaw ilayhi wahum yajma<u>h</u>uun<strong>a</strong>', 'Sekiranya mereka memperoleh tempat perlindungan, gua-gua atau lubang-lubang (dalam tanah), niscaya mereka pergi (lari) ke sana dengan secepat-cepatnya.'),
	(1293, 9, 58, 'وَمِنْهُمْ مَّنْ يَّلْمِزُكَ فِى الصَّدَقٰتِۚ فَاِنْ اُعْطُوْا مِنْهَا رَضُوْا وَاِنْ لَّمْ يُعْطَوْا مِنْهَآ اِذَا هُمْ يَسْخَطُوْنَ ', 'waminhum man yalmizuka fii <strong>al</strong><u>shsh</u>adaq<u>aa</u>ti fa-in u\'<u>th</u>uu minh<u>aa</u> ra<u>dh</u>uu wa-in lam yu\'<u>th</u>aw minh<u>aa</u> i<u>dzaa</u> hum yaskha<u>th</u>uun<strong>a</strong>', 'Dan di antara mereka ada yang mencelamu tentang (pembagian) sedekah (zakat); jika mereka diberi bagian, mereka bersenang hati, dan jika mereka tidak diberi bagian, tiba-tiba mereka marah.'),
	(1294, 9, 59, 'وَلَوْ اَنَّهُمْ رَضُوْا مَآ اٰتٰىهُمُ اللّٰهُ وَرَسُوْلُهٗۙ وَقَالُوْا حَسْبُنَا اللّٰهُ سَيُؤْتِيْنَا اللّٰهُ مِنْ فَضْلِهٖ وَرَسُوْلُهٗٓ اِنَّآ اِلَى اللّٰهِ رَاغِبُوْنَ ࣖ ', 'walaw annahum ra<u>dh</u>uu m<u>aa</u> <u>aa</u>t<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu warasuuluhu waq<u>aa</u>luu <u>h</u>asbun<u>aa</u> <strong>al</strong>l<u>aa</u>hu sayu/tiin<u>aa</u> <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi warasuuluhu inn<u>aa</u> il<u>aa</u> <strong>al</strong>l<u>aa</u>hi r<u>aa</u>ghibuun<strong>a</strong>', 'Dan sekiranya mereka benar-benar rida dengan apa yang diberikan kepada mereka oleh Allah dan Rasul-Nya, dan berkata, “Cukuplah Allah bagi kami, Allah dan Rasul-Nya akan memberikan kepada kami sebagian dari karunia-Nya. Sesungguhnya kami orang-orang yang berharap kepada Allah.”'),
	(1295, 9, 60, '۞ اِنَّمَا الصَّدَقٰتُ لِلْفُقَرَاۤءِ وَالْمَسٰكِيْنِ وَالْعَامِلِيْنَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوْبُهُمْ وَفِى الرِّقَابِ وَالْغَارِمِيْنَ وَفِيْ سَبِيْلِ اللّٰهِ وَابْنِ السَّبِيْلِۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ', 'innam<u>aa</u> <strong>al</strong><u>shsh</u>adaq<u>aa</u>tu lilfuqar<u>aa</u>-i wa<strong>a</strong>lmas<u>aa</u>kiini wa<strong>a</strong>l\'<u>aa</u>miliina \'alayh<u>aa</u> wa<strong>a</strong>lmu-allafati quluubuhum wafii <strong>al</strong>rriq<u>aa</u>bi wa<strong>a</strong>lgh<u>aa</u>rimiina wafii sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>i</strong>bni <strong>al</strong>ssabiili farii<u>dh</u>atan mina <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiimun', 'Sesungguhnya zakat itu hanyalah untuk orang-orang fakir, orang miskin, amil zakat, yang dilunakkan hatinya (mualaf), untuk (memerdekakan) hamba sahaya, untuk (membebaskan) orang yang berutang, untuk jalan Allah dan untuk orang yang sedang dalam perjalanan, sebagai kewajiban dari Allah. Allah Maha Mengetahui, Mahabijaksana.'),
	(1296, 9, 61, 'وَمِنْهُمُ الَّذِيْنَ يُؤْذُوْنَ النَّبِيَّ وَيَقُوْلُوْنَ هُوَ اُذُنٌ ۗقُلْ اُذُنُ خَيْرٍ لَّكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِنِيْنَ وَرَحْمَةٌ لِّلَّذِيْنَ اٰمَنُوْا مِنْكُمْۗ وَالَّذِيْنَ يُؤْذُوْنَ رَسُوْلَ اللّٰهِ لَهُمْ عَذَابٌ اَلِيْمٌ ', 'waminhumu <strong>al</strong>la<u>dz</u>iina yu/<u>dz</u>uuna <strong>al</strong>nnabiyya wayaquuluuna huwa u<u>dz</u>unun qul u<u>dz</u>unu khayrin lakum yu/minu bi<strong>al</strong>l<u>aa</u>hi wayu/minu lilmu/miniina wara<u>h</u>matun lilla<u>dz</u>iina <u>aa</u>manuu minkum wa<strong>a</strong>lla<u>dz</u>iina yu/<u>dz</u>uuna rasuula <strong>al</strong>l<u>aa</u>hi lahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan di antara mereka (orang munafik) ada orang-orang yang menyakiti hati Nabi  (Muhammad) dan mengatakan, “Nabi mempercayai semua apa yang didengarnya.” Katakanlah, “Dia mempercayai semua yang baik bagi kamu, dia beriman kepada Allah, mempercayai orang-orang mukmin, dan menjadi rahmat bagi orang-orang yang beriman di antara kamu.” Dan orang-orang yang menyakiti Rasulullah akan mendapat azab yang pedih.'),
	(1297, 9, 62, 'يَحْلِفُوْنَ بِاللّٰهِ لَكُمْ لِيُرْضُوْكُمْ وَاللّٰهُ وَرَسُوْلُهٗٓ اَحَقُّ اَنْ يُّرْضُوْهُ اِنْ كَانُوْا مُؤْمِنِيْنَ ', 'ya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi lakum liyur<u>dh</u>uukum wa<strong>al</strong>l<u>aa</u>hu warasuuluhu a<u>h</u>aqqu an yur<u>dh</u>uuhu in k<u>aa</u>nuu mu/miniin<strong>a</strong>', 'Mereka bersumpah kepadamu dengan (nama) Allah untuk menyenangkan kamu, padahal Allah dan Rasul-Nya lebih pantas mereka mencari keridaan-Nya jika mereka orang mukmin.'),
	(1298, 9, 63, 'اَلَمْ يَعْلَمُوْٓا اَنَّهٗ مَنْ يُّحَادِدِ اللّٰهَ وَرَسُوْلَهٗ فَاَنَّ لَهٗ نَارَ جَهَنَّمَ خَالِدًا فِيْهَاۗ ذٰلِكَ الْخِزْيُ الْعَظِيْمُ ', 'alam ya\'lamuu annahu man yu<u>haa</u>didi <strong>al</strong>l<u>aa</u>ha warasuulahu fa-anna lahu n<u>aa</u>ra jahannama kh<u>aa</u>lidan fiih<u>aa</u> <u>dzaa</u>lika <strong>a</strong>lkhizyu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Tidakkah mereka (orang munafik) mengetahui bahwa barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya neraka Jahanamlah baginya, dia kekal di dalamnya. Itulah kehinaan yang besar.'),
	(1299, 9, 64, 'يَحْذَرُ الْمُنٰفِقُوْنَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُوْرَةٌ تُنَبِّئُهُمْ بِمَا فِيْ قُلُوْبِهِمْۗ قُلِ اسْتَهْزِءُوْاۚ اِنَّ اللّٰهَ مُخْرِجٌ مَّا تَحْذَرُوْنَ ', 'ya<u>hts</u>aru <strong>a</strong>lmun<u>aa</u>fiquuna an tunazzala \'alayhim suuratun tunabbi-uhum bim<u>aa</u> fii quluubihim quli istahzi-uu inna <strong>al</strong>l<u>aa</u>ha mukhrijun m<u>aa</u> ta<u>hts</u>aruun<strong>a</strong>', 'Orang-orang munafik itu takut jika diturunkan suatu surah yang menerangkan apa yang tersembunyi di dalam hati mereka. Katakanlah (kepada mereka), “Teruskanlah berolok-olok (terhadap Allah dan Rasul-Nya).” Sesungguhnya Allah akan mengungkapkan apa yang kamu takuti itu.'),
	(1300, 9, 65, 'وَلَىِٕنْ سَاَلْتَهُمْ لَيَقُوْلُنَّ اِنَّمَا كُنَّا نَخُوْضُ وَنَلْعَبُۗ  قُلْ اَبِاللّٰهِ وَاٰيٰتِهٖ وَرَسُوْلِهٖ كُنْتُمْ تَسْتَهْزِءُوْنَ ', 'wala-in sa-altahum layaquulunna innam<u>aa</u> kunn<u>aa</u> nakhuu<u>dh</u>u wanal\'abu qul abi<strong>al</strong>l<u>aa</u>hi wa<u>aa</u>y<u>aa</u>tihi warasuulihi kuntum tastahzi-uun<strong>a</strong>', 'Dan jika kamu tanyakan kepada mereka, niscaya mereka akan menjawab, “Sesungguhnya kami hanya bersenda gurau dan bermain-main saja.” Katakanlah, “Mengapa kepada Allah, dan ayat-ayat-Nya serta Rasul-Nya kamu selalu berolok-olok?”'),
	(1301, 9, 66, 'لَا تَعْتَذِرُوْا قَدْ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ ۗ اِنْ نَّعْفُ عَنْ طَاۤىِٕفَةٍ مِّنْكُمْ نُعَذِّبْ طَاۤىِٕفَةً ۢ بِاَنَّهُمْ كَانُوْا مُجْرِمِيْنَ ࣖ', 'l<u>aa</u> ta\'ta<u>dz</u>iruu qad kafartum ba\'da iim<u>aa</u>nikum in na\'fu \'an <u>th</u><u>aa</u>-ifatin minkum nu\'a<u>dzdz</u>ib <u>th</u><u>aa</u>-ifatan bi-annahum k<u>aa</u>nuu mujrimiin<strong>a</strong>', 'Tidak perlu kamu meminta maaf, karena kamu telah kafir setelah beriman. Jika Kami memaafkan sebagian dari kamu (karena telah tobat), niscaya Kami akan mengazab golongan (yang lain) karena sesungguhnya mereka adalah orang-orang yang (selalu) berbuat dosa.'),
	(1302, 9, 67, 'اَلْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ بَعْضُهُمْ مِّنْۢ بَعْضٍۘ يَأْمُرُوْنَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوْفِ وَيَقْبِضُوْنَ اَيْدِيَهُمْۗ نَسُوا اللّٰهَ فَنَسِيَهُمْ ۗ اِنَّ الْمُنٰفِقِيْنَ هُمُ الْفٰسِقُوْنَ ', 'almun<u>aa</u>fiquuna wa<strong>a</strong>lmun<u>aa</u>fiq<u>aa</u>tu ba\'<u>dh</u>uhum min ba\'<u>dh</u>in ya/muruuna bi<strong>a</strong>lmunkari wayanhawna \'ani <strong>a</strong>lma\'ruufi wayaqbi<u>dh</u>uuna aydiyahum nasuu <strong>al</strong>l<u>aa</u>ha fanasiyahum inna <strong>a</strong>lmun<u>aa</u>fiqiina humu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Orang-orang munafik laki-laki dan perempuan, satu dengan yang lain adalah (sama), mereka menyuruh (berbuat) yang mungkar dan mencegah (perbuatan) yang makruf dan mereka menggenggamkan tangannya (kikir). Mereka telah melupakan kepada Allah, maka Allah melupakan mereka (pula). Sesungguhnya orang-orang munafik itulah orang-orang yang fasik.'),
	(1303, 9, 68, 'وَعَدَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ هِيَ حَسْبُهُمْ ۚوَلَعَنَهُمُ اللّٰهُ ۚوَلَهُمْ عَذَابٌ مُّقِيْمٌۙ', 'wa\'ada <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmun<u>aa</u>fiqiina wa<strong>a</strong>lmun<u>aa</u>fiq<u>aa</u>ti wa<strong>a</strong>lkuff<u>aa</u>ra n<u>aa</u>ra jahannama kh<u>aa</u>lidiina fiih<u>aa</u> hiya <u>h</u>asbuhum wala\'anahumu <strong>al</strong>l<u>aa</u>hu walahum \'a<u>dzaa</u>bun muqiim<strong>un</strong>', 'Allah menjanjikan (mengancam) orang-orang munafik laki-laki dan perempuan dan orang-orang kafir dengan neraka Jahanam. Mereka kekal di dalamnya. Cukuplah (neraka) itu bagi mereka. Allah melaknat mereka; dan mereka mendapat azab yang kekal,'),
	(1304, 9, 69, 'كَالَّذِيْنَ مِنْ قَبْلِكُمْ كَانُوْٓا اَشَدَّ مِنْكُمْ قُوَّةً وَّاَكْثَرَ اَمْوَالًا وَّاَوْلَادًاۗ فَاسْتَمْتَعُوْا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِيْنَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِيْ خَاضُوْاۗ', 'ka<strong>a</strong>lla<u>dz</u>iina min qablikum k<u>aa</u>nuu asyadda minkum quwwatan wa-aktsara amw<u>aa</u>lan wa-awl<u>aa</u>dan fa<strong>i</strong>stamta\'uu bikhal<u>aa</u>qihim fa<strong>i</strong>stamta\'tum bikhal<u>aa</u>qikum kam<u>aa</u> istamta\'a <strong>al</strong>la<u>dz</u>iina min qablikum bikhal<u>aa</u>qihim wakhu<u>dh</u>tum ka<strong>a</strong>lla<u>dz</u>ii kh<u>aad</u>uu ul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati waul<u>aa</u>-ika humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', '(keadaan kamu kaum munafik dan musyrikin) seperti orang-orang sebelum kamu, mereka lebih kuat daripada kamu, dan lebih banyak harta dan anak-anaknya. Maka mereka telah menikmati bagiannya, dan kamu telah menikmati bagianmu sebagaimana orang-orang yang sebelummu menikmati bagiannya, dan kamu mempercakapkan (hal-hal yang batil) sebagaimana mereka mempercakapkannya. Mereka itu sia-sia amalnya di dunia dan di akhirat. Mereka itulah orang-orang yang rugi.'),
	(1305, 9, 70, 'اَلَمْ يَأْتِهِمْ نَبَاُ الَّذِيْنَ مِنْ قَبْلِهِمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۙ وَقَوْمِ اِبْرٰهِيْمَ وَاَصْحٰبِ مَدْيَنَ وَالْمُؤْتَفِكٰتِۗ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ', 'alam ya/tihim nabau <strong>al</strong>la<u>dz</u>iina min qablihim qawmi nuu<u>h</u>in wa\'<u>aa</u>din watsamuuda waqawmi ibr<u>aa</u>hiima wa-a<u>sh</u>-<u>haa</u>bi madyana wa<strong>a</strong>lmu/tafik<u>aa</u>ti atat-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liya<u>zh</u>limahum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Apakah tidak sampai kepada mereka berita (tentang) orang-orang yang sebelum mereka, (yaitu) kaum Nuh, ‘Ad, samud, kaum Ibrahim, penduduk Madyan, dan (penduduk) negeri-negeri yang telah musnah? Telah datang kepada mereka rasul-rasul dengan membawa bukti-bukti yang nyata; Allah tidak menzalimi mereka, tetapi merekalah yang menzalimi diri mereka sendiri.'),
	(1306, 9, 71, 'وَالْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۘ يَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَيُطِيْعُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاُولٰۤىِٕكَ سَيَرْحَمُهُمُ اللّٰهُ ۗاِنَّ ا', 'wa<strong>a</strong>lmu/minuuna wa<strong>a</strong>lmu/min<u>aa</u>tu ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in ya/muruuna bi<strong>a</strong>lma\'ruufi wayanhawna \'ani <strong>a</strong>lmunkari wayuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wayu/tuuna <strong>al</strong>zzak<u>aa</u>ta wayu<u>th</u>ii\'uuna <strong>al</strong>l<u>aa</u>ha warasuulahu ul<u>aa</u>-ika sayar<u>h</u>amuhumu <strong>al</strong>l<u>aa</u>hu inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan orang-orang yang beriman, laki-laki dan perempuan, sebagian mereka menjadi penolong bagi sebagian yang lain. Mereka menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar, melaksanakan salat, menunaikan zakat, dan taat kepada Allah dan Rasul-Nya. Mereka akan diberi rahmat oleh Allah. Sungguh, Allah Mahaperkasa, Mahabijaksana.'),
	(1307, 9, 72, 'وَعَدَ اللّٰهُ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍ ۗوَرِضْوَانٌ مِّنَ اللّٰهِ اَكْبَرُ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ࣖ ', 'wa\'ada <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmu/miniina wa<strong>a</strong>lmu/min<u>aa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wamas<u>aa</u>kina <u>th</u>ayyibatan fii jann<u>aa</u>ti \'adnin wari<u>dh</u>w<u>aa</u>nun mina <strong>al</strong>l<u>aa</u>hi akbaru <u>dzaa</u>lika huwa <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Allah menjanjikan kepada orang-orang mukmin laki-laki dan perempuan, (akan mendapat) surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya, dan (mendapat) tempat yang baik di surga ‘Adn. Dan keridaan Allah lebih besar. Itulah kemenangan yang agung.'),
	(1308, 9, 73, 'يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْ ۗوَمَأْوٰىهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيْرُ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu j<u>aa</u>hidi <strong>a</strong>lkuff<u>aa</u>ra wa<strong>a</strong>lmun<u>aa</u>fiqiina wa<strong>u</strong>ghlu<u>zh</u> \'alayhim wama/w<u>aa</u>hum jahannamu wabi/sa <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Wahai Nabi! Berjihadlah (melawan) orang-orang kafir dan orang-orang munafik, dan bersikap keraslah terhadap mereka. Tempat mereka adalah neraka Jahanam. Dan itulah seburuk-buruk tempat kembali.'),
	(1309, 9, 74, 'يَحْلِفُوْنَ بِاللّٰهِ مَا قَالُوْا ۗوَلَقَدْ قَالُوْا كَلِمَةَ الْكُفْرِ وَكَفَرُوْا بَعْدَ اِسْلَامِهِمْ وَهَمُّوْا بِمَا لَمْ يَنَالُوْاۚ وَمَا نَقَمُوْٓا اِلَّآ اَنْ اَغْنٰىهُمُ اللّٰهُ وَرَسُوْلُهٗ مِنْ فَضْلِهٖ ۚفَاِنْ يَّتُوْبُوْا يَكُ خَيْرًا لَّ', 'ya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi m<u>aa</u> q<u>aa</u>luu walaqad q<u>aa</u>luu kalimata <strong>a</strong>lkufri wakafaruu ba\'da isl<u>aa</u>mihim wahammuu bim<u>aa</u> lam yan<u>aa</u>luu wam<u>aa</u> naqamuu ill<u>aa</u> an aghn<u>aa</u>humu <strong>al</strong>l<u>aa</u>hu warasuuluhu min fa<u>dh</u>lihi fa-in yatuubuu yaku khayran lahum wa-in yatawallaw yu\'a<u>dzdz</u>ibhumu <strong>al</strong>l<u>aa</u>hu \'a<u>dzaa</u>ban <strong>a</strong>liiman fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wam<u>aa</u> lahum fii <strong>a</strong>l-ar<u>dh</u>i min waliyyin wal<u>aa</u> na<u>sh</u>iir<strong>in</strong>', 'Mereka (orang munafik) bersumpah dengan (nama) Allah, bahwa mereka tidak mengatakan (sesuatu yang menyakiti Muhammad). Sungguh, mereka telah mengucapkan perkataan kekafiran, dan telah menjadi kafir setelah Islam, dan menginginkan apa yang mereka tidak dapat mencapainya; dan mereka tidak mencela (Allah dan Rasul-Nya), sekiranya Allah dan Rasul-Nya telah melimpahkan karunia-Nya kepada mereka. Maka jika mereka bertobat, itu adalah lebih baik bagi mereka, dan jika mereka berpaling, niscaya Allah akan mengazab mereka dengan azab yang pedih di dunia dan akhirat; dan mereka tidak mempunyai pelindung dan tidak (pula) penolong di bumi.'),
	(1310, 9, 75, '۞ وَمِنْهُمْ مَّنْ عٰهَدَ اللّٰهَ لَىِٕنْ اٰتٰىنَا مِنْ فَضْلِهٖ لَنَصَّدَّقَنَّ وَلَنَكُوْنَنَّ مِنَ الصّٰلِحِيْنَ ', 'waminhum man \'<u>aa</u>hada <strong>al</strong>l<u>aa</u>ha la-in <u>aa</u>t<u>aa</u>n<u>aa</u> min fa<u>dh</u>lihi lana<u>shsh</u>addaqanna walanakuunanna mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan di antara mereka ada orang yang telah berjanji kepada Allah, “Sesungguhnya jika Allah memberikan sebagian dari karunia-Nya kepada kami, niscaya kami akan bersedekah dan niscaya kami termasuk orang-orang yang saleh.”'),
	(1311, 9, 76, 'فَلَمَّآ اٰتٰىهُمْ مِّنْ فَضْلِهٖ بَخِلُوْا بِهٖ وَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ ', 'falamm<u>aa</u> <u>aa</u>t<u>aa</u>hum min fa<u>dh</u>lihi bakhiluu bihi watawallaw wahum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Ketika Allah memberikan kepada mereka sebagian dari karunia-Nya, mereka menjadi kikir dan berpaling, dan selalu menentang (kebenaran).'),
	(1312, 9, 77, 'فَاَعْقَبَهُمْ نِفَاقًا فِيْ قُلُوْبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهٗ بِمَآ اَخْلَفُوا اللّٰهَ مَا وَعَدُوْهُ وَبِمَا كَانُوْا يَكْذِبُوْنَ ', 'fa-a\'qabahum nif<u>aa</u>qan fii quluubihim il<u>aa</u> yawmi yalqawnahu bim<u>aa</u> akhlafuu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> wa\'aduuhu wabim<u>aa</u> k<u>aa</u>nuu yak<u>dz</u>ibuun<strong>a</strong>', 'Maka Allah menanamkan kemunafikan dalam hati mereka sampai pada waktu mereka menemui-Nya, karena mereka telah mengingkari janji yang telah mereka ikrarkan kepada-Nya dan (juga) karena mereka selalu berdusta.'),
	(1313, 9, 78, 'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰىهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوْبِ ', 'alam ya\'lamuu anna <strong>al</strong>l<u>aa</u>ha ya\'lamu sirrahum wanajw<u>aa</u>hum wa-anna <strong>al</strong>l<u>aa</u>ha \'all<u>aa</u>mu <strong>a</strong>lghuyuub<strong>i</strong>', 'Tidakkah mereka mengetahui bahwa Allah mengetahui rahasia dan bisikan mereka, dan bahwa Allah mengetahui segala yang gaib?'),
	(1314, 9, 79, 'اَلَّذِيْنَ يَلْمِزُوْنَ الْمُطَّوِّعِيْنَ مِنَ الْمُؤْمِنِيْنَ فِى الصَّدَقٰتِ وَالَّذِيْنَ لَا يَجِدُوْنَ اِلَّا جُهْدَهُمْ فَيَسْخَرُوْنَ مِنْهُمْ ۗسَخِرَ اللّٰهُ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ', '<strong>al</strong>la<u>dz</u>iina yalmizuuna <strong>a</strong>lmu<u>ththh</u>awwi\'iina mina <strong>a</strong>lmu/miniina fii <strong>al</strong><u>shsh</u>adaq<u>aa</u>ti wa<strong>a</strong>lla<u>dz</u>iina l<u>aa</u> yajiduuna ill<u>aa</u> juhdahum fayaskharuuna minhum sakhira <strong>al</strong>l<u>aa</u>hu minhum walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', '(Orang munafik) yaitu mereka yang mencela orang-orang beriman yang memberikan sedekah dengan sukarela dan yang (mencela) orang-orang yang hanya memperoleh (untuk disedekahkan) sekedar kesanggupannya, maka orang-orang munafik itu menghina mereka. Allah akan membalas penghinaan mereka, dan mereka akan mendapat azab yang pedih.'),
	(1315, 9, 80, 'اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۗ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِيْنَ مَرَّةً فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ۗذٰلِكَ بِاَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ࣖ ', 'istaghfir lahum aw l<u>aa</u> tastaghfir lahum in tastaghfir lahum sab\'iina marratan falan yaghfira <strong>al</strong>l<u>aa</u>hu lahum <u>dzaa</u>lika bi-annahum kafaruu bi<strong>al</strong>l<u>aa</u>hi warasuulihi wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', '(Sama saja) engkau (Muhammad) memohonkan ampunan bagi mereka atau tidak memohonkan ampunan bagi mereka. Walaupun engkau memohonkan ampunan bagi mereka tujuh puluh kali, Allah tidak akan memberi ampunan kepada mereka. Yang demikian itu karena mereka ingkar (kafir) kepada Allah dan Rasul-Nya. Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik.'),
	(1316, 9, 81, 'فَرِحَ الْمُخَلَّفُوْنَ بِمَقْعَدِهِمْ خِلٰفَ رَسُوْلِ اللّٰهِ وَكَرِهُوْٓا اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَقَالُوْا لَا تَنْفِرُوْا فِى الْحَرِّۗ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَرًّاۗ  لَوْ كَانُوْا يَفْقَهُوْنَ ', 'fari<u>h</u>a <strong>a</strong>lmukhallafuuna bimaq\'adihim khil<u>aa</u>fa rasuuli <strong>al</strong>l<u>aa</u>hi wakarihuu an yuj<u>aa</u>hiduu bi-amw<u>aa</u>lihim wa-anfusihim fii sabiili <strong>al</strong>l<u>aa</u>hi waq<u>aa</u>luu l<u>aa</u> tanfiruu fii <strong>a</strong>l<u>h</u>arri qul n<u>aa</u>ru jahannama asyaddu <u>h</u>arran law k<u>aa</u>nuu yafqahuun<strong>a</strong>', 'Orang-orang yang ditinggalkan (tidak ikut berperang), merasa gembira dengan duduk-duduk diam sepeninggal Rasulullah. Mereka tidak suka berjihad dengan harta dan jiwa mereka di jalan Allah dan mereka berkata, “Janganlah kamu berangkat (pergi berperang) dalam panas terik ini.” Katakanlah (Muhammad), “Api neraka Jahanam lebih panas,” jika mereka mengetahui.'),
	(1317, 9, 82, 'فَلْيَضْحَكُوْا قَلِيْلًا وَّلْيَبْكُوْا كَثِيْرًاۚ جَزَاۤءًۢ بِمَا كَانُوْا يَكْسِبُوْنَ ', 'falya<u>dh</u>akuu qaliilan walyabkuu katsiiran jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'Maka biarkanlah mereka tertawa sedikit dan menangis yang banyak, sebagai balasan terhadap apa yang selalu mereka perbuat.'),
	(1318, 9, 83, 'فَاِنْ رَّجَعَكَ اللّٰهُ اِلٰى طَاۤىِٕفَةٍ مِّنْهُمْ فَاسْتَأْذَنُوْكَ لِلْخُرُوْجِ فَقُلْ لَّنْ تَخْرُجُوْا مَعِيَ اَبَدًا وَّلَنْ تُقَاتِلُوْا مَعِيَ عَدُوًّاۗ اِنَّكُمْ رَضِيْتُمْ بِالْقُعُوْدِ اَوَّلَ مَرَّةٍۗ فَاقْعُدُوْا مَعَ الْخَالِفِيْنَ ', 'fa-in raja\'aka <strong>al</strong>l<u>aa</u>hu il<u>aa</u> <u>th</u><u>aa</u>-ifatin minhum fa<strong>i</strong>sta/<u>dz</u>anuuka lilkhuruuji faqul lan takhrujuu ma\'iya abadan walan tuq<u>aa</u>tiluu ma\'iya \'aduwwan innakum ra<u>dh</u>iitum bi<strong>a</strong>lqu\'uudi awwala marratin fa<strong>u</strong>q\'uduu ma\'a <strong>a</strong>lkh<u>aa</u>lifiin<strong>a</strong>', 'Maka jika Allah mengembalikanmu (Muhammad) kepada suatu golongan dari mereka (orang-orang munafik), kemudian mereka meminta izin kepadamu untuk keluar (pergi berperang), maka katakanlah, “Kamu tidak boleh keluar bersamaku selama-lamanya dan tidak boleh memerangi musuh bersamaku. Sesungguhnya kamu telah rela tidak pergi (berperang) sejak semula. Karena itu duduklah (tinggallah) bersama orang-orang yang tidak ikut (berperang).”'),
	(1319, 9, 84, 'وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِّنْهُمْ مَّاتَ اَبَدًا وَّلَا تَقُمْ عَلٰى قَبْرِهٖۗ اِنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَمَاتُوْا وَهُمْ فٰسِقُوْنَ ', 'wal<u>aa</u> tu<u>sh</u>alli \'al<u>aa</u> a<u>h</u>adin minhum m<u>aa</u>ta abadan wal<u>aa</u> taqum \'al<u>aa</u> qabrihi innahum kafaruu bi<strong>al</strong>l<u>aa</u>hi warasuulihi wam<u>aa</u>tuu wahum f<u>aa</u>siquun<strong>a</strong>', 'Dan janganlah engkau (Muhammad) melaksanakan salat untuk seseorang yang mati di antara mereka (orang-orang munafik), selama-lamanya dan janganlah engkau berdiri (mendoakan) di atas kuburnya. Sesungguhnya mereka ingkar kepada Allah dan Rasul-Nya dan mereka mati dalam keadaan fasik.'),
	(1320, 9, 85, 'وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۗ اِنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّعَذِّبَهُمْ بِهَا فِى الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ', 'wal<u>aa</u> tu\'jibka amw<u>aa</u>luhum wa-awl<u>aa</u>duhum innam<u>aa</u> yuriidu <strong>al</strong>l<u>aa</u>hu an yu\'a<u>dzdz</u>ibahum bih<u>aa</u> fii <strong>al</strong>dduny<u>aa</u> watazhaqa anfusuhum wahum k<u>aa</u>firuun<strong>a</strong>', 'Dan janganlah engkau (Muhammad) kagum terhadap harta dan anak-anak mereka. Sesungguhnya dengan itu Allah hendak menyiksa mereka di dunia dan agar nyawa mereka melayang, sedang mereka dalam keadaan kafir.'),
	(1321, 9, 86, 'وَاِذَآ اُنْزِلَتْ سُوْرَةٌ اَنْ اٰمِنُوْا بِاللّٰهِ وَجَاهِدُوْا مَعَ رَسُوْلِهِ اسْتَأْذَنَكَ اُولُوا الطَّوْلِ مِنْهُمْ وَقَالُوْا ذَرْنَا نَكُنْ مَّعَ الْقٰعِدِيْنَ ', 'wa-i<u>dzaa</u> unzilat suuratun an <u>aa</u>minuu bi<strong>al</strong>l<u>aa</u>hi waj<u>aa</u>hiduu ma\'a rasuulihi ista/<u>dz</u>anaka uluu <strong>al</strong><u>ththh</u>awli minhum waq<u>aa</u>luu <u>dz</u>arn<u>aa</u> nakun ma\'a <strong>a</strong>lq<u>aa</u>\'idiin<strong>a</strong>', 'Dan apabila diturunkan suatu surah (yang memerintahkan kepada orang-orang munafik), “Berimanlah kepada Allah dan berjihadlah bersama Rasul-Nya,” niscaya orang-orang yang kaya dan berpengaruh di antara mereka meminta izin kepadamu (untuk tidak berjihad) dan mereka berkata, “Biarkanlah kami berada bersama orang-orang yang duduk (tinggal di rumah).”'),
	(1322, 9, 87, 'رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِ وَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ ', 'ra<u>dh</u>uu bi-an yakuunuu ma\'a <strong>a</strong>lkhaw<u>aa</u>lifi wa<u>th</u>ubi\'a \'al<u>aa</u> quluubihim fahum l<u>aa</u> yafqahuun<strong>a</strong>', 'Mereka rela berada bersama orang-orang yang tidak pergi berperang, dan hati mereka telah tertutup, sehingga mereka tidak memahami (kebahagiaan beriman dan berjihad).'),
	(1323, 9, 88, 'لٰكِنِ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ جَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاُولٰۤىِٕكَ لَهُمُ الْخَيْرٰتُ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ', 'l<u>aa</u>kini <strong>al</strong>rrasuulu wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu j<u>aa</u>haduu bi-amw<u>aa</u>lihim wa-anfusihim waul<u>aa</u>-ika lahumu <strong>a</strong>lkhayr<u>aa</u>tu waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Tetapi Rasul dan orang-orang yang beriman bersama dia, (mereka) berjihad dengan harta dan jiwa. Mereka itu memperoleh kebaikan. Mereka itulah orang-orang yang beruntung.'),
	(1324, 9, 89, 'اَعَدَّ اللّٰهُ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ ࣖ ', 'a\'adda <strong>al</strong>l<u>aa</u>hu lahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> <u>dzaa</u>lika <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Allah telah menyediakan bagi mereka surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah kemenangan yang agung.'),
	(1325, 9, 90, 'وَجَاۤءَ الْمُعَذِّرُوْنَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِيْنَ كَذَبُوا اللّٰهَ وَرَسُوْلَهٗ ۗسَيُصِيْبُ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ ', 'waj<u>aa</u>-a <strong>a</strong>lmu\'a<u>dzdz</u>iruuna mina <strong>a</strong>l-a\'r<u>aa</u>bi liyu/<u>dz</u>ana lahum waqa\'ada <strong>al</strong>la<u>dz</u>iina ka<u>dz</u>abuu <strong>al</strong>l<u>aa</u>ha warasuulahu sayu<u>sh</u>iibu <strong>al</strong>la<u>dz</u>iina kafaruu minhum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan di antara orang-orang Arab Badui datang (kepada Nabi) mengemukakan alasan, agar diberi izin (untuk tidak pergi berperang), sedang orang-orang yang mendustakan Allah dan Rasul-Nya, duduk berdiam. Kelak orang-orang yang kafir di antara mereka akan ditimpa azab yang pedih.'),
	(1326, 9, 91, 'لَيْسَ عَلَى الضُّعَفَاۤءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذِيْنَ لَا يَجِدُوْنَ مَا يُنْفِقُوْنَ حَرَجٌ اِذَا نَصَحُوْا لِلّٰهِ وَرَسُوْلِهٖۗ مَا عَلَى الْمُحْسِنِيْنَ مِنْ سَبِيْلٍ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ ', 'laysa \'al<u>aa</u> <strong>al</strong><u>dhdh</u>u\'af<u>aa</u>-i wal<u>aa</u> \'al<u>aa</u> <strong>a</strong>lmar<u>daa</u> wal<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yajiduuna m<u>aa</u> yunfiquuna <u>h</u>arajun i<u>dzaa</u> na<u>sh</u>a<u>h</u>uu lill<u>aa</u>hi warasuulihi m<u>aa</u> \'al<u>aa</u> <strong>a</strong>lmu<u>h</u>siniina min sabiilin wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Tidak ada dosa (karena tidak pergi berperang) atas orang yang lemah, orang yang sakit dan orang yang tidak memperoleh apa yang akan mereka infakkan, apabila mereka berlaku ikhlas kepada Allah dan Rasul-Nya. Tidak ada alasan apa pun untuk menyalahkan orang-orang yang berbuat baik. Dan Allah Maha Pengampun, Maha Penyayang,'),
	(1327, 9, 92, 'وَّلَا عَلَى الَّذِيْنَ اِذَا مَآ اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَآ اَجِدُ مَآ اَحْمِلُكُمْ عَلَيْهِ ۖتَوَلَّوْا وَّاَعْيُنُهُمْ تَفِيْضُ مِنَ الدَّمْعِ حَزَنًا اَلَّا يَجِدُوْا مَا يُنْفِقُوْنَۗ ', 'wal<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina i<u>dzaa</u> m<u>aa</u> atawka lita<u>h</u>milahum qulta l<u>aa</u> ajidu m<u>aa</u> a<u>h</u>milukum \'alayhi tawallaw wa-a\'yunuhum tafii<u>dh</u>u mina <strong>al</strong>ddam\'i <u>h</u>azanan <strong>al</strong>l<u>aa</u> yajiduu m<u>aa</u> yunfiquun<strong>a</strong>', 'dan tidak ada (pula dosa) atas orang-orang yang datang kepadamu (Muhammad), agar engkau memberi kendaraan kepada mereka, lalu engkau berkata, “Aku tidak memperoleh kendaraan untuk membawamu,” lalu mereka kembali, sedang mata mereka bercucuran air mata karena sedih, disebabkan mereka tidak memperoleh apa yang akan mereka infakkan (untuk ikut berperang).'),
	(1328, 9, 93, 'اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَسْتَأْذِنُوْنَكَ وَهُمْ اَغْنِيَاۤءُۚ رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَعْلَمُوْنَ  ۔', 'innam<u>aa</u> <strong>al</strong>ssabiilu \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina yasta/<u>dz</u>inuunaka wahum aghniy<u>aa</u>u ra<u>dh</u>uu bi-an yakuunuu ma\'a <strong>a</strong>lkhaw<u>aa</u>lifi wa<u>th</u>aba\'a <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> quluubihim fahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Sesungguhnya alasan (untuk menyalahkan) hanyalah terhadap orang-orang yang meminta izin kepadamu (untuk tidak ikut berperang), padahal mereka orang kaya. Mereka rela berada bersama orang-orang yang tidak ikut berperang dan Allah telah mengunci hati mereka, sehingga mereka tidak mengetahui (akibat perbuatan mereka).'),
	(1329, 9, 94, 'يَعْتَذِرُوْنَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْ ۗ قُلْ لَّا تَعْتَذِرُوْا لَنْ نُّؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ ثُمَّ تُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُك', 'ya\'ta<u>dz</u>iruuna ilaykum i<u>dzaa</u> raja\'tum ilayhim qul l<u>aa</u> ta\'ta<u>dz</u>iruu lan nu/mina lakum qad nabba-an<u>aa</u> <strong>al</strong>l<u>aa</u>hu min akhb<u>aa</u>rikum wasayar<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'amalakum warasuuluhu tsumma turadduuna il<u>aa</u> \'<u>aa</u>limi <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Mereka (orang-orang munafik yang tidak ikut berperang) akan mengemukakan alasannya kepadamu ketika kamu telah kembali kepada mereka. Katakanlah (Muhammad), “Janganlah kamu mengemukakan alasan; kami tidak percaya lagi kepadamu, sungguh, Allah telah memberitahukan kepada kami tentang beritamu. Dan Allah akan melihat pekerjaanmu, (demikian pula) Rasul-Nya, kemudian kamu dikembalikan kepada (Allah) Yang Maha Mengetahui segala yang gaib dan yang nyata, lalu Dia memberitakan kepadamu apa yang telah kamu kerjakan.”'),
	(1330, 9, 95, 'سَيَحْلِفُوْنَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوْا عَنْهُمْ ۗ فَاَعْرِضُوْا عَنْهُمْ ۗ اِنَّهُمْ رِجْسٌۙ وَّمَأْوٰىهُمْ جَهَنَّمُ جَزَاۤءً ۢبِمَا كَانُوْا يَكْسِبُوْنَ', 'saya<u>h</u>lifuuna bi<strong>al</strong>l<u>aa</u>hi lakum i<u>dzaa</u> inqalabtum ilayhim litu\'ri<u>dh</u>uu \'anhum fa-a\'ri<u>dh</u>uu \'anhum innahum rijsun wama/w<u>aa</u>hum jahannamu jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'Mereka akan bersumpah kepadamu dengan nama Allah, ketika kamu kembali kepada mereka, agar kamu berpaling dari mereka. Maka berpalinglah dari mereka; karena sesungguhnya mereka itu berjiwa kotor dan tempat mereka neraka Jahanam, sebagai balasan atas apa yang telah mereka kerjakan.'),
	(1331, 9, 96, 'يَحْلِفُوْنَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۚفَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفٰسِقِيْنَ', 'ya<u>h</u>lifuuna lakum litar<u>dh</u>aw \'anhum fa-in tar<u>dh</u>aw \'anhum fa-inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yar<u>daa</u> \'ani <strong>a</strong>lqawmi <strong>a</strong>lf<u>aa</u>siqiin<strong>a</strong>', 'Mereka akan bersumpah kepadamu agar kamu bersedia menerima mereka. Tetapi sekalipun kamu menerima mereka, Allah tidak akan rida kepada orang-orang yang fasik.'),
	(1332, 9, 97, 'اَلْاَعْرَابُ اَشَدُّ كُفْرًا وَّنِفَاقًا وَّاَجْدَرُ اَلَّا يَعْلَمُوْا حُدُوْدَ مَآ اَنْزَلَ اللّٰهُ عَلٰى رَسُوْلِهٖ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'al-a\'r<u>aa</u>bu asyaddu kufran wanif<u>aa</u>qan wa-ajdaru <strong>al</strong>l<u>aa</u> ya\'lamuu <u>h</u>uduuda m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> rasuulihi wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Orang-orang Arab Badui itu lebih kuat kekafiran dan kemunafikannya, dan sangat wajar tidak mengetahui hukum-hukum yang diturunkan Allah kepada Rasul-Nya. Allah Maha Mengetahui, Mahabijaksana.'),
	(1333, 9, 98, 'وَمِنَ الْاَعْرَابِ مَنْ يَّتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَّيَتَرَبَّصُ بِكُمُ الدَّوَاۤىِٕرَ ۗعَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِ ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ  ', 'wamina <strong>a</strong>l-a\'r<u>aa</u>bi man yattakhi<u>dz</u>u m<u>aa</u> yunfiqu maghraman wayatarabba<u>sh</u>u bikumu <strong>al</strong>ddaw<u>aa</u>-ira \'alayhim d<u>aa</u>-iratu <strong>al</strong>ssaw-i wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Dan di antara orang-orang Arab Badui itu ada yang memandang apa yang diinfakkannya (di jalan Allah) sebagai suatu kerugian; dia menanti-nanti marabahaya menimpamu, merekalah yang akan ditimpa marabahaya. Allah Maha Mendengar, Maha Mengetahui.'),
	(1334, 9, 99, 'وَمِنَ الْاَعْرَابِ مَنْ يُّؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبٰتٍ عِنْدَ اللّٰهِ وَصَلَوٰتِ الرَّسُوْلِ ۗ اَلَآ اِنَّهَا قُرْبَةٌ لَّهُمْ ۗ سَيُدْخِلُهُمُ اللّٰهُ فِيْ رَحْمَتِهٖ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ', 'wamina <strong>a</strong>l-a\'r<u>aa</u>bi man yu/minu bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri wayattakhi<u>dz</u>u m<u>aa</u> yunfiqu qurub<u>aa</u>tin \'inda <strong>al</strong>l<u>aa</u>hi wa<u>sh</u>alaw<u>aa</u>ti <strong>al</strong>rrasuuli <strong>a</strong>l<u>aa</u> innah<u>aa</u> qurbatun lahum sayudkhiluhumu <strong>al</strong>l<u>aa</u>hu fii ra<u>h</u>matihi inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan di antara orang-orang Arab Badui itu ada yang beriman kepada Allah dan hari kemudian, dan memandang apa yang diinfakkannya (di jalan Allah) sebagai jalan mendekatkan kepada Allah dan sebagai jalan untuk (memperoleh) doa Rasul. Ketahuilah, sesungguhnya infak itu suatu jalan bagi mereka untuk mendekatkan diri (kepada Allah). Kelak Allah akan memasukkan mereka ke dalam rahmat (surga)-Nya; sesungguhnya Allah Maha Pengampun, Maha Penyayang.'),
	(1335, 9, 100, 'وَالسّٰبِقُوْنَ الْاَوَّلُوْنَ مِنَ الْمُهٰجِرِيْنَ وَالْاَنْصَارِ وَالَّذِيْنَ اتَّبَعُوْهُمْ بِاِحْسَانٍۙ رَّضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ وَاَعَدَّ لَهُمْ جَنّٰتٍ تَجْرِيْ تَحْتَهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗذٰلِكَ الْفَوْزُ الْ', 'wa<strong>al</strong>ss<u>aa</u>biquuna <strong>a</strong>l-awwaluuna mina <strong>a</strong>lmuh<u>aa</u>jiriina wa<strong>a</strong>l-an<u>shaa</u>ri wa<strong>a</strong>lla<u>dz</u>iina ittaba\'uuhum bi-i<u>h</u>s<u>aa</u>nin ra<u>dh</u>iya <strong>al</strong>l<u>aa</u>hu \'anhum wara<u>dh</u>uu \'anhu wa-a\'adda lahum jann<u>aa</u>tin tajrii ta<u>h</u>tah<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan <u>dzaa</u>lika <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Dan orang-orang yang terdahulu lagi yang pertama-tama (masuk Islam) di antara orang-orang Muhajirin dan Ansar dan orang-orang yang mengikuti mereka dengan baik, Allah rida kepada mereka dan mereka pun rida kepada Allah. Allah menyediakan bagi mereka surga-surga yang mengalir di bawahnya sungai-sungai. Mereka kekal di dalamnya selama-lamanya. Itulah kemenangan yang agung.'),
	(1336, 9, 101, 'وَمِمَّنْ حَوْلَكُمْ مِّنَ الْاَعْرَابِ مُنٰفِقُوْنَ ۗوَمِنْ اَهْلِ الْمَدِيْنَةِ مَرَدُوْا عَلَى النِّفَاقِۗ  لَا تَعْلَمُهُمْۗ نَحْنُ نَعْلَمُهُمْۗ سَنُعَذِّبُهُمْ مَّرَّتَيْنِ ثُمَّ يُرَدُّوْنَ اِلٰى عَذَابٍ عَظِيْمٍ ۚ  ', 'wamimman <u>h</u>awlakum mina <strong>a</strong>l-a\'r<u>aa</u>bi mun<u>aa</u>fiquuna wamin ahli <strong>a</strong>lmadiinati maraduu \'al<u>aa</u> <strong>al</strong>nnif<u>aa</u>qi l<u>aa</u> ta\'lamuhum na<u>h</u>nu na\'lamuhum sanu\'a<u>dzdz</u>ibuhum marratayni tsumma yuradduuna il<u>aa</u> \'a<u>dzaa</u>bin \'a<u>zh</u>iim<strong>in</strong>', 'Dan di antara orang-orang Arab Badui yang (tinggal) di sekitarmu, ada orang-orang munafik. Dan di antara penduduk Madinah (ada juga orang-orang munafik), mereka keterlaluan dalam kemunafikannya. Engkau (Muhammad) tidak mengetahui mereka, tetapi Kami mengetahuinya. Nanti mereka akan Kami siksa dua kali, kemudian mereka akan dikembalikan kepada azab yang besar.'),
	(1337, 9, 102, 'وَاٰخَرُوْنَ اعْتَرَفُوْا بِذُنُوْبِهِمْ خَلَطُوْا عَمَلًا صَالِحًا وَّاٰخَرَ سَيِّئًاۗ عَسَى اللّٰهُ اَنْ يَّتُوْبَ عَلَيْهِمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'wa<u>aa</u>kharuuna i\'tarafuu bi<u>dz</u>unuubihim khala<u>th</u>uu \'amalan <u>shaa</u>li<u>h</u>an wa<u>aa</u>khara sayyi-an \'as<u>aa</u> <strong>al</strong>l<u>aa</u>hu an yatuuba \'alayhim inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan (ada pula) orang lain yang mengakui dosa-dosa mereka, mereka mencampuradukkan pekerjaan yang baik dengan pekerjaan lain yang buruk. Mudah-mudahan Allah menerima tobat mereka. Sesungguhnya Allah Maha Pengampun, Maha Penyayang.'),
	(1338, 9, 103, 'خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيْهِمْ بِهَا وَصَلِّ عَلَيْهِمْۗ اِنَّ صَلٰوتَكَ سَكَنٌ لَّهُمْۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ', 'khu<u>dz</u> min amw<u>aa</u>lihim <u>sh</u>adaqatan tu<u>th</u>ahhiruhum watuzakkiihim bih<u>aa</u> wa<u>sh</u>alli \'alayhim inna <u>sh</u>al<u>aa</u>taka sakanun lahum wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Ambillah zakat dari harta mereka, guna membersihkan dan menyucikan mereka, dan berdoalah untuk mereka. Sesungguhnya doamu itu (menumbuhkan) ketenteraman jiwa bagi mereka. Allah Maha Mendengar, Maha Mengetahui.'),
	(1339, 9, 104, 'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَأْخُذُ الصَّدَقٰتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ ', 'alam ya\'lamuu anna <strong>al</strong>l<u>aa</u>ha huwa yaqbalu <strong>al</strong>ttawbata \'an \'ib<u>aa</u>dihi waya/khu<u>dz</u>u <strong>al</strong><u>shsh</u>adaq<u>aa</u>ti wa-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>al</strong>ttaww<u>aa</u>bu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Tidakkah mereka mengetahui, bahwa Allah menerima tobat hamba-hamba-Nya dan menerima zakat(nya), dan bahwa Allah Maha Penerima tobat, Maha Penyayang?'),
	(1340, 9, 105, 'وَقُلِ اعْمَلُوْا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ وَالْمُؤْمِنُوْنَۗ وَسَتُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۚ  ', 'waquli i\'maluu fasayar<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'amalakum warasuuluhu wa<strong>a</strong>lmu/minuuna wasaturadduuna il<u>aa</u> \'<u>aa</u>limi <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan katakanlah, “Bekerjalah kamu, maka Allah akan melihat pekerjaanmu, begitu juga Rasul-Nya dan orang-orang mukmin, dan kamu akan dikembalikan kepada (Allah) Yang Mengetahui yang gaib dan yang nyata, lalu diberitakan-Nya kepada kamu apa yang telah kamu kerjakan.”'),
	(1341, 9, 106, 'وَاٰخَرُوْنَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوْبُ عَلَيْهِمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ', 'wa<u>aa</u>kharuuna murjawna li-amri <strong>al</strong>l<u>aa</u>hi imm<u>aa</u> yu\'a<u>dzdz</u>ibuhum wa-imm<u>aa</u> yatuubu \'alayhim wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Dan ada (pula) orang-orang lain yang ditangguhkan sampai ada keputusan Allah; mungkin Allah akan mengazab mereka dan mungkin Allah akan menerima tobat mereka. Allah Maha Mengetahui, Mahabijaksana.'),
	(1342, 9, 107, 'وَالَّذِيْنَ اتَّخَذُوْا مَسْجِدًا ضِرَارًا وَّكُفْرًا وَّتَفْرِيْقًاۢ بَيْنَ الْمُؤْمِنِيْنَ وَاِرْصَادًا لِّمَنْ حَارَبَ اللّٰهَ وَرَسُوْلَهٗ مِنْ قَبْلُ ۗوَلَيَحْلِفُنَّ اِنْ اَرَدْنَآ اِلَّا الْحُسْنٰىۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina ittakha<u>dz</u>uu masjidan <u>dh</u>ir<u>aa</u>ran wakufran watafriiqan bayna <strong>a</strong>lmu/miniina wa-ir<u>shaa</u>dan liman <u>haa</u>raba <strong>al</strong>l<u>aa</u>ha warasuulahu min qablu walaya<u>h</u>lifunna in aradn<u>aa</u> ill<u>aa</u> <strong>a</strong>l<u>h</u>usn<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu yasyhadu innahum lak<u>aadz</u>ibuun<strong>a</strong>', 'Dan (di antara orang-orang munafik itu) ada yang mendirikan masjid untuk menimbulkan bencana (pada orang-orang yang beriman), untuk kekafiran dan untuk memecah belah di antara orang-orang yang beriman serta menunggu kedatangan orang-orang yang telah memerangi Allah dan Rasul-Nya sejak dahulu. Mereka dengan pasti bersumpah, “Kami hanya menghendaki kebaikan.” Dan Allah menjadi saksi bahwa mereka itu pendusta (dalam sumpahnya).'),
	(1343, 9, 108, 'لَا تَقُمْ فِيْهِ اَبَدًاۗ  لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُوْمَ فِيْهِۗ فِيْهِ رِجَالٌ يُّحِبُّوْنَ اَنْ يَّتَطَهَّرُوْاۗ وَاللّٰهُ يُحِبُّ الْمُطَّهِّرِيْنَ ', 'l<u>aa</u> taqum fiihi abadan lamasjidun ussisa \'al<u>aa</u> <strong>al</strong>ttaqw<u>aa</u> min awwali yawmin a<u>h</u>aqqu an taquuma fiihi fiihi rij<u>aa</u>lun yu<u>h</u>ibbuuna an yata<u>th</u>ahharuu wa<strong>al</strong>l<u>aa</u>hu yu<u>h</u>ibbu <strong>a</strong>lmu<u>ththh</u>ahhiriin<strong>a</strong>', 'Janganlah engkau melaksanakan salat dalam masjid itu selama-lamanya. Sungguh, masjid yang didirikan atas dasar takwa, sejak hari pertama adalah lebih pantas engkau melaksanakan salat di dalamnya. Di dalamnya ada orang-orang yang ingin membersihkan diri. Allah menyukai orang-orang yang bersih.'),
	(1344, 9, 109, 'اَفَمَنْ اَسَّسَ بُنْيَانَهٗ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَّنْ اَسَّسَ بُنْيَانَهٗ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهٖ فِيْ نَارِ جَهَنَّمَۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ', 'afaman assasa buny<u>aa</u>nahu \'al<u>aa</u> taqw<u>aa</u> mina <strong>al</strong>l<u>aa</u>hi wari<u>dh</u>w<u>aa</u>nin khayrun am man assasa buny<u>aa</u>nahu \'al<u>aa</u> syaf<u>aa</u> jurufin h<u>aa</u>rin fa<strong>i</strong>nh<u>aa</u>ra bihi fii n<u>aa</u>ri jahannama wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Maka apakah orang-orang yang mendirikan bangunan (masjid) atas dasar takwa kepada Allah dan keridaan(-Nya) itu lebih baik, ataukah orang-orang yang mendirikan bangunannya di tepi jurang yang runtuh, lalu (bangunan) itu roboh bersama-sama dengan dia ke dalam neraka Jahanam? Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
	(1345, 9, 110, 'لَا يَزَالُ بُنْيَانُهُمُ الَّذِيْ بَنَوْا رِيْبَةً فِيْ قُلُوْبِهِمْ اِلَّآ اَنْ تَقَطَّعَ قُلُوْبُهُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ࣖ ', 'l<u>aa</u> yaz<u>aa</u>lu buny<u>aa</u>nuhumu <strong>al</strong>la<u>dz</u>ii banaw riibatan fii quluubihim ill<u>aa</u> an taqa<u>ththh</u>a\'a quluubuhum wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Bangunan yang mereka dirikan itu senantiasa menjadi penyebab keraguan dalam hati mereka, sampai hati mereka hancur. Dan Allah Maha Mengetahui, Mahabijaksana.'),
	(1346, 9, 111, '۞ اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِنِيْنَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۗ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ فَيَقْتُلُوْنَ وَيُقْتَلُوْنَ وَعْدًا عَلَيْهِ حَقًّا فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ وَالْقُرْاٰنِۗ وَمَنْ اَوْفٰى ', 'inna <strong>al</strong>l<u>aa</u>ha isytar<u>aa</u> mina <strong>a</strong>lmu/miniina anfusahum wa-amw<u>aa</u>lahum bi-anna lahumu <strong>a</strong>ljannata yuq<u>aa</u>tiluuna fii sabiili <strong>al</strong>l<u>aa</u>hi fayaqtuluuna wayuqtaluuna wa\'dan \'alayhi <u>h</u>aqqan fii <strong>al</strong>ttawr<u>aa</u>ti wa<strong>a</strong>l-injiili wa<strong>a</strong>lqur-<u>aa</u>ni waman awf<u>aa</u> bi\'ahdihi mina <strong>al</strong>l<u>aa</u>hi fa<strong>i</strong>stabsyiruu bibay\'ikumu <strong>al</strong>la<u>dz</u>ii b<u>aa</u>ya\'tum bihi wa<u>dzaa</u>lika huwa <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Sesungguhnya Allah membeli dari orang-orang mukmin, baik diri mau-pun harta mereka dengan memberikan surga untuk mereka. Mereka berperang di jalan Allah; sehingga mereka membunuh atau terbunuh, (sebagai) janji yang benar dari Allah di dalam Taurat, Injil, dan Al-Qur\'an. Dan siapakah yang lebih menepati janjinya selain Allah? Maka bergembiralah dengan jual beli yang telah kamu lakukan itu, dan demikian itulah kemenangan yang agung.'),
	(1347, 9, 112, 'اَلتَّاۤىِٕبُوْنَ الْعٰبِدُوْنَ الْحَامِدُوْنَ السَّاۤىِٕحُوْنَ الرَّاكِعُوْنَ السَّاجِدُوْنَ الْاٰمِرُوْنَ بِالْمَعْرُوْفِ وَالنَّاهُوْنَ عَنِ الْمُنْكَرِ وَالْحٰفِظُوْنَ لِحُدُوْدِ اللّٰهِ ۗوَبَشِّرِ الْمُؤْمِنِيْنَ ', 'a<strong>l</strong>tt<u>aa</u>-ibuuna <strong>a</strong>l\'<u>aa</u>biduuna <strong>a</strong>l<u>haa</u>miduuna <strong>al</strong>ss<u>aa</u>-i<u>h</u>uuna <strong>al</strong>rr<u>aa</u>ki\'uuna <strong>al</strong>ss<u>aa</u>jiduuna <strong>a</strong>l-<u>aa</u>miruuna bi<strong>a</strong>lma\'ruufi wa<strong>al</strong>nn<u>aa</u>huuna \'ani <strong>a</strong>lmunkari wa<strong>a</strong>l<u>haa</u>fi<u>zh</u>uuna li<u>h</u>uduudi <strong>al</strong>l<u>aa</u>hi wabasysyiri <strong>a</strong>lmu/miniin<strong>a</strong>', 'Mereka itu adalah orang-orang yang bertobat, beribadah, memuji (Allah), mengembara (demi ilmu dan agama), rukuk, sujud, menyuruh berbuat makruf dan mencegah dari yang mungkar dan yang memelihara hukum-hukum Allah. Dan gembirakanlah orang-orang yang beriman.'),
	(1348, 9, 113, 'مَا كَانَ لِلنَّبِيِّ وَالَّذِيْنَ اٰمَنُوْٓا اَنْ يَّسْتَغْفِرُوْا لِلْمُشْرِكِيْنَ وَلَوْ كَانُوْٓا اُولِيْ قُرْبٰى مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحٰبُ الْجَحِيْمِ ', 'm<u>aa</u> k<u>aa</u>na li<strong>l</strong>nnabiyyi wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu an yastaghfiruu lilmusyrikiina walaw k<u>aa</u>nuu ulii qurb<u>aa</u> min ba\'di m<u>aa</u> tabayyana lahum annahum a<u>sh</u>-<u>haa</u>bu <strong>a</strong>lja<u>h</u>iim<strong>i</strong>', 'Tidak pantas bagi Nabi dan orang-orang yang beriman memohonkan ampunan (kepada Allah) bagi orang-orang musyrik, sekalipun orang-orang itu kaum kerabat(nya), setelah jelas bagi mereka, bahwa orang-orang musyrik itu penghuni neraka Jahanam.'),
	(1349, 9, 114, 'وَمَا كَانَ اسْتِغْفَارُ اِبْرٰهِيْمَ لِاَبِيْهِ اِلَّا عَنْ مَّوْعِدَةٍ وَّعَدَهَآ اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَهٗٓ اَنَّهٗ عَدُوٌّ لِّلّٰهِ تَبَرَّاَ مِنْهُۗ اِنَّ اِبْرٰهِيْمَ لَاَوَّاهٌ حَلِيْمٌ ', 'wam<u>aa</u> k<u>aa</u>na istighf<u>aa</u>ru ibr<u>aa</u>hiima li-abiihi ill<u>aa</u> \'an maw\'idatin wa\'adah<u>aa</u> iyy<u>aa</u>hu falamm<u>aa</u> tabayyana lahu annahu \'aduwwun lill<u>aa</u>hi tabarra-a minhu inna ibr<u>aa</u>hiima la-aww<u>aa</u>hun <u>h</u>aliim<strong>un</strong>', 'Adapun permohonan ampunan Ibrahim (kepada Allah) untuk bapaknya, tidak lain hanyalah karena suatu janji yang telah diikrarkannya kepada bapaknya. Maka ketika jelas bagi Ibrahim bahwa bapaknya adalah musuh Allah, maka Ibrahim berlepas diri darinya. Sungguh, Ibrahim itu seorang yang sangat lembut hatinya lagi penyantun.'),
	(1350, 9, 115, 'وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْمًاۢ بَعْدَ اِذْ هَدٰىهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَّا يَتَّقُوْنَۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ', 'wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liyu<u>dh</u>illa qawman ba\'da i<u>dz</u> had<u>aa</u>hum <u>h</u>att<u>aa</u> yubayyina lahum m<u>aa</u> yattaquuna inna <strong>al</strong>l<u>aa</u>ha bikulli syay-in \'aliim<strong>un</strong>', 'Dan Allah sekali-kali tidak akan menyesatkan suatu kaum, setelah mereka diberi-Nya petunjuk, sehingga dapat dijelaskan kepada mereka apa yang harus mereka jauhi. Sungguh, Allah Maha Mengetahui segala sesuatu.'),
	(1351, 9, 116, 'اِنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُحْيٖ وَيُمِيْتُۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ', 'inna <strong>al</strong>l<u>aa</u>ha lahu mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i yu<u>h</u>yii wayumiitu wam<u>aa</u> lakum min duuni <strong>al</strong>l<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir<strong>in</strong>', 'Sesungguhnya Allah memiliki kekuasaan langit dan bumi. Dia menghidupkan dan mematikan. Tidak ada pelindung dan penolong bagimu selain Allah.'),
	(1352, 9, 117, 'لَقَدْ تَّابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهٰجِرِيْنَ وَالْاَنْصَارِ الَّذِيْنَ اتَّبَعُوْهُ فِيْ سَاعَةِ الْعُسْرَةِ مِنْۢ بَعْدِ مَا كَادَ يَزِيْغُ قُلُوْبُ فَرِيْقٍ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۗ اِنَّهٗ بِهِمْ رَءُوْفٌ رَّحِيْمٌ ۙ', 'laqad t<u>aa</u>ba <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> <strong>al</strong>nnabiyyi wa<strong>a</strong>lmuh<u>aa</u>jiriina wa<strong>a</strong>l-an<u>shaa</u>ri <strong>al</strong>la<u>dz</u>iina ittaba\'uuhu fii s<u>aa</u>\'ati <strong>a</strong>l\'usrati min ba\'di m<u>aa</u> k<u>aa</u>da yaziighu quluubu fariiqin minhum tsumma t<u>aa</u>ba \'alayhim innahu bihim rauufun ra<u>h</u>iim<strong>un</strong>', 'Sungguh, Allah telah menerima tobat Nabi, orang-orang Muhajirin dan orang-orang Ansar, yang mengikuti Nabi pada masa-masa sulit, setelah hati segolongan dari mereka hampir berpaling, kemudian Allah menerima tobat mereka. Sesungguhnya Allah Maha Pengasih, Maha Penyayang kepada mereka,'),
	(1353, 9, 118, 'وَّعَلَى الثَّلٰثَةِ الَّذِيْنَ خُلِّفُوْاۗ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّوْٓا اَنْ لَّا مَلْجَاَ مِنَ اللّٰهِ اِلَّآ اِلَيْهِۗ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوْبُوْاۗ اِنَّ اللّٰهَ هُوَ ', 'wa\'al<u>aa</u> <strong>al</strong>tstsal<u>aa</u>tsati <strong>al</strong>la<u>dz</u>iina khullifuu <u>h</u>att<u>aa</u> i<u>dzaa</u> <u>daa</u>qat \'alayhimu <strong>a</strong>l-ar<u>dh</u>u bim<u>aa</u> ra<u>h</u>ubat wa<u>daa</u>qat \'alayhim anfusuhum wa<u>zh</u>annuu an l<u>aa</u> malja-a mina <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> ilayhi tsumma t<u>aa</u>ba \'alayhim liyatuubuu inna <strong>al</strong>l<u>aa</u>ha huwa <strong>al</strong>ttaww<u>aa</u>bu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'dan terhadap tiga orang yang ditinggalkan. Hingga ketika bumi terasa sempit bagi mereka, padahal bumi itu luas dan jiwa mereka pun telah (pula terasa) sempit bagi mereka, serta mereka telah mengetahui bahwa tidak ada tempat lari dari (siksaan) Allah, melainkan kepada-Nya saja, kemudian Allah menerima tobat mereka agar mereka tetap dalam tobatnya. Sesungguhnya Allah Maha Penerima tobat, Maha Penyayang.'),
	(1354, 9, 119, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُوْنُوْا مَعَ الصّٰدِقِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ittaquu <strong>al</strong>l<u>aa</u>ha wakuunuu ma\'a <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Wahai orang-orang yang beriman! Bertakwalah kepada Allah, dan bersamalah kamu dengan orang-orang yang benar.'),
	(1355, 9, 120, 'مَا كَانَ لِاَهْلِ الْمَدِيْنَةِ وَمَنْ حَوْلَهُمْ مِّنَ الْاَعْرَابِ اَنْ يَّتَخَلَّفُوْا عَنْ رَّسُوْلِ اللّٰهِ وَلَا يَرْغَبُوْا بِاَنْفُسِهِمْ عَنْ نَّفْسِهٖۗ ذٰلِكَ بِاَنَّهُمْ لَا يُصِيْبُهُمْ ظَمَاٌ وَّلَا نَصَبٌ وَّلَا مَخْمَصَةٌ فِيْ سَبِيْلِ الل', 'm<u>aa</u> k<u>aa</u>na li-ahli <strong>a</strong>lmadiinati waman <u>h</u>awlahum mina <strong>a</strong>l-a\'r<u>aa</u>bi an yatakhallafuu \'an rasuuli <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> yarghabuu bi-anfusihim \'an nafsihi <u>dzaa</u>lika bi-annahum l<u>aa</u> yu<u>sh</u>iibuhum <u>zh</u>amaun wal<u>aa</u> na<u>sh</u>abun wal<u>aa</u> makhma<u>sh</u>atun fii sabiili <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> ya<u>th</u>auuna maw<u>th</u>i-an yaghii<u>zh</u>u <strong>a</strong>lkuff<u>aa</u>ra wal<u>aa</u> yan<u>aa</u>luuna min \'aduwwin naylan ill<u>aa</u> kutiba lahum bihi \'amalun <u>shaa</u>li<u>h</u>un inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Tidak pantas bagi penduduk Madinah dan orang-orang Arab Badui yang berdiam di sekitar mereka, tidak turut menyertai Rasulullah (pergi berperang) dan tidak pantas (pula) bagi mereka lebih mencintai diri mereka daripada (mencintai) diri Rasul. Yang demikian itu karena mereka tidak ditimpa kehausan, kepayahan dan kelaparan di jalan Allah, dan tidak (pula) menginjak suatu tempat yang membangkitkan amarah orang-orang kafir, dan tidak menimpakan suatu bencana kepada musuh, kecuali (semua) itu akan dituliskan bagi mereka sebagai suatu amal kebajikan. Sungguh, Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik,'),
	(1356, 9, 121, 'وَلَا يُنْفِقُوْنَ نَفَقَةً صَغِيْرَةً وَّلَا كَبِيْرَةً وَّلَا يَقْطَعُوْنَ وَادِيًا اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوْا يَعْمَلُوْنَ ', 'wal<u>aa</u> yunfiquuna nafaqatan <u>sh</u>aghiiratan wal<u>aa</u> kabiiratan wal<u>aa</u> yaq<u>th</u>a\'uuna w<u>aa</u>diyan ill<u>aa</u> kutiba lahum liyajziyahumu <strong>al</strong>l<u>aa</u>hu a<u>h</u>sana m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'dan tidaklah mereka memberikan infak, baik yang kecil maupun yang besar dan tidak (pula) melintasi suatu lembah (berjihad), kecuali akan dituliskan bagi mereka (sebagai amal kebajikan), untuk diberi balasan oleh Allah (dengan) yang lebih baik daripada apa yang telah mereka kerjakan.'),
	(1357, 9, 122, '۞ وَمَا كَانَ الْمُؤْمِنُوْنَ لِيَنْفِرُوْا كَاۤفَّةًۗ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِّنْهُمْ طَاۤىِٕفَةٌ لِّيَتَفَقَّهُوْا فِى الدِّيْنِ وَلِيُنْذِرُوْا قَوْمَهُمْ اِذَا رَجَعُوْٓا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُوْنَ ࣖ ', 'wam<u>aa</u> k<u>aa</u>na <strong>a</strong>lmu/minuuna liyanfiruu k<u>aa</u>ffatan falawl<u>aa</u> nafara min kulli firqatin minhum <u>th</u><u>aa</u>-ifatun liyatafaqqahuu fii <strong>al</strong>ddiini waliyun<u>dz</u>iruu qawmahum i<u>dzaa</u> raja\'uu ilayhim la\'allahum ya<u>hts</u>aruun<strong>a</strong>', 'Dan tidak sepatutnya orang-orang mukmin itu semuanya pergi (ke medan perang). Mengapa sebagian dari setiap golongan di antara mereka tidak pergi untuk memperdalam pengetahuan agama mereka dan untuk memberi peringatan kepada kaumnya apabila mereka telah kembali, agar mereka dapat menjaga dirinya.'),
	(1358, 9, 123, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قَاتِلُوا الَّذِيْنَ يَلُوْنَكُمْ مِّنَ الْكُفَّارِ وَلْيَجِدُوْا فِيْكُمْ غِلْظَةًۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu q<u>aa</u>tiluu <strong>al</strong>la<u>dz</u>iina yaluunakum mina <strong>a</strong>lkuff<u>aa</u>ri walyajiduu fiikum ghil<u>zh</u>atan wa<strong>i</strong>\'lamuu anna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Wahai orang yang beriman! Perangilah orang-orang kafir yang di sekitar kamu, dan hendaklah mereka merasakan sikap tegas darimu, dan ketahuilah bahwa Allah beserta orang yang bertakwa.'),
	(1359, 9, 124, 'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ فَمِنْهُمْ مَّنْ يَّقُوْلُ اَيُّكُمْ زَادَتْهُ هٰذِهٖٓ اِيْمَانًاۚ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَزَادَتْهُمْ اِيْمَانًا وَّهُمْ يَسْتَبْشِرُوْنَ', 'wa-i<u>dzaa</u> m<u>aa</u> unzilat suuratun faminhum man yaquulu ayyukum z<u>aa</u>dat-hu h<u>aadz</u>ihi iim<u>aa</u>nan fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu faz<u>aa</u>dat-hum iim<u>aa</u>nan wahum yastabsyiruun<strong>a</strong>', 'Dan apabila diturunkan suatu surah, maka di antara mereka (orang-orang munafik) ada yang berkata, “Siapakah di antara kamu yang bertambah imannya dengan (turunnya) surah ini?” Adapun orang-orang yang beriman, maka surah ini menambah imannya, dan mereka merasa gembira.'),
	(1360, 9, 125, 'وَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ فَزَادَتْهُمْ رِجْسًا اِلٰى رِجْسِهِمْ وَمَاتُوْا وَهُمْ كٰفِرُوْنَ ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina fii quluubihim mara<u>dh</u>un faz<u>aa</u>dat-hum rijsan il<u>aa</u> rijsihim wam<u>aa</u>tuu wahum k<u>aa</u>firuun<strong>a</strong>', 'Dan adapun orang-orang yang di dalam hatinya ada penyakit, maka (dengan surah itu) akan menambah kekafiran mereka yang telah ada dan mereka akan mati dalam keadaan kafir.'),
	(1361, 9, 126, 'اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُوْنَ فِيْ كُلِّ عَامٍ مَّرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوْبُوْنَ وَلَا هُمْ يَذَّكَّرُوْنَ ', 'awa l<u>aa</u> yarawna annahum yuftanuuna fii kulli \'<u>aa</u>min marratan aw marratayni tsumma l<u>aa</u> yatuubuuna wal<u>aa</u> hum ya<u>dzdz</u>akkaruun<strong>a</strong>', 'Dan tidakkah mereka (orang-orang munafik) memperhatikan bahwa mereka diuji sekali atau dua kali setiap tahun, namun mereka tidak (juga) bertobat dan tidak (pula) mengambil pelajaran?'),
	(1362, 9, 127, 'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ نَّظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۗ هَلْ يَرٰىكُمْ مِّنْ اَحَدٍ ثُمَّ انْصَرَفُوْاۗ صَرَفَ اللّٰهُ قُلُوْبَهُمْ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ', 'wa-i<u>dzaa</u> m<u>aa</u> unzilat suuratun na<u>zh</u>ara ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in hal yar<u>aa</u>kum min a<u>h</u>adin tsumma in<u>sh</u>arafuu <u>sh</u>arafa <strong>al</strong>l<u>aa</u>hu quluubahum bi-annahum qawmun l<u>aa</u> yafqahuun<strong>a</strong>', 'Dan apabila diturunkan suatu surah, satu sama lain di antara mereka saling berpandangan (sambil berkata), “Adakah seseorang (dari kaum muslimin) yang melihat kamu?”  Setelah itu mereka pun pergi. Allah memalingkan hati mereka disebabkan mereka adalah kaum yang tidak memahami.'),
	(1363, 9, 128, 'لَقَدْ جَاۤءَكُمْ رَسُوْلٌ مِّنْ اَنْفُسِكُمْ عَزِيْزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيْصٌ عَلَيْكُمْ بِالْمُؤْمِنِيْنَ رَءُوْفٌ رَّحِيْمٌ ', 'laqad j<u>aa</u>-akum rasuulun min anfusikum \'aziizun \'alayhi m<u>aa </u>\'anittum <u>h</u>arii<u>sh</u>un \'alaykum bi<strong>a</strong>lmu/miniina rauufun ra<u>h</u>iim<strong>un</strong>', 'Sungguh, telah datang kepadamu seorang rasul dari kaummu sendiri, berat terasa olehnya penderitaan yang kamu alami, (dia) sangat menginginkan (keimanan dan keselamatan) bagimu, penyantun dan penyayang terhadap orang-orang yang beriman.'),
	(1364, 9, 129, 'فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيْمِ ࣖ', 'fa-in tawallaw faqul <u>h</u>asbiya <strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa \'alayhi tawakkaltu wahuwa rabbu <strong>a</strong>l\'arsyi <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Maka jika mereka berpaling (dari keimanan), maka katakanlah (Muhammad), “Cukuplah Allah bagiku; tidak ada tuhan selain Dia. Hanya kepada-Nya aku bertawakal, dan Dia adalah Tuhan yang memiliki ‘Arsy (singgasana) yang agung.”    '),
	(1365, 10, 1, 'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِ ', 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>l<u>h</u>akiim<strong>i</strong>', 'Alif Lam Ra. Inilah ayat-ayat Al-Qur\'an yang penuh hikmah.'),
	(1366, 10, 2, 'اَكَانَ لِلنَّاسِ عَجَبًا اَنْ اَوْحَيْنَآ اِلٰى رَجُلٍ مِّنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِيْنَ اٰمَنُوْٓا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ ۗ قَالَ الْكٰفِرُوْنَ اِنَّ هٰذَا لَسٰحِرٌ مُّبِيْنٌ ', 'ak<u>aa</u>na li<strong>l</strong>nn<u>aa</u>si \'ajaban an aw<u>h</u>ayn<u>aa</u> il<u>aa</u> rajulin minhum an an<u>dz</u>iri <strong>al</strong>nn<u>aa</u>sa wabasysyiri <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu anna lahum qadama <u>sh</u>idqin \'inda rabbihim q<u>aa</u>la <strong>a</strong>lk<u>aa</u>firuuna inna h<u>aadzaa</u> las<u>aah</u>irun mubiin<strong>un</strong>', 'Pantaskah manusia menjadi heran bahwa Kami memberi wahyu kepada seorang laki-laki di antara mereka, “Berilah peringatan kepada manusia dan gembirakanlah orang-orang beriman bahwa mereka mempunyai kedudukan yang tinggi di sisi Tuhan.” Orang-orang kafir berkata, “Orang ini (Muhammad) benar-benar pesihir.”'),
	(1367, 10, 3, 'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۗ مَا مِنْ شَفِيْعٍ اِلَّا مِنْۢ بَعْدِ اِذْنِهٖۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوْهُۗ اَفَلَا تَذَكَّرُوْنَ ', 'inna rabbakumu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'arsyi yudabbiru <strong>a</strong>l-amra m<u>aa</u> min syafii\'in ill<u>aa</u> min ba\'di i<u>dz</u>nihi <u>dzaa</u>likumu <strong>al</strong>l<u>aa</u>hu rabbukum fa<strong>u</strong>\'buduuhu afal<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Sesungguhnya Tuhan kamu Dialah Allah yang menciptakan langit dan bumi dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy (singgasana) untuk mengatur segala urusan. Tidak ada yang dapat memberi syafaat kecuali setelah ada izin-Nya. Itulah Allah, Tuhanmu, maka sembahlah Dia. Apakah kamu tidak mengambil pelajaran?'),
	(1368, 10, 4, 'اِلَيْهِ مَرْجِعُكُمْ جَمِيْعًاۗ وَعْدَ اللّٰهِ حَقًّاۗ اِنَّهٗ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ بِالْقِسْطِۗ وَالَّذِيْنَ كَفَرُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُو', 'ilayhi marji\'ukum jamii\'an wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqan innahu yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu liyajziya <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti bi<strong>a</strong>lqis<u>th</u>i wa<strong>a</strong>lla<u>dz</u>iina kafaruu lahum syar<u>aa</u>bun min <u>h</u>amiimin wa\'a<u>dzaa</u>bun <strong>a</strong>liimun bim<u>aa</u> k<u>aa</u>nuu yakfuruun<strong>a</strong>', 'Hanya kepada-Nya kamu semua akan kembali. Itu merupakan janji Allah yang benar dan pasti. Sesungguhnya Dialah yang memulai penciptaan makhluk kemudian mengulanginya (menghidupkannya kembali setelah berbangkit), agar Dia memberi balasan kepada orang-orang yang beriman dan mengerjakan kebajikan dengan adil. Sedangkan untuk orang-orang kafir (disediakan) minuman air yang mendidih dan siksaan yang pedih karena kekafiran mereka.'),
	(1369, 10, 5, 'هُوَ الَّذِيْ جَعَلَ الشَّمْسَ ضِيَاۤءً وَّالْقَمَرَ نُوْرًا وَّقَدَّرَهٗ مَنَازِلَ لِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۗ يُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ', 'huwa <strong>al</strong>la<u>dz</u>ii ja\'ala <strong>al</strong>sysyamsa <u>dh</u>iy<u>aa</u>-an wa<strong>a</strong>lqamara nuuran waqaddarahu man<u>aa</u>zila lita\'lamuu \'adada <strong>al</strong>ssiniina wa<strong>a</strong>l<u>h</u>is<u>aa</u>ba m<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu <u>dzaa</u>lika ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi yufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun<strong>a</strong>', 'Dialah yang menjadikan matahari bersinar dan bulan bercahaya, dan Dialah yang menetapkan tempat-tempat orbitnya, agar kamu mengetahui bilangan tahun, dan perhitungan (waktu). Allah tidak menciptakan demikian itu melainkan dengan benar. Dia menjelaskan tanda-tanda (kebesaran-Nya) kepada orang-orang yang mengetahui.'),
	(1370, 10, 6, 'اِنَّ فِى اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّتَّقُوْنَ ', 'inna fii ikhtil<u>aa</u>fi <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri wam<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i la<u>aa</u>y<u>aa</u>tin liqawmin yattaquun<strong>a</strong>', 'Sesungguhnya pada pergantian malam dan siang dan pada apa yang diciptakan Allah di langit dan di bumi, pasti terdapat tanda-tanda (kebesaran-Nya) bagi orang-orang yang bertakwa.'),
	(1371, 10, 7, 'اِنَّ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا وَرَضُوْا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَـَٔنُّوْا بِهَا وَالَّذِيْنَ هُمْ عَنْ اٰيٰتِنَا غٰفِلُوْنَۙ', 'inna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> wara<u>dh</u>uu bi<strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wa<strong>i</strong><u>th</u>ma-annuu bih<u>aa</u> wa<strong>a</strong>lla<u>dz</u>iina hum \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> gh<u>aa</u>filuun<strong>a</strong>', 'Sesungguhnya orang-orang yang tidak mengharapkan (tidak percaya akan) pertemuan dengan Kami, dan merasa puas dengan kehidupan dunia serta merasa tenteram dengan (kehidupan) itu, dan orang-orang yang melalaikan ayat-ayat Kami,'),
	(1372, 10, 8, 'اُولٰۤىِٕكَ مَأْوٰىهُمُ النَّارُ بِمَا كَانُوْا يَكْسِبُوْنَ ', 'ul<u>aa</u>-ika ma/w<u>aa</u>humu <strong>al</strong>nn<u>aa</u>ru bim<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'mereka itu tempatnya di neraka, karena apa yang telah mereka lakukan.'),
	(1373, 10, 9, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ يَهْدِيْهِمْ رَبُّهُمْ بِاِيْمَانِهِمْۚ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ فِيْ جَنّٰتِ النَّعِيْمِ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti yahdiihim rabbuhum bi-iim<u>aa</u>nihim tajrii min ta<u>h</u>tihimu <strong>a</strong>l-anh<u>aa</u>ru fii jann<u>aa</u>ti <strong>al</strong>nna\'iim<strong>i</strong>', 'Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan, niscaya diberi petunjuk oleh Tuhan karena keimanannya. Mereka di dalam surga yang penuh kenikmatan, mengalir di bawahnya sungai-sungai.'),
	(1374, 10, 10, 'دَعْوٰىهُمْ فِيْهَا سُبْحٰنَكَ اللهم وَتَحِيَّتُهُمْ فِيْهَا سَلٰمٌۚ وَاٰخِرُ دَعْوٰىهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ', 'da\'w<u>aa</u>hum fiih<u>aa</u> sub<u>haa</u>naka <strong>al</strong>l<u>aa</u>humma wata<u>h</u>iyyatuhum fiih<u>aa</u> sal<u>aa</u>mun wa<u>aa</u>khiru da\'w<u>aa</u>hum ani <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Doa mereka di dalamnya ialah, “Subhanakallahumma” (Mahasuci Engkau, ya Tuhan kami), dan salam penghormatan mereka ialah, “Salam” (salam sejahtera). Dan penutup doa mereka ialah, “Al-hamdu lillahi Rabbil ‘alamin” (segala puji bagi Allah Tuhan seluruh alam).'),
	(1375, 10, 11, '۞ وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۗ فَنَذَرُ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ', 'walaw yu\'ajjilu <strong>al</strong>l<u>aa</u>hu li<strong>l</strong>nn<u>aa</u>si <strong>al</strong>sysyarra isti\'j<u>aa</u>lahum bi<strong>a</strong>lkhayri laqu<u>dh</u>iya ilayhim ajaluhum fana<u>dz</u>aru <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun<strong>a</strong>', 'Dan kalau Allah menyegerakan keburukan bagi manusia seperti permintaan mereka untuk menyegerakan kebaikan, pasti diakhiri umur mereka. Namun Kami biarkan orang-orang yang tidak mengharapkan pertemuan dengan Kami, bingung di dalam kesesatan mereka.'),
	(1376, 10, 12, 'وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْۢبِهٖٓ اَوْ قَاعِدًا اَوْ قَاۤىِٕمًا ۚفَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهٗ مَرَّ كَاَنْ لَّمْ يَدْعُنَآ اِلٰى ضُرٍّ مَّسَّهٗۗ  كَذٰلِكَ زُيِّنَ لِلْمُسْرِفِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ', 'wa-i<u>dzaa</u> massa <strong>a</strong>l-ins<u>aa</u>na <strong>al</strong><u>dhdh</u>urru da\'<u>aa</u>n<u>aa</u> lijanbihi aw q<u>aa</u>\'idan aw q<u>aa</u>-iman falamm<u>aa</u> kasyafn<u>aa</u> \'anhu <u>dh</u>urrahu marra ka-an lam yad\'un<u>aa</u> il<u>aa</u> <u>dh</u>urrin massahu ka<u>dzaa</u>lika zuyyina lilmusrifiina m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan apabila manusia ditimpa bahaya dia berdoa kepada Kami dalam keadaan berbaring, duduk atau berdiri, tetapi setelah Kami hilangkan bahaya itu darinya, dia kembali (ke jalan yang sesat), seolah-olah dia tidak pernah berdoa kepada Kami untuk (menghilangkan) bahaya yang telah menimpanya. Demikianlah dijadikan terasa indah bagi orang-orang yang melampaui batas apa yang mereka kerjakan.'),
	(1377, 10, 13, 'وَلَقَدْ اَهْلَكْنَا الْقُرُوْنَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوْاۙ وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَمَا كَانُوْا لِيُؤْمِنُوْا ۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ ', 'walaqad ahlakn<u>aa</u> <strong>a</strong>lquruuna min qablikum lamm<u>aa</u> <u>zh</u>alamuu waj<u>aa</u>-at-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti wam<u>aa</u> k<u>aa</u>nuu liyu/minuu ka<u>dzaa</u>lika najzii <strong>a</strong>lqawma <strong>a</strong>lmujrimiin<strong>a</strong>', ' Dan sungguh, Kami telah membinasakan umat-umat sebelum kamu, ketika mereka berbuat zalim, padahal para rasul mereka telah datang membawa keterangan-keterangan (yang nyata), tetapi mereka sama sekali tidak mau beriman. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat dosa.'),
	(1378, 10, 14, 'ثُمَّ جَعَلْنٰكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِ مِنْۢ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُوْنَ ', 'tsumma ja\'aln<u>aa</u>kum khal<u>aa</u>-ifa fii <strong>a</strong>l-ar<u>dh</u>i min ba\'dihim linan<u>zh</u>ura kayfa ta\'maluun<strong>a</strong>', 'Kemudian Kami jadikan kamu sebagai pengganti-pengganti (mereka) di bumi setelah mereka, untuk Kami lihat bagaimana kamu berbuat.'),
	(1379, 10, 15, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنٰتٍۙ قَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَآ اَوْ بَدِّلْهُ ۗ قُلْ مَا يَكُوْنُ لِيْٓ اَنْ اُبَدِّلَهٗ مِنْ تِلْقَاۤئِ نَفْسِيْ ۚاِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ ۚ اِ', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> i/ti biqur-<u>aa</u>nin ghayri h<u>aadzaa</u> aw baddilhu qul m<u>aa</u> yakuunu lii an ubaddilahu min tilq<u>aa</u>-i nafsii in attabi\'u ill<u>aa</u> m<u>aa</u> yuu<u>haa</u> ilayya innii akh<u>aa</u>fu in \'a<u>sh</u>aytu rabbii \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Dan apabila dibacakan kepada mereka ayat-ayat Kami dengan jelas, orang-orang yang tidak mengharapkan pertemuan dengan Kami berkata, “Datangkanlah kitab selain Al-Qur\'an ini atau gantilah.” Katakanlah (Muhammad), “Tidaklah pantas bagiku menggantinya atas kemauanku sendiri. Aku hanya  mengikuti apa yang diwahyukan kepadaku. Aku benar-benar takut akan azab hari yang besar (Kiamat) jika mendurhakai Tuhanku.”'),
	(1380, 10, 16, 'قُلْ لَّوْ شَاۤءَ اللّٰهُ مَا تَلَوْتُهٗ عَلَيْكُمْ وَلَآ اَدْرٰىكُمْ بِهٖ ۖفَقَدْ لَبِثْتُ فِيْكُمْ عُمُرًا مِّنْ قَبْلِهٖۗ اَفَلَا تَعْقِلُوْنَ', 'qul law sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> talawtuhu \'alaykum wal<u>aa</u> adr<u>aa</u>kum bihi faqad labitstu fiikum \'umuran min qablihi afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Katakanlah (Muhammad), “Jika Allah menghendaki, niscaya aku tidak membacakannya kepadamu dan Allah tidak (pula) memberitahukannya kepadamu.” Aku telah tinggal bersamamu beberapa lama sebelumnya (sebelum turun Al-Qur\'an). Apakah kamu tidak mengerti?'),
	(1381, 10, 17, 'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الْمُجْرِمُوْنَ ', 'faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi innahu l<u>aa</u> yufli<u>h</u>u <strong>a</strong>lmujrimuun<strong>a</strong>', 'Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah atau mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang berbuat dosa itu tidak akan beruntung.'),
	(1382, 10, 18, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُوْلُوْنَ هٰٓؤُلَاۤءِ شُفَعَاۤؤُنَا عِنْدَ اللّٰهِ ۗقُلْ اَتُنَبِّـُٔوْنَ اللّٰهَ بِمَا لَا يَعْلَمُ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْ', 'waya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ya<u>dh</u>urruhum wal<u>aa</u> yanfa\'uhum wayaquuluuna h<u>aa</u>ul<u>aa</u>-i syufa\'<u>aa</u>un<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi qul atunabbi-uuna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> l<u>aa</u> ya\'lamu fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Dan mereka menyembah selain Allah, sesuatu yang tidak dapat mendatangkan bencana kepada mereka dan tidak (pula) memberi manfaat, dan mereka berkata, “Mereka itu adalah pemberi syafaat kami di hadapan Allah.” Katakanlah, “Apakah kamu akan memberitahu kepada Allah sesuatu yang tidak diketahui-Nya apa yang di langit dan tidak (pula) yang di bumi?” Mahasuci Allah dan Mahatinggi dari apa yang mereka persekutukan itu.'),
	(1383, 10, 19, 'وَمَا كَانَ النَّاسُ اِلَّآ اُمَّةً وَّاحِدَةً فَاخْتَلَفُوْاۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيْمَا فِيْهِ يَخْتَلِفُوْنَ ', 'wam<u>aa</u> k<u>aa</u>na <strong>al</strong>nn<u>aa</u>su ill<u>aa</u> ummatan w<u>aah</u>idatan fa<strong>i</strong>khtalafuu walawl<u>aa</u> kalimatun sabaqat min rabbika laqu<u>dh</u>iya baynahum fiim<u>aa</u> fiihi yakhtalifuun<strong>a</strong>', 'Dan manusia itu dahulunya hanyalah satu umat, kemudian mereka berselisih. Kalau tidak karena suatu ketetapan yang telah ada dari Tuhanmu, pastilah telah diberi keputusan (di dunia) di antara mereka, tentang apa yang mereka perselisihkan itu.'),
	(1384, 10, 20, 'وَيَقُوْلُوْنَ لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُوْاۚ اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ࣖ ', 'wayaquuluuna lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi faqul innam<u>aa</u> <strong>a</strong>lghaybu lill<u>aa</u>hi fa<strong>i</strong>nta<u>zh</u>iruu innii ma\'akum mina <strong>a</strong>lmunta<u>zh</u>iriin<strong>a</strong>', 'Dan mereka berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu bukti (mukjizat) dari Tuhannya?” Katakanlah, “Sungguh, segala yang gaib itu hanya milik Allah; sebab itu tunggu (sajalah) olehmu. Ketahuilah aku juga menunggu bersama kamu.”'),
	(1385, 10, 21, 'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً مِّنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُمْ اِذَا لَهُمْ مَّكْرٌ فِيْٓ اٰيٰتِنَاۗ قُلِ اللّٰهُ اَسْرَعُ مَكْرًاۗ اِنَّ رُسُلَنَا يَكْتُبُوْنَ مَا تَمْكُرُوْنَ ', 'wa-i<u>dzaa</u> a<u>dz</u>aqn<u>aa</u> <strong>al</strong>nn<u>aa</u>sa ra<u>h</u>matan min ba\'di <u>dh</u>arr<u>aa</u>-a massat-hum i<u>dzaa</u> lahum makrun fii <u>aa</u>y<u>aa</u>tin<u>aa</u> quli <strong>al</strong>l<u>aa</u>hu asra\'u makran inna rusulan<u>aa</u> yaktubuuna m<u>aa</u> tamkuruun<strong>a</strong>', 'Dan apabila Kami memberikan suatu rahmat kepada manusia, setelah mereka ditimpa bencana, mereka segera melakukan segala tipu daya (menentang) ayat-ayat Kami. Katakanlah, “Allah lebih cepat pembalasannya (atas tipu daya itu).” Sesungguhnya malaikat-malaikat Kami mencatat tipu dayamu.'),
	(1386, 10, 22, 'هُوَ الَّذِيْ يُسَيِّرُكُمْ فِى الْبَرِّ وَالْبَحْرِۗ حَتّٰٓى اِذَا كُنْتُمْ فِىْ الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِرِيْحٍ طَيِّبَةٍ وَّفَرِحُوْا بِهَا جَاۤءَتْهَا رِيْحٌ عَاصِفٌ وَّجَاۤءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَّظَنُّوْٓا اَنَّهُمْ اُحِيْطَ بِهِ', 'huwa <strong>al</strong>la<u>dz</u>ii yusayyirukum fii <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri <u>h</u>att<u>aa</u> i<u>dzaa</u> kuntum fii <strong>a</strong>lfulki wajarayna bihim birii<u>h</u>in <u>th</u>ayyibatin wafari<u>h</u>uu bih<u>aa</u> j<u>aa</u>-at-h<u>aa</u> rii<u>h</u>un \'<u>aas</u>ifun waj<u>aa</u>-ahumu <strong>a</strong>lmawju min kulli mak<u>aa</u>nin wa<u>zh</u>annuu annahum u<u>h</u>ii<u>th</u>a bihim da\'awuu <strong>al</strong>l<u>aa</u>ha mukhli<u>sh</u>iina lahu <strong>al</strong>ddiina la-in anjaytan<u>aa</u> min h<u>aadz</u>ihi lanakuunanna mina <strong>al</strong>sysy<u>aa</u>kiriin<strong>a</strong>', 'Dialah Tuhan yang menjadikan kamu dapat berjalan di daratan, (dan berlayar) di lautan. Sehingga ketika kamu berada di dalam kapal, dan meluncurlah (kapal) itu membawa mereka (orang-orang yang ada di dalamnya) dengan tiupan angin yang baik, dan mereka bergembira karenanya; tiba-tiba datanglah badai dan gelombang menimpanya dari segenap penjuru, dan mereka mengira telah terkepung (bahaya), maka mereka berdoa dengan tulus ikhlas kepada Allah semata. (Seraya berkata), “Sekiranya Engkau menyelamatkan kami dari (bahaya) ini, pasti kami termasuk orang-orang yang bersyukur.”'),
	(1387, 10, 23, 'فَلَمَّآ اَنْجٰىهُمْ اِذَا هُمْ يَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ ۗيٰٓاَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْ مَّتَاعَ الْحَيٰوةِ الدُّنْيَاۖ ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'falamm<u>aa</u> anj<u>aa</u>hum i<u>dzaa</u> hum yabghuuna fii <strong>a</strong>l-ar<u>dh</u>i bighayri <strong>a</strong>l<u>h</u>aqqi y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su innam<u>aa</u> baghyukum \'al<u>aa</u> anfusikum mat<u>aa</u>\'a <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> tsumma ilayn<u>aa</u> marji\'ukum fanunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a </strong>', 'Tetapi ketika Allah menyelamatkan mereka, malah mereka berbuat kezaliman di bumi tanpa (alasan) yang benar. Wahai manusia! Sesungguhnya kezalimanmu bahayanya akan menimpa dirimu sendiri; itu hanya kenikmatan hidup duniawi, selanjutnya kepada Kamilah kembalimu, kelak akan Kami kabarkan kepadamu apa yang telah kamu kerjakan.'),
	(1388, 10, 24, 'اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُ ۗحَتّٰٓى اِذَآ اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ اَهْلُهَآ اَنَّهُمْ قٰدِرُوْنَ عَ', 'innam<u>aa</u> matsalu <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> kam<u>aa</u>-in anzaln<u>aa</u>hu mina <strong>al</strong>ssam<u>aa</u>-i fa<strong>i</strong>khtala<u>th</u>a bihi nab<u>aa</u>tu <strong>a</strong>l-ar<u>dh</u>i mimm<u>aa</u> ya/kulu <strong>al</strong>nn<u>aa</u>su wa<strong>a</strong>l-an\'<u>aa</u>mu <u>h</u>att<u>aa</u> i<u>dzaa</u> akha<u>dz</u>ati <strong>a</strong>l-ar<u>dh</u>u zukhrufah<u>aa</u> wa<strong>i</strong>zzayyanat wa<u>zh</u>anna ahluh<u>aa</u> annahum q<u>aa</u>diruuna \'alayh<u>aa</u> at<u>aa</u>h<u>aa</u> amrun<u>aa</u> laylan aw nah<u>aa</u>ran faja\'aln<u>aa</u>h<u>aa</u> <u>h</u>a<u>sh</u>iidan ka-an lam taghna bi<strong>a</strong>l-amsi ka<u>dzaa</u>lika nufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin yatafakkaruun<strong>a</strong>', 'Sesungguhnya perumpamaan kehidupan duniawi itu, hanya seperti air (hujan) yang Kami turunkan dari langit, lalu tumbuhlah tanaman-tanaman bumi dengan subur (karena air itu), di antaranya ada yang dimakan manusia dan hewan ternak. Hingga apabila bumi itu telah sempurna keindahannya, dan berhias, dan pemiliknya mengira bahwa mereka pasti menguasainya (memetik hasilnya), datanglah kepadanya azab Kami pada waktu malam atau siang, lalu Kami jadikan (tanaman)nya seperti tanaman yang sudah disabit, seakan-akan belum pernah tumbuh kemarin. Demikianlah Kami menjelaskan tanda-tanda (kekuasaan Kami) kepada orang yang berpikir.'),
	(1389, 10, 25, 'وَاللّٰهُ يَدْعُوْٓ اِلٰى دَارِ السَّلٰمِ ۚوَيَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'wa<strong>al</strong>l<u>aa</u>hu yad\'uu il<u>aa</u> d<u>aa</u>ri <strong>al</strong>ssal<u>aa</u>mi wayahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dan Allah menyeru (manusia) ke Darus-salam (surga), dan memberikan petunjuk kepada orang yang Dia kehendaki ke jalan yang lurus (Islam).'),
	(1390, 10, 26, '۞ لِلَّذِيْنَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌ  ۗوَلَا يَرْهَقُ وُجُوْهَهُمْ قَتَرٌ وَّلَا ذِلَّةٌ  ۗاُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ هُمْ فِيْهَا خٰلِدُوْنَ ', 'lilla<u>dz</u>iina a<u>h</u>sanuu <strong>a</strong>l<u>h</u>usn<u>aa</u> waziy<u>aa</u>datun wal<u>aa</u> yarhaqu wujuuhahum qatarun wal<u>aa</u> <u>dz</u>illatun ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Bagi orang-orang yang berbuat baik, ada pahala yang terbaik (surga) dan tambahannya (kenikmatan melihat Allah). Dan wajah mereka tidak ditutupi debu hitam dan tidak (pula) dalam kehinaan. Mereka itulah penghuni surga, mereka kekal di dalamnya.'),
	(1391, 10, 27, 'وَالَّذِيْنَ كَسَبُوا السَّيِّاٰتِ جَزَاۤءُ سَيِّئَةٍ ۢبِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌ  ۗمَا لَهُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ  كَاَنَّمَآ اُغْشِيَتْ وُجُوْهُهُمْ قِطَعًا مِّنَ الَّيْلِ مُظْلِمًاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚهُمْ فِيْهَا خٰلِدُوْ', 'wa<strong>a</strong>lla<u>dz</u>iina kasabuu <strong>al</strong>ssayyi-<u>aa</u>ti jaz<u>aa</u>u sayyi-atin bimitslih<u>aa</u> watarhaquhum <u>dz</u>illatun m<u>aa</u> lahum mina <strong>al</strong>l<u>aa</u>hi min \'<u>aas</u>imin ka-annam<u>aa</u> ughsyiyat wujuuhuhum qi<u>th</u>a\'an mina <strong>al</strong>layli mu<u>zh</u>liman ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Adapun orang-orang yang berbuat kejahatan (akan mendapat) balasan kejahatan yang setimpal dan mereka diselubungi kehinaan. Tidak ada bagi mereka seorang pelindung pun dari (azab) Allah, seakan-akan wajah mereka ditutupi dengan kepingan-kepingan malam yang gelap gulita. Mereka itulah penghuni neraka, mereka kekal di dalamnya.           '),
	(1392, 10, 28, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْا مَكَانَكُمْ اَنْتُمْ وَشُرَكَاۤؤُكُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَاۤؤُهُمْ مَّا كُنْتُمْ اِيَّانَا تَعْبُدُوْنَ', 'wayawma na<u>h</u>syuruhum jamii\'an tsumma naquulu lilla<u>dz</u>iina asyrakuu mak<u>aa</u>nakum antum wasyurak<u>aa</u>ukum fazayyaln<u>aa</u> baynahum waq<u>aa</u>la syurak<u>aa</u>uhum m<u>aa</u> kuntum iyy<u>aa</u>n<u>aa</u> ta\'buduun<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) itu Kami mengumpulkan mereka semuanya, kemudian Kami berkata kepada orang yang mempersekutukan (Allah), “Tetaplah di tempatmu, kamu dan para sekutumu.” Lalu Kami pisahkan mereka dan berkatalah sekutu-sekutu mereka, “Kamu sekali-kali tidak pernah menyembah kami. '),
	(1393, 10, 29, 'فَكَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغٰفِلِيْنَ ', 'fakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi syahiidan baynan<u>aa</u> wabaynakum in kunn<u>aa</u> \'an \'ib<u>aa</u>datikum lagh<u>aa</u>filiin<strong>a</strong>', 'Maka cukuplah Allah menjadi saksi antara kami dengan kamu, sebab kami tidak tahu-menahu tentang penyembahan kamu (kepada kami).”'),
	(1394, 10, 30, 'هُنَالِكَ تَبْلُوْا كُلُّ نَفْسٍ مَّآ اَسْلَفَتْ وَرُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ࣖ ', 'hun<u>aa</u>lika tabluu kullu nafsin m<u>aa</u> aslafat warudduu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi mawl<u>aa</u>humu <strong>a</strong>l<u>h</u>aqqi wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Di tempat itu (padang Mahsyar), setiap jiwa merasakan pembalasan dari apa yang telah dikerjakannya (dahulu) dan mereka dikembalikan kepada Allah, pelindung mereka yang sebenarnya, dan lenyaplah dari mereka apa (pelindung palsu) yang mereka ada-adakan.'),
	(1395, 10, 31, 'قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِ اَمَّنْ يَّمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُّخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُّدَبِّرُ الْاَمْرَۗ فَسَيَقُوْلُوْنَ اللّٰهُ ۚفَقُلْ اَفَلَا تَتَّقُوْن', 'qul man yarzuqukum mina <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i amman yamliku <strong>al</strong>ssam\'a wa<strong>a</strong>l-ab<u>shaa</u>ra waman yukhriju <strong>a</strong>l<u>h</u>ayya mina <strong>a</strong>lmayyiti wayukhriju <strong>a</strong>lmayyita mina <strong>a</strong>l<u>h</u>ayyi waman yudabbiru <strong>a</strong>l-amra fasayaquuluuna <strong>al</strong>l<u>aa</u>hu faqul afal<u>aa</u> tattaquun<strong>a</strong>', 'Katakanlah (Muhammad), “Siapakah yang memberi rezeki kepadamu dari langit dan bumi, atau siapakah yang kuasa (menciptakan) pendengaran dan penglihatan, dan siapakah yang mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup, dan siapakah yang mengatur segala urusan?” Maka mereka akan menjawab, “Allah.” Maka katakanlah, “Mengapa kamu tidak bertakwa (kepada-Nya)?”'),
	(1396, 10, 32, 'فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلٰلُ  ۖفَاَنّٰى تُصْرَفُوْنَ ', 'fa<u>dzaa</u>likumu <strong>al</strong>l<u>aa</u>hu rabbukumu <strong>a</strong>l<u>h</u>aqqu fam<u>aatsaa</u> ba\'da <strong>a</strong>l<u>h</u>aqqi ill<u>aa</u> <strong>al</strong><u>dhdh</u>al<u>aa</u>lu fa-ann<u>aa</u> tu<u>sh</u>rafuun<strong>a</strong>', 'Maka itulah Allah, Tuhan kamu yang sebenarnya; maka tidak ada setelah kebenaran itu melainkan kesesatan. Maka mengapa kamu berpaling (dari kebenaran)?'),
	(1397, 10, 33, 'كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ فَسَقُوْٓا اَنَّهُمْ لَا يُؤْمِنُوْنَ ', 'ka<u>dzaa</u>lika <u>h</u>aqqat kalimatu rabbika \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina fasaquu annahum l<u>aa</u> yu/minuun<strong>a</strong>', 'Demikianlah telah tetap (hukuman) Tuhanmu terhadap orang-orang yang fasik, karena sesungguhnya mereka tidak beriman.'),
	(1398, 10, 34, 'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗۗ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ فَاَنّٰى تُؤْفَكُوْنَ ', 'qul hal min syurak<u>aa</u>-ikum man yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu quli <strong>al</strong>l<u>aa</u>hu yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu fa-ann<u>aa</u> tu/fakuun<strong>a</strong>', 'Katakanlah, “Adakah di antara sekutumu yang dapat memulai penciptaan (makhluk), kemudian mengulanginya (menghidupkannya) kembali?” Katakanlah, “Allah memulai (penciptaan) makhluk, kemudian mengulanginya. Maka bagaimana kamu dipalingkan (menyembah selain Allah)?”'),
	(1399, 10, 35, 'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّهْدِيْٓ اِلَى الْحَقِّۗ قُلِ اللّٰهُ يَهْدِيْ لِلْحَقِّۗ اَفَمَنْ يَّهْدِيْٓ اِلَى الْحَقِّ اَحَقُّ اَنْ يُّتَّبَعَ اَمَّنْ لَّا يَهِدِّيْٓ اِلَّآ اَنْ يُّهْدٰىۚ فَمَا لَكُمْۗ  كَيْفَ تَحْكُمُوْنَ ', 'qul hal min syurak<u>aa</u>-ikum man yahdii il<u>aa</u> <strong>a</strong>l<u>h</u>aqqi quli <strong>al</strong>l<u>aa</u>hu yahdii lil<u>h</u>aqqi afaman yahdii il<u>aa</u> <strong>a</strong>l<u>h</u>aqqi a<u>h</u>aqqu an yuttaba\'a amman l<u>aa</u> yahiddii ill<u>aa</u> an yuhd<u>aa</u> fam<u>aa</u> lakum kayfa ta<u>h</u>kumuun<strong>a</strong>', 'Katakanlah, “Apakah di antara sekutumu ada yang membimbing kepada kebenaran?” Katakanlah, “Allah-lah yang membimbing kepada kebenaran.” Maka manakah yang lebih berhak diikuti, Tuhan yang membimbing kepada kebenaran itu, ataukah orang yang tidak mampu membimbing bahkan perlu dibimbing? Maka mengapa kamu (berbuat demikian)? Bagaimanakah kamu mengambil keputusan?'),
	(1400, 10, 36, 'وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَنًّاۗ اِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَفْعَلُوْنَ ', 'wam<u>aa</u> yattabi\'u aktsaruhum ill<u>aa</u> <u>zh</u>annan inna <strong>al</strong><u>zhzh</u>anna l<u>aa</u> yughnii mina <strong>a</strong>l<u>h</u>aqqi syay-an inna <strong>al</strong>l<u>aa</u>ha \'aliimun bim<u>aa</u> yaf\'aluun<strong>a</strong>', 'Dan kebanyakan mereka hanya mengikuti dugaan. Sesungguhnya dugaan itu tidak sedikit pun berguna untuk melawan kebenaran. Sungguh, Allah Maha Mengetahui apa yang mereka kerjakan.'),
	(1401, 10, 37, 'وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُّفْتَرٰى مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ  ', 'wam<u>aa</u> k<u>aa</u>na h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>nu an yuftar<u>aa</u> min duuni <strong>al</strong>l<u>aa</u>hi wal<u>aa</u>kin ta<u>sh</u>diiqa <strong>al</strong>la<u>dz</u>ii bayna yadayhi wataf<u>sh</u>iila <strong>a</strong>lkit<u>aa</u>bi l<u>aa</u> rayba fiihi min rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan tidak mungkin Al-Qur\'an ini dibuat-buat oleh selain Allah; tetapi (Al-Qur\'an) membenarkan (kitab-kitab) yang sebelumnya dan menjelaskan hukum-hukum yang telah ditetapkannya, tidak ada keraguan di dalamnya, (diturunkan) dari Tuhan seluruh alam.'),
	(1402, 10, 38, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ فَأْتُوْا بِسُوْرَةٍ مِّثْلِهٖ وَادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'am yaquuluuna iftar<u>aa</u>hu qul fa/tuu bisuuratin mitslihi wa<strong>u</strong>d\'uu mani ista<u>th</u>a\'tum min duuni <strong>al</strong>l<u>aa</u>hi in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Apakah pantas mereka mengatakan dia (Muhammad) yang telah membuat-buatnya? Katakanlah, “Buatlah sebuah surah yang semisal dengan surah (Al-Qur\'an), dan ajaklah siapa saja di antara kamu orang yang mampu (membuatnya) selain Allah, jika kamu orang-orang yang benar.”'),
	(1403, 10, 39, 'بَلْ كَذَّبُوْا بِمَا لَمْ يُحِيْطُوْا بِعِلْمِهٖ وَلَمَّا يَأْتِهِمْ تَأْوِيْلُهٗۗ  كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ', 'bal ka<u>dzdz</u>abuu bim<u>aa</u> lam yu<u>h</u>ii<u>th</u>uu bi\'ilmihi walamm<u>aa</u> ya/tihim ta/wiiluhu ka<u>dzaa</u>lika ka<u>dzdz</u>aba <strong>al</strong>la<u>dz</u>iina min qablihim fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Bahkan (yang sebenarnya), mereka mendustakan apa yang mereka belum mengetahuinya dengan sempurna dan belum mereka peroleh penjelasannya. Demikianlah halnya umat-umat yang ada sebelum mereka telah mendustakan (rasul). Maka perhatikanlah bagaimana akibat orang yang zalim. '),
	(1404, 10, 40, 'وَمِنْهُمْ مَّنْ يُّؤْمِنُ بِهٖ وَمِنْهُمْ مَّنْ لَّا يُؤْمِنُ بِهٖۗ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِدِيْنَ ࣖ ', 'waminhum man yu/minu bihi waminhum man l<u>aa</u> yu/minu bihi warabbuka a\'lamu bi<strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan di antara mereka ada orang-orang yang beriman kepadanya (Al-Qur\'an), dan di antaranya ada (pula) orang-orang yang tidak beriman kepadanya. Sedangkan Tuhanmu lebih mengetahui tentang orang-orang yang berbuat kerusakan.'),
	(1405, 10, 41, 'وَاِنْ كَذَّبُوْكَ فَقُلْ لِّيْ عَمَلِيْ وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَرِيْۤـُٔوْنَ مِمَّآ اَعْمَلُ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ', 'wa-in ka<u>dzdz</u>abuuka faqul lii \'amalii walakum \'amalukum antum barii-uuna mimm<u>aa</u> a\'malu wa-an<u>aa</u> barii-un mimm<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan jika mereka (tetap) mendustakanmu (Muhammad), maka katakanlah, “Bagiku pekerjaanku dan bagimu pekerjaanmu. Kamu tidak bertanggung jawab terhadap apa yang aku kerjakan dan aku pun tidak bertanggung jawab terhadap apa yang kamu kerjakan.”'),
	(1406, 10, 42, 'وَمِنْهُمْ مَّنْ يَّسْتَمِعُوْنَ اِلَيْكَۗ اَفَاَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوْا لَا يَعْقِلُوْنَ ', 'waminhum man yastami\'uuna ilayka afa-anta tusmi\'u <strong>al</strong><u>shsh</u>umma walaw k<u>aa</u>nuu l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Dan di antara mereka ada yang mendengarkan engkau (Muhammad). Tetapi apakah engkau dapat menjadikan orang yang tuli itu mendengar walaupun mereka tidak mengerti?'),
	(1407, 10, 43, 'وَمِنْهُمْ مَّنْ يَّنْظُرُ اِلَيْكَۗ اَفَاَنْتَ تَهْدِى الْعُمْيَ وَلَوْ كَانُوْا لَا يُبْصِرُوْنَ ', 'waminhum man yan<u>zh</u>uru ilayka afa-anta tahdii <strong>a</strong>l\'umya walaw k<u>aa</u>nuu l<u>aa</u> yub<u>sh</u>iruun<strong>a</strong>', 'Dan di antara mereka ada yang melihat kepada engkau. Tetapi apakah engkau dapat memberi petunjuk kepada orang yang buta, walaupun mereka tidak memperhatikan?'),
	(1408, 10, 44, 'اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـًٔا وَّلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُوْنَ', 'inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> ya<u>zh</u>limu <strong>al</strong>nn<u>aa</u>sa syay-an wal<u>aa</u>kinna <strong>al</strong>nn<u>aa</u>sa anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Sesungguhnya Allah tidak menzalimi manusia sedikit pun, tetapi manusia itulah yang menzalimi dirinya sendiri.'),
	(1409, 10, 45, 'وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَّمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنَ النَّهَارِ يَتَعَارَفُوْنَ بَيْنَهُمْۗ قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ وَمَا كَانُوْا مُهْتَدِيْنَ ', 'wayawma ya<u>h</u>syuruhum ka-an lam yalbatsuu ill<u>aa</u> s<u>aa</u>\'atan mina <strong>al</strong>nnah<u>aa</u>ri yata\'<u>aa</u>rafuuna baynahum qad khasira <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu biliq<u>aa</u>-i <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> k<u>aa</u>nuu muhtadiin<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka, (mereka merasa) seakan-akan tidak pernah berdiam (di dunia) kecuali sesaat saja pada siang hari, (pada waktu) mereka saling berkenalan. Sungguh rugi orang yang mendustakan pertemuan mereka dengan Allah dan mereka tidak mendapat petunjuk.'),
	(1410, 10, 46, 'وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَهِيْدٌ عَلٰى مَا يَفْعَلُوْنَ ', 'wa-imm<u>aa</u> nuriyannaka ba\'<u>dh</u>a <strong>al</strong>la<u>dz</u>ii na\'iduhum aw natawaffayannaka fa-ilayn<u>aa</u> marji\'uhum tsumma <strong>al</strong>l<u>aa</u>hu syahiidun \'al<u>aa</u> m<u>aa</u> yaf\'aluun<strong>a</strong>', 'Dan jika Kami perlihatkan kepadamu (Muhammad) sebagian dari (siksaan) yang Kami janjikan kepada mereka, (tentulah engkau akan melihatnya) atau (jika) Kami wafatkan engkau (sebelum itu), maka kepada Kami (jualah) mereka kembali, dan Allah menjadi saksi atas apa yang mereka kerjakan.'),
	(1411, 10, 47, 'وَلِكُلِّ اُمَّةٍ رَّسُوْلٌ ۚفَاِذَا جَاۤءَ رَسُوْلُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ', 'walikulli ummatin rasuulun fa-i<u>dzaa</u> j<u>aa</u>-a rasuuluhum qu<u>dh</u>iya baynahum bi<strong>a</strong>lqis<u>th</u>i wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Dan setiap umat (mempunyai) rasul. Maka apabila rasul mereka telah datang, diberlakukanlah hukum bagi mereka dengan adil dan (sedikit pun) tidak dizalimi.'),
	(1412, 10, 48, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> <strong>a</strong>lwa\'du in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan mereka mengatakan,  ”Bilakah (datangnya) ancaman itu, jika kamu orang-orang yang benar?”'),
	(1413, 10, 49, 'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ ضَرًّا وَّلَا نَفْعًا اِلَّا مَا شَاۤءَ اللّٰهُ ۗ لِكُلِّ اُمَّةٍ اَجَلٌ ۚاِذَا جَاۤءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ', 'qul l<u>aa</u> amliku linafsii <u>dh</u>arran wal<u>aa</u> naf\'an ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu likulli ummatin ajalun i<u>dzaa</u> j<u>aa</u>-a ajaluhum fal<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun<strong>a</strong>', 'Katakanlah (Muhammad), “Aku tidak kuasa menolak mudarat maupun mendatangkan manfaat kepada diriku, kecuali apa yang Allah kehendaki.” Bagi setiap umat mempunyai ajal (batas waktu). Apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun. '),
	(1414, 10, 50, 'قُلْ اَرَءَيْتُمْ اِنْ اَتٰىكُمْ عَذَابُهٗ بَيَاتًا اَوْ نَهَارًا مَّاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُوْنَ', 'qul ara-aytum in at<u>aa</u>kum \'a<u>dzaa</u>buhu bay<u>aa</u>tan aw nah<u>aa</u>ran m<u>aatsaa</u> yasta\'jilu minhu <strong>a</strong>lmujrimuun<strong>a</strong>', 'Katakanlah, “Terangkanlah kepadaku, jika datang kepada kamu siksaan-Nya pada waktu malam atau siang hari, manakah yang diminta untuk disegerakan orang-orang yang berdosa itu?”'),
	(1415, 10, 51, 'اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِهٖۗ  اٰۤلْـٰٔنَ وَقَدْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ ', 'atsumma i<u>dzaa</u> m<u>aa</u> waqa\'a <u>aa</u>mantum bihi <u>aa</u>l-<u>aa</u>na waqad kuntum bihi tasta\'jiluun<strong>a</strong>', 'Kemudian apakah setelah azab itu terjadi, kamu baru mempercayainya? Apakah (baru) sekarang, padahal sebelumnya kamu selalu meminta agar disegerakan?'),
	(1416, 10, 52, 'ثُمَّ قِيْلَ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُوْنَ ', 'tsumma qiila lilla<u>dz</u>iina <u>zh</u>alamuu <u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>a</strong>lkhuldi hal tujzawna ill<u>aa</u> bim<u>aa</u> kuntum taksibuun<strong>a</strong>', 'Kemudian dikatakan kepada orang-orang yang zalim itu, “Rasakanlah olehmu siksaan yang kekal. Kamu tidak diberi balasan, melainkan (sesuai) dengan apa yang telah kamu lakukan.”'),
	(1417, 10, 53, 'وَيَسْتَنْۢبِـُٔوْنَكَ اَحَقٌّ هُوَ ۗ قُلْ اِيْ وَرَبِّيْٓ اِنَّهٗ لَحَقٌّ ۗوَمَآ اَنْتُمْ بِمُعْجِزِيْنَ ࣖ ', 'wayastanbi-uunaka a<u>h</u>aqqun huwa qul ii warabbii innahu la<u>h</u>aqqun wam<u>aa</u> antum bimu\'jiziin<strong>a</strong>', 'Dan mereka menanyakan kepadamu (Muhammad), “Benarkah (azab yang dijanjikan) itu?” Katakanlah, “Ya, demi Tuhanku, sesungguhnya (azab) itu pasti benar dan kamu sekali-kali tidak dapat menghindar.”'),
	(1418, 10, 54, 'وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِى الْاَرْضِ لَافْتَدَتْ بِهٖۗ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ', 'walaw anna likulli nafsin <u>zh</u>alamat m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i la<strong>i</strong>ftadat bihi wa-asarruu <strong>al</strong>nnad<u>aa</u>mata lamm<u>aa</u> ra-awuu <strong>a</strong>l\'a<u>dzaa</u>ba waqu<u>dh</u>iya baynahum bi<strong>a</strong>lqis<u>th</u>i wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Dan kalau setiap orang yang zalim itu (mempunyai) segala yang ada di bumi, tentu dia menebus dirinya dengan itu, dan mereka menyembunyikan penyesalannya ketika mereka telah menyaksikan azab itu. Kemudian diberi keputusan di antara mereka dengan adil, dan mereka tidak dizalimi.'),
	(1419, 10, 55, 'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  اَلَآ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ', 'al<u>aa</u> inna lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <strong>a</strong>l<u>aa</u> inna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Ketahuilah sesungguhnya milik Allah-lah apa yang ada di langit dan di bumi. Bukankah janji Allah itu benar? Tetapi kebanyakan mereka tidak mengetahui.'),
	(1420, 10, 56, 'هُوَ يُحْيٖ وَيُمِيْتُ وَاِلَيْهِ تُرْجَعُوْنَ ', 'huwa yu<u>h</u>yii wayumiitu wa-ilayhi turja\'uun<strong>a</strong>', 'Dialah yang menghidupkan dan mematikan dan hanya kepada-Nyalah kamu dikembalikan.'),
	(1421, 10, 57, 'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَتْكُمْ مَّوْعِظَةٌ مِّنْ رَّبِّكُمْ وَشِفَاۤءٌ لِّمَا فِى الصُّدُوْرِۙ وَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su qad j<u>aa</u>-atkum maw\'i<u>zh</u>atun min rabbikum wasyif<u>aa</u>un lim<u>aa</u> fii <strong>al</strong><u>shsh</u>uduuri wahudan wara<u>h</u>matun lilmu/miniin<strong>a</strong>', 'Wahai manusia! Sungguh, telah datang kepadamu pelajaran (Al-Qur\'an) dari Tuhanmu, penyembuh bagi penyakit yang ada dalam dada dan petunjuk serta rahmat bagi orang yang beriman.'),
	(1422, 10, 58, 'قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِهٖ فَبِذٰلِكَ فَلْيَفْرَحُوْاۗ هُوَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ', 'qul bifa<u>dh</u>li <strong>al</strong>l<u>aa</u>hi wabira<u>h</u>matihi fabi<u>dzaa</u>lika falyafra<u>h</u>uu huwa khayrun mimm<u>aa</u> yajma\'uun<strong>a</strong>', 'Katakanlah (Muhammad), “Dengan karunia Allah dan rahmat-Nya, hendaklah dengan itu mereka bergembira. Itu lebih baik daripada apa yang mereka kumpulkan.”'),
	(1423, 10, 59, 'قُلْ اَرَءَيْتُمْ مَّآ اَنْزَلَ اللّٰهُ لَكُمْ مِّنْ رِّزْقٍ فَجَعَلْتُمْ مِّنْهُ حَرَامًا وَّحَلٰلًا ۗ قُلْ اٰۤللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُوْنَ', 'qul ara-aytum m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu lakum min rizqin faja\'altum minhu <u>h</u>ar<u>aa</u>man wa<u>h</u>al<u>aa</u>lan qul <u>aa</u>ll<u>aa</u>hu a<u>dz</u>ina lakum am \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi taftaruun<strong>a</strong>', 'Katakanlah (Muhammad), “Terangkanlah kepadaku tentang rezeki yang diturunkan Allah kepadamu, lalu kamu jadikan sebagiannya haram dan sebagiannya halal.”  Katakanlah, “Apakah Allah telah memberikan izin kepadamu (ten-tang ini) ataukah kamu mengada-ada atas nama Allah?”'),
	(1424, 10, 60, 'وَمَا ظَنُّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِ ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ ࣖ ', 'wam<u>aa</u> <u>zh</u>annu <strong>al</strong>la<u>dz</u>iina yaftaruuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba yawma <strong>a</strong>lqiy<u>aa</u>mati inna <strong>al</strong>l<u>aa</u>ha la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si wal<u>aa</u>kinna aktsarahum l<u>aa</u> yasykuruun<strong>a</strong>', 'Dan apakah dugaan orang-orang yang mengada-adakan kebohongan terhadap Allah pada hari Kiamat? Sesungguhnya Allah benar-benar mempunyai karunia (yang dilimpahkan) kepada manusia, tetapi kebanyakan mereka tidak bersyukur.'),
	(1425, 10, 61, 'وَمَا تَكُوْنُ فِيْ شَأْنٍ وَّمَا تَتْلُوْا مِنْهُ مِنْ قُرْاٰنٍ وَّلَا تَعْمَلُوْنَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوْدًا اِذْ تُفِيْضُوْنَ فِيْهِۗ وَمَا يَعْزُبُ عَنْ رَّبِّكَ مِنْ مِّثْقَالِ ذَرَّةٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ وَلَآ ا', 'wam<u>aa</u> takuunu fii sya/nin wam<u>aa</u> tatluu minhu min qur-<u>aa</u>nin wal<u>aa</u> ta\'maluuna min \'amalin ill<u>aa</u> kunn<u>aa</u> \'alaykum syuhuudan i<u>dz</u> tufii<u>dh</u>uuna fiihi wam<u>aa</u> ya\'zubu \'an rabbika min mitsq<u>aa</u>li <u>dz</u>arratin fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>-i wal<u>aa</u> a<u>sh</u>ghara min <u>dzaa</u>lika wal<u>aa</u> akbara ill<u>aa</u> fii kit<u>aa</u>bin mubiin<strong>un</strong>', 'Dan tidakkah engkau (Muhammad) berada dalam suatu urusan, dan tidak membaca suatu ayat Al-Qur\'an serta tidak pula kamu melakukan suatu pekerjaan, melainkan Kami menjadi saksi atasmu ketika kamu melakukannya. Tidak lengah sedikit pun dari pengetahuan Tuhanmu biarpun sebesar zarrah, baik di bumi ataupun di langit. Tidak ada sesuatu yang lebih kecil dan yang lebih besar daripada itu, melainkan semua tercatat dalam Kitab yang nyata (Lauh Mahfuzh).'),
	(1426, 10, 62, 'اَلَآ اِنَّ اَوْلِيَاۤءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ  ', 'al<u>aa</u> inna awliy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hi l<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun<strong>a</strong>', 'Ingatlah wali-wali Allah itu, tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.'),
	(1427, 10, 63, 'اَلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَۗ  ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun<strong>a </strong>', '(Yaitu) orang-orang yang beriman dan senantiasa bertakwa.'),
	(1428, 10, 64, 'لَهُمُ الْبُشْرٰى فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۗ  لَا تَبْدِيْلَ لِكَلِمٰتِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۗ  ', 'lahumu <strong>a</strong>lbusyr<u>aa</u> fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wafii <strong>a</strong>l-<u>aa</u>khirati l<u>aa</u> tabdiila likalim<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>lika huwa <strong>a</strong>lfawzu <strong>a</strong>l\'a<u>zh</u>iim<strong>u</strong>', 'Bagi mereka berita gembira di dalam kehidupan di dunia dan di akhirat. Tidak ada perubahan bagi janji-janji Allah. Demikian itulah kemenangan yang agung.'),
	(1429, 10, 65, 'وَلَا يَحْزُنْكَ قَوْلُهُمْۘ اِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ هُوَ السَّمِيْعُ الْعَلِيْمُ', 'wal<u>aa</u> ya<u>h</u>zunka qawluhum inna <strong>a</strong>l\'izzata lill<u>aa</u>hi jamii\'an huwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', ' Dan janganlah engkau (Muhammad) sedih oleh perkataan mereka. Sungguh, kekuasaan itu seluruhnya milik Allah. Dia Maha Mendengar, Maha Mengetahui.'),
	(1430, 10, 66, 'اَلَآ اِنَّ لِلّٰهِ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِۗ وَمَا يَتَّبِعُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ شُرَكَاۤءَ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ', 'al<u>aa</u> inna lill<u>aa</u>hi man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti waman fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> yattabi\'u <strong>al</strong>la<u>dz</u>iina yad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi syurak<u>aa</u>-a in yattabi\'uuna ill<u>aa</u> <strong>al</strong><u>zhzh</u>anna wa-in hum ill<u>aa</u> yakhru<u>sh</u>uun<strong>a</strong>', 'Ingatlah, milik Allah meliputi siapa yang ada di langit dan siapa yang ada di bumi. Dan orang-orang yang menyeru sekutu-sekutu selain Allah, tidaklah mengikuti (suatu keyakinan). Mereka hanya mengikuti persangkaan belaka, dan mereka hanyalah menduga-duga.'),
	(1431, 10, 67, 'هُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ ', 'huwa <strong>al</strong>la<u>dz</u>ii ja\'ala lakumu <strong>al</strong>layla litaskunuu fiihi wa<strong>al</strong>nnah<u>aa</u>ra mub<u>sh</u>iran inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yasma\'uun<strong>a</strong>', 'Dialah yang menjadikan malam bagimu agar kamu beristirahat padanya dan menjadikan siang terang benderang. Sungguh, yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang mendengar.'),
	(1432, 10, 68, 'قَالُوا اتَّخَذَ اللّٰهُ وَلَدًا سُبْحٰنَهٗ ۗ هُوَ الْغَنِيُّ ۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اِنْ عِنْدَكُمْ مِّنْ سُلْطٰنٍۢ بِهٰذَاۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ', 'q<u>aa</u>luu ittakha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu waladan sub<u>haa</u>nahu huwa <strong>a</strong>lghaniyyu lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i in \'indakum min sul<u>th</u><u>aa</u>nin bih<u>aadzaa</u> ataquuluuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Mereka (orang-orang Yahudi dan Nasrani) berkata, “Allah mempunyai anak.” Mahasuci Dia, Dialah Yang Mahakaya; milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Kamu tidak mempunyai alasan kuat tentang ini. Pantaskah kamu mengatakan tentang Allah apa yang kamu tidak ketahui?'),
	(1433, 10, 69, 'قُلْ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ  ', 'qul inna <strong>al</strong>la<u>dz</u>iina yaftaruuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba l<u>aa</u> yufli<u>h</u>uun<strong>a</strong>', 'Katakanlah, “Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tidak akan beruntung.”'),
	(1434, 10, 70, 'مَتَاعٌ فِى الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيْقُهُمُ الْعَذَابَ الشَّدِيْدَ بِمَا كَانُوْا يَكْفُرُوْنَ ࣖ ', 'mat<u>aa</u>\'un fii <strong>al</strong>dduny<u>aa</u> tsumma ilayn<u>aa</u> marji\'uhum tsumma nu<u>dz</u>iiquhumu <strong>a</strong>l\'a<u>dzaa</u>ba <strong>al</strong>sysyadiida bim<u>aa</u> k<u>aa</u>nuu yakfuruun<strong>a</strong>', '(Bagi mereka) kesenangan (sesaat) ketika di dunia, selanjutnya kepada Kamilah mereka kembali, kemudian Kami rasakan kepada mereka azab yang berat, karena kekafiran mereka.'),
	(1435, 10, 71, '۞ وَاتْلُ عَلَيْهِمْ نَبَاَ نُوْحٍۘ اِذْ قَالَ لِقَوْمِهٖ يٰقَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَّقَامِيْ وَتَذْكِيْرِيْ بِاٰيٰتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُوْٓا اَمْرَكُمْ وَشُرَكَاۤءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّة', 'wa<strong>u</strong>tlu \'alayhim naba-a nuu<u>h</u>in i<u>dz</u> q<u>aa</u>la liqawmihi y<u>aa</u> qawmi in k<u>aa</u>na kabura \'alaykum maq<u>aa</u>mii wata<u>dz</u>kiirii bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi fa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi tawakkaltu fa-ajmi\'uu amrakum wasyurak<u>aa</u>-akum tsumma l<u>aa</u> yakun amrukum \'alaykum ghummatan tsumma iq<u>dh</u>uu ilayya wal<u>aa</u> tun<u>zh</u>iruun<strong>i</strong>', 'Dan bacakanlah kepada mereka berita penting (tentang) Nuh ketika (dia) berkata kepada kaumnya, “Wahai kaumku! Jika terasa berat bagimu aku tinggal (bersamamu) dan peringatanku dengan ayat-ayat Allah, maka kepada Allah aku bertawakal. Karena itu bulatkanlah keputusanmu dan kumpulkanlah sekutu-sekutumu (untuk membinasakanku), dan janganlah keputusanmu itu dirahasiakan. Kemudian bertindaklah terhadap diriku, dan janganlah kamu tunda lagi.'),
	(1436, 10, 72, 'فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِّنْ اَجْرٍۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۙوَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ', 'fa-in tawallaytum fam<u>aa</u> sa-altukum min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi waumirtu an akuuna mina <strong>a</strong>lmuslimiin<strong>a</strong>', 'Maka jika kamu berpaling (dari peringatanku), aku tidak meminta imbalan sedikit pun darimu. Imbalanku tidak lain hanyalah dari Allah, dan aku diperintah agar aku termasuk golongan orang-orang Muslim (berserah diri).”'),
	(1437, 10, 73, 'فَكَذَّبُوْهُ فَنَجَّيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ وَجَعَلْنٰهُمْ خَلٰۤىِٕفَ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَ ', 'faka<u>dzdz</u>abuuhu fanajjayn<u>aa</u>hu waman ma\'ahu fii <strong>a</strong>lfulki waja\'aln<u>aa</u>hum khal<u>aa</u>-ifa wa-aghraqn<u>aa</u> <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmun<u>dz</u>ariin<strong>a</strong>', 'Kemudian mereka mendustakannya (Nuh), lalu Kami selamatkan dia dan orang yang bersamanya di dalam kapal, dan Kami jadikan mereka itu khalifah dan Kami tenggelamkan orang yang mendustakan ayat-ayat Kami. Maka perhatikanlah bagaimana kesudahan orang-orang yang diberi peringatan itu.'),
	(1438, 10, 74, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهٖ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا بِهٖ مِنْ قَبْلُ ۗ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوْبِ الْمُعْتَدِيْنَ ', 'tsumma ba\'atsn<u>aa</u> min ba\'dihi rusulan il<u>aa</u> qawmihim faj<u>aa</u>uuhum bi<strong>a</strong>lbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nuu liyu/minuu bim<u>aa</u> ka<u>dzdz</u>abuu bihi min qablu ka<u>dzaa</u>lika na<u>th</u>ba\'u \'al<u>aa</u> quluubi <strong>a</strong>lmu\'tadiin<strong>a</strong>', 'Kemudian setelahnya (Nuh), Kami utus beberapa rasul kepada kaum mereka (masing-masing), maka rasul-rasul itu datang kepada mereka dengan membawa keterangan yang jelas, tetapi mereka tidak mau beriman karena mereka dahulu telah (biasa) mendustakannya. Demikianlah Kami mengunci hati orang-orang yang melampaui batas.'),
	(1439, 10, 75, 'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى وَهٰرُوْنَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ بِاٰيٰتِنَا فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ ', 'tsumma ba\'atsn<u>aa</u> min ba\'dihim muus<u>aa</u> wah<u>aa</u>ruuna il<u>aa</u> fir\'awna wamala-ihi bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fa<strong>i</strong>stakbaruu wak<u>aa</u>nuu qawman mujrimiin<strong>a</strong>', 'Kemudian setelah mereka, Kami utus Musa dan Harun kepada Fir‘aun dan para pemuka kaumnya, dengan membawa tanda-tanda (kekuasaan) Kami. Ternyata mereka menyombongkan diri dan mereka adalah orang-orang yang berdosa.'),
	(1440, 10, 76, 'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْٓا اِنَّ هٰذَا لَسِحْرٌ مُّبِيْنٌ ', 'falamm<u>aa</u> j<u>aa</u>-ahumu <strong>a</strong>l<u>h</u>aqqu min \'indin<u>aa</u> q<u>aa</u>luu inna h<u>aadzaa</u> lasi<u>h</u>run mubiin<strong>un </strong>', 'Maka ketika telah datang kepada mereka kebenaran dari sisi Kami, mereka berkata, “Ini benar-benar sihir yang nyata.”'),
	(1441, 10, 77, 'قَالَ مُوْسٰٓى اَتَقُوْلُوْنَ لِلْحَقِّ لَمَّا جَاۤءَكُمْ ۗ اَسِحْرٌ هٰذَاۗ وَلَا يُفْلِحُ السَّاحِرُوْنَ ', 'q<u>aa</u>la muus<u>aa</u> ataquuluuna lil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-akum asi<u>h</u>run h<u>aadzaa</u> wal<u>aa</u> yufli<u>h</u>u <strong>al</strong>ss<u>aah</u>iruun<strong>a</strong>', 'Musa berkata, “Pantaskah kamu mengatakan terhadap kebenaran ketika ia datang kepadamu, ‘sihirkah ini?’ Padahal para pesihir itu tidaklah mendapat kemenangan.”'),
	(1442, 10, 78, 'قَالُوْٓا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا وَتَكُوْنَ لَكُمَا الْكِبْرِيَاۤءُ فِى الْاَرْضِۗ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِيْنَ ', 'q<u>aa</u>luu aji/tan<u>aa</u> litalfitan<u>aa</u> \'amm<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> watakuuna lakum<u>aa</u> <strong>a</strong>lkibriy<u>aa</u>u fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> na<u>h</u>nu lakum<u>aa</u> bimu/miniin<strong>a</strong>', 'Mereka berkata, “Apakah engkau datang kepada kami untuk memalingkan kami dari apa (kepercayaan) yang kami dapati nenek moyang kami mengerjakannya (menyembah berhala), dan agar kamu berdua mempunyai kekuasaan di bumi (negeri Mesir)? Kami tidak akan mempercayai kamu berdua.”'),
	(1443, 10, 79, 'وَقَالَ فِرْعَوْنُ ائْتُوْنِيْ بِكُلِّ سٰحِرٍ عَلِيْمٍ ', 'waq<u>aa</u>la fir\'awnu i/tuunii bikulli s<u>aah</u>irin \'aliim<strong>in</strong>', 'Dan Fir‘aun berkata (kepada pemuka kaumnya), “Datangkanlah kepadaku semua pesihir yang ulung!”'),
	(1444, 10, 80, 'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ ', 'falamm<u>aa</u> j<u>aa</u>-a <strong>al</strong>ssa<u>h</u>aratu q<u>aa</u>la lahum muus<u>aa</u> <strong>a</strong>lquu m<u>aa</u> antum mulquun<strong>a </strong>', 'Maka ketika para pesihir itu datang, Musa berkata kepada mereka, “Lemparkanlah apa yang hendak kamu lemparkan!”'),
	(1445, 10, 81, 'فَلَمَّآ اَلْقَوْا قَالَ مُوْسٰى مَا جِئْتُمْ بِهِ ۙالسِّحْرُۗ اِنَّ اللّٰهَ سَيُبْطِلُهٗۗ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِيْنَ ࣖ ', 'falamm<u>aa</u> <strong>a</strong>lqaw q<u>aa</u>la muus<u>aa</u> m<u>aa</u> ji/tum bihi <strong>al</strong>ssi<u>h</u>ru inna <strong>al</strong>l<u>aa</u>ha sayub<u>th</u>iluhu inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>sh</u>li<u>h</u>u \'amala <strong>a</strong>lmufsidiin<strong>a</strong>', ' Setelah mereka melemparkan, Musa berkata, “Apa yang kamu lakukan itu, itulah sihir, sesungguhnya Allah akan menampakkan kepalsuan sihir itu. Sungguh, Allah tidak akan membiarkan terus berlangsungnya pekerjaan orang yang berbuat kerusakan.”'),
	(1446, 10, 82, 'وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمٰتِهٖ وَلَوْ كَرِهَ الْمُجْرِمُوْنَ ', 'wayu<u>h</u>iqqu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l<u>h</u>aqqa bikalim<u>aa</u>tihi walaw kariha <strong>a</strong>lmujrimuun<strong>a</strong>', 'Dan Allah akan mengukuhkan yang benar dengan ketetapan-Nya, walaupun orang-orang yang berbuat dosa tidak menyukainya.'),
	(1447, 10, 83, 'فَمَآ اٰمَنَ لِمُوْسٰىٓ اِلَّا ذُرِّيَّةٌ مِّنْ قَوْمِهٖ عَلٰى خَوْفٍ مِّنْ فِرْعَوْنَ وَمَلَا۟ىِٕهِمْ اَنْ يَّفْتِنَهُمْ ۗوَاِنَّ فِرْعَوْنَ لَعَالٍ فِى الْاَرْضِۚ وَاِنَّهٗ لَمِنَ الْمُسْرِفِيْنَ ', 'fam<u>aa</u> <u>aa</u>mana limuus<u>aa</u> ill<u>aa</u> <u>dz</u>urriyyatun min qawmihi \'al<u>aa</u> khawfin min fir\'awna wamala-ihim an yaftinahum wa-inna fir\'awna la\'<u>aa</u>lin fii <strong>a</strong>l-ar<u>dh</u>i wa-innahu lamina <strong>a</strong>lmusrifiin<strong>a</strong>', 'Maka tidak ada yang beriman kepada Musa, selain keturunan dari kaumnya dalam keadaan takut bahwa Fir‘aun dan para pemuka (kaum)nya akan menyiksa mereka. Dan sungguh, Fir‘aun itu benar-benar telah berbuat sewenang-wenang di bumi, dan benar-benar termasuk orang yang melampaui batas.'),
	(1448, 10, 84, 'وَقَالَ مُوْسٰى يٰقَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّسْلِمِيْنَ ', 'waq<u>aa</u>la muus<u>aa</u> y<u>aa</u> qawmi in kuntum <u>aa</u>mantum bi<strong>al</strong>l<u>aa</u>hi fa\'alayhi tawakkaluu in kuntum muslimiin<strong>a</strong>', 'Dan Musa berkata, “Wahai kaumku! Apabila kamu beriman kepada Allah, maka bertawakallah kepada-Nya, jika kamu benar-benar orang Muslim (berserah diri).”'),
	(1449, 10, 85, 'فَقَالُوْا عَلَى اللّٰهِ تَوَكَّلْنَا ۚرَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلْقَوْمِ الظّٰلِمِيْنَ ', 'faq<u>aa</u>luu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi tawakkaln<u>aa</u> rabban<u>aa</u> l<u>aa</u> taj\'aln<u>aa</u> fitnatan lilqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Lalu mereka berkata, “Kepada Allah-lah kami bertawakal. Ya Tuhan kami, janganlah Engkau jadikan kami (sasaran) fitnah bagi kaum yang zalim,'),
	(1450, 10, 86, 'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكٰفِرِيْنَ ', 'wanajjin<u>aa</u> bira<u>h</u>matika mina <strong>a</strong>lqawmi <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'dan selamatkanlah kami dengan rahmat-Mu dari orang-orang kafir.”'),
	(1451, 10, 87, 'وَاَوْحَيْنَآ اِلٰى مُوْسٰى وَاَخِيْهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوْتًا وَّاجْعَلُوْا بُيُوْتَكُمْ قِبْلَةً وَّاَقِيْمُوا الصَّلٰوةَۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ', 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> wa-akhiihi an tabawwa<u>aa</u> liqawmikum<u>aa</u> bimi<u>sh</u>ra buyuutan wa<strong>i</strong>j\'aluu buyuutakum qiblatan wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wabasysyiri <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan Kami wahyukan kepada Musa dan saudaranya, “Ambillah beberapa rumah di Mesir untuk (tempat tinggal) kaummu dan jadikanlah rumah-rumahmu itu tempat ibadah dan laksanakanlah salat serta gembirakanlah orang-orang mukmin.”'),
	(1452, 10, 88, 'وَقَالَ مُوْسٰى رَبَّنَآ اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهٗ زِيْنَةً وَّاَمْوَالًا فِى الْحَيٰوةِ الدُّنْيَاۗ رَبَّنَا لِيُضِلُّوْا عَنْ سَبِيْلِكَ ۚرَبَّنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوْبِهِمْ فَلَا يُؤْمِنُوْا حَتّٰى يَرَوُا ا', 'waq<u>aa</u>la muus<u>aa</u> rabban<u>aa</u> innaka <u>aa</u>tayta fir\'awna wamala-ahu ziinatan wa-amw<u>aa</u>lan fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> rabban<u>aa</u> liyu<u>dh</u>illuu \'an sabiilika rabban<u>aa</u> i<u>th</u>mis \'al<u>aa</u> amw<u>aa</u>lihim wa<strong>u</strong>sydud \'al<u>aa</u> quluubihim fal<u>aa</u> yu/minuu <u>h</u>att<u>aa</u> yarawuu <strong>a</strong>l\'a<u>dzaa</u>ba <strong>a</strong>l-aliim<strong>a</strong>', 'Dan Musa berkata, “Ya Tuhan kami, Engkau telah memberikan kepada Fir‘aun dan para pemuka kaumnya perhiasan dan harta kekayaan dalam kehidupan dunia. Ya Tuhan kami, (akibatnya) mereka menyesatkan (manusia) dari jalan-Mu. Ya Tuhan, binasakanlah harta mereka, dan kuncilah hati mereka, sehingga mereka tidak beriman sampai mereka melihat azab yang pedih.”'),
	(1453, 10, 89, 'قَالَ قَدْ اُجِيْبَتْ دَّعْوَتُكُمَا فَاسْتَقِيْمَا وَلَا تَتَّبِعٰۤنِّ سَبِيْلَ الَّذِيْنَ لَا يَعْلَمُوْنَ ', 'q<u>aa</u>la qad ujiibat da\'watukum<u>aa</u> fa<strong>i</strong>staqiim<u>aa</u> wal<u>aa</u> tattabi\'<u>aa</u>nni sabiila <strong>al</strong>la<u>dz</u>iina l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dia Allah berfirman, “Sungguh, telah diperkenankan permohonan kamu berdua, sebab itu tetaplah kamu berdua pada jalan yang lurus dan jangan sekali-kali kamu mengikuti jalan orang yang tidak mengetahui.”'),
	(1454, 10, 90, '۞ وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُوْدُهٗ بَغْيًا وَّعَدْوًا ۗحَتّٰىٓ اِذَآ اَدْرَكَهُ الْغَرَقُ قَالَ اٰمَنْتُ اَنَّهٗ لَآ اِلٰهَ اِلَّا الَّذِيْٓ اٰمَنَتْ بِهٖ بَنُوْٓا اِسْرَاۤءِيْلَ وَاَنَا۠ مِنَ الْمُس', 'waj<u>aa</u>wazn<u>aa</u> bibanii isr<u>aa</u>-iila <strong>a</strong>lba<u>h</u>ra fa-atba\'ahum fir\'awnu wajunuuduhu baghyan wa\'adwan <u>h</u>att<u>aa</u> i<u>dzaa</u> adrakahu <strong>a</strong>lgharaqu q<u>aa</u>la <u>aa</u>mantu annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> <strong>al</strong>la<u>dz</u>ii <u>aa</u>manat bihi banuu isr<u>aa</u>-iila wa-an<u>aa</u> mina <strong>a</strong>lmuslimiin<strong>a</strong>', 'Dan Kami selamatkan Bani Israil melintasi laut, kemudian Fir‘aun dan bala tentaranya mengikuti mereka, untuk menzalimi dan menindas (mereka). Sehingga ketika Fir‘aun hampir tenggelam dia berkata, “Aku percaya bahwa tidak ada tuhan melainkan Tuhan yang dipercayai oleh Bani Israil, dan aku termasuk orang-orang Muslim (berserah diri).”'),
	(1455, 10, 91, 'اٰۤلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِيْنَ', '<u>aa</u>l-<u>aa</u>na waqad \'a<u>sh</u>ayta qablu wakunta mina <strong>a</strong>lmufsidiin<strong>a</strong>', 'Mengapa baru sekarang (kamu beriman), padahal sesungguhnya engkau telah durhaka sejak dahulu, dan engkau termasuk orang yang berbuat kerusakan.'),
	(1456, 10, 92, 'فَالْيَوْمَ نُنَجِّيْكَ بِبَدَنِكَ لِتَكُوْنَ لِمَنْ خَلْفَكَ اٰيَةً ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ عَنْ اٰيٰتِنَا لَغٰفِلُوْنَ ', 'fa<strong>a</strong>lyawma nunajjiika bibadanika litakuuna liman khalfaka <u>aa</u>yatan wa-inna katsiiran mina <strong>al</strong>nn<u>aa</u>si \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> lagh<u>aa</u>filuun<strong>a</strong>', 'Maka pada hari ini Kami selamatkan jasadmu agar engkau dapat menjadi pelajaran bagi orang-orang yang datang setelahmu, tetapi kebanyakan manusia tidak mengindahkan tanda-tanda (kekuasaan) Kami.'),
	(1457, 10, 93, 'وَلَقَدْ بَوَّأْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مُبَوَّاَ صِدْقٍ وَّرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ ۚفَمَا اخْتَلَفُوْا حَتّٰى جَاۤءَهُمُ الْعِلْمُ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ', 'walaqad bawwa/n<u>aa</u> banii isr<u>aa</u>-iila mubawwa-a <u>sh</u>idqin warazaqn<u>aa</u>hum mina <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti fam<u>aa</u> ikhtalafuu <u>h</u>att<u>aa</u> j<u>aa</u>-ahumu <strong>a</strong>l\'ilmu inna rabbaka yaq<u>dh</u>ii baynahum yawma <strong>a</strong>lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun<strong>a</strong>', 'Dan sungguh, Kami telah menempatkan Bani Israil di tempat kediaman yang bagus dan Kami beri mereka rezeki yang baik. Maka mereka tidak berselisih, kecuali setelah datang kepada mereka pengetahuan (yang tersebut dalam Taurat). Sesungguhnya Tuhan kamu akan memberi keputusan antara mereka pada hari Kiamat tentang apa yang mereka perselisihkan itu.'),
	(1458, 10, 94, 'فَاِنْ كُنْتَ فِيْ شَكٍّ مِّمَّآ اَنْزَلْنَآ اِلَيْكَ فَسْـَٔلِ الَّذِيْنَ يَقْرَءُوْنَ الْكِتٰبَ مِنْ قَبْلِكَ ۚ لَقَدْ جَاۤءَكَ الْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَۙ', 'fa-in kunta fii syakkin mimm<u>aa</u> anzalnn<u>aa</u> ilayka fa<strong>i</strong>s-ali <strong>al</strong>la<u>dz</u>iina yaqrauuna <strong>a</strong>lkit<u>aa</u>ba min qablika laqad j<u>aa</u>-aka <strong>a</strong>l<u>h</u>aqqu min rabbika fal<u>aa</u> takuunanna mina <strong>a</strong>lmumtariin<strong>a</strong>', 'Maka jika engkau (Muhammad) berada dalam keragu-raguan tentang apa yang Kami turunkan kepadamu, maka tanyakanlah kepada orang yang membaca kitab sebelummu. Sungguh, telah datang kebenaran kepadamu dari Tuhanmu, maka janganlah sekali-kali engkau termasuk orang yang ragu.'),
	(1459, 10, 95, 'وَلَا تَكُوْنَنَّ مِنَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ فَتَكُوْنَ مِنَ الْخٰسِرِيْنَ ', 'wal<u>aa</u> takuunanna mina <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi fatakuuna mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Dan janganlah sekali-kali engkau termasuk orang yang mendustakan ayat-ayat Allah, nanti engkau termasuk orang yang rugi.'),
	(1460, 10, 96, 'اِنَّ الَّذِيْنَ حَقَّتْ عَلَيْهِمْ كَلِمَةُ رَبِّكَ لَا يُؤْمِنُوْنَ ', 'inna <strong>al</strong>la<u>dz</u>iina <u>h</u>aqqat \'alayhim kalimatu rabbika l<u>aa</u> yu/minuun<strong>a</strong>', 'Sungguh, orang-orang yang telah dipastikan mendapat ketetapan Tuhanmu, tidaklah akan beriman,'),
	(1461, 10, 97, 'وَلَوْ جَاۤءَتْهُمْ كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ', 'walaw j<u>aa</u>-at-hum kullu <u>aa</u>yatin <u>h</u>att<u>aa</u> yarawuu <strong>a</strong>l\'a<u>dzaa</u>ba <strong>a</strong>l-aliim<strong>a</strong>', 'meskipun mereka mendapat tanda-tanda (kebesaran Allah), hingga mereka menyaksikan azab yang pedih.'),
	(1462, 10, 98, 'فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَآ اِيْمَانُهَآ اِلَّا قَوْمَ يُوْنُسَۗ  لَمَّآ اٰمَنُوْا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ ', 'falawl<u>aa</u> k<u>aa</u>nat qaryatun <u>aa</u>manat fanafa\'ah<u>aa</u> iim<u>aa</u>nuh<u>aa</u> ill<u>aa</u> qawma yuunusa lamm<u>aa</u> <u>aa</u>manuu kasyafn<u>aa</u> \'anhum \'a<u>dzaa</u>ba <strong>a</strong>lkhizyi fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wamatta\'n<u>aa</u>hum il<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Maka mengapa tidak ada (penduduk) suatu negeri pun yang beriman, lalu imannya itu bermanfaat kepadanya selain kaum Yunus? Ketika mereka (kaum Yunus itu) beriman, Kami hilangkan dari mereka azab yang menghinakan dalam kehidupan dunia, dan Kami beri kesenangan kepada mereka sampai waktu tertentu.'),
	(1463, 10, 99, 'وَلَوْ شَاۤءَ رَبُّكَ لَاٰمَنَ مَنْ فِى الْاَرْضِ كُلُّهُمْ جَمِيْعًاۗ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُوْنُوْا مُؤْمِنِيْنَ ', 'walaw sy<u>aa</u>-a rabbuka la<u>aa</u>mana man fii <strong>a</strong>l-ar<u>dh</u>i kulluhum jamii\'an afa-anta tukrihu <strong>al</strong>nn<u>aa</u>sa <u>h</u>att<u>aa</u> yakuunuu mu/miniin<strong>a </strong>', 'Dan jika Tuhanmu menghendaki, tentulah beriman semua orang di bumi seluruhnya. Tetapi apakah kamu (hendak) memaksa manusia agar mereka menjadi orang-orang yang beriman?'),
	(1464, 10, 100, 'وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِ ۗوَيَجْعَلُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يَعْقِلُوْنَ ', 'wam<u>aa</u> k<u>aa</u>na linafsin an tu/mina ill<u>aa</u> bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi wayaj\'alu <strong>al</strong>rrijsa \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Dan tidak seorang pun akan beriman kecuali dengan izin Allah, dan Allah menimpakan azab kepada orang yang tidak mengerti.'),
	(1465, 10, 101, 'قُلِ انْظُرُوْا مَاذَا فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَمَا تُغْنِى الْاٰيٰتُ وَالنُّذُرُ عَنْ قَوْمٍ لَّا يُؤْمِنُوْنَ ', 'quli un<u>zh</u>uruu m<u>aatsaa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> tughnii <strong>a</strong>l-<u>aa</u>y<u>aa</u>tu wa<strong>al</strong>nnu<u>dz</u>uru \'an qawmin l<u>aa</u> yu/minuun<strong>a</strong>', 'Katakanlah, “Perhatikanlah apa yang ada di langit dan di bumi!” Tidaklah bermanfaat tanda-tanda (kebesaran Allah) dan rasul-rasul yang memberi peringatan bagi orang yang tidak beriman.'),
	(1466, 10, 102, 'فَهَلْ يَنْتَظِرُوْنَ اِلَّا مِثْلَ اَيَّامِ الَّذِيْنَ خَلَوْا مِنْ قَبْلِهِمْۗ قُلْ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ', 'fahal yanta<u>zh</u>iruuna ill<u>aa</u> mitsla ayy<u>aa</u>mi <strong>al</strong>la<u>dz</u>iina khalaw min qablihim qul fa<strong>i</strong>nta<u>zh</u>iruu innii ma\'akum mina <strong>a</strong>lmunta<u>zh</u>iriin<strong>a</strong>', 'Maka mereka tidak menunggu-nunggu kecuali (kejadian-kejadian) yang sama dengan kejadian-kejadian (yang menimpa) orang-orang terdahulu sebelum mereka. Katakanlah, “Maka tunggulah, aku pun termasuk orang yang menunggu bersama kamu.”'),
	(1467, 10, 103, 'ثُمَّ نُنَجِّيْ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا كَذٰلِكَ ۚحَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِيْنَ ࣖ ', 'tsumma nunajjii rusulan<u>aa</u> wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ka<u>dzaa</u>lika <u>h</u>aqqan \'alayn<u>aa</u> nunjii <strong>a</strong>lmu/miniin<strong>a</strong>', 'Kemudian Kami selamatkan rasul-rasul Kami dan orang-orang yang beri-man, demikianlah menjadi kewajiban Kami menyelamatkan orang yang beriman.'),
	(1468, 10, 104, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ شَكٍّ مِّنْ دِيْنِيْ فَلَآ اَعْبُدُ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذِيْ يَتَوَفّٰىكُمْ ۖ وَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُؤْمِنِيْنَ', 'qul y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su in kuntum fii syakkin min diinii fal<u>aa</u> a\'budu <strong>al</strong>la<u>dz</u>iina ta\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi wal<u>aa</u>kin a\'budu <strong>al</strong>l<u>aa</u>ha <strong>al</strong>la<u>dz</u>ii yatawaff<u>aa</u>kum waumirtu an akuuna mina <strong>a</strong>lmu/miniin<strong></strong>', 'Katakanlah (Muhammad), “Wahai manusia! Jika kamu masih dalam keragu-raguan tentang agamaku, maka (ketahuilah) aku tidak menyembah yang kamu sembah selain Allah, tetapi aku menyembah Allah yang akan mematikan kamu dan aku telah diperintah agar termasuk orang yang beriman,”'),
	(1469, 10, 105, 'وَاَنْ اَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۚ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ', 'wa-an aqim wajhaka li<strong>l</strong>ddiini <u>h</u>aniifan wal<u>aa</u> takuunanna mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'dan (aku telah diperintah), “Hadapkanlah wajahmu kepada agama dengan tulus dan ikhlas dan jangan sekali-kali engkau termasuk orang yang musyrik.'),
	(1470, 10, 106, 'وَلَا تَدْعُ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَ ۚفَاِنْ فَعَلْتَ فَاِنَّكَ اِذًا مِّنَ الظّٰلِمِيْنَ ', 'wal<u>aa</u> tad\'u min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'uka wal<u>aa</u> ya<u>dh</u>urruka fa-in fa\'alta fa-innaka i<u>dz</u>an mina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan jangan engkau menyembah sesuatu yang tidak memberi manfaat dan tidak (pula) memberi bencana kepadamu selain Allah, sebab jika engkau lakukan (yang demikian), maka sesungguhnya engkau termasuk orang-orang zalim.”'),
	(1471, 10, 107, 'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗ ٓاِلَّا هُوَ ۚوَاِنْ يُّرِدْكَ بِخَيْرٍ فَلَا رَاۤدَّ لِفَضْلِهٖۗ يُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَهُوَ الْغَفُوْرُ الرَّحِيْمُ ', 'wa-in yamsaska <strong>al</strong>l<u>aa</u>hu bi<u>dh</u>urrin fal<u>aa</u> k<u>aa</u>syifa lahu ill<u>aa</u> huwa wa-in yuridka bikhayrin fal<u>aa</u> r<u>aa</u>dda lifa<u>dh</u>lihi yu<u>sh</u>iibu bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wahuwa <strong>a</strong>lghafuuru <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan jika Allah menimpakan suatu bencana kepadamu, maka tidak ada yang dapat menghilangkannya kecuali Dia. Dan jika Allah menghendaki kebaikan bagi kamu, maka tidak ada yang dapat menolak karunia-Nya. Dia memberikan kebaikan kepada siapa saja yang Dia kehendaki di antara hamba-hamba-Nya. Dia Maha Pengampun, Maha Penyayang.'),
	(1472, 10, 108, 'قُلْ يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الْحَقُّ مِنْ رَّبِّكُمْ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖ ۚوَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنَا۠ عَلَيْكُمْ بِوَكِيْلٍۗ  ', 'qul y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su qad j<u>aa</u>-akumu <strong>a</strong>l<u>h</u>aqqu min rabbikum famani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla fa-innam<u>aa</u> ya<u>dh</u>illu \'alayh<u>aa</u> wam<u>aa</u> an<u>aa</u> \'alaykum biwakiil<strong>in</strong>', 'Katakanlah (Muhammad), “Wahai manusia! Telah datang kepadamu kebenaran (Al-Qur\'an) dari Tuhanmu, sebab itu barang siapa mendapat petunjuk, maka sebenarnya (petunjuk itu) untuk (kebaikan) dirinya sendiri. Dan barang siapa sesat, sesungguhnya kesesatannya itu (mencelakakan) dirinya sendiri. Dan Aku bukanlah pemelihara dirimu.”'),
	(1473, 10, 109, 'وَاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُ ۚوَهُوَ خَيْرُ الْحٰكِمِيْنَ ࣖ ', 'wa<strong>i</strong>ttabi\' m<u>aa</u> yuu<u>haa</u> ilayka wa<strong>i</strong><u>sh</u>bir <u>h</u>att<u>aa</u> ya<u>h</u>kuma <strong>al</strong>l<u>aa</u>hu wahuwa khayru <strong>a</strong>l<u>haa</u>kimiin<strong>a</strong>', 'Dan ikutilah apa yang diwahyukan kepadamu, dan bersabarlah hingga Allah memberi keputusan. Dialah hakim yang terbaik.'),
	(1474, 11, 1, 'الۤرٰ ۗ كِتٰبٌ اُحْكِمَتْ اٰيٰتُهٗ ثُمَّ فُصِّلَتْ مِنْ لَّدُنْ حَكِيْمٍ خَبِيْرٍۙ', 'alif-l<u>aa</u>m-r<u>aa</u> kit<u>aa</u>bun u<u>h</u>kimat <u>aa</u>y<u>aa</u>tuhu tsumma fu<u>shsh</u>ilat min ladun <u>h</u>akiimin khabiir<strong>in</strong>', 'Alif Lam Ra. (Inilah) Kitab yang ayat-ayatnya disusun dengan rapi kemudian dijelaskan secara terperinci, (yang diturunkan) dari sisi (Allah) Yang Mahabijaksana, Mahateliti,'),
	(1475, 11, 2, 'اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنَّنِيْ لَكُمْ مِّنْهُ نَذِيْرٌ وَّبَشِيْرٌۙ ', '<strong>al</strong>l<u>aa</u> ta\'buduu ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha innanii lakum minhu na<u>dz</u>iirun wabasyiir<strong>un</strong>', 'agar kamu tidak menyembah selain Allah. Sesungguhnya aku (Muhammad) adalah pemberi peringatan dan pembawa berita gembira dari-Nya untukmu.'),
	(1476, 11, 3, 'وَّاَنِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُمَتِّعْكُمْ مَّتَاعًا حَسَنًا اِلٰٓى اَجَلٍ مُّسَمًّى وَّيُؤْتِ كُلَّ ذِيْ فَضْلٍ فَضْلَهٗ ۗوَاِنْ تَوَلَّوْا فَاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيْرٍ', 'wa-ani istaghfiruu rabbakum tsumma tuubuu ilayhi yumatti\'kum mat<u>aa</u>\'an <u>h</u>asanan il<u>aa</u> ajalin musamman wayu/ti kulla <u>dz</u>ii fa<u>dh</u>lin fa<u>dh</u>lahu wa-in tawallaw fa-inii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin kabiir<strong>in</strong>', 'Dan hendaklah kamu memohon ampunan kepada Tuhanmu dan bertobat kepada-Nya, niscaya Dia akan memberi kenikmatan yang baik kepadamu sampai waktu yang telah ditentukan. Dan Dia akan memberikan karunia-Nya kepada setiap orang yang berbuat baik. Dan jika kamu berpaling, maka sungguh, aku takut kamu akan ditimpa azab pada hari yang besar (Kiamat).'),
	(1477, 11, 4, 'اِلَى اللّٰهِ مَرْجِعُكُمْ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'il<u>aa</u> <strong>al</strong>l<u>aa</u>hi marji\'ukum wahuwa \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Kepada Allah-lah kamu kembali. Dia Mahakuasa atas segala sesuatu.'),
	(1478, 11, 5, 'اَلَآ اِنَّهُمْ يَثْنُوْنَ صُدُوْرَهُمْ لِيَسْتَخْفُوْا مِنْهُۗ  اَلَا حِيْنَ يَسْتَغْشُوْنَ ثِيَابَهُمْ ۙيَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَۚ اِنَّهٗ عَلِيْمٌ ۢ بِذَاتِ الصُّدُوْرِ  ۔', 'al<u>aa</u> innahum yatsnuuna <u>sh</u>uduurahum liyastakhfuu minhu <strong>a</strong>l<u>aa</u> <u>h</u>iina yastaghsyuuna tsiy<u>aa</u>bahum ya\'lamu m<u>aa</u> yusirruuna wam<u>aa</u> yu\'linuuna innahu \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Ingatlah, sesungguhnya mereka (orang-orang munafik) memalingkan dada untuk menyembunyikan diri dari dia (Muhammad). Ingatlah, ketika mereka menyelimuti dirinya dengan kain, Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka nyatakan, sungguh, Allah Maha Mengetahui (segala) isi hati.'),
	(1479, 11, 6, '۞ وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۗ كُلٌّ فِيْ كِتٰبٍ مُّبِيْنٍ', 'wam<u>aa</u> min d<u>aa</u>bbatin fii <strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi rizquh<u>aa</u> waya\'lamu mustaqarrah<u>aa</u> wamustawda\'ah<u>aa</u> kullun fii kit<u>aa</u>bin mubiin<strong>in</strong>', 'Dan tidak satupun makhluk bergerak (bernyawa) di bumi melainkan semuanya dijamin Allah rezekinya. Dia mengetahui tempat kediamannya dan tempat penyimpanannya. Semua (tertulis) dalam Kitab yang nyata (Lauh Mahfuzh).'),
	(1480, 11, 7, 'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ وَّكَانَ عَرْشُهٗ عَلَى الْمَاۤءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًا ۗوَلَىِٕنْ قُلْتَ اِنَّكُمْ مَّبْعُوْثُوْنَ مِنْۢ بَعْدِ الْمَوْتِ لَيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰ', 'wahuwa <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a fii sittati ayy<u>aa</u>min wak<u>aa</u>na \'arsyuhu \'al<u>aa</u> <strong>a</strong>lm<u>aa</u>-i liyabluwakum ayyukum a<u>h</u>sanu \'amalan wala-in qulta innakum mab\'uutsuuna min ba\'di <strong>a</strong>lmawti layaquulanna <strong>al</strong>la<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin<strong>un</strong>', 'Dan Dialah yang menciptakan langit dan bumi dalam enam masa, dan ‘Arsy-Nya di atas air, agar Dia menguji siapakah di antara kamu yang lebih baik amalnya. Jika engkau berkata (kepada penduduk Mekah), “Sesungguhnya kamu akan dibangkitkan setelah mati,” niscaya orang kafir itu akan berkata, “Ini hanyalah sihir yang nyata.”'),
	(1481, 11, 8, 'وَلَىِٕنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَّعْدُوْدَةٍ لَّيَقُوْلُنَّ مَا يَحْبِسُهٗ ۗ اَلَا يَوْمَ يَأْتِيْهِمْ لَيْسَ مَصْرُوْفًا عَنْهُمْ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ ', 'wala-in akhkharn<u>aa</u> \'anhumu <strong>a</strong>l\'a<u>dzaa</u>ba il<u>aa</u> ummatin ma\'duudatin layaquulunna m<u>aa</u> ya<u>h</u>bisuhu <strong>a</strong>l<u>aa</u> yawma ya/tiihim laysa ma<u>sh</u>ruufan \'anhum wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Dan sungguh, jika Kami tangguhkan azab terhadap mereka sampai waktu yang ditentukan, niscaya mereka akan berkata, “Apakah yang menghalanginya?” Ketahuilah, ketika azab itu datang kepada mereka, tidaklah dapat dielakkan oleh mereka. Mereka dikepung oleh (azab) yang dahulu mereka memperolok-olokkannya.'),
	(1482, 11, 9, 'وَلَىِٕنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنٰهَا مِنْهُۚ اِنَّهٗ لَيَـُٔوْسٌ كَفُوْرٌ', 'wala-in a<u>dz</u>aqn<u>aa</u> <strong>a</strong>l-ins<u>aa</u>na minn<u>aa</u> ra<u>h</u>matan tsumma naza\'n<u>aa</u>h<u>aa</u> minhu innahu layauusun kafuur<strong>un</strong>', 'Dan jika Kami berikan rahmat Kami kepada manusia, kemudian (rahmat itu) Kami cabut kembali, pastilah dia menjadi putus asa dan tidak berterima kasih.'),
	(1483, 11, 10, 'وَلَىِٕنْ اَذَقْنٰهُ نَعْمَاۤءَ بَعْدَ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ ذَهَبَ السَّيِّاٰتُ عَنِّيْ ۗاِنَّهٗ لَفَرِحٌ فَخُوْرٌۙ  ', 'wala-in a<u>dz</u>aqn<u>aa</u>hu na\'m<u>aa</u>-a ba\'da <u>dh</u>arr<u>aa</u>-a massat-hu layaquulanna <u>dz</u>ahaba <strong>al</strong>ssayyi-<u>aa</u>tu \'annii innahu lafari<u>h</u>un fakhuur<strong>un</strong>', 'Dan jika Kami berikan kebahagiaan kepadanya setelah ditimpa bencana yang menimpanya, niscaya dia akan berkata, “Telah hilang bencana itu dariku.” Sesungguhnya dia (merasa) sangat gembira dan bangga,'),
	(1484, 11, 11, 'اِلَّا الَّذِيْنَ صَبَرُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>sh</u>abaruu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti ul<u>aa</u>-ika lahum maghfiratun wa-ajrun kabiir<strong>un</strong>', 'kecuali orang-orang yang sabar, dan mengerjakan kebajikan, mereka memperoleh ampunan dan pahala yang besar.'),
	(1485, 11, 12, 'فَلَعَلَّكَ تَارِكٌۢ بَعْضَ مَا يُوْحٰىٓ اِلَيْكَ وَضَاۤىِٕقٌۢ بِهٖ صَدْرُكَ اَنْ يَّقُوْلُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَاۤءَ مَعَهٗ مَلَكٌ  ۗاِنَّمَآ اَنْتَ نَذِيْرٌ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ  ۗ', 'fala\'allaka t<u>aa</u>rikun ba\'<u>dh</u>a m<u>aa</u> yuu<u>haa</u> ilayka wa<u>daa</u>-iqun bihi <u>sh</u>adruka an yaquuluu lawl<u>aa</u> unzila \'alayhi kanzun aw j<u>aa</u>-a ma\'ahu malakun innam<u>aa</u> anta na<u>dz</u>iirun wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in wakiil<strong>un</strong>', 'Maka boleh jadi engkau (Muhammad) hendak meninggalkan sebagian dari apa yang diwahyukan kepadamu dan dadamu sempit karenanya, karena mereka akan mengatakan, “Mengapa tidak diturunkan kepadanya harta (kekayaan) atau datang bersamanya malaikat?” Sungguh, engkau hanyalah seorang pemberi peringatan dan Allah pemelihara segala sesuatu.'),
	(1486, 11, 13, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗقُلْ فَأْتُوْا بِعَشْرِ سُوَرٍ مِّثْلِهٖ مُفْتَرَيٰتٍ وَّادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'am yaquuluuna iftar<u>aa</u>hu qul fa/tuu bi\'asyri suwarin mitslihi muftaray<u>aa</u>tin wa<strong>u</strong>d\'uu mani ista<u>th</u>a\'tum min duuni <strong>al</strong>l<u>aa</u>hi in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Bahkan mereka mengatakan, “Dia (Muhammad) telah membuat-buat Al-Qur\'an itu.” Katakanlah, “(Kalau demikian), datangkanlah sepuluh surah semisal dengannya (Al-Qur\'an) yang dibuat-buat, dan ajaklah siapa saja di antara kamu yang sanggup selain Allah, jika kamu orang-orang yang benar.”'),
	(1487, 11, 14, 'فَاِلَّمْ يَسْتَجِيْبُوْا لَكُمْ فَاعْلَمُوْٓا اَنَّمَآ اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَّآ اِلٰهَ اِلَّا هُوَ ۚفَهَلْ اَنْتُمْ مُّسْلِمُوْنَ', 'fa-illam yastajiibuu lakum fa<strong>i</strong>\'lamuu annam<u>aa</u> unzila bi\'ilmi <strong>al</strong>l<u>aa</u>hi wa-an l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fahal antum muslimuun<strong>a</strong>', 'Maka jika mereka tidak memenuhi tantanganmu, maka (katakanlah), “Ketahuilah, bahwa (Al-Qur\'an) itu diturunkan dengan ilmu Allah, dan bahwa tidak ada tuhan selain Dia, maka maukah kamu berserah diri (masuk Islam)?”'),
	(1488, 11, 15, 'مَنْ كَانَ يُرِيْدُ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ فِيْهَا وَهُمْ فِيْهَا لَا يُبْخَسُوْنَ', 'man k<u>aa</u>na yuriidu <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> waziinatah<u>aa</u> nuwaffi ilayhim a\'m<u>aa</u>lahum fiih<u>aa</u> wahum fiih<u>aa</u> l<u>aa</u> yubkhasuun<strong>a</strong>', 'Barangsiapa menghendaki kehidupan dunia dan perhiasannya, pasti Kami berikan (balasan) penuh atas pekerjaan mereka di dunia (dengan sempurna) dan mereka di dunia tidak akan dirugikan.'),
	(1489, 11, 16, 'اُولٰۤىِٕكَ الَّذِيْنَ لَيْسَ لَهُمْ فِى الْاٰخِرَةِ اِلَّا النَّارُ ۖوَحَبِطَ مَا صَنَعُوْا فِيْهَا وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina laysa lahum fii <strong>a</strong>l-<u>aa</u>khirati ill<u>aa</u> <strong>al</strong>nn<u>aa</u>ru wa<u>h</u>abi<u>th</u>a m<u>aa</u> <u>sh</u>ana\'uu fiih<u>aa</u> wab<u>aath</u>ilun m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Itulah orang-orang yang tidak memperoleh (sesuatu) di akhirat kecuali neraka, dan sia-sialah di sana apa yang telah mereka usahakan (di dunia) dan terhapuslah apa yang telah mereka kerjakan. '),
	(1490, 11, 17, 'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ وَيَتْلُوْهُ شَاهِدٌ مِّنْهُ وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰىٓ اِمَامًا وَّرَحْمَةًۗ  اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗوَمَنْ يَّكْفُرْ بِهٖ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهٗ فَلَا تَكُ فِيْ مِرْيَةٍ مِّنْهُ', 'afaman k<u>aa</u>na \'al<u>aa</u> bayyinatin min rabbihi wayatluuhu sy<u>aa</u>hidun minhu wamin qablihi kit<u>aa</u>bu muus<u>aa</u> im<u>aa</u>man wara<u>h</u>matan ul<u>aa</u>-ika yu/minuuna bihi waman yakfur bihi mina <strong>a</strong>l-a<u>h</u>z<u>aa</u>bi fa<strong>al</strong>nn<u>aa</u>ru maw\'iduhu fal<u>aa</u> taku fii miryatin minhu innahu <strong>a</strong>l<u>h</u>aqqu min rabbika wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> yu/minuun<strong>a</strong>', 'Maka apakah (orang-orang kafir itu sama dengan) orang yang sudah mempunyai bukti yang nyata (Al-Qur\'an) dari Tuhannya, dan diikuti oleh saksi dari-Nya dan sebelumnya sudah ada pula Kitab Musa yang menjadi pedoman dan rahmat? Mereka beriman kepadanya (Al-Qur\'an). Barangsiapa mengingkarinya (Al-Qur\'an) di antara kelompok-kelompok (orang Quraisy), maka nerakalah tempat yang diancamkan baginya, karena itu janganlah engkau ragu terhadap Al-Qur\'an. Sungguh, Al-Qur\'an itu benar-benar dari Tuhanmu, tetapi kebanyakan manusia tidak beriman.'),
	(1491, 11, 18, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ اُولٰۤىِٕكَ يُعْرَضُوْنَ عَلٰى رَبِّهِمْ وَيَقُوْلُ الْاَشْهَادُ هٰٓؤُلَاۤءِ الَّذِيْنَ كَذَبُوْا عَلٰى رَبِّهِمْۚ  اَلَا لَعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ۙ  ', 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban ul<u>aa</u>-ika yu\'ra<u>dh</u>uuna \'al<u>aa</u> rabbihim wayaquulu <strong>a</strong>l-asyh<u>aa</u>du h<u>aa</u>ul<u>aa</u>-i <strong>al</strong>la<u>dz</u>iina ka<u>dz</u>abuu \'al<u>aa</u> rabbihim <strong>a</strong>l<u>aa</u> la\'natu <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan siapakah yang lebih zalim daripada orang yang mengada-adakan suatu kebohongan terhadap Allah? Mereka itu akan dihadapkan kepada Tuhan mereka, dan para saksi akan berkata, “Orang-orang inilah yang telah berbohong terhadap Tuhan mereka.” Ingatlah, laknat Allah (ditimpakan) kepada orang yang zalim,'),
	(1492, 11, 19, 'الَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۗ وَهُمْ بِالْاٰخِرَةِ هُمْ كفِٰرُوْنَ', '<strong>al</strong>la<u>dz</u>iina ya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan wahum bi<strong>a</strong>l-<u>aa</u>khirati hum k<u>aa</u>firuun<strong>a</strong>', '(yaitu) mereka yang menghalangi dari jalan Allah dan menghendaki agar jalan itu bengkok. Dan mereka itulah orang yang tidak percaya adanya hari akhirat.'),
	(1493, 11, 20, 'اُولٰۤىِٕكَ لَمْ يَكُوْنُوْا مُعْجِزِيْنَ فِى الْاَرْضِ وَمَا كَانَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ۘ يُضٰعَفُ لَهُمُ الْعَذَابُ ۗمَا كَانُوْا يَسْتَطِيْعُوْنَ السَّمْعَ وَمَا كَانُوْا يُبْصِرُوْنَ', 'ul<u>aa</u>-ika lam yakuunuu mu\'jiziina fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> k<u>aa</u>na lahum min duuni <strong>al</strong>l<u>aa</u>hi min awliy<u>aa</u>-a yu<u>daa</u>\'afu lahumu <strong>a</strong>l\'a<u>dzaa</u>bu m<u>aa</u> k<u>aa</u>nuu yasta<u>th</u>ii\'uuna <strong>al</strong>ssam\'a wam<u>aa</u> k<u>aa</u>nuu yub<u>sh</u>iruun<strong>a</strong>', 'Mereka tidak mampu menghalangi (siksaan Allah) di bumi, dan tidak akan ada bagi mereka penolong selain Allah. Azab itu dilipatgandakan kepada mereka. Mereka tidak mampu mendengar (kebenaran) dan tidak dapat melihat(nya).'),
	(1494, 11, 21, 'اُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina khasiruu anfusahum wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Mereka itulah orang yang merugikan dirinya sendiri, dan lenyaplah dari mereka apa yang selalu mereka ada-adakan.'),
	(1495, 11, 22, 'لَاجَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ', 'l<u>aa</u> jarama annahum fii <strong>a</strong>l-<u>aa</u>khirati humu <strong>a</strong>l-akhsaruun<strong>a</strong>', 'Pasti mereka itu (menjadi) orang yang paling rugi di akhirat.'),
	(1496, 11, 23, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَخْبَتُوْٓا اِلٰى رَبِّهِمْۙ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shaa</u>li<u>haa</u>ti wa-akhbatuu il<u>aa</u> rabbihim ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan dan merendahkan diri kepada Tuhan, mereka itu penghuni surga, mereka kekal di dalamnya.'),
	(1497, 11, 24, '۞ مَثَلُ الْفَرِيْقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَصِيْرِ وَالسَّمِيْعِۗ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَفَلَا تَذَكَّرُوْنَ ࣖ ', 'matsalu <strong>a</strong>lfariiqayni ka<strong>a</strong>l<strong>-</strong>a\'m<u>aa</u> wa<strong>a</strong>l-a<u>sh</u>ammi wa<strong>a</strong>lba<u>sh</u>iiri wa<strong>al</strong>ssamii\'i hal yastawiy<u>aa</u>ni matsalan afal<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Perumpamaan kedua golongan (orang kafir dan mukmin), seperti orang buta dan tuli dengan orang yang dapat melihat dan dapat mendengar. Samakah kedua golongan itu? Maka tidakkah kamu mengambil pelajaran?'),
	(1498, 11, 25, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۙ', 'walaqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi innii lakum na<u>dz</u>iirun mubiin<strong>un</strong>', 'Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, (dia berkata), “Sungguh, aku ini adalah pemberi peringatan yang nyata bagi kamu,'),
	(1499, 11, 26, 'اَنْ لَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۖاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَلِيْمٍ', 'an l<u>aa</u> ta\'buduu ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin <strong>a</strong>liim<strong>in</strong>', 'agar kamu tidak menyembah selain Allah. Aku benar-benar khawatir kamu akan ditimpa azab (pada) hari yang sangat pedih.”'),
	(1500, 11, 27, 'فَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا نَرٰىكَ اِلَّا بَشَرًا مِّثْلَنَا وَمَا نَرٰىكَ اتَّبَعَكَ اِلَّا الَّذِيْنَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍۢ بَلْ نَظُنُّكُمْ كٰذِبِيْنَ', 'faq<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina kafaruu min qawmihi m<u>aa</u> nar<u>aa</u>ka ill<u>aa</u> basyaran mitslan<u>aa</u> wam<u>aa</u> nar<u>aa</u>ka ittaba\'aka ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina hum ar<u>aadz</u>ilun<u>aa</u> b<u>aa</u>diya <strong>al</strong>rra/yi wam<u>aa</u> nar<u>aa</u> lakum \'alayn<u>aa</u> min fa<u>dh</u>lin bal na<u>zh</u>unnukum k<u>aadz</u>ibiin<strong>a</strong>', 'Maka berkatalah para pemuka yang kafir dari kaumnya, “Kami tidak melihat engkau, melainkan hanyalah seorang manusia (biasa) seperti kami, dan kami tidak melihat orang yang mengikuti engkau, melainkan orang yang hina dina di antara kami yang lekas percaya. Kami tidak melihat kamu memiliki suatu kelebihan apa pun atas kami, bahkan kami menganggap kamu adalah orang pendusta.”'),
	(1501, 11, 28, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَاٰتٰىنِيْ رَحْمَةً مِّنْ عِنْدِهٖ فَعُمِّيَتْ عَلَيْكُمْۗ اَنُلْزِمُكُمُوْهَا وَاَنْتُمْ لَهَا كٰرِهُوْنَ', 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii wa<u>aa</u>t<u>aa</u>nii ra<u>h</u>matan min \'indihi fa\'ummiyat \'alaykum anulzimukumuuh<u>aa</u> wa-antum lah<u>aa</u> k<u>aa</u>rihuun<strong>a</strong>', 'Dia (Nuh) berkata,  ”Wahai kaumku! Apa pendapatmu jika aku mempunyai bukti yang nyata dari Tuhanku, dan aku diberi rahmat dari sisi-Nya, sedangkan (rahmat itu) disamarkan bagimu. Apa kami akan memaksa kamu untuk menerimanya, padahal kamu tidak menyukainya?'),
	(1502, 11, 29, 'وَيٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ مَالًاۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَآ اَنَا۠ بِطَارِدِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ', 'way<u>aa</u> qawmi l<u>aa</u> as-alukum \'alayhi m<u>aa</u>lan in ajriya ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> an<u>aa</u> bi<u>th</u><u>aa</u>ridi <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu innahum mul<u>aa</u>quu rabbihim wal<u>aa</u>kinnii ar<u>aa</u>kum qawman tajhaluun<strong>a</strong>', 'Dan wahai kaumku! Aku tidak meminta harta kepada kamu (sebagai imbalan) atas seruanku. Imbalanku hanyalah dari Allah dan aku sekali-kali tidak akan mengusir orang yang telah beriman. Sungguh, mereka akan bertemu dengan Tuhannya, dan sebaliknya aku memandangmu sebagai kaum yang bodoh.'),
	(1503, 11, 30, 'وَيٰقَوْمِ مَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ طَرَدْتُّهُمْ ۗ اَفَلَا تَذَكَّرُوْنَ', 'way<u>aa</u> qawmi man yan<u>sh</u>urunii mina <strong>al</strong>l<u>aa</u>hi in <u>th</u>aradtuhum afal<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Dan wahai kaumku! Siapakah yang akan menolongku dari (azab) Allah jika aku mengusir mereka. Tidakkah kamu mengambil pelajaran?'),
	(1504, 11, 31, 'وَلَآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ اِنِّيْ مَلَكٌ وَّلَآ اَقُوْلُ لِلَّذِيْنَ تَزْدَرِيْٓ اَعْيُنُكُمْ لَنْ يُّؤْتِيَهُمُ اللّٰهُ خَيْرًا ۗ اَللّٰهُ اَعْلَمُ بِمَا فِيْٓ اَنْفُسِهِمْ ۚاِنِّيْٓ اِ', 'wal<u>aa</u> aquulu lakum \'indii khaz<u>aa</u>-inu <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> a\'lamu <strong>a</strong>lghayba wal<u>aa</u> aquulu innii malakun wal<u>aa</u> aquulu lilla<u>dz</u>iina tazdarii a\'yunukum lan yu/tiyahumu <strong>al</strong>l<u>aa</u>hu khayran <strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> fii anfusihim innii i<u>dz</u>an lamina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan aku tidak mengatakan kepadamu, bahwa aku mempunyai gudang-gudang rezeki dan kekayaan dari Allah, dan aku tidak mengetahui yang gaib, dan tidak (pula) mengatakan bahwa sesungguhnya aku adalah malaikat, dan aku tidak (juga) mengatakan kepada orang yang dipandang hina oleh penglihatanmu, “Bahwa Allah tidak akan memberikan kebaikan kepada mereka. Allah lebih mengetahui apa yang ada pada diri mereka. Sungguh, jika demikian aku benar-benar termasuk orang-orang yang zalim.”'),
	(1505, 11, 32, 'قَالُوْا يٰنُوْحُ قَدْ جَادَلْتَنَا فَاَ كْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'q<u>aa</u>luu y<u>aa</u> nuu<u>h</u>u qad j<u>aa</u>daltan<u>aa</u> fa-aktsarta jid<u>aa</u>lan<u>aa</u> fa/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Mereka berkata, “Wahai Nuh! Sungguh, engkau telah berbantah dengan kami, dan engkau telah memperpanjang bantahanmu terhadap kami, maka datangkanlah kepada kami azab yang engkau ancamkan, jika kamu termasuk orang yang benar.”'),
	(1506, 11, 33, 'قَالَ اِنَّمَا يَأْتِيْكُمْ بِهِ اللّٰهُ اِنْ شَاۤءَ وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ', 'q<u>aa</u>la innam<u>aa</u> ya/tiikum bihi <strong>al</strong>l<u>aa</u>hu in sy<u>aa</u>-a wam<u>aa</u> antum bimu\'jiziin<strong>a</strong>', 'Dia (Nuh) menjawab, “Hanya Allah yang akan mendatangkan azab kepadamu jika Dia menghendaki, dan kamu tidak akan dapat melepaskan diri.'),
	(1507, 11, 34, 'وَلَا يَنْفَعُكُمْ نُصْحِيْٓ اِنْ اَرَدْتُّ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُرِيْدُ اَنْ يُّغْوِيَكُمْ ۗهُوَ رَبُّكُمْ ۗوَاِلَيْهِ تُرْجَعُوْنَۗ ', 'wal<u>aa</u> yanfa\'ukum nu<u>sy</u>ii in aradtu an an<u>sh</u>a<u>h</u>a lakum in k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu yuriidu an yughwiyakum huwa rabbukum wa-ilayhi turja\'uun<strong>a</strong>', 'Dan nasihatku tidak akan bermanfaat bagimu sekalipun aku ingin memberi nasihat kepadamu, kalau Allah hendak menyesatkan kamu. Dia adalah Tuhanmu, dan kepada-Nyalah kamu dikembalikan.”'),
	(1508, 11, 35, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُۗ قُلْ اِنِ افْتَرَيْتُهٗ فَعَلَيَّ اِجْرَامِيْ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تُجْرِمُوْنَ ࣖ ', 'am yaquuluuna iftar<u>aa</u>hu qul ini iftaraytuhu fa\'alayya ijr<u>aa</u>mii wa-an<u>aa</u> barii-un mimm<u>aa</u> tujrimuun<strong>a</strong>', 'Bahkan mereka (orang kafir) berkata, “Dia cuma mengada-ada saja.” Katakanlah (Muhammad), “Jika aku mengada-ada, akulah yang akan memikul dosanya, dan aku bebas dari dosa yang kamu perbuat.”'),
	(1509, 11, 36, 'وَاُوْحِيَ اِلٰى نُوْحٍ اَنَّهٗ لَنْ يُّؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَفْعَلُوْنَۖ  ', 'wauu<u>h</u>iya il<u>aa</u> nuu<u>h</u>in annahu lan yu/mina min qawmika ill<u>aa</u> man qad <u>aa</u>mana fal<u>aa</u> tabta-is bim<u>aa</u> k<u>aa</u>nuu yaf\'aluun<strong>a</strong>', 'Dan diwahyukan kepada Nuh, “Ketahuilah tidak akan beriman di an-tara kaummu, kecuali orang yang benar-benar beriman (saja), karena itu janganlah engkau bersedih hati tentang apa yang mereka perbuat.'),
	(1510, 11, 37, 'وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْا ۚاِنَّهُمْ مُّغْرَقُوْنَ', 'wa<strong>i</strong><u>sh</u>na\'i <strong>a</strong>lfulka bi-a\'yunin<u>aa</u> wawa<u>h</u>yin<u>aa</u> wal<u>aa</u> tukh<u>aath</u>ibnii fii <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu innahum mughraquun<strong>a</strong>', 'Dan buatlah kapal itu dengan pengawasan dan petunjuk wahyu Kami, dan janganlah engkau bicarakan dengan Aku tentang orang-orang yang zalim. Sesungguhnya mereka itu akan ditenggelamkan.”'),
	(1511, 11, 38, 'وَيَصْنَعُ الْفُلْكَۗ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِّنْ قَوْمِهٖ سَخِرُوْا مِنْهُ ۗقَالَ اِنْ تَسْخَرُوْا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُوْنَۗ ', 'waya<u>sh</u>na\'u <strong>a</strong>lfulka wakullam<u>aa</u> marra \'alayhi malaun min qawmihi sakhiruu minhu q<u>aa</u>la in taskharuu minn<u>aa</u> fa-inn<u>aa</u> naskharu minkum kam<u>aa</u> taskharuun<strong>a</strong>', 'Dan mulailah dia (Nuh) membuat kapal. Setiap kali pemimpin kaumnya berjalan melewatinya, mereka mengejeknya. Dia (Nuh) berkata, “Jika kamu mengejek kami, maka kami (pun) akan mengejekmu sebagaimana kamu mengejek (kami).'),
	(1512, 11, 39, 'فَسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ', 'fasawfa ta\'lamuuna man ya/tiihi \'a<u>dzaa</u>bun yukhziihi waya<u>h</u>illu \'alayhi \'a<u>dzaa</u>bun muqiim<strong>un</strong>', 'Maka kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakan dan (siapa) yang akan ditimpa azab yang kekal.”'),
	(1513, 11, 40, 'حَتّٰىٓ اِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ قُلْنَا احْمِلْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَ ۗوَمَآ اٰمَنَ مَعَهٗٓ اِلَّا قَلِيْلٌ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a amrun<u>aa</u> waf<u>aa</u>ra <strong>al</strong>ttannuuru quln<u>aa</u> i<u>h</u>mil fiih<u>aa</u> min kullin zawjayni itsnayni wa-ahlaka ill<u>aa</u> man sabaqa \'alayhi <strong>a</strong>lqawlu waman <u>aa</u>mana wam<u>aa</u> <u>aa</u>mana ma\'ahu ill<u>aa</u> qaliil<strong>un</strong>', 'Hingga apabila perintah Kami datang dan tanur (dapur) telah memancarkan air, Kami berfirman, “Muatkanlah ke dalamnya (kapal itu) dari masing-masing (hewan) sepasang (jantan dan betina), dan (juga) keluargamu kecuali orang yang telah terkena ketetapan terdahulu dan (muatkan pula) orang yang beriman.” Ternyata orang-orang beriman yang bersama dengan Nuh hanya sedikit.'),
	(1514, 11, 41, '۞ وَقَالَ ارْكَبُوْا فِيْهَا بِسْمِ اللّٰهِ مَجْرٰ۪ىهَا وَمُرْسٰىهَا ۗاِنَّ رَبِّيْ لَغَفُوْرٌ رَّحِيْمٌ', 'waq<u>aa</u>la irkabuu fiih<u>aa</u> bismi <strong>al</strong>l<u>aa</u>hi majr<u>aa</u>h<u>aa</u> wamurs<u>aa</u>h<u>aa</u> inna rabbii laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan dia berkata,  ”Naiklah kamu semua ke dalamnya (kapal) dengan (menyebut) nama Allah pada waktu berlayar dan berlabuhnya. Sesungguhnya Tuhanku Maha Pengampun, Maha Penyayang.”'),
	(1515, 11, 42, 'وَهِيَ تَجْرِيْ بِهِمْ فِيْ مَوْجٍ كَالْجِبَالِۗ وَنَادٰى نُوْحُ ِۨابْنَهٗ وَكَانَ فِيْ مَعْزِلٍ يّٰبُنَيَّ ارْكَبْ مَّعَنَا وَلَا تَكُنْ مَّعَ الْكٰفِرِيْنَ', 'wahiya tajrii bihim fii mawjin ka<strong>a</strong>ljib<u>aa</u>li wan<u>aa</u>d<u>aa</u> nuu<u>h</u>unu ibnahu wak<u>aa</u>na fii ma\'zilin y<u>aa</u> bunayya irkab ma\'an<u>aa</u> wal<u>aa</u> takun ma\'a <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan kapal itu berlayar membawa mereka ke dalam gelombang laksana gunung-gunung. Dan Nuh memanggil anaknya, ketika dia (anak itu) berada di tempat yang jauh terpencil, “Wahai anakku! Naiklah (ke kapal) bersama kami dan janganlah engkau bersama orang-orang kafir.”'),
	(1516, 11, 43, 'قَالَ سَاٰوِيْٓ اِلٰى جَبَلٍ يَّعْصِمُنِيْ مِنَ الْمَاۤءِ ۗقَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَّحِمَ ۚوَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِيْنَ', 'q<u>aa</u>la sa<u>aa</u>wii il<u>aa</u> jabalin ya\'<u>sh</u>imunii mina <strong>a</strong>lm<u>aa</u>-i q<u>aa</u>la l<u>aa</u> \'<u>aas</u>ima <strong>a</strong>lyawma min amri <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> man ra<u>h</u>ima wa<u>haa</u>la baynahum<u>aa</u> <strong>a</strong>lmawju fak<u>aa</u>na mina <strong>a</strong>lmughraqiin<strong>a</strong>', 'Dia (anaknya) menjawab, “Aku akan mencari perlindungan ke gunung yang dapat menghindarkan aku dari air bah!”  (Nuh) berkata, “Tidak ada yang melindungi dari siksaan Allah pada hari ini selain Allah yang Maha Penyayang.” Dan gelombang menjadi penghalang antara keduanya; maka dia (anak itu) termasuk orang yang ditenggelamkan.'),
	(1517, 11, 44, 'وَقِيْلَ يٰٓاَرْضُ ابْلَعِيْ مَاۤءَكِ وَيَا سَمَاۤءُ اَقْلِعِيْ وَغِيْضَ الْمَاۤءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُوْدِيِّ وَقِيْلَ بُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ', 'waqiila y<u>aa</u> ar<u>dh</u>u ibla\'ii m<u>aa</u>-aki way<u>aa</u> sam<u>aa</u>u aqli\'ii waghii<u>dh</u>a <strong>a</strong>lm<u>aa</u>u waqu<u>dh</u>iya <strong>a</strong>l-amru wa<strong>i</strong>stawat \'al<u>aa</u> <strong>a</strong>ljuudiyyi waqiila bu\'dan lilqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan difirmankan, “Wahai bumi! Telanlah airmu dan wahai langit (hujan!) berhentilah.” Dan air pun disurutkan, dan perintah pun diselesaikan dan kapal itupun berlabuh di atas gunung Judi, dan dikatakan,  ”Binasalah orang-orang zalim.”'),
	(1518, 11, 45, 'وَنَادٰى نُوْحٌ رَّبَّهٗ فَقَالَ رَبِّ اِنَّ ابْنِيْ مِنْ اَهْلِيْۚ وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحٰكِمِيْنَ', 'wan<u>aa</u>d<u>aa</u> nuu<u>h</u>un rabbahu faq<u>aa</u>la rabbi inna ibnii min ahlii wa-inna wa\'daka <strong>a</strong>l<u>h</u>aqqu wa-anta a<u>h</u>kamu <strong>a</strong>l<u>haa</u>kimiin<strong>a</strong>', 'Dan Nuh memohon kepada Tuhannya sambil berkata, “Ya Tuhanku, sesungguhnya anakku adalah termasuk keluargaku, dan janji-Mu itu pasti benar. Engkau adalah hakim yang paling adil.”'),
	(1519, 11, 46, 'قَالَ يٰنُوْحُ اِنَّهٗ لَيْسَ مِنْ اَهْلِكَ ۚاِنَّهٗ عَمَلٌ غَيْرُ صَالِحٍ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنِّيْٓ اَعِظُكَ اَنْ تَكُوْنَ مِنَ الْجٰهِلِيْنَ', 'q<u>aa</u>la y<u>aa</u> nuu<u>h</u>u innahu laysa min ahlika innahu \'amalun ghayru <u>shaa</u>li<u>h</u>in fal<u>aa</u> tas-alni m<u>aa</u> laysa laka bihi \'ilmun innii a\'i<u>zh</u>uka an takuuna mina <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Dia (Allah) berfirman, “Wahai Nuh! Sesungguhnya dia bukanlah termasuk keluargamu, karena perbuatannya sungguh tidak baik, sebab itu jangan engkau memohon kepada-Ku sesuatu yang tidak engkau ketahui (hakikatnya). Aku menasihatimu agar (engkau) tidak termasuk orang yang bodoh.”'),
	(1520, 11, 47, 'قَالَ رَبِّ اِنِّيْٓ اَعُوْذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ ۗوَاِلَّا تَغْفِرْ لِيْ وَتَرْحَمْنِيْٓ اَكُنْ مِّنَ الْخٰسِرِيْنَ', 'q<u>aa</u>la rabbi innii a\'uu<u>dz</u>u bika an as-alaka m<u>aa</u> laysa lii bihi \'ilmun wa-ill<u>aa</u> taghfir lii watar<u>h</u>amnii akun mina <strong>a</strong>lkh<u>aa</u>siriin<strong>a</strong>', 'Dia (Nuh) berkata, “Ya Tuhanku, sesungguhnya aku berlindung kepada-Mu untuk memohon kepada-Mu sesuatu yang aku tidak mengetahui (hakikatnya). Kalau Engkau tidak mengampuniku, dan (tidak) menaruh belas kasihan kepadaku, niscaya aku termasuk orang yang rugi.”'),
	(1521, 11, 48, 'قِيْلَ يٰنُوْحُ اهْبِطْ بِسَلٰمٍ مِّنَّا وَبَرَكٰتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِّمَّنْ مَّعَكَ ۗوَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِّنَّا عَذَابٌ اَلِيْمٌ', 'qiila y<u>aa</u> nuu<u>h</u>u ihbi<u>th</u> bisal<u>aa</u>min minn<u>aa</u> wabarak<u>aa</u>tin \'alayka wa\'al<u>aa</u> umamin mimman ma\'aka waumamun sanumatti\'uhum tsumma yamassuhum minn<u>aa</u> \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Difirmankan, “Wahai Nuh! Turunlah dengan selamat sejahtera dan penuh keberkahan dari Kami, bagimu dan bagi semua umat (mukmin) yang bersamamu. Dan ada umat-umat yang Kami beri kesenangan (dalam kehidupan dunia), kemudian mereka akan ditimpa azab Kami yang pedih.”'),
	(1522, 11, 49, 'تِلْكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهَآ اِلَيْكَ ۚمَا كُنْتَ تَعْلَمُهَآ اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۚ فَاصْبِرْۚ اِنَّ الْعَاقِبَةَ لِلْمُتَّقِيْنَ ࣖ ', 'tilka min anb<u>aa</u>-i <strong>a</strong>lghaybi nuu<u>h</u>iih<u>aa</u> ilayka m<u>aa</u> kunta ta\'lamuh<u>aa</u> anta wal<u>aa</u> qawmuka min qabli h<u>aadzaa</u> fa<strong>i</strong><u>sh</u>bir inna <strong>a</strong>l\'<u>aa</u>qibata lilmuttaqiin<strong>a</strong>', 'Itulah sebagian dari berita-berita gaib yang Kami wahyukan kepadamu (Muhammad); tidak pernah engkau mengetahuinya dan tidak (pula) kaummu sebelum ini. Maka bersabarlah, sungguh, kesudahan (yang baik) adalah bagi orang yang bertakwa.'),
	(1523, 11, 50, 'وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗاِنْ اَنْتُمْ اِلَّا مُفْتَرُوْنَ', 'wa-il<u>aa</u> \'<u>aa</u>din akh<u>aa</u>hum huudan q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu in antum ill<u>aa</u> muftaruun<strong>a</strong>', 'Dan kepada kaum ‘Ad (Kami utus) saudara mereka, Hud. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. (Selama ini) kamu hanyalah mengada-ada.'),
	(1524, 11, 51, 'يٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا ۗاِنْ اَجْرِيَ اِلَّا عَلَى الَّذِيْ فَطَرَنِيْ ۗ اَفَلَا تَعْقِلُوْنَ ', 'y<u>aa</u> qawmi l<u>aa</u> as-alukum \'alayhi ajran in ajriya ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>la<u>dz</u>ii fa<u>th</u>aranii afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Wahai kaumku! Aku tidak meminta imbalan kepadamu atas (seruanku) ini. Imbalanku hanyalah dari Allah yang telah menciptakanku. Tidakkah kamu mengerti?”'),
	(1525, 11, 52, 'وَيٰقَوْمِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًا وَّيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِيْنَ', 'way<u>aa</u> qawmi istaghfiruu rabbakum tsumma tuubuu ilayhi yursili <strong>al</strong>ssam<u>aa</u>-a \'alaykum midr<u>aa</u>ran wayazidkum quwwatan il<u>aa</u> quwwatikum wal<u>aa</u> tatawallaw mujrimiin<strong>a</strong>', 'Dan (Hud berkata), “Wahai kaumku! Mohonlah ampunan kepada Tuhanmu lalu bertobatlah kepada-Nya, niscaya Dia menurunkan hujan yang sangat deras, Dia akan menambahkan kekuatan di atas kekuatanmu, dan janganlah kamu berpaling menjadi orang yang berdosa.”'),
	(1526, 11, 53, 'قَالُوْا يٰهُوْدُ مَاجِئْتَنَا بِبَيِّنَةٍ وَّمَا نَحْنُ بِتَارِكِيْٓ  اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ', 'q<u>aa</u>luu y<u>aa</u> huudu m<u>aa</u> ji/tan<u>aa</u> bibayyinatin wam<u>aa</u> na<u>h</u>nu bit<u>aa</u>rikii <u>aa</u>lihatin<u>aa</u> \'an qawlika wam<u>aa</u> na<u>h</u>nu laka bimu/miniin<strong>a</strong>', ' Mereka (kaum ‘Ad) berkata, “Wahai Hud! Engkau tidak mendatangkan suatu bukti yang nyata kepada kami, dan kami tidak akan meninggalkan sesembahan kami karena perkataanmu dan kami tidak akan mempercayaimu,'),
	(1527, 11, 54, 'اِنْ نَّقُوْلُ اِلَّا اعْتَرٰىكَ بَعْضُ اٰلِهَتِنَا بِسُوْۤءٍ ۗقَالَ اِنِّيْٓ اُشْهِدُ اللّٰهَ وَاشْهَدُوْٓا اَنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ', 'in naquulu ill<u>aa</u> i\'tar<u>aa</u>ka ba\'<u>dh</u>u <u>aa</u>lihatin<u>aa</u> bisuu-in q<u>aa</u>la innii usyhidu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>syhaduu annii barii-un mimm<u>aa</u> tusyrikuun<strong>a</strong>', 'kami hanya mengatakan bahwa sebagian sesembahan kami telah menimpakan penyakit gila atas dirimu.” Dia (Hud) menjawab, “Sesungguhnya aku bersaksi kepada Allah dan saksikanlah bahwa aku berlepas diri dari apa yang kamu persekutukan,'),
	(1528, 11, 55, 'مِنْ دُوْنِهٖ فَكِيْدُوْنِيْ جَمِيْعًا ثُمَّ لَا تُنْظِرُوْنِ', 'min duunihi fakiiduunii jamii\'an tsumma l<u>aa</u> tun<u>zh</u>iruun<strong>i</strong>', 'dengan yang lain, sebab itu jalankanlah semua tipu dayamu terhadapku dan jangan kamu tunda lagi.'),
	(1529, 11, 56, 'اِنِّيْ تَوَكَّلْتُ عَلَى اللّٰهِ رَبِّيْ وَرَبِّكُمْ ۗمَا مِنْ دَاۤبَّةٍ اِلَّا هُوَ اٰخِذٌۢ بِنَاصِيَتِهَا ۗاِنَّ رَبِّيْ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ', 'innii tawakkaltu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi rabbii warabbikum m<u>aa</u> min d<u>aa</u>bbatin ill<u>aa</u> huwa <u>aa</u>khi<u>dz</u>un bin<u>aas</u>iyatih<u>aa</u> inna rabbii \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Sesungguhnya aku bertawakal kepada Allah Tuhanku dan Tuhanmu. Tidak satu pun makhluk bergerak yang bernyawa melainkan Dialah yang memegang ubun-ubunnya (menguasainya). Sungguh, Tuhanku di jalan yang lurus (adil).'),
	(1530, 11, 57, 'فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَّآ اُرْسِلْتُ بِهٖٓ اِلَيْكُمْ ۗوَيَسْتَخْلِفُ رَبِّيْ قَوْمًا غَيْرَكُمْۗ وَلَا تَضُرُّوْنَهٗ شَيْـًٔا ۗاِنَّ رَبِّيْ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ', 'fa-in tawallaw faqad ablaghtukum m<u>aa</u> ursiltu bihi ilaykum wayastakhlifu rabbii qawman ghayrakum wal<u>aa</u> ta<u>dh</u>urruunahu syay-an inna rabbii \'al<u>aa</u> kulli syay-in <u>h</u>afii<u>zh</u><strong>un</strong>', 'Maka jika kamu berpaling, maka sungguh, aku telah menyampaikan kepadamu apa yang menjadi tugasku sebagai rasul kepadamu. Dan Tuhanku akan mengganti kamu dengan kaum yang lain, sedang kamu tidak dapat mendatangkan mudarat kepada-Nya sedikit pun. Sesungguhnya Tuhanku Maha Pemelihara segala sesuatu.” '),
	(1531, 11, 58, 'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا هُوْدًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَنَجَّيْنٰهُمْ مِّنْ عَذَابٍ غَلِيْظٍ', 'walamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> huudan wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wanajjayn<u>aa</u>hum min \'a<u>dzaa</u>bin ghalii<u>zh</u><strong>in</strong>', 'Dan ketika azab Kami datang, Kami selamatkan Hud dan orang-orang yang beriman bersama dia dengan rahmat Kami. Kami selamatkan (pula) mereka  (di akhirat) dari azab yang berat.'),
	(1532, 11, 59, 'وَتِلْكَ عَادٌ  ۖجَحَدُوْا بِاٰيٰتِ رَبِّهِمْ وَعَصَوْا رُسُلَهٗ وَاتَّبَعُوْٓا اَمْرَ كُلِّ جَبَّارٍ عَنِيْدٍ', 'watilka \'<u>aa</u>dun ja<u>h</u>aduu bi-<u>aa</u>y<u>aa</u>ti rabbihim wa\'a<u>sh</u>aw rusulahu wa<strong>i</strong>ttaba\'uu amra kulli jabb<u>aa</u>rin \'aniid<strong>in</strong>', 'Dan itulah (kisah) kaum ‘Ad yang mengingkari tanda-tanda (kekuasaan) Tuhan. Mereka mendurhakai rasul-rasul-Nya dan menuruti perintah semua penguasa yang sewenang-wenang lagi durhaka.'),
	(1533, 11, 60, 'وَاُتْبِعُوْا فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً وَّيَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ عَادًا كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّعَادٍ قَوْمِ هُوْدٍ ࣖ ', 'wautbi\'uu fii h<u>aadz</u>ihi <strong>al</strong>dduny<u>aa</u> la\'natan wayawma <strong>a</strong>lqiy<u>aa</u>mati <strong>a</strong>l<u>aa</u> inna \'<u>aa</u>dan kafaruu rabbahum <strong>a</strong>l<u>aa</u> bu\'dan li\'<u>aa</u>din qawmi huud<strong>in</strong>', 'Dan mereka selalu diikuti dengan laknat di dunia ini dan (begitu pula) di hari Kiamat. Ingatlah, kaum ‘Ad itu ingkar kepada Tuhan mereka. Sungguh, binasalah kaum ‘Ad, umat Hud itu,'),
	(1534, 11, 61, '۞ وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا ۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗهُوَ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ فِيْهَا فَاسْتَغْفِرُوْهُ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ قَرِيْبٌ مُّجِيْبٌ', 'wa-il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu huwa ansya-akum mina <strong>a</strong>l-ar<u>dh</u>i wa<strong>i</strong>sta\'marakum fiih<u>aa</u> fa<strong>i</strong>staghfiruuhu tsumma tuubuu ilayhi inna rabbii qariibun mujiib<strong>un</strong>', 'dan kepada kaum samud (Kami utus) saudara mereka, Saleh. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. Dia telah menciptakanmu dari bumi (tanah) dan menjadikanmu pemakmurnya, karena itu mohonlah ampunan kepada-Nya, kemudian bertobatlah kepada-Nya. Sesungguhnya Tuhanku sangat dekat (rahmat-Nya) dan memperkenankan (doa hamba-Nya).”'),
	(1535, 11, 62, 'قَالُوْا يٰصٰلِحُ قَدْ كُنْتَ فِيْنَا مَرْجُوًّا قَبْلَ هٰذَآ اَتَنْهٰىنَآ اَنْ نَّعْبُدَ مَا يَعْبُدُ اٰبَاۤؤُنَا وَاِنَّنَا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَآ اِلَيْهِ مُرِيْبٍ', 'q<u>aa</u>luu y<u>aa</u> <u>shaa</u>li<u>h</u>u qad kunta fiin<u>aa</u> marjuwwan qabla h<u>aadzaa</u> atanh<u>aa</u>n<u>aa</u> an na\'buda m<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> wa-innan<u>aa</u> lafii syakkin mimm<u>aa</u> tad\'uun<u>aa</u> ilayhi muriib<strong>un</strong>', 'Mereka (kaum samud) berkata, “Wahai Saleh! Sungguh, engkau sebelum ini berada di tengah-tengah kami merupakan orang yang di harapkan, mengapa engkau melarang kami menyembah apa yang disembah oleh nenek moyang kami? Sungguh, kami benar-benar dalam keraguan dan kegelisahan terhadap apa (agama) yang engkau serukan kepada kami.”'),
	(1536, 11, 63, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْۗ وَاٰتٰىنِيْ مِنْهُ رَحْمَةً فَمَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ عَصَيْتُهٗ ۗفَمَا تَزِيْدُوْنَنِيْ غَيْرَ تَخْسِيْرٍ', 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii wa<u>aa</u>t<u>aa</u>nii minhu ra<u>h</u>matan faman yan<u>sh</u>urunii mina <strong>al</strong>l<u>aa</u>hi in \'a<u>sh</u>aytuhu fam<u>aa</u> taziiduunanii ghayra takhsiir<strong>in</strong>', 'Dia (Saleh) berkata, “Wahai kaumku! Terangkanlah kepadaku jika aku mempunyai bukti yang nyata dari Tuhanku dan diberi-Nya aku rahmat (kenabian) dari-Nya, maka siapa yang akan menolongku dari (azab) Allah jika aku mendurhakai-Nya? Maka kamu hanya akan menambah kerugian kepadaku.'),
	(1537, 11, 64, 'وَيٰقَوْمِ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيْبٌ', 'way<u>aa</u> qawmi h<u>aadz</u>ihi n<u>aa</u>qatu <strong>al</strong>l<u>aa</u>hi lakum <u>aa</u>yatan fa<u>dz</u>aruuh<u>aa</u> ta/kul fii ar<u>dh</u>i <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> tamassuuh<u>aa</u> bisuu-in faya/khu<u>dz</u>akum \'a<u>dzaa</u>bun qariib<strong>un</strong>', 'Dan wahai kaumku! Inilah unta betina dari Allah, sebagai mukjizat untukmu, sebab itu biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apa pun yang akan menyebabkan kamu segera ditimpa (azab).”'),
	(1538, 11, 65, 'فَعَقَرُوْهَا فَقَالَ تَمَتَّعُوْا فِيْ دَارِكُمْ ثَلٰثَةَ اَيَّامٍ ۗذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوْبٍ', 'fa\'aqaruuh<u>aa</u> faq<u>aa</u>la tamatta\'uu fii d<u>aa</u>rikum tsal<u>aa</u>tsata ayy<u>aa</u>min <u>dzaa</u>lika wa\'dun ghayru mak<u>dz</u>uub<strong>in</strong>', 'Maka mereka menyembelih unta itu, kemudian dia (Saleh) berkata, “Bersukarialah kamu semua di rumahmu selama tiga hari. Itu adalah janji yang tidak dapat didustakan.”'),
	(1539, 11, 66, 'فَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا صٰلِحًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّا وَمِنْ خِزْيِ يَوْمِىِٕذٍ ۗاِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيْزُ', 'falamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> <u>shaa</u>li<u>h</u>an wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wamin khizyi yawmi-i<u>dz</u>in inna rabbaka huwa <strong>a</strong>lqawiyyu <strong>a</strong>l\'aziiz<strong>u</strong>', 'Maka ketika keputusan Kami datang, Kami selamatkan Saleh dan orang-orang yang beriman bersamanya dengan rahmat Kami dan (Kami selamatkan) dari kehinaan pada hari itu. Sungguh, Tuhanmu, Dia Mahakuat, Mahaperkasa.'),
	(1540, 11, 67, 'وَاَخَذَ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ  ', 'wa-akha<u>dz</u>a <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu <strong>al</strong><u>shsh</u>ay<u>h</u>atu fa-a<u>sh</u>ba<u>h</u>uu fii diy<u>aa</u>rihim j<u>aa</u>tsimiin<strong>a</strong>', ' Kemudian suara yang mengguntur menimpa orang-orang zalim itu, sehingga mereka mati bergelimpangan di rumahnya.'),
	(1541, 11, 68, 'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَآ اِنَّ ثَمُوْدَا۠ كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّثَمُوْدَ ࣖ ', 'ka-an lam yaghnaw fiih<u>aa</u> <strong>a</strong>l<u>aa</u> inna tsamuuda kafaruu rabbahum <strong>a</strong>l<u>aa</u> bu\'dan litsamuud<strong>a</strong>', 'Seolah-olah mereka belum pernah tinggal di tempat itu. Ingatlah, kaum samud mengingkari Tuhan mereka. Ingatlah, binasalah kaum samud.'),
	(1542, 11, 69, 'وَلَقَدْ جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰى قَالُوْا سَلٰمًا ۖقَالَ سَلٰمٌ فَمَا لَبِثَ اَنْ جَاۤءَ بِعِجْلٍ حَنِيْذٍ', 'walaqad j<u>aa</u>-at rusulun<u>aa</u> ibr<u>aa</u>hiima bi<strong>a</strong>lbusyr<u>aa</u> q<u>aa</u>luu sal<u>aa</u>man q<u>aa</u>la sal<u>aa</u>mun fam<u>aa</u> labitsa an j<u>aa</u>-a bi\'ijlin <u>h</u>anii<u>dz</u><strong>in</strong>', 'Dan para utusan Kami (para malaikat) telah datang kepada Ibrahim dengan membawa kabar gembira, mereka mengucapkan, “Selamat.” Dia (Ibrahim) menjawab, “Selamat (atas kamu).” Ma-ka tidak lama kemudian Ibrahim menyuguhkan daging anak sapi yang dipanggang.'),
	(1543, 11, 70, 'فَلَمَّا رَاٰىٓ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمِ لُوْطٍۗ ', 'falamm<u>aa</u> ra<u>aa</u> aydiyahum l<u>aa</u> ta<u>sh</u>ilu ilayhi nakirahum wa-awjasa minhum khiifatan q<u>aa</u>luu l<u>aa</u> takhaf inn<u>aa</u> ursiln<u>aa</u> il<u>aa</u> qawmi luu<u>th</u><strong>in</strong>', 'Maka ketika dilihatnya tangan mereka tidak menjamahnya, dia (Ibrahim) mencurigai mereka, dan merasa takut kepada mereka. Mereka (malaikat) berkata, “Jangan takut, sesungguhnya kami diutus kepada kaum Lut.”'),
	(1544, 11, 71, 'وَامْرَاَتُهٗ قَاۤىِٕمَةٌ فَضَحِكَتْ فَبَشَّرْنٰهَا بِاِسْحٰقَۙ وَمِنْ وَّرَاۤءِ اِسْحٰقَ يَعْقُوْبَ', 'wa<strong>i</strong>mra-atuhu q<u>aa</u>-imatun fa<u>dh</u>a<u>h</u>ikat fabasysyarn<u>aa</u>h<u>aa</u> bi-is<u>haa</u>qa wamin war<u>aa</u>-i is<u>haa</u>qa ya\'quub<strong>a</strong>', 'Dan istrinya berdiri lalu dia tersenyum. Maka Kami sampaikan kepadanya kabar gembira tentang (kelahiran) Ishak dan setelah Ishak (akan lahir) Yakub.'),
	(1545, 11, 72, 'قَالَتْ يٰوَيْلَتٰىٓ ءَاَلِدُ وَاَنَا۠ عَجُوْزٌ وَّهٰذَا بَعْلِيْ شَيْخًا ۗاِنَّ هٰذَا لَشَيْءٌ عَجِيْبٌ', 'q<u>aa</u>lat y<u>aa</u> waylat<u>aa</u> a-alidu wa-an<u>aa</u> \'ajuuzun wah<u>aadzaa</u> ba\'lii syaykhan inna h<u>aadzaa</u> lasyay-un \'ajiib<strong>un</strong>', 'Dia (istrinya) berkata, “Sungguh ajaib, mungkinkah aku akan melahirkan anak padahal aku sudah tua, dan suamiku ini sudah sangat tua? Ini benar-benar sesuatu yang ajaib.”'),
	(1546, 11, 73, 'قَالُوْٓا اَتَعْجَبِيْنَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكٰتُهٗ عَلَيْكُمْ اَهْلَ الْبَيْتِۗ اِنَّهُ حَمِيْدٌ مَّجِيْدٌ', 'q<u>aa</u>luu ata\'jabiina min amri <strong>al</strong>l<u>aa</u>hi ra<u>h</u>matu <strong>al</strong>l<u>aa</u>hi wabarak<u>aa</u>tuhu \'alaykum ahla <strong>a</strong>lbayti innahu <u>h</u>amiidun majiid<strong>un</strong>', 'Mereka (para malaikat) berkata, “Mengapa engkau merasa heran tentang ketetapan Allah? (Itu adalah) rahmat dan berkah Allah, dicurahkan kepada kamu, wahai ahlulbait! Sesungguhnya Allah Maha Terpuji, Maha Pengasih.”'),
	(1547, 11, 74, 'فَلَمَّا ذَهَبَ عَنْ اِبْرٰهِيْمَ الرَّوْعُ وَجَاۤءَتْهُ الْبُشْرٰى يُجَادِلُنَا فِيْ قَوْمِ لُوْطٍ', 'falamm<u>aa</u> <u>dz</u>ahaba \'an ibr<u>aa</u>hiima <strong>al</strong>rraw\'u waj<u>aa</u>-at-hu <strong>a</strong>lbusyr<u>aa </u>yuj<u>aa</u>dilun<u>aa</u> fii qawmi luu<u>th</u><strong>in</strong>', 'Maka ketika rasa takut hilang dari Ibrahim dan kabar gembira telah datang kepadanya, dia pun bersoal jawab dengan (para malaikat) Kami tentang kaum Lut.'),
	(1548, 11, 75, 'اِنَّ اِبْرٰهِيْمَ لَحَلِيْمٌ اَوَّاهٌ مُّنِيْبٌ', 'inna ibr<u>aa</u>hiima la<u>h</u>aliimun aww<u>aa</u>hun muniib<strong>un</strong>', 'Ibrahim sungguh penyantun, lembut hati dan suka kembali (kepada Allah).'),
	(1549, 11, 76, 'يٰٓاِبْرٰهِيْمُ اَعْرِضْ عَنْ هٰذَا ۚاِنَّهٗ قَدْ جَاۤءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰتِيْهِمْ عَذَابٌ غَيْرُ مَرْدُوْدٍ', 'y<u>aa</u> ibr<u>aa</u>hiimu a\'ri<u>dh</u> \'an h<u>aadzaa</u> innahu qad j<u>aa</u>-a amru rabbika wa-innahum <u>aa</u>tiihim \'a<u>dzaa</u>bun ghayru marduud<strong>in</strong>', 'Wahai Ibrahim! Tinggalkanlah (perbincangan) ini, sungguh, ketetapan Tuhanmu telah datang, dan mereka itu akan ditimpa azab yang tidak dapat ditolak.'),
	(1550, 11, 77, 'وَلَمَّا جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالَ هٰذَا يَوْمٌ عَصِيْبٌ', 'walamm<u>aa</u> j<u>aa</u>-at rusulun<u>aa</u> luu<u>th</u>an sii-a bihim wa<u>daa</u>qa bihim <u>dz</u>ar\'an waq<u>aa</u>la h<u>aadzaa</u> yawmun \'a<u>sh</u>iib<strong>un</strong>', 'Dan ketika para utusan Kami (para malaikat) itu datang kepada Lut, dia merasa curiga dan dadanya merasa sempit karena (kedatangan)nya. Dia (Lut) berkata, “Ini hari yang sangat sulit.”'),
	(1551, 11, 78, 'وَجَاۤءَهٗ قَوْمُهٗ يُهْرَعُوْنَ اِلَيْهِۗ وَمِنْ قَبْلُ كَانُوْا يَعْمَلُوْنَ السَّيِّاٰتِۗ قَالَ يٰقَوْمِ هٰٓؤُلَاۤءِ بَنَاتِيْ هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ فِيْ ضَيْفِيْۗ  اَلَيْسَ مِنْكُمْ رَجُلٌ رَّشِيْدٌ', 'waj<u>aa</u>-ahu qawmuhu yuhra\'uuna ilayhi wamin qablu k<u>aa</u>nuu ya\'maluuna <strong>al</strong>ssayyi-<u>aa</u>ti q<u>aa</u>la y<u>aa</u> qawmi h<u>aa</u>ul<u>aa</u>-i ban<u>aa</u>tii hunna a<u>th</u>haru lakum fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> tukhzuuni fii <u>dh</u>ayfii alaysa minkum rajulun rasyiid<strong>un</strong>', 'Dan kaumnya segera datang kepadanya. Dan sejak dahulu mereka selalu melakukan perbuatan keji. Lut berkata, “Wahai kaumku! Inilah putri-putri (negeri)ku mereka lebih suci bagimu, maka bertakwalah kepada Allah dan janganlah kamu mencemarkan (nama)ku terhadap tamuku ini. Tidak adakah di antaramu orang yang pandai?”'),
	(1552, 11, 79, 'قَالُوْا لَقَدْ عَلِمْتَ مَا لَنَا فِيْ بَنٰتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُرِيْدُ', 'q<u>aa</u>luu laqad \'alimta m<u>aa</u> lan<u>aa</u> fii ban<u>aa</u>tika min <u>h</u>aqqin wa-innaka lata\'lamu m<u>aa</u> nuriid<strong>u</strong>', 'Mereka menjawab, “Sesungguhnya eng-kau pasti tahu bahwa kami tidak mempunyai keinginan (syahwat) terhadap putri-putrimu; dan engkau tentu mengetahui apa yang (sebenarnya) kami kehendaki.”'),
	(1553, 11, 80, 'قَالَ لَوْ اَنَّ لِيْ بِكُمْ قُوَّةً اَوْ اٰوِيْٓ اِلٰى رُكْنٍ شَدِيْدٍ', 'q<u>aa</u>la law anna lii bikum quwwatan aw <u>aa</u>wii il<u>aa</u> ruknin syadiid<strong>in</strong>', 'Dia (Lut) berkata, “Sekiranya aku mempunyai kekuatan (untuk menolakmu) atau aku dapat berlindung kepada keluarga yang kuat (tentu aku lakukan).”'),
	(1554, 11, 81, 'قَالُوْا يٰلُوْطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَّصِلُوْٓا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۗ اِنَّهٗ مُصِيْبُهَا مَآ اَصَابَهُمْ  ۗاِنَّ مَوْعِدَهُمُ الصُّبْحُ ۗ اَلَيْسَ الصُّبْحُ بِق', 'q<u>aa</u>luu y<u>aa</u> luu<u>th</u>u inn<u>aa</u> rusulu rabbika lan ya<u>sh</u>iluu ilayka fa-asri bi-ahlika biqi<u>th</u>\'in mina <strong>al</strong>layli wal<u>aa</u> yaltafit minkum a<u>h</u>adun ill<u>aa</u> imra-ataka innahu mu<u>sh</u>iibuh<u>aa</u> m<u>aa</u> a<u>shaa</u>bahum inna maw\'idahumu <strong>al</strong><u>shsh</u>ub<u>h</u>u alaysa <strong>al</strong><u>shsh</u>ub<u>h</u>u biqariib<strong>in</strong>', 'Mereka (para malaikat) berkata, “Wahai Lut! Sesungguhnya kami adalah para utusan Tuhanmu, mereka tidak akan dapat mengganggu kamu, sebab itu pergilah beserta keluargamu pada akhir malam dan jangan ada seorang pun di antara kamu yang menoleh ke belakang, kecuali istrimu. Sesungguhnya dia (juga) akan ditimpa (siksaan) yang menimpa mereka. Sesungguhnya saat terjadinya siksaan bagi mereka itu pada waktu subuh. Bukankah subuh itu sudah dekat?”'),
	(1555, 11, 82, 'فَلَمَّا جَاۤءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِّنْ سِجِّيْلٍ مَّنْضُوْدٍ', 'falamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> ja\'aln<u>aa</u> \'<u>aa</u>liyah<u>aa</u> s<u>aa</u>filah<u>aa</u> wa-am<u>th</u>arn<u>aa</u> \'alayh<u>aa</u> <u>h</u>ij<u>aa</u>ratan min sijjiilin man<u>dh</u>uud<strong>in</strong>', 'Maka ketika keputusan Kami datang, Kami menjungkirbalikkannya negeri kaum Lut, dan Kami hujani mereka bertubi-tubi dengan batu dari tanah yang terbakar, '),
	(1556, 11, 83, 'مُسَوَّمَةً عِنْدَ رَبِّكَۗ وَمَا هِيَ مِنَ الظّٰلِمِيْنَ بِبَعِيْدٍ ࣖ ', 'musawwamatan \'inda rabbika wam<u>aa</u> hiya mina <strong>al</strong><u>zhzhaa</u>limiina biba\'iid<strong>in</strong>', 'yang diberi tanda oleh Tuhanmu. Dan siksaan itu tiadalah jauh dari orang yang zalim.'),
	(1557, 11, 84, '۞ وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗوَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيْزَانَ اِنِّيْٓ اَرٰىكُمْ بِخَيْرٍ وَّاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُّحِيْطٍ', 'wa-il<u>aa</u> madyana akh<u>aa</u>hum syu\'ayban q<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu wal<u>aa</u> tanqu<u>sh</u>uu <strong>a</strong>lmiky<u>aa</u>la wa<strong>a</strong>lmiiz<u>aa</u>na innii ar<u>aa</u>kum bikhayrin wa-innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin mu<u>h</u>ii<u>th</u><strong>in</strong>', 'Dan kepada (penduduk) Madyan (Kami utus) saudara mereka, Syuaib. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. Dan janganlah kamu kurangi takaran dan timbangan. Sesungguhnya aku melihat kamu dalam keadaan yang baik (makmur). Dan sesungguhnya aku khawatir kamu akan ditimpa azab pada hari yang membinasakan (Kiamat).'),
	(1558, 11, 85, 'وَيٰقَوْمِ اَوْفُوا الْمِكْيَالَ وَالْمِيْزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ', 'way<u>aa</u> qawmi awfuu <strong>a</strong>lmiky<u>aa</u>la wa<strong>a</strong>lmiiz<u>aa</u>na bi<strong>a</strong>lqis<u>th</u>i wal<u>aa</u> tabkhasuu <strong>al</strong>nn<u>aa</u>sa asyy<u>aa</u>-ahum wal<u>aa</u> ta\'tsaw fii <strong>a</strong>l-ar<u>dh</u>i mufsidiin<strong>a</strong>', 'Dan wahai kaumku! Penuhilah takaran dan timbangan dengan adil, dan janganlah kamu merugikan manusia terhadap hak-hak mereka dan jangan kamu membuat kejahatan di bumi dengan berbuat kerusakan.'),
	(1559, 11, 86, 'بَقِيَّتُ اللّٰهِ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ەۚ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ', 'baqiyyatu <strong>al</strong>l<u>aa</u>hi khayrun lakum in kuntum mu/miniina wam<u>aa</u> an<u>aa</u> \'alaykum bi<u>h</u>afii<u>zh</u><strong>in</strong>', 'Sisa (yang halal) dari Allah adalah lebih baik bagimu jika kamu orang yang beriman. Dan aku bukanlah seorang penjaga atas dirimu.”'),
	(1560, 11, 87, 'قَالُوْا يٰشُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَّتْرُكَ مَا يَعْبُدُ اٰبَاۤؤُنَآ اَوْ اَنْ نَّفْعَلَ فِيْٓ اَمْوَالِنَا مَا نَشٰۤؤُا ۗاِنَّكَ لَاَنْتَ الْحَلِيْمُ الرَّشِيْدُ', 'q<u>aa</u>luu y<u>aa</u> syu\'aybu a<u>sh</u>al<u>aa</u>tuka ta/muruka an natruka m<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> aw an naf\'ala fii amw<u>aa</u>lin<u>aa</u> m<u>aa</u> nasy<u>aa</u>u innaka la-anta <strong>a</strong>l<u>h</u>aliimu <strong>al</strong>rrasyiid<strong>u</strong>', 'Mereka berkata, “Wahai Syuaib! Apakah agamamu yang menyuruhmu agar kami meninggalkan apa yang disembah nenek moyang kami atau melarang kami mengelola harta kami menurut cara yang kami kehendaki? Sesungguhnya engkau benar-benar orang yang sangat penyantun dan pandai.”'),
	(1561, 11, 88, 'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَرَزَقَنِيْ مِنْهُ رِزْقًا حَسَنًا وَّمَآ اُرِيْدُ اَنْ اُخَالِفَكُمْ اِلٰى مَآ اَنْهٰىكُمْ عَنْهُ ۗاِنْ اُرِيْدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۗ وَمَا تَوْفِيْقِيْٓ اِلَّا بِا', 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii warazaqanii minhu rizqan <u>h</u>asanan wam<u>aa</u> uriidu an ukh<u>aa</u>lifakum il<u>aa</u> m<u>aa</u> anh<u>aa</u>kum \'anhu in uriidu ill<u>aa</u> <strong>a</strong>l-i<u>sh</u>l<u>aah</u>a m<u>aa</u> ista<u>th</u>a\'tu wam<u>aa</u> tawfiiqii ill<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi \'alayhi tawakkaltu wa-ilayhi uniib<strong>u</strong>', 'Dia (Syuaib) berkata, “Wahai kaumku! Terangkan padaku jika aku mempunyai bukti yang nyata dari Tuhanku dan aku dianugerahi-Nya rezeki yang baik (pantaskah aku menyalahi perintah-Nya)? Aku tidak bermaksud menyalahi kamu terhadap apa yang aku larang darinya. Aku hanya bermaksud (mendatangkan) perbaikan selama aku masih sanggup. Dan petunjuk yang aku ikuti hanya dari Allah. Kepada-Nya aku bertawakal dan kepada-Nya (pula) aku kembali.'),
	(1562, 11, 89, 'وَيٰقَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِيْٓ اَنْ يُّصِيْبَكُمْ مِّثْلُ مَآ اَصَابَ قَوْمَ نُوْحٍ اَوْ قَوْمَ هُوْدٍ اَوْ قَوْمَ صٰلِحٍ ۗوَمَا قَوْمُ لُوْطٍ مِّنْكُمْ بِبَعِيْدٍ', 'way<u>aa</u> qawmi l<u>aa</u> yajrimannakum syiq<u>aa</u>qii an yu<u>sh</u>iibakum mitslu m<u>aa</u> a<u>shaa</u>ba qawma nuu<u>h</u>in aw qawma huudin aw qawma <u>shaa</u>li<u>h</u>in wam<u>aa</u> qawmu luu<u>th</u>in minkum biba\'iid<strong>in</strong>', 'Dan wahai kaumku! Janganlah pertentangan antara aku (dengan kamu) menyebabkan kamu berbuat dosa, sehingga kamu ditimpa siksaan seperti yang menimpa kaum Nuh, kaum Hud atau kaum Saleh, sedang kaum Lut tidak jauh dari kamu.'),
	(1563, 11, 90, 'وَاسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ رَحِيْمٌ وَّدُوْدٌ', 'wa<strong>i</strong>staghfiruu rabbakum tsumma tuubuu ilayhi inna rabbii ra<u>h</u>iimun waduud<strong>un</strong>', ' Dan mohonlah ampunan kepada Tuhanmu, kemudian bertobatlah kepada-Nya. Sungguh, Tuhanku Maha Penyayang, Maha Pengasih.”'),
	(1564, 11, 91, 'قَالُوْا يٰشُعَيْبُ مَا نَفْقَهُ كَثِيْرًا مِّمَّا تَقُوْلُ وَاِنَّا لَنَرٰىكَ فِيْنَا ضَعِيْفًا ۗوَلَوْلَا رَهْطُكَ لَرَجَمْنٰكَ ۖوَمَآ اَنْتَ عَلَيْنَا بِعَزِيْزٍ ', 'q<u>aa</u>luu y<u>aa</u> syu\'aybu m<u>aa</u> nafqahu katsiiran mimm<u>aa</u> taquulu wa-inn<u>aa</u> lanar<u>aa</u>ka fiin<u>aa</u> <u>dh</u>a\'iifan walawl<u>aa</u> rah<u>th</u>uka larajamn<u>aa</u>ka wam<u>aa</u> anta \'alayn<u>aa</u> bi\'aziiz<strong>in</strong>', 'Mereka berkata, “Wahai Syuaib! Kami tidak banyak mengerti tentang apa yang engkau katakan itu, sedang kenyataannya kami memandang engkau seorang yang lemah di antara kami. Kalau tidak karena keluargamu, tentu kami telah merajam engkau, sedang engkau pun bukan seorang yang berpengaruh di lingkungan kami.”'),
	(1565, 11, 92, 'قَالَ يٰقَوْمِ اَرَهْطِيْٓ اَعَزُّ عَلَيْكُمْ مِّنَ اللّٰهِ ۗوَاتَّخَذْتُمُوْهُ وَرَاۤءَكُمْ ظِهْرِيًّا ۗاِنَّ رَبِّيْ بِمَا تَعْمَلُوْنَ مُحِيْطٌ', 'q<u>aa</u>la y<u>aa</u> qawmi arah<u>th</u>ii a\'azzu \'alaykum mina <strong>al</strong>l<u>aa</u>hi wa<strong>i</strong>ttakha<u>dz</u>tumuuhu war<u>aa</u>-akum <u>zh</u>ihriyyan inna rabbii bim<u>aa</u> ta\'maluuna mu<u>h</u>ii<u>th</u><strong>un</strong>', 'Dia (Syuaib) menjawab, “Wahai kaumku! Apakah keluargaku lebih terhormat menurut pandanganmu daripada Allah, bahkan Dia kamu tempatkan di belakangmu (diabaikan)? Ketahuilah sesungguhnya (pengetahuan) Tuhanku meliputi apa yang kamu kerjakan.'),
	(1566, 11, 93, 'وَيٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ  ۗسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَمَنْ هُوَ كَاذِبٌۗ وَارْتَقِبُوْٓا اِنِّيْ مَعَكُمْ رَقِيْبٌ', 'way<u>aa</u> qawmi i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum innii \'<u>aa</u>milun sawfa ta\'lamuuna man ya/tiihi \'a<u>dzaa</u>bun yukhziihi waman huwa k<u>aadz</u>ibun wa<strong>i</strong>rtaqibuu inne ma\'akum raqiib<strong>un</strong>', 'Dan wahai kaumku! Berbuatlah menurut kemampuanmu, sesungguhnya aku pun berbuat (pula). Kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakan dan siapa yang berdusta. Dan tunggulah! Sesungguhnya aku bersamamu adalah orang yang menunggu.”'),
	(1567, 11, 94, 'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا شُعَيْبًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَاَخَذَتِ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ  ', 'walamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> syu\'ayban wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wa-akha<u>dz</u>ati <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu <strong>al</strong><u>shsh</u>ay<u>h</u>atu fa-a<u>sh</u>ba<u>h</u>uu fii diy<u>aa</u>rihim j<u>aa</u>tsimiin<strong>a</strong>', 'Maka ketika keputusan Kami datang, Kami selamatkan Syuaib dan orang-orang yang beriman bersamanya dengan rahmat Kami. Sedang orang yang zalim dibinasakan oleh suara yang mengguntur, sehingga mereka mati bergelimpangan di rumahnya,'),
	(1568, 11, 95, 'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَا بُعْدًا لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُوْدُ ࣖ ', 'ka-an lam yaghnaw fiih<u>aa</u> <strong>a</strong>l<u>aa</u> bu\'dan limadyana kam<u>aa</u> ba\'idat tsamuud<strong>u</strong>', 'seolah-olah mereka belum pernah tinggal di tempat itu. Ingatlah, binasalah penduduk Madyan sebagaimana kaum samud (juga) telah binasa.'),
	(1569, 11, 96, 'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ ', 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wasul<u>th</u><u>aa</u>nin mubiin<strong>in</strong>', 'Dan sungguh, Kami telah mengutus Musa dengan tanda-tanda (kekuasaan) Kami dan bukti yang nyata,'),
	(1570, 11, 97, 'اِلٰى فِرْعَوْنَ وَملَا۟ىِٕهٖ فَاتَّبَعُوْٓا اَمْرَ فِرْعَوْنَ ۚوَمَآ اَمْرُ فِرْعَوْنَ بِرَشِيْدٍ', 'il<u>aa</u> fir\'awna wamala-ihi fa<strong>i</strong>ttaba\'uu amra fir\'awna wam<u>aa</u> amru fir\'awna birasyiid<strong>in</strong>', 'Kepada Fir‘aun dan para pemuka kaumnya, tetapi mereka mengikuti perintah Fir‘aun, padahal perintah Fir‘aun bukanlah (perintah) yang benar.'),
	(1571, 11, 98, 'يَقْدُمُ قَوْمَهٗ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَ ۗوَبِئْسَ الْوِرْدُ الْمَوْرُوْدُ', 'yaqdumu qawmahu yawma <strong>a</strong>lqiy<u>aa</u>mati fa-awradahumu <strong>al</strong>nn<u>aa</u>ra wabi/sa <strong>a</strong>lwirdu <strong>a</strong>lmawruud<strong>u</strong>', 'Dia (Fir‘aun) berjalan di depan kaumnya di hari Kiamat, lalu membawa mereka masuk ke dalam neraka. Neraka itu seburuk-buruk tempat yang dimasuki.'),
	(1572, 11, 99, 'وَاُتْبِعُوْا فِيْ هٰذِهٖ لَعْنَةً وَّيَوْمَ الْقِيٰمَةِۗ بِئْسَ الرِّفْدُ الْمَرْفُوْدُ', 'wautbi\'uu fii h<u>aadz</u>ihi la\'natan wayawma <strong>a</strong>lqiy<u>aa</u>mati bi/sa <strong>al</strong>rrifdu <strong>a</strong>lmarfuud<strong>u</strong>', 'Dan mereka diikuti dengan laknat di sini (dunia) dan (begitu pula) pada hari Kiamat. (Laknat) itu seburuk-buruk pemberian yang diberikan.'),
	(1573, 11, 100, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْقُرٰى نَقُصُّهٗ عَلَيْكَ مِنْهَا قَاۤىِٕمٌ وَّحَصِيْدٌ', '<u>dzaa</u>lika min anb<u>aa</u>-i <strong>a</strong>lqur<u>aa</u> naqu<u>shsh</u>uhu \'alayka minh<u>aa</u> q<u>aa</u>-imun wa<u>h</u>a<u>sh</u>iid<strong>un</strong>', 'Itulah beberapa berita tentang negeri-negeri (yang telah dibinasakan) yang Kami ceritakan kepadamu (Muhammad). Di antara negeri-negeri itu sebagian masih ada bekas-bekasnya dan ada (pula) yang telah musnah.'),
	(1574, 11, 101, 'وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ ظَلَمُوْٓا اَنْفُسَهُمْ فَمَآ اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّتِيْ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ مِنْ شَيْءٍ لَّمَّا جَاۤءَ اَمْرُ رَبِّكَۗ وَمَا زَادُوْهُمْ غَيْرَ تَتْبِيْبٍ ', 'wam<u>aa</u> <u>zh</u>alamn<u>aa</u>hum wal<u>aa</u>kin <u>zh</u>alamuu anfusahum fam<u>aa</u> aghnat \'anhum <u>aa</u>lihatuhumu <strong>al</strong>latii yad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi min syay-in lamm<u>aa</u> j<u>aa</u>-a amru rabbika wam<u>aa</u> z<u>aa</u>duuhum ghayra tatbiib<strong>i</strong>', 'Dan Kami tidak menzalimi mereka, tetapi merekalah yang menzalimi diri mereka sendiri, karena itu tidak bermanfaat sedikit pun bagi mereka sesembahan yang mereka sembah selain Allah, ketika siksaan Tuhanmu datang. Sesembahan itu hanya menambah kebinasaan bagi mereka.'),
	(1575, 11, 102, 'وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَآ اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌ  ۗاِنَّ اَخْذَهٗٓ اَلِيْمٌ شَدِيْدٌ', 'waka<u>dzaa</u>lika akh<u>dz</u>u rabbika i<u>dzaa</u> akha<u>dz</u>a <strong>a</strong>lqur<u>aa</u> wahiya <u>zhaa</u>limatun inna akh<u>dz</u>ahu <strong>a</strong>liimun syadiid<strong>un</strong>', 'Dan begitulah siksa Tuhanmu apabila Dia menyiksa (penduduk) negeri-negeri yang berbuat zalim. Sungguh, siksa-Nya sangat pedih, sangat berat.'),
	(1576, 11, 103, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّمَنْ خَافَ عَذَابَ الْاٰخِرَةِ ۗذٰلِكَ يَوْمٌ مَّجْمُوْعٌۙ لَّهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَّشْهُوْدٌ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan liman kh<u>aa</u>fa \'a<u>dzaa</u>ba <strong>a</strong>l-<u>aa</u>khirati <u>dzaa</u>lika yawmun majmuu\'un lahu <strong>al</strong>nn<u>aa</u>su wa<u>dzaa</u>lika yawmun masyhuudun', 'Sesungguhnya pada yang demikian itu pasti terdapat pelajaran bagi orang-orang yang takut kepada azab akhirat. Itulah hari ketika semua manusia dikumpulkan (untuk dihisab), dan itulah hari yang disaksikan (oleh semua makhluk).'),
	(1577, 11, 104, 'وَمَا نُؤَخِّرُهٗٓ اِلَّا لِاَجَلٍ مَّعْدُوْدٍۗ ', 'wam<u>aa</u> nu-akhkhiruhu ill<u>aa</u> li-ajalin ma\'duud<strong>in</strong>', 'Dan Kami tidak akan menunda, kecuali sampai waktu yang sudah ditentukan.'),
	(1578, 11, 105, 'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِاِذْنِهٖۚ فَمِنْهُمْ شَقِيٌّ وَّسَعِيْدٌ', 'yawma ya/ti l<u>aa</u> takallamu nafsun ill<u>aa</u> bi-i<u>dz</u>nihi faminhum syaqiyyun wasa\'iid<strong>in</strong>', 'Ketika hari itu datang, tidak seorang pun yang berbicara, kecuali dengan izin-Nya; maka di antara mereka ada yang sengsara dan ada yang berbahagia.'),
	(1579, 11, 106, 'فَاَمَّا الَّذِيْنَ شَقُوْا فَفِى النَّارِ لَهُمْ فِيْهَا زَفِيْرٌ وَّشَهِيْقٌۙ  ', 'fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina syaquu fafii <strong>al</strong>nn<u>aa</u>ri lahum fiih<u>aa</u> zafiirun wasyahiiq<strong>un</strong>', 'Maka adapun orang-orang yang sengsara, maka (tempatnya) di dalam neraka, di sana mereka mengeluarkan dan menarik nafas dengan merintih,'),
	(1580, 11, 107, 'خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ اِنَّ رَبَّكَ فَعَّالٌ لِّمَا يُرِيْدُ', 'kh<u>aa</u>lidiina fiih<u>aa</u> m<u>aa</u> d<u>aa</u>mati <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu wa<strong>a</strong>l-ar<u>dh</u>u ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a rabbuka inna rabbaka fa\'\'<u>aa</u>lun lim<u>aa</u> yuriid<strong>u</strong>', 'mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain). Sungguh, Tuhanmu Maha Pelaksana terhadap apa yang Dia kehendaki.'),
	(1581, 11, 108, '۞ وَاَمَّا الَّذِيْنَ سُعِدُوْا فَفِى الْجَنَّةِ خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ عَطَاۤءً غَيْرَ مَجْذُوْذٍ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina su\'iduu fafii <strong>a</strong>ljannati kh<u>aa</u>lidiina fiih<u>aa</u> m<u>aa</u> d<u>aa</u>mati <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu wa<strong>a</strong>l-ar<u>dh</u>u ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a rabbuka \'a<u>th</u><u>aa</u>-an ghayra maj<u>dz</u>uu<u>dz</u><strong>in</strong>', 'Dan adapun orang-orang yang berbahagia, maka (tempatnya) di dalam surga; mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain); sebagai karunia yang tidak ada putus-putusnya.'),
	(1582, 11, 109, 'فَلَا تَكُ فِيْ مِرْيَةٍ مِّمَّا يَعْبُدُ هٰٓؤُلَاۤءِ ۗمَا يَعْبُدُوْنَ اِلَّا كَمَا يَعْبُدُ اٰبَاۤؤُهُمْ مِّنْ قَبْلُ ۗوَاِنَّا لَمُوَفُّوْهُمْ نَصِيْبَهُمْ غَيْرَ مَنْقُوْصٍ ࣖ ', 'fal<u>aa</u> taku fii miryatin mimm<u>aa</u> ya\'budu h<u>aa</u>ul<u>aa</u>-i m<u>aa</u> ya\'buduuna ill<u>aa</u> kam<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>uhum min qablu wa-inn<u>aa</u> lamuwaffuuhum na<u>sh</u>iibahum ghayra manquu<u>sh</u><strong>in</strong>', 'Maka janganlah engkau (Muhammad) ragu-ragu tentang apa yang mereka sembah. Mereka menyembah sebagaimana nenek moyang mereka dahulu menyembah. Kami pasti akan menyempurnakan pembalasan (terhadap) mereka tanpa dikurangi sedikit pun.'),
	(1583, 11, 110, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba fa<strong>i</strong>khtulifa fiihi walawl<u>aa</u> kalimatun sabaqat min rabbika laqu<u>dh</u>iya baynahum wa-innahum lafii syakkin minhu muriib<strong>un</strong>', 'Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa, lalu diperselisihkannya. Dan kalau tidak ada ketetapan yang terdahulu dari Tuhanmu, niscaya telah dilaksanakan hukuman di antara mereka. Sungguh, mereka (orang kafir Mekah) benar-benar dalam kebimbangan dan keraguan terhadapnya (Al-Qur\'an).'),
	(1584, 11, 111, 'وَاِنَّ كُلًّا لَّمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْ ۗاِنَّهٗ بِمَا يَعْمَلُوْنَ خَبِيْرٌ', 'wa-inna kullan lamm<u>aa</u> layuwaffiyannahum rabbuka a\'m<u>aa</u>lahum innahu bim<u>aa</u> ya\'maluuna khabiir<strong>un</strong>', 'Dan sesungguhnya kepada masing-masing (yang berselisih itu) pasti Tuhanmu akan memberi balasan secara penuh atas perbuatan mereka. Sungguh, Dia Mahateliti terhadap apa yang mereka kerjakan.'),
	(1585, 11, 112, 'فَاسْتَقِمْ كَمَآ اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۗ اِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ', 'fa<strong>i</strong>staqim kam<u>aa</u> umirta waman t<u>aa</u>ba ma\'aka wal<u>aa</u> ta<u>th</u>ghaw innahu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'Maka tetaplah engkau (Muhammad) (di jalan yang benar), sebagaimana telah diperintahkan kepadamu dan (juga) orang yang bertobat bersamamu, dan janganlah kamu melampaui batas. Sungguh, Dia Maha Melihat apa yang kamu kerjakan.'),
	(1586, 11, 113, 'وَلَا تَرْكَنُوْٓا اِلَى الَّذِيْنَ ظَلَمُوْا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ثُمَّ لَا تُنْصَرُوْنَ', 'wal<u>aa</u> tarkanuu il<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu fatamassakumu <strong>al</strong>nn<u>aa</u>ru wam<u>aa</u> lakum min duuni <strong>al</strong>l<u>aa</u>hi min awliy<u>aa</u>-a tsumma l<u>aa</u> tun<u>sh</u>aruun<strong>a</strong>', 'Dan janganlah kamu cenderung kepada orang yang zalim yang menyebabkan kamu disentuh api neraka, sedangkan kamu tidak mempunyai seorang penolong pun selain Allah, sehingga kamu tidak akan diberi pertolongan.'),
	(1587, 11, 114, 'وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِّنَ الَّيْلِ ۗاِنَّ الْحَسَنٰتِ يُذْهِبْنَ السَّيِّاٰتِۗ ذٰلِكَ ذِكْرٰى لِلذَّاكِرِيْنَ', 'wa-aqimi <strong>al</strong><u>shsh</u>al<u>aa</u>ta <u>th</u>arafayi <strong>al</strong>nnah<u>aa</u>ri wazulafan mina <strong>al</strong>layli inna <strong>a</strong>l<u>h</u>asan<u>aa</u>ti yu<u>dz</u>hibna <strong>al</strong>ssayyi-<u>aa</u>ti <u>dzaa</u>lika <u>dz</u>ikr<u>aa</u> li<strong>l</strong><u>dzdzaa</u>kiriin<strong>a</strong>', 'Dan laksanakanlah salat pada kedua ujung siang (pagi dan petang) dan pada bagian permulaan malam. Perbuatan-perbuatan baik itu menghapus kesalahan-kesalahan. Itulah peringatan bagi orang-orang yang selalu mengingat (Allah).'),
	(1588, 11, 115, 'وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'wa<strong>i</strong><u>sh</u>bir fa-inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan bersabarlah, karena sesungguhnya Allah tidak menyia-nyiakan pahala orang yang berbuat kebaikan.'),
	(1589, 11, 116, 'فَلَوْلَا كَانَ مِنَ الْقُرُوْنِ مِنْ قَبْلِكُمْ اُولُوْا بَقِيَّةٍ يَّنْهَوْنَ عَنِ الْفَسَادِ فِى الْاَرْضِ اِلَّا قَلِيْلًا مِّمَّنْ اَنْجَيْنَا مِنْهُمْ ۚوَاتَّبَعَ الَّذِيْنَ ظَلَمُوْا مَآ اُتْرِفُوْا فِيْهِ وَكَانُوْا مُجْرِمِيْنَ', 'falawl<u>aa</u> k<u>aa</u>na mina <strong>a</strong>lquruuni min qablikum uluu baqiyyatin yanhawna \'ani <strong>a</strong>lfas<u>aa</u>di fii <strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> qaliilan mimman anjayn<u>aa</u> minhum wa<strong>i</strong>ttaba\'a <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu m<u>aa</u> utrifuu fiihi wak<u>aa</u>nuu mujrimiin<strong>a</strong>', 'Maka mengapa tidak ada di antara umat-umat sebelum kamu orang yang mempunyai keutamaan yang melarang (berbuat) kerusakan di bumi, kecuali sebagian kecil di antara orang yang telah Kami selamatkan. Dan orang-orang yang zalim hanya mementingkan kenikmatan dan kemewahan. Dan mereka adalah orang-orang yang berdosa. '),
	(1590, 11, 117, 'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا مُصْلِحُوْنَ', 'wam<u>aa</u> k<u>aa</u>na rabbuka liyuhlika <strong>a</strong>lqur<u>aa</u> bi<u>zh</u>ulmin wa-ahluh<u>aa</u> mu<u>sh</u>li<u>h</u>uun<strong>a</strong>', 'Dan Tuhanmu tidak akan membinasakan negeri-negeri secara zalim, selama penduduknya orang-orang yang berbuat kebaikan.'),
	(1591, 11, 118, 'وَلَوْ شَاۤءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَّاحِدَةً وَّلَا يَزَالُوْنَ مُخْتَلِفِيْنَۙ  ', 'walaw sy<u>aa</u>-a rabbuka laja\'ala <strong>al</strong>nn<u>aa</u>sa ummatan w<u>aah</u>idatan wal<u>aa</u> yaz<u>aa</u>luuna mukhtalifiin<strong>a</strong>', 'Dan jika Tuhanmu menghendaki, tentu Dia jadikan manusia umat yang satu, tetapi mereka senantiasa berselisih (pendapat),'),
	(1592, 11, 119, 'اِلَّا مَنْ رَّحِمَ رَبُّكَ ۗوَلِذٰلِكَ خَلَقَهُمْ ۗوَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ', 'ill<u>aa</u> man ra<u>h</u>ima rabbuka wali<u>dzaa</u>lika khalaqahum watammat kalimatu rabbika la-amla-anna jahannama mina <strong>a</strong>ljinnati wa<strong>al</strong>nn<u>aa</u>si ajma\'iin<strong>a</strong>', 'kecuali orang yang diberi rahmat oleh Tuhanmu. Dan untuk itulah Allah menciptakan mereka. Kalimat (keputusan) Tuhanmu telah tetap, “Aku pasti akan memenuhi neraka Jahanam dengan jin dan manusia (yang durhaka) semuanya.”'),
	(1593, 11, 120, 'وَكُلًّا نَّقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ الرُّسُلِ مَا نُثَبِّتُ بِهٖ فُؤَادَكَ وَجَاۤءَكَ فِيْ هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَّذِكْرٰى لِلْمُؤْمِنِيْنَ ', 'wakullan naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-i <strong>al</strong>rrusuli m<u>aa</u> nutsabbitu bihi fu-<u>aa</u>daka waj<u>aa</u>-aka fii h<u>aadz</u>ihi <strong>a</strong>l<u>h</u>aqqu wamaw\'i<u>zh</u>atun wa<u>dz</u>ikr<u>aa</u> lilmu/miniin<strong>a</strong>', 'Dan semua kisah rasul-rasul, Kami ceritakan kepadamu (Muhammad), agar dengan kisah itu Kami teguhkan hatimu; dan di dalamnya telah diberikan kepadamu (segala) kebenaran, nasihat dan peringatan bagi orang yang beriman.'),
	(1594, 11, 121, 'وَقُلْ لِّلَّذِيْنَ لَا يُؤْمِنُوْنَ اعْمَلُوْا عَلٰى مَكَانَتِكُمْۗ اِنَّا عٰمِلُوْنَۙ  ', 'waqul lilla<u>dz</u>iina l<u>aa</u> yu/minuuna i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum inn<u>aa</u> \'<u>aa</u>miluun<strong>a</strong>', 'Dan katakanlah (Muhammad) kepada orang yang tidak beriman, “Berbuatlah menurut kedudukanmu; kami pun benar-benar akan berbuat,'),
	(1595, 11, 122, 'وَانْتَظِرُوْاۚ اِنَّا مُنْتَظِرُوْنَ', 'wa<strong>i</strong>nta<u>zh</u>iruu inn<u>aa</u> munta<u>zh</u>iruun<strong>a</strong>', 'dan tunggulah, sesungguhnya kami pun termasuk yang menunggu.”'),
	(1596, 11, 123, 'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهٗ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ࣖ ', 'walill<u>aa</u>hi ghaybu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa-ilayhi yurja\'u <strong>a</strong>l-amru kulluhu fa<strong>u</strong>\'budhu watawakkal \'alayhi wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan milik Allah meliputi rahasia langit dan bumi dan kepada-Nya segala urusan dikembalikan. Maka sembahlah Dia dan bertawakallah kepada-Nya. Dan Tuhanmu tidak akan lengah terhadap apa yang kamu kerjakan.'),
	(1597, 12, 1, 'الۤرٰ ۗ تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِۗ  ', 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>lmubiin<strong>u</strong>', 'Alif Lam Ra.  Ini adalah ayat-ayat Kitab (Al-Qur\'an) yang jelas.'),
	(1598, 12, 2, 'اِنَّآ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَ', 'inn<u>aa</u> anzaln<u>aa</u>hu qur-<u>aa</u>nan \'arabiyyan la\'allakum ta\'qiluun<strong>a</strong>', 'Sesungguhnya Kami menurunkannya sebagai Qur\'an berbahasa Arab, agar kamu mengerti.'),
	(1599, 12, 3, 'نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَآ اَوْحَيْنَآ اِلَيْكَ هٰذَا الْقُرْاٰنَۖ وَاِنْ كُنْتَ مِنْ قَبْلِهٖ لَمِنَ الْغٰفِلِيْنَ', 'na<u>h</u>nu naqu<u>shsh</u>u \'alayka a<u>h</u>sana <strong>a</strong>lqa<u>sh</u>a<u>sh</u>i bim<u>aa</u> aw<u>h</u>ayn<u>aa</u> ilayka h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>na wa-in kunta min qablihi lamina <strong>a</strong>lgh<u>aa</u>filiin<strong>a</strong>', 'Kami menceritakan kepadamu (Muhammad) kisah yang paling baik dengan mewahyukan Al-Qur\'an ini kepadamu, dan sesungguhnya engkau sebelum itu termasuk orang yang tidak mengetahui.'),
	(1600, 12, 4, 'اِذْ قَالَ يُوْسُفُ لِاَبِيْهِ يٰٓاَبَتِ اِنِّيْ رَاَيْتُ اَحَدَ عَشَرَ كَوْكَبًا وَّالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ لِيْ سٰجِدِيْنَ', 'i<u>dz</u> q<u>aa</u>la yuusufu li-abiihi y<u>aa</u> abati innii ra-aytu a<u>h</u>ada \'asyara kawkaban wa<strong>al</strong>sysyamsa wa<strong>a</strong>lqamara ra-aytuhum lii s<u>aa</u>jidiin<strong>a</strong>', '(Ingatlah), ketika Yusuf berkata kepada ayahnya, “Wahai ayahku! Sungguh, aku (bermimpi) melihat sebelas bintang, matahari dan bulan; kulihat semuanya sujud kepadaku.”'),
	(1601, 12, 5, 'قَالَ يٰبُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَكِيْدُوْا لَكَ كَيْدًا ۗاِنَّ الشَّيْطٰنَ لِلْاِنْسَانِ عَدُوٌّ مُّبِيْنٌ', 'q<u>aa</u>la y<u>aa</u> bunayya l<u>aa</u> taq<u>sh</u>u<u>sh</u> ru/y<u>aa</u>ka \'al<u>aa</u> ikhwatika fayakiiduu laka kaydan inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na lil-ins<u>aa</u>ni \'aduwwun mubiin<strong>un</strong>', 'Dia (ayahnya) berkata, “Wahai anakku! Janganlah engkau ceritakan mimpimu kepada saudara-saudaramu, mereka akan membuat tipu daya (untuk membinasakan)mu. Sungguh, setan itu musuh yang jelas bagi manusia.”'),
	(1602, 12, 6, 'وَكَذٰلِكَ يَجْتَبِيْكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيْلِ الْاَحَادِيْثِ وَيُتِمُّ نِعْمَتَهٗ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوْبَ كَمَآ اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰهِيْمَ وَاِسْحٰقَۗ اِنَّ رَبَّكَ عَلِيْمٌ حَكِيْمٌ ࣖ', 'waka<u>dzaa</u>lika yajtabiika rabbuka wayu\'allimuka min ta/wiili <strong>a</strong>l-a<u>haa</u>diitsi wayutimmu ni\'matahu \'alayka wa\'al<u>aa</u> <u>aa</u>li ya\'quuba kam<u>aa</u> atammah<u>aa</u> \'al<u>aa</u> abawayka min qablu ibr<u>aa</u>hiima wa-is<u>haa</u>qa inna rabbaka \'aliimun <u>h</u>akiim<strong>un</strong>', 'Dan demikianlah, Tuhan memilih engkau (untuk menjadi Nabi) dan mengajarkan kepadamu sebagian dari takwil mimpi dan menyempurnakan (nikmat-Nya) kepadamu dan kepada keluarga Yakub, sebagaimana Dia telah menyempurnakan nikmat-Nya kepada kedua orang kakekmu sebelum itu, (yaitu) Ibrahim dan Ishak. Sungguh, Tuhanmu Maha Mengetahui, Mahabijak-sana.'),
	(1603, 12, 7, '۞ لَقَدْ كَانَ فِيْ يُوْسُفَ وَاِخْوَتِهٖٓ اٰيٰتٌ لِّلسَّاۤىِٕلِيْنَ ', 'laqad k<u>aa</u>na fii yuusufa wa-ikhwatihi <u>aa</u>y<u>aa</u>tun li<strong>l</strong>ss<u>aa</u>-iliin<strong>a</strong>', 'Sungguh, dalam (kisah) Yusuf dan saudara-saudaranya terdapat tanda-tanda (kekuasaan Allah) bagi orang yang bertanya. '),
	(1604, 12, 8, 'اِذْ قَالُوْا لَيُوْسُفُ وَاَخُوْهُ اَحَبُّ اِلٰٓى اَبِيْنَا مِنَّا وَنَحْنُ عُصْبَةٌ  ۗاِنَّ اَبَانَا لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ  ', 'i<u>dz</u> q<u>aa</u>luu layuusufu wa-akhuuhu a<u>h</u>abbu il<u>aa</u> abiin<u>aa</u> minn<u>aa</u> wana<u>h</u>nu \'u<u>sh</u>batun inna ab<u>aa</u>n<u>aa</u> lafii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Ketika mereka berkata, “Sesungguhnya Yusuf dan saudaranya (Bunyamin) lebih dicintai ayah daripada kita, padahal kita adalah satu golongan (yang kuat). Sungguh, ayah kita dalam kekeliruan yang nyata.'),
	(1605, 12, 9, ' ۨاقْتُلُوْا يُوْسُفَ اَوِ اطْرَحُوْهُ اَرْضًا يَّخْلُ لَكُمْ وَجْهُ اَبِيْكُمْ وَتَكُوْنُوْا مِنْۢ بَعْدِهٖ قَوْمًا صٰلِحِيْنَ', 'uqtuluu yuusufa awi i<u>th</u>ra<u>h</u>uuhu ar<u>dh</u>an yakhlu lakum wajhu abiikum watakuunuu min ba\'dihi qawman <u>shaa</u>li<u>h</u>iin<strong>a</strong>', 'Bunuhlah Yusuf atau buanglah dia ke suatu tempat agar perhatian ayah tertumpah kepadamu, dan setelah itu kamu menjadi orang yang baik.”'),
	(1606, 12, 10, 'قَالَ قَاۤئِلٌ مِّنْهُمْ لَا تَقْتُلُوْا يُوْسُفَ وَاَلْقُوْهُ فِيْ غَيٰبَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فٰعِلِيْنَ', 'q<u>aa</u>la q<u>aa</u>-ilun minhum l<u>aa</u> taqtuluu yuusufa wa-alquuhu fii ghay<u>aa</u>bati <strong>a</strong>ljubbi yaltaqi<u>th</u>hu ba\'<u>dh</u>u <strong>al</strong>ssayy<u>aa</u>rati in kuntum f<u>aa</u>\'iliin<strong>a</strong>', 'Seorang di antara mereka berkata, “Janganlah kamu membunuh Yusuf, tetapi masukan saja dia ke dasar sumur agar dia dipungut oleh sebagian musafir, jika kamu hendak berbuat.”'),
	(1607, 12, 11, 'قَالُوْا يٰٓاَبَانَا مَالَكَ لَا تَأْمَنَّ۫ا عَلٰى يُوْسُفَ وَاِنَّا لَهٗ لَنَاصِحُوْنَ', 'q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> m<u>aa</u> laka l<u>aa</u> ta/mann<u>aa</u> \'al<u>aa</u> yuusufa wa-inn<u>aa</u> lahu lan<u>aas</u>i<u>h</u>uun<strong>a</strong>', 'Mereka berkata, “Wahai ayah kami! Mengapa engkau tidak mempercayai kami terhadap Yusuf, padahal sesungguhnya kami semua menginginkan kebaikan baginya.'),
	(1608, 12, 12, 'اَرْسِلْهُ مَعَنَا غَدًا يَّرْتَعْ وَيَلْعَبْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ', 'arsilhu ma\'an<u>aa</u> ghadan yarta\' wayal\'ab wa-inn<u>aa</u> lahu la<u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'Biarkanlah dia pergi bersama kami besok pagi, agar dia bersenang-senang dan bermain-main, dan kami pasti menjaganya.”'),
	(1609, 12, 13, 'قَالَ اِنِّيْ لَيَحْزُنُنِيْٓ اَنْ تَذْهَبُوْا بِهٖ وَاَخَافُ اَنْ يَّأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غٰفِلُوْنَ ', 'q<u>aa</u>la innii laya<u>h</u>zununii an tsa<u>dz</u>habuu bihi wa-akh<u>aa</u>fu an ya/kulahu <strong>al</strong><u>dzdz</u>i/bu wa-antum \'anhu gh<u>aa</u>filuun<strong>a</strong>', 'Dia (Yakub) berkata, “Sesungguhnya kepergian kamu bersama dia (Yusuf) sangat menyedihkanku dan aku khawatir dia dimakan serigala, sedang kamu lengah darinya.”'),
	(1610, 12, 14, 'قَالُوْا لَىِٕنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّآ اِذًا لَّخٰسِرُوْنَ', 'q<u>aa</u>luu la-in akalahu <strong>al</strong><u>dzdz</u>i/bu wana<u>h</u>nu \'u<u>sh</u>batun inn<u>aa</u> i<u>dz</u>an lakh<u>aa</u>siruun<strong>a</strong>', 'Sesungguhnya mereka berkata, “Jika dia dimakan serigala, padahal kami golongan (yang kuat), kalau demikian tentu kami orang-orang yang rugi.”'),
	(1611, 12, 15, 'فَلَمَّا ذَهَبُوْا بِهٖ وَاَجْمَعُوْٓا اَنْ يَّجْعَلُوْهُ فِيْ غَيٰبَتِ الْجُبِّۚ وَاَوْحَيْنَآ اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُوْنَ', 'falamm<u>aa</u> <u>dz</u>ahabuu bihi wa-ajma\'uu an yaj\'aluuhu fii ghay<u>aa</u>bati <strong>a</strong>ljubbi wa-aw<u>h</u>ayn<u>aa</u> ilayhi latunabi-annahum bi-amrihim h<u>aadzaa</u> wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Maka ketika mereka membawanya dan sepakat memasukkan ke dasar sumur, Kami wahyukan kepadanya, “Engkau kelak pasti akan menceritakan perbuatan ini kepada mereka, sedang mereka tidak menyadari.”'),
	(1612, 12, 16, 'وَجَاۤءُوْٓ اَبَاهُمْ عِشَاۤءً يَّبْكُوْنَۗ', 'waj<u>aa</u>uu ab<u>aa</u>hum \'isy<u>aa</u>-an yabkuun<strong>a</strong>', 'Kemudian mereka datang kepada ayah mereka pada petang hari sambil menangis.'),
	(1613, 12, 17, 'قَالُوْا يٰٓاَبَانَآ اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوْسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَآ اَنْتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صٰدِقِيْنَ', 'q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> inn<u>aa</u> <u>dz</u>ahabn<u>aa</u> nastabiqu watarakn<u>aa</u> yuusufa \'inda mat<u>aa</u>\'in<u>aa</u> fa-akalahu <strong>al</strong><u>dzdz</u>i/bu wam<u>aa</u> anta bimu/minin lan<u>aa</u> walaw kunn<u>aa</u> <u>shaa</u>diqiin<strong>a</strong>', 'Mereka berkata, “Wahai ayah kami! Sesungguhnya kami pergi berlomba dan kami tinggalkan Yusuf di dekat barang-barang kami, lalu dia dimakan serigala; dan engkau tentu tidak akan percaya kepada kami, sekalipun kami berkata benar.”'),
	(1614, 12, 18, 'وَجَاۤءُوْ عَلٰى قَمِيْصِهٖ بِدَمٍ كَذِبٍۗ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ  ۗوَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ', 'waj<u>aa</u>uu \'al<u>aa</u> qamii<u>sh</u>ihi bidamin ka<u>dz</u>ibin q<u>aa</u>la bal sawwalat lakum anfusukum amran fa<u>sh</u>abrun jamiilun wa<strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmusta\'<u>aa</u>nu \'al<u>aa</u> m<u>aa</u> ta<u>sh</u>ifuun<strong>a</strong>', 'Dan mereka datang membawa baju gamisnya (yang berlumuran) darah palsu. Dia (Yakub) berkata, “Sebenarnya hanya dirimu sendirilah yang memandang baik urusan yang buruk itu; maka hanya bersabar itulah yang terbaik (bagiku). Dan kepada Allah saja memohon pertolongan-Nya terhadap apa yang kamu ceritakan.”'),
	(1615, 12, 19, 'وَجَاۤءَتْ سَيَّارَةٌ فَاَرْسَلُوْا وَارِدَهُمْ فَاَدْلٰى دَلْوَهٗ ۗقَالَ يٰبُشْرٰى هٰذَا غُلٰمٌ ۗوَاَسَرُّوْهُ بِضَاعَةً ۗوَاللّٰهُ عَلِيْمٌ ۢبِمَا يَعْمَلُوْنَ', 'waj<u>aa</u>-at sayy<u>aa</u>ratun fa-arsaluu w<u>aa</u>ridahum fa-adl<u>aa</u> dalwahu q<u>aa</u>la y<u>aa</u> busyr<u>aa</u> h<u>aadzaa</u> ghul<u>aa</u>mun wa-asarruuhu bi<u>daa</u>\'atan wa<strong>al</strong>l<u>aa</u>hu \'aliimun bim<u>aa</u> ya\'maluun<strong>a</strong>', 'Dan datanglah sekelompok musafir, mereka menyuruh seorang pengambil air. Lalu dia menurunkan timbanya. Dia berkata, “Oh, senangnya, ini ada seorang anak muda!” Kemudian mereka menyembunyikannya sebagai barang dagangan. Dan Allah Maha Mengetahui apa yang mereka kerjakan.'),
	(1616, 12, 20, 'وَشَرَوْهُ بِثَمَنٍۢ بَخْسٍ دَرَاهِمَ مَعْدُوْدَةٍ ۚوَكَانُوْا فِيْهِ مِنَ الزَّاهِدِيْنَ ࣖ ', 'wasyarawhu bitsamanin bakhsin dar<u>aa</u>hima ma\'duudatin wak<u>aa</u>nuu fiihi mina <strong>al</strong>zz<u>aa</u>hidiin<strong>a</strong>', 'Dan mereka menjualnya (Yusuf) dengan harga rendah, yaitu beberapa dirham saja, sebab mereka tidak tertarik kepadanya.'),
	(1617, 12, 21, 'وَقَالَ الَّذِى اشْتَرٰىهُ مِنْ مِّصْرَ لِامْرَاَتِهٖٓ اَكْرِمِيْ مَثْوٰىهُ عَسٰىٓ اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا ۗوَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِۖ وَلِنُعَلِّمَهٗ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۗ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِه', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>ii isytar<u>aa</u>hu min mi<u>sh</u>ra li<strong>i</strong>mra-atihi akrimii matsw<u>aa</u>hu \'as<u>aa</u> an yanfa\'an<u>aa</u> aw nattakhi<u>dz</u>ahu waladan waka<u>dzaa</u>lika makkann<u>aa</u> liyuusufa fii <strong>a</strong>l-ar<u>dh</u>i walinu\'allimahu min ta/wiili <strong>a</strong>l-a<u>haa</u>diitsi wa<strong>al</strong>l<u>aa</u>hu gh<u>aa</u>libun \'al<u>aa</u> amrihi wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan orang dari Mesir yang membelinya berkata kepada istrinya,” Berikanlah kepadanya tempat (dan layanan) yang baik, mudah-mudahan dia bermanfaat bagi kita atau kita pungut dia sebagai anak.” Dan demikianlah Kami memberikan kedudukan yang baik kepada Yusuf di negeri (Mesir), dan agar Kami ajarkan kepadanya takwil mimpi. Dan Allah berkuasa terhadap urusan-Nya, tetapi kebanyakan manusia tidak mengerti.'),
	(1618, 12, 22, 'وَلَمَّا بَلَغَ اَشُدَّهٗٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ', 'walamm<u>aa</u> balagha asyuddahu <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman waka<u>dzaa</u>lika najzii <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan ketika dia telah cukup dewasa Kami berikan kepadanya kekuasaan dan ilmu. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
	(1619, 12, 23, 'وَرَاوَدَتْهُ الَّتِيْ هُوَ فِيْ بَيْتِهَا عَنْ نَّفْسِهٖ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۗقَالَ مَعَاذَ اللّٰهِ اِنَّهٗ رَبِّيْٓ اَحْسَنَ مَثْوَايَۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ', 'war<u>aa</u>wadat-hu <strong>al</strong>latii huwa fii baytih<u>aa</u> \'an nafsihi waghallaqati <strong>a</strong>l-abw<u>aa</u>ba waq<u>aa</u>lat hayta laka q<u>aa</u>la ma\'<u>aadz</u>a <strong>al</strong>l<u>aa</u>hi innahu rabbii a<u>h</u>sana matsw<u>aa</u>ya innahu l<u>aa</u> yufli<u>h</u>u <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Dan perempuan yang dia (Yusuf) tinggal di rumahnya menggoda dirinya. Dan dia menutup pintu-pintu, lalu berkata, “Marilah mendekat kepadaku.” Yusuf berkata, “Aku berlindung kepada Allah, sungguh, tuanku telah memperlakukan aku dengan baik.” Sesungguhnya orang yang zalim itu tidak akan beruntung.'),
	(1620, 12, 24, 'وَلَقَدْ هَمَّتْ بِهٖۙ وَهَمَّ بِهَا ۚ لَوْلَآ اَنْ رَّاٰى بُرْهَانَ رَبِّهٖۗ  كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّوْۤءَ وَالْفَحْشَاۤءَۗ اِنَّهٗ مِنْ عِبَادِنَا الْمُخْلَصِيْنَ ', 'walaqad hammat bihi wahamma bih<u>aa</u> lawl<u>aa</u> an ra<u>aa</u> burh<u>aa</u>na rabbihi ka<u>dzaa</u>lika lina<u>sh</u>rifa \'anhu <strong>al</strong>ssuu-a wa<strong>a</strong>lfa<u>h</u>sy<u>aa</u>-a innahu min \'ib<u>aa</u>din<u>aa</u> <strong>a</strong>lmukhla<u>sh</u>iin<strong>a</strong>', 'Dan sungguh, perempuan itu telah berkehendak kepadanya (Yusuf). Dan Yusuf pun berkehendak kepadanya, sekiranya dia tidak melihat tanda (dari) Tuhannya. Demikianlah, Kami palingkan darinya keburukan dan kekejian. Sungguh, dia (Yusuf) termasuk hamba Kami yang terpilih.'),
	(1621, 12, 25, 'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيْصَهٗ مِنْ دُبُرٍ وَّاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۗ قَالَتْ مَا جَزَاۤءُ مَنْ اَرَادَ بِاَهْلِكَ سُوْۤءًا اِلَّآ اَنْ يُّسْجَنَ اَوْ عَذَابٌ اَلِيْمٌ ', 'wa<strong>i</strong>stabaq<u>aa</u> <strong>a</strong>lb<u>aa</u>ba waqaddat qamii<u>sh</u>ahu min duburin wa-alfay<u>aa</u> sayyidah<u>aa</u> lad<u>aa</u> <strong>a</strong>lb<u>aa</u>bi q<u>aa</u>lat m<u>aa</u> jaz<u>aa</u>u man ar<u>aa</u>da bi-ahlika suu-an ill<u>aa</u> an yusjana aw \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan keduanya berlomba menuju pintu dan perempuan itu menarik baju gamisnya (Yusuf) dari belakang hingga koyak dan keduanya mendapati suami perempuan itu di depan pintu. Dia (perempuan itu) berkata, “Apakah balasan terhadap orang yang bermaksud buruk terhadap istrimu, selain dipenjarakan atau (dihukum) dengan siksa yang pedih?”'),
	(1622, 12, 26, 'قَالَ هِيَ رَاوَدَتْنِيْ عَنْ نَّفْسِيْ وَشَهِدَ شَاهِدٌ مِّنْ اَهْلِهَاۚ اِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكٰذِبِيْنَ ', 'q<u>aa</u>la hiya r<u>aa</u>wadatnii \'an nafsii wasyahida sy<u>aa</u>hidun min ahlih<u>aa</u> in k<u>aa</u>na qamii<u>sh</u>uhu qudda min qubulin fa<u>sh</u>adaqat wahuwa mina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dia (Yusuf) berkata, “Dia yang menggodaku dan merayu diriku.”  Seorang saksi dari keluarga perempuan itu memberikan kesaksian, “Jika baju gamisnya koyak di bagian depan, maka perempuan itu benar, dan dia (Yusuf) termasuk orang yang dusta.'),
	(1623, 12, 27, 'وَاِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصّٰدِقِيْنَ ', 'wa-in k<u>aa</u>na qamii<u>sh</u>uhu qudda min duburin faka<u>dz</u>abat wahuwa mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Dan jika baju gamisnya koyak di bagian belakang, maka perempuan itulah yang dusta, dan dia (Yusuf) termasuk orang yang benar.”'),
	(1624, 12, 28, 'فَلَمَّا رَاٰى قَمِيْصَهٗ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهٗ مِنْ كَيْدِكُنَّ ۗاِنَّ كَيْدَكُنَّ عَظِيْمٌ ', 'falamm<u>aa</u> ra<u>aa</u> qamii<u>sh</u>ahu qudda min duburin q<u>aa</u>la innahu min kaydikunna inna kaydakunna \'a<u>zh</u>iim<strong>un</strong>', 'Maka ketika dia (suami perempuan itu) melihat baju gamisnya (Yusuf) koyak di bagian belakang, dia berkata, “Sesungguhnya ini adalah tipu dayamu. Tipu dayamu benar-benar hebat.”'),
	(1625, 12, 29, 'يُوْسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِرِيْ لِذَنْۢبِكِۖ اِنَّكِ كُنْتِ مِنَ الْخٰطِـِٕيْنَ ࣖ ', 'yuusufu a\'ri<u>dh</u> \'an h<u>aadzaa</u> wa<strong>i</strong>staghfirii li<u>dz</u>anbiki innaki kunti mina <strong>a</strong>lkh<u>aath</u>i-iin<strong>a</strong>', 'Wahai Yusuf! ”Lupakanlah ini, dan (istriku) mohonlah ampunan atas dosamu, karena engkau termasuk orang yang bersalah.”'),
	(1626, 12, 30, '۞ وَقَالَ نِسْوَةٌ فِى الْمَدِيْنَةِ امْرَاَتُ الْعَزِيْزِ تُرَاوِدُ فَتٰىهَا عَنْ نَّفْسِهٖۚ قَدْ شَغَفَهَا حُبًّاۗ اِنَّا لَنَرٰىهَا فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'waq<u>aa</u>la niswatun fii <strong>a</strong>lmadiinati imra-atu <strong>a</strong>l\'aziizi tur<u>aa</u>widu fat<u>aa</u>h<u>aa</u> \'an nafsihi qad syaghafah<u>aa</u> <u>h</u>ubban inn<u>aa</u> lanar<u>aa</u>h<u>aa</u> fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Dan perempuan-perempuan di kota berkata, “Istri Al-Aziz menggoda dan merayu pelayannya untuk menundukkan dirinya, pelayannya benar-benar membuatnya mabuk cinta. Kami pasti memandang dia dalam kesesatan yang nyata.”'),
	(1627, 12, 31, 'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَاً وَّاٰتَتْ كُلَّ وَاحِدَةٍ مِّنْهُنَّ سِكِّيْنًا وَّقَالَتِ اخْرُجْ عَلَيْهِنَّ ۚ فَلَمَّا رَاَيْنَهٗٓ اَكْبَرْنَهٗ وَقَطَّعْنَ اَيْدِيَهُنَّۖ وَقُلْنَ حَاشَ لِلّٰهِ مَا ', 'falamm<u>aa</u> sami\'at bimakrihinna arsalat ilayhinna wa-a\'tadat lahunna muttaka-an wa<u>aa</u>tat kulla w<u>aah</u>idatin minhunna sikkiinan waq<u>aa</u>lati ukhruj \'alayhinna falamm<u>aa</u> ra-aynahu akbarnahu waqa<u>ththh</u>a\'na aydiyahunna waqulna <u>haa</u>sya lill<u>aa</u>hi m<u>aa</u> h<u>aadzaa</u> basyaran in h<u>aadzaa</u> ill<u>aa</u> malakun kariim<strong>un</strong>', 'Maka ketika perempuan itu mendengar cercaan mereka, diundangnyalah perempuan-perempuan itu dan disediakannya tempat duduk bagi mereka, dan kepada masing-masing mereka diberikan sebuah pisau (untuk memotong jamuan), kemudian dia berkata (kepada Yusuf), “Keluarlah (tampakkanlah dirimu) kepada mereka.” Ketika perempuan-perempuan itu melihatnya, mereka terpesona kepada (keelokan rupa)nya, dan mereka (tanpa sadar) melukai tangannya sendiri. Seraya berkata, “Mahasempurna Allah, ini bukanlah manusia. Ini benar-benar malaikat yang mulia.”'),
	(1628, 12, 32, 'قَالَتْ فَذٰلِكُنَّ الَّذِيْ لُمْتُنَّنِيْ فِيْهِ ۗوَلَقَدْ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ فَاسْتَعْصَمَ ۗوَلَىِٕنْ لَّمْ يَفْعَلْ مَآ اٰمُرُهٗ لَيُسْجَنَنَّ وَلَيَكُوْنًا مِّنَ الصّٰغِرِيْنَ ', 'q<u>aa</u>lat fa<u>dzaa</u>likunna <strong>al</strong>la<u>dz</u>ii lumtunnanii fiihi walaqad r<u>aa</u>wadtuhu \'an nafsihi fa<strong>i</strong>st\'<u>sh</u>ama wala-in lam yaf\'al m<u>aa</u> <u>aa</u>muruhu layusjananna walayakuunan mina <strong>al</strong><u>shshaa</u>ghiriin<strong>a</strong>', 'Dia (istri Al-Aziz) berkata, “Itulah orangnya yang menyebabkan kamu mencela aku karena (aku tertarik) kepadanya, dan sungguh, aku telah menggoda untuk menundukkan dirinya tetapi dia menolak. Jika dia tidak melakukan apa yang aku perintahkan kepadanya, niscaya dia akan dipenjarakan, dan dia akan menjadi orang yang hina.”'),
	(1629, 12, 33, 'قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُوْنَنِيْٓ اِلَيْهِ ۚوَاِلَّا تَصْرِفْ عَنِّيْ كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِّنَ الْجٰهِلِيْنَ ', 'q<u>aa</u>la rabbi <strong>al</strong>ssijnu a<u>h</u>abbu ilayya mimm<u>aa</u> yad\'uunanii ilayhi wa-ill<u>aa</u> ta<u>sh</u>rif \'annii kaydahunna a<u>sh</u>bu ilayhinna wa-akun mina <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Yusuf berkata, “Wahai Tuhanku! Penjara lebih aku sukai daripada memenuhi ajakan mereka. Jika aku tidak Engkau hindarkan dari tipu daya mereka, niscaya aku akan cenderung untuk (memenuhi keinginan mereka) dan tentu aku termasuk orang yang bodoh.”'),
	(1630, 12, 34, 'فَاسْتَجَابَ لَهٗ رَبُّهٗ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ', 'fa<strong>i</strong>staj<u>aa</u>ba lahu rabbuhu fa<u>sh</u>arafa \'anhu kaydahunna innahu huwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Maka Tuhan memperkenankan doa Yusuf, dan Dia menghindarkan Yusuf dari tipu daya mereka. Dialah Yang Maha Mendengar, Maha Mengetahui.'),
	(1631, 12, 35, 'ثُمَّ بَدَا لَهُمْ مِّنْۢ بَعْدِ مَا رَاَوُا الْاٰيٰتِ لَيَسْجُنُنَّهٗ حَتّٰى حِيْنٍ ࣖ ', 'tsumma bad<u>aa</u> lahum min ba\'di m<u>aa</u> ra-awuu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti layasjununnahu <u>h</u>att<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Kemudian timbul pikiran pada mereka setelah melihat tanda-tanda (kebenaran Yusuf) bahwa mereka harus memenjarakannya sampai waktu tertentu.'),
	(1632, 12, 36, 'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيٰنِ ۗقَالَ اَحَدُهُمَآ اِنِّيْٓ اَرٰىنِيْٓ اَعْصِرُ خَمْرًا ۚوَقَالَ الْاٰخَرُ اِنِّيْٓ اَرٰىنِيْٓ اَحْمِلُ فَوْقَ رَأْسِيْ خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۗنَبِّئْنَا بِتَأْوِيْلِهٖ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ', 'wadakhala ma\'ahu <strong>al</strong>ssijna fatay<u>aa</u>ni q<u>aa</u>la a<u>h</u>aduhum<u>aa</u> innii ar<u>aa</u>nii a\'<u>sh</u>iru khamran waq<u>aa</u>la <strong>a</strong>l-<u>aa</u>kharu innii ar<u>aa</u>nii a<u>h</u>milu fawqa ra/sii khubzan ta/kulu <strong>al</strong><u>ththh</u>ayru minhu nabbi/n<u>aa</u> bita/wiilihi inn<u>aa</u> nar<u>aa</u>ka mina <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan bersama dia masuk pula dua orang pemuda ke dalam penjara. Salah satunya berkata, “Sesungguhnya aku bermimpi memeras anggur,” dan yang lainnya berkata, “Aku bermimpi, membawa roti di atas kepalaku, sebagiannya dimakan burung.” Berikanlah kepada kami takwilnya. Sesungguhnya kami memandangmu termasuk orang yang berbuat baik.'),
	(1633, 12, 37, 'قَالَ لَا يَأْتِيْكُمَا طَعَامٌ تُرْزَقٰنِهٖٓ اِلَّا نَبَّأْتُكُمَا بِتَأْوِيْلِهٖ قَبْلَ اَنْ يَّأْتِيَكُمَا ۗذٰلِكُمَا مِمَّا عَلَّمَنِيْ رَبِّيْۗ اِنِّيْ تَرَكْتُ مِلَّةَ قَوْمٍ لَّا يُؤْمِنُوْنَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَۙ ', 'q<u>aa</u>la l<u>aa</u> ya/tiikum<u>aa</u> <u>th</u>a\'<u>aa</u>mun turzaq<u>aa</u>nihi ill<u>aa</u> nabba/tukum<u>aa</u> bita/wiilihi qabla an ya/tiyakum<u>aa</u> <u>dzaa</u>likum<u>aa</u> mimm<u>aa</u> \'allamanii rabbii innii taraktu millata qawmin l<u>aa</u> yu/minuuna bi<strong>al</strong>l<u>aa</u>hi wahum bi<strong>a</strong>l-<u>aa</u>khirati hum k<u>aa</u>firuun<strong>a</strong>', 'Dia (Yusuf) berkata, “Makanan apa pun yang akan diberikan kepadamu berdua aku telah dapat menerangkan takwilnya, sebelum (makanan) itu sampai kepadamu. Itu sebagian dari yang diajarkan Tuhan kepadaku. Sesungguhnya aku telah meninggalkan agama orang-orang yang tidak beriman kepada Allah, bahkan mereka tidak percaya kepada hari akhirat.'),
	(1634, 12, 38, 'وَاتَّبَعْتُ مِلَّةَ اٰبَاۤءِيْٓ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَۗ مَا كَانَ لَنَآ اَنْ نُّشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۗ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ', 'wa<strong>i</strong>ttaba\'tu millata <u>aa</u>b<u>aa</u>-ii ibr<u>aa</u>hiima wa-is<u>haa</u>qa waya\'quuba m<u>aa</u> k<u>aa</u>na lan<u>aa</u> an nusyrika bi<strong>al</strong>l<u>aa</u>hi min syay-in <u>dzaa</u>lika min fa<u>dh</u>li <strong>al</strong>l<u>aa</u>hi \'alayn<u>aa</u> wa\'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> yasykuruun<strong>a</strong>', 'Dan aku mengikuti agama nenek moyangku: Ibrahim, Ishak dan Yakub. Tidak pantas bagi kami (para nabi) mempersekutukan sesuatu apa pun dengan Allah. Itu adalah dari karunia Allah kepada kami dan kepada manusia (semuanya); tetapi kebanyakan manusia tidak bersyukur.'),
	(1635, 12, 39, 'يٰصَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُوْنَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۗ  ', 'y<u>aa</u> <u>shaah</u>ibayi <strong>al</strong>ssijni a-arb<u>aa</u>bun mutafarriquuna khayrun ami <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lw<u>aah</u>idu <strong>a</strong>lqahh<u>aa</u>r<strong>u</strong>', 'Wahai kedua penghuni penjara! Manakah yang baik, tuhan-tuhan yang bermacam-macam itu ataukah Allah Yang Maha Esa, Mahaperkasa?'),
	(1636, 12, 40, 'مَا تَعْبُدُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اَسْمَاۤءً سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗاَمَرَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ وَلٰكِنَّ اَكْ', 'm<u>aa</u> ta\'buduuna min duunihi ill<u>aa</u> asm<u>aa</u>-an sammaytumuuh<u>aa</u> antum wa<u>aa</u>b<u>aa</u>ukum m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu bih<u>aa</u> min sul<u>th</u><u>aa</u>nin ini <strong>a</strong>l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi amara <strong>al</strong>l<u>aa</u> ta\'buduu ill<u>aa</u> iyy<u>aa</u>hu <u>dzaa</u>lika <strong>al</strong>ddiinu <strong>a</strong>lqayyimu wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Apa yang kamu sembah selain Dia, hanyalah nama-nama yang kamu buat-buat baik oleh kamu sendiri maupun oleh nenek moyangmu. Allah tidak menurunkan suatu keterangan pun tentang hal (nama-nama) itu. Keputusan itu hanyalah milik Allah. Dia telah memerintahkan agar kamu tidak menyembah selain Dia. Itulah agama yang lurus, tetapi kebanyakan manusia tidak mengetahui.'),
	(1637, 12, 41, 'يٰصَاحِبَيِ السِّجْنِ اَمَّآ اَحَدُكُمَا فَيَسْقِيْ رَبَّهٗ خَمْرًا ۗوَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَّأْسِهٖ ۗ قُضِيَ الْاَمْرُ الَّذِيْ فِيْهِ تَسْتَفْتِيٰنِۗ ', 'y<u>aa</u> <u>shaah</u>ibayi <strong>al</strong>ssijni amm<u>aa</u> a<u>h</u>adukum<u>aa</u> fayasqii rabbahu khamran wa-amm<u>aa</u> <strong>a</strong>l-<u>aa</u>kharu fayu<u>sh</u>labu fata/kulu <strong>al</strong><u>ththh</u>ayru min ra/sihi qu<u>dh</u>iya <strong>a</strong>l-amru <strong>al</strong>la<u>dz</u>ii fiihi tastaftiy<u>aa</u>n<strong>i</strong>', 'Wahai kedua penghuni penjara, “Salah seorang di antara kamu, akan bertugas menyediakan minuman khamar bagi tuannya. Adapun yang seorang lagi dia akan disalib, lalu burung memakan sebagian kepalanya. Telah terjawab perkara yang kamu tanyakan (kepadaku).”'),
	(1638, 12, 42, 'وَقَالَ لِلَّذِيْ ظَنَّ اَنَّهٗ نَاجٍ مِّنْهُمَا اذْكُرْنِيْ عِنْدَ رَبِّكَۖ فَاَنْسٰىهُ الشَّيْطٰنُ ذِكْرَ رَبِّهٖ فَلَبِثَ فِى السِّجْنِ بِضْعَ سِنِيْنَ ࣖ ', 'waq<u>aa</u>la lilla<u>dz</u>ii <u>zh</u>anna annahu n<u>aa</u>jin minhum<u>aa</u> u<u>dz</u>kurnii \'inda rabbika fa-ans<u>aa</u>hu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu <u>dz</u>ikra rabbihi falabitsa fii <strong>al</strong>ssijni bi<u>dh</u>\'a siniin<strong>a</strong>', 'Dan dia (Yusuf) berkata kepada orang yang diketahuinya akan selamat di antara mereka berdua, “Terangkanlah keadaanku kepada tuanmu.” Maka setan menjadikan dia lupa untuk menerangkan (keadaan Yusuf) kepada tuannya. Karena itu dia (Yusuf) tetap dalam penjara beberapa tahun lamanya.'),
	(1639, 12, 43, 'وَقَالَ الْمَلِكُ اِنِّيْٓ اَرٰى سَبْعَ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعَ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۗ  يٰٓاَيُّهَا الْمَلَاُ اَفْتُوْنِيْ فِيْ رُؤْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُوْنَ', 'waq<u>aa</u>la <strong>a</strong>lmaliku innii ar<u>aa</u> sab\'a baqar<u>aa</u>tin sim<u>aa</u>nin ya/kuluhunna sab\'un \'ij<u>aa</u>fun wasab\'a sunbul<u>aa</u>tin khu<u>dh</u>rin waukhara y<u>aa</u>bis<u>aa</u>tin y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>lmalau aftuunii fii ru/y<u>aa</u>ya in kuntum li<strong>l</strong>rru/y<u>aa</u> ta\'buruun<strong>a</strong>', 'Dan raja berkata (kepada para pemuka kaumnya), “Sesungguhnya aku bermimpi melihat tujuh ekor sapi betina yang gemuk dimakan oleh tujuh ekor sapi betina yang kurus; tujuh tangkai (gandum) yang hijau dan (tujuh tangkai) lainnya yang kering. Wahai orang yang terkemuka! Terangkanlah kepadaku tentang takwil mimpiku itu jika kamu dapat menakwilkan mimpi.” '),
	(1640, 12, 44, 'قَالُوْٓا اَضْغَاثُ اَحْلَامٍ ۚوَمَا نَحْنُ بِتَأْوِيْلِ الْاَحْلَامِ بِعٰلِمِيْنَ ', 'q<u>aa</u>luu a<u>dh</u>gh<u>aa</u>tsu a<u>h</u>l<u>aa</u>min wam<u>aa</u> na<u>h</u>nu bita/wiili <strong>a</strong>l-a<u>h</u>l<u>aa</u>mi bi\'<u>aa</u>limiin<strong>a</strong>', 'Mereka menjawab, “(Itu) mimpi-mimpi yang kosong dan kami tidak mampu menakwilkan mimpi itu.”'),
	(1641, 12, 45, 'وَقَالَ الَّذِيْ نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۠ اُنَبِّئُكُمْ بِتَأْوِيْلِهٖ فَاَرْسِلُوْنِ ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>ii naj<u>aa</u> minhum<u>aa</u> wa<strong>i</strong>ddakara ba\'da ummatin an<u>aa</u> unabbi-ukum bita/wiilihi fa-arsiluun<strong>i</strong>', 'Dan berkatalah orang yang selamat di antara mereka berdua dan teringat (kepada Yusuf) setelah beberapa waktu lamanya, “Aku akan memberitahukan kepadamu tentang (orang yang pandai) menakwilkan mimpi itu, maka utuslah aku (kepadanya).”'),
	(1642, 12, 46, 'يُوْسُفُ اَيُّهَا الصِّدِّيْقُ اَفْتِنَا فِيْ سَبْعِ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعِ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۙ  لَّعَلِّيْٓ اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُوْنَ ', 'yuusufu ayyuh<u>aa</u> <strong>al</strong><u>shsh</u>iddiiqu aftin<u>aa</u> fii sab\'i baqar<u>aa</u>tin sim<u>aa</u>nin ya/kuluhunna sab\'un \'ij<u>aa</u>fun wasab\'i sunbul<u>aa</u>tin khu<u>dh</u>rin waukhara y<u>aa</u>bis<u>aa</u>tin la\'allii arji\'u il<u>aa</u> <strong>al</strong>nn<u>aa</u>si la\'allahum ya\'lamuun<strong>a</strong>', '”Yusuf, wahai orang yang sangat dipercaya! Terangkanlah kepada kami (takwil mimpi) tentang tujuh ekor sapi betina yang gemuk yang dimakan oleh tujuh (ekor sapi betina) yang kurus, tujuh tangkai (gandum) yang hijau dan (tujuh tangkai) lainnya yang kering agar aku kembali kepada orang-orang itu, agar mereka mengetahui.”'),
	(1643, 12, 47, 'قَالَ تَزْرَعُوْنَ سَبْعَ سِنِيْنَ دَاَبًاۚ فَمَا حَصَدْتُّمْ فَذَرُوْهُ فِيْ سُنْۢبُلِهٖٓ اِلَّا قَلِيْلًا مِّمَّا تَأْكُلُوْنَ ', 'q<u>aa</u>la tazra\'uuna sab\'a siniina da-aban fam<u>aa</u> <u>h</u>a<u>sh</u>adtum fa<u>dz</u>aruuhu fii sunbulihi ill<u>aa</u> qaliilan mimm<u>aa</u> ta/kuluun<strong>a</strong>', 'Dia (Yusuf) berkata, “Agar kamu bercocok tanam tujuh tahun (berturut-turut) sebagaimana biasa; kemudian apa yang kamu tuai hendaklah kamu biarkan di tangkainya kecuali sedikit untuk kamu makan.'),
	(1644, 12, 48, 'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَّأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَلِيْلًا مِّمَّا تُحْصِنُوْنَ', 'tsumma ya/tii min ba\'di <u>dzaa</u>lika sab\'un syid<u>aa</u>dun ya/kulna m<u>aa</u> qaddamtum lahunna ill<u>aa</u> qaliilan mimm<u>aa</u> tu<u>hs</u>inuun<strong>a</strong>', 'Kemudian setelah  itu akan datang tujuh (tahun) yang sangat sulit, yang menghabiskan apa yang kamu simpan untuk menghadapinya (tahun sulit), kecuali sedikit dari apa (bibit gandum) yang kamu simpan. '),
	(1645, 12, 49, 'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ عَامٌ فِيْهِ يُغَاثُ النَّاسُ وَفِيْهِ يَعْصِرُوْنَ ࣖ', 'tsumma ya/tii min ba\'di <u>dzaa</u>lika \'<u>aa</u>mun fiihi yugh<u>aa</u>tsu <strong>al</strong>nn<u>aa</u>su wafiihi ya\'<u>sh</u>iruun<strong>a</strong>', 'Setelah itu akan datang tahun, di mana manusia diberi hujan (dengan cukup) dan pada masa itu mereka memeras (anggur).”'),
	(1646, 12, 50, 'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖ ۚفَلَمَّا جَاۤءَهُ الرَّسُوْلُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰتِيْ قَطَّعْنَ اَيْدِيَهُنَّ ۗاِنَّ رَبِّيْ بِكَيْدِهِنَّ عَلِيْمٌ ', 'waq<u>aa</u>la <strong>a</strong>lmaliku i/tuunii bihi falamm<u>aa</u> j<u>aa</u>-ahu <strong>al</strong>rrasuulu q<u>aa</u>la irji\' il<u>aa</u> rabbika fa<strong>i</strong>s-alhu m<u>aa</u> b<u>aa</u>lu <strong>al</strong>nniswati <strong>al</strong>l<u>aa</u>tii qa<u>ththh</u>a\'na aydiyahunna inna rabbii bikaydihinna \'aliim<strong>un</strong>', 'Dan raja berkata, “Bawalah dia kepadaku.” Ketika utusan itu datang kepadanya, dia (Yusuf) berkata, “Kembalilah kepada tuanmu dan tanyakan kepadanya bagaimana halnya perempuan-perempuan yang telah melukai tangannya. Sungguh, Tuhanku Maha Mengetahui tipu daya mereka.”'),
	(1647, 12, 51, 'قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُّنَّ يُوْسُفَ عَنْ نَّفْسِهٖۗ  قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوْۤءٍ ۗقَالَتِ امْرَاَتُ الْعَزِيْزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۖ اَنَا۠ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ وَاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ ', 'q<u>aa</u>la m<u>aa</u> kha<u>th</u>bukunna i<u>dz</u> r<u>aa</u>wadtunna yuusufa \'an nafsihi qulna <u>haa</u>sya lill<u>aa</u>hi m<u>aa</u> \'alimn<u>aa</u> \'alayhi min suu-in q<u>aa</u>lati imra-atu <strong>a</strong>l\'aziizi <strong>a</strong>l-<u>aa</u>na <u>h</u>a<u>sy</u>a<u>sh</u>a <strong>a</strong>l<u>h</u>aqqu an<u>aa</u> r<u>aa</u>wadtuhu \'an nafsihi wa-innahu lamina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Dia (raja) berkata (kepada perempuan-perempuan itu), “Bagaimana keadaanmu ketika kamu menggoda Yusuf untuk menundukkan dirinya?” Mereka berkata, “Mahasempurna Allah, kami tidak mengetahui sesuatu keburukan darinya.” Istri Al-Aziz berkata, “Sekarang jelaslah kebenaran itu, akulah yang menggoda dan merayunya, dan sesungguhnya dia termasuk orang yang benar.”'),
	(1648, 12, 52, 'ذٰلِكَ لِيَعْلَمَ اَنِّيْ لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْدِيْ كَيْدَ الْخَاۤىِٕنِيْنَ  ۔', '<u>dzaa</u>lika liya\'lama annii lam akhunhu bi<strong>a</strong>lghaybi wa-anna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii kayda <strong>a</strong>lkh<u>aa</u>-iniin<strong>a</strong>', '(Yusuf berkata), “Yang demikian itu agar dia (Al-Aziz) mengetahui bahwa aku benar-benar tidak mengkhianatinya ketika dia tidak ada (di rumah), dan bahwa Allah tidak meridai tipu daya orang-orang yang berkhianat. '),
	(1649, 12, 53, '۞ وَمَآ اُبَرِّئُ نَفْسِيْۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ ۢ بِالسُّوْۤءِ اِلَّا مَا رَحِمَ رَبِّيْۗ اِنَّ رَبِّيْ غَفُوْرٌ رَّحِيْمٌ', 'wam<u>aa</u> ubarri-u nafsii inna <strong>al</strong>nnafsa la-amm<u>aa</u>ratun bi<strong>al</strong>ssuu-i ill<u>aa</u> m<u>aa</u> ra<u>h</u>ima rabbii inna rabbii ghafuurun ra<u>h</u>iim<strong>un </strong>', 'Dan aku tidak (menyatakan) diriku bebas (dari kesalahan), karena sesungguhnya nafsu itu selalu mendorong kepada kejahatan, kecuali (nafsu) yang diberi rahmat oleh Tuhanku. Sesungguhnya Tuhanku Maha Pengampun, Maha Penyayang.'),
	(1650, 12, 54, 'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖٓ اَسْتَخْلِصْهُ لِنَفْسِيْۚ فَلَمَّا كَلَّمَهٗ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَكِيْنٌ اَمِيْنٌ', 'waq<u>aa</u>la <strong>a</strong>lmaliku i/tuunii bihi astakhli<u>sh</u>hu linafsii falamm<u>aa</u> kallamahu q<u>aa</u>la innaka <strong>a</strong>lyawma ladayn<u>aa</u> makiinun amiin<strong>un</strong>', 'Dan raja berkata, “Bawalah dia (Yusuf) kepadaku, agar aku memilih dia (sebagai orang yang dekat) kepadaku.”  Ketika dia (raja) telah bercakap-cakap dengan dia, dia (raja) berkata, “Sesungguhnya kamu (mulai) hari ini menjadi seorang yang berkedudukan tinggi di lingkungan kami dan dipercaya.”'),
	(1651, 12, 55, 'قَالَ اجْعَلْنِيْ عَلٰى خَزَاۤىِٕنِ الْاَرْضِۚ اِنِّيْ حَفِيْظٌ عَلِيْمٌ', 'q<u>aa</u>la ij\'alnii \'al<u>aa</u> khaz<u>aa</u>-ini <strong>a</strong>l-ar<u>dh</u>i innii <u>h</u>afii<u>zh</u>un \'aliim<strong>un</strong>', 'Dia (Yusuf) berkata, “Jadikanlah aku bendaharawan negeri (Mesir); karena sesungguhnya aku adalah orang yang pandai menjaga, dan berpengetahuan.”'),
	(1652, 12, 56, 'وَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَاۤءُۗ نُصِيْبُ بِرَحْمَتِنَا مَنْ نَّشَاۤءُ وَلَا نُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'waka<u>dzaa</u>lika makann<u>aa</u> liyuusufa fii <strong>a</strong>l-ar<u>dh</u>i yatabawwau minh<u>aa</u> <u>h</u>aytsu yasy<u>aa</u>u nu<u>sh</u>iibu bira<u>h</u>matin<u>aa</u> man nasy<u>aa</u>u wal<u>aa</u> nu<u>dh</u>ii\'u ajra <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan demikianlah Kami memberi kedudukan kepada Yusuf di negeri ini (Mesir); untuk tinggal di mana saja yang dia kehendaki. Kami melimpahkan rahmat kepada siapa yang Kami kehendaki dan Kami tidak menyia-nyiakan pahala orang yang berbuat baik.'),
	(1653, 12, 57, 'وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ ࣖ', 'wala-ajru <strong>a</strong>l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun<strong>a</strong>', 'Dan sungguh, pahala akhirat itu lebih baik bagi orang-orang yang beriman dan selalu bertakwa.'),
	(1654, 12, 58, 'وَجَاۤءَ اِخْوَةُ يُوْسُفَ فَدَخَلُوْا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهٗ مُنْكِرُوْنَ', 'waj<u>aa</u>-a ikhwatu yuusufa fadakhaluu \'alayhi fa\'arafahum wahum lahu munkiruun<strong>a</strong>', 'Dan saudara-saudara Yusuf datang (ke Mesir) lalu mereka masuk ke (tempat)nya. Maka dia (Yusuf) mengenal mereka, sedang mereka tidak mengenalinya (lagi) kepadanya.'),
	(1655, 12, 59, 'وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُوْنِيْ بِاَخٍ لَّكُمْ مِّنْ اَبِيْكُمْ ۚ اَلَا تَرَوْنَ اَنِّيْٓ اُوْفِى الْكَيْلَ وَاَنَا۠ خَيْرُ الْمُنْزِلِيْنَ ', 'walamm<u>aa</u> jahhazahum bijah<u>aa</u>zihim q<u>aa</u>la i/tuunii bi-akhin lakum min abiikum <strong>a</strong>l<u>aa</u> tarawna annii uufii <strong>a</strong>lkayla wa-an<u>aa</u> khayru <strong>a</strong>lmunziliin<strong>a</strong>', 'Dan ketika dia (Yusuf) menyiapkan bahan makanan untuk mereka, dia berkata, “Bawalah kepadaku saudaramu yang seayah dengan kamu (Bunyamin), tidakkah kamu melihat bahwa aku menyempurnakan takaran dan aku adalah penerima tamu yang terbaik?'),
	(1656, 12, 60, 'فَاِنْ لَّمْ تَأْتُوْنِيْ بِهٖ فَلَا كَيْلَ لَكُمْ عِنْدِيْ وَلَا تَقْرَبُوْنِ', 'fa-in lam ta/tuunii bihi fal<u>aa</u> kayla lakum \'indii wal<u>aa</u> taqrabuun<strong>i</strong>', 'Maka jika kamu tidak membawanya kepadaku, maka kamu tidak akan mendapat jatah (gandum) lagi dariku dan jangan kamu mendekatiku.”'),
	(1657, 12, 61, 'قَالُوْا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُوْنَ', 'q<u>aa</u>luu sanur<u>aa</u>widu \'anhu ab<u>aa</u>hu wa-inn<u>aa</u> laf<u>aa</u>\'iluun<strong>a</strong>', 'Mereka berkata, “Kami akan membujuk ayahnya (untuk membawanya) dan kami benar-benar akan melaksanakannya.”'),
	(1658, 12, 62, 'وَقَالَ لِفِتْيٰنِهِ اجْعَلُوْا بِضَاعَتَهُمْ فِيْ رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُوْنَهَآ اِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُوْنَ', 'waq<u>aa</u>la lifity<u>aa</u>nihi ij\'aluu bi<u>daa</u>\'atahum fii ri<u>haa</u>lihim la\'allahum ya\'rifuunah<u>aa</u> i<u>dzaa</u> inqalabuu il<u>aa</u> ahlihim la\'allahum yarji\'uun<strong>a</strong>', 'Dan dia (Yusuf) berkata kepada pelayan-pelayannya, “Masukkanlah barang-barang (penukar) mereka ke dalam karung-karungnya, agar mereka mengetahuinya apabila telah kembali kepada keluarganya, mudah-mudahan mereka kembali lagi.”'),
	(1659, 12, 63, 'فَلَمَّا رَجَعُوْٓا اِلٰٓى اَبِيْهِمْ قَالُوْا يٰٓاَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَآ اَخَانَا نَكْتَلْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ', 'falamm<u>aa</u> raja\'uu il<u>aa</u> abiihim q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> muni\'a minn<u>aa</u> <strong>a</strong>lkaylu fa-arsil ma\'an<u>aa</u> akh<u>aa</u>n<u>aa</u> naktal wa-inn<u>aa</u> lahu la<u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'Maka ketika mereka telah kembali kepada ayahnya (Yakub) mereka berkata, “Wahai ayah kami! Kami tidak akan mendapat jatah (gandum) lagi, (jika tidak membawa saudara kami), sebab itu biarkanlah saudara kami pergi bersama kami agar kami mendapat jatah, dan kami benar-benar akan menjaganya.”'),
	(1660, 12, 64, 'قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَآ اَمِنْتُكُمْ عَلٰٓى اَخِيْهِ مِنْ قَبْلُۗ فَاللّٰهُ خَيْرٌ حٰفِظًا وَّهُوَ اَرْحَمُ الرّٰحِمِيْنَ', 'q<u>aa</u>la hal <u>aa</u>manukum \'alayhi ill<u>aa</u> kam<u>aa</u> amintukum \'al<u>aa</u> akhiihi min qablu fa<strong>al</strong>l<u>aa</u>hu khayrun <u>haa</u>fi<u>zh</u>an wahuwa ar<u>h</u>amu <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Dia (Yakub) berkata, “Bagaimana aku akan mempercayakannya (Bunyamin) kepadamu, seperti aku telah mempercayakan saudaranya (Yusuf) kepada kamu dahulu?” Maka Allah adalah penjaga yang terbaik dan Dia Maha Penyayang di antara para penyayang.'),
	(1661, 12, 65, 'وَلَمَّا فَتَحُوْا مَتَاعَهُمْ وَجَدُوْا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۗ قَالُوْا يٰٓاَبَانَا مَا نَبْغِيْۗ هٰذِهٖ بِضَاعَتُنَا رُدَّتْ اِلَيْنَا وَنَمِيْرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَعِيْرٍۗ ذٰلِكَ كَيْلٌ يَّسِيْرٌ', 'walamm<u>aa</u> fata<u>h</u>uu mat<u>aa</u>\'ahum wajaduu bi<u>daa</u>\'atahum ruddat ilayhim q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> m<u>aa</u> nabghii h<u>aadz</u>ihi bi<u>daa</u>\'atun<u>aa</u> ruddat ilayn<u>aa</u> wanamiiru ahlan<u>aa</u> wana<u>h</u>fa<u>zh</u>u akh<u>aa</u>n<u>aa</u> wanazd<u>aa</u>du kayla ba\'iirin <u>dzaa</u>lika kaylun yasiir<strong>un</strong>', 'Dan ketika mereka membuka barang-barangnya, mereka menemukan barang-barang (penukar) mereka dikembalikan kepada mereka. Mereka berkata, “Wahai ayah kami! Apalagi yang kita inginkan. Ini barang-barang kita dikembalikan kepada kita, dan kita akan dapat memberi makan keluarga kita, dan kami akan memelihara saudara kami, dan kita akan mendapat tambahan jatah (gandum) seberat beban seekor unta. Itu suatu hal yang mudah (bagi raja Mesir).”'),
	(1662, 12, 66, 'قَالَ لَنْ اُرْسِلَهٗ مَعَكُمْ حَتّٰى تُؤْتُوْنِ مَوْثِقًا مِّنَ اللّٰهِ لَتَأْتُنَّنِيْ بِهٖٓ اِلَّآ اَنْ يُّحَاطَ بِكُمْۚ فَلَمَّآ اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ', 'q<u>aa</u>la lan ursilahu ma\'akum <u>h</u>att<u>aa</u> tu/tuuni mawtsiqan mina <strong>al</strong>l<u>aa</u>hi lata/tunnanii bihi ill<u>aa</u> an yu<u>hath</u>a bikum falamm<u>aa</u> <u>aa</u>tawhu mawtsiqahum q<u>aa</u>la <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> m<u>aa</u> naquulu wakiil<strong>un</strong>', 'Dia (Yakub) berkata, “Aku tidak akan melepaskannya (pergi) bersama kamu, sebelum kamu bersumpah kepadaku atas (nama) Allah, bahwa kamu pasti akan membawanya kepadaku kembali, kecuali jika kamu dikepung (musuh).” Setelah mereka mengucapkan sumpah, dia (Yakub) berkata, “Allah adalah saksi terhadap apa yang kita ucapkan.”'),
	(1663, 12, 67, 'وَقَالَ يٰبَنِيَّ لَا تَدْخُلُوْا مِنْۢ بَابٍ وَّاحِدٍ وَّادْخُلُوْا مِنْ اَبْوَابٍ مُّتَفَرِّقَةٍۗ وَمَآ اُغْنِيْ عَنْكُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ', 'waq<u>aa</u>la y<u>aa</u> baniyya l<u>aa</u> tadkhuluu min b<u>aa</u>bin w<u>aah</u>idin wa<strong>u</strong>dkhuluu min abw<u>aa</u>bin mutafarriqatin wam<u>aa</u> ughnii \'ankum mina <strong>al</strong>l<u>aa</u>hi min syay-in ini <strong>a</strong>l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi \'alayhi tawakkaltu wa\'alayhi falyatawakkali <strong>a</strong>lmutawakkiluun<strong>a</strong>', 'Dan dia (Yakub) berkata, “Wahai anak-anakku! Janganlah kamu masuk dari satu pintu gerbang, dan masuklah dari pintu-pintu gerbang yang berbeda; namun demikian aku tidak dapat mempertahankan kamu sedikit pun dari (takdir) Allah. Keputusan itu hanyalah bagi Allah. Kepada-Nya aku bertawakal dan kepada-Nya pula bertawakallah orang-orang yang bertawakal.”'),
	(1664, 12, 68, 'وَلَمَّا دَخَلُوْا مِنْ حَيْثُ اَمَرَهُمْ اَبُوْهُمْۗ مَا كَانَ يُغْنِيْ عَنْهُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً فِيْ نَفْسِ يَعْقُوْبَ قَضٰىهَاۗ وَاِنَّهٗ لَذُوْ عِلْمٍ لِّمَا عَلَّمْنٰهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ࣖ', 'walamm<u>aa</u> dakhaluu min <u>h</u>aytsu amarahum abuuhum m<u>aa</u> k<u>aa</u>na yughnii \'anhum mina <strong>al</strong>l<u>aa</u>hi min syay-in ill<u>aa</u> <u>haa</u>jatan fii nafsi ya\'quuba qa<u>daa</u>h<u>aa</u> wa-innahu la<u>dz</u>uu \'ilmin lim<u>aa</u> \'allamn<u>aa</u>hu wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan ketika mereka masuk sesuai dengan perintah ayah mereka, (masuknya mereka itu) tidak dapat menolak sedikit pun keputusan Allah, (tetapi itu) hanya suatu keinginan pada diri Yakub yang telah ditetapkannya. Dan sesungguhnya dia mempunyai pengetahuan, karena Kami telah mengajarkan kepadanya. Tetapi kebanyakan manusia tidak mengetahui.'),
	(1665, 12, 69, 'وَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنِّيْٓ اَنَا۠ اَخُوْكَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'walamm<u>aa</u> dakhaluu \'al<u>aa</u> yuusufa <u>aa</u>w<u>aa</u> ilayhi akh<u>aa</u>hu q<u>aa</u>la innii an<u>aa</u> akhuuka fal<u>aa</u> tabta-is bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan ketika mereka masuk ke (tempat) Yusuf, dia menempatkan saudaranya (Bunyamin) di tempatnya, dia (Yusuf) berkata, “Sesungguhnya aku adalah  saudaramu, jangan engkau bersedih hati terhadap apa yang telah mereka kerjakan.”'),
	(1666, 12, 70, 'فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِيْ رَحْلِ اَخِيْهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْعِيْرُ اِنَّكُمْ لَسَارِقُوْنَ', 'falamm<u>aa</u> jahhazahum bijah<u>aa</u>zihim ja\'ala <strong>al</strong>ssiq<u>aa</u>yata fii ra<u>h</u>li akhiihi tsumma a<u>dzdz</u>ana mu-a<u>dzdz</u>inun ayyatuh<u>aa</u> <strong>a</strong>l\'iiru innakum las<u>aa</u>riquun<strong>a</strong>', 'Maka ketika telah disiapkan bahan makanan untuk mereka, dia (Yusuf) memasukkan piala ke dalam karung saudaranya. Kemudian berteriaklah seseorang yang menyerukan, “Wahai kafilah! Sesungguhnya kamu pasti pencuri.” '),
	(1667, 12, 71, 'قَالُوْا وَاَقْبَلُوْا عَلَيْهِمْ مَّاذَا تَفْقِدُوْنَ', 'q<u>aa</u>luu wa-aqbaluu \'alayhim m<u>aatsaa</u> tafqiduun<strong>a</strong>', 'Mereka bertanya, sambil menghadap kepada mereka (yang menuduh), “Kamu kehilangan apa?”'),
	(1668, 12, 72, 'قَالُوْا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاۤءَ بِهٖ حِمْلُ بَعِيْرٍ وَّاَنَا۠ بِهٖ زَعِيْمٌ', 'q<u>aa</u>luu nafqidu <u>sh</u>uw<u>aa</u>\'a <strong>a</strong>lmaliki waliman j<u>aa</u>-a bihi <u>h</u>imlu ba\'iirin wa-an<u>aa</u> bihi za\'iim<strong>un</strong>', 'Mereka menjawab, “Kami kehilangan piala raja, dan siapa yang dapat mengembalikannya akan memperoleh (bahan makanan seberat) beban unta, dan aku jamin itu.”'),
	(1669, 12, 73, 'قَالُوْا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَّا جِئْنَا لِنُفْسِدَ فِى الْاَرْضِ وَمَا كُنَّا سَارِقِيْنَ', 'q<u>aa</u>luu ta<strong>al</strong>l<u>aa</u>hi laqad \'alimtum m<u>aa</u> ji/n<u>aa</u> linufsida fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> kunn<u>aa</u> s<u>aa</u>riqiin<strong>a</strong>', 'Mereka (saudara-saudara Yusuf) menjawab, “Demi Allah, sungguh, kamu mengetahui bahwa kami datang bukan untuk berbuat kerusakan di negeri ini dan kami bukanlah para pencuri.”'),
	(1670, 12, 74, 'قَالُوْا فَمَا جَزَاۤؤُهٗٓ اِنْ كُنْتُمْ كٰذِبِيْنَ', 'q<u>aa</u>luu fam<u>aa</u> jaz<u>aa</u>uhu in kuntum k<u>aadz</u>ibiin<strong>a</strong>', 'Mereka berkata, “Tetapi apa hukumannya jika kamu dusta?”'),
	(1671, 12, 75, 'قَالُوْا جَزَاۤؤُهٗ مَنْ وُّجِدَ فِيْ رَحْلِهٖ فَهُوَ جَزَاۤؤُهٗ ۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ', 'q<u>aa</u>luu jaz<u>aa</u>uhu man wujida fii ra<u>h</u>lihi fahuwa jaz<u>aa</u>uhu ka<u>dzaa</u>lika najzii <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Mereka menjawab, “Hukumannya ialah pada siapa ditemukan dalam karungnya (barang yang hilang itu), maka dia sendirilah menerima hukumannya. Demikianlah kami memberi hukuman kepada orang-orang zalim.”'),
	(1672, 12, 76, 'فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَاۤءِ اَخِيْهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِّعَاۤءِ اَخِيْهِۗ  كَذٰلِكَ كِدْنَا لِيُوْسُفَۗ مَا كَانَ لِيَأْخُذَ اَخَاهُ فِيْ دِيْنِ الْمَلِكِ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗنَرْفَعُ دَرَجَاتٍ مَّنْ نَّشَاۤءُۗ وَفَوْقَ ', 'fabada-a bi-aw\'iyatihim qabla wi\'<u>aa</u>-i akhiihi tsumma istakhrajah<u>aa</u> min wi\'<u>aa</u>-i akhiihi ka<u>dzaa</u>lika kidn<u>aa</u> liyuusufa m<u>aa</u> k<u>aa</u>na liya/khu<u>dz</u>a akh<u>aa</u>hu fii diini <strong>a</strong>lmaliki ill<u>aa</u> an yasy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu narfa\'u daraj<u>aa</u>tin man nasy<u>aa</u>u wafawqa kulli <u>dz</u>ii \'ilmin \'aliim<strong>un</strong>', 'Maka mulailah dia (memeriksa) karung-karung mereka sebelum (memeriksa) karung saudaranya sendiri, kemudian dia mengeluarkan (piala raja) itu dari karung saudaranya. Demikianlah Kami mengatur (rencana) untuk Yusuf. Dia tidak dapat menghukum saudaranya menurut undang-undang raja, kecuali Allah menghendakinya. Kami angkat derajat orang yang Kami kehendaki; dan di atas setiap orang yang berpengetahuan ada yang lebih mengetahui.'),
	(1673, 12, 77, '۞ قَالُوْٓا اِنْ يَّسْرِقْ فَقَدْ سَرَقَ اَخٌ لَّهٗ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوْسُفُ فِيْ نَفْسِهٖ وَلَمْ يُبْدِهَا لَهُمْۚ قَالَ اَنْتُمْ شَرٌّ مَّكَانًا ۚوَاللّٰهُ اَعْلَمُ بِمَا تَصِفُوْنَ', 'q<u>aa</u>luu in yasriq faqad saraqa akhun lahu min qablu fa-asarrah<u>aa</u> yuusufu fii nafsihi walam yubdih<u>aa</u> lahum q<u>aa</u>la antum syarrun mak<u>aa</u>nan wa<strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> ta<u>sh</u>ifuun<strong>a</strong>', 'Mereka berkata, “Jika dia mencuri, maka sungguh sebelum itu saudaranya pun pernah pula mencuri.” Maka Yusuf menyembunyikan (kejengkelan) dalam hatinya dan tidak ditampakkannya kepada mereka. Dia berkata (dalam hatinya), “Kedudukanmu justru lebih buruk. Dan Allah Maha Mengetahui apa yang kamu terangkan.”'),
	(1674, 12, 78, 'قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ اِنَّ لَهٗٓ اَبًا شَيْخًا كَبِيْرًا فَخُذْ اَحَدَنَا مَكَانَهٗ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ', 'q<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>l\'aziizu inna lahu aban syaykhan kabiiran fakhu<u>dz</u> a<u>h</u>adan<u>aa</u> mak<u>aa</u>nahu inn<u>aa</u> nar<u>aa</u>ka mina <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Mereka berkata, “Wahai Al-Aziz! Dia mempunyai ayah yang sudah lanjut usia, karena itu ambillah salah seorang di antara kami sebagai gantinya, sesungguhnya kami melihat engkau termasuk orang-orang yang berbuat baik.”'),
	(1675, 12, 79, 'قَالَ مَعَاذَ اللّٰهِ اَنْ نَّأْخُذَ اِلَّا مَنْ وَّجَدْنَا مَتَاعَنَا عِنْدَهٗٓ  ۙاِنَّآ اِذًا لَّظٰلِمُوْنَ ࣖ', 'q<u>aa</u>la ma\'<u>aadz</u>a <strong>al</strong>l<u>aa</u>hi an na/khu<u>dz</u>a ill<u>aa</u> man wajadn<u>aa</u> mat<u>aa</u>\'an<u>aa</u> \'indahu inn<u>aa</u> i<u>dz</u>an la<u>zhaa</u>limuun<strong>a</strong>', 'Dia (Yusuf) berkata, “Aku memohon perlindungan kepada Allah dari menahan (seseorang), kecuali orang yang kami temukan harta kami padanya, jika kami (berbuat) demikian, berarti kami orang yang zalim.”'),
	(1676, 12, 80, 'فَلَمَّا اسْتَا۟يْـَٔسُوْا مِنْهُ خَلَصُوْا نَجِيًّاۗ قَالَ كَبِيْرُهُمْ اَلَمْ تَعْلَمُوْٓا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَّوْثِقًا مِّنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُّمْ فِيْ يُوْسُفَ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ لِيْٓ اَبِي', 'falamm<u>aa</u> istay-asuu minhu khala<u>sh</u>uu najiyyan q<u>aa</u>la kabiiruhum alam ta\'lamuu anna ab<u>aa</u>kum qad akha<u>dz</u>a \'alaykum mawtsiqan mina <strong>al</strong>l<u>aa</u>hi wamin qablu m<u>aa</u> farra<u>th</u>tum fii yuusufa falan abra<u>h</u>a al-ar<u>dh</u>a <u>h</u>att<u>aa</u> ya/<u>dz</u>ana lii abii aw ya<u>h</u>kuma <strong>al</strong>l<u>aa</u>hu lii wahuwa khayru al<u>haa</u>kimiin<strong>a</strong>', 'Maka ketika mereka berputus asa darinya (putusan Yusuf) mereka menyendiri (sambil berunding) dengan berbisik-bisik. Yang tertua di antara mereka berkata, “Tidakkah kamu ketahui bahwa ayahmu telah mengambil janji dari kamu dengan (nama) Allah dan sebelum itu kamu telah menyia-nyiakan Yusuf? Sebab itu aku tidak akan meninggalkan negeri ini (Mesir), sampai ayahku mengizinkan (untuk kembali), atau Allah memberi keputusan terhadapku. Dan Dia adalah hakim yang terbaik.”'),
	(1677, 12, 81, 'اِرْجِعُوْٓا اِلٰٓى اَبِيْكُمْ فَقُوْلُوْا يٰٓاَبَانَآ اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَآ اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حٰفِظِيْنَ', 'irji\'uu il<u>aa</u> abiikum faquuluu y<u>aa</u> ab<u>aa</u>n<u>aa</u> inna ibnaka saraqa wam<u>aa</u> syahidn<u>aa</u> ill<u>aa</u> bim<u>aa</u> \'alimn<u>aa</u> wam<u>aa</u> kunn<u>aa</u> lilghaybi <u>haa</u>fi<u>zh</u>iin<strong>a</strong>', 'Kembalilah kepada ayahmu dan katakanlah, “Wahai ayah kami! Sesungguhnya anakmu telah mencuri dan kami hanya menyaksikan apa yang kami ketahui dan kami tidak mengetahui apa yang di balik itu.'),
	(1678, 12, 82, 'وَسْـَٔلِ الْقَرْيَةَ الَّتِيْ كُنَّا فِيْهَا وَالْعِيْرَ الَّتِيْٓ اَقْبَلْنَا فِيْهَاۗ وَاِنَّا لَصٰدِقُوْنَ', 'wa<strong>i</strong>s-ali <strong>a</strong>lqaryata <strong>al</strong>latii kunn<u>aa</u> fiih<u>aa</u> wa<strong>a</strong>l\'iira <strong>al</strong>latii aqbaln<u>aa</u> fiih<u>aa</u> wa-inn<u>aa</u> la<u>shaa</u>diquun<strong>a</strong>', 'Dan tanyalah (penduduk) negeri tempat kami berada, dan kafilah yang datang bersama kami. Dan kami adalah orang yang benar.”'),
	(1679, 12, 83, 'قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ  ۗعَسَى اللّٰهُ اَنْ يَّأْتِيَنِيْ بِهِمْ جَمِيْعًاۗ اِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ', 'q<u>aa</u>la bal sawwalat lakum anfusukum amran fa<u>sh</u>abrun jamiilun \'as<u>aa</u> <strong>al</strong>l<u>aa</u>hu an ya/tiyanii bihim jamii\'an innahu huwa <strong>a</strong>l\'aliimu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Dia (Yakub) berkata, “Sebenarnya hanya dirimu sendiri yang memandang baik urusan (yang buruk) itu. Maka (kesabaranku) adalah kesabaran yang baik. Mudah-mudahan Allah mendatangkan mereka semuanya kepadaku. Sungguh, Dialah Yang Maha Mengetahui, Mahabijaksana.”'),
	(1680, 12, 84, 'وَتَوَلّٰى عَنْهُمْ وَقَالَ يٰٓاَسَفٰى عَلٰى يُوْسُفَ وَابْيَضَّتْ عَيْنٰهُ مِنَ الْحُزْنِ فَهُوَ كَظِيْمٌ ', 'watawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> asaf<u>aa</u> \'al<u>aa</u> yuusufa wa<strong>i</strong>bya<u>dhdh</u>at \'ayn<u>aa</u>hu mina <strong>a</strong>l<u>h</u>uzni fahuwa ka<u>zh</u>iim<strong>un</strong>', 'Dan dia (Yakub) berpaling dari mereka (anak-anaknya) seraya berkata, “Aduhai dukacitaku terhadap Yusuf,” dan kedua matanya menjadi putih karena sedih. Dia diam menahan amarah (terhadap anak-anaknya).'),
	(1681, 12, 85, 'قَالُوْا تَاللّٰهِ تَفْتَؤُا تَذْكُرُ يُوْسُفَ حَتّٰى تَكُوْنَ حَرَضًا اَوْ تَكُوْنَ مِنَ الْهَالِكِيْنَ', 'q<u>aa</u>luu ta<strong>al</strong>l<u>aa</u>hi taftau ta<u>dz</u>kuru yuusufa <u>h</u>att<u>aa</u> takuuna <u>h</u>ara<u>dh</u>an aw takuuna mina <strong>a</strong>lh<u>aa</u>likiin<strong>a</strong>', 'Mereka berkata, “Demi Allah, engkau tidak henti-hentinya mengingat Yusuf, sehingga engkau (mengidap) penyakit berat atau engkau termasuk orang-orang yang akan binasa.”'),
	(1682, 12, 86, 'قَالَ اِنَّمَآ اَشْكُوْا بَثِّيْ وَحُزْنِيْٓ اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'q<u>aa</u>la innam<u>aa</u> asykuu batstsii wa<u>h</u>uznii il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wa-a\'lamu mina <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dia (Yakub) menjawab, “Hanya kepada Allah aku mengadukan kesusahan dan kesedihanku. Dan aku mengetahui dari Allah apa yang tidak kamu ketahui.'),
	(1683, 12, 87, 'يٰبَنِيَّ اذْهَبُوْا فَتَحَسَّسُوْا مِنْ يُّوْسُفَ وَاَخِيْهِ وَلَا تَا۟يْـَٔسُوْا مِنْ رَّوْحِ اللّٰهِ ۗاِنَّهٗ لَا يَا۟يْـَٔسُ مِنْ رَّوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكٰفِرُوْنَ', 'y<u>aa</u> baniyya i<u>dz</u>habuu fata<u>h</u>assasuu min yuusufa wa-akhiihi wal<u>aa</u> tay-asuu min raw<u>h</u>i <strong>al</strong>l<u>aa</u>hi innahu l<u>aa</u> yay-asu min raw<u>h</u>i <strong>al</strong>l<u>aa</u>hi ill<u>aa</u> <strong>a</strong>lqawmu <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Wahai anak-anakku! Pergilah kamu, carilah (berita) tentang Yusuf dan saudaranya dan jangan kamu berputus asa dari rahmat Allah. Sesungguhnya yang berputus asa dari rahmat Allah, hanyalah orang-orang yang kafir.”'),
	(1684, 12, 88, 'فَلَمَّا دَخَلُوْا عَلَيْهِ قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُّزْجٰىةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۗ اِنَّ اللّٰهَ يَجْزِى الْمُتَصَدِّقِيْنَ', 'falamm<u>aa</u> dakhaluu \'alayhi q<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>l\'aziizu massan<u>aa</u> wa-ahlan<u>aa</u> <strong>al</strong><u>dhdh</u>urru waji/n<u>aa</u> bibi<u>daa</u>\'atin muzj<u>aa</u>tin fa-awfi lan<u>aa</u> <strong>a</strong>lkayla wata<u>sh</u>addaq \'alayn<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha yajzii <strong>a</strong>lmuta<u>sh</u>addiqiin<strong>a</strong>', 'Maka ketika mereka masuk ke (tempat) Yusuf, mereka berkata, “Wahai Al-Aziz! Kami dan keluarga kami telah ditimpa kesengsaraan dan kami datang membawa barang-barang yang tidak berharga, maka penuhilah jatah (gandum) untuk kami, dan bersedekahlah kepada kami. Sesungguhnya Allah memberi balasan kepada orang yang bersedekah.”'),
	(1685, 12, 89, 'قَالَ هَلْ عَلِمْتُمْ مَّا فَعَلْتُمْ بِيُوْسُفَ وَاَخِيْهِ اِذْ اَنْتُمْ جَاهِلُوْنَ', 'q<u>aa</u>la hal \'alimtum m<u>aa</u> fa\'altum biyuusufa wa-akhiihi i<u>dz</u> antum j<u>aa</u>hiluun<strong>a</strong>', 'Dia (Yusuf) berkata, “Tahukah kamu (kejelekan) apa yang telah kamu perbuat terhadap Yusuf dan saudaranya karena kamu tidak menyadari (akibat) perbuatanmu itu?”'),
	(1686, 12, 90, 'قَالُوْٓا ءَاِنَّكَ لَاَنْتَ يُوْسُفُۗ قَالَ اَنَا۠ يُوْسُفُ وَهٰذَآ اَخِيْ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۗ اِنَّهٗ مَنْ يَّتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ', 'q<u>aa</u>luu a-innaka la-anta yuusufa q<u>aa</u>la an<u>aa</u> yuusufu wah<u>aadzaa</u> akhii qad manna <strong>al</strong>l<u>aa</u>hu \'alayn<u>aa</u> innahu man yattaqi waya<u>sh</u>bir fa-inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Mereka berkata, “Apakah engkau benar-benar Yusuf?” Dia (Yusuf) menjawab, “Aku Yusuf dan ini saudaraku. Sungguh, Allah telah melimpahkan karunia-Nya kepada kami. Sesungguhnya barangsiapa bertakwa dan bersabar, maka Sungguh, Allah tidak menyia-nyiakan pahala orang yang berbuat baik.”'),
	(1687, 12, 91, 'قَالُوْا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخٰطِـِٕيْنَ', 'q<u>aa</u>luu ta<strong>al</strong>l<u>aa</u>hi laqad <u>aa</u>tsaraka <strong>al</strong>l<u>aa</u>hu \'alayn<u>aa</u> wa-in kunn<u>aa</u> lakh<u>aath</u>i-iin<strong>a</strong>', 'Mereka berkata, “Demi Allah, sungguh Allah telah melebihkan engkau di atas kami, dan sesungguhnya kami adalah orang yang bersalah (berdosa).”'),
	(1688, 12, 92, 'قَالَ لَا تَثْرِيْبَ عَلَيْكُمُ الْيَوْمَۗ يَغْفِرُ اللّٰهُ لَكُمْ ۖوَهُوَ اَرْحَمُ الرّٰحِمِيْنَ', 'q<u>aa</u>la l<u>aa</u> tatsriiba \'alaykumu <strong>a</strong>lyawma yaghfiru <strong>al</strong>l<u>aa</u>hu lakum wahuwa ar<u>h</u>amu <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Dia (Yusuf) berkata, “Pada hari ini tidak ada cercaan terhadap kamu, mudah-mudahan Allah mengampuni kamu. Dan Dia Maha Penyayang di antara para penyayang.'),
	(1689, 12, 93, 'اِذْهَبُوْا بِقَمِيْصِيْ هٰذَا فَاَلْقُوْهُ عَلٰى وَجْهِ اَبِيْ يَأْتِ بَصِيْرًا ۚوَأْتُوْنِيْ بِاَهْلِكُمْ اَجْمَعِيْنَ ࣖ', 'i<u>dz</u>habuu biqamii<u>sh</u>ii h<u>aadzaa</u> fa-alquuhu \'al<u>aa</u> wajhi abii ya/ti ba<u>sh</u>iiran wa/tuunii bi-ahlikum ajma\'iin<strong>a</strong>', 'Pergilah kamu dengan membawa bajuku ini, lalu usapkan ke wajah ayahku, nanti dia akan melihat kembali; dan bawalah seluruh keluargamu kepadaku.”'),
	(1690, 12, 94, 'وَلَمَّا فَصَلَتِ الْعِيْرُ قَالَ اَبُوْهُمْ اِنِّيْ لَاَجِدُ رِيْحَ يُوْسُفَ لَوْلَآ اَنْ تُفَنِّدُوْنِ', 'walamm<u>aa</u> fa<u>sh</u>alati <strong>a</strong>l\'iiru q<u>aa</u>la abuuhum innii la-ajidu rii<u>h</u>a yuusufa lawl<u>aa</u> an tufanniduun<strong>i</strong>', 'Dan ketika kafilah itu telah keluar (dari negeri Mesir), ayah mereka berkata, “Sesungguhnya Aku mencium bau Yusuf, sekiranya kamu tidak menuduhku lemah akal (tentu kamu membenarkan aku).”'),
	(1691, 12, 95, 'قَالُوْا تَاللّٰهِ اِنَّكَ لَفِيْ ضَلٰلِكَ الْقَدِيْمِ', 'q<u>aa</u>luu ta<strong>al</strong>l<u>aa</u>hi innaka lafii <u>dh</u>al<u>aa</u>lika <strong>a</strong>lqadiim<strong>i</strong>', 'Mereka (keluarganya) berkata, “Demi Allah, sesungguhnya engkau masih dalam kekeliruanmu yang dahulu.”'),
	(1692, 12, 96, 'فَلَمَّآ اَنْ جَاۤءَ الْبَشِيْرُ اَلْقٰىهُ عَلٰى وَجْهِهٖ فَارْتَدَّ بَصِيْرًاۗ قَالَ اَلَمْ اَقُلْ لَّكُمْۙ اِنِّيْٓ اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ', 'falamm<u>aa</u> an j<u>aa</u>-a albasyiiru alq<u>aa</u>hu \'al<u>aa</u> wajhihi fa<strong>i</strong>rtadda ba<u>sh</u>iiran q<u>aa</u>la alam aqul lakum innii a\'lamu mina <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Maka ketika telah tiba pembawa kabar gembira itu, maka diusapkannya (baju itu) ke wajahnya (Yakub), lalu dia dapat melihat kembali. Dia (Yakub) berkata, “Bukankah telah aku katakan kepadamu, bahwa aku mengetahui dari Allah apa yang tidak kamu ketahui.”'),
	(1693, 12, 97, 'قَالُوْا يٰٓاَبَانَا اسْتَغْفِرْ لَنَا ذُنُوْبَنَآ اِنَّا كُنَّا خٰطِـِٕيْنَ', 'q<u>aa</u>la sawfa astaghfiru lakum rabbii innahu huwa <strong>a</strong>lghafuuru <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Mereka berkata, “Wahai ayah kami! Mohonkanlah ampunan untuk kami atas dosa-dosa kami, sesungguhnya kami adalah orang yang bersalah (berdosa).” '),
	(1694, 12, 98, 'قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبِّيْ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ', 'falamm<u>aa</u> dakhaluu \'al<u>aa</u> yuusufa <u>aa</u>w<u>aa</u> ilayhi abawayhi waq<u>aa</u>la udkhuluu mi<u>sh</u>ra in sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu <u>aa</u>miniin<strong>a</strong>', 'Dia (Yakub) berkata, “Aku akan memohonkan ampunan bagimu kepada Tuhanku. Sungguh, Dia Yang Maha Pengampun, Maha Penyayang.”'),
	(1695, 12, 99, 'فَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوْا مِصْرَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَ ۗ ', 'warafa\'a abawayhi \'al<u>aa</u> <strong>a</strong>l\'arsyi wakharruu lahu sujjadan waq<u>aa</u>la y<u>aa</u> abati h<u>aadzaa</u> ta/wiilu ru/y<u>aa</u>ya min qablu qad ja\'alah<u>aa</u> rabbii <u>h</u>aqqan waqad a<u>h</u>sana bii i<u>dz</u> akhrajanii mina <strong>al</strong>ssijni waj<u>aa</u>-a bikum mina <strong>a</strong>lbadwi min ba\'di an nazagha <strong>al</strong>sysyay<u>th</u><u>aa</u>nu baynii wabayna ikhwatii inna rabbii la<u>th</u>iifun lim<u>aa</u> yasy<u>aa</u>u innahu huwa <strong>a</strong>l\'aliimu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Maka ketika mereka masuk ke (tempat) Yusuf, dia merangkul (dan menyiapkan tempat untuk) kedua orang tuanya seraya berkata, “Masuklah kamu ke negeri Mesir, insya Allah dalam keadaan aman.”'),
	(1696, 12, 100, 'وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوْا لَهٗ سُجَّدًاۚ وَقَالَ يٰٓاَبَتِ هٰذَا تَأْوِيْلُ رُءْيَايَ مِنْ قَبْلُ  ۖقَدْ جَعَلَهَا رَبِّيْ حَقًّاۗ وَقَدْ اَحْسَنَ بِيْٓ اِذْ اَخْرَجَنِيْ مِنَ السِّجْنِ وَجَاۤءَ بِكُمْ مِّنَ الْبَدْوِ مِنْۢ بَعْدِ اَن', 'rabbi qad <u>aa</u>taytanii mina <strong>a</strong>lmulki wa\'allamtanii min ta/wiili <strong>a</strong>l-a<u>haa</u>diitsi f<u>aath</u>ira <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i anta waliyyii fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati tawaffanii musliman wa-al<u>h</u>iqnii bi<strong>al</strong><u>shshaa</u>li<u>h</u>iin', 'Dan dia menaikkan kedua orang tuanya ke atas singgasana. Dan mereka (semua) tunduk bersujud kepadanya (Yusuf). Dan dia (Yusuf) berkata, “Wahai ayahku! Inilah takwil mimpiku yang dahulu itu. Dan sesungguhnya Tuhanku telah menjadikannya kenyataan. Sesungguhnya Tuhanku telah berbuat baik kepadaku, ketika Dia membebaskan aku dari penjara dan ketika membawa kamu dari dusun, setelah setan merusak (hubungan) antara aku dengan saudara-saudaraku. Sungguh, Tuhanku Mahalembut terhadap apa yang Dia kehendaki. Sungguh, Dia Yang Maha Mengetahui, Mahabijaksana.'),
	(1697, 12, 101, '۞ رَبِّ قَدْ اٰتَيْتَنِيْ مِنَ الْمُلْكِ وَعَلَّمْتَنِيْ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۚ فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِۗ اَنْتَ وَلِيّٖ فِى الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّنِيْ مُسْلِمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ', 'rabbi qad <u>aa</u>taytanii mina <strong>a</strong>lmulki wa\'allamtanii min ta/wiili <strong>a</strong>l-a<u>haa</u>diitsi f<u>aath</u>ira <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i anta waliyyii fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati tawaffanii musliman wa-al<u>h</u>iqnii bi<strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Tuhanku, sesungguhnya Engkau telah menganugerahkan kepadaku sebagian kekuasaan dan telah mengajarkan kepadaku sebagian takwil mimpi. (Wahai Tuhan) pencipta langit dan bumi, Engkaulah pelindungku di dunia dan di akhirat, wafatkanlah aku dalam keadaan muslim dan gabungkanlah aku dengan orang yang saleh.” '),
	(1698, 12, 102, 'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُوْٓا اَمْرَهُمْ وَهُمْ يَمْكُرُوْنَ', '<u>dzaa</u>lika min anb<u>aa</u>-i <strong>a</strong>lghaybi nuu<u>h</u>iihi ilayka wam<u>aa</u> kunta ladayhim i<u>dz</u> ajma\'uu amrahum wahum yamkuruun<strong>a</strong>', 'Itulah sebagian berita gaib yang Kami wahyukan kepadamu (Muhammad); padahal engkau tidak berada di samping mereka, ketika mereka bersepakat mengatur tipu muslihat (untuk memasukkan Yusuf ke dalam sumur).'),
	(1699, 12, 103, 'وَمَآ اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِيْنَ', 'wam<u>aa</u> aktsaru <strong>al</strong>nn<u>aa</u>si walaw <u>h</u>ara<u>sh</u>ta bimu/miniin<strong>a</strong>', 'Dan kebanyakan manusia tidak akan beriman walaupun engkau sangat           menginginkannya.'),
	(1700, 12, 104, 'وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۗ اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ ࣖ', 'wam<u>aa</u> tas-aluhum \'alayhi min ajrin in huwa ill<u>aa</u> <u>dz</u>ikrun lil\'<u>aa</u>lamiin<strong>a</strong>', 'Dan engkau tidak meminta imbalan apa pun kepada mereka (terhadap seruanmu ini), sebab (seruan) itu adalah pengajaran bagi seluruh alam.'),
	(1701, 12, 105, 'وَكَاَيِّنْ مِّنْ اٰيَةٍ فِى السَّمٰوٰتِ وَالْاَرْضِ يَمُرُّوْنَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُوْنَ', 'waka-ayyin min <u>aa</u>yatin fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i yamurruuna \'alayh<u>aa</u> wahum \'anh<u>aa</u> mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan berapa banyak tanda-tanda (kebesaran Allah) di langit dan di bumi yang mereka lalui, namun mereka berpaling daripadanya.'),
	(1702, 12, 106, 'وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُّشْرِكُوْنَ', 'wam<u>aa</u> yu/minu aktsaruhum bi<strong>al</strong>l<u>aa</u>hi ill<u>aa</u> wahum musyrikuun<strong>a</strong>', 'Dan kebanyakan mereka tidak beriman kepada Allah, bahkan mereka mempersekutukan-Nya.'),
	(1703, 12, 107, 'اَفَاَمِنُوْٓا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِّنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'afa-aminuu an ta/tiyahum gh<u>aa</u>syiyatun min \'a<u>dzaa</u>bi <strong>al</strong>l<u>aa</u>hi aw ta/tiyahumu <strong>al</strong>ss<u>aa</u>\'atu baghtatan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Apakah mereka merasa aman dari kedatangan siksa Allah yang meliputi mereka, atau kedatangan Kiamat kepada mereka secara mendadak, sedang mereka tidak menyadarinya?'),
	(1704, 12, 108, 'قُلْ هٰذِهٖ سَبِيْلِيْٓ اَدْعُوْٓا اِلَى اللّٰهِ ۗعَلٰى بَصِيْرَةٍ اَنَا۠ وَمَنِ اتَّبَعَنِيْ ۗوَسُبْحٰنَ اللّٰهِ وَمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَ', 'qul h<u>aadz</u>ihi sabiilii ad\'uu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> ba<u>sh</u>iiratin an<u>aa</u> wamani ittaba\'anii wasub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> an<u>aa</u> mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Katakanlah (Muhammad), “Inilah jalanku, aku dan orang-orang yang mengikutiku mengajak (kamu) kepada Allah dengan yakin, Mahasuci Allah, dan aku tidak termasuk orang-orang musyrik.”'),
	(1705, 12, 109, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ مِّنْ اَهْلِ الْقُرٰىۗ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اتَّقَوْاۗ اَفَلَا ', 'wam<u>aa</u> arsaln<u>aa</u> min qablika ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim min ahli <strong>a</strong>lqur<u>aa</u> afalam yasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong>la<u>dz</u>iina min qablihim walad<u>aa</u>ru <strong>a</strong>l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina ittaqaw afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Dan Kami tidak mengutus sebelummu (Muhammad), melainkan orang laki-laki yang Kami berikan wahyu kepadanya di antara penduduk negeri. Tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul). Dan sungguh, negeri akhirat itu lebih baik bagi orang yang bertakwa. Tidakkah kamu mengerti?'),
	(1706, 12, 110, 'حَتّٰٓى اِذَا اسْتَا۟يْـَٔسَ الرُّسُلُ وَظَنُّوْٓا اَنَّهُمْ قَدْ كُذِبُوْا جَاۤءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَّشَاۤءُ ۗوَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِيْنَ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> istay-asa <strong>al</strong>rrusulu wa<u>zh</u>annuu annahum qad ku<u>dz</u>ibuu j<u>aa</u>-ahum na<u>sh</u>run<u>aa</u> fanujjiya man nasy<u>aa</u>u wal<u>aa</u> yuraddu ba/sun<u>aa</u> \'ani <strong>a</strong>lqawmi <strong>a</strong>lmujrimiin<strong>a</strong>', 'Sehingga apabila para rasul tidak mempunyai harapan lagi (tentang keimanan kaumnya) dan telah meyakini bahwa mereka telah didustakan, datanglah kepada mereka (para rasul) itu pertolongan Kami, lalu diselamatkan orang yang Kami kehendaki. Dan siksa Kami tidak dapat ditolak dari orang yang berdosa.'),
	(1707, 12, 111, 'لَقَدْ كَانَ فِيْ قَصَصِهِمْ عِبْرَةٌ لِّاُولِى الْاَلْبَابِۗ مَا كَانَ حَدِيْثًا يُّفْتَرٰى وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ كُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ࣖ', 'laqad k<u>aa</u>na fii qa<u>sh</u>a<u>sh</u>ihim \'ibratun li-ulii <strong>a</strong>l-alb<u>aa</u>bi m<u>aa</u> k<u>aa</u>na <u>h</u>adiitsan yuftar<u>aa</u> wal<u>aa</u>kin ta<u>sh</u>diiqa <strong>al</strong>la<u>dz</u>ii bayna yadayhi wataf<u>sh</u>iila kulli syay-in wahudan wara<u>h</u>matan liqawmin yu/minuun<strong>a</strong>', 'Sungguh, pada kisah-kisah mereka itu terdapat pengajaran bagi orang yang mempunyai akal. (Al-Qur\'an) itu bukanlah cerita yang dibuat-buat, tetapi membenarkan (kitab-kitab) yang sebelumnya, menjelaskan segala sesuatu, dan (sebagai) petunjuk dan rahmat bagi orang-orang yang beriman.'),
	(1708, 13, 1, 'الۤمّۤرٰۗ تِلْكَ اٰيٰتُ الْكِتٰبِۗ وَالَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ ', 'alif-l<u>aa</u>m-miim-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi wa<strong>a</strong>lla<u>dz</u>ii unzila ilayka min rabbika <strong>a</strong>l<u>h</u>aqqu wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> yu/minuun<strong>a</strong>', 'Alif Lam Mim Ra. Ini adalah ayat-ayat Kitab (Al-Qur\'an). Dan (Kitab) yang diturunkan kepadamu (Muhammad) dari Tuhanmu itu adalah benar; tetapi kebanyakan manusia tidak beriman (kepadanya).'),
	(1709, 13, 2, 'اَللّٰهُ الَّذِيْ رَفَعَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ  كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيٰتِ لَعَلَّكُمْ بِلِقَاۤءِ رَبِّكُمْ تُوْقِنُوْنَ ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii rafa\'a <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti bighayri \'amadin tarawnah<u>aa</u> tsumma istaw<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'arsyi wasakhkhara <strong>al</strong>sysyamsa wa<strong>a</strong>lqamara kullun yajrii li-ajalin musamman yudabbiru <strong>a</strong>l-amra yufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti la\'allakum biliq<u>aa</u>-i rabbikum tuuqinuun<strong>a</strong>', 'Allah yang meninggikan langit tanpa tiang (sebagaimana) yang kamu lihat, kemudian Dia bersemayam di atas ‘Arsy. Dia menundukkan matahari dan bulan; masing-masing beredar menurut waktu yang telah ditentukan. Dia mengatur urusan (makhluk-Nya), dan menjelaskan tanda-tanda (kebesaran-Nya), agar kamu yakin akan pertemuan dengan Tuhanmu.'),
	(1710, 13, 3, 'وَهُوَ الَّذِيْ مَدَّ الْاَرْضَ وَجَعَلَ فِيْهَا رَوَاسِيَ وَاَنْهٰرًا ۗوَمِنْ كُلِّ الثَّمَرٰتِ جَعَلَ فِيْهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِى الَّيْلَ النَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii madda <strong>a</strong>l-ar<u>dh</u>a waja\'ala fiih<u>aa</u> raw<u>aa</u>siya wa-anh<u>aa</u>ran wamin kulli <strong>al</strong>tstsamar<u>aa</u>ti ja\'ala fiih<u>aa</u> zawjayni itsnayni yughsyii <strong>al</strong>layla <strong>al</strong>nnah<u>aa</u>ra inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yatafakkaruun<strong>a</strong>', 'Dan Dia yang menghamparkan bumi dan menjadikan gunung-gunung dan sungai-sungai di atasnya. Dan padanya Dia menjadikan semua buah-buahan berpasang-pasangan; Dia menutupkan malam kepada siang. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang berpikir.'),
	(1711, 13, 4, 'وَفِى الْاَرْضِ قِطَعٌ مُّتَجٰوِرٰتٌ وَّجَنّٰتٌ مِّنْ اَعْنَابٍ وَّزَرْعٌ وَّنَخِيْلٌ صِنْوَانٌ وَّغَيْرُ صِنْوَانٍ يُّسْقٰى بِمَاۤءٍ وَّاحِدٍۙ وَّنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِى الْاُكُلِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ', 'wafii <strong>a</strong>l-ar<u>dh</u>i qi<u>th</u>a\'un mutaj<u>aa</u>wir<u>aa</u>tun wajann<u>aa</u>tun min a\'n<u>aa</u>bin wazar\'un wanakhiilun <u>sh</u>inw<u>aa</u>nun waghayru <u>sh</u>inw<u>aa</u>nin yusq<u>aa</u> bim<u>aa</u>-in w<u>aah</u>idin wanufa<u>dhdh</u>ilu ba\'<u>dh</u>ah<u>aa</u> \'al<u>aa</u> ba\'<u>dh</u>in fii <strong>a</strong>lukuli inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun<strong>a</strong>', 'Dan di bumi terdapat bagian-bagian yang berdampingan, kebun-kebun anggur, tanaman-tanaman, pohon kurma yang bercabang, dan yang tidak bercabang; disirami dengan air yang sama, tetapi Kami lebihkan tanaman yang satu dari yang lainnya dalam hal rasanya. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang mengerti.'),
	(1712, 13, 5, '۞ وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَابًا ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْۚ وَاُولٰۤىِٕكَ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ', 'wa-in ta\'jab fa\'ajabun qawluhum a-i<u>dzaa</u> kunn<u>aa</u> tur<u>aa</u>ban a-inn<u>aa</u> lafii khalqin jadiidin ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina kafaruu birabbihim waul<u>aa</u>-ika <strong>a</strong>l-aghl<u>aa</u>lu fii a\'n<u>aa</u>qihim waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>al</strong>nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Dan jika engkau merasa heran, maka yang mengherankan adalah ucapan mereka, “Apabila kami telah menjadi tanah, apakah kami akan (dikembalikan) menjadi makhluk yang baru?” Mereka itulah yang ingkar kepada Tuhannya; dan mereka itulah (yang dilekatkan) belenggu di lehernya. Mereka adalah penghuni neraka, mereka kekal di dalamnya.'),
	(1713, 13, 6, 'وَيَسْتَعْجِلُوْنَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلٰتُۗ وَاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ لِّلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَدِيْدُ الْعِقَابِ ', 'wayasta\'jiluunaka bi<strong>al</strong>ssayyi-ati qabla <strong>a</strong>l<u>h</u>asanati waqad khalat min qablihimu <strong>a</strong>lmatsul<u>aa</u>tu wa-inna rabbaka la<u>dz</u>uu maghfiratin li<strong>l</strong>nn<u>aa</u>si \'al<u>aa</u> <u>zh</u>ulmihim wa-inna rabbaka lasyadiidu <strong>a</strong>l\'iq<u>aa</u>b<strong>i</strong>', 'Dan mereka meminta kepadamu agar dipercepat (datangnya) siksaan, sebelum (mereka meminta) kebaikan, padahal telah terjadi bermacam-macam contoh siksaan sebelum mereka. Sungguh, Tuhanmu benar-benar memiliki ampunan bagi manusia atas kezaliman mereka, dan sungguh, Tuhanmu sangat keras siksaan-Nya.'),
	(1714, 13, 7, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ اِنَّمَآ اَنْتَ مُنْذِرٌ وَّلِكُلِّ قَوْمٍ هَادٍ ࣖ ', 'wayaquulu <strong>al</strong>la<u>dz</u>iina kafaruu lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi innam<u>aa</u> anta mun<u>dz</u>irun walikulli qawmin h<u>aa</u>d<strong>in</strong>', 'Dan orang-orang kafir berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu tanda (mukjizat) dari Tuhannya?” Sesungguhnya engkau hanyalah seorang pemberi peringatan; dan bagi setiap kaum ada orang yang memberi petunjuk.'),
	(1715, 13, 8, 'اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغِيْضُ الْاَرْحَامُ وَمَا تَزْدَادُ ۗوَكُلُّ شَيْءٍ عِنْدَهٗ بِمِقْدَارٍ ', '<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> ta<u>h</u>milu kullu unts<u>aa</u> wam<u>aa</u> taghii<u>dh</u>u <strong>a</strong>l-ar<u>haa</u>mu wam<u>aa</u> tazd<u>aa</u>du wakullu syay-in \'indahu bimiqd<u>aa</u>r<strong>in</strong>', 'Allah mengetahui apa yang dikandung oleh setiap perempuan, apa yang kurang sempurna dan apa yang bertambah dalam rahim. Dan segala sesuatu ada ukuran di sisi-Nya.'),
	(1716, 13, 9, 'عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيْرُ الْمُتَعَالِ ', '\'<u>aa</u>limu <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati <strong>a</strong>lkabiiru <strong>a</strong>lmuta\'<u>aa</u>l<strong>i</strong>', '(Allah) Yang mengetahui semua yang gaib dan yang nyata; Yang Mahabesar, Mahatinggi.'),
	(1717, 13, 10, 'سَوَاۤءٌ مِّنْكُمْ مَّنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهٖ وَمَنْ هُوَ مُسْتَخْفٍۢ بِالَّيْلِ وَسَارِبٌۢ بِالنَّهَارِ ', 'saw<u>aa</u>un minkum man asarra <strong>a</strong>lqawla waman jahara bihi waman huwa mustakhfin bi<strong>a</strong>llayli was<u>aa</u>ribun bi<strong>al</strong>nnah<u>aa</u>r<strong>i</strong>', 'Sama saja (bagi Allah), siapa di antaramu yang merahasiakan ucapannya dan siapa yang berterus terang dengannya; dan siapa yang bersembunyi pada malam hari dan yang berjalan pada siang hari.'),
	(1718, 13, 11, 'لَهٗ مُعَقِّبٰتٌ مِّنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ يَحْفَظُوْنَهٗ مِنْ اَمْرِ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۗ وَاِذَآ اَرَادَ اللّٰهُ بِقَوْمٍ سُوْۤءًا فَلَا مَرَدَّ لَهٗ ۚوَمَا لَهُمْ مِّنْ ', 'lahu mu\'aqqib<u>aa</u>tun min bayni yadayhi wamin khalfihi ya<u>h</u>fa<u>zh</u>uunahu min amri <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yughayyiru m<u>aa</u> biqawmin <u>h</u>att<u>aa</u> yughayyiruu m<u>aa</u> bi-anfusihim wa-i<u>dzaa</u> ar<u>aa</u>da <strong>al</strong>l<u>aa</u>hu biqawmin suu-an fal<u>aa</u> maradda lahu wam<u>aa</u> lahum min duunihi min w<u>aa</u>l<strong>in</strong>', 'Baginya (manusia) ada malaikat-malaikat yang selalu menjaganya bergiliran, dari depan dan belakangnya. Mereka menjaganya atas perintah Allah. Sesungguhnya Allah tidak akan mengubah keadaan suatu kaum sebelum mereka mengubah keadaan diri mereka sendiri. Dan apabila Allah menghendaki keburukan terhadap suatu kaum, maka tak ada yang dapat menolaknya dan tidak ada pelindung bagi mereka selain Dia.'),
	(1719, 13, 12, 'هُوَ الَّذِيْ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنْشِئُ السَّحَابَ الثِّقَالَۚ  ', 'huwa <strong>al</strong>la<u>dz</u>ii yuriikumu <strong>a</strong>lbarqa khawfan wa<u>th</u>ama\'an wayunsyi-u <strong>al</strong>ssa<u>haa</u>ba <strong>al</strong>tstsiq<u>aa</u>l<strong>a</strong>', 'Dialah yang memperlihatkan kilat kepadamu, yang menimbulkan ketakutan dan harapan, dan Dia menjadikan mendung. '),
	(1720, 13, 13, 'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهٖ وَالْمَلٰۤىِٕكَةُ مِنْ خِيْفَتِهٖۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيْبُ بِهَا مَنْ يَّشَاۤءُ وَهُمْ يُجَادِلُوْنَ فِى اللّٰهِ ۚوَهُوَ شَدِيْدُ الْمِحَالِۗ  ', 'wayusabbi<u>h</u>u <strong>al</strong>rra\'du bi<u>h</u>amdihi wa<strong>a</strong>lmal<u>aa</u>-ikatu min khiifatihi wayursilu <strong>al</strong><u>shsh</u>aw<u>aa</u>\'iqa fayu<u>sh</u>iibu bih<u>aa</u> man yasy<u>aa</u>u wahum yuj<u>aa</u>diluuna fii <strong>al</strong>l<u>aa</u>hi wahuwa syadiidu <strong>a</strong>lmi<u>haa</u>l<strong>i</strong>', 'Dan guruh bertasbih memuji-Nya, (demikian pula) para malaikat karena takut kepada-Nya, dan Allah melepaskan halilintar, lalu menimpakannya kepada siapa yang Dia kehendaki, sementara mereka berbantah-bantahan tentang Allah, dan Dia Mahakeras siksaan-Nya.'),
	(1721, 13, 14, 'لَهٗ دَعْوَةُ الْحَقِّۗ وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَجِيْبُوْنَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَاۤءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهٖۗ وَمَا دُعَاۤءُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ ', 'lahu da\'watu <strong>a</strong>l<u>h</u>aqqi wa<strong>a</strong>lla<u>dz</u>iina yad\'uuna min duunihi l<u>aa</u> yastajiibuuna lahum bisyay-in ill<u>aa</u> kab<u>aa</u>si<u>th</u>i kaffayhi il<u>aa</u> <strong>a</strong>lm<u>aa</u>-i liyablugha f<u>aa</u>hu wam<u>aa</u> huwa bib<u>aa</u>lighihi wam<u>aa</u> du\'<u>aa</u>u <strong>a</strong>lk<u>aa</u>firiina ill<u>aa</u> fii <u>dh</u>al<u>aa</u>l<strong>in</strong>', 'Hanya kepada Allah doa yang benar. Berhala-berhala yang mereka sembah selain Allah tidak dapat mengabulkan apa pun bagi mereka, tidak ubahnya seperti orang yang membukakan kedua telapak tangannya ke dalam air agar (air) sampai ke mulutnya. Padahal air itu tidak akan sampai ke mulutnya. Dan doa orang-orang kafir itu, hanyalah sia-sia belaka.'),
	(1722, 13, 15, 'وَلِلّٰهِ يَسْجُدُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّظِلٰلُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ ۩', 'walill<u>aa</u>hi yasjudu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <u>th</u>aw\'an wakarhan wa<u>zh</u>il<u>aa</u>luhum bi<strong>a</strong>lghuduwwi wa<strong>a</strong>l-<u>aasaa</u>l<strong>i</strong>', 'Dan semua sujud kepada Allah baik yang di langit maupun yang di bumi, baik dengan kemauan sendiri maupun terpaksa (dan sujud pula) bayang-bayang mereka, pada waktu pagi dan petang hari.'),
	(1723, 13, 16, 'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۗقُلْ اَفَاتَّخَذْتُمْ مِّنْ دُوْنِهٖٓ اَوْلِيَاۤءَ لَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ نَفْعًا وَّلَا ضَرًّاۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ اَمْ هَلْ تَسْتَوِى الظُّلُمٰتُ وَالنُّوْرُ ە', 'qul man rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i quli <strong>al</strong>l<u>aa</u>hu qul afa<strong>i</strong>ttakha<u>dz</u>tum min duunihi awliy<u>aa</u>-a l<u>aa</u> yamlikuuna li-anfusihim naf\'an wal<u>aa</u> <u>dh</u>arran qul hal yastawii <strong>a</strong>l-a\'m<u>aa</u> wa<strong>a</strong>lba<u>sh</u>iiru am hal tastawii <strong>al</strong><u>zhzh</u>ulum<u>aa</u>tu wa<strong>al</strong>nnuuru am ja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a khalaquu kakhalqihi fatasy<u>aa</u>baha <strong>a</strong>lkhalqu \'alayhim quli <strong>al</strong>l<u>aa</u>hu kh<u>aa</u>liqu kulli syay-in wahuwa <strong>a</strong>lw<u>aah</u>idu <strong>a</strong>lqahh<u>aa</u>r<strong>u</strong>', 'Katakanlah (Muhammad), “Siapakah Tuhan langit dan bumi?” Katakanlah, “Allah.”  Katakanlah, “Pantaskah kamu mengambil pelindung-pelindung selain Allah, padahal mereka tidak kuasa mendatangkan manfaat maupun menolak mudarat bagi dirinya sendiri?”  Katakanlah, “Samakah orang yang buta dengan yang dapat melihat? Atau samakah yang gelap dengan yang terang? Apakah mereka menjadikan sekutu-sekutu bagi Allah yang dapat menciptakan seperti ciptaan-Nya sehingga kedua ciptaan itu serupa menurut pandangan mereka?” Katakanlah, “Allah adalah Pencipta segala sesuatu dan Dia Tuhan Yang Maha Esa, Mahaperkasa.”'),
	(1724, 13, 17, 'اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَالَتْ اَوْدِيَةٌ ۢ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَّابِيًا ۗوَمِمَّا يُوْقِدُوْنَ عَلَيْهِ فِى النَّارِ ابْتِغَاۤءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِّثْلُهٗ  ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَ ', 'anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fas<u>aa</u>lat awdiyatun biqadarih<u>aa</u> fa<strong>i</strong><u>h</u>tamala <strong>al</strong>ssaylu zabadan r<u>aa</u>biyan wamimm<u>aa</u> yuuqiduuna \'alayhi fii <strong>al</strong>nn<u>aa</u>ri ibtigh<u>aa</u>-a <u>h</u>ilyatin aw mat<u>aa</u>\'in zabadun mitsluhu ka<u>dzaa</u>lika ya<u>dh</u>ribu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l<u>h</u>aqqa wa<strong>a</strong>lb<u>aath</u>ila fa-amm<u>aa</u> <strong>al</strong>zzabadu faya<u>dz</u>habu juf<u>aa</u>-an wa-amm<u>aa</u> m<u>aa</u> yanfa\'u <strong>al</strong>nn<u>aa</u>sa fayamkutsu fii <strong>a</strong>l-ar<u>dh</u>i ka<u>dzaa</u>lika ya<u>dh</u>ribu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l-amts<u>aa</u>l<strong>a</strong>', 'Allah telah menurunkan air (hujan) dari langit, maka mengalirlah ia (air) di lembah-lembah menurut ukurannya, maka arus itu membawa buih yang mengambang. Dan dari apa (logam) yang mereka lebur dalam api untuk membuat perhiasan atau alat-alat, ada (pula) buihnya seperti (buih arus) itu. Demikianlah Allah membuat perumpamaan tentang yang benar dan yang batil. Adapun buih, akan hilang sebagai sesuatu yang tidak ada gunanya; tetapi yang bermanfaat bagi manusia, akan tetap ada di bumi. Demikianlah Allah membuat perumpamaan.'),
	(1725, 13, 18, 'لِلَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمُ الْحُسْنٰىۗ وَالَّذِيْنَ لَمْ يَسْتَجِيْبُوْا لَهٗ لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ ۗ اُولٰۤىِٕكَ لَهُمْ سُوْۤءُ الْحِسَابِ ەۙ وَمَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِ', 'lilla<u>dz</u>iina istaj<u>aa</u>buu lirabbihimu <strong>a</strong>l<u>h</u>usn<u>aa</u> wa<strong>a</strong>lla<u>dz</u>iina lam yastajiibuu lahu law anna lahum m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an wamitslahu ma\'ahu la<strong>i</strong>ftadaw bihi ul<u>aa</u>-ika lahum suu-u <strong>a</strong>l<u>h</u>is<u>aa</u>bi wama/w<u>aa</u>hum jahannamu wabi/sa <strong>a</strong>lmih<u>aa</u>d<strong>u</strong>', 'Bagi orang-orang yang memenuhi seruan Tuhan, mereka (disediakan) balasan yang baik. Dan orang-orang yang tidak memenuhi seruan-Nya, sekiranya mereka memiliki semua yang ada di bumi dan (ditambah) sebanyak itu lagi, niscaya mereka akan menebus dirinya dengan itu. Orang-orang itu mendapat hisab (perhitungan) yang buruk dan tempat kediaman mereka Jahanam, dan itulah seburuk-buruk tempat kediaman.'),
	(1726, 13, 19, '۞ اَفَمَنْ يَّعْلَمُ اَنَّمَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِۙ  ', 'afaman ya\'lamu annam<u>aa</u> unzila ilayka min rabbika <strong>a</strong>l<u>h</u>aqqu kaman huwa a\'m<u>aa</u> innam<u>aa</u> yata<u>dz</u>akkaru uluu <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', 'Maka apakah orang yang mengetahui bahwa apa yang diturunkan Tuhan kepadamu adalah kebenaran, sama dengan orang yang buta? Hanya orang berakal saja yang dapat mengambil pelajaran,'),
	(1727, 13, 20, 'الَّذِيْنَ يُوْفُوْنَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُوْنَ الْمِيْثَاقَۙ  ', '<strong>al</strong>la<u>dz</u>iina yuufuuna bi\'ahdi <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> yanqu<u>dh</u>uuna <strong>a</strong>lmiits<u>aa</u>q<strong>a</strong>', '(yaitu) orang yang memenuhi janji Allah dan tidak melanggar perjanjian,'),
	(1728, 13, 21, 'وَالَّذِيْنَ يَصِلُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُوْنَ سُوْۤءَ الْحِسَابِ ۗ   ', 'wa<strong>a</strong>lla<u>dz</u>iina ya<u>sh</u>iluuna m<u>aa</u> amara <strong>al</strong>l<u>aa</u>hu bihi an yuu<u>sh</u>ala wayakhsyawna rabbahum wayakh<u>aa</u>fuuna suu-a <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'dan orang-orang yang menghubungkan apa yang diperintahkan Allah agar dihubungkan, dan mereka takut kepada Tuhannya dan takut kepada hisab yang buruk.'),
	(1729, 13, 22, 'وَالَّذِيْنَ صَبَرُوا ابْتِغَاۤءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً وَّيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ اُولٰۤىِٕكَ لَهُمْ عُقْبَى الدَّارِۙ  ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>sh</u>abaruu ibtigh<u>aa</u>-a wajhi rabbihim wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa-anfaquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan wayadrauuna bi<strong>a</strong>l<u>h</u>asanati <strong>al</strong>ssayyi-ata ul<u>aa</u>-ika lahum \'uqb<u>aa</u> <strong>al</strong>dd<u>aa</u>r<strong>i</strong>', 'Dan orang yang sabar karena mengharap keridaan Tuhannya, melaksanakan salat, dan menginfakkan sebagian rezeki yang Kami berikan kepada mereka, secara sembunyi atau terang-terangan serta menolak kejahatan dengan kebaikan; orang itulah yang men-dapat tempat kesudahan (yang baik),'),
	(1730, 13, 23, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ وَالْمَلٰۤىِٕكَةُ يَدْخُلُوْنَ عَلَيْهِمْ مِّنْ كُلِّ بَابٍۚ ', 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> waman <u>sh</u>ala<u>h</u>a min <u>aa</u>b<u>aa</u>-ihim wa-azw<u>aa</u>jihim wa<u>dz</u>urriyy<u>aa</u>tihim wa<strong>a</strong>lmal<u>aa</u>-ikatu yadkhuluuna \'alayhim min kulli b<u>aa</u>b<strong>in</strong>', '(yaitu) surga-surga ‘Adn, mereka masuk ke dalamnya bersama dengan orang yang saleh dari nenek moyangnya, pasangan-pasangannya, dan anak cucunya, sedang para malaikat masuk ke tempat-tempat mereka dari semua pintu;'),
	(1731, 13, 24, 'سَلٰمٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۗ   ', 'sal<u>aa</u>mun \'alaykum bim<u>aa</u> <u>sh</u>abartum fani\'ma \'uqb<u>aa</u> <strong>al</strong>dd<u>aa</u>r<strong>i</strong>', '(sambil mengucapkan), “Selamat sejahtera atasmu karena kesabaranmu.” Maka alangkah nikmatnya tempat kesudahan itu.'),
	(1732, 13, 25, 'وَالَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْ ۢ بَعْدِ مِيْثَاقِهٖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۙ اُولٰۤىِٕكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ ', 'wa<strong>a</strong>lla<u>dz</u>iina yanqu<u>dh</u>uuna \'ahda <strong>al</strong>l<u>aa</u>hi min ba\'di miits<u>aa</u>qihi wayaq<u>th</u>a\'uuna m<u>aa</u> amara <strong>al</strong>l<u>aa</u>hu bihi an yuu<u>sh</u>ala wayufsiduuna fii <strong>a</strong>l-ar<u>dh</u>i ul<u>aa</u>-ika lahumu <strong>al</strong>la\'natu walahum suu-u <strong>al</strong>dd<u>aa</u>r<strong>i</strong>', 'Dan orang-orang yang melanggar janji Allah setelah diikrarkannya, dan memutuskan apa yang diperintahkan Allah agar disambungkan dan berbuat kerusakan di bumi; mereka itu memperoleh kutukan dan tempat kediaman yang buruk (Jahanam).'),
	(1733, 13, 26, 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗوَفَرِحُوْا بِالْحَيٰوةِ الدُّنْيَاۗ وَمَا الْحَيٰوةُ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا مَتَاعٌ  ࣖ  ', '<strong>al</strong>l<u>aa</u>hu yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u wayaqdiru wafari<u>h</u>uu bi<strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wam<u>aa</u> <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> fii <strong>a</strong>l-<u>aa</u>khirati ill<u>aa</u> mat<u>aa</u>\'<strong>un</strong>', 'Allah melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasi (bagi siapa yang Dia kehendaki). Mereka bergembira dengan kehidupan dunia, padahal kehidupan dunia hanyalah kesenangan (yang sedikit) dibanding kehidupan akhirat.'),
	(1734, 13, 27, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ  قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ اَنَابَۖ  ', 'wayaquulu <strong>al</strong>la<u>dz</u>iina kafaruu lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi qul inna <strong>al</strong>l<u>aa</u>ha yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii ilayhi man an<u>aa</u>b<strong>a</strong>', 'Dan orang-orang kafir berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) tanda (mukjizat) dari Tuhannya?” Katakanlah (Muhammad), “Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan memberi petunjuk orang yang bertobat kepada-Nya,”'),
	(1735, 13, 28, 'الَّذِيْنَ اٰمَنُوْا وَتَطْمَىِٕنُّ قُلُوْبُهُمْ بِذِكْرِ اللّٰهِ ۗ اَلَا بِذِكْرِ اللّٰهِ تَطْمَىِٕنُّ الْقُلُوْبُ ۗ   ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wata<u>th</u>ma-innu quluubuhum bi<u>dz</u>ikri <strong>al</strong>l<u>aa</u>hi <strong>a</strong>l<u>aa</u> bi<u>dz</u>ikri <strong>al</strong>l<u>aa</u>hi ta<u>th</u>ma-innu <strong>a</strong>lquluub<strong>u</strong>', '(yaitu) orang-orang yang beriman dan hati mereka menjadi tenteram dengan mengingat Allah. Ingatlah, hanya dengan mengingat Allah hati menjadi tenteram.'),
	(1736, 13, 29, 'اَلَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ طُوْبٰى لَهُمْ وَحُسْنُ مَاٰبٍ ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti <u>th</u>uub<u>aa</u> lahum wa<u>h</u>usnu ma<u>aa</u>b<strong>in</strong>', 'Orang-orang yang beriman dan mengerjakan kebajikan, mereka mendapat kebahagiaan dan tempat kembali yang baik.'),
	(1737, 13, 30, 'كَذٰلِكَ اَرْسَلْنٰكَ فِيْٓ اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَآ اُمَمٌ لِّتَتْلُوَا۟ عَلَيْهِمُ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَهُمْ يَكْفُرُوْنَ بِالرَّحْمٰنِۗ قُلْ هُوَ رَبِّيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ ', 'ka<u>dzaa</u>lika arsaln<u>aa</u>ka fii ummatin qad khalat min qablih<u>aa</u> umamun litatluwa \'alayhimu <strong>al</strong>la<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka wahum yakfuruuna bi<strong>al</strong>rra<u>h</u>m<u>aa</u>ni qul huwa rabbii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa \'alayhi tawakkaltu wa-ilayhi mat<u>aa</u>b<strong>i</strong>', 'Demikianlah, Kami telah mengutus engkau (Muhammad) kepada suatu umat yang sungguh sebelumnya telah berlalu beberapa umat, agar engkau bacakan kepada mereka (Al-Qur\'an) yang Kami wahyukan kepadamu, padahal mereka ingkar kepada Tuhan Yang Maha Pengasih. Katakanlah, “Dia Tuhanku, tidak ada tuhan selain Dia; hanya kepada-Nya aku bertawakal dan hanya kepada-Nya aku bertobat.”'),
	(1738, 13, 31, 'وَلَوْ اَنَّ قُرْاٰنًا سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۗ بَلْ لِّلّٰهِ الْاَمْرُ جَمِيْعًاۗ اَفَلَمْ يَا۟يْـَٔسِ الَّذِيْنَ اٰمَنُوْٓا اَنْ لَّوْ يَشَاۤءُ اللّٰهُ لَهَدَى النَّاسَ جَمِيْعًاۗ وَلَا يَزَال', 'walaw anna qur-<u>aa</u>nan suyyirat bihi <strong>a</strong>ljib<u>aa</u>lu aw qu<u>ththh</u>i\'at bihi <strong>a</strong>l-ar<u>dh</u>u aw kullima bihi <strong>a</strong>lmawt<u>aa</u> bal lill<u>aa</u>hi <strong>a</strong>l-amru jamii\'an afalam yay-asi <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu an law yasy<u>aa</u>u <strong>al</strong>l<u>aa</u>hu lahad<u>aa</u> <strong>al</strong>nn<u>aa</u>sa jamii\'an wal<u>aa</u> yaz<u>aa</u>lu <strong>al</strong>la<u>dz</u>iina kafaruu tu<u>sh</u>iibuhum bim<u>aa</u> <u>sh</u>ana\'uu q<u>aa</u>ri\'atun aw ta<u>h</u>ullu qariiban min d<u>aa</u>rihim <u>h</u>att<u>aa</u> ya/tiya wa\'du <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yukhlifu <strong>a</strong>lmii\'<u>aa</u>d<strong>a</strong>', 'Dan sekiranya ada suatu bacaan (Kitab Suci) yang dengan itu gunung-gunung dapat digoncangkan, atau bumi jadi terbelah, atau orang yang sudah mati dapat berbicara, (itulah Al-Qur\'an). Sebenarnya segala urusan itu milik Allah. Maka tidakkah orang-orang yang beriman mengetahui bahwa sekiranya Allah menghendaki (semua manusia beriman), tentu Allah memberi petunjuk kepada manusia semuanya. Dan orang-orang kafir senantiasa ditimpa bencana disebabkan perbuatan mereka sendiri atau bencana itu terjadi dekat tempat kediaman mereka, sampai datang janji Allah (penaklukkan Mekah). Sungguh, Allah tidak menyalahi janji.'),
	(1739, 13, 32, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذِيْنَ كَفَرُوْا ثُمَّ اَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ ', 'walaqadi istuhzi-a birusulin min qablika fa-amlaytu lilla<u>dz</u>iina kafaruu tsumma akha<u>dz</u>tuhum fakayfa k<u>aa</u>na \'iq<u>aa</u>b<strong>i</strong>', 'Dan sesungguhnya beberapa rasul sebelum engkau (Muhammad) telah diperolok-olokkan, maka Aku beri tenggang waktu kepada orang-orang kafir itu, kemudian Aku binasakan mereka. Maka alangkah hebatnya siksaan-Ku itu! '),
	(1740, 13, 33, 'اَفَمَنْ هُوَ قَاۤىِٕمٌ عَلٰى كُلِّ نَفْسٍۢ بِمَا كَسَبَتْۚ وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ ۗ قُلْ سَمُّوْهُمْۗ اَمْ تُنَبِّـُٔوْنَهٗ بِمَا لَا يَعْلَمُ فِى الْاَرْضِ اَمْ بِظَاهِرٍ مِّنَ الْقَوْلِ ۗبَلْ زُيِّنَ لِلَّذِيْنَ كَفَرُوْا مَكْرُهُمْ وَصُدُّوْا ', 'afaman huwa q<u>aa</u>-imun \'al<u>aa</u> kulli nafsin bim<u>aa</u> kasabat waja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a qul sammuuhum am tunabbi-uunahu bim<u>aa</u> l<u>aa</u> ya\'lamu fii <strong>a</strong>l-ar<u>dh</u>i am bi<u>zhaa</u>hirin mina <strong>a</strong>lqawli bal zuyyina lilla<u>dz</u>iina kafaruu makruhum wa<u>sh</u>udduu \'ani <strong>al</strong>ssabiili waman yu<u>dh</u>lili <strong>al</strong>l<u>aa</u>hu fam<u>aa</u> lahu min h<u>aa</u>d<strong>in</strong>', 'Maka apakah Tuhan yang menjaga setiap jiwa terhadap apa yang diperbuatnya (sama dengan yang lain)? Mereka menjadikan sekutu-sekutu bagi Allah. Katakanlah, “Sebutkanlah sifat-sifat mereka itu.” Atau apakah kamu hendak memberitahukan kepada Allah apa yang tidak diketahui-Nya di bumi, atau (mengatakan tentang hal itu) sekedar perkataan pada lahirnya saja. Sebenarnya bagi orang kafir, tipu daya mereka itu dijadikan terasa indah, dan mereka dihalangi dari jalan (yang benar). Dan barangsiapa disesatkan Allah, maka tidak ada seorang pun yang memberi petunjuk baginya.'),
	(1741, 13, 34, 'لَهُمْ عَذَابٌ فِى الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ ', 'lahum \'a<u>dzaa</u>bun fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wala\'a<u>dzaa</u>bu <strong>a</strong>l-<u>aa</u>khirati asyaqqu wam<u>aa</u> lahum mina <strong>al</strong>l<u>aa</u>hi min w<u>aa</u>q<strong>in</strong>', 'Mereka mendapat siksaan dalam kehidupan dunia, dan azab akhirat pasti lebih keras. Tidak ada seorang pun yang melindungi mereka dari (azab) Allah.'),
	(1742, 13, 35, '۞ مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ  اُكُلُهَا دَاۤىِٕمٌ وَّظِلُّهَاۗ تِلْكَ عُقْبَى الَّذِيْنَ اتَّقَوْا ۖوَّعُقْبَى الْكٰفِرِيْنَ النَّارُ ', 'matsalu <strong>a</strong>ljannati <strong>al</strong>latii wu\'ida <strong>a</strong>lmuttaquuna tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru ukuluh<u>aa</u> d<u>aa</u>-imun wa<u>zh</u>illuh<u>aa</u> tilka \'uqb<u>aa</u> <strong>al</strong>la<u>dz</u>iina ittaqaw wa\'uqb<u>aa</u> <strong>a</strong>lk<u>aa</u>firiina <strong>al</strong>nn<u>aa</u>r<strong>u</strong>', 'Perumpamaan surga yang dijanjikan kepada orang yang bertakwa (ialah seperti taman), mengalir di bawahnya sungai-sungai; senantiasa berbuah dan teduh. Itulah tempat kesudahan bagi orang yang bertakwa; sedang tempat kesudahan bagi orang yang ingkar kepada Tuhan ialah neraka.'),
	(1743, 13, 36, 'وَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَفْرَحُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُّنْكِرُ بَعْضَهٗ ۗ قُلْ اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَآ اُشْرِكَ بِهٖ ۗاِلَيْهِ اَدْعُوْا وَاِلَيْهِ مَاٰبِ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba yafra<u>h</u>uuna bim<u>aa</u> unzila ilayka wamina <strong>a</strong>l-a<u>h</u>z<u>aa</u>bi man yunkiru ba\'<u>dh</u>ahu qul innam<u>aa</u> umirtu an a\'buda <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> usyrika bihi ilayhi ad\'uu wa-ilayhi ma<u>aa</u>b<strong>i</strong>', 'Dan orang yang telah Kami berikan kitab kepada mereka bergembira dengan apa (kitab) yang diturunkan kepadamu (Muhammad), dan ada di antara golongan (Yahudi dan Nasrani), yang mengingkari sebagiannya. Katakanlah, “Aku hanya diperintah untuk menyembah Allah dan tidak mempersekutukan-Nya. Hanya kepada-Nya aku seru (manusia) dan hanya kepada-Nya aku kembali.”'),
	(1744, 13, 37, 'وَكَذٰلِكَ اَنْزَلْنٰهُ حُكْمًا عَرَبِيًّاۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَمَا جَاۤءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا وَاقٍ ࣖ ', 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu <u>h</u>ukman \'arabiyyan wala-ini ittaba\'ta ahw<u>aa</u>-ahum ba\'da m<u>aa</u> j<u>aa</u>-aka mina <strong>a</strong>l\'ilmi m<u>aa</u> laka mina <strong>al</strong>l<u>aa</u>hi min waliyyin wal<u>aa</u> w<u>aa</u>q<strong>in</strong>', 'Dan demikianlah Kami telah menurunkannya (Al-Qur\'an) sebagai peraturan (yang benar) dalam bahasa Arab. Sekiranya engkau mengikuti keinginan mereka setelah datang pengetahuan kepadamu, maka tidak ada yang melindungi dan yang menolong engkau dari (siksaan) Allah.'),
	(1745, 13, 38, 'وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجًا وَّذُرِّيَّةً ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗلِكُلِّ اَجَلٍ كِتَابٌ ', 'walaqad arsaln<u>aa</u> rusulan min qablika waja\'aln<u>aa</u> lahum azw<u>aa</u>jan wa<u>dz</u>urriyyatan wam<u>aa</u> k<u>aa</u>na lirasuulin an ya/tiya bi-<u>aa</u>yatin ill<u>aa</u> bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi likulli ajalin kit<u>aa</u>b<strong>un</strong>', 'Dan sungguh, Kami telah mengutus beberapa rasul sebelum engkau (Muhammad) dan Kami berikan kepada mereka istri-istri dan keturunan. Tidak ada hak bagi seorang rasul mendatangkan sesuatu bukti (mukjizat) melainkan dengan izin Allah. Untuk setiap masa ada Kitab (tertentu).'),
	(1746, 13, 39, 'يَمْحُوا اللّٰهُ مَا يَشَاۤءُ وَيُثْبِتُ ۚوَعِنْدَهٗٓ اُمُّ الْكِتٰبِ ', 'yam<u>h</u>uu <strong>al</strong>l<u>aa</u>hu m<u>aa</u> yasy<u>aa</u>u wayutsbitu wa\'indahu ummu <strong>a</strong>lkit<u>aa</u>b<strong>i</strong>', 'Allah menghapus dan menetapkan apa yang Dia kehendaki. Dan di sisi-Nya terdapat Ummul-Kitab (Lauh Mahfuzh).'),
	(1747, 13, 40, 'وَاِنْ مَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلٰغُ وَعَلَيْنَا الْحِسَابُ ', 'wa-in m<u>aa</u> nuriyannaka ba\'<u>dh</u>a <strong>al</strong>la<u>dz</u>ii na\'iduhum aw natawaffayannaka fa-innam<u>aa</u> \'alayka <strong>a</strong>lbal<u>aa</u>ghu wa\'alayn<u>aa</u> <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>u</strong>', 'Dan sungguh jika Kami perlihatkan kepadamu (Muhammad) sebagian (siksaan) yang Kami ancamkan kepada mereka atau Kami wafatkan engkau, maka sesungguhnya tugasmu hanya menyampaikan saja, dan Kamilah yang memperhitungkan (amal mereka).'),
	(1748, 13, 41, 'اَوَلَمْ يَرَوْا اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهٖۗ وَهُوَ سَرِيْعُ الْحِسَابِ ', 'awa lam yaraw ann<u>aa</u> na/tii <strong>a</strong>l-ar<u>dh</u>a nanqu<u>sh</u>uh<u>aa</u> min a<u>th</u>r<u>aa</u>fih<u>aa</u> wa<strong>al</strong>l<u>aa</u>hu ya<u>h</u>kumu l<u>aa</u> mu\'aqqiba li<u>h</u>ukmihi wahuwa sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Dan apakah mereka tidak melihat bahwa Kami mendatangi daerah-daerah (orang yang ingkar kepada Allah), lalu Kami kurangi (daerah-daerah) itu (sedikit demi sedikit) dari tepi-tepinya? Dan Allah menetapkan hukum (menurut kehendak-Nya), tidak ada yang dapat menolak ketetapan-Nya; Dia Mahacepat perhitungan-Nya.'),
	(1749, 13, 42, 'وَقَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَمِيْعًا ۗيَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۗ وَسَيَعْلَمُ الْكُفّٰرُ لِمَنْ عُقْبَى الدَّارِ ', 'waqad makara <strong>al</strong>la<u>dz</u>iina min qablihim falill<u>aa</u>hi <strong>a</strong>lmakru jamii\'an ya\'lamu m<u>aa</u> taksibu kullu nafsin wasaya\'lamu <strong>a</strong>lkuff<u>aa</u>ru liman \'uqb<u>aa</u> <strong>al</strong>dd<u>aa</u>r<strong>i</strong>', 'Dan sungguh, orang sebelum mereka (kafir Mekah) telah mengadakan tipu daya, tetapi semua tipu daya itu dalam kekuasaan Allah. Dia mengetahui apa yang diusahakan oleh setiap orang, dan orang yang ingkar kepada Tuhan akan mengetahui untuk siapa tempat kesudahan (yang baik).'),
	(1750, 13, 43, 'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَسْتَ مُرْسَلًا ۗ قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۙ وَمَنْ عِنْدَهٗ عِلْمُ الْكِتٰبِ ࣖ ', 'wayaquulu <strong>al</strong>la<u>dz</u>iina kafaruu lasta mursalan qul kaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi syahiidan baynii wabaynakum waman \'indahu \'ilmu <strong>a</strong>lkit<u>aa</u>b<strong>i</strong>', ' Dan orang-orang kafir berkata, “Engkau (Muhammad) bukanlah seorang Rasul.” Katakanlah, “Cukuplah Allah dan orang yang menguasai ilmu Al-Kitab menjadi saksi antara aku dan kamu.” '),
	(1751, 14, 1, 'الۤرٰ ۗ كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ بِاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِۙ', 'alif-l<u>aa</u>m-r<u>aa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu ilayka litukhrija <strong>al</strong>nn<u>aa</u>sa mina <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> <strong>al</strong>nnuuri bi-i<u>dz</u>ni rabbihim il<u>aa</u> <u>sh</u>ir<u>aath</u>i <strong>a</strong>l\'aziizi <strong>a</strong>l<u>h</u>amiid<strong>i</strong>', 'Alif Lam Ra. (Ini adalah) Kitab yang Kami turunkan kepadamu (Muhammad) agar engkau mengeluarkan manusia dari kegelapan kepada cahaya terang-benderang dengan izin Tuhan, (yaitu) menuju jalan Tuhan Yang Mahaperkasa, Maha Terpuji.'),
	(1752, 14, 2, 'اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَوَيْلٌ لِّلْكٰفِرِيْنَ مِنْ عَذَابٍ شَدِيْدٍۙ  ', '<strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wawaylun lilk<u>aa</u>firiina min \'a<u>dzaa</u>bin syadiid<strong>in</strong>', 'Allah yang memiliki apa yang ada di langit dan apa yang ada di bumi. Celakalah bagi orang yang ingkar kepada Tuhan karena siksaan yang sangat berat, '),
	(1753, 14, 3, ' ۨالَّذِيْنَ يَسْتَحِبُّوْنَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًا ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍۢ بَعِيْدٍ', '<strong>al</strong>la<u>dz</u>iina yasta<u>h</u>ibbuuna <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-<u>aa</u>khirati waya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan ul<u>aa</u>-ika fii <u>dh</u>al<u>aa</u>lin ba\'iid<strong>in</strong>', '(yaitu) orang yang lebih menyukai kehidupan dunia daripada (kehidupan) akhirat, dan menghalang-halangi (manusia) dari jalan Allah dan menginginkan (jalan yang) bengkok. Mereka itu berada dalam kesesatan yang jauh.'),
	(1754, 14, 4, 'وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا بِلِسَانِ قَوْمِهٖ لِيُبَيِّنَ لَهُمْ ۗفَيُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ', 'wam<u>aa</u> arsaln<u>aa</u> min rasuulin ill<u>aa</u> bilis<u>aa</u>ni qawmihi liyubayyina lahum fayu<u>dh</u>illu <strong>al</strong>l<u>aa</u>hu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Dan Kami tidak mengutus seorang rasul pun, melainkan dengan bahasa kaumnya, agar dia dapat memberi penjelasan kepada mereka. Maka Allah menyesatkan siapa yang Dia kehendaki, dan memberi petunjuk kepada siapa yang Dia kehendaki. Dia Yang Mahaperkasa, Mahabijaksana.'),
	(1755, 14, 5, 'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ وَذَكِّرْهُمْ بِاَيّٰىمِ اللّٰهِ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ', 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> an akhrij qawmaka mina <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> <strong>al</strong>nnuuri wa<u>dz</u>akkirhum bi-ayy<u>aa</u>mi <strong>al</strong>l<u>aa</u>hi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur<strong>in</strong>', 'Dan sungguh, Kami telah mengutus Musa dengan membawa tanda-tanda (kekuasaan) Kami, (dan Kami perintahkan kepadanya), “Keluarkanlah kaummu dari kegelapan kepada cahaya terang-benderang dan ingatkanlah mereka kepada hari-hari Allah.” Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang penyabar dan banyak bersyukur.'),
	(1756, 14, 6, 'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰىكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ وَيُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗوَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِي', 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi u<u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum i<u>dz</u> anj<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a <strong>a</strong>l\'a<u>dzaa</u>bi wayu<u>dz</u>abbi<u>h</u>uuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim<strong>un</strong>', 'Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Ingatlah nikmat Allah atasmu ketika Dia menyelamatkan kamu dari pengikut-pengikut Fir‘aun; mereka menyiksa kamu dengan siksa yang pedih, dan menyembelih anak-anakmu yang laki-laki, dan membiarkan hidup anak-anak perempuanmu; pada yang demikian itu suatu cobaan yang besar dari Tuhanmu.'),
	(1757, 14, 7, 'وَاِذْ تَاَذَّنَ رَبُّكُمْ لَىِٕنْ شَكَرْتُمْ لَاَزِيْدَنَّكُمْ وَلَىِٕنْ كَفَرْتُمْ اِنَّ عَذَابِيْ لَشَدِيْدٌ ', 'wa-i<u>dz</u> ta-a<u>dzdz</u>ana rabbukum la-in syakartum la-aziidannakum wala-in kafartum inna \'a<u>dzaa</u>bii lasyadiid<strong>un</strong>', 'Dan (ingatlah) ketika Tuhanmu memaklumkan, “Sesungguhnya jika kamu bersyukur, niscaya Aku akan menambah (nikmat) kepadamu, tetapi jika kamu mengingkari (nikmat-Ku), maka pasti azab-Ku sangat berat.”'),
	(1758, 14, 8, 'وَقَالَ مُوْسٰٓى اِنْ تَكْفُرُوْٓا اَنْتُمْ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۙفَاِنَّ اللّٰهَ لَغَنِيٌّ حَمِيْدٌ ', 'waq<u>aa</u>la muus<u>aa</u> in takfuruu antum waman fii <strong>a</strong>l-ar<u>dh</u>i jamii\'an fa-inna <strong>al</strong>l<u>aa</u>ha laghaniyyun <u>h</u>amiid<strong>un</strong>', 'Dan Musa berkata, “Jika kamu dan orang yang ada di bumi semuanya  mengingkari (nikmat Allah), maka sesungguhnya Allah Mahakaya, Maha Terpuji.'),
	(1759, 14, 9, 'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ مِنْ قَبْلِكُمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۗ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُ  ۗجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرَدُّوْٓا اَيْدِيَهُمْ فِيْٓ اَفْوَاهِهِمْ وَقَالُوْٓا اِ', 'alam ya/tikum nabau <strong>al</strong>la<u>dz</u>iina min qablikum qawmi nuu<u>h</u>in wa\'<u>aa</u>din watsamuuda wa<strong>a</strong>lla<u>dz</u>iina min ba\'dihim l<u>aa</u> ya\'lamuhum ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu j<u>aa</u>-at-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti faradduu aydiyahum fii afw<u>aa</u>hihim waq<u>aa</u>luu inn<u>aa</u> kafarn<u>aa</u> bim<u>aa</u> ursiltum bihi wa-inn<u>aa</u> lafii syakkin mimm<u>aa</u> tad\'uunan<u>aa</u> ilayhi muriib<strong>un</strong>', 'Apakah belum sampai kepadamu berita orang-orang sebelum kamu (yaitu) kaum Nuh, ‘Ad, samud dan orang-orang setelah mereka. Tidak ada yang mengetahui mereka selain Allah. Rasul-rasul telah datang kepada mereka membawa bukti-bukti (yang nyata), namun mereka menutupkan tangannya ke mulutnya (karena kebencian), dan berkata, “Sesungguhnya kami tidak percaya akan (bukti bahwa) kamu diutus (kepada kami), dan kami benar-benar dalam keraguan yang menggelisahkan terhadap apa yang kamu serukan kepada kami.”'),
	(1760, 14, 10, '۞ قَالَتْ رُسُلُهُمْ اَفِى اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِۗ يَدْعُوْكُمْ لِيَغْفِرَ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ قَالُوْٓا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَا ۗ تُرِيْدُوْنَ اَنْ تَصُدُّوْنَا عَمَّا ك', 'q<u>aa</u>lat rusuluhum afii <strong>al</strong>l<u>aa</u>hi syakkun f<u>aath</u>iri <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i yad\'uukum liyaghfira lakum min <u>dz</u>unuubikum wayu-akhkhirakum il<u>aa</u> ajalin musamman q<u>aa</u>luu in antum ill<u>aa</u> basyarun mitslun<u>aa</u> turiiduuna an ta<u>sh</u>udduun<u>aa</u> \'amm<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> fa/tuun<u>aa</u> bisul<u>th</u><u>aa</u>nin mubiin<strong>in</strong>', 'Rasul-rasul mereka berkata, “Apakah ada keraguan terhadap Allah, Pencipta langit dan bumi? Dia menyeru kamu (untuk beriman) agar Dia mengampuni sebagian dosa-dosamu dan menangguhkan (siksaan)mu sampai waktu yang ditentukan?” Mereka berkata, “Kamu hanyalah manusia seperti kami juga. Kamu ingin menghalangi kami (menyembah) apa yang dari dahulu disembah nenek moyang kami, karena itu datangkanlah kepada kami bukti yang nyata.”'),
	(1761, 14, 11, 'قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَّحْنُ اِلَّا بَشَرٌ مِّثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَمَا كَانَ لَنَآ اَنْ نَّأْتِيَكُمْ بِسُلْطٰنٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ', 'q<u>aa</u>lat lahum rusuluhum in na<u>h</u>nu ill<u>aa</u> basyarun mitslukum wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yamunnu \'al<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wam<u>aa</u> k<u>aa</u>na lan<u>aa</u> an na/tiyakum bisul<u>th</u><u>aa</u>nin ill<u>aa</u> bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmu/minuun<strong>a</strong>', 'Rasul-rasul mereka berkata kepada mereka, “Kami hanyalah manusia seperti kamu, tetapi Allah memberi karunia kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Tidak pantas bagi kami mendatangkan suatu bukti kepada kamu melainkan dengan izin Allah. Dan hanya kepada Allah saja hendaknya orang yang beriman bertawakal.'),
	(1762, 14, 12, 'وَمَا لَنَآ اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰىنَا سُبُلَنَاۗ وَلَنَصْبِرَنَّ عَلٰى مَآ اٰذَيْتُمُوْنَاۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ ࣖ ', 'wam<u>aa</u> lan<u>aa</u> <strong>al</strong>l<u>aa</u> natawakkala \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi waqad had<u>aa</u>n<u>aa</u> subulan<u>aa</u> walana<u>sh</u>biranna \'al<u>aa</u> m<u>aa</u> <u>aadz</u>aytumuun<u>aa</u> wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi falyatawakkali <strong>a</strong>lmutawakkiluun<strong>a</strong>', 'Dan mengapa kami tidak akan bertawakal kepada Allah, sedangkan Dia telah menunjukkan jalan kepada kami, dan kami sungguh, akan tetap bersabar terhadap gangguan yang kamu lakukan kepada kami. Dan hanya kepada Allah saja orang yang bertawakal berserah diri.”'),
	(1763, 14, 13, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِّنْ اَرْضِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظّٰلِمِيْنَ ۗ   ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lirusulihim lanukhrijannakum min ar<u>dh</u>in<u>aa</u> aw lata\'uudunna fii millatin<u>aa</u> fa-aw<u>haa</u> ilayhim rabbuhum lanuhlikanna <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan orang-orang kafir berkata kepada rasul-rasul mereka, “Kami pasti akan mengusir kamu dari negeri kami atau kamu benar-benar kembali kepada agama kami.” Maka Tuhan mewahyukan kepada mereka, “Kami pasti akan membinasakan orang yang zalim itu.'),
	(1764, 14, 14, 'وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْۢ بَعْدِهِمْ ۗذٰلِكَ لِمَنْ خَافَ مَقَامِيْ وَخَافَ وَعِيْدِ ', 'walanuskinannakumu <strong>a</strong>l-ar<u>dh</u>a min ba\'dihim <u>dzaa</u>lika liman kh<u>aa</u>fa maq<u>aa</u>mii wakh<u>aa</u>fa wa\'iid<strong>i</strong>', 'Dan Kami pasti akan menempatkan kamu di negeri-negeri itu setelah mereka. Yang demikian itu (adalah untuk) orang-orang yang takut (menghadap) ke hadirat-Ku dan takut akan ancaman-Ku.”'),
	(1765, 14, 15, 'وَاسْتَفْتَحُوْا وَخَابَ كُلُّ جَبَّارٍ عَنِيْدٍۙ  ', 'wa<strong>i</strong>stafta<u>h</u>uu wakh<u>aa</u>ba kullu jabb<u>aa</u>rin \'aniid<strong>in</strong>', 'Dan mereka memohon diberi kemenangan dan binasalah semua orang yang berlaku sewenang-wenang lagi keras kepala, '),
	(1766, 14, 16, 'مِّنْ وَّرَاۤىِٕهٖ جَهَنَّمُ وَيُسْقٰى مِنْ مَّاۤءٍ صَدِيْدٍۙ ', 'min war<u>aa</u>-ihi jahannamu wayusq<u>aa</u> min m<u>aa</u>-in <u>sh</u>adiid<strong>in</strong>', 'di hadapannya ada neraka Jahanam dan dia akan diberi minuman dengan air nanah,'),
	(1767, 14, 17, 'يَّتَجَرَّعُهٗ وَلَا يَكَادُ يُسِيْغُهٗ وَيَأْتِيْهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَّمَا هُوَ بِمَيِّتٍۗ وَمِنْ وَّرَاۤىِٕهٖ عَذَابٌ غَلِيْظٌ ', 'yatajarra\'uhu wal<u>aa</u> yak<u>aa</u>du yusiighuhu waya/tiihi <strong>a</strong>lmawtu min kulli mak<u>aa</u>nin wam<u>aa</u> huwa bimayyitin wamin war<u>aa</u>-ihi \'a<u>dzaa</u>bun ghalii<u>zh</u><strong>un</strong>', 'diteguk-teguknya (air nanah itu) dan dia hampir tidak bisa menelannya dan datanglah (bahaya) maut kepadanya dari segenap penjuru, tetapi dia tidak juga mati; dan di hadapannya (masih ada) azab yang berat.'),
	(1768, 14, 18, 'مَثَلُ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادِ ِۨاشْتَدَّتْ بِهِ الرِّيْحُ فِيْ يَوْمٍ عَاصِفٍۗ  لَا يَقْدِرُوْنَ مِمَّا كَسَبُوْا عَلٰى شَيْءٍ ۗذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ', 'matsalu <strong>al</strong>la<u>dz</u>iina kafaruu birabbihim a\'m<u>aa</u>luhum karam<u>aa</u>din isytaddat bihi <strong>al</strong>rrii<u>h</u>u fii yawmin \'<u>aas</u>ifin l<u>aa</u> yaqdiruuna mimm<u>aa</u> kasabuu \'al<u>aa</u> syay-in <u>dzaa</u>lika huwa <strong>al</strong><u>dhdh</u>al<u>aa</u>lu <strong>a</strong>lba\'iid<strong>u</strong>', 'Perumpamaan orang yang ingkar kepada Tuhannya, perbuatan mereka seperti abu yang ditiup oleh angin keras pada suatu hari yang berangin kencang. Mereka tidak kuasa (mendatangkan manfaat) sama sekali dari apa yang telah mereka usahakan (di dunia). Yang demikian itu adalah kesesatan yang jauh.'),
	(1769, 14, 19, 'اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۙ  ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a bi<strong>a</strong>l<u>h</u>aqqi in yasya/ yu<u>dz</u>hibkum waya/ti bikhalqin jadiid<strong>in </strong>', 'Tidakkah kamu memperhatikan, bahwa sesungguhnya Allah telah menciptakan langit dan bumi dengan hak (benar)? Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu),'),
	(1770, 14, 20, 'وَّمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ', 'wam<u>aa</u> <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi bi\'aziiz<strong>in</strong>', 'dan yang demikian itu tidak sukar bagi Allah.'),
	(1771, 14, 21, 'وَبَرَزُوْا لِلّٰهِ جَمِيْعًا فَقَالَ الضُّعَفٰۤؤُا لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍ ۗقَالُوْا لَوْ هَدٰىنَا اللّٰهُ لَهَدَيْنٰكُمْۗ سَوَاۤءٌ عَلَيْنَآ اَجَزِعْنَ', 'wabarazuu lill<u>aa</u>hi jamii\'an faq<u>aa</u>la <strong>al</strong><u>dhdh</u>u\'af<u>aa</u>u lilla<u>dz</u>iina istakbaruu inn<u>aa</u> kunn<u>aa</u> lakum taba\'an fahal antum mughnuuna \'ann<u>aa</u> min \'a<u>dzaa</u>bi <strong>al</strong>l<u>aa</u>hi min syay-in q<u>aa</u>luu law had<u>aa</u>n<u>aa</u> <strong>al</strong>l<u>aa</u>hu lahadayn<u>aa</u>kum saw<u>aa</u>un \'alayn<u>aa</u> ajazi\'n<u>aa</u> am <u>sh</u>abarn<u>aa</u> m<u>aa</u> lan<u>aa</u> min ma<u>h</u>ii<u>sh</u><strong>in</strong>', 'Dan mereka semua (di padang Mahsyar) berkumpul untuk menghadap ke hadirat Allah, lalu orang yang lemah berkata kepada orang yang sombong, “Sesungguhnya kami dahulu adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan kami dari azab Allah (walaupun) sedikit saja?” Mereka menjawab, “Sekiranya Allah memberi petunjuk kepada kami, niscaya kami dapat memberi petunjuk kepadamu. Sama saja bagi kita, apakah kita mengeluh atau bersabar. Kita tidak mempunyai tempat untuk melarikan diri.”'),
	(1772, 14, 22, 'وَقَالَ الشَّيْطٰنُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُّكُمْ فَاَخْلَفْتُكُمْۗ وَمَا كَانَ لِيَ عَلَيْكُمْ مِّنْ سُلْطٰنٍ اِلَّآ اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِيْ ۚفَلَا تَلُوْمُوْنِيْ وَلُوْمُوْٓا اَنْفُسَكُم', 'waq<u>aa</u>la <strong>al</strong>sysyay<u>th</u><u>aa</u>nu lamm<u>aa</u> qu<u>dh</u>iya <strong>a</strong>l-amru inna <strong>al</strong>l<u>aa</u>ha wa\'adakum wa\'da <strong>a</strong>l<u>h</u>aqqi wawa\'adtukum fa-akhlaftukum wam<u>aa</u> k<u>aa</u>na liya \'alaykum min sul<u>th</u><u>aa</u>nin ill<u>aa</u> an da\'awtukum fa<strong>i</strong>stajabtum lii fal<u>aa</u> taluumuunii waluumuu anfusakum m<u>aa</u> an<u>aa</u> bimu<u>sh</u>rikhikum wam<u>aa</u> antum bimu<u>sh</u>rikhiyya innii kafartu bim<u>aa</u> asyraktumuuni min qablu inna <strong>al</strong><u>zhzhaa</u>limiina lahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan setan berkata ketika perkara (hisab) telah diselesaikan, “Sesungguhnya Allah telah menjanjikan kepadamu janji yang benar, dan aku pun telah menjanjikan kepadamu tetapi aku menyalahinya. Tidak ada kekuasaan bagiku terhadapmu, melainkan (sekedar) aku menyeru kamu lalu kamu mematuhi seruanku, oleh sebab itu janganlah kamu mencerca aku, tetapi cercalah dirimu sendiri. Aku tidak dapat menolongmu, dan kamu pun tidak dapat menolongku. Sesungguhnya aku tidak membenarkan perbuatanmu mempersekutukan aku (dengan Allah) sejak dahulu.” Sungguh, orang yang zalim akan mendapat siksaan yang pedih.'),
	(1773, 14, 23, 'وَاُدْخِلَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا بِاِذْنِ رَبِّهِمْۗ تَحِيَّتُهُمْ فِيْهَا سَلٰمٌ ', 'waudkhila <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> bi-i<u>dz</u>ni rabbihim ta<u>h</u>iyyatuhum fiih<u>aa</u> sal<u>aa</u>m<strong>un</strong>', 'Dan orang yang beriman dan mengerjakan kebajikan dimasukkan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Mereka kekal di dalamnya dengan seizin Tuhan mereka. Ucapan penghormatan mereka dalam (surga) itu ialah salam.'),
	(1774, 14, 24, 'اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَّفَرْعُهَا فِى السَّمَاۤءِۙ ', 'alam tara kayfa <u>dh</u>araba <strong>al</strong>l<u>aa</u>hu matsalan kalimatan <u>th</u>ayyibatan kasyajaratin <u>th</u>ayyibatin a<u>sh</u>luh<u>aa</u> ts<u>aa</u>bitun wafar\'uh<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>/-<strong>i</strong>', 'Tidakkah kamu memperhatikan bagai-mana Allah telah membuat perumpamaan kalimat yang baik seperti pohon yang baik, akarnya kuat dan cabangnya (menjulang) ke langit,'),
	(1775, 14, 25, 'تُؤْتِيْٓ اُكُلَهَا كُلَّ حِيْنٍ ۢبِاِذْنِ رَبِّهَاۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ', 'tu/tii ukulah<u>aa</u> kulla <u>h</u>iinin bi-i<u>dz</u>ni rabbih<u>aa</u> waya<u>dh</u>ribu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l-amts<u>aa</u>la li<strong>l</strong>nn<u>aa</u>si la\'allahum yata<u>dz</u>akkaruun<strong>a</strong>', '(pohon) itu menghasilkan buahnya pada setiap waktu dengan seizin Tuhannya. Dan Allah membuat perumpamaan itu untuk manusia agar mereka selalu ingat.'),
	(1776, 14, 26, 'وَمَثَلُ كَلِمَةٍ خَبِيْثَةٍ كَشَجَرَةٍ خَبِيْثَةِ ِۨاجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ ', 'wamatsalu kalimatin khabiitsatin kasyajaratin khabiitsatin ijtutstsat min fawqi <strong>a</strong>l-ar<u>dh</u>i m<u>aa</u> lah<u>aa</u> min qar<u>aa</u>r<strong>in</strong>', 'Dan perumpamaan kalimat yang buruk seperti pohon yang buruk, yang telah dicabut akar-akarnya dari permukaan bumi; tidak dapat tetap (tegak) sedikit pun.'),
	(1777, 14, 27, 'يُثَبِّتُ اللّٰهُ الَّذِيْنَ اٰمَنُوْا بِالْقَوْلِ الثَّابِتِ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظّٰلِمِيْنَۗ وَيَفْعَلُ اللّٰهُ مَا يَشَاۤءُ ࣖ   ', 'yutsabbitu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu bi<strong>a</strong>lqawli <strong>al</strong>tsts<u>aa</u>biti fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wafii <strong>a</strong>l-<u>aa</u>khirati wayu<u>dh</u>illu <strong>al</strong>l<u>aa</u>hu <strong>al</strong><u>zhzhaa</u>limiina wayaf\'alu <strong>al</strong>l<u>aa</u>hu m<u>aa</u> yasy<u>aa</u>/<strong>u</strong>', ' Allah meneguhkan (iman) orang-orang yang beriman dengan ucapan yang teguh (dalam kehidupan) di dunia dan di akhirat; dan Allah menyesatkan orang-orang yang zalim dan Allah berbuat apa yang Dia kehendaki.'),
	(1778, 14, 28, '۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ بَدَّلُوْا نِعْمَتَ اللّٰهِ كُفْرًا وَّاَحَلُّوْا قَوْمَهُمْ دَارَ الْبَوَارِۙ  ', 'alam tara il<u>aa</u> <strong>al</strong>la<u>dz</u>iina baddaluu ni\'mata <strong>al</strong>l<u>aa</u>hi kufran wa-a<u>h</u>alluu qawmahum d<u>aa</u>ra <strong>a</strong>lbaw<u>aa</u>r<strong>i</strong>', 'Tidakkah kamu memperhatikan orang-orang yang telah menukar nikmat Allah dengan ingkar kepada Allah dan menjatuhkan kaumnya ke lembah kebinasaan?'),
	(1779, 14, 29, 'جَهَنَّمَ ۚيَصْلَوْنَهَاۗ وَبِئْسَ الْقَرَارُ ', 'jahannama ya<u>sh</u>lawnah<u>aa</u> wabi/sa <strong>a</strong>lqar<u>aa</u>r<strong>u</strong>', ' yaitu neraka Jahanam; mereka masuk ke dalamnya; dan itulah seburuk-buruk tempat kediaman.'),
	(1780, 14, 30, 'وَجَعَلُوْا لِلّٰهِ اَنْدَادًا لِّيُضِلُّوْا عَنْ سَبِيْلِهٖۗ قُلْ تَمَتَّعُوْا فَاِنَّ مَصِيْرَكُمْ اِلَى النَّارِ ', 'waja\'aluu lill<u>aa</u>hi and<u>aa</u>dan liyu<u>dh</u>illuu \'an sabiilihi qul tamatta\'uu fa-inna ma<u>sh</u>iirakum il<u>aa</u> <strong>al</strong>nn<u>aa</u>r<strong>i</strong>', 'Dan mereka (orang kafir) itu telah menjadikan tandingan bagi Allah untuk menyesatkan (manusia) dari jalan-Nya. Katakanlah (Muhammad), “Bersenang-senanglah kamu, karena sesungguhnya tempat kembalimu ke neraka.”'),
	(1781, 14, 31, 'قُلْ لِّعِبَادِيَ الَّذِيْنَ اٰمَنُوْا يُقِيْمُوا الصَّلٰوةَ وَيُنْفِقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خِلٰلٌ ', 'qul li\'ib<u>aa</u>diya <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu yuqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wayunfiquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan min qabli an ya/tiya yawmun l<u>aa</u> bay\'un fiihi wal<u>aa</u> khil<u>aa</u>l<strong>un</strong>', 'Katakanlah (Muhammad) kepada hamba-hamba-Ku yang telah beriman, “Hendaklah mereka melaksanakan salat, menginfakkan sebagian rezeki yang Kami berikan secara sembunyi atau terang-terangan sebelum datang hari, ketika tidak ada lagi jual beli dan persahabatan.” '),
	(1782, 14, 32, 'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚوَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِى الْبَحْرِ بِاَمْرِهٖ ۚوَسَخَّرَ لَكُمُ الْاَنْهٰرَ ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wa-anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-akhraja bihi mina <strong>al</strong>tstsamar<u>aa</u>ti rizqan lakum wasakhkhara lakumu <strong>a</strong>lfulka litajriya fii <strong>a</strong>lba<u>h</u>ri bi-amrihi wasakhkhara lakumu <strong>a</strong>l-anh<u>aa</u>r<strong>a</strong>', 'Allah-lah yang telah menciptakan langit dan bumi dan menurunkan air (hujan) dari langit, kemudian dengan (air hujan) itu Dia mengeluarkan berbagai buah-buahan sebagai rezeki untukmu; dan Dia telah menundukkan kapal bagimu agar berlayar di lautan dengan kehendak-Nya, dan Dia telah menundukkan sungai-sungai bagimu. '),
	(1783, 14, 33, 'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَاۤىِٕبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَ ۚ  ', 'wasakhkhara lakumu <strong>al</strong>sysyamsa wa<strong>a</strong>lqamara d<u>aa</u>-ibayni wasakhkhara lakumu <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>r<strong>a</strong>', 'Dan Dia telah menundukkan matahari dan bulan bagimu yang terus-menerus beredar (dalam orbitnya); dan telah menundukkan malam dan siang bagimu.'),
	(1784, 14, 34, 'وَاٰتٰىكُمْ مِّنْ كُلِّ مَا سَاَلْتُمُوْهُۗ وَاِنْ تَعُدُّوْا نِعْمَتَ اللّٰهِ لَا تُحْصُوْهَاۗ اِنَّ الْاِنْسَانَ لَظَلُوْمٌ كَفَّارٌ ࣖ  ', 'wa<u>aa</u>t<u>aa</u>kum min kulli m<u>aa</u> sa-altumuuhu wa-in ta\'udduu ni\'mata <strong>al</strong>l<u>aa</u>hi l<u>aa</u> tu<u>hs</u>uuh<u>aa</u> inna <strong>a</strong>l-ins<u>aa</u>na la<u>zh</u>aluumun kaff<u>aa</u>r<strong>un</strong>', 'Dan Dia telah memberikan kepadamu segala apa yang kamu mohonkan kepada-Nya. Dan jika kamu menghitung nikmat Allah, niscaya kamu tidak akan mampu menghitungnya. Sungguh, manusia itu sangat zalim dan sangat mengingkari (nikmat Allah).'),
	(1785, 14, 35, 'وَاِذْ قَالَ اِبْرٰهِيْمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِنًا وَّاجْنُبْنِيْ وَبَنِيَّ اَنْ نَّعْبُدَ الْاَصْنَامَ ۗ   ', 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbi ij\'al h<u>aadzaa</u> <strong>a</strong>lbalada <u>aa</u>minan wa<strong>u</strong>jnubnii wabaniyya an na\'buda <strong>a</strong>l-a<u>sh</u>n<u>aa</u>m<strong>a</strong>', 'Dan (ingatlah), ketika Ibrahim berdoa, “Ya Tuhan, jadikanlah negeri ini (Mekah), negeri yang aman, dan jauhkanlah aku beserta anak cucuku agar tidak menyembah berhala.'),
	(1786, 14, 36, 'رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَثِيْرًا مِّنَ النَّاسِۚ فَمَنْ تَبِعَنِيْ فَاِنَّهٗ مِنِّيْۚ وَمَنْ عَصَانِيْ فَاِنَّكَ غَفُوْرٌ رَّحِيْمٌ ', 'rabbi innahunna a<u>dh</u>lalna katsiiran mina <strong>al</strong>nn<u>aa</u>si faman tabi\'anii fa-innahu minnii waman \'a<u>shaa</u>nii fa-innaka ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Ya Tuhan, berhala-berhala itu telah menyesatkan banyak dari manusia. Barangsiapa mengikutiku, maka orang itu termasuk golonganku, dan barang-siapa mendurhakaiku, maka Engkau Maha Pengampun, Maha Penyayang.'),
	(1787, 14, 37, 'رَبَّنَآ اِنِّيْٓ اَسْكَنْتُ مِنْ ذُرِّيَّتِيْ بِوَادٍ غَيْرِ ذِيْ زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّنَا لِيُقِيْمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٕدَةً مِّنَ النَّاسِ تَهْوِيْٓ اِلَيْهِمْ وَارْزُقْهُمْ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَشْكُرُوْنَ', 'rabban<u>aa</u> innii askantu min <u>dz</u>urriyyatii biw<u>aa</u>din ghayri <u>dz</u>ii zar\'in \'inda baytika <strong>a</strong>lmu<u>h</u>arrami rabban<u>aa</u> liyuqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta fa<strong>i</strong>j\'al af-idatan mina <strong>al</strong>nn<u>aa</u>si tahwii ilayhim wa<strong>u</strong>rzuqhum mina <strong>al</strong>tstsamar<u>aa</u>ti la\'allahum yasykuruun<strong>a</strong>', 'Ya Tuhan, sesungguhnya aku telah menempatkan sebagian keturunanku di lembah yang tidak mempunyai tanam-tanaman di dekat rumah Engkau (Baitullah) yang dihormati, ya Tuhan (yang demikian itu) agar mereka melaksanakan salat, maka jadikanlah hati sebagian manusia cenderung kepada mereka dan berilah mereka rezeki dari buah-buahan, mudah-mudahan mereka bersyukur.'),
	(1788, 14, 38, 'رَبَّنَآ اِنَّكَ تَعْلَمُ مَا نُخْفِيْ وَمَا نُعْلِنُۗ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ', 'rabban<u>aa</u> innaka ta\'lamu m<u>aa</u> nukhfii wam<u>aa</u> nu\'linu wam<u>aa</u> yakhf<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi min syay-in fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>/-<strong>i</strong>', 'Ya Tuhan kami, sesungguhnya Engkau mengetahui apa yang kami sembunyikan dan apa yang kami tampakkan; dan tidak ada sesuatu pun yang tersembunyi bagi Allah, baik yang ada di bumi maupun yang ada di langit.'),
	(1789, 14, 39, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ وَهَبَ لِيْ عَلَى الْكِبَرِ اِسْمٰعِيْلَ وَاِسْحٰقَۗ اِنَّ رَبِّيْ لَسَمِيْعُ الدُّعَاۤءِ ', 'al<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii wahaba lii \'al<u>aa</u> <strong>a</strong>lkibari ism<u>aa</u>\'iila wa-is<u>haa</u>qa inna rabbii lasamii\'u <strong>al</strong>ddu\'<u>aa</u>/-<strong>i</strong>', 'Segala puji bagi Allah yang telah menganugerahkan kepadaku di hari tua(ku) Ismail dan Ishak. Sungguh, Tuhanku benar-benar Maha Mendengar (memperkenankan) doa.'),
	(1790, 14, 40, 'رَبِّ اجْعَلْنِيْ مُقِيْمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّتِيْۖ رَبَّنَا وَتَقَبَّلْ دُعَاۤءِ ', 'rabbi ij\'alnii muqiima <strong>al</strong><u>shsh</u>al<u>aa</u>ti wamin <u>dz</u>urriyyatii rabban<u>aa</u> wtaqabbal du\'<u>aa</u>/-<strong>i</strong>', ' Ya Tuhanku, jadikanlah aku dan anak cucuku orang yang tetap melaksanakan salat, ya Tuhan kami, perkenankanlah doaku.'),
	(1791, 14, 41, 'رَبَّنَا اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِلْمُؤْمِنِيْنَ يَوْمَ يَقُوْمُ الْحِسَابُ ࣖ ', 'rabban<u>aa</u> ighfir lii waliw<u>aa</u>lidayya walilmu/miniina yawma yaquumu <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>u</strong>', ' Ya Tuhan kami, ampunilah aku dan kedua ibu bapakku dan semua orang yang beriman pada hari diadakan perhitungan (hari Kiamat).”'),
	(1792, 14, 42, 'وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلًا عَمَّا يَعْمَلُ الظّٰلِمُوْنَ ەۗ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيْهِ الْاَبْصَارُۙ', 'wal<u>aa</u> ta<u>h</u>sabanna <strong>al</strong>l<u>aa</u>ha gh<u>aa</u>filan \'amm<u>aa</u> ya\'malu <strong>al</strong><u>zhzhaa</u>limuuna innam<u>aa</u> yu-akhkhiruhum liyawmin tasykha<u>sh</u>u fiihi <strong>a</strong>l-ab<u>shaa</u>r<strong>u</strong>', 'Dan janganlah engkau mengira, bahwa Allah lengah dari apa yang diperbuat oleh orang yang zalim. Sesungguhnya Allah menangguhkan mereka sampai hari yang pada waktu itu mata (mereka) terbelalak,'),
	(1793, 14, 43, 'مُهْطِعِيْنَ مُقْنِعِيْ رُءُوْسِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْ ۚوَاَفْـِٕدَتُهُمْ هَوَاۤءٌ ۗ  ', 'muh<u>th</u>i\'iina muqni\'ii ruuusihim l<u>aa</u> yartaddu ilayhim <u>th</u>arfuhum wa-af-idatuhum haw<u>aa</u>/<strong>un</strong>', 'mereka datang tergesa-gesa (memenuhi panggilan) dengan mengangkat kepalanya, sedang mata mereka tidak berkedip-kedip dan hati mereka kosong. '),
	(1794, 14, 44, 'وَاَنْذِرِ النَّاسَ يَوْمَ يَأْتِيْهِمُ الْعَذَابُۙ فَيَقُوْلُ الَّذِيْنَ ظَلَمُوْا رَبَّنَآ اَخِّرْنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۙ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَۗ اَوَلَمْ تَكُوْنُوْٓا اَقْسَمْتُمْ مِّنْ قَبْلُ مَا لَكُمْ مِّنْ زَوَالٍۙ  ', 'wa-an<u>dz</u>iri <strong>al</strong>nn<u>aa</u>sa yawma ya/tiihimu <strong>a</strong>l\'a<u>dzaa</u>bu fayaquulu <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu rabban<u>aa</u> akhkhirn<u>aa</u> il<u>aa</u> ajalin qariibin nujib da\'wataka wanattabi\'i <strong>al</strong>rrusula awa lam takuunuu aqsamtum min qablu m<u>aa</u> lakum min zaw<u>aa</u>l<strong>in</strong>', 'Dan berikanlah peringatan (Muhammad) kepada manusia pada hari (ketika) azab datang kepada mereka, maka orang yang zalim berkata, “Ya Tuhan kami, berilah kami kesempatan (kembali ke dunia) walaupun sebentar, niscaya kami akan mematuhi seruan Engkau dan akan mengikuti rasul-rasul.” (Kepada mereka dikatakan), “Bukankah dahulu (di dunia) kamu telah bersumpah bahwa sekali-kali kamu tidak akan binasa?'),
	(1795, 14, 45, 'وَّسَكَنْتُمْ فِيْ مَسٰكِنِ الَّذِيْنَ ظَلَمُوْٓا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ ', 'wasakantum fii mas<u>aa</u>kini <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu anfusahum watabayyana lakum kayfa fa\'aln<u>aa</u> bihim wa<u>dh</u>arabn<u>aa</u> lakumu <strong>a</strong>l-amts<u>aa</u>l<strong>a</strong>', 'Dan kamu telah tinggal di tempat orang yang menzalimi diri sendiri, dan telah nyata bagimu bagaimana Kami telah berbuat terhadap mereka dan telah Kami berikan kepadamu beberapa perumpamaan.”'),
	(1796, 14, 46, 'وَقَدْ مَكَرُوْا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۗ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُوْلَ مِنْهُ الْجِبَالُ ', 'waqad makaruu makrahum wa\'inda <strong>al</strong>l<u>aa</u>hi makruhum wa-in k<u>aa</u>na makruhum litazuula minhu <strong>a</strong>ljib<u>aa</u>l<strong>u</strong>', 'Dan sungguh, mereka telah membuat tipu daya padahal Allah (mengetahui dan akan membalas) tipu daya mereka. Dan sesungguhnya tipu daya mereka tidak mampu melenyapkan gunung-gunung.'),
	(1797, 14, 47, 'فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِهٖ رُسُلَهٗ ۗاِنَّ اللّٰهَ عَزِيْزٌ ذُو انْتِقَامٍۗ   ', 'fal<u>aa</u> ta<u>h</u>sabanna <strong>al</strong>l<u>aa</u>ha mukhlifa wa\'dihi rusulahu inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>dz</u>uu intiq<u>aa</u>m<strong>in</strong>', ' Maka karena itu jangan sekali-kali kamu mengira bahwa Allah mengingkari janji-Nya kepada rasul-rasul-Nya. Sungguh, Allah Mahaperkasa dan mempunyai pembalasan.'),
	(1798, 14, 48, 'يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوٰتُ وَبَرَزُوْا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ ', 'yawma tubaddalu <strong>a</strong>l-ar<u>dh</u>u ghayra <strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu wabarazuu lill<u>aa</u>hi <strong>a</strong>lw<u>aah</u>idi <strong>a</strong>lqahh<u>aa</u>r<strong>i</strong>', '(Yaitu) pada hari (ketika) bumi diganti dengan bumi yang lain dan (demikian pula) langit, dan mereka (manusia) berkumpul (di Padang Mahsyar) menghadap Allah Yang Maha Esa, Mahaperkasa. '),
	(1799, 14, 49, 'وَتَرَى الْمُجْرِمِيْنَ يَوْمَىِٕذٍ مُّقَرَّنِيْنَ فِى الْاَصْفَادِۚ  ', 'watar<u>aa</u> <strong>a</strong>lmujrimiina yawma-i<u>dz</u>in muqarraniina fii <strong>a</strong>l-a<u>sh</u>f<u>aa</u>d<strong>i</strong>', 'Dan pada hari itu engkau akan melihat orang yang berdosa bersama-sama diikat dengan belenggu.'),
	(1800, 14, 50, 'سَرَابِيْلُهُمْ مِّنْ قَطِرَانٍ وَّتَغْشٰى وُجُوْهَهُمُ النَّارُۙ  ', 'sar<u>aa</u>biiluhum min qa<u>th</u>r<u>aa</u>nin wataghsy<u>aa</u> wujuuhahumu <strong>al</strong>nn<u>aa</u>r<strong>u</strong>', 'Pakaian mereka dari cairan aspal, dan wajah mereka ditutup oleh api neraka,'),
	(1801, 14, 51, 'لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَّا كَسَبَتْۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ ', 'liyajziya <strong>al</strong>l<u>aa</u>hu kulla nafsin m<u>aa</u> kasabat inna <strong>al</strong>l<u>aa</u>ha sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'agar Allah memberi balasan kepada setiap orang terhadap apa yang dia usahakan. Sungguh, Allah Mahacepat perhitungan-Nya. '),
	(1802, 14, 52, 'هٰذَا بَلٰغٌ لِّلنَّاسِ وَلِيُنْذَرُوْا بِهٖ وَلِيَعْلَمُوْٓا اَنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّلِيَذَّكَّرَ اُولُوا الْاَلْبَابِ ࣖ ', 'h<u>aadzaa</u> bal<u>aa</u>ghun li<strong>l</strong>nn<u>aa</u>si waliyun<u>dz</u>aruu bihi waliya\'lamuu annam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun waliya<u>dzdz</u>akkara uluu <strong>a</strong>l-alb<u>aa</u>b<strong>i</strong>', 'Dan (Al-Qur\'an) ini adalah penjelasan (yang sempurna) bagi manusia, agar mereka diberi peringatan dengannya, agar mereka mengetahui bahwa Dia adalah Tuhan Yang Maha Esa dan agar orang yang berakal mengambil pelajaran.'),
	(1803, 15, 1, 'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ وَقُرْاٰنٍ مُّبِيْنٍ  ۔', 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi waqur-<u>aa</u>nin mubiin<strong>in</strong>', 'Alif Lam Ra. (Surah) ini adalah (sebagian dari) ayat-ayat Kitab (yang sempurna) yaitu (ayat-ayat) Al-Qur\'an yang memberi penjelasan.'),
	(1804, 15, 2, 'رُبَمَا يَوَدُّ الَّذِيْنَ كَفَرُوْا لَوْ كَانُوْا مُسْلِمِيْنَ ', 'rubam<u>aa</u> yawaddu <strong>al</strong>la<u>dz</u>iina kafaruu law k<u>aa</u>nuu muslimiin<strong>a</strong>', 'Orang kafir itu kadang-kadang (nanti di akhirat) menginginkan, sekiranya mereka dahulu (di dunia) menjadi orang Muslim.'),
	(1805, 15, 3, 'ذَرْهُمْ يَأْكُلُوْا وَيَتَمَتَّعُوْا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُوْنَ ', '<u>dz</u>arhum ya/kuluu wayatamatta\'uu wayulhihimu <strong>a</strong>l-amalu fasawfa ya\'lamuun<strong>a</strong>', 'Biarkanlah mereka (di dunia ini) makan dan bersenang-senang dan dilalaikan oleh angan-angan (kosong) mereka, kelak mereka akan mengetahui (akibat perbuatannya).'),
	(1806, 15, 4, 'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَّعْلُوْمٌ ', 'wam<u>aa</u> ahlakn<u>aa</u> min qaryatin ill<u>aa</u> walah<u>aa</u> kit<u>aa</u>bun ma\'luum<strong>un</strong>', 'Dan Kami tidak membinasakan suatu negeri, melainkan sudah ada ketentuan yang ditetapkan baginya.'),
	(1807, 15, 5, 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ', 'm<u>aa</u> tasbiqu min ummatin ajalah<u>aa</u> wam<u>aa</u> yasta/khiruun<strong>a</strong>', 'Tidak ada suatu umat pun yang dapat mendahului ajalnya, dan tidak (pula) dapat meminta penundaan(nya).'),
	(1808, 15, 6, 'وَقَالُوْا يٰٓاَيُّهَا الَّذِيْ نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُوْنٌ  ۗ  ', 'waq<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>ii nuzzila \'alayhi <strong>al</strong><u>dzdz</u>ikru innaka lamajnuun<strong>un</strong>', 'Dan mereka berkata, “Wahai orang yang kepadanya diturunkan Al-Qur\'an, sesungguhnya engkau (Muhammad) benar-benar orang gila.'),
	(1809, 15, 7, 'لَوْمَا تَأْتِيْنَا بِالْمَلٰۤىِٕكَةِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ', 'law m<u>aa</u> ta/tiin<u>aa</u> bi<strong>a</strong>lmal<u>aa</u>-ikati in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Mengapa engkau tidak mendatangkan malaikat kepada kami, jika engkau termasuk orang yang benar?”'),
	(1810, 15, 8, 'مَا نُنَزِّلُ الْمَلٰۤىِٕكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُوْٓا اِذًا مُّنْظَرِيْنَ ', 'm<u>aa</u> nunazzilu <strong>a</strong>lmal<u>aa</u>-ikata ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wam<u>aa</u> k<u>aa</u>nuu i<u>dz</u>an mun<u>zh</u>ariin<strong>a</strong>', 'Kami tidak menurunkan malaikat melainkan dengan kebenaran (untuk membawa azab) dan mereka ketika itu tidak diberikan penangguhan'),
	(1811, 15, 9, 'اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهٗ لَحٰفِظُوْنَ ', 'inn<u>aa</u> na<u>h</u>nu nazzaln<u>aa</u> <strong>al</strong><u>dzdz</u>ikra wa-inn<u>aa</u> lahu la<u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'Sesungguhnya Kamilah yang menurunkan Al-Qur\'an, dan pasti Kami (pula) yang memeliharanya.'),
	(1812, 15, 10, 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ شِيَعِ الْاَوَّلِيْنَ ', 'walaqad arsaln<u>aa</u> min qablika fii syiya\'i <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dan sungguh, Kami telah mengutus (beberapa rasul) sebelum engkau (Muhammad) kepada umat-umat terdahulu.'),
	(1813, 15, 11, 'وَمَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ', 'wam<u>aa</u> ya/tiihim min rasuulin ill<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Dan setiap kali seorang rasul datang kepada mereka, mereka selalu memperolok-olokannya.'),
	(1814, 15, 12, 'كَذٰلِكَ نَسْلُكُهٗ فِيْ قُلُوْبِ الْمُجْرِمِيْنَۙ', 'ka<u>dzaa</u>lika naslukuhu fii quluubi <strong>a</strong>lmujrimiin<strong>a</strong>', 'Demikianlah, Kami memasukkannya (olok-olok itu) ke dalam hati orang yang berdosa,'),
	(1815, 15, 13, 'لَا يُؤْمِنُوْنَ بِهٖ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّلِيْنَ ', 'l<u>aa</u> yu/minuuna bihi waqad khalat sunnatu <strong>a</strong>l-awwaliin<strong>a</strong>', 'mereka tidak beriman kepadanya (Al-Qur\'an) padahal telah berlalu sunatullah terhadap orang-orang terdahulu.'),
	(1816, 15, 14, 'وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِّنَ السَّمَاۤءِ فَظَلُّوْا فِيْهِ يَعْرُجُوْنَۙ', 'walaw fata<u>h</u>n<u>aa</u> \'alayhim b<u>aa</u>ban mina <strong>al</strong>ssam<u>aa</u>-i fa<u>zh</u>alluu fiihi ya\'rujuun<strong>a</strong>', 'Dan kalau Kami bukakan kepada mereka salah satu pintu langit, lalu mereka terus menerus naik ke atasnya,'),
	(1817, 15, 15, 'لَقَالُوْٓا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَّسْحُوْرُوْنَ ࣖ ', 'laq<u>aa</u>luu innam<u>aa</u> sukkirat ab<u>shaa</u>run<u>aa</u> bal na<u>h</u>nu qawmun mas<u>h</u>uuruun<strong>a</strong>', 'tentulah mereka berkata, “Sesungguhnya pandangan kamilah yang dikaburkan, bahkan kami adalah orang yang terkena sihir.”  '),
	(1818, 15, 16, 'وَلَقَدْ جَعَلْنَا فِى السَّمَاۤءِ بُرُوْجًا وَّزَيَّنّٰهَا لِلنّٰظِرِيْنَۙ', 'walaqad ja\'aln<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>-i buruujan wazayyann<u>aa</u>h<u>aa</u> li<strong>l</strong>nn<u>aats</u>iriin<strong>a</strong>', 'Dan sungguh, Kami telah menciptakan gugusan bintang di langit dan men-jadikannya terasa indah bagi orang yang memandang(nya),'),
	(1819, 15, 17, 'وَحَفِظْنٰهَا مِنْ كُلِّ شَيْطٰنٍ رَّجِيْمٍۙ', 'wa<u>h</u>afi<u>zh</u>n<u>aa</u>h<u>aa</u> min kulli syay<u>th</u><u>aa</u>nin rajiim<strong>in</strong>', 'dan Kami menjaganya dari setiap (gangguan) setan yang terkutuk,'),
	(1820, 15, 18, 'اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهٗ شِهَابٌ مُّبِيْنٌ ', 'ill<u>aa</u> mani istaraqa <strong>al</strong>ssam\'a fa-atba\'ahu syih<u>aa</u>bun mubiin<strong>un</strong>', 'kecuali (setan) yang mencuri-curi (berita) yang dapat didengar (dari malaikat) lalu dikejar oleh semburan api yang terang.'),
	(1821, 15, 19, 'وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ شَيْءٍ مَّوْزُوْنٍ', 'wa<strong>a</strong>l-ar<u>dh</u>a madadn<u>aa</u>h<u>aa</u> wa-alqayn<u>aa</u> fiih<u>aa</u> raw<u>aa</u>siya wa-anbatn<u>aa</u> fiih<u>aa</u> min kulli syay-in mawzuun<strong>in</strong>', 'Dan Kami telah menghamparkan bumi dan Kami pancangkan padanya gunung-gunung serta Kami tumbuhkan di sana segala sesuatu menurut ukuran.'),
	(1822, 15, 20, 'وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَ وَمَنْ لَّسْتُمْ لَهٗ بِرَازِقِيْنَ ', 'waja\'aln<u>aa</u> lakum fiih<u>aa</u> ma\'<u>aa</u>yisya waman lastum lahu bir<u>aa</u>ziqiin<strong>a</strong>', 'Dan Kami telah menjadikan padanya sumber-sumber kehidupan untuk keperluanmu, dan (Kami ciptakan pula) makhluk-makhluk yang bukan kamu pemberi rezekinya.'),
	(1823, 15, 21, 'وَاِنْ مِّنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَاۤىِٕنُهٗ وَمَا نُنَزِّلُهٗٓ اِلَّا بِقَدَرٍ مَّعْلُوْمٍ ', 'wa-in min syay-in ill<u>aa</u> \'indan<u>aa</u> khaz<u>aa</u>-inuhu wam<u>aa</u> nunazziluhu ill<u>aa</u> biqadarin ma\'luum<strong>in</strong>', 'Dan tidak ada sesuatu pun, melainkan pada sisi Kamilah khazanahnya; Kami tidak menurunkannya melainkan dengan ukuran tertentu.'),
	(1824, 15, 22, 'وَاَرْسَلْنَا الرِّيٰحَ لَوَاقِحَ فَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَسْقَيْنٰكُمُوْهُۚ وَمَآ اَنْتُمْ لَهٗ بِخَازِنِيْنَ', 'wa-arsaln<u>aa</u> <strong>al</strong>rriy<u>aah</u>a law<u>aa</u>qi<u>h</u>a fa-anzaln<u>aa</u> mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-asqayn<u>aa</u>kumuuhu wam<u>aa</u> antum lahu bikh<u>aa</u>ziniin<strong>a</strong>', 'Dan Kami telah meniupkan angin untuk mengawinkan dan Kami turunkan hujan dari langit, lalu Kami beri minum kamu dengan (air) itu, dan bukanlah kamu yang menyimpannya.'),
	(1825, 15, 23, 'وَاِنَّا لَنَحْنُ نُحْيٖ وَنُمِيْتُ وَنَحْنُ الْوَارِثُوْنَ ', 'wa-inn<u>aa</u> lana<u>h</u>nu nu<u>h</u>yii wanumiitu wana<u>h</u>nu <strong>a</strong>lw<u>aa</u>ritsuun<strong>a</strong>', 'Dan sungguh, Kamilah yang menghidupkan dan mematikan dan Kami (pulalah) yang mewarisi.'),
	(1826, 15, 24, 'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِيْنَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِيْنَ ', 'walaqad \'alimn<u>aa</u> <strong>a</strong>lmustaqdimiina minkum walaqad \'alimn<u>aa</u> <strong>a</strong>lmusta/khiriin<strong>a</strong>', 'Dan sungguh, Kami mengetahui orang yang terdahulu sebelum kamu dan Kami mengetahui pula orang yang terkemudian.'),
	(1827, 15, 25, 'وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ࣖ ', 'wa-inna rabbaka huwa ya<u>h</u>syuruhum innahu <u>h</u>akiimun \'aliim<strong>u</strong>', 'Dan sesungguhnya Tuhanmu, Dialah yang akan mengumpulkan mereka. Sungguh, Dia Mahabijaksana, Maha Mengetahui.'),
	(1828, 15, 26, 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ  ', 'walaqad khalaqn<u>aa</u> <strong>a</strong>l-ins<u>aa</u>na min <u>sh</u>al<u>shaa</u>lin min <u>h</u>ama-in masnuun<strong>in</strong>', 'Dan sungguh, Kami telah menciptakan manusia (Adam) dari tanah liat kering dari lumpur hitam yang diberi bentuk.'),
	(1829, 15, 27, 'وَالْجَاۤنَّ خَلَقْنٰهُ مِنْ قَبْلُ مِنْ نَّارِ السَّمُوْمِ  ', 'wa<strong>a</strong>lj<u>aa</u>nna khalaqn<u>aa</u>hu min qablu min n<u>aa</u>ri <strong>al</strong>ssamuum<strong>i</strong>', 'Dan Kami telah menciptakan jin sebelum (Adam) dari api yang sangat panas.'),
	(1830, 15, 28, 'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ  ', 'wa-i<u>dz</u> q<u>aa</u>la rabbuka lilmal<u>aa</u>-ikati innii kh<u>aa</u>liqun basyaran min <u>sh</u>al<u>shaa</u>lin min <u>h</u>ama-in masnuun<strong>in</strong>', 'Dan (ingatlah), ketika Tuhanmu berfirman kepada para malaikat, “Sungguh, Aku akan menciptakan seorang manusia dari tanah liat kering dari lumpur hitam yang diberi bentuk.'),
	(1831, 15, 29, 'فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ ', 'fa-i<u>dzaa</u> sawwaytuhu wanafakhtu fiihi min ruu<u>h</u>ii faqa\'uu lahu s<u>aa</u>jidiin<strong>a</strong>', 'Maka apabila Aku telah menyempurnakan (kejadian)nya, dan Aku telah meniupkan roh (ciptaan)-Ku ke dalamnya, maka tunduklah kamu kepadanya dengan bersujud.”'),
	(1832, 15, 30, 'فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ', 'fasajada <strong>a</strong>lmal<u>aa</u>-ikatu kulluhum ajma\'uun<strong>a</strong>', 'Maka bersujudlah para malaikat itu semuanya bersama-sama,'),
	(1833, 15, 31, 'اِلَّآ اِبْلِيْسَۗ اَبٰىٓ اَنْ يَّكُوْنَ مَعَ السّٰجِدِيْنَ ', 'ill<u>aa</u> ibliisa ab<u>aa</u> an yakuuna ma\'a <strong>al</strong>ss<u>aa</u>jidiin<strong>a</strong>', 'kecuali Iblis. Ia enggan ikut bersama-sama para (malaikat) yang sujud itu.'),
	(1834, 15, 32, 'قَالَ يٰٓاِبْلِيْسُ مَا لَكَ اَلَّا تَكُوْنَ مَعَ السّٰجِدِيْنَ ', 'q<u>aa</u>la y<u>aa</u> ibliisu m<u>aa</u> laka <strong>al</strong>l<u>aa</u> takuuna ma\'a <strong>al</strong>ss<u>aa</u>jidiin<strong>a</strong>', 'Dia (Allah) berfirman, “Wahai Iblis! Apa sebabnya kamu (tidak ikut) sujud bersama mereka?”'),
	(1835, 15, 33, 'قَالَ لَمْ اَكُنْ لِّاَسْجُدَ لِبَشَرٍ خَلَقْتَهٗ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍ ', 'q<u>aa</u>la lam akun li-asjuda libasyarin khalaqtahu min <u>sh</u>al<u>shaa</u>lin min <u>h</u>ama-in masnuun<strong>in</strong>', 'Ia (Iblis) berkata, “Aku sekali-kali tidak akan sujud kepada manusia yang Engkau telah menciptakannya dari tanah liat kering dari lumpur hitam yang diberi bentuk.”'),
	(1836, 15, 34, 'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۙ', 'q<u>aa</u>la fa<strong>u</strong>khruj minh<u>aa</u> fa-innaka rajiim<strong>un</strong>', 'Dia (Allah) berfirman, “(Kalau begitu) keluarlah dari surga, karena sesungguhnya kamu terkutuk,'),
	(1837, 15, 35, 'وَّاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدِّيْنِ ', 'wa-inna \'alayka <strong>al</strong>la\'nata il<u>aa</u> yawmi <strong>al</strong>ddiin<strong>i</strong>', ' dan sesungguhnya kutukan itu tetap menimpamu sampai hari Kiamat.” '),
	(1838, 15, 36, 'قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ', 'q<u>aa</u>la rabbi fa-an<u>zh</u>irnii il<u>aa</u> yawmi yub\'atsuun<strong>a</strong>', 'Ia (Iblis) berkata, “Ya Tuhanku,  (kalau begitu) maka berilah penangguhan kepadaku sampai hari (manusia) dibangkitkan.”'),
	(1839, 15, 37, 'قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ', 'q<u>aa</u>la fa-innaka mina <strong>a</strong>lmun<u>zh</u>ariin<strong>a</strong>', ' Allah berfirman, “(Baiklah) maka sesungguhnya kamu termasuk yang diberi penangguhan,'),
	(1840, 15, 38, 'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ ', 'il<u>aa</u> yawmi <strong>a</strong>lwaqti <strong>a</strong>lma\'luum<strong>i</strong>', 'sampai hari yang telah ditentukan (kiamat).”'),
	(1841, 15, 39, 'قَالَ رَبِّ بِمَآ اَغْوَيْتَنِيْ لَاُزَيِّنَنَّ لَهُمْ فِى الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ  ', 'q<u>aa</u>la rabbi bim<u>aa</u> aghwaytanii lauzayyinanna lahum fii <strong>a</strong>l-ar<u>dh</u>i walaughwiyannahum ajma\'iin<strong>a</strong>', 'Ia (Iblis) berkata, “Tuhanku, oleh karena Engkau telah memutuskan bahwa aku sesat, aku pasti akan jadikan (kejahatan) terasa indah bagi mereka di bumi, dan aku akan menyesatkan mereka semuanya,'),
	(1842, 15, 40, 'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ ', 'ill<u>aa</u> \'ib<u>aa</u>daka minhumu <strong>a</strong>lmukhla<u>sh</u>iin<strong>a</strong>', ' kecuali hamba-hamba-Mu yang terpilih di antara mereka.”'),
	(1843, 15, 41, 'قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيْمٌ ', 'q<u>aa</u>la h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un \'alayya mustaqiim<strong>un</strong>', 'Dia (Allah) berfirman, “Ini adalah jalan yang lurus (menuju) kepada-Ku.”'),
	(1844, 15, 42, 'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغٰوِيْنَ ', 'inna \'ib<u>aa</u>dii laysa laka \'alayhim sul<u>th</u><u>aa</u>nun ill<u>aa</u> mani ittaba\'aka mina <strong>a</strong>lgh<u>aa</u>wiin<strong>a</strong>', 'Sesungguhnya kamu (Iblis) tidak kuasa atas hamba-hamba-Ku, kecuali mereka yang mengikutimu, yaitu orang yang sesat.'),
	(1845, 15, 43, 'وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَعِيْنَۙ', 'wa-inna jahannama lamaw\'iduhum ajma\'iin<strong>a</strong>', 'Dan sungguh, Jahanam itu benar-benar (tempat) yang telah dijanjikan untuk mereka (pengikut setan) semuanya.'),
	(1846, 15, 44, 'لَهَا سَبْعَةُ اَبْوَابٍۗ لِكُلِّ بَابٍ مِّنْهُمْ جُزْءٌ مَّقْسُوْمٌ ࣖ  ', 'lah<u>aa</u> sab\'atu abw<u>aa</u>bin likulli b<u>aa</u>bin minhum juz-un maqsuum<strong>un</strong>', '(Jahanam) itu mempunyai tujuh pintu. Setiap pintu (telah ditetapkan) untuk golongan tertentu dari mereka.'),
	(1847, 15, 45, 'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۗ  ', 'inna <strong>a</strong>lmuttaqiina fii jann<u>aa</u>tin wa\'uyuun<strong>in</strong>', 'Sesungguhnya orang yang bertakwa itu berada dalam surga-surga (taman-taman), dan (di dekat) mata air (yang mengalir).'),
	(1848, 15, 46, 'اُدْخُلُوْهَا بِسَلٰمٍ اٰمِنِيْنَ ', 'udkhuluuh<u>aa</u> bisal<u>aa</u>min <u>aa</u>miniin<strong>a</strong>', '(Allah berfirman), “Masuklah ke dalamnya dengan sejahtera dan aman.”'),
	(1849, 15, 47, 'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ اِخْوَانًا عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ ', 'wanaza\'n<u>aa</u> m<u>aa</u> fii <u>sh</u>uduurihim min ghillin ikhw<u>aa</u>nan \'al<u>aa</u> sururin mutaq<u>aa</u>biliin<strong>a</strong>', 'Dan Kami lenyapkan segala rasa dendam yang ada dalam hati mereka; mereka merasa bersaudara, duduk berhadap-hadapan di atas dipan-dipan.'),
	(1850, 15, 48, 'لَا يَمَسُّهُمْ فِيْهَا نَصَبٌ وَّمَا هُمْ مِّنْهَا بِمُخْرَجِيْنَ ', 'l<u>aa</u> yamassuhum fiih<u>aa</u> na<u>sh</u>abun wam<u>aa</u> hum minh<u>aa</u> bimukhrajiin<strong>a</strong>', 'Mereka tidak merasa lelah di dalamnya dan mereka tidak akan dikeluarkan darinya.'),
	(1851, 15, 49, '۞ نَبِّئْ عِبَادِيْٓ اَنِّيْٓ اَنَا الْغَفُوْرُ الرَّحِيْمُۙ', 'nabbi/ \'ib<u>aa</u>dii annii an<u>aa</u> <strong>a</strong>lghafuuru <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Kabarkanlah kepada hamba-hamba-Ku, bahwa Akulah Yang Maha Pengampun, Maha Penyayang,'),
	(1852, 15, 50, 'وَاَنَّ عَذَابِيْ هُوَ الْعَذَابُ الْاَلِيْمُ ', 'wa-anna \'a<u>dzaa</u>bii huwa <strong>a</strong>l\'a<u>dzaa</u>bu <strong>a</strong>l-aliim<strong>u</strong>', 'dan sesungguhnya azab-Ku adalah azab yang sangat pedih.'),
	(1853, 15, 51, 'وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰهِيْمَۘ ', 'wanabbi/hum \'an <u>dh</u>ayfi ibr<u>aa</u>hiim<strong>a</strong>', 'Dan kabarkanlah (Muhammad) kepada mereka tentang tamu Ibrahim (malaikat).'),
	(1854, 15, 52, 'اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًاۗ قَالَ اِنَّا مِنْكُمْ وَجِلُوْنَ ', 'i<u>dz</u> dakhaluu \'alayhi faq<u>aa</u>luu sal<u>aa</u>man q<u>aa</u>la inn<u>aa</u> minkum wajiluun<strong>a</strong>', 'Ketika mereka masuk ke tempatnya, lalu mereka mengucapkan, “Salam.” Dia (Ibrahim) berkata, “Kami benar-benar merasa takut kepadamu.” '),
	(1855, 15, 53, 'قَالُوْا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلٰمٍ عَلِيْمٍ ', 'q<u>aa</u>luu l<u>aa</u> tawjal inn<u>aa</u> nubasysyiruka bighul<u>aa</u>min \'aliim<strong>in</strong>', '(Mereka) berkata, “Janganlah engkau merasa takut, sesungguhnya kami memberi kabar gembira kepadamu dengan (kelahiran seorang) anak laki-laki (yang akan menjadi) orang yang pandai (Ishak).”  '),
	(1856, 15, 54, 'قَالَ اَبَشَّرْتُمُوْنِيْ عَلٰٓى اَنْ مَّسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُوْنَ ', 'q<u>aa</u>la abasysyartumuunii \'al<u>aa</u> an massaniya <strong>a</strong>lkibaru fabima tubasysyiruun<strong>i</strong>', 'Dia (Ibrahim) berkata, “Benarkah kamu memberi kabar gembira kepadaku padahal usiaku telah lanjut, lalu (dengan cara) bagaimana kamu memberi (kabar gembira) tersebut?”'),
	(1857, 15, 55, 'قَالُوْا بَشَّرْنٰكَ بِالْحَقِّ فَلَا تَكُنْ مِّنَ الْقٰنِطِيْنَ ', 'q<u>aa</u>luu basysyarn<u>aa</u>ka bi<strong>a</strong>l<u>h</u>aqqi fal<u>aa</u> takun mina <strong>a</strong>lq<u>aa</u>ni<u>th</u>iin<strong>a</strong>', '(Mereka) menjawab, “Kami menyampaikan kabar gembira kepadamu dengan benar, maka janganlah engkau termasuk orang yang berputus asa.”'),
	(1858, 15, 56, 'قَالَ وَمَنْ يَّقْنَطُ مِنْ رَّحْمَةِ رَبِّهٖٓ اِلَّا الضَّاۤلُّوْنَ ', 'q<u>aa</u>la waman yaqna<u>th</u>u min ra<u>h</u>mati rabbihi ill<u>aa</u> <strong>al</strong><u>dhdhaa</u>lluun<strong>a</strong>', 'Dia (Ibrahim) berkata, “Tidak ada yang berputus asa dari rahmat Tuhannya, kecuali orang yang sesat.”'),
	(1859, 15, 57, 'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَ ', 'q<u>aa</u>la fam<u>aa</u> kha<u>th</u>bukum ayyuh<u>aa</u> <strong>a</strong>lmursaluun<strong>a</strong>', ' Dia (Ibrahim) berkata, “Apakah urusanmu yang penting, wahai para utusan?”'),
	(1860, 15, 58, 'قَالُوْٓا اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ', 'q<u>aa</u>luu inn<u>aa</u> ursiln<u>aa</u> il<u>aa</u> qawmin mujrimiin<strong>a</strong>', '(Mereka) menjawab, “Sesungguhnya kami diutus kepada kaum yang berdosa,'),
	(1861, 15, 59, 'اِلَّآ اٰلَ لُوْطٍۗ اِنَّا لَمُنَجُّوْهُمْ اَجْمَعِيْنَۙ', 'ill<u>aa</u> <u>aa</u>la luu<u>th</u>in inn<u>aa</u> lamunajjuuhum ajma\'iin<strong>a</strong>', 'kecuali para pengikut Lut. Sesungguhnya kami pasti menyelamatkan mereka semuanya,'),
	(1862, 15, 60, 'اِلَّا امْرَاَتَهٗ قَدَّرْنَآ اِنَّهَا لَمِنَ الْغٰبِرِيْنَ ࣖ ', 'ill<u>aa</u> imra-atahu qaddarn<u>aa</u> innah<u>aa</u> lamina <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'kecuali istrinya, kami telah menentukan, bahwa dia termasuk orang yang tertinggal (bersama orang kafir lainnya).”'),
	(1863, 15, 61, 'فَلَمَّا جَاۤءَ اٰلَ لُوْطِ ِۨالْمُرْسَلُوْنَۙ', 'falamm<u>aa</u> j<u>aa</u>-a <u>aa</u>la luu<u>th</u>in <strong>a</strong>lmursaluun<strong>a</strong>', 'Maka ketika utusan itu datang kepada para pengikut Lut,'),
	(1864, 15, 62, 'قَالَ اِنَّكُمْ قَوْمٌ مُّنْكَرُوْنَ ', 'q<u>aa</u>la innakum qawmun munkaruun<strong>a</strong>', 'dia (Lut) berkata, “Sesungguhnya kamu orang yang tidak kami kenal.” '),
	(1865, 15, 63, 'قَالُوْا بَلْ جِئْنٰكَ بِمَا كَانُوْا فِيْهِ يَمْتَرُوْنَ ', 'q<u>aa</u>luu bal ji/n<u>aa</u>ka bim<u>aa</u> k<u>aa</u>nuu fiihi yamtaruun<strong>a</strong>', '(Para utusan) menjawab, “Sebenarnya kami ini datang kepadamu membawa azab yang selalu mereka dustakan.'),
	(1866, 15, 64, 'وَاَتَيْنٰكَ بِالْحَقِّ وَاِنَّا لَصٰدِقُوْنَ ', 'wa-atayn<u>aa</u>ka bi<strong>a</strong>l<u>h</u>aqqi wa-inn<u>aa</u> la<u>shaa</u>diquun<strong>a</strong>', 'Dan kami datang kepadamu membawa kebenaran dan sungguh, kami orang yang benar.'),
	(1867, 15, 65, 'فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَاتَّبِعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَّامْضُوْا حَيْثُ تُؤْمَرُوْنَ ', 'fa-asri bi-ahlika biqi<u>th</u>\'in mina <strong>al</strong>layli wa<strong>i</strong>ttabi\' adb<u>aa</u>rahum wal<u>aa</u> yaltafit minkum a<u>h</u>adun wa<strong>i</strong>m<u>dh</u>uu <u>h</u>aytsu tu/maruun<strong>a</strong>', 'Maka pergilah kamu pada akhir malam beserta keluargamu, dan ikutilah mereka dari belakang. Jangan ada di antara kamu yang menoleh ke belakang dan teruskanlah perjalanan ke tempat yang diperintahkan kepadamu.”  '),
	(1868, 15, 66, 'وَقَضَيْنَآ اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُلَاۤءِ مَقْطُوْعٌ مُّصْبِحِيْنَ ', 'waqa<u>dh</u>ayn<u>aa</u> ilayhi <u>dzaa</u>lika <strong>a</strong>l-amra anna d<u>aa</u>bira h<u>aa</u>ul<u>aa</u>-i maq<u>th</u>uu\'un mu<u>sh</u>bi<u>h</u>iin<strong>a</strong>', 'Dan telah Kami tetapkan kepadanya (Lut) keputusan itu, bahwa akhirnya mereka akan ditumpas habis pada waktu subuh.'),
	(1869, 15, 67, 'وَجَاۤءَ اَهْلُ الْمَدِيْنَةِ يَسْتَبْشِرُوْنَ ', 'waj<u>aa</u>-a ahlu <strong>a</strong>lmadiinati yastabsyiruun<strong>a</strong>', 'Dan datanglah penduduk kota itu (ke rumah Lut) dengan gembira (karena kedatangan tamu itu).'),
	(1870, 15, 68, 'قَالَ اِنَّ هٰٓؤُلَاۤءِ ضَيْفِيْ فَلَا تَفْضَحُوْنِۙ', 'q<u>aa</u>la inna h<u>aa</u>ul<u>aa</u>-i <u>dh</u>ayfii fal<u>aa</u> taf<u>dh</u>a<u>h</u>uun<strong>i</strong>', 'Dia (Lut) berkata, “Sesungguhnya mereka adalah tamuku; maka jangan kamu mempermalukan aku,'),
	(1871, 15, 69, 'وَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ ', 'wa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> tukhzuun<strong>i</strong>', 'Dan bertakwalah kepada Allah dan janganlah kamu membuat aku terhina.” '),
	(1872, 15, 70, 'قَالُوْٓا اَوَلَمْ نَنْهَكَ عَنِ الْعٰلَمِيْنَ ', 'q<u>aa</u>luu awa lam nanhaka \'ani <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', ' (Mereka) berkata, “Bukankah kami telah melarangmu dari (melindungi) manusia?”'),
	(1873, 15, 71, 'قَالَ هٰٓؤُلَاۤءِ بَنٰتِيْٓ اِنْ كُنْتُمْ فٰعِلِيْنَۗ ', 'q<u>aa</u>la h<u>aa</u>ul<u>aa</u>-i ban<u>aa</u>tii in kuntum f<u>aa</u>\'iliin<strong>a</strong>', 'Dia (Lut) berkata, “Mereka itulah putri-putri (negeri)ku (nikahlah dengan mereka), jika kamu hendak berbuat.”'),
	(1874, 15, 72, 'لَعَمْرُكَ اِنَّهُمْ لَفِيْ سَكْرَتِهِمْ يَعْمَهُوْنَ ', 'la\'amruka innahum lafii sakratihim ya\'mahuuna', '(Allah berfirman), “Demi umurmu (Muhammad), sungguh, mereka terombang-ambing dalam kemabukan (kesesatan).”'),
	(1875, 15, 73, 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِيْنَۙ', 'fa-akha<u>dz</u>at-humu <strong>al</strong><u>shsh</u>ay<u>h</u>atu musyriqiin<strong>a</strong>', 'Maka mereka dibinasakan oleh suara keras yang mengguntur, ketika matahari akan terbit.'),
	(1876, 15, 74, 'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِّنْ سِجِّيْلٍ ', 'faja\'aln<u>aa</u> \'<u>aa</u>liyah<u>aa</u> s<u>aa</u>filah<u>aa</u> wa-am<u>th</u>arn<u>aa</u> \'alayhim <u>h</u>ij<u>aa</u>ratan min sijjiil<strong>in</strong>', 'Maka Kami jungkirbalikkan (negeri itu) dan Kami hujani mereka dengan batu dari tanah yang keras.'),
	(1877, 15, 75, 'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْمُتَوَسِّمِيْنَۙ', 'inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin lilmutawassimiin<strong>a</strong>', 'Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang yang memperhatikan tanda-tanda,'),
	(1878, 15, 76, 'وَاِنَّهَا لَبِسَبِيْلٍ مُّقِيْمٍ ', 'wa-innah<u>aa</u> labisabiilin muqiim<strong>in</strong>', 'dan sungguh, (negeri) itu benar-benar terletak di jalan yang masih tetap (dilalui manusia).'),
	(1879, 15, 77, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَۗ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan lilmu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi orang yang beriman.'),
	(1880, 15, 78, 'وَاِنْ كَانَ اَصْحٰبُ الْاَيْكَةِ لَظٰلِمِيْنَۙ', 'wa-in k<u>aa</u>na a<u>sh</u>-<u>haa</u>bu <strong>a</strong>l-aykati la<u>zhaa</u>limiin<strong>a</strong>', 'Dan sesungguhnya penduduk Aikah itu benar-benar kaum yang zalim,'),
	(1881, 15, 79, 'فَانْتَقَمْنَا مِنْهُمْۘ وَاِنَّهُمَا لَبِاِمَامٍ مُّبِيْنٍۗ  ࣖ  ', 'fa<strong>i</strong>ntaqamn<u>aa</u> minhum wa-innahum<u>aa</u> labi-im<u>aa</u>min mubiin<strong>in</strong>', 'maka Kami membinasakan mereka. Dan sesungguhnya kedua (negeri) itu terletak di satu jalur jalan raya.'),
	(1882, 15, 80, 'وَلَقَدْ كَذَّبَ اَصْحٰبُ الْحِجْرِ الْمُرْسَلِيْنَۙ', 'walaqad ka<u>dzdz</u>aba a<u>sh</u>-<u>haa</u>bu <strong>a</strong>l<u>h</u>ijri <strong>a</strong>lmursaliin<strong>a</strong>', 'Dan sesungguhnya penduduk negeri Hijr benar-benar telah mendustakan para rasul (mereka),'),
	(1883, 15, 81, 'وَاٰتَيْنٰهُمْ اٰيٰتِنَا فَكَانُوْا عَنْهَا مُعْرِضِيْنَۙ', 'wa<u>aa</u>tayn<u>aa</u>hum <u>aa</u>y<u>aa</u>tin<u>aa</u> fak<u>aa</u>nuu \'anh<u>aa</u> mu\'ri<u>dh</u>iin<strong>a</strong>', 'dan Kami telah mendatangkan kepada mereka tanda-tanda (kekuasaan) Kami, tetapi mereka selalu berpaling darinya, '),
	(1884, 15, 82, 'وَكَانُوْا يَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا اٰمِنِيْنَ ', 'wak<u>aa</u>nuu yan<u>h</u>ituuna mina <strong>a</strong>ljib<u>aa</u>li buyuutan <u>aa</u>miniin<strong>a</strong>', 'dan mereka memahat rumah-rumah dari gunung batu, (yang didiami) dengan rasa aman.'),
	(1885, 15, 83, 'فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِيْنَۙ', 'fa-akha<u>dz</u>at-humu <strong>al</strong><u>shsh</u>ay<u>h</u>atu mu<u>sh</u>bi<u>h</u>iin<strong>a</strong>', 'Kemudian mereka dibinasakan oleh suara keras yang mengguntur pada pagi hari,'),
	(1886, 15, 84, 'فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَۗ  ', 'fam<u>aa</u> aghn<u>aa</u> \'anhum m<u>aa</u> k<u>aa</u>nuu yaksibuun<strong>a</strong>', 'sehingga tidak berguna bagi mereka, apa yang telah mereka usahakan. '),
	(1887, 15, 85, 'وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّۗ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيْلَ ', 'wam<u>aa</u> khalaqn<u>aa</u> <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wa-inna <strong>al</strong>ss<u>aa</u>\'ata la<u>aa</u>tiyatun fa<strong>i</strong><u>sh</u>fa<u>h</u>i <strong>al</strong><u>shsh</u>af<u>h</u>a <strong>a</strong>ljamiil<strong>a</strong>', 'Dan Kami tidak menciptakan langit dan bumi serta apa yang ada di antara keduanya, melainkan dengan kebenaran. Dan sungguh, Kiamat pasti akan datang, maka maafkanlah (mereka) dengan cara yang baik.'),
	(1888, 15, 86, 'اِنَّ رَبَّكَ هُوَ الْخَلّٰقُ الْعَلِيْمُ ', 'inna rabbaka huwa <strong>a</strong>lkhall<u>aa</u>qu <strong>a</strong>l\'aliim<strong>u</strong>', 'Sungguh, Tuhanmu, Dialah Yang Maha Pencipta, Maha Mengetahui.'),
	(1889, 15, 87, 'وَلَقَدْ اٰتَيْنٰكَ سَبْعًا مِّنَ الْمَثَانِيْ وَالْقُرْاٰنَ الْعَظِيْمَ ', 'walaqad <u>aa</u>tayn<u>aa</u>ka sab\'an mina <strong>a</strong>lmats<u>aa</u>nii wa<strong>a</strong>lqur-<u>aa</u>na <strong>a</strong>l\'a<u>zh</u>iim<strong>a</strong>', 'Dan sungguh, Kami telah memberikan kepadamu tujuh (ayat) yang (dibaca) berulang-ulang dan Al-Qur\'an yang agung.'),
	(1890, 15, 88, 'لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِيْنَ ', 'l<u>aa</u> tamuddanna \'aynayka il<u>aa</u> m<u>aa</u> matta\'n<u>aa</u> bihi azw<u>aa</u>jan minhum wal<u>aa</u> ta<u>h</u>zan \'alayhim wa<strong>i</strong>khfi<u>dh</u> jan<u>aah</u>aka lilmu/miniin<strong>a</strong>', 'Jangan sekali-kali engkau (Muhammad) tujukan pandanganmu kepada kenikmatan hidup yang telah Kami berikan kepada beberapa golongan di antara mereka (orang kafir), dan jangan engkau bersedih hati terhadap mereka dan berendah hatilah engkau terhadap orang yang beriman.'),
	(1891, 15, 89, 'وَقُلْ اِنِّيْٓ اَنَا النَّذِيْرُ الْمُبِيْنُۚ  ', 'waqul innii an<u>aa</u> <strong>al</strong>nna<u>dz</u>iiru <strong>a</strong>lmubiin<strong>u</strong>', 'Dan katakanlah (Muhammad), “Sesungguhnya aku adalah pemberi peringatan yang jelas.”'),
	(1892, 15, 90, 'كَمَآ اَنْزَلْنَا عَلَى الْمُقْتَسِمِيْنَۙ', 'kam<u>aa</u> anzaln<u>aa</u> \'al<u>aa</u> <strong>a</strong>lmuqtasimiin<strong>a</strong>', 'Sebagaimana (Kami telah memberi peringatan), Kami telah menurunkan (azab) kepada orang yang memilah-milah (Kitab Allah),'),
	(1893, 15, 91, 'الَّذِيْنَ جَعَلُوا الْقُرْاٰنَ عِضِيْنَ ', '<strong>al</strong>la<u>dz</u>iina ja\'aluu <strong>a</strong>lqur-<u>aa</u>na \'i<u>dh</u>iin<strong>a</strong>', '(yaitu) orang-orang yang telah menjadikan Al-Qur\'an itu terbagi-bagi.'),
	(1894, 15, 92, 'فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَعِيْنَۙ', 'fawarabbika lanas-alannahum ajma\'iin<strong>a</strong>', 'Maka demi Tuhanmu, Kami pasti akan menanyai mereka semua,'),
	(1895, 15, 93, 'عَمَّا كَانُوْا يَعْمَلُوْنَ ', '\'amm<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'tentang apa yang telah mereka kerjakan dahulu. '),
	(1896, 15, 94, 'فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ ', 'fa<strong>i</strong><u>sh</u>da\' bim<u>aa</u> tu/maru wa-a\'ri<u>dh</u> \'ani <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Maka sampaikanlah (Muhammad) secara terang-terangan segala apa yang diperintahkan (kepadamu) dan berpalinglah dari orang yang musyrik.'),
	(1897, 15, 95, 'اِنَّا كَفَيْنٰكَ الْمُسْتَهْزِءِيْنَۙ', 'inn<u>aa</u> kafayn<u>aa</u>ka <strong>a</strong>lmustahzi-iin<strong>a</strong>', 'Sesungguhnya Kami memelihara engkau (Muhammad) dari (kejahatan) orang yang memperolok-olokkan (engkau),'),
	(1898, 15, 96, 'الَّذِيْنَ يَجْعَلُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۚ فَسَوْفَ يَعْلَمُوْنَ ', '<strong>al</strong>la<u>dz</u>iina yaj\'aluuna ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fasawfa ya\'lamuun<strong>a</strong>', ' (yaitu) orang yang menganggap adanya tuhan selain Allah; mereka kelak akan mengetahui (akibatnya).'),
	(1899, 15, 97, 'وَلَقَدْ نَعْلَمُ اَنَّكَ يَضِيْقُ صَدْرُكَ بِمَا يَقُوْلُوْنَۙ', 'walaqad na\'lamu annaka ya<u>dh</u>iiqu <u>sh</u>adruka bim<u>aa</u> yaquuluun<strong>a</strong>', 'Dan sungguh, Kami mengetahui bahwa dadamu menjadi sempit disebabkan apa yang mereka ucapkan, '),
	(1900, 15, 98, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُنْ مِّنَ السَّاجِدِيْنَۙ', 'fasabbi<u>h</u> bi<u>h</u>amdi rabbika wakun mina <strong>al</strong>ss<u>aa</u>jidiin<strong>a</strong>', 'maka bertasbihlah dengan memuji Tuhanmu dan jadilah engkau di antara orang yang bersujud (salat), '),
	(1901, 15, 99, 'وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَقِيْنُ ࣖࣖ ', 'wa<strong>u</strong>\'bud rabbaka <u>h</u>att<u>aa</u> ya/tiyaka <strong>a</strong>lyaqiin<strong>u</strong>', 'Dan sembahlah Tuhanmu sampai yakin (ajal) datang kepadamu.'),
	(1902, 16, 1, 'اَتٰىٓ اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوْهُ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ', 'at<u>aa</u> amru <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> tasta\'jiluuhu sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Ketetapan Allah pasti datang, maka janganlah kamu meminta agar dipercepat (datang)nya. Mahasuci Allah dan Mahatinggi Dia dari apa yang mereka persekutukan.'),
	(1903, 16, 2, 'يُنَزِّلُ الْمَلٰۤىِٕكَةَ بِالرُّوْحِ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اَنْ اَنْذِرُوْٓا اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاتَّقُوْنِ ', 'at<u>aa</u> amru <strong>al</strong>l<u>aa</u>hi fal<u>aa</u> tasta\'jiluuhu sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Dia menurunkan para malaikat membawa wahyu dengan perintah-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, (dengan berfirman) yaitu, “Peringatkanlah (hamba-hamba-Ku), bahwa tidak ada tuhan selain Aku, maka hendaklah kamu bertakwa kepada-Ku.”       '),
	(1904, 16, 3, 'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ تَعٰلٰى عَمَّا يُشْرِكُوْنَ ', 'khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a bi<strong>a</strong>l<u>h</u>aqqi ta\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Dia menciptakan langit dan bumi dengan kebenaran. Mahatinggi Allah dari apa yang mereka persekutukan.'),
	(1905, 16, 4, 'خَلَقَ الْاِنْسَانَ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ ', 'khalaqa <strong>a</strong>l-ins<u>aa</u>na min nu<u>th</u>fatin fa-i<u>dzaa</u> huwa kha<u>sh</u>iimun mubiin<strong>un</strong>', 'Dia telah menciptakan manusia dari mani, ternyata dia menjadi pembantah yang nyata.'),
	(1906, 16, 5, 'وَالْاَنْعَامَ خَلَقَهَا لَكُمْ فِيْهَا دِفْءٌ وَّمَنَافِعُ وَمِنْهَا تَأْكُلُوْنَ ', 'wa<strong>a</strong>l-an\'<u>aa</u>ma khalaqah<u>aa</u> lakum fiih<u>aa</u> dif-un waman<u>aa</u>fi\'u waminh<u>aa</u> ta-kuluun<strong>a</strong>', 'Dan hewan ternak telah diciptakan-Nya, untuk kamu padanya ada (bulu) yang menghangatkan dan berbagai manfaat, dan sebagiannya kamu makan.'),
	(1907, 16, 6, 'وَلَكُمْ فِيْهَا جَمَالٌ حِيْنَ تُرِيْحُوْنَ وَحِيْنَ تَسْرَحُوْنَۖ', 'walakum fiih<u>aa</u> jam<u>aa</u>lun <u>h</u>iina turii<u>h</u>uuna wa<u>h</u>iina tasra<u>h</u>uun<strong>a</strong>', 'Dan kamu memperoleh keindahan padanya, ketika kamu membawanya kembali ke kandang dan ketika kamu melepaskannya (ke tempat penggembalaan).'),
	(1908, 16, 7, 'وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَّمْ تَكُوْنُوْا بٰلِغِيْهِ اِلَّا بِشِقِّ الْاَنْفُسِۗ اِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌۙ', 'wata<u>h</u>milu atsq<u>aa</u>lakum il<u>aa</u> baladin lam takuunuu b<u>aa</u>lighiihi ill<u>aa</u> bisyiqqi <strong>a</strong>l-anfusi inna rabbakum larauufun ra<u>h</u>iim<strong>un</strong>', 'Dan ia mengangkut beban-bebanmu ke suatu negeri yang kamu tidak sanggup mencapainya, kecuali dengan susah payah. Sungguh, Tuhanmu Maha Pengasih, Maha Penyayang,'),
	(1909, 16, 8, 'وَّالْخَيْلَ وَالْبِغَالَ وَالْحَمِيْرَ لِتَرْكَبُوْهَا وَزِيْنَةًۗ وَيَخْلُقُ مَا لَا تَعْلَمُوْنَ', 'wa<strong>a</strong>lkhayla wa<strong>a</strong>lbigh<u>aa</u>la wa<strong>a</strong>l<u>h</u>amiira litarkabuuh<u>aa</u> waziinatan wayakhluqu m<u>aa</u> l<u>aa</u> ta\'lamuun<strong>a</strong>', 'dan (Dia telah menciptakan) kuda, bagal, dan keledai, untuk kamu tunggangi dan (menjadi) perhiasan. Allah menciptakan apa yang tidak kamu ketahui.'),
	(1910, 16, 9, 'وَعَلَى اللّٰهِ قَصْدُ السَّبِيْلِ وَمِنْهَا جَاۤىِٕرٌ ۗوَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ࣖ  ', 'wa\'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi qa<u>sh</u>du <strong>al</strong>ssabiili waminh<u>aa</u> j<u>aa</u>-irun walaw sy<u>aa</u>-a lahad<u>aa</u>kum ajma\'iin<strong>a</strong>', 'Dan hak Allah menerangkan jalan yang lurus, dan di antaranya ada (jalan) yang menyimpang. Dan jika Dia menghendaki, tentu Dia memberi petunjuk kamu semua (ke jalan yang benar).'),
	(1911, 16, 10, 'هُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً لَّكُمْ مِّنْهُ شَرَابٌ وَّمِنْهُ شَجَرٌ فِيْهِ تُسِيْمُوْنَ   ', 'huwa <strong>al</strong>la<u>dz</u>ii anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an lakum minhu syar<u>aa</u>bun waminhu syajarun fiihi tusiimuun<strong>a</strong>', 'Dialah yang telah menurunkan air (hujan) dari langit untuk kamu, sebagiannya menjadi minuman dan sebagiannya (menyuburkan) tumbuhan, padanya kamu menggembalakan ternakmu.'),
	(1912, 16, 11, 'يُنْۢبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُوْنَ وَالنَّخِيْلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرٰتِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'yunbitu lakum bihi <strong>al</strong>zzar\'a wa<strong>al</strong>zzaytuuna wa<strong>al</strong>nnakhiila wa<strong>a</strong>l-a\'n<u>aa</u>ba wamin kulli <strong>al</strong>tstsamar<u>aa</u>ti inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yatafakkaruun<strong>a</strong>', 'Dengan (air hujan) itu Dia menumbuhkan untuk kamu tanam-tanaman, zaitun, kurma, anggur dan segala macam buah-buahan. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang berpikir.'),
	(1913, 16, 12, 'وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَ ۗوَالنُّجُوْمُ مُسَخَّرٰتٌۢ بِاَمْرِهٖ ۗاِنَّ فِي ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَۙ', 'wasakhkhara lakumu <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra wa<strong>al</strong>sysyamsa wa<strong>a</strong>lqamara wa<strong>al</strong>nnujuumu musakhkhar<u>aa</u>tun bi-amrihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun<strong>a</strong>', 'Dia menundukkan malam dan siang, matahari dan bulan untukmu, dan bin-tang-bintang dikendalikan dengan perintah-Nya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang yang mengerti,'),
	(1914, 16, 13, 'وَمَا ذَرَاَ لَكُمْ فِى الْاَرْضِ مُخْتَلِفًا اَلْوَانُهٗ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّذَّكَّرُوْنَ ', 'wam<u>aa</u> <u>dz</u>ara-a lakum fii <strong>a</strong>l-ar<u>dh</u>i mukhtalifan <strong>a</strong>lw<u>aa</u>nuhu inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya<u>dzdz</u>akkaruun<strong>a</strong>', 'dan (Dia juga mengendalikan) apa yang Dia ciptakan untukmu di bumi ini dengan berbagai jenis dan macam warnanya. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi kaum yang mengambil pelajaran.'),
	(1915, 16, 14, 'وَهُوَ الَّذِيْ سَخَّرَ الْبَحْرَ لِتَأْكُلُوْا مِنْهُ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْا مِنْهُ حِلْيَةً تَلْبَسُوْنَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii sakhkhara <strong>a</strong>lba<u>h</u>ra lita/kuluu minhu la<u>h</u>man <u>th</u>ariyyan watastakhrijuu minhu <u>h</u>ilyatan talbasuunah<u>aa</u> watar<u>aa</u> <strong>a</strong>lfulka maw<u>aa</u>khira fiihi walitabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun<strong>a</strong>', 'Dan Dialah yang menundukkan lautan (untukmu), agar kamu dapat memakan daging yang segar (ikan) darinya, dan (dari lautan itu) kamu mengeluarkan perhiasan yang kamu pakai. Kamu (juga) melihat perahu berlayar padanya, dan agar kamu mencari sebagian karunia-Nya, dan agar kamu bersyukur.'),
	(1916, 16, 15, 'وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَاَنْهٰرًا وَّسُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَۙ', 'wa-alq<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bikum wa-anh<u>aa</u>ran wasubulan la\'allakum tahtaduun<strong>a</strong>', 'Dan Dia menancapkan gunung di bumi agar bumi itu tidak goncang bersama kamu, (dan Dia menciptakan) sungai-sungai dan jalan-jalan agar kamu mendapat petunjuk,'),
	(1917, 16, 16, 'وَعَلٰمٰتٍۗ وَبِالنَّجْمِ هُمْ يَهْتَدُوْنَ  ', 'wa\'al<u>aa</u>m<u>aa</u>tin wabi<strong>al</strong>nnajmi hum yahtaduun<strong>a</strong>', 'dan (Dia menciptakan) tanda-tanda (penunjuk jalan). Dan dengan bintang-bintang mereka mendapat petunjuk.'),
	(1918, 16, 17, 'اَفَمَنْ يَّخْلُقُ كَمَنْ لَّا يَخْلُقُۗ اَفَلَا تَذَكَّرُوْنَ ', 'afaman yakhluqu kaman l<u>aa</u> yakhluqu afal<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Maka apakah (Allah) yang menciptakan sama dengan yang tidak dapat menciptakan (sesuatu)? Mengapa kamu tidak mengambil pelajaran?'),
	(1919, 16, 18, 'وَاِنْ تَعُدُّوْا نِعْمَةَ اللّٰهِ لَا تُحْصُوْهَا ۗاِنَّ اللّٰهَ لَغَفُوْرٌ رَّحِيْمٌ ', 'wa-in ta\'udduu ni\'mata <strong>al</strong>l<u>aa</u>hi l<u>aa</u> tu<u>hs</u>uuh<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan jika kamu menghitung nikmat Allah, niscaya kamu tidak akan mampu menghitungnya. Sungguh, Allah benar-benar Maha Pengampun, Maha Penyayang.'),
	(1920, 16, 19, 'وَاللّٰهُ يَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَ ', 'wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> tusirruuna wam<u>aa</u> tu\'linuun<strong>a</strong>', 'Dan Allah mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan.'),
	(1921, 16, 20, 'وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۗ ', 'wa<strong>a</strong>lla<u>dz</u>iina yad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yakhluquuna syay-an wahum yukhlaquun<strong>a</strong>', 'Dan (berhala-berhala) yang mereka seru selain Allah, tidak dapat membuat sesuatu apa pun, sedang berhala-berhala itu (sendiri) dibuat orang.'),
	(1922, 16, 21, 'اَمْوَاتٌ غَيْرُ اَحْيَاۤءٍ ۗوَمَا يَشْعُرُوْنَۙ اَيَّانَ يُبْعَثُوْنَ ࣖ ', 'amw<u>aa</u>tun ghayru a<u>h</u>y<u>aa</u>-in wam<u>aa</u> yasy\'uruuna ayy<u>aa</u>na yub\'atsuun<strong>a</strong>', '(Berhala-berhala itu) benda mati, tidak hidup, dan berhala-berhala itu tidak mengetahui kapankah (penyembahnya) dibangkitkan.'),
	(1923, 16, 22, 'اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ ۚفَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ قُلُوْبُهُمْ مُّنْكِرَةٌ وَّهُمْ مُّسْتَكْبِرُوْنَ ', 'il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun fa<strong>a</strong>lla<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati quluubuhum munkiratun wahum mustakbiruun<strong>a</strong>', 'Tuhan kamu adalah Tuhan Yang Maha Esa. Maka orang yang tidak beriman kepada akhirat, hati mereka mengingkari (keesaan Allah), dan mereka adalah orang yang sombong.'),
	(1924, 16, 23, 'لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ۗاِنَّهٗ لَا يُحِبُّ الْمُسْتَكْبِرِيْنَ ', 'l<u>aa</u> jarama anna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> yusirruuna wam<u>aa</u> yu\'linuuna innahu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmustakbiriin<strong>a</strong>', 'Tidak diragukan lagi bahwa Allah mengetahui apa yang mereka rahasiakan dan apa yang mereka lahirkan. Sesungguhnya Dia tidak menyukai orang yang sombong.'),
	(1925, 16, 24, 'وَاِذَا قِيْلَ لَهُمْ مَّاذَآ اَنْزَلَ رَبُّكُمْ ۙقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ ', 'wa-i<u>dzaa</u> qiila lahum m<u>aatsaa</u> anzala rabbukum q<u>aa</u>luu as<u>aath</u>iiru <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dan apabila dikatakan kepada mereka, “Apakah yang telah diturunkan Tuhanmu?” Mereka menjawab, “Dongeng-dongeng orang dahulu,”'),
	(1926, 16, 25, 'لِيَحْمِلُوْٓا اَوْزَارَهُمْ كَامِلَةً يَّوْمَ الْقِيٰمَةِ ۙوَمِنْ اَوْزَارِ الَّذِيْنَ يُضِلُّوْنَهُمْ بِغَيْرِ عِلْمٍ ۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ ࣖ ', 'liya<u>h</u>miluu awz<u>aa</u>rahum k<u>aa</u>milatan yawma <strong>a</strong>lqiy<u>aa</u>mati wamin awz<u>aa</u>ri <strong>al</strong>la<u>dz</u>iina yu<u>dh</u>illuunahum bighayri \'ilmin <strong>a</strong>l<u>aa</u> s<u>aa</u>-a m<u>aa</u> yaziruun<strong>a</strong>', '(ucapan mereka) menyebabkan mereka pada hari Kiamat memikul dosa-dosanya sendiri secara sempurna, dan sebagian dosa-dosa orang yang mereka sesatkan yang tidak mengetahui sedikit pun (bahwa mereka disesatkan). Ingatlah, alangkah buruknya (dosa) yang mereka pikul itu.'),
	(1927, 16, 26, 'قَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِّنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ ', 'qad makara <strong>al</strong>la<u>dz</u>iina min qablihim fa-at<u>aa</u> <strong>al</strong>l<u>aa</u>hu buny<u>aa</u>nahum mina <strong>a</strong>lqaw<u>aa</u>\'idi fakharra \'alayhimu <strong>al</strong>ssaqfu min fawqihim wa-at<u>aa</u>humu <strong>a</strong>l\'a<u>dzaa</u>bu min <u>h</u>aytsu l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Sungguh, orang-orang yang sebelum mereka telah mengadakan tipu daya, maka Allah menghancurkan rumah-rumah mereka mulai dari pondasinya, lalu atap (rumah itu) jatuh menimpa mereka dari atas, dan siksa itu datang kepada mereka dari arah yang tidak mereka sadari.'),
	(1928, 16, 27, 'ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْزِيْهِمْ وَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تُشَاۤقُّوْنَ فِيْهِمْ ۗقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوْۤءَ عَلَى الْكٰفِرِيْنَۙ', 'tsumma yawma <strong>a</strong>lqiy<u>aa</u>mati yukhziihim wayaquulu ayna syurak<u>aa</u>-iya <strong>al</strong>la<u>dz</u>iina kuntum tusy<u>aa</u>qquuna fiihim q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma inna <strong>a</strong>lkhizya <strong>a</strong>lyawma wa<strong>al</strong>ssuu-a \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Kemudian Allah menghinakan mereka pada hari Kiamat, dan berfirman, “Di manakah sekutu-sekutu-Ku itu (yang karena membelanya) kamu selalu memusuhi mereka (nabi-nabi dan orang yang beriman)?” Orang-orang yang diberi ilmu berkata, “Sesungguhnya kehinaan dan azab pada hari ini ditimpakan kepada orang yang kafir,”'),
	(1929, 16, 28, 'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ ۖفَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوْۤءٍ ۗبَلٰىٓ اِنَّ اللّٰهَ عَلِيْمٌۢ بِمَا كُنْتُمْ تَعْمَلُوْنَ ', '<strong>al</strong>la<u>dz</u>iina tatawaff<u>aa</u>humu <strong>a</strong>lmal<u>aa</u>-ikatu <u>zhaa</u>limii anfusihim fa-alqawuu <strong>al</strong>ssalama m<u>aa</u> kunn<u>aa</u> na\'malu min suu-in bal<u>aa</u> inna <strong>al</strong>l<u>aa</u>ha \'aliimun bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', '(yaitu) orang yang dicabut nyawanya oleh para malaikat dalam keadaan (berbuat) zalim kepada diri sendiri, lalu mereka menyerahkan diri (sambil berkata), “Kami tidak pernah mengerjakan sesuatu kejahatan pun.” (Malaikat menjawab), “Pernah! Sesungguhnya Allah Maha Mengetahui apa yang telah kamu kerjakan.”'),
	(1930, 16, 29, 'فَادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۗفَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ ', 'fa<strong>u</strong>dkhuluu abw<u>aa</u>ba jahannama kh<u>aa</u>lidiina fiih<u>aa</u> falabi/sa matsw<u>aa</u> <strong>a</strong>lmutakabbiriin<strong>a</strong>', 'Maka masukilah pintu-pintu neraka Jahanam, kamu kekal di dalamnya. Pasti itu seburuk-buruk tempat orang yang menyombongkan diri.'),
	(1931, 16, 30, '۞ وَقِيْلَ لِلَّذِيْنَ اتَّقَوْا مَاذَآ اَنْزَلَ رَبُّكُمْ ۗقَالُوْا خَيْرًا ۚلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ  ۗوَلَدَارُ الْاٰخِرَةِ خَيْرٌ ۗوَلَنِعْمَ دَارُ الْمُتَّقِيْنَۙ', 'waqiila lilla<u>dz</u>iina ittaqaw m<u>aatsaa</u> anzala rabbukum q<u>aa</u>luu khayran lilla<u>dz</u>iina a<u>h</u>sanuu fii h<u>aadz</u>ihi <strong>al</strong>dduny<u>aa</u> <u>h</u>asanatun walad<u>aa</u>ru <strong>a</strong>l-<u>aa</u>khirati khayrun walani\'ma d<u>aa</u>ru <strong>a</strong>lmuttaqiin<strong>a</strong>', 'Dan kemudian dikatakan kepada orang yang bertakwa, “Apakah yang telah diturunkan oleh Tuhanmu?” Mereka menjawab, “Kebaikan.” Bagi orang yang berbuat baik di dunia ini mendapat (balasan) yang baik. Dan sesungguhnya negeri akhirat pasti lebih baik. Dan itulah sebaik-baik tempat bagi orang yang bertakwa,'),
	(1932, 16, 31, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ ۗ كَذٰلِكَ يَجْزِى اللّٰهُ الْمُتَّقِيْنَۙ', 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru lahum fiih<u>aa</u> m<u>aa</u> yasy<u>aa</u>uuna ka<u>dzaa</u>lika yajzii <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmuttaqiin<strong>a</strong>', '(yaitu) surga-surga ‘Adn yang mereka masuki, mengalir di bawahnya sungai-sungai, di dalam (surga) itu mereka mendapat segala apa yang diinginkan. Demikianlah Allah memberi balasan kepada orang yang bertakwa,'),
	(1933, 16, 32, 'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ طَيِّبِيْنَ ۙيَقُوْلُوْنَ سَلٰمٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُوْنَ ', '<strong>al</strong>la<u>dz</u>iina tatawaff<u>aa</u>humu <strong>a</strong>lmal<u>aa</u>-ikatu <u>th</u>ayyibiina yaquuluuna sal<u>aa</u>mun \'alaykumu udkhuluu <strong>a</strong>ljannata bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', '(yaitu) orang yang ketika diwafatkan oleh para malaikat dalam keadaan baik, mereka (para malaikat) mengatakan (kepada mereka), “Salamun ‘alaikum, masuklah ke dalam surga karena apa yang telah kamu kerjakan.”'),
	(1934, 16, 33, 'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗوَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ', 'hal yan<u>zh</u>uruuna ill<u>aa</u> an ta/tiyahumu <strong>a</strong>lmal<u>aa</u>-ikatu aw ya/tiya amru rabbika ka<u>dzaa</u>lika fa\'ala <strong>al</strong>la<u>dz</u>iina min qablihim wam<u>aa</u> <u>zh</u>alamahumu <strong>al</strong>l<u>aa</u>hu wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Tidak ada yang ditunggu mereka (orang kafir) selain datangnya para malaikat kepada mereka atau datangnya perintah Tuhanmu. Demikianlah yang telah diperbuat oleh orang-orang (kafir) sebelum mereka. Allah tidak menzalimi mereka, justru merekalah yang (selalu) menzalimi diri mereka sendiri.'),
	(1935, 16, 34, 'فَاَصَابَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ ', 'fa-a<u>shaa</u>bahum sayyi-<u>aa</u>tu m<u>aa</u> \'amiluu wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Maka mereka ditimpa azab (akibat) perbuatan mereka dan diliputi oleh azab yang dulu selalu mereka perolok-olokkan.'),
	(1936, 16, 35, 'وَقَالَ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ نَّحْنُ وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚفَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلٰغُ الْمُبِي', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina asyrakuu law sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu m<u>aa</u> \'abadn<u>aa</u> min duunihi min syay-in na<u>h</u>nu wal<u>aa</u> <u>aa</u>b<u>aa</u>un<u>aa</u> wal<u>aa</u> <u>h</u>arramn<u>aa</u> min duunihi min syay-in ka<u>dzaa</u>lika fa\'ala <strong>al</strong>la<u>dz</u>iina min qablihim fahal \'al<u>aa</u> <strong>al</strong>rrusuli ill<u>aa</u> <strong>a</strong>lbal<u>aa</u>ghu <strong>a</strong>lmubiin<strong>u</strong>', 'Dan orang musyrik berkata, “Jika Allah menghendaki, niscaya kami tidak akan menyembah sesuatu apa pun selain Dia, baik kami maupun bapak-bapak kami, dan tidak (pula) kami mengharamkan sesuatu pun tanpa (izin)-Nya.” Demikianlah yang diperbuat oleh orang sebelum mereka. Bukankah kewajiban para rasul hanya menyampaikan (amanat Allah) dengan jelas.'),
	(1937, 16, 36, 'وَلَقَدْ بَعَثْنَا فِيْ كُلِّ اُمَّةٍ رَّسُوْلًا اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوْتَۚ فَمِنْهُمْ مَّنْ هَدَى اللّٰهُ وَمِنْهُمْ مَّنْ حَقَّتْ عَلَيْهِ الضَّلٰلَةُ ۗ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْ', 'walaqad ba\'atsn<u>aa</u> fii kulli ummatin rasuulan ani u\'buduu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>jtanibuu <strong>al</strong><u>ththaa</u>ghuuta faminhum man had<u>aa</u> <strong>al</strong>l<u>aa</u>hu waminhum man <u>h</u>aqqat \'alayhi <strong>al</strong><u>dhdh</u>al<u>aa</u>latu fasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fa<strong>u</strong>n<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmuka<u>dzdz</u>ibiin<strong>a</strong>', 'Dan sungguh, Kami telah mengutus seorang rasul untuk setiap umat (untuk menyerukan), “Sembahlah Allah, dan jauhilah tagut”, kemudian di antara mereka ada yang diberi petunjuk oleh Allah dan ada pula yang tetap dalam kesesatan. Maka berjalanlah kamu di bumi dan perhatikanlah bagaimana kesudahan orang yang mendustakan (rasul-rasul).'),
	(1938, 16, 37, 'اِنْ تَحْرِصْ عَلٰى هُدٰىهُمْ فَاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ يُّضِلُّ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ', 'in ta<u>h</u>ri<u>sh</u> \'al<u>aa</u> hud<u>aa</u>hum fa-inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii man yu<u>dh</u>illu wam<u>aa</u> lahum min n<u>aas</u>iriin<strong>a</strong>', 'Jika engkau (Muhammad) sangat mengharapkan agar mereka mendapat petunjuk, maka sesungguhnya Allah tidak akan memberi petunjuk kepada orang yang disesatkan-Nya, dan mereka tidak mempunyai penolong.'),
	(1939, 16, 38, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ  لَا يَبْعَثُ اللّٰهُ مَنْ يَّمُوْتُۗ بَلٰى وَعْدًا عَلَيْهِ حَقًّا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ', 'wa-aqsamuu bi<strong>al</strong>l<u>aa</u>hi jahda aym<u>aa</u>nihim l<u>aa</u> yab\'atsu <strong>al</strong>l<u>aa</u>hu man yamuutu bal<u>aa</u> wa\'dan \'alayhi <u>h</u>aqqan wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan mereka bersumpah dengan (nama) Allah dengan sumpah yang sungguh-sungguh, “Allah tidak akan membangkitkan orang yang mati.” Tidak demikian (pasti Allah akan membangkitkannya), sebagai suatu janji yang benar dari-Nya, tetapi kebanyakan manusia tidak mengetahui,'),
	(1940, 16, 39, 'لِيُبَيِّنَ لَهُمُ الَّذِيْ يَخْتَلِفُوْنَ فِيْهِ وَلِيَعْلَمَ الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ كَانُوْا كٰذِبِيْنَ  ', 'liyubayyina lahumu <strong>al</strong>la<u>dz</u>ii yakhtalifuuna fiihi waliya\'lama <strong>al</strong>la<u>dz</u>iina kafaruu annahum k<u>aa</u>nuu k<u>aadz</u>ibiin<strong>a</strong>', 'agar Dia menjelaskan kepada mereka apa yang mereka perselisihkan itu, dan agar orang kafir itu mengetahui bahwa mereka adalah orang yang berdusta. '),
	(1941, 16, 40, 'اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَآ اَرَدْنٰهُ اَنْ نَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ ࣖ ', 'innam<u>aa</u> qawlun<u>aa</u> lisyay-in i<u>dzaa</u> aradn<u>aa</u>hu an naquula lahu kun fayakuun<strong>u</strong>', 'Sesungguhnya firman Kami terhadap sesuatu apabila Kami menghendakinya, Kami hanya mengatakan kepadanya, “Jadilah!” Maka jadilah sesuatu itu.'),
	(1942, 16, 41, 'وَالَّذِيْنَ هَاجَرُوْا فِى اللّٰهِ مِنْۢ بَعْدِ مَا ظُلِمُوْا لَنُبَوِّئَنَّهُمْ فِى الدُّنْيَا حَسَنَةً  ۗوَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَۙ', 'wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>jaruu fii <strong>al</strong>l<u>aa</u>hi min ba\'di m<u>aa</u> <u>zh</u>ulimuu lanubawwi-annahum fii <strong>al</strong>dduny<u>aa</u> <u>h</u>asanatan wala-ajru <strong>a</strong>l-<u>aa</u>khirati akbaru law k<u>aa</u>nuu ya\'lamuun<strong>a</strong>', 'Dan orang yang berhijrah karena Allah setelah mereka dizalimi, pasti Kami akan memberikan tempat yang baik kepada mereka di dunia. Dan pahala di akhirat pasti lebih besar, sekiranya mereka mengetahui,'),
	(1943, 16, 42, 'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ', '<strong>al</strong>la<u>dz</u>iina <u>sh</u>abaruu wa\'al<u>aa</u> rabbihim yatawakkaluun<strong>a</strong>', '(yaitu) orang yang sabar dan hanya kepada Tuhan mereka bertawakal.'),
	(1944, 16, 43, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ  فَاسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَۙ', 'wam<u>aa</u> arsaln<u>aa</u> min qablika ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim fa<strong>i</strong>s-aluu ahla <strong>al</strong><u>dzdz</u>ikri in kuntum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan Kami tidak mengutus sebelum engkau (Muhammad), melainkan orang laki-laki yang Kami beri wahyu kepada mereka; maka bertanyalah kepada orang yang mempunyai pengetahuan jika kamu tidak mengetahui,'),
	(1945, 16, 44, 'بِالْبَيِّنٰتِ وَالزُّبُرِۗ وَاَنْزَلْنَآ اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُوْنَ ', 'bi<strong>a</strong>lbayyin<u>aa</u>ti wa<strong>al</strong>zzuburi wa-anzaln<u>aa</u> ilayka <strong>al</strong><u>dzdz</u>ikra litubayyina li<strong>l</strong>nn<u>aa</u>si m<u>aa</u> nuzzila ilayhim wala\'allahum yatafakkaruun<strong>a</strong>', '(mereka Kami utus) dengan membawa keterangan-keterangan (mukjizat) dan kitab-kitab. Dan Kami turunkan Ad-Dzikr (Al-Qur\'an) kepadamu, agar engkau menerangkan kepada manusia apa yang telah diturunkan kepada mereka dan agar mereka memikirkan,'),
	(1946, 16, 45, 'اَفَاَمِنَ الَّذِيْنَ مَكَرُوا السَّيِّاٰتِ اَنْ يَّخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَۙ', 'afa-amina <strong>al</strong>la<u>dz</u>iina makaruu <strong>al</strong>ssayyi-<u>aa</u>ti an yakhsifa <strong>al</strong>l<u>aa</u>hu bihimu <strong>a</strong>l-ar<u>dh</u>a aw ya/tiyahumu <strong>a</strong>l\'a<u>dzaa</u>bu min <u>h</u>aytsu l<u>aa</u> yasy\'uruun<strong>a</strong>', 'maka apakah orang yang membuat tipu daya yang jahat itu, merasa aman (dari bencana) dibenamkannya bumi oleh Allah bersama mereka, atau (terhadap) datangnya siksa kepada mereka dari arah yang tidak mereka sadari, '),
	(1947, 16, 46, 'اَوْ يَأْخُذَهُمْ فِيْ تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِيْنَۙ', 'aw ya/khu<u>dz</u>ahum fii taqallubihim fam<u>aa</u> hum bimu\'jiziin<strong>a </strong>', 'atau Allah mengazab mereka pada waktu mereka dalam perjalanan; sehingga mereka tidak berdaya menolak (azab itu),'),
	(1948, 16, 47, 'اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۗ فَاِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌ ', 'aw ya/khu<u>dz</u>ahum \'al<u>aa</u> takhawwufin fa-inna rabbakum larauufun ra<u>h</u>iim<strong>un</strong>', 'atau Allah mengazab mereka dengan berangsur-angsur (sampai binasa). Maka sungguh, Tuhanmu Maha Pengasih, Maha Penyayang.'),
	(1949, 16, 48, 'اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَّتَفَيَّؤُا ظِلٰلُهٗ عَنِ الْيَمِيْنِ وَالشَّمَاۤىِٕلِ سُجَّدًا لِّلّٰهِ وَهُمْ دَاخِرُوْنَ ', 'awa lam yaraw il<u>aa</u> m<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu min syay-in yatafayyau <u>zh</u>il<u>aa</u>luhu \'ani <strong>a</strong>lyamiini wa<strong>al</strong>sysyam<u>aa</u>-ili sujjadan lill<u>aa</u>hi wahum d<u>aa</u>khiruun<strong>a</strong>', 'Dan apakah mereka tidak memperhatikan suatu benda yang diciptakan Allah, bayang-bayangnya berbolak-balik ke kanan dan ke kiri, dalam keadaan sujud kepada Allah, dan mereka (bersikap) rendah hati.'),
	(1950, 16, 49, 'وَلِلّٰهِ يَسْجُدُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ مِنْ دَاۤبَّةٍ وَّالْمَلٰۤىِٕكَةُ وَهُمْ لَا يَسْتَكْبِرُوْنَ ', 'walill<u>aa</u>hi yasjudu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i min d<u>aa</u>bbatin wa<strong>a</strong>lmal<u>aa</u>-ikatu wahum l<u>aa</u> yastakbiruun<strong>a</strong>', 'Dan segala apa yang ada di langit dan di bumi hanya bersujud kepada Allah yaitu semua makhluk bergerak (bernyawa) dan (juga) para malaikat, dan mereka (malaikat) tidak menyombongkan diri.'),
	(1951, 16, 50, 'يَخَافُوْنَ رَبَّهُمْ مِّنْ فَوْقِهِمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ࣖ ۩ ', 'yakh<u>aa</u>fuuna rabbahum min fawqihim wayaf\'aluuna m<u>aa</u> yu/maruun<strong>a</strong>16', 'Mereka takut kepada Tuhan yang (berkuasa) di atas mereka dan melaksanakan apa yang diperintahkan (kepada mereka).'),
	(1952, 16, 51, '۞ وَقَالَ اللّٰهُ لَا تَتَّخِذُوْٓا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ فَاِيَّايَ فَارْهَبُوْنِ ', 'waq<u>aa</u>la <strong>al</strong>l<u>aa</u>hu l<u>aa</u> tattakhi<u>dz</u>uu il<u>aa</u>hayni itsnayni innam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun fa-iyy<u>aa</u>ya fa<strong>i</strong>rhabuun<strong>i</strong>', 'Dan Allah berfirman, “Janganlah kamu menyembah dua tuhan; hanyalah Dia Tuhan Yang Maha Esa. Maka hendaklah kepada-Ku saja kamu takut.”'),
	(1953, 16, 52, 'وَلَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَلَهُ الدِّيْنُ وَاصِبًاۗ اَفَغَيْرَ اللّٰهِ تَتَّقُوْنَ ', 'walahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i walahu <strong>al</strong>ddiinu w<u>aas</u>iban afaghayra <strong>al</strong>l<u>aa</u>hi tattaquun<strong>a</strong>', 'Dan milik-Nya meliputi segala apa yang ada di langit dan di bumi, dan kepada-Nyalah (ibadah dan) ketaatan selama-lamanya. Mengapa kamu takut kepada selain Allah?'),
	(1954, 16, 53, 'وَمَا بِكُمْ مِّنْ نِّعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُوْنَۚ  ', 'wam<u>aa</u> bikum min ni\'matin famina <strong>al</strong>l<u>aa</u>hi tsumma i<u>dzaa</u> massakumu <strong>al</strong><u>dhdh</u>urru fa-ilayhi taj-aruun<strong>a</strong>', 'Dan segala nikmat yang ada padamu (datangnya) dari Allah, kemudian apabila kamu ditimpa kesengsaraan, maka kepada-Nyalah kamu meminta pertolongan.'),
	(1955, 16, 54, 'ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَرِيْقٌ مِّنْكُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ', 'tsumma i<u>dzaa</u> kasyafa <strong>al</strong><u>dhdh</u>urra \'ankum i<u>dzaa</u> fariiqun minkum birabbihim yusyrikuun<strong>a</strong>', 'Kemudian apabila Dia telah menghilangkan bencana dari kamu, malah sebagian kamu mempersekutukan Tuhan dengan (yang lain).'),
	(1956, 16, 55, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ  ', 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum fatamatta\'uu fasawfa ta\'lamuun<strong>a</strong>', 'Biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka; bersenang-senanglah kamu. Kelak kamu akan mengetahui (akibatnya).'),
	(1957, 16, 56, 'وَيَجْعَلُوْنَ لِمَا لَا يَعْلَمُوْنَ نَصِيْبًا مِّمَّا رَزَقْنٰهُمْۗ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُوْنَ ', 'wayaj\'aluuna lim<u>aa</u> l<u>aa</u> ya\'lamuuna na<u>sh</u>iiban mimm<u>aa</u> razaqn<u>aa</u>hum ta<strong>al</strong>l<u>aa</u>hi latus-alunna \'amm<u>aa</u> kuntum taftaruun<strong>a</strong>', 'Dan mereka menyediakan sebagian dari rezeki yang telah Kami berikan kepada mereka, untuk berhala-berhala yang mereka tidak mengetahui (kekuasaannya). Demi Allah,  kamu pasti akan ditanyai tentang apa yang telah kamu ada-adakan.'),
	(1958, 16, 57, 'وَيَجْعَلُوْنَ لِلّٰهِ الْبَنٰتِ سُبْحٰنَهٗۙ وَلَهُمْ مَّا يَشْتَهُوْنَ ', 'wayaj\'aluuna lill<u>aa</u>hi <strong>a</strong>lban<u>aa</u>ti sub<u>haa</u>nahu walahum m<u>aa</u> yasytahuun<strong>a</strong>', 'Dan mereka menetapkan anak perempuan bagi Allah. Mahasuci Dia, sedang untuk mereka sendiri apa yang mereka sukai (anak laki-laki).'),
	(1959, 16, 58, 'وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌۚ ', 'wa-i<u>dzaa</u> busysyira a<u>h</u>aduhum bi<strong>a</strong>lunts<u>aa</u> <u>zh</u>alla wajhuhu muswaddan wahuwa ka<u>zh</u>iim<strong>un</strong>', 'Padahal apabila seseorang dari mereka diberi kabar dengan (kelahiran) anak perempuan, wajahnya menjadi hitam (merah padam), dan dia sangat marah.'),
	(1960, 16, 59, 'يَتَوٰرٰى مِنَ الْقَوْمِ مِنْ سُوْۤءِ مَا بُشِّرَ بِهٖۗ اَيُمْسِكُهٗ عَلٰى هُوْنٍ اَمْ يَدُسُّهٗ فِى التُّرَابِۗ  اَلَا سَاۤءَ مَا يَحْكُمُوْنَ ', 'yataw<u>aa</u>r<u>aa</u> mina <strong>a</strong>lqawmi min suu-i m<u>aa</u> busysyira bihi ayumsikuhu \'al<u>aa</u> huunin am yadussuhu fii <strong>al</strong>ttur<u>aa</u>bi <strong>a</strong>l<u>aa</u> s<u>aa</u>-a m<u>aa</u> ya<u>h</u>kumuun<strong>a</strong>', 'Dia bersembunyi dari orang banyak, disebabkan kabar buruk yang disampaikan kepadanya. Apakah dia akan memeliharanya dengan (menanggung) kehinaan atau akan membenamkannya ke dalam tanah (hidup-hidup)? Ingatlah alangkah buruknya (putusan) yang mereka tetapkan itu.'),
	(1961, 16, 60, 'لِلَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ', 'lilla<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati matsalu <strong>al</strong>ssaw-i walill<u>aa</u>hi <strong>a</strong>lmatsalu <strong>a</strong>l-a\'l<u>aa</u> wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Bagi orang-orang yang tidak beriman pada (kehidupan) akhirat, (mempunyai) sifat yang buruk; dan Allah mempunyai sifat Yang Mahatinggi. Dan Dia Mahaperkasa, Mahabijaksana.'),
	(1962, 16, 61, 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَّا تَرَكَ عَلَيْهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً  وَّلَا يَسْتَقْدِمُوْنَ ', 'walaw yu-<u>aa</u>khi<u>dz</u>u <strong>al</strong>l<u>aa</u>hu <strong>al</strong>nn<u>aa</u>sa bi<u>zh</u>ulmihim m<u>aa</u> taraka \'alayh<u>aa</u> min d<u>aa</u>bbatin wal<u>aa</u>kin yu-akhkhiruhum il<u>aa</u> ajalin musamman fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum l<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun<strong>a</strong>', 'Dan kalau Allah menghukum manusia karena kezalimannya, niscaya tidak akan ada yang ditinggalkan-Nya (di bumi) dari makhluk yang melata sekalipun, tetapi Allah menangguhkan mereka sampai waktu yang sudah ditentukan. Maka apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun. '),
	(1963, 16, 62, 'وَيَجْعَلُوْنَ لِلّٰهِ مَا يَكْرَهُوْنَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰى لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُّفْرَطُوْنَ ', 'wayaj\'aluuna lill<u>aa</u>hi m<u>aa</u> yakrahuuna wata<u>sh</u>ifu <strong>a</strong>lsinatuhumu <strong>a</strong>lka<u>dz</u>iba anna lahumu <strong>a</strong>l<u>h</u>usn<u>aa</u> l<u>aa</u> jarama anna lahumu <strong>al</strong>nn<u>aa</u>ra wa-annahum mufra<u>th</u>uun<strong>a</strong>', 'Dan mereka menetapkan bagi Allah apa yang mereka sendiri membencinya, dan lidah mereka mengucapkan kebohongan, bahwa sesungguhnya (segala) yang baik-baik untuk mereka. Tidaklah diragukan bahwa nerakalah bagi mereka, dan sesungguhnya mereka segera akan dimasukkan (ke dalamnya).'),
	(1964, 16, 63, 'تَاللّٰهِ لَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَلِيْمٌ ', 'ta<strong>al</strong>l<u>aa</u>hi laqad arsaln<u>aa</u> il<u>aa</u> umamin min qablika fazayyana lahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum fahuwa waliyyuhumu <strong>a</strong>lyawma walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Demi Allah, sungguh Kami telah mengutus (rasul-rasul) kepada umat-umat sebelum engkau (Muhammad), tetapi setan menjadikan terasa indah bagi mereka perbuatan mereka (yang buruk), sehingga dia (setan) menjadi pemimpin mereka pada hari ini dan mereka akan mendapat azab yang sangat pedih.'),
	(1965, 16, 64, 'وَمَآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِى اخْتَلَفُوْا فِيْهِۙ وَهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ', 'wam<u>aa</u> anzaln<u>aa</u> \'alayka <strong>a</strong>lkit<u>aa</u>ba ill<u>aa</u> litubayyina lahumu <strong>al</strong>la<u>dz</u>ii ikhtalafuu fiihi wahudan wara<u>h</u>matan liqawmin yu/minuun<strong>a</strong>', 'Dan Kami tidak menurunkan Kitab (Al-Qur\'an) ini kepadamu (Muhammad), melainkan agar engkau dapat menjelaskan kepada mereka apa yang mereka perselisihkan, serta menjadi petunjuk dan rahmat bagi orang-orang yang beriman.'),
	(1966, 16, 65, 'وَاللّٰهُ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّسْمَعُوْنَ ࣖ ', 'wa<strong>al</strong>l<u>aa</u>hu anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-a<u>h</u>y<u>aa</u> bihi <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yasma\'uun<strong>a</strong>', 'Dan Allah menurunkan air (hujan) dari langit dan dengan air itu dihidupkan-Nya bumi yang tadinya sudah mati. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang mendengarkan (pelajaran).'),
	(1967, 16, 66, 'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةً ۚ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهٖ مِنْۢ بَيْنِ فَرْثٍ وَّدَمٍ لَّبَنًا خَالِصًا سَاۤىِٕغًا لِّلشّٰرِبِيْنَ ', 'wa-inna lakum fii <strong>a</strong>l-an\'<u>aa</u>mi la\'ibratan nusqiikum mimm<u>aa</u> fii bu<u>th</u>uunihi min bayni fartsin wadamin labanan kh<u>aa</u>li<u>sh</u>an s<u>aa</u>-ighan li<strong>l</strong>sysy<u>aa</u>ribiin<strong>a</strong>', 'Dan sungguh, pada hewan ternak itu benar-benar terdapat pelajaran bagi kamu. Kami memberimu minum dari apa yang ada dalam perutnya (berupa) susu murni antara kotoran dan darah, yang mudah ditelan bagi orang yang meminumnya.'),
	(1968, 16, 67, 'وَمِنْ ثَمَرٰتِ النَّخِيْلِ وَالْاَعْنَابِ تَتَّخِذُوْنَ مِنْهُ سَكَرًا وَّرِزْقًا حَسَنًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْقِلُوْنَ ', 'wamin tsamar<u>aa</u>ti <strong>al</strong>nnakhiili wa<strong>a</strong>l-a\'n<u>aa</u>bi tattakhi<u>dz</u>uuna minhu sakaran warizqan <u>h</u>asanan inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya\'qiluun<strong>a</strong>', 'Dan dari buah kurma dan anggur, kamu membuat minuman yang memabukkan dan rezeki yang baik. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang mengerti.'),
	(1969, 16, 68, 'وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذِيْ مِنَ الْجِبَالِ بُيُوْتًا وَّمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُوْنَۙ', 'wa-aw<u>haa</u> rabbuka il<u>aa</u> <strong>al</strong>nna<u>h</u>li ani ittakhi<u>dz</u>ii mina <strong>a</strong>ljib<u>aa</u>li buyuutan wamina <strong>al</strong>sysyajari wamimm<u>aa</u> ya\'risyuun<strong>a</strong>', 'Dan Tuhanmu mengilhamkan kepada lebah, “Buatlah sarang di gunung-gunung, di pohon-pohon kayu, dan di tempat-tempat yang dibikin manusia,'),
	(1970, 16, 69, 'ثُمَّ كُلِيْ مِنْ كُلِّ الثَّمَرٰتِ فَاسْلُكِيْ سُبُلَ رَبِّكِ ذُلُلًاۗ يَخْرُجُ مِنْ بُطُوْنِهَا شَرَابٌ مُّخْتَلِفٌ اَلْوَانُهٗ ۖفِيْهِ شِفَاۤءٌ لِّلنَّاسِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ ', 'tsumma kulii min kulli <strong>al</strong>tstsamar<u>aa</u>ti fa<strong>u</strong>slukii subula rabbiki <u>dz</u>ululan yakhruju min bu<u>th</u>uunih<u>aa</u> syar<u>aa</u>bun mukhtalifun <strong>a</strong>lw<u>aa</u>nuhu fiihi syif<u>aa</u>un li<strong>l</strong>nn<u>aa</u>si inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yatafakkaruun<strong>a</strong>', 'kemudian makanlah dari segala (macam) buah-buahan lalu tempuhlah jalan Tuhanmu yang telah dimudahkan (bagimu).” Dari perut lebah itu keluar minuman (madu) yang bermacam-macam warnanya, di dalamnya terdapat obat yang menyembuhkan bagi manusia. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang berpikir.'),
	(1971, 16, 70, 'وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰىكُمْ وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ قَدِيْرٌ ࣖ  ', 'wa<strong>al</strong>l<u>aa</u>hu khalaqakum tsumma yatawaff<u>aa</u>kum waminkum man yuraddu il<u>aa</u> ar<u>dz</u>ali <strong>a</strong>l\'umuri likay l<u>aa</u> ya\'lama ba\'da \'ilmin syay-an inna <strong>al</strong>l<u>aa</u>ha \'aliimun qadiir<strong>un</strong>', 'Dan Allah telah menciptakan kamu, kemudian mewafatkanmu, di antara kamu ada yang dikembalikan kepada usia yang tua renta (pikun), sehingga dia tidak mengetahui lagi sesuatu yang pernah diketahuinya. Sungguh, Allah Maha Mengetahui, Mahakuasa.'),
	(1972, 16, 71, 'وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِى الرِّزْقِۚ فَمَا الَّذِيْنَ فُضِّلُوْا بِرَاۤدِّيْ رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ فِيْهِ سَوَاۤءٌۗ اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُوْنَ  ', 'wa<strong>al</strong>l<u>aa</u>hu fa<u>dhdh</u>ala ba\'<u>dh</u>akum \'al<u>aa</u> ba\'<u>dh</u>in fii <strong>al</strong>rrizqi fam<u>aa</u> <strong>al</strong>la<u>dz</u>iina fu<u>dhdh</u>iluu bir<u>aa</u>ddii rizqihim \'al<u>aa</u> m<u>aa</u> malakat aym<u>aa</u>nuhum fahum fiihi saw<u>aa</u>un afabini\'mati <strong>al</strong>l<u>aa</u>hi yaj<u>h</u>aduun<strong>a</strong>', 'Dan Allah melebihkan sebagian kamu atas sebagian yang lain dalam hal rezeki, tetapi orang yang dilebihkan (rezekinya itu) tidak mau memberikan rezekinya kepada para hamba sahaya yang mereka miliki, sehingga mereka sama-sama (merasakan) rezeki itu. Mengapa mereka mengingkari nikmat Allah?'),
	(1973, 16, 72, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّجَعَلَ لَكُمْ مِّنْ اَزْوَاجِكُمْ بَنِيْنَ وَحَفَدَةً وَّرَزَقَكُمْ مِّنَ الطَّيِّبٰتِۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُوْنَۙ', 'wa<strong>al</strong>l<u>aa</u>hu ja\'ala lakum min anfusikum azw<u>aa</u>jan waja\'ala lakum min azw<u>aa</u>jikum baniina wa<u>h</u>afadatan warazaqakum mina <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti afabi<strong>a</strong>lb<u>aath</u>ili yu/minuuna wabini\'mati <strong>al</strong>l<u>aa</u>hi hum yakfuruun<strong>a</strong>', 'Dan Allah menjadikan bagimu pasangan (suami atau istri) dari jenis kamu sendiri dan menjadikan anak dan cucu bagimu dari pasanganmu, serta memberimu rezeki dari yang baik. Mengapa mereka beriman kepada yang batil dan mengingkari nikmat Allah?'),
	(1974, 16, 73, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِّنَ السَّمٰوٰتِ وَالْاَرْضِ شَيْـًٔا وَّلَا يَسْتَطِيْعُوْنَ ۚ  ', 'waya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yamliku lahum rizqan mina <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i syay-an wal<u>aa</u> yasta<u>th</u>ii\'uun<strong>a</strong>', 'Dan mereka menyembah selain Allah, sesuatu yang sama sekali tidak dapat memberikan rezeki kepada mereka, dari langit dan bumi, dan tidak akan sanggup (berbuat apa pun).'),
	(1975, 16, 74, 'فَلَا تَضْرِبُوْا لِلّٰهِ الْاَمْثَالَ ۗاِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ', 'fal<u>aa</u> ta<u>dh</u>ribuu lill<u>aa</u>hi <strong>a</strong>l-amts<u>aa</u>la inna <strong>al</strong>l<u>aa</u>ha ya\'lamu wa-antum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Maka janganlah kamu mengadakan sekutu-sekutu bagi Allah. Sungguh, Allah mengetahui, sedang kamu tidak mengetahui.'),
	(1976, 16, 75, '۞ ضَرَبَ اللّٰهُ مَثَلًا عَبْدًا مَّمْلُوْكًا لَّا يَقْدِرُ عَلٰى شَيْءٍ وَّمَنْ رَّزَقْنٰهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَّجَهْرًاۗ هَلْ يَسْتَوٗنَ ۚ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', '<u>dh</u>araba <strong>al</strong>l<u>aa</u>hu matsalan \'abdan mamluukan l<u>aa</u> yaqdiru \'al<u>aa</u> syay-in waman razaqn<u>aa</u>hu minn<u>aa</u> rizqan <u>h</u>asanan fahuwa yunfiqu minhu sirran wajahran hal yastawuuna <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Allah membuat perumpamaan seorang hamba sahaya di bawah kekuasaan orang lain, yang tidak berdaya berbuat sesuatu, dan seorang yang Kami beri rezeki yang baik, lalu dia menginfakkan sebagian rezeki itu secara sembunyi-sembunyi dan secara terang-terangan. Samakah mereka itu? Segala puji hanya bagi Allah, tetapi kebanyakan mereka tidak mengetahui.'),
	(1977, 16, 76, 'وَضَرَبَ اللّٰهُ مَثَلًا رَّجُلَيْنِ اَحَدُهُمَآ اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَّهُوَ كَلٌّ عَلٰى مَوْلٰىهُ ۗ اَيْنَمَا يُوَجِّهْهُّ لَا يَأْتِ بِخَيْرٍ ۖهَلْ يَسْتَوِيْ هُوَۙ وَمَنْ يَّأْمُرُ بِالْعَدْلِ وَهُوَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ࣖ ', 'wa<u>dh</u>araba <strong>al</strong>l<u>aa</u>hu matsalan rajulayni a<u>h</u>aduhum<u>aa</u> abkamu l<u>aa</u> yaqdiru \'al<u>aa</u> syay-in wahuwa kallun \'al<u>aa</u> mawl<u>aa</u>hu aynam<u>aa</u> yuwajjihhu l<u>aa</u> ya/ti bikhayrin hal yastawii huwa waman ya/muru bi<strong>a</strong>l\'adli wahuwa \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dan Allah (juga) membuat perumpamaan, dua orang laki-laki, yang seorang bisu, tidak dapat berbuat sesuatu dan dia menjadi beban penanggungnya, ke mana saja dia disuruh (oleh penanggungnya itu), dia sama sekali tidak dapat mendatangkan suatu kebaikan. Samakah orang itu dengan orang yang menyuruh berbuat keadilan, dan dia berada di jalan yang lurus?'),
	(1978, 16, 77, 'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ وَمَآ اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'walill<u>aa</u>hi ghaybu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> amru <strong>al</strong>ss<u>aa</u>\'ati ill<u>aa</u> kalam<u>h</u>i <strong>a</strong>lba<u>sh</u>ari aw huwa aqrabu inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan milik Allah (segala) yang tersembunyi di langit dan di bumi. Urusan kejadian Kiamat itu, hanya seperti sekejap mata atau lebih cepat (lagi). Sesungguhnya Allah Mahakuasa atas segala sesuatu.'),
	(1979, 16, 78, 'وَاللّٰهُ اَخْرَجَكُمْ مِّنْۢ بُطُوْنِ اُمَّهٰتِكُمْ لَا تَعْلَمُوْنَ شَيْـًٔاۙ وَّجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَ ۙ لَعَلَّكُمْ تَشْكُرُوْنَ ', 'wa<strong>al</strong>l<u>aa</u>hu akhrajakum min bu<u>th</u>uuni ummah<u>aa</u>tikum l<u>aa</u> ta\'lamuuna syay-an waja\'ala lakumu <strong>al</strong>ssam\'a wa<strong>a</strong>l-ab<u>shaa</u>ra wa<strong>a</strong>l-af-idata la\'allakum tasykuruun<strong>a</strong>', 'Dan Allah mengeluarkan kamu dari perut ibumu dalam keadaan tidak mengetahui sesuatu pun, dan Dia memberimu pendengaran, penglihatan, dan hati nurani, agar kamu bersyukur.'),
	(1980, 16, 79, 'اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرٰتٍ فِيْ جَوِّ السَّمَاۤءِ ۗمَا يُمْسِكُهُنَّ اِلَّا اللّٰهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ', 'alam yaraw il<u>aa</u> <strong>al</strong><u>ththh</u>ayri musakhkhar<u>aa</u>tin fii jawwi <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u> yumsikuhunna ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun<strong>a</strong>', 'Tidakkah mereka memperhatikan burung-burung yang dapat terbang di angkasa dengan mudah. Tidak ada yang menahannya selain Allah. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman.'),
	(1981, 16, 80, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْۢ بُيُوْتِكُمْ سَكَنًا وَّجَعَلَ لَكُمْ مِّنْ جُلُوْدِ الْاَنْعَامِ بُيُوْتًا تَسْتَخِفُّوْنَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْ ۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَآ اَثَاثًا وَّمَتَاعًا اِلٰى حِيْنٍ ', 'wa<strong>al</strong>l<u>aa</u>hu ja\'ala lakum min buyuutikum sakanan waja\'ala lakum min juluudi <strong>a</strong>l-an\'<u>aa</u>mi buyuutan tastakhiffuunah<u>aa</u> yawma <u>zh</u>a\'nikum wayawma iq<u>aa</u>matikum wamin a<u>sh</u>w<u>aa</u>fih<u>aa</u> wa-awb<u>aa</u>rih<u>aa</u> wa-asy\'<u>aa</u>rih<u>aa</u> ats<u>aa</u>tsan wamat<u>aa</u>\'an il<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Dan Allah menjadikan rumah-rumah bagimu sebagai tempat tinggal dan Dia menjadikan bagimu rumah-rumah (kemah-kemah) dari kulit hewan ternak yang kamu merasa ringan (membawa)nya pada waktu kamu bepergian dan pada waktu kamu bermukim dan (dijadikan-Nya pula) dari bulu domba, bulu unta, dan bulu kambing, alat-alat rumah tangga dan kesenangan sampai waktu (tertentu).'),
	(1982, 16, 81, 'وَاللّٰهُ جَعَلَ لَكُمْ مِّمَّا خَلَقَ ظِلٰلًا وَّجَعَلَ لَكُمْ مِّنَ الْجِبَالِ اَكْنَانًا وَّجَعَلَ لَكُمْ سَرَابِيْلَ تَقِيْكُمُ الْحَرَّ وَسَرَابِيْلَ تَقِيْكُمْ بَأْسَكُمْ ۚ  كَذٰلِكَ يُتِمُّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُوْنَ', 'wa<strong>al</strong>l<u>aa</u>hu ja\'ala lakum mimm<u>aa</u> khalaqa <u>zh</u>il<u>aa</u>lan waja\'ala lakum mina <strong>a</strong>ljib<u>aa</u>li akn<u>aa</u>nan waja\'ala lakum sar<u>aa</u>biila taqiikumu <strong>a</strong>l<u>h</u>arra wasar<u>aa</u>biila taqiikum ba/sakum ka<u>dzaa</u>lika yutimmu ni\'matahu \'alaykum la\'allakum tuslimuun<strong>a</strong>', 'Dan Allah menjadikan tempat bernaung bagimu dari apa yang telah Dia ciptakan, Dia menjadikan bagimu tempat-tempat tinggal di gunung-gunung, dan Dia menjadikan pakaian bagimu yang memeliharamu dari panas dan pakaian (baju besi) yang memelihara kamu dalam peperangan. Demikian Allah menyempurnakan nikmat-Nya kepadamu agar kamu berserah diri (kepada-Nya). '),
	(1983, 16, 82, 'فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ الْمُبِيْنُ ', 'fa-in tawallaw fa-innam<u>aa</u> \'alayka <strong>a</strong>lbal<u>aa</u>ghu <strong>a</strong>lmubiin<strong>u</strong>', 'Maka jika mereka berpaling, maka ketahuilah kewajiban yang dibebankan atasmu (Muhammad) hanyalah menyampaikan (amanat Allah) dengan terang.'),
	(1984, 16, 83, 'يَعْرِفُوْنَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُوْنَهَا وَاَكْثَرُهُمُ الْكٰفِرُوْنَ ࣖ ', 'ya\'rifuuna ni\'mata <strong>al</strong>l<u>aa</u>hi tsumma yunkiruunah<u>aa</u> wa-aktsaruhumu <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Mereka mengetahui nikmat Allah, kemudian mereka mengingkarinya dan kebanyakan mereka adalah orang yang ingkar kepada Allah.'),
	(1985, 16, 84, 'وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَهِيْدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِيْنَ كَفَرُوْا وَلَا هُمْ يُسْتَعْتَبُوْنَ ', 'wayawma nab\'atsu min kulli ummatin syahiidan tsumma l<u>aa</u> yu/<u>dz</u>anu lilla<u>dz</u>iina kafaruu wal<u>aa</u> hum yusta\'tabuun<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Kami bangkitkan seorang saksi (rasul) dari setiap umat, kemudian tidak diizinkan kepada orang yang kafir (untuk membela diri) dan tidak (pula) dibolehkan memohon ampunan.'),
	(1986, 16, 85, 'وَاِذَا رَاَ الَّذِيْنَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُوْنَ', 'wa-i<u>dzaa</u> ra<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu <strong>a</strong>l\'a<u>dzaa</u>ba fal<u>aa</u> yukhaffafu \'anhum wal<u>aa</u> hum yun<u>zh</u>aruun<strong>a</strong>', 'Dan apabila orang zalim telah menyaksikan azab, maka mereka tidak mendapat keringanan dan tidak (pula) diberi penangguhan.'),
	(1987, 16, 86, 'وَاِذَا رَاَ الَّذِيْنَ اَشْرَكُوْا شُرَكَاۤءَهُمْ قَالُوْا رَبَّنَا هٰٓؤُلَاۤءِ شُرَكَاۤؤُنَا الَّذِيْنَ كُنَّا نَدْعُوْا مِنْ دُوْنِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكٰذِبُوْنَۚ ', 'wa-i<u>dzaa</u> ra<u>aa</u> <strong>al</strong>la<u>dz</u>iina asyrakuu syurak<u>aa</u>-ahum q<u>aa</u>luu rabban<u>aa</u> h<u>aa</u>ul<u>aa</u>-i syurak<u>aa</u>un<u>aa</u> <strong>al</strong>la<u>dz</u>iina kunn<u>aa</u> nad\'uu min duunika fa-alqaw ilayhimu <strong>a</strong>lqawla innakum lak<u>aadz</u>ibuun<strong>a</strong>', 'Dan apabila orang yang mempersekutukan (Allah) melihat sekutu-sekutu mereka, mereka berkata, “Ya Tuhan kami, mereka inilah sekutu-sekutu kami yang dahulu kami sembah selain Engkau.” Lalu sekutu mereka menyatakan kepada mereka, “Kamu benar-benar pendusta.”'),
	(1988, 16, 87, 'وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَىِٕذِ ِۨالسَّلَمَ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ  ', 'wa-alqaw il<u>aa</u> <strong>al</strong>l<u>aa</u>hi yawma-i<u>dz</u>in <strong>al</strong>ssalama wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Dan pada hari itu mereka menyatakan tunduk kepada Allah dan lenyaplah segala yang mereka ada-adakan.'),
	(1989, 16, 88, 'اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ زِدْنٰهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوْا يُفْسِدُوْنَ ', '<strong>al</strong>la<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiili <strong>al</strong>l<u>aa</u>hi zidn<u>aa</u>hum \'a<u>dzaa</u>ban fawqa <strong>a</strong>l\'a<u>dzaa</u>bi bim<u>aa</u> k<u>aa</u>nuu yufsiduun<strong>a</strong>', 'Orang yang kafir dan menghalangi (manusia) dari jalan Allah, Kami tambahkan kepada mereka siksaan demi siksaan disebabkan mereka selalu berbuat kerusakan.'),
	(1990, 16, 89, 'وَيَوْمَ نَبْعَثُ فِيْ كُلِّ اُمَّةٍ شَهِيْدًا عَلَيْهِمْ مِّنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَهِيْدًا عَلٰى هٰٓؤُلَاۤءِۗ وَنَزَّلْنَا عَلَيْكَ الْكِتٰبَ تِبْيَانًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً وَّبُشْرٰى لِلْمُسْلِمِيْنَ ࣖ ', 'wayawma nab\'atsu fii kulli ummatin syahiidan \'alayhim min anfusihim waji/n<u>aa</u> bika syahiidan \'al<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wanazzaln<u>aa</u> \'alayka <strong>a</strong>lkit<u>aa</u>ba tiby<u>aa</u>nan likulli syay-in wahudan wara<u>h</u>matan wabusyr<u>aa</u> lilmuslimiin<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Kami bangkitkan pada setiap umat seorang saksi atas mereka dari mereka sendiri, dan Kami datangkan engkau (Muhammad) menjadi saksi atas mereka. Dan Kami turunkan Kitab (Al-Qur\'an) kepadamu untuk menjelaskan segala sesuatu, sebagai petunjuk, serta rahmat dan kabar gembira bagi orang yang berserah diri (Muslim).'),
	(1991, 16, 90, '۞ اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَاِيْتَاۤئِ ذِى الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ', 'inna <strong>al</strong>l<u>aa</u>ha ya/muru bi<strong>a</strong>l\'adli wa<strong>a</strong>l-i<u>h</u>s<u>aa</u>ni wa-iit<u>aa</u>-i <u>dz</u>ii <strong>a</strong>lqurb<u>aa</u> wayanh<u>aa</u> \'ani <strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i wa<strong>a</strong>lmunkari wa<strong>a</strong>lbaghyi ya\'i<u>zh</u>ukum la\'allakum ta<u>dz</u>akkaruun<strong>a</strong>', 'Sesungguhnya Allah menyuruh (kamu) berlaku adil dan berbuat kebajikan, memberi bantuan kepada kerabat, dan Dia melarang (melakukan) perbuatan keji, kemungkaran, dan permusuhan. Dia memberi pengajaran kepadamu agar kamu dapat mengambil pelajaran.'),
	(1992, 16, 91, 'وَاَوْفُوْا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُّمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْكِيْدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَفِيْلًا ۗاِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُوْنَ ', 'wa-awfuu bi\'ahdi <strong>al</strong>l<u>aa</u>hi i<u>dzaa</u> \'<u>aa</u>hadtum wal<u>aa</u> tanqu<u>dh</u>uu <strong>a</strong>l-aym<u>aa</u>na ba\'da tawkiidih<u>aa</u> waqad ja\'altumu <strong>al</strong>l<u>aa</u>ha \'alaykum kafiilan inna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> taf\'aluun<strong>a</strong>', 'Dan tepatilah janji dengan Allah apabila kamu berjanji dan janganlah kamu melanggar sumpah, setelah diikrarkan, sedang kamu telah menjadikan Allah sebagai saksimu (terhadap sumpah itu).  Sesungguhnya Allah mengetahui apa yang kamu perbuat.'),
	(1993, 16, 92, 'وَلَا تَكُوْنُوْا كَالَّتِيْ نَقَضَتْ غَزْلَهَا مِنْۢ بَعْدِ قُوَّةٍ اَنْكَاثًاۗ تَتَّخِذُوْنَ اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ اَنْ تَكُوْنَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍ ۗاِنَّمَا يَبْلُوْكُمُ اللّٰهُ بِهٖۗ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَ', 'wal<u>aa</u> takuunuu ka<strong>a</strong>llatii naqa<u>dh</u>at ghazlah<u>aa</u> min ba\'di quwwatin ank<u>aa</u>tsan tattakhi<u>dz</u>uuna aym<u>aa</u>nakum dakhalan baynakum an takuuna ummatun hiya arb<u>aa</u> min ummatin innam<u>aa</u> yabluukumu <strong>al</strong>l<u>aa</u>hu bihi walayubayyinanna lakum yawma <strong>a</strong>lqiy<u>aa</u>mati m<u>aa</u> kuntum fiihi takhtalifuun<strong>a</strong>', 'Dan janganlah kamu seperti seorang perempuan yang menguraikan benangnya yang sudah dipintal dengan kuat, menjadi cerai berai kembali. Kamu menjadikan sumpah (perjanjian)mu sebagai alat penipu di antaramu, disebabkan adanya satu golongan yang lebih banyak jumlahnya dari golongan yang lain. Allah hanya menguji kamu dengan hal itu, dan pasti pada hari Kiamat akan dijelaskan-Nya kepadamu apa yang dahulu kamu perselisihkan itu.'),
	(1994, 16, 93, 'وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُوْنَ ', 'walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu laja\'alakum ummatan w<u>aah</u>idatan wal<u>aa</u>kin yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u walatus-alunna \'amm<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan jika Allah menghendaki niscaya Dia menjadikan kamu satu umat (saja), tetapi Dia menyesatkan siapa yang Dia kehendaki dan memberi petunjuk kepada siapa yang Dia kehendaki. Tetapi kamu pasti akan ditanya tentang apa yang telah kamu kerjakan.'),
	(1995, 16, 94, 'وَلَا تَتَّخِذُوْٓا اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ فَتَزِلَّ قَدَمٌۢ بَعْدَ ثُبُوْتِهَا وَتَذُوْقُوا السُّوْۤءَ بِمَا صَدَدْتُّمْ عَنْ سَبِيْلِ اللّٰهِ ۚوَلَكُمْ عَذَابٌ عَظِيْمٌ ', 'wal<u>aa</u> tattakhi<u>dz</u>uu aym<u>aa</u>nakum dakhalan baynakum fatazilla qadamun ba\'da tsubuutih<u>aa</u> wata<u>dz</u>uuquu <strong>al</strong>ssuu-a bim<u>aa</u> <u>sh</u>adadtum \'an sabiili <strong>al</strong>l<u>aa</u>hi walakum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Dan janganlah kamu jadikan sumpah-sumpahmu sebagai alat penipu di antaramu, yang menyebabkan kaki(mu) tergelincir setelah tegaknya (kukuh), dan kamu akan merasakan keburukan (di dunia) karena kamu menghalangi (manusia) dari jalan Allah, dan kamu akan mandapat azab yang besar.'),
	(1996, 16, 95, 'وَلَا تَشْتَرُوْا بِعَهْدِ اللّٰهِ ثَمَنًا قَلِيْلًاۗ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ', 'wal<u>aa</u> tasytaruu bi\'ahdi <strong>al</strong>l<u>aa</u>hi tsamanan qaliilan innam<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi huwa khayrun lakum in kuntum ta\'lamuun<strong>a</strong>', 'Dan janganlah kamu jual perjanjian (dengan) Allah dengan harga murah, karena sesungguhnya apa yang ada di sisi Allah lebih baik bagimu jika kamu mengetahui.'),
	(1997, 16, 96, 'مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۗ وَلَنَجْزِيَنَّ الَّذِيْنَ صَبَرُوْٓا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ ', 'm<u>aa</u> \'indakum yanfadu wam<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi b<u>aa</u>qin walanajziyanna <strong>al</strong>la<u>dz</u>iina <u>sh</u>abaruu ajrahum bi-a<u>h</u>sani m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Apa yang ada di sisimu akan lenyap, dan apa yang ada di sisi Allah adalah kekal. Dan  Kami pasti akan memberi balasan kepada orang yang sabar dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.'),
	(1998, 16, 97, 'مَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهٗ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ  ', 'man \'amila <u>shaa</u>li<u>h</u>an min <u>dz</u>akarin aw unts<u>aa</u> wahuwa mu/minun falanu<u>h</u>yiyannahu <u>h</u>ay<u>aa</u>tan <u>th</u>ayyibatan walanajziyannahum ajrahum bi-a<u>h</u>sani m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Barangsiapa mengerjakan kebajikan, baik laki-laki maupun perempuan dalam keadaan beriman, maka pasti akan Kami berikan kepadanya kehidupan yang baik dan akan Kami beri balasan dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.'),
	(1999, 16, 98, 'فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطٰنِ الرَّجِيْمِ ', 'fa-i<u>dzaa</u> qara/ta <strong>a</strong>lqur-<u>aa</u>na fa<strong>i</strong>sta\'i<u>dz</u> bi<strong>al</strong>l<u>aa</u>hi mina <strong>al</strong>sysyay<u>th</u><u>aa</u>ni <strong>al</strong>rrajiim<strong>i</strong>', 'Maka apabila engkau (Muhammad) hendak membaca Al-Qur\'an, mohonlah perlindungan kepada Allah dari setan yang terkutuk.'),
	(2000, 16, 99, 'اِنَّهٗ لَيْسَ لَهٗ سُلْطٰنٌ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ', 'innahu laysa lahu sul<u>th</u><u>aa</u>nun \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'al<u>aa</u> rabbihim yatawakkaluun<strong>a</strong>', 'Sungguh, setan itu tidak akan berpengaruh terhadap orang yang beriman dan bertawakal kepada Tuhan.'),
	(2001, 16, 100, 'اِنَّمَا سُلْطٰنُهٗ عَلَى الَّذِيْنَ يَتَوَلَّوْنَهٗ وَالَّذِيْنَ هُمْ بِهٖ مُشْرِكُوْنَ ࣖ', 'innam<u>aa</u> sul<u>th</u><u>aa</u>nuhu \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina yatawallawnahu wa<strong>a</strong>lla<u>dz</u>iina hum bihi musyrikuun<strong>a</strong>', 'Pengaruhnya hanyalah terhadap orang yang menjadikannya pemimpin dan terhadap orang yang mempersekutukannya dengan Allah.'),
	(2002, 16, 101, 'وَاِذَا بَدَّلْنَآ اٰيَةً مَّكَانَ اٰيَةٍ ۙوَّاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُوْٓا اِنَّمَآ اَنْتَ مُفْتَرٍۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ', 'wa-i<u>dzaa</u> baddaln<u>aa</u> <u>aa</u>yatan mak<u>aa</u>na <u>aa</u>yatin wa<strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> yunazzilu q<u>aa</u>luu innam<u>aa</u> anta muftarin bal aktsaruhum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan apabila Kami mengganti suatu ayat dengan ayat yang lain, dan  Allah lebih mengetahui apa yang diturunkan-Nya, mereka berkata, “Sesungguhnya engkau (Muhammad) hanya mengada-ada saja.” Sebenarnya kebanyakan mereka tidak mengetahui.'),
	(2003, 16, 102, 'قُلْ نَزَّلَهٗ رُوْحُ الْقُدُسِ مِنْ رَّبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِيْنَ اٰمَنُوْا وَهُدًى وَّبُشْرٰى لِلْمُسْلِمِيْنَ ', 'qul nazzalahu ruu<u>h</u>u <strong>a</strong>lqudusi min rabbika bi<strong>a</strong>l<u>h</u>aqqi liyutsabbita <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wahudan wabusyr<u>aa</u> lilmuslimiin<strong>a</strong>', 'Katakanlah, “Rohulkudus (Jibril) menurunkan Al-Qur\'an itu dari Tuhanmu dengan kebenaran, untuk meneguhkan (hati) orang yang telah beriman, dan menjadi petunjuk serta kabar gembira bagi orang yang berserah diri (kepada Allah).”'),
	(2004, 16, 103, 'وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُوْلُوْنَ اِنَّمَا يُعَلِّمُهٗ بَشَرٌۗ لِسَانُ الَّذِيْ يُلْحِدُوْنَ اِلَيْهِ اَعْجَمِيٌّ وَّهٰذَا لِسَانٌ عَرَبِيٌّ مُّبِيْنٌ ', 'walaqad na\'lamu annahum yaquuluuna innam<u>aa</u> yu\'allimuhu basyarun lis<u>aa</u>nu <strong>al</strong>la<u>dz</u>ii yul<u>h</u>iduuna ilayhi a\'jamiyyun wah<u>aadzaa</u> lis<u>aa</u>nun \'arabiyyun mubiin<strong>un</strong>', 'Dan sesungguhnya Kami mengetahui bahwa mereka berkata, “Sesungguhnya Al-Qur\'an itu hanya diajarkan oleh seorang manusia kepadanya (Muhammad).” Bahasa orang yang mereka tuduhkan (bahwa Muhammad belajar) kepadanya adalah bahasa ‘Ajam, padahal ini (Al-Qur\'an) adalah dalam bahasa Arab yang jelas.'),
	(2005, 16, 104, 'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۙ لَا يَهْدِيْهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَلِيْمٌ ', 'inna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yahdiihimu <strong>al</strong>l<u>aa</u>hu walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Sesungguhnya orang yang tidak beriman kepada ayat-ayat Allah (Al-Qur\'an), Allah tidak akan memberi petunjuk kepada mereka dan mereka akan mendapat azab yang pedih.'),
	(2006, 16, 105, 'اِنَّمَا يَفْتَرِى الْكَذِبَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۚ وَاُولٰۤىِٕكَ هُمُ الْكٰذِبُوْنَ ', 'innam<u>aa</u> yaftarii <strong>a</strong>lka<u>dz</u>iba <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi waul<u>aa</u>-ika humu <strong>a</strong>lk<u>aadz</u>ibuun<strong>a</strong>', 'Sesungguhnya yang mengada-adakan kebohongan, hanyalah orang yang tidak beriman kepada ayat-ayat Allah, dan mereka itulah pembohong.'),
	(2007, 16, 106, 'مَنْ كَفَرَ بِاللّٰهِ مِنْۢ بَعْدِ اِيْمَانِهٖٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهٗ مُطْمَىِٕنٌّۢ بِالْاِيْمَانِ وَلٰكِنْ مَّنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِّنَ اللّٰهِ ۗوَلَهُمْ عَذَابٌ عَظِيْمٌ ', 'man kafara bi<strong>al</strong>l<u>aa</u>hi min ba\'di iim<u>aa</u>nihi ill<u>aa</u> man ukriha waqalbuhu mu<u>th</u>ma-innun bi<strong>a</strong>l-iim<u>aa</u>ni wal<u>aa</u>kin man syara<u>h</u>a bi<strong>a</strong>lkufri <u>sh</u>adran fa\'alayhim gha<u>dh</u>abun mina <strong>al</strong>l<u>aa</u>hi walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Barangsiapa kafir kepada Allah setelah dia beriman (dia mendapat kemurkaan Allah), kecuali orang yang dipaksa kafir padahal hatinya tetap tenang dalam beriman (dia tidak berdosa), tetapi orang yang melapangkan dadanya untuk kekafiran, maka kemurkaan Allah menimpanya dan mereka akan mendapat azab yang besar.'),
	(2008, 16, 107, 'ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ ', '<u>dzaa</u>lika bi-annahumu ista<u>h</u>abbuu <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-<u>aa</u>khirati wa-anna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Yang demikian itu disebabkan karena mereka lebih mencintai kehidupan di dunia daripada akhirat, dan Allah tidak memberi petunjuk kepada kaum yang kafir.'),
	(2009, 16, 108, 'اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۗ وَاُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina <u>th</u>aba\'a <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> quluubihim wasam\'ihim wa-ab<u>shaa</u>rihim waul<u>aa</u>-ika humu <strong>a</strong>lgh<u>aa</u>filuun<strong>a</strong>', 'Mereka itulah orang yang hati, pendengaran, dan penglihatannya telah dikunci oleh Allah. Mereka itulah orang yang lalai.'),
	(2010, 16, 109, 'لَا جَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْخٰسِرُوْنَ ', 'l<u>aa</u> jarama annahum fii <strong>a</strong>l-<u>aa</u>khirati humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', 'Pastilah mereka termasuk orang yang rugi di akhirat nanti.'),
	(2011, 16, 110, 'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ هَاجَرُوْا مِنْۢ بَعْدِ مَا فُتِنُوْا ثُمَّ جَاهَدُوْا وَصَبَرُوْاۚ اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ࣖ  ', 'tsumma inna rabbaka lilla<u>dz</u>iina h<u>aa</u>jaruu min ba\'di m<u>aa</u> futinuu tsumma j<u>aa</u>haduu wa<u>sh</u>abaruu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Kemudian Tuhanmu (pelindung) bagi orang yang berhijrah setelah menderita cobaan, kemudian mereka berjihad dan bersabar, sungguh, Tuhanmu setelah itu benar-benar Maha Pengampun, Maha Penyayang.'),
	(2012, 16, 111, '۞ يَوْمَ تَأْتِيْ كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَّفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُمْ لَا يُظْلَمُوْنَ ', 'yawma ta/tii kullu nafsin tuj<u>aa</u>dilu \'an nafsih<u>aa</u> watuwaff<u>aa</u> kullu nafsin m<u>aa</u> \'amilat wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', '(Ingatlah) pada hari (ketika) setiap orang datang untuk membela dirinya sendiri dan bagi setiap orang diberi (balasan) penuh sesuai dengan apa yang telah dikerjakannya, dan mereka tidak dizalimi (dirugikan).'),
	(2013, 16, 112, 'وَضَرَبَ اللّٰهُ مَثَلًا قَرْيَةً كَانَتْ اٰمِنَةً مُّطْمَىِٕنَّةً يَّأْتِيْهَا رِزْقُهَا رَغَدًا مِّنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوْعِ وَالْخَوْفِ بِمَا كَانُوْا يَصْنَعُوْنَ ', 'wa<u>dh</u>araba <strong>al</strong>l<u>aa</u>hu matsalan qaryatan k<u>aa</u>nat <u>aa</u>minatan mu<u>th</u>ma-innatan ya/tiih<u>aa</u> rizquh<u>aa</u> raghadan min kulli mak<u>aa</u>nin fakafarat bi-an\'umi <strong>al</strong>l<u>aa</u>hi fa-a<u>dzaa</u>qah<u>aa</u> <strong>al</strong>l<u>aa</u>hu lib<u>aa</u>sa <strong>a</strong>ljuu\'i wa<strong>a</strong>lkhawfi bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun<strong>a</strong>', 'Dan Allah telah membuat suatu perumpamaan (dengan) sebuah negeri yang dahulunya aman lagi tenteram, rezeki datang kepadanya melimpah ruah dari segenap tempat, tetapi (pen-duduk)nya mengingkari nikmat-nikmat Allah, karena itu Allah menimpakan kepada mereka bencana kelaparan dan ketakutan, disebabkan apa yang mereka perbuat.'),
	(2014, 16, 113, 'وَلَقَدْ جَاۤءَهُمْ رَسُوْلٌ مِّنْهُمْ فَكَذَّبُوْهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظٰلِمُوْنَ ', 'walaqad j<u>aa</u>-ahum rasuulun minhum faka<u>dzdz</u>abuuhu fa-akha<u>dz</u>ahumu <strong>a</strong>l\'a<u>dzaa</u>bu wahum <u>zhaa</u>limuun<strong>a</strong>', 'Dan sungguh, telah datang kepada mereka seorang rasul dari (kalangan) mereka sendiri, tetapi mereka mendustakannya, karena itu mereka ditimpa azab dan mereka adalah orang yang zalim.'),
	(2015, 16, 114, 'فَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًاۖ وَّاشْكُرُوْا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ', 'fakuluu mimm<u>aa</u> razaqakumu <strong>al</strong>l<u>aa</u>hu <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wa<strong>u</strong>sykuruu ni\'mata <strong>al</strong>l<u>aa</u>hi in kuntum iyy<u>aa</u>hu ta\'buduun<strong>a</strong>', 'Maka makanlah yang halal lagi baik dari rezeki yang telah diberikan Allah kepadamu; dan syukurilah nikmat Allah, jika kamu hanya menyembah kepada-Nya.'),
	(2016, 16, 115, 'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ', 'innam<u>aa</u> <u>h</u>arrama \'alaykumu <strong>a</strong>lmaytata wa<strong>al</strong>ddama wala<u>h</u>ma <strong>a</strong>lkhinziiri wam<u>aa</u> uhilla lighayri <strong>al</strong>l<u>aa</u>hi bihi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Sesungguhnya Allah hanya mengharamkan atasmu bangkai, darah, daging babi, dan (hewan) yang disembelih dengan (menyebut nama) selain Allah, tetapi barangsiapa terpaksa (memakannya) bukan karena menginginkannya dan tidak (pula) melampaui batas, maka  sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(2017, 16, 116, 'وَلَا تَقُوْلُوْا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلٰلٌ وَّهٰذَا حَرَامٌ لِّتَفْتَرُوْا عَلَى اللّٰهِ الْكَذِبَۗ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ ', 'wal<u>aa</u> taquuluu lim<u>aa</u> ta<u>sh</u>ifu <strong>a</strong>lsinatukumu <strong>a</strong>lka<u>dz</u>iba h<u>aadzaa</u> <u>h</u>al<u>aa</u>lun wah<u>aadzaa</u> <u>h</u>ar<u>aa</u>mun litaftaruu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba inna <strong>al</strong>la<u>dz</u>iina yaftaruuna \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lka<u>dz</u>iba l<u>aa</u> yufli<u>h</u>uun<strong>a</strong>', 'Dan janganlah kamu mengatakan terhadap apa yang disebut-sebut oleh lidahmu secara dusta ”Ini halal dan ini haram,” untuk mengada-adakan kebohongan terhadap Allah. Sesungguhnya orang yang mengada-adakan kebohongan terhadap Allah tidak akan beruntung.'),
	(2018, 16, 117, 'مَتَاعٌ قَلِيْلٌ  ۖوَّلَهُمْ عَذَابٌ اَلِيْمٌ ', 'mat<u>aa</u>\'un qaliilun walahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', '(Itu adalah) kesenangan yang sedikit; dan mereka akan mendapat azab yang pedih.'),
	(2019, 16, 118, 'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ ۗوَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ', 'wa\'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina h<u>aa</u>duu <u>h</u>arramn<u>aa</u> m<u>aa</u> qa<u>sh</u>a<u>sh</u>n<u>aa</u> \'alayka min qablu wam<u>aa</u> <u>zh</u>alamn<u>aa</u>hum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Dan terhadap orang Yahudi, Kami haramkan apa yang telah Kami ceritakan dahulu kepadamu (Muhammad). Kami tidak menzalimi mereka, justru merekalah yang menzalimi diri sendiri.'),
	(2020, 16, 119, 'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ عَمِلُوا السُّوْۤءَ بِجَهَالَةٍ ثُمَّ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ࣖ ', 'tsumma inna rabbaka lilla<u>dz</u>iina \'amiluu <strong>al</strong>ssuu-a bijah<u>aa</u>latin tsumma t<u>aa</u>buu min ba\'di <u>dzaa</u>lika wa-a<u>sh</u>la<u>h</u>uu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim<strong>un</strong>', 'Kemudian, sesungguhnya Tuhanmu (mengampuni) orang yang mengerjakan kesalahan karena kebodohannya, kemudian mereka bertobat setelah itu dan memperbaiki (dirinya), sungguh, Tuhanmu setelah itu benar-benar Maha Pengampun, Maha Penyayang.'),
	(2021, 16, 120, 'اِنَّ اِبْرٰهِيْمَ كَانَ اُمَّةً قَانِتًا لِّلّٰهِ حَنِيْفًاۗ وَلَمْ يَكُ مِنَ الْمُشْرِكِيْنَۙ', 'inna ibr<u>aa</u>hiima k<u>aa</u>na ummatan q<u>aa</u>nitan lill<u>aa</u>hi <u>h</u>aniifan walam yaku mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Sungguh, Ibrahim adalah seorang imam (yang dapat dijadikan teladan), patuh kepada Allah dan hanif. Dan dia bukanlah termasuk orang musyrik (yang mempersekutukan Allah),'),
	(2022, 16, 121, 'شَاكِرًا لِّاَنْعُمِهِ ۖاجْتَبٰىهُ وَهَدٰىهُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'sy<u>aa</u>kiran li-an\'umihi ijtab<u>aa</u>hu wahad<u>aa</u>hu il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'dia mensyukuri nikmat-nikmat-Nya. Allah telah memilihnya dan menunjukinya ke jalan yang lurus.'),
	(2023, 16, 122, 'وَاٰتَيْنٰهُ فِى الدُّنْيَا حَسَنَةً ۗوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ۗ  ', 'wa<u>aa</u>tayn<u>aa</u>hu fii <strong>al</strong>dduny<u>aa</u> <u>h</u>asanatan wa-innahu fii <strong>a</strong>l-<u>aa</u>khirati lamina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan Kami berikan kepadanya kebaikan di dunia, dan sesungguhnya di akhirat dia termasuk orang yang saleh.'),
	(2024, 16, 123, 'ثُمَّ اَوْحَيْنَآ اِلَيْكَ اَنِ اتَّبِعْ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ', 'tsumma aw<u>h</u>ayn<u>aa</u> ilayka ani ittabi\' millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'Kemudian Kami wahyukan kepadamu (Muhammad), “Ikutilah agama Ibrahim yang lurus, dan dia bukanlah termasuk orang musyrik.”'),
	(2025, 16, 124, 'اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِيْنَ اخْتَلَفُوْا فِيْهِۗ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ', 'innam<u>aa</u> ju\'ila <strong>al</strong>ssabtu \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina ikhtalafuu fiihi wa-inna rabbaka laya<u>h</u>kumu baynahum yawma <strong>a</strong>lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun<strong>a</strong>', 'Sesungguhnya (menghormati) hari Sabtu hanya diwajibkan atas orang (Yahudi) yang memperselisihkannya. Dan sesungguhnya Tuhanmu pasti akan memberi keputusan di antara mereka pada hari Kiamat terhadap apa yang telah mereka perselisihkan itu.'),
	(2026, 16, 125, 'اُدْعُ اِلٰى سَبِيْلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّتِيْ هِيَ اَحْسَنُۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ', 'ud\'u il<u>aa</u> sabiili rabbika bi<strong>a</strong>l<u>h</u>ikmati wa<strong>a</strong>lmaw\'i<u>zh</u>ati <strong>a</strong>l<u>h</u>asanati waj<u>aa</u>dilhum bi<strong>a</strong>llatii hiya a<u>h</u>sanu inna rabbaka huwa a\'lamu biman <u>dh</u>alla \'an sabiilihi wahuwa a\'lamu bi<strong>a</strong>lmuhtadiin<strong>a</strong>', 'Serulah (manusia) kepada jalan Tuhanmu dengan hikmah dan pengajaran yang baik, dan berdebatlah dengan mereka dengan cara yang baik. Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui siapa yang sesat dari jalan-Nya dan Dialah yang lebih mengetahui siapa yang mendapat petunjuk.'),
	(2027, 16, 126, 'وَاِنْ عَاقَبْتُمْ فَعَاقِبُوْا بِمِثْلِ مَا عُوْقِبْتُمْ بِهٖۗ وَلَىِٕنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِّلصّٰبِرِيْنَ ', 'wa-in \'<u>aa</u>qabtum fa\'<u>aa</u>qibuu bimitsli m<u>aa</u> \'uuqibtum bihi wala-in <u>sh</u>abartum lahuwa khayrun li<strong>l</strong><u>shshaa</u>biriin<strong>a</strong>', 'Dan jika kamu membalas, maka balaslah dengan (balasan) yang sama dengan siksaan yang ditimpakan kepadamu. Tetapi jika kamu bersabar, sesungguhnya itulah yang lebih baik bagi orang yang sabar.'),
	(2028, 16, 127, 'وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ', 'wa<strong>i</strong><u>sh</u>bir wam<u>aa</u> <u>sh</u>abruka ill<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wal<u>aa</u> ta<u>h</u>zan \'alayhim wal<u>aa</u> taku fii <u>dh</u>ayqin mimm<u>aa</u> yamkuruun<strong>a</strong>', 'Dan bersabarlah (Muhammad) dan kesabaranmu itu semata-mata dengan pertolongan Allah dan janganlah engkau bersedih hati terhadap (kekafiran) mereka dan jangan (pula) bersempit dada terhadap tipu daya yang mereka rencanakan.'),
	(2029, 16, 128, 'اِنَّ اللّٰهَ مَعَ الَّذِيْنَ اتَّقَوْا وَّالَّذِيْنَ هُمْ مُّحْسِنُوْنَ ࣖ   ۔', 'inna <strong>al</strong>l<u>aa</u>ha ma\'a <strong>al</strong>la<u>dz</u>iina ittaqaw wa<strong>a</strong>lla<u>dz</u>iina hum mu<u>h</u>sinuun<strong>a</strong>', 'Sungguh, Allah beserta orang-orang yang bertakwa dan orang-orang yang berbuat kebaikan.'),
	(2030, 17, 1, 'سُبْحٰنَ الَّذِيْٓ اَسْرٰى بِعَبْدِهٖ لَيْلًا مِّنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذِيْ بٰرَكْنَا حَوْلَهٗ لِنُرِيَهٗ مِنْ اٰيٰتِنَاۗ اِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ', 'sub<u>haa</u>na <strong>al</strong>la<u>dz</u>ii asr<u>aa</u> bi\'abdihi laylan mina <strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi il<u>aa</u> <strong>a</strong>lmasjidi <strong>a</strong>l-aq<u>shaa</u> <strong>al</strong>la<u>dz</u>ii b<u>aa</u>rakn<u>aa</u> <u>h</u>awlahu linuriyahu min <u>aa</u>y<u>aa</u>tin<u>aa</u> innahu huwa <strong>al</strong>ssamii\'u <strong>a</strong>lba<u>sh</u>iir<strong>u</strong>', 'Mahasuci (Allah), yang telah memperjalankan hamba-Nya (Muhammad) pada malam hari dari Masjidilharam ke Masjidil Aqsa yang telah Kami berkahi sekelilingnya agar Kami perlihatkan kepadanya sebagian tanda-tanda (kebesaran) Kami. Sesungguhnya Dia Maha Mendengar, Maha Melihat.'),
	(2031, 17, 2, 'وَاٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَ اَلَّا تَتَّخِذُوْا مِنْ دُوْنِيْ وَكِيْلًاۗ ', 'wa<u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba waja\'aln<u>aa</u>hu hudan libanii isr<u>aa</u>-iila <strong>al</strong>l<u>aa</u> tattakhi<u>dz</u>uu min duunii wakiil<u>aa</u><strong>n</strong>', 'Dan Kami berikan kepada Musa, Kitab (Taurat) dan Kami jadikannya petunjuk bagi Bani Israil (dengan firman), “Janganlah kamu mengambil (pelindung) selain Aku. '),
	(2032, 17, 3, 'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوْحٍۗ اِنَّهٗ كَانَ عَبْدًا شَكُوْرًا', '<u>dz</u>urriyyata man <u>h</u>amaln<u>aa</u> ma\'a nuu<u>h</u>in innahu k<u>aa</u>na \'abdan syakuur<u>aa</u><strong>n</strong>', '(Wahai) keturunan orang yang Kami bawa bersama Nuh. Sesungguhnya dia (Nuh) adalah hamba (Allah) yang banyak bersyukur.”'),
	(2033, 17, 4, 'وَقَضَيْنَآ اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ فِى الْكِتٰبِ لَتُفْسِدُنَّ فِى الْاَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيْرًا', 'waqa<u>dh</u>ayn<u>aa</u> il<u>aa</u> banii isr<u>aa</u>-iila fii <strong>a</strong>lkit<u>aa</u>bi latufsidunna fii <strong>a</strong>l-ar<u>dh</u>i marratayni walata\'lunna \'uluwwan kabiir<u>aa</u><strong>n</strong>', 'Dan Kami tetapkan terhadap Bani Israil dalam Kitab itu, “Kamu pasti akan berbuat kerusakan di bumi ini dua kali dan pasti kamu akan menyombongkan diri dengan kesombongan yang besar.”'),
	(2034, 17, 5, 'فَاِذَا جَاۤءَ وَعْدُ اُوْلٰىهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَّنَآ اُولِيْ بَأْسٍ شَدِيْدٍ فَجَاسُوْا خِلٰلَ الدِّيَارِۗ وَكَانَ وَعْدًا مَّفْعُوْلًا', 'fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du uul<u>aa</u>hum<u>aa</u> ba\'atsn<u>aa</u> \'alaykum \'ib<u>aa</u>dan lan<u>aa</u> ulii ba/sin syadiidin faj<u>aa</u>suu khil<u>aa</u>la <strong>al</strong>ddiy<u>aa</u>ri wak<u>aa</u>na wa\'dan maf\'uul<u>aa</u><strong>n</strong>', 'Maka apabila datang saat hukuman bagi (kejahatan) yang pertama dari kedua (kejahatan) itu, Kami datangkan kepadamu hamba-hamba Kami yang perkasa, lalu mereka merajalela di kampung-kampung. Dan itulah ketetapan yang pasti terlaksana.'),
	(2035, 17, 6, 'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنٰكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَجَعَلْنٰكُمْ اَكْثَرَ نَفِيْرًا', 'tsumma radadn<u>aa</u> lakumu <strong>a</strong>lkarrata \'alayhim wa-amdadn<u>aa</u>kum bi-amw<u>aa</u>lin wabaniina waja\'aln<u>aa</u>kum aktsara nafiir<u>aa</u><strong>n</strong>', 'Kemudian Kami berikan kepadamu giliran untuk mengalahkan mereka, Kami membantumu dengan harta kekayaan dan anak-anak dan Kami jadikan kamu kelompok yang lebih besar.'),
	(2036, 17, 7, 'اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ ۗوَاِنْ اَسَأْتُمْ فَلَهَاۗ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ لِيَسٗۤـُٔوْا وُجُوْهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوْهُ اَوَّلَ مَرَّةٍ وَّلِيُتَبِّرُوْا مَا عَلَوْا تَتْبِيْرًا', 'in a<u>h</u>santum a<u>h</u>santum li-anfusikum wa-in asa/tum falah<u>aa</u> fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du <strong>a</strong>l-<u>aa</u>khirati liyasuu-uu wujuuhakum waliyadkhuluu <strong>a</strong>lmasjida kam<u>aa</u> dakhaluuhu awwala marratin waliyutabbiruu m<u>aa</u> \'alaw tatbiir<u>aa</u><strong>n</strong>', 'Jika kamu berbuat baik (berarti) kamu berbuat baik untuk dirimu sendiri. Dan jika kamu berbuat jahat, maka (kerugian kejahatan) itu untuk dirimu sendiri. Apabila datang saat hukuman (kejahatan) yang kedua, (Kami bangkitkan musuhmu) untuk menyuramkan wajahmu lalu mereka masuk ke dalam masjid (Masjidil Aqsa), sebagaimana ketika mereka memasukinya pertama kali dan mereka membinasakan apa saja yang mereka kuasai.'),
	(2037, 17, 8, 'عَسٰى رَبُّكُمْ اَنْ يَّرْحَمَكُمْۚ وَاِنْ عُدْتُّمْ عُدْنَاۘ وَجَعَلْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ حَصِيْرًا ', '\'as<u>aa</u> rabbukum an yar<u>h</u>amakum wa-in \'udtum \'udn<u>aa</u> waja\'aln<u>aa</u> jahannama lilk<u>aa</u>firiina <u>h</u>a<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Mudah-mudahan Tuhan kamu melimpahkan rahmat kepada kamu; tetapi jika kamu kembali (melakukan kejahatan), niscaya Kami kembali (mengazabmu). Dan Kami jadikan neraka Jahanam penjara bagi orang kafir.'),
	(2038, 17, 9, 'اِنَّ هٰذَا الْقُرْاٰنَ يَهْدِيْ لِلَّتِيْ هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا كَبِيْرًاۙ  ', 'inna h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>na yahdii lillatii hiya aqwamu wayubasysyiru <strong>a</strong>lmu/miniina <strong>al</strong>la<u>dz</u>iina ya\'maluuna <strong>al</strong><u>shshaa</u>li<u>haa</u>ti anna lahum ajran kabiir<u>aa</u><strong>n</strong>', 'Sungguh, Al-Qur\'an ini memberi petunjuk ke (jalan) yang paling lurus dan memberi kabar gembira kepada orang mukmin yang mengerjakan kebajikan, bahwa mereka akan mendapat pahala yang besar,'),
	(2039, 17, 10, 'وَّاَنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا ࣖ', 'wa-anna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati a\'tadn<u>aa</u> lahum \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'dan bahwa orang yang tidak beriman kepada kehidupan akhirat, Kami sediakan bagi mereka azab yang pedih.'),
	(2040, 17, 11, 'وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَاۤءَهٗ بِالْخَيْرِۗ وَكَانَ الْاِنْسَانُ عَجُوْلًا  ', 'wayad\'u <strong>a</strong>l-ins<u>aa</u>nu bi<strong>al</strong>sysyarri du\'<u>aa</u>-ahu bi<strong>a</strong>lkhayri wak<u>aa</u>na <strong>a</strong>l-ins<u>aa</u>nu \'ajuul<u>aa</u><strong>n</strong>', 'Dan Manusia (seringkali) berdoa untuk kejahatan sebagaimana (biasanya) dia berdoa untuk kebaikan. Dan memang manusia bersifat tergesa-gesa.'),
	(2041, 17, 12, 'وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَآ اٰيَةَ الَّيْلِ وَجَعَلْنَآ اٰيَةَ النَّهَارِ مُبْصِرَةً لِّتَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ وَلِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ وَكُلَّ شَيْءٍ فَصَّلْنٰهُ تَفْصِيْلًا', 'waja\'aln<u>aa</u> <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra <u>aa</u>yatayni fama<u>h</u>awn<u>aa</u> <u>aa</u>yata <strong>al</strong>layli waja\'aln<u>aa</u> <u>aa</u>yata <strong>al</strong>nnah<u>aa</u>ri mub<u>sh</u>iratan litabtaghuu fa<u>dh</u>lan min rabbikum walita\'lamuu \'adada <strong>al</strong>ssiniina wa<strong>a</strong>l<u>h</u>is<u>aa</u>ba wakulla syay-in fa<u>shsh</u>aln<u>aa</u>hu taf<u>sh</u>iil<u>aa</u><strong>n</strong>', 'Dan Kami jadikan malam dan siang sebagai dua tanda (kebesaran Kami), kemudian Kami hapuskan tanda malam dan Kami jadikan tanda siang itu terang benderang, agar kamu (dapat) mencari karunia dari Tuhanmu, dan agar kamu mengetahui bilangan tahun dan perhitungan (waktu). Dan segala sesuatu telah Kami terangkan dengan jelas.'),
	(2042, 17, 13, 'وَكُلَّ اِنْسَانٍ اَلْزَمْنٰهُ طٰۤىِٕرَهٗ فِيْ عُنُقِهٖۗ وَنُخْرِجُ لَهٗ يَوْمَ الْقِيٰمَةِ كِتٰبًا يَّلْقٰىهُ مَنْشُوْرًا', 'wakulla ins<u>aa</u>nin <strong>a</strong>lzamn<u>aa</u>hu <u>th</u><u>aa</u>-irahu fii \'unuqihi wanukhriju lahu yawma <strong>a</strong>lqiy<u>aa</u>mati kit<u>aa</u>ban yalq<u>aa</u>hu mansyuur<u>aa</u><strong>n</strong>', 'Dan setiap manusia telah Kami kalungkan (catatan) amal perbuatannya di lehernya. Dan pada hari Kiamat Kami keluarkan baginya sebuah kitab dalam keadaan terbuka. '),
	(2043, 17, 14, 'اِقْرَأْ كِتَابَكَۗ  كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيْبًاۗ', 'iqra/ kit<u>aa</u>baka kaf<u>aa</u> binafsika <strong>a</strong>lyawma \'alayka <u>h</u>asiib<u>aa</u><strong>n</strong>', '“Bacalah kitabmu, cukuplah dirimu sendiri pada hari ini sebagai penghitung atas dirimu.”'),
	(2044, 17, 15, 'مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ وَمَا كُنَّا مُعَذِّبِيْنَ حَتّٰى نَبْعَثَ رَسُوْلًا', 'mani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla fa-innam<u>aa</u> ya<u>dh</u>illu \'alayh<u>aa</u> wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> wam<u>aa</u> kunn<u>aa</u> mu\'a<u>dzdz</u>ibiina <u>h</u>att<u>aa</u> nab\'atsa rasuul<u>aa</u><strong>n</strong>', 'Barangsiapa berbuat sesuai dengan petunjuk (Allah), maka sesungguhnya itu untuk (keselamatan) dirinya sendiri; dan barang siapa tersesat maka sesungguhnya (kerugian) itu bagi dirinya sendiri. Dan seorang yang berdosa tidak dapat memikul dosa orang lain, tetapi Kami tidak akan menyiksa sebelum Kami mengutus seorang rasul.'),
	(2045, 17, 16, 'وَاِذَآ اَرَدْنَآ اَنْ نُّهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَفِيْهَا فَفَسَقُوْا فِيْهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنٰهَا تَدْمِيْرًا ', 'wa-i<u>dzaa</u> aradn<u>aa</u> an nuhlika qaryatan amarn<u>aa</u> mutrafiih<u>aa</u> fafasaquu fiih<u>aa</u> fa<u>h</u>aqqa \'alayh<u>aa</u> <strong>a</strong>lqawlu fadammarn<u>aa</u>h<u>aa</u> tadmiir<u>aa</u><strong>n</strong>', 'Dan jika Kami hendak membinasakan suatu negeri, maka Kami perintahkan kepada orang yang hidup mewah di negeri itu (agar menaati Allah), tetapi bila mereka melakukan kedurhakaan di dalam (negeri) itu, maka sepantasnya berlakulah terhadapnya perkataan (hukuman Kami), kemudian Kami binasakan sama sekali (negeri itu).'),
	(2046, 17, 17, 'وَكَمْ اَهْلَكْنَا مِنَ الْقُرُوْنِ مِنْۢ بَعْدِ نُوْحٍۗ وَكَفٰى بِرَبِّكَ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا', 'wakam ahlakn<u>aa</u> mina <strong>a</strong>lquruuni min ba\'di nuu<u>h</u>in wakaf<u>aa</u> birabbika bi<u>dz</u>unuubi \'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan berapa banyak kaum setelah Nuh, yang telah Kami binasakan. Dan cukuplah Tuhanmu Yang Maha Mengetahui, Maha Melihat dosa hamba-hamba-Nya.'),
	(2047, 17, 18, 'مَنْ كَانَ يُرِيْدُ الْعَاجِلَةَ عَجَّلْنَا لَهٗ فِيْهَا مَا نَشَاۤءُ لِمَنْ نُّرِيْدُ ثُمَّ جَعَلْنَا لَهٗ جَهَنَّمَۚ يَصْلٰىهَا مَذْمُوْمًا مَّدْحُوْرًا', 'man k<u>aa</u>na yuriidu <strong>a</strong>l\'<u>aa</u>jilata \'ajjaln<u>aa</u> lahu fiih<u>aa</u> m<u>aa</u> nasy<u>aa</u>u liman nuriidu tsumma ja\'aln<u>aa</u> lahu jahannama ya<u>sh</u>l<u>aa</u>h<u>aa</u> ma<u>dz</u>muuman mad<u>h</u>uur<u>aa</u><strong>n</strong>', 'Barangsiapa menghendaki kehidupan sekarang (duniawi), maka Kami segerakan baginya di (dunia) ini apa yang Kami kehendaki bagi orang yang Kami kehendaki. Kemudian Kami sediakan baginya (di akhirat) neraka Jahanam; dia akan memasukinya dalam keadaan tercela dan terusir.'),
	(2048, 17, 19, 'وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ كَانَ سَعْيُهُمْ مَّشْكُوْرًا', 'waman ar<u>aa</u>da <strong>a</strong>l-<u>aa</u>khirata wasa\'<u>aa</u> lah<u>aa</u> sa\'yah<u>aa</u> wahuwa mu/minun faul<u>aa</u>-ika k<u>aa</u>na sa\'yuhum masykuur<u>aa</u><strong>n</strong>', 'Dan barang siapa menghendaki kehidupan akhirat dan berusaha ke arah itu dengan sungguh-sungguh, sedangkan dia beriman, maka mereka itulah orang yang usahanya dibalas dengan baik.'),
	(2049, 17, 20, 'كُلًّا نُّمِدُّ هٰٓؤُلَاۤءِ وَهٰٓؤُلَاۤءِ مِنْ عَطَاۤءِ رَبِّكَ ۗوَمَا كَانَ عَطَاۤءُ رَبِّكَ مَحْظُوْرًا ', 'kullan numiddu h<u>aa</u>ul<u>aa</u>-i wah<u>aa</u>ul<u>aa</u>-i min \'a<u>th</u><u>aa</u>-i rabbika wam<u>aa</u> k<u>aa</u>na \'a<u>th</u><u>aa</u>u rabbika ma<u>hts</u>uur<u>aa</u><strong>n</strong>', 'Kepada masing-masing (golongan), baik (golongan) ini (yang menginginkan dunia) maupun (golongan) itu (yang menginginkan akhirat), Kami berikan bantuan dari kemurahan Tuhanmu. Dan kemurahan Tuhanmu tidak dapat dihalangi.'),
	(2050, 17, 21, 'اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۗ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجٰتٍ وَّاَكْبَرُ تَفْضِيْلًا', 'un<u>zh</u>ur kayfa fa<u>dhdh</u>aln<u>aa</u> ba\'<u>dh</u>ahum \'al<u>aa</u> ba\'<u>dh</u>in walal-<u>aa</u>khiratu akbaru daraj<u>aa</u>tin wa-akbaru taf<u>dh</u>iil<u>aa</u><strong>n</strong>', 'Perhatikanlah bagaimana Kami melebihkan sebagian mereka atas sebagian (yang lain). Dan kehidupan akhirat lebih tinggi derajatnya dan lebih besar keutamaan.'),
	(2051, 17, 22, 'لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَقْعُدَ مَذْمُوْمًا مَّخْذُوْلًا ࣖ ', 'l<u>aa</u> taj\'al ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fataq\'uda ma<u>dz</u>muuman makh<u>dz</u>uul<u>aa</u><strong>n</strong>', 'Janganlah engkau mengadakan tuhan yang lain di samping Allah, nanti engkau menjadi tercela dan terhina.'),
	(2052, 17, 23, '۞ وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسٰنًاۗ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَآ اَوْ كِلٰهُمَا فَلَا تَقُلْ لَّهُمَآ اُفٍّ وَّلَا تَنْهَرْهُمَا وَقُلْ لَّهُمَا قَوْلًا كَرِيْمًا', 'waqa<u>daa</u> rabbuka <strong>al</strong>l<u>aa</u> ta\'buduu ill<u>aa</u> iyy<u>aa</u>hu wabi<strong>a</strong>lw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan imm<u>aa</u> yablughanna \'indaka <strong>a</strong>lkibara a<u>h</u>aduhum<u>aa</u> aw kil<u>aa</u>hum<u>aa</u> fal<u>aa</u> taqul lahum<u>aa</u> uffin wal<u>aa</u> tanharhum<u>aa</u> waqul lahum<u>aa</u> qawlan kariim<u>aa</u><strong>n</strong>', 'Dan Tuhanmu telah memerintahkan agar kamu jangan menyembah selain Dia dan hendaklah berbuat baik kepada ibu bapak. Jika salah seorang di antara keduanya atau kedua-duanya sampai berusia lanjut dalam pemeliharaanmu, maka sekali-kali janganlah engkau mengatakan kepada keduanya perkataan “ah” dan janganlah engkau membentak keduanya, dan ucapkanlah kepada keduanya perkataan yang baik.'),
	(2053, 17, 24, 'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَّبِّ ارْحَمْهُمَا كَمَا رَبَّيٰنِيْ صَغِيْرًاۗ', 'wa<strong>i</strong>khfi<u>dh</u> lahum<u>aa</u> jan<u>aah</u>a <strong>al</strong><u>dzdz</u>ulli mina <strong>al</strong>rra<u>h</u>mati waqul rabbi ir<u>h</u>amhum<u>aa</u> kam<u>aa</u> rabbay<u>aa</u>nii <u>sh</u>aghiir<u>aa</u><strong>n</strong>', 'Dan rendahkanlah dirimu terhadap keduanya dengan penuh kasih sayang dan ucapkanlah, “Wahai Tuhanku! Sayangilah keduanya sebagaimana mereka berdua telah mendidik aku pada waktu kecil.”'),
	(2054, 17, 25, 'رَبُّكُمْ اَعْلَمُ بِمَا فِيْ نُفُوْسِكُمْ ۗاِنْ تَكُوْنُوْا صٰلِحِيْنَ فَاِنَّهٗ كَانَ لِلْاَوَّابِيْنَ غَفُوْرًا', 'rabbukum a\'lamu bim<u>aa</u> fii nufuusikum in takuunuu <u>shaa</u>li<u>h</u>iina fa-innahu k<u>aa</u>na lil-aww<u>aa</u>biina ghafuur<u>aa</u><strong>n</strong>', 'Tuhanmu lebih mengetahui apa yang ada dalam hatimu; jika kamu orang yang baik, maka sungguh, Dia Maha Pengampun kepada orang yang bertobat.'),
	(2055, 17, 26, 'وَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِ وَلَا تُبَذِّرْ تَبْذِيْرًا ', 'wa<u>aa</u>ti <u>dzaa</u> <strong>a</strong>lqurb<u>aa</u> <u>h</u>aqqahu wa<strong>a</strong>lmiskiina wa<strong>i</strong>bna <strong>al</strong>ssabiili wal<u>aa</u> tuba<u>dzdz</u>ir tab<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Dan berikanlah haknya kepada kerabat dekat, juga kepada orang miskin dan orang yang dalam perjalanan; dan janganlah kamu menghambur-hamburkan (hartamu) secara boros.'),
	(2056, 17, 27, 'اِنَّ الْمُبَذِّرِيْنَ كَانُوْٓا اِخْوَانَ الشَّيٰطِيْنِ ۗوَكَانَ الشَّيْطٰنُ لِرَبِّهٖ كَفُوْرًا', 'inna <strong>a</strong>lmuba<u>dzdz</u>iriina k<u>aa</u>nuu ikhw<u>aa</u>na <strong>al</strong>sysyay<u>aath</u>iini wak<u>aa</u>na <strong>al</strong>sysyay<u>th</u><u>aa</u>nu lirabbihi kafuur<u>aa</u><strong>n</strong>', 'Sesungguhnya orang-orang yang pemboros itu adalah saudara setan dan setan itu sangat ingkar kepada Tuhannya.'),
	(2057, 17, 28, 'وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاۤءَ رَحْمَةٍ مِّنْ رَّبِّكَ تَرْجُوْهَا فَقُلْ لَّهُمْ قَوْلًا مَّيْسُوْرًا', 'wa-imm<u>aa</u> tu\'ri<u>dh</u>anna \'anhumu ibtigh<u>aa</u>-a ra<u>h</u>matin min rabbika tarjuuh<u>aa</u> faqul lahum qawlan maysuur<u>aa</u><strong>n</strong>', 'Dan jika engkau berpaling dari mereka untuk memperoleh rahmat dari Tuhanmu yang engkau harapkan, maka katakanlah kepada mereka ucapan yang lemah lembut.'),
	(2058, 17, 29, 'وَلَا تَجْعَلْ يَدَكَ مَغْلُوْلَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوْمًا مَّحْسُوْرًا ', 'wal<u>aa</u> taj\'al yadaka maghluulatan il<u>aa</u> \'unuqika wal<u>aa</u> tabsu<u>th</u>h<u>aa</u> kulla <strong>a</strong>lbas<u>th</u>i fataq\'uda maluuman ma<u>h</u>suur<u>aa</u><strong>n</strong>', 'Dan janganlah engkau jadikan tanganmu terbelenggu pada lehermu dan jangan (pula) engkau terlalu mengulurkannya (sangat pemurah) nanti kamu menjadi tercela dan menyesal.'),
	(2059, 17, 30, 'اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا ࣖ', 'inna rabbaka yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u wayaqdiru innahu k<u>aa</u>na bi\'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Sungguh, Tuhanmu melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasi (bagi siapa yang Dia kehendaki); sungguh, Dia Maha Mengetahui, Maha Melihat hamba-hamba-Nya.'),
	(2060, 17, 31, 'وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۗ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۗ اِنَّ قَتْلَهُمْ كَانَ خِطْـًٔا كَبِيْرًا ', 'wal<u>aa</u> taqtuluu awl<u>aa</u>dakum khasyyata iml<u>aa</u>qin na<u>h</u>nu narzuquhum wa-iyy<u>aa</u>kum inna qatlahum k<u>aa</u>na khi<u>th</u>-an kabiir<u>aa</u><strong>n</strong>', 'Dan janganlah kamu membunuh anak-anakmu karena takut miskin. Kamilah yang memberi rezeki kepada mereka dan kepadamu. Membunuh mereka itu sungguh suatu dosa yang besar.'),
	(2061, 17, 32, 'وَلَا تَقْرَبُوا الزِّنٰىٓ اِنَّهٗ كَانَ فَاحِشَةً ۗوَسَاۤءَ سَبِيْلًا ', 'wal<u>aa</u> taqrabuu <strong>al</strong>zzin<u>aa</u> innahu k<u>aa</u>na f<u>aah</u>isyatan was<u>aa</u>-a sabiil<u>aa</u><strong>n</strong>', 'Dan janganlah kamu mendekati zina; (zina) itu sungguh suatu perbuatan keji, dan suatu jalan yang buruk.'),
	(2062, 17, 33, 'وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ وَمَنْ قُتِلَ مَظْلُوْمًا فَقَدْ جَعَلْنَا لِوَلِيِّهٖ سُلْطٰنًا فَلَا يُسْرِفْ فِّى الْقَتْلِۗ اِنَّهٗ كَانَ مَنْصُوْرًا', 'wal<u>aa</u> taqtuluu <strong>al</strong>nnafsa <strong>al</strong>latii <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi waman qutila ma<u>zh</u>luuman faqad ja\'aln<u>aa</u> liwaliyyihi sul<u>th</u><u>aa</u>nan fal<u>aa</u> yusrif fii <strong>a</strong>lqatli innahu k<u>aa</u>na man<u>sh</u>uur<u>aa</u><strong>n</strong>', 'Dan janganlah kamu membunuh orang yang diharamkan Allah (membunuhnya), kecuali dengan suatu (alasan) yang benar. Dan barang siapa dibunuh secara zalim, maka sungguh, Kami telah memberi kekuasaan kepada walinya, tetapi janganlah walinya itu melampaui batas dalam pembunuhan. Sesungguhnya dia adalah orang yang mendapat pertolongan.'),
	(2063, 17, 34, 'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗۖ وَاَوْفُوْا بِالْعَهْدِۖ اِنَّ الْعَهْدَ كَانَ مَسْـُٔوْلًا', 'wal<u>aa</u> taqrabuu m<u>aa</u>la <strong>a</strong>lyatiimi ill<u>aa</u> bi<strong>a</strong>llatii hiya a<u>h</u>sanu <u>h</u>att<u>aa</u> yablugha asyuddahu wa-awfuu bi<strong>a</strong>l\'ahdi inna <strong>a</strong>l\'ahda k<u>aa</u>na mas-uul<u>aa</u><strong>n</strong>', 'Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih baik (bermanfaat) sampai dia dewasa, dan penuhilah janji, karena janji itu pasti diminta pertanggungjawabannya.'),
	(2064, 17, 35, 'وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا', 'wa-awfuu <strong>a</strong>lkayla i<u>dzaa</u> kiltum wazinuu bi<strong>a</strong>lqis<u>th</u><u>aa</u>si <strong>a</strong>lmustaqiimi <u>dzaa</u>lika khayrun wa-a<u>h</u>sanu ta/wiil<u>aa</u><strong>n</strong>', 'Dan sempurnakanlah takaran apabila kamu menakar, dan timbanglah dengan timbangan yang benar. Itulah yang lebih utama (bagimu) dan lebih baik akibatnya.'),
	(2065, 17, 36, 'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ اُولٰۤىِٕكَ كَانَ عَنْهُ مَسْـُٔوْلًا ', 'wal<u>aa</u> taqfu m<u>aa</u> laysa laka bihi \'ilmun inna <strong>al</strong>ssam\'a wa<strong>a</strong>lba<u>sh</u>ara wa<strong>a</strong>lfu-<u>aa</u>da kullu ul<u>aa</u>-ika k<u>aa</u>na \'anhu mas-uul<u>aa</u><strong>n</strong>', 'Dan janganlah kamu mengikuti sesuatu yang tidak kamu ketahui. Karena pendengaran, penglihatan dan hati nurani, semua itu akan diminta pertanggungjawabannya.'),
	(2066, 17, 37, 'وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُوْلًا', 'wal<u>aa</u> tamsyi fii <strong>a</strong>l-ar<u>dh</u>i mara<u>h</u>an innaka lan takhriqa <strong>a</strong>l-ar<u>dh</u>a walan tablugha <strong>a</strong>ljib<u>aa</u>la <u>th</u>uul<u>aa</u><strong>n</strong>', 'Dan janganlah engkau berjalan di bumi ini dengan sombong, karena sesungguhnya engkau tidak akan dapat menembus bumi dan tidak akan mampu menjulang setinggi gunung.'),
	(2067, 17, 38, 'كُلُّ ذٰلِكَ كَانَ سَيِّئُهٗ عِنْدَ رَبِّكَ مَكْرُوْهًا ', 'kullu <u>dzaa</u>lika k<u>aa</u>na sayyi-uhu \'inda rabbika makruuh<u>aa</u><strong>n</strong>', 'Semua itu kejahatannya sangat dibenci di sisi Tuhanmu.'),
	(2068, 17, 39, 'ذٰلِكَ مِمَّآ اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۗ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتُلْقٰى فِيْ جَهَنَّمَ مَلُوْمًا مَّدْحُوْرًا ', 'afa-a<u>sh</u>f<u>aa</u>kum rabbukum bi<strong>a</strong>lbaniina wa<strong>i</strong>ttakha<u>dz</u>a mina <strong>a</strong>lmal<u>aa</u>-ikati in<u>aa</u>tsan innakum lataquuluuna qawlan \'a<u>zh</u>iim<u>aa</u><strong>n </strong>', 'Itulah sebagian hikmah yang diwahyukan Tuhan kepadamu (Muhammad). Dan janganlah engkau mengadakan tuhan yang lain di samping Allah, nanti engkau dilemparkan ke dalam neraka dalam keadaan tercela dan dijauhkan (dari rahmat Allah).'),
	(2069, 17, 40, 'اَفَاَصْفٰىكُمْ رَبُّكُمْ بِالْبَنِيْنَ وَاتَّخَذَ مِنَ الْمَلٰۤىِٕكَةِ اِنَاثًاۗ اِنَّكُمْ لَتَقُوْلُوْنَ قَوْلًا عَظِيْمًا ࣖ ', 'afa-a<u>sh</u>f<u>aa</u>kum rabbukum bi<strong>a</strong>lbaniina wa<strong>i</strong>ttakha<u>dz</u>a mina <strong>a</strong>lmal<u>aa</u>-ikati in<u>aa</u>tsan innakum lataquuluuna qawlan \'a<u>zh</u>iim<u>aa</u><strong>n </strong>', 'Maka apakah pantas Tuhan memilihkan anak laki-laki untukmu dan Dia mengambil anak perempuan dari malaikat? Sungguh, kamu benar-benar mengucapkan kata yang besar (dosanya).'),
	(2070, 17, 41, 'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُوْاۗ وَمَا يَزِيْدُهُمْ اِلَّا نُفُوْرًا', 'walaqad <u>sh</u>arrafn<u>aa</u> fii h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni liya<u>dzdz</u>akkaruu wam<u>aa</u> yaziiduhum ill<u>aa</u> nufuur<u>aa</u><strong>n</strong>', 'Dan sungguh, dalam Al-Qur\'an ini telah Kami (jelaskan) berulang-ulang (peringatan), agar mereka selalu ingat. Tetapi (peringatan) itu hanya menambah mereka lari (dari kebenaran).'),
	(2071, 17, 42, 'قُلْ لَّوْ كَانَ مَعَهٗ ٓ اٰلِهَةٌ كَمَا يَقُوْلُوْنَ اِذًا لَّابْتَغَوْا اِلٰى ذِى الْعَرْشِ سَبِيْلًا', 'qul law k<u>aa</u>na ma\'ahu <u>aa</u>lihatun kam<u>aa</u> yaquuluuna i<u>dz</u>an la<strong>i</strong>btaghaw il<u>aa</u> <u>dz</u>ii <strong>a</strong>l\'arsyi sabiil<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Jika ada tuhan-tuhan di samping-Nya, sebagai-mana yang mereka katakan, niscaya tuhan-tuhan itu mencari jalan kepada Tuhan yang mempunyai ’Arsy.”'),
	(2072, 17, 43, ' سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَقُوْلُوْنَ عُلُوًّا كَبِيْرًا', 'sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yaquuluuna \'uluwwan kabiir<u>aa</u><strong>n</strong>', 'Mahasuci dan Mahatinggi Dia dari apa yang mereka katakan, luhur dan agung (tidak ada bandingannya).'),
	(2073, 17, 44, 'تُسَبِّحُ لَهُ السَّمٰوٰتُ السَّبْعُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ وَاِنْ مِّنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِهٖ وَلٰكِنْ لَّا تَفْقَهُوْنَ تَسْبِيْحَهُمْۗ اِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا', 'tusabbi<u>h</u>u lahu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu <strong>al</strong>ssab\'u wa<strong>a</strong>l-ar<u>dh</u>u waman fiihinna wa-in min syay-in ill<u>aa</u> yusabbi<u>h</u>u bi<u>h</u>amdihi wal<u>aa</u>kin l<u>aa</u> tafqahuuna tasbii<u>h</u>ahum innahu k<u>aa</u>na <u>h</u>aliiman ghafuur<u>aa</u><strong>n</strong>', 'Langit yang tujuh, bumi dan semua yang ada di dalamnya bertasbih kepada Allah. Dan tidak ada sesuatu pun melainkan bertasbih dengan memuji-Nya, tetapi kamu tidak mengerti tasbih mereka. Sungguh, Dia Maha Penyantun, Maha Pengampun.'),
	(2074, 17, 45, 'وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ حِجَابًا مَّسْتُوْرًاۙ  ', 'wa-i<u>dzaa</u> qara/ta <strong>a</strong>lqur-<u>aa</u>na ja\'aln<u>aa</u> baynaka wabayna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati <u>h</u>ij<u>aa</u>ban mastuur<u>aa</u><strong>n</strong>', 'Dan apabila engkau (Muhammad) membaca Al-Qur\'an, Kami adakan suatu dinding yang tidak terlihat antara engkau dan orang-orang yang tidak beriman kepada kehidupan akhirat,'),
	(2075, 17, 46, 'وَّجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِذَا ذَكَرْتَ رَبَّكَ فِى الْقُرْاٰنِ وَحْدَهٗ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوْرًا', 'waja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-i<u>dzaa</u> <u>dz</u>akarta rabbaka fii <strong>a</strong>lqur-<u>aa</u>ni wa<u>h</u>dahu wallaw \'al<u>aa</u> adb<u>aa</u>rihim nufuur<u>aa</u><strong>n</strong>', 'Dan Kami jadikan hati mereka tertutup dan telinga mereka tersumbat, agar mereka tidak dapat memahaminya. Dan apabila engkau menyebut Tuhanmu saja dalam Al-Qur\'an, mereka berpaling ke belakang melarikan diri (karena benci).'),
	(2076, 17, 47, 'نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُوْنَ بِهٖٓ اِذْ يَسْتَمِعُوْنَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُوْلُ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا ', 'na<u>h</u>nu a\'lamu bim<u>aa</u> yastami\'uuna bihi i<u>dz</u> yastami\'uuna ilayka wa-i<u>dz</u> hum najw<u>aa</u> i<u>dz</u> yaquulu <strong>al</strong><u>zhzhaa</u>limuuna in tattabi\'uuna ill<u>aa</u> rajulan mas<u>h</u>uur<u>aa</u><strong>n</strong>', 'Kami lebih mengetahui dalam keadaan bagaimana mereka mendengarkan sewaktu mereka mendengarkan engkau (Muhammad), dan sewaktu mereka berbisik-bisik (yaitu) ketika orang zalim itu berkata, “Kamu hanyalah mengikuti seorang laki-laki yang kena sihir.”'),
	(2077, 17, 48, 'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا', 'un<u>zh</u>ur kayfa <u>dh</u>arabuu laka <strong>a</strong>l-amts<u>aa</u>la fa<u>dh</u>alluu fal<u>aa</u> yasta<u>th</u>ii\'uuna sabiil<u>aa</u><strong>n</strong>', 'Lihatlah bagaimana mereka membuat perumpamaan untukmu (Muhammad); karena itu mereka menjadi sesat dan tidak dapat lagi menemukan jalan (yang benar).'),
	(2078, 17, 49, 'وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ', 'waq<u>aa</u>luu a-i<u>dzaa</u> kunn<u>aa</u> \'i<u>zhaa</u>man waruf<u>aa</u>tan a-inn<u>aa</u> lamab\'uutsuuna khalqan jadiid<u>aa</u><strong>n</strong>', 'Dan mereka berkata, “Apabila kami telah menjadi tulang-belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk yang baru?”'),
	(2079, 17, 50, '۞ قُلْ كُوْنُوْا حِجَارَةً اَوْ حَدِيْدًاۙ ', 'qul kuunuu <u>h</u>ij<u>aa</u>ratan aw <u>h</u>adiid<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Jadilah kamu batu atau besi,'),
	(2080, 17, 51, 'اَوْ خَلْقًا مِّمَّا يَكْبُرُ فِيْ صُدُوْرِكُمْ ۚفَسَيَقُوْلُوْنَ مَنْ يُّعِيْدُنَاۗ قُلِ الَّذِيْ فَطَرَكُمْ اَوَّلَ مَرَّةٍۗ فَسَيُنْغِضُوْنَ اِلَيْكَ رُءُوْسَهُمْ وَيَقُوْلُوْنَ مَتٰى هُوَۗ قُلْ عَسٰٓى اَنْ يَّكُوْنَ قَرِيْبًا', 'aw khalqan mimm<u>aa</u> yakburu fii <u>sh</u>uduurikum fasayaquuluuna man yu\'iidun<u>aa</u> quli <strong>al</strong>la<u>dz</u>ii fa<u>th</u>arakum awwala marratin fasayunghi<u>dh</u>uuna ilayka ruuusahum wayaquuluuna mat<u>aa</u> huwa qul \'as<u>aa</u> an yakuuna qariib<u>aa</u><strong>n</strong>', 'atau menjadi makhluk yang besar (yang tidak mungkin hidup kembali) menurut pikiranmu.” Maka mereka akan bertanya, “Siapa yang akan menghidupkan kami kembali?” Katakanlah, “Yang telah menciptakan kamu pertama kali.” Lalu mereka akan menggeleng-gelengkan kepalanya kepadamu dan berkata, “Kapan (Kiamat) itu (akan terjadi)?” Katakanlah, “Barang kali waktunya sudah dekat,”'),
	(2081, 17, 52, 'يَوْمَ يَدْعُوْكُمْ فَتَسْتَجِيْبُوْنَ بِحَمْدِهٖ وَتَظُنُّوْنَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا ࣖ ', 'yawma yad\'uukum fatastajiibuuna bi<u>h</u>amdihi wata<u>zh</u>unnuuna in labitstum ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'yaitu pada hari (ketika) Dia memanggil kamu, dan kamu mematuhi-Nya sambil memuji-Nya dan kamu mengira, (rasanya) hanya sebentar saja kamu berdiam (di dalam kubur).'),
	(2082, 17, 53, 'وَقُلْ لِّعِبَادِيْ يَقُوْلُوا الَّتِيْ هِيَ اَحْسَنُۗ اِنَّ الشَّيْطٰنَ يَنْزَغُ بَيْنَهُمْۗ اِنَّ الشَّيْطٰنَ كَانَ لِلْاِنْسَانِ عَدُوًّا مُّبِيْنًا', 'waqul li\'ib<u>aa</u>dii yaquuluu <strong>al</strong>latii hiya a<u>h</u>sanu inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na yanzaghu baynahum inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na k<u>aa</u>na lil-ins<u>aa</u>ni \'aduwwan mubiin<u>aa</u><strong>n</strong>', 'Dan katakanlah kepada hamba-hamba-Ku, “Hendaklah mereka mengucapkan perkataan yang lebih baik (benar). Sungguh, setan itu (selalu) menimbulkan perselisihan di antara mereka. Sungguh, setan adalah musuh yang nyata bagi manusia.'),
	(2083, 17, 54, 'رَبُّكُمْ اَعْلَمُ بِكُمْ اِنْ يَّشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَّشَأْ يُعَذِّبْكُمْۗ وَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ وَكِيْلًا', 'rabbukum a\'lamu bikum in yasya/ yar<u>h</u>amkum aw in yasya/ yu\'a<u>dzdz</u>ibkum wam<u>aa</u> arsaln<u>aa</u>ka \'alayhim wakiil<u>aa</u><strong>n</strong>', 'Tuhanmu lebih mengetahui tentang kamu. Jika Dia menghendaki, niscaya Dia akan memberi rahmat kepadamu, dan jika Dia menghendaki, pasti Dia akan mengazabmu. Dan Kami tidaklah mengutusmu (Muhammad) untuk menjadi penjaga bagi mereka.'),
	(2084, 17, 55, 'وَرَبُّكَ اَعْلَمُ بِمَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّٖنَ عَلٰى بَعْضٍ وَّاٰتَيْنَا دَاوٗدَ زَبُوْرًا', 'warabbuka a\'lamu biman fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i walaqad fa<u>dhdh</u>aln<u>aa</u> ba\'<u>dh</u>a <strong>al</strong>nnabiyyiina \'al<u>aa</u> ba\'<u>dh</u>in wa<u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda zabuur<u>aa</u><strong>n</strong>', 'Dan Tuhanmu lebih mengetahui siapa yang di langit dan di bumi. Dan sungguh, Kami telah memberikan kelebihan kepada sebagian nabi-nabi atas sebagian (yang lain), dan Kami berikan Zabur kepada Dawud.'),
	(2085, 17, 56, 'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِهٖ فَلَا يَمْلِكُوْنَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْوِيْلًا', 'quli ud\'u <strong>al</strong>la<u>dz</u>iina za\'amtum min duunihi fal<u>aa</u> yamlikuuna kasyfa <strong>al</strong><u>dhdh</u>urri \'ankum wal<u>aa</u> ta<u>h</u>wiil<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Panggillah mereka yang kamu anggap (tuhan) selain Allah, mereka tidak kuasa untuk menghilangkan bahaya darimu dan tidak (pula) mampu mengubahnya.”'),
	(2086, 17, 57, 'اُولٰۤىِٕكَ الَّذِيْنَ يَدْعُوْنَ يَبْتَغُوْنَ اِلٰى رَبِّهِمُ الْوَسِيْلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُوْنَ رَحْمَتَهٗ وَيَخَافُوْنَ عَذَابَهٗۗ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوْرًا', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina yad\'uuna yabtaghuuna il<u>aa</u> rabbihimu <strong>a</strong>lwasiilata ayyuhum aqrabu wayarjuuna ra<u>h</u>matahu wayakh<u>aa</u>fuuna \'a<u>dzaa</u>bahu inna \'a<u>dzaa</u>ba rabbika k<u>aa</u>na ma<u>hts</u>uur<u>aa</u><strong>n</strong>', 'Orang-orang yang mereka seru itu, mereka sendiri mencari jalan kepada Tuhan siapa di antara mereka yang lebih dekat (kepada Allah). Mereka mengharapkan rahmat-Nya dan takut akan azab-Nya. Sungguh, azab Tuhanmu itu sesuatu yang (harus) ditakuti.”'),
	(2087, 17, 58, 'وَاِنْ مِّنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوْهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوْهَا عَذَابًا شَدِيْدًاۗ كَانَ ذٰلِكَ فىِ الْكِتٰبِ مَسْطُوْرًا', 'wa-in min qaryatin ill<u>aa</u> na<u>h</u>nu muhlikuuh<u>aa</u> qabla yawmi <strong>a</strong>lqiy<u>aa</u>mati aw mu\'a<u>dzdz</u>ibuuh<u>aa</u> \'a<u>dzaa</u>ban syadiidan k<u>aa</u>na <u>dzaa</u>lika fii <strong>a</strong>lkit<u>aa</u>bi mas<u>th</u>uur<u>aa</u><strong>n</strong>', 'Dan tidak ada suatu negeri pun (yang durhaka penduduknya), melainkan Kami membinasakannya sebelum hari Kiamat atau Kami siksa (penduduknya) dengan siksa yang sangat keras. Yang demikian itu telah tertulis di dalam Kitab (Lauh Mahfuzh).'),
	(2088, 17, 59, 'وَمَا مَنَعَنَآ اَنْ نُّرْسِلَ بِالْاٰيٰتِ اِلَّآ اَنْ كَذَّبَ بِهَا الْاَوَّلُوْنَۗ وَاٰتَيْنَا ثَمُوْدَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوْا بِهَاۗ وَمَا نُرْسِلُ بِالْاٰيٰتِ اِلَّا تَخْوِيْفًا', 'wam<u>aa</u> mana\'an<u>aa</u> an nursila bi<strong>a</strong>l-<u>aa</u>y<u>aa</u>ti ill<u>aa</u> an ka<u>dzdz</u>aba bih<u>aa</u> <strong>a</strong>l-awwaluuna wa<u>aa</u>tayn<u>aa</u> tsamuuda <strong>al</strong>nn<u>aa</u>qata mub<u>sh</u>iratan fa<u>zh</u>alamuu bih<u>aa</u> wam<u>aa</u> nursilu bi<strong>a</strong>l-<u>aa</u>y<u>aa</u>ti ill<u>aa</u> takhwiif<u>aa</u><strong>n</strong>', 'Dan tidak ada yang menghalangi Kami untuk mengirimkan (kepadamu) tanda-tanda (kekuasaan Kami), melainkan karena (tanda-tanda) itu telah didustakan oleh orang terdahulu. Dan telah Kami berikan kepada kaum samud unta betina (sebagai mukjizat) yang dapat dilihat, tetapi mereka menganiaya (unta betina itu). Dan Kami tidak mengirimkan tanda-tanda itu melainkan untuk menakut-nakuti.'),
	(2089, 17, 60, 'وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۗ وَمَا جَعَلْنَا الرُّءْيَا الَّتِيْٓ اَرَيْنٰكَ اِلَّا فِتْنَةً لِّلنَّاسِ وَالشَّجَرَةَ الْمَلْعُوْنَةَ فِى الْقُرْاٰنِ ۗ وَنُخَوِّفُهُمْۙ فَمَا يَزِيْدُهُمْ اِلَّا طُغْيَانًا كَبِيْرًا ࣖ ', 'wa-i<u>dz</u> quln<u>aa</u> laka inna rabbaka a<u>hath</u>a bi<strong>al</strong>nn<u>aa</u>si wam<u>aa</u> ja\'aln<u>aa</u> <strong>al</strong>rru/y<u>aa</u> <strong>al</strong>latii arayn<u>aa</u>ka ill<u>aa</u> fitnatan li<strong>l</strong>nn<u>aa</u>si wa<strong>al</strong>sysyajarata <strong>a</strong>lmal\'uunata fii <strong>a</strong>lqur-<u>aa</u>ni wanukhawwifuhum fam<u>aa</u> yaziiduhum ill<u>aa</u> <u>th</u>ughy<u>aa</u>nan kabiir<u>aa</u><strong>n</strong>', 'Dan (ingatlah) ketika Kami wahyukan kepadamu, “Sungguh, (ilmu) Tuhanmu meliputi seluruh manusia.” Dan Kami tidak menjadikan mimpi yang telah Kami perlihatkan kepadamu, melainkan sebagai ujian bagi manusia dan (begitu pula) pohon yang terkutuk (zaqqum) dalam Al-Qur\'an. Dan Kami menakut-nakuti mereka, tetapi yang demikian itu hanyalah menambah besar kedurhakaan mereka.'),
	(2090, 17, 61, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ طِيْنًاۚ ', 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa q<u>aa</u>la a-asjudu liman khalaqta <u>th</u>iin<u>aa</u><strong>n</strong>', 'Dan (ingatlah), ketika Kami berfirman kepada para malaikat, “Sujudlah kamu semua kepada Adam,” lalu mereka sujud, kecuali Iblis. Ia (Iblis) berkata, “Apakah aku harus bersujud kepada orang yang Engkau ciptakan dari tanah?”'),
	(2091, 17, 62, 'قَالَ اَرَاَيْتَكَ هٰذَا الَّذِيْ كَرَّمْتَ عَلَيَّ لَىِٕنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهٗٓ اِلَّا قَلِيْلًا', 'q<u>aa</u>la ara-aytaka h<u>aadzaa</u> <strong>al</strong>la<u>dz</u>ii karramta \'alayya la-in akhkhartani il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati la-a<u>h</u>tanikanna <u>dz</u>urriyyatahu ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', ' Ia (Iblis) berkata, “Terangkanlah kepadaku, inikah yang lebih Engkau muliakan daripada aku? Sekiranya Engkau memberi waktu kepadaku sampai hari Kiamat, pasti akan aku sesatkan keturunannya, kecuali sebagian kecil.”'),
	(2092, 17, 63, 'قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَاۤؤُكُمْ جَزَاۤءً مَّوْفُوْرًا', 'q<u>aa</u>la i<u>dz</u>hab faman tabi\'aka minhum fa-inna jahannama jaz<u>aa</u>ukum jaz<u>aa</u>-an mawfuur<u>aa</u><strong>n</strong>', 'Dia (Allah) berfirman, “Pergilah, tetapi barang siapa di antara mereka yang mengikuti kamu, maka sungguh, neraka Jahanamlah balasanmu semua, sebagai pembalasan yang cukup.'),
	(2093, 17, 64, 'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِى الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا', 'wa<strong>i</strong>stafziz mani ista<u>th</u>a\'ta minhum bi<u>sh</u>awtika wa-ajlib \'alayhim bikhaylika warajlika wasy<u>aa</u>rik-hum fii <strong>a</strong>l-amw<u>aa</u>li wa<strong>a</strong>l-awl<u>aa</u>di wa\'idhum wam<u>aa</u> ya\'iduhumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu ill<u>aa</u> ghuruur<u>aa</u><strong>n</strong>', 'Dan perdayakanlah siapa saja di antara mereka yang engkau (Iblis) sanggup dengan suaramu (yang memukau), kerahkanlah pasukanmu terhadap mereka, yang berkuda dan yang berjalan kaki, dan bersekutulah dengan mereka pada harta dan anak-anak lalu beri janjilah kepada mereka.” Padahal setan itu hanya menjanjikan tipuan belaka kepada mereka.'),
	(2094, 17, 65, 'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌۗ وَكَفٰى بِرَبِّكَ وَكِيْلًا', 'inna \'ib<u>aa</u>dii laysa laka \'alayhim sul<u>th</u><u>aa</u>nun wakaf<u>aa</u> birabbika wakiil<u>aa</u><strong>n</strong>', '“Sesungguhnya (terhadap) hamba-hamba-Ku, engkau (Iblis) tidaklah dapat berkuasa atas mereka. Dan cukuplah Tuhanmu sebagai penjaga.”'),
	(2095, 17, 66, 'رَبُّكُمُ الَّذِيْ يُزْجِيْ لَكُمُ الْفُلْكَ فِى الْبَحْرِ لِتَبْتَغُوْا مِنْ فَضْلِهٖۗ اِنَّهٗ كَانَ بِكُمْ رَحِيْمًا', 'rabbukumu <strong>al</strong>la<u>dz</u>ii yuzjii lakumu <strong>a</strong>lfulka fii <strong>a</strong>lba<u>h</u>ri litabtaghuu min fa<u>dh</u>lihi innahu k<u>aa</u>na bikum ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Tuhanmulah yang melayarkan kapal-kapal di lautan untukmu, agar kamu mencari karunia-Nya. Sungguh, Dia Maha Penyayang terhadapmu.'),
	(2096, 17, 67, 'وَاِذَا مَسَّكُمُ الضُّرُّ فِى الْبَحْرِ ضَلَّ مَنْ تَدْعُوْنَ اِلَّآ اِيَّاهُۚ فَلَمَّا نَجّٰىكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۗ وَكَانَ الْاِنْسَانُ كَفُوْرًا', 'wa-i<u>dzaa</u> massakumu <strong>al</strong><u>dhdh</u>urru fii <strong>a</strong>lba<u>h</u>ri <u>dh</u>alla man tad\'uuna ill<u>aa</u> iyy<u>aa</u>hu falamm<u>aa</u> najj<u>aa</u>kum il<u>aa</u> <strong>a</strong>lbarri a\'ra<u>dh</u>tum wak<u>aa</u>na <strong>a</strong>l-ins<u>aa</u>nu kafuur<u>aa</u><strong>n</strong>', 'Dan apabila kamu ditimpa bahaya di lautan, niscaya hilang semua yang (bi-asa) kamu seru, kecuali Dia. Tetapi ketika Dia menyelamatkan kamu ke daratan, kamu berpaling (dari-Nya). Dan manusia memang selalu ingkar (tidak bersyukur).'),
	(2097, 17, 68, 'اَفَاَمِنْتُمْ اَنْ يَّخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوْا لَكُمْ وَكِيْلًا ۙ ', 'afa-amintum an yakhsifa bikum j<u>aa</u>niba <strong>a</strong>lbarri aw yursila \'alaykum <u>has</u>iban tsumma l<u>aa</u> tajiduu lakum wakiil<u>aa</u><strong>n</strong>', 'Maka apakah kamu merasa aman bahwa Dia tidak akan membenamkan sebagian daratan bersama kamu atau Dia meniupkan (angin keras yang membawa) batu-batu kecil? Dan kamu tidak akan mendapat seorang pelindung pun,'),
	(2098, 17, 69, 'اَمْ اَمِنْتُمْ اَنْ يُّعِيْدَكُمْ فِيْهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِّنَ الرِّيْحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوْا لَكُمْ عَلَيْنَا بِهٖ تَبِيْعًا', 'am amintum an yu\'iidakum fiihi t<u>aa</u>ratan ukhr<u>aa</u> fayursila \'alaykum q<u>aas</u>ifan mina <strong>al</strong>rrii<u>h</u>i fayughriqakum bim<u>aa</u> kafartum tsumma l<u>aa</u> tajiduu lakum \'alayn<u>aa</u> bihi tabii\'<u>aa</u><strong>n</strong>', 'ataukah kamu merasa aman bahwa Dia tidak akan mengembalikan kamu ke laut sekali lagi, lalu Dia tiupkan angin topan kepada kamu dan ditenggelamkan-Nya kamu disebabkan kekafiranmu? Kemudian kamu tidak akan mendapatkan seorang penolong pun dalam menghadapi (siksaan) Kami.  '),
	(2099, 17, 70, '۞ وَلَقَدْ كَرَّمْنَا بَنِيْٓ اٰدَمَ وَحَمَلْنٰهُمْ فِى الْبَرِّ وَالْبَحْرِ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلٰى كَثِيْرٍ مِّمَّنْ خَلَقْنَا تَفْضِيْلًا ࣖ ', 'walaqad karramn<u>aa</u> banii <u>aa</u>dama wa<u>h</u>amaln<u>aa</u>hum fii <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri warazaqn<u>aa</u>hum mina <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti wafa<u>dhdh</u>aln<u>aa</u>hum \'al<u>aa</u> katsiirin mimman khalaqn<u>aa</u> taf<u>dh</u>iil<u>aa</u><strong>n</strong>', 'Dan sungguh, Kami telah memuliakan anak cucu Adam, dan Kami angkut mereka di darat dan di laut, dan Kami beri mereka rezeki dari yang baik-baik dan Kami lebihkan mereka di atas banyak makhluk yang Kami ciptakan dengan kelebihan yang sempurna.'),
	(2100, 17, 71, 'يَوْمَ نَدْعُوْا كُلَّ اُنَاسٍۢ بِاِمَامِهِمْۚ فَمَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَاُولٰۤىِٕكَ يَقْرَءُوْنَ كِتٰبَهُمْ وَلَا يُظْلَمُوْنَ فَتِيْلًا', 'yawma nad\'uu kulla un<u>aa</u>sin bi-im<u>aa</u>mihim faman uutiya kit<u>aa</u>bahu biyamiinihi faul<u>aa</u>-ika yaqrauuna kit<u>aa</u>bahum wal<u>aa</u> yu<u>zh</u>lamuuna fatiil<u>aa</u><strong>n</strong>', '(Ingatlah), pada hari (ketika) Kami panggil setiap umat dengan pemimpinnya; dan barang siapa diberikan catatan amalnya di tangan kanannya mereka akan membaca catatannya (dengan baik), dan mereka tidak akan dirugikan sedikit pun.'),
	(2101, 17, 72, 'وَمَنْ كَانَ فِيْ هٰذِهٖٓ اَعْمٰى فَهُوَ فِى الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَبِيْلًا', 'waman k<u>aa</u>na fii h<u>aadz</u>ihi a\'m<u>aa</u> fahuwa fii <strong>a</strong>l-<u>aa</u>khirati a\'m<u>aa</u> wa-a<u>dh</u>allu sabiil<u>aa</u><strong>n</strong>', 'Dan barang siapa buta (hatinya) di dunia ini, maka di akhirat dia akan buta dan tersesat jauh dari jalan (yang benar).'),
	(2102, 17, 73, 'وَاِنْ كَادُوْا لَيَفْتِنُوْنَكَ عَنِ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهٗۖ وَاِذًا لَّاتَّخَذُوْكَ خَلِيْلًا', 'wa-in k<u>aa</u>duu layaftinuunaka \'ani <strong>al</strong>la<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka litaftariya \'alayn<u>aa</u> ghayrahu wa-i<u>dz</u>an la<strong>i</strong>ttakha<u>dz</u>uuka khaliil<u>aa</u><strong>n</strong>', 'Dan mereka hampir memalingkan engkau (Muhammad) dari apa yang telah Kami wahyukan kepadamu, agar engkau mengada-ada yang lain terhadap Kami; dan jika demikian tentu mereka menjadikan engkau sahabat yang setia.'),
	(2103, 17, 74, 'وَلَوْلَآ اَنْ ثَبَّتْنٰكَ لَقَدْ كِدْتَّ تَرْكَنُ اِلَيْهِمْ شَيْـًٔا قَلِيْلًا ۙ  ', 'walawl<u>aa</u> an tsabbatn<u>aa</u>ka laqad kidta tarkanu ilayhim syay-an qaliil<u>aa</u><strong>n</strong>', 'Dan sekiranya Kami tidak memperteguh (hati)mu, niscaya engkau hampir saja condong sedikit kepada mereka.'),
	(2104, 17, 75, 'اِذًا لَّاَذَقْنٰكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيْرًا', 'i<u>dz</u>an la-a<u>dz</u>aqn<u>aa</u>ka <u>dh</u>i\'fa <strong>a</strong>l<u>h</u>ay<u>aa</u>ti wa<u>dh</u>i\'fa <strong>a</strong>lmam<u>aa</u>ti tsumma l<u>aa</u> tajidu laka \'alayn<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Jika demikian, tentu akan Kami rasakan kepadamu (siksaan) dua kali lipat di dunia ini dan dua kali lipat setelah mati, dan engkau (Muhammad) tidak akan mendapat seorang penolong pun terhadap Kami.'),
	(2105, 17, 76, 'وَاِنْ كَادُوْا لَيَسْتَفِزُّوْنَكَ مِنَ الْاَرْضِ لِيُخْرِجُوْكَ مِنْهَا وَاِذًا لَّا يَلْبَثُوْنَ خِلٰفَكَ اِلَّا قَلِيْلًا', 'wa-in k<u>aa</u>duu layastafizzuunaka mina <strong>a</strong>l-ar<u>dh</u>i liyukhrijuuka minh<u>aa</u> wa-i<u>dz</u>an l<u>aa</u> yalbatsuuna khil<u>aa</u>faka ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Dan sungguh, mereka hampir membuatmu (Muhammad) gelisah di negeri (Mekah) karena engkau harus keluar dari negeri itu, dan kalau terjadi demikian, niscaya sepeninggalmu mereka tidak akan tinggal (di sana), melainkan sebentar saja.'),
	(2106, 17, 77, 'سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُّسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيْلًا ࣖ ', 'sunnata man qad arsaln<u>aa</u> qablaka min rusulin<u>aa</u> wal<u>aa</u> tajidu lisunnatin<u>aa</u> ta<u>h</u>wiil<u>aa</u><strong>n</strong>', '(Yang demikian itu) merupakan ketetapan bagi para rasul Kami yang Kami utus sebelum engkau, dan tidak akan engkau dapati perubahan atas ketetapan Kami.'),
	(2107, 17, 78, 'اَقِمِ الصَّلٰوةَ لِدُلُوْكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۗ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوْدًا ', 'aqimi <strong>al</strong><u>shsh</u>al<u>aa</u>ta liduluuki <strong>al</strong>sysyamsi il<u>aa</u> ghasaqi <strong>al</strong>layli waqur-<u>aa</u>na <strong>a</strong>lfajri inna qur-<u>aa</u>na <strong>a</strong>lfajri k<u>aa</u>na masyhuud<u>aa</u><strong>n</strong>', 'Laksanakanlah salat sejak matahari tergelincir sampai gelapnya malam dan (laksanakan pula salat) Subuh. Sungguh, salat subuh itu disaksikan (oleh malaikat).'),
	(2108, 17, 79, 'وَمِنَ الَّيْلِ فَتَهَجَّدْ بِهٖ نَافِلَةً لَّكَۖ عَسٰٓى اَنْ يَّبْعَثَكَ رَبُّكَ مَقَامًا مَّحْمُوْدًا', 'wamina <strong>al</strong>layli fatahajjad bihi n<u>aa</u>filatan laka \'as<u>aa</u> an yab\'atsaka rabbuka maq<u>aa</u>man ma<u>h</u>muud<u>aa</u><strong>n</strong>', 'Dan pada sebagian malam, lakukanlah salat tahajud (sebagai suatu ibadah) tambahan bagimu: mudah-mudahan Tuhanmu mengangkatmu ke tempat yang terpuji.'),
	(2109, 17, 80, 'وَقُلْ رَّبِّ اَدْخِلْنِيْ مُدْخَلَ صِدْقٍ وَّاَخْرِجْنِيْ مُخْرَجَ صِدْقٍ وَّاجْعَلْ لِّيْ مِنْ لَّدُنْكَ سُلْطٰنًا نَّصِيْرًا', 'waqul rabbi adkhilnii mudkhala <u>sh</u>idqin wa-akhrijnii mukhraja <u>sh</u>idqin wa<strong>i</strong>j\'al lii min ladunka sul<u>th</u><u>aa</u>nan na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan katakanlah (Muhammad), ya Tuhanku, masukkan aku ke tempat masuk yang benar dan keluarkan (pula) aku ke tempat keluar yang benar dan berikanlah kepadaku dari sisi-Mu kekuasaan yang dapat menolong(ku).'),
	(2110, 17, 81, 'وَقُلْ جَاۤءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ  ۖاِنَّ الْبَاطِلَ كَانَ زَهُوْقًا', 'waqul rabbi adkhilnii mudkhala <u>sh</u>idqin wa-akhrijnii mukhraja <u>sh</u>idqin wa<strong>i</strong>j\'al lii min ladunka sul<u>th</u><u>aa</u>nan na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan katakanlah, “Kebenaran telah datang dan yang batil telah lenyap.” Sungguh, yang batil itu pasti lenyap.'),
	(2111, 17, 82, 'وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَاۤءٌ وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَۙ وَلَا يَزِيْدُ الظّٰلِمِيْنَ اِلَّا خَسَارًا', 'wanunazzilu mina <strong>a</strong>lqur-<u>aa</u>ni m<u>aa</u> huwa syif<u>aa</u>un wara<u>h</u>matun lilmu/miniina wal<u>aa</u> yaziidu <strong>al</strong><u>zhzhaa</u>limiina ill<u>aa</u> khas<u>aa</u>r<u>aa</u><strong>n</strong>', 'Dan Kami turunkan dari Al-Qur\'an (sesuatu) yang menjadi penawar dan rahmat bagi orang yang beriman, sedangkan bagi orang yang zalim (Al-Qur\'an itu) hanya akan menambah kerugian.'),
	(2112, 17, 83, 'وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَـُٔوْسًا', 'wa-i<u>dzaa</u> an\'amn<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-ins<u>aa</u>ni a\'ra<u>dh</u>a wana<u>aa</u> bij<u>aa</u>nibihi wa-i<u>dzaa</u> massahu <strong>al</strong>sysyarru k<u>aa</u>na yauus<u>aa</u><strong>n</strong>', 'Dan apabila Kami berikan kesenangan kepada manusia, niscaya dia berpaling dan menjauhkan diri dengan sombong; dan apabila dia ditimpa kesusahan, niscaya dia berputus asa.'),
	(2113, 17, 84, 'قُلْ كُلٌّ يَّعْمَلُ عَلٰى شَاكِلَتِهٖۗ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَبِيْلًا ࣖ', 'qul kullun ya\'malu \'al<u>aa</u> sy<u>aa</u>kilatihi farabbukum a\'lamu biman huwa ahd<u>aa</u> sabiil<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Setiap orang berbuat sesuai dengan pembawaannya masing-masing.” Maka Tuhanmu lebih mengetahui siapa yang lebih benar jalannya.'),
	(2114, 17, 85, 'وَيَسْـَٔلُوْنَكَ عَنِ الرُّوْحِۗ قُلِ الرُّوْحُ مِنْ اَمْرِ رَبِّيْ وَمَآ اُوْتِيْتُمْ مِّنَ الْعِلْمِ اِلَّا قَلِيْلًا ', 'wayas-aluunaka \'ani <strong>al</strong>rruu<u>h</u>i quli <strong>al</strong>rruu<u>h</u>u min amri rabbii wam<u>aa</u> uutiitum mina <strong>a</strong>l\'ilmi ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Dan mereka bertanya kepadamu (Muhammad) tentang ruh. Katakanlah, “Ruh itu termasuk urusan Tuhanku, sedangkan kamu diberi pengetahuan hanya sedikit.”'),
	(2115, 17, 86, 'وَلَىِٕنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهٖ عَلَيْنَا وَكِيْلًاۙ؉؉', 'wala-in syi/n<u>aa</u> lana<u>dz</u>habanna bi<strong>a</strong>lla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka tsumma l<u>aa</u> tajidu laka bihi \'alayn<u>aa</u> wakiil<u>aa</u><strong>n</strong>', 'Dan sesungguhnya jika Kami menghendaki, niscaya Kami lenyapkan apa yang telah Kami wahyukan kepadamu (Muhammad), dan engkau tidak akan mendapatkan seorang pembela pun terhadap Kami,'),
	(2116, 17, 87, 'اِلَّا رَحْمَةً مِّنْ رَّبِّكَۗ اِنَّ فَضْلَهٗ كَانَ عَلَيْكَ كَبِيْرًا', 'ill<u>aa</u> ra<u>h</u>matan min rabbika inna fa<u>dh</u>lahu k<u>aa</u>na \'alayka kabiir<u>aa</u><strong>n</strong>', 'kecuali karena rahmat dari Tuhanmu. Sungguh, karunia-Nya atasmu (Muhammad) sangat besar. '),
	(2117, 17, 88, 'قُلْ لَّىِٕنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَّأْتُوْا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُوْنَ بِمِثْلِهٖ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيْرًا', 'qul la-ini ijtama\'ati <strong>a</strong>l-insu wa<strong>a</strong>ljinnu \'al<u>aa</u> an ya/tuu bimitsli h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni l<u>aa</u> ya/tuuna bimitslihi walaw k<u>aa</u>na ba\'<u>dh</u>uhum liba\'<u>dh</u>in <u>zh</u>ahiir<u>aa</u><strong>n</strong>', 'Katakanlah, “Sesungguhnya jika manusia dan jin berkumpul untuk membuat yang serupa (dengan) Al-Qur\'an ini, mereka tidak akan dapat membuat yang serupa dengannya, sekalipun mereka saling membantu satu sama lain.”'),
	(2118, 17, 89, 'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا', 'walaqad <u>sh</u>arrafn<u>aa</u> li<strong>l</strong>nn<u>aa</u>si fii h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni min kulli matsalin fa-ab<u>aa</u> aktsaru <strong>al</strong>nn<u>aa</u>si ill<u>aa</u> kufuur<u>aa</u><strong>n</strong>', ' Dan sungguh, Kami telah menjelaskan berulang-ulang kepada manusia dalam Al-Qur\'an ini dengan bermacam-macam perumpamaan, tetapi kebanyakan manusia tidak menyukainya bahkan mengingkari(nya).'),
	(2119, 17, 90, 'وَقَالُوْا لَنْ نُّؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْۢبُوْعًاۙ  ', 'waq<u>aa</u>luu lan nu/mina laka <u>h</u>att<u>aa</u> tafjura lan<u>aa</u> mina <strong>a</strong>l-ar<u>dh</u>i yanbuu\'<u>aa</u><strong>n</strong>', 'Dan mereka berkata, “Kami tidak akan percaya kepadamu (Muhammad) sebelum engkau memancarkan mata air dari bumi untuk kami,'),
	(2120, 17, 91, 'اَوْ تَكُوْنَ لَكَ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّعِنَبٍ فَتُفَجِّرَ الْاَنْهٰرَ خِلٰلَهَا تَفْجِيْرًاۙ  ', 'aw takuuna laka jannatun min nakhiilin wa\'inabin fatufajjira <strong>a</strong>l-anh<u>aa</u>ra khil<u>aa</u>lah<u>aa</u> tafjiir<u>aa</u><strong>n</strong>', 'atau engkau mempunyai sebuah kebun kurma dan anggur, lalu engkau alirkan di celah-celahnya sungai yang deras alirannya,'),
	(2121, 17, 92, 'اَوْ تُسْقِطَ السَّمَاۤءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰۤىِٕكَةِ قَبِيْلًاۙ  ', 'aw tusqi<u>th</u>a <strong>al</strong>ssam<u>aa</u>-a kam<u>aa</u> za\'amta \'alayn<u>aa</u> kisafan aw ta/tiya bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmal<u>aa</u>-ikati qabiil<u>aa</u><strong>n</strong>', 'atau engkau jatuhkan langit berkeping-keping atas kami, sebagaimana engkau katakan, atau (sebelum) engkau datangkan Allah dan para malaikat berhadapan muka dengan kami,'),
	(2122, 17, 93, 'اَوْ يَكُوْنَ لَكَ بَيْتٌ مِّنْ زُخْرُفٍ اَوْ تَرْقٰى فِى السَّمَاۤءِ ۗوَلَنْ نُّؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتٰبًا نَّقْرَؤُهٗۗ قُلْ سُبْحَانَ رَبِّيْ هَلْ كُنْتُ اِلَّا بَشَرًا رَّسُوْلًا ࣖ  ', 'aw yakuuna laka baytun min zukhrufin aw tarq<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>-i walan nu/mina liruqiyyika <u>h</u>att<u>aa</u> tunazzila \'alayn<u>aa</u> kit<u>aa</u>ban naqrauhu qul sub<u>haa</u>na rabbii hal kuntu ill<u>aa</u> basyaran rasuul<u>aa</u><strong>n</strong>', 'atau engkau mempunyai sebuah rumah (terbuat) dari emas, atau engkau naik ke langit. Dan kami tidak akan mempercayai kenaikanmu itu sebelum engkau turunkan kepada kami sebuah kitab untuk kami baca.” Katakanlah (Muhammad), “Mahasuci Tuhanku, bukankah aku ini hanya seorang manusia yang menjadi rasul?”'),
	(2123, 17, 94, 'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰٓى اِلَّآ اَنْ قَالُوْٓا اَبَعَثَ اللّٰهُ بَشَرًا رَّسُوْلًا', 'wam<u>aa</u> mana\'a <strong>al</strong>nn<u>aa</u>sa an yu/minuu i<u>dz</u> j<u>aa</u>-ahumu <strong>a</strong>lhud<u>aa</u> ill<u>aa</u> an q<u>aa</u>luu aba\'atsa <strong>al</strong>l<u>aa</u>hu basyaran rasuul<u>aa</u><strong>n</strong>', 'Dan tidak ada sesuatu yang menghalangi manusia untuk beriman ketika petunjuk datang kepadanya, selain perkataan mereka, “Mengapa Allah mengutus seorang manusia menjadi rasul?”'),
	(2124, 17, 95, 'قُلْ لَّوْ كَانَ فِى الْاَرْضِ مَلٰۤىِٕكَةٌ يَّمْشُوْنَ مُطْمَىِٕنِّيْنَ لَنَزَّلْنَا عَلَيْهِمْ مِّنَ السَّمَاۤءِ مَلَكًا رَّسُوْلًا', 'qul law k<u>aa</u>na fii <strong>a</strong>l-ar<u>dh</u>i mal<u>aa</u>-ikatun yamsyuuna mu<u>th</u>ma-inniina lanazzaln<u>aa</u> \'alayhim mina <strong>al</strong>ssam<u>aa</u>-i malakan rasuul<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Sekiranya di bumi ada para malaikat, yang berjalan-jalan dengan tenang, niscaya Kami turunkan kepada mereka malaikat dari langit untuk menjadi rasul.”'),
	(2125, 17, 96, 'قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۗ اِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا', 'qul kaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi syahiidan baynii wabaynakum innahu k<u>aa</u>na bi\'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Cukuplah Allah menjadi saksi antara aku dan kamu sekalian. Sungguh, Dia Maha Mengetahui, Maha Melihat akan hamba-hamba-Nya.”'),
	(2126, 17, 97, 'وَمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَاۤءَ مِنْ دُوْنِهٖۗ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوْهِهِمْ عُمْيًا وَّبُكْمًا وَّصُمًّاۗ مَأْوٰىهُمْ جَهَنَّمُۗ  كُلَّمَا خَبَتْ زِدْنٰهُمْ سَعِيْرًا', 'waman yahdi <strong>al</strong>l<u>aa</u>hu fahuwa <strong>a</strong>lmuhtadi waman yu<u>dh</u>lil falan tajida lahum awliy<u>aa</u>-a min duunihi wana<u>h</u>syuruhum yawma <strong>a</strong>lqiy<u>aa</u>mati \'al<u>aa</u> wujuuhihim \'umyan wabukman wa<u>sh</u>umman ma/w<u>aa</u>hum jahannamu kullam<u>aa</u> khabat zidn<u>aa</u>hum sa\'iir<u>aa</u><strong>n</strong>', 'Dan barang siapa diberi petunjuk oleh Allah, dialah yang mendapat petunjuk, dan barang siapa Dia sesatkan, maka engkau tidak akan mendapatkan penolong-penolong bagi mereka selain Dia. Dan Kami akan mengumpulkan mereka pada hari Kiamat dengan wajah tersungkur, dalam keadaan buta, bisu, dan tuli. Tempat kediaman mereka adalah neraka Jahanam. Setiap kali nyala api Jahanam itu akan padam, Kami tambah lagi nyalanya bagi mereka.'),
	(2127, 17, 98, 'ذٰلِكَ جَزَاۤؤُهُمْ بِاَنَّهُمْ كَفَرُوْا بِاٰيٰتِنَا وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ', '<u>dzaa</u>lika jaz<u>aa</u>uhum bi-annahum kafaruu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waq<u>aa</u>luu a-i<u>dzaa</u> kunn<u>aa</u> \'i<u>zhaa</u>man waruf<u>aa</u>tan a-inn<u>aa</u> lamab\'uutsuuna khalqan jadiid<u>aa</u><strong>n</strong>', 'Itulah balasan bagi mereka, karena sesungguhnya mereka kafir kepada ayat-ayat Kami dan (karena mereka) berkata, “Apabila kami telah menjadi tulang belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk baru?”'),
	(2128, 17, 99, '۞ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلًا لَّا رَيْبَ فِيْهِۗ فَاَبَى الظّٰلِمُوْنَ اِلَّا كُفُوْرًا', 'awa lam yaraw anna <strong>al</strong>l<u>aa</u>ha <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a q<u>aa</u>dirun \'al<u>aa</u> an yakhluqa mitslahum waja\'ala lahum ajalan l<u>aa</u> rayba fiihi fa-ab<u>aa</u> <strong>al</strong><u>zhzhaa</u>limuuna ill<u>aa</u> kufuur<u>aa</u><strong>n</strong>', 'Dan apakah mereka tidak memperhatikan bahwa Allah yang menciptakan langit dan bumi adalah Mahakuasa (pula) menciptakan yang serupa dengan mereka, dan Dia telah menetapkan waktu tertentu (mati atau dibangkitkan) bagi mereka, yang tidak diragukan lagi? Maka orang zalim itu tidak menolaknya kecuali dengan kekafiran.'),
	(2129, 17, 100, 'قُلْ لَّوْ اَنْتُمْ تَمْلِكُوْنَ خَزَاۤىِٕنَ رَحْمَةِ رَبِّيْٓ اِذًا لَّاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۗ وَكَانَ الْاِنْسَانُ قَتُوْرًا ࣖ', 'qul law antum tamlikuuna khaz<u>aa</u>-ina ra<u>h</u>mati rabbii i<u>dz</u>an la-amsaktum khasyyata <strong>a</strong>l-inf<u>aa</u>qi wak<u>aa</u>na <strong>a</strong>l-ins<u>aa</u>nu qatuur<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Sekiranya kamu menguasai perbendaharaan rahmat Tuhanku, niscaya (perbendaharaan) itu kamu tahan, karena takut membelanjakannya.” Dan manusia itu memang sangat kikir.'),
	(2130, 17, 101, 'وَلَقَدْ اٰتَيْنَا مُوْسٰى تِسْعَ اٰيٰتٍۢ بَيِّنٰتٍ فَسْـَٔلْ بَنِيْٓ اِسْرَاۤءِيْلَ اِذْ جَاۤءَهُمْ فَقَالَ لَهٗ فِرْعَوْنُ اِنِّيْ لَاَظُنُّكَ يٰمُوْسٰى مَسْحُوْرًا ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> tis\'a <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin fa<strong>i</strong>s-al banii isr<u>aa</u>-iila i<u>dz</u> j<u>aa</u>-ahum faq<u>aa</u>la lahu fir\'awnu innii la-a<u>zh</u>unnuka y<u>aa</u> muus<u>aa</u> mas<u>h</u>uur<u>aa</u><strong>n</strong>', 'Dan sungguh, Kami telah memberikan kepada Musa sembilan mukjizat yang nyata maka tanyakanlah kepada Bani Israil, ketika Musa datang kepada mereka lalu Fir‘aun berkata kepadanya, “Wahai Musa! Sesungguhnya aku benar-benar menduga engkau terkena sihir.”'),
	(2131, 17, 102, 'قَالَ لَقَدْ عَلِمْتَ مَآ اَنْزَلَ هٰٓؤُلَاۤءِ اِلَّا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ بَصَاۤىِٕرَۚ وَاِنِّيْ لَاَظُنُّكَ يٰفِرْعَوْنُ مَثْبُوْرًا ', 'q<u>aa</u>la laqad \'alimta m<u>aa</u> anzala h<u>aa</u>ul<u>aa</u>-i ill<u>aa</u> rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i ba<u>shaa</u>-ira wa-innii la-a<u>zh</u>unnuka y<u>aa</u> fir\'awnu matsbuur<u>aa</u><strong>n</strong>', 'Dia (Musa) menjawab, ”Sungguh, engkau telah mengetahui, bahwa tidak ada yang menurunkan (mukjizat-mukjizat) itu kecuali Tuhan (yang memelihara) langit dan bumi sebagai bukti-bukti yang nyata; dan sungguh, aku benar-benar menduga engkau akan binasa, wahai Fir‘aun.”'),
	(2132, 17, 103, 'فَاَرَادَ اَنْ يَّسْتَفِزَّهُمْ مِّنَ الْاَرْضِ فَاَغْرَقْنٰهُ وَمَنْ مَّعَهٗ جَمِيْعًاۙ ', 'fa-ar<u>aa</u>da an yastafizzahum mina <strong>a</strong>l-ar<u>dh</u>i fa-aghraqn<u>aa</u>hu waman ma\'ahu jamii\'<u>aa</u><strong>n</strong>', 'Kemudian dia (Fir‘aun) hendak mengusir mereka (Musa dan pengikutnya) dari bumi (Mesir), maka Kami tenggelamkan dia (Fir‘aun) beserta orang yang bersama dia seluruhnya.'),
	(2133, 17, 104, 'وَّقُلْنَا مِنْۢ بَعْدِهٖ لِبَنِيْٓ اِسْرَاۤءِيْلَ اسْكُنُوا الْاَرْضَ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَفِيْفًاۗ   ', 'waquln<u>aa</u> min ba\'dihi libanii isr<u>aa</u>-iila uskunuu <strong>a</strong>l-ar<u>dh</u>a fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du <strong>a</strong>l-<u>aa</u>khirati ji/n<u>aa</u> bikum lafiif<u>aa</u><strong>n</strong>', 'Dan setelah itu Kami berfirman kepada Bani Israil, “Tinggallah di negeri ini, tetapi apabila masa berbangkit datang, niscaya Kami kumpulkan kamu dalam keadaan bercampur baur.”'),
	(2134, 17, 105, 'وَبِالْحَقِّ اَنْزَلْنٰهُ وَبِالْحَقِّ نَزَلَۗ وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًاۘ', 'wabi<strong>a</strong>l<u>h</u>aqqi anzaln<u>aa</u>hu wabi<strong>a</strong>l<u>h</u>aqqi nazala wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> mubasysyiran wana<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Dan Kami turunkan (Al-Qur\'an) itu dengan sebenarnya dan (Al-Qur\'an) itu turun dengan (membawa) kebenaran. Dan Kami mengutus engkau (Muhammad), hanya sebagai pembawa berita gembira dan pemberi peringatan.'),
	(2135, 17, 106, 'وَقُرْاٰنًا فَرَقْنٰهُ لِتَقْرَاَهٗ عَلَى النَّاسِ عَلٰى مُكْثٍ وَّنَزَّلْنٰهُ تَنْزِيْلًا ', 'waqur-<u>aa</u>nan faraqn<u>aa</u>hu litaqra-ahu \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si \'al<u>aa</u> muktsin wanazzaln<u>aa</u>hu tanziil<u>aa</u><strong>n</strong>', 'Dan Al-Qur\'an (Kami turunkan) berangsur-angsur agar engkau (Muhammad) membacakannya kepada manusia perlahan-lahan dan Kami menurunkannya secara bertahap.'),
	(2136, 17, 107, 'قُلْ اٰمِنُوْا بِهٖٓ اَوْ لَا تُؤْمِنُوْاۗ اِنَّ الَّذِيْنَ اُوْتُوا الْعِلْمَ مِنْ قَبْلِهٖٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّوْنَ لِلْاَذْقَانِ سُجَّدًاۙ', 'qul <u>aa</u>minuu bihi aw l<u>aa</u> tu/minuu inna <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma min qablihi i<u>dzaa</u> yutl<u>aa</u> \'alayhim yakhirruuna lil-a<u>dz</u>q<u>aa</u>ni sujjad<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Berimanlah kamu kepadanya (Al-Qur\'an) atau tidak usah beriman (sama saja bagi Allah). Sesungguhnya orang yang telah diberi pengetahuan sebelumnya, apabila (Al-Qur\'an) dibacakan kepada mereka, mereka menyungkurkan wajah, bersujud,”'),
	(2137, 17, 108, 'وَّيَقُوْلُوْنَ سُبْحٰنَ رَبِّنَآ اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُوْلًا', 'wayaquuluuna sub<u>haa</u>na rabbin<u>aa</u> in k<u>aa</u>na wa\'du rabbin<u>aa</u> lamaf\'uul<u>aa</u><strong>n</strong>', 'dan mereka berkata, “Mahasuci Tuhan kami; sungguh, janji Tuhan kami pasti dipenuhi.”  '),
	(2138, 17, 109, 'وَيَخِرُّوْنَ لِلْاَذْقَانِ يَبْكُوْنَ وَيَزِيْدُهُمْ خُشُوْعًا ۩', 'wayakhirruuna lil-a<u>dz</u>q<u>aa</u>ni yabkuuna wayaziiduhum khusyuu\'<u>aa</u><strong>n</strong>', 'Dan mereka menyungkurkan wajah sambil menangis dan mereka bertambah khusyuk.'),
	(2139, 17, 110, 'قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۗ اَيًّا مَّا تَدْعُوْا فَلَهُ الْاَسْمَاۤءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَبِيْلًا', 'quli ud\'uu <strong>al</strong>l<u>aa</u>ha awi ud\'uu <strong>al</strong>rra<u>h</u>m<u>aa</u>na ayyan m<u>aa</u> tad\'uu falahu <strong>a</strong>l-asm<u>aa</u>u <strong>a</strong>l<u>h</u>usn<u>aa</u> wal<u>aa</u> tajhar bi<u>sh</u>al<u>aa</u>tika wal<u>aa</u> tukh<u>aa</u>fit bih<u>aa</u> wa<strong>i</strong>btaghi bayna <u>dzaa</u>lika sabiil<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Serulah Allah atau serulah Ar-Rahman. Dengan nama yang mana saja kamu dapat menyeru, karena Dia mempunyai nama-nama yang terbaik (Asma‘ul husna) dan janganlah engkau mengeraskan suaramu dalam salat dan janganlah (pula) merendahkannya dan usahakan jalan tengah di antara kedua itu.”'),
	(2140, 17, 111, 'وَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ لَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَلَمْ يَكُنْ لَّهٗ وَلِيٌّ مِّنَ الذُّلِّ وَكَبِّرْهُ تَكْبِيْرًا ࣖ', 'waquli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii lam yattakhi<u>dz</u> waladan walam yakun lahu syariikun fii <strong>a</strong>lmulki walam yakun lahu waliyyun mina <strong>al</strong><u>dzdz</u>ulli wakabbirhu takbiir<u>aa</u><strong>n</strong>', 'Dan katakanlah, “Segala puji bagi Allah yang tidak mempunyai anak dan tidak (pula) mempunyai sekutu dalam kerajaan-Nya dan Dia tidak memerlukan penolong dari kehinaan dan agungkanlah Dia seagung-agungnya.'),
	(2141, 18, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْٓ اَنْزَلَ عَلٰى عَبْدِهِ الْكِتٰبَ وَلَمْ يَجْعَلْ لَّهٗ عِوَجًا ۜ', 'al<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii anzala \'al<u>aa</u> \'abdihi <strong>a</strong>lkit<u>aa</u>ba walam yaj\'al lahu \'iwaj<u>aa</u><strong>n</strong>', 'Segala puji bagi Allah yang telah menurunkan Kitab (Al-Qur\'an) kepada hamba-Nya dan Dia tidak menjadikannya bengkok;'),
	(2142, 18, 2, 'قَيِّمًا لِّيُنْذِرَ بَأْسًا شَدِيْدًا مِّنْ لَّدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا حَسَنًاۙ ', 'qayyiman liyun<u>dz</u>ira ba/san syadiidan min ladunhu wayubasysyira <strong>a</strong>lmu/miniina <strong>al</strong>la<u>dz</u>iina ya\'maluuna <strong>al</strong><u>shshaa</u>li<u>haa</u>ti anna lahum ajran <u>h</u>asan<u>aa</u><strong>n</strong>', 'sebagai bimbingan yang lurus, untuk memperingatkan akan siksa yang sangat pedih dari sisi-Nya dan memberikan kabar gembira kepada orang-orang mukmin yang mengerjakan kebajikan bahwa mereka akan mendapat balasan yang baik,'),
	(2143, 18, 3, 'مَّاكِثِيْنَ فِيْهِ اَبَدًاۙ ', 'm<u>aa</u>kitsiina fiihi abad<u>aa</u><strong>n</strong>', 'mereka kekal di dalamnya untuk selama-lamanya.'),
	(2144, 18, 4, 'وَّيُنْذِرَ الَّذِيْنَ قَالُوا اتَّخَذَ اللّٰهُ وَلَدًاۖ  ', 'wayun<u>dz</u>ira <strong>al</strong>la<u>dz</u>iina q<u>aa</u>luu ittakha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu walad<u>aa</u><strong>n</strong>', 'Dan untuk memperingatkan kepada orang yang berkata, “Allah mengambil seorang anak.”'),
	(2145, 18, 5, 'مَّا لَهُمْ بِهٖ مِنْ عِلْمٍ وَّلَا لِاٰبَاۤىِٕهِمْۗ  كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۗ اِنْ يَّقُوْلُوْنَ اِلَّا كَذِبًا', 'm<u>aa</u> lahum bihi min \'ilmin wal<u>aa</u> li-<u>aa</u>b<u>aa</u>-ihim kaburat kalimatan takhruju min afw<u>aa</u>hihim in yaquuluuna ill<u>aa</u> ka<u>dz</u>ib<u>aa</u><strong>n</strong>', 'Mereka sama sekali tidak mempunyai pengetahuan tentang hal itu, begitu pula nenek moyang mereka. Alangkah jeleknya kata-kata yang keluar dari mulut mereka; mereka hanya mengatakan (sesuatu) kebohongan belaka.'),
	(2146, 18, 6, 'فَلَعَلَّكَ بَاخِعٌ نَّفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَّمْ يُؤْمِنُوْا بِهٰذَا الْحَدِيْثِ اَسَفًا', 'fala\'allaka b<u>aa</u>khi\'un nafsaka \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim in lam yu/minuu bih<u>aadzaa</u> <strong>a</strong>l<u>h</u>adiitsi asaf<u>aa</u><strong>n</strong>', 'Maka barangkali engkau (Muhammad) akan mencelakakan dirimu karena bersedih hati setelah mereka berpaling, sekiranya mereka tidak beriman kepada keterangan ini (Al-Qur\'an).'),
	(2147, 18, 7, 'اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ زِيْنَةً لَّهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلًا', 'inn<u>aa</u> ja\'aln<u>aa</u> m<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i ziinatan lah<u>aa</u> linabluwahum ayyuhum a<u>h</u>sanu \'amal<u>aa</u><strong>n</strong>', 'Sesungguhnya Kami telah menjadikan apa yang ada di bumi sebagai perhiasan baginya, untuk Kami menguji mereka, siapakah di antaranya yang terbaik perbuatannya.'),
	(2148, 18, 8, 'وَاِنَّا لَجَاعِلُوْنَ مَا عَلَيْهَا صَعِيْدًا جُرُزًاۗ ', 'wa-inn<u>aa</u> laj<u>aa</u>\'iluuna m<u>aa</u> \'alayh<u>aa</u> <u>sh</u>a\'iidan juruz<u>aa</u><strong>n</strong>', 'Dan Kami benar-benar akan menjadikan (pula) apa yang di atasnya menjadi tanah yang tandus lagi kering.'),
	(2149, 18, 9, 'اَمْ حَسِبْتَ اَنَّ اَصْحٰبَ الْكَهْفِ وَالرَّقِيْمِ كَانُوْا مِنْ اٰيٰتِنَا عَجَبًا', 'am <u>h</u>asibta anna a<u>sh</u>-<u>haa</u>ba <strong>a</strong>lkahfi wa<strong>al</strong>rraqiimi k<u>aa</u>nuu min <u>aa</u>y<u>aa</u>tin<u>aa</u> \'ajab<u>aa</u><strong>n</strong>', 'Apakah engkau mengira bahwa orang yang mendiami gua, dan (yang mempunyai) raqim itu, termasuk tanda-tanda (kebesaran) Kami yang menakjubkan?'),
	(2150, 18, 10, 'اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوْا رَبَّنَآ اٰتِنَا مِنْ لَّدُنْكَ رَحْمَةً وَّهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَدًا', 'i<u>dz</u> aw<u>aa</u> <strong>a</strong>lfityatu il<u>aa</u> <strong>a</strong>lkahfi faq<u>aa</u>luu rabban<u>aa</u> <u>aa</u>tin<u>aa</u> min ladunka ra<u>h</u>matan wahayyi/ lan<u>aa</u> min amrin<u>aa</u> rasyad<u>aa</u><strong>n</strong><u> </u>', '(Ingatlah) ketika pemuda-pemuda itu berlindung ke dalam gua lalu mereka berdoa, “Ya Tuhan kami. Berikanlah rahmat kepada kami dari sisi-Mu dan sempurnakanlah petunjuk yang lurus bagi kami dalam urusan kami.”'),
	(2151, 18, 11, 'فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِى الْكَهْفِ سِنِيْنَ عَدَدًاۙ  ', 'fa<u>dh</u>arabn<u>aa</u> \'al<u>aa</u> <u>aatsaa</u>nihim fii <strong>a</strong>lkahfi siniina \'adad<u>aa</u><strong>n</strong>', 'Maka Kami tutup telinga mereka di dalam gua itu, selama beberapa tahun.'),
	(2152, 18, 12, 'ثُمَّ بَعَثْنٰهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُوْٓا اَمَدًا ࣖ', 'tsumma ba\'atsn<u>aa</u>hum lina\'lama ayyu <strong>a</strong>l<u>h</u>izbayni a<u>hsaa</u> lim<u>aa</u> labitsuu amad<u>aa</u><strong>n</strong>', 'Kemudian Kami bangunkan mereka, agar Kami mengetahui manakah di antara ke dua golongan itu yang lebih tepat dalam menghitung berapa lamanya mereka tinggal (dalam gua itu).'),
	(2153, 18, 13, 'نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۗ اِنَّهُمْ فِتْيَةٌ اٰمَنُوْا بِرَبِّهِمْ وَزِدْنٰهُمْ هُدًىۖ   ', 'na<u>h</u>nu naqu<u>shsh</u>u \'alayka naba-ahum bi<strong>a</strong>l<u>h</u>aqqi innahum fityatun <u>aa</u>manuu birabbihim wazidn<u>aa</u>hum hud<u>aa</u><strong>n</strong>', 'Kami ceritakan kepadamu (Muhammad) kisah mereka dengan sebenarnya. Sesungguhnya mereka adalah pemuda-pemuda yang beriman kepada Tuhan mereka, dan Kami tambahkan petunjuk kepada mereka.'),
	(2154, 18, 14, 'وَّرَبَطْنَا عَلٰى قُلُوْبِهِمْ اِذْ قَامُوْا فَقَالُوْا رَبُّنَا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ لَنْ نَّدْعُوَا۟ مِنْ دُوْنِهٖٓ اِلٰهًا لَّقَدْ قُلْنَآ اِذًا شَطَطًا', 'waraba<u>th</u>n<u>aa</u> \'al<u>aa</u> quluubihim i<u>dz</u> q<u>aa</u>muu faq<u>aa</u>luu rabbun<u>aa</u> rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i lan nad\'uwa min duunihi il<u>aa</u>han laqad quln<u>aa</u> i<u>dz</u>an sya<u>th</u>a<u>th</u><u>aa</u><strong>n</strong>', 'Dan Kami teguhkan hati mereka ketika mereka berdiri lalu mereka berkata, “Tuhan kami adalah Tuhan langit dan bumi; kami tidak menyeru tuhan selain Dia. Sungguh, kalau kami berbuat demikian, tentu kami telah mengucapkan perkataan yang sangat jauh dari kebenaran.”'),
	(2155, 18, 15, 'هٰٓؤُلَاۤءِ قَوْمُنَا اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةًۗ  لَوْلَا يَأْتُوْنَ عَلَيْهِمْ بِسُلْطٰنٍۢ بَيِّنٍۗ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ  ', 'h<u>aa</u>ul<u>aa</u>-i qawmun<u>aa</u> ittakha<u>dz</u>uu min duunihi <u>aa</u>lihatan lawl<u>aa</u> ya/tuuna \'alayhim bisul<u>th</u><u>aa</u>nin bayyinin faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>ib<u>aa</u><strong>n</strong>', 'Mereka itu kaum kami yang telah menjadikan tuhan-tuhan (untuk disembah) selain Dia. Mengapa mereka tidak mengemukakan alasan yang jelas (tentang kepercayaan mereka)? Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah?'),
	(2156, 18, 16, 'وَاِذِ اعْتَزَلْتُمُوْهُمْ وَمَا يَعْبُدُوْنَ اِلَّا اللّٰهَ فَأْوٗٓا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِّنْ رَّحْمَتِهٖ وَيُهَيِّئْ لَكُمْ مِّنْ اَمْرِكُمْ مِّرْفَقًا', 'wa-i<u>dz</u>i i\'tazaltumuuhum wam<u>aa</u> ya\'buduuna ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha fa/wuu il<u>aa</u> <strong>a</strong>lkahfi yansyur lakum rabbukum min ra<u>h</u>matihi wayuhayyi/ lakum min amrikum mirfaq<u>aa</u><strong>n</strong>', 'Dan apabila kamu meninggalkan mereka dan apa yang mereka sembah selain Allah, maka carilah tempat berlindung ke dalam gua itu, niscaya Tuhanmu akan melimpahkan sebagian rahmat-Nya kepadamu dan menyediakan sesuatu yang berguna bagimu dalam urusanmu.'),
	(2157, 18, 17, '۞ وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَّزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِيْنِ وَاِذَا غَرَبَتْ تَّقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِيْ فَجْوَةٍ مِّنْهُۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ ۗمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُّضْلِلْ فَلَنْ تَجِد', 'watar<u>aa</u> <strong>al</strong>sysyamsa i<u>dzaa</u> <u>th</u>ala\'at taz<u>aa</u>waru \'an kahfihim <u>dzaa</u>ta <strong>a</strong>lyamiini wa-i<u>dzaa</u> gharabat taqri<u>dh</u>uhum <u>dzaa</u>ta <strong>al</strong>sysyim<u>aa</u>li wahum fii fajwatin minhu <u>dzaa</u>lika min <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi man yahdi <strong>al</strong>l<u>aa</u>hu fahuwa <strong>a</strong>lmuhtadi waman yu<u>dh</u>lil falan tajida lahu waliyyan mursyid<u>aa</u><strong>n</strong>', 'Dan engkau akan melihat matahari ketika terbit, condong dari gua mereka ke sebelah kanan, dan apabila matahari itu terbenam, menjauhi mereka ke sebelah kiri sedang mereka berada dalam tempat yang luas di dalam (gua) itu. Itulah sebagian dari tanda-tanda (kebesaran) Allah. Barangsiapa diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa disesatkan-Nya, maka engkau tidak akan mendapatkan seorang penolong yang dapat memberi petunjuk kepadanya.'),
	(2158, 18, 18, 'وَتَحْسَبُهُمْ اَيْقَاظًا وَّهُمْ رُقُوْدٌ  ۖوَّنُقَلِّبُهُمْ ذَاتَ الْيَمِيْنِ وَذَاتَ الشِّمَالِ ۖوَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيْدِۗ  لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَّلَمُلِئْتَ مِنْهُمْ رُعْبًا', 'wata<u>h</u>sabuhum ayq<u>aats</u>an wahum ruquudun wanuqallibuhum <u>dzaa</u>ta <strong>a</strong>lyamiini wa<u>dzaa</u>ta <strong>al</strong>sysyim<u>aa</u>li wakalbuhum b<u>aa</u>si<u>th</u>un <u>dz</u>ir<u>aa</u>\'ayhi bi<strong>a</strong>lwa<u>sh</u>iidi lawi i<u>ththh</u>ala\'ta \'alayhim lawallayta minhum fir<u>aa</u>ran walamuli/ta minhum ru\'b<u>aa</u><strong>n</strong>', 'Dan engkau mengira mereka itu tidak tidur, padahal mereka tidur; dan Kami bolak-balikkan mereka ke kanan dan ke kiri, sedang anjing mereka membentangkan kedua lengannya di depan pintu gua. Dan jika kamu menyaksikan mereka tentu kamu akan berpaling melarikan (diri) dari mereka dan pasti kamu akan dipenuhi rasa takut terhadap mereka.'),
	(2159, 18, 19, 'وَكَذٰلِكَ بَعَثْنٰهُمْ لِيَتَسَاۤءَلُوْا بَيْنَهُمْۗ قَالَ قَاۤىِٕلٌ مِّنْهُمْ كَمْ لَبِثْتُمْۗ قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالُوْا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْۗ فَابْعَثُوْٓا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِهٖٓ اِلَى الْمَدِيْنَةِ', 'waka<u>dzaa</u>lika ba\'atsn<u>aa</u>hum liyatas<u>aa</u>-aluu baynahum q<u>aa</u>la q<u>aa</u>-ilun minhum kam labitstum q<u>aa</u>luu labitsn<u>aa</u> yawman aw ba\'<u>dh</u>a yawmin q<u>aa</u>luu rabbukum a\'lamu bim<u>aa</u> labitstum fa<strong>i</strong>b\'atsuu a<u>h</u>adakum biwariqikum h<u>aadz</u>ihi il<u>aa</u> <strong>a</strong>lmadiinati falyan<u>zh</u>ur ayyuh<u>aa</u> azk<u>aa</u> <u>th</u>a\'<u>aa</u>man falya/tikum birizqin minhu walyatala<u>ththh</u>af wal<u>aa</u> yusy\'iranna bikum a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Dan demikianlah Kami bangunkan mereka, agar di antara mereka saling bertanya. Salah seorang di antara mereka berkata, “Sudah berapa lama kamu berada (di sini)?” Mereka menjawab, “Kita berada (di sini) sehari atau setengah hari.” Berkata (yang lain lagi), “Tuhanmu lebih mengetahui berapa lama kamu berada (di sini). Maka suruhlah salah seorang di antara kamu pergi ke kota dengan membawa uang perakmu ini, dan hendaklah dia lihat manakah makanan yang lebih baik, dan bawalah sebagian makanan itu untukmu, dan hendaklah dia berlaku lemah lembut dan jangan sekali-kali menceritakan halmu kepada siapa pun.'),
	(2160, 18, 20, 'اِنَّهُمْ اِنْ يَّظْهَرُوْا عَلَيْكُمْ يَرْجُمُوْكُمْ اَوْ يُعِيْدُوْكُمْ فِيْ مِلَّتِهِمْ وَلَنْ تُفْلِحُوْٓا اِذًا اَبَدًا', 'innahum in ya<u>zh</u>haruu \'alaykum yarjumuukum aw yu\'iiduukum fii millatihim walan tufli<u>h</u>uu i<u>dz</u>an abad<u>aa</u><strong>n</strong>', 'Sesungguhnya jika mereka dapat mengetahui tempatmu, niscaya mereka akan melempari kamu dengan batu, atau memaksamu kembali kepada agama mereka, dan jika demikian niscaya kamu tidak akan beruntung selama-lamanya.”      '),
	(2161, 18, 21, 'وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوْٓا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاَنَّ السَّاعَةَ لَا رَيْبَ فِيْهَاۚ اِذْ يَتَنَازَعُوْنَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوْا عَلَيْهِمْ بُنْيَانًاۗ رَبُّهُمْ اَعْلَمُ بِهِمْۗ قَالَ الَّذِيْنَ غَلَبُوْا ', 'waka<u>dzaa</u>lika a\'tsarn<u>aa</u> \'alayhim liya\'lamuu anna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun wa-anna <strong>al</strong>s<u>aa</u>\'ata l<u>aa</u> rayba fiih<u>aa</u> i<u>dz</u> yatan<u>aa</u>za\'uuna baynahum amrahum faq<u>aa</u>luu ibnuu \'alayhim buny<u>aa</u>nan rabbuhum a\'lamu bihim q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina ghalabuu \'al<u>aa</u> amrihim lanattakhi<u>dz</u>anna \'alayhim masjid<u>aa</u><strong>n</strong>', 'Dan demikian (pula) Kami perlihatkan (manusia) dengan mereka, agar mereka tahu, bahwa janji Allah benar, dan bahwa (kedatangan) hari Kiamat tidak ada keraguan padanya. Ketika mereka berselisih tentang urusan mereka, maka mereka berkata, “Dirikanlah sebuah bangunan di atas (gua) mereka, Tuhan mereka lebih mengetahui tentang mereka.” Orang yang berkuasa atas urusan mereka berkata, “Kami pasti akan mendirikan sebuah rumah ibadah di atasnya.”'),
	(2162, 18, 22, 'سَيَقُوْلُوْنَ ثَلٰثَةٌ رَّابِعُهُمْ كَلْبُهُمْۚ وَيَقُوْلُوْنَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًاۢ بِالْغَيْبِۚ وَيَقُوْلُوْنَ سَبْعَةٌ وَّثَامِنُهُمْ كَلْبُهُمْ ۗقُلْ رَّبِّيْٓ اَعْلَمُ بِعِدَّتِهِمْ مَّا يَعْلَمُهُمْ اِلَّا قَلِيْلٌ ەۗ فَلَا تُمَا', 'sayaquuluuna tsal<u>aa</u>tsatun r<u>aa</u>bi\'uhum kalbuhum wayaquuluuna khamsatun s<u>aa</u>disuhum kalbuhum rajman bi<strong>a</strong>lghaybi wayaquuluuna sab\'atun wats<u>aa</u>minuhum kalbuhum qul rabbii a\'lamu bi\'iddatihim m<u>aa</u> ya\'lamuhum ill<u>aa</u> qaliilun fal<u>aa</u> tum<u>aa</u>ri fiihim ill<u>aa</u> mir<u>aa</u>-an <u>zhaa</u>hiran wal<u>aa</u> tastafti fiihim minhum a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Nanti (ada orang yang akan) mengatakan, ”(Jumlah mereka) tiga (orang), yang ke empat adalah anjingnya,” dan (yang lain) mengatakan, “(Jumlah mereka) lima (orang), yang ke enam adalah anjingnya,” sebagai terkaan terhadap yang gaib; dan (yang lain lagi) mengatakan, “(Jumlah mereka) tujuh (orang), yang ke delapan adalah anjingnya.” Katakanlah (Muhammad), “Tuhanku lebih mengetahui jumlah mereka; tidak ada yang mengetahui (bilangan) mereka kecuali sedikit.” Karena itu janganlah engkau (Muhammad) berbantah tentang hal mereka, kecuali perbantahan lahir saja dan jangan engkau menanyakan tentang mereka (pemuda-pemuda itu) kepada siapa pun.'),
	(2163, 18, 23, 'وَلَا تَقُوْلَنَّ لِشَا۟يْءٍ اِنِّيْ فَاعِلٌ ذٰلِكَ غَدًاۙ  ', 'wal<u>aa</u> taquulanna lisyay-in innii f<u>aa</u>\'ilun <u>dzaa</u>lika ghad<u>aa</u><strong>n</strong>', 'Dan jangan sekali-kali engkau mengatakan terhadap sesuatu, “Aku pasti melakukan itu besok pagi,”'),
	(2164, 18, 24, 'اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۖوَاذْكُرْ رَّبَّكَ اِذَا نَسِيْتَ وَقُلْ عَسٰٓى اَنْ يَّهْدِيَنِ رَبِّيْ لِاَقْرَبَ مِنْ هٰذَا رَشَدًا', 'ill<u>aa</u> an yasy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu wa<strong>u</strong><u>dz</u>kur rabbaka i<u>dzaa</u> nasiita waqul \'as<u>aa</u> an yahdiyani rabbii li-aqraba min h<u>aadzaa</u> rasyad<u>aa</u><strong>n</strong>', 'kecuali (dengan mengatakan), “Insya Allah.” Dan ingatlah kepada Tuhanmu apabila engkau lupa dan katakanlah, “Mudah-mudahan Tuhanku akan memberiku petunjuk kepadaku agar aku yang lebih dekat (kebenarannya) daripada ini.”'),
	(2165, 18, 25, 'وَلَبِثُوْا فِيْ كَهْفِهِمْ ثَلٰثَ مِائَةٍ سِنِيْنَ وَازْدَادُوْا تِسْعًا', 'walabitsuu fii kahfihim tsal<u>aa</u>tsa mi-atin siniina wa<strong>i</strong>zd<u>aa</u>duu tis\'<u>aa</u><strong>n</strong>', 'Dan mereka tinggal dalam gua selama tiga ratus tahun dan ditambah sembilan tahun.'),
	(2166, 18, 26, 'قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُوْا ۚ لَهٗ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ اَبْصِرْ بِهٖ وَاَسْمِعْۗ مَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّۗ وَلَا يُشْرِكُ فِيْ حُكْمِهٖٓ اَحَدًا', 'quli <strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> labitsuu lahu ghaybu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i ab<u>sh</u>ir bihi wa-asmi\' m<u>aa</u> lahum min duunihi min waliyyin wal<u>aa</u> yusyriku fii <u>h</u>ukmihi a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Katakanlah, “Allah lebih mengetahui berapa lamanya mereka tinggal (di gua); milik-Nya semua yang tersembunyi di langit dan di bumi. Alangkah terang penglihatan-Nya dan alangkah tajam pendengaran-Nya; tidak ada seorang pelindung pun bagi mereka selain Dia; dan Dia tidak mengambil seorang pun menjadi sekutu-Nya dalam menetapkan keputusan.”'),
	(2167, 18, 27, 'وَاتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۗ  لَا مُبَدِّلَ لِكَلِمٰتِهٖۗ وَلَنْ تَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا', 'wa<strong>u</strong>tlu m<u>aa</u> uu<u>h</u>iya ilayka min kit<u>aa</u>bi rabbika l<u>aa</u> mubaddila likalim<u>aa</u>tihi walan tajida min duunihi multa<u>h</u>ad<u>aa</u><strong>n</strong>', 'Dan bacakanlah (Muhammad) apa yang diwahyukan kepadamu, yaitu Kitab Tuhanmu (Al-Qur\'an). Tidak ada yang dapat mengubah kalimat-kalimat-Nya. Dan engkau tidak akan dapat menemukan tempat berlindung selain kepada-Nya.'),
	(2168, 18, 28, 'وَاصْبِرْ نَفْسَكَ مَعَ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ وَلَا تَعْدُ عَيْنٰكَ عَنْهُمْۚ تُرِيْدُ زِيْنَةَ الْحَيٰوةِ الدُّنْيَاۚ وَلَا تُطِعْ مَنْ اَغْفَلْنَا قَلْبَهٗ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰىهُ وَكَا', 'wa<strong>i</strong><u>sh</u>bir nafsaka ma\'a <strong>al</strong>la<u>dz</u>iina yad\'uuna rabbahum bi<strong>a</strong>lghad<u>aa</u>ti wa<strong>a</strong>l\'asyiyyi yuriiduuna wajhahu wal<u>aa</u> ta\'du \'ayn<u>aa</u>ka \'anhum turiidu ziinata <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wal<u>aa</u> tu<u>th</u>i\' man aghfaln<u>aa</u> qalbahu \'an <u>dz</u>ikrin<u>aa</u> wa<strong>i</strong>ttaba\'a haw<u>aa</u>hu wak<u>aa</u>na amruhu furu<u>th</u><u>aa</u><strong>n</strong>', 'Dan bersabarlah engkau (Muhammad) bersama orang yang menyeru Tuhannya pada pagi dan senja hari dengan mengharap keridaan-Nya; dan janganlah kedua matamu berpaling dari mereka (karena) mengharapkan perhiasan kehidupan dunia; dan janganlah engkau mengikuti orang yang hatinya telah Kami lalaikan dari mengingat Kami, serta menuruti keinginannya dan keadaannya sudah melewati batas.'),
	(2169, 18, 29, 'وَقُلِ الْحَقُّ مِنْ رَّبِّكُمْۗ فَمَنْ شَاۤءَ فَلْيُؤْمِنْ وَّمَنْ شَاۤءَ فَلْيَكْفُرْۚ اِنَّآ اَعْتَدْنَا لِلظّٰلِمِيْنَ نَارًاۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۗ وَاِنْ يَّسْتَغِيْثُوْا يُغَاثُوْا بِمَاۤءٍ كَالْمُهْلِ يَشْوِى الْوُجُوْهَۗ بِئْسَ الشَّرَابُ', 'waquli <strong>a</strong>l<u>h</u>aqqu min rabbikum faman sy<u>aa</u>-a falyu/min waman sy<u>aa</u>-a falyakfur inn<u>aa</u> a\'tadn<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina n<u>aa</u>ran a<u>hath</u>a bihim sur<u>aa</u>diquh<u>aa</u> wa-in yastaghiitsuu yugh<u>aa</u>tsuu bim<u>aa</u>-in ka<strong>a</strong>lmuhli yasywii <strong>a</strong>lwujuuha bi/sa <strong>al</strong>sysyar<u>aa</u>bu was<u>aa</u>-at murtafaq<u>aa</u><strong>n</strong>', 'Dan katakanlah (Muhammad), “Kebenaran itu datangnya dari Tuhanmu; barangsiapa menghendaki (beriman) hendaklah dia beriman, dan barangsiapa menghendaki (kafir) biarlah dia kafir.” Sesungguhnya Kami telah menyediakan neraka bagi orang zalim, yang gejolaknya mengepung mereka. Jika mereka meminta pertolongan (minum), mereka akan diberi air seperti besi yang mendidih yang menghanguskan wajah. (Itulah) minuman yang paling buruk dan tempat istirahat yang paling jelek.'),
	(2170, 18, 30, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اِنَّا لَا نُضِيْعُ اَجْرَ مَنْ اَحْسَنَ عَمَلًاۚ  ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti inn<u>aa</u> l<u>aa</u> nu<u>dh</u>ii\'u ajra man a<u>h</u>sana \'amal<u>aa</u><strong>n</strong>', 'Sungguh, mereka yang beriman dan mengerjakan kebajikan, Kami benar-benar tidak akan menyia-nyiakan pahala orang yang mengerjakan perbuatan yang baik itu.'),
	(2171, 18, 31, 'اُولٰۤىِٕكَ لَهُمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّيَلْبَسُوْنَ ثِيَابًا خُضْرًا مِّنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَّكِىِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۗ نِعْمَ الثَّوَابُۗ وَحَسُنَ', 'ul<u>aa</u>-ika lahum jann<u>aa</u>tu \'adnin tajrii min ta<u>h</u>tihimu <strong>a</strong>l-anh<u>aa</u>ru yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin way<strong>a</strong>lbasuuna tsiy<u>aa</u>ban khu<u>dh</u>ran min sundusin wa-istabraqin muttaki-iina fiih<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-ar<u>aa</u>-iki ni\'ma <strong>al</strong>tstsaw<u>aa</u>bu wa<u>h</u>asunat murtafaq<u>aa</u><strong>n</strong>', 'Mereka itulah yang memperoleh Surga ‘Adn, yang mengalir di bawahnya sungai-sungai; (dalam surga itu) mereka diberi hiasan gelang emas dan mereka memakai pakaian hijau dari sutera halus dan sutera tebal, sedang mereka duduk sambil bersandar di atas dipan-dipan yang indah. (Itulah) sebaik-baik pahala dan tempat istirahat yang indah.'),
	(2172, 18, 32, '۞ وَاضْرِبْ لَهُمْ مَّثَلًا رَّجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَّحَفَفْنٰهُمَا بِنَخْلٍ وَّجَعَلْنَا بَيْنَهُمَا زَرْعًاۗ ', 'wa<strong>i</strong><u>dh</u>rib lahum matsalan rajulayni ja\'aln<u>aa</u> li-a<u>h</u>adihim<u>aa</u> jannatayni min a\'n<u>aa</u>bin wa<u>h</u>afafn<u>aa</u>hum<u>aa</u> binakhlin waja\'aln<u>aa</u> baynahum<u>aa</u> zar\'<u>aa</u><strong>n</strong>', 'Dan berikanlah (Muhammad) kepada mereka sebuah perumpamaan, dua orang laki-laki, yang seorang (yang kafir) Kami beri dua buah kebun anggur dan Kami kelilingi kedua kebun itu dengan pohon-pohon kurma dan di antara keduanya (kebun itu) Kami buatkan ladang.'),
	(2173, 18, 33, 'كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِّنْهُ شَيْـًٔاۙ وَّفَجَّرْنَا خِلٰلَهُمَا نَهَرًاۙ  ', 'kilt<u>aa</u> <strong>a</strong>ljannatayni <u>aa</u>tat ukulah<u>aa</u> walam ta<u>zh</u>lim minhu syay-an wafajjarn<u>aa</u> khil<u>aa</u>lahum<u>aa</u> nahar<u>aa</u><strong>n</strong>', 'Kedua kebun itu menghasilkan buahnya, dan tidak berkurang (buahnya) sedikit pun, dan di celah-celah kedua kebun itu Kami alirkan sungai, '),
	(2174, 18, 34, 'وَّكَانَ لَهٗ ثَمَرٌۚ فَقَالَ لِصَاحِبِهٖ وَهُوَ يُحَاوِرُهٗٓ اَنَا۠ اَكْثَرُ مِنْكَ مَالًا وَّاَعَزُّ نَفَرًا', 'wak<u>aa</u>na lahu tsamarun faq<u>aa</u>la li<u>shaah</u>ibihi wahuwa yu<u>haa</u>wiruhu an<u>aa</u> aktsaru minka m<u>aa</u>lan wa-a\'azzu nafar<u>aa</u><strong>n</strong>', 'dan dia memiliki kekayaan besar, maka dia berkata kepada kawannya (yang beriman) ketika bercakap-cakap dengan dia, “Hartaku lebih banyak daripada hartamu dan pengikutku lebih kuat.” '),
	(2175, 18, 35, 'وَدَخَلَ جَنَّتَهٗ وَهُوَ ظَالِمٌ لِّنَفْسِهٖۚ قَالَ مَآ اَظُنُّ اَنْ تَبِيْدَ هٰذِهٖٓ اَبَدًاۙ  ', 'wadakhala jannatahu wahuwa <u>zhaa</u>limun linafsihi q<u>aa</u>la m<u>aa</u> a<u>zh</u>unnu an tabiida h<u>aadz</u>ihi abad<u>aa</u><strong>n</strong>', 'Dan dia memasuki kebunnya dengan sikap merugikan dirinya sendiri (karena angkuh dan kafir); dia berkata, “Aku kira kebun ini tidak akan binasa selama-lamanya,'),
	(2176, 18, 36, 'وَّمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةً وَّلَىِٕنْ رُّدِدْتُّ اِلٰى رَبِّيْ لَاَجِدَنَّ خَيْرًا مِّنْهَا مُنْقَلَبًا ', 'wam<u>aa</u> a<u>zh</u>unnu <strong>al</strong>ss<u>aa</u>\'ata q<u>aa</u>-imatan wala-in rudidtu il<u>aa</u> rabbii la-ajidanna khayran minh<u>aa</u> munqalab<u>aa</u><strong>n</strong>', 'dan aku kira hari Kiamat itu tidak akan datang, dan sekiranya aku dikembalikan kepada Tuhanku, pasti aku akan mendapat tempat kembali yang lebih baik dari pada ini.”'),
	(2177, 18, 37, 'قَالَ لَهٗ صَاحِبُهٗ وَهُوَ يُحَاوِرُهٗٓ اَكَفَرْتَ بِالَّذِيْ خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ سَوّٰىكَ رَجُلًاۗ  ', 'q<u>aa</u>la lahu <u>shaah</u>ibuhu wahuwa yu<u>haa</u>wiruhu akafarta bi<strong>a</strong>lla<u>dz</u>ii khalaqaka min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma saww<u>aa</u>ka rajul<u>aa</u><strong>n</strong>', 'Kawannya (yang beriman) berkata kepadanya sambil bercakap-cakap dengannya, “Apakah engkau ingkar kepada (Tuhan) yang menciptakan engkau dari tanah, kemudian dari setetes air mani, lalu Dia menjadikan engkau seorang laki-laki yang sempurna?'),
	(2178, 18, 38, 'لٰكِنَّا۠ هُوَ اللّٰهُ رَبِّيْ وَلَآ اُشْرِكُ بِرَبِّيْٓ اَحَدًا ', 'l<u>aa</u>kinna huwa <strong>al</strong>l<u>aa</u>hu rabbii wal<u>aa</u> usyriku birabbii a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Tetapi aku (percaya bahwa), Dialah Allah, Tuhanku, dan aku tidak mempersekutukan Tuhanku dengan sesuatu pun.'),
	(2179, 18, 39, 'وَلَوْلَآ اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاۤءَ اللّٰهُ ۙ لَا قُوَّةَ اِلَّا بِاللّٰهِ ۚاِنْ تَرَنِ اَنَا۠ اَقَلَّ مِنْكَ مَالًا وَّوَلَدًاۚ ', 'walawl<u>aa</u> i<u>dz</u> dakhalta jannataka qulta m<u>aa</u> sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu l<u>aa</u> quwwata ill<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi in tarani an<u>aa</u> aqalla minka m<u>aa</u>lan wawalad<u>aa</u><strong>n</strong>', 'Dan mengapa ketika engkau memasuki kebunmu tidak mengucapkan ”Masya Allah, la quwwata illa billah” (Sungguh, atas kehendak Allah, semua ini terwujud), tidak ada kekuatan kecuali dengan (pertolongan) Allah, sekalipun engkau anggap harta dan keturunanku lebih sedikit daripadamu.'),
	(2180, 18, 40, 'فَعَسٰى رَبِّيْٓ اَنْ يُّؤْتِيَنِ خَيْرًا مِّنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِّنَ السَّمَاۤءِ فَتُصْبِحَ صَعِيْدًا زَلَقًاۙ ', 'fa\'as<u>aa</u> rabbii an yu/tiyani khayran min jannatika wayursila \'alayh<u>aa</u> <u>h</u>usb<u>aa</u>nan mina <strong>al</strong>ssam<u>aa</u>-i fatu<u>sh</u>bi<u>h</u>a <u>sh</u>a\'iidan zalaq<u>aa</u><strong>n</strong>', 'Maka mudah-mudahan Tuhanku, akan memberikan kepadaku (kebun) yang lebih baik dari kebunmu (ini); dan Dia mengirimkan petir dari langit ke kebunmu, sehingga (kebun itu) menjadi tanah yang licin,'),
	(2181, 18, 41, 'اَوْ يُصْبِحَ مَاۤؤُهَا غَوْرًا فَلَنْ تَسْتَطِيْعَ لَهٗ طَلَبًا ', 'aw yu<u>sh</u>bi<u>h</u>a m<u>aa</u>uh<u>aa</u> ghawran falan tasta<u>th</u>ii\'a lahu <u>th</u>alab<u>aa</u><strong>n</strong>', 'atau airnya menjadi surut ke dalam tanah, maka engkau tidak akan dapat menemukannya lagi.”'),
	(2182, 18, 42, 'وَاُحِيْطَ بِثَمَرِهٖ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَآ اَنْفَقَ فِيْهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَا وَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُشْرِكْ بِرَبِّيْٓ اَحَدًا', 'wau<u>h</u>ii<u>th</u>a bitsamarihi fa-a<u>sh</u>ba<u>h</u>a yuqallibu kaffayhi \'al<u>aa</u> m<u>aa</u> anfaqa fiih<u>aa</u> wahiya kh<u>aa</u>wiyatun \'al<u>aa</u> \'uruusyih<u>aa</u> wayaquulu y<u>aa</u> laytanii lam usyrik birabbii a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Dan harta kekayaannya dibinasakan, lalu dia membolak-balikkan kedua telapak tangannya (tanda menyesal) terhadap apa yang telah dia belanjakan untuk itu, sedang pohon anggur roboh bersama penyangganya (para-para) lalu dia berkata, “Betapa sekiranya dahulu aku tidak mempersekutukan Tuhanku dengan sesuatu pun.”'),
	(2183, 18, 43, 'وَلَمْ تَكُنْ لَّهٗ فِئَةٌ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ وَمَا كَانَ مُنْتَصِرًاۗ  ', 'walam takun lahu fi-atun yan<u>sh</u>uruunahu min duuni <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> k<u>aa</u>na munta<u>sh</u>ir<u>aa</u><strong>n</strong>', 'Dan tidak ada (lagi) baginya segolongan pun yang dapat menolongnya selain Allah; dan dia pun tidak akan dapat membela dirinya.'),
	(2184, 18, 44, 'هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۗ هُوَ خَيْرٌ ثَوَابًا وَّخَيْرٌ عُقْبًا ࣖ', 'hun<u>aa</u>lika <strong>a</strong>lwal<u>aa</u>yatu lill<u>aa</u>hi <strong>a</strong>l<u>h</u>aqqi huwa khayrun tsaw<u>aa</u>ban wakhayrun \'uqb<u>aa</u><strong>n</strong>', 'Di sana, pertolongan itu hanya dari Allah Yang Mahabenar. Dialah (pemberi) pahala terbaik dan (pemberi) balasan terbaik.'),
	(2185, 18, 45, 'وَاضْرِبْ لَهُمْ مَّثَلَ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَشِيْمًا تَذْرُوْهُ الرِّيٰحُ ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقْتَدِرًا', 'wa<strong>i</strong><u>dh</u>rib lahum matsala <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> kam<u>aa</u>-in anzaln<u>aa</u>hu mina <strong>al</strong>ssam<u>aa</u>-i fa<strong>i</strong>khtala<u>th</u>a bihi nab<u>aa</u>tu <strong>a</strong>l-ar<u>dh</u>i fa-a<u>sh</u>ba<u>h</u>a hasyiiman ta<u>dz</u>ruuhu <strong>al</strong>rriy<u>aah</u>u wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in muqtadir<u>aa</u><strong>n</strong>', 'Dan buatkanlah untuk mereka (manusia) perumpamaan kehidupan dunia ini, ibarat air (hujan) yang Kami turunkan dari langit, sehingga menyuburkan tumbuh-tumbuhan di bumi, kemudian (tumbuh-tumbuhan) itu menjadi kering yang diterbangkan oleh angin. Dan Allah Mahakuasa atas segala sesuatu.'),
	(2186, 18, 46, 'اَلْمَالُ وَالْبَنُوْنَ زِيْنَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ اَمَلًا', 'alm<u>aa</u>lu wa<strong>a</strong>lbanuuna ziinatu <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>lb<u>aa</u>qiy<u>aa</u>tu <strong>al</strong><u>shshaa</u>li<u>haa</u>tu khayrun \'inda rabbika tsaw<u>aa</u>ban wakhayrun amal<u>aa</u><strong>n</strong>', 'Harta dan anak-anak adalah perhiasan kehidupan dunia tetapi amal kebajikan yang terus-menerus adalah lebih baik pahalanya di sisi Tuhanmu serta lebih baik untuk menjadi harapan.'),
	(2187, 18, 47, 'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَّحَشَرْنٰهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَدًاۚ ', 'wayawma nusayyiru <strong>a</strong>ljib<u>aa</u>la watar<u>aa</u> <strong>a</strong>l-ar<u>dh</u>a b<u>aa</u>rizatan wa<u>h</u>asyarn<u>aa</u>hum falam nugh<u>aa</u>dir minhum a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Dan (ingatlah) pada hari (ketika) Kami perjalankan gunung-gunung dan engkau akan melihat bumi itu rata dan Kami kumpulkan mereka (seluruh manusia), dan tidak Kami tinggalkan seorang pun dari mereka.'),
	(2188, 18, 48, 'وَعُرِضُوْا عَلٰى رَبِّكَ صَفًّاۗ  لَقَدْ جِئْتُمُوْنَا كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍۢ  ۖبَلْ زَعَمْتُمْ اَلَّنْ نَّجْعَلَ لَكُمْ مَّوْعِدًا', 'wa\'uri<u>dh</u>uu \'al<u>aa</u> rabbika <u>sh</u>affan laqad ji/tumuun<u>aa</u> kam<u>aa</u> khalaqn<u>aa</u>kum awwala marratin bal za\'amtum <strong>al</strong>lan naj\'ala lakum maw\'id<u>aa</u><strong>n</strong>', 'Dan mereka akan dibawa ke hadapan Tuhanmu dengan berbaris. (Allah berfirman), “Sesungguhnya kamu datang kepada Kami, sebagaimana Kami menciptakan kamu pada pertama kali; bahkan kamu menganggap bahwa Kami tidak akan menetapkan bagi kamu waktu (berbangkit untuk memenuhi) perjanjian.”'),
	(2189, 18, 49, 'وَوُضِعَ الْكِتٰبُ فَتَرَى الْمُجْرِمِيْنَ مُشْفِقِيْنَ مِمَّا فِيْهِ وَيَقُوْلُوْنَ يٰوَيْلَتَنَا مَالِ هٰذَا الْكِتٰبِ لَا يُغَادِرُ صَغِيْرَةً وَّلَا كَبِيْرَةً اِلَّآ اَحْصٰىهَاۚ وَوَجَدُوْا مَا عَمِلُوْا حَاضِرًاۗ وَلَا يَظْلِمُ رَبُّكَ اَحَدًا ࣖ ', 'wawu<u>dh</u>i\'a <strong>a</strong>lkit<u>aa</u>bu fatar<u>aa</u> <strong>a</strong>lmujrimiina musyfiqiina mimm<u>aa</u> fiihi wayaquuluuna y<u>aa</u> waylatan<u>aa</u> m<u>aa</u> lih<u>aadzaa</u> <strong>a</strong>lkit<u>aa</u>bi l<u>aa</u> yugh<u>aa</u>diru <u>sh</u>aghiiratan wal<u>aa</u> kabiiratan ill<u>aa</u> a<u>hsaa</u>h<u>aa</u> wawajaduu m<u>aa</u> \'amiluu <u>had</u>iran wal<u>aa</u> ya<u>zh</u>limu rabbuka a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Dan diletakkanlah kitab (catatan amal), lalu engkau akan melihat orang yang berdosa merasa ketakutan terhadap apa yang (tertulis) di dalamnya, dan mereka berkata, “Betapa celaka kami, kitab apakah ini, tidak ada yang tertinggal, yang kecil dan yang besar melainkan tercatat semuanya,” dan mereka dapati (semua) apa yang telah mereka kerjakan (tertulis). Dan Tuhanmu tidak menzalimi seorang jua pun.'),
	(2190, 18, 50, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّهٖۗ اَفَتَتَّخِذُوْنَهٗ وَذُرِّيَّتَهٗٓ اَوْلِيَاۤءَ مِنْ دُوْنِيْ وَهُمْ لَكُمْ عَدُوٌّۗ بِئْسَ لِلظّٰلِمِيْنَ بَدَلًا ', 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa k<u>aa</u>na mina <strong>a</strong>ljinni fafasaqa \'an amri rabbihi afatattakhi<u>dz</u>uunahu wa<u>dz</u>urriyyatahu awliy<u>aa</u>-a min duunii wahum lakum \'aduwwun bi/sa li<strong>l</strong><u>zhzhaa</u>limiina badal<u>aa</u><strong>n</strong>', 'Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Maka mereka pun sujud kecuali Iblis. Dia adalah dari (golongan) jin, maka dia mendurhakai perintah Tuhannya. Pantaskah kamu menjadikan dia dan keturunannya sebagai pemimpin selain Aku, padahal mereka adalah musuhmu? Sangat buruklah (Iblis itu) sebagai pengganti (Allah) bagi orang yang zalim.'),
	(2191, 18, 51, '۞ مَآ اَشْهَدْتُّهُمْ خَلْقَ السَّمٰوٰتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّيْنَ عَضُدًا', 'm<u>aa</u> asyhadtuhum khalqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> khalqa anfusihim wam<u>aa</u> kuntu muttakhi<u>dz</u>a <strong>a</strong>lmu<u>dh</u>illiina \'a<u>dh</u>ud<u>aa</u><strong>n</strong>', 'Aku tidak menghadirkan mereka (Iblis dan anak cucunya) untuk menyaksikan penciptaan langit dan bumi dan tidak (pula) penciptaan diri mereka sendiri; dan Aku tidak menjadikan orang yang menyesatkan itu sebagai penolong.'),
	(2192, 18, 52, 'وَيَوْمَ يَقُوْلُ نَادُوْا شُرَكَاۤءِيَ الَّذِيْنَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَّوْبِقًا', 'wayawma yaquulu n<u>aa</u>duu syurak<u>aa</u>-iya <strong>al</strong>la<u>dz</u>iina za\'amtum fada\'awhum falam yastajiibuu lahum waja\'aln<u>aa</u> baynahum mawbiq<u>aa</u><strong>n</strong>', 'Dan (ingatlah) pada hari (ketika) Dia berfirman, “Panggillah olehmu sekutu-sekutu-Ku yang kamu anggap itu.” Mereka lalu memanggilnya, tetapi mereka (sekutu-sekutu) tidak membalas (seruan) mereka dan Kami adakan untuk mereka tempat kebinasaan (neraka).'),
	(2193, 18, 53, 'وَرَاَ الْمُجْرِمُوْنَ النَّارَ فَظَنُّوْٓا اَنَّهُمْ مُّوَاقِعُوْهَا وَلَمْ يَجِدُوْا عَنْهَا مَصْرِفًا ࣖ ', 'wara<u>aa</u> <strong>a</strong>lmujrimuuna <strong>al</strong>nn<u>aa</u>ra fa<u>zh</u>annuu annahum muw<u>aa</u>qi\'uuh<u>aa</u> walam yajiduu \'anh<u>aa</u> ma<u>sh</u>rif<u>aa</u><strong>n</strong>', 'Dan orang yang berdosa melihat neraka, lalu mereka menduga, bahwa mereka akan jatuh ke dalamnya, dan mereka tidak menemukan tempat berpaling darinya. '),
	(2194, 18, 54, 'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۗ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلًا ', 'walaqad <u>sh</u>arrafn<u>aa</u> fii h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni li<strong>l</strong>nn<u>aa</u>si min kulli matsalin wak<u>aa</u>na <strong>a</strong>l-ins<u>aa</u>nu aktsara syay-in jadal<u>aa</u><strong>n</strong>', 'Dan sesungguhnya Kami telah menjelaskan berulang-ulang kepada manusia dalam Al-Qur\'an ini dengan bermacam-macam perumpamaan. Tetapi manusia adalah memang yang paling banyak membantah.'),
	(2195, 18, 55, 'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوْا رَبَّهُمْ اِلَّآ اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّلِيْنَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا', 'wam<u>aa</u> mana\'a <strong>al</strong>nn<u>aa</u>sa an yu/minuu i<u>dz</u> j<u>aa</u>-ahumu <strong>a</strong>lhud<u>aa</u> wayastaghfiruu rabbahum ill<u>aa</u> an ta/tiyahum sunnatu <strong>a</strong>l-awwaliina aw ya/tiyahumu <strong>a</strong>l\'a<u>dzaa</u>bu qubul<u>aa</u><strong>n</strong>', 'Dan tidak ada (sesuatu pun) yang menghalangi manusia untuk beriman ketika petunjuk telah datang kepada mereka dan memohon ampunan kepada Tuhannya, kecuali (keinginan menanti) datangnya hukum (Allah yang telah berlaku pada) umat yang terdahulu atau datangnya azab atas mereka dengan nyata.'),
	(2196, 18, 56, 'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ وَيُجَادِلُ الَّذِيْنَ كَفَرُوْا بِالْبَاطِلِ لِيُدْحِضُوْا بِهِ الْحَقَّ وَاتَّخَذُوْٓا اٰيٰتِيْ وَمَآ اُنْذِرُوْا هُزُوًا', 'wam<u>aa</u> nursilu <strong>a</strong>lmursaliina ill<u>aa</u> mubasysyiriina wamun<u>dz</u>iriina wayuj<u>aa</u>dilu <strong>al</strong>la<u>dz</u>iina kafaruu bi<strong>a</strong>lb<u>aath</u>ili liyud<u>h</u>i<u>dh</u>uu bihi <strong>a</strong>l<u>h</u>aqqa wa<strong>i</strong>ttakha<u>dz</u>uu <u>aa</u>y<u>aa</u>tii wam<u>aa</u> un<u>dz</u>iruu huzuw<u>aa</u><strong>n</strong>', 'Dan Kami tidak mengutus rasul-rasul melainkan sebagai pembawa kabar gembira dan pemberi peringatan; tetapi orang yang kafir membantah dengan (cara) yang batil agar dengan demikian mereka dapat melenyapkan yang hak (kebenaran), dan mereka menjadikan ayat-ayat-Ku dan apa yang diperingatkan terhadap mereka sebagai olok-olokan.'),
	(2197, 18, 57, 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۗ اِنَّا جَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَّهْتَدُوْٓا اِذًا ا', 'waman a<u>zh</u>lamu mimman <u>dz</u>ukkira bi-<u>aa</u>y<u>aa</u>ti rabbihi fa-a\'ra<u>dh</u>a \'anh<u>aa</u> wanasiya m<u>aa</u> qaddamat yad<u>aa</u>hu inn<u>aa</u> ja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-in tad\'uhum il<u>aa</u> <strong>a</strong>lhud<u>aa</u> falan yahtaduu i<u>dz</u>an abad<u>aa</u><strong>n</strong>', 'Dan siapakah yang lebih zalim dari pada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, lalu dia berpaling darinya dan melupakan apa yang telah dikerjakan oleh kedua tangannya? Sungguh, Kami telah menjadikan hati mereka tertutup, (sehingga mereka tidak) memahaminya, dan (Kami letakkan pula) sumbatan di telinga mereka. Kendatipun engkau (Muhammad) menyeru mereka kepada petunjuk, niscaya mereka tidak akan mendapat petunjuk untuk selama-lamanya.'),
	(2198, 18, 58, 'وَرَبُّكَ الْغَفُوْرُ ذُو الرَّحْمَةِۗ  لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوْا لَعَجَّلَ لَهُمُ الْعَذَابَۗ بَلْ لَّهُمْ مَّوْعِدٌ لَّنْ يَّجِدُوْا مِنْ دُوْنِهٖ مَوْىِٕلًا', 'warabbuka <strong>a</strong>lghafuuru <u>dz</u>uu <strong>al</strong>rra<u>h</u>mati law yu-<u>aa</u>khi<u>dz</u>uhum bim<u>aa</u> kasabuu la\'ajjala lahumu <strong>a</strong>l\'a<u>dzaa</u>ba bal lahum maw\'idun lan yajiduu min duunihi maw-il<u>aa</u><strong>n</strong>', 'Dan Tuhanmu Maha Pengampun, memiliki kasih sayang. Jika Dia hendak menyiksa mereka karena perbuatan mereka, tentu Dia akan menyegerakan siksa bagi mereka. Tetapi bagi mereka ada waktu tertentu (untuk mendapat siksa) yang mereka tidak akan menemukan tempat berlindung dari-Nya.'),
	(2199, 18, 59, 'وَتِلْكَ الْقُرٰٓى اَهْلَكْنٰهُمْ لَمَّا ظَلَمُوْا وَجَعَلْنَا لِمَهْلِكِهِمْ مَّوْعِدًا ࣖ', 'watilka <strong>a</strong>lqur<u>aa</u> ahlakn<u>aa</u>hum lamm<u>aa</u> <u>zh</u>alamuu waja\'aln<u>aa</u> limahlikihim maw\'id<u>aa</u><strong>n</strong>', 'Dan (penduduk) negeri itu telah Kami binasakan ketika mereka berbuat zalim, dan telah Kami tetapkan waktu tertentu bagi kebinasaan mereka.'),
	(2200, 18, 60, 'وَاِذْ قَالَ مُوْسٰى لِفَتٰىهُ لَآ اَبْرَحُ حَتّٰٓى  اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُبًا', 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> lifat<u>aa</u>hu l<u>aa</u> abra<u>h</u>u <u>h</u>att<u>aa</u> ablugha majma\'a <strong>a</strong>lba<u>h</u>rayni aw am<u>dh</u>iya <u>h</u>uqub<u>aa</u><strong>n</strong>', 'Dan (ingatlah) ketika Musa berkata kepada pembantunya, “Aku tidak akan berhenti (berjalan) sebelum sampai ke pertemuan dua laut; atau aku akan berjalan (terus sampai) bertahun-tahun.”'),
	(2201, 18, 61, 'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوْتَهُمَا فَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ سَرَبًا ', 'falamm<u>aa</u> balagh<u>aa</u> majma\'a baynihim<u>aa</u> nasiy<u>aa</u> <u>h</u>uutahum<u>aa</u> fa<strong>i</strong>ttakha<u>dz</u>a sabiilahu fii <strong>a</strong>lba<u>h</u>ri sarab<u>aa</u><strong>n</strong>', ' Maka ketika mereka sampai ke pertemuan dua laut itu, mereka lupa ikannya, lalu (ikan) itu melompat mengambil jalannya ke laut itu.'),
	(2202, 18, 62, 'فَلَمَّا جَاوَزَا قَالَ لِفَتٰىهُ اٰتِنَا غَدَاۤءَنَاۖ  لَقَدْ لَقِيْنَا مِنْ سَفَرِنَا هٰذَا نَصَبًا ', 'falamm<u>aa</u> j<u>aa</u>waz<u>aa</u> q<u>aa</u>la lifat<u>aa</u>hu <u>aa</u>tin<u>aa</u> ghad<u>aa</u>-an<u>aa</u> laqad laqiin<u>aa</u> min safarin<u>aa</u> h<u>aadzaa</u> na<u>sh</u>ab<u>aa</u><strong>n</strong>', 'Maka ketika mereka telah melewati (tempat itu), Musa berkata kepada pembantunya, “Bawalah kemari makanan kita; sungguh kita telah merasa letih karena perjalanan kita ini.”'),
	(2203, 18, 63, 'قَالَ اَرَاَيْتَ اِذْ اَوَيْنَآ اِلَى الصَّخْرَةِ فَاِنِّيْ نَسِيْتُ الْحُوْتَۖ وَمَآ اَنْسٰىنِيْهُ اِلَّا الشَّيْطٰنُ اَنْ اَذْكُرَهٗۚ وَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ عَجَبًا ', 'q<u>aa</u>la ara-ayta i<u>dz</u> awayn<u>aa</u> il<u>aa</u> <strong>al</strong><u>shsh</u>akhrati fa-innii nasiitu <strong>a</strong>l<u>h</u>uuta wam<u>aa</u> ans<u>aa</u>niihu ill<u>aa</u> <strong>al</strong>sysyay<u>th</u><u>aa</u>nu an a<u>dz</u>kurahu wa<strong>i</strong>ttakha<u>dz</u>a sabiilahu fii <strong>a</strong>lba<u>h</u>ri \'ajab<u>aa</u><strong>n</strong>', 'Dia (pembantunya) menjawab, “Tahukah engkau ketika kita mencari tempat berlindung di batu tadi, maka aku lupa (menceritakan tentang) ikan itu dan tidak ada yang membuat aku lupa untuk mengingatnya kecuali setan, dan (ikan) itu mengambil jalannya ke laut dengan cara yang aneh sekali.”'),
	(2204, 18, 64, 'قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۖ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصًاۙ  ', 'q<u>aa</u>la <u>dzaa</u>lika m<u>aa</u> kunn<u>aa</u> nabghi fa<strong>i</strong>rtadd<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim<u>aa</u> qa<u>sh</u>a<u>shaa</u><strong>n</strong>', 'Dia (Musa) berkata, “Itulah (tempat) yang kita cari.” Lalu keduanya kembali, mengikuti jejak mereka semula.'),
	(2205, 18, 65, 'فَوَجَدَا عَبْدًا مِّنْ عِبَادِنَآ اٰتَيْنٰهُ رَحْمَةً مِّنْ عِنْدِنَا وَعَلَّمْنٰهُ مِنْ لَّدُنَّا عِلْمًا', 'fawajad<u>aa</u> \'abdan min \'ib<u>aa</u>din<u>aa</u> <u>aa</u>tayn<u>aa</u>hu ra<u>h</u>matan min \'indin<u>aa</u> wa\'allamn<u>aa</u>hu min ladunn<u>aa</u> \'ilm<u>aa</u><strong>n</strong>', ' Lalu mereka berdua bertemu dengan seorang hamba di antara hamba-hamba Kami, yang telah Kami berikan rahmat kepadanya dari sisi Kami, dan yang telah Kami ajarkan ilmu kepadanya dari sisi Kami.'),
	(2206, 18, 66, 'قَالَ لَهٗ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا', 'q<u>aa</u>la lahu muus<u>aa</u> hal attabi\'uka \'al<u>aa</u> an tu\'allimani mimm<u>aa</u> \'ullimta rusyd<u>aa</u><strong>n</strong>', 'Musa berkata kepadanya, “Bolehkah aku mengikutimu agar engkau mengajarkan kepadaku (ilmu yang benar) yang telah diajarkan kepadamu (untuk menjadi) petunjuk?”'),
	(2207, 18, 67, 'قَالَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ', 'q<u>aa</u>la innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u><strong>n</strong>', 'Dia menjawab, “Sungguh, engkau tidak akan sanggup sabar bersamaku.'),
	(2208, 18, 68, 'وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِهٖ خُبْرًا', 'wakayfa ta<u>sh</u>biru \'al<u>aa</u> m<u>aa</u> lam tu<u>h</u>i<u>th</u> bihi khubr<u>aa</u><strong>n</strong>', 'Dan bagaimana engkau akan dapat bersabar atas sesuatu, sedang engkau belum mempunyai pengetahuan yang cukup tentang hal itu?”'),
	(2209, 18, 69, 'قَالَ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ صَابِرًا وَّلَآ اَعْصِيْ لَكَ اَمْرًا', 'q<u>aa</u>la satajidunii in sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu <u>shaa</u>biran wal<u>aa</u> a\'<u>sh</u>ii laka amr<u>aa</u><strong>n</strong>', 'Dia (Musa) berkata, “Insya Allah akan engkau dapati aku orang yang sabar, dan aku tidak akan menentangmu dalam urusan apa pun.”'),
	(2210, 18, 70, 'قَالَ فَاِنِ اتَّبَعْتَنِيْ فَلَا تَسْـَٔلْنِيْ عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْرًا ࣖ ', 'q<u>aa</u>la fa-ini ittaba\'tanii fal<u>aa</u> tas-alnii \'an syay-in <u>h</u>att<u>aa</u> u<u>h</u>ditsa laka minhu <u>dz</u>ikr<u>aa</u><strong>n</strong>', 'Dia berkata, “Jika engkau mengikutiku, maka janganlah engkau menanyakan kepadaku tentang sesuatu apa pun, sampai aku menerangkannya kepadamu.”'),
	(2211, 18, 71, 'فَانْطَلَقَاۗ حَتّٰٓى اِذَا رَكِبَا فِى السَّفِيْنَةِ خَرَقَهَاۗ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ  لَقَدْ جِئْتَ شَيْـًٔا اِمْرًا ', 'fa<strong>i</strong>n<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> rakib<u>aa</u> fii <strong>al</strong>ssafiinati kharaqah<u>aa</u> q<u>aa</u>la akharaqtah<u>aa</u> litughriqa ahlah<u>aa</u> laqad ji/ta syay-an imr<u>aa</u><strong>n</strong>', 'Maka berjalanlah keduanya, hingga ketika keduanya menaiki perahu lalu dia melubanginya. Dia (Musa) berkata, “Mengapa engkau melubangi perahu itu, apakah untuk menenggelamkan penumpangnya?” Sungguh, engkau telah berbuat suatu kesalahan yang besar.'),
	(2212, 18, 72, 'قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ', 'q<u>aa</u>la alam aqul innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u><strong>n</strong>', 'Dia berkata, “Bukankah sudah aku katakan, bahwa sesungguhnya engkau tidak akan mampu sabar bersamaku?”'),
	(2213, 18, 73, 'قَالَ لَا تُؤَاخِذْنِيْ بِمَا نَسِيْتُ وَلَا تُرْهِقْنِيْ مِنْ اَمْرِيْ عُسْرًا', 'q<u>aa</u>la l<u>aa</u> tu-<u>aa</u>khi<u>dz</u>nii bim<u>aa</u> nasiitu wal<u>aa</u> turhiqnii min amrii \'usr<u>aa</u><strong>n</strong>', 'Dia (Musa) berkata, “Janganlah engkau menghukum aku karena kelupaanku dan janganlah engkau membebani aku dengan suatu kesulitan dalam urusanku.”'),
	(2214, 18, 74, 'فَانْطَلَقَا ۗحَتّٰٓى اِذَا لَقِيَا غُلٰمًا فَقَتَلَهٗ ۙقَالَ اَقَتَلْتَ نَفْسًا زَكِيَّةً؈ۢبِغَيْرِ نَفْسٍۗ  لَقَدْ جِئْتَ شَيْـًٔا نُكْرًا   ۔', 'fa<strong>i</strong>n<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> laqiy<u>aa</u> ghul<u>aa</u>man faqatalahu q<u>aa</u>la aqatalta nafsan zakiyyatan bighayri nafsin laqad ji/ta syay-an nukr<u>aa</u><strong>n</strong>', 'Maka berjalanlah keduanya; hingga ketika keduanya berjumpa dengan seorang anak muda, maka dia membunuhnya. Dia (Musa) berkata, “Mengapa engkau bunuh jiwa yang bersih, bukan karena dia membunuh orang lain? Sungguh, engkau telah melakukan sesuatu yang sangat mungkar.”'),
	(2215, 18, 75, '۞ قَالَ اَلَمْ اَقُلْ لَّكَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ', 'q<u>aa</u>la alam aqul laka innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u><strong>n</strong>', 'Dia berkata, “Bukankah sudah kukatakan kepadamu, bahwa engkau tidak akan mampu sabar bersamaku?”'),
	(2216, 18, 76, 'قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍۢ بَعْدَهَا فَلَا تُصٰحِبْنِيْۚ قَدْ بَلَغْتَ مِنْ لَّدُنِّيْ عُذْرًا ', 'q<u>aa</u>la in sa-altuka \'an syay-in ba\'dah<u>aa</u> fal<u>aa</u> tu<u>shaah</u>ibnii qad balaghta min ladunnii \'u<u>dz</u>r<u>aa</u><strong>n</strong>', 'Dia (Musa) berkata, “Jika aku bertanya kepadamu tentang sesuatu setelah ini, maka jangan lagi engkau memperbolehkan aku menyertaimu, sesungguhnya engkau sudah cukup (bersabar) menerima alasan dariku.”'),
	(2217, 18, 77, 'فَانْطَلَقَا ۗحَتّٰىٓ اِذَآ اَتَيَآ اَهْلَ قَرْيَةِ ِۨاسْتَطْعَمَآ اَهْلَهَا فَاَبَوْا اَنْ يُّضَيِّفُوْهُمَا فَوَجَدَا فِيْهَا جِدَارًا يُّرِيْدُ اَنْ يَّنْقَضَّ فَاَقَامَهٗ ۗقَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْرًا ', 'fa<strong>i</strong>n<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> atay<u>aa</u> ahla qaryatin ista<u>th</u>\'am<u>aa</u> ahlah<u>aa</u> fa-abaw an yu<u>dh</u>ayyifuuhum<u>aa</u> fawajad<u>aa</u> fiih<u>aa</u> jid<u>aa</u>ran yuriidu an yanqa<u>dhdh</u>a fa-aq<u>aa</u>mahu q<u>aa</u>la law syi/ta la<strong>i</strong>ttakha<u>dz</u>ta \'alayhi ajr<u>aa</u><strong>n</strong>', 'Maka keduanya berjalan; hingga ketika keduanya sampai kepada penduduk suatu negeri, mereka berdua meminta dijamu oleh penduduknya, tetapi mereka (penduduk negeri itu) tidak mau menjamu mereka, kemudian keduanya mendapatkan dinding rumah yang hampir roboh (di negeri itu), lalu dia menegakkannya. Dia (Musa) berkata, “Jika engkau mau, niscaya engkau dapat meminta imbalan untuk itu.”'),
	(2218, 18, 78, 'قَالَ هٰذَا فِرَاقُ بَيْنِيْ وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْوِيْلِ مَا لَمْ تَسْتَطِعْ عَّلَيْهِ صَبْرًا ', 'q<u>aa</u>la h<u>aadzaa</u> fir<u>aa</u>qu baynii wabaynika sa-unabbi-uka bita/wiili m<u>aa</u> lam tasta<u>th</u>i\' \'alayhi <u>sh</u>abr<u>aa</u><strong>n</strong>', 'Dia berkata, “Inilah perpisahan antara aku dengan engkau; aku akan memberikan penjelasan kepadamu atas perbuatan yang engkau tidak mampu sabar terhadapnya.'),
	(2219, 18, 79, 'اَمَّا السَّفِيْنَةُ فَكَانَتْ لِمَسٰكِيْنَ يَعْمَلُوْنَ فِى الْبَحْرِ فَاَرَدْتُّ اَنْ اَعِيْبَهَاۗ وَكَانَ وَرَاۤءَهُمْ مَّلِكٌ يَّأْخُذُ كُلَّ سَفِيْنَةٍ غَصْبًا ', 'amm<u>aa</u> <strong>al</strong>ssafiinatu fak<u>aa</u>nat limas<u>aa</u>kiina ya\'maluuna fii <strong>a</strong>lba<u>h</u>ri fa-aradtu an a\'iibah<u>aa</u> wak<u>aa</u>na war<u>aa</u>-ahum malikun ya/khu<u>dz</u>u kulla safiinatin gha<u>sh</u>b<u>aa</u><strong>n</strong>', ' Adapun perahu itu adalah milik orang miskin yang bekerja di laut; aku bermaksud merusaknya, karena di hadapan mereka ada seorang raja yang akan merampas setiap perahu.'),
	(2220, 18, 80, 'وَاَمَّا الْغُلٰمُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَشِيْنَآ اَنْ يُّرْهِقَهُمَا طُغْيَانًا وَّكُفْرًا ۚ', 'wa-amm<u>aa</u> <strong>a</strong>lghul<u>aa</u>mu fak<u>aa</u>na abaw<u>aa</u>hu mu/minayni fakhasyiin<u>aa</u> an yurhiqahum<u>aa</u> <u>th</u>ughy<u>aa</u>nan wakufr<u>aa</u><strong>n</strong>', 'Dan adapun anak muda (kafir) itu, kedua orang tuanya mukmin, dan kami khawatir kalau dia akan memaksa kedua orang tuanya kepada kesesatan dan kekafiran.'),
	(2221, 18, 81, 'فَاَرَدْنَآ اَنْ يُّبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِّنْهُ زَكٰوةً وَّاَقْرَبَ رُحْمًا ', 'fa-aradn<u>aa</u> an yubdilahum<u>aa</u> rabbuhum<u>aa</u> khayran minhu zak<u>aa</u>tan wa-aqraba ru<u>h</u>m<u>aa</u><strong>n</strong>', ' Kemudian kami menghendaki, sekiranya Tuhan mereka menggantinya dengan (seorang anak) lain yang lebih baik kesuciannya daripada (anak) itu dan lebih sayang (kepada ibu bapaknya).'),
	(2222, 18, 82, 'وَاَمَّا الْجِدَارُ فَكَانَ لِغُلٰمَيْنِ يَتِيْمَيْنِ فِى الْمَدِيْنَةِ وَكَانَ تَحْتَهٗ كَنْزٌ لَّهُمَا وَكَانَ اَبُوْهُمَا صَالِحًا ۚفَاَرَادَ رَبُّكَ اَنْ يَّبْلُغَآ اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِّنْ رَّبِّكَۚ وَمَا فَعَلْتُهٗ عَ', 'wa-amm<u>aa</u> <strong>a</strong>ljid<u>aa</u>ru fak<u>aa</u>na lighul<u>aa</u>mayni yatiimayni fii <strong>a</strong>lmadiinati wak<u>aa</u>na ta<u>h</u>tahu kanzun lahum<u>aa</u> wak<u>aa</u>na abuuhum<u>aa</u> <u>shaa</u>li<u>h</u>an fa-ar<u>aa</u>da rabbuka an yablugh<u>aa</u> asyuddahum<u>aa</u> wayastakhrij<u>aa</u> kanzahum<u>aa</u> ra<u>h</u>matan min rabbika wam<u>aa</u> fa\'altuhu \'an amrii <u>dzaa</u>lika ta/wiilu m<u>aa</u> lam tas<u>th</u>i\' \'alayhi <u>sh</u>abr<u>aa</u><strong>n</strong>', '. Dan adapun dinding rumah itu adalah milik dua anak yatim di kota itu, yang di bawahnya tersimpan harta bagi mereka berdua, dan ayahnya seorang yang saleh. Maka Tuhanmu menghendaki agar keduanya sampai dewasa dan keduanya mengeluarkan simpanannya itu sebagai rahmat dari Tuhanmu. Apa yang kuperbuat bukan menurut kemauanku sendiri. Itulah keterangan perbuatan-perbuatan yang engkau tidak sabar terhadapnya.” '),
	(2223, 18, 83, 'وَيَسْـَٔلُوْنَكَ عَنْ ذِى الْقَرْنَيْنِۗ قُلْ سَاَتْلُوْا عَلَيْكُمْ مِّنْهُ ذِكْرًا ۗ', 'wayas-aluunaka \'an <u>dz</u>ii <strong>a</strong>lqarnayni qul sa-atluu \'alaykum minhu <u>dz</u>ikr<u>aa</u><strong>n</strong>', ' Dan mereka bertanya kepadamu (Muhammad) tentang Zulkarnain. Katakanlah, “Akan kubacakan kepadamu kisahnya.”'),
	(2224, 18, 84, 'اِنَّا مَكَّنَّا لَهٗ فِى الْاَرْضِ وَاٰتَيْنٰهُ مِنْ كُلِّ شَيْءٍ سَبَبًا ۙ', 'inn<u>aa</u> makkann<u>aa</u> lahu fii <strong>a</strong>l-ar<u>dh</u>i wa<u>aa</u>tayn<u>aa</u>hu min kulli syay-in sabab<u>aa</u><strong>n</strong>', ' Sungguh, Kami telah memberi kedudukan kepadanya di bumi, dan Kami telah memberikan jalan kepadanya (untuk mencapai) segala sesuatu,'),
	(2225, 18, 85, 'فَاَتْبَعَ سَبَبًا', 'fa-atba\'a sabab<u>aa</u><strong>n</strong>', 'maka dia pun menempuh suatu jalan.'),
	(2226, 18, 86, 'حَتّٰىٓ اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِيْ عَيْنٍ حَمِئَةٍ وَّوَجَدَ عِنْدَهَا قَوْمًا ەۗ قُلْنَا يٰذَا الْقَرْنَيْنِ اِمَّآ اَنْ تُعَذِّبَ وَاِمَّآ اَنْ تَتَّخِذَ فِيْهِمْ حُسْنًا', '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha maghriba <strong>al</strong>sysyamsi wajadah<u>aa</u> taghrubu fii \'aynin <u>h</u>ami-atin wawajada \'indah<u>aa</u> qawman quln<u>aa</u> y<u>aa</u> <u>dzaa</u> <strong>a</strong>lqarnayni imm<u>aa</u> an tu\'a<u>dzdz</u>iba wa-imm<u>aa</u> an tattakhi<u>dz</u>a fiihim <u>h</u>usn<u>aa</u><strong>n</strong>', ' Hingga ketika dia telah sampai di tempat matahari terbenam, dia melihatnya (matahari) terbenam di dalam laut yang berlumpur hitam, dan di sana ditemukannya suatu kaum (tidak beragama). Kami berfirman, “Wahai Zulkarnain! Engkau boleh menghukum atau berbuat kebaikan (mengajak beriman) kepada mereka.”'),
	(2227, 18, 87, 'قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهٗ ثُمَّ يُرَدُّ اِلٰى رَبِّهٖ فَيُعَذِّبُهٗ عَذَابًا نُّكْرًا ', 'q<u>aa</u>la amm<u>aa</u> man <u>zh</u>alama fasawfa nu\'a<u>dzdz</u>ibuhu tsumma yuraddu il<u>aa</u> rabbihi fayu\'a<u>dzdz</u>ibuhu \'a<u>dzaa</u>ban nukr<u>aa</u><strong>n</strong>', ' Dia (Zulkarnain) berkata, “Barangsiapa berbuat zalim, kami akan menghukumnya, lalu dia akan dikembalikan kepada Tuhannya, kemudian Tuhan mengazabnya dengan azab yang sangat keras.'),
	(2228, 18, 88, 'وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًا فَلَهٗ جَزَاۤءً ۨالْحُسْنٰىۚ وَسَنَقُوْلُ لَهٗ مِنْ اَمْرِنَا يُسْرًا ۗ', 'wa-amm<u>aa</u> man <u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an falahu jaz<u>aa</u>-an <strong>a</strong>l<u>h</u>usn<u>aa</u> wasanaquulu lahu min amrin<u>aa</u> yusr<u>aa</u><strong>n</strong>', ' Adapun orang yang beriman dan mengerjakan kebajikan, maka dia mendapat (pahala) yang terbaik sebagai balasan, dan akan kami sampaikan kepadanya perintah kami yang mudah-mudah.”'),
	(2229, 18, 89, 'ثُمَّ اَتْبَعَ سَبَبًا ', 'tsumma atba\'a sabab<u>aa</u><strong>n</strong>', 'Kemudian dia menempuh suatu jalan (yang lain).'),
	(2230, 18, 90, 'حَتّٰىٓ اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَّمْ نَجْعَلْ لَّهُمْ مِّنْ دُوْنِهَا سِتْرًا ۙ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha ma<u>th</u>li\'a <strong>al</strong>sysyamsi wajadah<u>aa</u> ta<u>th</u>lu\'u \'al<u>aa</u> qawmin lam naj\'al lahum min duunih<u>aa</u> sitr<u>aa</u><strong>n</strong>', ' Hingga ketika dia sampai di tempat terbit matahari (sebelah timur) didapatinya (matahari) bersinar di atas suatu kaum yang tidak Kami buatkan suatu pelindung bagi mereka dari (cahaya matahari) itu,'),
	(2231, 18, 91, 'كَذٰلِكَۗ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْرًا ', 'ka<u>dzaa</u>lika waqad a<u>h</u>a<u>th</u>n<u>aa</u> bim<u>aa</u> ladayhi khubr<u>aa</u><strong>n</strong>', 'demikianlah, dan sesungguhnya Kami mengetahui segala sesuatu yang ada padanya (Zulkarnain).'),
	(2232, 18, 92, 'ثُمَّ اَتْبَعَ سَبَبًا ', 'tsumma atba\'a sabab<u>aa</u><strong>n</strong>', 'Kemudian dia menempuh suatu jalan (yang lain lagi).'),
	(2233, 18, 93, 'حَتّٰىٓ اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُوْنِهِمَا قَوْمًاۙ  لَّا يَكَادُوْنَ يَفْقَهُوْنَ قَوْلًا ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha bayna <strong>al</strong>ssaddayni wajada min duunihim<u>aa</u> qawman l<u>aa</u> yak<u>aa</u>duuna yafqahuuna qawl<u>aa</u><strong>n</strong>', ' Hingga ketika dia sampai di antara dua gunung, didapatinya di belakang (kedua gunung itu) suatu kaum yang hampir tidak memahami pembicaraan.'),
	(2234, 18, 94, 'قَالُوْا يٰذَا الْقَرْنَيْنِ اِنَّ يَأْجُوْجَ وَمَأْجُوْجَ مُفْسِدُوْنَ فِى الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا ', 'q<u>aa</u>luu y<u>aa</u> <u>dzaa</u> <strong>a</strong>lqarnayni inna ya/juuja wama/juuja mufsiduuna fii <strong>a</strong>l-ar<u>dh</u>i fahal naj\'alu laka kharjan \'al<u>aa</u> an taj\'ala baynan<u>aa</u> wabaynahum sadd<u>aa</u><strong>n</strong>', ' Mereka berkata, “Wahai Zulkarnain! Sungguh, Yakjuj dan Makjuj itu (makhluk yang) berbuat kerusakan di bumi, maka bolehkah kami membayarmu imbalan agar engkau membuatkan dinding penghalang antara kami dan mereka?”'),
	(2235, 18, 95, 'قَالَ مَا مَكَّنِّيْ فِيْهِ رَبِّيْ خَيْرٌ فَاَعِيْنُوْنِيْ بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا ۙ', 'q<u>aa</u>la m<u>aa</u> makkannii fiihi rabbii khayrun fa-a\'iinuunii biquwwatin aj\'al baynakum wabaynahum radm<u>aa</u><strong>n</strong>', 'Dia (Zulkarnain) berkata, “Apa yang telah dianugerahkan Tuhan kepadaku lebih baik (daripada imbalanmu), maka bantulah aku dengan kekuatan, agar aku dapat membuatkan dinding penghalang antara kamu dan mereka.'),
	(2236, 18, 96, 'اٰتُوْنِيْ زُبَرَ الْحَدِيْدِۗ حَتّٰىٓ اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوْا ۗحَتّٰىٓ اِذَا جَعَلَهٗ نَارًاۙ قَالَ اٰتُوْنِيْٓ اُفْرِغْ عَلَيْهِ قِطْرًا ۗ', '<u>aa</u>tuunii zubara <strong>a</strong>l<u>h</u>adiidi <u>h</u>att<u>aa</u> i<u>dzaa</u> s<u>aa</u>w<u>aa</u> bayna <strong>al</strong><u>sh</u>adafayni q<u>aa</u>la unfukhuu <u>h</u>att<u>aa</u> i<u>dzaa</u> ja\'alahu n<u>aa</u>ran q<u>aa</u>la <u>aa</u>tuunii ufrigh \'alayhi qi<u>th</u>r<u>aa</u><strong>n</strong>', ' Berilah aku potongan-potongan besi!” Hingga ketika (potongan) besi itu telah (terpasang) sama rata dengan kedua (puncak) gunung itu, dia (Zulkarnain) berkata, “Tiuplah (api itu)!” Ketika (besi) itu sudah menjadi (merah seperti) api, dia pun berkata, “Berilah aku tembaga (yang mendidih) agar kutuangkan ke atasnya (besi panas itu).”'),
	(2237, 18, 97, 'فَمَا اسْطَاعُوْٓا اَنْ يَّظْهَرُوْهُ وَمَا اسْتَطَاعُوْا لَهٗ نَقْبًا ', 'fam<u>aa</u> is<u>th</u><u>aa</u>\'uu an ya<u>zh</u>haruuhu wam<u>aa</u> ista<u>th</u><u>aa</u>\'uu lahu naqb<u>aa</u><strong>n</strong>', ' Maka mereka (Yakjuj dan Makjuj) tidak dapat mendakinya dan tidak dapat (pula) melubanginya.'),
	(2238, 18, 98, 'قَالَ هٰذَا رَحْمَةٌ مِّنْ رَّبِّيْۚ فَاِذَا جَاۤءَ وَعْدُ رَبِّيْ جَعَلَهٗ دَكَّاۤءَۚ وَكَانَ وَعْدُ رَبِّيْ حَقًّا ۗ', 'q<u>aa</u>la h<u>aadzaa</u> ra<u>h</u>matun min rabbii fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du rabbii ja\'alahu dakk<u>aa</u>-a wak<u>aa</u>na wa\'du rabbii <u>h</u>aqq<u>aa</u><strong>n</strong>', 'Dia (Zulkarnain) berkata, “(Dinding) ini adalah rahmat dari Tuhanku, maka apabila janji Tuhanku sudah datang, Dia akan menghancurluluhkannya; dan janji Tuhanku itu benar.”'),
	(2239, 18, 99, '۞ وَتَرَكْنَا بَعْضَهُمْ يَوْمَىِٕذٍ يَّمُوْجُ فِيْ بَعْضٍ وَّنُفِخَ فِى الصُّوْرِ فَجَمَعْنٰهُمْ جَمْعًا ۙ', 'watarakn<u>aa</u> ba\'<u>dh</u>ahum yawma-i<u>dz</u>in yamuuju fii ba\'<u>dh</u>in wanufikha fii <strong>al</strong><u>shsh</u>uuri fajama\'n<u>aa</u>hum jam\'<u>aa</u><strong>n</strong>', 'Dan pada hari itu Kami biarkan mereka (Yakjuj dan Makjuj) berbaur antara satu dengan yang lain, dan (apabila) sangkakala ditiup (lagi), akan Kami kumpulkan mereka semuanya.'),
	(2240, 18, 100, 'وَّعَرَضْنَا جَهَنَّمَ يَوْمَىِٕذٍ لِّلْكٰفِرِيْنَ عَرْضًا ۙ', 'wa\'ara<u>dh</u>n<u>aa</u> jahannama yawma-i<u>dz</u>in lilk<u>aa</u>firiina \'ar<u>daa</u><strong>n </strong>', 'Dan Kami perlihatkan (neraka) Jahanam dengan jelas pada hari itu kepada orang kafir,'),
	(2241, 18, 101, ' ۨالَّذِيْنَ كَانَتْ اَعْيُنُهُمْ فِيْ غِطَاۤءٍ عَنْ ذِكْرِيْ وَكَانُوْا لَا يَسْتَطِيْعُوْنَ سَمْعًا ࣖ', '<strong>al</strong>la<u>dz</u>iina k<u>aa</u>nat a\'yunuhum fii ghi<u>th</u><u>aa</u>-in \'an <u>dz</u>ikrii wak<u>aa</u>nuu l<u>aa</u> yasta<u>th</u>ii\'uuna sam\'<u>aa</u><strong>n</strong>', '(yaitu) orang yang mata (hati)nya dalam keadaan tertutup (tidak mampu) dari memperhatikan tanda-tanda (kebesaran)-Ku, dan mereka tidak sanggup mendengar.'),
	(2242, 18, 102, 'اَفَحَسِبَ الَّذِيْنَ كَفَرُوْٓا اَنْ يَّتَّخِذُوْا عِبَادِيْ مِنْ دُوْنِيْٓ اَوْلِيَاۤءَ ۗاِنَّآ اَعْتَدْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ نُزُلًا ', 'afa<u>h</u>asiba <strong>al</strong>la<u>dz</u>iina kafaruu an yattakhi<u>dz</u>uu \'ib<u>aa</u>dii min duunii awliy<u>aa</u>-a inn<u>aa</u> a\'tadn<u>aa</u> jahannama lilk<u>aa</u>firiina nuzul<u>aa</u><strong>n</strong>', 'Maka apakah orang kafir menyangka bahwa mereka (dapat) mengambil hamba-hamba-Ku menjadi penolong selain Aku? Sungguh, Kami telah menyediakan (neraka) Jahanam sebagai tempat tinggal bagi orang-orang kafir.'),
	(2243, 18, 103, 'قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَرِيْنَ اَعْمَالًا ۗ', 'qul hal nunabbi-ukum bi<strong>a</strong>l-akhsariina a\'m<u>aa</u>l<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Apakah perlu Kami beritahukan kepadamu tentang orang yang paling rugi perbuatannya?”'),
	(2244, 18, 104, 'اَلَّذِيْنَ ضَلَّ سَعْيُهُمْ فِى الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُوْنَ اَنَّهُمْ يُحْسِنُوْنَ صُنْعًا ', '<strong>al</strong>la<u>dz</u>iina <u>dh</u>alla sa\'yuhum fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wahum ya<u>h</u>sabuuna annahum yu<u>h</u>sinuuna <u>sh</u>un\'<u>aa</u><strong>n</strong>', '(Yaitu) orang yang sia-sia perbuatannya dalam kehidupan dunia, sedangkan mereka mengira telah berbuat sebaik-baiknya.'),
	(2245, 18, 105, 'اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ وَلِقَاۤىِٕهٖ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُقِيْمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْنًا ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>ti rabbihim waliq<u>aa</u>-ihi fa<u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fal<u>aa</u> nuqiimu lahum yawma <strong>a</strong>lqiy<u>aa</u>mati wazn<u>aa</u><strong>n</strong>', 'Mereka itu adalah orang yang mengingkari ayat-ayat Tuhan mereka dan (tidak percaya) terhadap pertemuan dengan-Nya. Maka sia-sia amal mereka, dan Kami tidak memberikan penimbangan terhadap (amal) mereka pada hari Kiamat.'),
	(2246, 18, 106, 'ذٰلِكَ جَزَاۤؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوْا وَاتَّخَذُوْٓا اٰيٰتِيْ وَرُسُلِيْ هُزُوًا ', '<u>dzaa</u>lika jaz<u>aa</u>uhum jahannamu bim<u>aa</u> kafaruu wa<strong>i</strong>ttakha<u>dz</u>uu <u>aa</u>y<u>aa</u>tii warusulii huzuw<u>aa</u><strong>n</strong>', 'Demikianlah, balasan mereka itu neraka Jahanam, karena kekafiran mereka, dan karena mereka menjadikan ayat-ayat-Ku dan rasul-rasul-Ku sebagai bahan olok-olok.'),
	(2247, 18, 107, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَانَتْ لَهُمْ جَنّٰتُ الْفِرْدَوْسِ نُزُلًا ۙ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti k<u>aa</u>nat lahum jann<u>aa</u>tu <strong>a</strong>lfirdawsi nuzul<u>aa</u><strong>n</strong>', 'Sungguh, orang yang beriman dan mengerjakan kebajikan, untuk mereka disediakan surga Firdaus sebagai tempat tinggal,'),
	(2248, 18, 108, 'خٰلِدِيْنَ فِيْهَا لَا يَبْغُوْنَ عَنْهَا حِوَلًا ', 'kh<u>aa</u>lidiina fiih<u>aa</u> l<u>aa</u> yabghuuna \'anh<u>aa</u> <u>h</u>iwal<u>aa</u><strong>n</strong>', 'mereka kekal di dalamnya, mereka tidak ingin pindah dari sana.'),
	(2249, 18, 109, 'قُلْ لَّوْ كَانَ الْبَحْرُ مِدَادًا لِّكَلِمٰتِ رَبِّيْ لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمٰتُ رَبِّيْ وَلَوْ جِئْنَا بِمِثْلِهٖ مَدَدًا ', 'qul law k<u>aa</u>na <strong>a</strong>lba<u>h</u>ru mid<u>aa</u>dan likalim<u>aa</u>ti rabbii lanafida <strong>a</strong>lba<u>h</u>ru qabla an tanfada kalim<u>aa</u>tu rabbii walaw ji/n<u>aa</u> bimitslihi madad<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Seandainya lautan menjadi tinta untuk (menulis) kalimat-kalimat Tuhanku, maka pasti habislah lautan itu sebelum selesai (penulisan) kalimat-kalimat Tuhanku, meskipun Kami datangkan tambahan sebanyak itu (pula).”'),
	(2250, 18, 110, 'قُلْ اِنَّمَآ اَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَمَنْ كَانَ يَرْجُوْا لِقَاۤءَ رَبِّهٖ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَّلَا يُشْرِكْ بِعِبَادَةِ رَبِّهٖٓ اَحَدًا ࣖ', 'qul innam<u>aa</u> an<u>aa</u> basyarun mitslukum yuu<u>haa</u> ilayya annam<u>aa</u> il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun faman k<u>aa</u>na yarjuu liq<u>aa</u>-a rabbihi falya\'mal \'amalan <u>shaa</u>li<u>h</u>an wal<u>aa</u> yusyrik bi\'ib<u>aa</u>dati rabbihi a<u>h</u>ad<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Sesungguhnya aku ini hanya seorang manusia seperti kamu, yang telah menerima wahyu, bahwa sesungguhnya Tuhan kamu adalah Tuhan Yang Maha Esa.” Maka barangsiapa mengharap pertemuan dengan Tuhannya maka hendaklah dia mengerjakan kebajikan dan janganlah dia mempersekutukan dengan sesuatu pun dalam beribadah kepada Tuhannya.”'),
	(2251, 19, 1, 'كۤهٰيٰعۤصۤ ۚ ', 'k<u>aa</u>f-h<u>aa</u>-y<u>aa</u>-\'ayn-<u>shaa</u>d', 'Kaf  Ha Ya ‘Ain Shad.'),
	(2252, 19, 2, 'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهٗ زَكَرِيَّا ۚ', '<u>dz</u>ikru ra<u>h</u>mati rabbika \'abdahu zakariyy<u>aa</u>', 'Yang dibacakan ini adalah) penjelasan tentang rahmat Tuhanmu kepada hamba-Nya, Zakaria,'),
	(2253, 19, 3, 'اِذْ نَادٰى رَبَّهٗ نِدَاۤءً خَفِيًّا ', 'i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu nid<u>aa</u>-an khafiyy<u>aa</u><strong>n</strong>', ' (yaitu) ketika dia berdoa kepada Tuhannya dengan suara yang lembut.'),
	(2254, 19, 4, 'قَالَ رَبِّ اِنِّيْ وَهَنَ الْعَظْمُ مِنِّيْ وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَّلَمْ اَكُنْۢ بِدُعَاۤىِٕكَ رَبِّ شَقِيًّا ', 'q<u>aa</u>la rabbi innii wahana <strong>a</strong>l\'a<u>zh</u>mu minnii wa<strong>i</strong>syta\'ala <strong>al</strong>rra/su syayban walam akun bidu\'<u>aa</u>-ika rabbi syaqiyy<u>aa</u><strong>n</strong>', 'Dia (Zakaria) berkata, “Ya Tuhanku, sungguh tulangku telah lemah dan kepalaku telah dipenuhi uban, dan aku belum pernah kecewa dalam berdoa kepada-Mu, ya Tuhanku.'),
	(2255, 19, 5, 'وَاِنِّيْ خِفْتُ الْمَوَالِيَ مِنْ وَّرَاۤءِيْ وَكَانَتِ امْرَاَتِيْ عَاقِرًا فَهَبْ لِيْ مِنْ لَّدُنْكَ وَلِيًّا ۙ', 'wa-innii khiftu <strong>a</strong>lmaw<u>aa</u>liya min war<u>aa</u>-ii wak<u>aa</u>nati imra-atii \'<u>aa</u>qiran fahab lii min ladunka waliyy<u>aa</u><strong>n</strong>', 'Dan sungguh, aku khawatir terhadap kerabatku sepeninggalku, padahal istriku seorang yang mandul, maka anugerahilah aku seorang anak dari sisi-Mu,'),
	(2256, 19, 6, 'يَّرِثُنِيْ وَيَرِثُ مِنْ اٰلِ يَعْقُوْبَ وَاجْعَلْهُ رَبِّ رَضِيًّا ', 'yaritsunii wayaritsu min <u>aa</u>li ya\'quuba wa<strong>i</strong>j\'alhu rabbi ra<u>dh</u>iyy<u>aa</u><strong>n</strong>', 'yang akan mewarisi aku dan mewarisi dari keluarga Yakub; dan jadikanlah dia, ya Tuhanku, seorang yang diridai.”'),
	(2257, 19, 7, 'يٰزَكَرِيَّآ اِنَّا نُبَشِّرُكَ بِغُلٰمِ ِۨاسْمُهٗ يَحْيٰىۙ لَمْ نَجْعَلْ لَّهٗ مِنْ قَبْلُ سَمِيًّا ', 'y<u>aa</u> zakariyy<u>aa</u> inn<u>aa</u> nubasysyiruka bighul<u>aa</u>min ismuhu ya<u>h</u>y<u>aa</u> lam naj\'al lahu min qablu samiyy<u>aa</u><strong>n</strong>', '(Allah berfirman), “Wahai Zakaria! Kami memberi kabar gembira kepadamu dengan seorang anak laki-laki namanya Yahya, yang Kami belum pernah memberikan nama seperti itu sebelumnya.”'),
	(2258, 19, 8, 'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّكَانَتِ امْرَاَتِيْ عَاقِرًا وَّقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا ', 'q<u>aa</u>la rabbi ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun wak<u>aa</u>nati imra-atii \'<u>aa</u>qiran waqad balaghtu mina <strong>a</strong>lkibari \'itiyy<u>aa</u><strong>n</strong>', 'Dia (Zakaria) berkata, “Ya Tuhanku, bagaimana aku akan mempunyai anak, padahal istriku seorang yang mandul dan aku (sendiri) sesungguhnya sudah mencapai usia yang sangat tua?”'),
	(2259, 19, 9, 'قَالَ كَذٰلِكَۗ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَّقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـًٔا ', 'q<u>aa</u>la ka<u>dzaa</u>lika q<u>aa</u>la rabbuka huwa \'alayya hayyinun waqad khalaqtuka min qablu walam taku syay-<u>aa</u><strong>n</strong>', '(Allah) berfirman, “Demikianlah.” Tuhanmu berfirman, “Hal itu mudah bagi-Ku; sungguh, engkau telah Aku ciptakan sebelum itu, padahal (pada waktu itu) engkau belum berwujud sama sekali.”'),
	(2260, 19, 10, 'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗقَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِيًّا ', 'q<u>aa</u>la rabbi ij\'al lii <u>aa</u>yatan q<u>aa</u>la <u>aa</u>yatuka <strong>al</strong>l<u>aa</u> tukallima <strong>al</strong>nn<u>aa</u>sa tsal<u>aa</u>tsa lay<u>aa</u>lin sawiyy<u>aa</u><strong>n</strong>', 'Dia (Zakaria) berkata, “Ya Tuhanku, berilah aku suatu tanda.” (Allah) berfirman, “Tandamu ialah engkau tidak dapat bercakap-cakap dengan manusia selama tiga malam, padahal engkau sehat.”'),
	(2261, 19, 11, 'فَخَرَجَ عَلٰى قَوْمِهٖ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوْا بُكْرَةً وَّعَشِيًّا ', 'fakharaja \'al<u>aa</u> qawmihi mina <strong>a</strong>lmi<u>h</u>r<u>aa</u>bi fa-aw<u>haa</u> ilayhim an sabbi<u>h</u>uu bukratan wa\'asyiyy<u>aa</u><strong>n</strong>', 'Maka dia keluar dari mihrab menuju kaumnya, lalu dia memberi isyarat kepada mereka; bertasbihlah kamu pada waktu pagi dan petang.'),
	(2262, 19, 12, 'يٰيَحْيٰى خُذِ الْكِتٰبَ بِقُوَّةٍ ۗوَاٰتَيْنٰهُ الْحُكْمَ صَبِيًّاۙ ', 'y<u>aa</u> ya<u>h</u>y<u>aa</u> khu<u>dz</u>i <strong>a</strong>lkit<u>aa</u>ba biquwwatin wa<u>aa</u>tayn<u>aa</u>hu <strong>a</strong>l<u>h</u>ukma <u>sh</u>abiyy<u>aa</u><strong>n</strong>', ' ”Wahai Yahya! Ambillah (pelajarilah) Kitab (Taurat) itu dengan sungguh-sungguh.” Dan Kami berikan hikmah kepadanya (Yahya) selagi dia masih kanak-kanak,'),
	(2263, 19, 13, 'وَّحَنَانًا مِّنْ لَّدُنَّا وَزَكٰوةً ۗوَكَانَ تَقِيًّا ۙ', 'wa<u>h</u>an<u>aa</u>nan min ladunn<u>aa</u> wazak<u>aa</u>tan wak<u>aa</u>na taqiyy<u>aa</u><strong>n</strong>', 'dan (Kami jadikan) rasa kasih sayang (kepada sesama) dari Kami dan bersih (dari dosa). Dan dia pun seorang yang bertakwa, '),
	(2264, 19, 14, 'وَّبَرًّاۢ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا ', 'wabarran biw<u>aa</u>lidayhi walam yakun jabb<u>aa</u>ran \'a<u>sh</u>iyy<u>aa</u><strong>n</strong>', ' dan sangat berbakti kepada kedua orang tuanya, dan dia bukan orang yang sombong (bukan pula) orang yang durhaka.'),
	(2265, 19, 15, 'وَسَلٰمٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوْتُ وَيَوْمَ يُبْعَثُ حَيًّا ࣖ', 'wasal<u>aa</u>mun \'alayhi yawma wulida wayawma yamuutu wayawma yub\'atsu <u>h</u>ayy<u>aa</u><strong>n</strong>', 'Dan kesejahteraan bagi dirinya pada hari lahirnya, pada hari wafatnya, dan pada hari dia dibangkitkan hidup kembali.'),
	(2266, 19, 16, 'وَاذْكُرْ فِى الْكِتٰبِ مَرْيَمَۘ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَانًا شَرْقِيًّا ۙ', 'wa<strong>u</strong><u>dz</u>kur fii <strong>a</strong>lkit<u>aa</u>bi maryama i<u>dz</u>i intaba<u>dz</u>at min ahlih<u>aa</u> mak<u>aa</u>nan syarqiyy<u>aa</u><strong>n</strong>', 'Dan ceritakanlah (Muhammad) kisah Maryam di dalam Kitab (Al-Qur\'an), (yaitu) ketika dia mengasingkan diri dari keluarganya ke suatu tempat di sebelah timur (Baitulmaqdis),'),
	(2267, 19, 17, 'فَاتَّخَذَتْ مِنْ دُوْنِهِمْ حِجَابًاۗ فَاَرْسَلْنَآ اِلَيْهَا رُوْحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا ', 'fa<strong>i</strong>ttakha<u>dz</u>at min duunihim <u>h</u>ij<u>aa</u>ban fa-arsaln<u>aa</u> ilayh<u>aa</u> ruu<u>h</u>an<u>aa</u> fatamatstsala lah<u>aa</u> basyaran sawiyy<u>aa</u><strong>n</strong>', 'lalu dia memasang tabir (yang melindunginya) dari mereka; lalu Kami mengutus roh Kami (Jibril) kepadanya, maka dia menampakkan diri di hadapannya dalam bentuk manusia yang sempurna.'),
	(2268, 19, 18, 'قَالَتْ اِنِّيْٓ اَعُوْذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِيًّا ', 'q<u>aa</u>lat innii a\'uu<u>dz</u>u bi<strong>al</strong>rra<u>h</u>m<u>aa</u>ni minka in kunta taqiyy<u>aa</u><strong>n</strong>', 'Dia (Maryam) berkata, “Sungguh, aku berlindung kepada Tuhan Yang Maha Pengasih terhadapmu, jika engkau orang yang bertakwa.”'),
	(2269, 19, 19, 'قَالَ اِنَّمَآ اَنَا۠ رَسُوْلُ رَبِّكِۖ لِاَهَبَ لَكِ غُلٰمًا زَكِيًّا ', 'q<u>aa</u>la innam<u>aa</u> an<u>aa</u> rasuulu rabbiki li-ahaba laki ghul<u>aa</u>man zakiyy<u>aa</u><strong>n</strong>', 'Dia (Jibril) berkata, “Sesungguhnya aku hanyalah utusan Tuhanmu, untuk menyampaikan anugerah kepadamu seorang anak laki-laki yang suci.”'),
	(2270, 19, 20, 'قَالَتْ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ وَّلَمْ اَكُ بَغِيًّا ', 'q<u>aa</u>lat ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun walam yamsasnii basyarun walam aku baghiyy<u>aa</u><strong>n</strong>', 'Dia (Maryam) berkata, “Bagaimana mungkin aku mempunyai anak laki-laki, padahal tidak pernah ada orang (laki-laki) yang menyentuhku dan aku bukan seorang pezina!”'),
	(2271, 19, 21, 'قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَهٗٓ اٰيَةً لِّلنَّاسِ وَرَحْمَةً مِّنَّاۚ وَكَانَ اَمْرًا مَّقْضِيًّا ', 'q<u>aa</u>la ka<u>dzaa</u>liki q<u>aa</u>la rabbuki huwa \'alayya hayyinun walinaj\'alahu <u>aa</u>yatan li<strong>l</strong>nn<u>aa</u>si wara<u>h</u>matan minn<u>aa</u> wak<u>aa</u>na amran maq<u>dh</u>iyy<u>aa</u><strong>n</strong>', 'Dia (Jibril) berkata, “Demikianlah.” Tuhanmu berfirman, “Hal itu mudah bagi-Ku, dan agar Kami menjadikannya suatu tanda (kebesaran Allah) bagi manusia dan sebagai rahmat dari Kami; dan hal itu adalah suatu urusan yang (sudah) diputuskan.”'),
	(2272, 19, 22, '۞ فَحَمَلَتْهُ فَانْتَبَذَتْ بِهٖ مَكَانًا قَصِيًّا ', 'fa<u>h</u>amalat-hu fa<strong>i</strong>ntaba<u>dz</u>at bihi mak<u>aa</u>nan qa<u>sh</u>iyy<u>aa</u><strong>n</strong>', ' Maka dia (Maryam) mengandung, lalu dia mengasingkan diri dengan kandungannya itu ke tempat yang jauh.');
INSERT INTO `tbl_ayat` (`id`, `surah`, `nomor`, `ar`, `tr`, `idn`) VALUES
	(2273, 19, 23, 'فَاَجَاۤءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يٰلَيْتَنِيْ مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْيًا مَّنْسِيًّا ', 'fa-aj<u>aa</u>-ah<u>aa</u> <strong>a</strong>lmakh<u>aad</u>u il<u>aa</u> ji<u>dz</u>\'i <strong>al</strong>nnakhlati q<u>aa</u>lat y<u>aa</u> laytanii mittu qabla h<u>aadzaa</u> wakuntu nasyan mansiyy<u>aa</u><strong>n</strong>', 'Kemudian rasa sakit akan melahirkan memaksanya (bersandar) pada pangkal pohon kurma, dia (Maryam) berkata, “Wahai, betapa (baiknya) aku mati sebelum ini, dan aku menjadi seorang yang tidak diperhatikan dan dilupakan.”'),
	(2274, 19, 24, 'فَنَادٰىهَا مِنْ تَحْتِهَآ اَلَّا تَحْزَنِيْ قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا ', 'fan<u>aa</u>d<u>aa</u>h<u>aa</u> min ta<u>h</u>tih<u>aa</u> <strong>al</strong>l<u>aa</u> ta<u>h</u>zanii qad ja\'ala rabbuki ta<u>h</u>taki sariyy<u>aa</u><strong>n</strong>', 'Maka dia (Jibril) berseru kepadanya dari tempat yang rendah, “Janganlah engkau bersedih hati, sesungguhnya Tuhanmu telah menjadikan anak sungai di bawahmu.'),
	(2275, 19, 25, 'وَهُزِّيْٓ اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسٰقِطْ عَلَيْكِ رُطَبًا جَنِيًّا ۖ', 'wahuzzii ilayki biji<u>dz</u>\'i <strong>al</strong>nnakhlati tus<u>aa</u>qi<u>th</u> \'alayki ru<u>th</u>aban janiyy<u>aa</u><strong>n</strong>', 'Dan goyanglah pangkal pohon kurma itu ke arahmu, niscaya (pohon) itu akan menggugurkan buah kurma yang masak kepadamu.'),
	(2276, 19, 26, 'فَكُلِيْ وَاشْرَبِيْ وَقَرِّيْ عَيْنًا ۚفَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَدًاۙ فَقُوْلِيْٓ اِنِّيْ نَذَرْتُ لِلرَّحْمٰنِ صَوْمًا فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِيًّا ۚ', 'fakulii wa<strong>i</strong>syrabii waqarrii \'aynan fa-imm<u>aa</u> tarayinna mina <strong>a</strong>lbasyari a<u>h</u>adan faquulii innii na<u>dz</u>artu li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni <u>sh</u>awman falan ukallima <strong>a</strong>lyawma insiyy<u>aa</u><strong>n</strong>', 'Maka makan, minum dan bersenanghatilah engkau. Jika engkau melihat seseorang, maka katakanlah, “Sesungguhnya aku telah bernazar berpuasa untuk Tuhan Yang Maha Pengasih, maka aku tidak akan berbicara dengan siapa pun pada hari ini.”'),
	(2277, 19, 27, 'فَاَتَتْ بِهٖ قَوْمَهَا تَحْمِلُهٗ ۗقَالُوْا يٰمَرْيَمُ لَقَدْ جِئْتِ شَيْـًٔا فَرِيًّا ', 'fa-atat bihi qawmah<u>aa</u> ta<u>h</u>miluhu q<u>aa</u>luu y<u>aa</u> maryamu laqad ji/ti syay-an fariyy<u>aa</u><strong>n</strong>', 'Kemudian dia (Maryam) membawa dia (bayi itu) kepada kaumnya dengan menggendongnya. Mereka (kaumnya) berkata, “Wahai Maryam! Sungguh, engkau telah membawa sesuatu yang sangat mungkar.'),
	(2278, 19, 28, 'يٰٓاُخْتَ هٰرُوْنَ مَا كَانَ اَبُوْكِ امْرَاَ سَوْءٍ وَّمَا كَانَتْ اُمُّكِ بَغِيًّا ۖ', 'y<u>aa</u> ukhta h<u>aa</u>ruuna m<u>aa</u> k<u>aa</u>na abuuki imra-a saw-in wam<u>aa</u> k<u>aa</u>nat ummuki baghiyy<u>aa</u><strong>n</strong>', 'Wahai saudara perempuan Harun (Maryam)! Ayahmu bukan seorang yang buruk perangai dan ibumu bukan seorang perempuan pezina.”'),
	(2279, 19, 29, 'فَاَشَارَتْ اِلَيْهِۗ قَالُوْا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِى الْمَهْدِ صَبِيًّا ', 'fa-asy<u>aa</u>rat ilayhi q<u>aa</u>luu kayfa nukallimu man k<u>aa</u>na fii <strong>a</strong>lmahdi <u>sh</u>abiyy<u>aa</u><strong>n</strong>', 'Maka dia (Maryam) menunjuk kepada (anak)nya. Mereka berkata, “Bagaimana kami akan berbicara dengan anak kecil yang masih dalam ayunan?” '),
	(2280, 19, 30, 'قَالَ اِنِّيْ عَبْدُ اللّٰهِ ۗاٰتٰنِيَ الْكِتٰبَ وَجَعَلَنِيْ نَبِيًّا ۙ', 'q<u>aa</u>la innii \'abdu <strong>al</strong>l<u>aa</u>hi <u>aa</u>t<u>aa</u>niya <strong>a</strong>lkit<u>aa</u>ba waja\'alanii nabiyy<u>aa</u><strong>n</strong>', 'Dia (Isa) berkata, “Sesungguhnya aku hamba Allah, Dia memberiku Kitab (Injil) dan Dia menjadikan aku seorang Nabi.'),
	(2281, 19, 31, 'وَّجَعَلَنِيْ مُبٰرَكًا اَيْنَ مَا كُنْتُۖ وَاَوْصٰنِيْ بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَيًّا ۖ', 'waja\'alanii mub<u>aa</u>rakan aynam<u>aa</u> kuntu wa-aw<u>shaa</u>nii bi<strong>al</strong><u>shsh</u>al<u>aa</u>ti wa<strong>al</strong>zzak<u>aa</u>ti m<u>aa</u> dumtu <u>h</u>ayy<u>aa</u><strong>n</strong>', 'Dan Dia menjadikan aku seorang yang diberkahi di mana saja aku berada, dan Dia memerintahkan kepadaku (melaksanakan) salat dan (menunaikan) zakat selama aku hidup;'),
	(2282, 19, 32, 'وَّبَرًّاۢ بِوَالِدَتِيْ وَلَمْ يَجْعَلْنِيْ جَبَّارًا شَقِيًّا ', 'wabarran biw<u>aa</u>lidatii walam yaj\'alnii jabb<u>aa</u>ran syaqiyy<u>aa</u><strong>n</strong>', 'dan berbakti kepada ibuku, dan Dia tidak menjadikan aku seorang yang sombong lagi celaka.'),
	(2283, 19, 33, 'وَالسَّلٰمُ عَلَيَّ يَوْمَ وُلِدْتُّ وَيَوْمَ اَمُوْتُ وَيَوْمَ اُبْعَثُ حَيًّا ', 'wa<strong>al</strong>ssal<u>aa</u>mu \'alayya yawma wulidtu wayawma amuutu wayawma ub\'atsu <u>h</u>ayy<u>aa</u><strong>n</strong>', ' Dan kesejahteraan semoga dilimpahkan kepadaku, pada hari kelahiranku, pada hari wafatku, dan pada hari aku dibangkitkan hidup kembali.”'),
	(2284, 19, 34, 'ذٰلِكَ عِيْسَى ابْنُ مَرْيَمَ ۚقَوْلَ الْحَقِّ الَّذِيْ فِيْهِ يَمْتَرُوْنَ ', '<u>dzaa</u>lika \'iis<u>aa</u> ibnu maryama qawla <strong>a</strong>l<u>h</u>aqqi <strong>al</strong>la<u>dz</u>ii fiihi yamtaruun<strong>a</strong>', 'Itulah Isa putra Maryam, (yang mengatakan) perkataan yang benar, yang mereka ragukan kebenarannya.'),
	(2285, 19, 35, 'مَا كَانَ لِلّٰهِ اَنْ يَّتَّخِذَ مِنْ وَّلَدٍ سُبْحٰنَهٗ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ۗ', 'm<u>aa</u> k<u>aa</u>na lill<u>aa</u>hi an yattakhi<u>dz</u>a min waladin sub<u>haa</u>nahu i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun<strong>u</strong>', 'Tidak patut bagi Allah mempunyai anak, Mahasuci Dia. Apabila Dia hendak menetapkan sesuatu, maka Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu.'),
	(2286, 19, 36, 'وَاِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ', 'wa-inna <strong>al</strong>l<u>aa</u>ha rabbii warabbukum fa<strong>u</strong>\'buduuhu h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un mustaqiim<strong>un</strong>', '(Isa berkata), “Dan sesungguhnya Allah itu Tuhanku dan Tuhanmu, maka sembahlah Dia. Ini adalah jalan yang lurus.”'),
	(2287, 19, 37, 'فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْۚ فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ مَّشْهَدِ يَوْمٍ عَظِيْمٍ ', 'fa<strong>i</strong>khtalafa <strong>a</strong>l-a<u>h</u>z<u>aa</u>bu min baynihim fawaylun lilla<u>dz</u>iina kafaruu min masyhadi yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Maka berselisihlah golongan-golongan (yang ada) di antara mereka (Yahudi dan Nasrani). Maka celakalah orang-orang kafir pada waktu menyaksikan hari yang agung!'),
	(2288, 19, 38, 'اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُوْنَنَا لٰكِنِ الظّٰلِمُوْنَ الْيَوْمَ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'asmi\' bihim wa-ab<u>sh</u>ir yawma ya/tuunan<u>aa</u> l<u>aa</u>kini <strong>al</strong><u>zhzhaa</u>limuuna <strong>a</strong>lyawma fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Alangkah tajam pendengaran mereka dan alangkah terang penglihatan mereka pada hari mereka datang kepada Kami. Tetapi orang-orang yang zalim pada hari ini (di dunia) berada dalam kesesatan yang nyata.'),
	(2289, 19, 39, 'وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۘ وَهُمْ فِيْ غَفْلَةٍ وَّهُمْ لَا يُؤْمِنُوْنَ ', 'wa-an<u>dz</u>irhum yawma <strong>a</strong>l<u>h</u>asrati i<u>dz</u> qu<u>dh</u>iya <strong>a</strong>l-amru wahum fii ghaflatin wahum l<u>aa</u> yu/minuun<strong>a</strong>', ' Dan berilah mereka peringatan (Muhammad) tentang hari penyesalan, (yaitu) ketika segala perkara telah diputus, sedang mereka dalam kelalaian dan mereka tidak beriman.'),
	(2290, 19, 40, 'اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُوْنَ ࣖ', 'inn<u>aa</u> na<u>h</u>nu naritsu <strong>a</strong>l-ar<u>dh</u>a waman \'alayh<u>aa</u> wa-ilayn<u>aa</u> yurja\'uun<strong>a</strong>', 'Sesungguhnya Kamilah yang mewarisi bumi dan semua yang ada di atasnya, dan hanya kepada Kami mereka dikembalikan.'),
	(2291, 19, 41, 'وَاذْكُرْ فِى الْكِتٰبِ اِبْرٰهِيْمَ ەۗ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا', 'wa<strong>u</strong><u>dz</u>kur fii <strong>a</strong>lkit<u>aa</u>bi ibr<u>aa</u>hiima innahu k<u>aa</u>na <u>sh</u>iddiiqan nabiyy<u>aa</u><strong>n</strong>', 'Dan ceritakanlah (Muhammad) kisah Ibrahim di dalam Kitab (Al-Qur\'an), sesungguhnya dia adalah seorang yang sangat membenarkan, seorang Nabi. '),
	(2292, 19, 42, 'اِذْ قَالَ لِاَبِيْهِ يٰٓاَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِيْ عَنْكَ شَيْـًٔا ', 'i<u>dz</u> q<u>aa</u>la li-abiihi y<u>aa</u> abati lima ta\'budu m<u>aa</u> l<u>aa</u> yasma\'u wal<u>aa</u> yub<u>sh</u>iru wal<u>aa</u> yughnii \'anka syay-<u>aa</u><strong>n</strong>', '(Ingatlah) ketika dia (Ibrahim) berkata kepada ayahnya, “Wahai ayahku! Mengapa engkau menyembah sesuatu yang tidak mendengar, tidak melihat, dan tidak dapat menolongmu sedikit pun?'),
	(2293, 19, 43, 'يٰٓاَبَتِ اِنِّي قَدْ جَاۤءَنِيْ مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِيْٓ اَهْدِكَ صِرَاطًا سَوِيًّا ', 'y<u>aa</u> abati innii qad j<u>aa</u>-anii mina <strong>a</strong>l\'ilmi m<u>aa</u> lam ya/tika fa<strong>i</strong>ttabi\'nii ahdika <u>sh</u>ir<u>aath</u>an sawiyy<u>aa</u><strong>n</strong>', 'Wahai ayahku! Sungguh, telah sampai kepadaku sebagian ilmu yang tidak diberikan kepadamu, maka ikutilah aku, niscaya aku akan menunjukkan kepadamu jalan yang lurus.'),
	(2294, 19, 44, 'يٰٓاَبَتِ لَا تَعْبُدِ الشَّيْطٰنَۗ اِنَّ الشَّيْطٰنَ كَانَ لِلرَّحْمٰنِ عَصِيًّا ', 'y<u>aa</u> abati l<u>aa</u> ta\'budi <strong>al</strong>sysyay<u>th</u><u>aa</u>na inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na k<u>aa</u>na li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni \'a<u>sh</u>iyy<u>aa</u><strong>n</strong>', 'Wahai ayahku! Janganlah engkau menyembah setan. Sungguh, setan itu durhaka kepada Tuhan Yang Maha Pengasih.'),
	(2295, 19, 45, 'يٰٓاَبَتِ اِنِّيْٓ اَخَافُ اَنْ يَّمَسَّكَ عَذَابٌ مِّنَ الرَّحْمٰنِ فَتَكُوْنَ لِلشَّيْطٰنِ وَلِيًّا ', 'y<u>aa</u> abati innii akh<u>aa</u>fu an yamassaka \'a<u>dzaa</u>bun mina <strong>al</strong>rra<u>h</u>m<u>aa</u>ni fatakuuna li<strong>l</strong>sysyya<u>th</u><u>aa</u>ni waliyy<u>aa</u><strong>n</strong>', 'Wahai ayahku! Aku sungguh khawatir engkau akan ditimpa azab dari Tuhan Yang Maha Pengasih, sehingga engkau menjadi teman bagi setan.”'),
	(2296, 19, 46, 'قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَتِيْ يٰٓاِبْرٰهِيْمُ ۚ لَىِٕنْ لَّمْ تَنْتَهِ لَاَرْجُمَنَّكَ وَاهْجُرْنِيْ مَلِيًّا ', 'q<u>aa</u>la ar<u>aa</u>ghibun anta \'an <u>aa</u>lihatii y<u>aa</u> ibr<u>aa</u>hiimu la-in lam tantahi la-arjumannaka wa<strong>u</strong>hjurnii maliyy<u>aa</u><strong>n</strong>', 'Dia (ayahnya) berkata, “Bencikah engkau kepada tuhan-tuhanku, wahai Ibrahim? Jika engkau tidak berhenti, pasti engkau akan kurajam, maka tinggalkanlah aku untuk waktu yang lama.” '),
	(2297, 19, 47, 'قَالَ سَلٰمٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبِّيْۗ اِنَّهٗ كَانَ بِيْ حَفِيًّا ', 'q<u>aa</u>la sal<u>aa</u>mun \'alayka sa-astaghfiru laka rabbii innahu k<u>aa</u>na bii <u>h</u>afiyy<u>aa</u><strong>n</strong>', 'Dia (Ibrahim) berkata, “Semoga keselamatan dilimpahkan kepadamu, aku akan memohonkan ampunan bagimu kepada Tuhanku. Sesungguhnya Dia sangat baik kepadaku.'),
	(2298, 19, 48, 'وَاَعْتَزِلُكُمْ وَمَا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ وَاَدْعُوْ رَبِّيْۖ عَسٰٓى اَلَّآ اَكُوْنَ بِدُعَاۤءِ رَبِّيْ شَقِيًّا ', 'wa-a\'tazilukum wam<u>aa</u> tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi wa-ad\'uu rabbii \'as<u>aa</u> <strong>al</strong>l<u>aa</u> akuuna bidu\'<u>aa</u>-i rabbii syaqiyy<u>aa</u><strong>n</strong>', ' Dan aku akan menjauhkan diri darimu dan dari apa yang engkau sembah selain Allah, dan aku akan berdoa kepada Tuhanku, mudah-mudahan aku tidak akan kecewa dengan berdoa kepada Tuhanku.”'),
	(2299, 19, 49, 'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۙوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ وَكُلًّا جَعَلْنَا نَبِيًّا ', 'falamm<u>aa</u> i\'tazalahum wam<u>aa</u> ya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi wahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba wakullan ja\'aln<u>aa</u> nabiyy<u>aa</u><strong>n</strong>', ' Maka ketika dia (Ibrahim) sudah menjauhkan diri dari mereka dan dari apa yang mereka sembah selain Allah, Kami anugerahkan kepadanya Ishak dan Yakub. Dan masing-masing Kami angkat menjadi nabi.'),
	(2300, 19, 50, 'وَوَهَبْنَا لَهُمْ مِّنْ رَّحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا ࣖ', 'wawahabn<u>aa</u> lahum min ra<u>h</u>matin<u>aa</u> waja\'aln<u>aa</u> lahum lis<u>aa</u>na <u>sh</u>idqin \'aliyy<u>aa</u><strong>n</strong>', 'Dan Kami anugerahkan kepada mereka sebagian dari rahmat Kami dan Kami jadikan mereka buah tutur yang baik dan mulia.'),
	(2301, 19, 51, 'وَاذْكُرْ فِى الْكِتٰبِ مُوْسٰٓىۖ اِنَّهٗ كَانَ مُخْلَصًا وَّكَانَ رَسُوْلًا نَّبِيًّا ', 'wa<strong>u</strong><u>dz</u>kur fii <strong>a</strong>lkit<u>aa</u>bi muus<u>aa</u> innahu k<u>aa</u>na mukhla<u>sh</u>an wak<u>aa</u>na rasuulan nabiyy<u>aa</u><strong>n</strong>', 'Dan ceritakanlah (Muhammad), kisah Musa di dalam Kitab (Al-Qur\'an). Dia benar-benar orang yang terpilih, seorang rasul dan nabi.'),
	(2302, 19, 52, 'وَنَادَيْنٰهُ مِنْ جَانِبِ الطُّوْرِ الْاَيْمَنِ وَقَرَّبْنٰهُ نَجِيًّا ', 'wan<u>aa</u>dayn<u>aa</u>hu min j<u>aa</u>nibi <strong>al</strong><u>ththh</u>uuri <strong>a</strong>l-aymani waqarrabn<u>aa</u>hu najiyy<u>aa</u><strong>n</strong>', 'Dan Kami telah memanggilnya dari sebelah kanan gunung (Sinai) dan Kami dekatkan dia untuk bercakap-cakap.'),
	(2303, 19, 53, 'وَوَهَبْنَا لَهٗ مِنْ رَّحْمَتِنَآ اَخَاهُ هٰرُوْنَ نَبِيًّا ', 'wawahabn<u>aa</u> lahu min ra<u>h</u>matin<u>aa</u> akh<u>aa</u>hu h<u>aa</u>ruuna nabiyy<u>aa</u><strong>n</strong>', ' Dan Kami telah menganugerahkan sebagian rahmat Kami kepadanya, yaitu (bahwa) saudaranya, Harun, menjadi seorang nabi.'),
	(2304, 19, 54, 'وَاذْكُرْ فِى الْكِتٰبِ اِسْمٰعِيْلَ ۖاِنَّهٗ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُوْلًا نَّبِيًّا ۚ', 'wa<strong>u</strong><u>dz</u>kur fii <strong>a</strong>lkit<u>aa</u>bi ism<u>aa</u>\'iila innahu k<u>aa</u>na <u>shaa</u>diqa <strong>a</strong>lwa\'di wak<u>aa</u>na rasuulan nabiyy<u>aa</u><strong>n</strong>', 'Dan ceritakanlah (Muhammad), kisah Ismail di dalam Kitab (Al-Qur\'an). Dia benar-benar seorang yang benar janjinya, seorang rasul dan nabi.'),
	(2305, 19, 55, 'وَكَانَ يَأْمُرُ اَهْلَهٗ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّهٖ مَرْضِيًّا ', 'wak<u>aa</u>na ya/muru ahlahu bi<strong>al</strong><u>shsh</u>al<u>aa</u>ti wa<strong>al</strong>zzak<u>aa</u>ti wak<u>aa</u>na \'inda rabbihi mar<u>dh</u>iyy<u>aa</u><strong>n</strong>', 'Dan dia menyuruh keluarganya untuk (melaksanakan) salat dan (menunaikan) zakat, dan dia seorang yang diridai di sisi Tuhannya.'),
	(2306, 19, 56, 'وَاذْكُرْ فِى الْكِتٰبِ اِدْرِيْسَۖ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا ۙ', 'wa<strong>u</strong><u>dz</u>kur fii <strong>a</strong>lkit<u>aa</u>bi idriisa innahu k<u>aa</u>na <u>sh</u>iddiiqan nabiyy<u>aa</u><strong>n</strong>', 'Dan ceritakanlah (Muhammad) kisah Idris di dalam Kitab (Al-Qur\'an). Sesungguhnya dia seorang yang sangat mencintai kebenaran dan seorang nabi,'),
	(2307, 19, 57, 'وَّرَفَعْنٰهُ مَكَانًا عَلِيًّا ', 'warafa\'n<u>aa</u>hu mak<u>aa</u>nan \'aliyy<u>aa</u><strong>n</strong>', 'dan Kami telah mengangkatnya ke martabat yang tinggi.'),
	(2308, 19, 58, 'اُولٰۤىِٕكَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوْحٍۖ وَّمِنْ ذُرِّيَّةِ اِبْرٰهِيْمَ وَاِسْرَاۤءِيْلَ ۖوَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۗ اِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُ الرَّحْمٰنِ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina an\'ama <strong>al</strong>l<u>aa</u>hu \'alayhim mina <strong>al</strong>nnabiyyiina min <u>dz</u>urriyyati <u>aa</u>dama wamimman <u>h</u>amaln<u>aa</u> ma\'a nuu<u>h</u>in wamin <u>dz</u>urriyyati ibr<u>aa</u>hiima wa-isr<u>aa</u>-iila wamimman hadayn<u>aa</u> wa<strong>i</strong>jtabayn<u>aa</u> i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tu <strong>al</strong>rra<u>h</u>m<u>aa</u>ni kharruu sujjadan wabukiyy<u>aa</u><strong>n</strong>', 'Mereka itulah orang yang telah diberi nikmat oleh Allah, yaitu dari (golongan) para nabi dari keturunan Adam, dan dari orang yang Kami bawa (dalam kapal) bersama Nuh, dan dari keturunan Ibrahim dan Israil (Yakub) dan dari orang yang telah Kami beri petunjuk dan telah Kami pilih. Apabila dibacakan ayat-ayat Allah Yang Maha Pengasih kepada mereka, maka mereka tunduk sujud dan menangis.'),
	(2309, 19, 59, '۞ فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوٰتِ فَسَوْفَ يَلْقَوْنَ غَيًّا ۙ', 'fakhalafa min ba\'dihim khalfun a<u>daa</u>\'uu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<strong>i</strong>ttaba\'uu <strong>al</strong>sysyahaw<u>aa</u>ti fasawfa yalqawna ghayy<u>aa</u><strong>n</strong>', 'Kemudian datanglah setelah mereka pengganti yang mengabaikan salat dan mengikuti keinginannya, maka mereka kelak akan tersesat,'),
	(2310, 19, 60, 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ شَيْـًٔا ۙ', 'ill<u>aa</u> man t<u>aa</u>ba wa<u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika yadkhuluuna <strong>a</strong>ljannata wal<u>aa</u> yu<u>zh</u>lamuuna syay-<u>aa</u><strong>n</strong>', 'Kecuali orang yang bertobat, beriman dan mengerjakan kebajikan, maka mereka itu akan masuk surga dan tidak dizalimi (dirugikan) sedikit pun,'),
	(2311, 19, 61, 'جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدَ الرَّحْمٰنُ عِبَادَهٗ بِالْغَيْبِۗ اِنَّهٗ كَانَ وَعْدُهٗ مَأْتِيًّا ', 'jann<u>aa</u>ti \'adnin <strong>al</strong>latii wa\'ada <strong>al</strong>rra<u>h</u>m<u>aa</u>nu \'ib<u>aa</u>dahu bi<strong>al</strong>ghaybi innahu k<u>aa</u>na wa\'duhu ma/tiyy<u>aa</u><strong>n</strong>', 'yaitu surga ‘Adn yang telah dijanjikan oleh Tuhan Yang Maha Pengasih kepada hamba-hamba-Nya, sekalipun (surga itu) tidak tampak. Sungguh, (janji Allah) itu pasti ditepati.'),
	(2312, 19, 62, 'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا اِلَّا سَلٰمًاۗ وَلَهُمْ رِزْقُهُمْ فِيْهَا بُكْرَةً وَّعَشِيًّا ', 'l<u>aa</u> yasma\'uuna fiih<u>aa</u> laghwan ill<u>aa</u> sal<u>aa</u>man walahum rizquhum fiih<u>aa</u> bukratan wa\'asyiyy<u>aa</u><strong>n</strong>', 'Di dalamnya mereka tidak mendengar perkataan yang tidak berguna, kecuali (ucapan) salam. Dan di dalamnya bagi mereka ada rezeki pagi dan petang.'),
	(2313, 19, 63, 'تِلْكَ الْجَنَّةُ الَّتِيْ نُوْرِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا ', 'tilka <strong>a</strong>ljannatu <strong>al</strong>latii nuuritsu min \'ib<u>aa</u>din<u>aa</u> man k<u>aa</u>na taqiyy<u>aa</u><strong>n</strong>', 'Itulah surga yang akan Kami wariskan kepada hamba-hamba Kami yang selalu bertakwa.'),
	(2314, 19, 64, 'وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ  لَهٗ مَا بَيْنَ اَيْدِيْنَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَ وَمَا كَانَ رَبُّكَ نَسِيًّا ۚ', 'wam<u>aa</u> natanazzalu ill<u>aa</u> bi-amri rabbika lahu m<u>aa</u> bayna aydiin<u>aa</u> wam<u>aa </u>khalfan<u>aa</u> wam<u>aa</u> bayna <u>dzaa</u>lika wam<u>aa</u> k<u>aa</u>na rabbuka nasiyy<u>aa</u><strong>n</strong>', ' Dan tidaklah kami (Jibril) turun, kecuali atas perintah Tuhanmu. Milik-Nya segala yang ada di hadapan kita, yang ada di belakang kita, dan segala yang ada di antara keduanya, dan Tuhanmu tidak lupa.'),
	(2315, 19, 65, 'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهٖۗ هَلْ تَعْلَمُ لَهٗ سَمِيًّا ࣖ', 'rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> fa<strong>u</strong>\'budhu wa<strong>i</strong><u>sth</u>abir li\'ib<u>aa</u>datihi hal ta\'lamu lahu samiyy<u>aa</u><strong>n</strong>', '(Dialah) Tuhan (yang menguasai) langit dan bumi dan segala yang ada di antara keduanya, maka sembahlah Dia dan berteguhhatilah dalam beribadah kepada-Nya. Apakah engkau mengetahui ada sesuatu yang sama dengan-Nya?'),
	(2316, 19, 66, 'وَيَقُوْلُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَيًّا ', 'wayaquulu <strong>a</strong>l-ins<u>aa</u>nu a-i<u>dzaa</u> m<u>aa</u> mittu lasawfa ukhraju <u>h</u>ayy<u>aa</u><strong>n</strong>', ' Dan orang (kafir) berkata, “Betulkah apabila aku telah mati, kelak aku sungguh-sungguh akan dibangkitkan hidup kembali?”'),
	(2317, 19, 67, 'اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـًٔا ', 'awa l<u>aa</u> ya<u>dz</u>kuru <strong>a</strong>l-ins<u>aa</u>nu ann<u>aa</u> khalaqn<u>aa</u>hu min qablu walam yaku syay-<u>aa</u><strong>n</strong>', 'Dan tidakkah manusia itu memikirkan bahwa sesungguhnya Kami telah menciptakannya dahulu, padahal (sebelumnya) dia belum berwujud sama sekali?'),
	(2318, 19, 68, 'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيٰطِيْنَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا ', 'fawarabbika lana<u>h</u>syurannahum wa<strong>al</strong>sysyay<u>aath</u>iina tsumma lanu<u>hd</u>irannahum <u>h</u>awla jahannama jitsiyy<u>aa</u><strong>n</strong>', 'Maka demi Tuhanmu, sungguh, pasti akan Kami kumpulkan mereka bersama setan, kemudian pasti akan Kami datangkan mereka ke sekeliling Jahanam dengan berlutut.'),
	(2319, 19, 69, 'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيْعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِيًّا ۚ', 'tsumma lananzi\'anna min kulli syii\'atin ayyuhum asyaddu \'al<u>aa</u> <strong>al</strong>rra<u>h</u>m<u>aa</u>ni \'itiyy<u>aa</u><strong>n</strong>', 'Kemudian pasti akan Kami tarik dari setiap golongan siapa di antara mereka yang sangat durhaka kepada Tuhan Yang Maha Pengasih.'),
	(2320, 19, 70, 'ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذِيْنَ هُمْ اَوْ لٰى بِهَا صِلِيًّا ', 'tsumma lana<u>h</u>nu a\'lamu bi<strong>a</strong>lla<u>dz</u>iina hum awl<u>aa</u> bih<u>aa</u> <u>sh</u>iliyy<u>aa</u><strong>n</strong>', 'Selanjutnya Kami sungguh lebih mengetahui orang yang seharusnya (dimasukkan) ke dalam neraka.'),
	(2321, 19, 71, 'وَاِنْ مِّنْكُمْ اِلَّا وَارِدُهَا ۚ كَانَ عَلٰى رَبِّكَ حَتْمًا مَّقْضِيًّا ۚ', 'wa-in minkum ill<u>aa</u> w<u>aa</u>riduh<u>aa</u> k<u>aa</u>na \'al<u>aa</u> rabbika <u>h</u>atman maq<u>dh</u>iyy<u>aa</u><strong>n</strong>', 'Dan tidak ada seorang pun di antara kamu yang tidak mendatanginya (neraka). Hal itu bagi Tuhanmu adalah suatu ketentuan yang sudah ditetapkan.'),
	(2322, 19, 72, 'ثُمَّ نُنَجِّى الَّذِيْنَ اتَّقَوْا وَّنَذَرُ الظّٰلِمِيْنَ فِيْهَا جِثِيًّا ', 'tsumma nunajjii <strong>al</strong>la<u>dz</u>iina ittaqaw wana<u>dz</u>aru <strong>al</strong><u>zhzhaa</u>limiina fiih<u>aa</u> jitsiyy<u>aa</u><strong>n</strong>', ' Kemudian Kami akan menyelamatkan orang-orang yang bertakwa dan membiarkan orang-orang yang zalim di dalam (neraka) dalam keadaan berlutut.'),
	(2323, 19, 73, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓاۙ اَيُّ الْفَرِيْقَيْنِ خَيْرٌ مَّقَامًا وَّاَحْسَنُ نَدِيًّا ', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lilla<u>dz</u>iina <u>aa</u>manuu ayyu <strong>a</strong>lfariiqayni khayrun maq<u>aa</u>man wa-a<u>h</u>sanu nadiyy<u>aa</u><strong>n</strong>', ' Dan apabila dibacakan kepada mereka ayat-ayat Kami yang jelas (maksudnya), orang-orang yang kafir berkata kepada orang-orang yang beriman, “Manakah di antara kedua golongan yang lebih baik tempat tinggalnya dan lebih indah tempat pertemuan(nya)?”'),
	(2324, 19, 74, 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثًا وَّرِءْيًا ', 'wakam ahlakn<u>aa</u> qablahum min qarnin hum a<u>h</u>sanu ats<u>aa</u>tsan wari/y<u>aa</u><strong>n</strong>', ' Dan berapa banyak umat (yang ingkar) yang telah Kami binasakan sebelum mereka, padahal mereka lebih bagus perkakas rumah tangganya dan (lebih sedap) dipandang mata.'),
	(2325, 19, 75, 'قُلْ مَنْ كَانَ فِى الضَّلٰلَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَدًّا ەۚ حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَ ۗفَسَيَعْلَمُوْنَ مَنْ هُوَ شَرٌّ مَّكَانًا وَّاَضْعَفُ جُنْدًا', 'qul man k<u>aa</u>na fii <strong>al</strong><u>dhdh</u>al<u>aa</u>lati falyamdud lahu <strong>al</strong>rra<u>h</u>m<u>aa</u>nu maddan <u>h</u>att<u>aa</u> i<u>dzaa</u> ra-aw m<u>aa</u> yuu\'aduuna imm<u>aa</u> <strong>a</strong>l\'a<u>dzaa</u>ba wa-imm<u>aa</u> <strong>al</strong>ss<u>aa</u>\'ata fasaya\'lamuuna man huwa syarrun mak<u>aa</u>nan wa-a<u>dh</u>\'afu jund<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Barangsiapa berada dalam kesesatan, maka biarlah Tuhan Yang Maha Pengasih memperpanjang (waktu) baginya; sehingga apabila mereka telah melihat apa yang diancamkan kepada mereka, baik azab maupun Kiamat, maka mereka akan mengetahui siapa yang lebih jelek kedudukannya dan lebih lemah bala tentaranya.”'),
	(2326, 19, 76, 'وَيَزِيْدُ اللّٰهُ الَّذِيْنَ اهْتَدَوْا هُدًىۗ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ مَّرَدًّا ', 'wayaziidu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina ihtadaw hudan wa<strong>a</strong>lb<u>aa</u>qiy<u>aa</u>tu <strong>al</strong><u>shshaa</u>li<u>haa</u>tu khayrun \'inda rabbika tsaw<u>aa</u>ban wakhayrun maradd<u>aa</u><strong>n</strong>', 'Dan Allah akan menambah petunjuk kepada mereka yang telah mendapat petunjuk. Dan amal kebajikan yang kekal itu lebih baik pahalanya di sisi Tuhanmu dan lebih baik kesudahannya.'),
	(2327, 19, 77, 'اَفَرَاَيْتَ الَّذِيْ كَفَرَ بِاٰيٰتِنَا وَقَالَ لَاُوْتَيَنَّ مَالًا وَّوَلَدًا ۗ', 'afara-ayta <strong>al</strong>la<u>dz</u>ii kafara bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waq<u>aa</u>la lauutayanna m<u>aa</u>lan wawalad<u>aa</u><strong>n</strong>', 'Lalu apakah engkau telah melihat orang yang mengingkari ayat-ayat Kami dan dia mengatakan, “Pasti aku akan diberi harta dan anak.”'),
	(2328, 19, 78, 'اَطَّلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۙ', 'a<u>ththh</u>ala\'a <strong>a</strong>lghayba ami ittakha<u>dz</u>a \'inda <strong>al</strong>rra<u>h</u>m<u>aa</u>ni \'ahd<u>aa</u><strong>n</strong>', 'Adakah dia melihat yang gaib atau dia telah membuat perjanjian di sisi Tuhan Yang Maha Pengasih?'),
	(2329, 19, 79, 'كَلَّاۗ سَنَكْتُبُ مَا يَقُوْلُ وَنَمُدُّ لَهٗ مِنَ الْعَذَابِ مَدًّا ۙ', 'kall<u>aa</u> sanaktubu m<u>aa</u> yaquulu wanamuddu lahu mina <strong>a</strong>l\'a<u>dzaa</u>bi madd<u>aa</u><strong>n</strong>', 'Sama sekali tidak! Kami akan menulis apa yang dia katakan, dan Kami akan memperpanjang azab untuknya secara sempurna,'),
	(2330, 19, 80, 'وَّنَرِثُهٗ مَا يَقُوْلُ وَيَأْتِيْنَا فَرْدًا ', 'wanaritsuhu m<u>aa</u> yaquulu waya/tiin<u>aa</u> fard<u>aa</u><strong>n</strong>', 'dan Kami akan mewarisi apa yang dia katakan itu, dan dia akan datang kepada Kami seorang diri.'),
	(2331, 19, 81, 'وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لِّيَكُوْنُوْا لَهُمْ عِزًّا ۙ', 'wa<strong>i</strong>ttakha<u>dz</u>uu min duuni <strong>al</strong>l<u>aa</u>hi <u>aa</u>lihatan liyakuunuu lahum \'izz<u>aa</u><strong>n</strong>', 'Dan mereka telah memilih tuhan-tuhan selain Allah, agar tuhan-tuhan itu menjadi pelindung bagi mereka.'),
	(2332, 19, 82, 'كَلَّا ۗسَيَكْفُرُوْنَ بِعِبَادَتِهِمْ وَيَكُوْنُوْنَ عَلَيْهِمْ ضِدًّا ࣖ', 'kall<u>aa</u> sayakfuruuna bi\'ib<u>aa</u>datihim wayakuunuuna \'alayhim <u>dh</u>idd<u>aa</u><strong>n</strong>', 'Sama sekali tidak! Kelak mereka (sesembahan) itu akan mengingkari penyembahan mereka terhadapnya, dan akan menjadi musuh bagi mereka.'),
	(2333, 19, 83, 'اَلَمْ تَرَ اَنَّآ اَرْسَلْنَا الشَّيٰطِيْنَ عَلَى الْكٰفِرِيْنَ تَؤُزُّهُمْ اَزًّا ۙ', 'alam tara ann<u>aa</u> arsaln<u>aa</u> <strong>al</strong>sysyay<u>aath</u>iina \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiina tauzzuhum azz<u>aa</u><strong>n</strong>', 'Tidakkah engkau melihat, bahwa sesungguhnya Kami telah mengutus setan-setan itu kepada orang-orang kafir untuk mendorong mereka (berbuat maksiat) dengan sungguh-sungguh?'),
	(2334, 19, 84, 'فَلَا تَعْجَلْ عَلَيْهِمْۗ اِنَّمَا نَعُدُّ لَهُمْ عَدًّا ۗ', 'fal<u>aa</u> ta\'jal \'alayhim innam<u>aa</u> na\'uddu lahum \'add<u>aa</u><strong>n</strong>', 'Maka janganlah engkau (Muhammad) tergesa-gesa (memintakan azab) terhadap mereka, karena Kami menghitung dengan hitungan teliti (datangnya hari siksaan) untuk mereka.'),
	(2335, 19, 85, 'يَوْمَ نَحْشُرُ الْمُتَّقِيْنَ اِلَى الرَّحْمٰنِ وَفْدًا ', 'yawma na<u>h</u>syuru <strong>a</strong>lmuttaqiina il<u>aa</u> <strong>al</strong>rra<u>h</u>m<u>aa</u>ni wafd<u>aa</u><strong>n</strong>', '(Ingatlah) pada hari (ketika) Kami mengumpulkan orang-orang yang bertakwa kepada (Allah) Yang Maha Pengasih, bagaikan kafilah yang terhormat,'),
	(2336, 19, 86, 'وَنَسُوْقُ الْمُجْرِمِيْنَ اِلٰى جَهَنَّمَ وِرْدًا ۘ', 'wanasuuqu <strong>a</strong>lmujrimiina il<u>aa</u> jahannama wird<u>aa</u><strong>n</strong>', 'dan Kami akan menggiring orang yang durhaka ke neraka Jahanam dalam keadaan dahaga.'),
	(2337, 19, 87, 'لَا يَمْلِكُوْنَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۘ', 'l<u>aa</u> yamlikuuna <strong>al</strong>sysyaf<u>aa</u>\'ata ill<u>aa</u> mani ittakha<u>dz</u>a \'inda <strong>al</strong>rra<u>h</u>m<u>aa</u>ni \'ahd<u>aa</u><strong>n</strong>', ' Mereka tidak berhak mendapat syafaat, (pertolongan) kecuali orang yang telah mengadakan perjanjian di sisi (Allah) Yang Maha Pengasih.'),
	(2338, 19, 88, 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا ۗ', 'waq<u>aa</u>luu ittakha<u>dz</u>a <strong>al</strong>rra<u>h</u>m<u>aa</u>nu walad<u>aa</u><strong>n</strong>', 'Dan mereka berkata, “(Allah) Yang Maha Pengasih mempunyai anak.”'),
	(2339, 19, 89, 'لَقَدْ جِئْتُمْ شَيْـًٔا اِدًّا ۙ', 'laqad ji/tum syay-an idd<u>aa</u><strong>n</strong>', 'Sungguh, kamu telah membawa sesuatu yang sangat mungkar,'),
	(2340, 19, 90, 'تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا ۙ', 'tak<u>aa</u>du <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu yatafa<u>ththh</u>arna minhu watansyaqqu <strong>a</strong>l-ar<u>dh</u>u watakhirru <strong>a</strong>ljib<u>aa</u>lu hadd<u>aa</u><strong>n</strong>', 'hampir saja langit pecah, dan bumi terbelah, dan gunung-gunung runtuh, (karena ucapan itu),'),
	(2341, 19, 91, 'اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَدًا ۚ', 'an da\'aw li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni walad<u>aa</u><strong>n</strong>', 'karena mereka menganggap (Allah) Yang Maha Pengasih mempunyai anak.'),
	(2342, 19, 92, 'وَمَا يَنْۢبَغِيْ لِلرَّحْمٰنِ اَنْ يَّتَّخِذَ وَلَدًا ۗ', 'wam<u>aa</u> yanbaghii li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni an yattakhi<u>dz</u>a walad<u>aa</u><strong>n</strong>', 'Dan tidak mungkin bagi (Allah) Yang Maha Pengasih mempunyai anak.'),
	(2343, 19, 93, 'اِنْ كُلُّ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ اِلَّآ اٰتِى الرَّحْمٰنِ عَبْدًا ۗ', 'in kullu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> <u>aa</u>tii <strong>al</strong>rra<u>h</u>m<u>aa</u>ni \'abd<u>aa</u><strong>n</strong>', 'Tidak ada seorang pun di langit dan di bumi, melainkan akan datang kepada (Allah) Yang Maha Pengasih sebagai seorang hamba.'),
	(2344, 19, 94, 'لَقَدْ اَحْصٰىهُمْ وَعَدَّهُمْ عَدًّا ۗ', 'laqad a<u>hsaa</u>hum wa\'addahum \'add<u>aa</u><strong>n</strong>', 'Dia (Allah) benar-benar telah menentukan jumlah mereka dan menghitung mereka dengan hitungan yang teliti.'),
	(2345, 19, 95, 'وَكُلُّهُمْ اٰتِيْهِ يَوْمَ الْقِيٰمَةِ فَرْدًا ', 'wakulluhum <u>aa</u>tiihi yawma <strong>a</strong>lqiy<u>aa</u>mati fard<u>aa</u><strong>n</strong>', 'Dan setiap orang dari mereka akan datang kepada Allah sendiri-sendiri pada hari Kiamat.'),
	(2346, 19, 96, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُدًّا ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti sayaj\'alu lahumu <strong>al</strong>rra<u>h</u>m<u>aa</u>nu wudd<u>aa</u><strong>n</strong>', 'Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, kelak (Allah) Yang Maha Pengasih akan menanamkan rasa kasih sayang (dalam hati mereka).'),
	(2347, 19, 97, 'فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِيْنَ وَتُنْذِرَ بِهٖ قَوْمًا لُّدًّا ', 'fa-innam<u>aa</u> yassarn<u>aa</u>hu bilis<u>aa</u>nika litubasysyira bihi <strong>a</strong>lmuttaqiina watun<u>dz</u>ira bihi qawman ludd<u>aa</u><strong>n</strong>', 'Maka sungguh, telah Kami mudahkan (Al-Qur\'an) itu dengan bahasamu (Muhammad), agar dengan itu engkau dapat memberi kabar gembira kepada orang-orang yang bertakwa, dan agar engkau dapat memberi peringatan kepada kaum yang membangkang.'),
	(2348, 19, 98, 'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍۗ هَلْ تُحِسُّ مِنْهُمْ مِّنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزًا ࣖ', 'wakam ahlakn<u>aa</u> qablahum min qarnin hal tu<u>h</u>issu minhum min a<u>h</u>adin aw tasma\'u lahum rikz<u>aa</u><strong>n</strong>', 'Dan berapa banyak umat yang telah Kami binasakan sebelum mereka. Adakah engkau (Muhammad) melihat salah seorang dari mereka atau engkau mendengar bisikan mereka?'),
	(2349, 20, 1, 'طٰهٰ ۚ', '<u>th</u><u>aa</u>-h<u>aa</u>', 'Thaha'),
	(2350, 20, 2, 'مَآ اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰٓى ۙ', 'm<u>aa</u> anzaln<u>aa</u> \'alayka <strong>a</strong>lqur-<u>aa</u>na litasyq<u>aa</u>', ' Kami tidak menurunkan Al-Qur\'an ini kepadamu (Muhammad) agar engkau menjadi susah;'),
	(2351, 20, 3, 'اِلَّا تَذْكِرَةً لِّمَنْ يَّخْشٰى ۙ', 'ill<u>aa</u> ta<u>dz</u>kiratan liman yakhsy<u>aa</u>', ' melainkan sebagai peringatan bagi orang yang takut (kepada Allah),'),
	(2352, 20, 4, 'تَنْزِيْلًا مِّمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوٰتِ الْعُلٰى ۗ', 'tanziilan mimman khalaqa <strong>a</strong>l-ar<u>dh</u>a wa<strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti <strong>a</strong>l\'ul<u>aa</u>', 'diturunkan dari (Allah) yang menciptakan bumi dan langit yang tinggi,'),
	(2353, 20, 5, 'اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى ', 'a<strong>l</strong>rra<u>h</u>m<u>aa</u>nu \'al<u>aa</u> <strong>a</strong>l\'arsyi istaw<u>aa</u>', '(yaitu) Yang Maha Pengasih, yang bersemayam di atas ‘Arsy.'),
	(2354, 20, 6, 'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى ', 'lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> wam<u>aa</u> ta<u>h</u>ta <strong>al</strong>tstsar<u>aa</u>', 'Milik-Nyalah apa yang ada di langit, apa yang ada di bumi, apa yang ada di antara keduanya, dan apa yang ada di bawah tanah.'),
	(2355, 20, 7, 'وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهٗ يَعْلَمُ السِّرَّ وَاَخْفٰى ', 'wa-in tajhar bi<strong>a</strong>lqawli fa-innahu ya\'lamu <strong>al</strong>ssirra wa-akhf<u>aa</u>', 'Dan jika engkau mengeraskan ucapanmu, sungguh, Dia mengetahui rahasia dan yang lebih tersembunyi.'),
	(2356, 20, 8, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ  لَهُ الْاَسْمَاۤءُ الْحُسْنٰى ', '<strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa lahu <strong>a</strong>l-asm<u>aa</u>u <strong>a</strong>l<u>h</u>usn<u>aa</u>', '(Dialah) Allah, tidak ada tuhan selain Dia, yang mempunyai nama-nama yang terbaik.'),
	(2357, 20, 9, 'وَهَلْ اَتٰىكَ حَدِيْثُ مُوْسٰى ۘ', 'wahal at<u>aa</u>ka <u>h</u>adiitsu muus<u>aa</u>', ' Dan apakah telah sampai kepadamu kisah Musa?'),
	(2358, 20, 10, 'اِذْ رَاٰ نَارًا فَقَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى ', 'i<u>dz</u> ra<u>aa</u> n<u>aa</u>ran faq<u>aa</u>la li-ahlihi umkutsuu innii <u>aa</u>nastu n<u>aa</u>ran la\'allii <u>aa</u>tiikum minh<u>aa</u> biqabasin aw ajidu \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>ri hud<u>aa</u><strong>n</strong>', ' Ketika dia (Musa) melihat api, lalu dia berkata kepada keluarganya, “Tinggallah kamu (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa sedikit nyala api kepadamu atau aku akan mendapat petunjuk di tempat api itu.”'),
	(2359, 20, 11, 'فَلَمَّآ اَتٰىهَا نُوْدِيَ يٰمُوْسٰٓى ۙ', 'falamm<u>aa</u> at<u>aa</u>h<u>aa</u> nuudiya y<u>aa</u> muus<u>aa</u>', 'Maka ketika dia mendatanginya (ke tempat api itu) dia dipanggil, “Wahai Musa!'),
	(2360, 20, 12, 'اِنِّيْٓ اَنَا۠ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى ۗ', 'innii an<u>aa</u> rabbuka fa<strong>i</strong>khla\' na\'layka innaka bi<strong>a</strong>lw<u>aa</u>di <strong>a</strong>lmuqaddasi <u>th</u>uw<u>aa</u><strong>n</strong>', 'Sungguh, Aku adalah Tuhanmu, maka lepaskan kedua terompahmu. Karena sesungguhnya engkau berada di lembah yang suci, Tuwa.'),
	(2361, 20, 13, 'وَاَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوْحٰى ', 'wa-an<u>aa</u> ikhtartuka fa<strong>i</strong>stami\' lim<u>aa</u> yuu<u>haa</u>', ' Dan Aku telah memilih engkau, maka dengarkanlah apa yang akan diwahyukan (kepadamu).'),
	(2362, 20, 14, 'اِنَّنِيْٓ اَنَا اللّٰهُ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدْنِيْۙ وَاَقِمِ الصَّلٰوةَ لِذِكْرِيْ ', 'innanii an<u>aa</u> <strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> an<u>aa</u> fa<strong>u</strong>\'budnii wa-aqimi <strong>al</strong><u>shsh</u>al<u>aa</u>ta li<u>dz</u>ikrii', ' Sungguh, Aku ini Allah, tidak ada tuhan selain Aku, maka sembahlah Aku dan laksanakanlah salat untuk mengingat Aku.'),
	(2363, 20, 15, 'اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْفِيْهَا لِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا تَسْعٰى ', 'inna <strong>al</strong>ss<u>aa</u>\'ata <u>aa</u>tiyatun ak<u>aa</u>du ukhfiih<u>aa</u> litujz<u>aa</u> kullu nafsin bim<u>aa</u> tas\'<u>aa</u>', 'Sungguh, hari Kiamat itu akan datang, Aku merahasiakan (waktunya) agar setiap orang dibalas sesuai dengan apa yang telah dia usahakan.'),
	(2364, 20, 16, 'فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَّا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوٰىهُ فَتَرْدٰى ', 'fal<u>aa</u> ya<u>sh</u>uddannaka \'anh<u>aa</u> man l<u>aa</u> yu/minu bih<u>aa</u> wa<strong>i</strong>ttaba\'a haw<u>aa</u>hu fatard<u>aa</u>', ' Maka janganlah engkau dipalingkan dari (Kiamat itu) oleh orang yang tidak beriman kepadanya dan oleh orang yang mengikuti keinginannya, yang menyebabkan engkau binasa.”'),
	(2365, 20, 17, 'وَمَا تِلْكَ بِيَمِيْنِكَ يٰمُوْسٰى ', 'wam<u>aa</u> tilka biyamiinika y<u>aa</u> muus<u>aa</u>', '”Dan apakah yang ada di tangan kananmu, wahai Musa? ”'),
	(2366, 20, 18, 'قَالَ هِيَ عَصَايَۚ اَتَوَكَّؤُا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَمِيْ وَلِيَ فِيْهَا مَاٰرِبُ اُخْرٰى ', 'q<u>aa</u>la hiya \'a<u>shaa</u>ya atawakkau \'alayh<u>aa</u> wa-ahusysyu bih<u>aa</u> \'al<u>aa</u> ghanamii waliya fiih<u>aa</u> ma<u>aa</u>ribu ukhr<u>aa</u>', 'Dia (Musa) berkata, “Ini adalah tongkatku, aku bertumpu padanya, dan aku merontokkan (daun-daun) dengannya untuk (makanan) kambingku, dan bagiku masih ada lagi manfaat yang lain.”'),
	(2367, 20, 19, 'قَالَ اَلْقِهَا يٰمُوْسٰى ', 'q<u>aa</u>la <strong>a</strong>lqih<u>aa</u> y<u>aa</u> muus<u>aa</u>', 'Dia (Allah) berfirman, “Lemparkanlah ia, wahai Musa!”'),
	(2368, 20, 20, 'فَاَلْقٰىهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى ', 'fa-alq<u>aa</u>h<u>aa</u> fa-i<u>dzaa</u> hiya <u>h</u>ayyatun tas\'<u>aa</u>', ' Lalu (Musa) melemparkan tongkat itu, maka tiba-tiba ia menjadi seekor ular yang merayap dengan cepat.'),
	(2369, 20, 21, 'قَالَ خُذْهَا وَلَا تَخَفْۗ سَنُعِيْدُهَا سِيْرَتَهَا الْاُوْلٰى ', 'q<u>aa</u>la khu<u>dz</u>h<u>aa</u> wal<u>aa</u> takhaf sanu\'iiduh<u>aa</u> siiratah<u>aa</u> <strong>a</strong>l-uul<u>aa</u>', 'Dia (Allah) berfirman, “Peganglah ia dan jangan takut, Kami akan mengembalikannya kepada keadaannya semula,'),
	(2370, 20, 22, 'وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ اٰيَةً اُخْرٰىۙ ', 'wa<strong>u</strong><u>dh</u>mum yadaka il<u>aa</u> jan<u>aah</u>ika takhruj bay<u>dhaa</u>-a min ghayri suu-in <u>aa</u>yatan ukhr<u>aa</u>', 'dan kepitlah tanganmu ke ketiakmu, niscaya ia keluar menjadi putih (bercahaya) tanpa cacat, sebagai mukjizat yang lain,'),
	(2371, 20, 23, 'لِنُرِيَكَ مِنْ اٰيٰتِنَا الْكُبْرٰى ۚ', 'linuriyaka min <u>aa</u>y<u>aa</u>tin<u>aa</u> <strong>a</strong>lkubr<u>aa</u>', 'untuk Kami perlihatkan kepadamu (sebagian) dari tanda-tanda kebesaran Kami yang sangat besar,'),
	(2372, 20, 24, 'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰى ࣖ', 'i<u>dz</u>hab il<u>aa</u> fir\'awna innahu <u>th</u>agh<u>aa</u>', 'Pergilah kepada Fir‘aun; dia benar-benar telah melampaui batas.”'),
	(2373, 20, 25, 'قَالَ رَبِّ اشْرَحْ لِيْ صَدْرِيْ ۙ', 'q<u>aa</u>la rabbi isyra<u>h</u> lii <u>sh</u>adrii', ' Dia (Musa) berkata, “Ya Tuhanku, lapangkanlah dadaku,'),
	(2374, 20, 26, 'وَيَسِّرْ لِيْٓ اَمْرِيْ ۙ', 'wayassir lii amrii', 'dan mudahkanlah untukku urusanku, '),
	(2375, 20, 27, 'وَاحْلُلْ عُقْدَةً مِّنْ لِّسَانِيْ ۙ', 'wa<strong>u</strong><u>h</u>lul \'uqdatan min lis<u>aa</u>nii', 'dan lepaskanlah kekakuan dari lidahku,'),
	(2376, 20, 28, 'يَفْقَهُوْا قَوْلِيْ  ۖ', 'yafqahuu qawlii', 'agar mereka mengerti perkataanku,'),
	(2377, 20, 29, 'وَاجْعَلْ لِّيْ وَزِيْرًا مِّنْ اَهْلِيْ ۙ', 'yafqahuu qawlii', 'dan jadikanlah untukku seorang pembantu dari keluargaku,'),
	(2378, 20, 30, 'هٰرُوْنَ اَخِى ۙ', 'h<u>aa</u>ruuna akhii', '(yaitu) Harun, saudaraku,'),
	(2379, 20, 31, 'اشْدُدْ بِهٖٓ اَزْرِيْ ۙ', 'usydud bihi azrii', 'teguhkanlah kekuatanku dengan (adanya) dia,'),
	(2380, 20, 32, 'وَاَشْرِكْهُ فِيْٓ اَمْرِيْ ۙ', 'wa-asyrik-hu fii amrii', 'dan jadikanlah dia teman dalam urusanku,'),
	(2381, 20, 33, 'كَيْ نُسَبِّحَكَ كَثِيْرًا ۙ', 'kay nusabbi<u>h</u>aka katsiir<u>aa</u><strong>n</strong>', 'agar kami banyak bertasbih kepada-Mu,'),
	(2382, 20, 34, 'وَّنَذْكُرَكَ كَثِيْرًا ۗ', 'wana<u>dz</u>kuraka katsiir<u>aa</u><strong>n</strong>', 'dan banyak mengingat-Mu,'),
	(2383, 20, 35, 'اِنَّكَ كُنْتَ بِنَا بَصِيْرًا ', 'innaka kunta bin<u>aa</u> ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'sesungguhnya Engkau Maha Melihat (keadaan) kami.”'),
	(2384, 20, 36, 'قَالَ قَدْ اُوْتِيْتَ سُؤْلَكَ يٰمُوْسٰى ', 'q<u>aa</u>la qad uutiita su/laka y<u>aa</u> muus<u>aa</u>', 'Dia (Allah) berfirman, “Sungguh, telah diperkenankan permintaanmu, wahai Musa!'),
	(2385, 20, 37, 'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىٓ ۙ', 'walaqad manann<u>aa</u> \'alayka marratan ukhr<u>aa</u>', 'Dan sungguh, Kami telah memberi nikmat kepadamu pada kesempatan yang lain (sebelum ini),'),
	(2386, 20, 38, 'اِذْ اَوْحَيْنَآ اِلٰٓى اُمِّكَ مَا يُوْحٰىٓ ۙ', 'i<u>dz</u> aw<u>h</u>ayn<u>aa</u> il<u>aa</u> ummika m<u>aa</u> yuu<u>haa</u>', '(yaitu) ketika Kami mengilhamkan kepada ibumu sesuatu yang diilhamkan,'),
	(2387, 20, 39, 'اَنِ اقْذِفِيْهِ فِى التَّابُوْتِ فَاقْذِفِيْهِ فِى الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِّيْ وَعَدُوٌّ لَّهٗ  ۗوَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِّنِّيْ ەۚ وَلِتُصْنَعَ عَلٰى عَيْنِيْ ۘ', 'ani iq<u>dz</u>ifiihi fii <strong>al</strong>tt<u>aa</u>buuti fa<strong>i</strong>q<u>dz</u>ifiihi fii <strong>a</strong>lyammi falyulqihi <strong>a</strong>lyammu bi<strong>al</strong>ss<u>aah</u>ili ya/khu<u>dz</u>hu \'aduwwun lii wa\'aduwwun lahu wa-alqaytu \'alayka ma<u>h</u>abbatan minnii walitu<u>sh</u>na\'a \'al<u>aa</u> \'aynii', '(yaitu), letakkanlah dia (Musa) di dalam peti, kemudian hanyutkanlah dia ke sungai (Nil), maka biarlah (arus) sungai itu membawanya ke tepi, dia akan diambil oleh (Fir‘aun) musuh-Ku dan musuhnya. Aku telah melimpahkan kepadamu kasih sayang yang datang dari-Ku; dan agar engkau diasuh di bawah pengawasan-Ku.'),
	(2388, 20, 40, 'اِذْ تَمْشِيْٓ اُخْتُكَ فَتَقُوْلُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَّكْفُلُهٗ ۗفَرَجَعْنٰكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ەۗ وَقَتَلْتَ نَفْسًا فَنَجَّيْنٰكَ مِنَ الْغَمِّ وَفَتَنّٰكَ فُتُوْنًا ەۗ فَلَبِثْتَ سِنِيْنَ فِيْٓ اَهْلِ مَدْي', 'i<u>dz</u> tamsyii ukhtuka fataquulu hal adullukum \'al<u>aa</u> man yakfuluhu faraja\'n<u>aa</u>ka il<u>aa</u> ummika kay taqarra \'aynuh<u>aa</u> wal<u>aa</u> ta<u>h</u>zana waqatalta nafsan fanajjayn<u>aa</u>ka mina <strong>a</strong>lghammi wafatann<u>aa</u>ka futuunan falabitsta siniina fii ahli madyana tsumma ji/ta \'al<u>aa</u> qadarin y<u>aa</u> muus<u>aa</u>', '(Yaitu) ketika saudara perempuanmu berjalan, lalu dia berkata (kepada keluarga Fir‘aun), ‘Bolehkah saya menunjukkan kepadamu orang yang akan memeliharanya?’ Maka Kami mengembalikanmu kepada ibumu, agar senang hatinya dan tidak bersedih hati. Dan engkau pernah membunuh seseorang, lalu Kami selamatkan engkau dari kesulitan (yang besar) dan Kami telah mencobamu dengan beberapa cobaan (yang berat); lalu engkau tinggal beberapa tahun di antara penduduk Madyan, kemudian engkau, wahai Musa, datang menurut waktu yang ditetapkan,'),
	(2389, 20, 41, 'وَاصْطَنَعْتُكَ لِنَفْسِيْۚ  ', 'wa<strong>i</strong><u>sth</u>ana\'tuka linafsii', 'dan Aku telah memilihmu (menjadi rasul) untuk diri-Ku.'),
	(2390, 20, 42, 'اِذْهَبْ اَنْتَ وَاَخُوْكَ بِاٰيٰتِيْ وَلَا تَنِيَا فِيْ ذِكْرِيْۚ  ', 'i<u>dz</u>hab anta wa-akhuuka bi-<u>aa</u>y<u>aa</u>tii wal<u>aa</u> taniy<u>aa</u> fii <u>dz</u>ikrii', 'Pergilah engkau beserta saudaramu dengan membawa tanda-tanda (kekuasaan)-Ku, dan janganlah kamu berdua lalai mengingat-Ku;'),
	(2391, 20, 43, 'اِذْهَبَآ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۚ ', 'i<u>dz</u>hab<u>aa</u> il<u>aa</u> fir\'awna innahu <u>th</u>agh<u>aa</u>', 'pergilah kamu berdua kepada Fir‘aun, karena dia benar-benar telah melampaui batas;'),
	(2392, 20, 44, 'فَقُوْلَا لَهٗ قَوْلًا لَّيِّنًا لَّعَلَّهٗ يَتَذَكَّرُ اَوْ يَخْشٰى ', 'faquul<u>aa</u> lahu qawlan layyinan la\'allahu yata<u>dz</u>akkaru aw yakhsy<u>aa</u>', 'maka berbicaralah kamu berdua kepadanya (Fir‘aun) dengan kata-kata yang lemah lembut, mudah-mudahan dia sadar atau takut.'),
	(2393, 20, 45, 'قَالَا رَبَّنَآ اِنَّنَا نَخَافُ اَنْ يَّفْرُطَ عَلَيْنَآ اَوْ اَنْ يَّطْغٰى ', 'q<u>aa</u>l<u>aa</u> rabban<u>aa</u> innan<u>aa</u> nakh<u>aa</u>fu an yafru<u>th</u>a \'alayn<u>aa</u> aw an ya<u>th</u>gh<u>aa</u>', 'Keduanya berkata, “Ya Tuhan kami, sungguh, kami khawatir dia akan segera menyiksa kami atau akan bertambah melampaui batas,”'),
	(2394, 20, 46, 'قَالَ لَا تَخَافَآ اِنَّنِيْ مَعَكُمَآ اَسْمَعُ وَاَرٰى ', 'q<u>aa</u>la l<u>aa</u> takh<u>aa</u>f<u>aa</u> innanii ma\'akum<u>aa</u> asma\'u wa-ar<u>aa</u>', 'Dia (Allah) berfirman, “Janganlah kamu berdua khawatir, sesungguhnya Aku bersama kamu berdua, Aku mendengar dan melihat.'),
	(2395, 20, 47, 'فَأْتِيٰهُ فَقُوْلَآ اِنَّا رَسُوْلَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ وَلَا تُعَذِّبْهُمْۗ قَدْ جِئْنٰكَ بِاٰيَةٍ مِّنْ رَّبِّكَ ۗوَالسَّلٰمُ عَلٰى مَنِ اتَّبَعَ الْهُدٰى', 'fa/tiy<u>aa</u>hu faquul<u>aa</u> inn<u>aa</u> rasuul<u>aa</u> rabbika fa-arsil ma\'an<u>aa</u> banii isr<u>aa</u>-iila wal<u>aa</u> tu\'a<u>dzdz</u>ibhum qad ji/n<u>aa</u>ka bi-<u>aa</u>yatin min rabbika wa<strong>al</strong>ssal<u>aa</u>mu \'al<u>aa</u> mani ittaba\'a <strong>a</strong>lhud<u>aa</u>', 'Maka pergilah kamu berdua kepadanya (Fir‘aun) dan katakanlah, “Sungguh, kami berdua adalah utusan Tuhanmu, maka lepaskanlah Bani Israil bersama kami dan janganlah engkau menyiksa mereka. Sungguh, kami datang kepadamu dengan membawa bukti (atas kerasulan kami) dari Tuhanmu. Dan keselamatan itu dilimpahkan kepada orang yang mengikuti petunjuk.'),
	(2396, 20, 48, 'اِنَّا قَدْ اُوْحِيَ اِلَيْنَآ اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى ', 'inn<u>aa</u> qad uu<u>h</u>iya ilayn<u>aa</u> anna <strong>a</strong>l\'a<u>dzaa</u>ba \'al<u>aa</u> man ka<u>dzdz</u>aba watawall<u>aa</u>', 'Sungguh, telah diwahyukan kepada kami bahwa siksa itu (ditimpakan) pada siapa pun yang mendustakan (ajaran agama yang kami bawa) dan berpaling (tidak mempedulikannya).”'),
	(2397, 20, 49, 'قَالَ فَمَنْ رَّبُّكُمَا يٰمُوْسٰى ', 'q<u>aa</u>la faman rabbukum<u>aa</u> y<u>aa</u> muus<u>aa</u>', 'Dia (Fir‘aun) berkata, “Siapakah Tuhanmu berdua, wahai Musa?”'),
	(2398, 20, 50, 'قَالَ رَبُّنَا الَّذِيْٓ اَعْطٰى كُلَّ شَيْءٍ خَلْقَهٗ ثُمَّ هَدٰى ', 'q<u>aa</u>la rabbun<u>aa</u> <strong>al</strong>la<u>dz</u>ii a\'<u>th</u><u>aa</u> kulla syay-in khalqahu tsumma had<u>aa</u>', 'Dia (Musa) menjawab, “Tuhan kami ialah (Tuhan) yang telah memberikan bentuk kejadian kepada segala sesuatu, kemudian memberinya petunjuk.”'),
	(2399, 20, 51, 'قَالَ فَمَا بَالُ الْقُرُوْنِ الْاُوْلٰى ', 'q<u>aa</u>la fam<u>aa</u> b<u>aa</u>lu <strong>a</strong>lquruuni <strong>a</strong>l-uul<u>aa</u>', 'Dia (Fir‘aun) berkata, “Jadi bagaimana keadaan umat-umat yang dahulu?”'),
	(2400, 20, 52, 'قَالَ عِلْمُهَا عِنْدَ رَبِّيْ فِيْ كِتٰبٍۚ  لَا يَضِلُّ رَبِّيْ وَلَا يَنْسَىۖ ', 'q<u>aa</u>la \'ilmuh<u>aa</u> \'inda rabbii fii kit<u>aa</u>bin l<u>aa</u> ya<u>dh</u>illu rabbii wal<u>aa</u> yans<u>aa</u>', 'Dia (Musa) menjawab, “Pengetahuan tentang itu ada pada Tuhanku, di dalam sebuah Kitab (Lauh Mahfuzh), Tuhanku tidak akan salah ataupun lupa;'),
	(2401, 20, 53, 'الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّسَلَكَ لَكُمْ فِيْهَا سُبُلًا وَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۗ فَاَخْرَجْنَا بِهٖٓ اَزْوَاجًا مِّنْ نَّبَاتٍ شَتّٰى ', '<strong>al</strong>la<u>dz</u>ii ja\'ala lakumu <strong>a</strong>l-ar<u>dh</u>a mahdan wasalaka lakum fiih<u>aa</u> subulan wa-anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi azw<u>aa</u>jan min nab<u>aa</u>tin syatt<u>aa</u>', '(Tuhan) yang telah menjadikan bumi sebagai hamparan bagimu, dan menjadikan jalan-jalan di atasnya bagimu, dan yang menurunkan air (hujan) dari langit.” Kemudian Kami tumbuhkan dengannya (air hujan itu) berjenis-jenis aneka macam tumbuh-tumbuhan.'),
	(2402, 20, 54, 'كُلُوْا وَارْعَوْا اَنْعَامَكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى ࣖ', 'kuluu wa<strong>i</strong>r\'aw an\'<u>aa</u>makum inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin li-ulii <strong>al</strong>nnuh<u>aa</u>', 'Makanlah dan gembalakanlah hewan-hewanmu. Sungguh, pada yang demikian itu, terdapat tanda-tanda (kebesaran Allah) bagi orang yang berakal.'),
	(2403, 20, 55, '۞ مِنْهَا خَلَقْنٰكُمْ وَفِيْهَا نُعِيْدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى ', 'minh<u>aa</u> khalaqn<u>aa</u>kum wafiih<u>aa</u> nu\'iidukum waminh<u>aa</u> nukhrijukum t<u>aa</u>ratan ukhr<u>aa</u>', 'Darinya (tanah) itulah Kami menciptakan kamu dan kepadanyalah Kami akan mengembalikan kamu dan dari sanalah Kami akan mengeluarkan kamu pada waktu yang lain.'),
	(2404, 20, 56, 'وَلَقَدْ اَرَيْنٰهُ اٰيٰتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى ', 'walaqad arayn<u>aa</u>hu <u>aa</u>y<u>aa</u>tin<u>aa</u> kullah<u>aa</u> faka<u>dzdz</u>aba wa-ab<u>aa</u>', 'Dan sungguh, Kami telah memperlihatkan kepadanya (Fir‘aun) tanda-tanda (kebesaran) Kami semuanya, ternyata dia mendustakan dan enggan (menerima kebenaran).'),
	(2405, 20, 57, 'قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يٰمُوْسٰى ', 'q<u>aa</u>la aji/tan<u>aa</u> litukhrijan<u>aa</u> min ar<u>dh</u>in<u>aa</u> bisi<u>h</u>rika y<u>aa</u> muus<u>aa</u>', 'Dia (Fir‘aun) berkata, “Apakah engkau datang kepada kami untuk mengusir kami dari negeri kami dengan sihirmu, wahai Musa?'),
	(2406, 20, 58, 'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِّثْلِهٖ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَّا نُخْلِفُهٗ نَحْنُ وَلَآ اَنْتَ مَكَانًا سُوًى ', 'falana/tiyannaka bisi<u>h</u>rin mitslihi fa<strong>i</strong>j\'al baynan<u>aa</u> wabaynaka maw\'idan l<u>aa</u> nukhlifuhu na<u>h</u>nu wal<u>aa</u> anta mak<u>aa</u>nan suw<u>aa</u><strong>n</strong>', 'Maka kami pun pasti akan mendatangkan sihir semacam itu kepadamu, maka buatlah suatu perjanjian untuk pertemuan antara kami dan engkau yang kami tidak akan menyalahinya dan tidak (pula) engkau, di suatu tempat yang terbuka.”'),
	(2407, 20, 59, 'قَالَ مَوْعِدُكُمْ يَوْمُ الزِّيْنَةِ وَاَنْ يُّحْشَرَ النَّاسُ ضُحًى ', 'q<u>aa</u>la maw\'idukum yawmu <strong>al</strong>zziinati wa-an yu<u>h</u>syara <strong>al</strong>nn<u>aa</u>su <u>dh</u>u<u>haa</u><strong>n</strong>', 'Dia (Musa) berkata, “(Perjanjian) waktu (untuk pertemuan kami dengan kamu itu) ialah pada hari raya dan hendaklah orang-orang dikumpulkan pada pagi hari (duha).”'),
	(2408, 20, 60, 'فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهٗ ثُمَّ اَتٰى ', 'fatawall<u>aa</u> fir\'awnu fajama\'a kaydahu tsumma at<u>aa</u>', 'Maka Fir‘aun meninggalkan (tempat itu), lalu mengatur tipu dayanya, kemudian dia datang kembali (pada hari yang ditentukan).'),
	(2409, 20, 61, 'قَالَ لَهُمْ مُّوْسٰى وَيْلَكُمْ لَا تَفْتَرُوْا عَلَى اللّٰهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى ', 'q<u>aa</u>la lahum muus<u>aa</u> waylakum l<u>aa</u> taftaruu \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban fayus<u>h</u>itakum bi\'a<u>dzaa</u>bin waqad kh<u>aa</u>ba mani iftar<u>aa</u>', 'Musa berkata kepada mereka (para pesihir), “Celakalah kamu! Janganlah kamu mengada-adakan kedustaan terhadap Allah, nanti Dia membinasakan kamu dengan azab.” Dan sungguh rugi orang yang mengada-adakan kedustaan.'),
	(2410, 20, 62, 'فَتَنَازَعُوْٓا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى ', 'fatan<u>aa</u>za\'uu amrahum baynahum wa-asarruu <strong>al</strong>nnajw<u>aa</u>', 'Maka mereka berbantah-bantahan tentang urusan mereka dan mereka merahasiakan percakapan (mereka).'),
	(2411, 20, 63, 'قَالُوْٓا اِنْ هٰذٰنِ لَسٰحِرَانِ يُرِيْدَانِ اَنْ يُّخْرِجٰكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيْقَتِكُمُ الْمُثْلٰى ', 'q<u>aa</u>luu in h<u>aadzaa</u>ni las<u>aah</u>ir<u>aa</u>ni yuriid<u>aa</u>ni an yukhrij<u>aa</u>kum min ar<u>dh</u>ikum bisi<u>h</u>rihim<u>aa</u> waya<u>dz</u>hab<u>aa</u> bi<u>th</u>ariiqatikumu <strong>a</strong>lmutsl<u>aa</u>', 'Mereka (para pesihir) berkata, “Sesungguhnya dua orang ini adalah pesihir yang hendak mengusirmu (Fir‘aun) dari negerimu dengan sihir mereka berdua, dan hendak melenyapkan adat kebiasaanmu yang utama.'),
	(2412, 20, 64, 'فَاَجْمِعُوْا كَيْدَكُمْ ثُمَّ ائْتُوْا صَفًّاۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى ', 'fa-ajmi\'uu kaydakum tsumma i/tuu <u>sh</u>affan waqad afla<u>h</u>a <strong>a</strong>lyawma mani ista\'l<u>aa</u>', 'Maka kumpulkanlah segala tipu daya (sihir) kamu, kemudian datanglah dengan berbaris, dan sungguh, beruntung orang yang menang pada hari ini.”'),
	(2413, 20, 65, 'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ اَوَّلَ مَنْ اَلْقٰى ', 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> imm<u>aa</u> an tulqiya wa-imm<u>aa</u> an nakuuna awwala man <strong>a</strong>lq<u>aa</u>', 'Mereka berkata, “Wahai Musa! Apakah engkau yang melemparkan (dahulu) atau kami yang lebih dahulu melemparkan?”'),
	(2414, 20, 66, 'قَالَ بَلْ اَلْقُوْاۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى', 'q<u>aa</u>la bal <strong>a</strong>lquu fa-i<u>dzaa</u> <u>h</u>ib<u>aa</u>luhum wa\'i<u>sh</u>iyyuhum yukhayyalu ilayhi min si<u>h</u>rihim annah<u>aa</u> tas\'<u>aa</u>', 'Dia (Musa) berkata, “Silakan kamu melemparkan!” Maka tiba-tiba tali-tali dan tongkat-tongkat mereka terbayang olehnya (Musa) seakan-akan ia merayap cepat, karena sihir mereka.'),
	(2415, 20, 67, 'فَاَوْجَسَ فِيْ نَفْسِهٖ خِيْفَةً مُّوْسٰى ', 'fa-awjasa fii nafsihi khiifatan muus<u>aa</u>', 'Maka Musa merasa takut dalam hatinya.'),
	(2416, 20, 68, 'قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى ', 'quln<u>aa</u> l<u>aa</u> takhaf innaka anta <strong>a</strong>l-a\'l<u>aa</u>', 'Kami berfirman, “Jangan takut! Sungguh, engkaulah yang unggul (menang).'),
	(2417, 20, 69, 'وَاَلْقِ مَا فِيْ يَمِيْنِكَ تَلْقَفْ مَا صَنَعُوْاۗ اِنَّمَا صَنَعُوْا كَيْدُ سٰحِرٍۗ وَلَا يُفْلِحُ السّٰحِرُ حَيْثُ اَتٰى ', 'wa-alqi m<u>aa</u> fii yamiinika talqaf m<u>aa</u> <u>sh</u>ana\'uu innam<u>aa</u> <u>sh</u>ana\'uu kaydu s<u>aah</u>irin wal<u>aa</u> yufli<u>h</u>u <strong>al</strong>ss<u>aah</u>iru <u>h</u>aytsu at<u>aa</u>', 'Dan lemparkan apa yang ada di tangan kananmu, niscaya ia akan menelan apa yang mereka buat. Apa yang mereka buat itu hanyalah tipu daya pesihir (belaka). Dan tidak akan menang pesihir itu, dari mana pun ia datang.”'),
	(2418, 20, 70, 'فَاُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوْٓا اٰمَنَّا بِرَبِّ هٰرُوْنَ وَمُوْسٰى ', 'faulqiya <strong>al</strong>ssa<u>h</u>aratu sujjadan q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi h<u>aa</u>ruuna wamuus<u>aa</u>', 'Lalu para pesihir itu merunduk bersujud, seraya berkata, “Kami telah percaya kepada Tuhannya Harun dan Musa.”'),
	(2419, 20, 71, 'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۗ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ فِيْ جُذُوْعِ النَّخْلِۖ وَلَتَعْلَمُنَّ اَيُّنَآ اَشَدُّ عَذَابًا وّ', 'q<u>aa</u>la <u>aa</u>mantum lahu qabla an <u>aadz</u>ana lakum innahu lakabiirukumu <strong>al</strong>la<u>dz</u>ii \'allamakumu <strong>al</strong>ssi<u>h</u>ra falauqa<u>ththh</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin walau<u>sh</u>allibannakum fii ju<u>dz</u>uu\'i <strong>al</strong>nnakhli walata\'lamunna ayyun<u>aa</u> asyaddu \'a<u>dzaa</u>ban wa-abq<u>aa</u>', 'Dia (Fir‘aun) berkata, “Apakah kamu telah beriman kepadanya (Musa) sebelum aku memberi izin kepadamu? Sesungguhnya dia itu pemimpinmu yang mengajarkan sihir kepadamu. Maka sungguh, akan kupotong tangan dan kakimu secara bersilang, dan sungguh, akan aku salib kamu pada pangkal pohon kurma dan sungguh, kamu pasti akan mengetahui siapa di antara kita yang lebih pedih dan lebih kekal siksaannya.”'),
	(2420, 20, 72, 'قَالُوْا لَنْ نُّؤْثِرَكَ عَلٰى مَا جَاۤءَنَا مِنَ الْبَيِّنٰتِ وَالَّذِيْ فَطَرَنَا فَاقْضِ مَآ اَنْتَ قَاضٍۗ اِنَّمَا تَقْضِيْ هٰذِهِ الْحَيٰوةَ الدُّنْيَا ۗ', 'q<u>aa</u>luu lan nu/tsiraka \'al<u>aa</u> m<u>aa</u> j<u>aa</u>-an<u>aa</u> mina <strong>a</strong>lbayyin<u>aa</u>ti wa<strong>a</strong>lla<u>dz</u>ii fa<u>th</u>aran<u>aa</u> fa<strong>i</strong>q<u>dh</u>i m<u>aa</u> anta q<u>aad</u>in innam<u>aa</u> taq<u>dh</u>ii h<u>aadz</u>ihi <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u>', 'Mereka (para pesihir) berkata, “Kami tidak akan memilih (tunduk) kepadamu atas bukti-bukti nyata (mukjizat), yang telah datang kepada kami dan atas (Allah) yang telah menciptakan kami. Maka putuskanlah yang hendak engkau putuskan. Sesungguhnya engkau hanya dapat memutuskan pada kehidupan di dunia ini.'),
	(2421, 20, 73, 'اِنَّآ اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطٰيٰنَا وَمَآ اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۗ وَاللّٰهُ خَيْرٌ وَّاَبْقٰى ', 'inn<u>aa</u> <u>aa</u>mann<u>aa</u> birabbin<u>aa</u> liyaghfira lan<u>aa</u> kha<u>th</u><u>aa</u>y<u>aa</u>n<u>aa</u> wam<u>aa</u> akrahtan<u>aa</u> \'alayhi mina <strong>al</strong>ssi<u>h</u>ri wa<strong>al</strong>l<u>aa</u>hu khayrun wa-abq<u>aa</u>', 'Kami benar-benar telah beriman kepada Tuhan kami, agar Dia mengampuni kesalahan-kesalahan kami dan sihir yang telah engkau paksakan kepada kami. Dan Allah lebih baik (pahala-Nya) dan lebih kekal (azab-Nya).”'),
	(2422, 20, 74, 'اِنَّهٗ مَنْ يَّأْتِ رَبَّهٗ مُجْرِمًا فَاِنَّ لَهٗ جَهَنَّمَ ۗ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰى ', 'innahu man ya/ti rabbahu mujriman fa-inna lahu jahannama l<u>aa</u> yamuutu fiih<u>aa</u> wal<u>aa</u> ya<u>h</u>y<u>aa</u>', 'Sesungguhnya barang siapa datang kepada Tuhannya dalam keadaan berdosa, maka sungguh, baginya adalah neraka Jahanam. Dia tidak mati (terus merasakan azab) di dalamnya dan tidak (pula) hidup (tidak dapat bertobat).'),
	(2423, 20, 75, 'وَمَنْ يَّأْتِهٖ مُؤْمِنًا قَدْ عَمِلَ الصّٰلِحٰتِ فَاُولٰۤىِٕكَ لَهُمُ الدَّرَجٰتُ الْعُلٰى ۙ', 'waman ya/tihi mu/minan qad \'amila <strong>al</strong><u>shshaa</u>li<u>haa</u>ti faul<u>aa</u>-ika lahumu <strong>al</strong>ddaraj<u>aa</u>tu <strong>a</strong>l\'ul<u>aa</u>', 'Tetapi barang siapa datang kepada-Nya dalam keadaan beriman, dan telah mengerjakan kebajikan, maka mereka itulah orang yang memperoleh derajat yang tinggi (mulia),'),
	(2424, 20, 76, 'جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗوَذٰلِكَ جَزٰۤؤُا مَنْ تَزَكّٰى ࣖ', 'jann<u>aa</u>tu \'<u>aa</u>dnin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika jaz<u>aa</u>u man tazakk<u>aa</u>', '(yaitu) surga-surga ‘Adn, yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah balasan bagi orang yang menyucikan diri.'),
	(2425, 20, 77, 'وَلَقَدْ اَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْ فَاضْرِبْ لَهُمْ طَرِيْقًا فِى الْبَحْرِ يَبَسًاۙ  لَّا تَخٰفُ دَرَكًا وَّلَا تَخْشٰى ', 'walaqad aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> an asri bi\'ib<u>aa</u>dii fa<strong>i</strong><u>dh</u>rib lahum <u>th</u>ariiqan fii <strong>a</strong>lba<u>h</u>ri yabasan l<u>aa</u> takh<u>aa</u>fu darakan wal<u>aa</u> takhsy<u>aa</u>', 'Dan sungguh, telah Kami wahyukan kepada Musa, “Pergilah bersama hamba-hamba-Ku (Bani Israil) pada malam hari, dan pukullah (buatlah) untuk mereka jalan yang kering di laut itu, (engkau) tidak perlu takut akan tersusul dan tidak perlu khawatir (akan tenggelam).”'),
	(2426, 20, 78, 'فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُوْدِهٖ فَغَشِيَهُمْ مِّنَ الْيَمِّ مَا غَشِيَهُمْ ۗ', 'fa-atba\'ahum fir\'awnu bijunuudihi faghasyiyahum mina <strong>a</strong>lyammi m<u>aa</u> ghasyiyahum', 'Kemudian Fir‘aun dengan bala tentaranya mengejar mereka, tetapi mereka digulung ombak laut yang menenggelamkan mereka.'),
	(2427, 20, 79, 'وَاَضَلَّ فِرْعَوْنُ قَوْمَهٗ وَمَا هَدٰى ', 'wa-a<u>dh</u>alla fir\'awnu qawmahu wam<u>aa</u> had<u>aa</u>', 'Dan Fir‘aun telah menyesatkan kaumnya dan tidak memberi petunjuk.'),
	(2428, 20, 80, 'يٰبَنِيْٓ اِسْرَاۤءِيْلَ قَدْ اَنْجَيْنٰكُمْ مِّنْ عَدُوِّكُمْ وَوٰعَدْنٰكُمْ جَانِبَ الطُّوْرِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ', 'y<u>aa</u> banii isr<u>aa</u>-iila qad anjayn<u>aa</u>kum min \'aduwwikum waw<u>aa</u>\'adn<u>aa</u>kum j<u>aa</u>niba <strong>al</strong><u>ththh</u>uuri <strong>a</strong>l-aymana wanazzaln<u>aa</u> \'alaykumu <strong>a</strong>lmanna wa<strong>al</strong>ssalw<u>aa</u>', 'Wahai Bani Israil! Sungguh, Kami telah menyelamatkan kamu dari musuhmu, dan Kami telah mengadakan perjanjian dengan kamu (untuk bermunajat) di sebelah kanan gunung itu (gunung Sinai) dan Kami telah menurunkan kepada kamu mann dan salwa.'),
	(2429, 20, 81, 'كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۙ وَلَا تَطْغَوْا فِيْهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِيْۚ وَمَنْ يَّحْلِلْ عَلَيْهِ غَضَبِيْ فَقَدْ هَوٰى ', 'kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wal<u>aa</u> ta<u>th</u>ghaw fiihi faya<u>h</u>illa \'alaykum gha<u>dh</u>abii waman ya<u>h</u>lil \'alayhi gha<u>dh</u>abii faqad haw<u>aa</u>', 'Makanlah dari rezeki yang baik-baik yang telah Kami berikan kepadamu, dan janganlah melampaui batas, yang menyebabkan kemurkaan-Ku menimpamu. Barangsiapa ditimpa kemurkaan-Ku, maka sungguh, binasalah dia.'),
	(2430, 20, 82, 'وَاِنِّي لَغَفَّارٌ لِّمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدٰى ', 'wa-innii laghaff<u>aa</u>run liman t<u>aa</u>ba wa<u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an tsumma ihtad<u>aa</u>', 'Dan sungguh, Aku Maha Pengampun bagi yang bertobat, beriman dan berbuat kebajikan, kemudian tetap dalam petunjuk.'),
	(2431, 20, 83, '۞ وَمَآ اَعْجَلَكَ عَنْ قَوْمِكَ يٰمُوْسٰى ', 'wam<u>aa</u> a\'jalaka \'an qawmika y<u>aa</u> muus<u>aa</u>', '“Dan mengapa engkau datang lebih cepat daripada kaummu, wahai Musa?”'),
	(2432, 20, 84, 'قَالَ هُمْ اُولَاۤءِ عَلٰٓى اَثَرِيْ وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى ', 'q<u>aa</u>la hum ul<u>aa</u>-i \'al<u>aa</u> atsarii wa\'ajiltu ilayka rabbi litar<u>daa</u>', 'Dia (Musa) berkata, “Itu mereka sedang menyusul aku dan aku bersegera kepada-Mu, Ya Tuhanku, agar Engkau rida (kepadaku).”'),
	(2433, 20, 85, 'قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْۢ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ ', 'q<u>aa</u>la fa-inn<u>aa</u> qad fatann<u>aa</u> qawmaka min ba\'dika wa-a<u>dh</u>allahumu <strong>al</strong>ss<u>aa</u>miriy<strong>yu</strong>', 'Dia (Allah) berfirman, “Sungguh, Kami telah menguji kaummu setelah engkau tinggalkan, dan mereka telah disesatkan oleh Samiri.” '),
	(2434, 20, 86, 'فَرَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًا ەۚ قَالَ يٰقَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ەۗ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُّمْ اَنْ يَّحِلَّ عَلَيْكُمْ غَضَبٌ مِّنْ رَّبِّكُمْ فَاَخْلَفْتُمْ مَّوْعِدِيْ', 'faraja\'a muus<u>aa</u> il<u>aa</u> qawmihi gha<u>dh</u>b<u>aa</u>na asifan q<u>aa</u>la y<u>aa</u> qawmi alam ya\'idkum rabbukum wa\'dan <u>h</u>asanan afa<u>th</u><u>aa</u>la \'alaykumu al\'ahdu am aradtum an ya<u>h</u>illa \'alaykum gha<u>dh</u>abun min rabbikum fa-akhlaftum maw\'idii', 'Kemudian Musa kembali kepada kaumnya dengan marah dan bersedih hati. Dia (Musa) berkata, “Wahai kaumku! Bukankah Tuhanmu telah menjanjikan kepadamu suatu janji yang baik? Apakah terlalu lama masa perjanjian itu bagimu atau kamu menghendaki agar kemurkaan Tuhan menimpamu, mengapa kamu melanggar perjanjianmu dengan aku?”'),
	(2435, 20, 87, 'قَالُوْا مَآ اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْنَآ اَوْزَارًا مِّنْ زِيْنَةِ الْقَوْمِ فَقَذَفْنٰهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّ ۙ', 'q<u>aa</u>luu m<u>aa</u> akhlafn<u>aa</u> maw\'idaka bimalkin<u>aa</u> wal<u>aa</u>kinn<u>aa</u> <u>h</u>ummiln<u>aa</u> awz<u>aa</u>ran min ziinati <strong>a</strong>lqawmi faqa<u>dz</u>afn<u>aa</u>h<u>aa</u> faka<u>dzaa</u>lika <strong>a</strong>lq<u>aa</u> <strong>al</strong>ss<u>aa</u>miriy<strong>yu</strong>', 'Mereka berkata, “Kami tidak melanggar perjanjianmu dengan kemauan kami sendiri, tetapi kami harus membawa beban berat dari perhiasan kaum (Fir‘aun) itu, kemudian kami melemparkannya (ke dalam api), dan demikian pula Samiri melemparkannya,'),
	(2436, 20, 88, 'فَاَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌ فَقَالُوْا هٰذَآ اِلٰهُكُمْ وَاِلٰهُ مُوْسٰى ەۙ فَنَسِيَ ۗ', 'fa-akhraja lahum \'ijlan jasadan lahu khuw<u>aa</u>run faq<u>aa</u>luu h<u>aadzaa</u> il<u>aa</u>hukum wa-il<u>aa</u>hu muus<u>aa</u> fanasiy<strong>a</strong>', 'kemudian (dari lubang api itu) dia (Samiri) mengeluarkan (patung) anak sapi yang bertubuh dan bersuara untuk mereka, maka mereka berkata, “Inilah Tuhanmu dan Tuhannya Musa, tetapi dia (Musa) telah lupa.”'),
	(2437, 20, 89, 'اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلًا ەۙ وَّلَا يَمْلِكُ لَهُمْ ضَرًّا وَّلَا نَفْعًا ࣖ', 'afal<u>aa</u> yarawna <strong>al</strong>l<u>aa</u> yarji\'u ilayhim qawlan wal<u>aa</u> yamliku lahum <u>dh</u>arran wal<u>aa</u> naf\'<u>aa</u><strong>n</strong>', 'Maka tidakkah mereka memperhatikan bahwa (patung anak sapi itu) tidak dapat memberi jawaban kepada mereka, dan tidak kuasa menolak mudarat mau-pun mendatangkan manfaat kepada mereka?'),
	(2438, 20, 90, 'وَلَقَدْ قَالَ لَهُمْ هٰرُوْنُ مِنْ قَبْلُ يٰقَوْمِ اِنَّمَا فُتِنْتُمْ بِهٖۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُوْنِيْ وَاَطِيْعُوْٓا اَمْرِيْ ', 'walaqad q<u>aa</u>la lahum h<u>aa</u>ruunu min qablu y<u>aa</u> qawmi innam<u>aa</u> futintum bihi wa-inna rabbakumu <strong>al</strong>rra<u>h</u>m<u>aa</u>nu fa<strong>i</strong>ttabi\'uunii wa-a<u>th</u>ii\'uu amrii', 'Dan sungguh, sebelumnya Harun telah berkata kepada mereka, “Wahai kaumku! Sesungguhnya kamu hanya sekedar diberi cobaan (dengan patung anak sapi) itu dan sungguh, Tuhanmu ialah (Allah) Yang Maha Pengasih, maka ikutilah aku dan taatilah perintahku.”'),
	(2439, 20, 91, 'قَالُوْا لَنْ نَّبْرَحَ عَلَيْهِ عٰكِفِيْنَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوْسٰى ', 'q<u>aa</u>luu lan nabra<u>h</u>a \'alayhi \'<u>aa</u>kifiina <u>h</u>att<u>aa</u> yarji\'a ilayn<u>aa</u> muus<u>aa</u>', 'Mereka menjawab, “Kami tidak akan meninggalkannya (dan) tetap menyembahnya (patung anak sapi) sampai Musa kembali kepada kami.”'),
	(2440, 20, 92, 'قَالَ يٰهٰرُوْنُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّوْٓا ۙ', 'q<u>aa</u>la y<u>aa</u> h<u>aa</u>ruunu m<u>aa</u> mana\'aka i<u>dz</u> ra-aytahum <u>dh</u>alluu', 'Dia (Musa) berkata, “Wahai Harun! Apa yang menghalangimu ketika engkau melihat mereka telah sesat,'),
	(2441, 20, 93, 'اَلَّا تَتَّبِعَنِۗ اَفَعَصَيْتَ اَمْرِيْ ', '<strong>al</strong>l<u>aa</u> tattabi\'ani afa\'a<u>sh</u>ayta amrii', '(sehingga) engkau tidak mengikuti aku? Apakah engkau telah (sengaja) melanggar perintahku?”'),
	(2442, 20, 94, 'قَالَ يَبْنَؤُمَّ لَا تَأْخُذْ بِلِحْيَتِيْ وَلَا بِرَأْسِيْۚ اِنِّيْ خَشِيْتُ اَنْ تَقُوْلَ فَرَّقْتَ بَيْنَ بَنِيْٓ اِسْرَاۤءِيْلَ وَلَمْ تَرْقُبْ قَوْلِيْ ', 'q<u>aa</u>la yabnaumma l<u>aa</u> ta/khu<u>dz</u> bili<u>h</u>yatii wal<u>aa</u> bira/sii innii khasyiitu an taquula farraqta bayna banii isr<u>aa</u>-iila walam tarqub qawlii', 'Dia (Harun) menjawab, “Wahai putra ibuku! Janganlah engkau pegang janggutku dan jangan (pula) kepalaku. Aku sungguh khawatir engkau akan berkata (kepadaku), ‘Engkau telah memecah belah antara Bani Israil dan engkau tidak memelihara amanatku.’” '),
	(2443, 20, 95, 'قَالَ فَمَا خَطْبُكَ يٰسَامِرِيُّ ', 'q<u>aa</u>la fam<u>aa</u> kha<u>th</u>buka y<u>aa</u> s<u>aa</u>miriy<strong>yu</strong>', 'Dia (Musa) berkata, “Apakah yang mendorongmu (berbuat demikian) wahai Samiri?”'),
	(2444, 20, 96, 'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوْا بِهٖ فَقَبَضْتُ قَبْضَةً مِّنْ اَثَرِ الرَّسُوْلِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ لِيْ نَفْسِيْ ', 'q<u>aa</u>la ba<u>sh</u>urtu bim<u>aa</u> lam yab<u>sh</u>uruu bihi faqaba<u>dh</u>tu qab<u>dh</u>atan min atsari <strong>al</strong>rrasuuli fanaba<u>dz</u>tuh<u>aa</u> waka<u>dzaa</u>lika sawwalat lii nafsii', 'Dia (Samiri) menjawab, “Aku mengetahui sesuatu yang tidak mereka ketahui, jadi aku ambil segenggam (tanah dari) jejak rasul lalu aku melemparkannya (ke dalam api itu), demikianlah nafsuku membujukku.”'),
	(2445, 20, 97, 'قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِى الْحَيٰوةِ اَنْ تَقُوْلَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِدًا لَّنْ تُخْلَفَهٗۚ وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذِيْ ظَلْتَ عَلَيْهِ عَاكِفًا ۗ  لَنُحَرِّقَنَّهٗ ثُمَّ لَنَنْسِفَنَّهٗ فِى الْيَمِّ نَسْفًا ', 'q<u>aa</u>la fa<strong>i</strong><u>dz</u>hab fa-inna laka fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti an taquula l<u>aa</u> mis<u>aa</u>sa wa-inna laka maw\'idan lan tukhlafahu wa<strong>u</strong>n<u>zh</u>ur il<u>aa</u> il<u>aa</u>hika <strong>al</strong>la<u>dz</u>ii <u>zh</u>alta \'alayhi \'<u>aa</u>kifan lanu<u>h</u>arriqannahu tsumma lanansifannahu fii <strong>a</strong>lyammi nasf<u>aa</u><strong>n</strong>', 'Dia (Musa) berkata, “Pergilah kau! Maka sesungguhnya di dalam kehidupan (di dunia) engkau (hanya dapat) mengatakan, ‘Janganlah menyentuh (aku),’. Dan engkau pasti mendapat (hukuman) yang telah dijanjikan (di akhirat) yang tidak akan dapat engkau hindari, dan lihatlah tuhanmu itu yang engkau tetap menyembahnya. Kami pasti akan membakarnya, kemudian sungguh kami akan menghamburkannya (abunya) ke dalam laut (berserakan).'),
	(2446, 20, 98, 'اِنَّمَآ اِلٰهُكُمُ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۗ وَسِعَ كُلَّ شَيْءٍ عِلْمًا ', 'innam<u>aa</u> il<u>aa</u>hukumu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wasi\'a kulla syay-in \'ilm<u>aa</u><strong>n</strong>', ' Sungguh, Tuhanmu hanyalah Allah, tidak ada tuhan selain Dia. Pengetahuan-Nya meliputi segala sesuatu.”'),
	(2447, 20, 99, 'كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنٰكَ مِنْ لَّدُنَّا ذِكْرًا ۚ', 'ka<u>dzaa</u>lika naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-i m<u>aa</u> qad sabaqa waqad <u>aa</u>tayn<u>aa</u>ka min ladunn<u>aa</u> tsikra<strong>n</strong>', 'Demikianlah Kami kisahkan kepadamu (Muhammad) sebagian kisah (umat) yang telah lalu, dan sungguh, telah Kami berikan kepadamu suatu peringatan (Al-Qur\'an) dari sisi Kami.'),
	(2448, 20, 100, 'مَنْ اَعْرَضَ عَنْهُ فَاِنَّهٗ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْرًا ', 'man a\'ra<u>dh</u>a \'anhu fa-innahu ya<u>h</u>milu yawma <strong>a</strong>lqiy<u>aa</u>mati wizr<u>aa</u><strong>n</strong>', 'Barangsiapa berpaling darinya (Al-Qur\'an), maka sesungguhnya dia akan memikul beban yang berat (dosa) pada hari Kiamat, '),
	(2449, 20, 101, 'خٰلِدِيْنَ فِيْهِ ۗوَسَاۤءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلًاۙ ', 'kh<u>aa</u>lidiina fiihi was<u>aa</u>-a lahum yawma <strong>a</strong>lqiy<u>aa</u>mati <u>h</u>iml<u>aa</u><strong>n</strong>', 'mereka kekal di dalam keadaan itu. Dan sungguh buruk beban dosa itu bagi mereka pada hari Kiamat, '),
	(2450, 20, 102, 'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ وَنَحْشُرُ الْمُجْرِمِيْنَ يَوْمَىِٕذٍ زُرْقًا ۖ', 'yawma yunfakhu fii <strong>al</strong><u>shsh</u>uuri wana<u>h</u>syuru <strong>a</strong>lmujrimiina yawma-i<u>dz</u>in zurq<u>aa</u><strong>n</strong>', 'pada hari (Kiamat) sangkakala ditiup (yang kedua kali) dan pada hari itu Kami kumpulkan orang-orang yang berdosa dengan (wajah) biru muram,'),
	(2451, 20, 103, 'يَّتَخَافَتُوْنَ بَيْنَهُمْ اِنْ لَّبِثْتُمْ اِلَّا عَشْرًا ', 'yatakh<u>aa</u>fatuuna baynahum in labitstum ill<u>aa</u> \'asyr<u>aa</u><strong>n</strong>', 'mereka saling berbisik satu sama lain, “Kamu tinggal (di dunia) tidak lebih dari sepuluh (hari).”'),
	(2452, 20, 104, 'نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ اِذْ يَقُوْلُ اَمْثَلُهُمْ طَرِيْقَةً اِنْ لَّبِثْتُمْ اِلَّا يَوْمًا ࣖ', 'na<u>h</u>nu a\'lamu bim<u>aa</u> yaquuluuna i<u>dz</u> yaquulu amtsaluhum <u>th</u>ariiqatan in labitstum ill<u>aa</u> yawm<u>aa</u><strong>n</strong>', 'Kami lebih mengetahui apa yang akan mereka katakan, ketika orang yang paling lurus jalannya mengatakan, “Kamu tinggal (di dunia), tidak lebih dari sehari saja.”'),
	(2453, 20, 105, 'وَيَسْـَٔلُوْنَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّيْ نَسْفًا ۙ', 'wayas-aluunaka \'ani <strong>a</strong>ljib<u>aa</u>li faqul yansifuh<u>aa</u> rabbii nasf<u>aa</u><strong>n</strong>', 'Dan mereka bertanya kepadamu (Muhammad) tentang gunung-gunung, maka katakanlah, “Tuhanku akan menghancurkannya (pada hari Kiamat) sehancur-hancurnya,'),
	(2454, 20, 106, 'فَيَذَرُهَا قَاعًا صَفْصَفًا ۙ', 'faya<u>dz</u>aruh<u>aa</u> q<u>aa</u>\'an <u>sh</u>af<u>sh</u>af<u>aa</u><strong>n</strong>', 'kemudian Dia akan menjadikan (bekas gunung-gunung) itu rata sama sekali, '),
	(2455, 20, 107, 'لَّا تَرٰى فِيْهَا عِوَجًا وَّلَآ اَمْتًا ۗ', 'l<u>aa</u> tar<u>aa</u> fiih<u>aa</u> \'iwajan wal<u>aa</u> amt<u>aa</u><strong>n</strong>', '(sehingga) kamu tidak akan melihat lagi ada tempat yang rendah dan yang tinggi di sana.” '),
	(2456, 20, 108, 'يَوْمَىِٕذٍ يَّتَّبِعُوْنَ الدَّاعِيَ لَا عِوَجَ لَهٗ ۚوَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْسًا ', 'yawma-i<u>dz</u>in yattabi\'uuna <strong>al</strong>dd<u>aa</u>\'iya l<u>aa</u> \'iwaja lahu wakhasya\'ati <strong>a</strong>l-a<u>sh</u>w<u>aa</u>tu li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni fal<u>aa</u> tasma\'u ill<u>aa</u> hams<u>aa</u><strong>n</strong>', 'Pada hari itu mereka mengikuti (panggilan) penyeru (malaikat) tanpa berbelok-belok (membantah); dan semua suara tunduk merendah kepada Tuhan Yang Maha Pengasih, sehingga yang kamu dengar hanyalah bisik-bisik.'),
	(2457, 20, 109, 'يَوْمَىِٕذٍ لَّا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهٗ قَوْلًا ', 'yawma-i<u>dz</u>in l<u>aa</u> tanfa\'u <strong>al</strong>sysyaf<u>aa</u>\'atu ill<u>aa</u> man a<u>dz</u>ina lahu <strong>al</strong>rra<u>h</u>m<u>aa</u>nu wara<u>dh</u>iya lahu qawl<u>aa</u><strong>n</strong>', 'Pada hari itu tidak berguna syafaat (pertolongan), kecuali dari orang yang telah diberi izin oleh Tuhan Yang Maha Pengasih, dan Dia ridai perkataannya.'),
	(2458, 20, 110, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيْطُوْنَ بِهٖ عِلْمًا ', 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wal<u>aa</u> yu<u>h</u>ii<u>th</u>uuna bihi \'ilm<u>aa</u><strong>n</strong>', ' Dia (Allah) mengetahui apa yang di hadapan mereka (yang akan terjadi) dan apa yang di belakang mereka (yang telah terjadi), sedang ilmu mereka tidak dapat meliputi ilmu-Nya.'),
	(2459, 20, 111, '۞ وَعَنَتِ الْوُجُوْهُ لِلْحَيِّ الْقَيُّوْمِۗ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا ', 'wa\'anati <strong>a</strong>lwujuuhu lil<u>h</u>ayyi <strong>a</strong>lqayyuumi waqad kh<u>aa</u>ba man <u>h</u>amala <u>zh</u>ulm<u>aa</u><strong>n</strong>', 'Dan semua wajah tertunduk di hadapan (Allah) Yang Hidup dan Yang Berdiri Sendiri. Sungguh rugi orang yang melakukan kezaliman.'),
	(2460, 20, 112, 'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا يَخٰفُ ظُلْمًا وَّلَا هَضْمًا ', 'waman ya\'mal mina <strong>al</strong><u>shshaa</u>li<u>haa</u>ti wahuwa mu/minun fal<u>aa</u> yakh<u>aa</u>fu <u>zh</u>ulman wal<u>aa</u> ha<u>dh</u>m<u>aa</u><strong>n</strong>', 'Dan barang siapa mengerjakan kebajikan sedang dia (dalam keadaan) beriman, maka dia tidak khawatir akan perlakuan zalim (terhadapnya) dan tidak (pula khawatir) akan pengurangan haknya.'),
	(2461, 20, 113, 'وَكَذٰلِكَ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا وَّصَرَّفْنَا فِيْهِ مِنَ الْوَعِيْدِ لَعَلَّهُمْ يَتَّقُوْنَ اَوْ يُحْدِثُ لَهُمْ ذِكْرًا ', 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu qur-<u>aa</u>nan \'arabiyyan wa<u>sh</u>arrafn<u>aa</u> fiihi mina <strong>a</strong>lwa\'iidi la\'allahum yattaquuna aw yu<u>h</u>ditsu lahum <u>dz</u>ikr<u>aa</u><strong>n</strong>', 'Dan demikianlah Kami menurunkan Al-Qur\'an dalam bahasa Arab, dan Kami telah menjelaskan berulang-ulang di dalamnya sebagian dari ancaman, agar mereka bertakwa, atau agar (Al-Qur\'an) itu memberi pengajaran bagi mereka.'),
	(2462, 20, 114, 'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُّقْضٰٓى اِلَيْكَ وَحْيُهٗ ۖوَقُلْ رَّبِّ زِدْنِيْ عِلْمًا ', 'fata\'<u>aa</u>l<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmaliku <strong>a</strong>l<u>h</u>aqqu wal<u>aa</u> ta\'jal bi<strong>a</strong>lqur-<u>aa</u>ni min qabli an yuq<u>daa</u> ilayka wa<u>h</u>yuhu waqul rabbi zidnii \'ilm<u>aa</u><strong>n</strong>', 'Maka Mahatinggi Allah, Raja yang sebenar-benarnya. Dan janganlah engkau (Muhammad) tergesa-gesa (membaca) Al-Qur\'an sebelum selesai diwahyukan kepadamu, dan katakanlah, “Ya Tuhanku, tambahkanlah ilmu kepadaku. ”'),
	(2463, 20, 115, 'وَلَقَدْ عَهِدْنَآ اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهٗ عَزْمًا ࣖ', 'walaqad \'ahidn<u>aa</u> il<u>aa</u> <u>aa</u>dama min qablu fanasiya walam najid lahu \'azm<u>aa</u><strong>n</strong>', 'Dan sungguh telah Kami pesankan kepada Adam dahulu, tetapi dia lupa, dan Kami tidak dapati kemauan yang kuat padanya.'),
	(2464, 20, 116, 'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَ اَبٰى ۗ', 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa ab<u>aa</u>', 'Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Lalu mereka pun sujud kecuali Iblis; dia menolak.'),
	(2465, 20, 117, 'فَقُلْنَا يٰٓاٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى ', 'faquln<u>aa</u> y<u>aa</u> <u>aa</u>damu inna h<u>aadzaa</u> \'aduwwun laka walizawjika fal<u>aa</u> yukhrijannakum<u>aa</u> mina <strong>a</strong>ljannati fatasyq<u>aa</u>', 'Kemudian Kami berfirman, “Wahai Adam! Sungguh ini (Iblis) musuh bagimu dan bagi istrimu, maka sekali-kali jangan sampai dia mengeluarkan kamu berdua dari surga, nanti kamu celaka.'),
	(2466, 20, 118, 'اِنَّ لَكَ اَلَّا تَجُوْعَ فِيْهَا وَلَا تَعْرٰى ۙ', 'inna laka <strong>al</strong>l<u>aa</u> tajuu\'a fiih<u>aa</u> wal<u>aa</u> ta\'r<u>aa</u>', 'Sungguh, ada (jaminan) untukmu di sana, engkau tidak akan kelaparan dan tidak akan telanjang.'),
	(2467, 20, 119, 'وَاَنَّكَ لَا تَظْمَؤُا فِيْهَا وَلَا تَضْحٰى ', 'wa-annaka l<u>aa</u> ta<u>zh</u>mau fiih<u>aa</u> wal<u>aa</u> ta<u>dhaa</u>', 'Dan sungguh, di sana engkau tidak akan merasa dahaga dan tidak akan ditimpa panas matahari.” '),
	(2468, 20, 120, 'فَوَسْوَسَ اِلَيْهِ الشَّيْطٰنُ قَالَ يٰٓاٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَّا يَبْلٰى ', 'fawaswasa ilayhi <strong>al</strong>sysyay<u>th</u><u>aa</u>nu q<u>aa</u>la y<u>aa</u> <u>aa</u>damu hal adulluka \'al<u>aa</u> syajarati <strong>a</strong>lkhuldi wamulkin l<u>aa</u> yabl<u>aa</u>', ' Kemudian setan membisikkan (pikiran jahat) kepadanya, dengan berkata, “Wahai Adam! Maukah aku tunjukkan kepadamu pohon keabadian (khuldi) dan kerajaan yang tidak akan binasa?”'),
	(2469, 20, 121, 'فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۚ وَعَصٰٓى اٰدَمُ رَبَّهٗ فَغَوٰى ۖ', 'fa-akal<u>aa</u> minh<u>aa</u> fabadat lahum<u>aa</u> saw-<u>aa</u>tuhum<u>aa</u> wa<u>th</u>afiq<u>aa</u> yakh<u>sh</u>if<u>aa</u>ni \'alayhim<u>aa</u> min waraqi <strong>a</strong>ljannati wa\'a<u>shaa</u> <u>aa</u>damu rabbahu faghaw<u>aa</u>', 'Lalu keduanya memakannya, lalu tampaklah oleh keduanya aurat mereka dan mulailah keduanya menutupinya dengan daun-daun (yang ada di) surga, dan telah durhakalah Adam kepada Tuhannya, dan sesatlah dia.'),
	(2470, 20, 122, 'ثُمَّ اجْتَبٰىهُ رَبُّهٗ فَتَابَ عَلَيْهِ وَهَدٰى ', 'tsumma ijtab<u>aa</u>hu rabbuhu fat<u>aa</u>ba \'alayhi wahad<u>aa</u>', 'Kemudian Tuhannya memilih dia, maka Dia menerima tobatnya dan memberinya petunjuk.'),
	(2471, 20, 123, 'قَالَ اهْبِطَا مِنْهَا جَمِيعًاۢ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚفَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى ەۙ فَمَنِ اتَّبَعَ هُدٰيَ فَلَا يَضِلُّ وَلَا يَشْقٰى', 'q<u>aa</u>la ihbi<u>th</u><u>aa</u> minh<u>aa</u> jamii\'an ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun fa-imm<u>aa</u> ya/tiyannakum minnii hudan famani ittaba\'a hud<u>aa</u>ya fal<u>aa</u> ya<u>dh</u>illu wal<u>aa</u> yasyq<u>aa</u>', 'Dia (Allah) berfirman, “Turunlah kamu berdua dari surga bersama-sama, sebagian kamu menjadi musuh bagi sebagian yang lain. Jika datang kepadamu petunjuk dari-Ku, maka (ketahuilah) barang siapa mengikuti petunjuk-Ku, dia tidak akan sesat dan tidak akan celaka.'),
	(2472, 20, 124, 'وَمَنْ اَعْرَضَ عَنْ ذِكْرِيْ فَاِنَّ لَهٗ مَعِيْشَةً ضَنْكًا وَّنَحْشُرُهٗ يَوْمَ الْقِيٰمَةِ اَعْمٰى ', 'waman a\'ra<u>dh</u>a \'an <u>dz</u>ikrii fa-inna lahu ma\'iisyatan <u>dh</u>ank<u>aa</u><strong>n</strong> wana<u>h</u>syuruhu yawma <strong>a</strong>lqiy<u>aa</u>mati a\'m<u>aa</u><strong>n</strong>', 'Dan barang siapa berpaling dari peringatan-Ku, maka sungguh, dia akan menjalani kehidupan yang sempit, dan Kami akan mengumpulkannya pada hari Kiamat dalam keadaan buta.”'),
	(2473, 20, 125, 'قَالَ رَبِّ لِمَ حَشَرْتَنِيْٓ اَعْمٰى وَقَدْ كُنْتُ بَصِيْرًا ', 'q<u>aa</u>la rabbi lima <u>h</u>asyartanii a\'m<u>aa</u> waqad kuntu ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dia berkata, “Ya Tuhanku, mengapa Engkau kumpulkan aku dalam keadaan buta, padahal dahulu aku dapat melihat?”'),
	(2474, 20, 126, 'قَالَ كَذٰلِكَ اَتَتْكَ اٰيٰتُنَا فَنَسِيْتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى ', 'q<u>aa</u>la ka<u>dzaa</u>lika atatka <u>aa</u>y<u>aa</u>tun<u>aa</u> fanasiitah<u>aa</u> waka<u>dzaa</u>lika <strong>a</strong>lyawma tuns<u>aa</u>', 'Dia (Allah) berfirman, “Demikianlah, dahulu telah datang kepadamu ayat-ayat Kami, dan kamu mengabaikannya, jadi begitu (pula) pada hari ini kamu diabaikan.”'),
	(2475, 20, 127, 'وَكَذٰلِكَ نَجْزِيْ مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْۢ بِاٰيٰتِ رَبِّهٖۗ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى ', 'waka<u>dzaa</u>lika najzii man asrafa walam yu/min bi-<u>aa</u>y<u>aa</u>ti rabbihi wala\'a<u>dzaa</u>bu <strong>a</strong>l-<u>aa</u>khirati asyaddu wa-abq<u>aa</u>', 'Dan demikianlah Kami membalas orang yang melampaui batas dan tidak percaya kepada ayat-ayat Tuhannya. Sungguh, azab di akhirat itu lebih berat dan lebih kekal. '),
	(2476, 20, 128, 'اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى ࣖ', 'afalam yahdi lahum kam ahlakn<u>aa</u> qablahum mina <strong>a</strong>lquruuni yamsyuuna fii mas<u>aa</u>kinihim inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin li-ulii <strong>al</strong>nnuh<u>aa</u>', 'Maka tidakkah menjadi petunjuk bagi mereka (orang-orang musyrik) berapa banyak (generasi) sebelum mereka yang telah Kami binasakan, padahal mereka melewati (bekas-bekas) tempat tinggal mereka (umat-umat itu)? Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang berakal.'),
	(2477, 20, 129, 'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَكَانَ لِزَامًا وَّاَجَلٌ مُّسَمًّى ۗ', 'walawl<u>aa</u> kalimatun sabaqat min rabbika lak<u>aa</u>na liz<u>aa</u>man wa-ajalun musamm<u>aa</u><strong>n</strong>', 'Dan kalau tidak ada suatu ketetapan terdahulu dari Tuhanmu serta tidak ada batas yang telah ditentukan (ajal), pasti (siksaan itu) menimpa mereka.'),
	(2478, 20, 130, 'فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ غُرُوْبِهَا ۚوَمِنْ اٰنَاۤئِ الَّيْلِ فَسَبِّحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى', 'fa<strong>i</strong><u>sh</u>bir \'al<u>aa</u> m<u>aa</u> yaquuluuna wasabbi<u>h</u> bi<u>h</u>amdi rabbika qabla <u>th</u>uluu\'i <strong>al</strong>sysyamsi waqabla ghuruubih<u>aa</u> wamin <u>aa</u>n<u>aa</u>-i <strong>al</strong>layli fasabbi<u>h</u> wa-a<u>th</u>r<u>aa</u>fa <strong>al</strong>nnah<u>aa</u>ri la\'allaka tar<u>daa</u>', 'Maka sabarlah engkau (Muhammad) atas apa yang mereka katakan, dan bertasbihlah dengan memuji Tuhanmu, sebelum matahari terbit, dan sebelum terbenam; dan bertasbihlah (pula) pada waktu tengah malam dan di ujung siang hari, agar engkau merasa tenang.'),
	(2479, 20, 131, 'وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا ەۙ لِنَفْتِنَهُمْ فِيْهِ ۗوَرِزْقُ رَبِّكَ خَيْرٌ وَّاَبْقٰى', 'wal<u>aa</u> tamuddanna \'aynayka il<u>aa</u> m<u>aa</u> matta\'n<u>aa</u> bihi azw<u>aa</u>jan minhum zahrata <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> linaftinahum fiihi warizqu rabbika khayrun wa-abq<u>aa</u>', 'Dan janganlah engkau tujukan pandangan matamu kepada kenikmatan yang telah Kami berikan kepada beberapa golongan dari mereka, (sebagai) bunga kehidupan dunia agar Kami uji mereka dengan (kesenangan) itu. Karunia Tuhanmu lebih baik dan lebih kekal.'),
	(2480, 20, 132, 'وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۗ  لَا نَسْـَٔلُكَ رِزْقًاۗ نَحْنُ نَرْزُقُكَۗ وَالْعَاقِبَةُ لِلتَّقْوٰى ', 'wa/mur ahlaka bi<strong>al</strong><u>shsh</u>al<u>aa</u>ti wa<strong>i</strong><u>sth</u>abir \'alayh<u>aa</u> l<u>aa</u> nas-aluka rizqan na<u>h</u>nu narzuquka wa<strong>a</strong>l\'<u>aa</u>qibatu li<strong>l</strong>ttaqw<u>aa</u>', 'Dan perintahkanlah keluargamu melaksanakan salat dan sabar dalam mengerjakannya. Kami tidak meminta rezeki kepadamu, Kamilah yang memberi rezeki kepadamu. Dan akibat (yang baik di akhirat) adalah bagi orang yang bertakwa.'),
	(2481, 20, 133, 'وَقَالُوْا لَوْلَا يَأْتِيْنَا بِاٰيَةٍ مِّنْ رَّبِّهٖۗ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِى الصُّحُفِ الْاُولٰى ', 'waq<u>aa</u>luu lawl<u>aa</u> ya/tiin<u>aa</u> bi-<u>aa</u>yatin min rabbihi awa lam ta/tihim bayyinatu m<u>aa</u> fii <strong>al</strong><u>shsh</u>u<u>h</u>ufi <strong>a</strong>l-uul<u>aa</u>', 'Dan mereka berkata, “Mengapa dia tidak membawa tanda (bukti) kepada kami dari Tuhannya?” Bukankah telah datang kepada mereka bukti (yang nyata) sebagaimana yang tersebut di dalam kitab-kitab yang dahulu?'),
	(2482, 20, 134, 'وَلَوْ اَنَّآ اَهْلَكْنٰهُمْ بِعَذَابٍ مِّنْ قَبْلِهٖ لَقَالُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ مِنْ قَبْلِ اَنْ نَّذِلَّ وَنَخْزٰى ', 'walaw ann<u>aa</u> ahlakn<u>aa</u>hum bi\'a<u>dzaa</u>bin min qablihi laq<u>aa</u>luu rabban<u>aa</u> lawl<u>aa</u> arsalta ilayn<u>aa</u> rasuulan fanattabi\'a <u>aa</u>y<u>aa</u>tika min qabli an na<u>dz</u>illa wanakhz<u>aa</u>', 'Dan kalau mereka Kami binasakan dengan suatu siksaan sebelumnya (Al-Qur\'an itu diturunkan), tentulah mereka berkata, “Ya Tuhan kami, mengapa tidak Engkau utus seorang rasul kepada kami, sehingga kami mengikuti ayat-ayat-Mu sebelum kami menjadi hina dan rendah?”'),
	(2483, 20, 135, 'قُلْ كُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوْاۚ فَسَتَعْلَمُوْنَ مَنْ اَصْحٰبُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى ࣖ    ۔', 'qul kullun mutarabbi<u>sh</u>un fatarabba<u>sh</u>uu fasata\'lamuuna man a<u>sh</u>-<u>haa</u>bu <strong>al</strong><u>shsh</u>ir<u>aath</u>i <strong>al</strong>ssawiyyi wamani ihtad<u>aa</u>', 'Katakanlah (Muhammad), “Masing-masing (kita) menanti, maka nantikanlah olehmu! Dan kelak kamu akan mengetahui, siapa yang menempuh jalan yang lurus, dan siapa yang telah mendapat petunjuk.”'),
	(2484, 21, 1, 'اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِيْ غَفْلَةٍ مُّعْرِضُوْنَ ۚ ', 'iqtaraba li<strong>l</strong>nn<u>aa</u>si <u>h</u>is<u>aa</u>buhum wahum fii ghaflatin mu\'ri<u>dh</u>uun<strong>a</strong>', 'Telah semakin dekat kepada manusia perhitungan amal mereka, sedang mereka dalam keadaan lalai (dengan dunia), berpaling (dari akhirat).'),
	(2485, 21, 2, 'مَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنْ رَّبِّهِمْ مُّحْدَثٍ اِلَّا اسْتَمَعُوْهُ وَهُمْ يَلْعَبُوْنَ ۙ', 'm<u>aa</u> ya/tiihim min <u>dz</u>ikrin min rabbihim mu<u>h</u>datsin ill<u>aa</u> istama\'uuhu wahum yal\'abuun<strong>a</strong>', 'Setiap diturunkan kepada mereka ayat-ayat yang baru dari Tuhan, mereka mendengarkannya sambil bermain-main.'),
	(2486, 21, 3, 'لَاهِيَةً قُلُوْبُهُمْۗ وَاَسَرُّوا النَّجْوَىۖ الَّذِيْنَ ظَلَمُوْاۖ هَلْ هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۚ اَفَتَأْتُوْنَ السِّحْرَ وَاَنْتُمْ تُبْصِرُوْنَ ', 'l<u>aa</u>hiyatan quluubuhum wa-asarruu <strong>al</strong>nnajw<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu hal h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum afata/tuuna <strong>al</strong>ssi<u>h</u>ra wa-antum tub<u>sh</u>iruun<strong>a</strong>', 'Hati mereka dalam keadaan lalai. Dan orang-orang yang zalim itu merahasiakan pembicaraan mereka, “(Orang) ini (Muhammad) tidak lain hanyalah seorang manusia (juga) seperti kamu. Apakah kamu menerima sihir itu padahal kamu menyaksikannya?”'),
	(2487, 21, 4, 'قٰلَ رَبِّيْ يَعْلَمُ الْقَوْلَ فِى السَّمَاۤءِ وَالْاَرْضِۖ وَهُوَ السَّمِيْعُ الْعَلِيْمُ ', 'q<u>aa</u>la rabbii ya\'lamu <strong>a</strong>lqawla fii <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i wahuwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Dia (Muhammad) berkata, “Tuhanku mengetahui (semua) perkataan di langit dan di bumi, dan Dia Maha Mendengar, Maha Mengetahui!”'),
	(2488, 21, 5, 'بَلْ قَالُوْٓا اَضْغَاثُ اَحْلَامٍۢ بَلِ افْتَرٰىهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَآ اُرْسِلَ الْاَوَّلُوْنَ ', 'bal q<u>aa</u>luu a<u>dh</u>gh<u>aa</u>tsu a<u>h</u>l<u>aa</u>min bali iftar<u>aa</u>hu bal huwa sy<u>aa</u>\'irun falya/tin<u>aa</u> bi-<u>aa</u>yatin kam<u>aa</u> ursila <strong>a</strong>l-awwaluun<strong>a</strong>', ' Bahkan mereka mengatakan, “(Al-Qur\'an itu buah) mimpi-mimpi yang kacau, atau hasil rekayasanya (Muhammad), atau bahkan dia hanya seorang penyair, cobalah dia datangkan kepada kita suatu tanda (bukti), seperti halnya rasul-rasul yang diutus terdahulu.”'),
	(2489, 21, 6, 'مَآ اٰمَنَتْ قَبْلَهُمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَاۚ اَفَهُمْ يُؤْمِنُوْنَ ', 'm<u>aa</u> <u>aa</u>manat qablahum min qaryatin ahlakn<u>aa</u>h<u>aa</u> afahum yu/minuun<strong>a</strong>', ' Penduduk suatu negeri sebelum mereka, yang telah Kami binasakan, mereka itu tidak beriman (padahal telah Kami kirimkan bukti). Apakah mereka akan beriman?'),
	(2490, 21, 7, 'وَمَآ اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَ ', 'wam<u>aa</u> arsaln<u>aa</u> qablaka ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim fa<strong>i</strong>s-aluu ahla <strong>al</strong><u>dzdz</u>ikri in kuntum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan Kami tidak mengutus (rasul-rasul) sebelum engkau (Muhammad), melainkan beberapa orang laki-laki yang Kami beri wahyu kepada mereka, maka tanyakanlah kepada orang yang berilmu, jika kamu tidak mengetahui.'),
	(2491, 21, 8, 'وَمَا جَعَلْنٰهُمْ جَسَدًا لَّا يَأْكُلُوْنَ الطَّعَامَ وَمَا كَانُوْا خٰلِدِيْنَ ', 'wam<u>aa</u> ja\'aln<u>aa</u>hum jasadan l<u>aa</u> ya/kuluuna <strong>al</strong><u>ththh</u>a\'<u>aa</u>ma wam<u>aa</u> k<u>aa</u>nuu kh<u>aa</u>lidiin<strong>a</strong>', 'Dan Kami tidak menjadikan mereka (rasul-rasul) suatu tubuh yang tidak memakan makanan dan mereka tidak (pula) hidup kekal.'),
	(2492, 21, 9, 'ثُمَّ صَدَقْنٰهُمُ الْوَعْدَ فَاَنْجَيْنٰهُمْ وَمَنْ نَّشَاۤءُ وَاَهْلَكْنَا الْمُسْرِفِيْنَ ', 'tsumma <u>sh</u>adaqn<u>aa</u>humu <strong>a</strong>lwa\'da fa-anjayn<u>aa</u>hum waman nasy<u>aa</u>u wa-ahlakn<u>aa</u> <strong>a</strong>lmusrifiin<strong>a</strong>', 'Kemudian Kami tepati janji (yang telah Kami janjikan) kepada mereka. Maka Kami selamatkan mereka dan orang-orang yang Kami kehendaki, dan Kami binasakan orang-orang yang melampaui batas.'),
	(2493, 21, 10, 'لَقَدْ اَنْزَلْنَآ اِلَيْكُمْ كِتٰبًا فِيْهِ ذِكْرُكُمْۗ اَفَلَا تَعْقِلُوْنَ ࣖ', 'laqad anzaln<u>aa</u> ilaykum kit<u>aa</u>ban fiihi <u>dz</u>ikrukum afal<u>aa</u> ta\'qiluun<strong>a</strong>', ' Sungguh, telah Kami turunkan kepadamu sebuah Kitab (Al-Qur\'an) yang di dalamnya terdapat peringatan bagimu. Maka apakah kamu tidak mengerti?'),
	(2494, 21, 11, 'وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَّاَنْشَأْنَا بَعْدَهَا قَوْمًا اٰخَرِيْنَ ', 'wakam qa<u>sh</u>amn<u>aa</u> min qaryatin k<u>aa</u>nat <u>zhaa</u>limatan wa-ansya/n<u>aa</u> ba\'dah<u>aa</u> qawman <u>aa</u>khariin<strong>a</strong>', ' Dan berapa banyak (penduduk) negeri yang zalim yang telah Kami binasakan, dan Kami jadikan generasi yang lain setelah mereka itu (sebagai penggantinya).'),
	(2495, 21, 12, 'فَلَمَّآ اَحَسُّوْا بَأْسَنَآ اِذَا هُمْ مِّنْهَا يَرْكُضُوْنَ ۗ', 'falamm<u>aa</u> a<u>h</u>assuu ba/san<u>aa</u> i<u>dzaa</u> hum minh<u>aa</u> yarku<u>dh</u>uun<strong>a</strong>', ' Maka ketika mereka merasakan azab Kami, tiba-tiba mereka melarikan diri dari (negerinya) itu.'),
	(2496, 21, 13, 'لَا تَرْكُضُوْا وَارْجِعُوْٓا اِلٰى مَآ اُتْرِفْتُمْ فِيْهِ وَمَسٰكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُوْنَ ', 'l<u>aa</u> tarku<u>dh</u>uu wa<strong>i</strong>rji\'uu il<u>aa</u> m<u>aa</u> utriftum fiihi wamas<u>aa</u>kinikum la\'allakum tus-aluun<strong>a</strong>', 'Janganlah kamu lari tergesa-gesa; kembalilah kamu kepada kesenangan hidupmu dan tempat-tempat kediamanmu (yang baik), agar kamu dapat ditanya.'),
	(2497, 21, 14, 'قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ', 'q<u>aa</u>luu y<u>aa</u> waylan<u>aa</u> inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin<strong>a</strong>', 'Mereka berkata, “Betapa celaka kami, sungguh, kami orang-orang yang zalim.”'),
	(2498, 21, 15, 'فَمَا زَالَتْ تِّلْكَ دَعْوٰىهُمْ حَتّٰى جَعَلْنٰهُمْ حَصِيْدًا خَامِدِيْنَ ', 'fam<u>aa</u> z<u>aa</u>lat tilka da\'w<u>aa</u>hum <u>h</u>att<u>aa</u> ja\'aln<u>aa</u>hum <u>h</u>a<u>sh</u>iidan kh<u>aa</u>midiin<strong>a</strong>', ' Maka demikianlah keluhan mereka berkepanjangan, sehingga mereka Kami jadikan sebagai tanaman yang telah dituai, yang tidak dapat hidup lagi.'),
	(2499, 21, 16, 'وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ ', 'wam<u>aa</u> khalaqn<u>aa</u> <strong>al</strong>ssam<u>aa</u>-a wa<strong>a</strong>l-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> l<u>aa</u>\'ibiin<strong>a</strong>', 'Dan Kami tidak menciptakan langit dan bumi dan segala apa yang ada di antara keduanya dengan main-main.'),
	(2500, 21, 17, 'لَوْ اَرَدْنَآ اَنْ نَّتَّخِذَ لَهْوًا لَّاتَّخَذْنٰهُ مِنْ لَّدُنَّآ  ۖاِنْ كُنَّا فٰعِلِيْنَ ', 'law aradn<u>aa</u> an nattakhi<u>dz</u>a lahwan la<strong>i</strong>ttakha<u>dz</u>n<u>aa</u>hu min ladunn<u>aa</u> in kunn<u>aa</u> f<u>aa</u>\'iliin<strong>a</strong>', ' Seandainya Kami hendak membuat suatu permainan (istri dan anak), tentulah Kami membuatnya dari sisi Kami, jika Kami benar-benar menghendaki berbuat demikian.'),
	(2501, 21, 18, 'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهٗ فَاِذَا هُوَ زَاهِقٌۗ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُوْنَ ', 'bal naq<u>dz</u>ifu bi<strong>a</strong>l<u>h</u>aqqi \'al<u>aa</u> <strong>a</strong>lb<u>aath</u>ili fayadmaghuhu fa-i<u>dzaa</u> huwa z<u>aa</u>hiqun walakumu <strong>a</strong>lwaylu mimm<u>aa</u> ta<u>sh</u>ifuun<strong>a</strong>', 'Sebenarnya Kami melemparkan yang hak (kebenaran) kepada yang batil (tidak benar) lalu yang hak itu menghancurkannya, maka seketika itu (yang batil) lenyap. Dan celaka kamu karena kamu menyifati (Allah dengan sifat-sifat yang tidak pantas bagi-Nya).'),
	(2502, 21, 19, 'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَمَنْ عِنْدَهٗ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَلَا يَسْتَحْسِرُوْنَ ۚ', 'walahu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i waman \'indahu l<u>aa</u> yastakbiruuna \'an \'ib<u>aa</u>datihi wal<u>aa</u> yasta<u>h</u>siruun<strong>a</strong>', 'Dan milik-Nya siapa yang di langit dan di bumi. Dan (malaikat-malaikat) yang di sisi-Nya, tidak mempunyai rasa angkuh untuk menyembah-Nya dan tidak (pula) merasa letih.'),
	(2503, 21, 20, 'يُسَبِّحُوْنَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُوْنَ ', 'yusabbi<u>h</u>uuna <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra l<u>aa</u> yafturuun<strong>a</strong>', 'Mereka (malaikat-malaikat) bertasbih tidak henti-hentinya malam dan siang.'),
	(2504, 21, 21, 'اَمِ اتَّخَذُوْٓا اٰلِهَةً مِّنَ الْاَرْضِ هُمْ يُنْشِرُوْنَ ', 'ami ittakha<u>dz</u>uu <u>aa</u>lihatan mina <strong>a</strong>l-ar<u>dh</u>i hum yunsyiruun<strong>a</strong>', 'Apakah mereka mengambil tuhan-tuhan dari bumi, yang dapat menghidupkan (orang-orang yang mati)?'),
	(2505, 21, 22, 'لَوْ كَانَ فِيْهِمَآ اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحٰنَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ ', 'law k<u>aa</u>na fiihim<u>aa</u> <u>aa</u>lihatun ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu lafasadat<u>aa</u> fasub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi rabbi <strong>a</strong>l\'arsyi \'amm<u>aa</u> ya<u>sh</u>ifuun<strong>a</strong>', 'Seandainya pada keduanya (di langit dan di bumi) ada tuhan-tuhan selain Allah, tentu keduanya telah binasa. Mahasuci Allah yang memiliki ‘Arsy, dari apa yang mereka sifatkan.'),
	(2506, 21, 23, 'لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُوْنَ ', 'l<u>aa</u> yus-alu \'amm<u>aa</u> yaf\'alu wahum yus-aluun<strong>a</strong>', 'Dia (Allah) tidak ditanya tentang apa yang dikerjakan, tetapi merekalah yang akan ditanya.'),
	(2507, 21, 24, 'اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً ۗقُلْ هَاتُوْا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَّعِيَ وَذِكْرُ مَنْ قَبْلِيْۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَۙ الْحَقَّ فَهُمْ مُّعْرِضُوْنَ ', 'ami ittakha<u>dz</u>uu min duunihi <u>aa</u>lihatan qul h<u>aa</u>tuu burh<u>aa</u>nakum h<u>aadzaa</u> <u>dz</u>ikru man ma\'iya wa<u>dz</u>ikru man qablii bal aktsaruhum l<u>aa</u> ya\'lamuuna <strong>a</strong>l<u>h</u>aqqa fahum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Atau apakah mereka mengambil tuhan-tuhan selain Dia? Katakanlah (Muhammad), “Kemukakanlah alasan-alasanmu! (Al-Qur\'an) ini adalah peringatan bagi orang yang bersamaku, dan peringatan bagi orang sebelumku.” Tetapi kebanyakan mereka tidak mengetahui yang hak (kebenaran), karena itu mereka berpaling.'),
	(2508, 21, 25, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ اِلَّا نُوْحِيْٓ اِلَيْهِ اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدُوْنِ ', 'wam<u>aa</u> arsaln<u>aa</u> min qablika min rasuulin ill<u>aa</u> nuu<u>h</u>ii ilayhi annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> an<u>aa</u> fa<strong>u</strong>\'buduun<strong>i</strong>', 'Dan Kami tidak mengutus seorang rasul pun sebelum engkau (Muhammad), melainkan Kami wahyukan kepadanya, bahwa tidak ada tuhan (yang berhak disembah) selain Aku, maka sembahlah Aku.'),
	(2509, 21, 26, 'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا سُبْحٰنَهٗ ۗبَلْ عِبَادٌ مُّكْرَمُوْنَ ۙ', 'waq<u>aa</u>luu ittakha<u>dz</u>a <strong>al</strong>rra<u>h</u>m<u>aa</u>nu waladan sub<u>haa</u>nahu bal \'ib<u>aa</u>dun mukramuun<strong>a</strong>', 'Dan mereka berkata, “Tuhan Yang Maha Pengasih telah menjadikan (malaikat) sebagai anak.” Mahasuci Dia. Sebenarnya mereka (para malaikat itu) adalah hamba-hamba yang dimuliakan,'),
	(2510, 21, 27, 'لَا يَسْبِقُوْنَهٗ بِالْقَوْلِ وَهُمْ بِاَمْرِهٖ يَعْمَلُوْنَ ', 'l<u>aa</u> yasbiquunahu bi<strong>a</strong>lqawli wahum bi-amrihi ya\'maluun<strong>a</strong>', 'mereka tidak berbicara mendahului-Nya dan mereka mengerjakan perintah-perintah-Nya.'),
	(2511, 21, 28, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُوْنَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِّنْ خَشْيَتِهٖ مُشْفِقُوْنَ ', 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wal<u>aa</u> yasyfa\'uuna ill<u>aa</u> limani irta<u>daa</u> wahum min khasyyatihi musyfiquun<strong>a</strong>', 'Dia (Allah) mengetahui segala sesuatu yang di hadapan mereka (malaikat) dan yang di belakang mereka, dan mereka tidak memberi syafaat melainkan kepada orang yang diridai (Allah), dan mereka selalu berhati-hati karena takut kepada-Nya.'),
	(2512, 21, 29, '۞ وَمَنْ يَّقُلْ مِنْهُمْ اِنِّيْٓ اِلٰهٌ مِّنْ دُوْنِهٖ فَذٰلِكَ نَجْزِيْهِ جَهَنَّمَۗ  كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ ࣖ', 'waman yaqul minhum innii il<u>aa</u>hun min duunihi fa<u>dzaa</u>lika najziihi jahannama ka<u>dzaa</u>lika najzii <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan barangsiapa di antara mereka berkata, “Sungguh, aku adalah tuhan selain Allah,” maka orang itu Kami beri balasan dengan Jahanam. Demikianlah Kami memberikan balasan kepada orang-orang yang zalim.'),
	(2513, 21, 30, 'اَوَلَمْ يَرَ الَّذِيْنَ كَفَرُوْٓا اَنَّ السَّمٰوٰتِ وَالْاَرْضَ كَانَتَا رَتْقًا فَفَتَقْنٰهُمَاۗ وَجَعَلْنَا مِنَ الْمَاۤءِ كُلَّ شَيْءٍ حَيٍّۗ  اَفَلَا يُؤْمِنُوْنَ ', 'awa lam yar<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu anna <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a k<u>aa</u>nat<u>aa</u> ratqan fafataqn<u>aa</u>hum<u>aa</u> waja\'aln<u>aa</u> mina <strong>a</strong>lm<u>aa</u>-i kulla syay-in <u>h</u>ayyin afal<u>aa</u> yu/minuun<strong>a</strong>', 'Dan apakah orang-orang kafir tidak mengetahui bahwa langit dan bumi keduanya dahulunya menyatu, kemudian Kami pisahkan antara keduanya; dan Kami jadikan segala sesuatu yang hidup berasal dari air; maka mengapa mereka tidak beriman?'),
	(2514, 21, 31, 'وَجَعَلْنَا فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِهِمْۖ وَجَعَلْنَا فِيْهَا فِجَاجًا سُبُلًا لَّعَلَّهُمْ يَهْتَدُوْنَ ', 'waja\'aln<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bihim waja\'aln<u>aa</u> fiih<u>aa </u>fij<u>aa</u>jan subulan la\'allahum yahtaduun<strong>a</strong>', 'Dan Kami telah menjadikan di bumi ini gunung-gunung yang kokoh agar ia (tidak) guncang bersama mereka, dan Kami jadikan (pula) di sana jalan-jalan yang luas, agar mereka mendapat petunjuk.'),
	(2515, 21, 32, 'وَجَعَلْنَا السَّمَاۤءَ سَقْفًا مَّحْفُوْظًاۚ وَهُمْ عَنْ اٰيٰتِهَا مُعْرِضُوْنَ ', 'waja\'aln<u>aa</u> <strong>al</strong>ssam<u>aa</u>-a saqfan ma<u>h</u>fuu<u>zh</u>an wahum \'an <u>aa</u>y<u>aa</u>tih<u>aa</u> mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan Kami menjadikan langit sebagai atap yang terpelihara, namun mereka tetap berpaling dari tanda-tanda (kebesaran Allah) itu (matahari, bulan, angin, awan, dan lain-lain).'),
	(2516, 21, 33, 'وَهُوَ الَّذِيْ خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۗ  كُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra wa<strong>al</strong>sysyamsa wa<strong>a</strong>lqamara kullun fii falakin yasba<u>h</u>uun<strong>a</strong>', 'Dan Dialah yang telah menciptakan malam dan siang, matahari dan bulan. Masing-masing beredar pada garis edarnya.'),
	(2517, 21, 34, 'وَمَا جَعَلْنَا لِبَشَرٍ مِّنْ قَبْلِكَ الْخُلْدَۗ اَفَا۟ىِٕنْ مِّتَّ فَهُمُ الْخٰلِدُوْنَ ', 'wam<u>aa</u> ja\'aln<u>aa</u> libasyarin min qablika <strong>a</strong>lkhulda afa-in mitta fahumu <strong>a</strong>lkh<u>aa</u>liduun<strong>a</strong>', 'Dan Kami tidak menjadikan hidup abadi bagi seorang manusia sebelum engkau (Muhammad); maka jika engkau wafat, apakah mereka akan kekal?'),
	(2518, 21, 35, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَنَبْلُوْكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۗوَاِلَيْنَا تُرْجَعُوْنَ ', 'kullu nafsin <u>dzaa</u>-iqatu <strong>a</strong>lmawti wanabluukum bi<strong>al</strong>sysyarri wa<strong>a</strong>lkhayri fitnatan wa-ilayn<u>aa</u> turja\'uun<strong>a</strong>', 'Setiap yang bernyawa akan merasakan mati. Kami akan menguji kamu dengan keburukan dan kebaikan sebagai cobaan. Dan kamu akan dikembalikan hanya kepada Kami.'),
	(2519, 21, 36, 'وَاِذَا رَاٰكَ الَّذِيْنَ كَفَرُوْٓا اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كٰفِرُوْنَ  ', 'wa-i<u>dzaa</u> ra<u>aa</u>ka <strong>al</strong>la<u>dz</u>iina kafaruu in yattakhi<u>dz</u>uunaka ill<u>aa</u> huzuwan ah<u>aadzaa</u> <strong>al</strong>la<u>dz</u>ii ya<u>dz</u>kuru <u>aa</u>lihatakum wahum bi<u>dz</u>ikri <strong>al</strong>rra<u>h</u>m<u>aa</u>ni hum k<u>aa</u>firuun<strong>a</strong>', 'Dan apabila orang-orang kafir itu melihat engkau (Muhammad), mereka hanya memperlakukan engkau menjadi bahan ejekan. (Mereka mengatakan), “Apakah ini orang yang mencela tuhan-tuhanmu?” Padahal mereka orang yang ingkar mengingat Allah Yang Maha Pengasih.'),
	(2520, 21, 37, 'خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۗ سَاُورِيْكُمْ اٰيٰتِيْ فَلَا تَسْتَعْجِلُوْنِ ', 'khuliqa <strong>a</strong>l-ins<u>aa</u>nu min \'ajalin sauriikum <u>aa</u>y<u>aa</u>tii fal<u>aa</u> tasta\'jiluun<strong>a</strong>', 'Manusia diciptakan (bersifat) tergesa-gesa. Kelak akan Aku perlihatkan kepadamu tanda-tanda (kekuasaan)-Ku. Maka janganlah kamu meminta Aku menyegerakannya.'),
	(2521, 21, 38, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> <strong>a</strong>lwa\'du in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan mereka berkata, “Kapankah janji itu (akan datang), jika kamu orang yang benar?”'),
	(2522, 21, 39, 'لَوْ يَعْلَمُ الَّذِيْنَ كَفَرُوْا حِيْنَ لَا يَكُفُّوْنَ عَنْ وُّجُوْهِهِمُ النَّارَ وَلَا عَنْ ظُهُوْرِهِمْ وَلَا هُمْ يُنْصَرُوْنَ ', 'law ya\'lamu <strong>al</strong>la<u>dz</u>iina kafaruu <u>h</u>iina l<u>aa</u> yakuffuuna \'an wujuuhihimu <strong>al</strong>nn<u>aa</u>ra wal<u>aa</u> \'an <u>zh</u>uhuurihim wal<u>aa</u> hum yun<u>sh</u>aruun<strong>a</strong>', 'Seandainya orang kafir itu mengetahui, ketika mereka itu tidak mampu mengelakkan api neraka dari wajah dan punggung mereka, sedang mereka tidak mendapat pertolongan (tentulah mereka tidak meminta disegerakan).'),
	(2523, 21, 40, 'بَلْ تَأْتِيْهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيْعُوْنَ رَدَّهَا وَلَا هُمْ يُنْظَرُوْنَ ', 'bal ta/tiihim baghtatan fatabhatuhum fal<u>aa</u> yasta<u>th</u>ii\'uuna raddah<u>aa</u> wal<u>aa</u> hum yun<u>zh</u>aruun<strong>a</strong>', ' Sebenarnya (hari Kiamat) itu akan datang kepada mereka secara tiba-tiba, lalu mereka menjadi panik; maka mereka tidak sanggup menolaknya dan tidak (pula) diberi penangguhan (waktu).'),
	(2524, 21, 41, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ', 'walaqadi istuhzi-a birusulin min qablika fa<u>haa</u>qa bi<strong>a</strong>lla<u>dz</u>iina sakhiruu minhum m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Dan sungguh, rasul-rasul sebelum engkau (Muhammad) pun telah diperolok-olokkan, maka turunlah (siksaan) kepada orang-orang yang mencemoohkan apa (rasul-rasul) yang selalu mereka perolok-olokkan.'),
	(2525, 21, 42, 'قُلْ مَنْ يَّكْلَؤُكُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۗ  بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُّعْرِضُوْنَ ', 'qul man yaklaukum bi<strong>a</strong>llayli wa<strong>al</strong>nnah<u>aa</u>ri mina <strong>al</strong>rra<u>h</u>m<u>aa</u>ni bal hum \'an <u>dz</u>ikri rabbihim mu\'ri<u>dh</u>uun<strong>a</strong>', 'Katakanlah, “Siapakah yang akan menjaga kamu pada waktu malam dan siang dari (siksaan) Allah Yang Maha Pengasih?” Tetapi mereka enggan mengingat Tuhan mereka.'),
	(2526, 21, 43, 'اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِّنْ دُوْنِنَاۗ  لَا يَسْتَطِيْعُوْنَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِّنَّا يُصْحَبُوْنَ', 'am lahum <u>aa</u>lihatun tamna\'uhum min duunin<u>aa</u> l<u>aa</u> yasta<u>th</u>ii\'uuna na<u>sh</u>ra anfusihim wal<u>aa</u> hum minn<u>aa</u> yu<u>sy</u>abuun<strong>a</strong>', 'Ataukah mereka mempunyai tuhan-tuhan yang dapat memelihara mereka dari (azab) Kami? Tuhan-tuhan mereka itu tidak sanggup menolong diri mereka sendiri dan tidak (pula) mereka dilindungi dari (azab) Kami.'),
	(2527, 21, 44, 'بَلْ مَتَّعْنَا هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۗ اَفَلَا يَرَوْنَ اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ اَفَهُمُ الْغٰلِبُوْنَ ', 'bal matta\'n<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wa<u>aa</u>b<u>aa</u>-ahum <u>h</u>att<u>aa</u> <u>th</u><u>aa</u>la \'alayhimu <strong>a</strong>l\'umuru afal<u>aa</u> yarawna ann<u>aa</u> na/tii <strong>a</strong>l-ar<u>dh</u>a nanqu<u>sh</u>uh<u>aa</u> min a<u>th</u>r<u>aa</u>fih<u>aa</u> afahumu <strong>a</strong>lgh<u>aa</u>libuun<strong>a</strong>', 'Sebenarnya Kami telah memberi mereka dan nenek moyang mereka kenikmatan (hidup di dunia) hingga panjang usia mereka. Maka apakah mereka tidak melihat bahwa Kami mendatangi negeri (yang berada di bawah kekuasaan orang kafir), lalu Kami kurangi luasnya dari ujung-ujung negeri. Apakah mereka yang menang?'),
	(2528, 21, 45, 'قُلْ اِنَّمَآ اُنْذِرُكُمْ بِالْوَحْيِۖ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاۤءَ اِذَا مَا يُنْذَرُوْنَ ', 'qul innam<u>aa</u> un<u>dz</u>irukum bi<strong>a</strong>lwa<u>h</u>yi wal<u>aa</u> yasma\'u <strong>al</strong><u>shsh</u>ummu <strong>al</strong>ddu\'<u>aa</u>-a i<u>dzaa</u> m<u>aa</u> yun<u>dz</u>aruun<strong>a</strong>', 'Katakanlah (Muhammad), “Sesungguhnya aku hanya memberimu peringatan sesuai dengan wahyu.” Tetapi orang tuli tidak mendengar seruan apabila mereka diberi peringatan.'),
	(2529, 21, 46, 'وَلَىِٕنْ مَّسَّتْهُمْ نَفْحَةٌ مِّنْ عَذَابِ رَبِّكَ لَيَقُوْلُنَّ يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ', 'wala-in massat-hum naf<u>h</u>atun min \'a<u>dzaa</u>bi rabbika layaquulunna y<u>aa </u>waylan<u>aa</u> inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin<strong>a</strong>', 'Dan jika mereka ditimpa sedikit saja azab Tuhanmu, pastilah mereka berkata, “Celakalah kami! Sesungguhnya kami termasuk orang yang selalu menzalimi (diri sendiri).”'),
	(2530, 21, 47, 'وَنَضَعُ الْمَوَازِيْنَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـًٔاۗ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ اَتَيْنَا بِهَاۗ وَكَفٰى بِنَا حَاسِبِيْنَ ', 'wana<u>dh</u>a\'u <strong>a</strong>lmaw<u>aa</u>ziina <strong>a</strong>lqis<u>th</u>a liyawmi <strong>a</strong>lqiy<u>aa</u>mati fal<u>aa</u> tu<u>zh</u>lamu nafsun syay-an wa-in k<u>aa</u>na mitsq<u>aa</u>la <u>h</u>abbatin min khardalin atayn<u>aa</u> bih<u>aa</u> wakaf<u>aa</u> bin<u>aa</u> <u>haa</u>sibiin<strong>a</strong>', 'Dan Kami akan memasang timbangan yang tepat pada hari Kiamat, maka tidak seorang pun dirugikan walau sedikit; sekalipun hanya seberat biji sawi, pasti Kami mendatangkannya (pahala). Dan cukuplah Kami yang membuat perhitungan.'),
	(2531, 21, 48, 'وَلَقَدْ اٰتَيْنَا مُوْسٰى وَهٰرُوْنَ الْفُرْقَانَ وَضِيَاۤءً وَّذِكْرًا لِّلْمُتَّقِيْنَ ۙ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> wah<u>aa</u>ruuna <strong>a</strong>lfurq<u>aa</u>na wa<u>dh</u>iy<u>aa</u>-an wa<u>dz</u>ikran lilmuttaqiin<strong>a</strong>', 'Dan sungguh, Kami telah memberikan kepada Musa dan Harun, Furqan (Kitab Taurat) dan penerangan serta pelajaran bagi orang-orang yang bertakwa.'),
	(2532, 21, 49, 'الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِّنَ السَّاعَةِ مُشْفِقُوْنَ ', '<strong>al</strong>la<u>dz</u>iina yakhsyawna rabbahum bi<strong>a</strong>lghaybi wahum mina <strong>al</strong>ss<u>aa</u>\'ati musyfiquun<strong>a</strong>', '(Yaitu) orang-orang yang takut (azab) Tuhannya, sekalipun mereka tidak melihat-Nya, dan mereka merasa takut akan (tibanya) hari Kiamat.'),
	(2533, 21, 50, 'وَهٰذَا ذِكْرٌ مُّبٰرَكٌ اَنْزَلْنٰهُۗ اَفَاَنْتُمْ لَهٗ مُنْكِرُوْنَ ࣖ', 'wah<u>aadzaa</u> <u>dz</u>ikrun mub<u>aa</u>rakun anzaln<u>aa</u>hu afa-antum lahu munkiruun<strong>a</strong>', 'Dan ini (Al-Qur\'an) adalah suatu peringatan yang mempunyai berkah yang telah Kami turunkan. Maka apakah kamu mengingkarinya?'),
	(2534, 21, 51, '۞ وَلَقَدْ اٰتَيْنَآ اِبْرٰهِيْمَ رُشْدَهٗ مِنْ قَبْلُ وَكُنَّا بِهٖ عٰلِمِيْنَ ', 'walaqad <u>aa</u>tayn<u>aa</u> ibr<u>aa</u>hiima rusydahu min qablu wakunn<u>aa</u> bihi \'<u>aa</u>limiin<strong>a</strong>', 'Dan sungguh, sebelum dia (Musa dan Harun) telah Kami berikan kepada Ibrahim petunjuk, dan Kami telah mengetahui dia.'),
	(2535, 21, 52, 'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا هٰذِهِ التَّمَاثِيْلُ الَّتِيْٓ اَنْتُمْ لَهَا عَاكِفُوْنَ ', 'i<u>dz</u> q<u>aa</u>la li-abiihi waqawmihi m<u>aa</u> h<u>aadz</u>ihi <strong>al</strong>ttam<u>aa</u>tsiilu <strong>al</strong>latii antum lah<u>aa</u> \'<u>aa</u>kifuun<strong>a</strong>', '(Ingatlah), ketika dia (Ibrahim) berkata kepada ayahnya dan kaumnya, “Patung-patung apakah ini yang kamu tekun menyembahnya?”'),
	(2536, 21, 53, 'قَالُوْا وَجَدْنَآ اٰبَاۤءَنَا لَهَا عٰبِدِيْنَ ', 'q<u>aa</u>luu wajadn<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> lah<u>aa</u> \'<u>aa</u>bidiin<strong>a</strong>', 'Mereka menjawab, “Kami mendapati nenek moyang kami menyembahnya.”'),
	(2537, 21, 54, 'قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَاۤؤُكُمْ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'q<u>aa</u>la laqad kuntum antum wa<u>aa</u>b<u>aa</u>ukum fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Dia (Ibrahim) berkata, “Sesungguhnya kamu dan nenek moyang kamu berada dalam kesesatan yang nyata.”'),
	(2538, 21, 55, 'قَالُوْٓا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللّٰعِبِيْنَ ', 'q<u>aa</u>luu aji/tan<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi am anta mina <strong>al</strong>l<u>aa</u>\'ibiin<strong>a</strong>', 'Mereka berkata, “Apakah engkau da-tang kepada kami membawa kebenaran atau engkau main-main?”'),
	(2539, 21, 56, 'قَالَ بَلْ رَّبُّكُمْ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ الَّذِيْ فَطَرَهُنَّۖ وَاَنَا۠ عَلٰى ذٰلِكُمْ مِّنَ الشّٰهِدِيْنَ ', 'q<u>aa</u>la bal rabbukum rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <strong>al</strong>la<u>dz</u>ii fa<u>th</u>arahunna wa-an<u>aa</u> \'al<u>aa</u> <u>dzaa</u>likum mina <strong>al</strong>sysy<u>aa</u>hidiin<strong>a</strong>', 'Dia (Ibrahim) menjawab, “Sebenarnya Tuhan kamu ialah Tuhan (pemilik) langit dan bumi; (Dialah) yang telah menciptakannya; dan aku termasuk orang yang dapat bersaksi atas itu.”'),
	(2540, 21, 57, 'وَتَاللّٰهِ لَاَكِيْدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوْا مُدْبِرِيْنَ ', 'wata<strong>al</strong>l<u>aa</u>hi la-akiidanna a<u>sh</u>n<u>aa</u>makum ba\'da an tuwalluu mudbiriin<strong>a</strong>', 'Dan demi Allah, sungguh, aku akan melakukan tipu daya terhadap berhala-berhalamu setelah kamu pergi meninggalkannya.'),
	(2541, 21, 58, 'فَجَعَلَهُمْ جُذَاذًا اِلَّا كَبِيْرًا لَّهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُوْنَ ', 'faja\'alahum ju<u>dzats</u>an ill<u>aa</u> kabiiran lahum la\'allahum ilayhi yarji\'uun<strong>a</strong>', 'Maka dia (Ibrahim) menghancurkan (berhala-berhala itu) berkeping-keping, kecuali yang terbesar (induknya); agar mereka kembali (untuk bertanya) kepadanya.'),
	(2542, 21, 59, 'قَالُوْا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَآ اِنَّهٗ لَمِنَ الظّٰلِمِيْنَ ', 'q<u>aa</u>luu man fa\'ala h<u>aadzaa</u> bi-<u>aa</u>lihatin<u>aa</u> innahu lamina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Mereka berkata, “Siapakah yang melakukan (perbuatan) ini terhadap tuhan-tuhan kami? Sungguh, dia termasuk orang yang zalim.”'),
	(2543, 21, 60, 'قَالُوْا سَمِعْنَا فَتًى يَّذْكُرُهُمْ يُقَالُ لَهٗٓ اِبْرٰهِيْمُ ۗ', 'q<u>aa</u>luu sami\'n<u>aa</u> fatan ya<u>dz</u>kuruhum yuq<u>aa</u>lu lahu ibr<u>aa</u>hiim<strong>u</strong>', 'Mereka (yang lain) berkata, “Kami mendengar ada seorang pemuda yang mencela (berhala-berhala ini), namanya Ibrahim.”'),
	(2544, 21, 61, 'قَالُوْا فَأْتُوْا بِهٖ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُوْنَ ', 'q<u>aa</u>luu fa/tuu bihi \'al<u>aa</u> a\'yuni <strong>al</strong>nn<u>aa</u>si la\'allahum yasyhaduun<strong>a</strong>', 'Mereka berkata, “(Kalau demikian) bawalah dia dengan diperlihatkan kepada orang banyak, agar mereka menyaksikan.”'),
	(2545, 21, 62, 'قَالُوْٓا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يٰٓاِبْرٰهِيْمُ ۗ', 'q<u>aa</u>luu a-anta fa\'alta h<u>aadzaa</u> bi-<u>aa</u>lihatin<u>aa</u> y<u>aa</u> ibr<u>aa</u>hiim<strong>u</strong>', ' Mereka bertanya, “Apakah engkau yang melakukan (perbuatan) ini terhadap tuhan-tuhan kami, wahai Ibrahim?”'),
	(2546, 21, 63, 'قَالَ بَلْ فَعَلَهٗ كَبِيْرُهُمْ هٰذَا فَسْـَٔلُوْهُمْ اِنْ كَانُوْا يَنْطِقُوْنَ ', 'q<u>aa</u>la bal fa\'alahu kabiiruhum h<u>aadzaa</u> fa<strong>i</strong>s-aluuhum in k<u>aa</u>nuu yan<u>th</u>iquun<strong>a</strong>', 'Dia (Ibrahim) menjawab, “Sebenarnya (patung) besar itu yang melakukannya, maka tanyakanlah kepada mereka, jika mereka dapat berbicara.”'),
	(2547, 21, 64, 'فَرَجَعُوْٓا اِلٰٓى اَنْفُسِهِمْ فَقَالُوْٓا اِنَّكُمْ اَنْتُمُ الظّٰلِمُوْنَ ۙ', 'faraja\'uu il<u>aa</u> anfusihim faq<u>aa</u>luu innakum antumu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Maka mereka kembali kepada kesadaran mereka dan berkata, “Sesungguhnya kamulah yang menzalimi (diri sendiri).”'),
	(2548, 21, 65, 'ثُمَّ نُكِسُوْا عَلٰى رُءُوْسِهِمْۚ  لَقَدْ عَلِمْتَ مَا هٰٓؤُلَاۤءِ يَنْطِقُوْنَ ', 'tsumma nukisuu \'al<u>aa</u> ruuusihim laqad \'alimta m<u>aa</u> h<u>aa</u>ul<u>aa</u>-i yan<u>th</u>iquun<strong>a</strong>', 'Kemudian mereka menundukkan kepala (lalu berkata), “Engkau (Ibrahim) pasti tahu bahwa (berhala-berhala) itu tidak dapat berbicara.”'),
	(2549, 21, 66, 'قَالَ اَفَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـًٔا وَّلَا يَضُرُّكُمْ ۗ', 'q<u>aa</u>la afata\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'ukum syay-an wal<u>aa</u> ya<u>dh</u>urrukum', 'Dia (Ibrahim) berkata, “Mengapa kamu menyembah selain Allah, sesuatu yang tidak dapat memberi manfaat sedikit pun, dan tidak (pula) mendatangkan mudarat kepada kamu?'),
	(2550, 21, 67, 'اُفٍّ لَّكُمْ وَلِمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَفَلَا تَعْقِلُوْنَ ', 'uffin lakum walim<u>aa</u> ta\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Celakalah kamu dan apa yang kamu sembah selain Allah! Tidakkah kamu mengerti?”'),
	(2551, 21, 68, 'قَالُوْا حَرِّقُوْهُ وَانْصُرُوْٓا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فٰعِلِيْنَ ', 'q<u>aa</u>luu <u>h</u>arriquuhu wa<strong>u</strong>n<u>sh</u>uruu <u>aa</u>lihatakum in kuntum f<u>aa</u>\'iliin<strong>a</strong>', ' Mereka berkata, “Bakarlah dia dan bantulah tuhan-tuhan kamu, jika kamu benar-benar hendak berbuat.”'),
	(2552, 21, 69, 'قُلْنَا يَا نَارُ كُوْنِيْ بَرْدًا وَّسَلٰمًا عَلٰٓى اِبْرٰهِيْمَ ۙ', 'quln<u>aa</u> y<u>aa</u> n<u>aa</u>ru kuunii bardan wasal<u>aa</u>man \'al<u>aa</u> ibr<u>aa</u>hiim<strong>a</strong>', 'Kami (Allah) berfirman, “Wahai api! Jadilah kamu dingin, dan penyelamat bagi Ibrahim!”'),
	(2553, 21, 70, 'وَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَخْسَرِيْنَ ۚ', 'wa-ar<u>aa</u>duu bihi kaydan faja\'aln<u>aa</u>humu <strong>a</strong>l-akhsariin<strong>a</strong>', 'Dan mereka hendak berbuat jahat terhadap Ibrahim, maka Kami menjadikan mereka itu orang-orang yang paling rugi.'),
	(2554, 21, 71, 'وَنَجَّيْنٰهُ وَلُوْطًا اِلَى الْاَرْضِ الَّتِيْ بٰرَكْناَ فِيْهَا لِلْعٰلَمِيْنَ ', 'wanajjayn<u>aa</u>hu waluu<u>th</u>an il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i <strong>al</strong>latii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> lil\'<u>aa</u>lamiin<strong>a</strong>', 'Dan Kami selamatkan dia (Ibrahim) dan Lut ke sebuah negeri yang telah Kami berkahi untuk seluruh alam.'),
	(2555, 21, 72, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ نَافِلَةً ۗوَكُلًّا جَعَلْنَا صٰلِحِيْنَ ', 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba n<u>aa</u>filatan wakullan ja\'aln<u>aa</u> <u>shaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan Kami menganugerahkan kepadanya (Ibrahim) Ishak dan Yakub, sebagai suatu anugerah. Dan masing-masing Kami jadikan orang yang saleh.'),
	(2556, 21, 73, 'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا وَاَوْحَيْنَآ اِلَيْهِمْ فِعْلَ الْخَيْرٰتِ وَاِقَامَ الصَّلٰوةِ وَاِيْتَاۤءَ الزَّكٰوةِۚ وَكَانُوْا لَنَا عٰبِدِيْنَ ۙ', 'waja\'aln<u>aa</u>hum a-immatan yahduuna bi-amrin<u>aa</u> wa-aw<u>h</u>ayn<u>aa</u> ilayhim fi\'la <strong>a</strong>lkhayr<u>aa</u>ti wa-iq<u>aa</u>ma <strong>al</strong><u>shsh</u>al<u>aa</u>ti wa-iit<u>aa</u>-a <strong>al</strong>zzak<u>aa</u>ti wak<u>aa</u>nuu lan<u>aa</u> \'<u>aa</u>bidiin<strong>a</strong>', 'Dan Kami menjadikan mereka itu sebagai pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami dan Kami wahyukan kepada mereka agar berbuat kebaikan, melaksanakan salat dan menunaikan zakat, dan hanya kepada Kami mereka menyembah.'),
	(2557, 21, 74, 'وَلُوْطًا اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا وَّنَجَّيْنٰهُ مِنَ الْقَرْيَةِ الَّتِيْ كَانَتْ تَّعْمَلُ الْخَبٰۤىِٕثَ ۗاِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فٰسِقِيْنَۙ', 'waluu<u>th</u>an <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman wanajjayn<u>aa</u>hu mina <strong>a</strong>lqaryati <strong>al</strong>latii k<u>aa</u>nat ta\'malu <strong>a</strong>lkhab<u>aa</u>-itsa innahum k<u>aa</u>nuu qawma saw-in f<u>aa</u>siqiin<strong>a</strong>', 'Kepada Lut, Kami berikan hikmah dan ilmu, dan Kami selamatkan dia dari (azab yang telah menimpa penduduk) kota yang melakukan perbuatan keji. Sungguh, mereka orang-orang yang jahat lagi fasik.  '),
	(2558, 21, 75, 'وَاَدْخَلْنٰهُ فِيْ رَحْمَتِنَاۗ اِنَّهٗ مِنَ الصّٰلِحِيْنَ ࣖ ', 'wa-adkhaln<u>aa</u>hu fii ra<u>h</u>matin<u>aa</u> innahu mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', ' Dan Kami masukkan dia ke dalam rahmat Kami; sesungguhnya dia termasuk golongan orang yang saleh.'),
	(2559, 21, 76, 'وَنُوْحًا اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهٗ فَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِ ۚ', 'wanuu<u>h</u>an i<u>dz</u> n<u>aa</u>d<u>aa</u> min qablu fa<strong>i</strong>stajabn<u>aa</u> lahu fanajjayn<u>aa</u>hu wa-ahlahu mina <strong>a</strong>lkarbi <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Dan (ingatlah kisah) Nuh, sebelum itu, ketika dia berdoa. Kami perkenankan (doa)nya, lalu Kami selamatkan dia bersama pengikutnya dari bencana yang besar.'),
	(2560, 21, 77, 'وَنَصَرْنٰهُ مِنَ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَ ', 'wana<u>sh</u>arn<u>aa</u>hu mina <strong>a</strong>lqawmi <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> innahum k<u>aa</u>nuu qawma saw-in fa-aghraqn<u>aa</u>hum ajma\'iin<strong>a</strong>', ' Dan Kami menolongnya dari orang-orang yang telah mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah orang-orang yang jahat, maka Kami tenggelamkan mereka semuanya.'),
	(2561, 21, 78, 'وَدَاوٗدَ وَسُلَيْمٰنَ اِذْ يَحْكُمٰنِ فِى الْحَرْثِ اِذْ نَفَشَتْ فِيْهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شٰهِدِيْنَ ۖ', 'wad<u>aa</u>wuuda wasulaym<u>aa</u>na i<u>dz</u> ya<u>h</u>kum<u>aa</u>ni fii <strong>a</strong>l<u>h</u>artsi i<u>dz</u> nafasyat fiihi ghanamu <strong>a</strong>lqawmi wakunn<u>aa</u> li<u>h</u>ukmihim sy<u>aa</u>hidiin<strong>a</strong>', ' Dan (ingatlah kisah) Dawud dan Sulaiman, ketika keduanya memberikan keputusan mengenai ladang, karena (ladang itu) dirusak oleh kambing-kambing milik kaumnya. Dan Kami menyaksikan keputusan (yang diberikan) oleh mereka itu.'),
	(2562, 21, 79, 'فَفَهَّمْنٰهَا سُلَيْمٰنَۚ وَكُلًّا اٰتَيْنَا حُكْمًا وَّعِلْمًاۖ وَّسَخَّرْنَا مَعَ دَاوٗدَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۗ وَكُنَّا فٰعِلِيْنَ', 'fafahhamn<u>aa</u>h<u>aa</u> sulaym<u>aa</u>na wakullan <u>aa</u>tayn<u>aa</u> <u>h</u>ukman wa\'ilman wasakhkharn<u>aa</u> ma\'a d<u>aa</u>wuuda <strong>a</strong>ljib<u>aa</u>la yusabbi<u>h</u>na wa<strong>al</strong><u>ththh</u>ayra wakunn<u>aa</u> f<u>aa</u>\'iliin<strong>a</strong>', ' Dan Kami memberikan pengertian kepada Sulaiman (tentang hukum yang lebih tepat); dan kepada masing-masing Kami berikan hikmah dan ilmu, dan Kami tundukkan gunung-gunung dan burung-burung, semua bertasbih bersama Dawud. Dan Kamilah yang melakukannya.'),
	(2563, 21, 80, 'وَعَلَّمْنٰهُ صَنْعَةَ لَبُوْسٍ لَّكُمْ لِتُحْصِنَكُمْ مِّنْۢ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُوْنَ', 'wa\'allamn<u>aa</u>hu <u>sh</u>an\'ata labuusin lakum litu<u>hs</u>inakum min ba/sikum fahal antum sy<u>aa</u>kiruun<strong>a</strong>', 'Dan Kami ajarkan (pula) kepada Dawud cara membuat baju besi untukmu, guna melindungi kamu dalam peperangan. Apakah kamu bersyukur (kepada Allah)?'),
	(2564, 21, 81, 'وَلِسُلَيْمٰنَ الرِّيْحَ عَاصِفَةً تَجْرِيْ بِاَمْرِهٖٓ اِلَى الْاَرْضِ الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَكُنَّا بِكُلِّ شَيْءٍ عٰلِمِيْنَ', 'walisulaym<u>aa</u>na <strong>al</strong>rrii<u>h</u>a \'<u>aas</u>ifatan tajrii bi-amrihi il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i <strong>al</strong>latii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> wakunn<u>aa</u> bikulli syay-in \'<u>aa</u>limiin<strong>a</strong>', 'Dan (Kami tundukkan) untuk Sulaiman angin yang sangat kencang tiupannya yang berhembus dengan perintahnya ke negeri yang Kami beri berkah padanya. Dan Kami Maha Mengetahui segala sesuatu.'),
	(2565, 21, 82, 'وَمِنَ الشَّيٰطِيْنِ مَنْ يَّغُوْصُوْنَ لَهٗ وَيَعْمَلُوْنَ عَمَلًا دُوْنَ ذٰلِكَۚ وَكُنَّا لَهُمْ حٰفِظِيْنَ ۙ', 'wamina <strong>al</strong>sysyay<u>aath</u>iini man yaghuu<u>sh</u>uuna lahu waya\'maluuna \'amalan duuna <u>dzaa</u>lika wakunn<u>aa</u> lahum <u>haa</u>fi<u>zh</u>iin<strong>a</strong>', 'Dan (Kami tundukkan pula kepada Sulaiman) segolongan setan-setan yang menyelam (ke dalam laut) untuknya dan mereka mengerjakan pekerjaan selain  itu; dan  Kami yang memelihara mereka itu.'),
	(2566, 21, 83, '۞ وَاَيُّوْبَ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِمِيْنَ ۚ', 'wa-ayyuuba i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu annii massaniya <strong>al</strong><u>dhdh</u>urru wa-anta ar<u>h</u>amu <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Dan (ingatlah kisah) Ayub, ketika dia berdoa kepada Tuhannya, “(Ya Tuhanku), sungguh, aku telah ditimpa penyakit, padahal Engkau Tuhan Yang Maha Penyayang dari semua yang penyayang.”'),
	(2567, 21, 84, 'فَاسْتَجَبْنَا لَهٗ فَكَشَفْنَا مَا بِهٖ مِنْ ضُرٍّ وَّاٰتَيْنٰهُ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنْ عِنْدِنَا وَذِكْرٰى لِلْعٰبِدِيْنَ ۚ', 'fa<strong>i</strong>stajabn<u>aa</u> lahu fakasyafn<u>aa</u> m<u>aa</u> bihi min <u>dh</u>urrin wa<u>aa</u>tayn<u>aa</u>hu ahlahu wamitslahum ma\'ahum ra<u>h</u>matan min \'indin<u>aa</u> wa<u>dz</u>ikr<u>aa</u> lil\'<u>aa</u>bidiin<strong>a</strong>', 'Maka Kami kabulkan (doa)nya, lalu Kami lenyapkan penyakit yang ada padanya dan Kami kembalikan keluarganya kepadanya, dan (Kami lipat gandakan jumlah mereka) sebagai suatu rahmat dari Kami, dan untuk menjadi peringatan bagi semua yang menyembah Kami.'),
	(2568, 21, 85, 'وَاِسْمٰعِيْلَ وَاِدْرِيْسَ وَذَا الْكِفْلِۗ  كُلٌّ مِّنَ الصّٰبِرِيْنَ ۙ', 'wa-ism<u>aa</u>\'iila wa-idriisa wa<u>dzaa</u> <strong>a</strong>lkifli kullun mina <strong>al</strong><u>shshaa</u>biriin<strong>a</strong>', ' Dan (ingatlah kisah) Ismail, Idris dan Zulkifli. Mereka semua termasuk orang-orang yang sabar.'),
	(2569, 21, 86, 'وَاَدْخَلْنٰهُمْ فِيْ رَحْمَتِنَاۗ اِنَّهُمْ مِّنَ الصّٰلِحِيْنَ ', 'wa-adkhaln<u>aa</u>hum fii ra<u>h</u>matin<u>aa</u> innahum mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan Kami masukkan mereka ke dalam rahmat Kami. Sungguh, mereka termasuk orang-orang yang saleh.'),
	(2570, 21, 87, 'وَذَا النُّوْنِ اِذْ ذَّهَبَ مُغَاضِبًا فَظَنَّ اَنْ لَّنْ نَّقْدِرَ عَلَيْهِ فَنَادٰى فِى الظُّلُمٰتِ اَنْ لَّآ اِلٰهَ اِلَّآ اَنْتَ سُبْحٰنَكَ اِنِّيْ كُنْتُ مِنَ الظّٰلِمِيْنَ ۚ', 'wa<u>dzaa</u> <strong>al</strong>nnuuni i<u>dz</u> <u>dz</u>ahaba mugh<u>aad</u>iban fa<u>zh</u>anna an lan naqdira \'alayhi fan<u>aa</u>d<u>aa</u> fii <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti an l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> anta sub<u>haa</u>naka innii kuntu mina <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan (ingatlah kisah) Zun Nun (Yunus), ketika dia pergi dalam keadaan marah, lalu dia menyangka bahwa Kami tidak akan menyulitkannya, maka dia berdoa dalam keadaan yang sangat gelap, ”Tidak ada tuhan selain Engkau, Mahasuci Engkau. Sungguh, aku  termasuk orang-orang yang zalim.”'),
	(2571, 21, 88, 'فَاسْتَجَبْنَا لَهٗۙ وَنَجَّيْنٰهُ مِنَ الْغَمِّۗ وَكَذٰلِكَ نُـْۨجِى الْمُؤْمِنِيْنَ ', 'fa<strong>i</strong>stajabn<u>aa</u> lahu wanajjayn<u>aa</u>hu mina <strong>a</strong>lghammi waka<u>dzaa</u>lika nunjii <strong>a</strong>lmu/miniin<strong>a</strong>', 'Maka Kami kabulkan (doa)nya dan Kami selamatkan dia dari kedukaan. Dan demikianlah Kami menyelamatkan orang-orang yang beriman.'),
	(2572, 21, 89, 'وَزَكَرِيَّآ اِذْ نَادٰى رَبَّهٗ رَبِّ لَا تَذَرْنِيْ فَرْدًا وَّاَنْتَ خَيْرُ الْوٰرِثِيْنَ ۚ', 'wazakariyy<u>aa</u> i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu rabbi l<u>aa</u> ta<u>dz</u>arnii fardan wa-anta khayru <strong>a</strong>lw<u>aa</u>ritsiin<strong>a</strong>', ' Dan (ingatlah kisah) Zakaria, ketika dia berdoa kepada Tuhannya, “Ya Tuhanku, janganlah Engkau biarkan aku hidup seorang diri (tanpa keturunan) dan Engkaulah ahli waris yang terbaik.'),
	(2573, 21, 90, 'فَاسْتَجَبْنَا لَهٗ  ۖوَوَهَبْنَا لَهٗ يَحْيٰى وَاَصْلَحْنَا لَهٗ زَوْجَهٗۗ اِنَّهُمْ كَانُوْا يُسٰرِعُوْنَ فِى الْخَيْرٰتِ وَيَدْعُوْنَنَا رَغَبًا وَّرَهَبًاۗ وَكَانُوْا لَنَا خٰشِعِيْنَ ', 'fa<strong>i</strong>stajabn<u>aa</u> lahu wawahabn<u>aa</u> lahu ya<u>h</u>y<u>aa</u> wa-a<u>sh</u>la<u>h</u>n<u>aa</u> lahu zawjahu innahum k<u>aa</u>nuu yus<u>aa</u>ri\'uuna fii <strong>a</strong>lkhayr<u>aa</u>ti wayad\'uunan<u>aa</u> raghaban warahaban wak<u>aa</u>nuu lan<u>aa</u> kh<u>aa</u>syi\'iin<strong>a</strong>', 'Maka Kami kabulkan (doa)nya, dan Kami anugerahkan kepadanya Yahya, dan Kami jadikan istrinya (dapat mengandung). Sungguh, mereka selalu bersegera dalam (mengerjakan) kebaikan, dan mereka berdoa kepada Kami dengan penuh harap dan cemas. Dan mereka orang-orang yang khusyuk kepada Kami.'),
	(2574, 21, 91, 'وَالَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهَا مِنْ رُّوْحِنَا وَجَعَلْنٰهَا وَابْنَهَآ اٰيَةً لِّلْعٰلَمِيْنَ ', 'wa<strong>a</strong>llatii a<u>hs</u>anat farjah<u>aa</u> fanafakhn<u>aa</u> fiih<u>aa</u> min ruu<u>h</u>in<u>aa</u> waja\'aln<u>aa</u>h<u>aa</u> wa<strong>i</strong>bnah<u>aa</u> <u>aa</u>yatan lil\'<u>aa</u>lamiin<strong>a</strong>', 'Dan (ingatlah kisah Maryam) yang memelihara kehormatannya, lalu Kami tiupkan (roh) dari Kami ke dalam (tubuh)nya; Kami jadikan dia dan anaknya sebagai tanda (kebesaran Allah)  bagi seluruh alam.'),
	(2575, 21, 92, 'اِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةًۖ وَّاَنَا۠ رَبُّكُمْ فَاعْبُدُوْنِ ', 'inna h<u>aadz</u>ihi ummatukum ummatan w<u>aah</u>idatan wa-an<u>aa</u> rabbukum fau\'buduun<strong>i</strong>', 'Sungguh, (agama tauhid) inilah agama kamu, agama yang satu, dan Aku adalah Tuhanmu, maka sembahlah Aku.'),
	(2576, 21, 93, 'وَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْۗ كُلٌّ اِلَيْنَا رَاجِعُوْنَ ࣖ', 'wataqa<u>ththh</u>a\'uu amrahum baynahum kullun ilayn<u>aa</u> r<u>aa</u>ji\'uun<strong>a</strong>', 'Tetapi mereka terpecah belah dalam urusan (agama) mereka di antara mereka. Masing-masing (golongan itu semua) akan kembali kepada Kami.'),
	(2577, 21, 94, 'فَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهٖۚ وَاِنَّا لَهٗ كَاتِبُوْنَ ', 'faman ya\'mal mina <strong>al</strong><u>shshaa</u>li<u>haa</u>ti wahuwa mu/minun fal<u>aa</u> kufr<u>aa</u>na lisa\'yihi wa-inn<u>aa</u> lahu k<u>aa</u>tibuun<strong>a</strong>', 'Barangsiapa mengerjakan kebajikan, dan dia beriman, maka usahanya tidak akan diingkari (disia-siakan), dan sungguh, Kamilah yang mencatat untuknya.'),
	(2578, 21, 95, 'وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنٰهَآ اَنَّهُمْ لَا يَرْجِعُوْنَ ', 'wa<u>h</u>ar<u>aa</u>mun \'al<u>aa</u> qaryatin ahlakn<u>aa</u>h<u>aa</u> annahum l<u>aa</u> yarji\'uun<strong>a</strong>', 'Dan tidak mungkin bagi (penduduk) suatu negeri yang telah Kami binasakan, bahwa mereka tidak akan kembali (kepada Kami).'),
	(2579, 21, 96, 'حَتّٰىٓ اِذَا فُتِحَتْ يَأْجُوْجُ وَمَأْجُوْجُ وَهُمْ مِّنْ كُلِّ حَدَبٍ يَّنْسِلُوْنَ ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> futi<u>h</u>at ya/juuju wama/juuju wahum min kulli <u>h</u>adabin yansiluun<strong>a</strong>', 'Hingga apabila (tembok) Yakjuj dan Makjuj dibukakan dan mereka turun dengan cepat dari seluruh tempat yang tinggi.'),
	(2580, 21, 97, 'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذِيْنَ كَفَرُوْاۗ يٰوَيْلَنَا قَدْ كُنَّا فِيْ غَفْلَةٍ مِّنْ هٰذَا بَلْ كُنَّا ظٰلِمِيْنَ ', 'wa<strong>i</strong>qtaraba <strong>a</strong>lwa\'du <strong>a</strong>l<u>h</u>aqqu fa-i<u>dzaa</u> hiya sy<u>aa</u>khi<u>sh</u>atun ab<u>shaa</u>ru <strong>al</strong>la<u>dz</u>iina kafaruu y<u>aa</u> waylan<u>aa</u> qad kunn<u>aa</u> fii ghaflatin min h<u>aadzaa</u> bal kunn<u>aa</u> <u>zhaa</u>limiin<strong>a</strong>', 'Dan (apabila) janji yang benar (hari berbangkit) telah dekat, maka tiba-tiba mata orang-orang yang kafir terbelalak. (Mereka berkata), ”Alangkah celakanya kami! Kami benar-benar lengah tentang ini, bahkan kami benar-benar orang yang zalim.”'),
	(2581, 21, 98, 'اِنَّكُمْ وَمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ حَصَبُ جَهَنَّمَۗ اَنْتُمْ لَهَا وَارِدُوْنَ ', 'innakum wam<u>aa</u> ta\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi <u>h</u>a<u>sh</u>abu jahannama antum lah<u>aa</u> w<u>aa</u>riduun<strong>a</strong>', 'Sungguh, kamu (orang kafir) dan apa yang kamu sembah selain Allah, adalah bahan bakar Jahanam. Kamu (pasti) masuk ke dalamnya.'),
	(2582, 21, 99, 'لَوْ كَانَ هٰٓؤُلَاۤءِ اٰلِهَةً مَّا وَرَدُوْهَاۗ وَكُلٌّ فِيْهَا خٰلِدُوْنَ ', 'law k<u>aa</u>na h<u>aa</u>ul<u>aa</u>-i <u>aa</u>lihatan m<u>aa</u> waraduuh<u>aa</u> wakullun fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', 'Seandainya (berhala-berhala) itu tuhan, tentu mereka tidak akan memasukinya (neraka). Tetapi semuanya akan kekal di dalamnya.'),
	(2583, 21, 100, 'لَهُمْ فِيْهَا زَفِيْرٌ وَّهُمْ فِيْهَا لَا يَسْمَعُوْنَ ', 'lahum fiih<u>aa</u> zafiirun wahum fiih<u>aa</u> l<u>aa</u> yasma\'uun<strong>a</strong>', 'Mereka merintih dan menjerit di dalamnya (neraka), dan mereka di dalamnya tidak dapat mendengar.'),
	(2584, 21, 101, 'اِنَّ الَّذِيْنَ سَبَقَتْ لَهُمْ مِّنَّا الْحُسْنٰىٓۙ اُولٰۤىِٕكَ عَنْهَا مُبْعَدُوْنَ ۙ', 'inna <strong>al</strong>la<u>dz</u>iina sabaqat lahum minn<u>aa</u> <strong>a</strong>l<u>h</u>usn<u>aa</u> ul<u>aa</u>-ika \'anh<u>aa </u>mub\'aduun<strong>a</strong>', 'Sungguh, sejak dahulu bagi orang-orang yang telah ada (ketetapan) yang baik dari Kami, mereka itu akan dijauhkan (dari neraka).'),
	(2585, 21, 102, 'لَا يَسْمَعُوْنَ حَسِيْسَهَاۚ وَهُمْ فِيْ مَا اشْتَهَتْ اَنْفُسُهُمْ خٰلِدُوْنَ ۚ', 'l<u>aa</u> yasma\'uuna <u>h</u>asiisah<u>aa</u> wahum fii m<u>aa</u> isytahat anfusuhum kh<u>aa</u>liduun<strong>a</strong>', 'Mereka tidak mendengar bunyi desis (api neraka), dan mereka kekal dalam (menikmati) semua yang mereka ingini.  '),
	(2586, 21, 103, 'لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰىهُمُ الْمَلٰۤىِٕكَةُۗ هٰذَا يَوْمُكُمُ الَّذِيْ كُنْتُمْ تُوْعَدُوْنَ ', 'l<u>aa</u> ya<u>h</u>zunuhumu <strong>a</strong>lfaza\'u <strong>a</strong>l-akbaru watatalaqq<u>aa</u>humu <strong>a</strong>lmal<u>aa</u>-ikatu h<u>aadzaa</u> yawmukumu <strong>al</strong>la<u>dz</u>ii kuntum tuu\'aduun<strong>a</strong>', 'Kejutan yang dahsyat tidak membuat mereka merasa sedih, dan para malaikat akan menyambut mereka (dengan ucapan), “Inilah harimu yang telah dijanjikan kepadamu.”'),
	(2587, 21, 104, 'يَوْمَ نَطْوِى السَّمَاۤءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۗ  كَمَا بَدَأْنَآ اَوَّلَ خَلْقٍ نُّعِيْدُهٗۗ وَعْدًا عَلَيْنَاۗ اِنَّا كُنَّا فٰعِلِيْنَ ', 'yawma na<u>th</u>wii <strong>al</strong>ssam<u>aa</u>-a ka<u>th</u>ayyi <strong>al</strong>ssijlli lilkutubi kam<u>aa</u> bada/n<u>aa</u> awwala khalqin nu\'iiduhu wa\'dan \'alayn<u>aa</u> inn<u>aa</u> kunn<u>aa</u> f<u>aa</u>\'iliin<strong>a</strong>', '(Ingatlah) pada hari langit Kami gulung seperti menggulung lembaran-lembaran kertas. Sebagaimana Kami telah memulai penciptaan pertama, begitulah Kami akan mengulanginya lagi. (Suatu) janji yang pasti Kami tepati; sungguh, Kami akan melaksanakannya.'),
	(2588, 21, 105, 'وَلَقَدْ كَتَبْنَا فِى الزَّبُوْرِ مِنْۢ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصّٰلِحُوْنَ ', 'walaqad katabn<u>aa</u> fii <strong>al</strong>zzabuuri min ba\'di <strong>al</strong><u>dzdz</u>ikri anna <strong>a</strong>l-ar<u>dh</u>a yaritsuh<u>aa</u> \'ib<u>aa</u>diya <strong>al</strong><u>shshaa</u>li<u>h</u>uun<strong>a</strong>', 'Dan sungguh, telah Kami tulis di dalam Zabur setelah (tertulis) di dalam Az-Zikr (Lauh Mahfuzh), bahwa bumi ini akan diwarisi oleh hamba-hamba-Ku yang saleh.'),
	(2589, 21, 106, 'اِنَّ فِيْ هٰذَا لَبَلٰغًا لِّقَوْمٍ عٰبِدِيْنَ ۗ', 'inna fii h<u>aadzaa</u> labal<u>aa</u>ghan liqawmin \'<u>aa</u>bidiin<strong>a</strong>', 'Sungguh, (apa yang disebutkan) di dalam (Al-Qur\'an) ini, benar-benar menjadi petunjuk (yang lengkap) bagi orang-orang yang menyembah (Allah).'),
	(2590, 21, 107, 'وَمَآ اَرْسَلْنٰكَ اِلَّا رَحْمَةً لِّلْعٰلَمِيْنَ ', 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> ra<u>h</u>matan lil\'<u>aa</u>lamiin<strong>a</strong>', 'Dan Kami tidak mengutus engkau (Muhammad) melainkan untuk (menjadi) rahmat bagi seluruh alam.'),
	(2591, 21, 108, 'قُلْ اِنَّمَا يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَهَلْ اَنْتُمْ مُّسْلِمُوْنَ', 'qul innam<u>aa</u> yuu<u>haa</u> ilayya annam<u>aa</u> il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun fahal antum muslimuun<strong>a</strong>', 'Katakanlah (Muhammad), “Sungguh, apa yang diwahyukan kepadaku ialah bahwa Tuhanmu adalah Tuhan Yang Esa, maka apakah kamu telah berserah diri (kepada-Nya)?”'),
	(2592, 21, 109, 'فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَاۤءٍۗ وَاِنْ اَدْرِيْٓ اَقَرِيْبٌ اَمْ بَعِيْدٌ مَّا تُوْعَدُوْنَ ', 'fa-in tawallaw faqul <u>aadz</u>antukum \'al<u>aa</u> saw<u>aa</u>-in wa-in adrii aqariibun am ba\'iidun m<u>aa</u> tuu\'aduun<strong>a</strong>', 'Maka jika mereka berpaling, maka katakanlah (Muhammad), “Aku telah menyampaikan kepadamu (ajaran) yang sama (antara kita) dan aku tidak tahu apakah yang diancamkan kepadamu itu sudah dekat atau masih jauh.”'),
	(2593, 21, 110, 'اِنَّهٗ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُوْنَ ', 'innahu ya\'lamu <strong>a</strong>ljahra mina <strong>a</strong>lqawli waya\'lamu m<u>aa</u> taktumuun<strong>a</strong>', 'Sungguh, Dia (Allah) mengetahui perkataan (yang kamu ucapkan) dengan terang-terangan, dan mengetahui (pula) apa yang kamu rahasiakan.'),
	(2594, 21, 111, 'وَاِنْ اَدْرِيْ لَعَلَّهٗ فِتْنَةٌ لَّكُمْ وَمَتَاعٌ اِلٰى حِيْنٍ ', 'wa-in adrii la\'allahu fitnatun lakum wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Dan aku tidak tahu, boleh jadi hal itu cobaan bagi kamu dan kesenangan sampai waktu yang ditentukan.'),
	(2595, 21, 112, 'قَالَ رَبِّ احْكُمْ بِالْحَقِّۗ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ ࣖ', 'q<u>aa</u>la rabbi u<u>h</u>kum bi<strong>a</strong>l<u>h</u>aqqi warabbun<u>aa</u> <strong>al</strong>rra<u>h</u>m<u>aa</u>nu <strong>a</strong>lmusta\'<u>aa</u>nu \'al<u>aa</u> m<u>aa</u> ta<u>sh</u>ifuun<strong>a</strong>', 'Dia (Muhammad) berkata, “Ya Tuhanku, berilah keputusan dengan adil. Dan Tuhan kami Maha Pengasih, tempat memohon segala pertolongan atas semua yang kamu katakan.”'),
	(2596, 22, 1, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيْمٌ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su ittaquu rabbakum inna zalzalata <strong>al</strong>ss<u>aa</u>\'ati syay-un \'a<u>zh</u>iim<strong>un</strong>', 'Wahai manusia! Bertakwalah kepada Tuhanmu; sungguh, guncangan (hari) Kiamat itu adalah suatu (kejadian) yang sangat besar.'),
	(2597, 22, 2, 'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّآ اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكٰرٰى وَمَا هُمْ بِسُكٰرٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَدِيْدٌ ', 'yawma tarawnah<u>aa</u> ta<u>dz</u>halu kullu mur<u>dh</u>i\'atin \'amm<u>aa</u> ar<u>dh</u>a\'at wata<u>dh</u>a\'u kullu <u>dzaa</u>ti <u>h</u>amlin <u>h</u>amlah<u>aa</u> watar<u>aa</u> <strong>al</strong>nn<u>aa</u>sa suk<u>aa</u>r<u>aa</u> wam<u>aa</u> hum bisuk<u>aa</u>r<u>aa</u> wal<u>aa</u>kinna \'a<u>dzaa</u>ba <strong>al</strong>l<u>aa</u>hi syadiid<strong>un</strong>', '(Ingatlah) pada hari ketika kamu melihatnya (goncangan itu), semua perempuan yang menyusui anaknya akan lalai terhadap anak yang disusuinya, dan setiap perempuan yang hamil akan keguguran kandungannya, dan kamu melihat manusia dalam keadaan mabuk, padahal sebenarnya mereka tidak mabuk, tetapi azab Allah itu sangat keras.'),
	(2598, 22, 3, 'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّيَتَّبِعُ كُلَّ شَيْطٰنٍ مَّرِيْدٍۙ ', 'wamina <strong>al</strong>nn<u>aa</u>si man yuj<u>aa</u>dilu fii <strong>al</strong>l<u>aa</u>hi bighayri \'ilmin wayattabi\'u kulla syay<u>th</u><u>aa</u>nin mariid<strong>in</strong>', 'Dan di antara manusia ada yang berbantahan tentang Allah tanpa ilmu dan hanya mengikuti para setan yang sangat jahat.'),
	(2599, 22, 4, 'كُتِبَ عَلَيْهِ اَنَّهٗ مَنْ تَوَلَّاهُ فَاَنَّهٗ يُضِلُّهٗ وَيَهْدِيْهِ اِلٰى عَذَابِ السَّعِيْرِ ', 'kutiba \'alayhi annahu man tawall<u>aa</u>hu fa-annahu yu<u>dh</u>illuhu wayahdiihi il<u>aa</u> \'a<u>dzaa</u>bi <strong>al</strong>ssa\'iir<strong>i</strong>', '(Tentang setan), telah ditetapkan bahwa siapa yang berkawan dengan dia, maka dia akan menyesatkannya, dan membawanya ke azab neraka.'),
	(2600, 22, 5, 'يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ رَيْبٍ مِّنَ الْبَعْثِ فَاِنَّا خَلَقْنٰكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُّضْغَةٍ مُّخَلَّقَةٍ وَّغَيْرِ مُخَلَّقَةٍ لِّنُبَيِّنَ لَكُمْۗ وَنُقِرُّ فِى الْاَرْحَامِ مَا نَشَاۤء', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su in kuntum fii raybin mina <strong>a</strong>lba\'tsi fa-inn<u>aa</u> khalaqn<u>aa</u>kum min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma min \'alaqatin tsumma min mu<u>dh</u>ghatin mukhallaqatin waghayri mukhallaqatin linubayyina lakum wanuqirru fii <strong>a</strong>l-ar<u>haa</u>mi m<u>aa</u> nasy<u>aa</u>u il<u>aa</u> ajalin musamman tsumma nukhrijukum <u>th</u>iflan tsumma litablughuu asyuddakum waminkum man yutawaff<u>aa</u> waminkum man yuraddu il<u>aa</u> ar<u>dz</u>ali <strong>a</strong>l\'umuri likayl<u>aa</u> ya\'lama min ba\'di \'ilmin syay-an watar<u>aa</u> <strong>a</strong>l-ar<u>dh</u>a h<u>aa</u>midatan fa-i<u>dzaa</u> anzaln<u>aa</u> \'alayh<u>aa</u> <strong>a</strong>lm<u>aa</u>-a ihtazzat warabat wa-anbatat min kulli zawjin bahiij<strong>in</strong>', 'Wahai manusia! Jika kamu meragukan (hari) kebangkitan, maka sesungguhnya Kami telah menjadikan kamu dari tanah, kemudian dari setetes mani, kemudian dari segumpal darah, kemudian dari segumpal daging yang sempurna kejadiannya dan yang tidak sempurna, agar Kami jelaskan kepada kamu; dan Kami tetapkan dalam rahim menurut kehendak Kami sampai waktu yang sudah ditentukan, kemudian Kami keluarkan kamu sebagai bayi, kemudian (dengan berangsur-angsur) kamu sampai kepada usia dewasa, dan di antara kamu ada yang diwafatkan dan (ada pula) di antara kamu yang dikembalikan sampai usia sangat tua (pikun), sehingga dia tidak mengetahui lagi sesuatu yang telah diketahuinya. Dan kamu lihat bumi ini kering, kemudian apabila telah Kami turunkan air (hujan) di atasnya, hiduplah bumi itu dan menjadi subur dan menumbuhkan berbagai jenis pasangan (tetumbuhan) yang indah.'),
	(2601, 22, 6, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهٗ يُحْيِ الْمَوْتٰى وَاَنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۙ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l<u>h</u>aqqu wa-annahu yu<u>h</u>yii <strong>a</strong>lmawt<u>aa</u> wa-annahu \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Yang demikian itu karena sungguh, Allah, Dialah yang hak dan sungguh, Dialah yang menghidupkan segala yang telah mati, dan sungguh, Dia Mahakuasa atas segala sesuatu.'),
	(2602, 22, 7, 'وَّاَنَّ السَّاعَةَ اٰتِيَةٌ لَّا رَيْبَ فِيْهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِى الْقُبُوْرِ ', 'wa-anna <strong>al</strong>ss<u>aa</u>\'ata <u>aa</u>tiyatun l<u>aa</u> rayba fiih<u>aa</u> wa-anna <strong>al</strong>l<u>aa</u>ha yab\'atsu man fii <strong>a</strong>lqubuur<strong>i</strong>', 'Dan sungguh, (hari) Kiamat itu pasti datang, tidak ada keraguan padanya; dan sungguh, Allah akan membangkitkan siapa pun yang di dalam kubur.'),
	(2603, 22, 8, 'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ ۙ', 'wamina <strong>al</strong>nn<u>aa</u>si man yuj<u>aa</u>dilu fii <strong>al</strong>l<u>aa</u>hi bighayri \'ilmin wal<u>aa</u> hudan wal<u>aa</u> kit<u>aa</u>bin muniir<strong>in</strong>', 'Dan di antara manusia ada yang berbantahan tentang Allah tanpa ilmu, tanpa petunjuk dan tanpa kitab (wahyu) yang memberi penerangan.'),
	(2604, 22, 9, 'ثَانِيَ عِطْفِهٖ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ ۗ لَهٗ فِى الدُّنْيَا خِزْيٌ وَّنُذِيْقُهٗ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَرِيْقِ', 'ts<u>aa</u>niya \'i<u>th</u>fihi liyu<u>dh</u>illa \'an sabiili <strong>al</strong>l<u>aa</u>hi lahu fii <strong>al</strong>dduny<u>aa</u> khizyun wanu<u>dz</u>iiquhu yawma <strong>a</strong>lqiy<u>aa</u>mati \'a<u>dzaa</u>ba <strong>a</strong>l<u>h</u>ariiq<strong>i</strong>', 'Sambil memalingkan lambungnya (dengan congkak) untuk menyesatkan manusia dari jalan Allah. Dia mendapat kehinaan di dunia, dan pada hari Kiamat Kami berikan kepadanya rasa azab neraka yang membakar.'),
	(2605, 22, 10, 'ذٰلِكَ بِمَا قَدَّمَتْ يَدٰكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِ ࣖ', '<u>dzaa</u>lika bim<u>aa</u> qaddamat yad<u>aa</u>ka wa-anna <strong>al</strong>l<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid<strong>i</strong>', '(Akan dikatakan kepadanya), “Itu karena perbuatan yang dilakukan dahulu oleh kedua tanganmu, dan Allah sekali-kali tidak menzalimi hamba-hamba-Nya.'),
	(2606, 22, 11, 'وَمِنَ النَّاسِ مَنْ يَّعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهٗ خَيْرُ ِۨاطْمَـَٔنَّ بِهٖۚ وَاِنْ اَصَابَتْهُ فِتْنَةُ ِۨانْقَلَبَ عَلٰى وَجْهِهٖۗ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۗ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ ', 'wamina <strong>al</strong>nn<u>aa</u>si man ya\'budu <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> <u>h</u>arfin fa-in a<u>shaa</u>bahu khayrun i<u>th</u>ma-anna bihi wa-in a<u>shaa</u>bat-hu fitnatun inqalaba \'al<u>aa</u> wajhihi khasira <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirata <u>dzaa</u>lika huwa <strong>a</strong>lkhusr<u>aa</u>nu <strong>a</strong>lmubiin<strong>u</strong>', 'Dan di antara manusia ada yang menyembah Allah hanya di tepi; maka jika dia memperoleh kebajikan, dia merasa puas, dan jika dia ditimpa suatu cobaan, dia berbalik ke belakang. Dia rugi di dunia dan di akhirat. Itulah kerugian yang nyata.'),
	(2607, 22, 12, 'يَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهٗ وَمَا لَا يَنْفَعُهٗۗ ذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ۚ', 'yad\'uu min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> ya<u>dh</u>urruhu wam<u>aa</u> l<u>aa</u> yanfa\'uhu <u>dzaa</u>lika huwa <strong>al</strong><u>dhdh</u>al<u>aa</u>lu <strong>a</strong>lba\'iid<strong>u</strong>', 'Dia menyeru kepada selain Allah sesuatu yang tidak dapat mendatangkan bencana dan tidak (pula) memberi manfaat kepadanya. Itulah kesesatan yang jauh.'),
	(2608, 22, 13, 'يَدْعُوْا لَمَنْ ضَرُّهٗٓ اَقْرَبُ مِنْ نَّفْعِهٖۗ  لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَشِيْرُ ', 'yad\'uu laman <u>dh</u>arruhu aqrabu min naf\'ihi labi/sa <strong>a</strong>lmawl<u>aa</u> walabi/sa <strong>a</strong>l\'asyiir<strong>u</strong>', 'Dia menyeru kepada sesuatu yang (sebenarnya) bencananya lebih dekat daripada manfaatnya. Sungguh, itu seburuk-buruk penolong dan sejahat-jahat kawan.'),
	(2609, 22, 14, 'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ ', 'inna <strong>al</strong>l<u>aa</u>ha yudkhilu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru inna <strong>al</strong>l<u>aa</u>ha yaf\'alu m<u>aa</u> yuriid<strong>u</strong>', '(Sungguh,) Allah akan memasukkan orang-orang yang beriman dan mengerjakan kebajikan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Sungguh, Allah berbuat apa yang Dia kehendaki.'),
	(2610, 22, 15, 'مَنْ كَانَ يَظُنُّ اَنْ لَّنْ يَّنْصُرَهُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَاۤءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهٗ مَا يَغِيْظُ ', 'man k<u>aa</u>na ya<u>zh</u>unnu an lan yan<u>sh</u>urahu <strong>al</strong>l<u>aa</u>hu fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati falyamdud bisababin il<u>aa</u> <strong>al</strong>ssam<u>aa</u>-i tsumma liyaq<u>th</u>a\' falyan<u>zh</u>ur hal yu<u>dz</u>hibanna kayduhu m<u>aa</u> yaghii<u>zh</u><strong>u</strong>', 'Barangsiapa menyangka bahwa Allah tidak akan menolongnya (Muhammad) di dunia dan di akhirat, maka hendaklah dia merentangkan tali ke langit-langit, ) lalu menggantung (diri), kemudian pikirkanlah apakah tipu dayanya itu dapat melenyapkan apa yang menyakitkan hatinya.'),
	(2611, 22, 16, 'وَكَذٰلِكَ اَنْزَلْنٰهُ اٰيٰتٍۢ بَيِّنٰتٍۙ وَّاَنَّ اللّٰهَ يَهْدِيْ مَنْ يُّرِيْدُ ', 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin wa-anna <strong>al</strong>l<u>aa</u>ha yahdii man yuriid<strong>u</strong>', 'Dan demikianlah Kami telah menurunkannya (Al-Qur\'an) yang merupakan ayat-ayat yang nyata; sesungguhnya Allah memberikan petunjuk kepada siapa yang Dia kehendaki.'),
	(2612, 22, 17, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـِٕيْنَ وَالنَّصٰرٰى وَالْمَجُوْسَ وَالَّذِيْنَ اَشْرَكُوْٓا ۖاِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>duu wa<strong>al</strong><u>shshaa</u>bi-iina wa<strong>al</strong>nna<u>shaa</u>r<u>aa</u> wa<strong>a</strong>lmajuusa wa<strong>a</strong>lla<u>dz</u>iina asyrakuu inna <strong>al</strong>l<u>aa</u>ha yaf<u>sh</u>ilu baynahum yawma <strong>a</strong>lqiy<u>aa</u>mati inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in syahiid<strong>un</strong>', 'Sesungguhnya orang-orang beriman, orang Yahudi, orang Sabiin, orang Nasrani, orang Majusi dan orang musyrik, Allah pasti memberi keputusan di antara mereka pada hari Kiamat. Sungguh, Allah menjadi saksi atas segala sesuatu.'),
	(2613, 22, 18, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُوْمُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَاۤبُّ وَكَثِيْرٌ مِّنَ النَّاسِۗ وَكَثِيْرٌ حَقَّ عَلَيْهِ الْعَذَابُۗ وَمَنْ يُّهِنِ اللّٰهُ فَمَا ل', 'alam tara anna <strong>al</strong>l<u>aa</u>ha yasjudu lahu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti waman fii <strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong>sysyamsu wa<strong>a</strong>lqamaru wa<strong>al</strong>nnujuumu wa<strong>a</strong>ljib<u>aa</u>lu wa<strong>al</strong>sysyajaru wa<strong>al</strong>ddaw<u>aa</u>bbu wakatsiirun mina <strong>al</strong>nn<u>aa</u>si wakatsiirun <u>h</u>aqqa \'alayhi <strong>a</strong>l\'a<u>dzaa</u>bu waman yuhini <strong>al</strong>l<u>aa</u>hu fam<u>aa</u> lahu min mukrimin inna <strong>al</strong>l<u>aa</u>ha yaf\'alu m<u>aa</u> yasy<u>aa</u>/<strong>u</strong>', 'Tidakkah engkau tahu bahwa siapa yang ada di langit dan siapa yang ada di bumi bersujud kepada Allah, juga matahari, bulan, bintang, gunung-gunung, pohon-pohon, hewan-hewan yang melata dan banyak di antara manusia? Tetapi banyak (manusia) yang pantas mendapatkan azab. Barangsiapa dihinakan Allah, tidak seorang pun yang akan memuliakannya. Sungguh, Allah berbuat apa saja yang Dia kehendaki.'),
	(2614, 22, 19, '۞ هٰذَانِ خَصْمٰنِ اخْتَصَمُوْا فِيْ رَبِّهِمْ  فَالَّذِيْنَ كَفَرُوْا قُطِّعَتْ لَهُمْ ثِيَابٌ مِّنْ نَّارٍۗ يُصَبُّ مِنْ فَوْقِ رُءُوْسِهِمُ الْحَمِيْمُ ۚ', 'h<u>aadzaa</u>ni kha<u>sh</u>m<u>aa</u>ni ikhta<u>sh</u>amuu fii rabbihim fa<strong>a</strong>lla<u>dz</u>iina kafaruu qu<u>ththh</u>i\'at lahum tsiy<u>aa</u>bun min n<u>aa</u>rin yu<u>sh</u>abbu min fawqi ruuusihimu <strong>a</strong>l<u>h</u>amiim<strong>u</strong>', 'Inilah dua golongan (golongan mukmin dan kafir) yang bertengkar, mereka bertengkar mengenai Tuhan mereka. Maka bagi orang kafir akan dibuatkan pakaian-pakaian dari api (neraka) untuk mereka. Ke atas kepala mereka akan disiramkan air yang mendidih.'),
	(2615, 22, 20, 'يُصْهَرُ بِهٖ مَا فِيْ بُطُوْنِهِمْ وَالْجُلُوْدُ ۗ', 'yu<u>sh</u>haru bihi m<u>aa</u> fii bu<u>th</u>uunihim wa<strong>a</strong>ljuluud<strong>u</strong>', 'Dengan (air mendidih) itu akan dihancurluluhkan apa yang ada dalam perut dan kulit mereka.'),
	(2616, 22, 21, 'وَلَهُمْ مَّقَامِعُ مِنْ حَدِيْدٍ ', 'walahum maq<u>aa</u>mi\'u min <u>h</u>adiid<strong>in</strong>', 'Dan (azab) untuk mereka cambuk-cambuk dari besi.'),
	(2617, 22, 22, 'كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَا مِنْ غَمٍّ اُعِيْدُوْا فِيْهَا وَذُوْقُوْا عَذَابَ الْحَرِيْقِ ࣖ', 'kullam<u>aa</u> ar<u>aa</u>duu an yakhrujuu minh<u>aa</u> min ghammin u\'iiduu fiih<u>aa</u> wa<u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>a</strong>l<u>h</u>ariiq<strong>i</strong>', 'Setiap kali mereka hendak keluar darinya (neraka) karena tersiksa, mereka dikembalikan (lagi) ke dalamnya. (Kepada mereka dikatakan), “Rasakanlah azab yang membakar ini!”'),
	(2618, 22, 23, 'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًاۗ وَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ', 'inna <strong>al</strong>l<u>aa</u>ha yudkhilu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin walu/lu-an walib<u>aa</u>suhum fiih<u>aa</u> <u>h</u>ariir<strong>un</strong>', 'Sungguh, Allah akan memasukkan orang-orang yang beriman dan mengerjakan kebajikan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Di sana mereka diberi perhiasan gelang-gelang emas dan mutiara, dan pakaian mereka dari sutera.'),
	(2619, 22, 24, 'وَهُدُوْٓا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۚ وَهُدُوْٓا اِلٰى صِرَاطِ الْحَمِيْدِ ', 'wahuduu il<u>aa</u> <strong>al</strong><u>ththh</u>ayyibi mina <strong>a</strong>lqawli wahuduu il<u>aa</u> <u>sh</u>ir<u>aath</u>i <strong>a</strong>l<u>h</u>amiid<strong>i</strong>', 'Dan mereka diberi petunjuk kepada ucapan-ucapan yang baik dan diberi petunjuk (pula) kepada jalan (Allah) yang terpuji.'),
	(2620, 22, 25, 'اِنَّ الَّذِيْنَ كَفَرُوْا وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِيْ جَعَلْنٰهُ لِلنَّاسِ سَوَاۤءً ۨالْعَاكِفُ فِيْهِ وَالْبَادِۗ وَمَنْ يُّرِدْ فِيْهِ بِاِلْحَادٍۢ بِظُلْمٍ نُّذِقْهُ مِنْ عَذَابٍ اَلِيْمٍ ࣖ', 'inna <strong>al</strong>la<u>dz</u>iina kafaruu waya<u>sh</u>udduuna \'an sabiili <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lmasjidi <strong>a</strong>l<u>h</u>ar<u>aa</u>mi <strong>al</strong>la<u>dz</u>ii ja\'aln<u>aa</u>hu li<strong>l</strong>nn<u>aa</u>si saw<u>aa</u>-an <strong>a</strong>l\'<u>aa</u>kifu fiihi wa<strong>a</strong>lb<u>aa</u>di waman yurid fiihi bi-il<u>haa</u>din bi<u>zh</u>ulmin nu<u>dz</u>iqhu min \'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Sungguh, orang-orang kafir dan yang menghalangi (manusia) dari jalan Allah dan dari Masjidilharam yang telah Kami jadikan terbuka untuk semua manusia, baik yang bermukim di sana maupun yang datang dari luar dan siapa saja yang bermaksud melakukan kejahatan secara zalim di dalamnya, niscaya akan Kami rasakan kepadanya siksa yang pedih.'),
	(2621, 22, 26, 'وَاِذْ بَوَّأْنَا لِاِبْرٰهِيْمَ مَكَانَ الْبَيْتِ اَنْ لَّا تُشْرِكْ بِيْ شَيْـًٔا وَّطَهِّرْ بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْقَاۤىِٕمِيْنَ وَالرُّكَّعِ السُّجُوْدِ ', 'wa-i<u>dz</u> bawwa/n<u>aa</u> li-ibr<u>aa</u>hiima mak<u>aa</u>na <strong>a</strong>lbayti an l<u>aa</u> tusyrik bii syay-an wa<u>th</u>ahhir baytiya li<strong>l</strong><u>ththaa</u>-ifiina wa<strong>a</strong>lq<u>aa</u>-imiina wa<strong>al</strong>rrukka\'i <strong>al</strong>ssujuud<strong>i</strong>', 'Dan (ingatlah), ketika Kami tempatkan Ibrahim di tempat Baitullah (dengan mengatakan), “Janganlah engkau mempersekutukan Aku dengan apa pun dan sucikanlah rumah-Ku bagi orang-orang yang tawaf, dan orang yang beribadah dan orang yang rukuk dan sujud.'),
	(2622, 22, 27, 'وَاَذِّنْ فِى النَّاسِ بِالْحَجِّ يَأْتُوْكَ رِجَالًا وَّعَلٰى كُلِّ ضَامِرٍ يَّأْتِيْنَ مِنْ كُلِّ فَجٍّ عَمِيْقٍ ۙ', 'wa-a<u>dzdz</u>in fii <strong>al</strong>nn<u>aa</u>si bi<strong>a</strong>l<u>h</u>ajji ya/tuuka rij<u>aa</u>lan wa\'al<u>aa</u> kulli <u>daa</u>mirin ya/tiina min kulli fajjin \'amiiq<strong>in</strong>', 'Dan serulah manusia untuk mengerjakan haji, niscaya mereka akan datang kepadamu dengan berjalan kaki, atau mengendarai setiap unta yang kurus, mereka datang dari segenap penjuru yang jauh.'),
	(2623, 22, 28, 'لِّيَشْهَدُوْا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ فِيْٓ اَيَّامٍ مَّعْلُوْمٰتٍ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۚ فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْبَاۤىِٕسَ الْفَقِيْرَ ۖ', 'liyasyhaduu man<u>aa</u>fi\'a lahum waya<u>dz</u>kuruu isma <strong>al</strong>l<u>aa</u>hi fii ayy<u>aa</u>min ma\'luum<u>aa</u>tin \'al<u>aa</u> m<u>aa</u> razaqahum min bahiimati <strong>a</strong>l-an\'<u>aa</u>mi fakuluu minh<u>aa</u> wa-a<u>th</u>\'imuu <strong>a</strong>lb<u>aa</u>-isa <strong>a</strong>lfaqiir<strong>a</strong>', 'Agar mereka menyaksikan berbagai manfaat untuk mereka dan agar mere-ka menyebut nama Allah pada beberapa hari yang telah ditentukan atas rezeki yang diberikan Dia kepada mereka berupa hewan ternak. Maka makanlah sebagian darinya dan (sebagian lagi) berikanlah untuk dimakan orang-orang yang sengsara dan fakir.'),
	(2624, 22, 29, 'ثُمَّ لْيَقْضُوْا تَفَثَهُمْ وَلْيُوْفُوْا نُذُوْرَهُمْ وَلْيَطَّوَّفُوْا بِالْبَيْتِ الْعَتِيْقِ ', 'tsumma lyaq<u>dh</u>uu tafatsahum walyuufuu nu<u>dz</u>uurahum walya<u>ththh</u>awwafuu bi<strong>a</strong>lbayti <strong>a</strong>l\'atiiq<strong>i</strong>', 'Kemudian, hendaklah mereka menghilangkan kotoran (yang ada di badan) mereka, menyempurnakan nazar-nazar mereka dan melakukan tawaf sekeliling rumah tua (Baitullah). '),
	(2625, 22, 30, 'ذٰلِكَ وَمَنْ يُّعَظِّمْ حُرُمٰتِ اللّٰهِ فَهُوَ خَيْرٌ لَّهٗ عِنْدَ رَبِّهٖۗ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوْا قَوْلَ الزُّوْرِ ۙ', '<u>dzaa</u>lika waman yu\'a<u>zhzh</u>im <u>h</u>urum<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi fahuwa khayrun lahu \'inda rabbihi wau<u>h</u>illat lakumu <strong>a</strong>l-an\'<u>aa</u>mu ill<u>aa</u> m<u>aa</u> yutl<u>aa</u> \'alaykum fa<strong>i</strong>jtanibuu <strong>al</strong>rrijsa mina <strong>a</strong>l-awts<u>aa</u>ni wa<strong>i</strong>jtanibuu qawla <strong>al</strong>zzuur<strong>i</strong>', 'Demikianlah (perintah Allah). Dan barang siapa mengagungkan apa yang terhormat di sisi Allah (hurumat) maka itu lebih baik baginya di sisi Tuhannya. Dan dihalalkan bagi kamu semua hewan ternak, kecuali yang diterangkan kepadamu (keharamannya), maka jauhilah olehmu (penyembahan) berhala-berhala yang najis itu dan jauhilah perkataan dusta.'),
	(2626, 22, 31, 'حُنَفَاۤءَ لِلّٰهِ غَيْرَ مُشْرِكِيْنَ بِهٖۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَاۤءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْوِيْ بِهِ الرِّيْحُ فِيْ مَكَانٍ سَحِيْقٍ ', '<u>h</u>unaf<u>aa</u>-a lill<u>aa</u>hi ghayra musyrikiina bihi waman yusyrik bi<strong>al</strong>l<u>aa</u>hi faka-annam<u>aa</u> kharra mina <strong>al</strong>ssam<u>aa</u>-i fatakh<u>th</u>afuhu <strong>al</strong><u>ththh</u>ayru aw tahwii bihi <strong>al</strong>rrii<u>h</u>u fii mak<u>aa</u>nin sa<u>h</u>iiq<strong>in</strong>', '(Beribadahlah) dengan ikhlas kepada Allah, tanpa mempersekutukan-Nya. Barangsiapa mempersekutukan Allah, maka seakan-akan dia jatuh dari langit lalu disambar oleh burung, atau diterbangkan angin ke tempat yang jauh.'),
	(2627, 22, 32, 'ذٰلِكَ وَمَنْ يُّعَظِّمْ شَعَاۤىِٕرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوْبِ ', '<u>dzaa</u>lika waman yu\'a<u>zhzh</u>im sya\'<u>aa</u>-ira <strong>al</strong>l<u>aa</u>hi fa-innah<u>aa</u> min taqw<u>aa</u> <strong>a</strong>lquluub<strong>i</strong>', 'Demikianlah (perintah Allah). Dan barangsiapa mengagungkan syiar-syiar Allah, maka sesungguhnya hal itu timbul dari ketakwaan hati.'),
	(2628, 22, 33, 'لَكُمْ فِيْهَا مَنَافِعُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ مَحِلُّهَآ اِلَى الْبَيْتِ الْعَتِيْقِ ࣖ', 'lakum fiih<u>aa</u> man<u>aa</u>fi\'u il<u>aa</u> ajalin musamman tsumma ma<u>h</u>illuh<u>aa</u> il<u>aa</u> <strong>a</strong>lbayti <strong>a</strong>l\'atiiq<strong>i</strong>', 'Bagi kamu padanya (hewan hadyu) ada beberapa manfaat, sampai waktu yang ditentukan, kemudian tempat penyembelihannya adalah di sekitar Baitul Atiq (Baitullah).'),
	(2629, 22, 34, 'وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا لِّيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۗ فَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَلَهٗٓ اَسْلِمُوْاۗ وَبَشِّرِ الْمُخْبِتِيْنَ ۙ', 'walikulli ummatin ja\'aln<u>aa</u> mansakan liya<u>dz</u>kuruu isma <strong>al</strong>l<u>aa</u>hi \'al<u>aa</u> m<u>aa</u> razaqahum min bahiimati <strong>a</strong>l-an\'<u>aa</u>mi fa-il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun falahu aslimuu wabasysyiri <strong>a</strong>lmukhbitiin<strong>a</strong>', 'Dan bagi setiap umat telah Kami syariatkan penyembelihan (kurban), agar mereka menyebut nama Allah atas rezeki yang dikaruniakan Allah kepada mereka berupa hewan ternak. Maka Tuhanmu ialah Tuhan Yang Maha Esa, karena itu berserahdirilah kamu kepada-Nya. Dan sampaikanlah (Muhammad) kabar gembira kepada orang-orang yang tunduk patuh (kepada Allah),'),
	(2630, 22, 35, 'الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَالصَّابِرِيْنَ عَلٰى مَآ اَصَابَهُمْ وَالْمُقِيْمِى الصَّلٰوةِۙ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ', '<strong>al</strong>la<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukira <strong>al</strong>l<u>aa</u>hu wajilat quluubuhum wa<strong>al</strong><u>shshaa</u>biriina \'al<u>aa</u> m<u>aa</u> a<u>shaa</u>bahum wa<strong>a</strong>lmuqiimii <strong>al</strong><u>shsh</u>al<u>aa</u>ti wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun<strong>a</strong>', '(yaitu) orang-orang yang apabila disebut nama Allah hati mereka bergetar, orang yang sabar atas apa yang menimpa mereka, dan orang yang melaksanakan salat dan orang yang menginfakkan sebagian rezeki yang Kami karuniakan kepada mereka.'),
	(2631, 22, 36, 'وَالْبُدْنَ جَعَلْنٰهَا لَكُمْ مِّنْ شَعَاۤىِٕرِ اللّٰهِ لَكُمْ فِيْهَا خَيْرٌۖ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَاۤفَّۚ فَاِذَا وَجَبَتْ جُنُوْبُهَا فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّۗ  كَذٰلِكَ سَخَّرْنٰهَا لَكُمْ لَعَلَّكُمْ', 'wa<strong>a</strong>lbudna ja\'aln<u>aa</u>h<u>aa</u> lakum min sya\'<u>aa</u>-iri <strong>al</strong>l<u>aa</u>hi lakum fiih<u>aa</u> khayrun fa<strong>u</strong><u>dz</u>kuruu isma <strong>al</strong>l<u>aa</u>hi \'alayh<u>aa</u> <u>sh</u>aw<u>aa</u>ffa fa-i<u>dzaa</u> wajabat junuubuh<u>aa</u> fakuluu minh<u>aa</u> wa-a<u>th</u>\'imuu <strong>a</strong>lq<u>aa</u>ni\'a wa<strong>a</strong>lmu\'tarra ka<u>dzaa</u>lika sakhkharn<u>aa</u>h<u>aa</u> lakum la\'allakum tasykuruun<strong>a</strong>', 'Dan unta-unta itu Kami jadikan untuk-mu bagian dari syiar agama Allah, kamu banyak memperoleh kebaikan padanya. Maka sebutlah nama Allah (ketika kamu akan menyembelihnya) dalam keadaan berdiri (dan kaki-kaki telah terikat). Kemudian apabila telah rebah (mati), maka makanlah sebagiannya dan berilah makanlah orang yang merasa cukup dengan apa yang ada padanya (tidak meminta-minta) dan orang yang meminta. Demikianlah Kami tundukkan (unta-unta itu) untukmu, agar kamu bersyukur.'),
	(2632, 22, 37, 'لَنْ يَّنَالَ اللّٰهَ لُحُوْمُهَا وَلَا دِمَاۤؤُهَا وَلٰكِنْ يَّنَالُهُ التَّقْوٰى مِنْكُمْۗ  كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ ۗ وَبَشِّرِ الْمُحْسِنِيْنَ ', 'lan yan<u>aa</u>la <strong>al</strong>l<u>aa</u>ha lu<u>h</u>uumuh<u>aa</u> wal<u>aa</u> dim<u>aa</u>uh<u>aa</u> wal<u>aa</u>kin yan<u>aa</u>luhu <strong>al</strong>ttaqw<u>aa</u> minkum ka<u>dzaa</u>lika sakhkharah<u>aa</u> lakum litukabbiruu <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> m<u>aa</u> had<u>aa</u>kum wabasysyiri <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Daging (hewan kurban) dan darahnya itu sekali-kali tidak akan sampai kepada Allah, tetapi yang sampai kepada-Nya adalah ketakwaan kamu. Demi-kianlah Dia menundukkannya untuk-mu agar kamu mengagungkan Allah atas petunjuk yang Dia berikan kepadamu. Dan sampaikanlah kabar gembira kepada orang-orang yang berbuat baik.'),
	(2633, 22, 38, '۞ اِنَّ اللّٰهَ يُدَافِعُ عَنِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُوْرٍ ࣖ', 'inna <strong>al</strong>l<u>aa</u>ha yud<u>aa</u>fi\'u \'ani <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu kulla khaww<u>aa</u>nin kafuur<strong>in</strong>', 'Sesungguhnya Allah membela orang yang beriman. Sungguh, Allah tidak menyukai setiap orang yang berkhianat dan kufur nikmat.'),
	(2634, 22, 39, 'اُذِنَ لِلَّذِيْنَ يُقَاتَلُوْنَ بِاَنَّهُمْ ظُلِمُوْاۗ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَدِيْرٌ ۙ', 'u<u>dz</u>ina lilla<u>dz</u>iina yuq<u>aa</u>taluuna bi-annahum <u>zh</u>ulimuu wa-inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> na<u>sh</u>rihim laqadiir<strong>un</strong>', 'Diizinkan (berperang) kepada orang-orang yang diperangi, karena sesungguhnya mereka dizalimi. Dan sung-guh, Allah Mahakuasa menolong mereka itu,'),
	(2635, 22, 40, ' ۨالَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّآ اَنْ يَّقُوْلُوْا رَبُّنَا اللّٰهُ ۗوَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَّصَلَوٰتٌ وَّمَسٰجِدُ يُذْكَرُ فِيْهَا اسْمُ اللّٰهِ كَثِيْرًاۗ وَ', '<strong>al</strong>la<u>dz</u>iina ukhrijuu min diy<u>aa</u>rihim bighayri <u>h</u>aqqin ill<u>aa</u> an yaquuluu rabbun<u>aa</u> <strong>al</strong>l<u>aa</u>hu walawl<u>aa</u> daf\'u <strong>al</strong>l<u>aa</u>hi <strong>al</strong>nn<u>aa</u>sa ba\'<u>dh</u>ahum biba\'<u>dh</u>in lahuddimat <u>sh</u>aw<u>aa</u>mi\'u wabiya\'un wa<u>sh</u>alaw<u>aa</u>tun wamas<u>aa</u>jidu yu<u>dz</u>karu fiih<u>aa</u> ismu <strong>al</strong>l<u>aa</u>hi katsiiran walayan<u>sh</u>uranna <strong>al</strong>l<u>aa</u>hu man yan<u>sh</u>uruhu inna <strong>al</strong>l<u>aa</u>ha laqawiyyun \'aziiz<strong>un</strong>', '(yaitu) orang-orang yang diusir dari kampung halamannya tanpa alasan yang benar, hanya karena mereka berkata, “Tuhan kami ialah Allah.” Seandainya Allah tidak menolak (keganasan) sebagian manusia dengan sebagian yang lain, tentu telah dirobohkan biara-biara Nasrani, gereja-gereja, rumah-rumah ibadah orang Yahudi dan masjid-masjid, yang di dalamnya banyak disebut nama Allah. Allah pasti akan menolong orang yang menolong (agama)-Nya. Sungguh, Allah Mahakuat, Mahaperkasa.'),
	(2636, 22, 41, 'اَلَّذِيْنَ اِنْ مَّكَّنّٰهُمْ فِى الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوْا بِالْمَعْرُوْفِ وَنَهَوْا عَنِ الْمُنْكَرِۗ وَلِلّٰهِ عَاقِبَةُ الْاُمُوْرِ ', '<strong>al</strong>la<u>dz</u>iina in makkann<u>aa</u>hum fii <strong>a</strong>l-ar<u>dh</u>i aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu <strong>al</strong>zzak<u>aa</u>ta wa-amaruu bi<strong>a</strong>lma\'ruufi wanahaw \'ani <strong>a</strong>lmunkari walill<u>aa</u>hi \'<u>aa</u>qibatu <strong>a</strong>l-umuur<strong>i</strong>', '(Yaitu) orang-orang yang jika Kami beri kedudukan di bumi, mereka melaksanakan salat, menunaikan zakat, dan menyuruh berbuat yang makruf dan mencegah dari yang mungkar; dan kepada Allah-lah kembali segala urusan.'),
	(2637, 22, 42, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّثَمُوْدُ ۙ', 'wa-in yuka<u>dzdz</u>ibuuka faqad ka<u>dzdz</u>abat qablahum qawmu nuu<u>h</u>in wa\'<u>aa</u>dun watsamuud<strong>u</strong>', 'Dan jika mereka (orang-orang musyrik) mendustakan engkau (Muhammad), begitu pulalah kaum-kaum yang sebelum mereka, kaum Nuh, ‘Ad, dan Samud (juga telah mendustakan rasul-rasul-Nya),'),
	(2638, 22, 43, 'وَقَوْمُ اِبْرٰهِيْمَ وَقَوْمُ لُوْطٍ ۙ', 'waqawmu ibr<u>aa</u>hiima waqawmu luu<u>th</u><strong>in</strong>', 'dan (demikian juga) kaum Ibrahim dan kaum Lut,'),
	(2639, 22, 44, 'وَّاَصْحٰبُ مَدْيَنَۚ وَكُذِّبَ مُوْسٰى فَاَمْلَيْتُ لِلْكٰفِرِيْنَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَكِيْرِ ', 'wa-a<u>sh</u>-<u>haa</u>bu madyana waku<u>dzdz</u>iba muus<u>aa</u> fa-amlaytu lilk<u>aa</u>firiina tsumma akha<u>dz</u>tuhum fakayfa k<u>aa</u>na nakiir<strong>i</strong>', 'dan penduduk Madyan. Dan Musa (juga) telah didustakan, namun Aku beri tenggang waktu kepada orang-orang kafir, kemudian Aku siksa mereka, maka betapa hebatnya siksaan-Ku.'),
	(2640, 22, 45, 'فَكَاَيِّنْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۖ وَبِئْرٍ مُّعَطَّلَةٍ وَّقَصْرٍ مَّشِيْدٍ ', 'faka-ayyin min qaryatin ahlakn<u>aa</u>h<u>aa</u> wahiya <u>zhaa</u>limatun fahiya kh<u>aa</u>wiyatun \'al<u>aa</u> \'uruusyih<u>aa</u> wabi/rin mu\'a<u>ththh</u>alatin waqa<u>sh</u>rin masyiid<strong>in</strong>', 'Maka betapa banyak negeri yang telah Kami binasakan karena (penduduk)nya dalam keadaan zalim, sehingga runtuh bangunan-bangunannya dan (betapa banyak pula) sumur yang telah ditinggalkan dan istana yang tinggi (ti-dak ada penghuninya).'),
	(2641, 22, 46, 'اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَتَكُوْنَ لَهُمْ قُلُوْبٌ يَّعْقِلُوْنَ بِهَآ اَوْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوْبُ الَّتِيْ فِى الصُّدُوْرِ ', 'afalam yasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fatakuuna lahum quluubun ya\'qiluuna bih<u>aa</u> aw <u>aatsaa</u>nun yasma\'uuna bih<u>aa</u> fa-innah<u>aa</u> l<u>aa</u> ta\'m<u>aa</u> <strong>a</strong>l-ab<u>shaa</u>ru wal<u>aa</u>kin ta\'m<u>aa</u> <strong>a</strong>lquluubu <strong>al</strong>latii fii <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Maka tidak pernahkah mereka berjalan di bumi, sehingga hati (akal) mereka dapat memahami, telinga mereka dapat mendengar? Sebenarnya bukan mata itu yang buta, tetapi yang buta ialah hati yang di dalam dada. '),
	(2642, 22, 47, 'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِ وَلَنْ يُّخْلِفَ اللّٰهُ وَعْدَهٗۗ وَاِنَّ يَوْمًا عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِّمَّا تَعُدُّوْنَ ', 'wayasta\'jiluunaka bi<strong>a</strong>l\'a<u>dzaa</u>bi walan yukhlifa <strong>al</strong>l<u>aa</u>hu wa\'dahu wa-inna yawman \'inda rabbika ka-alfi sanatin mimm<u>aa</u> ta\'udduun<strong>a</strong>', 'Dan mereka meminta kepadamu (Muhammad) agar azab itu disegerakan, padahal Allah tidak akan menyalahi janji-Nya. Dan sesungguhnya sehari di sisi Tuhanmu adalah seperti seribu tahun menurut perhitunganmu.'),
	(2643, 22, 48, 'وَكَاَيِّنْ مِّنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَصِيْرُ ࣖ', 'waka-ayyin min qaryatin amlaytu lah<u>aa</u> wahiya <u>zhaa</u>limatun tsumma akha<u>dz</u>tuh<u>aa</u> wa-ilayya <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan berapa banyak negeri yang Aku tangguhkan (penghancuran)nya, karena penduduknya berbuat zalim, kemudian Aku azab mereka, dan hanya kepada-Kulah tempat kembali (segala sesuatu).'),
	(2644, 22, 49, 'قُلْ يٰٓاَيُّهَا النَّاسُ اِنَّمَآ اَنَا۠ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۚ', 'qul y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su innam<u>aa</u> an<u>aa</u> lakum na<u>dz</u>iirun mubiin<strong>un</strong>', 'Katakanlah (Muhammad), “Wahai manusia! Sesungguhnya aku (diutus) kepadamu sebagai pemberi peringatan yang nyata.”'),
	(2645, 22, 50, 'فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ', 'fa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lahum maghfiratun warizqun kariim<strong>un</strong>', 'Maka orang-orang yang beriman dan mengerjakan kebajikan, mereka memperoleh ampunan dan rezeki yang mulia.'),
	(2646, 22, 51, 'وَالَّذِيْنَ سَعَوْا فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ', 'wa<strong>a</strong>lla<u>dz</u>iina sa\'aw fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu <strong>a</strong>lja<u>h</u>iim<strong>i</strong>', 'Tetapi orang-orang yang berusaha menentang ayat-ayat Kami dengan maksud melemahkan (kemauan untuk beriman), mereka itu adalah penghuni-penghuni neraka Jahim.'),
	(2647, 22, 52, 'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ وَّلَا نَبِيٍّ اِلَّآ اِذَا تَمَنّٰىٓ اَلْقَى الشَّيْطٰنُ فِيْٓ اُمْنِيَّتِهٖۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِى الشَّيْطٰنُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ۙ', 'wam<u>aa</u> arsaln<u>aa</u> min qablika min rasuulin wal<u>aa</u> nabiyyin ill<u>aa</u> i<u>dzaa</u> tamann<u>aa</u> <strong>a</strong>lq<u>aa</u> <strong>al</strong>sysyay<u>th</u><u>aa</u>nu fii umniyyatihi fayansakhu <strong>al</strong>l<u>aa</u>hu m<u>aa</u> yulqii <strong>al</strong>sysyay<u>th</u><u>aa</u>nu tsumma yu<u>h</u>kimu <strong>al</strong>l<u>aa</u>hu <u>aa</u>y<u>aa</u>tihi wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Dan Kami tidak mengutus seorang rasul dan tidak (pula) seorang nabi sebelum engkau (Muhammad), mela-inkan apabila dia mempunyai suatu keinginan, setan pun memasukkan godaan-godaan ke dalam keinginannya itu. Tetapi Allah menghilangkan apa yang dimasukkan setan itu, dan Allah akan menguatkan ayat-ayat-Nya. Dan Allah Maha Mengetahui, Mahabijaksana,'),
	(2648, 22, 53, 'لِّيَجْعَلَ مَا يُلْقِى الشَّيْطٰنُ فِتْنَةً لِّلَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْقَاسِيَةِ قُلُوْبُهُمْۗ وَاِنَّ الظّٰلِمِيْنَ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ۙ', 'liyaj\'ala m<u>aa</u> yulqii <strong>al</strong>sysyay<u>th</u><u>aa</u>nu fitnatan lilla<u>dz</u>iina fii quluubihim mara<u>dh</u>un wa<strong>a</strong>lq<u>aa</u>siyati quluubuhum wa-inna <strong>al</strong><u>zhzhaa</u>limiina lafii syiq<u>aa</u>qin ba\'iid<strong>in</strong>', 'Dia (Allah) ingin menjadikan godaan yang ditimbulkan setan itu, sebagai cobaan bagi orang-orang yang dalam hatinya ada penyakit dan orang yang berhati keras. Dan orang-orang yang zalim itu benar-benar dalam permu-suhan yang jauh,'),
	(2649, 22, 54, 'وَّلِيَعْلَمَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَّبِّكَ فَيُؤْمِنُوْا بِهٖ فَتُخْبِتَ لَهٗ قُلُوْبُهُمْۗ وَاِنَّ اللّٰهَ لَهَادِ الَّذِيْنَ اٰمَنُوْٓا اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'waliya\'lama <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma annahu <strong>a</strong>l<u>h</u>aqqu min rabbika fayu/minuu bihi fatukhbita lahu quluubuhum wa-inna <strong>al</strong>l<u>aa</u>ha lah<u>aa</u>di <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'dan agar orang-orang yang telah diberi ilmu, meyakini bahwa (Al-Qur\'an) itu benar dari Tuhanmu lalu mereka beriman dan hati mereka tunduk kepadanya. Dan sungguh, Allah pemberi petunjuk bagi orang-orang yang beriman kepada jalan yang lurus.'),
	(2650, 22, 55, 'وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا فِيْ مِرْيَةٍ مِّنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيْمٍ ', 'wal<u>aa</u> yaz<u>aa</u>lu <strong>al</strong>la<u>dz</u>iina kafaruu fii miryatin minhu <u>h</u>att<u>aa</u> ta/tiyahumu <strong>al</strong>ss<u>aa</u>\'atu baghtatan aw ya/tiyahum \'a<u>dzaa</u>bu yawmin \'aqiim<strong>in</strong>', 'Dan orang-orang kafir itu senantiasa ragu mengenai hal itu (Al-Qur\'an), hingga saat (kematiannya) datang kepada mereka dengan tiba-tiba, atau azab hari Kiamat yang datang kepada mereka.'),
	(2651, 22, 56, 'اَلْمُلْكُ يَوْمَىِٕذٍ لِّلّٰهِ ۗيَحْكُمُ بَيْنَهُمْۗ فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ جَنّٰتِ النَّعِيْمِ ', 'almulku yawma-i<u>dz</u>in lill<u>aa</u>hi ya<u>h</u>kumu baynahum fa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti fii jann<u>aa</u>ti <strong>al</strong>nna\'iim<strong>i</strong>', 'Kekuasaan pada hari itu ada pada Allah, Dia memberi keputusan di an-tara mereka. Maka orang-orang yang beriman dan mengerjakan kebajikan berada dalam surga-surga yang penuh kenikmatan.'),
	(2652, 22, 57, 'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا فَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ ࣖ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> faul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun muhiin<strong>un</strong>', 'Dan orang-orang kafir dan yang men-dustakan ayat-ayat Kami, maka mere-ka akan merasakan azab yang meng-hinakan.'),
	(2653, 22, 58, 'وَالَّذِيْنَ هَاجَرُوْا فِيْ سَبِيْلِ اللّٰهِ ثُمَّ قُتِلُوْٓا اَوْ مَاتُوْا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقًا حَسَنًاۗ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرّٰزِقِيْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina h<u>aa</u>jaruu fii sabiili <strong>al</strong>l<u>aa</u>hi tsumma qutiluu aw m<u>aa</u>tuu layarzuqannahumu <strong>al</strong>l<u>aa</u>hu rizqan <u>h</u>asanan wa-inna <strong>al</strong>l<u>aa</u>ha lahuwa khayru <strong>al</strong>rr<u>aa</u>ziqiin<strong>a</strong>', 'Dan orang-orang yang berhijrah di jalan Allah, kemudian mereka terbunuh atau mati, sungguh, Allah akan memberikan kepada mereka rezeki yang baik (surga). Dan sesungguhnya Allah adalah pemberi rezeki yang terbaik.'),
	(2654, 22, 59, 'لَيُدْخِلَنَّهُمْ مُّدْخَلًا يَّرْضَوْنَهٗۗ وَاِنَّ اللّٰهَ لَعَلِيْمٌ حَلِيْمٌ', 'layudkhilannahum mudkhalan yar<u>dh</u>awnahu wa-inna <strong>al</strong>l<u>aa</u>ha la\'aliimun <u>h</u>aliim<strong>un</strong>', 'Sungguh, Dia (Allah) pasti akan memasukkan mereka ke tempat masuk (surga) yang mereka sukai. Dan sesungguhnya Allah Maha Mengetahui, Maha Penyantun.'),
	(2655, 22, 60, '۞ ذٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوْقِبَ بِهٖ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُ ۗاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ ', '<u>dzaa</u>lika waman \'<u>aa</u>qaba bimitsli m<u>aa</u> \'uuqiba bihi tsumma bughiya \'alayhi layan<u>sh</u>urannahu <strong>al</strong>l<u>aa</u>hu inna <strong>al</strong>l<u>aa</u>ha la\'afuwwun ghafuur<strong>un</strong>', 'Demikianlah, dan barangsiapa membalas seimbang dengan (kezaliman) penganiayaan yang pernah dia derita kemudian dia dizalimi (lagi), pasti Allah akan menolongnya. Sungguh, Allah Maha Pemaaf, Maha Pengampun.'),
	(2656, 22, 61, 'ذٰلِكَ بِاَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَاَنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha yuuliju <strong>al</strong>layla fii <strong>al</strong>nnah<u>aa</u>ri wayuuliju <strong>al</strong>nnah<u>aa</u>ra fii <strong>al</strong>layli wa-anna <strong>al</strong>l<u>aa</u>ha samii\'un ba<u>sh</u>iir<strong>un</strong>', 'Demikianlah karena Allah (kuasa) memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan sungguh, Allah Maha Mendengar, Maha Melihat.'),
	(2657, 22, 62, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l<u>h</u>aqqu wa-anna m<u>aa</u> yad\'uuna min duunihi huwa <strong>a</strong>lb<u>aath</u>ilu wa-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l\'aliyyu <strong>a</strong>lkabiir<strong>u</strong>', 'Demikianlah (kebesaran Allah) karena Allah, Dialah (Tuhan) Yang Hak. Dan apa saja yang mereka seru selain Dia, itulah yang batil, dan sungguh Allah, Dialah Yang Mahatinggi, Mahabesar.'),
	(2658, 22, 63, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۖ فَتُصْبِحُ الْاَرْضُ مُخْضَرَّةًۗ اِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ ۚ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fatu<u>sh</u>bi<u>h</u>u <strong>a</strong>l-ar<u>dh</u>u mukh<u>dh</u>arratan inna <strong>al</strong>l<u>aa</u>ha la<u>th</u>iifun khabiir<strong>un</strong>', 'Tidakkah engkau memperhatikan, bahwa Allah menurunkan air (hujan) dari langit, sehingga bumi menjadi hijau? Sungguh, Allah Mahahalus, Maha Mengetahui.'),
	(2659, 22, 64, 'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَمِيْدُ ࣖ', 'lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa-inna <strong>al</strong>l<u>aa</u>ha lahuwa <strong>a</strong>lghaniyyu <strong>a</strong>l<u>h</u>amiid<strong>u</strong>', 'Milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Dan Allah benar-benar Mahakaya, Maha Terpuji.'),
	(2660, 22, 65, 'اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى الْاَرْضِ وَالْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِاَمْرِهٖۗ وَيُمْسِكُ السَّمَاۤءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِهٖۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha sakhkhara lakum m<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa<strong>a</strong>lfulka tajrii fii <strong>a</strong>lba<u>h</u>ri bi-amrihi wayumsiku <strong>al</strong>ssam<u>aa</u>-a an taqa\'a \'al<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> bi-i<u>dz</u>nihi inna <strong>al</strong>l<u>aa</u>ha bi<strong>al</strong>nn<u>aa</u>si larauufun ra<u>h</u>iim<strong>un</strong>', 'Tidakkah engkau memperhatikan bahwa Allah menundukkan bagimu (manusia) apa yang ada di bumi dan kapal yang berlayar di lautan dengan perintah-Nya. Dan Dia menahan (benda-benda) langit agar tidak jatuh ke bumi, melainkan dengan izin-Nya? Sungguh, Allah Maha Pengasih, Maha Penyayang kepada manusia.'),
	(2661, 22, 66, 'وَهُوَ الَّذِيْٓ اَحْيَاكُمْ ۖ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ اِنَّ الْاِنْسَانَ لَكَفُوْرٌ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii a<u>h</u>y<u>aa</u>kum tsumma yumiitukum tsumma yu<u>h</u>yiikum inna <strong>a</strong>l-ins<u>aa</u>na lakafuur<strong>un</strong>', 'Dan Dialah yang menghidupkan kamu, kemudian mematikan kamu, kemudian menghidupkan kamu kembali (pada hari kebangkitan). Sungguh, manusia itu sangat kufur nikmat.'),
	(2662, 22, 67, 'لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوْهُ فَلَا يُنَازِعُنَّكَ فِى الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۗ اِنَّكَ لَعَلٰى هُدًى مُّسْتَقِيْمٍ ', 'likulli ummatin ja\'aln<u>aa</u> mansakan hum n<u>aa</u>sikuuhu fal<u>aa</u> yun<u>aa</u>zi\'unnaka fii <strong>a</strong>l-amri wa<strong>u</strong>d\'u il<u>aa</u> rabbika innaka la\'al<u>aa</u> hudan mustaqiim<strong>in</strong>', 'Bagi setiap umat telah Kami tetapkan syariat tertentu yang (harus) mereka amalkan, maka tidak sepantasnya mereka berbantahan dengan engkau dalam urusan (syariat) ini dan serulah (mereka) kepada Tuhanmu. Sungguh, engkau (Muhammad) berada di jalan yang lurus.'),
	(2663, 22, 68, 'وَاِنْ جَادَلُوْكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُوْنَ ', 'wa-in j<u>aa</u>daluuka faquli <strong>al</strong>l<u>aa</u>hu a\'lamu bim<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan jika mereka membantah engkau, maka katakanlah, “Allah lebih tahu tentang apa yang kamu kerjakan.”'),
	(2664, 22, 69, 'اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ', '<strong>al</strong>l<u>aa</u>hu ya<u>h</u>kumu baynakum yawma <strong>a</strong>lqiy<u>aa</u>mati fiim<u>aa</u> kuntum fiihi takhtalifuun<strong>a</strong>', 'Allah akan mengadili di antara kamu pada hari Kiamat tentang apa yang dahulu kamu memperselisihkannya. '),
	(2665, 22, 70, 'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمَاۤءِ وَالْاَرْضِۗ اِنَّ ذٰلِكَ فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ', 'alam ta\'lam anna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i inna <u>dzaa</u>lika fii kit<u>aa</u>bin inna <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<strong>un</strong>', 'Tidakkah engkau tahu bahwa Allah mengetahui apa yang di langit dan di bumi? Sungguh, yang demikian itu sudah terdapat dalam sebuah Kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu sangat mudah bagi Allah. '),
	(2666, 22, 71, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّمَا لَيْسَ لَهُمْ بِهٖ عِلْمٌ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ', 'waya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wam<u>aa</u> laysa lahum bihi \'ilmun wam<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina min na<u>sh</u>iir<strong>in</strong>', 'Dan mereka menyembah selain Allah, tanpa dasar yang jelas tentang itu, dan mereka tidak mempunyai pengetahuan (pula) tentang itu. Bagi orang-orang yang zalim tidak ada seorang penolong pun.'),
	(2667, 22, 72, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ تَعْرِفُ فِيْ وُجُوْهِ الَّذِيْنَ كَفَرُوا الْمُنْكَرَۗ يَكَادُوْنَ يَسْطُوْنَ بِالَّذِيْنَ يَتْلُوْنَ عَلَيْهِمْ اٰيٰتِنَاۗ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكُمْۗ  اَلنَّارُۗ وَعَدَهَا اللّٰهُ الَّذِ', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin ta\'rifu fii wujuuhi <strong>al</strong>la<u>dz</u>iina kafaruu <strong>a</strong>lmunkara yak<u>aa</u>duuna yas<u>th</u>uuna bi<strong>a</strong>lla<u>dz</u>iina yatluuna \'alayhim <u>aa</u>y<u>aa</u>tin<u>aa</u> qul afa-unabbi-ukum bisyarrin min <u>dzaa</u>likum <strong>al</strong>nn<u>aa</u>ru wa\'adah<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina kafaruu wabi/sa <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan apabila dibacakan di hadapan mereka ayat-ayat Kami yang terang, niscaya engkau akan melihat (tanda-tanda) keingkaran pada wajah orang-orang yang kafir itu. Hampir-hampir mereka menyerang orang-orang yang membacakan ayat-ayat Kami kepada mereka. Katakanlah (Muhammad), “Apakah akan aku kabarkan kepada-mu (mengenai sesuatu) yang lebih buruk dari itu, (yaitu) neraka?” Allah telah mengancamkannya (neraka) kepada orang-orang kafir. Dan (neraka itu) seburuk-buruk tempat kembali.'),
	(2668, 22, 73, 'يٰٓاَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوْا لَهٗ ۗاِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَنْ يَّخْلُقُوْا ذُبَابًا وَّلَوِ اجْتَمَعُوْا لَهٗ ۗوَاِنْ يَّسْلُبْهُمُ الذُّبَابُ شَيْـًٔا لَّا يَسْتَنْقِذُوْهُ مِنْهُۗ ضَعُفَ الطَّالِبُ وَالْمَط', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su <u>dh</u>uriba matsalun fa<strong>i</strong>stami\'uu lahu inna <strong>al</strong>la<u>dz</u>iina tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi lan yakhluquu <u>dz</u>ub<u>aa</u>ban walawi ijtama\'uu lahu wa-in yaslubuhumu <strong>al</strong><u>dzdz</u>ub<u>aa</u>bu syay-an l<u>aa</u> yastanqi<u>dz</u>uuhu minhu <u>dh</u>a\'ufa <strong>al</strong><u>ththaa</u>libu wa<strong>a</strong>lma<u>th</u>luub<strong>u</strong>', 'Wahai manusia! Telah dibuat suatu perumpamaan. Maka dengarkanlah! Sesungguhnya segala yang kamu seru selain Allah tidak dapat menciptakan seekor lalat pun, walaupun mereka bersatu untuk menciptakannya. Dan jika lalat itu merampas sesuatu dari mereka, mereka tidak akan dapat merebutnya kembali dari lalat itu. Sama lemahnya yang menyembah dan yang disembah.'),
	(2669, 22, 74, 'مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ ', 'm<u>aa</u> qadaruu <strong>al</strong>l<u>aa</u>ha <u>h</u>aqqa qadrihi inna <strong>al</strong>l<u>aa</u>ha laqawiyyun \'aziiz<strong>un</strong>', 'Mereka tidak mengagungkan Allah dengan sebenar-benarnya. Sungguh, Allah Mahakuat, Mahaperkasa.'),
	(2670, 22, 75, 'اَللّٰهُ يَصْطَفِيْ مِنَ الْمَلٰۤىِٕكَةِ رُسُلًا وَّمِنَ النَّاسِۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ۚ', '<strong>al</strong>l<u>aa</u>hu ya<u>sth</u>afii mina <strong>a</strong>lmal<u>aa</u>-ikati rusulan wamina <strong>al</strong>nn<u>aa</u>si inna <strong>al</strong>l<u>aa</u>ha samii\'un ba<u>sh</u>iir<strong>un</strong>', 'Allah memilih para utusan(-Nya) dari malaikat dan dari manusia. Sesungguhnya Allah Maha Mendengar, Maha Melihat.'),
	(2671, 22, 76, 'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ', 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi turja\'u <strong>a</strong>l-umuur<strong>u</strong>', 'Dia (Allah) mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka. Dan hanya kepada Allah dikembalikan segala urusan.'),
	(2672, 22, 77, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ارْكَعُوْا وَاسْجُدُوْا وَاعْبُدُوْا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُوْنَ ۚ۩', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu irka\'uu wa<strong>u</strong>sjuduu wa<strong>u</strong>\'buduu rabbakum wa<strong>i</strong>f\'aluu <strong>a</strong>lkhayra la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Wahai orang-orang yang beriman! Rukuklah, sujudlah, dan sembahlah Tuhanmu; dan berbuatlah kebaikan, agar kamu beruntung.'),
	(2673, 22, 78, 'وَجَاهِدُوْا فِى اللّٰهِ حَقَّ جِهَادِهٖۗ هُوَ اجْتَبٰىكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِى الدِّيْنِ مِنْ حَرَجٍۗ مِلَّةَ اَبِيْكُمْ اِبْرٰهِيْمَۗ هُوَ سَمّٰىكُمُ الْمُسْلِمِيْنَ ەۙ مِنْ قَبْلُ وَفِيْ هٰذَا لِيَكُوْنَ الرَّسُوْلُ شَهِيْدًا عَلَيْكُمْ وَتَكُو', 'waj<u>aa</u>hiduu fii <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqa jih<u>aa</u>dihi huwa ijtab<u>aa</u>kum wam<u>aa</u> ja\'ala \'alaykum fii <strong>al</strong>ddiini min <u>h</u>arajin millata abiikum ibr<u>aa</u>hiima huwa samm<u>aa</u>kumu <strong>a</strong>lmuslimiina min qablu wafii h<u>aadzaa</u> liyakuuna <strong>al</strong>rrasuulu syahiidan \'alaykum watakuunuu syuhad<u>aa</u>-a \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si fa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu <strong>al</strong>zzak<u>aa</u>ta wa<strong>i</strong>\'ta<u>sh</u>imuu bi<strong>al</strong>l<u>aa</u>hi huwa mawl<u>aa</u>kum fani\'ma <strong>a</strong>lmawl<u>aa</u> wani\'ma <strong>al</strong>nna<u>sh</u>iir<strong>u</strong>', 'Dan berjihadlah kamu di jalan Allah dengan jihad yang sebenar-benarnya. Dia telah memilih kamu, dan Dia tidak menjadikan kesukaran untukmu dalam agama. (Ikutilah) agama nenek moyangmu Ibrahim. Dia (Allah) telah menamakan kamu orang-orang muslim sejak dahulu, dan (begitu pula) dalam (Al-Qur\'an) ini, agar Rasul (Muhammad) itu menjadi saksi atas dirimu dan agar kamu semua menjadi saksi atas segenap manusia. Maka laksanakanlah salat; tunaikanlah zakat, dan berpegangteguhlah kepada Allah. Dialah Pelindungmu; Dia sebaik-baik pelindung dan sebaik-baik penolong.'),
	(2674, 23, 1, ' قَدْ اَفْلَحَ الْمُؤْمِنُوْنَ ۙ', 'qad afla<u>h</u>a <strong>a</strong>lmu/minuun<strong>a</strong>', 'Sungguh beruntung orang-orang yang beriman,'),
	(2675, 23, 2, ' الَّذِيْنَ هُمْ فِيْ صَلٰو تِهِمْ خَاشِعُوْنَ', '<strong>al</strong>la<u>dz</u>iina hum fii <u>sh</u>al<u>aa</u>tihim kh<u>aa</u>syi\'uun<strong>a</strong>', '(yaitu) orang yang khusyuk dalam salatnya,'),
	(2676, 23, 3, 'وَالَّذِيْنَ هُمْ عَنِ اللَّغْوِ مُعْرِضُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum \'ani <strong>al</strong>laghwi mu\'ri<u>dh</u>uun<strong>a</strong>', 'dan orang yang menjauhkan diri dari (perbuatan dan perkataan) yang tidak berguna,'),
	(2677, 23, 4, 'وَالَّذِيْنَ هُمْ لِلزَّكٰوةِ فَاعِلُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum li<strong>l</strong>zzak<u>aa</u>ti f<u>aa</u>\'iluun<strong>a</strong>', 'dan orang yang menunaikan zakat,'),
	(2678, 23, 5, ' وَالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum lifuruujihim <u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'dan orang yang memelihara kemaluannya,'),
	(2679, 23, 6, 'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ', 'ill<u>aa</u> \'al<u>aa</u> azw<u>aa</u>jihim aw m<u>aa</u> malakat aym<u>aa</u>nuhum fa-innahum ghayru maluumiin<strong>a</strong>', 'kecuali terhadap istri-istri mereka atau hamba sahaya yang mereka miliki; maka sesungguhnya mereka tidak tercela.'),
	(2680, 23, 7, 'فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَ ۚ', 'famani ibtagh<u>aa</u> war<u>aa</u>-a <u>dzaa</u>lika faul<u>aa</u>-ika humu <strong>a</strong>l\'<u>aa</u>duun<strong>a</strong>', 'Tetapi barang siapa mencari di balik itu (zina, dan sebagainya), maka mereka itulah orang-orang yang melampaui batas.'),
	(2681, 23, 8, 'وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum li-am<u>aa</u>n<u>aa</u>tihim wa\'ahdihim r<u>aa</u>\'uun<strong>a</strong>', 'Dan (sungguh beruntung) orang yang memelihara amanat-amanat dan janjinya,'),
	(2682, 23, 9, 'وَالَّذِيْنَ هُمْ عَلٰى صَلَوٰتِهِمْ يُحَافِظُوْنَ ۘ', 'wa<strong>a</strong>lla<u>dz</u>iina hum \'al<u>aa</u> <u>sh</u>alaw<u>aa</u>tihim yu<u>haa</u>fi<u>zh</u>uun<strong>a</strong>', 'serta orang yang memelihara salatnya.'),
	(2683, 23, 10, 'اُولٰۤىِٕكَ هُمُ الْوَارِثُوْنَ ۙ', 'ul<u>aa</u>-ika humu <strong>a</strong>lw<u>aa</u>ritsuun<strong>a</strong>', 'Mereka itulah orang yang akan mewarisi,'),
	(2684, 23, 11, 'الَّذِيْنَ يَرِثُوْنَ الْفِرْدَوْسَۗ هُمْ فِيْهَا خٰلِدُوْنَ ', '<strong>al</strong>la<u>dz</u>iina yaritsuuna <strong>a</strong>lfirdawsa hum fiih<u>aa</u> kh<u>aa</u>liduun<strong>a</strong>', '(yakni) yang akan mewarisi (surga) Firdaus. Mereka kekal di dalamnya.'),
	(2685, 23, 12, 'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلٰلَةٍ مِّنْ طِيْنٍ ۚ', 'walaqad khalaqn<u>aa</u> <strong>a</strong>l-ins<u>aa</u>na min sul<u>aa</u>latin min <u>th</u>iin<strong>in</strong>', 'Dan sungguh, Kami telah menciptakan manusia dari saripati (berasal) dari tanah.'),
	(2686, 23, 13, 'ثُمَّ جَعَلْنٰهُ نُطْفَةً فِيْ قَرَارٍ مَّكِيْنٍ ۖ', 'tsumma ja\'aln<u>aa</u>hu nu<u>th</u>fatan fii qar<u>aa</u>rin makiin<strong>in</strong>', 'Kemudian Kami menjadikannya air mani (yang disimpan) dalam tempat yang kokoh (rahim).'),
	(2687, 23, 14, 'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظٰمًا فَكَسَوْنَا الْعِظٰمَ لَحْمًا ثُمَّ اَنْشَأْنٰهُ خَلْقًا اٰخَرَۗ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِقِيْنَۗ ', 'tsumma khalaqn<u>aa</u> <strong>al</strong>nnu<u>th</u>fata \'alaqatan fakhalaqn<u>aa</u> <strong>a</strong>l\'alaqata mu<u>dh</u>ghatan fakhalaqn<u>aa</u> <strong>a</strong>lmu<u>dh</u>ghata \'i<u>zhaa</u>man fakasawn<u>aa</u> <strong>a</strong>l\'i<u>zhaa</u>ma la<u>h</u>man tsumma ansya/n<u>aa</u>hu khalqan <u>aa</u>khara fatab<u>aa</u>raka <strong>al</strong>l<u>aa</u>hu a<u>h</u>sanu <strong>a</strong>lkh<u>aa</u>liqiin<strong>a</strong>', 'Kemudian, air mani itu Kami jadikan sesuatu yang melekat, lalu sesuatu yang melekat itu Kami jadikan segumpal daging, dan segumpal daging itu Kami jadikan tulang belulang, lalu tulang belulang itu Kami bungkus dengan daging. Kemudian, Kami menjadikannya makhluk yang (berbentuk) lain. Mahasuci Allah, Pencipta yang paling baik.'),
	(2688, 23, 15, ' ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُوْنَ ۗ', 'tsumma innakum ba\'da <u>dzaa</u>lika lamayyituun<strong>a</strong>', 'Kemudian setelah itu, sesungguhnya kamu pasti mati.'),
	(2689, 23, 16, 'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُوْنَ', 'tsumma innakum yawma <strong>a</strong>lqiy<u>aa</u>mati tub\'atsuun<strong>a</strong>', 'Kemudian, sesungguhnya kamu akan dibangkitkan (dari kuburmu) pada hari Kiamat.'),
	(2690, 23, 17, 'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَاۤىِٕقَۖ وَمَا كُنَّا عَنِ الْخَلْقِ غٰفِلِيْنَ', 'walaqad khalaqn<u>aa</u> fawqakum sab\'a <u>th</u>ar<u>aa</u>-iqa wam<u>aa</u> kunn<u>aa</u> \'ani <strong>a</strong>lkhalqi gh<u>aa</u>filiin<strong>a</strong>', 'Dan sungguh, Kami telah menciptakan tujuh (lapis) langit di atas kamu, dan Kami tidaklah lengah terhadap ciptaan (Kami).'),
	(2691, 23, 18, 'وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍ فَاَسْكَنّٰهُ فِى الْاَرْضِۖ وَاِنَّا عَلٰى ذَهَابٍۢ بِهٖ لَقٰدِرُوْنَ ۚ', 'wa-anzaln<u>aa</u> mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an biqadarin fa-askann<u>aa</u>hu fii <strong>a</strong>l-ar<u>dh</u>i wa-inn<u>aa</u> \'al<u>aa</u> <u>dz</u>ah<u>aa</u>bin bihi laq<u>aa</u>diruun<strong>a</strong>', 'Dan Kami turunkan air dari langit dengan suatu ukuran; lalu Kami jadikan air itu menetap di bumi, dan pasti Kami berkuasa melenyapkannya. '),
	(2692, 23, 19, 'فَاَنْشَأْنَا لَكُمْ بِهٖ جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍۘ لَكُمْ فِيْهَا فَوَاكِهُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ', 'fa-ansya/n<u>aa</u> lakum bihi jann<u>aa</u>tin min nakhiilin wa-a\'n<u>aa</u>bin lakum fiih<u>aa</u> faw<u>aa</u>kihu katsiiratun waminh<u>aa</u> ta/kuluun<strong>a</strong>', 'Lalu dengan (air) itu, Kami tumbuhkan untukmu kebun-kebun kurma dan anggur; di sana kamu memperoleh buah-buahan yang banyak dan sebagian dari (buah-buahan) itu kamu makan, '),
	(2693, 23, 20, 'وَشَجَرَةً تَخْرُجُ مِنْ طُوْرِ سَيْنَاۤءَ تَنْۢبُتُ بِالدُّهْنِ وَصِبْغٍ لِّلْاٰكِلِيْنَ ', 'wasyajaratan takhruju min <u>th</u>uuri sayn<u>aa</u>-a tanbutu bi<strong>al</strong>dduhni wa<u>sh</u>ibghin lil<u>aa</u>kiliin<strong>a</strong>', 'dan (Kami tumbuhkan) pohon (zaitun) yang tumbuh dari gunung Sinai, yang menghasilkan minyak, dan bahan pembangkit selera bagi orang-orang yang makan. '),
	(2694, 23, 21, 'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةًۗ  نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهَا وَلَكُمْ فِيْهَا مَنَافِعُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ ', 'wa-inna lakum fii <strong>a</strong>l-an\'<u>aa</u>mi la\'ibratan nusqiikum mimm<u>aa</u> fii bu<u>th</u>uunih<u>aa</u> walakum fiih<u>aa</u> man<u>aa</u>fi\'u katsiiratun waminh<u>aa</u> ta/kuluun<strong>a</strong>', 'Dan sesungguhnya pada hewan-hewan ternak terdapat suatu pelajaran bagimu. Kami memberi minum kamu dari (air susu) yang ada dalam perutnya, dan padanya juga terdapat banyak manfaat untukmu, dan sebagian darinya kamu makan,'),
	(2695, 23, 22, 'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَ ࣖ', 'wa\'alayh<u>aa</u> wa\'al<u>aa</u> <strong>a</strong>lfulki tu<u>h</u>maluun<strong>a</strong>', 'di atasnya (hewan-hewan ternak) dan di atas kapal-kapal kamu diangkut.'),
	(2696, 23, 23, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ', 'walaqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi faq<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun<strong>a</strong>', 'Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, lalu dia berkata, “Wahai kaumku! Sembahlah Allah, (karena) tidak ada tuhan (yang berhak disembah) bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?”'),
	(2697, 23, 24, 'فَقَالَ الْمَلَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يُرِيْدُ اَنْ يَّتَفَضَّلَ عَلَيْكُمْۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَنْزَلَ مَلٰۤىِٕكَةً  ۖمَّا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ۚ', 'faq<u>aa</u>la <strong>a</strong>lmalau <strong>al</strong>la<u>dz</u>iina kafaruu min qawmihi m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum yuriidu an yatafa<u>dhdh</u>ala \'alaykum walaw sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu la-anzala mal<u>aa</u>-ikatan m<u>aa</u> sami\'n<u>aa</u> bih<u>aadzaa</u> fii <u>aa</u>b<u>aa</u>-in<u>aa</u> <strong>a</strong>l-awwaliin<strong>a</strong>', 'Maka berkatalah para pemuka orang kafir dari kaumnya, “Orang ini tidak lain hanyalah manusia seperti kamu, yang ingin menjadi orang yang lebih mulia daripada kamu. Dan seandainya Allah menghendaki, tentu Dia mengutus malaikat. Belum pernah kami mendengar (seruan yang seperti) ini pada (masa) nenek moyang kami dahulu.'),
	(2698, 23, 25, 'اِنْ هُوَ اِلَّا رَجُلٌۢ بِهٖ جِنَّةٌ فَتَرَبَّصُوْا بِهٖ حَتّٰى حِيْنٍ', 'in huwa ill<u>aa</u> rajulun bihi jinnatun fatarabba<u>sh</u>uu bihi <u>h</u>att<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Dia hanyalah seorang laki-laki yang gila, maka tunggulah (sabarlah) terhadapnya sampai waktu yang ditentukan.”'),
	(2699, 23, 26, 'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ ', 'q<u>aa</u>la rabbi un<u>sh</u>urnii bim<u>aa</u> ka<u>dzdz</u>abuun<strong>i</strong>', 'Dia (Nuh) berdoa, “Ya Tuhanku, tolonglah aku karena mereka mendustakan aku.”'),
	(2700, 23, 27, 'فَاَوْحَيْنَآ اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ فَاسْلُكْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْنِيْ فِى ال', 'fa-aw<u>h</u>ayn<u>aa</u> ilayhi ani i<u>sh</u>na\'i <strong>a</strong>lfulka bi-a\'yunin<u>aa</u> wawa<u>h</u>yin<u>aa</u> fa-i<u>dzaa</u> j<u>aa</u>-a amrun<u>aa</u> waf<u>aa</u>ra <strong>al</strong>ttannuuru fa<strong>u</strong>sluk fiih<u>aa</u> min kullin zawjayni itsnayni wa-ahlaka ill<u>aa</u> man sabaqa \'alayhi <strong>a</strong>lqawlu minhum wal<u>aa</u> tukh<u>aath</u>ibnii fii <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu innahum mughraquun<strong>a</strong>', 'Lalu Kami wahyukan kepadanya, “Buatlah kapal di bawah pengawasan dan petunjuk Kami, maka apabila perintah Kami datang dan tanur (dapur) telah memancarkan air, maka masukkanlah ke dalam (kapal) itu sepasang-sepasang dari setiap jenis, juga keluargamu, kecuali orang yang lebih dahulu ditetapkan (akan ditimpa siksaan) di antara mereka. Dan janganlah engkau bicarakan dengan-Ku tentang orang-orang yang zalim, sesungguhnya mereka itu akan ditenggelamkan.'),
	(2701, 23, 28, 'فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَّعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ نَجّٰىنَا مِنَ الْقَوْمِ الظّٰلِمِيْنَ', 'fa-i<u>dzaa</u> istawayta anta waman ma\'aka \'al<u>aa</u> <strong>a</strong>lfulki faquli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii najj<u>aa</u>n<u>aa</u> mina <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan apabila engkau dan orang-orang yang bersamamu telah berada di atas kapal, maka ucapkanlah, “Segala puji bagi Allah yang telah menyelamatkan kami dari orang-orang yang zalim.”'),
	(2702, 23, 29, 'وَقُلْ رَّبِّ اَنْزِلْنِيْ مُنْزَلًا مُّبٰرَكًا وَّاَنْتَ خَيْرُ الْمُنْزِلِيْنَ', 'waqul rabbi anzilnii munzalan mub<u>aa</u>rakan wa-anta khayru <strong>a</strong>lmunziliin<strong>a</strong>', 'Dan berdoalah, “Ya Tuhanku, tempatkanlah aku pada tempat yang diberkahi, dan Engkau adalah sebaik-baik pemberi tempat.”'),
	(2703, 23, 30, 'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ وَّاِنْ كُنَّا لَمُبْتَلِيْنَ', 'inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin wa-in kunn<u>aa</u> lamubtaliin<strong>a</strong>', 'Sungguh, pada (kejadian) itu benar-benar terdapat tanda-tanda (kebesaran Allah); dan sesungguhnya Kami benar-benar menimpakan siksaan (kepada kaum Nuh itu).'),
	(2704, 23, 31, 'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ ۚ', 'tsumma ansya/n<u>aa</u> min ba\'dihim qarnan <u>aa</u>khariin<strong>a</strong>', 'Kemudian setelah mereka, Kami ciptakan umat yang lain (kaum ‘Ad).'),
	(2705, 23, 32, 'فَاَرْسَلْنَا فِيْهِمْ رَسُوْلًا مِّنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ࣖ ', 'fa-arsaln<u>aa</u> fiihim rasuulan minhum ani u\'buduu <strong>al</strong>l<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun<strong>a</strong>', 'Lalu Kami utus kepada mereka, seorang rasul dari kalangan mereka sendiri (yang berkata), “Sembahlah Allah! Tidak ada tuhan (yang berhak disembah) bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?”'),
	(2706, 23, 33, 'وَقَالَ الْمَلَاُ مِنْ قَوْمِهِ الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِلِقَاۤءِ الْاٰخِرَةِ وَاَتْرَفْنٰهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُوْنَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُوْنَ ', 'waq<u>aa</u>la <strong>a</strong>lmalau min qawmihi <strong>al</strong>la<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu biliq<u>aa</u>-i <strong>a</strong>l-<u>aa</u>khirati wa-atrafn<u>aa</u>hum fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum ya/kulu mimm<u>aa</u> ta/kuluuna minhu wayasyrabu mimm<u>aa</u> tasyrabuun<strong>a</strong>', 'Dan berkatalah para pemuka orang kafir dari kaumnya dan yang mendustakan pertemuan hari akhirat serta mereka yang telah Kami beri kemewahan dan kesenangan dalam kehidupan di dunia, “(Orang) ini tidak lain hanyalah manusia seperti kamu, dia makan apa yang kamu makan, dan dia minum apa yang kamu minum.”'),
	(2707, 23, 34, 'وَلَىِٕنْ اَطَعْتُمْ بَشَرًا مِّثْلَكُمْ اِنَّكُمْ اِذًا لَّخٰسِرُوْنَ ۙ', 'wala-in a<u>th</u>a\'tum basyaran mitslakum innakum i<u>dz</u>an lakh<u>aa</u>siruun<strong>a</strong>', 'Dan sungguh, jika kamu menaati manusia seperti kamu, niscaya kamu pasti rugi.'),
	(2708, 23, 35, 'اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَّعِظَامًا اَنَّكُمْ مُّخْرَجُوْنَ ۖ ', 'aya\'idukum annakum i<u>dzaa</u> mittum wakuntum tur<u>aa</u>ban wa\'i<u>zhaa</u>man annakum mukhrajuun<strong>a</strong>', 'Adakah dia menjanjikan kepada kamu, bahwa apabila kamu telah mati dan menjadi tanah dan tulang belulang, sesungguhnya kamu akan dikeluarkan (dari kuburmu)?'),
	(2709, 23, 36, '۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوْعَدُوْنَ ۖ ', 'hayh<u>aa</u>ta hayh<u>aa</u>ta lim<u>aa</u> tuu\'aduun<strong>a</strong>', 'Jauh! Jauh sekali (dari kebenaran) apa yang diancamkan kepada kamu, '),
	(2710, 23, 37, 'اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ۖ ', 'in hiya ill<u>aa</u> <u>h</u>ay<u>aa</u>tun<u>aa</u> <strong>al</strong>dduny<u>aa</u> namuutu wana<u>h</u>y<u>aa</u> wam<u>aa</u> na<u>h</u>nu bimab\'uutsiin<strong>a</strong>', '(kehidupan itu) tidak lain hanyalah kehidupan kita di dunia ini, (di sanalah) kita mati dan hidup dan tidak akan dibangkitkan (lagi),'),
	(2711, 23, 38, 'اِنْ هُوَ اِلَّا رَجُلُ ِۨافْتَرٰى عَلَى اللّٰهِ كَذِبًا وَّمَا نَحْنُ لَهٗ بِمُؤْمِنِيْنَ', 'in huwa ill<u>aa</u> rajulun iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban wam<u>aa</u> na<u>h</u>nu lahu bimu/miniin<strong>a</strong>', 'Dia tidak lain hanyalah seorang laki-laki yang mengada-adakan kebohongan terhadap Allah, dan kita tidak akan mempercayainya.'),
	(2712, 23, 39, 'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ', 'q<u>aa</u>la rabbi un<u>sh</u>urnii bim<u>aa</u> ka<u>dzdz</u>abuun<strong>i</strong>', 'Dia (Hud) berdoa, “Ya Tuhanku, tolonglah aku karena mereka mendustakan aku.”'),
	(2713, 23, 40, 'قَالَ عَمَّا قَلِيْلٍ لَّيُصْبِحُنَّ نٰدِمِيْنَ ۚ ', 'q<u>aa</u>la \'amm<u>aa</u> qaliilin layu<u>sh</u>bi<u>h</u>unna n<u>aa</u>dimiin<strong>a</strong>', 'Dia (Allah) berfirman, “Tidak lama lagi mereka pasti akan menyesal.”'),
	(2714, 23, 41, 'فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنٰهُمْ غُثَاۤءًۚ فَبُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ', 'fa-akha<u>dz</u>at-humu <strong>al</strong><u>shsh</u>ay<u>h</u>atu bi<strong>a</strong>l<u>h</u>aqqi faja\'aln<u>aa</u>hum ghuts<u>aa</u>-an fabu\'dan lilqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Lalu mereka benar-benar dimusnahkan oleh suara yang mengguntur, dan Kami jadikan mereka (seperti) sampah yang dibawa banjir. Maka binasalah bagi orang-orang yang zalim. '),
	(2715, 23, 42, 'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قُرُوْنًا اٰخَرِيْنَ ۗ', 'tsumma ansya/n<u>aa</u> min ba\'dihim quruunan <u>aa</u>khariin<strong>a</strong>', 'Kemudian setelah mereka Kami ciptakan umat-umat yang lain.'),
	(2716, 23, 43, 'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ۗ', 'm<u>aa</u> tasbiqu min ummatin ajalah<u>aa</u> wam<u>aa</u> yasta/khiruun<strong>a</strong>', 'Tidak ada satu umat pun yang dapat menyegerakan ajalnya, dan tidak (pula) menangguhkannya.'),
	(2717, 23, 44, 'ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۗ  كُلَّمَا جَاۤءَ اُمَّةً رَّسُوْلُهَا كَذَّبُوْهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضًا وَّجَعَلْنٰهُمْ اَحَادِيْثَۚ فَبُعْدًا لِّقَوْمٍ لَّا يُؤْمِنُوْنَ ', 'tsumma arsaln<u>aa</u> rusulan<u>aa</u> tatr<u>aa</u> kulla m<u>aa</u> j<u>aa</u>-a ummatan rasuuluh<u>aa</u> ka<u>dzdz</u>abuuhu fa-atba\'n<u>aa</u> ba\'<u>dh</u>ahum ba\'<u>dh</u>an waja\'aln<u>aa</u>hum a<u>haa</u>diitsa fabu\'dan liqawmin l<u>aa</u> yu/minuun<strong>a</strong>', 'Kemudian, Kami utus rasul-rasul Kami berturut-turut. Setiap kali seorang rasul datang kepada suatu umat, mereka mendustakannya, maka Kami silihgantikan sebagian mereka dengan sebagian yang lain (dalam kebinasaan). Dan Kami jadikan mereka bahan cerita (bagi manusia). Maka kebinasaanlah bagi kaum yang tidak beriman.'),
	(2718, 23, 45, 'ثُمَّ اَرْسَلْنَا مُوْسٰى وَاَخَاهُ هٰرُوْنَ ەۙ بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ', 'tsumma arsaln<u>aa</u> muus<u>aa</u> wa-akh<u>aa</u>hu h<u>aa</u>ruuna bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wasul<u>th</u><u>aa</u>nin mubiin<strong>in</strong>', 'Kemudian Kami utus Musa dan saudaranya Harun dengan membawa tanda-tanda (kebesaran) Kami, dan bukti yang nyata,'),
	(2719, 23, 46, 'اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا عَالِيْنَ ۚ', 'il<u>aa</u> fir\'awna wamala-ihi fa<strong>i</strong>stakbaruu wak<u>aa</u>nuu qawman \'<u>aa</u>liin<strong>a</strong>', 'kepada Fir‘aun dan para pemuka kaumnya, tetapi mereka angkuh dan mereka memang kaum yang sombong.'),
	(2720, 23, 47, 'فَقَالُوْٓا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عٰبِدُوْنَ ۚ', 'faq<u>aa</u>luu anu/minu libasyarayni mitslin<u>aa</u> waqawmuhum<u>aa</u> lan<u>aa</u> \'<u>aa</u>biduun<strong>a</strong>', 'Maka mereka berkata, “Apakah (pantas) kita percaya kepada dua orang manusia seperti kita, padahal kaum mereka (Bani Israil) adalah orang-orang yang menghambakan diri kepada kita?”'),
	(2721, 23, 48, 'فَكَذَّبُوْهُمَا فَكَانُوْا مِنَ الْمُهْلَكِيْنَ ', 'faka<u>dzdz</u>abuuhum<u>aa</u> fak<u>aa</u>nuu mina <strong>a</strong>lmuhlakiin<strong>a</strong>', 'Maka mereka mendustakan keduanya, sebab itu mereka termasuk orang yang dibinasakan.'),
	(2722, 23, 49, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ لَعَلَّهُمْ يَهْتَدُوْنَ ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba la\'allahum yahtaduun<strong>a</strong>', 'Dan sungguh, telah Kami anugerahi kepada Musa Kitab (Taurat), agar mereka (Bani Israil) mendapat petunjuk.'),
	(2723, 23, 50, 'وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهٗٓ اٰيَةً وَّاٰوَيْنٰهُمَآ اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَّمَعِيْنٍ ࣖ', 'waja\'aln<u>aa</u> ibna maryama waummahu <u>aa</u>yatan wa<u>aa</u>wayn<u>aa</u>hum<u>aa</u> il<u>aa</u> rabwatin <u>dzaa</u>ti qar<u>aa</u>rin wama\'iin<strong>in</strong>', 'Dan telah Kami jadikan (Isa) putra Maryam bersama ibunya sebagai suatu bukti yang nyata bagi (kebesaran Kami), dan Kami melindungi mereka di sebuah dataran tinggi, (tempat yang tenang, rindang dan banyak buah-buahan) dengan mata air yang mengalir.'),
	(2724, 23, 51, 'يٰٓاَيُّهَا الرُّسُلُ كُلُوْا مِنَ الطَّيِّبٰتِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ۗ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>rrusulu kuluu mina <strong>al</strong><u>ththh</u>ayyib<u>aa</u>ti wa<strong>i</strong>\'maluu <u>shaa</u>li<u>h</u>an innii bim<u>aa</u> ta\'maluuna \'aliim<strong>un</strong>', 'Allah berfirman, “Wahai para rasul! Makanlah dari (makanan) yang baik-baik, dan kerjakanlah kebajikan. Sungguh, Aku Maha Mengetahui apa yang kamu kerjakan.'),
	(2725, 23, 52, 'وَاِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةً وَّاَنَا۠ رَبُّكُمْ فَاتَّقُوْنِ', 'wa-inna h<u>aadz</u>ihi ummatukum ummatan w<u>aah</u>idatan wa-an<u>aa</u> rabbukum fa<strong>i</strong>ttaquun<strong>i</strong>', 'Dan sungguh, (agama tauhid) inilah agama kamu, agama yang satu dan Aku adalah Tuhanmu, maka bertakwalah kepada-Ku.”'),
	(2726, 23, 53, 'فَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْ زُبُرًاۗ  كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ ', 'fataqa<u>ththh</u>a\'uu amrahum baynahum zuburan kullu <u>h</u>izbin bim<u>aa</u> ladayhim fari<u>h</u>uun<strong>a</strong>', 'Kemudian mereka terpecah belah dalam urusan (agama)nya menjadi beberapa golongan. Setiap golongan (merasa) bangga dengan apa yang ada pada mereka (masing-masing).'),
	(2727, 23, 54, 'فَذَرْهُمْ فِيْ غَمْرَتِهِمْ حَتّٰى حِيْنٍ ', 'fa<u>dz</u>arhum fii ghamratihim <u>h</u>att<u>aa</u> <u>h</u>iin<strong>in</strong>', 'Maka biarkanlah mereka dalam kesesatannya sampai waktu yang ditentukan.'),
	(2728, 23, 55, 'اَيَحْسَبُوْنَ اَنَّمَا نُمِدُّهُمْ بِهٖ مِنْ مَّالٍ وَّبَنِيْنَ ۙ', 'aya<u>h</u>sabuuna annam<u>aa</u> numidduhum bihi min m<u>aa</u>lin wabaniin<strong>a</strong>', 'Apakah mereka mengira bahwa Kami memberikan harta dan anak-anak kepada mereka itu (berarti bahwa),'),
	(2729, 23, 56, 'نُسَارِعُ لَهُمْ فِى الْخَيْرٰتِۗ بَلْ لَّا يَشْعُرُوْنَ', 'nus<u>aa</u>ri\'u lahum fii <strong>a</strong>lkhayr<u>aa</u>ti bal l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Kami segera memberikan kebaikan-kebaikan kepada mereka? (Tidak), tetapi mereka tidak menyadarinya.'),
	(2730, 23, 57, 'اِنَّ الَّذِيْنَ هُمْ مِّنْ خَشْيَةِ رَبِّهِمْ مُّشْفِقُوْنَ ۙ', 'inna <strong>al</strong>la<u>dz</u>iina hum min khasyyati rabbihim musyfiquun<strong>a</strong>', 'Sungguh, orang-orang yang karena takut (azab) Tuhannya, mereka sangat berhati-hati,'),
	(2731, 23, 58, 'وَالَّذِيْنَ هُمْ بِاٰيٰتِ رَبِّهِمْ يُؤْمِنُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum bi-<u>aa</u>y<u>aa</u>ti rabbihim yu/minuun<strong>a</strong>', 'dan mereka yang beriman dengan tanda-tanda (kekuasaan) Tuhannya,'),
	(2732, 23, 59, 'وَالَّذِيْنَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina hum birabbihim l<u>aa</u> yusyrikuun<strong>a</strong>', 'dan mereka yang tidak mempersekutukan Tuhannya,'),
	(2733, 23, 60, 'وَالَّذِيْنَ يُؤْتُوْنَ مَآ اٰتَوْا وَّقُلُوْبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رٰجِعُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina yu/tuuna m<u>aa</u> <u>aa</u>taw waquluubuhum wajilatun annahum il<u>aa</u> rabbihim r<u>aa</u>ji\'uun<strong>a</strong>', 'dan mereka yang memberikan apa yang mereka berikan (sedekah) dengan hati penuh rasa takut (karena mereka tahu) bahwa sesungguhnya mereka akan kembali kepada Tuhannya,'),
	(2734, 23, 61, 'اُولٰۤىِٕكَ يُسَارِعُوْنَ فِى الْخَيْرٰتِ وَهُمْ لَهَا سٰبِقُوْنَ', 'ul<u>aa</u>-ika yus<u>aa</u>ri\'uuna fii <strong>a</strong>lkhayr<u>aa</u>ti wah', 'mereka itu bersegera dalam kebaikan-kebaikan, dan merekalah orang-orang yang lebih dahulu memperolehnya.'),
	(2735, 23, 62, 'وَلَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۖ وَلَدَيْنَا كِتٰبٌ يَّنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ ', 'wal<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> waladayn<u>aa</u> kit<u>aa</u>bun yan<u>th</u>iqu bi<strong>a</strong>l<u>h</u>aqqi wahum l<u>aa</u> yu<u>zh</u>lamuun<strong>a</strong>', 'Dan Kami tidak membebani seseorang melainkan menurut kesanggupannya, dan pada Kami ada suatu catatan yang menuturkan dengan sebenarnya, dan mereka tidak dizalimi (dirugikan). '),
	(2736, 23, 63, 'بَلْ قُلُوْبُهُمْ فِيْ غَمْرَةٍ مِّنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِّنْ دُوْنِ ذٰلِكَ هُمْ لَهَا عَامِلُوْنَ', 'bal quluubuhum fii ghamratin min h<u>aadzaa</u> walahum a\'m<u>aa</u>lun min duuni <u>dzaa</u>lika hum lah<u>aa</u> \'<u>aa</u>miluun<strong>a</strong>', 'Tetapi, hati mereka (orang-orang kafir) itu dalam kesesatan dari (memahami Al-Qur\'an) ini, dan mereka mempunyai (kebiasaan banyak mengerjakan) perbuatan-perbuatan lain (buruk) yang terus mereka kerjakan.'),
	(2737, 23, 64, 'حَتّٰٓى اِذَآ اَخَذْنَا مُتْرَفِيْهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُوْنَ ۗ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> akha<u>dz</u>n<u>aa</u> mutrafiihim bi<strong>a</strong>l\'a<u>dzaa</u>bi i<u>dzaa</u> hum yaj-aruun<strong>a</strong>', 'Sehingga apabila Kami timpakan siksaan kepada orang-orang yang hidup bermewah-mewah di antara mereka, seketika itu mereka berteriak-teriak meminta tolong.'),
	(2738, 23, 65, 'لَا تَجْـَٔرُوا الْيَوْمَۖ اِنَّكُمْ مِّنَّا لَا تُنْصَرُوْنَ ', 'l<u>aa</u> taj-aruu <strong>a</strong>lyawma innakum minn<u>aa</u> l<u>aa</u> tun<u>sh</u>aruun<strong>a</strong>', 'Janganlah kamu berteriak-teriak meminta tolong pada hari ini! Sungguh, kamu tidak akan mendapat pertolongan dari Kami.'),
	(2739, 23, 66, 'قَدْ كَانَتْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُوْنَ ۙ', 'qad k<u>aa</u>nat <u>aa</u>y<u>aa</u>tii tutl<u>aa</u> \'alaykum fakuntum \'al<u>aa</u> a\'q<u>aa</u>bikum tanki<u>sh</u>uun<strong>a</strong>', 'Sesungguhnya ayat-ayat-Ku (Al-Qur\'an) selalu dibacakan kepada kamu, tetapi kamu selalu berpaling ke belakang,'),
	(2740, 23, 67, 'مُسْتَكْبِرِيْنَۙ بِهٖ سٰمِرًا تَهْجُرُوْنَ ', 'mustakbiriina bihi s<u>aa</u>miran tahjuruun<strong>a</strong>', 'dengan menyombongkan diri dan mengucapkan perkataan-perkataan keji terhadapnya (Al-Qur\'an) pada waktu kamu bercakap-cakap pada malam hari.'),
	(2741, 23, 68, 'اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَاۤءَهُمْ مَّا لَمْ يَأْتِ اٰبَاۤءَهُمُ الْاَوَّلِيْنَ ۖ', 'afalam yaddabbaruu <strong>a</strong>lqawla am j<u>aa</u>-ahum m<u>aa</u> lam ya/ti <u>aa</u>b<u>aa</u>-ahumu <strong>a</strong>l-awwaliin<strong>a</strong>', 'Maka tidakkah mereka menghayati firman (Allah), atau adakah telah datang kepada mereka apa yang tidak pernah datang kepada nenek moyang mereka terdahulu?'),
	(2742, 23, 69, 'اَمْ لَمْ يَعْرِفُوْا رَسُوْلَهُمْ فَهُمْ لَهٗ مُنْكِرُوْنَ ۖ', 'am lam ya\'rifuu rasuulahum fahum lahu munkiruun<strong>a</strong>', 'Ataukah mereka tidak mengenal Rasul mereka (Muhammad), karena itu mereka mengingkarinya?'),
	(2743, 23, 70, 'اَمْ يَقُوْلُوْنَ بِهٖ جِنَّةٌ ۗ بَلْ جَاۤءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كٰرِهُوْنَ ', 'am yaquuluuna bihi jinnatun bal j<u>aa</u>-ahum bi<strong>a</strong>l<u>h</u>aqqi wa-aktsaruhum lil<u>h</u>aqqi k<u>aa</u>rihuun<strong>a</strong>', 'Atau mereka berkata, “Orang itu (Muhammad) gila.” Padahal, dia telah datang membawa kebenaran kepada mereka, tetapi kebanyakan mereka membenci kebenaran. '),
	(2744, 23, 71, 'وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَاۤءَهُمْ لَفَسَدَتِ السَّمٰوٰتُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ بَلْ اَتَيْنٰهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُّعْرِضُوْنَ ۗ', 'walawi ittaba\'a <strong>a</strong>l<u>h</u>aqqu ahw<u>aa</u>-ahum lafasadati <strong>al</strong>ssam<u>aa</u>w<u>aa</u>tu wa<strong>a</strong>l-ar<u>dh</u>u waman fiihinna bal atayn<u>aa</u>hum bi<u>dz</u>ikrihim fahum \'an <u>dz</u>ikrihim mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan seandainya kebenaran itu menuruti keinginan mereka, pasti binasalah langit dan bumi, dan semua yang ada di dalamnya. Bahkan Kami telah memberikan peringatan kepada mereka, tetapi mereka berpaling dari peringatan itu.'),
	(2745, 23, 72, 'اَمْ تَسْـَٔلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖوَّهُوَ خَيْرُ الرّٰزِقِيْنَ', 'am tas-aluhum kharjan fakhar<u>aa</u>ju rabbika khayrun wahuwa khayru <strong>al</strong>rr<u>aa</u>ziqiin<strong>a</strong>', 'Atau engkau (Muhammad) meminta imbalan kepada mereka? Sedangkan imbalan dari Tuhanmu lebih baik, karena Dia pemberi rezeki yang terbaik.'),
	(2746, 23, 73, 'وَاِنَّكَ لَتَدْعُوْهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'wa-innaka latad\'uuhum il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Dan sesungguhnya engkau pasti telah menyeru mereka kepada jalan yang lurus.'),
	(2747, 23, 74, 'وَاِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُوْنَ ', 'wa-inna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati \'ani <strong>al</strong><u>shsh</u>ir<u>aath</u>i lan<u>aa</u>kibuun<strong>a</strong>', 'Dan sesungguhnya orang-orang yang tidak beriman kepada akhirat benar-benar telah menyimpang jauh dari jalan (yang lurus).'),
	(2748, 23, 75, '۞ وَلَوْ رَحِمْنٰهُمْ وَكَشَفْنَا مَا بِهِمْ مِّنْ ضُرٍّ لَّلَجُّوْا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ', 'walaw ra<u>h</u>imn<u>aa</u>hum wakasyafn<u>aa</u> m<u>aa</u> bihim min <u>dh</u>urrin lalajjuu fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun<strong>a</strong>', 'Dan seandainya mereka Kami kasihani, dan Kami lenyapkan malapetaka yang menimpa mereka, pasti mereka akan terus-menerus terombang-ambing dalam kesesatan mereka.'),
	(2749, 23, 76, 'وَلَقَدْ اَخَذْنٰهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوْا لِرَبِّهِمْ وَمَا يَتَضَرَّعُوْنَ', 'walaqad akha<u>dz</u>n<u>aa</u>hum bi<strong>a</strong>l\'a<u>dzaa</u>bi fam<u>aa</u> istak<u>aa</u>nuu lirabbihim wam<u>aa</u> yata<u>dh</u>arra\'uun<strong>a</strong>', 'Dan sungguh Kami telah menimpakan siksaan kepada mereka, tetapi mereka tidak mau tunduk kepada Tuhannya, dan (juga) tidak merendahkan diri.'),
	(2750, 23, 77, 'حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيْدٍ اِذَا هُمْ فِيْهِ مُبْلِسُوْنَ ࣖ ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> fata<u>h</u>n<u>aa</u> \'alayhim b<u>aa</u>ban <u>dzaa</u> \'a<u>dzaa</u>bin syadiidin i<u>dzaa</u> hum fiihi mublisuun<strong>a</strong>', 'Sehingga apabila Kami bukakan untuk mereka pintu azab yang sangat keras, seketika itu mereka menjadi putus asa.'),
	(2751, 23, 78, 'وَهُوَ الَّذِيْٓ اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ansya-a lakumu <strong>al</strong>ssam\'a wa<strong>a</strong>l-ab<u>shaa</u>ra wa<strong>a</strong>l-af-idata qaliilan m<u>aa</u> tasykuruun<strong>a</strong>', 'Dan Dialah yang telah menciptakan bagimu pendengaran, penglihatan dan hati nurani, tetapi sedikit sekali kamu bersyukur.'),
	(2752, 23, 79, 'وَهُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ', 'wahuwa <strong>al</strong>la<u>dz</u>ii <u>dz</u>ara-akum fii <strong>a</strong>l-ar<u>dh</u>i wa-ilayhi tu<u>h</u>syaruun<strong>a</strong>', 'Dan Dialah yang menciptakan dan mengembangbiakkan kamu di bumi dan kepada-Nyalah kamu akan dikumpulkan.'),
	(2753, 23, 80, 'وَهُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۗ اَفَلَا تَعْقِلُوْنَ', 'wahuwa <strong>al</strong>la<u>dz</u>ii yu<u>h</u>yii wayumiitu walahu ikhtil<u>aa</u>fu <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Dan Dialah yang menghidupkan dan mematikan, dan Dialah yang (mengatur) pergantian malam dan siang. Tidakkah kamu mengerti?'),
	(2754, 23, 81, 'بَلْ قَالُوْا مِثْلَ مَا قَالَ الْاَوَّلُوْنَ', 'bal q<u>aa</u>luu mitsla m<u>aa</u> q<u>aa</u>la <strong>a</strong>l-awwaluun<strong>a</strong>', 'Bahkan mereka mengucapkan perkataan yang serupa dengan apa yang diucapkan oleh orang-orang terdahulu.'),
	(2755, 23, 82, 'قَالُوْٓا ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَ', 'q<u>aa</u>luu a-i<u>dzaa</u> mitn<u>aa</u> wakunn<u>aa</u> tur<u>aa</u>ban wa\'i<u>zhaa</u>man a-inn<u>aa</u> lamab\'uutsuun<strong>a</strong>', 'Mereka berkata, “Apakah betul, apabila kami telah mati dan telah menjadi tanah dan tulang belulang, kami benar-benar akan dibangkitkan kembali?  '),
	(2756, 23, 83, 'لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَاۤؤُنَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ', 'laqad wu\'idn<u>aa</u> na<u>h</u>nu wa<u>aa</u>b<u>aa</u>un<u>aa</u> h<u>aadzaa</u> min qablu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru <strong>a</strong>l-awwaliin<strong>a</strong>', 'Sungguh, yang demikian ini sudah dijanjikan kepada kami dan kepada nenek moyang kami dahulu, ini hanyalah dongeng orang-orang terdahulu!”'),
	(2757, 23, 84, 'قُلْ لِّمَنِ الْاَرْضُ وَمَنْ فِيْهَآ اِنْ كُنْتُمْ تَعْلَمُوْنَ ', 'qul limani <strong>a</strong>l-ar<u>dh</u>u waman fiih<u>aa</u> in kuntum ta\'lamuun<strong>a</strong>', 'Katakanlah (Muhammad), “Milik siapakah bumi, dan semua yang ada di dalamnya, jika kamu mengetahui?”'),
	(2758, 23, 85, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَذَكَّرُوْنَ ', 'sayaquuluuna lill<u>aa</u>hi qul afal<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Mereka akan menjawab, “Milik Allah.” Katakanlah, “Maka apakah kamu tidak ingat?”'),
	(2759, 23, 86, 'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيْمِ', 'qul man rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti <strong>al</strong>ssab\'i warabbu <strong>a</strong>l\'arsyi <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Katakanlah, “Siapakah Tuhan yang memiliki langit yang tujuh dan yang memiliki ‘Arsy yang agung?”'),
	(2760, 23, 87, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَتَّقُوْنَ', 'sayaquuluuna lill<u>aa</u>hi qul afal<u>aa</u> tattaquun<strong>a</strong>', 'Mereka akan menjawab, “(Milik) Allah.” Katakanlah, “Maka mengapa kamu tidak bertakwa?”'),
	(2761, 23, 88, 'قُلْ مَنْۢ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّهُوَ يُجِيْرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُوْنَ ', 'qul man biyadihi malakuutu kulli syay-in wahuwa yujiiru wal<u>aa</u> yuj<u>aa</u>ru \'alayhi in kuntum ta\'lamuun<strong>a</strong>', 'Katakanlah, “Siapakah yang di tangan-Nya berada kekuasaan segala sesuatu. Dia melindungi, dan tidak ada yang dapat dilindungi dari (azab-Nya), jika kamu mengetahui?”'),
	(2762, 23, 89, 'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ فَاَنّٰى تُسْحَرُوْنَ', 'sayaquuluuna lill<u>aa</u>hi qul fa-ann<u>aa</u> tus<u>h</u>aruun<strong>a</strong>', 'Mereka akan menjawab, “(Milik) Allah.” Katakanlah, “(Kalau demikian), maka bagaimana kamu sampai tertipu?”'),
	(2763, 23, 90, 'بَلْ اَتَيْنٰهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكٰذِبُوْنَ', 'bal atayn<u>aa</u>hum bi<strong>a</strong>l<u>h</u>aqqi wa-innahum lak<u>aadz</u>ibuun<strong>a</strong>', 'Padahal Kami telah membawa kebenaran kepada mereka, tetapi mere-ka benar-benar pendusta.'),
	(2764, 23, 91, 'مَا اتَّخَذَ اللّٰهُ مِنْ وَّلَدٍ وَّمَا كَانَ مَعَهٗ مِنْ اِلٰهٍ اِذًا لَّذَهَبَ كُلُّ اِلٰهٍۢ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۗ سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَ ۙ', 'm<u>aa</u> ittakha<u>dz</u>a <strong>al</strong>l<u>aa</u>hu min waladin wam<u>aa</u> k<u>aa</u>na ma\'ahu min il<u>aa</u>hin i<u>dz</u>an la<u>dz</u>ahaba kullu il<u>aa</u>hin bim<u>aa</u> khalaqa wala\'al<u>aa</u> ba\'<u>dh</u>uhum \'al<u>aa</u> ba\'<u>dh</u>in sub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi \'amm<u>aa</u> ya<u>sh</u>ifuun<strong>a</strong>', 'Allah tidak mempunyai anak, dan tidak ada tuhan (yang lain) bersama-Nya, (sekiranya tuhan banyak), maka masing-masing tuhan itu akan membawa apa (makhluk) yang diciptakannya, dan sebagian dari tuhan-tuhan itu akan mengalahkan sebagian yang lain. Mahasuci Allah dari apa yang mereka sifatkan itu,'),
	(2765, 23, 92, 'عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ࣖ', '\'<u>aa</u>limi <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati fata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', '(Dialah Tuhan) yang mengetahui semua yang gaib dan semua yang tampak. Mahatinggi (Allah) dari apa yang mereka persekutukan.'),
	(2766, 23, 93, 'قُلْ رَّبِّ اِمَّا تُرِيَنِّيْ مَا يُوْعَدُوْنَ ۙ', 'qul rabbi imm<u>aa</u> turiyannii m<u>aa</u> yuu\'aduun<strong>a</strong>', 'Katakanlah (Muhammad), “Ya Tuhanku, seandainya Engkau hendak memperlihatkan kepadaku apa (azab) yang diancamkan kepada mereka,'),
	(2767, 23, 94, 'رَبِّ فَلَا تَجْعَلْنِيْ فِى الْقَوْمِ الظّٰلِمِيْنَ', 'rabbi fal<u>aa</u> taj\'alnii fii <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Ya Tuhanku, maka janganlah Engkau jadikan aku dalam golongan orang-orang zalim.”'),
	(2768, 23, 95, 'وَاِنَّا عَلٰٓى اَنْ نُّرِيَكَ مَا نَعِدُهُمْ لَقٰدِرُوْنَ', 'wa-inn<u>aa</u> \'al<u>aa</u> an nuriyaka m<u>aa</u> na\'iduhum laq<u>aa</u>diruun<strong>a</strong>', 'Dan sungguh, Kami kuasa untuk memperlihatkan kepadamu (Muhammad) apa yang Kami ancamkan kepada mereka.'),
	(2769, 23, 96, 'اِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ السَّيِّئَةَۗ  نَحْنُ اَعْلَمُ بِمَا يَصِفُوْنَ ', 'idfa\' bi<strong>a</strong>llatii hiya a<u>h</u>sanu <strong>al</strong>ssayyi-ata na<u>h</u>nu a\'lamu bim<u>aa</u> ya<u>sh</u>ifuun<strong>a</strong>', 'Tolaklah perbuatan buruk mereka dengan (cara) yang lebih baik, Kami lebih mengetahui apa yang mereka sifatkan (kepada Allah).'),
	(2770, 23, 97, 'وَقُلْ رَّبِّ اَعُوْذُ بِكَ مِنْ هَمَزٰتِ الشَّيٰطِيْنِ ۙ', 'waqul rabbi a\'uu<u>dz</u>u bika min hamaz<u>aa</u>ti <strong>al</strong>sysyay<u>aath</u>iin<strong>i</strong>', 'Dan katakanlah, “Ya Tuhanku, aku berlindung kepada Engkau dari bisikan-bisikan setan,'),
	(2771, 23, 98, 'وَاَعُوْذُ بِكَ رَبِّ اَنْ يَّحْضُرُوْنِ ', 'wa-a\'uu<u>dz</u>u bika rabbi an ya<u>hd</u>uruun<strong>i</strong>', 'dan aku berlindung (pula) kepada Engkau ya Tuhanku, agar mereka tidak mendekati aku.”'),
	(2772, 23, 99, 'حَتّٰٓى اِذَا جَاۤءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُوْنِ ۙ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a a<u>h</u>adahumu <strong>a</strong>lmawtu q<u>aa</u>la rabbi irji\'uun<strong>i</strong>', '(Demikianlah keadaan orang-orang kafir itu), hingga apabila datang kematian kepada seseorang dari mereka, dia berkata, “Ya Tuhanku, kembalikanlah aku (ke dunia),'),
	(2773, 23, 100, 'لَعَلِّيْٓ اَعْمَلُ صَالِحًا فِيْمَا تَرَكْتُ كَلَّاۗ اِنَّهَا كَلِمَةٌ هُوَ قَاۤىِٕلُهَاۗ وَمِنْ وَّرَاۤىِٕهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُوْنَ ', 'la\'allii a\'malu <u>shaa</u>li<u>h</u>an fiim<u>aa</u> taraktu kall<u>aa</u> innah<u>aa</u> kalimatun huwa q<u>aa</u>-iluh<u>aa</u> wamin war<u>aa</u>-ihim barzakhun il<u>aa</u> yawmi yub\'atsuun<strong>a</strong>', 'agar aku dapat berbuat kebajikan yang telah aku tinggalkan.” Sekali-kali tidak! Sesungguhnya itu adalah dalih yang diucapkannya saja. Dan di hadapan mereka ada barzakh sampai pada hari mereka dibangkitkan.'),
	(2774, 23, 101, 'فَاِذَا نُفِخَ فِى الصُّوْرِ فَلَآ اَنْسَابَ بَيْنَهُمْ يَوْمَىِٕذٍ وَّلَا يَتَسَاۤءَلُوْنَ', 'fa-i<u>dzaa</u> nufikha fii <strong>al</strong><u>shsh</u>uuri fal<u>aa</u> ans<u>aa</u>ba baynahum yawma-i<u>dz</u>in wal<u>aa</u> yatas<u>aa</u>-aluun<strong>a </strong>', 'Apabila sangkakala ditiup maka tidak ada lagi pertalian keluarga di antara mereka pada hari itu (hari Kiamat), dan tidak (pula) mereka saling bertanya.'),
	(2775, 23, 102, 'فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ', 'faman tsaqulat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Barangsiapa berat timbangan (kebaikan)nya, maka mereka itulah orang-orang yang beruntung.'),
	(2776, 23, 103, 'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فِيْ جَهَنَّمَ خٰلِدُوْنَ ۚ', 'waman khaffat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina khasiruu anfusahum fii jahannama kh<u>aa</u>liduun<strong>a</strong>', 'Dan barang siapa ringan timbangan (kebaikan)nya, maka mereka itulah orang-orang yang merugikan dirinya sendiri, mereka kekal di dalam neraka Jahanam'),
	(2777, 23, 104, 'تَلْفَحُ وُجُوْهَهُمُ النَّارُ وَهُمْ فِيْهَا كَالِحُوْنَ ', 'talfa<u>h</u>u wujuuhahumu <strong>al</strong>nn<u>aa</u>ru wahum fiih<u>aa</u> k<u>aa</u>li<u>h</u>uun<strong>a</strong>', 'Wajah mereka dibakar api neraka, dan mereka di neraka dalam keadaan muram dengan bibir yang cacat.'),
	(2778, 23, 105, 'اَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُوْنَ', 'alam takun <u>aa</u>y<u>aa</u>tii tutl<u>aa</u> \'alaykum fakuntum bih<u>aa</u> tuka<u>dzdz</u>ibuun<strong>a</strong>', 'Bukankah ayat-ayat-Ku telah dibacakan kepadamu, tetapi kamu selalu mendustakannya?'),
	(2779, 23, 106, 'قَالُوْا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَاۤلِّيْنَ', 'q<u>aa</u>luu rabban<u>aa</u> ghalabat \'alayn<u>aa</u> syiqwatun<u>aa</u> wakunn<u>aa</u> qawman <u>daa</u>lliin<strong>a</strong>', 'Mereka berkata, “Ya Tuhan kami, kami telah dikuasai oleh kejahatan kami, dan kami adalah orang-orang yang sesat.'),
	(2780, 23, 107, 'رَبَّنَآ اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظٰلِمُوْنَ', 'rabban<u>aa</u> akhrijn<u>aa</u> minh<u>aa</u> fa-in \'udn<u>aa</u> fa-inn<u>aa</u> <u>zhaa</u>limuun<strong>a</strong>', 'Ya Tuhan kami, keluarkanlah kami darinya (kembalikanlah kami ke dunia), jika kami masih juga kembali (kepada kekafiran), sungguh, kami adalah orang-orang yang zalim.”'),
	(2781, 23, 108, 'قَالَ اخْسَـُٔوْا فِيْهَا وَلَا تُكَلِّمُوْنِ', 'q<u>aa</u>la ikhsauu fiih<u>aa</u> wal<u>aa</u> tukallimuun<strong>i</strong>', 'Dia (Allah) berfirman, “Tinggallah dengan hina di dalamnya, dan janganlah kamu berbicara dengan Aku.”'),
	(2782, 23, 109, 'اِنَّهٗ كَانَ فَرِيْقٌ مِّنْ عِبَادِيْ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ۚ', 'innahu k<u>aa</u>na fariiqun min \'ib<u>aa</u>dii yaquuluuna rabban<u>aa</u> <u>aa</u>mann<u>aa</u> fa<strong>i</strong>ghfir lan<u>aa</u> wa<strong>i</strong>r<u>h</u>amn<u>aa</u> wa-anta khayru <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Sesungguhnya ada segolongan dari hamba-hamba-Ku berdoa, “Ya Tuhan kami, kami telah beriman, maka ampunilah kami dan berilah kami rahmat, Engkau adalah pemberi rahmat yang terbaik.”'),
	(2783, 23, 110, 'فَاتَّخَذْتُمُوْهُمْ سِخْرِيًّا حَتّٰٓى اَنْسَوْكُمْ ذِكْرِيْ وَكُنْتُمْ مِّنْهُمْ تَضْحَكُوْنَ', 'fa<strong>i</strong>ttakha<u>dz</u>tumuuhum sikhriyyan <u>h</u>att<u>aa</u> ansawkum <u>dz</u>ikrii wakuntum minhum ta<u>dh</u>akuun<strong>a</strong>', 'Lalu kamu jadikan mereka buah ejekan, sehingga kamu lupa mengingat Aku, dan kamu (selalu) menertawakan mereka,'),
	(2784, 23, 111, 'اِنِّيْ جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوْٓاۙ اَنَّهُمْ هُمُ الْفَاۤىِٕزُوْنَ', 'innii jazaytuhumu <strong>a</strong>lyawma bim<u>aa</u> <u>sh</u>abaruu annahum humu <strong>a</strong>lf<u>aa</u>-izuun<strong>a</strong>', 'sesungguhnya pada hari ini Aku memberi balasan kepada mereka, karena kesabaran mereka; sesungguhnya mereka itulah orang-orang yang memperoleh kemenangan.'),
	(2785, 23, 112, 'قٰلَ كَمْ لَبِثْتُمْ فِى الْاَرْضِ عَدَدَ سِنِيْنَ', 'q<u>aa</u>la kam labitstum fii <strong>a</strong>l-ar<u>dh</u>i \'adada siniin<strong>a</strong>', 'Dia (Allah) berfirman, “Berapa tahunkah lamanya kamu tinggal di bumi?”'),
	(2786, 23, 113, 'قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَاۤدِّيْنَ ', 'q<u>aa</u>luu labitsn<u>aa</u> yawman aw ba\'<u>dh</u>a yawmin fa<strong>i</strong>s-ali <strong>a</strong>l\'<u>aa</u>ddiin<strong>a</strong>', 'Mereka menjawab, “Kami tinggal (di bumi) sehari atau setengah hari, maka tanyakanlah kepada mereka yang menghitung.”'),
	(2787, 23, 114, 'قٰلَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا لَّوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُوْنَ', 'q<u>aa</u>la in labitstum ill<u>aa</u> qaliilan law annakum kuntum ta\'lamuun<strong>a</strong>', 'Dia (Allah) berfirman, “Kamu tinggal (di bumi) hanya sebentar saja, jika kamu benar-benar mengetahui.” '),
	(2788, 23, 115, 'اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنٰكُمْ عَبَثًا وَّاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُوْنَ', 'afa<u>h</u>asibtum annam<u>aa</u> khalaqn<u>aa</u>kum \'abatsan wa-annakum ilayn<u>aa</u> l<u>aa</u> turja\'uun<strong>a</strong>', 'Maka apakah kamu mengira, bahwa Kami menciptakan kamu main-main (tanpa ada maksud) dan bahwa kamu tidak akan dikembalikan kepada Kami?'),
	(2789, 23, 116, 'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ  لَآ اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَرِيْمِ', 'fata\'<u>aa</u>l<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmaliku <strong>a</strong>l<u>h</u>aqqu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa rabbu <strong>a</strong>l\'arsyi <strong>a</strong>lkariim<strong>i</strong>', 'Maka Mahatinggi Allah, Raja yang sebenarnya; tidak ada tuhan (yang berhak disembah) selain Dia, Tuhan (yang memiliki) ‘Arsy yang mulia.'),
	(2790, 23, 117, 'وَمَنْ يَّدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ لَا بُرْهَانَ لَهٗ بِهٖۙ فَاِنَّمَا حِسَابُهٗ عِنْدَ رَبِّهٖۗ اِنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ', 'waman yad\'u ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara l<u>aa</u> burh<u>aa</u>na lahu bihi fa-innam<u>aa</u> <u>h</u>is<u>aa</u>buhu \'inda rabbihi innahu l<u>aa</u> yufli<u>h</u>u <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Dan barang siapa menyembah tuhan yang lain selain Allah, padahal tidak ada suatu bukti pun baginya tentang itu, maka perhitungannya hanya pada Tuhannya. Sesungguhnya orang-orang kafir itu tidak akan beruntung.'),
	(2791, 23, 118, 'وَقُلْ رَّبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ࣖ', 'waqul rabbi ighfir wa<strong>i</strong>r<u>h</u>am wa-anta khayru <strong>al</strong>rr<u>aah</u>imiin<strong>a</strong>', 'Dan katakanlah (Muhammad), “Ya Tuhanku, berilah ampunan dan (berilah) rahmat, Engkaulah pemberi rahmat yang terbaik.”'),
	(2792, 24, 1, 'سُوْرَةٌ اَنْزَلْنٰهَا وَفَرَضْنٰهَا وَاَنْزَلْنَا فِيْهَآ اٰيٰتٍۢ بَيِّنٰتٍ لَّعَلَّكُمْ تَذَكَّرُوْنَ', 'suuratun anzaln<u>aa</u>h<u>aa</u> wafara<u>dh</u>n<u>aa</u>h<u>aa</u> wa-anzaln<u>aa</u> fiih<u>aa</u> <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin la\'allakum ta<u>dz</u>akkaruun<strong>a</strong>', '(Inilah) suatu surah yang Kami turunkan dan Kami wajibkan (menjalankan hukum-hukum)nya, dan Kami turunkan di dalamnya tanda-tanda (kebesaran Allah) yang jelas, agar kamu ingat.'),
	(2793, 24, 2, 'اَلزَّانِيَةُ وَالزَّانِيْ فَاجْلِدُوْا كُلَّ وَاحِدٍ مِّنْهُمَا مِائَةَ جَلْدَةٍ ۖوَّلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِيْ دِيْنِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَاۤىِٕفَةٌ مِّنَ الْمُؤْمِنِ', 'a<strong>l</strong>zz<u>aa</u>niyatu wa<strong>al</strong>zz<u>aa</u>nii fa<strong>i</strong>jliduu kulla w<u>aah</u>idin minhum<u>aa</u> mi-ata jaldatin wal<u>aa</u> ta/khu<u>dz</u>kum bihim<u>aa</u> ra/fatun fii diini <strong>al</strong>l<u>aa</u>hi in kuntum tu/minuuna bi<strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>lyawmi <strong>a</strong>l-<u>aa</u>khiri walyasyhad \'a<u>dzaa</u>bahum<u>aa</u> <u>th</u><u>aa</u>-ifatun mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Pezina perempuan dan pezina laki-laki, deralah masing-masing dari keduanya seratus kali, dan janganlah rasa belas kasihan kepada keduanya mencegah kamu untuk (menjalankan) agama (hukum) Allah, jika kamu beriman kepada Allah dan hari kemudian; dan hendaklah (pelaksanaan) hukuman mereka disaksikan oleh sebagian orang-orang yang beriman.'),
	(2794, 24, 3, 'اَلزَّانِيْ لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةً ۖوَّالزَّانِيَةُ لَا يَنْكِحُهَآ اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِنِيْنَ', 'a<strong>l</strong>zz<u>aa</u>nii l<u>aa</u> yanki<u>h</u>u ill<u>aa</u> z<u>aa</u>niyatan aw musyrikatan wa<strong>al</strong>zz<u>aa</u>niyatu l<u>aa</u> yanki<u>h</u>uh<u>aa</u> ill<u>aa</u> z<u>aa</u>nin aw musyrikun wa<u>h</u>urrima <u>dzaa</u>lika \'al<u>aa</u> <strong>a</strong>lmu/miniin<strong>a</strong>', 'Pezina laki-laki tidak boleh menikah kecuali dengan pezina perempuan, atau dengan perempuan musyrik; dan pezina perempuan tidak boleh menikah kecuali dengan pezina laki-laki atau dengan laki-laki musyrik; dan yang demikian itu diharamkan bagi orang-orang mukmin.'),
	(2795, 24, 4, 'وَالَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ ثُمَّ لَمْ يَأْتُوْا بِاَرْبَعَةِ شُهَدَاۤءَ فَاجْلِدُوْهُمْ ثَمٰنِيْنَ جَلْدَةً وَّلَا تَقْبَلُوْا لَهُمْ شَهَادَةً اَبَدًاۚ وَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina yarmuuna <strong>a</strong>lmu<u>hs</u>an<u>aa</u>ti tsumma lam ya/tuu bi-arba\'ati syuhad<u>aa</u>-a fa<strong>i</strong>jliduuhum tsam<u>aa</u>niina jaldatan wal<u>aa</u> taqbaluu lahum syah<u>aa</u>datan abadan waul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Dan orang-orang yang menuduh perempuan-perempuan yang baik (berzina) dan mereka tidak mendatangkan empat orang saksi, maka deralah mereka delapan puluh kali, dan janganlah kamu terima kesaksian mereka untuk selama-lamanya. Mereka itulah orang-orang yang fasik,'),
	(2796, 24, 5, 'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۚ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina t<u>aa</u>buu min ba\'di <u>dzaa</u>lika wa-a<u>sh</u>la<u>h</u>uu fa-inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', 'kecuali mereka yang bertobat setelah itu dan memperbaiki (dirinya), maka sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(2797, 24, 6, 'وَالَّذِيْنَ يَرْمُوْنَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَّهُمْ شُهَدَاۤءُ اِلَّآ اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهٰدٰتٍۢ بِاللّٰهِ ۙاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ', 'wa<strong>a</strong>lla<u>dz</u>iina yarmuuna azw<u>aa</u>jahum walam yakun lahum syuhad<u>aa</u>u ill<u>aa</u> anfusuhum fasyah<u>aa</u>datu a<u>h</u>adihim arba\'u syah<u>aa</u>d<u>aa</u>tin bi<strong>al</strong>l<u>aa</u>hi innahu lamina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Dan orang-orang yang menuduh istrinya (berzina), padahal mereka tidak mempunyai saksi-saksi selain diri mereka sendiri, maka kesaksian masing-masing orang itu ialah empat kali bersumpah dengan (nama) Allah, bahwa sesungguhnya dia termasuk orang yang berkata benar.'),
	(2798, 24, 7, 'وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ كَانَ مِنَ الْكٰذِبِيْنَ', 'wa<strong>a</strong>lkh<u>aa</u>misatu anna la\'nata <strong>al</strong>l<u>aa</u>hi \'alayhi in k<u>aa</u>na mina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dan (sumpah) yang kelima bahwa laknat Allah akan menimpanya, jika dia termasuk orang yang berdusta.'),
	(2799, 24, 8, 'وَيَدْرَؤُا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهٰدٰتٍۢ بِاللّٰهِ اِنَّهٗ لَمِنَ الْكٰذِبِيْنَ ۙ', 'wayadrau \'anh<u>aa</u> <strong>a</strong>l\'a<u>dzaa</u>ba an tasyhada arba\'a syah<u>aa</u>d<u>aa</u>tin bi<strong>al</strong>l<u>aa</u>hi innahu lamina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dan istri itu terhindar dari hukuman apabila dia bersumpah empat kali atas (nama) Allah bahwa dia (suaminya) benar-benar termasuk orang-orang yang berdusta,'),
	(2800, 24, 9, 'وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَآ اِنْ كَانَ مِنَ الصّٰدِقِيْنَ ', 'wa<strong>a</strong>lkh<u>aa</u>misata anna gha<u>dh</u>aba <strong>al</strong>l<u>aa</u>hi \'alayh<u>aa</u> in k<u>aa</u>na mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'dan (sumpah) yang kelima bahwa kemurkaan Allah akan menimpanya (istri), jika dia (suaminya) itu termasuk orang yang berkata benar.   '),
	(2801, 24, 10, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ تَوَّابٌ حَكِيْمٌ ࣖ', 'walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu wa-anna <strong>al</strong>l<u>aa</u>ha taww<u>aa</u>bun <u>h</u>akiim<strong>un</strong>', 'Dan seandainya bukan karena karunia Allah dan rahmat-Nya kepadamu (niscaya kamu akan menemui kesulitan). Dan sesungguhnya Allah Maha Penerima Tobat, Mahabijaksana.'),
	(2802, 24, 11, 'اِنَّ الَّذِيْنَ جَاۤءُوْ بِالْاِفْكِ عُصْبَةٌ مِّنْكُمْۗ  لَا تَحْسَبُوْهُ شَرًّا لَّكُمْۗ بَلْ هُوَ خَيْرٌ لَّكُمْۗ لِكُلِّ امْرِئٍ مِّنْهُمْ مَّا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذِيْ تَوَلّٰى كِبْرَهٗ مِنْهُمْ لَهٗ عَذَابٌ عَظِيْمٌ', 'inna <strong>al</strong>la<u>dz</u>iina j<u>aa</u>uu bi<strong>a</strong>l-ifki \'u<u>sh</u>batun minkum l<u>aa</u> ta<u>h</u>sabuuhu syarran lakum bal huwa khayrun lakum likulli imri-in minhum m<u>aa</u> iktasaba mina <strong>a</strong>l-itsmi wa<strong>a</strong>lla<u>dz</u>ii tawall<u>aa</u> kibrahu minhum lahu \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Sesungguhnya orang-orang yang membawa berita bohong itu adalah dari golongan kamu (juga). Janganlah kamu mengira berita itu buruk bagi kamu bahkan itu baik bagi kamu. Setiap orang dari mereka akan mendapat balasan dari dosa yang diperbuatnya. Dan barangsiapa di antara mereka yang mengambil bagian terbesar (dari dosa yang diperbuatnya), dia mendapat azab yang besar (pula).'),
	(2803, 24, 12, 'لَوْلَآ اِذْ سَمِعْتُمُوْهُ ظَنَّ الْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بِاَنْفُسِهِمْ خَيْرًاۙ وَّقَالُوْا هٰذَآ اِفْكٌ مُّبِيْنٌ', 'lawl<u>aa</u> i<u>dz</u> sami\'tumuuhu <u>zh</u>anna <strong>a</strong>lmu/minuuna wa<strong>a</strong>lmu/min<u>aa</u>tu bi-anfusihim khayran waq<u>aa</u>luu h<u>aadzaa</u> ifkun mubiin<strong>un</strong>', 'Mengapa orang-orang mukmin dan mukminat tidak berbaik sangka terhadap diri mereka sendiri, ketika kamu mendengar berita bohong itu dan berkata, “Ini adalah (suatu berita) bohong yang nyata.”'),
	(2804, 24, 13, 'لَوْلَا جَاۤءُوْ عَلَيْهِ بِاَرْبَعَةِ شُهَدَاۤءَۚ فَاِذْ لَمْ يَأْتُوْا بِالشُّهَدَاۤءِ فَاُولٰۤىِٕكَ عِنْدَ اللّٰهِ هُمُ الْكٰذِبُوْنَ', 'lawl<u>aa</u> j<u>aa</u>uu \'alayhi bi-arba\'ati syuhad<u>aa</u>-a fa-i<u>dz</u> lam ya/tuu bi<strong>al</strong>sysyuhad<u>aa</u>-i faul<u>aa</u>-ika \'inda <strong>al</strong>l<u>aa</u>hi humu <strong>a</strong>lk<u>aadz</u>ibuun<strong>a</strong>', 'Mengapa mereka (yang menuduh itu) tidak datang membawa empat saksi? Oleh karena mereka tidak membawa saksi-saksi, maka mereka itu dalam pandangan Allah adalah orang-orang yang berdusta.'),
	(2805, 24, 14, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ فِى الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ فِيْ مَآ اَفَضْتُمْ فِيْهِ عَذَابٌ عَظِيْمٌ ', 'walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati lamassakum fii m<u>aa</u> afa<u>dh</u>tum fiihi \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Dan seandainya bukan karena karunia Allah dan rahmat-Nya kepadamu di dunia dan di akhirat, niscaya kamu ditimpa azab yang besar, disebabkan oleh pembicaraan kamu tentang hal itu (berita bohong itu).'),
	(2806, 24, 15, 'اِذْ تَلَقَّوْنَهٗ بِاَلْسِنَتِكُمْ وَتَقُوْلُوْنَ بِاَفْوَاهِكُمْ مَّا لَيْسَ لَكُمْ بِهٖ عِلْمٌ وَّتَحْسَبُوْنَهٗ هَيِّنًاۙ وَّهُوَ عِنْدَ اللّٰهِ عَظِيْمٌ ۚ', 'i<u>dz</u> talaqqawnahu bi-alsinatikum wataquuluuna bi-afw<u>aa</u>hikum m<u>aa</u> laysa lakum bihi \'ilmun wata<u>h</u>sabuunahu hayyinan wahuwa \'inda <strong>al</strong>l<u>aa</u>hi \'a<u>zh</u>iim<strong>un</strong>', '(Ingatlah) ketika kamu menerima (berita bohong) itu dari mulut ke mulut dan kamu katakan dengan mulutmu apa yang tidak kamu ketahui sedikit pun, dan kamu menganggapnya remeh, padahal dalam pandangan Allah itu soal besar.'),
	(2807, 24, 16, 'وَلَوْلَآ اِذْ سَمِعْتُمُوْهُ قُلْتُمْ مَّا يَكُوْنُ لَنَآ اَنْ نَّتَكَلَّمَ بِهٰذَاۖ سُبْحٰنَكَ هٰذَا بُهْتَانٌ عَظِيْمٌ', 'walawl<u>aa</u> i<u>dz</u> sami\'tumuuhu qultum m<u>aa</u> yakuunu lan<u>aa</u> an natakallama bih<u>aadzaa</u> sub<u>haa</u>naka h<u>aadzaa</u> buht<u>aa</u>nun \'a<u>zh</u>iim<strong>un</strong>', 'Dan mengapa kamu tidak berkata ketika mendengarnya, “Tidak pantas bagi kita membicarakan ini.  Mahasuci Engkau, ini adalah kebohongan yang besar.”'),
	(2808, 24, 17, 'يَعِظُكُمُ اللّٰهُ اَنْ تَعُوْدُوْا لِمِثْلِهٖٓ اَبَدًا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ۚ', 'ya\'i<u>zh</u>ukumu <strong>al</strong>l<u>aa</u>hu an ta\'uuduu limitslihi abadan in kuntum mu/miniin<strong>a</strong>', 'Allah memperingatkan kamu agar (jangan) kembali mengulangi seperti itu selama-lamanya, jika kamu orang beriman,'),
	(2809, 24, 18, 'وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ', 'wayubayyinu <strong>al</strong>l<u>aa</u>hu lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'dan Allah menjelaskan ayat-ayat(-Nya) kepada kamu. Dan Allah Maha Mengetahui, Mahabijaksana.'),
	(2810, 24, 19, 'اِنَّ الَّذِيْنَ يُحِبُّوْنَ اَنْ تَشِيْعَ الْفَاحِشَةُ فِى الَّذِيْنَ اٰمَنُوْا لَهُمْ عَذَابٌ اَلِيْمٌۙ فِى الدُّنْيَا وَالْاٰخِرَةِۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ', 'inna <strong>al</strong>la<u>dz</u>iina yu<u>h</u>ibbuuna an tasyii\'a <strong>a</strong>lf<u>aah</u>isyatu fii <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu lahum \'a<u>dzaa</u>bun <strong>a</strong>liimun fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wa<strong>al</strong>l<u>aa</u>hu ya\'lamu wa-antum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Sesungguhnya orang-orang yang ingin agar perbuatan yang sangat keji itu (berita bohong) tersiar di kalangan orang-orang yang beriman, mereka mendapat azab yang pedih di dunia dan di akhirat. Dan Allah mengetahui, sedang kamu tidak mengetahui.'),
	(2811, 24, 20, 'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ رَءُوْفٌ رَّحِيْمٌ ࣖ ', 'walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu wa-anna <strong>al</strong>l<u>aa</u>ha rauufun ra<u>h</u>iim<strong>un</strong>', 'Dan kalau bukan karena karunia Allah dan rahmat-Nya kepadamu (niscaya kamu akan ditimpa azab yang besar). Sungguh, Allah Maha Penyantun, Ma-ha Penyayang. '),
	(2812, 24, 21, '۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ وَمَنْ يَّتَّبِعْ خُطُوٰتِ الشَّيْطٰنِ فَاِنَّهٗ يَأْمُرُ بِالْفَحْشَاۤءِ وَالْمُنْكَرِۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ مَا زَكٰى مِنْكُمْ مِّنْ اَحَدٍ اَبَدًاۙ وّ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti <strong>al</strong>sysyay<u>th</u><u>aa</u>ni waman yattabi\' khu<u>th</u>uw<u>aa</u>ti <strong>al</strong>sysyay<u>th</u><u>aa</u>ni fa-innahu ya/muru bi<strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i wa<strong>a</strong>lmunkari walawl<u>aa</u> fa<u>dh</u>lu <strong>al</strong>l<u>aa</u>hi \'alaykum wara<u>h</u>matuhu m<u>aa</u> zak<u>aa</u> minkum min a<u>h</u>adin abadan wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yuzakkii man yasy<u>aa</u>u wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu mengikuti langkah-langkah setan. Barangsiapa mengikuti langkah-langkah setan, maka sesungguhnya dia (setan) menyuruh mengerjakan perbuatan yang keji dan mungkar. Kalau bukan karena karunia Allah dan rahmat-Nya kepadamu, niscaya tidak seorang pun di antara kamu bersih (dari perbuatan keji dan mungkar itu) selama-lamanya, tetapi Allah membersihkan siapa yang Dia kehendaki. Dan Allah Maha Mendengar, Maha Mengetahui.'),
	(2813, 24, 22, 'وَلَا يَأْتَلِ اُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُّؤْتُوْٓا اُولِى الْقُرْبٰى وَالْمَسٰكِيْنَ وَالْمُهٰجِرِيْنَ فِيْ سَبِيْلِ اللّٰهِ ۖوَلْيَعْفُوْا وَلْيَصْفَحُوْاۗ  اَلَا تُحِبُّوْنَ اَنْ يَّغْفِرَ اللّٰهُ لَكُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ', 'wal<u>aa</u> ya/tali uluu <strong>a</strong>lfa<u>dh</u>li minkum wa<strong>al</strong>ssa\'ati an yu/tuu ulii <strong>a</strong>lqurb<u>aa</u> wa<strong>a</strong>lmas<u>aa</u>kiina wa<strong>a</strong>lmuh<u>aa</u>jiriina fii sabiili <strong>al</strong>l<u>aa</u>hi walya\'fuu walya<u>sh</u>fa<u>h</u>uu <strong>a</strong>l<u>aa</u> tu<u>h</u>ibbuuna an yaghfira <strong>al</strong>l<u>aa</u>hu lakum wa<strong>al</strong>l<u>aa</u>hu ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan janganlah orang-orang yang mempunyai kelebihan dan kelapangan di antara kamu bersumpah bahwa mereka (tidak) akan memberi (bantuan) kepada kerabat(nya), orang-orang miskin dan orang-orang yang berhijrah di jalan Allah, dan hendaklah mereka memaafkan dan berlapang dada. Apakah kamu tidak suka bahwa Allah mengampunimu? Dan Allah Maha Pengampun, Maha Penyayang.'),
	(2814, 24, 23, 'اِنَّ الَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ الْغٰفِلٰتِ الْمُؤْمِنٰتِ لُعِنُوْا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظِيْمٌ ۙ', 'inna <strong>al</strong>la<u>dz</u>iina yarmuuna <strong>a</strong>lmu<u>hs</u>an<u>aa</u>ti <strong>a</strong>lgh<u>aa</u>fil<u>aa</u>ti <strong>a</strong>lmu/min<u>aa</u>ti lu\'inuu fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim<strong>un</strong>', 'Sungguh, orang-orang yang menuduh perempuan-perempuan baik, yang lengah dan beriman (dengan tuduhan berzina), mereka dilaknat di dunia dan di akhirat, dan mereka akan mendapat azab yang besar,'),
	(2815, 24, 24, 'يَّوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْدِيْهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ', 'yawma tasyhadu \'alayhim <strong>a</strong>lsinatuhum wa-aydiihim wa-arjuluhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'pada hari, (ketika) lidah, tangan dan kaki mereka menjadi saksi atas mereka terhadap apa yang dahulu mereka kerjakan.'),
	(2816, 24, 25, 'يَوْمَىِٕذٍ يُّوَفِّيْهِمُ اللّٰهُ دِيْنَهُمُ الْحَقَّ وَيَعْلَمُوْنَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُبِيْنُ', 'yawma-i<u>dz</u>in yuwaffiihimu <strong>al</strong>l<u>aa</u>hu diinahumu <strong>a</strong>l<u>h</u>aqqa waya\'lamuuna anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l<u>h</u>aqqu <strong>a</strong>lmubiin<strong>u</strong>', 'Pada hari itu Allah menyempurnakan balasan yang sebenarnya bagi mereka, dan mereka tahu bahwa Allah Maha-benar, Maha Menjelaskan.'),
	(2817, 24, 26, 'اَلْخَبِيْثٰتُ لِلْخَبِيْثِيْنَ وَالْخَبِيْثُوْنَ لِلْخَبِيْثٰتِۚ وَالطَّيِّبٰتُ لِلطَّيِّبِيْنَ وَالطَّيِّبُوْنَ لِلطَّيِّبٰتِۚ اُولٰۤىِٕكَ مُبَرَّءُوْنَ مِمَّا يَقُوْلُوْنَۗ  لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ࣖ', 'alkhabiits<u>aa</u>tu lilkhabiitsiina wa<strong>a</strong>lkhabiitsuuna lilkhabiits<u>aa</u>ti wa<strong>al</strong><u>ththh</u>ayyib<u>aa</u>tu li<strong>l</strong><u>ththh</u>ayyibiina wa<strong>al</strong><u>ththh</u>ayyibuuna li<strong>l</strong><u>ththh</u>ayyib<u>aa</u>ti ul<u>aa</u>-ika mubarrauuna mimm<u>aa</u> yaquuluuna lahum maghfiratun warizqun kariim<strong>un</strong>', 'Perempuan-perempuan yang keji untuk laki-laki yang keji, dan laki-laki yang keji untuk perempuan-perempuan yang keji (pula), sedangkan perempuan-perempuan yang baik untuk laki-laki yang baik dan laki-laki yang baik untuk perempuan-perempuan yang baik (pula). Mereka itu bersih dari apa yang dituduhkan orang. Mereka memperoleh ampunan dan rezeki yang mulia (surga).'),
	(2818, 24, 27, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتًا غَيْرَ بُيُوْتِكُمْ حَتّٰى تَسْتَأْنِسُوْا وَتُسَلِّمُوْا عَلٰٓى اَهْلِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tadkhuluu buyuutan ghayra buyuutikum <u>h</u>att<u>aa</u> tasta/nisuu watusallimuu \'al<u>aa</u> ahlih<u>aa</u> <u>dzaa</u>likum khayrun lakum la\'allakum ta<u>dz</u>akkaruun<strong>a</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu memasuki rumah yang bukan rumahmu sebelum meminta izin dan memberi salam kepada penghuninya. Yang demikian itu lebih baik bagimu, agar kamu (selalu) ingat.'),
	(2819, 24, 28, 'فَاِنْ لَّمْ تَجِدُوْا فِيْهَآ اَحَدًا فَلَا تَدْخُلُوْهَا حَتّٰى يُؤْذَنَ لَكُمْ وَاِنْ قِيْلَ لَكُمُ ارْجِعُوْا فَارْجِعُوْا هُوَ اَزْكٰى لَكُمْ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ', 'fa-in lam tajiduu fiih<u>aa</u> a<u>h</u>adan fal<u>aa</u> tadkhuluuh<u>aa</u> <u>h</u>att<u>aa</u> yu/<u>dz</u>ana lakum wa-in qiila lakumu irji\'uu fa<strong>i</strong>rji\'uu huwa azk<u>aa</u> lakum wa<strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna \'aliim<strong>un</strong>', 'Dan jika kamu tidak menemui seorang pun di dalamnya, maka janganlah kamu masuk sebelum kamu mendapat izin. Dan jika dikatakan kepadamu, “Kembalilah!” Maka (hendaklah) kamu kembali. Itu lebih suci bagimu, dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
	(2820, 24, 29, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوْا بُيُوْتًا غَيْرَ مَسْكُوْنَةٍ فِيْهَا مَتَاعٌ لَّكُمْۗ وَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ', 'laysa \'alaykum jun<u>aah</u>un an tadkhuluu buyuutan ghayra maskuunatin fiih<u>aa</u> mat<u>aa</u>\'un lakum wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> tubduuna wam<u>aa</u> taktumuun<strong>a</strong>', 'Tidak ada dosa atasmu memasuki rumah yang tidak dihuni, yang di dalamnya ada kepentingan kamu; Allah mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan.'),
	(2821, 24, 30, 'قُلْ لِّلْمُؤْمِنِيْنَ يَغُضُّوْا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوْا فُرُوْجَهُمْۗ ذٰلِكَ اَزْكٰى لَهُمْۗ اِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا يَصْنَعُوْنَ', 'qul lilmu/miniina yaghu<u>dhdh</u>uu min ab<u>shaa</u>rihum waya<u>h</u>fa<u>zh</u>uu furuujahum <u>dzaa</u>lika azk<u>aa</u> lahum inna <strong>al</strong>l<u>aa</u>ha khabiirun bim<u>aa</u> ya<u>sh</u>na\'uun<strong>a</strong>', 'Katakanlah kepada laki-laki yang beriman, agar mereka menjaga pandangannya, dan memelihara kemaluannya; yang demikian itu, lebih suci bagi mereka. Sungguh, Allah Maha Mengetahui apa yang mereka perbuat.'),
	(2822, 24, 31, 'وَقُلْ لِّلْمُؤْمِنٰتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوْجَهُنَّ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوْبِهِنَّۖ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا لِبُعُوْلَتِهِنَّ اَوْ اٰبَاۤ', 'waqul lilmu/min<u>aa</u>ti yagh<u>dh</u>u<u>dh</u>na min ab<u>shaa</u>rihinna waya<u>h</u>fa<u>zh</u>na furuujahunna wal<u>aa</u> yubdiina ziinatahunna ill<u>aa</u> m<u>aa</u> <u>zh</u>ahara minh<u>aa</u> walya<u>dh</u>ribna bikhumurihinna \'al<u>aa</u> juyuubihinna wal<u>aa</u> yubdiina ziinatahunna ill<u>aa</u> libu\'uulatihinna aw <u>aa</u>b<u>aa</u>-ihinna aw <u>aa</u>b<u>aa</u>-i bu\'uulatihinna aw abn<u>aa</u>-ihinna aw abn<u>aa</u>-i bu\'uulatihinna aw ikhw<u>aa</u>nihinna aw banii ikhw<u>aa</u>nihinna aw banii akhaw<u>aa</u>tihinna aw nis<u>aa</u>-ihinna aw m<u>aa</u> malakat aym<u>aa</u>nuhunna awi <strong>al</strong>tt<u>aa</u>bi\'iina ghayri ulii <strong>a</strong>l-irbati mina <strong>al</strong>rrij<u>aa</u>li awi <strong>al</strong><u>ththh</u>ifli <strong>al</strong>la<u>dz</u>iina lam ya<u>zh</u>haruu \'al<u>aa</u> \'awr<u>aa</u>ti <strong>al</strong>nnis<u>aa</u>-i wal<u>aa</u> ya<u>dh</u>ribna bi-arjulihinna liyu\'lama m<u>aa</u> yukhfiina min ziinatihinna watuubuu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi jamii\'an ayyuh<u>aa</u> <strong>a</strong>lmu/minuuna la\'allakum tufli<u>h</u>uun<strong>a</strong>', 'Dan katakanlah kepada para perempuan yang beriman, agar mereka menjaga pandangannya, dan memelihara kemaluannya, dan janganlah menampakkan perhiasannya (auratnya), kecuali yang (biasa) terlihat. Dan hendaklah mereka menutupkan kain kerudung ke dadanya, dan janganlah menampakkan perhiasannya (auratnya), kecuali kepada suami mereka, atau ayah mereka, atau ayah suami mereka, atau putra-putra mereka, atau putra-putra suami mereka, atau saudara-saudara laki-laki mereka, atau putra-putra saudara laki-laki mereka, atau putra-putra saudara perempuan mereka, atau para perempuan (sesama Islam) mereka, atau hamba sahaya yang mereka miliki, atau para pelayan laki-laki (tua) yang tidak mempunyai keinginan (terhadap perempuan) atau anak-anak yang belum mengerti tentang aurat perempuan. Dan janganlah mereka menghentakkan kakinya agar diketahui perhiasan yang mereka sembunyikan. Dan bertobatlah kamu semua kepada Allah, wahai orang-orang yang beriman, agar kamu beruntung.'),
	(2823, 24, 32, 'وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصّٰلِحِيْنَ مِنْ عِبَادِكُمْ وَاِمَاۤىِٕكُمْۗ اِنْ يَّكُوْنُوْا فُقَرَاۤءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ', 'wa-anki<u>h</u>uu <strong>a</strong>l-ay<u>aa</u>m<u>aa</u> minkum wa<strong>al</strong><u>shshaa</u>li<u>h</u>iina min \'ib<u>aa</u>dikum wa-im<u>aa</u>-ikum in yakuunuu fuqar<u>aa</u>-a yughnihimu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi wa<strong>al</strong>l<u>aa</u>hu w<u>aa</u>si\'un \'aliim<strong>un</strong>', 'Dan nikahkanlah orang-orang yang masih membujang di antara kamu, dan juga orang-orang yang layak (menikah) dari hamba-hamba sahayamu yang laki-laki dan perempuan. Jika mereka miskin, Allah akan memberi kemampuan kepada mereka dengan karunia-Nya. Dan Allah Mahaluas (pemberian-Nya), Maha Mengetahui.'),
	(2824, 24, 33, 'وَلْيَسْتَعْفِفِ الَّذِيْنَ لَا يَجِدُوْنَ نِكَاحًا حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِهٖ ۗوَالَّذِيْنَ يَبْتَغُوْنَ الْكِتٰبَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوْهُمْ اِنْ عَلِمْتُمْ فِيْهِمْ خَيْرًا وَّاٰتُوْهُمْ مِّنْ مَّالِ اللّٰهِ الَّذِيْٓ ', 'walyasta\'fifi <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yajiduuna nik<u>aah</u>an <u>h</u>att<u>aa</u> yughniyahumu <strong>al</strong>l<u>aa</u>hu min fa<u>dh</u>lihi wa<strong>a</strong>lla<u>dz</u>iina yabtaghuuna <strong>a</strong>lkit<u>aa</u>ba mimm<u>aa</u> malakat aym<u>aa</u>nukum fak<u>aa</u>tibuuhum in \'alimtum fiihim khayran wa<u>aa</u>tuuhum min m<u>aa</u>li <strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii <u>aa</u>t<u>aa</u>kum wal<u>aa</u> tukrihuu fatay<u>aa</u>tikum \'al<u>aa</u> <strong>a</strong>lbigh<u>aa</u>-i in aradna ta<u>h</u>a<u>shsh</u>unan litabtaghuu \'ara<u>dh</u>a <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> waman yukrihhunna fa-inna <strong>al</strong>l<u>aa</u>ha min ba\'di ikr<u>aa</u>hihinna ghafuurun ra<u>h</u>iim<strong>un</strong>', 'Dan orang-orang yang tidak mampu menikah hendaklah menjaga kesucian (diri)nya, sampai Allah memberi kemampuan kepada mereka dengan karunia-Nya. Dan jika hamba sahaya yang kamu miliki menginginkan perjanjian (kebebasan), hendaklah kamu buat perjanjian kepada mereka, jika kamu mengetahui ada kebaikan pada mereka, dan berikanlah kepada mereka sebagian dari harta Allah yang dikaruniakan-Nya kepadamu. Dan janganlah kamu paksa hamba sahaya perempuanmu untuk melakukan pelacuran, sedang mereka sendiri menginginkan kesucian, karena kamu hendak mencari keuntungan kehidupan duniawi. Barangsiapa memaksa mereka, maka sungguh, Allah Maha Pengampun, Maha Penyayang (kepada mereka) setelah mereka dipaksa.'),
	(2825, 24, 34, 'وَلَقَدْ اَنْزَلْنَآ اِلَيْكُمْ اٰيٰتٍ مُّبَيِّنٰتٍ وَّمَثَلًا مِّنَ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِّلْمُتَّقِيْنَ ࣖ', 'walaqad anzaln<u>aa</u> ilaykum <u>aa</u>y<u>aa</u>tin mubayyin<u>aa</u>tin wamatsalan mina <strong>al</strong>la<u>dz</u>iina khalaw min qablikum wamaw\'i<u>zh</u>atan lilmuttaqiin<strong>a</strong>', 'Dan sungguh, Kami telah menurunkan kepada kamu ayat-ayat yang memberi penjelasan, dan contoh-contoh dari orang-orang yang terdahulu sebelum kamu dan sebagai pelajaran bagi orang-orang yang bertakwa.'),
	(2826, 24, 35, '۞ اَللّٰهُ نُوْرُ السَّمٰوٰتِ وَالْاَرْضِۗ مَثَلُ نُوْرِهٖ كَمِشْكٰوةٍ فِيْهَا مِصْبَاحٌۗ  اَلْمِصْبَاحُ فِيْ زُجَاجَةٍۗ  اَلزُّجَاجَةُ كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُّوْقَدُ مِنْ شَجَرَةٍ مُّبٰرَكَةٍ زَيْتُوْنَةٍ لَّا شَرْقِيَّةٍ وَّلَا غَرْبِيَّةٍۙ يَّك', '<strong>al</strong>l<u>aa</u>hu nuuru <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i matsalu nuurihi kamisyk<u>aa</u>tin fiih<u>aa</u> mi<u>sh</u>b<u>aah</u>un <strong>a</strong>lmi<u>sh</u>b<u>aah</u>u fii zuj<u>aa</u>jatin <strong>al</strong>zzuj<u>aa</u>jatu ka-annah<u>aa</u> kawkabun durriyyun yuuqadu min syajaratin mub<u>aa</u>rakatin zaytuunatin l<u>aa</u> syarqiyyatin wal<u>aa</u> gharbiyyatin yak<u>aa</u>du zaytuh<u>aa</u> yu<u>dh</u>ii-u walaw lam tamsas-hu n<u>aa</u>run nuurun \'al<u>aa</u> nuurin yahdii <strong>al</strong>l<u>aa</u>hu linuurihi man yasy<u>aa</u>u waya<u>dh</u>ribu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l-amts<u>aa</u>la li<strong>l</strong>nn<u>aa</u>si wa<strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<strong>un</strong>', 'Allah (pemberi) cahaya (kepada) langit dan bumi. Perumpamaan cahaya-Nya, seperti sebuah lubang yang tidak tembus, yang di dalamnya ada pelita besar. Pelita itu di dalam tabung kaca (dan) tabung kaca itu bagaikan bintang yang berkilauan, yang dinyalakan dengan minyak dari pohon yang diberkahi, (yaitu) pohon zaitun yang tumbuh tidak di timur dan tidak pula di barat, yang minyaknya (saja) hampir-hampir menerangi, walaupun tidak disentuh api. Cahaya di atas cahaya (berlapis-lapis), Allah memberi petunjuk kepada cahaya-Nya bagi orang yang Dia kehendaki, dan Allah membuat perumpamaan-perumpamaan bagi manusia. Dan Allah Maha Mengetahui segala sesuatu.'),
	(2827, 24, 36, 'فِيْ بُيُوْتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ فِيْهَا اسْمُهٗۙ يُسَبِّحُ لَهٗ فِيْهَا بِالْغُدُوِّ وَالْاٰصَالِ ۙ', 'fii buyuutin a<u>dz</u>ina <strong>al</strong>l<u>aa</u>hu an turfa\'a wayu<u>dz</u>kara fiih<u>aa</u> ismuhu yusabbi<u>h</u>u lahu fiih<u>aa</u> bi<strong>a</strong>lghuduwwi wa<strong>a</strong>l-<u>aasaa</u>l<strong>i</strong>', '(Cahaya itu) di rumah-rumah yang di sana telah diperintahkan Allah untuk memuliakan dan menyebut nama-Nya, di sana bertasbih (menyucikan)  nama-Nya pada waktu pagi dan petang,'),
	(2828, 24, 37, 'رِجَالٌ لَّا تُلْهِيْهِمْ تِجَارَةٌ وَّلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَاِيْتَاۤءِ الزَّكٰوةِ ۙيَخَافُوْنَ يَوْمًا تَتَقَلَّبُ فِيْهِ الْقُلُوْبُ وَالْاَبْصَارُ ۙ', 'rij<u>aa</u>lun l<u>aa</u> tulhiihim tij<u>aa</u>ratun wal<u>aa</u> bay\'un \'an <u>dz</u>ikri <strong>al</strong>l<u>aa</u>hi wa-iq<u>aa</u>mi <strong>al</strong><u>shsh</u>al<u>aa</u>ti wa-iit<u>aa</u>-i <strong>al</strong>zzak<u>aa</u>ti yakh<u>aa</u>fuuna yawman tataqallabu fiihi <strong>a</strong>lquluubu wa<strong>a</strong>l-ab<u>shaa</u>r<strong>u</strong>', 'orang yang tidak dilalaikan oleh perdagangan dan jual beli dari mengingat Allah, melaksanakan salat, dan menunaikan zakat. Mereka takut kepada hari ketika hati dan penglihatan menjadi guncang (hari Kiamat),'),
	(2829, 24, 38, 'لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوْا وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ', 'liyajziyahumu <strong>al</strong>l<u>aa</u>hu a<u>h</u>sana m<u>aa</u> \'amiluu wayaziidahum min fa<u>dh</u>lihi wa<strong>al</strong>l<u>aa</u>hu yarzuqu man yasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b<strong>in</strong>', '(mereka melakukan itu) agar Allah memberi balasan kepada mereka dengan yang lebih baik daripada apa yang telah mereka kerjakan, dan agar Dia menambah karunia-Nya kepada mereka. Dan Allah memberi rezeki kepada siapa saja yang Dia kehendaki tanpa batas.'),
	(2830, 24, 39, 'وَالَّذِيْنَ كَفَرُوْٓا اَعْمَالُهُمْ كَسَرَابٍۢ بِقِيْعَةٍ يَّحْسَبُهُ الظَّمْاٰنُ مَاۤءًۗ حَتّٰٓى اِذَا جَاۤءَهٗ لَمْ يَجِدْهُ شَيْـًٔا وَّوَجَدَ اللّٰهَ عِنْدَهٗ فَوَفّٰىهُ حِسَابَهٗ ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu a\'m<u>aa</u>luhum kasar<u>aa</u>bin biqii\'atin ya<u>h</u>sabuhu <strong>al</strong><u>zhzh</u>am-<u>aa</u>nu m<u>aa</u>-an <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-ahu lam yajidhu syay-an wawajada <strong>al</strong>l<u>aa</u>ha \'indahu fawaff<u>aa</u>hu <u>h</u>is<u>aa</u>bahu wa<strong>al</strong>l<u>aa</u>hu sarii\'u <strong>a</strong>l<u>h</u>is<u>aa</u>b<strong>i</strong>', 'Dan orang-orang yang kafir, amal perbuatan mereka seperti fatamorgana di tanah yang datar, yang disangka air oleh orang-orang yang dahaga, tetapi apabila (air) itu didatangi tidak ada apa pun. Dan didapatinya (ketetapan) Allah baginya. Lalu Allah memberikan kepadanya perhitungan (amal-amal) dengan sempurna dan Allah sangat cepat perhitungan-Nya.'),
	(2831, 24, 40, 'اَوْ كَظُلُمٰتٍ فِيْ بَحْرٍ لُّجِّيٍّ يَّغْشٰىهُ مَوْجٌ مِّنْ فَوْقِهٖ مَوْجٌ مِّنْ فَوْقِهٖ سَحَابٌۗ ظُلُمٰتٌۢ بَعْضُهَا فَوْقَ بَعْضٍۗ اِذَآ اَخْرَجَ يَدَهٗ لَمْ يَكَدْ يَرٰىهَاۗ وَمَنْ لَّمْ يَجْعَلِ اللّٰهُ لَهٗ نُوْرًا فَمَا لَهٗ مِنْ نُّوْرٍ ࣖ', 'aw ka<u>zh</u>ulum<u>aa</u>tin fii ba<u>h</u>rin lujjiyyin yaghsy<u>aa</u>hu mawjun min fawqihi mawjun min fawqihi sa<u>haa</u>bun <u>zh</u>ulum<u>aa</u>tun ba\'<u>dh</u>uh<u>aa</u> fawqa ba\'<u>dh</u>in i<u>dzaa</u> akhraja yadahu lam yakad yar<u>aa</u>h<u>aa</u> waman lam yaj\'ali <strong>al</strong>l<u>aa</u>hu lahu nuuran fam<u>aa</u> lahu min nuur<strong>in</strong>', 'Atau (keadaan orang-orang kafir) seperti gelap gulita di lautan yang dalam, yang diliputi oleh gelombang demi gelombang, di atasnya ada (lagi) awan gelap. Itulah gelap gulita yang berlapis-lapis. Apabila dia mengeluarkan tangannya hampir tidak dapat melihatnya. Barangsiapa tidak diberi cahaya (petunjuk) oleh Allah, maka dia tidak mempunyai cahaya sedikit pun. '),
	(2832, 24, 41, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّحُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ وَالطَّيْرُ صٰۤفّٰتٍۗ كُلٌّ قَدْ عَلِمَ صَلَاتَهٗ وَتَسْبِيْحَهٗۗ وَاللّٰهُ عَلِيْمٌۢ بِمَا يَفْعَلُوْنَ ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha yusabbi<u>h</u>u lahu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>al</strong><u>ththh</u>ayru <u>shaa</u>ff<u>aa</u>tin kullun qad \'alima <u>sh</u>al<u>aa</u>tahu watasbii<u>h</u>ahu wa<strong>al</strong>l<u>aa</u>hu \'aliimun bim<u>aa</u> yaf\'aluun<strong>a</strong>', 'Tidakkah engkau (Muhammad) tahu bahwa kepada Allah-lah bertasbih apa yang di langit dan di bumi, dan juga burung yang mengembangkan sayapnya. Masing-masing sungguh, telah mengetahui (cara) berdoa dan bertasbih. Allah Maha Mengetahui apa yang mereka kerjakan.'),
	(2833, 24, 42, 'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَصِيْرُ', 'walill<u>aa</u>hi mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan milik Allah-lah kerajaan langit dan bumi dan hanya kepada Allah-lah kembali (seluruh makhluk).'),
	(2834, 24, 43, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْجِيْ سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهٗ ثُمَّ يَجْعَلُهٗ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ وَيُنَزِّلُ مِنَ السَّمَاۤءِ مِنْ جِبَالٍ فِيْهَا مِنْۢ بَرَدٍ فَيُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ وَيَصْرِفُهٗ عَنْ مَّنْ ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha yuzjii sa<u>haa</u>ban tsumma yu-allifu baynahu tsumma yaj\'aluhu ruk<u>aa</u>man fatar<u>aa</u> <strong>a</strong>lwadqa yakhruju min khil<u>aa</u>lihi wayunazzilu mina <strong>al</strong>ssam<u>aa</u>-i min jib<u>aa</u>lin fiih<u>aa</u> min baradin fayu<u>sh</u>iibu bihi man yasy<u>aa</u>u waya<u>sh</u>rifuhu \'an man yasy<u>aa</u>u yak<u>aa</u>du san<u>aa</u> barqihi ya<u>dz</u>habu bi<strong>a</strong>l-ab<u>shaa</u>r<strong>i</strong>', 'Tidakkah engkau melihat bahwa Allah menjadikan awan bergerak perlahan, kemudian mengumpulkannya, lalu Dia menjadikannya bertumpuk-tumpuk, lalu engkau lihat hujan keluar dari celah-celahnya dan Dia (juga) menurunkan (butiran-butiran) es dari langit, (yaitu) dari (gumpalan-gumpalan awan seperti) gunung-gunung, maka ditimpakan-Nya (butiran-butiran es) itu kepada siapa yang Dia kehendaki dan dihindarkan-Nya dari siapa yang Dia kehendaki. Kilauan kilatnya hampir-hampir menghilangkan penglihatan.'),
	(2835, 24, 44, 'يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ ', 'yuqallibu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra inna fii <u>dzaa</u>lika la\'ibratan li-ulii <strong>a</strong>l-ab<u>shaa</u>r<strong>i</strong>', 'Allah mempergantikan malam dan siang. Sesungguhnya pada yang demikian itu, pasti terdapat pelajaran bagi orang-orang yang mempunyai penglihatan (yang tajam).'),
	(2836, 24, 45, 'وَاللّٰهُ خَلَقَ كُلَّ دَاۤبَّةٍ مِّنْ مَّاۤءٍۚ فَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى بَطْنِهٖۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰٓى اَرْبَعٍۗ يَخْلُقُ اللّٰهُ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'wa<strong>al</strong>l<u>aa</u>hu khalaqa kulla d<u>aa</u>bbatin min m<u>aa</u>-in faminhum man yamsyii \'al<u>aa</u> ba<u>th</u>nihi waminhum man yamsyii \'al<u>aa</u> rijlayni waminhum man yamsyii \'al<u>aa</u> arba\'in yakhluqu <strong>al</strong>l<u>aa</u>hu m<u>aa</u> yasy<u>aa</u>u inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Dan Allah menciptakan semua jenis hewan dari air, maka sebagian ada yang berjalan di atas perutnya dan sebagian berjalan dengan dua kaki, sedang sebagian (yang lain) berjalan dengan empat kaki. Allah menciptakan apa yang Dia kehendaki. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(2837, 24, 46, 'لَقَدْ اَنْزَلْنَآ اٰيٰتٍ مُّبَيِّنٰتٍۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ', 'laqad anzaln<u>aa</u> <u>aa</u>y<u>aa</u>tin mubayyin<u>aa</u>tin wa<strong>al</strong>l<u>aa</u>hu yahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim<strong>in</strong>', 'Sungguh, Kami telah menurunkan ayat-ayat yang memberi penjelasan. Dan Allah memberi petunjuk siapa yang Dia kehendaki ke jalan yang lurus.'),
	(2838, 24, 47, 'وَيَقُوْلُوْنَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُوْلِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ مِّنْۢ بَعْدِ ذٰلِكَۗ وَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ', 'wayaquuluuna <u>aa</u>mann<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wabi<strong>al</strong>rrasuuli wa-a<u>th</u>a\'n<u>aa</u> tsumma yatawall<u>aa</u> fariiqun minhum min ba\'di <u>dzaa</u>lika wam<u>aa</u> ul<u>aa</u>-ika bi<strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan mereka (orang-orang munafik) berkata, “Kami telah beriman kepada Allah dan Rasul (Muhammad), dan kami menaati (keduanya).”  Kemudian sebagian dari mereka berpaling setelah itu. Mereka itu bukanlah orang-orang beriman.'),
	(2839, 24, 48, 'وَاِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَرِيْقٌ مِّنْهُمْ مُّعْرِضُوْنَ ', 'wa-i<u>dzaa</u> du\'uu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi warasuulihi liya<u>h</u>kuma baynahum i<u>dzaa</u> fariiqun minhum mu\'ri<u>dh</u>uun<strong>a</strong>', 'Dan apabila mereka diajak kepada Allah dan Rasul-Nya, agar (Rasul) memutuskan perkara di antara mereka, tiba-tiba sebagian dari mereka menolak (untuk datang).'),
	(2840, 24, 49, 'وَاِنْ يَّكُنْ لَّهُمُ الْحَقُّ يَأْتُوْٓا اِلَيْهِ مُذْعِنِيْنَ', 'wa-in yakun lahumu <strong>a</strong>l<u>h</u>aqqu ya/tuu ilayhi mu<u>dz</u>\'iniin<strong>a</strong>', 'Tetapi, jika kebenaran di pihak mereka, mereka datang kepadanya (Rasul) dengan patuh.'),
	(2841, 24, 50, 'اَفِيْ قُلُوْبِهِمْ مَّرَضٌ اَمِ ارْتَابُوْٓا اَمْ يَخَافُوْنَ اَنْ يَّحِيْفَ اللّٰهُ عَلَيْهِمْ وَرَسُوْلُهٗ ۗبَلْ اُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ࣖ', 'afii quluubihim mara<u>dh</u>un ami irt<u>aa</u>buu am yakh<u>aa</u>fuuna an ya<u>h</u>iifa <strong>al</strong>l<u>aa</u>hu \'alayhim warasuuluhu bal ul<u>aa</u>-ika humu <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Apakah (ketidakhadiran mereka karena) dalam hati mereka ada penyakit, atau (karena) mereka ragu-ragu ataukah (karena) takut kalau-kalau Allah dan Rasul-Nya berlaku zalim kepada mereka? Sebenarnya, mereka itulah orang-orang yang zalim.'),
	(2842, 24, 51, 'اِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِيْنَ اِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَّقُوْلُوْا سَمِعْنَا وَاَطَعْنَاۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ', 'innam<u>aa</u> k<u>aa</u>na qawla <strong>a</strong>lmu/miniina i<u>dzaa</u> du\'uu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi warasuulihi liya<u>h</u>kuma baynahum an yaquuluu sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Hanya ucapan orang-orang mukmin, yang apabila mereka diajak kepada Allah dan Rasul-Nya agar Rasul memutuskan (perkara) di antara mereka, mereka berkata, “Kami mendengar, dan kami taat.” Dan mereka itulah orang-orang yang beruntung.'),
	(2843, 24, 52, 'وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ', 'waman yu<u>th</u>i\'i <strong>al</strong>l<u>aa</u>ha warasuulahu wayakhsya <strong>al</strong>l<u>aa</u>ha wayattaqhi faul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>-izuun<strong>a</strong>', 'Dan barangsiapa taat kepada Allah dan Rasul-Nya serta takut kepada Allah dan bertakwa kepada-Nya, mereka itulah orang-orang yang mendapat kemenangan.'),
	(2844, 24, 53, '۞ وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۗ قُلْ لَّا تُقْسِمُوْاۚ طَاعَةٌ مَّعْرُوْفَةٌ  ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ', 'wa-aqsamuu bi<strong>al</strong>l<u>aa</u>hi jahda aym<u>aa</u>nihim la-in amartahum layakhrujunna qul l<u>aa</u> tuqsimuu <u>th</u><u>aa</u>\'atun ma\'ruufatun inna <strong>al</strong>l<u>aa</u>ha khabiirun bim<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan mereka bersumpah dengan (nama) Allah dengan sumpah sungguh-sungguh, bahwa jika engkau suruh mereka berperang, pastilah mereka akan pergi. Katakanlah (Muhammad), “Janganlah kamu bersumpah, (karena yang diminta) adalah ketaatan yang baik. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan.”'),
	(2845, 24, 54, 'قُلْ اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَّا حُمِّلْتُمْۗ وَاِنْ تُطِيْعُوْهُ تَهْتَدُوْاۗ وَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ', 'qul a<u>th</u>ii\'uu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uu <strong>al</strong>rrasuula fa-in tawallaw fa-innam<u>aa</u> \'alayhi m<u>aa</u> <u>h</u>ummila wa\'alaykum m<u>aa</u> <u>h</u>ummiltum wa-in tu<u>th</u>ii\'uuhu tahtaduu wam<u>aa</u> \'al<u>aa</u> <strong>al</strong>rrasuuli ill<u>aa</u> <strong>a</strong>lbal<u>aa</u>ghu <strong>a</strong>lmubiin<strong>a</strong>', 'Katakanlah, “Taatlah kepada Allah dan taatlah kepada Rasul; jika kamu berpaling, maka sesungguhnya kewajiban Rasul (Muhammad) itu hanyalah apa yang dibebankan kepadanya, dan kewajiban kamu hanyalah apa yang dibebankan kepadamu. Jika kamu taat kepadanya, niscaya kamu mendapat petunjuk. Kewajiban Rasul hanyalah menyampaikan (amanat Allah) dengan jelas.”'),
	(2846, 24, 55, 'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَعَمِلُوا الصّٰلِحٰتِ لَيَسْتَخْلِفَنَّهُمْ فِى الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذِيْنَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ دِيْنَهُمُ الَّذِى ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِّنْۢ بَعْدِ خَوْفِهِمْ ', 'wa\'ada <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu minkum wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti layastakhlifannahum fii <strong>a</strong>l-ar<u>dh</u>i kam<u>aa</u> istakhlafa <strong>al</strong>la<u>dz</u>iina min qablihim walayumakkinanna lahum diinahumu <strong>al</strong>la<u>dz</u>ii irta<u>daa</u> lahum walayubaddilannahum min ba\'di khawfihim amnan ya\'buduunanii l<u>aa</u> yusyrikuuna bii syay-an waman kafara ba\'da <u>dzaa</u>lika faul<u>aa</u>-ika humu <strong>a</strong>lf<u>aa</u>siquun<strong>a</strong>', 'Allah telah menjanjikan kepada orang-orang di antara kamu yang beriman dan yang mengerjakan kebajikan, bahwa Dia sungguh, akan menjadikan mereka berkuasa di bumi, sebagaimana Dia telah menjadikan orang-orang sebelum mereka berkuasa, dan sungguh, Dia akan meneguhkan bagi mereka dengan agama yang telah Dia ridai. Dan Dia benar-benar mengubah (keadaan) mereka, setelah berada dalam ketakutan menjadi aman sentosa. Mereka (tetap) menyembah-Ku dengan tidak mempersekutukan-Ku dengan sesuatu apa pun. Tetapi barangsiapa (tetap) kafir setelah (janji) itu, maka mereka itulah orang-orang yang fasik.'),
	(2847, 24, 56, 'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا الرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَ', 'wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu <strong>al</strong>zzak<u>aa</u>ta wa-a<u>th</u>ii\'uu <strong>al</strong>rrasuula la\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dan laksanakanlah salat, tunaikanlah zakat, dan taatlah kepada Rasul (Muhammad), agar kamu diberi rahmat.'),
	(2848, 24, 57, 'لَا تَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا مُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَأْوٰىهُمُ النَّارُۗ وَلَبِئْسَ الْمَصِيْرُ ࣖ', 'l<u>aa</u> ta<u>h</u>sabanna <strong>al</strong>la<u>dz</u>iina kafaruu mu\'jiziina fii <strong>a</strong>l-ar<u>dh</u>i wama/w<u>aa</u>humu <strong>al</strong>nn<u>aa</u>ru walabi/sa <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Janganlah engkau mengira bahwa orang-orang yang kafir itu dapat luput dari siksaan Allah di bumi; sedang tempat kembali mereka (di akhirat) adalah neraka. Dan itulah seburuk-buruk tempat kembali.'),
	(2849, 24, 58, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِيَسْتَأْذِنْكُمُ الَّذِيْنَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذِيْنَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرّٰتٍۗ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَحِيْنَ تَضَعُوْنَ ثِيَابَكُمْ مِّنَ الظَّهِيْرَةِ وَمِنْۢ بَعْدِ صَلٰوةِ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu liyasta/<u>dz</u>inkumu <strong>al</strong>la<u>dz</u>iina malakat aym<u>aa</u>nukum wa<strong>a</strong>lla<u>dz</u>iina lam yablughuu <strong>a</strong>l<u>h</u>uluma minkum tsal<u>aa</u>tsa marr<u>aa</u>tin min qabli <u>sh</u>al<u>aa</u>ti <strong>a</strong>lfajri wa<u>h</u>iina ta<u>dh</u>a\'uuna tsiy<u>aa</u>bakum mina <strong>al</strong><u>zhzh</u>ahiirati wamin ba\'di <u>sh</u>al<u>aa</u>ti <strong>a</strong>l\'isy<u>aa</u>-i tsal<u>aa</u>tsu \'awr<u>aa</u>tin lakum laysa \'alaykum wal<u>aa</u> \'alayhim jun<u>aah</u>un ba\'dahunna <u>th</u>aww<u>aa</u>fuuna \'alaykum ba\'<u>dh</u>ukum \'al<u>aa</u> ba\'<u>dh</u>in ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Wahai orang-orang yang beriman! Hendaklah hamba sahaya (laki-laki dan perempuan) yang kamu miliki, dan orang-orang yang belum balig (dewasa) di antara kamu, meminta izin kepada kamu pada tiga kali (kesempatan) yaitu, sebelum salat Subuh, ketika kamu menanggalkan pakaian (luar)mu di tengah hari, dan setelah salat Isya. (Itulah) tiga aurat (waktu) bagi kamu. Tidak ada dosa bagimu dan tidak (pula) bagi mereka selain dari (tiga waktu) itu; mereka keluar masuk melayani kamu, sebagian kamu atas sebagian yang lain. Demikianlah Allah menjelaskan ayat-ayat itu kepadamu. Dan Allah Maha Mengetahui, Mahabijaksana.'),
	(2850, 24, 59, 'وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوْا كَمَا اسْتَأْذَنَ الَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ', 'wa-i<u>dzaa</u> balagha <strong>a</strong>l-a<u>th</u>f<u>aa</u>lu minkumu <strong>a</strong>l<u>h</u>uluma falyasta/<u>dz</u>inuu kam<u>aa</u> ista/<u>dz</u>ana <strong>al</strong>la<u>dz</u>iina min qablihim ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi wa<strong>al</strong>l<u>aa</u>hu \'aliimun <u>h</u>akiim<strong>un</strong>', 'Dan apabila anak-anakmu telah sampai umur dewasa, maka hendaklah mereka (juga) meminta izin, seperti orang-orang yang lebih dewasa meminta izin. Demikianlah Allah menjelaskan ayat-ayat-Nya kepadamu. Allah Maha Mengetahui, Mahabijaksana.'),
	(2851, 24, 60, 'وَالْقَوَاعِدُ مِنَ النِّسَاۤءِ الّٰتِيْ لَا يَرْجُوْنَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَّضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجٰتٍۢ بِزِيْنَةٍۗ وَاَنْ يَّسْتَعْفِفْنَ خَيْرٌ لَّهُنَّۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ', 'wa<strong>a</strong>lqaw<u>aa</u>\'idu mina <strong>al</strong>nnis<u>aa</u>-i <strong>al</strong>l<u>aa</u>tii l<u>aa</u> yarjuuna nik<u>aah</u>an falaysa \'alayhinna jun<u>aah</u>un an ya<u>dh</u>a\'na tsiy<u>aa</u>bahunna ghayra mutabarrij<u>aa</u>tin biziinatin wa-an yasta\'fifna khayrun lahunna wa<strong>al</strong>l<u>aa</u>hu samii\'un \'aliim<strong>un</strong>', 'Dan para perempuan tua yang telah berhenti (dari haid dan mengandung) yang tidak ingin menikah (lagi), maka tidak ada dosa menanggalkan pakaian (luar) mereka dengan tidak (bermaksud) menampakkan perhiasan; tetapi memelihara kehormatan adalah lebih baik bagi mereka. Allah Maha Mendengar, Maha Mengetahui.'),
	(2852, 24, 61, 'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ وَّلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوْا مِنْۢ بُيُوْتِكُمْ اَوْ بُيُوْتِ اٰبَاۤىِٕكُمْ اَوْ بُيُوْتِ اُمَّهٰتِكُمْ اَوْ بُيُوْتِ اِخْوَانِكُمْ اَوْ بُيُوْتِ', 'laysa \'al<u>aa</u> <strong>a</strong>l-a\'m<u>aa</u> <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> <strong>a</strong>l-a\'raji <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> <strong>a</strong>lmarii<u>dh</u>i <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> anfusikum an ta/kuluu min buyuutikum aw buyuuti <u>aa</u>b<u>aa</u>-ikum aw buyuuti ummah<u>aa</u>tikum aw buyuuti ikhw<u>aa</u>nikum aw buyuuti akhaw<u>aa</u>tikum aw buyuuti a\'m<u>aa</u>mikum aw buyuuti \'amm<u>aa</u>tikum aw buyuuti akhw<u>aa</u>likum aw buyuuti kh<u>aa</u>l<u>aa</u>tikum aw m<u>aa</u> malaktum maf<u>aa</u>ti<u>h</u>ahu aw <u>sh</u>adiiqikum laysa \'alaykum jun<u>aah</u>un an ta/kuluu jamii\'an aw asyt<u>aa</u>tan fa-i<u>dzaa</u> dakhaltum buyuutan fasallimuu \'al<u>aa</u> anfusikum ta<u>h</u>iyyatan min \'indi <strong>al</strong>l<u>aa</u>hi mub<u>aa</u>rakatan <u>th</u>ayyibatan ka<u>dzaa</u>lika yubayyinu <strong>al</strong>l<u>aa</u>hu lakumu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti la\'allakum ta\'qiluun<strong>a</strong>', 'Tidak ada halangan bagi orang buta, tidak (pula) bagi orang pincang, tidak (pula) bagi orang sakit, dan tidak (pula) bagi dirimu, makan (bersama-sama mereka) di rumah kamu atau di rumah bapak-bapakmu, di rumah ibu-ibumu, di rumah saudara-saudaramu yang laki-laki, di rumah saudara-saudaramu yang perempuan, di rumah saudara-saudara bapakmu yang laki-laki, di rumah saudara-saudara bapakmu yang perempuan, di rumah saudara-saudara ibumu yang laki-laki, di rumah saudara-saudara ibumu yang perempuan, (di rumah) yang kamu miliki kuncinya atau (di rumah) kawan-kawanmu. Tidak ada halangan bagi kamu makan bersama-sama mereka atau sendiri-sendiri. Apabila kamu memasuki rumah-rumah hendaklah kamu memberi salam (kepada penghuninya, yang berarti memberi salam) kepada dirimu sendiri, dengan salam yang penuh berkah dan baik dari sisi Allah. Demikianlah Allah menjelaskan ayat-ayat(-Nya) bagimu, agar kamu mengerti. '),
	(2853, 24, 62, 'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاِذَا كَانُوْا مَعَهٗ عَلٰٓى اَمْرٍ جَامِعٍ لَّمْ يَذْهَبُوْا حَتّٰى يَسْتَأْذِنُوْهُۗ اِنَّ الَّذِيْنَ يَسْتَأْذِنُوْنَكَ اُولٰۤىِٕكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖۚ ف', 'innam<u>aa</u> <strong>a</strong>lmu/minuuna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu bi<strong>al</strong>l<u>aa</u>hi warasuulihi wa-i<u>dzaa</u> k<u>aa</u>nuu ma\'ahu \'al<u>aa</u> amrin j<u>aa</u>mi\'in lam ya<u>dz</u>habuu <u>h</u>att<u>aa</u> yasta/<u>dz</u>inuuhu inna <strong>al</strong>la<u>dz</u>iina yasta/<u>dz</u>inuunaka ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina yu/minuuna bi<strong>al</strong>l<u>aa</u>hi warasuulihi fa-i<u>dzaa</u> ista/<u>dz</u>anuuka liba\'<u>dh</u>i sya/nihim fa/<u>dz</u>an liman syi/ta minhum wa<strong>i</strong>staghfir lahumu <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha ghafuurun ra<u>h</u>iim<strong>un</strong>', '(Yang disebut) orang mukmin hanyalah orang yang beriman kepada Allah dan Rasul-Nya (Muhammad), dan apabila mereka berada bersama-sama dengan dia (Muhammad) dalam suatu urusan bersama, mereka tidak meninggalkan (Rasulullah) sebelum meminta izin kepadanya. Sesungguhnya orang-orang yang meminta izin kepadamu (Muhammad), mereka itulah orang-orang yang (benar-benar) beriman kepada Allah dan Rasul-Nya. Maka apabila mereka meminta izin kepadamu karena suatu keperluan, berilah izin kepada siapa yang engkau kehendaki di antara mereka, dan mohonkanlah ampunan untuk mereka kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(2854, 24, 63, 'لَا تَجْعَلُوْا دُعَاۤءَ الرَّسُوْلِ بَيْنَكُمْ كَدُعَاۤءِ بَعْضِكُمْ بَعْضًاۗ قَدْ يَعْلَمُ اللّٰهُ الَّذِيْنَ يَتَسَلَّلُوْنَ مِنْكُمْ لِوَاذًاۚ فَلْيَحْذَرِ الَّذِيْنَ يُخَالِفُوْنَ عَنْ اَمْرِهٖٓ اَنْ تُصِيْبَهُمْ فِتْنَةٌ اَوْ يُصِيْبَهُمْ عَذَابٌ اَ', 'l<u>aa</u> taj\'aluu du\'<u>aa</u>-a <strong>al</strong>rrasuuli baynakum kadu\'<u>aa</u>-i ba\'<u>dh</u>ikum ba\'<u>dh</u>an qad ya\'lamu <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina yatasallaluuna minkum liw<u>aadz</u>an falya<u>hts</u>ari <strong>al</strong>la<u>dz</u>iina yukh<u>aa</u>lifuuna \'an amrihi an tu<u>sh</u>iibahum fitnatun aw yu<u>sh</u>iibahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Janganlah kamu jadikan panggilan Rasul (Muhammad) di antara kamu seperti panggilan sebagian kamu kepada sebagian (yang lain). Sungguh, Allah mengetahui orang-orang yang keluar (secara) sembunyi-sembunyi di antara kamu dengan berlindung (kepada kawannya), maka hendaklah orang-orang yang menyalahi perintah Rasul-Nya takut akan mendapat cobaan atau ditimpa azab yang pedih.'),
	(2855, 24, 64, 'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قَدْ يَعْلَمُ مَآ اَنْتُمْ عَلَيْهِۗ وَيَوْمَ يُرْجَعُوْنَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ', 'al<u>aa</u> inna lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i qad ya\'lamu m<u>aa</u> antum \'alayhi wayawma yurja\'uuna ilayhi fayunabbi-uhum bim<u>aa</u> \'amiluu wa<strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<strong>un</strong>', 'Ketahuilah, sesungguhnya milik Allah-lah apa yang di langit dan di bumi. Dia mengetahui keadaan kamu sekarang. Dan (mengetahui pula) hari (ketika mereka) dikembalikan kepada-Nya, lalu diterangkan-Nya kepada mereka apa yang telah mereka kerjakan. Dan Allah Maha Mengetahui segala sesuatu.'),
	(2856, 25, 1, 'تَبٰرَكَ الَّذِيْ نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِهٖ لِيَكُوْنَ لِلْعٰلَمِيْنَ نَذِيْرًا ۙ', 'tab<u>aa</u>raka <strong>al</strong>la<u>dz</u>ii nazzala <strong>a</strong>lfurq<u>aa</u>na \'al<u>aa</u> \'abdihi liyakuuna lil\'<u>aa</u>lamiina na<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Mahasuci Allah yang telah menurunkan Furqan (Al-Qur\'an) kepada hamba-Nya (Muhammad), agar dia menjadi pemberi peringatan kepada seluruh alam (jin dan manusia).'),
	(2857, 25, 2, ' ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهٗ تَقْدِيْرًا', '<strong>al</strong>la<u>dz</u>ii lahu mulku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i walam yattakhi<u>dz</u> waladan walam yakun lahu syariikun fii <strong>a</strong>lmulki wakhalaqa kulla syay-in faqaddarahu taqdiir<u>aa</u><strong>n</strong>', 'Yang memiliki kerajaan langit dan bumi, tidak mempunyai anak, tidak ada sekutu bagi-Nya dalam kekuasaan(-Nya), dan Dia menciptakan segala sesuatu, lalu menetapkan ukuran-ukurannya dengan tepat. '),
	(2858, 25, 3, 'وَاتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً لَّا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَ وَلَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ ضَرًّا وَّلَا نَفْعًا وَّلَا يَمْلِكُوْنَ مَوْتًا وَّلَا حَيٰوةً وَّلَا نُشُوْرًا', 'wa<strong>i</strong>ttakha<u>dz</u>uu min duunihi <u>aa</u>lihatan l<u>aa</u> yakhluquuna syay-an wahum yukhlaquuna wal<u>aa</u> yamlikuuna li-anfusihim <u>dh</u>arran wal<u>aa</u> naf\'an wal<u>aa</u> yamlikuuna mawtan wal<u>aa</u> <u>h</u>ay<u>aa</u>tan wal<u>aa</u> nusyuur<u>aa</u><strong>n</strong>', 'Namun mereka mengambil tuhan-tuhan selain Dia (untuk disembah), padahal mereka (tuhan-tuhan itu) tidak menciptakan apa pun, bahkan mereka sendiri diciptakan dan tidak kuasa untuk (menolak) bahaya terhadap dirinya dan tidak dapat (mendatangkan) manfaat serta tidak kuasa mematikan, menghidupkan dan tidak (pula) membangkitkan.'),
	(2859, 25, 4, 'وَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اِفْكُ ِۨافْتَرٰىهُ وَاَعَانَهٗ عَلَيْهِ قَوْمٌ اٰخَرُوْنَۚ فَقَدْ جَاۤءُوْ ظُلْمًا وَّزُوْرًا ۚ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> ifkun iftar<u>aa</u>hu wa-a\'<u>aa</u>nahu \'alayhi qawmun <u>aa</u>kharuuna faqad j<u>aa</u>uu <u>zh</u>ulman wazuur<u>aa</u><strong>n</strong>', 'Dan orang-orang kafir berkata, “(Al-Qur\'an) ini tidak lain hanyalah kebohongan yang diada-adakan oleh dia (Muhammad), dibantu oleh orang-orang lain,” Sungguh, mereka telah berbuat zalim dan dusta yang besar.'),
	(2860, 25, 5, 'وَقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَّاَصِيْلًا', 'waq<u>aa</u>luu as<u>aath</u>iiru <strong>a</strong>l-awwaliina iktatabah<u>aa</u> fahiya tuml<u>aa</u> \'alayhi bukratan wa-a<u>sh</u>iil<u>aa</u><strong>n</strong>', 'Dan mereka berkata, “(Itu hanya) dongeng-dongeng orang-orang terdahulu, yang diminta agar dituliskan, lalu dibacakanlah dongeng itu kepadanya setiap pagi dan petang.”'),
	(2861, 25, 6, 'قُلْ اَنْزَلَهُ الَّذِيْ يَعْلَمُ السِّرَّ  فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ كَانَ غَفُوْرًا رَّحِيْمًا', 'qul anzalahu <strong>al</strong>la<u>dz</u>ii ya\'lamu <strong>al</strong>ssirra fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i innahu k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “(Al-Qur\'an) itu diturunkan oleh (Allah) yang mengetahui rahasia di langit dan di bumi. Sungguh, Dia Maha Pengampun, Maha Penyayang.” '),
	(2862, 25, 7, 'وَقَالُوْا مَالِ هٰذَا الرَّسُوْلِ يَأْكُلُ الطَّعَامَ وَيَمْشِيْ فِى الْاَسْوَاقِۗ  لَوْلَآ اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُوْنَ مَعَهٗ نَذِيْرًا ۙ', 'waq<u>aa</u>luu m<u>aa</u>li h<u>aadzaa</u> <strong>al</strong>rrasuuli ya/kulu <strong>al</strong><u>ththh</u>a\'<u>aa</u>ma wayamsyii fii <strong>a</strong>l-asw<u>aa</u>qi lawl<u>aa</u> unzila ilayhi malakun fayakuuna ma\'ahu na<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Dan mereka berkata, “Mengapa Rasul (Muhammad) ini memakan makanan dan berjalan di pasar-pasar? Mengapa malaikat tidak diturunkan kepadanya (agar malaikat) itu memberikan peringatan bersama dia,'),
	(2863, 25, 8, 'اَوْ يُلْقٰىٓ اِلَيْهِ كَنْزٌ اَوْ تَكُوْنُ لَهٗ جَنَّةٌ يَّأْكُلُ مِنْهَاۗ وَقَالَ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا', 'aw yulq<u>aa</u> ilayhi kanzun aw takuunu lahu jannatun ya/kulu minh<u>aa</u> waq<u>aa</u>la <strong>al</strong><u>zhzhaa</u>limuuna in tattabi\'uuna ill<u>aa</u> rajulan mas<u>h</u>uur<u>aa</u><strong>n</strong>', 'atau (mengapa tidak) diturunkan kepadanya harta kekayaan atau (mengapa tidak ada) kebun baginya, sehingga dia dapat makan dari (hasil)nya?” Dan orang-orang zalim itu berkata, “Kamu hanyalah mengikuti seorang laki-laki yang kena sihir.”'),
	(2864, 25, 9, 'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا ࣖ', 'un<u>zh</u>ur kayfa <u>dh</u>arabuu laka <strong>a</strong>l-amts<u>aa</u>la fa<u>dh</u>alluu fal<u>aa</u> yasta<u>th</u>ii\'uuna sabiil<u>aa</u><strong>n</strong>', 'Perhatikanlah, bagaimana mereka membuat perumpamaan-perumpamaan tentang engkau, maka sesatlah mereka, mereka tidak sanggup (mendapatkan) jalan (untuk menentang kerasulanmu). '),
	(2865, 25, 10, 'تَبٰرَكَ الَّذِيْٓ اِنْ شَاۤءَ جَعَلَ لَكَ خَيْرًا مِّنْ ذٰلِكَ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ وَيَجْعَلْ لَّكَ قُصُوْرًا', 'tab<u>aa</u>raka <strong>al</strong>la<u>dz</u>ii in sy<u>aa</u>-a ja\'ala laka khayran min <u>dzaa</u>lika jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru wayaj\'al laka qu<u>sh</u>uur<u>aa</u><strong>n</strong>', 'Mahasuci (Allah) yang jika Dia menghendaki, niscaya Dia jadikan bagimu yang lebih baik daripada itu, (yaitu) surga-surga yang mengalir di bawahnya sungai-sungai, dan Dia jadikan (pula) istana-istana untukmu.'),
	(2866, 25, 11, 'بَلْ كَذَّبُوْا بِالسَّاعَةِۙ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيْرًا', 'bal ka<u>dzdz</u>abuu bi<strong>al</strong>ss<u>aa</u>\'ati wa-a\'tadn<u>aa</u> liman ka<u>dzdz</u>aba bi<strong>al</strong>ss<u>aa</u>\'ati sa\'iir<u>aa</u><strong>n</strong>', 'Bahkan mereka mendustakan hari Kiamat. Dan Kami menyediakan neraka yang menyala-nyala bagi siapa yang mendustakan hari Kiamat.'),
	(2867, 25, 12, 'اِذَا رَاَتْهُمْ مِّنْ مَّكَانٍۢ بَعِيْدٍ سَمِعُوْا لَهَا تَغَيُّظًا وَّزَفِيْرًا', 'i<u>dzaa</u> ra-at-hum min mak<u>aa</u>nin ba\'iidin sami\'uu lah<u>aa</u> taghayyu<u>zh</u>an wazafiir<u>aa</u><strong>n</strong>', 'Apabila ia (neraka) melihat mereka dari tempat yang jauh, mereka mendengar suaranya yang gemuruh karena marahnya.'),
	(2868, 25, 13, 'وَاِذَآ اُلْقُوْا مِنْهَا مَكَانًا ضَيِّقًا مُّقَرَّنِيْنَ دَعَوْا هُنَالِكَ ثُبُوْرًا ۗ', 'wa-i<u>dzaa</u> ulquu minh<u>aa</u> mak<u>aa</u>nan <u>dh</u>ayyiqan muqarraniina da\'aw hun<u>aa</u>lika tsubuur<u>aa</u><strong>n</strong>', 'Dan apabila mereka dilemparkan ke tempat yang sempit di neraka dengan dibelenggu, mereka di sana berteriak mengharapkan kebinasaan.'),
	(2869, 25, 14, 'لَا تَدْعُوا الْيَوْمَ ثُبُوْرًا وَّاحِدًا وَّادْعُوْا ثُبُوْرًا كَثِيْرًا ', 'l<u>aa</u> tad\'uu <strong>a</strong>lyawma tsubuuran w<u>aah</u>idan wa<strong>u</strong>d\'uu tsubuuran katsiir<u>aa</u><strong>n</strong>', '(Akan dikatakan kepada mereka), “Janganlah kamu mengharapkan pada hari ini satu kebinasaan, melainkan harapkanlah kebinasaan yang berulang-ulang.” '),
	(2870, 25, 15, 'قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ  كَانَتْ لَهُمْ جَزَاۤءً وَّمَصِيْرًا ', 'qul a<u>dzaa</u>lika khayrun am jannatu <strong>a</strong>lkhuldi <strong>al</strong>latii wu\'ida <strong>a</strong>lmuttaquuna k<u>aa</u>nat lahum jaz<u>aa</u>-an wama<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Apakah (azab) seperti itu yang baik, atau surga yang kekal yang dijanjikan kepada orang-orang yang bertakwa sebagai balasan, dan tempat kembali bagi mereka?”'),
	(2871, 25, 16, 'لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ خٰلِدِيْنَۗ  كَانَ عَلٰى رَبِّكَ وَعْدًا مَّسْـُٔوْلًا', 'lahum fiih<u>aa</u> m<u>aa</u> yasy<u>aa</u>uuna kh<u>aa</u>lidiina k<u>aa</u>na \'al<u>aa</u> rabbika wa\'dan mas-uul<u>aa</u><strong>n</strong>', 'Bagi mereka segala yang mereka kehendaki ada di dalamnya (surga), mereka kekal (di dalamnya). Itulah janji Tuhanmu yang pantas dimohonkan (kepada-Nya).'),
	(2872, 25, 17, 'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَقُوْلُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَادِيْ هٰٓؤُلَاۤءِ اَمْ هُمْ ضَلُّوا السَّبِيْلَ ۗ', 'wayawma ya<u>h</u>syuruhum wam<u>aa</u> ya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi fayaquulu a-antum a<u>dh</u>laltum \'ib<u>aa</u>dii h<u>aa</u>ul<u>aa</u>-i am hum <u>dh</u>alluu <strong>al</strong>ssabiil<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka bersama apa yang mereka sembah selain Allah, lalu Dia berfirman (kepada yang disembah), “Apakah kamu yang menyesatkan hamba-hamba-Ku itu, atau mereka sendirikah yang sesat dari jalan (yang benar)?” '),
	(2873, 25, 18, 'قَالُوْا سُبْحٰنَكَ مَا كَانَ يَنْۢبَغِيْ لَنَآ اَنْ نَّتَّخِذَ مِنْ دُوْنِكَ مِنْ اَوْلِيَاۤءَ وَلٰكِنْ مَّتَّعْتَهُمْ وَاٰبَاۤءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوْا قَوْمًاۢ بُوْرًا', 'q<u>aa</u>luu sub<u>haa</u>naka m<u>aa</u> k<u>aa</u>na yanbaghii lan<u>aa</u> an nattakhi<u>dz</u>a min duunika min awliy<u>aa</u>-a wal<u>aa</u>kin matta\'tahum wa<u>aa</u>b<u>aa</u>-ahum <u>h</u>att<u>aa</u> nasuu <strong>al</strong><u>dzdz</u>ikra wak<u>aa</u>nuu qawman buur<u>aa</u><strong>n</strong>', 'Mereka (yang disembah itu) menjawab, “Mahasuci Engkau, tidaklah pantas bagi kami mengambil pelindung selain Engkau, tetapi Engkau telah memberi mereka dan nenek moyang mereka kenikmatan hidup, sehingga mereka melupakan peringatan; dan mereka kaum yang binasa.”'),
	(2874, 25, 19, 'فَقَدْ كَذَّبُوْكُمْ بِمَا تَقُوْلُوْنَۙ فَمَا تَسْتَطِيْعُوْنَ صَرْفًا وَّلَا نَصْرًاۚ وَمَنْ يَّظْلِمْ مِّنْكُمْ نُذِقْهُ عَذَابًا كَبِيْرًا', 'faqad ka<u>dzdz</u>abuukum bim<u>aa</u> taquuluuna fam<u>aa</u> tasta<u>th</u>ii\'uuna <u>sh</u>arfan wal<u>aa</u> na<u>sh</u>ran waman ya<u>zh</u>lim minkum nu<u>dz</u>iqhu \'a<u>dzaa</u>ban kabiir<u>aa</u><strong>n</strong>', 'Maka sungguh, mereka (yang disembah itu) telah mengingkari apa yang kamu katakan, maka kamu tidak akan dapat menolak (azab) dan tidak dapat (pula) menolong (dirimu), dan barangsiapa di antara kamu berbuat zalim, niscaya Kami timpakan kepadanya rasa azab yang besar.'),
	(2875, 25, 20, 'وَمَآ اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِيْنَ اِلَّآ اِنَّهُمْ لَيَأْكُلُوْنَ الطَّعَامَ وَيَمْشُوْنَ فِى الْاَسْوَاقِۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً  ۗ اَتَصْبِرُوْنَۚ وَكَانَ رَبُّكَ بَصِيْرًا ࣖ   ۔', 'wam<u>aa</u> arsaln<u>aa</u> qablaka mina <strong>a</strong>lmursaliina ill<u>aa</u> innahum laya/kuluuna <strong>al</strong><u>ththh</u>a\'<u>aa</u>ma wayamsyuuna fii <strong>a</strong>l-asw<u>aa</u>qi waja\'aln<u>aa</u> ba\'<u>dh</u>akum liba\'<u>dh</u>in fitnatan ata<u>sh</u>biruuna wak<u>aa</u>na rabbuka ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan Kami tidak mengutus rasul-rasul sebelummu (Muhammad), melainkan mereka pasti memakan makanan dan berjalan di pasar-pasar. Dan Kami jadikan sebagian kamu sebagai cobaan bagi sebagian yang lain. Maukah kamu bersabar? Dan Tuhanmu Maha Melihat.'),
	(2876, 25, 21, '۞ وَقَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا لَوْلَآ اُنْزِلَ عَلَيْنَا الْمَلٰۤىِٕكَةُ اَوْ نَرٰى رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوْا فِيْٓ اَنْفُسِهِمْ وَعَتَوْ عُتُوًّا كَبِيْرًا', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> lawl<u>aa</u> unzila \'alayn<u>aa</u> <strong>a</strong>lmal<u>aa</u>-ikatu aw nar<u>aa</u> rabban<u>aa</u> laqadi istakbaruu fii anfusihim wa\'ataw \'utuwwan kabiir<u>aa</u><strong>n</strong>', 'Dan orang-orang yang tidak mengharapkan pertemuan dengan Kami (di akhirat) berkata, “Mengapa bukan para malaikat yang diturunkan kepada kita atau (mengapa) kita (tidak) melihat Tuhan kita?” Sungguh, mereka telah menyombongkan diri mereka dan benar-benar telah melampaui batas (dalam melakukan kezaliman).'),
	(2877, 25, 22, 'يَوْمَ يَرَوْنَ الْمَلٰۤىِٕكَةَ لَا بُشْرٰى يَوْمَىِٕذٍ لِّلْمُجْرِمِيْنَ وَيَقُوْلُوْنَ حِجْرًا مَّحْجُوْرًا ', 'yawma yarawna <strong>a</strong>lmal<u>aa</u>-ikata l<u>aa</u> busyr<u>aa</u> yawma-i<u>dz</u>in lilmujrimiina wayaquuluuna <u>h</u>ijran ma<u>h</u>juur<u>aa</u><strong>n</strong>', '(Ingatlah) pada hari (ketika) mereka melihat para malaikat, pada hari itu tidak ada kabar gembira bagi orang-orang yang berdosa dan mereka berkata, “Hijran mahjura.”'),
	(2878, 25, 23, 'وَقَدِمْنَآ اِلٰى مَا عَمِلُوْا مِنْ عَمَلٍ فَجَعَلْنٰهُ هَبَاۤءً مَّنْثُوْرًا ', 'waqadimn<u>aa</u> il<u>aa</u> m<u>aa</u> \'amiluu min \'amalin faja\'aln<u>aa</u>hu hab<u>aa</u>-an mantsuur<u>aa</u><strong>n</strong>', 'Dan Kami akan perlihatkan segala amal yang mereka kerjakan, lalu Kami akan jadikan amal itu (bagaikan) debu yang beterbangan. '),
	(2879, 25, 24, 'اَصْحٰبُ الْجَنَّةِ يَوْمَىِٕذٍ خَيْرٌ مُّسْتَقَرًّا وَّاَحْسَنُ مَقِيْلًا ', 'a<u>sh</u>-<u>haa</u>bu <strong>a</strong>ljannati yawma-i<u>dz</u>in khayrun mustaqarran wa-a<u>h</u>sanu maqiil<u>aa</u><strong>n</strong>', 'Penghuni-penghuni surga pada hari itu paling baik tempat tinggalnya dan paling indah tempat istirahatnya. '),
	(2880, 25, 25, 'وَيَوْمَ تَشَقَّقُ السَّمَاۤءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰۤىِٕكَةُ تَنْزِيْلًا ', 'wayawma tasyaqqaqu <strong>al</strong>ssam<u>aa</u>u bi<strong>a</strong>lgham<u>aa</u>mi wanuzzila <strong>a</strong>lmal<u>aa</u>-ikatu tanziil<u>aa</u><strong>n</strong>', 'Dan (ingatlah) pada hari (ketika) langit pecah mengeluarkan kabut putih dan para malaikat diturunkan (secara) bergelombang.'),
	(2881, 25, 26, 'اَلْمُلْكُ يَوْمَىِٕذِ ِۨالْحَقُّ لِلرَّحْمٰنِۗ وَكَانَ يَوْمًا عَلَى الْكٰفِرِيْنَ عَسِيْرًا ', 'almulku yawma-i<u>dz</u>in <strong>a</strong>l<u>h</u>aqqu li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni wak<u>aa</u>na yawman \'al<u>aa</u> <strong>a</strong>lk<u>aa</u>firiina \'asiir<u>aa</u><strong>n</strong>', 'Kerajaan yang hak pada hari itu adalah milik Tuhan Yang Maha Pengasih. Dan itulah hari yang sulit bagi orang-orang kafir. '),
	(2882, 25, 27, 'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُوْلُ يٰلَيْتَنِى اتَّخَذْتُ مَعَ الرَّسُوْلِ سَبِيْلًا ', 'wayawma ya\'a<u>dhdh</u>u <strong>al</strong><u>zhzhaa</u>limu \'al<u>aa</u> yadayhi yaquulu y<u>aa</u> laytanii ittakha<u>dz</u>tu ma\'a <strong>al</strong>rrasuuli sabiil<u>aa</u><strong>n</strong>', 'Dan (ingatlah) pada hari (ketika) orang-orang zalim menggigit dua jarinya, (menyesali perbuatannya) seraya berkata, “Wahai! Sekiranya (dulu) aku mengambil jalan bersama Rasul.  '),
	(2883, 25, 28, 'يٰوَيْلَتٰى لَيْتَنِيْ لَمْ اَتَّخِذْ فُلَانًا خَلِيْلًا ', 'y<u>aa</u> waylat<u>aa</u> laytanii lam attakhi<u>dz</u> ful<u>aa</u>nan khaliil<u>aa</u><strong>n</strong>', 'Wahai, celaka aku! Sekiranya (dulu) aku tidak menjadikan si fulan itu teman akrab(ku), '),
	(2884, 25, 29, 'لَقَدْ اَضَلَّنِيْ عَنِ الذِّكْرِ بَعْدَ اِذْ جَاۤءَنِيْۗ وَكَانَ الشَّيْطٰنُ لِلْاِنْسَانِ خَذُوْلًا ', 'laqad a<u>dh</u>allanii \'ani <strong>al</strong><u>dzdz</u>ikri ba\'da i<u>dz</u> j<u>aa</u>-anii wak<u>aa</u>na <strong>al</strong>sysyay<u>th</u><u>aa</u>nu lil-ins<u>aa</u>ni kha<u>dz</u>uul<u>aa</u><strong>n</strong>', 'sungguh, dia telah menyesatkan aku dari peringatan (Al-Qur\'an) ketika (Al-Qur\'an) itu telah datang kepadaku. Dan setan memang pengkhianat manusia.” '),
	(2885, 25, 30, 'وَقَالَ الرَّسُوْلُ يٰرَبِّ اِنَّ قَوْمِى اتَّخَذُوْا هٰذَا الْقُرْاٰنَ مَهْجُوْرًا ', 'waq<u>aa</u>la <strong>al</strong>rrasuulu y<u>aa</u> rabbi inna qawmii ittakha<u>dz</u>uu h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>na mahjuur<u>aa</u><strong>n</strong>', 'Dan Rasul (Muhammad) berkata, “Ya Tuhanku, sesungguhnya kaumku telah menjadikan Al-Qur\'an ini diabaikan.”'),
	(2886, 25, 31, 'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِّنَ الْمُجْرِمِيْنَۗ وَكَفٰى بِرَبِّكَ هَادِيًا وَّنَصِيْرًا ', 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> likulli nabiyyin \'aduwwan mina <strong>a</strong>lmujrimiina wakaf<u>aa</u> birabbika h<u>aa</u>diyan wana<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Begitulah, bagi setiap nabi, telah Kami adakan musuh dari orang-orang yang berdosa. Tetapi cukuplah Tuhanmu menjadi pemberi petunjuk dan penolong. '),
	(2887, 25, 32, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَّاحِدَةً ۛ كَذٰلِكَ ۛ لِنُثَبِّتَ بِهٖ فُؤَادَكَ وَرَتَّلْنٰهُ تَرْتِيْلًا ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lawl<u>aa</u> nuzzila \'alayhi <strong>a</strong>lqur-<u>aa</u>nu jumlatan w<u>aah</u>idatan ka<u>dzaa</u>lika linutsabbita bihi fu-<u>aa</u>daka warattaln<u>aa</u>hu tartiil<u>aa</u><strong>n</strong>', 'Dan orang-orang kafir berkata, “Mengapa Al-Qur\'an itu tidak diturunkan kepadanya sekaligus?” Demikianlah, agar Kami memperteguh hatimu (Muhammad) dengannya dan Kami membacakannya secara tartil (berangsur-angsur, perlahan dan benar). '),
	(2888, 25, 33, 'وَلَا يَأْتُوْنَكَ بِمَثَلٍ اِلَّا جِئْنٰكَ بِالْحَقِّ وَاَحْسَنَ تَفْسِيْرًا ۗ', 'wal<u>aa</u> ya/tuunaka bimatsalin ill<u>aa</u> ji/n<u>aa</u>ka bi<strong>a</strong>l<u>h</u>aqqi wa-a<u>h</u>sana tafsiir<u>aa</u><strong>n</strong>', 'Dan mereka (orang-orang kafir itu) tidak datang kepadamu (membawa) sesuatu yang aneh, melainkan Kami datangkan kepadamu yang benar dan penjelasan yang paling baik. '),
	(2889, 25, 34, 'اَلَّذِيْنَ يُحْشَرُوْنَ عَلٰى وُجُوْهِهِمْ اِلٰى جَهَنَّمَۙ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ سَبِيْلًا ࣖ ', '<strong>al</strong>la<u>dz</u>iina yu<u>h</u>syaruuna \'al<u>aa</u> wujuuhihim il<u>aa</u> jahannama ul<u>aa</u>-ika syarrun mak<u>aa</u>nan wa-a<u>dh</u>allu sabiil<u>aa</u><strong>n</strong>', 'Orang-orang yang dikumpulkan ke neraka Jahanam dengan diseret wajahnya, mereka itulah yang paling buruk tempatnya dan paling sesat jalannya. '),
	(2890, 25, 35, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنَا مَعَهٗٓ اَخَاهُ هٰرُوْنَ وَزِيْرًا ۚ ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba waja\'aln<u>aa</u> ma\'ahu akh<u>aa</u>hu h<u>aa</u>ruuna waziir<u>aa</u><strong>n</strong>', 'Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa dan Kami telah menjadikan Harun saudaranya, menyertai dia sebagai wazir (pembantu). '),
	(2891, 25, 36, 'فَقُلْنَا اذْهَبَآ اِلَى الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ فَدَمَّرْنٰهُمْ تَدْمِيْرًا ۗ', 'faquln<u>aa</u> i<u>dz</u>hab<u>aa</u> il<u>aa</u> <strong>a</strong>lqawmi <strong>al</strong>la<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fadammarn<u>aa</u>hum tadmiir<u>aa</u><strong>n</strong>', 'Kemudian Kami berfirman (kepada keduanya), “Pergilah kamu berdua kepada kaum yang mendustakan ayat-ayat Kami.” Lalu Kami hancurkan mereka dengan sehancur-hancurnya. '),
	(2892, 25, 37, 'وَقَوْمَ نُوْحٍ لَّمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنٰهُمْ وَجَعَلْنٰهُمْ لِلنَّاسِ اٰيَةًۗ وَاَعْتَدْنَا لِلظّٰلِمِيْنَ عَذَابًا اَلِيْمًا ۚ ', 'waqawma nuu<u>h</u>in lamm<u>aa</u> ka<u>dzdz</u>abuu <strong>al</strong>rrusula aghraqn<u>aa</u>hum waja\'aln<u>aa</u>hum li<strong>l</strong>nn<u>aa</u>si <u>aa</u>yatan wa-a\'tadn<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'Dan (telah Kami binasakan) kaum Nuh ketika mereka mendustakan para rasul. Kami tenggelamkam mereka dan Kami jadikan (cerita) mereka itu pelajaran bagi manusia. Dan Kami telah sediakan bagi orang-orang zalim azab yang pedih; '),
	(2893, 25, 38, 'وَعَادًا وَّثَمُوْدَا۟ وَاَصْحٰبَ الرَّسِّ وَقُرُوْنًاۢ بَيْنَ ذٰلِكَ كَثِيْرًا ', 'wa\'<u>aa</u>dan watsamuuda wa-a<u>sh</u>-<u>haa</u>ba <strong>al</strong>rrassi waquruunan bayna <u>dzaa</u>lika katsiir<u>aa</u><strong>n</strong>', 'dan (telah Kami binasakan) kaum ‘Ad dan samud dan penduduk Rass serta banyak (lagi) generasi di antara (kaum-kaum) itu. '),
	(2894, 25, 39, 'وَكُلًّا ضَرَبْنَا لَهُ الْاَمْثَالَۖ وَكُلًّا تَبَّرْنَا تَتْبِيْرًا ', 'wakullan <u>dh</u>arabn<u>aa</u> lahu <strong>a</strong>l-amts<u>aa</u>la wakullan tabbarn<u>aa</u> tatbiir<u>aa</u><strong>n</strong>', 'Dan masing-masing telah Kami jadikan perumpamaan dan masing-masing telah Kami hancurkan sehancur-hancurnya.  '),
	(2895, 25, 40, 'وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّتِيْٓ اُمْطِرَتْ مَطَرَ السَّوْءِۗ اَفَلَمْ يَكُوْنُوْا يَرَوْنَهَاۚ بَلْ كَانُوْا لَا يَرْجُوْنَ نُشُوْرًا ', 'walaqad ataw \'al<u>aa</u> <strong>a</strong>lqaryati <strong>al</strong>latii um<u>th</u>irat ma<u>th</u>ara <strong>al</strong>ssaw-i afalam yakuunuu yarawnah<u>aa</u> bal k<u>aa</u>nuu l<u>aa</u> yarjuuna nusyuur<u>aa</u><strong>n</strong>', 'Dan sungguh, mereka (kaum musyrik Mekah) telah melalui negeri (Sodom) yang (dulu) dijatuhi hujan yang buruk (hujan batu). Tidakkah mereka menyaksikannya? Bahkan mereka itu sebenarnya tidak mengharapkan hari kebangkitan. '),
	(2896, 25, 41, 'وَاِذَا رَاَوْكَ اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ بَعَثَ اللّٰهُ رَسُوْلًا ', 'wa-i<u>dzaa</u> ra-awka in yattakhi<u>dz</u>uunaka ill<u>aa</u> huzuwan ah<u>aadzaa</u> <strong>al</strong>la<u>dz</u>ii ba\'atsa <strong>al</strong>l<u>aa</u>hu rasuul<u>aa</u><strong>n</strong>', 'Dan apabila mereka melihat engkau (Muhammad), mereka hanyalah menjadikan engkau sebagai ejekan (dengan mengatakan), “Inikah orangnya yang diutus Allah sebagai Rasul? '),
	(2897, 25, 42, 'اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَآ اَنْ صَبَرْنَا عَلَيْهَاۗ وَسَوْفَ يَعْلَمُوْنَ حِيْنَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَبِيْلًا ', 'in k<u>aa</u>da layu<u>dh</u>illun<u>aa</u> \'an <u>aa</u>lihatin<u>aa</u> lawl<u>aa</u> an <u>sh</u>abarn<u>aa</u> \'alayh<u>aa </u>wasawfa ya\'lamuuna <u>h</u>iina yarawna <strong>a</strong>l\'a<u>dzaa</u>ba man a<u>dh</u>allu sabiil<u>aa</u><strong>n</strong>', 'Sungguh, hampir saja dia menyesatkan kita dari sesembahan kita, seandainya kita tidak tetap bertahan (menyembah)nya.” Dan kelak mereka akan mengetahui pada saat mereka melihat azab, siapa yang paling sesat jalannya.'),
	(2898, 25, 43, 'اَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُۗ اَفَاَنْتَ تَكُوْنُ عَلَيْهِ وَكِيْلًا ۙ', 'ara-ayta mani ittakha<u>dz</u>a il<u>aa</u>hahu haw<u>aa</u>hu afa-anta takuunu \'alayhi wakiil<u>aa</u><strong>n</strong>', 'Sudahkah engkau (Muhammad) melihat orang yang menjadikan keinginannya sebagai tuhannya. Apakah engkau akan menjadi pelindungnya? '),
	(2899, 25, 44, 'اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُوْنَ اَوْ يَعْقِلُوْنَۗ اِنْ هُمْ اِلَّا كَالْاَنْعَامِ  بَلْ هُمْ اَضَلُّ سَبِيْلًا ࣖ', 'am ta<u>h</u>sabu anna aktsarahum yasma\'uuna aw ya\'qiluuna in hum ill<u>aa</u> ka<strong>a</strong>l-an\'<u>aa</u>mi bal hum a<u>dh</u>allu sabiil<u>aa</u><strong>n</strong>', 'Atau apakah engkau mengira bahwa kebanyakan mereka itu mendengar atau memahami? Mereka itu hanyalah seperti hewan ternak, bahkan lebih sesat jalannya.'),
	(2900, 25, 45, 'اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَاۤءَ لَجَعَلَهٗ سَاكِنًاۚ  ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيْلًا ۙ', 'alam tara il<u>aa</u> rabbika kayfa madda <strong>al</strong><u>zhzh</u>illa walaw sy<u>aa</u>-a laja\'alahu s<u>aa</u>kinan tsumma ja\'aln<u>aa</u> <strong>al</strong>sysyamsa \'alayhi daliil<u>aa</u><strong>n</strong>', 'Tidakkah engkau memperhatikan (penciptaan) Tuhanmu, bagaimana Dia memanjangkan (dan memendekkan) bayang-bayang; dan sekiranya Dia menghendaki, niscaya Dia jadikannya (bayang-bayang itu) tetap, kemudian Kami jadikan matahari sebagai petunjuk, '),
	(2901, 25, 46, 'ثُمَّ قَبَضْنٰهُ اِلَيْنَا قَبْضًا يَّسِيْرًا ', 'tsumma qaba<u>dh</u>n<u>aa</u>hu ilayn<u>aa</u> qab<u>dh</u>an yasiir<u>aa</u><strong>n</strong>', 'kemudian Kami menariknya (bayang-bayang itu) kepada Kami sedikit demi sedikit. '),
	(2902, 25, 47, 'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِبَاسًا وَّالنَّوْمَ سُبَاتًا وَّجَعَلَ النَّهَارَ نُشُوْرًا  ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ja\'ala lakumu <strong>al</strong>layla lib<u>aa</u>san wa<strong>al</strong>nnawma sub<u>aa</u>tan waja\'ala <strong>al</strong>nnah<u>aa</u>ra nusyuur<u>aa</u><strong>n</strong>', 'Dan Dialah yang menjadikan malam untukmu (sebagai) pakaian, dan tidur untuk istirahat, dan Dia menjadikan siang untuk bangkit berusaha. '),
	(2903, 25, 48, 'وَهُوَ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۚ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً طَهُوْرًا ۙ', 'wahuwa <strong>al</strong>la<u>dz</u>ii arsala <strong>al</strong>rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi wa-anzaln<u>aa</u> mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an <u>th</u>ahuur<u>aa</u><strong>n</strong>', 'Dan Dialah yang meniupkan angin (sebagai) pembawa kabar gembira sebelum kedatangan rahmat-Nya (hujan); dan Kami turunkan dari langit air yang sangat bersih, '),
	(2904, 25, 49, 'لِّنُحْيِ َۧ بِهٖ بَلْدَةً مَّيْتًا وَّنُسْقِيَهٗ مِمَّا خَلَقْنَآ اَنْعَامًا وَّاَنَاسِيَّ كَثِيْرًا ', 'linu<u>h</u>yiya bihi baldatan maytan wanusqiyahu mimm<u>aa</u> khalaqn<u>aa</u> an\'<u>aa</u>man wa-an<u>aa</u>siyya katsiir<u>aa</u><strong>n</strong>', 'agar (dengan air itu) Kami menghidupkan negeri yang mati (tandus), dan Kami memberi minum kepada sebagian apa yang telah Kami ciptakan, (berupa) hewan-hewan ternak dan manusia yang banyak. '),
	(2905, 25, 50, 'وَلَقَدْ صَرَّفْنٰهُ بَيْنَهُمْ لِيَذَّكَّرُوْاۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا ', 'walaqad <u>sh</u>arrafn<u>aa</u>hu baynahum liya<u>dzdz</u>akkaruu fa-ab<u>aa</u> aktsaru <strong>al</strong>nn<u>aa</u>si ill<u>aa</u> kufuur<u>aa</u><strong>n</strong>', 'Dan sungguh, Kami telah mempergilirkan (hujan) itu di antara mereka agar mereka mengambil pelajaran; tetapi kebanyakan manusia tidak mau (bersyukur), bahkan mereka mengingkari (nikmat).'),
	(2906, 25, 51, 'وَلَوْ شِئْنَا لَبَعَثْنَا فِيْ كُلِّ قَرْيَةٍ نَّذِيْرًا ۖ', 'walaw syi/n<u>aa</u> laba\'atsn<u>aa</u> fii kulli qaryatin na<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Dan sekiranya Kami menghendaki, niscaya Kami utus seorang pemberi peringatan pada setiap negeri. '),
	(2907, 25, 52, 'فَلَا تُطِعِ الْكٰفِرِيْنَ وَجَاهِدْهُمْ بِهٖ جِهَادًا كَبِيْرًا ', 'fal<u>aa</u> tu<u>th</u>i\'i <strong>a</strong>lk<u>aa</u>firiina waj<u>aa</u>hidhum bihi jih<u>aa</u>dan kabiir<u>aa</u><strong>n</strong>', 'Maka janganlah engkau taati orang-orang kafir, dan berjuanglah terhadap mereka dengannya (Al-Qur\'an) dengan (semangat) perjuangan yang besar. '),
	(2908, 25, 53, '۞ وَهُوَ الَّذِيْ مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَّهٰذَا مِلْحٌ  اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَّحِجْرًا مَّحْجُوْرًا ', 'wahuwa <strong>al</strong>la<u>dz</u>ii maraja <strong>a</strong>lba<u>h</u>rayni h<u>aadzaa</u> \'a<u>dz</u>bun fur<u>aa</u>tun wah<u>aadzaa</u> mil<u>h</u>un uj<u>aa</u>jun waja\'ala baynahum<u>aa</u> barzakhan wa<u>h</u>ijran ma<u>h</u>juur<u>aa</u><strong>n</strong>', 'Dan Dialah yang membiarkan dua laut mengalir (berdampingan); yang ini tawar dan segar dan yang lain sangat asin lagi pahit; dan Dia jadikan antara keduanya dinding dan batas yang tidak tembus. '),
	(2909, 25, 54, 'وَهُوَ الَّذِيْ خَلَقَ مِنَ الْمَاۤءِ بَشَرًا فَجَعَلَهٗ نَسَبًا وَّصِهْرًاۗ وَكَانَ رَبُّكَ قَدِيْرًا ', 'wahuwa <strong>al</strong>la<u>dz</u>ii khalaqa mina <strong>a</strong>lm<u>aa</u>-i basyaran faja\'alahu nasaban wa<u>sh</u>ihran wak<u>aa</u>na rabbuka qadiir<u>aa</u><strong>n</strong>', 'Dan Dia (pula) yang menciptakan manusia dari air, lalu Dia jadikan manusia itu (mempunyai) keturunan dan musaharah dan Tuhanmu adalah Mahakuasa. '),
	(2910, 25, 55, 'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۗ وَكَانَ الْكَافِرُ عَلٰى رَبِّهٖ ظَهِيْرًا ', 'waya\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'uhum wal<u>aa</u> ya<u>dh</u>urruhum wak<u>aa</u>na <strong>a</strong>lk<u>aa</u>firu \'al<u>aa</u> rabbihi <u>zh</u>ahiir<u>aa</u><strong>n</strong>', 'Dan mereka menyembah selain Allah apa yang tidak memberi manfaat kepada mereka dan tidak (pula) mendatangkan bencana kepada mereka. Orang-orang kafir adalah penolong (setan untuk berbuat durhaka) terhadap Tuhannya. '),
	(2911, 25, 56, 'وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًا ', 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> mubasysyiran wana<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Dan tidaklah Kami mengutus engkau (Muhammad) melainkan hanya sebagai pembawa kabar gembira dan pemberi peringatan. '),
	(2912, 25, 57, 'قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَاۤءَ اَنْ يَّتَّخِذَ اِلٰى رَبِّهٖ سَبِيْلًا ', 'qul m<u>aa</u> as-alukum \'alayhi min ajrin ill<u>aa</u> man sy<u>aa</u>-a an yattakhi<u>dz</u>a il<u>aa</u> rabbihi sabiil<u>aa</u><strong>n</strong>', 'Katakanlah, “Aku tidak meminta imbalan apa pun dari kamu dalam menyampaikan (risalah) itu, melainkan (mengharapkan agar) orang-orang mau mengambil jalan kepada Tuhannya.” '),
	(2913, 25, 58, 'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِيْ لَا يَمُوْتُ وَسَبِّحْ بِحَمْدِهٖۗ وَكَفٰى بِهٖ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًا ۚ', 'watawakkal \'al<u>aa</u> <strong>a</strong>l<u>h</u>ayyi <strong>al</strong>la<u>dz</u>ii l<u>aa</u> yamuutu wasabbi<u>h</u> bi<u>h</u>amdihi wakaf<u>aa</u> bihi bi<u>dz</u>unuubi \'ib<u>aa</u>dihi khabiir<u>aa</u><strong>n</strong>', 'Dan bertawakallah kepada Allah Yang Hidup, Yang tidak mati, dan bertasbihlah dengan memuji-Nya. Dan cukuplah Dia Maha Mengetahui dosa hamba-hamba-Nya, '),
	(2914, 25, 59, 'اَلَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ  اَلرَّحْمٰنُ فَسْـَٔلْ بِهٖ خَبِيْرًا ', '<strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'arsyi <strong>al</strong>rra<u>h</u>m<u>aa</u>nu fa<strong>i</strong>s-al bihi khabiir<u>aa</u><strong>n</strong>', 'yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy, (Dialah) Yang Maha Pengasih, maka tanyakanlah (tentang Allah) kepada orang yang lebih mengetahui (Muhammad). '),
	(2915, 25, 60, 'وَاِذَا قِيْلَ لَهُمُ اسْجُدُوْا لِلرَّحْمٰنِ قَالُوْا وَمَا الرَّحْمٰنُ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوْرًا ۩ ࣖ', 'wa-i<u>dzaa</u> qiila lahumu usjuduu li<strong>l</strong>rra<u>h</u>m<u>aa</u>ni q<u>aa</u>luu wam<u>aa</u> <strong>al</strong>rra<u>h</u>m<u>aa</u>nu anasjudu lim<u>aa</u> ta/murun<u>aa</u> waz<u>aa</u>dahum nufuur<u>aa</u><strong>n</strong>', 'Dan apabila dikatakan kepada mereka, “Sujudlah kepada Yang Maha Pengasih”, mereka menjawab, “Siapakah yang Maha Pengasih itu? Apakah kami harus sujud kepada Allah yang engkau (Muhammad) perintahkan kepada kami (bersujud kepada-Nya)?” Dan mereka makin jauh lari (dari kebenaran). '),
	(2916, 25, 61, 'تَبٰرَكَ الَّذِيْ جَعَلَ فِى السَّمَاۤءِ بُرُوْجًا وَّجَعَلَ فِيْهَا سِرَاجًا وَّقَمَرًا مُّنِيْرًا  ', 'tab<u>aa</u>raka <strong>al</strong>la<u>dz</u>ii ja\'ala fii <strong>al</strong>ssam<u>aa</u>-i buruujan waja\'ala fiih<u>aa</u> sir<u>aa</u>jan waqamaran muniir<u>aa</u><strong>n</strong>', 'Mahasuci Allah yang menjadikan di langit gugusan bintang-bintang dan Dia juga menjadikan padanya matahari dan bulan yang bersinar. '),
	(2917, 25, 62, 'وَهُوَ الَّذِيْ جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِّمَنْ اَرَادَ اَنْ يَّذَّكَّرَ اَوْ اَرَادَ شُكُوْرًا ', 'wahuwa <strong>al</strong>la<u>dz</u>ii ja\'ala <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra khilfatan liman ar<u>aa</u>da an ya<u>dzdz</u>akkara aw ar<u>aa</u>da syukuur<u>aa</u><strong>n</strong>', 'Dan Dia (pula) yang menjadikan malam dan siang silih berganti bagi orang yang ingin mengambil pelajaran atau yang ingin bersyukur.'),
	(2918, 25, 63, 'وَعِبَادُ الرَّحْمٰنِ الَّذِيْنَ يَمْشُوْنَ عَلَى الْاَرْضِ هَوْنًا وَّاِذَا خَاطَبَهُمُ الْجٰهِلُوْنَ قَالُوْا سَلٰمًا  ', 'wa\'ib<u>aa</u>du <strong>al</strong>rra<u>h</u>m<u>aa</u>ni <strong>al</strong>la<u>dz</u>iina yamsyuuna \'al<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i hawnan wa-i<u>dzaa</u> kh<u>aath</u>abahumu <strong>a</strong>lj<u>aa</u>hiluuna q<u>aa</u>luu sal<u>aa</u>m<u>aa</u><strong>n</strong>', 'Adapun hamba-hamba Tuhan Yang Maha Pengasih itu adalah orang-orang yang berjalan di bumi dengan rendah hati dan apabila orang-orang bodoh menyapa mereka (dengan kata-kata yang menghina), mereka mengucapkan “salam,” '),
	(2919, 25, 64, 'وَالَّذِيْنَ يَبِيْتُوْنَ لِرَبِّهِمْ سُجَّدًا وَّقِيَامًا ', 'wa<strong>a</strong>lla<u>dz</u>iina yabiituuna lirabbihim sujjadan waqiy<u>aa</u>m<u>aa</u><strong>n</strong>', 'dan orang-orang yang menghabiskan waktu malam untuk beribadah kepada Tuhan mereka dengan bersujud dan berdiri. '),
	(2920, 25, 65, 'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۖ اِنَّ عَذَابَهَا كَانَ غَرَامًا ۖ', 'wa<strong>a</strong>lla<u>dz</u>iina yaquuluuna rabban<u>aa</u> i<u>sh</u>rif \'ann<u>aa</u> \'a<u>dzaa</u>ba jahannama inna \'a<u>dzaa</u>bah<u>aa</u> k<u>aa</u>na ghar<u>aa</u>m<u>aa</u><strong>n</strong>', 'Dan orang-orang yang berkata, “Ya Tuhan kami, jauhkanlah azab Jahanam dari kami, karena sesungguhnya azabnya itu membuat kebinasaan yang kekal,”'),
	(2921, 25, 66, 'اِنَّهَا سَاۤءَتْ مُسْتَقَرًّا وَّمُقَامًا ', 'nnah<u>aa</u> s<u>aa</u>-at mustaqarran wamuq<u>aa</u>m<u>aa</u><strong>n</strong>', 'sungguh, Jahanam itu seburuk-buruk tempat menetap dan tempat kediaman.'),
	(2922, 25, 67, 'وَالَّذِيْنَ اِذَآ اَنْفَقُوْا لَمْ يُسْرِفُوْا وَلَمْ يَقْتُرُوْا وَكَانَ بَيْنَ ذٰلِكَ قَوَامًا ', 'wa<strong>a</strong>lla<u>dz</u>iina i<u>dzaa</u> anfaquu lam yusrifuu walam yaqturuu wak<u>aa</u>na bayna <u>dzaa</u>lika qaw<u>aa</u>m<u>aa</u><strong>n</strong>', 'Dan (termasuk hamba-hamba Tuhan Yang Maha Pengasih) orang-orang yang apabila menginfakkan (harta), mereka tidak berlebihan, dan tidak (pula) kikir, di antara keduanya secara wajar,'),
	(2923, 25, 68, 'وَالَّذِيْنَ لَا يَدْعُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ وَلَا يَقْتُلُوْنَ النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُوْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ يَلْقَ اَثَامًا ۙ', 'wa<strong>a</strong>lla<u>dz</u>iina l<u>aa</u> yad\'uuna ma\'<u>aa</u> <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara wal<u>aa</u> yaqtuluuna <strong>al</strong>nnafsa <strong>al</strong>latii <u>h</u>arrama <strong>al</strong>l<u>aa</u>hu ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wal<u>aa</u> yaznuuna waman yaf\'al <u>dzaa</u>lika yalqa ats<u>aa</u>m<u>aa</u><strong>n</strong>', 'dan orang-orang yang tidak mempersekutukan Allah dengan sembahan lain dan tidak membunuh orang yang diharamkan Allah kecuali dengan (alasan) yang benar, dan tidak berzina; dan barangsiapa melakukan demikian itu, niscaya dia mendapat hukuman yang berat,'),
	(2924, 25, 69, 'يُّضٰعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ فِيْهٖ مُهَانًا ۙ', 'yu<u>daa</u>\'af lahu <strong>a</strong>l\'a<u>dzaa</u>bu yawma <strong>a</strong>lqiy<u>aa</u>mati wayakhlud fiihi muh<u>aa</u>n<u>aa</u><strong>n</strong>', '(yakni) akan dilipatgandakan azab untuknya pada hari Kiamat dan dia akan kekal dalam azab itu, dalam keadaan terhina, '),
	(2925, 25, 70, 'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَاُولٰۤىِٕكَ يُبَدِّلُ اللّٰهُ سَيِّاٰتِهِمْ حَسَنٰتٍۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ', 'ill<u>aa</u> man t<u>aa</u>ba wa<u>aa</u>mana wa\'amila \'amalan <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika yubaddilu <strong>al</strong>l<u>aa</u>hu sayyi-<u>aa</u>tihim <u>h</u>asan<u>aa</u>tin wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'kecuali orang-orang yang bertobat dan beriman dan mengerjakan kebajikan; maka kejahatan mereka diganti Allah dengan kebaikan. Allah Maha Pengampun, Maha Penyayang.'),
	(2926, 25, 71, 'وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَاِنَّهٗ يَتُوْبُ اِلَى اللّٰهِ مَتَابًا ', 'waman t<u>aa</u>ba wa\'amila <u>shaa</u>li<u>h</u>an fa-innahu yatuubu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi mat<u>aa</u>b<u>aa</u><strong>n</strong>', 'Dan barangsiapa bertobat dan mengerjakan kebajikan, maka sesungguhnya dia bertobat kepada Allah dengan tobat yang sebenar-benarnya.'),
	(2927, 25, 72, 'وَالَّذِيْنَ لَا يَشْهَدُوْنَ الزُّوْرَۙ وَاِذَا مَرُّوْا بِاللَّغْوِ مَرُّوْا كِرَامًا ', 'wa<strong>a</strong>lla<u>dz</u>iina l<u>aa</u> yasyhaduuna <strong>al</strong>zzuura wa-i<u>dzaa</u> marruu bi<strong>a</strong>llaghwi marruu kir<u>aa</u>m<u>aa</u><strong>n</strong>', 'Dan orang-orang yang tidak memberikan kesaksian palsu, dan apabila mereka bertemu dengan (orang-orang) yang mengerjakan perbuatan-perbuatan yang tidak berfaedah, mereka berlalu dengan menjaga kehormatan dirinya,'),
	(2928, 25, 73, 'وَالَّذِيْنَ اِذَا ذُكِّرُوْا بِاٰيٰتِ رَبِّهِمْ لَمْ يَخِرُّوْا عَلَيْهَا صُمًّا وَّعُمْيَانًا ', 'wa<strong>a</strong>lla<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukkiruu bi-<u>aa</u>y<u>aa</u>ti rabbihim lam yakhirruu \'alayh<u>aa</u> <u>sh</u>umman wa\'umy<u>aa</u>n<u>aa</u><strong>n</strong>', 'dan orang-orang yang apabila diberi peringatan dengan ayat-ayat Tuhan mereka, mereka tidak bersikap sebagai orang-orang yang tuli dan buta,'),
	(2929, 25, 74, 'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيّٰتِنَا قُرَّةَ اَعْيُنٍ وَّاجْعَلْنَا لِلْمُتَّقِيْنَ اِمَامًا ', 'wa<strong>a</strong>lla<u>dz</u>iina yaquuluuna rabban<u>aa</u> hab lan<u>aa</u> min azw<u>aa</u>jin<u>aa</u> wa<u>dz</u>urriyy<u>aa</u>tin<u>aa</u> qurrata a\'yunin wa<strong>i</strong>j\'aln<u>aa</u> lilmuttaqiina im<u>aa</u>m<u>aa</u><strong>n</strong>', 'Dan orang-orang yang berkata, “Ya Tuhan kami, anugerahkanlah kepada kami pasangan kami dan keturunan kami sebagai penyenang hati (kami), dan jadikanlah kami pemimpin bagi orang-orang yang bertakwa.”'),
	(2930, 25, 75, 'اُولٰۤىِٕكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوْا وَيُلَقَّوْنَ فِيْهَا تَحِيَّةً وَّسَلٰمًا ۙ', 'ul<u>aa</u>-ika yujzawna <strong>a</strong>lghurfata bim<u>aa</u> <u>sh</u>abaruu wayulaqqawna fiih<u>aa</u> ta<u>h</u>iyyatan wasal<u>aa</u>m<u>aa</u><strong>n</strong>', 'Mereka itu akan diberi balasan dengan tempat yang tinggi (dalam surga) atas kesabaran mereka, dan di sana mereka akan disambut dengan penghormatan dan salam.'),
	(2931, 25, 76, 'خٰلِدِيْنَ فِيْهَاۗ حَسُنَتْ مُسْتَقَرًّا وَّمُقَامًا ', 'kh<u>aa</u>lidiina fiih<u>aa</u> <u>h</u>asunat mustaqarran wamuq<u>aa</u>m<u>aa</u><strong>n</strong>', 'Mereka kekal di dalamnya. Surga itu sebaik-baik tempat menetap dan tempat kediaman.'),
	(2932, 25, 77, 'قُلْ مَا يَعْبَؤُا بِكُمْ رَبِّيْ لَوْلَا دُعَاۤؤُكُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُوْنُ لِزَامًا ࣖ', 'qul m<u>aa</u> ya\'bau bikum rabbii lawl<u>aa</u> du\'<u>aa</u>ukum faqad ka<u>dzdz</u>abtum fasawfa yakuunu liz<u>aa</u>m<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad, kepada orang-orang musyrik), “Tuhanku tidak akan mengindahkan kamu, kalau tidak karena ibadahmu. (Tetapi bagaimana kamu beribadah kepada-Nya), padahal sungguh, kamu telah mendustakan-Nya? Karena itu, kelak (azab) pasti (menimpamu).”'),
	(2933, 26, 1, 'طٰسۤمّۤ ', '<u>th</u><u>aa</u>-siin-miim', 'Tha Sin Mim'),
	(2934, 26, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ', 'tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>lmubiin<strong>i</strong>', 'Inilah ayat-ayat Kitab (Al-Qur\'an) yang jelas.'),
	(2935, 26, 3, 'لَعَلَّكَ بَاخِعٌ نَّفْسَكَ اَلَّا يَكُوْنُوْا مُؤْمِنِيْنَ ', 'la\'allaka b<u>aa</u>khi\'un nafsaka <strong>al</strong>l<u>aa</u> yakuunuu mu/miniin<strong>a</strong>', 'Boleh jadi engkau (Muhammad) akan membinasakan dirimu (dengan kesedihan), karena mereka (penduduk Mekah) tidak beriman.'),
	(2936, 26, 4, 'اِنْ نَّشَأْ نُنَزِّلْ عَلَيْهِمْ مِّنَ السَّمَاۤءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِعِيْنَ ', 'in nasya/ nunazzil \'alayhim mina <strong>al</strong>ssam<u>aa</u>-i <u>aa</u>yatan fa<u>zh</u>allat a\'n<u>aa</u>quhum lah<u>aa</u> kh<u>aad</u>i\'iin<strong>a</strong>', 'Jika Kami menghendaki, niscaya Kami turunkan kepada mereka mukjizat dari langit, yang akan membuat tengkuk mereka tunduk dengan rendah hati kepadanya.'),
	(2937, 26, 5, 'وَمَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوْا عَنْهُ مُعْرِضِيْنَ ', 'wam<u>aa</u> ya/tiihim min <u>dz</u>ikrin mina <strong>al</strong>rra<u>h</u>m<u>aa</u>ni mu<u>h</u>datsin ill<u>aa</u> k<u>aa</u>nuu \'anhu mu\'ri<u>dh</u>iin<strong>a</strong>', 'Dan setiap kali disampaikan kepada mereka suatu peringatan baru (ayat Al-Qur\'an yang diturunkan) dari Tuhan Yang Maha Pengasih, mereka selalu berpaling darinya.'),
	(2938, 26, 6, 'فَقَدْ كَذَّبُوْا فَسَيَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ', 'faqad ka<u>dzdz</u>abuu fasaya/tiihim anb<u>aa</u>u m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun<strong>a</strong>', 'Sungguh, mereka telah mendustakan (Al-Qur\'an), maka kelak akan datang kepada mereka (kebenaran) berita-berita mengenai apa (azab) yang dulu mereka perolok-olokkan.'),
	(2939, 26, 7, 'اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ', 'awa lam yaraw il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i kam anbatn<u>aa</u> fiih<u>aa</u> min kulli zawjin kariim<strong>in</strong>', 'Dan apakah mereka tidak memperhatikan bumi, betapa banyak Kami tumbuhkan di bumi itu berbagai macam pasangan (tumbuh-tumbuhan) yang baik?'),
	(2940, 26, 8, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةًۗ وَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu terdapat tanda (kebesaran Allah), tetapi kebanyakan mereka tidak beriman.'),
	(2941, 26, 9, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(2942, 26, 10, 'وَاِذْ نَادٰى رَبُّكَ مُوْسٰٓى اَنِ ائْتِ الْقَوْمَ الظّٰلِمِيْنَ ۙ', 'wa-i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbuka muus<u>aa</u> ani i/ti <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Dan (ingatlah) ketika Tuhanmu menyeru Musa (dengan firman-Nya), “Datangilah kaum yang zalim itu,'),
	(2943, 26, 11, 'قَوْمَ فِرْعَوْنَ ۗ اَلَا يَتَّقُوْنَ  ', 'qawma fir\'awna <strong>a</strong>l<u>aa</u> yattaquun<strong>a</strong>', '(yaitu) kaum Fir‘aun. Mengapa mereka tidak bertakwa?”'),
	(2944, 26, 12, 'قَالَ رَبِّ اِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ۗ', 'q<u>aa</u>la rabbi innii akh<u>aa</u>fu an yuka<u>dzdz</u>ibuun<strong>i</strong>', 'Dia (Musa) berkata, “Ya Tuhanku, sungguh, aku takut mereka akan mendustakan aku,'),
	(2945, 26, 13, 'وَيَضِيْقُ صَدْرِيْ وَلَا يَنْطَلِقُ لِسَانِيْ فَاَرْسِلْ اِلٰى هٰرُوْنَ ', 'waya<u>dh</u>iiqu <u>sh</u>adrii wal<u>aa</u> yan<u>th</u>aliqu lis<u>aa</u>nii fa-arsil il<u>aa</u> h<u>aa</u>ruun<strong>a</strong>', 'sehingga dadaku terasa sempit dan lidahku tidak lancar, maka utuslah Harun (bersamaku).'),
	(2946, 26, 14, 'وَلَهُمْ عَلَيَّ ذَنْۢبٌ فَاَخَافُ اَنْ يَّقْتُلُوْنِ  ۚ', 'walahum \'alayya <u>dz</u>anbun fa-akh<u>aa</u>fu an yaqtuluun<strong>i</strong>', 'Sebab aku berdosa terhadap mereka, maka aku takut mereka akan membunuhku.”'),
	(2947, 26, 15, 'قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيٰتِنَآ اِنَّا مَعَكُمْ مُّسْتَمِعُوْنَ ۙ ', 'q<u>aa</u>la kall<u>aa</u> fa<strong>i</strong><u>dz</u>hab<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> inn<u>aa</u> ma\'akum mustami\'uun<strong>a</strong>', '(Allah) berfirman, “Jangan takut (mereka tidak akan dapat membunuhmu)! Maka pergilah kamu berdua dengan membawa ayat-ayat Kami (mukjizat-mukjizat); sungguh, Kami bersamamu mendengarkan (apa yang mereka katakan),'),
	(2948, 26, 16, 'فَأْتِيَا فِرْعَوْنَ فَقُوْلَآ اِنَّا رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ۙ', 'fa/tiy<u>aa</u> fir\'awna faquul<u>aa</u> inn<u>aa</u> rasuulu rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'maka datanglah kamu berdua kepada Fir‘aun dan katakan, “Sesungguhnya kami adalah rasul-rasul Tuhan seluruh alam,'),
	(2949, 26, 17, 'اَنْ اَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ', 'an arsil ma\'an<u>aa</u> banii isr<u>aa</u>-iil<strong>a</strong>', 'lepaskanlah Bani Israil (pergi) bersama kami.”'),
	(2950, 26, 18, 'قَالَ اَلَمْ نُرَبِّكَ فِيْنَا وَلِيْدًا وَّلَبِثْتَ فِيْنَا مِنْ عُمُرِكَ سِنِيْنَ ۗ ', 'q<u>aa</u>la alam nurabbika fiin<u>aa</u> waliidan walabitsta fiin<u>aa</u> min \'umurika siniin<strong>a</strong>', 'Dia (Fir‘aun) menjawab, “Bukankah kami telah mengasuhmu dalam lingkungan (keluarga) kami, waktu engkau masih kanak-kanak dan engkau tinggal bersama kami beberapa tahun dari umurmu.'),
	(2951, 26, 19, 'وَفَعَلْتَ فَعْلَتَكَ الَّتِيْ فَعَلْتَ وَاَنْتَ مِنَ الْكٰفِرِيْنَ  ', 'wafa\'alta fa\'lataka <strong>al</strong>latii fa\'alta wa-anta mina <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'Dan engkau (Musa) telah melakukan (kesalahan dari) perbuatan yang telah engkau lakukan dan engkau termasuk orang yang tidak tahu berterima kasih.”'),
	(2952, 26, 20, 'قَالَ فَعَلْتُهَآ اِذًا وَّاَنَا۠ مِنَ الضَّاۤلِّيْنَ  ', 'q<u>aa</u>la fa\'altuh<u>aa</u> i<u>dz</u>an wa-an<u>aa</u> mina <strong>al</strong><u>dhdhaa</u>lliin<strong>a</strong>', 'Dia (Musa) berkata, “Aku telah melakukannya, dan ketika itu aku termasuk orang yang khilaf.'),
	(2953, 26, 21, 'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِيْ رَبِّيْ حُكْمًا وَّجَعَلَنِيْ مِنَ الْمُرْسَلِيْنَ  ', 'fafarartu minkum lamm<u>aa</u> khiftukum fawahaba lii rabbii <u>h</u>ukman waja\'alanii mina <strong>a</strong>lmursaliin<strong>a</strong>', 'Lalu aku lari darimu karena aku takut kepadamu, kemudian Tuhanku menganugerahkan ilmu kepadaku serta Dia menjadikan aku salah seorang di antara rasul-rasul.'),
	(2954, 26, 22, 'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَّ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ', 'watilka ni\'matun tamunnuh<u>aa</u> \'alayya an \'abbadta banii isr<u>aa</u>-iil<strong>a</strong>', 'Dan itulah kebaikan yang telah engkau berikan kepadaku, (sementara) itu engkau telah memperbudak Bani Israil.”'),
	(2955, 26, 23, 'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعٰلَمِيْنَ ۗ ', 'q<u>aa</u>la fir\'awnu wam<u>aa</u> rabbu <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Fir‘aun bertanya, “Siapa Tuhan seluruh alam itu?”'),
	(2956, 26, 24, 'قَالَ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ مُّوْقِنِيْنَ ', 'q<u>aa</u>la rabbu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> in kuntum muuqiniin<strong>a</strong>', 'Dia (Musa) menjawab, “Tuhan pencipta langit dan bumi dan apa yang ada di antara keduanya (itulah Tuhanmu), jika kamu mempercayai-Nya.”'),
	(2957, 26, 25, 'قَالَ لِمَنْ حَوْلَهٗٓ اَلَا تَسْتَمِعُوْنَ ', 'q<u>aa</u>la liman <u>h</u>awlahu <strong>a</strong>l<u>aa</u> tastami\'uun<strong>a</strong>', 'Dia (Fir‘aun) berkata kepada orang-orang di sekelilingnya, “Apakah kamu tidak mendengar (apa yang dikatakannya)?”'),
	(2958, 26, 26, 'قَالَ رَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ ', 'q<u>aa</u>la rabbukum warabbu <u>aa</u>b<u>aa</u>-ikumu <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dia (Musa) berkata, “(Dia) Tuhanmu dan juga Tuhan nenek moyangmu terdahulu.”'),
	(2959, 26, 27, 'قَالَ اِنَّ رَسُوْلَكُمُ الَّذِيْٓ اُرْسِلَ اِلَيْكُمْ لَمَجْنُوْنٌ ', 'q<u>aa</u>la inna rasuulakumu <strong>al</strong>la<u>dz</u>ii ursila ilaykum lamajnuun<strong>un</strong>', 'Dia (Fir‘aun) berkata, “Sungguh, Rasulmu yang diutus kepada kamu benar-benar orang gila.” '),
	(2960, 26, 28, 'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ تَعْقِلُوْنَ  ', 'q<u>aa</u>la rabbu <strong>a</strong>lmasyriqi wa<strong>a</strong>lmaghribi wam<u>aa</u> baynahum<u>aa</u> in kuntum ta\'qiluun<strong>a</strong>', 'Dia (Musa) berkata, “(Dialah) Tuhan (yang menguasai) timur dan barat dan apa yang ada di antara keduanya; jika kamu mengerti.”'),
	(2961, 26, 29, 'قَالَ لَىِٕنِ اتَّخَذْتَ اِلٰهًا غَيْرِيْ لَاَجْعَلَنَّكَ مِنَ الْمَسْجُوْنِيْنَ ', 'q<u>aa</u>la la-ini ittakha<u>dz</u>ta il<u>aa</u>han ghayrii la-aj\'alannaka mina <strong>a</strong>lmasjuuniin<strong>a</strong>', 'Dia (Fir‘aun) berkata, “Sungguh, jika engkau menyembah Tuhan selain aku, pasti aku masukkan engkau ke dalam penjara.”'),
	(2962, 26, 30, 'قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُّبِيْنٍ ', 'q<u>aa</u>la awa law ji/tuka bisyay-in mubiin<strong>in</strong>', 'Dia (Musa) berkata, “Apakah (engkau akan melakukan itu) sekalipun aku tunjukkan kepadamu sesuatu (bukti) yang nyata?”'),
	(2963, 26, 31, 'قَالَ فَأْتِ بِهٖٓ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'q<u>aa</u>la fa/ti bihi in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Dia (Fir‘aun) berkata, “Tunjukkan sesuatu (bukti yang nyata) itu, jika engkau termasuk orang yang benar!”'),
	(2964, 26, 32, 'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۚ ', 'fa-alq<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya tsu\'b<u>aa</u>nun mubiin<strong>un</strong>', 'Maka dia (Musa) melemparkan tongkatnya, tiba-tiba tongkat itu menjadi ular besar yang sebenarnya.'),
	(2965, 26, 33, 'وَنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ ࣖ ', 'wanaza\'a yadahu fa-i<u>dzaa</u> hiya bay<u>daa</u>u li<strong>l</strong>nn<u>aats</u>iriin<strong>a</strong>', 'Dan dia mengeluarkan tangannya (dari dalam bajunya), tiba-tiba tangan itu menjadi putih (bercahaya) bagi orang-orang yang melihatnya.'),
	(2966, 26, 34, 'قَالَ لِلْمَلَاِ حَوْلَهٗٓ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌ ۙ ', 'q<u>aa</u>la lilmala-i <u>h</u>awlahu inna h<u>aadzaa</u> las<u>aah</u>irun \'aliim<strong>un</strong>', 'Dia (Fir‘aun) berkata kepada para pemuka di sekelilingnya, “Sesungguhnya dia (Musa) ini pasti seorang pesihir yang pandai,'),
	(2967, 26, 35, 'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهٖۖ فَمَاذَا تَأْمُرُوْنَ ', 'yuriidu an yukhrijakum min ar<u>dh</u>ikum bisi<u>h</u>rihi fam<u>aatsaa</u> ta/muruun<strong>a</strong>', 'dia hendak mengusir kamu dari negerimu dengan sihirnya; karena itu apakah yang kamu sarankan?”'),
	(2968, 26, 36, 'قَالُوْٓا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۙ ', 'q<u>aa</u>luu arjih wa-akh<u>aa</u>hu wa<strong>i</strong>b\'ats fii <strong>a</strong>lmad<u>aa</u>-ini <u>haa</u>syiriin<strong>a</strong>', 'Mereka menjawab, “Tahanlah (untuk sementara) dia dan saudaranya, dan utuslah ke seluruh negeri orang-orang yang akan mengumpulkan (pesihir),'),
	(2969, 26, 37, 'يَأْتُوْكَ بِكُلِّ سَحَّارٍ عَلِيْمٍ ', 'ya/tuuka bikulli sa<u>hhaa</u>rin \'aliim<strong>in</strong>', 'niscaya mereka akan mendatangkan semua pesihir yang pandai kepadamu.” '),
	(2970, 26, 38, 'فَجُمِعَ السَّحَرَةُ لِمِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ۙ ', 'fajumi\'a <strong>al</strong>ssa<u>h</u>aratu limiiq<u>aa</u>ti yawmin ma\'luum<strong>in</strong>', 'Lalu dikumpulkanlah para pesihir pada waktu (yang ditetapkan) pada hari yang telah ditentukan, '),
	(2971, 26, 39, 'وَّقِيْلَ لِلنَّاسِ هَلْ اَنْتُمْ مُّجْتَمِعُوْنَ ۙ ', 'waqiila li<strong>l</strong>nn<u>aa</u>si hal antum mujtami\'uun<strong>a</strong>', 'dan diumumkan kepada orang banyak, “Berkumpullah kamu semua,'),
	(2972, 26, 40, 'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوْا هُمُ الْغٰلِبِيْنَ  ', 'la\'allan<u>aa</u> nattabi\'u <strong>al</strong>ssa<u>h</u>arata in k<u>aa</u>nuu humu <strong>a</strong>lgh<u>aa</u>libiin<strong>a</strong>', 'agar kita mengikuti para pesihir itu, jika mereka yang menang.”'),
	(2973, 26, 41, 'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالُوْا لِفِرْعَوْنَ اَىِٕنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ  ', 'falamm<u>aa</u> j<u>aa</u>-a <strong>al</strong>ssa<u>h</u>aratu q<u>aa</u>luu lifir\'awna a-inna lan<u>aa</u> la-ajran in kunn<u>aa</u> na<u>h</u>nu <strong>a</strong>lgh<u>aa</u>libiin<strong>a</strong>', 'Maka ketika para pesihir datang, mereka berkata kepada Fir‘aun, “Apakah kami benar-benar akan mendapat imbalan yang besar jika kami yang menang?”'),
	(2974, 26, 42, 'قَالَ نَعَمْ وَاِنَّكُمْ اِذًا لَّمِنَ الْمُقَرَّبِيْنَ ', 'q<u>aa</u>la na\'am wa-innakum i<u>dz</u>an lamina <strong>a</strong>lmuqarrabiin<strong>a</strong>', 'Dia (Fir‘aun) menjawab, “Ya, dan bahkan kamu pasti akan mendapat kedudukan yang dekat (kepadaku).”'),
	(2975, 26, 43, 'قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ  ', 'q<u>aa</u>la lahum muus<u>aa</u> <strong>a</strong>lquu m<u>aa</u> antum mulquun<strong>a</strong>', 'Dia (Musa) berkata kepada mereka, “Lemparkanlah apa yang hendak kamu lemparkan.”'),
	(2976, 26, 44, 'فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوْا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغٰلِبُوْنَ  ', 'fa-alqaw <u>h</u>ib<u>aa</u>lahum wa\'i<u>sh</u>iyyahum waq<u>aa</u>luu bi\'izzati fir\'awna inn<u>aa</u> lana<u>h</u>nu <strong>a</strong>lgh<u>aa</u>libuun<strong>a</strong>', 'Lalu mereka melemparkan tali temali dan tongkat-tongkat mereka seraya berkata, “Demi kekuasaan Fir‘aun, pasti kamilah yang akan menang.”'),
	(2977, 26, 45, 'فَاَلْقٰى مُوْسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَ ۚ ', 'fa-alq<u>aa</u> muus<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya talqafu m<u>aa</u> ya/fikuun<strong>a</strong>', 'Kemudian Musa melemparkan tongkatnya, maka tiba-tiba ia menelan benda-benda palsu yang mereka ada-adakan itu.'),
	(2978, 26, 46, 'فَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَ ۙ ', 'faulqiya <strong>al</strong>ssa<u>h</u>aratu s<u>aa</u>jidiin<strong>a</strong>', 'Maka menyungkurlah para pesihir itu, bersujud.'),
	(2979, 26, 47, 'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَ ۙ ', 'q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Mereka berkata, “Kami beriman kepada Tuhan seluruh alam,'),
	(2980, 26, 48, 'رَبِّ مُوْسٰى وَهٰرُوْنَ  ', 'rabbi muus<u>aa</u> wah<u>aa</u>ruun<strong>a</strong>', '(yaitu) Tuhannya Musa dan Harun.”'),
	(2981, 26, 49, 'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَسَوْفَ تَعْلَمُوْنَ ەۗ لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَۚ', 'q<u>aa</u>la <u>aa</u>mantum lahu qabla an <u>aadz</u>ana lakum innahu lakabiirukumu <strong>al</strong>la<u>dz</u>ii \'allamakumu <strong>al</strong>ssi<u>h</u>ra falasawfa ta\'lamuuna lauqa<u>ththh</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin walau<u>sh</u>allibannakum ajma\'iin<strong>a</strong>', 'Dia (Fir‘aun) berkata, “Mengapa kamu beriman kepada Musa sebelum aku memberi izin kepadamu? Sesungguhnya dia pemimpinmu yang mengajarkan sihir kepadamu. Nanti kamu pasti akan tahu (akibat perbuatanmu). Pasti akan kupotong tangan dan kakimu bersilang dan sungguh, akan kusalib kamu semuanya.”'),
	(2982, 26, 50, 'قَالُوْا لَا ضَيْرَ ۖاِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَ ۚ  ', 'q<u>aa</u>luu l<u>aa</u> <u>dh</u>ayra inn<u>aa</u> il<u>aa</u> rabbin<u>aa</u> munqalibuun<strong>a</strong>', 'Mereka berkata, “Tidak ada yang kami takutkan, karena kami akan kembali kepada Tuhan kami.'),
	(2983, 26, 51, ' اِنَّا نَطْمَعُ اَنْ يَّغْفِرَ لَنَا رَبُّنَا خَطٰيٰنَآ اَنْ كُنَّآ اَوَّلَ الْمُؤْمِنِيْنَ ۗ ࣖ', 'inn<u>aa</u> na<u>th</u>ma\'u an yaghfira lan<u>aa</u> rabbun<u>aa</u> kha<u>th</u><u>aa</u>y<u>aa</u>n<u>aa</u> an kunn<u>aa</u> awwala <strong>a</strong>lmu/miniin<strong>a</strong>', 'Sesungguhnya kami sangat menginginkan sekiranya Tuhan kami akan mengampuni kesalahan kami, karena kami menjadi orang yang pertama-tama beriman.”'),
	(2984, 26, 52, '۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْٓ اِنَّكُمْ مُّتَّبَعُوْنَ  ', 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> an asri bi\'ib<u>aa</u>dii innakum muttaba\'uun<strong>a</strong>', 'Dan Kami wahyukan (perintahkan) kepada Musa, “Pergilah pada malam hari dengan membawa hamba-hamba-Ku (Bani Israil), sebab pasti kamu akan dikejar.”'),
	(2985, 26, 53, 'فَاَرْسَلَ فِرْعَوْنُ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۚ ', 'fa-arsala fir\'awnu fii <strong>a</strong>lmad<u>aa</u>-ini <u>haa</u>syiriin<strong>a</strong>', 'Kemudian Fir‘aun mengirimkan orang ke kota-kota (untuk mengumpulkan bala tentaranya).'),
	(2986, 26, 54, 'اِنَّ هٰٓؤُلَاۤءِ لَشِرْذِمَةٌ قَلِيْلُوْنَۙ ', 'inna h<u>aa</u>ul<u>aa</u>-i lasyir<u>dz</u>imatun qaliiluun<strong>a</strong>', '(Fir‘aun berkata), “Sesungguhnya mereka (Bani Israil) hanya sekelompok kecil,'),
	(2987, 26, 55, 'وَاِنَّهُمْ لَنَا لَغَاۤىِٕظُوْنَ ۙ  ', 'wa-innahum lan<u>aa</u> lagh<u>aa</u>-i<u>zh</u>uun<strong>a</strong>', 'dan sesungguhnya mereka telah berbuat hal-hal yang menimbulkan amarah kita,'),
	(2988, 26, 56, 'وَاِنَّا لَجَمِيْعٌ حٰذِرُوْنَ ۗ ', 'wa-inn<u>aa</u> lajamii\'un <u>hats</u>iruun<strong>a</strong>', 'dan sesungguhnya kita semua tanpa kecuali harus selalu waspada.”'),
	(2989, 26, 57, 'فَاَخْرَجْنٰهُمْ مِّنْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ', 'fa-akhrajn<u>aa</u>hum min jann<u>aa</u>tin wa\'uyuun<strong>in</strong>', 'Kemudian, Kami keluarkan mereka (Fir‘aun dan kaumnya) dari taman-taman dan mata air,'),
	(2990, 26, 58, 'وَّكُنُوْزٍ وَّمَقَامٍ كَرِيْمٍ ۙ ', 'wakunuuzin wamaq<u>aa</u>min kariim<strong>in</strong>', 'dan (dari) harta kekayaan dan kedudukan yang mulia, '),
	(2991, 26, 59, 'كَذٰلِكَۚ وَاَوْرَثْنٰهَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ', 'ka<u>dzaa</u>lika wa-awratsn<u>aa</u>h<u>aa</u> banii isr<u>aa</u>-iil<strong>a</strong>', 'demikianlah, dan Kami anugerahkan semuanya (itu) kepada Bani Israil.'),
	(2992, 26, 60, 'فَاَتْبَعُوْهُمْ مُّشْرِقِيْنَ  ', 'fa-atba\'uuhum musyriqiin<strong>a</strong>', 'Lalu (Fir‘aun dan bala tentaranya) dapat menyusul mereka pada waktu matahari terbit.'),
	(2993, 26, 61, 'فَلَمَّا تَرَاۤءَ الْجَمْعٰنِ قَالَ اَصْحٰبُ مُوْسٰٓى اِنَّا لَمُدْرَكُوْنَ ۚ  ', 'falamm<u>aa</u> tar<u>aa</u>-a <strong>a</strong>ljam\'<u>aa</u>ni q<u>aa</u>la a<u>sh</u>-<u>haa</u>bu muus<u>aa</u> inn<u>aa</u> lamudrakuun<strong>a</strong>', 'Maka ketika kedua golongan itu saling melihat, berkatalah pengikut-pengikut Musa, “Kita benar-benar akan tersusul.”'),
	(2994, 26, 62, 'قَالَ كَلَّاۗ اِنَّ مَعِيَ رَبِّيْ سَيَهْدِيْنِ  ', 'q<u>aa</u>la kall<u>aa</u> inna ma\'iya rabbii sayahdiin<strong>i</strong>', 'Dia (Musa) menjawab, “Sekali-kali tidak akan (tersusul); sesungguhnya Tuhanku bersamaku, Dia akan memberi petunjuk kepadaku.”'),
	(2995, 26, 63, 'فَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنِ اضْرِبْ بِّعَصَاكَ الْبَحْرَۗ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيْمِ ۚ ', 'fa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> ani i<u>dh</u>rib bi\'a<u>shaa</u>ka <strong>a</strong>lba<u>h</u>ra fa<strong>i</strong>nfalaqa fak<u>aa</u>na kullu firqin ka<strong>al</strong><u>ththh</u>awdi <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Lalu Kami wahyukan kepada Musa, “Pukullah laut itu dengan tongkatmu.” Maka terbelahlah lautan itu, dan setiap belahan seperti gunung yang besar.'),
	(2996, 26, 64, 'وَاَزْلَفْنَا ثَمَّ الْاٰخَرِيْنَ ۚ ', 'wa-azlafn<u>aa</u> tsamma <strong>a</strong>l-<u>aa</u>khariin<strong>a</strong>', 'Dan di sanalah Kami dekatkan golongan yang lain. '),
	(2997, 26, 65, 'وَاَنْجَيْنَا مُوْسٰى وَمَنْ مَّعَهٗٓ اَجْمَعِيْنَ ۚ ', 'wa-anjayn<u>aa</u> muus<u>aa</u> waman ma\'ahu ajma\'iin<strong>a</strong>', 'Dan Kami selamatkan Musa dan orang-orang yang bersamanya.'),
	(2998, 26, 66, 'ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ ۗ ', 'tsumma aghraqn<u>aa</u> <strong>a</strong>l-<u>aa</u>khariin<strong>a</strong>', 'Kemudian Kami tenggelamkan golongan yang lain.'),
	(2999, 26, 67, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu terdapat suatu tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3000, 26, 68, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sesungguhnya Tuhanmu Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(3001, 26, 69, 'وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰهِيْمَ ۘ ', 'wa<strong>u</strong>tlu \'alayhim naba-a ibr<u>aa</u>hiim<strong>a</strong>', 'Dan bacakanlah kepada mereka kisah Ibrahim.'),
	(3002, 26, 70, 'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا تَعْبُدُوْنَ ', 'i<u>dz</u> q<u>aa</u>la li-abiihi waqawmihi m<u>aa</u> ta\'buduun<strong>a</strong>', 'Ketika dia (Ibrahim) berkata kepada ayahnya dan kaumnya, “Apakah yang kamu sembah?”'),
	(3003, 26, 71, 'قَالُوْا نَعْبُدُ اَصْنَامًا فَنَظَلُّ لَهَا عٰكِفِيْنَ  ', 'q<u>aa</u>luu na\'budu a<u>sh</u>n<u>aa</u>man fana<u>zh</u>allu lah<u>aa</u> \'<u>aa</u>kifiin<strong>a</strong>', 'Mereka menjawab, “Kami menyembah berhala-berhala dan kami senantiasa tekun menyembahnya.”'),
	(3004, 26, 72, 'قَالَ هَلْ يَسْمَعُوْنَكُمْ اِذْ تَدْعُوْنَ ۙ  ', 'q<u>aa</u>la hal yasma\'uunakum i<u>dz</u> tad\'uun<strong>a</strong>', 'Dia (Ibrahim) berkata, “Apakah mereka mendengarmu ketika kamu berdoa (kepadanya)?'),
	(3005, 26, 73, 'اَوْ يَنْفَعُوْنَكُمْ اَوْ يَضُرُّوْنَ  ', 'aw yanfa\'uunakum aw ya<u>dh</u>urruun<strong>a</strong>', 'Atau (dapatkah) mereka memberi manfaat atau mencelakakan kamu?”'),
	(3006, 26, 74, 'قَالُوْا بَلْ وَجَدْنَآ اٰبَاۤءَنَا كَذٰلِكَ يَفْعَلُوْنَ  ', 'q<u>aa</u>luu bal wajadn<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> ka<u>dzaa</u>lika yaf\'aluun<strong>a</strong>', 'Mereka menjawab, “Tidak, tetapi kami dapati nenek moyang kami berbuat begitu.”'),
	(3007, 26, 75, 'قَالَ اَفَرَءَيْتُمْ مَّا كُنْتُمْ تَعْبُدُوْنَ ۙ ', 'q<u>aa</u>la afara-aytum m<u>aa</u> kuntum ta\'buduun<strong>a</strong>', 'Dia (Ibrahim) berkata, “Apakah kamu memperhatikan apa yang kamu sembah,'),
	(3008, 26, 76, 'اَنْتُمْ وَاٰبَاۤؤُكُمُ الْاَقْدَمُوْنَ ۙ  ', 'antum wa<u>aa</u>b<u>aa</u>ukumu <strong>a</strong>l-aqdamuun<strong>a</strong>', 'kamu dan nenek moyang kamu yang terdahulu?'),
	(3009, 26, 77, 'فَاِنَّهُمْ عَدُوٌّ لِّيْٓ اِلَّا رَبَّ الْعٰلَمِيْنَ ۙ ', 'fa-innahum \'aduwwun lii ill<u>aa</u> rabba <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Sesungguhnya mereka (apa yang kamu sembah) itu musuhku, lain halnya Tuhan seluruh alam,'),
	(3010, 26, 78, 'الَّذِيْ خَلَقَنِيْ فَهُوَ يَهْدِيْنِ ۙ ', '<strong>al</strong>la<u>dz</u>ii khalaqanii fahuwa yahdiin<strong>i</strong>', '(yaitu) Yang telah menciptakan aku, maka Dia yang memberi petunjuk kepadaku,'),
	(3011, 26, 79, 'وَالَّذِيْ هُوَ يُطْعِمُنِيْ وَيَسْقِيْنِ ۙ ', 'wa<strong>a</strong>lla<u>dz</u>ii huwa yu<u>th</u>\'imunii wayasqiin<strong>i</strong>', 'dan Yang memberi makan dan minum kepadaku;'),
	(3012, 26, 80, 'وَاِذَا مَرِضْتُ فَهُوَ يَشْفِيْنِ ۙ  ', 'wa-i<u>dzaa</u> mari<u>dh</u>tu fahuwa yasyfiin<strong>i</strong>', 'dan apabila aku sakit, Dialah yang menyembuhkan aku, '),
	(3013, 26, 81, 'وَالَّذِيْ يُمِيْتُنِيْ ثُمَّ يُحْيِيْنِ ۙ ', 'wa<strong>a</strong>lla<u>dz</u>ii yumiitunii tsumma yu<u>h</u>yiin<strong>i</strong>', 'dan Yang akan mematikan aku, kemudian akan menghidupkan aku (kembali),'),
	(3014, 26, 82, 'وَالَّذِيْٓ اَطْمَعُ اَنْ يَّغْفِرَ لِيْ خَطِيْۤـَٔتِيْ يَوْمَ الدِّيْنِ ۗ', 'wa<strong>a</strong>lla<u>dz</u>ii a<u>th</u>ma\'u an yaghfira lii kha<u>th</u>ii-atii yawma <strong>al</strong>ddiin<strong>i</strong>', 'dan Yang sangat kuinginkan akan mengampuni kesalahanku pada hari Kiamat.”'),
	(3015, 26, 83, 'رَبِّ هَبْ لِيْ حُكْمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ ۙ', 'rabbi hab lii <u>h</u>ukman wa-al<u>h</u>iqnii bi<strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Ibrahim berdoa), “Ya Tuhanku, berikanlah kepadaku ilmu dan masukkanlah aku ke dalam golongan orang-orang yang saleh,'),
	(3016, 26, 84, 'وَاجْعَلْ لِّيْ لِسَانَ صِدْقٍ فِى الْاٰخِرِيْنَ ۙ ', 'wa<strong>i</strong>j\'al lii lis<u>aa</u>na <u>sh</u>idqin fii <strong>a</strong>l-<u>aa</u>khiriin<strong>a</strong>', 'dan jadikanlah aku buah tutur yang baik bagi orang-orang (yang datang) kemudian,'),
	(3017, 26, 85, 'وَاجْعَلْنِيْ مِنْ وَّرَثَةِ جَنَّةِ النَّعِيْمِ ۙ', 'wa<strong>i</strong>j\'alnii min waratsati jannati <strong>al</strong>nna\'iim<strong>i</strong>', 'dan jadikanlah aku termasuk orang yang mewarisi surga yang penuh kenikmatan,'),
	(3018, 26, 86, 'وَاغْفِرْ لِاَبِيْٓ اِنَّهٗ كَانَ مِنَ الضَّاۤلِّيْنَ ۙ ', 'wa<strong>i</strong>ghfir li-abii innahu k<u>aa</u>na mina <strong>al</strong><u>dhdhaa</u>lliin<strong>a</strong>', 'dan ampunilah ayahku, sesungguhnya dia termasuk orang yang sesat,'),
	(3019, 26, 87, 'وَلَا تُخْزِنِيْ يَوْمَ يُبْعَثُوْنَۙ', 'wal<u>aa</u> ya<u>sh</u>uddunnaka \'an <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi ba\'da i<u>dz</u> unzilat ilayka wa<strong>u</strong>d\'u il<u>aa</u> rabbika wal<u>aa</u> takuunanna mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'dan janganlah Engkau hinakan aku pada hari mereka dibangkitkan,'),
	(3020, 26, 88, 'يَوْمَ لَا يَنْفَعُ مَالٌ وَّلَا بَنُوْنَ ۙ  ', 'wal<u>aa</u> tad\'u ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa kullu syay-in h<u>aa</u>likun ill<u>aa</u> wajhahu lahu <strong>a</strong>l<u>h</u>ukmu wa-ilayhi turja\'uun<strong>a</strong>', '(yaitu) pada hari (ketika) harta dan anak-anak  tidak berguna,'),
	(3021, 26, 89, 'اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَلِيْمٍ ۗ ', 'ill<u>aa</u> man at<u>aa</u> <strong>al</strong>l<u>aa</u>ha biqalbin saliim<strong>in</strong>', 'kecuali orang-orang yang menghadap Allah dengan hati yang bersih,'),
	(3022, 26, 90, 'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ ۙ ', 'wauzlifati <strong>a</strong>ljannatu lilmuttaqiin<strong>a</strong>', 'dan surga didekatkan kepada orang-orang yang bertakwa,'),
	(3023, 26, 91, 'وَبُرِّزَتِ الْجَحِيْمُ لِلْغٰوِيْنَ ۙ ', 'waburrizati <strong>a</strong>lja<u>h</u>iimu lilgh<u>aa</u>wiin<strong>a</strong>', 'dan neraka Jahim diperlihatkan dengan jelas kepada orang-orang yang sesat,”'),
	(3024, 26, 92, 'وَقِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُوْنَ ۙ ', 'waqiila lahum ayna m<u>aa</u> kuntum ta\'buduun<strong>a</strong>', 'dan dikatakan kepada mereka, “Di mana berhala-berhala yang dahulu kamu sembah,'),
	(3025, 26, 93, 'مِنْ دُوْنِ اللّٰهِ ۗهَلْ يَنْصُرُوْنَكُمْ اَوْ يَنْتَصِرُوْنَ ۗ  ', 'min duuni <strong>al</strong>l<u>aa</u>hi hal yan<u>sh</u>uruunakum aw yanta<u>sh</u>iruun<strong>a</strong>', 'selain Allah? Dapatkah mereka menolong kamu atau menolong diri mereka sendiri?”'),
	(3026, 26, 94, 'فَكُبْكِبُوْا فِيْهَا هُمْ وَالْغَاوٗنَ ۙ ', 'fakubkibuu fiih<u>aa</u> hum wa<strong>a</strong>lgh<u>aa</u>wuun<strong>a</strong>', 'Maka mereka (sesembahan itu) dijungkirkan ke dalam neraka bersama orang-orang yang sesat,'),
	(3027, 26, 95, 'وَجُنُوْدُ اِبْلِيْسَ اَجْمَعُوْنَ ۗ  ', 'wajunuudu ibliisa ajma\'uun<strong>a</strong>', 'dan bala tentara Iblis semuanya.'),
	(3028, 26, 96, 'قَالُوْا وَهُمْ فِيْهَا يَخْتَصِمُوْنَ  ', 'q<u>aa</u>luu wahum fiih<u>aa</u> yakhta<u>sh</u>imuun<strong>a</strong>', 'Mereka berkata sambil bertengkar di dalamnya (neraka),'),
	(3029, 26, 97, 'تَاللّٰهِ اِنْ كُنَّا لَفِيْ ضَلٰلٍ مُّبِيْنٍ ۙ ', 'ta<strong>al</strong>l<u>aa</u>hi in kunn<u>aa</u> lafii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', '”Demi Allah, sesungguhnya kita dahulu (di dunia) dalam kesesatan yang nyata,'),
	(3030, 26, 98, 'اِذْ نُسَوِّيْكُمْ بِرَبِّ الْعٰلَمِيْنَ ', 'i<u>dz</u> nusawwiikum birabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'karena kita mempersamakan kamu (berhala-berhala) dengan Tuhan seluruh alam.'),
	(3031, 26, 99, 'وَمَآ اَضَلَّنَآ اِلَّا الْمُجْرِمُوْنَ ', 'wam<u>aa</u> a<u>dh</u>allan<u>aa</u> ill<u>aa</u> <strong>a</strong>lmujrimuun<strong>a</strong>', 'Dan tidak ada yang menyesatkan kita kecuali orang-orang yang berdosa.'),
	(3032, 26, 100, 'فَمَا لَنَا مِنْ شَافِعِيْنَ ۙ ', 'fam<u>aa</u> lan<u>aa</u> min sy<u>aa</u>fi\'iin<strong>a</strong>', 'Maka sehingga (sekarang) kita tidak mempunyai pemberi syafaat (penolong),'),
	(3033, 26, 101, 'وَلَا صَدِيْقٍ حَمِيْمٍ  ', 'wal<u>aa</u> <u>sh</u>adiiqin <u>h</u>amiim<strong>in</strong>', 'dan tidak pula mempunyai teman yang akrab,'),
	(3034, 26, 102, 'فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ  ', 'falaw anna lan<u>aa</u> karratan fanakuuna mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Maka seandainya kita dapat kembali (ke dunia) niscaya kita menjadi orang-orang yang beriman.”'),
	(3035, 26, 103, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3036, 26, 104, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu benar-benar Dialah Mahaperkasa, Maha Penyayang.'),
	(3037, 26, 105, 'كَذَّبَتْ قَوْمُ نُوْحِ ِۨالْمُرْسَلِيْنَ ۚ ', 'ka<u>dzdz</u>abat qawmu nuu<u>h</u>in <strong>a</strong>lmursaliin<strong>a</strong>', 'Kaum Nuh telah mendustakan para rasul.'),
	(3038, 26, 106, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ نُوْحٌ اَلَا تَتَّقُوْنَ ۚ ', 'i<u>dz</u> q<u>aa</u>la lahum akhuuhum nuu<u>h</u>un <strong>a</strong>l<u>aa</u> tattaquun<strong>a</strong>', 'Ketika saudara mereka (Nuh) berkata kepada mereka, “Mengapa kamu tidak bertakwa?'),
	(3039, 26, 107, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ  ', 'innii lakum rasuulun amiin<strong>un</strong>', 'Sesungguhnya aku ini seorang rasul kepercayaan (yang diutus) kepadamu,'),
	(3040, 26, 108, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ  ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'Maka bertakwalah kamu kepada Allah dan taatlah kepadaku.'),
	(3041, 26, 109, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۚ ', 'wam<u>aa</u> as-alukum \'alayhi min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam.'),
	(3042, 26, 110, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ  ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'Maka bertakwalah kamu kepada Allah dan taatlah kepadaku.”'),
	(3043, 26, 111, '۞ قَالُوْٓا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُوْنَ ۗ ', 'q<u>aa</u>luu anu/minu laka wa<strong>i</strong>ttaba\'aka <strong>a</strong>l-ar<u>dz</u>aluun<strong>a</strong>', 'Mereka berkata, “Apakah kami harus beriman kepadamu, padahal pengikut-pengikutmu orang-orang yang hina?”'),
	(3044, 26, 112, 'قَالَ وَمَا عِلْمِيْ بِمَا كَانُوْا يَعْمَلُوْنَ ۚ  ', 'q<u>aa</u>la wam<u>aa</u> \'ilmii bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dia (Nuh) menjawab, “Tidak ada pengetahuanku tentang apa yang mereka kerjakan.'),
	(3045, 26, 113, 'اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبِّيْ لَوْ تَشْعُرُوْنَ ۚ  ', 'in <u>h</u>is<u>aa</u>buhum ill<u>aa</u> \'al<u>aa</u> rabbii law tasy\'uruun<strong>a</strong>', 'Perhitungan (amal perbuatan) mereka tidak lain hanyalah kepada Tuhanku, jika kamu menyadari.'),
	(3046, 26, 114, 'وَمَآ اَنَا۠ بِطَارِدِ الْمُؤْمِنِيْنَ ۚ ', 'wam<u>aa</u> an<u>aa</u> bi<u>th</u><u>aa</u>ridi <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan aku tidak akan mengusir orang-orang yang beriman.'),
	(3047, 26, 115, 'اِنْ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ۗ ', 'in an<u>aa</u> ill<u>aa</u> na<u>dz</u>iirun mubiin<strong>un</strong>', 'Aku (ini) hanyalah pemberi peringatan yang jelas.”'),
	(3048, 26, 116, 'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰنُوْحُ لَتَكُوْنَنَّ مِنَ الْمَرْجُوْمِيْنَۗ  ', 'q<u>aa</u>luu la-in lam tantahi y<u>aa</u> nuu<u>h</u>u latakuunanna mina <strong>a</strong>lmarjuumiin<strong>a</strong>', 'Mereka berkata, “Wahai Nuh! Sungguh, jika engkau tidak (mau) berhenti, niscaya engkau termasuk orang yang dirajam (dilempari batu sampai mati).”'),
	(3049, 26, 117, 'قَالَ رَبِّ اِنَّ قَوْمِيْ كَذَّبُوْنِۖ ', 'q<u>aa</u>la rabbi inna qawmii ka<u>dzdz</u>abuun<strong>i</strong>', 'Dia (Nuh) berkata, “Ya Tuhanku, sungguh kaumku telah mendustakan aku;'),
	(3050, 26, 118, 'فَافْتَحْ بَيْنِيْ وَبَيْنَهُمْ فَتْحًا وَّنَجِّنِيْ وَمَنْ مَّعِيَ مِنَ الْمُؤْمِنِيْنَ  ', 'fa<strong>i</strong>fta<u>h</u> baynii wabaynahum fat<u>h</u>an wanajjinii waman ma\'iya mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'maka berilah keputusan antara aku dengan mereka, dan selamatkanlah aku dan mereka yang beriman bersamaku.” '),
	(3051, 26, 119, 'فَاَنْجَيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ الْمَشْحُوْنِ  ', 'fa-anjayn<u>aa</u>hu waman ma\'ahu fii <strong>a</strong>lfulki <strong>a</strong>lmasyhuun<strong>i</strong>', 'Kemudian Kami menyelamatkannya Nuh dan orang-orang yang bersamanya di dalam kapal yang penuh muatan.'),
	(3052, 26, 120, 'ثُمَّ اَغْرَقْنَا بَعْدُ الْبٰقِيْنَ  ', 'tsumma aghraqn<u>aa</u> ba\'du <strong>a</strong>lb<u>aa</u>qiin<strong>a</strong>', 'Kemudian setelah itu Kami tenggelamkan orang-orang yang tinggal.'),
	(3053, 26, 121, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3054, 26, 122, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(3055, 26, 123, 'كَذَّبَتْ عَادُ ِۨالْمُرْسَلِيْنَ ۖ ', 'ka<u>dzdz</u>abat \'<u>aa</u>dun <strong>a</strong>lmursaliin<strong>a</strong>', '(Kaum) ‘Ad telah mendustakan para rasul.'),
	(3056, 26, 124, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ هُوْدٌ اَلَا تَتَّقُوْنَ ۚ ', 'i<u>dz</u> q<u>aa</u>la lahum akhuuhum huudun <strong>a</strong>l<u>aa</u> tattaquun<strong>a</strong>', 'Ketika saudara mereka Hud berkata kepada mereka, “Mengapa kamu tidak bertakwa?'),
	(3057, 26, 125, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ', 'innii lakum rasuulun amiin<strong>un</strong>', 'Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,'),
	(3058, 26, 126, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'karena itu bertakwalah kepada Allah dan taatlah kepadaku.'),
	(3059, 26, 127, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ', 'wam<u>aa</u> as-alukum \'alayhi min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam.'),
	(3060, 26, 128, 'اَتَبْنُوْنَ بِكُلِّ رِيْعٍ اٰيَةً تَعْبَثُوْنَ ۙ ', 'atabnuuna bikulli rii\'in <u>aa</u>yatan ta\'batsuun<strong>a</strong>', 'Apakah kamu mendirikan istana-istana pada setiap tanah yang tinggi untuk kemegahan tanpa ditempati, '),
	(3061, 26, 129, 'وَتَتَّخِذُوْنَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُوْنَۚ  ', 'watattakhi<u>dz</u>uuna ma<u>shaa</u>ni\'a la\'allakum takhluduun<strong>a</strong>', 'dan kamu membuat benteng-benteng dengan harapan kamu hidup kekal?'),
	(3062, 26, 130, 'وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِيْنَۚ  ', 'wa-i<u>dzaa</u> ba<u>th</u>asytum ba<u>th</u>asytum jabb<u>aa</u>riin<strong>a</strong>', 'Dan apabila kamu menyiksa, maka kamu lakukan secara kejam dan bengis.'),
	(3063, 26, 131, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ  ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'Maka bertakwalah kepada Allah dan taatlah kepadaku,'),
	(3064, 26, 132, 'وَاتَّقُوا الَّذِيْٓ اَمَدَّكُمْ بِمَا تَعْلَمُوْنَ  ۚ', 'wa<strong>i</strong>ttaquu <strong>al</strong>la<u>dz</u>ii amaddakum bim<u>aa</u> ta\'lamuun<strong>a</strong>', 'dan tetaplah kamu bertakwa kepada-Nya yang telah menganugerahkan kepadamu apa yang kamu ketahui.'),
	(3065, 26, 133, 'اَمَدَّكُمْ بِاَنْعَامٍ وَّبَنِيْنَۙ  ', 'amaddakum bi-an\'<u>aa</u>min wabaniin<strong>a</strong>', 'Dia (Allah) telah menganugerahkan kepadamu hewan ternak dan anak-anak,'),
	(3066, 26, 134, 'وَجَنّٰتٍ وَّعُيُوْنٍۚ  ', 'wajann<u>aa</u>tin wa\'uyuun<strong>in</strong>', 'dan kebun-kebun, dan mata air,'),
	(3067, 26, 135, 'اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ۗ ', 'innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim<strong>in</strong>', 'sesungguhnya aku takut kamu akan ditimpa azab pada hari yang besar.” '),
	(3068, 26, 136, 'قَالُوْا سَوَاۤءٌ عَلَيْنَآ اَوَعَظْتَ اَمْ لَمْ تَكُنْ مِّنَ الْوَاعِظِيْنَ ۙ ', 'q<u>aa</u>luu saw<u>aa</u>un \'alayn<u>aa</u> awa\'a<u>zh</u>ta am lam takun mina <strong>a</strong>lw<u>aa</u>\'i<u>zh</u>iin<strong>a</strong>', 'Mereka menjawab, “Sama saja bagi kami, apakah engkau memberi nasihat atau tidak memberi nasihat,'),
	(3069, 26, 137, 'اِنْ هٰذَآ اِلَّا خُلُقُ الْاَوَّلِيْنَ ۙ ', 'in h<u>aadzaa</u> ill<u>aa</u> khuluqu <strong>a</strong>l-awwaliin<strong>a</strong>', '(agama kami) ini tidak lain hanyalah adat kebiasaan orang-orang terdahulu,'),
	(3070, 26, 138, 'وَمَا نَحْنُ بِمُعَذَّبِيْنَ ۚ ', 'wam<u>aa</u> na<u>h</u>nu bimu\'a<u>dzdz</u>abiin<strong>a</strong>', 'dan kami (sama sekali) tidak akan diazab.”'),
	(3071, 26, 139, 'فَكَذَّبُوْهُ فَاَهْلَكْنٰهُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'faka<u>dzdz</u>abuuhu fa-ahlakn<u>aa</u>hum inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Maka mereka mendustakannya (Hud), lalu Kami binasakan mereka. Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3072, 26, 140, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(3073, 26, 141, 'كَذَّبَتْ ثَمُوْدُ الْمُرْسَلِيْنَ  ۖ ', 'ka<u>dzdz</u>abat tsamuudu <strong>a</strong>lmursaliin<strong>a</strong>', 'Kaum Samud telah mendustakan para rasul.'),
	(3074, 26, 142, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ صٰلِحٌ اَلَا تَتَّقُوْنَ ۚ ', 'i<u>dz</u> q<u>aa</u>la lahum akhuuhum <u>shaa</u>li<u>h</u>un <strong>a</strong>l<u>aa</u> tattaquun<strong>a</strong>', 'Ketika saudara mereka Saleh berkata kepada mereka, “Mengapa kamu tidak bertakwa?'),
	(3075, 26, 143, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ  ', 'innii lakum rasuulun amiin<strong>un</strong>', 'Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,'),
	(3076, 26, 144, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ  ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'karena itu bertakwalah kepada Allah dan taatlah kepadaku.'),
	(3077, 26, 145, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ', 'wam<u>aa</u> as-alukum \'alayhi min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan aku tidak meminta sesuatu imbalan kepadamu atas ajakan itu, imbalanku hanyalah dari Tuhan seluruh alam.'),
	(3078, 26, 146, 'اَتُتْرَكُوْنَ فِيْ مَا هٰهُنَآ اٰمِنِيْنَ ۙ ', 'atutrakuuna fii m<u>aa</u> h<u>aa</u>hun<u>aa</u> <u>aa</u>miniin<strong>a</strong>', 'Apakah kamu (mengira) akan dibiarkan tinggal di sini (di negeri kamu ini) dengan aman,'),
	(3079, 26, 147, 'فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ', 'fii jann<u>aa</u>tin wa\'uyuun<strong>in</strong>', 'di dalam kebun-kebun dan mata air,'),
	(3080, 26, 148, 'وَّزُرُوْعٍ وَّنَخْلٍ طَلْعُهَا هَضِيْمٌ ۚ ', 'wazuruu\'in wanakhlin <u>th</u>al\'uh<u>aa</u> ha<u>dh</u>iim<strong>un</strong>', 'dan tanaman-tanaman dan pohon-pohon kurma yang mayangnya lembut.'),
	(3081, 26, 149, 'وَتَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا فٰرِهِيْنَ ', 'watan<u>h</u>ituuna mina <strong>a</strong>ljib<u>aa</u>li buyuutan f<u>aa</u>rihiin<strong>a</strong>', 'Dan kamu pahat dengan terampil sebagian gunung-gunung untuk dijadikan rumah-rumah;'),
	(3082, 26, 150, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'maka bertakwalah kepada Allah dan taatlah kepadaku;'),
	(3083, 26, 151, 'وَلَا تُطِيْعُوْٓا اَمْرَ الْمُسْرِفِيْنَ ۙ ', 'wal<u>aa</u> tu<u>th</u>ii\'uu amra <strong>a</strong>lmusrifiin<strong>a</strong>', 'dan janganlah kamu menaati perintah orang-orang yang melampaui batas,'),
	(3084, 26, 152, 'الَّذِيْنَ يُفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ  ', '<strong>al</strong>la<u>dz</u>iina yufsiduuna fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> yu<u>sh</u>li<u>h</u>uun<strong>a</strong>', 'yang berbuat kerusakan di bumi dan tidak mengadakan perbaikan.”'),
	(3085, 26, 153, 'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ', 'q<u>aa</u>luu innam<u>aa</u> anta mina <strong>a</strong>lmusa<u>hh</u>ariin<strong>a</strong>', 'Mereka berkata, “Sungguh, engkau hanyalah termasuk orang yang kena sihir;'),
	(3086, 26, 154, 'مَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَاۙ فَأْتِ بِاٰيَةٍ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ  ', 'm<u>aa</u> anta ill<u>aa</u> basyarun mitslun<u>aa</u> fa/ti bi-<u>aa</u>yatin in kunta mina <strong>al</strong><u>shshaa</u>diqiinaa', 'engkau hanyalah manusia seperti kami; maka datangkanlah sesuatu mukjizat jika engkau termasuk orang yang benar.”'),
	(3087, 26, 155, 'قَالَ هٰذِهٖ نَاقَةٌ لَّهَا شِرْبٌ وَّلَكُمْ شِرْبُ يَوْمٍ مَّعْلُوْمٍ ۚ ', 'q<u>aa</u>la h<u>aadz</u>ihi n<u>aa</u>qatun lah<u>aa</u> syirbun walakum syirbu yawmin ma\'luum<strong>in</strong>', 'Dia (Saleh) menjawab, “Ini seekor unta betina, yang berhak mendapatkan (giliran) minum, dan kamu juga berhak mendapatkan minum pada hari yang ditentukan.'),
	(3088, 26, 156, 'وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيْمٍ  ', 'wal<u>aa</u> tamassuuh<u>aa</u> bisuu-in faya/khu<u>dz</u>akum \'a<u>dzaa</u>bu yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Dan jangan kamu menyentuhnya (unta itu) dengan sesuatu kejahatan, nanti kamu akan ditimpa azab pada hari yang dahsyat.”'),
	(3089, 26, 157, 'فَعَقَرُوْهَا فَاَصْبَحُوْا نٰدِمِيْنَ ۙ ', 'fa\'aqaruuh<u>aa</u> fa-a<u>sh</u>ba<u>h</u>uu n<u>aa</u>dimiin<strong>a</strong>', 'Kemudian mereka membunuhnya, lalu mereka merasa menyesal,'),
	(3090, 26, 158, 'فَاَخَذَهُمُ الْعَذَابُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'fa-akha<u>dz</u>ahumu <strong>a</strong>l\'a<u>dzaa</u>bu inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'maka mereka ditimpa azab. Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3091, 26, 159, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(3092, 26, 160, 'كَذَّبَتْ قَوْمُ لُوْطِ ِۨالْمُرْسَلِيْنَ ۖ ', 'ka<u>dzdz</u>abat qawmu luu<u>th</u>in <strong>a</strong>lmursaliin<strong>a</strong>', 'Kaum Lut telah mendustakan para rasul,'),
	(3093, 26, 161, 'اِذْ قَالَ لَهُمْ اَخُوْهُمْ لُوْطٌ اَلَا تَتَّقُوْنَ ۚ  ', 'i<u>dz</u> q<u>aa</u>la lahum akhuuhum luu<u>th</u>un <strong>a</strong>l<u>aa</u> tattaquun<strong>a</strong>', 'ketika saudara mereka Lut berkata kepada mereka, “Mengapa kamu tidak bertakwa?”'),
	(3094, 26, 162, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ', 'innii lakum rasuulun amiin<strong>un</strong>', 'Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,'),
	(3095, 26, 163, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'maka bertakwalah kepada Allah dan taatlah kepadaku.'),
	(3096, 26, 164, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ', 'wam<u>aa</u> as-alukum \'alayhi min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam.'),
	(3097, 26, 165, 'اَتَأْتُوْنَ الذُّكْرَانَ مِنَ الْعٰلَمِيْنَ ۙ ', 'ata/tuuna <strong>al</strong><u>dzdz</u>ukr<u>aa</u>na mina <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Mengapa kamu mendatangi jenis laki-laki di antara manusia (berbuat homoseks),'),
	(3098, 26, 166, 'وَتَذَرُوْنَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِّنْ اَزْوَاجِكُمْۗ بَلْ اَنْتُمْ قَوْمٌ عٰدُوْنَ  ', 'wata<u>dz</u>aruuna m<u>aa</u> khalaqa lakum rabbukum min azw<u>aa</u>jikum bal antum qawmun \'<u>aa</u>duun<strong>a</strong>', 'dan kamu tinggalkan (perempuan) yang diciptakan Tuhan untuk menjadi istri-istri kamu? Kamu (memang) orang-orang yang melampaui batas.” '),
	(3099, 26, 167, 'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰلُوْطُ لَتَكُوْنَنَّ مِنَ الْمُخْرَجِيْنَ  ', 'q<u>aa</u>luu la-in lam tantahi y<u>aa</u> luu<u>th</u>u latakuunanna mina <strong>a</strong>lmukhrajiin<strong>a</strong>', 'Mereka menjawab, “Wahai Lut! Jika engkau tidak berhenti, engkau termasuk orang-orang yang terusir.”'),
	(3100, 26, 168, 'قَالَ ِانِّيْ لِعَمَلِكُمْ مِّنَ الْقَالِيْنَ  ۗ', 'q<u>aa</u>la innii li\'amalikum mina <strong>a</strong>lq<u>aa</u>liin<strong>a</strong>', 'Dia (Lut) berkata, “Aku sungguh benci kepada perbuatanmu.”'),
	(3101, 26, 169, 'رَبِّ نَجِّنِيْ وَاَهْلِيْ مِمَّا يَعْمَلُوْنَ  ', 'rabbi najjinii wa-ahlii mimm<u>aa</u> ya\'maluun<strong>a</strong>', '(Lut berdoa), “Ya Tuhanku, selamatkanlah aku dan keluargaku dari (akibat) perbuatan yang mereka kerjakan.”'),
	(3102, 26, 170, 'فَنَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْنَ ۙ ', 'fanajjayn<u>aa</u>hu wa-ahlahu ajma\'iin<strong>a</strong>', 'Lalu Kami selamatkan dia bersama keluarganya semua,'),
	(3103, 26, 171, 'اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ ۚ  ', 'ill<u>aa</u> \'ajuuzan fii <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'kecuali seorang perempuan tua (istrinya), yang termasuk dalam golongan yang tinggal.'),
	(3104, 26, 172, 'ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ ۚ ', 'tsumma dammarn<u>aa</u> <strong>a</strong>l-<u>aa</u>khariin<strong>a</strong>', 'Kemudian Kami binasakan yang lain.'),
	(3105, 26, 173, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ', 'wa-am<u>th</u>arn<u>aa</u> \'alayhim ma<u>th</u>aran fas<u>aa</u>-a ma<u>th</u>aru <strong>a</strong>lmun<u>dz</u>ariin<strong>a</strong>', 'Dan Kami hujani mereka (dengan hujan batu), maka betapa buruk hujan yang menimpa orang-orang yang telah diberi peringatan itu.'),
	(3106, 26, 174, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3107, 26, 175, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang.'),
	(3108, 26, 176, 'كَذَّبَ اَصْحٰبُ لْـَٔيْكَةِ الْمُرْسَلِيْنَ ۖ ', 'ka<u>dzdz</u>aba a<u>sh</u>-<u>haa</u>bu <strong>a</strong>l-aykati <strong>a</strong>lmursaliin<strong>a</strong>', 'Penduduk Aikah telah mendustakan para rasul;'),
	(3109, 26, 177, 'اِذْ قَالَ لَهُمْ شُعَيْبٌ اَلَا تَتَّقُوْنَ ۚ ', 'i<u>dz</u> q<u>aa</u>la lahum syu\'aybun <strong>a</strong>l<u>aa</u> tattaquun<strong>a</strong>', 'ketika Syuaib berkata kepada mereka, “Mengapa kamu tidak bertakwa?'),
	(3110, 26, 178, 'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ', 'innii lakum rasuulun amiin<strong>un</strong>', 'Sungguh, aku adalah rasul kepercayaan (yang diutus) kepadamu,'),
	(3111, 26, 179, 'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ', 'fa<strong>i</strong>ttaquu <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>ii\'uun<strong>i</strong>', 'maka bertakwalah kepada Allah dan taatlah kepadaku;'),
	(3112, 26, 180, 'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ', 'wam<u>aa</u> as-alukum \'alayhi min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u> rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam.'),
	(3113, 26, 181, '۞ اَوْفُوا الْكَيْلَ وَلَا تَكُوْنُوْا مِنَ الْمُخْسِرِيْنَ ۚ ', 'awfuu <strong>a</strong>lkayla wal<u>aa</u> takuunuu mina <strong>a</strong>lmukhsiriin<strong>a</strong>', 'Sempurnakanlah takaran dan janganlah kamu merugikan orang lain.'),
	(3114, 26, 182, 'وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِ ۚ ', 'wazinuu bi<strong>a</strong>lqis<u>th</u><u>aa</u>si <strong>a</strong>lmustaqiim<strong>i</strong>', 'Dan timbanglah dengan timbangan yang benar.'),
	(3115, 26, 183, 'وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۚ ', 'wal<u>aa</u> tabkhasuu <strong>al</strong>nn<u>aa</u>sa asyy<u>aa</u>-ahum wal<u>aa</u> ta\'tsaw fii <strong>a</strong>l-ar<u>dh</u>i mufsidiin<strong>a</strong>', 'Dan janganlah kamu merugikan manusia dengan mengurangi hak-haknya dan janganlah membuat kerusakan di bumi;'),
	(3116, 26, 184, 'وَاتَّقُوا الَّذِيْ خَلَقَكُمْ وَالْجِبِلَّةَ الْاَوَّلِيْنَ ۗ ', 'wa<strong>i</strong>ttaquu <strong>al</strong>la<u>dz</u>ii khalaqakum wa<strong>a</strong>ljibillata <strong>a</strong>l-awwaliin<strong>a</strong>', 'dan bertakwalah kepada Allah yang telah menciptakan kamu dan umat-umat yang terdahulu.”'),
	(3117, 26, 185, 'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ', 'q<u>aa</u>luu innam<u>aa</u> anta mina <strong>a</strong>lmusa<u>hh</u>ariin<strong>a</strong>', 'Mereka berkata, “Engkau tidak lain hanyalah orang-orang yang kena sihir.'),
	(3118, 26, 186, 'وَمَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَا وَاِنْ نَّظُنُّكَ لَمِنَ الْكٰذِبِيْنَ ۚ ', 'wam<u>aa</u> anta ill<u>aa</u> basyarun mitslun<u>aa</u> wa-in na<u>zh</u>unnuka lamina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dan engkau hanyalah manusia seperti kami, dan sesungguhnya kami yakin engkau termasuk orang-orang yang berdusta.'),
	(3119, 26, 187, 'فَاَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ السَّمَاۤءِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ۗ ', 'fa-asqi<u>th</u> \'alayn<u>aa</u> kisafan mina <strong>al</strong>ssam<u>aa</u>-i in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Maka jatuhkanlah kepada kami gumpalan dari langit, jika engkau termasuk orang-orang yang benar.”'),
	(3120, 26, 188, 'قَالَ رَبِّيْٓ اَعْلَمُ بِمَا تَعْمَلُوْنَ  ', 'q<u>aa</u>la rabbii a\'lamu bim<u>aa</u> ta\'maluun<strong>a</strong>', 'Dia (Syuaib) berkata, “Tuhanku lebih mengetahui apa yang kamu kerjakan.”'),
	(3121, 26, 189, 'فَكَذَّبُوْهُ فَاَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۗاِنَّهٗ كَانَ عَذَابَ يَوْمٍ عَظِيْمٍ ', 'faka<u>dzdz</u>abuuhu fa-akha<u>dz</u>ahum \'a<u>dzaa</u>bu yawmi <strong>al</strong><u>zhzh</u>ullati innahu k<u>aa</u>na \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim<strong>in</strong>', 'Kemudian mereka mendustakannya (Syuaib), lalu mereka ditimpa azab pada hari yang gelap. Sungguh, itulah azab pada hari yang dahsyat.'),
	(3122, 26, 190, 'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ', 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin<strong>a</strong>', 'Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
	(3123, 26, 191, 'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ', 'wa-inna rabbaka lahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dan sungguh, Tuhanmu, Dialah yang Mahaperkasa, Maha Penyayang.'),
	(3124, 26, 192, 'وَاِنَّهٗ لَتَنْزِيْلُ رَبِّ الْعٰلَمِيْنَ ۗ  ', 'wa-innahu latanziilu rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan sungguh, (Al-Qur\'an) ini benar-benar diturunkan oleh Tuhan seluruh alam,'),
	(3125, 26, 193, 'نَزَلَ بِهِ الرُّوْحُ الْاَمِيْنُ ۙ ', 'nazala bihi <strong>al</strong>rruu<u>h</u>u <strong>a</strong>l-amiin<strong>u</strong>', 'Yang dibawa turun oleh ar-Ruh al-Amin (Jibril), '),
	(3126, 26, 194, 'عَلٰى قَلْبِكَ لِتَكُوْنَ مِنَ الْمُنْذِرِيْنَ ۙ  ', '\'al<u>aa</u> qalbika litakuuna mina <strong>a</strong>lmun<u>dz</u>iriin<strong>a</strong>', 'ke dalam hatimu (Muhammad) agar engkau termasuk orang yang memberi peringatan,'),
	(3127, 26, 195, 'بِلِسَانٍ عَرَبِيٍّ مُّبِيْنٍ ۗ ', 'bilis<u>aa</u>nin \'arabiyyin mubiin<strong>in</strong>', 'dengan bahasa Arab yang jelas.'),
	(3128, 26, 196, 'وَاِنَّهٗ لَفِيْ زُبُرِ الْاَوَّلِيْنَ  ', 'wa-innahu lafii zuburi <strong>a</strong>l-awwaliin<strong>a</strong>', 'Dan sungguh, (Al-Qur\'an) itu (disebut) dalam kitab-kitab orang yang terdahulu.'),
	(3129, 26, 197, 'اَوَلَمْ يَكُنْ لَّهُمْ اٰيَةً اَنْ يَّعْلَمَهٗ عُلَمٰۤؤُا بَنِيْٓ اِسْرَاۤءِيْلَ  ', 'awa lam yakun lahum <u>aa</u>yatan an ya\'lamahu \'ulam<u>aa</u>u banii isr<u>aa</u>-iil<strong>a</strong>', 'Apakah tidak (cukup) menjadi bukti bagi mereka, bahwa para ulama Bani Israil mengetahuinya?'),
	(3130, 26, 198, 'وَلَوْ نَزَّلْنٰهُ عَلٰى بَعْضِ الْاَعْجَمِيْنَ ۙ ', 'walaw nazzaln<u>aa</u>hu \'al<u>aa</u> ba\'<u>dh</u>i <strong>a</strong>l-a\'jamiin<strong>a</strong>', 'Dan seandainya (Al-Qur\'an) itu Kami turunkan kepada sebagian dari golongan bukan Arab,'),
	(3131, 26, 199, 'فَقَرَاَهٗ عَلَيْهِمْ مَّا كَانُوْا بِهٖ مُؤْمِنِيْنَ ۗ  ', 'faqara-ahu \'alayhim m<u>aa</u> k<u>aa</u>nuu bihi mu/miniin<strong>a</strong>', 'lalu dia membacakannya kepada mereka (orang-orang kafir); niscaya mereka tidak juga akan beriman kepadanya.'),
	(3132, 26, 200, 'كَذٰلِكَ سَلَكْنٰهُ فِيْ قُلُوْبِ الْمُجْرِمِيْنَ ۗ  ', 'ka<u>dzaa</u>lika salakn<u>aa</u>hu fii quluubi <strong>a</strong>lmujrimiin<strong>a</strong>', 'Demikianlah, Kami masukkan (sifat dusta dan ingkar) ke dalam hati orang-orang yang berdosa,'),
	(3133, 26, 201, 'لَا يُؤْمِنُوْنَ بِهٖ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ  ', 'l<u>aa</u> yu/minuuna bihi <u>h</u>att<u>aa</u> yarawuu <strong>a</strong>l\'a<u>dzaa</u>ba <strong>a</strong>l-aliim<strong>a</strong>', 'mereka tidak akan beriman kepadanya, hingga mereka melihat azab yang pedih,'),
	(3134, 26, 202, 'فَيَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ۙ  ', 'faya/tiyahum baghtatan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'maka datang azab kepada mereka secara mendadak, ketika mereka tidak menyadarinya,'),
	(3135, 26, 203, 'فَيَقُوْلُوْا هَلْ نَحْنُ مُنْظَرُوْنَ ۗ ', 'fayaquuluu hal na<u>h</u>nu mun<u>zh</u>aruun<strong>a</strong>', 'lalu mereka berkata, “Apakah kami diberi penangguhan waktu?” '),
	(3136, 26, 204, 'اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ  ', 'afabi\'a<u>dzaa</u>bin<u>aa</u> yasta\'jiluun<strong>a</strong>', 'Bukankah mereka yang meminta agar azab Kami dipercepat?'),
	(3137, 26, 205, 'اَفَرَءَيْتَ اِنْ مَّتَّعْنٰهُمْ سِنِيْنَ ۙ  ', 'afara-ayta in matta\'n<u>aa</u>hum siniin<strong>a</strong>', 'Maka bagaimana pendapatmu jika kepada mereka Kami berikan kenikmatan hidup beberapa tahun,'),
	(3138, 26, 206, 'ثُمَّ جَاۤءَهُمْ مَّا كَانُوْا يُوْعَدُوْنَ ۙ  ', 'tsumma j<u>aa</u>-ahum m<u>aa</u> k<u>aa</u>nuu yuu\'aduun<strong>a</strong>', 'kemudian datang kepada mereka azab yang diancamkan kepada mereka,'),
	(3139, 26, 207, 'مَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يُمَتَّعُوْنَ ۗ ', 'm<u>aa</u> aghn<u>aa</u> \'anhum m<u>aa</u> k<u>aa</u>nuu yumatta\'uun<strong>a</strong>', 'niscaya tidak berguna bagi mereka kenikmatan yang mereka rasakan.'),
	(3140, 26, 208, 'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُوْنَ ۖ  ', 'wam<u>aa</u> ahlakn<u>aa</u> min qaryatin ill<u>aa</u> lah<u>aa</u> mun<u>dz</u>iruun<strong>a</strong>', 'Dan Kami tidak membinasakan sesuatu negeri, kecuali setelah ada orang-orang yang memberi peringatan kepadanya;'),
	(3141, 26, 209, 'ذِكْرٰىۚ وَمَا كُنَّا ظٰلِمِيْنَ  ', '<u>dz</u>ikr<u>aa</u> wam<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin<strong>a</strong>', 'untuk (menjadi) peringatan. Dan Kami tidak berlaku zalim. '),
	(3142, 26, 210, 'وَمَا تَنَزَّلَتْ بِهِ الشَّيٰطِيْنُ  ', 'wam<u>aa</u> tanazzalat bihi <strong>al</strong>sysyay<u>aath</u>iin<strong>u</strong>', 'Dan (Al-Qur\'an) itu tidaklah dibawa turun oleh setan-setan.'),
	(3143, 26, 211, 'وَمَا يَنْۢبَغِيْ لَهُمْ وَمَا يَسْتَطِيْعُوْنَ ۗ  ', 'wam<u>aa</u> yanbaghii lahum wam<u>aa</u> yasta<u>th</u>ii\'uun<strong>a</strong>', 'Dan tidaklah pantas bagi mereka (Al-Qur\'an itu), dan mereka pun tidak akan sanggup.'),
	(3144, 26, 212, 'اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُوْلُوْنَ ۗ ', 'innahum \'ani <strong>al</strong>ssam\'i lama\'zuuluuna', 'Sesungguhnya untuk mendengarkannya pun mereka dijauhkan.'),
	(3145, 26, 213, 'فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَكُوْنَ مِنَ الْمُعَذَّبِيْنَ', 'fal<u>aa</u> tad\'u ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fatakuuna mina <strong>a</strong>lmu\'a<u>dzdz</u>abiin<strong>a</strong>', 'Maka janganlah kamu menyeru (menyembah) tuhan selain Allah, nanti kamu termasuk orang-orang yang diazab.'),
	(3146, 26, 214, 'وَاَنْذِرْ عَشِيْرَتَكَ الْاَقْرَبِيْنَ ۙ ', 'wa-an<u>dz</u>ir \'asyiirataka <strong>a</strong>l-aqrabiin<strong>a</strong>', 'Dan berilah peringatan kepada kerabat-kerabatmu (Muhammad) yang terdekat,'),
	(3147, 26, 215, 'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ۚ  ', 'wa<strong>i</strong>khfi<u>dh</u> jan<u>aah</u>aka limani ittaba\'aka mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'dan rendahkanlah dirimu terhadap orang-orang yang beriman yang mengikutimu.'),
	(3148, 26, 216, 'فَاِنْ عَصَوْكَ فَقُلْ اِنِّيْ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ۚ ', 'fa-in \'a<u>sh</u>awka faqul innii barii-un mimm<u>aa</u> ta\'maluun<strong>a</strong>', 'Kemudian jika mereka mendurhakaimu maka katakanlah (Muhammad), “Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu kerjakan.”'),
	(3149, 26, 217, 'وَتَوَكَّلْ عَلَى الْعَزِيْزِ الرَّحِيْمِ ۙ ', 'watawakkal \'al<u>aa</u> <strong>a</strong>l\'aziizi <strong>al</strong>rra<u>h</u>iim<strong>i</strong>', 'Dan bertawakallah kepada (Allah) Yang Mahaperkasa, Maha Penyayang.'),
	(3150, 26, 218, 'الَّذِيْ يَرٰىكَ حِيْنَ تَقُوْمُ  ', '<strong>al</strong>la<u>dz</u>ii yar<u>aa</u>ka <u>h</u>iina taquum<strong>u</strong>', 'Yang melihat engkau ketika engkau berdiri (untuk salat),'),
	(3151, 26, 219, 'وَتَقَلُّبَكَ فِى السّٰجِدِيْنَ  ', 'wataqallubaka fii <strong>al</strong>ss<u>aa</u>jidiin<strong>a</strong>', 'dan (melihat) perubahan gerakan badanmu di antara orang-orang yang sujud.'),
	(3152, 26, 220, 'اِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ  ', 'innahu huwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Sungguh, Dia Maha Mendengar, Maha Mengetahui.'),
	(3153, 26, 221, 'هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيٰطِيْنُ ۗ  ', 'hal unabbi-ukum \'al<u>aa</u> man tanazzalu <strong>al</strong>sysyay<u>aath</u>iin<strong>u</strong>', 'Maukah Aku beritakan kepadamu, kepada siapa setan-setan itu turun?'),
	(3154, 26, 222, 'تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَثِيْمٍ ۙ ', 'tanazzalu \'al<u>aa</u> kulli aff<u>aa</u>kin atsiim<strong>in</strong>', 'Mereka (setan) turun kepada setiap pendusta yang banyak berdosa,'),
	(3155, 26, 223, 'يُّلْقُوْنَ السَّمْعَ وَاَكْثَرُهُمْ كٰذِبُوْنَ ۗ  ', 'yulquuna <strong>al</strong>ssam\'a wa-aktsaruhum k<u>aadz</u>ibuun<strong>a</strong>', 'mereka menyampaikan hasil pendengaran mereka, sedangkan kebanyakan mereka orang-orang pendusta.'),
	(3156, 26, 224, 'وَالشُّعَرَاۤءُ يَتَّبِعُهُمُ الْغَاوٗنَ ۗ ', 'wa<strong>al</strong>sysyu\'ar<u>aa</u>u yattabi\'uhumu <strong>a</strong>lgh<u>aa</u>wuun<strong>a</strong>', 'Dan penyair-penyair itu diikuti oleh orang-orang yang sesat.'),
	(3157, 26, 225, 'اَلَمْ تَرَ اَنَّهُمْ فِيْ كُلِّ وَادٍ يَّهِيْمُوْنَ ۙ ', 'alam tara annahum fii kulli w<u>aa</u>din yahiimuun<strong>a</strong>', 'Tidakkah engkau melihat bahwa mereka mengembara di setiap lembah,'),
	(3158, 26, 226, 'وَاَنَّهُمْ يَقُوْلُوْنَ مَا لَا يَفْعَلُوْنَ ۙ  ', 'wa-annahum yaquuluuna m<u>aa</u> l<u>aa</u> yaf\'aluun<strong>a</strong>', 'dan bahwa mereka mengatakan apa yang mereka sendiri tidak mengerjakan(nya)?'),
	(3159, 26, 227, 'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَذَكَرُوا اللّٰهَ كَثِيْرًا وَّانْتَصَرُوْا مِنْۢ بَعْدِ مَا ظُلِمُوْا ۗوَسَيَعْلَمُ الَّذِيْنَ ظَلَمُوْٓا اَيَّ مُنْقَلَبٍ يَّنْقَلِبُوْنَ ࣖ ', 'ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti wa<u>dz</u>akaruu <strong>al</strong>l<u>aa</u>ha katsiiran wa<strong>i</strong>nta<u>sh</u>aruu min ba\'di m<u>aa</u> <u>zh</u>ulimuu wasaya\'lamu <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu ayya munqalabin yanqalibuun<strong>a</strong>', 'Kecuali orang-orang (penyair-penyair) yang beriman dan berbuat kebajikan dan banyak mengingat Allah dan mendapat kemenangan setelah terzalimi (karena menjawab puisi-puisi orang-orang kafir). Dan orang-orang yang zalim kelak akan tahu ke tempat mana mereka akan kembali.'),
	(3160, 27, 1, 'طٰسۤ ۚ تِلْكَ اٰيٰتُ الْقُرْاٰنِ وَكِتَابٍ مُّبِيْنٍ ۙ ', '<u>th</u><u>aa</u>-siin tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lqur-<u>aa</u>ni wakit<u>aa</u>bin mubiin<strong>in</strong>', 'Tha Sin. Inilah ayat-ayat Al-Qur\'an, dan Kitab yang jelas,'),
	(3161, 27, 2, 'هُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ۙ  ', 'hudan wabusyr<u>aa</u> lilmu/miniin<strong>a</strong>', 'petunjuk dan berita gembira bagi orang-orang yang beriman,'),
	(3162, 27, 3, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَ ', '<strong>al</strong>la<u>dz</u>iina yuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wayu/tuuna <strong>al</strong>zzak<u>aa</u>ta wahum bi<strong>a</strong>l-<u>aa</u>khirati hum yuuqinuun<strong>a</strong>', '(yaitu) orang-orang yang melaksanakan salat dan menunaikan zakat, dan mereka meyakini adanya akhirat.'),
	(3163, 27, 4, 'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُوْنَ ۗ ', 'inna <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati zayyann<u>aa</u> lahum a\'m<u>aa</u>lahum fahum ya\'mahuun<strong>a</strong>', 'Sesungguhnya orang-orang yang tidak beriman kepada akhirat, Kami jadikan terasa indah bagi mereka perbuatan-perbuatan mereka (yang buruk), sehingga mereka bergelimang dalam kesesatan.'),
	(3164, 27, 5, 'اُولٰۤىِٕكَ الَّذِيْنَ لَهُمْ سُوْۤءُ الْعَذَابِ وَهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ ', 'ul<u>aa</u>-ika <strong>al</strong>la<u>dz</u>iina lahum suu-u <strong>a</strong>l\'a<u>dzaa</u>bi wahum fii <strong>a</strong>l-<u>aa</u>khirati humu <strong>a</strong>l-akhsaruun<strong>a</strong>', 'Mereka itulah orang-orang yang akan mendapat siksaan buruk (di dunia) dan mereka di akhirat adalah orang-orang yang paling rugi.'),
	(3165, 27, 6, 'وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَّدُنْ حَكِيْمٍ عَلِيْمٍ ', 'wa-innaka latulaqq<u>aa</u> <strong>a</strong>lqur-<u>aa</u>na min ladun <u>h</u>akiimin \'aliim<strong>in</strong>', 'Dan sesungguhnya engkau (Muhammad) benar-benar telah diberi Al-Qur\'an dari sisi (Allah) Yang Mahabijaksana, Maha Mengetahui.'),
	(3166, 27, 7, 'اِذْ قَالَ مُوْسٰى لِاَهْلِهٖٓ اِنِّيْٓ اٰنَسْتُ نَارًاۗ سَاٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ اٰتِيْكُمْ بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُوْنَ ', 'i<u>dz</u> q<u>aa</u>la muus<u>aa</u> li-ahlihi innii <u>aa</u>nastu n<u>aa</u>ran sa<u>aa</u>tiikum minh<u>aa</u> bikhabarin aw <u>aa</u>tiikum bisyih<u>aa</u>bin qabasin la\'allakum ta<u>sth</u>aluun<strong>a</strong>', '(Ingatlah) ketika Musa berkata kepada keluarganya, “Sungguh, aku melihat api. Aku akan membawa kabar tentang itu kepadamu, atau aku akan membawa suluh api (obor) kepadamu agar kamu dapat berdiang (menghangatkan badan dekat api).”'),
	(3167, 27, 8, 'فَلَمَّا جَاۤءَهَا نُوْدِيَ اَنْۢ بُوْرِكَ مَنْ فِى النَّارِ وَمَنْ حَوْلَهَاۗ وَسُبْحٰنَ اللّٰهِ رَبِّ الْعٰلَمِيْنَ ', 'falamm<u>aa</u> j<u>aa</u>-ah<u>aa</u> nuudiya an buurika man fii <strong>al</strong>nn<u>aa</u>ri waman <u>h</u>awlah<u>aa</u> wasub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Maka ketika dia tiba di sana (tempat api itu), dia diseru, “Telah diberkahi orang-orang yang berada di dekat api, dan orang-orang yang berada di sekitarnya. Mahasuci Allah, Tuhan seluruh alam.”'),
	(3168, 27, 9, 'يٰمُوْسٰٓى اِنَّهٗٓ اَنَا اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ۙ ', 'y<u>aa</u> muus<u>aa</u> innahu an<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', '(Allah berfirman), “Wahai Musa!  Sesungguhnya Aku adalah Allah, Yang Mahaperkasa, Mahabijaksana.'),
	(3169, 27, 10, 'وَاَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰى لَا تَخَفْۗ اِنِّيْ لَا يَخَافُ لَدَيَّ الْمُرْسَلُوْنَ ۖ  ', 'wa-alqi \'a<u>shaa</u>ka falamm<u>aa</u> ra<u>aa</u>h<u>aa</u> tahtazzu ka-annah<u>aa</u> j<u>aa</u>nnun wall<u>aa</u> mudbiran walam yu\'aqqib y<u>aa</u> muus<u>aa</u> l<u>aa</u> takhaf innii l<u>aa</u> yakh<u>aa</u>fu ladayya <strong>a</strong>lmursaluun<strong>a</strong>', 'Dan lemparkanlah tongkatmu!” Maka ketika (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seperti seekor ular yang gesit, larilah dia berbalik ke belakang tanpa menoleh. ”Wahai Musa! Jangan takut! Sesungguhnya di hadapan-Ku, para rasul tidak perlu takut,'),
	(3170, 27, 11, 'اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًاۢ بَعْدَ سُوْۤءٍ فَاِنِّيْ غَفُوْرٌ رَّحِيْمٌ  ', 'ill<u>aa</u> man <u>zh</u>alama tsumma baddala <u>h</u>usnan ba\'da suu-in fa-innii ghafuurun ra<u>h</u>iim<strong>un</strong>', 'kecuali orang yang berlaku zalim yang kemudian mengubah (dirinya) dengan kebaikan setelah kejahatan (bertobat); maka sungguh, Aku Maha Pengampun, Maha Penyayang.'),
	(3171, 27, 12, 'وَاَدْخِلْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍۙ فِيْ تِسْعِ اٰيٰتٍ اِلٰى فِرْعَوْنَ وَقَوْمِهٖۚ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ', 'wa-adkhil yadaka fii jaybika takhruj bay<u>dhaa</u>-a min ghayri suu-in fii tis\'i <u>aa</u>y<u>aa</u>tin il<u>aa</u> fir\'awna waqawmihi innahum k<u>aa</u>nuu qawman f<u>aa</u>siqiin<strong>a</strong>', 'Dan masukkanlah tanganmu ke leher bajumu, niscaya ia akan keluar menjadi putih (bersinar) tanpa cacat. (Kedua mukjizat ini) termasuk sembilan macam mukjizat (yang akan dikemukakan) kepada Fir‘aun dan kaumnya. Mereka benar-benar orang-orang yang fasik.”'),
	(3172, 27, 13, 'فَلَمَّا جَاۤءَتْهُمْ اٰيٰتُنَا مُبْصِرَةً قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ۚ  ', 'falamm<u>aa</u> j<u>aa</u>-at-hum <u>aa</u>y<u>aa</u>tun<u>aa</u> mub<u>sh</u>iratan q<u>aa</u>luu h<u>aadzaa</u> si<u>h</u>run mubiin<strong>un</strong>', 'Maka ketika mukjizat-mukjizat Kami yang terang itu sampai kepada mereka, mereka berkata, “Ini sihir yang nyata.”'),
	(3173, 27, 14, 'وَجَحَدُوْا بِهَا وَاسْتَيْقَنَتْهَآ اَنْفُسُهُمْ ظُلْمًا وَّعُلُوًّاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ࣖ  ', 'waja<u>h</u>aduu bih<u>aa</u> wa<strong>i</strong>stayqanat-h<u>aa</u> anfusuhum <u>zh</u>ulman wa\'uluwwan fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan mereka mengingkarinya karena kezaliman dan kesombongannya, padahal hati mereka meyakini (kebenaran)nya. Maka perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan.'),
	(3174, 27, 15, 'وَلَقَدْ اٰتَيْنَا دَاوٗدَ وَسُلَيْمٰنَ عِلْمًاۗ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذِيْ فَضَّلَنَا عَلٰى كَثِيْرٍ مِّنْ عِبَادِهِ الْمُؤْمِنِيْنَ  ', 'walaqad <u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda wasulaym<u>aa</u>na \'ilman waq<u>aa</u>l<u>aa</u> <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii fa<u>dhdh</u>alan<u>aa</u> \'al<u>aa</u> katsiirin min \'ib<u>aa</u>dihi <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan sungguh, Kami telah memberikan ilmu kepada Dawud dan Sulaiman; dan keduanya berkata, “Segala puji bagi Allah yang melebihkan kami dari banyak hamba-hamba-Nya yang beriman.”'),
	(3175, 27, 16, 'وَوَرِثَ سُلَيْمٰنُ دَاوٗدَ وَقَالَ يٰٓاَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُوْتِيْنَا مِنْ كُلِّ شَيْءٍۗ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُبِيْنُ  ', 'wawaritsa sulaym<u>aa</u>nu d<u>aa</u>wuuda waq<u>aa</u>la y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su \'ullimn<u>aa</u> man<u>th</u>iqa <strong>al</strong><u>ththh</u>ayri wauutiin<u>aa</u> min kulli syay-in inna h<u>aadzaa</u> lahuwa <strong>a</strong>lfa<u>dh</u>lu <strong>a</strong>lmubiin<strong>u</strong>', 'Dan Sulaiman telah mewarisi Dawud, dan dia (Sulaiman) berkata, “Wahai manusia! Kami telah diajari bahasa burung dan kami diberi segala sesuatu. Sungguh, (semua) ini benar-benar karunia yang nyata.”'),
	(3176, 27, 17, 'وَحُشِرَ لِسُلَيْمٰنَ جُنُوْدُهٗ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوْزَعُوْنَ  ', 'wa<u>h</u>usyira lisulaym<u>aa</u>na junuuduhu mina <strong>a</strong>ljinni wa<strong>a</strong>l-insi wa<strong>al</strong><u>ththh</u>ayri fahum yuuza\'uun<strong>a</strong>', 'Dan untuk Sulaiman dikumpulkan bala tentaranya dari jin, manusia dan burung, lalu mereka berbaris dengan tertib.'),
	(3177, 27, 18, 'حَتّٰىٓ اِذَآ اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يّٰٓاَيُّهَا النَّمْلُ ادْخُلُوْا مَسٰكِنَكُمْۚ  لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُوْدُهٗۙ وَهُمْ لَا يَشْعُرُوْنَ  ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> ataw \'al<u>aa</u> w<u>aa</u>di <strong>al</strong>nnamli q<u>aa</u>lat namlatun y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnamlu udkhuluu mas<u>aa</u>kinakum l<u>aa</u> ya<u>hth</u>imannakum sulaym<u>aa</u>nu wajunuuduhu wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Hingga ketika mereka sampai di lembah semut, berkatalah seekor semut, “Wahai semut-semut! Masuklah ke dalam sarang-sarangmu, agar kamu tidak diinjak oleh Sulaiman dan bala tentaranya, sedangkan mereka tidak menyadari.”'),
	(3178, 27, 19, 'فَتَبَسَّمَ ضَاحِكًا مِّنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَدْخِلْنِيْ بِرَحْمَتِكَ فِيْ عِبَادِكَ الصّٰلِحِيْنَ  ', 'fatabassama <u>dah</u>ikan min qawlih<u>aa</u> waq<u>aa</u>la rabbi awzi\'nii an asykura ni\'mataka <strong>al</strong>latii an\'amta \'alayya wa\'al<u>aa</u> w<u>aa</u>lidayya wa-an a\'mala <u>shaa</u>li<u>h</u>an tar<u>daa</u>hu wa-adkhilnii bira<u>h</u>matika fii \'ib<u>aa</u>dika <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Maka dia (Sulaiman) tersenyum lalu tertawa karena (mendengar) perkataan semut itu. Dan dia berdoa, “Ya Tuhanku, anugerahkanlah aku ilham untuk tetap mensyukuri nikmat-Mu yang telah Engkau anugerahkan kepadaku dan kepada kedua orang tuaku dan agar aku mengerjakan kebajikan yang Engkau ridai; dan masukkanlah aku dengan rahmat-Mu ke dalam golongan hamba-hamba-Mu yang saleh.”'),
	(3179, 27, 20, 'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَآ اَرَى الْهُدْهُدَۖ اَمْ كَانَ مِنَ الْغَاۤىِٕبِيْنَ  ', 'watafaqqada <strong>al</strong><u>ththh</u>ayra faq<u>aa</u>la m<u>aa</u> liya l<u>aa</u> ar<u>aa</u> <strong>a</strong>lhudhuda am k<u>aa</u>na mina <strong>a</strong>lgh<u>aa</u>-ibiin<strong>a</strong>', 'Dan dia memeriksa burung-burung lalu berkata, “Mengapa aku tidak melihat Hud-hud, apakah ia termasuk yang tidak hadir?'),
	(3180, 27, 21, 'لَاُعَذِّبَنَّهٗ عَذَابًا شَدِيْدًا اَوْ لَاَا۟ذْبَحَنَّهٗٓ اَوْ لَيَأْتِيَنِّيْ بِسُلْطٰنٍ مُّبِيْنٍ  ', 'lau\'a<u>dzdz</u>ibannahu \'a<u>dzaa</u>ban syadiidan aw la-a<u>dz</u>ba<u>h</u>annahu aw laya/tiyannii bisul<u>th</u><u>aa</u>nin mubiin<strong>in</strong>', 'Pasti akan kuhukum ia dengan hukuman yang berat atau kusembelih ia, kecuali jika ia datang kepadaku dengan alasan yang jelas.”'),
	(3181, 27, 22, 'فَمَكَثَ غَيْرَ بَعِيْدٍ فَقَالَ اَحَطْتُّ بِمَا لَمْ تُحِطْ بِهٖ وَجِئْتُكَ مِنْ سَبَاٍ ۢبِنَبَاٍ يَّقِيْنٍ ', 'famakatsa ghayra ba\'iidin faq<u>aa</u>la a<u>hath</u>tu bim<u>aa</u> lam tu<u>h</u>i<u>th</u> bihi waji/tuka min saba-in binaba-in yaqiin<strong>in</strong>', 'Maka tidak lama kemudian (datanglah Hud-hud), lalu ia berkata, “Aku telah mengetahui sesuatu yang belum engkau ketahui. Aku datang kepadamu dari negeri Saba\' membawa suatu berita yang meyakinkan.'),
	(3182, 27, 23, 'اِنِّيْ وَجَدْتُّ امْرَاَةً تَمْلِكُهُمْ وَاُوْتِيَتْ مِنْ كُلِّ شَيْءٍ وَّلَهَا عَرْشٌ عَظِيْمٌ  ', 'innii wajadtu imra-atan tamlikuhum wauutiyat min kulli syay-in walah<u>aa</u> \'arsyun \'a<u>zh</u>iim<strong>un</strong>', 'Sungguh, kudapati ada seorang perempuan yang memerintah mereka, dan dia dianugerahi segala sesuatu serta memiliki singgasana yang besar.'),
	(3183, 27, 24, 'وَجَدْتُّهَا وَقَوْمَهَا يَسْجُدُوْنَ لِلشَّمْسِ مِنْ دُوْنِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ فَهُمْ لَا يَهْتَدُوْنَۙ  ', 'wajadtuh<u>aa</u> waqawmah<u>aa</u> yasjuduuna li<strong>l</strong>sysyamsi min duuni <strong>al</strong>l<u>aa</u>hi wazayyana lahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum fa<u>sh</u>addahum \'ani <strong>al</strong>ssabiili fahum l<u>aa</u> yahtaduun<strong>a</strong>', 'Aku (burung Hud) dapati dia dan kaumnya menyembah matahari, bukan kepada Allah; dan setan telah menjadikan terasa indah bagi mereka perbuatan-perbuatan (buruk) mereka, sehingga menghalangi mereka dari jalan (Allah), maka mereka tidak mendapat petunjuk,'),
	(3184, 27, 25, 'اَلَّا يَسْجُدُوْا لِلّٰهِ الَّذِيْ يُخْرِجُ الْخَبْءَ فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُوْنَ وَمَا تُعْلِنُوْنَ  ', '<strong>al</strong>l<u>aa</u> yasjuduu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii yukhriju <strong>a</strong>lkhaba-a fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i waya\'lamu m<u>aa</u> tukhfuuna wam<u>aa</u> tu\'linuun<strong>a</strong>', 'mereka (juga) tidak menyembah Allah yang mengeluarkan apa yang terpendam di langit dan di bumi dan yang mengetahui apa yang kamu sembunyikan dan yang kamu nyatakan.'),
	(3185, 27, 26, 'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۙ رَبُّ الْعَرْشِ الْعَظِيْمِ ۩ ', '<strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa rabbu <strong>a</strong>l\'arsyi <strong>a</strong>l\'a<u>zh</u>iim<strong>i</strong>', 'Allah, tidak ada tuhan melainkan Dia, Tuhan yang mempunyai ‘Arsy yang agung.”'),
	(3186, 27, 27, '۞ قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكٰذِبِيْنَ ', 'q<u>aa</u>la sanan<u>zh</u>uru a<u>sh</u>adaqta am kunta mina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dia (Sulaiman) berkata, “Akan kami lihat, apa kamu benar, atau termasuk yang berdusta.'),
	(3187, 27, 28, 'اِذْهَبْ بِّكِتٰبِيْ هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُوْنَ ', 'i<u>dz</u>hab bikit<u>aa</u>bii h<u>aadzaa</u> fa-alqih ilayhim tsumma tawalla \'anhum fa<strong>u</strong>n<u>zh</u>ur m<u>aatsaa</u> yarji\'uun<strong>a</strong>', 'Pergilah dengan (membawa) suratku ini, lalu jatuhkanlah kepada mereka, kemudian berpalinglah dari mereka, lalu perhatikanlah apa yang mereka bicarakan.”'),
	(3188, 27, 29, 'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اِنِّيْٓ اُلْقِيَ اِلَيَّ كِتٰبٌ كَرِيْمٌ  ', 'q<u>aa</u>lat y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>lmalau innii ulqiya ilayya kit<u>aa</u>bun kariim<strong>un</strong>', 'Dia (Balqis) berkata, “Wahai para pembesar! Sesungguhnya telah disampaikan kepadaku sebuah surat yang mulia.”'),
	(3189, 27, 30, 'اِنَّهٗ مِنْ سُلَيْمٰنَ وَاِنَّهٗ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ ۙ  ', 'innahu min sulaym<u>aa</u>na wa-innahu bismi <strong>al</strong>l<u>aa</u>hi <strong>al</strong>rra<u>h</u>m<u>aa</u>ni <strong>al</strong>rra<u>h</u>iim<strong>i</strong>', 'Sesungguhnya (surat) itu dari Sulaiman yang isinya, “Dengan nama Allah Yang Maha Pengasih, Maha Penyayang,'),
	(3190, 27, 31, 'اَلَّا تَعْلُوْا عَلَيَّ وَأْتُوْنِيْ مُسْلِمِيْنَ ࣖ ', '<strong>al</strong>l<u>aa</u> ta\'luu \'alayya wa/tuunii muslimiin<strong>a</strong>', 'janganlah engkau berlaku sombong terhadapku dan datanglah kepadaku sebagai orang-orang yang berserah diri.”'),
	(3191, 27, 32, 'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اَفْتُوْنِيْ فِيْٓ اَمْرِيْۚ مَا كُنْتُ قَاطِعَةً اَمْرًا حَتّٰى تَشْهَدُوْنِ ', 'q<u>aa</u>lat y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>lmalau aftuunii fii amrii m<u>aa</u> kuntu q<u>aath</u>i\'atan amran <u>h</u>att<u>aa</u> tasyhaduun<strong>a</strong>', 'Dia (Balqis) berkata, “Wahai para pembesar! Berilah aku pertimbangan dalam perkaraku (ini). Aku tidak pernah memutuskan suatu perkara sebelum kamu hadir dalam majelis(ku).”'),
	(3192, 27, 33, 'قَالُوْا نَحْنُ اُولُوْا قُوَّةٍ وَّاُولُوْا بَأْسٍ شَدِيْدٍ ەۙ وَّالْاَمْرُ اِلَيْكِ فَانْظُرِيْ مَاذَا تَأْمُرِيْنَ', 'q<u>aa</u>luu na<u>h</u>nu uluu quwwatin wauluu ba/sin syadiidin wa<strong>a</strong>l-amru ilayki fa<strong>u</strong>n<u>zh</u>urii m<u>aatsaa</u> ta/muriin<strong>a</strong>', 'Mereka menjawab, “Kita memiliki kekuatan dan keberanian yang luar biasa (untuk berperang), tetapi keputusan berada di tanganmu; maka pertimbangkanlah apa yang akan engkau perintahkan.”'),
	(3193, 27, 34, 'قَالَتْ اِنَّ الْمُلُوْكَ اِذَا دَخَلُوْا قَرْيَةً اَفْسَدُوْهَا وَجَعَلُوْٓا اَعِزَّةَ اَهْلِهَآ اَذِلَّةً ۚوَكَذٰلِكَ يَفْعَلُوْنَ  ', 'q<u>aa</u>lat inna <strong>a</strong>lmuluuka i<u>dzaa</u> dakhaluu qaryatan afsaduuh<u>aa</u> waja\'aluu a\'izzata ahlih<u>aa</u> a<u>dz</u>illatan waka<u>dzaa</u>lika yaf\'aluun<strong>a</strong>', 'Dia (Balqis) berkata, “Sesungguhnya raja-raja apabila menaklukkan suatu negeri, mereka tentu membinasakannya, dan menjadikan penduduknya yang mulia jadi hina; dan demikian yang akan mereka perbuat.'),
	(3194, 27, 35, 'وَاِنِّيْ مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنٰظِرَةٌ ۢبِمَ يَرْجِعُ الْمُرْسَلُوْنَ ', 'wa-innii mursilatun ilayhim bihadiyyatin fan<u>aats</u>iratun bima yarji\'u <strong>a</strong>lmursaluun<strong>a</strong>', 'Dan sungguh, aku akan mengirim utusan kepada mereka dengan (membawa) hadiah, dan (aku) akan menunggu apa yang akan dibawa kembali oleh para utusan itu.”'),
	(3195, 27, 36, 'فَلَمَّا جَاۤءَ سُلَيْمٰنَ قَالَ اَتُمِدُّوْنَنِ بِمَالٍ فَمَآ اٰتٰىنِ  َۧ اللّٰهُ خَيْرٌ مِّمَّآ اٰتٰىكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُوْنَ  ', 'falamm<u>aa</u> j<u>aa</u>-a sulaym<u>aa</u>na q<u>aa</u>la atumidduunani bim<u>aa</u>lin fam<u>aa</u> <u>aa</u>t<u>aa</u>niya <strong>al</strong>l<u>aa</u>hu khayrun mimm<u>aa</u> <u>aa</u>t<u>aa</u>kum bal antum bihadiyyatikum tafra<u>h</u>uun<strong>a</strong>', 'Maka ketika para (utusan itu) sampai kepada Sulaiman, dia (Sulaiman) berkata, “Apakah kamu akan memberi harta kepadaku? Apa yang Allah berikan kepadaku lebih baik daripada apa yang Allah berikan kepadamu; tetapi kamu merasa bangga dengan hadiahmu. '),
	(3196, 27, 37, 'اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُوْدٍ لَّا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِّنْهَآ اَذِلَّةً وَّهُمْ صَاغِرُوْنَ ', 'falamm<u>aa</u> j<u>aa</u>-a sulaym<u>aa</u>na q<u>aa</u>la atumidduunani bim<u>aa</u>lin fam<u>aa</u> <u>aa</u>t<u>aa</u>niya <strong>al</strong>l<u>aa</u>hu khayrun mimm<u>aa</u> <u>aa</u>t<u>aa</u>kum bal antum bihadiyyatikum tafra<u>h</u>uun<strong>a</strong>', 'Kembalilah kepada mereka! Sungguh, Kami pasti akan mendatangi mereka dengan bala tentara yang mereka tidak mampu melawannya, dan akan kami usir mereka dari negeri itu (Saba\') secara terhina dan mereka akan menjadi (tawanan) yang hina dina.”'),
	(3197, 27, 38, 'قَالَ يٰٓاَيُّهَا الْمَلَؤُا اَيُّكُمْ يَأْتِيْنِيْ بِعَرْشِهَا قَبْلَ اَنْ يَّأْتُوْنِيْ مُسْلِمِيْنَ ', 'q<u>aa</u>la y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>lmalau ayyukum ya/tiinii bi\'arsyih<u>aa</u> qabla an ya/tuunii muslimiin<strong>a</strong>', 'Dia (Sulaiman) berkata, “Wahai para pembesar! Siapakah di antara kamu yang sanggup membawa singgasananya kepadaku sebelum mereka datang kepadaku menyerahkan diri?”'),
	(3198, 27, 39, 'قَالَ عِفْرِيْتٌ مِّنَ الْجِنِّ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ تَقُوْمَ مِنْ مَّقَامِكَۚ وَاِنِّيْ عَلَيْهِ لَقَوِيٌّ اَمِيْنٌ  ', 'q<u>aa</u>la \'ifriitun mina <strong>a</strong>ljinni an<u>aa</u> <u>aa</u>tiika bihi qabla an taquuma min maq<u>aa</u>mika wa-inne \'alayhi laqawiyyun amiin<strong>un</strong>', '‘Ifrit dari golongan jin berkata, “Akulah yang akan membawanya kepadamu sebelum engkau berdiri dari tempat dudukmu; dan sungguh, aku kuat melakukannya dan dapat dipercaya.”'),
	(3199, 27, 40, 'قَالَ الَّذِيْ عِنْدَهٗ عِلْمٌ مِّنَ الْكِتٰبِ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ يَّرْتَدَّ اِلَيْكَ طَرْفُكَۗ فَلَمَّا رَاٰهُ مُسْتَقِرًّا عِنْدَهٗ قَالَ هٰذَا مِنْ فَضْلِ رَبِّيْۗ  لِيَبْلُوَنِيْٓ ءَاَشْكُرُ اَمْ اَكْفُرُۗ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُ', 'q<u>aa</u>la <strong>al</strong>la<u>dz</u>ii \'indahu \'ilmun mina <strong>a</strong>lkit<u>aa</u>bi an<u>aa</u> <u>aa</u>tiika bihi qabla an yartadda ilayka <u>th</u>arfuka falamm<u>aa</u> ra<u>aa</u>hu mustaqirran \'indahu q<u>aa</u>la h<u>aadzaa</u> min fa<u>dh</u>li rabbii liyabluwanii a-asykuru am akfuru waman syakara fa-innam<u>aa</u> yasykuru linafsihi waman kafara fa-inna rabbii ghaniyyun kariim<strong>un</strong>', 'Seorang yang mempunyai ilmu dari Kitab berkata, “Aku akan membawa singgasana itu kepadamu sebelum matamu berkedip.” Maka ketika dia (Sulaiman) melihat singgasana itu terletak di hadapannya, dia pun berkata, “Ini termasuk karunia Tuhanku untuk mengujiku, apakah aku bersyukur atau mengingkari (nikmat-Nya). Barangsiapa bersyukur, maka sesungguhnya dia bersyukur untuk (kebaikan) dirinya sendiri, dan barangsiapa ingkar, maka sesungguhnya Tuhanku Mahakaya, Mahamulia.”'),
	(3200, 27, 41, 'قَالَ نَكِّرُوْا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَدِيْٓ اَمْ تَكُوْنُ مِنَ الَّذِيْنَ لَا يَهْتَدُوْنَ ', 'q<u>aa</u>la nakkiruu lah<u>aa</u> \'arsyah<u>aa</u> nan<u>zh</u>ur atahtadii am takuunu mina <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yahtaduun<strong>a</strong>', 'Dia (Sulaiman) berkata, “Ubahlah untuknya singgasananya; kita akan melihat apakah dia (Balqis) mengenal; atau tidak mengenalnya lagi.”'),
	(3201, 27, 42, 'فَلَمَّا جَاۤءَتْ قِيْلَ اَهٰكَذَا عَرْشُكِۗ قَالَتْ كَاَنَّهٗ هُوَۚ وَاُوْتِيْنَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِيْنَ  ', 'falamm<u>aa</u> j<u>aa</u>-at qiila ah<u>aa</u>ka<u>dzaa</u> \'arsyuki q<u>aa</u>lat ka-annahu huwa wauutiin<u>aa</u> <strong>a</strong>l\'ilma min qablih<u>aa</u> wakunn<u>aa</u> muslimiin<strong>a</strong>', 'Maka ketika dia (Balqis) datang, ditanyakanlah (kepadanya), “Serupa inikah singgasanamu?” Dia (Balqis) menjawab, “Seakan-akan itulah dia.” (Dan dia Balqis berkata), “Kami telah diberi pengetahuan sebelumnya dan kami adalah orang-orang yang berserah diri (kepada Allah).”'),
	(3202, 27, 43, 'وَصَدَّهَا مَا كَانَتْ تَّعْبُدُ مِنْ دُوْنِ اللّٰهِ ۗاِنَّهَا كَانَتْ مِنْ قَوْمٍ كٰفِرِيْنَ  ', 'wa<u>sh</u>addah<u>aa</u> m<u>aa</u> k<u>aa</u>nat ta\'budu min duuni <strong>al</strong>l<u>aa</u>hi innah<u>aa</u> k<u>aa</u>nat min qawmin k<u>aa</u>firiin<strong>a</strong>', 'Dan kebiasaannya menyembah selain Allah mencegahnya (untuk melahirkan keislamannya), sesungguhnya dia (Balqis) dahulu termasuk orang-orang kafir.'),
	(3203, 27, 44, 'قِيْلَ لَهَا ادْخُلِى الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَّكَشَفَتْ عَنْ سَاقَيْهَاۗ قَالَ اِنَّهٗ صَرْحٌ مُّمَرَّدٌ مِّنْ قَوَارِيْرَ ەۗ قَالَتْ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ', 'qiila lah<u>aa</u> udkhulii <strong>al</strong><u>shsh</u>ar<u>h</u>a falamm<u>aa</u> ra-at-hu <u>h</u>asibat-hu lujjatan wakasyafat \'an s<u>aa</u>qayh<u>aa</u> q<u>aa</u>la innahu <u>sh</u>ar<u>h</u>un mumarradun min qaw<u>aa</u>riira q<u>aa</u>lat rabbi innii <u>zh</u>alamtu nafsii wa-aslamtu ma\'a sulaym<u>aa</u>na lill<u>aa</u>hi rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dikatakan kepadanya (Balqis), “Masuklah ke dalam istana.” Maka ketika dia (Balqis) melihat (lantai istana) itu, dikiranya kolam air yang besar, dan disingkapkannya (penutup) kedua betisnya. Dia (Sulaiman) berkata, “Sesungguhnya ini hanyalah lantai istana yang dilapisi kaca.” Dia (Balqis) berkata, “Ya Tuhanku, sungguh, aku telah berbuat zalim terhadap diriku. Aku berserah diri bersama Sulaiman kepada Allah, Tuhan seluruh alam.”'),
	(3204, 27, 45, 'وَلَقَدْ اَرْسَلْنَآ اِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا اَنِ اعْبُدُوا اللّٰهَ فَاِذَا هُمْ فَرِيْقٰنِ يَخْتَصِمُوْنَ  ', 'walaqad arsaln<u>aa</u> il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an ani u\'buduu <strong>al</strong>l<u>aa</u>ha fa-i<u>dzaa</u> hum fariiq<u>aa</u>ni yakhta<u>sh</u>imuun<strong>a</strong>', 'Dan sungguh, Kami telah mengutus kepada (kaum) Samud saudara mereka yaitu Saleh (yang menyeru), “Sembahlah Allah!” Tetapi tiba-tiba mereka (menjadi) dua golongan yang bermusuhan.'),
	(3205, 27, 46, 'قَالَ يٰقَوْمِ لِمَ تَسْتَعْجِلُوْنَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ  لَوْلَا تَسْتَغْفِرُوْنَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ ', 'q<u>aa</u>la y<u>aa</u> qawmi lima tasta\'jiluuna bi<strong>al</strong>ssayyi-ati qabla <strong>a</strong>l<u>h</u>asanati lawl<u>aa</u> tastaghfiruuna <strong>al</strong>l<u>aa</u>ha la\'allakum tur<u>h</u>amuun<strong>a</strong>', 'Dia (Saleh) berkata, “Wahai kaumku! Mengapa kamu meminta disegerakan keburukan sebelum (kamu meminta) kebaikan? Mengapa kamu tidak memohon ampunan kepada Allah, agar kamu mendapat rahmat?”'),
	(3206, 27, 47, 'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَّعَكَۗ قَالَ طٰۤىِٕرُكُمْ عِنْدَ اللّٰهِ  بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُوْنَ ', 'q<u>aa</u>luu i<u>ththh</u>ayyarn<u>aa</u> bika wabiman ma\'aka q<u>aa</u>la <u>th</u><u>aa</u>-irukum \'inda <strong>al</strong>l<u>aa</u>hi bal antum qawmun tuftanuun<strong>a</strong>', 'Mereka menjawab, “Kami mendapat nasib yang malang disebabkan oleh kamu dan orang-orang yang bersamamu.” Dia (Saleh) berkata, “Nasibmu ada pada Allah (bukan kami yang menjadi sebab), tetapi kamu adalah kaum yang sedang diuji.”'),
	(3207, 27, 48, 'وَكَانَ فِى الْمَدِيْنَةِ تِسْعَةُ رَهْطٍ يُّفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ  ', 'wak<u>aa</u>na fii <strong>a</strong>lmadiinati tis\'atu rah<u>th</u>in yufsiduuna fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> yu<u>sh</u>li<u>h</u>uun<strong>a</strong>', 'Dan di kota itu ada sembilan orang laki-laki yang berbuat kerusakan di bumi, mereka tidak melakukan perbaikan.'),
	(3208, 27, 49, 'قَالُوْا تَقَاسَمُوْا بِاللّٰهِ لَنُبَيِّتَنَّهٗ وَاَهْلَهٗ ثُمَّ لَنَقُوْلَنَّ لِوَلِيِّهٖ مَا شَهِدْنَا مَهْلِكَ اَهْلِهٖ وَاِنَّا لَصٰدِقُوْنَ  ', 'q<u>aa</u>luu taq<u>aa</u>samuu bi<strong>al</strong>l<u>aa</u>hi lanubayyitannahu wa-ahlahu tsumma lanaquulanna liwaliyyihi m<u>aa</u> syahidn<u>aa</u> mahlika ahlihi wa-inn<u>aa</u> la<u>shaa</u>diquun<strong>a</strong>', 'Mereka berkata, “Bersumpahlah kamu dengan (nama) Allah, bahwa kita pasti akan menyerang dia bersama keluarganya pada malam hari, kemudian kita akan mengatakan kepada ahli warisnya (bahwa) kita tidak menyaksikan kebinasaan keluarganya itu, dan sungguh, kita orang yang benar.”'),
	(3209, 27, 50, 'وَمَكَرُوْا مَكْرًا وَّمَكَرْنَا مَكْرًا وَّهُمْ لَا يَشْعُرُوْنَ  ', 'wamakaruu makran wamakarn<u>aa</u> makran wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan mereka membuat tipu daya, dan Kami pun menyusun tipu daya, sedang mereka tidak menyadari.'),
	(3210, 27, 51, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ اَنَّا دَمَّرْنٰهُمْ وَقَوْمَهُمْ اَجْمَعِيْنَ  ', 'fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu makrihim ann<u>aa</u> dammarn<u>aa</u>hum waqawmahum ajma\'iin<strong>a</strong>', 'Maka perhatikanlah bagaimana akibat dari tipu daya mereka, bahwa Kami membinasakan mereka dan kaum mereka semuanya.'),
	(3211, 27, 52, 'فَتِلْكَ بُيُوْتُهُمْ خَاوِيَةً ۢبِمَا ظَلَمُوْاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْلَمُوْنَ  ', 'fatilka buyuutuhum kh<u>aa</u>wiyatan bim<u>aa</u> <u>zh</u>alamuu inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya\'lamuun<strong>a</strong>', 'Maka itulah rumah-rumah mereka yang runtuh karena kezaliman mereka. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi orang-orang yang mengetahui.'),
	(3212, 27, 53, 'وَاَنْجَيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ  ', 'wa-anjayn<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun<strong>a</strong>', 'Dan Kami selamatkan orang-orang yang beriman dan mereka selalu bertakwa.'),
	(3213, 27, 54, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُوْنَ  ', 'waluu<u>th</u>an i<u>dz</u> q<u>aa</u>la liqawmihi ata/tuuna <strong>a</strong>lf<u>aah</u>isyata wa-antum tub<u>sh</u>iruun<strong>a</strong>', 'Dan (ingatlah kisah) Lut, ketika dia berkata kepada kaumnya, “Mengapa kamu mengerjakan perbuatan fahisyah (keji), padahal kamu melihatnya (kekejian perbuatan maksiat itu)?”'),
	(3214, 27, 55, 'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِ ۗبَلْ اَنْتُمْ قَوْمٌ تَجْهَلُوْنَ', 'a-innakum lata/tuuna <strong>al</strong>rrij<u>aa</u>la syahwatan min duuni <strong>al</strong>nnis<u>aa</u>-i bal antum qawmun tajhaluun<strong>a</strong>', 'Mengapa kamu mendatangi laki-laki untuk (memenuhi) syahwat(mu), bukan (mendatangi) perempuan? Sungguh, kamu adalah kaum yang tidak mengetahui (akibat perbuatanmu).'),
	(3215, 27, 56, '۞ فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْٓا اٰلَ لُوْطٍ مِّنْ قَرْيَتِكُمْۙ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ  ', 'fam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu akhrijuu <u>aa</u>la luu<u>th</u>in min qaryatikum innahum un<u>aa</u>sun yata<u>th</u>ahharuun<strong>a</strong>', 'Jawaban kaumnya tidak lain hanya dengan mengatakan, “Usirlah Lut dan keluarganya dari negerimu; sesungguhnya mereka adalah orang-orang yang (menganggap dirinya) suci.”'),
	(3216, 27, 57, 'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ قَدَّرْنٰهَا مِنَ الْغٰبِرِيْنَ  ', 'fa-anjayn<u>aa</u>hu wa-ahlahu ill<u>aa</u> imra-atahu qaddarn<u>aa</u>h<u>aa</u> mina <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'Maka Kami selamatkan dia dan keluarganya, kecuali istrinya. Kami telah menentukan dia termasuk orang-orang yang tertinggal (dibinasakan).'),
	(3217, 27, 58, 'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ࣖ ', 'wa-am<u>th</u>arn<u>aa</u> \'alayhim ma<u>th</u>aran fas<u>aa</u>-a ma<u>th</u>aru <strong>a</strong>lmun<u>dz</u>ariin<strong>a</strong>', 'Dan Kami hujani mereka dengan hujan (batu), maka sangat buruklah hujan (yang ditimpakan) pada orang-orang yang diberi peringatan itu (tetapi tidak mengindahkan). '),
	(3218, 27, 59, 'قُلِ الْحَمْدُ لِلّٰهِ وَسَلٰمٌ عَلٰى عِبَادِهِ الَّذِيْنَ اصْطَفٰىۗ ءٰۤاللّٰهُ خَيْرٌ اَمَّا يُشْرِكُوْنَ  ۔ ', 'quli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi wasal<u>aa</u>mun \'al<u>aa</u> \'ib<u>aa</u>dihi <strong>al</strong>la<u>dz</u>iina i<u>sth</u>af<u>aa</u> <u>aa</u>ll<u>aa</u>hu khayrun amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Katakanlah (Muhammad), “Segala puji bagi Allah dan salam sejahtera atas hamba-hamba-Nya yang dipilih-Nya. Apakah Allah yang lebih baik, ataukah apa yang mereka persekutukan (dengan Dia)?”'),
	(3219, 27, 60, 'اَمَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِّنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا بِهٖ حَدَاۤىِٕقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْۢبِتُوْا شَجَرَهَاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ هُمْ قَوْمٌ يَّعْدِلُوْنَ ۗ ', 'amman khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wa-anzala lakum mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-anbatn<u>aa</u> bihi <u>h</u>ad<u>aa</u>-iqa <u>dzaa</u>ta bahjatin m<u>aa</u> k<u>aa</u>na lakum an tunbituu syajarah<u>aa</u> a-il<u>aa</u>hun ma\'a <strong>al</strong>l<u>aa</u>hi bal hum qawmun ya\'diluun<strong>a</strong>', 'Bukankah Dia (Allah) yang menciptakan langit dan bumi dan yang menurunkan air dari langit untukmu, lalu Kami tumbuhkan dengan air itu kebun-kebun yang berpemandangan indah? Kamu tidak akan mampu menumbuhkan pohon-pohonnya. Apakah di samping Allah ada tuhan (yang lain)? Sebenarnya mereka adalah orang-orang yang menyimpang (dari kebenaran).'),
	(3220, 27, 61, 'اَمَّنْ جَعَلَ الْاَرْضَ قَرَارًا وَّجَعَلَ خِلٰلَهَآ اَنْهٰرًا وَّجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ۗ  ', 'amman ja\'ala <strong>a</strong>l-ar<u>dh</u>a qar<u>aa</u>ran waja\'ala khil<u>aa</u>lah<u>aa</u> anh<u>aa</u>ran waja\'ala lah<u>aa</u> raw<u>aa</u>siya waja\'ala bayna <strong>a</strong>lba<u>h</u>rayni <u>haa</u>jizan a-il<u>aa</u>hun ma\'a <strong>al</strong>l<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Bukankah Dia (Allah) yang telah menjadikan bumi sebagai tempat berdiam, yang menjadikan sungai-sungai di celah-celahnya, yang menjadikan gunung-gunung untuk (mengokohkan)nya dan yang menjadikan suatu pemisah antara dua laut? Apakah di samping Allah ada tuhan (yang lain)? Sebenarnya kebanyakan mereka tidak mengetahui.'),
	(3221, 27, 62, 'اَمَّنْ يُّجِيْبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّوْۤءَ وَيَجْعَلُكُمْ خُلَفَاۤءَ الْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقَلِيْلًا مَّا تَذَكَّرُوْنَۗ  ', 'amman yujiibu <strong>a</strong>lmu<u>dth</u>arra i<u>dzaa</u> da\'<u>aa</u>hu wayaksyifu <strong>al</strong>ssuu-a wayaj\'alukum khulaf<u>aa</u>-a <strong>a</strong>l-ar<u>dh</u>i a-il<u>aa</u>hun ma\'a <strong>al</strong>l<u>aa</u>hi qaliilan m<u>aa</u> ta<u>dz</u>akkaruun<strong>a</strong>', 'Bukankah Dia (Allah) yang memperkenankan (doa) orang yang dalam kesulitan apabila dia berdoa kepada-Nya, dan menghilangkan kesusahan dan menjadikan kamu (manusia) sebagai khalifah (pemimpin) di bumi? Apakah di samping Allah ada tuhan (yang lain)? Sedikit sekali (nikmat Allah) yang kamu ingat.'),
	(3222, 27, 63, 'اَمَّنْ يَّهْدِيْكُمْ فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُّرْسِلُ الرِّيٰحَ بُشْرًا ۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ ', 'amman yahdiikum fii <u>zh</u>ulum<u>aa</u>ti <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri waman yursilu <strong>al</strong>rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi a-il<u>aa</u>hun ma\'a <strong>al</strong>l<u>aa</u>hi ta\'<u>aa</u>l<u>aa</u> <strong>al</strong>l<u>aa</u>hu \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Bukankah Dia (Allah) yang memberi petunjuk kepada kamu dalam kegelapan di daratan dan lautan dan yang mendatangkan angin sebagai kabar gembira sebelum (kedatangan) rahmat-Nya? Apakah di samping Allah ada tuhan (yang lain)? Mahatinggi Allah terhadap apa yang mereka persekutukan. '),
	(3223, 27, 64, 'اَمَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَمَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'amman yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu waman yarzuqukum mina <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i a-il<u>aa</u>hun ma\'a <strong>al</strong>l<u>aa</u>hi qul h<u>aa</u>tuu burh<u>aa</u>nakum in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Bukankah Dia (Allah) yang menciptakan (makhluk) dari permulaannya, kemudian mengulanginya (lagi) dan yang memberikan rezeki kepadamu dari langit dan bumi? Apakah di samping Allah ada tuhan (yang lain)? Katakanlah, “Kemukakanlah bukti kebenaranmu, jika kamu orang yang benar.”'),
	(3224, 27, 65, 'قُلْ لَّا يَعْلَمُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُ ۗوَمَا يَشْعُرُوْنَ اَيَّانَ يُبْعَثُوْنَ ', 'qul l<u>aa</u> ya\'lamu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i <strong>a</strong>lghayba ill<u>aa</u> <strong>al</strong>l<u>aa</u>hu wam<u>aa</u> yasy\'uruuna ayy<u>aa</u>na yub\'atsuun<strong>a</strong>', 'Katakanlah (Muhammad), “Tidak ada sesuatu pun di langit dan di bumi yang mengetahui perkara yang gaib, kecuali Allah. Dan mereka tidak mengetahui kapan mereka akan dibangkitkan.”'),
	(3225, 27, 66, 'بَلِ ادّٰرَكَ عِلْمُهُمْ فِى الْاٰخِرَةِۗ بَلْ هُمْ فِيْ شَكٍّ مِّنْهَاۗ بَلْ هُمْ مِّنْهَا عَمُوْنَ ࣖ ', 'bali idd<u>aa</u>raka \'ilmuhum fii <strong>a</strong>l-<u>aa</u>khirati bal hum fii syakkin minh<u>aa</u> bal hum minh<u>aa</u> \'amuun<strong>a</strong>', 'Bahkan pengetahuan mereka tentang akhirat tidak sampai (ke sana). Bahkan mereka ragu-ragu tentangnya (akhirat itu). Bahkan mereka buta tentang itu.'),
	(3226, 27, 67, 'وَقَالَ الَّذِيْنَ كَفَرُوْٓا ءَاِذَا كُنَّا تُرَابًا وَّاٰبَاۤؤُنَآ اَىِٕنَّا لَمُخْرَجُوْنَ ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu a-i<u>dzaa</u> kunn<u>aa</u> tur<u>aa</u>ban wa<u>aa</u>b<u>aa</u>un<u>aa</u> a-inn<u>aa</u> lamukhrajuun<strong>a</strong>', 'Dan orang-orang yang kafir berkata, “Setelah kita menjadi tanah dan (begitu pula) nenek moyang kita, apakah benar kita akan dikeluarkan (dari kubur)?'),
	(3227, 27, 68, 'لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَاۤؤُنَا مِنْ قَبْلُۙ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ', 'laqad wu\'idn<u>aa</u> h<u>aadzaa</u> na<u>h</u>nu wa<u>aa</u>b<u>aa</u>un<u>aa</u> min qablu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru <strong>a</strong>l-awwaliin<strong>a</strong>', 'Sejak dahulu kami telah diberi ancaman dengan ini (hari kebangkitan); kami dan nenek moyang kami. Sebenarnya ini hanyalah dongeng orang-orang terdahulu.”'),
	(3228, 27, 69, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ', 'qul siiruu fii <strong>a</strong>l-ar<u>dh</u>i fa<strong>u</strong>n<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>a</strong>lmujrimiin<strong>a</strong>', 'Katakanlah (Muhammad), “Berjalanlah kamu di bumi, lalu perhatikanlah bagaimana kesudahan orang-orang yang berdosa. '),
	(3229, 27, 70, 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ', 'wal<u>aa</u> ta<u>h</u>zan \'alayhim wal<u>aa</u> takun fii <u>dh</u>ayqin mimm<u>aa</u> yamkuruun<strong>a</strong>', 'Dan janganlah engkau bersedih hati terhadap mereka, dan janganlah (dadamu) merasa sempit terhadap upaya tipu daya mereka.”'),
	(3230, 27, 71, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> <strong>a</strong>lwa\'du in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan mereka (orang kafir) berkata, “Kapankah datangnya janji (azab) itu, jika kamu orang yang benar.”'),
	(3231, 27, 72, 'قُلْ عَسٰٓى اَنْ يَّكُوْنَ رَدِفَ لَكُمْ بَعْضُ الَّذِيْ تَسْتَعْجِلُوْنَ ', 'qul \'as<u>aa</u> an yakuuna radifa lakum ba\'<u>dh</u>u <strong>al</strong>la<u>dz</u>ii tasta\'jiluun<strong>a</strong>', 'Katakanlah (Muhammad), “Boleh jadi sebagian dari (azab) yang kamu minta disegerakan itu telah hampir sampai kepadamu.”'),
	(3232, 27, 73, 'وَاِنَّ رَبَّكَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ', 'wa-inna rabbaka la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> <strong>al</strong>nn<u>aa</u>si wal<u>aa</u>kinna aktsarahum l<u>aa</u> yasykuruun<strong>a</strong>', 'Dan sungguh, Tuhanmu benar-benar memiliki karunia (yang diberikan-Nya) kepada manusia, tetapi kebanyakan mereka tidak mensyukuri(nya).'),
	(3233, 27, 74, 'وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ', 'wa-inna rabbaka laya\'lamu m<u>aa</u> tukinnu <u>sh</u>uduuruhum wam<u>aa</u> yu\'linuun<strong>a</strong>', 'Dan sungguh, Tuhanmu mengetahui apa yang disembunyikan dalam dada mereka dan apa yang mereka nyatakan.'),
	(3234, 27, 75, 'وَمَا مِنْ غَاۤىِٕبَةٍ فِى السَّمَاۤءِ وَالْاَرْضِ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ', 'wam<u>aa</u> min gh<u>aa</u>-ibatin fii <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> fii kit<u>aa</u>bin mubiin<strong>in</strong>', 'Dan tidak ada sesuatu pun yang tersembunyi di langit dan di bumi, melainkan (tercatat) dalam Kitab yang jelas (Lauh Mahfuzh).'),
	(3235, 27, 76, 'اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَكْثَرَ الَّذِيْ هُمْ فِيْهِ يَخْتَلِفُوْنَ  ', 'inna h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>na yaqu<u>shsh</u>u \'al<u>aa</u> banii isr<u>aa</u>-iila aktsara <strong>al</strong>la<u>dz</u>ii hum fiihi yakhtalifuun<strong>a</strong>', 'Sungguh, Al-Qur\'an ini menjelaskan kepada Bani Israil sebagian besar dari (perkara) yang mereka perselisihkan.'),
	(3236, 27, 77, 'وَاِنَّهٗ لَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ', 'wa-innahu lahudan wara<u>h</u>matun lilmu/miniin<strong>a</strong>', 'Dan sungguh, (Al-Qur\'an) itu benar-benar menjadi petunjuk dan rahmat bagi orang-orang yang beriman.'),
	(3237, 27, 78, 'اِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ بِحُكْمِهٖۚ وَهُوَ الْعَزِيْزُ الْعَلِيْمُۚ ', 'inna rabbaka yaq<u>dh</u>ii baynahum bi<u>h</u>ukmihi wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l\'aliim<strong>u</strong>', 'Sungguh, Tuhanmu akan menyelesaikan (perkara) di antara mereka dengan hukum-Nya, dan Dia Mahaperkasa, Maha Mengetahui.'),
	(3238, 27, 79, 'فَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّكَ عَلَى الْحَقِّ الْمُبِيْنِ ', 'fatawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi innaka \'al<u>aa</u> <strong>a</strong>l<u>h</u>aqqi <strong>a</strong>lmubiin<strong>i</strong>', 'Maka bertawakallah kepada Allah, sungguh engkau (Muhammad) berada di atas kebenaran yang nyata.'),
	(3239, 27, 80, 'اِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ ', 'innaka l<u>aa</u> tusmi\'u <strong>a</strong>lmawt<u>aa</u> wal<u>aa</u> tusmi\'u <strong>al</strong><u>shsh</u>umma <strong>al</strong>ddu\'<u>aa</u>-a i<u>dzaa</u> wallaw mudbiriin<strong>a</strong>', 'Sungguh, engkau tidak dapat menjadikan orang yang mati dapat mendengar dan (tidak pula) menjadikan orang yang tuli dapat mendengar seruan, apabila mereka telah berpaling ke belakang.'),
	(3240, 27, 81, 'وَمَآ اَنْتَ بِهٰدِى الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ ', 'wam<u>aa</u> anta bih<u>aa</u>dii <strong>a</strong>l\'umyi \'an <u>dh</u>al<u>aa</u>latihim in tusmi\'u ill<u>aa</u> man yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum muslimuun<strong>a</strong>', 'Dan engkau tidak akan dapat memberi petunjuk orang buta dari kesesatannya. Engkau tidak dapat menjadikan (seorang pun) mendengar, kecuali orang-orang yang beriman kepada ayat-ayat Kami, lalu mereka berserah diri.'),
	(3241, 27, 82, '۞ وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَاۤبَّةً مِّنَ الْاَرْضِ تُكَلِّمُهُمْ اَنَّ النَّاسَ كَانُوْا بِاٰيٰتِنَا لَا يُوْقِنُوْنَ ࣖ', 'wa-i<u>dzaa</u> waqa\'a <strong>a</strong>lqawlu \'alayhim akhrajn<u>aa</u> lahum d<u>aa</u>bbatan mina <strong>a</strong>l-ar<u>dh</u>i tukallimuhum anna <strong>al</strong>nn<u>aa</u>sa k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> l<u>aa</u> yuuqinuun<strong>a</strong>', 'Dan apabila perkataan (ketentuan masa kehancuran alam) telah berlaku atas mereka, Kami keluarkan makhluk bergerak yang bernyawa dari bumi yang akan mengatakan kepada mereka bahwa manusia dahulu tidak yakin kepada ayat-ayat Kami. '),
	(3242, 27, 83, 'وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجًا مِّمَّنْ يُّكَذِّبُ بِاٰيٰتِنَا فَهُمْ يُوْزَعُوْنَ ', 'wayawma na<u>h</u>syuru min kulli ummatin fawjan mimman yuka<u>dzdz</u>ibu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum yuuza\'uun<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Kami mengumpulkan dari setiap umat, segolongan orang yang mendustakan ayat-ayat Kami, lalu mereka dibagi-bagi (dalam kelompok-kelompok).'),
	(3243, 27, 84, 'حَتّٰٓى اِذَا جَاۤءُوْ قَالَ اَكَذَّبْتُمْ بِاٰيٰتِيْ وَلَمْ تُحِيْطُوْا بِهَا عِلْمًا اَمَّاذَا كُنْتُمْ تَعْمَلُوْنَ ', '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uu q<u>aa</u>la aka<u>dzdz</u>abtum bi-<u>aa</u>y<u>aa</u>tii walam tu<u>h</u>ii<u>th</u>uu bih<u>aa</u> \'ilman amm<u>aatsaa</u> kuntum ta\'maluun<strong>a</strong>', 'Hingga apabila mereka datang, Dia (Allah) berfirman, “Mengapa kamu telah mendustakan ayat-ayat-Ku, pada-hal kamu tidak mempunyai pengetahuan tentang itu, atau apakah yang telah kamu kerjakan?”'),
	(3244, 27, 85, 'وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوْا فَهُمْ لَا يَنْطِقُوْنَ ', 'wawaqa\'a <strong>a</strong>lqawlu \'alayhim bim<u>aa</u> <u>zh</u>alamuu fahum l<u>aa</u> yan<u>th</u>iquun<strong>a</strong>', 'Dan berlakulah perkataan (janji azab) atas mereka karena kezaliman mereka, maka mereka tidak dapat berkata.'),
	(3245, 27, 86, 'اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ', 'alam yaraw ann<u>aa</u> ja\'aln<u>aa</u> <strong>al</strong>layla liyaskunuu fiihi wa<strong>al</strong>nnah<u>aa</u>ra mub<u>sh</u>iran inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun<strong>a</strong>', 'Apakah mereka tidak memperhatikan bahwa Kami telah menjadikan malam agar mereka beristirahat padanya dan (menjadikan) siang yang menerangi? Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman.'),
	(3246, 27, 87, 'وَيَوْمَ يُنْفَخُ فِى الصُّوْرِ فَفَزِعَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗوَكُلٌّ اَتَوْهُ دَاخِرِيْنَ ', 'wayawma yunfakhu fii <strong>al</strong><u>shsh</u>uuri fafazi\'a man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti waman fii <strong>a</strong>l-ar<u>dh</u>i ill<u>aa</u> man sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu wakullun atawhu d<u>aa</u>khiriin<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) sangkakala ditiup, maka terkejutlah apa yang ada di langit dan apa yang ada di bumi, kecuali siapa yang dikehendaki Allah. Dan semua mereka datang menghadap-Nya dengan merendahkan diri.'),
	(3247, 27, 88, 'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَّهِيَ تَمُرُّ مَرَّ السَّحَابِۗ صُنْعَ اللّٰهِ الَّذِيْٓ اَتْقَنَ كُلَّ شَيْءٍۗ اِنَّهٗ خَبِيْرٌ ۢبِمَا تَفْعَلُوْنَ', 'watar<u>aa</u> <strong>a</strong>ljib<u>aa</u>la ta<u>h</u>sabuh<u>aa</u> j<u>aa</u>midatan wahiya tamurru marra <strong>al</strong>ssa<u>haa</u>bi <u>sh</u>un\'a <strong>al</strong>l<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii atqana kulla syay-in innahu khabiirun bim<u>aa</u> taf\'aluun<strong>a</strong>', 'Dan engkau akan melihat gunung-gunung, yang engkau kira tetap di tempatnya, padahal ia berjalan (seperti) awan berjalan. (Itulah) ciptaan Allah yang mencipta dengan sempurna segala sesuatu. Sungguh, Dia Mahateliti apa yang kamu kerjakan.'),
	(3248, 27, 89, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَهُمْ مِّنْ فَزَعٍ يَّوْمَىِٕذٍ اٰمِنُوْنَ ', 'man j<u>aa</u>-a bi<strong>a</strong>l<u>h</u>asanati falahu khayrun minh<u>aa</u> wahum min faza\'in yawma-i<u>dz</u>in <u>aa</u>minuun<strong>a</strong>', 'Barangsiapa membawa kebaikan, maka dia memperoleh (balasan) yang lebih baik daripadanya, sedang mereka merasa aman dari kejutan (yang dahsyat) pada hari itu.'),
	(3249, 27, 90, 'وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوْهُهُمْ فِى النَّارِۗ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ ', 'waman j<u>aa</u>-a bi<strong>al</strong>ssayyi-ati fakubbat wujuuhuhum fii <strong>al</strong>nn<u>aa</u>ri hal tujzawna ill<u>aa</u> m<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan barangsiapa membawa kejahatan, maka disungkurkanlah wajah mereka ke dalam neraka. Kamu tidak diberi balasan, melainkan (setimpal) dengan apa yang telah kamu kerjakan.'),
	(3250, 27, 91, 'اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذِيْ حَرَّمَهَا وَلَهٗ كُلُّ شَيْءٍ وَّاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ ۙ', 'innam<u>aa</u> umirtu an a\'buda rabba h<u>aadz</u>ihi <strong>a</strong>lbaldati <strong>al</strong>la<u>dz</u>ii <u>h</u>arramah<u>aa</u> walahu kullu syay-in waumirtu an akuuna mina <strong>a</strong>lmuslimiin<strong>a</strong>', 'Aku (Muhammad) hanya diperintahkan menyembah Tuhan negeri ini (Mekah) yang Dia telah menjadikan suci padanya dan segala sesuatu adalah milik-Nya. Dan aku diperintahkan agar aku termasuk orang Muslim,'),
	(3251, 27, 92, 'وَاَنْ اَتْلُوَا الْقُرْاٰنَ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَقُلْ اِنَّمَآ اَنَا۠ مِنَ الْمُنْذِرِيْنَ ', 'wa-an atluwa <strong>a</strong>lqur-<u>aa</u>na famani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla faqul innam<u>aa</u> an<u>aa</u> mina <strong>a</strong>lmun<u>dz</u>iriin<strong>a</strong>', 'dan agar aku membacakan Al-Qur\'an (kepada manusia). Maka barangsiapa mendapat petunjuk maka sesungguhnya dia mendapat petunjuk untuk (kebaikan) dirinya, dan barangsiapa sesat, maka katakanlah, “Sesungguhnya aku (ini) tidak lain hanyalah salah seorang pemberi peringatan.”'),
	(3252, 27, 93, 'وَقُلِ الْحَمْدُ لِلّٰهِ سَيُرِيْكُمْ اٰيٰتِهٖ فَتَعْرِفُوْنَهَاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ࣖ', 'waquli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi sayuriikum <u>aa</u>y<u>aa</u>tihi fata\'rifuunah<u>aa</u> wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Dan katakanlah (Muhammad), “Segala puji bagi Allah, Dia akan memperlihatkan kepadamu tanda-tanda (kebesaran)-Nya, maka kamu akan mengetahuinya. Dan Tuhanmu tidak lengah terhadap apa yang kamu kerjakan.”'),
	(3253, 28, 1, ' طٰسۤمّۤ ', '<u>th</u><u>aa</u>-siin-miim', 'Tha Sin Mim'),
	(3254, 28, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ ', 'tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>lmubiin<strong>i</strong>', 'Ini ayat-ayat Kitab (Al-Qur\'an) yang jelas (dari Allah).'),
	(3255, 28, 3, 'نَتْلُوْا عَلَيْكَ مِنْ نَّبَاِ مُوْسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُّؤْمِنُوْنَ ', 'natluu \'alayka min naba-i muus<u>aa</u> wafir\'awna bi<strong>a</strong>l<u>h</u>aqqi liqawmin yu/minuun<strong>a</strong>', 'Kami membacakan kepadamu sebagian dari kisah Musa dan Fir‘aun dengan sebenarnya untuk orang-orang yang beriman.'),
	(3256, 28, 4, 'اِنَّ فِرْعَوْنَ عَلَا فِى الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعًا يَّسْتَضْعِفُ طَاۤىِٕفَةً مِّنْهُمْ يُذَبِّحُ اَبْنَاۤءَهُمْ وَيَسْتَحْيٖ نِسَاۤءَهُمْ ۗاِنَّهٗ كَانَ مِنَ الْمُفْسِدِيْنَ ', 'inna fir\'awna \'al<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i waja\'ala ahlah<u>aa</u> syiya\'an yasta<u>dh</u>\'ifu <u>th</u><u>aa</u>-ifatan minhum yu<u>dz</u>abbi<u>h</u>u abn<u>aa</u>-ahum wayasta<u>h</u>yii nis<u>aa</u>-ahum innahu k<u>aa</u>na mina <strong>a</strong>lmufsidiin<strong>a</strong>', 'Sungguh, Fir‘aun telah berbuat sewenang-wenang di bumi dan menjadikan penduduknya berpecah belah, dia menindas segolongan dari mereka (Bani Israil), dia menyembelih anak laki-laki mereka dan membiarkan hidup anak perempuan mereka. Sungguh, dia (Fir‘aun) termasuk orang yang berbuat kerusakan.'),
	(3257, 28, 5, 'وَنُرِيْدُ اَنْ نَّمُنَّ عَلَى الَّذِيْنَ اسْتُضْعِفُوْا فِى الْاَرْضِ وَنَجْعَلَهُمْ اَىِٕمَّةً وَّنَجْعَلَهُمُ الْوٰرِثِيْنَ ۙ', 'wanuriidu an namunna \'al<u>aa</u> <strong>al</strong>la<u>dz</u>iina istu<u>dh</u>\'ifuu fii <strong>a</strong>l-ar<u>dh</u>i wanaj\'alahum a-immatan wanaj\'alahumu <strong>a</strong>lw<u>aa</u>ritsiin<strong>a</strong>', 'Dan Kami hendak memberi karunia kepada orang-orang yang tertindas di bumi (Mesir) itu, dan hendak menjadikan mereka pemimpin dan menjadikan mereka orang-orang yang mewarisi (bumi),'),
	(3258, 28, 6, 'وَنُمَكِّنَ لَهُمْ فِى الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا مِنْهُمْ مَّا كَانُوْا يَحْذَرُوْنَ ', 'wanumakkina lahum fii <strong>a</strong>l-ar<u>dh</u>i wanuriya fir\'awna wah<u>aa</u>m<u>aa</u>na wajunuudahum<u>aa</u> minhum m<u>aa</u> k<u>aa</u>nuu ya<u>hts</u>aruun<strong>a</strong>', 'dan Kami teguhkan kedudukan mereka di bumi dan Kami perlihatkan kepada Fir‘aun dan Haman bersama bala tentaranya apa yang selalu mereka takutkan dari mereka.'),
	(3259, 28, 7, 'وَاَوْحَيْنَآ اِلٰٓى اُمِّ مُوْسٰٓى اَنْ اَرْضِعِيْهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْقِيْهِ فِى الْيَمِّ وَلَا تَخَافِيْ وَلَا تَحْزَنِيْ ۚاِنَّا رَاۤدُّوْهُ اِلَيْكِ وَجَاعِلُوْهُ مِنَ الْمُرْسَلِيْنَ ', 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> ummi muus<u>aa</u> an ar<u>dh</u>i\'iihi fa-i<u>dzaa</u> khifti \'alayhi fa-alqiihi fii <strong>a</strong>lyammi wal<u>aa</u> takh<u>aa</u>fii wal<u>aa</u> ta<u>h</u>zanii inn<u>aa</u> r<u>aa</u>dduuhu ilayki waj<u>aa</u>\'iluuhu mina <strong>a</strong>lmursaliin<strong>a</strong>', 'Dan Kami ilhamkan kepada ibunya Musa, “Susuilah dia (Musa), dan apabila engkau khawatir terhadapnya maka hanyutkanlah dia ke sungai (Nil). Dan janganlah engkau takut dan jangan (pula) bersedih hati, sesungguhnya Kami akan mengembalikannya kepadamu, dan menjadikannya salah seorang rasul.”'),
	(3260, 28, 8, 'فَالْتَقَطَهٗٓ اٰلُ فِرْعَوْنَ لِيَكُوْنَ لَهُمْ عَدُوًّا وَّحَزَنًاۗ اِنَّ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا كَانُوْا خٰطِـِٕيْنَ ', 'fa<strong>i</strong>ltaqa<u>th</u>ahu <u>aa</u>lu fir\'awna liyakuuna lahum \'aduwwan wa<u>h</u>azanan inna fir\'awna wah<u>aa</u>m<u>aa</u>na wajunuudahum<u>aa</u> k<u>aa</u>nuu kh<u>aath</u>i-iin<strong>a</strong>', 'Maka dia dipungut oleh keluarga Fir‘aun agar (kelak) dia menjadi musuh dan kesedihan bagi mereka. Sungguh, Fir‘aun dan Haman bersama bala tentaranya adalah orang-orang yang bersalah.'),
	(3261, 28, 9, 'وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّيْ وَلَكَۗ  لَا تَقْتُلُوْهُ ۖعَسٰٓى اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا وَّهُمْ لَا يَشْعُرُوْنَ ', 'waq<u>aa</u>lati imra-atu fir\'awna qurratu \'aynin lii walaka l<u>aa</u> taqtuluuhu \'as<u>aa</u> an yanfa\'an<u>aa</u> aw nattakhi<u>dz</u>ahu waladan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan istri Fir‘aun berkata, “(Dia) adalah penyejuk mata hati bagiku dan bagimu. Janganlah kamu membunuhnya, mudah-mudahan dia bermanfaat kepada kita atau kita ambil dia menjadi anak,” sedang mereka tidak menyadari.'),
	(3262, 28, 10, 'وَاَصْبَحَ فُؤَادُ اُمِّ مُوْسٰى فٰرِغًاۗ اِنْ كَادَتْ لَتُبْدِيْ بِهٖ لَوْلَآ اَنْ رَّبَطْنَا عَلٰى قَلْبِهَا لِتَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ', 'wa-a<u>sh</u>ba<u>h</u>a fu-<u>aa</u>du ummi muus<u>aa</u> f<u>aa</u>righan in k<u>aa</u>dat latubdii bihi lawl<u>aa</u> an raba<u>th</u>n<u>aa</u> \'al<u>aa</u> qalbih<u>aa</u> litakuuna mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan hati ibu Musa menjadi kosong. Sungguh, hampir saja dia menyatakannya (rahasia tentang Musa), seandainya tidak Kami teguhkan hatinya, agar dia termasuk orang-orang yang beriman (kepada janji Allah).'),
	(3263, 28, 11, 'وَقَالَتْ لِاُخْتِهٖ قُصِّيْهِۗ فَبَصُرَتْ بِهٖ عَنْ جُنُبٍ وَّهُمْ لَا يَشْعُرُوْنَ ۙ', 'waq<u>aa</u>lat li-ukhtihi qu<u>shsh</u>iihi faba<u>sh</u>urat bihi \'an junubin wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan dia (ibunya Musa) berkata kepada saudara perempuan Musa, “Ikutilah dia (Musa).” Maka kelihatan olehnya (Musa) dari jauh, sedang mereka tidak menyadarinya.'),
	(3264, 28, 12, '۞ وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَّكْفُلُوْنَهٗ لَكُمْ وَهُمْ لَهٗ نَاصِحُوْنَ ', 'wa<u>h</u>arramn<u>aa</u> \'alayhi <strong>a</strong>lmar<u>aad</u>i\'a min qablu faq<u>aa</u>lat hal adullukum \'al<u>aa</u> ahli baytin yakfuluunahu lakum wahum lahu n<u>aas</u>i<u>h</u>uun<strong>a</strong>', 'Dan Kami cegah dia (Musa) menyusu kepada perempuan-perempuan yang mau menyusui(nya) sebelum itu; maka berkatalah dia (saudaranya Musa), “Maukah aku tunjukkan kepadamu, keluarga yang akan memeliharanya untukmu dan mereka dapat berlaku baik padanya?”'),
	(3265, 28, 13, 'فَرَدَدْنٰهُ اِلٰٓى اُمِّهٖ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ࣖ', 'faradadn<u>aa</u>hu il<u>aa</u> ummihi kay taqarra \'aynuh<u>aa</u> wal<u>aa</u> ta<u>h</u>zana walita\'lama anna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Maka Kami kembalikan dia (Musa) kepada ibunya, agar senang hatinya dan tidak bersedih hati, dan agar dia mengetahui bahwa janji Allah adalah benar, tetapi kebanyakan mereka tidak mengetahuinya.'),
	(3266, 28, 14, 'وَلَمَّا بَلَغَ اَشُدَّهٗ وَاسْتَوٰىٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًاۗ وَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ ', 'walamm<u>aa</u> balagha asyuddahu wa<strong>i</strong>staw<u>aa</u> <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman waka<u>dzaa</u>lika najzii <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan setelah dia (Musa) dewasa dan sempurna akalnya, Kami anugerahkan kepadanya hikmah (kenabian) dan pengetahuan. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
	(3267, 28, 15, 'وَدَخَلَ الْمَدِيْنَةَ عَلٰى حِيْنِ غَفْلَةٍ مِّنْ اَهْلِهَا فَوَجَدَ فِيْهَا رَجُلَيْنِ يَقْتَتِلٰنِۖ هٰذَا مِنْ شِيْعَتِهٖ وَهٰذَا مِنْ عَدُوِّهٖۚ فَاسْتَغَاثَهُ الَّذِيْ مِنْ شِيْعَتِهٖ عَلَى الَّذِيْ مِنْ عَدُوِّهٖ ۙفَوَكَزَهٗ مُوْسٰى فَقَضٰى عَلَيْهِ', 'wadakhala <strong>a</strong>lmadiinata \'al<u>aa</u> <u>h</u>iini ghaflatin min ahlih<u>aa</u> fawajada fiih<u>aa</u> rajulayni yaqtatil<u>aa</u>ni h<u>aadzaa</u> min syii\'atihi wah<u>aadzaa</u> min \'aduwwihi fa<strong>i</strong>stagh<u>aa</u>tsahu <strong>al</strong>la<u>dz</u>ii min syii\'atihi \'al<u>aa</u> <strong>al</strong>la<u>dz</u>ii min \'aduwwihi fawakazahu muus<u>aa</u> faqa<u>daa</u> \'alayhi q<u>aa</u>la h<u>aadzaa</u> min \'amali <strong>al</strong>sysyay<u>th</u><u>aa</u>ni innahu \'aduwwun mu<u>dh</u>illun mubiin<strong>un</strong>', 'Dan dia (Musa) masuk ke kota (Memphis) ketika penduduknya sedang lengah, maka dia mendapati di dalam kota itu dua orang laki-laki sedang berkelahi; yang seorang dari golongannya (Bani Israil) dan yang seorang (lagi) dari pihak musuhnya (kaum Fir‘aun). Orang yang dari golongannya meminta pertolongan kepadanya, untuk (mengalahkan) orang yang dari pihak musuhnya, lalu Musa meninjunya, dan matilah musuhnya itu. Dia (Musa) berkata, “Ini adalah perbuatan setan. Sungguh, dia (setan itu) adalah musuh yang jelas menyesatkan.”'),
	(3268, 28, 16, 'قَالَ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ فَاغْفِرْ لِيْ فَغَفَرَ لَهٗ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ ', 'q<u>aa</u>la rabbi innii <u>zh</u>alamtu nafsii fa<strong>i</strong>ghfir lii faghafara lahu innahu huwa <strong>a</strong>lghafuuru <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Dia (Musa) berdoa, “Ya Tuhanku, sesungguhnya aku telah menzalimi diriku sendiri, maka ampunilah aku.” Maka Dia (Allah) mengampuninya. Sungguh, Allah, Dialah Yang Maha Pengampun, Maha Penyayang.'),
	(3269, 28, 17, 'قَالَ رَبِّ بِمَآ اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُوْنَ ظَهِيْرًا لِّلْمُجْرِمِيْنَ ', 'q<u>aa</u>la rabbi bim<u>aa</u> an\'amta \'alayya falan akuuna <u>zh</u>ahiiran lilmujrimiin<strong>a</strong>', 'Dia (Musa) berkata, “Ya Tuhanku! Demi nikmat yang telah Engkau anugerahkan kepadaku, maka aku tidak akan menjadi penolong bagi orang-orang yang berdosa.”'),
	(3270, 28, 18, 'فَاَصْبَحَ فِى الْمَدِيْنَةِ خَاۤىِٕفًا يَّتَرَقَّبُ فَاِذَا الَّذِى اسْتَنْصَرَهٗ بِالْاَمْسِ يَسْتَصْرِخُهٗ ۗقَالَ لَهٗ مُوْسٰٓى اِنَّكَ لَغَوِيٌّ مُّبِيْنٌ ', 'fa-a<u>sh</u>ba<u>h</u>a fii <strong>a</strong>lmadiinati kh<u>aa</u>-ifan yataraqqabu fa-i<u>dzaa</u> <strong>al</strong>la<u>dz</u>ii istan<u>sh</u>arahu bi<strong>a</strong>l-amsi yasta<u>sh</u>rikhuhu q<u>aa</u>la lahu muus<u>aa</u> innaka laghawiyyun mubiin<strong>un</strong>', 'Karena itu, dia (Musa) menjadi ketakutan berada di kota itu sambil menunggu (akibat perbuatannya), tiba-tiba orang yang kemarin meminta pertolongan berteriak meminta pertolongan kepadanya. Musa berkata kepadanya, “Engkau sungguh, orang yang nyata-nyata sesat.”'),
	(3271, 28, 19, 'فَلَمَّآ اَنْ اَرَادَ اَنْ يَّبْطِشَ بِالَّذِيْ هُوَ عَدُوٌّ لَّهُمَاۙ قَالَ يٰمُوْسٰٓى اَتُرِيْدُ اَنْ تَقْتُلَنِيْ كَمَا قَتَلْتَ نَفْسًاۢ بِالْاَمْسِۖ اِنْ تُرِيْدُ اِلَّآ اَنْ تَكُوْنَ جَبَّارًا فِى الْاَرْضِ وَمَا تُرِيْدُ اَنْ تَكُوْنَ مِنَ الْمُص', 'falamm<u>aa</u> an ar<u>aa</u>da an yab<u>th</u>isya bi<strong>a</strong>lla<u>dz</u>ii huwa \'aduwwun lahum<u>aa</u> q<u>aa</u>la y<u>aa</u> muus<u>aa</u> aturiidu an taqtulanii kam<u>aa</u> qatalta nafsan bi<strong>a</strong>l-amsi in turiidu ill<u>aa</u> an takuuna jabb<u>aa</u>ran fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> turiidu an takuuna mina <strong>a</strong>lmu<u>sh</u>li<u>h</u>iin<strong>a</strong>', 'Maka ketika dia (Musa) hendak memukul dengan keras orang yang menjadi musuh mereka berdua, dia (musuhnya) berkata, “Wahai Musa! Apakah engkau bermaksud membunuhku, sebagaimana kemarin engkau membunuh seseorang? Engkau hanya bermaksud menjadi orang yang berbuat sewenang-wenang di negeri (ini), dan engkau tidak bermaksud menjadi salah seorang dari orang-orang yang mengadakan perdamaian.”'),
	(3272, 28, 20, 'وَجَاۤءَ رَجُلٌ مِّنْ اَقْصَى الْمَدِيْنَةِ يَسْعٰىۖ قَالَ يٰمُوْسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُوْنَ بِكَ لِيَقْتُلُوْكَ فَاخْرُجْ اِنِّيْ لَكَ مِنَ النّٰصِحِيْنَ ', 'waj<u>aa</u>-a rajulun min aq<u>shaa</u> <strong>a</strong>lmadiinati yas\'<u>aa</u> q<u>aa</u>la y<u>aa</u> muus<u>aa</u> inna <strong>a</strong>lmala-a ya/tamiruuna bika liyaqtuluuka fa<strong>u</strong>khruj innii laka mina <strong>al</strong>nn<u>aas</u>i<u>h</u>iin<strong>a</strong>', 'Dan seorang laki-laki datang bergegas dari ujung kota seraya berkata, “Wahai Musa! Sesungguhnya para pembesar negeri sedang berunding tentang engkau untuk membunuhmu, maka keluarlah (dari kota ini), sesungguhnya aku termasuk orang-orang yang memberi nasihat kepadamu.”'),
	(3273, 28, 21, 'فَخَرَجَ مِنْهَا خَاۤىِٕفًا يَّتَرَقَّبُ ۖقَالَ رَبِّ نَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ࣖ ', 'fakharaja minh<u>aa</u> kh<u>aa</u>-ifan yataraqqabu q<u>aa</u>la rabbi najjinii mina <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Maka keluarlah dia (Musa) dari kota itu dengan rasa takut, waspada (kalau ada yang menyusul atau menangkapnya), dia berdoa, “Ya Tuhanku, selamatkanlah aku dari orang-orang yang zalim itu.”'),
	(3274, 28, 22, 'وَلَمَّا تَوَجَّهَ تِلْقَاۤءَ مَدْيَنَ قَالَ عَسٰى رَبِّيْٓ اَنْ يَّهْدِيَنِيْ سَوَاۤءَ السَّبِيْلِ ', 'walamm<u>aa</u> tawajjaha tilq<u>aa</u>-a madyana q<u>aa</u>la \'as<u>aa</u> rabbii an yahdiyanii saw<u>aa</u>-a <strong>al</strong>ssabiil<strong>i</strong>', 'Dan ketika dia menuju ke arah negeri Madyan dia berdoa lagi, “Mudah-mudahan Tuhanku memimpin aku ke jalan yang benar.”'),
	(3275, 28, 23, 'وَلَمَّا وَرَدَ مَاۤءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِّنَ النَّاسِ يَسْقُوْنَ ەۖ وَوَجَدَ مِنْ دُوْنِهِمُ امْرَاَتَيْنِ تَذُوْدٰنِۚ قَالَ مَا خَطْبُكُمَا ۗقَالَتَا لَا نَسْقِيْ حَتّٰى يُصْدِرَ الرِّعَاۤءُ وَاَبُوْنَا شَيْخٌ كَبِيْرٌ', 'walamm<u>aa</u> warada m<u>aa</u>-a madyana wajada \'alayhi ummatan mina <strong>al</strong>nn<u>aa</u>si yasquuna wawajada min duunihimu imra-atayni ta<u>dz</u>uud<u>aa</u>ni q<u>aa</u>la m<u>aa</u> kha<u>th</u>bukum<u>aa</u> q<u>aa</u>lat<u>aa</u> l<u>aa</u> nasqii <u>h</u>att<u>aa</u> yu<u>sh</u>dira <strong>al</strong>rri\'<u>aa</u>u wa-abuun<u>aa</u> syaykhun kabiir<strong>un</strong>', 'Dan ketika dia sampai di sumber air negeri Madyan, dia menjumpai di sana sekumpulan orang yang sedang memberi minum (ternaknya), dan dia menjumpai di belakang orang banyak itu, dua orang perempuan sedang menghambat (ternaknya). Dia (Musa) berkata, “Apakah maksudmu (dengan berbuat begitu)?” Kedua (perempuan) itu menjawab, “Kami tidak dapat memberi minum (ternak kami), sebelum penggembala-penggembala itu memulangkan (ternaknya), sedang ayah kami adalah orang tua yang telah lanjut usianya.”'),
	(3276, 28, 24, 'فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰىٓ اِلَى الظِّلِّ فَقَالَ رَبِّ اِنِّيْ لِمَآ اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَقِيْرٌ ', 'fasaq<u>aa</u> lahum<u>aa</u> tsumma tawall<u>aa</u> il<u>aa</u> <strong>al</strong><u>zhzh</u>illi faq<u>aa</u>la rabbi innii lim<u>aa</u> anzalta ilayya min khayrin faqiir<strong>un</strong>', 'Maka dia (Musa) memberi minum (ternak) kedua perempuan itu, kemudian dia kembali ke tempat yang teduh lalu berdoa, “Ya Tuhanku, sesungguhnya aku sangat memerlukan sesuatu kebaikan (makanan) yang Engkau turunkan kepadaku.”'),
	(3277, 28, 25, 'فَجَاۤءَتْهُ اِحْدٰىهُمَا تَمْشِيْ عَلَى اسْتِحْيَاۤءٍ  ۖقَالَتْ اِنَّ اَبِيْ يَدْعُوْكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۗ فَلَمَّا جَاۤءَهٗ وَقَصَّ عَلَيْهِ الْقَصَصَۙ  قَالَ لَا تَخَفْۗ نَجَوْتَ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ', 'faj<u>aa</u>-at-hu i<u>h</u>d<u>aa</u>hum<u>aa</u> tamsyii \'al<u>aa</u> isti<u>h</u>y<u>aa</u>-in q<u>aa</u>lat inna abii yad\'uuka liyajziyaka ajra m<u>aa</u> saqayta lan<u>aa</u> falamm<u>aa</u> j<u>aa</u>-ahu waqa<u>shsh</u>a \'alayhi <strong>a</strong>lqa<u>sh</u>a<u>sh</u>a q<u>aa</u>la l<u>aa</u> takhaf najawta mina <strong>a</strong>lqawmi <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Kemudian datanglah kepada Musa salah seorang dari kedua perempuan itu berjalan dengan malu-malu, dia berkata, “Sesungguhnya ayahku mengundangmu untuk memberi balasan sebagai imbalan atas (kebaikan)mu memberi minum (ternak) kami.” Ketika (Musa) mendatangi ayahnya  dan dia menceritakan kepadanya kisah (mengenai dirinya), dia berkata, “Janganlah engkau takut! Engkau telah selamat dari orang-orang yang zalim itu.”'),
	(3278, 28, 26, 'قَالَتْ اِحْدٰىهُمَا يٰٓاَبَتِ اسْتَأْجِرْهُ ۖاِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَمِيْنُ ', 'q<u>aa</u>lat i<u>h</u>d<u>aa</u>hum<u>aa</u> y<u>aa</u> abati ista/jirhu inna khayra mani ista/jarta <strong>a</strong>lqawiyyu <strong>a</strong>l-amiin<strong>u</strong>', 'Dan salah seorang dari kedua (perempuan) itu berkata, “Wahai ayahku! Jadikanlah dia sebagai pekerja (pada kita), sesungguhnya orang yang paling baik yang engkau ambil sebagai pekerja (pada kita) ialah orang yang kuat dan dapat dipercaya.”'),
	(3279, 28, 27, 'قَالَ اِنِّيْٓ اُرِيْدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هٰتَيْنِ عَلٰٓى اَنْ تَأْجُرَنِيْ ثَمٰنِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَۚ وَمَآ اُرِيْدُ اَنْ اَشُقَّ عَلَيْكَۗ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰلِحِيْنَ ', 'q<u>aa</u>la innii uriidu an unki<u>h</u>aka i<u>h</u>d<u>aa</u> ibnatayya h<u>aa</u>tayni \'al<u>aa</u> an ta/juranii tsam<u>aa</u>niya <u>h</u>ijajin fa-in atmamta \'asyran famin \'indika wam<u>aa</u> uriidu an asyuqqa \'alayka satajidunii in sy<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu mina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dia (Syekh Madyan) berkata, “Sesungguhnya aku bermaksud ingin menikahkan engkau dengan salah seorang dari kedua anak perempuanku ini, dengan ketentuan bahwa engkau bekerja padaku selama delapan tahun dan jika engkau sempurnakan sepuluh tahun maka itu adalah (suatu kebaikan) darimu, dan aku tidak bermaksud memberatkan engkau. Insya Allah engkau akan mendapatiku termasuk orang yang baik.”'),
	(3280, 28, 28, 'قَالَ ذٰلِكَ بَيْنِيْ وَبَيْنَكَۗ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۗ وَاللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ ࣖ', 'q<u>aa</u>la <u>dzaa</u>lika baynii wabaynaka ayyam<u>aa</u> <strong>a</strong>l-ajalayni qa<u>dh</u>aytu fal<u>aa</u> \'udw<u>aa</u>na \'alayya wa<strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> m<u>aa</u> naquulu wakiil<strong>un</strong>', 'Dia (Musa) berkata, “Itu (perjanjian) antara aku dan engkau. Yang mana saja dari kedua waktu yang ditentukan itu yang aku sempurnakan, maka tidak ada tuntutan (tambahan) atas diriku (lagi). Dan Allah menjadi saksi atas apa yang kita ucapkan.”'),
	(3281, 28, 29, '۞ فَلَمَّا قَضٰى مُوْسَى الْاَجَلَ وَسَارَ بِاَهْلِهٖٓ اٰنَسَ مِنْ جَانِبِ الطُّوْرِ نَارًاۗ قَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْٓ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِّنَ النَّارِ لَعَلَّكُمْ تَصْطَلُوْنَ', 'falamm<u>aa</u> qa<u>daa</u> muus<u>aa</u> <strong>a</strong>l-ajala was<u>aa</u>ra bi-ahlihi <u>aa</u>nasa min j<u>aa</u>nibi <strong>al</strong><u>ththh</u>uuri n<u>aa</u>ran q<u>aa</u>la li-ahlihi umkutsuu innii <u>aa</u>nastu n<u>aa</u>ran la\'allii <u>aa</u>tiikum minh<u>aa</u> bikhabarin aw ja<u>dz</u>watin mina <strong>al</strong>nn<u>aa</u>ri la\'allakum ta<u>sth</u>aluun<strong>a</strong>', 'Maka ketika Musa telah menyelesaikan waktu yang ditentukan itu dan dia berangkat dengan keluarganya, dia melihat api di lereng gunung. Dia berkata kepada keluarganya, “Tunggulah (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa suatu berita kepadamu dari (tempat) api itu atau (membawa) sepercik api, agar kamu dapat menghangatkan badan.”'),
	(3282, 28, 30, 'فَلَمَّآ اَتٰىهَا نُوْدِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِى الْبُقْعَةِ الْمُبٰرَكَةِ مِنَ الشَّجَرَةِ اَنْ يّٰمُوْسٰٓى اِنِّيْٓ اَنَا اللّٰهُ رَبُّ الْعٰلَمِيْنَ ۙ', 'falamm<u>aa</u> at<u>aa</u>h<u>aa</u> nuudiya min sy<u>aath</u>i-i <strong>a</strong>lw<u>aa</u>di <strong>a</strong>l-aymani fii <strong>a</strong>lbuq\'ati <strong>a</strong>lmub<u>aa</u>rakati mina <strong>al</strong>sysyajarati an y<u>aa</u> muus<u>aa</u> innii an<u>aa</u> <strong>al</strong>l<u>aa</u>hu rabbu <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Maka ketika dia (Musa) sampai ke (tempat) api itu, dia diseru dari (arah) pinggir sebelah kanan lembah, dari sebatang pohon, di sebidang tanah yang diberkahi, “Wahai Musa! Sungguh, Aku adalah Allah, Tuhan seluruh alam!'),
	(3283, 28, 31, 'وَاَنْ اَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰٓى اَقْبِلْ وَلَا تَخَفْۗ اِنَّكَ مِنَ الْاٰمِنِيْنَ ', 'wa-an <strong>a</strong>lqi \'a<u>shaa</u>ka falamm<u>aa</u> ra<u>aa</u>h<u>aa</u> tahtazzu ka-annah<u>aa</u> j<u>aa</u>nnun wall<u>aa</u> mudbiran walam yu\'aqqib y<u>aa</u> muus<u>aa</u> aqbil wal<u>aa</u> takhaf innaka mina <strong>a</strong>l-<u>aa</u>miniin<strong>a</strong>', 'Dan lemparkanlah tongkatmu.” Maka ketika dia (Musa) melihatnya bergerak-gerak seakan-akan seekor ular yang (gesit), dia lari berbalik ke belakang tanpa menoleh. (Allah berfirman), “Wahai Musa! Kemarilah dan jangan takut. Sesungguhnya engkau termasuk orang yang aman.'),
	(3284, 28, 32, 'اُسْلُكْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ ۖوَّاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ  فَذٰنِكَ بُرْهَانٰنِ مِنْ رَّبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ', 'usluk yadaka fii jaybika takhruj bay<u>dhaa</u>-a min ghayri suu-in wa<strong>u</strong><u>dh</u>mum ilayka jan<u>aah</u>aka mina <strong>al</strong>rrahbi fa<u>dzaa</u>nika burh<u>aa</u>n<u>aa</u>ni min rabbika il<u>aa</u> fir\'awna wamala-ihi innahum k<u>aa</u>nuu qawman f<u>aa</u>siqiin<strong>a</strong>', 'Masukkanlah tanganmu ke leher bajumu, dia akan keluar putih (bercahaya) tanpa cacat, dan dekapkanlah kedua tanganmu ke dadamu apabila ketakutan. Itulah dua mukjizat dari Tuhanmu (yang akan engkau pertunjukkan) kepada Fir‘aun dan para pembesarnya. Sungguh, mereka adalah orang-orang fasik.”    '),
	(3285, 28, 33, 'قَالَ رَبِّ اِنِّيْ قَتَلْتُ مِنْهُمْ نَفْسًا فَاَخَافُ اَنْ يَّقْتُلُوْنِ ', 'q<u>aa</u>la rabbi innii qataltu minhum nafsan fa-akh<u>aa</u>fu an yaqtuluun<strong>i</strong>', 'Dia (Musa) berkata, “Ya Tuhanku, sungguh aku telah membunuh seorang dari golongan mereka, sehingga aku takut mereka akan membunuhku. '),
	(3286, 28, 34, 'وَاَخِيْ هٰرُوْنُ هُوَ اَفْصَحُ مِنِّيْ لِسَانًا فَاَرْسِلْهُ مَعِيَ رِدْءًا يُّصَدِّقُنِيْٓ  ۖاِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ', 'wa-akhii h<u>aa</u>ruunu huwa af<u>sh</u>a<u>h</u>u minnii lis<u>aa</u>nan fa-arsilhu ma\'iya rid-an yu<u>sh</u>addiqunii innii akh<u>aa</u>fu an yuka<u>dzdz</u>ibuun<strong>i</strong>', 'Dan saudaraku Harun, dia lebih fasih lidahnya daripada aku, maka utuslah dia bersamaku sebagai pembantuku untuk membenarkan (perkataan)ku; sungguh, aku takut mereka akan mendustakanku.” '),
	(3287, 28, 35, 'قَالَ سَنَشُدُّ عَضُدَكَ بِاَخِيْكَ وَنَجْعَلُ لَكُمَا سُلْطٰنًا فَلَا يَصِلُوْنَ اِلَيْكُمَا  ۛبِاٰيٰتِنَا  ۛ اَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغٰلِبُوْنَ ', 'q<u>aa</u>la sanasyuddu \'a<u>dh</u>udaka bi-akhiika wanaj\'alu lakum<u>aa</u> sul<u>th</u><u>aa</u>nan fal<u>aa</u> ya<u>sh</u>iluuna ilaykum<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> antum<u>aa</u> wamani ittaba\'akum<u>aa</u> <strong>a</strong>lgh<u>aa</u>libuun<strong>a</strong>', 'Dia (Allah) berfirman, “Kami akan menguatkan engkau (membantumu) dengan saudaramu, dan Kami berikan kepadamu berdua kekuasaan yang besar, maka mereka tidak akan dapat mencapaimu; (berangkatlah kamu berdua) dengan membawa mukjizat Kami, kamu berdua dan orang yang mengikuti kamu yang akan menang.” '),
	(3288, 28, 36, 'فَلَمَّا جَاۤءَهُمْ مُّوْسٰى بِاٰيٰتِنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا سِحْرٌ مُّفْتَرًىۙ وَّمَا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ', 'falamm<u>aa</u> j<u>aa</u>-ahum muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>luu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run muftaran wam<u>aa</u> sami\'n<u>aa</u> bih<u>aadzaa</u> fii <u>aa</u>b<u>aa</u>-in<u>aa</u> <strong>a</strong>l-awwaliin<strong>a</strong>', 'Maka ketika Musa datang kepada mereka dengan (membawa) mukjizat Kami yang nyata, mereka berkata, “Ini hanyalah sihir yang dibuat-buat, dan kami tidak pernah mendengar (yang seperti) ini pada nenek moyang kami dahulu.” '),
	(3289, 28, 37, 'وَقَالَ مُوْسٰى رَبِّيْٓ اَعْلَمُ بِمَنْ جَاۤءَ بِالْهُدٰى مِنْ عِنْدِهٖ وَمَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ', 'waq<u>aa</u>la muus<u>aa</u> rabbii a\'lamu biman j<u>aa</u>-a bi<strong>a</strong>lhud<u>aa</u> min \'indihi waman takuunu lahu \'<u>aa</u>qibatu <strong>al</strong>dd<u>aa</u>ri innahu l<u>aa</u> yufli<u>h</u>u <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Dan dia (Musa) menjawab, “Tuhanku lebih mengetahui siapa yang (pantas) membawa petunjuk dari sisi-Nya dan siapa yang akan mendapat kesudahan (yang baik) di akhirat. Sesungguhnya orang-orang yang zalim tidak akan mendapat kemenangan.” '),
	(3290, 28, 38, 'وَقَالَ فِرْعَوْنُ يٰٓاَيُّهَا الْمَلَاُ مَا عَلِمْتُ لَكُمْ مِّنْ اِلٰهٍ غَيْرِيْۚ فَاَوْقِدْ لِيْ يٰهَامٰنُ عَلَى الطِّيْنِ فَاجْعَلْ لِّيْ صَرْحًا لَّعَلِّيْٓ اَطَّلِعُ اِلٰٓى اِلٰهِ مُوْسٰىۙ وَاِنِّيْ لَاَظُنُّهٗ مِنَ الْكٰذِبِيْنَ ', 'waq<u>aa</u>la fir\'awnu y<u>aa</u> ayyuh<u>aa</u> <strong>a</strong>lmalau m<u>aa</u> \'alimtu lakum min il<u>aa</u>hin ghayrii fa-awqid lii y<u>aa</u> h<u>aa</u>m<u>aa</u>nu \'al<u>aa</u> <strong>al</strong><u>ththh</u>iini fa<strong>i</strong>j\'al lii <u>sh</u>ar<u>h</u>an la\'allii a<u>ththh</u>ali\'u il<u>aa</u> il<u>aa</u>hi muus<u>aa</u> wa-innii la-a<u>zh</u>unnuhu mina <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dan Fir‘aun berkata, “Wahai para pembesar kaumku! Aku tidak mengetahui ada Tuhan bagimu selain aku. Maka bakarlah tanah liat untukku wahai Haman (untuk membuat batu bata), kemudian buatkanlah bangunan yang tinggi untukku agar aku dapat naik melihat Tuhannya Musa, dan aku yakin bahwa dia termasuk pendusta.” '),
	(3291, 28, 39, 'وَاسْتَكْبَرَ هُوَ وَجُنُوْدُهٗ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوْٓا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُوْنَ ', 'wa<strong>i</strong>stakbara huwa wajunuuduhu fii <strong>a</strong>l-ar<u>dh</u>i bighayri <strong>a</strong>l<u>h</u>aqqi wa<u>zh</u>annuu annahum ilayn<u>aa</u> l<u>aa</u> yurja\'uun<strong>a</strong>', 'Dan dia (Fir‘aun) dan bala tentaranya berlaku sombong, di bumi tanpa alasan yang benar, dan mereka mengira bahwa mereka tidak akan dikembalikan kepada Kami.'),
	(3292, 28, 40, 'فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ ۚفَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ', 'fa-akha<u>dz</u>n<u>aa</u>hu wajunuudahu fanaba<u>dz</u>n<u>aa</u>hum fii <strong>a</strong>lyammi fa<strong>u</strong>n<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Maka Kami siksa dia (Fir‘aun) dan bala tentaranya, lalu Kami lemparkan mereka ke dalam laut. Maka perhatikanlah bagaimana kesudahan orang yang zalim.'),
	(3293, 28, 41, 'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّدْعُوْنَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُوْنَ ', 'waja\'aln<u>aa</u>hum a-immatan yad\'uuna il<u>aa</u> <strong>al</strong>nn<u>aa</u>ri wayawma <strong>a</strong>lqiy<u>aa</u>mati l<u>aa</u> yun<u>sh</u>aruun<strong>a</strong>', 'Dan Kami jadikan mereka para pemimpin yang mengajak (manusia) ke neraka dan pada hari Kiamat mereka tidak akan ditolong.'),
	(3294, 28, 42, 'وَاَتْبَعْنٰهُمْ فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً ۚوَيَوْمَ الْقِيٰمَةِ هُمْ مِّنَ الْمَقْبُوْحِيْنَ ࣖ', 'wa-atba\'n<u>aa</u>hum fii h<u>aadz</u>ihi <strong>al</strong>dduny<u>aa</u> la\'natan wayawma <strong>a</strong>lqiy<u>aa</u>mati hum mina <strong>a</strong>lmaqbuu<u>h</u>iin<strong>a</strong>', 'Dan Kami susulkan laknat kepada mereka di dunia ini; sedangkan pada hari Kiamat mereka termasuk orang-orang yang dijauhkan (dari rahmat Allah).'),
	(3295, 28, 43, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ مِنْۢ بَعْدِ مَآ اَهْلَكْنَا الْقُرُوْنَ الْاُوْلٰى بَصَاۤىِٕرَ لِلنَّاسِ وَهُدًى وَّرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُوْنَ ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba min ba\'di m<u>aa</u> ahlakn<u>aa</u> <strong>a</strong>lquruuna <strong>a</strong>l-uul<u>aa</u> ba<u>shaa</u>-ira li<strong>l</strong>nn<u>aa</u>si wahudan wara<u>h</u>matan la\'allahum yata<u>dz</u>akkaruun<strong>a</strong>', 'Dan sungguh, telah Kami berikan kepada Musa Kitab (Taurat) setelah Kami binasakan umat-umat terdahulu, untuk menjadi pelita bagi manusia dan petunjuk serta rahmat, agar mereka mendapat pelajaran.'),
	(3296, 28, 44, 'وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَآ اِلٰى مُوْسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشّٰهِدِيْنَ ۙ', 'wam<u>aa</u> kunta bij<u>aa</u>nibi <strong>a</strong>lgharbiyyi i<u>dz</u> qa<u>dh</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> <strong>a</strong>l-amra wam<u>aa</u> kunta mina <strong>al</strong>sysy<u>aa</u>hidiin<strong>a</strong>', 'Dan engkau (Muhammad) tidak berada di sebelah barat (lembah suci Tuwa) ketika Kami menyampaikan perintah kepada Musa, dan engkau tidak (pula) termasuk orang-orang yang menyaksikan (kejadian itu).'),
	(3297, 28, 45, 'وَلٰكِنَّآ اَنْشَأْنَا قُرُوْنًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِيًا فِيْٓ اَهْلِ مَدْيَنَ تَتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِلِيْنَ ', 'wal<u>aa</u>kinn<u>aa</u> ansya-n<u>aa</u> quruunan fata<u>th</u><u>aa</u>wala \'alayhimu <strong>a</strong>l\'umuru wam<u>aa</u> kunta ts<u>aa</u>wiyan fii ahli madyana tatluu \'alayhim <u>aa</u>y<u>aa</u>tin<u>aa</u> wal<u>aa</u>kinn<u>aa</u> kunn<u>aa</u> mursiliin<strong>a</strong>', 'Tetapi Kami telah menciptakan beberapa umat, dan telah berlalu atas mereka masa yang panjang, dan engkau (Muhammad) tidak tinggal bersama-sama penduduk Madyan dengan membacakan ayat-ayat Kami kepada mereka, tetapi Kami telah mengutus rasul-rasul.'),
	(3298, 28, 46, 'وَمَا كُنْتَ بِجَانِبِ الطُّوْرِ اِذْ نَادَيْنَا وَلٰكِنْ رَّحْمَةً مِّنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ', 'wam<u>aa</u> kunta bij<u>aa</u>nibi <strong>al</strong><u>ththh</u>uuri i<u>dz</u> n<u>aa</u>dayn<u>aa</u> wal<u>aa</u>kin ra<u>h</u>matan min rabbika litun<u>dz</u>ira qawman m<u>aa</u> at<u>aa</u>hum min na<u>dz</u>iirin min qablika la\'allahum yata<u>dz</u>akkaruun<strong>a</strong>', 'Dan engkau (Muhammad) tidak berada di dekat Tur (gunung) ketika Kami menyeru (Musa), tetapi (Kami utus engkau) sebagai rahmat dari Tuhanmu, agar engkau memberi peringatan kepada kaum (Quraisy) yang tidak didatangi oleh pemberi peringatan sebelum engkau agar mereka mendapat pelajaran.'),
	(3299, 28, 47, 'وَلَوْلَآ اَنْ تُصِيْبَهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَيَقُوْلُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ', 'walawl<u>aa</u> an tu<u>sh</u>iibahum mu<u>sh</u>iibatun bim<u>aa</u> qaddamat aydiihim fayaquuluu rabban<u>aa</u> lawl<u>aa</u> arsalta ilayn<u>aa</u> rasuulan fanattabi\'a <u>aa</u>y<u>aa</u>tika wanakuuna mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan agar mereka tidak mengatakan ketika azab menimpa mereka disebabkan apa yang mereka kerjakan, “Ya Tuhan kami, mengapa Engkau tidak mengutus seorang rasul kepada kami, agar kami mengikuti ayat-ayat Engkau dan termasuk orang mukmin.”'),
	(3300, 28, 48, 'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْا لَوْلَآ اُوْتِيَ مِثْلَ مَآ اُوْتِيَ مُوْسٰىۗ اَوَلَمْ يَكْفُرُوْا بِمَآ اُوْتِيَ مُوْسٰى مِنْ قَبْلُۚ قَالُوْا سِحْرٰنِ تَظَاهَرَاۗ وَقَالُوْٓا اِنَّا بِكُلٍّ كٰفِرُوْنَ ', 'falamm<u>aa</u> j<u>aa</u>-ahumu <strong>a</strong>l<u>h</u>aqqu min \'indin<u>aa</u> q<u>aa</u>luu lawl<u>aa</u> uutiya mitsla m<u>aa</u> uutiya muus<u>aa</u> awa lam yakfuruu bim<u>aa</u> uutiya muus<u>aa</u> min qablu q<u>aa</u>luu si<u>h</u>r<u>aa</u>ni ta<u>zhaa</u>har<u>aa</u> waq<u>aa</u>luu inn<u>aa</u> bikullin k<u>aa</u>firuun<strong>a</strong>', 'Maka ketika telah datang kepada mereka kebenaran (Al-Qur\'an) dari sisi Kami, mereka berkata, “Mengapa tidak diberikan kepadanya (Muhammad) seperti apa yang telah diberikan kepada Musa dahulu?” Bukankah mereka itu telah ingkar (juga) kepada apa yang diberikan kepada Musa dahulu? Mereka dahulu berkata, “(Musa dan Harun adalah) dua pesihir yang bantu-membantu.” Dan mereka (juga) berkata, “Sesungguhnya kami sama sekali tidak mempercayai masing-masing mereka itu.”'),
	(3301, 28, 49, 'قُلْ فَأْتُوْا بِكِتٰبٍ مِّنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَآ اَتَّبِعْهُ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'qul fa/tuu bikit<u>aa</u>bin min \'indi <strong>al</strong>l<u>aa</u>hi huwa ahd<u>aa</u> minhum<u>aa</u> attabi\'hu in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Katakanlah (Muhammad), “Datangkanlah olehmu sebuah kitab dari sisi Allah yang kitab itu lebih memberi petunjuk daripada keduanya (Taurat dan Al-Qur\'an), niscaya aku mengikutinya, jika kamu orang yang benar.”'),
	(3302, 28, 50, 'فَاِنْ لَّمْ يَسْتَجِيْبُوْا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُوْنَ اَهْوَاۤءَهُمْۗ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰىهُ بِغَيْرِ هُدًى مِّنَ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ࣖ', 'fa-in lam yastajiibuu laka fa<strong>i</strong>\'lam annam<u>aa</u> yattabi\'uuna ahw<u>aa</u>-ahum waman a<u>dh</u>allu mimmani ittaba\'a haw<u>aa</u>hu bighayri hudan mina <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yahdii <strong>a</strong>lqawma <strong>al</strong><u>zhzhaa</u>limiin<strong>a</strong>', 'Maka jika mereka tidak menjawab (tantanganmu), maka ketahuilah bahwa mereka hanyalah mengikuti keinginan mereka. Dan siapakah yang lebih sesat daripada orang yang mengikuti keinginannya tanpa mendapat petunjuk dari Allah sedikit pun? Sungguh, Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
	(3303, 28, 51, '۞ وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ۗ ', 'walaqad wa<u>shsh</u>aln<u>aa</u> lahumu <strong>a</strong>lqawla la\'allahum yata<u>dz</u>akkaruun<strong>a</strong>', 'Dan sungguh, Kami telah menyampaikan perkataan ini (Al-Qur\'an) kepada mereka agar mereka selalu mengingatnya.'),
	(3304, 28, 52, 'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ مِنْ قَبْلِهٖ هُمْ بِهٖ يُؤْمِنُوْنَ ', '<strong>al</strong>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba min qablihi hum bihi yu/minuun<strong>a</strong>', 'Orang-orang yang telah Kami berikan kepada mereka Al-Kitab sebelum Al-Qur\'an, mereka beriman (pula) kepadanya (Al-Qur\'an).'),
	(3305, 28, 53, 'وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُوْٓا اٰمَنَّا بِهٖٓ اِنَّهُ الْحَقُّ مِنْ رَّبِّنَآ اِنَّا كُنَّا مِنْ قَبْلِهٖ مُسْلِمِيْنَ ', 'wa-i<u>dzaa</u> yutl<u>aa</u> \'alayhim q<u>aa</u>luu <u>aa</u>mann<u>aa</u> bihi innahu <strong>a</strong>l<u>h</u>aqqu min rabbin<u>aa</u> inn<u>aa</u> kunn<u>aa</u> min qablihi muslimiin<strong>a</strong>', 'Dan apabila (Al-Qur\'an) dibacakan kepada mereka, mereka berkata, “Kami beriman kepadanya, sesungguhnya (Al-Qur\'an) itu adalah suatu kebenaran dari Tuhan kami. Sungguh, sebelumnya kami adalah orang muslim.”'),
	(3306, 28, 54, 'اُولٰۤىِٕكَ يُؤْتَوْنَ اَجْرَهُمْ مَّرَّتَيْنِ بِمَا صَبَرُوْا وَيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ', 'ul<u>aa</u>-ika yu/tawna ajrahum marratayni bim<u>aa</u> <u>sh</u>abaruu wayadrauuna bi<strong>a</strong>l<u>h</u>asanati <strong>al</strong>ssayyi-ata wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun<strong>a</strong>', 'Mereka itu diberi pahala dua kali (karena beriman kepada Taurat dan Al-Qur\'an) disebabkan kesabaran mereka, dan mereka menolak kejahatan dengan kebaikan, dan menginfakkan sebagian dari rezeki yang telah Kami berikan kepada mereka.'),
	(3307, 28, 55, 'وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوْا عَنْهُ وَقَالُوْا لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۖسَلٰمٌ عَلَيْكُمْ ۖ لَا نَبْتَغِى الْجٰهِلِيْنَ', 'wa-i<u>dzaa</u> sami\'uu <strong>al</strong>laghwa a\'ra<u>dh</u>uu \'anhu waq<u>aa</u>luu lan<u>aa</u> a\'m<u>aa</u>lun<u>aa</u> walakum a\'m<u>aa</u>lukum sal<u>aa</u>mun \'alaykum l<u>aa</u> nabtaghii <strong>a</strong>lj<u>aa</u>hiliin<strong>a</strong>', 'Dan apabila mereka mendengar perkataan yang buruk, mereka berpaling darinya dan berkata, “Bagi kami amal-amal kami dan bagimu amal-amal kamu, semoga selamatlah kamu, kami tidak ingin (bergaul) dengan orang-orang bodoh.”'),
	(3308, 28, 56, 'اِنَّكَ لَا تَهْدِيْ مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۚوَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ', 'innaka l<u>aa</u> tahdii man a<u>h</u>babta wal<u>aa</u>kinna <strong>al</strong>l<u>aa</u>ha yahdii man yasy<u>aa</u>u wahuwa a\'lamu bi<strong>a</strong>lmuhtadiin<strong>a</strong>', 'Sungguh, engkau (Muhammad) tidak dapat memberi petunjuk kepada orang yang engkau kasihi, tetapi Allah memberi petunjuk kepada orang yang Dia kehendaki, dan Dia lebih mengetahui orang-orang yang mau menerima  petunjuk.'),
	(3309, 28, 57, 'وَقَالُوْٓا اِنْ نَّتَّبِعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۗ اَوَلَمْ نُمَكِّنْ لَّهُمْ حَرَمًا اٰمِنًا يُّجْبٰٓى اِلَيْهِ ثَمَرٰتُ كُلِّ شَيْءٍ رِّزْقًا مِّنْ لَّدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ', 'waq<u>aa</u>luu in nattabi\'i <strong>a</strong>lhud<u>aa</u> ma\'aka nutakha<u>ththh</u>af min ar<u>dh</u>in<u>aa</u> awa lam numakkin lahum <u>h</u>araman <u>aa</u>minan yujb<u>aa</u> ilayhi tsamar<u>aa</u>tu kulli syay-in rizqan min ladunn<u>aa</u> wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan mereka berkata, “Jika kami mengikuti petunjuk bersama engkau, niscaya kami akan diusir dari negeri kami.” (Allah berfirman) Bukankah Kami telah meneguhkan kedudukan mereka dalam tanah haram (tanah suci) yang aman, yang didatangkan ke tempat itu buah-buahan dari segala macam (tumbuh-tumbuhan) sebagai rezeki (bagimu) dari sisi Kami? Tetapi kebanyakan mereka tidak mengetahui.'),
	(3310, 28, 58, 'وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ ۢ بَطِرَتْ مَعِيْشَتَهَا ۚفَتِلْكَ مَسٰكِنُهُمْ لَمْ تُسْكَنْ مِّنْۢ بَعْدِهِمْ اِلَّا قَلِيْلًاۗ وَكُنَّا نَحْنُ الْوَارِثِيْنَ ', 'wakam ahlakn<u>aa</u> min qaryatin ba<u>th</u>irat ma\'iisyatah<u>aa</u> fatilka mas<u>aa</u>kinuhum lam tuskan min ba\'dihim ill<u>aa</u> qaliilan wakunn<u>aa</u> na<u>h</u>nu <strong>a</strong>lw<u>aa</u>ritsiin<strong>a</strong>', 'Dan betapa banyak (penduduk) negeri yang sudah bersenang-senang dalam kehidupannya yang telah Kami binasakan, maka itulah tempat kediaman mereka yang tidak didiami (lagi) setelah mereka, kecuali sebagian kecil. Dan Kamilah yang mewarisinya.” '),
	(3311, 28, 59, 'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ فِيْٓ اُمِّهَا رَسُوْلًا يَّتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۚ وَمَا كُنَّا مُهْلِكِى الْقُرٰىٓ اِلَّا وَاَهْلُهَا ظٰلِمُوْنَ ', 'wam<u>aa</u> k<u>aa</u>na rabbuka muhlika <strong>a</strong>lqur<u>aa</u> <u>h</u>att<u>aa</u> yab\'atsa fii ummih<u>aa</u> rasuulan yatluu \'alayhim <u>aa</u>y<u>aa</u>tin<u>aa</u> wam<u>aa</u> kunn<u>aa</u> muhlikii <strong>a</strong>lqur<u>aa</u> ill<u>aa</u> wa-ahluh<u>aa</u> <u>zhaa</u>limuun<strong>a</strong>', 'Dan Tuhanmu tidak akan membinasakan negeri-negeri, sebelum Dia mengutus seorang rasul di ibukotanya yang membacakan ayat-ayat Kami kepada mereka; dan tidak pernah (pula) Kami membinasakan (penduduk) negeri; kecuali penduduknya melakukan kezaliman. '),
	(3312, 28, 60, 'وَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَزِيْنَتُهَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰىۗ اَفَلَا تَعْقِلُوْنَ ࣖ', 'wam<u>aa</u> uutiitum min syay-in famat<u>aa</u>\'u <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> waziinatuh<u>aa</u> wam<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi khayrun wa-abq<u>aa</u> afal<u>aa</u> ta\'qiluun<strong>a</strong>', 'Dan apa saja (kekayaan, jabatan, keturunan) yang diberikan kepada kamu, maka itu adalah kesenangan hidup duniawi dan perhiasannya; sedang apa yang di sisi Allah adalah lebih baik dan lebih kekal. Tidakkah kamu mengerti?'),
	(3313, 28, 61, 'اَفَمَنْ وَّعَدْنٰهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيْهِ كَمَنْ مَّتَّعْنٰهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَرِيْنَ ', 'afaman wa\'adn<u>aa</u>hu wa\'dan <u>h</u>asanan fahuwa l<u>aa</u>qiihi kaman matta\'n<u>aa</u>hu mat<u>aa</u>\'a <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> tsumma huwa yawma <strong>a</strong>lqiy<u>aa</u>mati mina <strong>a</strong>lmu<u>hd</u>ariin<strong>a</strong>', 'Maka apakah sama orang yang Kami janjikan kepadanya suatu janji yang baik (surga) lalu dia memperolehnya, dengan orang yang Kami berikan kepadanya kesenangan hidup duniawi; kemudian pada hari Kiamat dia termasuk orang-orang yang diseret (ke dalam neraka)?'),
	(3314, 28, 62, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ', 'wayawma yun<u>aa</u>diihim fayaquulu ayna syurak<u>aa</u>-iya <strong>al</strong>la<u>dz</u>iina kuntum taz\'umuun<strong>a</strong>', 'Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka dan berfirman, “Di manakah sekutu-sekutu-Ku yang dahulu kamu sangka?”'),
	(3315, 28, 63, 'قَالَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُلَاۤءِ الَّذِيْنَ اَغْوَيْنَاۚ  اَغْوَيْنٰهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَآ اِلَيْكَ مَا كَانُوْٓا اِيَّانَا يَعْبُدُوْنَ ', 'q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina <u>h</u>aqqa \'alayhimu <strong>a</strong>lqawlu rabban<u>aa</u> h<u>aa</u>ul<u>aa</u>-i <strong>al</strong>la<u>dz</u>iina aghwayn<u>aa</u> aghwayn<u>aa</u>hum kam<u>aa</u> ghawayn<u>aa</u> tabarra/n<u>aa</u> ilayka m<u>aa</u> k<u>aa</u>nuu iyy<u>aa</u>n<u>aa</u> ya\'buduun<strong>a</strong>', 'Orang-orang yang sudah pasti akan mendapatkan hukuman berkata, “Ya Tuhan kami, mereka inilah orang-orang yang kami sesatkan itu; kami telah menyesatkan mereka sebagaimana kami (sendiri) sesat, kami menyatakan kepada Engkau berlepas diri (dari mereka), mereka sekali-kali tidak menyembah kami.” '),
	(3316, 28, 64, 'وَقِيْلَ ادْعُوْا شُرَكَاۤءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ ۗوَرَاَوُا الْعَذَابَۚ  لَوْ اَنَّهُمْ كَانُوْا يَهْتَدُوْنَ', 'waqiila ud\'uu syurak<u>aa</u>-akum fada\'awhum falam yastajiibuu lahum wara-awuu <strong>a</strong>l\'a<u>dzaa</u>ba law annahum k<u>aa</u>nuu yahtaduun<strong>a</strong>', 'Dan dikatakan (kepada mereka), “Serulah sekutu-sekutumu,” lalu mereka menyerunya, tetapi yang diseru tidak menyambutnya, dan mereka melihat azab. (Mereka itu berkeinginan) sekiranya mereka dahulu menerima petunjuk. '),
	(3317, 28, 65, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ مَاذَآ اَجَبْتُمُ الْمُرْسَلِيْنَ ', 'wayawma yun<u>aa</u>diihim fayaquulu m<u>aatsaa</u> ajabtumu <strong>a</strong>lmursaliin<strong>a</strong>', 'Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka, dan berfirman, “Apakah jawabanmu terhadap para rasul?”'),
	(3318, 28, 66, 'فَعَمِيَتْ عَلَيْهِمُ الْاَنْۢبَاۤءُ يَوْمَىِٕذٍ فَهُمْ لَا يَتَسَاۤءَلُوْنَ ', 'fa\'amiyat \'alayhimu <strong>a</strong>l-anb<u>aa</u>u yawma-i<u>dz</u>in fahum l<u>aa</u> yatas<u>aa</u>-aluun<strong>a</strong>', 'Maka gelaplah bagi mereka segala macam alasan pada hari itu, karena itu mereka tidak saling bertanya. '),
	(3319, 28, 67, 'فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَعَسٰٓى اَنْ يَّكُوْنَ مِنَ الْمُفْلِحِيْنَ ', 'fa-amm<u>aa</u> man t<u>aa</u>ba wa<u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an fa\'as<u>aa</u> an yakuuna mina <strong>a</strong>lmufli<u>h</u>iin<strong>a</strong>', 'Maka adapun orang yang bertobat dan beriman, serta mengerjakan kebajikan, maka mudah-mudahan dia termasuk orang yang beruntung. '),
	(3320, 28, 68, 'وَرَبُّكَ يَخْلُقُ مَا يَشَاۤءُ وَيَخْتَارُ ۗمَا كَانَ لَهُمُ الْخِيَرَةُ ۗسُبْحٰنَ اللّٰهِ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ', 'warabbuka yakhluqu m<u>aa</u> yasy<u>aa</u>u wayakht<u>aa</u>ru m<u>aa</u> k<u>aa</u>na lahumu <strong>a</strong>lkhiyaratu sub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Dan Tuhanmu menciptakan dan memilih apa yang Dia kehendaki. Bagi mereka (manusia) tidak ada pilihan. Mahasuci Allah dan Mahatinggi Dia dari apa yang mereka persekutukan.'),
	(3321, 28, 69, 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ', 'warabbuka ya\'lamu m<u>aa</u> tukinnu <u>sh</u>uduuruhum wam<u>aa</u> yu\'linuun<strong>a</strong>', 'Dan Tuhanmu mengetahui apa yang disembunyikan dalam dada mereka dan apa yang mereka nyatakan. '),
	(3322, 28, 70, 'وَهُوَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ لَهُ الْحَمْدُ فِى الْاُوْلٰى وَالْاٰخِرَةِ ۖوَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ', 'wahuwa <strong>al</strong>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa lahu <strong>a</strong>l<u>h</u>amdu fii <strong>a</strong>l-uul<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati walahu <strong>a</strong>l<u>h</u>ukmu wa-ilayhi turja\'uun<strong>a</strong>', 'Dan Dialah Allah, tidak ada tuhan (yang berhak disembah) selain Dia, segala puji bagi-Nya di dunia dan di akhirat, dan bagi-Nya segala penentuan dan kepada-Nya kamu dikembalikan.'),
	(3323, 28, 71, 'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِضِيَاۤءٍ ۗ اَفَلَا تَسْمَعُوْنَ ', 'qul ara-aytum in ja\'ala <strong>al</strong>l<u>aa</u>hu \'alaykumu <strong>al</strong>layla sarmadan il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati man il<u>aa</u>hun ghayru <strong>al</strong>l<u>aa</u>hi ya/tiikum bi<u>dh</u>iy<u>aa</u>-in afal<u>aa</u> tasma\'uun<strong>a</strong>', 'Katakanlah (Muhammad), “Bagaimana pendapatmu, jika Allah menjadikan untukmu malam itu terus-menerus sampai hari Kiamat. Siapakah tuhan selain Allah yang akan mendatangkan sinar terang kepadamu? Apakah kamu tidak mendengar?”'),
	(3324, 28, 72, 'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِلَيْلٍ تَسْكُنُوْنَ فِيْهِ ۗ اَفَلَا تُبْصِرُوْنَ ', 'qul ara-aytum in ja\'ala <strong>al</strong>l<u>aa</u>hu \'alaykumu <strong>al</strong>nnah<u>aa</u>ra sarmadan il<u>aa</u> yawmi <strong>a</strong>lqiy<u>aa</u>mati man il<u>aa</u>hun ghayru <strong>al</strong>l<u>aa</u>hi ya/tiikum bilaylin taskunuuna fiihi afal<u>aa</u> tub<u>sh</u>iruun<strong>a</strong>', 'Katakanlah (Muhammad), “Bagaimana pendapatmu, jika Allah menjadikan untukmu siang itu terus-menerus sampai hari Kiamat. Siapakah tuhan selain Allah yang akan mendatangkan malam kepadamu sebagai waktu istirahatmu? Apakah kamu tidak memperhatikan?”'),
	(3325, 28, 73, 'وَمِنْ رَّحْمَتِهٖ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوْا فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ', 'wamin ra<u>h</u>matihi ja\'ala lakumu <strong>al</strong>layla wa<strong>al</strong>nnah<u>aa</u>ra litaskunuu fiihi walitabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun<strong>a</strong>', 'Dan adalah karena rahmat-Nya, Dia jadikan untukmu malam dan siang, agar kamu beristirahat pada malam hari dan agar kamu mencari sebagian karunia-Nya (pada siang hari) dan agar kamu bersyukur kepada-Nya.'),
	(3326, 28, 74, 'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ', 'wayawma yun<u>aa</u>diihim fayaquulu ayna syurak<u>aa</u>-iya <strong>al</strong>la<u>dz</u>iina kuntum taz\'umuun<strong>a</strong>', 'Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka, dan berfirman, “Di manakah sekutu-sekutu-Ku yang dahulu kamu sangka?”'),
	(3327, 28, 75, 'وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَهِيْدًا فَقُلْنَا هَاتُوْا بُرْهَانَكُمْ فَعَلِمُوْٓا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ࣖ', 'wanaza\'n<u>aa</u> min kulli ummatin syahiidan faquln<u>aa</u> h<u>aa</u>tuu burh<u>aa</u>nakum fa\'alimuu anna <strong>a</strong>l<u>h</u>aqqa lill<u>aa</u>hi wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Dan Kami datangkan dari setiap umat seorang saksi, lalu Kami katakan, “Kemukakanlah bukti kebenaranmu,” maka tahulah mereka bahwa yang hak (kebenaran) itu milik Allah dan lenyaplah dari mereka apa yang dahulu mereka ada-adakan.'),
	(3328, 28, 76, '۞ اِنَّ قَارُوْنَ كَانَ مِنْ قَوْمِ مُوْسٰى فَبَغٰى عَلَيْهِمْ ۖوَاٰتَيْنٰهُ مِنَ الْكُنُوْزِ مَآ اِنَّ مَفَاتِحَهٗ لَتَنُوْۤاُ بِالْعُصْبَةِ اُولِى الْقُوَّةِ اِذْ قَالَ لَهٗ قَوْمُهٗ لَا تَفْرَحْ  اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِحِيْنَ ', 'inna q<u>aa</u>ruuna k<u>aa</u>na min qawmi muus<u>aa</u> fabagh<u>aa</u> \'alayhim wa<u>aa</u>tayn<u>aa</u>hu mina <strong>a</strong>lkunuuzi m<u>aa</u> inna maf<u>aa</u>ti<u>h</u>ahu latanuu-u bi<strong>a</strong>l\'u<u>sh</u>bati ulii <strong>a</strong>lquwwati i<u>dz</u> q<u>aa</u>la lahu qawmuhu l<u>aa</u> tafra<u>h</u> inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lfari<u>h</u>iin<strong>a</strong>', 'Sesungguhnya Karun termasuk kaum Musa, tetapi dia berlaku zalim terhadap mereka, dan Kami telah menganugerahkan kepadanya perbendaharaan harta yang kunci-kuncinya sungguh berat dipikul oleh sejumlah orang yang kuat-kuat. (Ingatlah) ketika kaumnya berkata kepadanya, “Janganlah engkau terlalu bangga. Sungguh, Allah tidak menyukai orang yang membanggakan diri.”'),
	(3329, 28, 77, 'وَابْتَغِ فِيْمَآ اٰتٰىكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ  وَلَا تَنْسَ نَصِيْبَكَ مِنَ الدُّنْيَا  وَاَحْسِنْ كَمَآ اَحْسَنَ اللّٰهُ اِلَيْكَ  وَلَا تَبْغِ الْفَسَادَ فِى الْاَرْضِ ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِدِيْنَ', 'wa<strong>i</strong>btaghi fiim<u>aa</u> <u>aa</u>t<u>aa</u>ka <strong>al</strong>l<u>aa</u>hu <strong>al</strong>dd<u>aa</u>ra <strong>a</strong>l-<u>aa</u>khirata wal<u>aa</u> tansa na<u>sh</u>iibaka mina <strong>al</strong>dduny<u>aa</u> wa-a<u>h</u>sin kam<u>aa</u> a<u>h</u>sana <strong>al</strong>l<u>aa</u>hu ilayka wal<u>aa</u> tabghi <strong>a</strong>lfas<u>aa</u>da fii <strong>a</strong>l-ar<u>dh</u>i inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dan carilah (pahala) negeri akhirat dengan apa yang telah dianugerahkan Allah kepadamu, tetapi janganlah kamu lupakan bagianmu di dunia dan berbuatbaiklah (kepada orang lain) sebagaimana Allah telah berbuat baik kepadamu, dan janganlah kamu berbuat kerusakan di bumi. Sungguh, Allah tidak menyukai orang yang berbuat kerusakan.'),
	(3330, 28, 78, 'قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ عِنْدِيْۗ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِهٖ مِنَ الْقُرُوْنِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَّاَكْثَرُ جَمْعًا ۗوَلَا يُسْـَٔلُ عَنْ ذُنُوْبِهِمُ الْمُجْرِمُوْنَ ', 'q<u>aa</u>la innam<u>aa</u> uutiituhu \'al<u>aa</u> \'ilmin \'indii awa lam ya\'lam anna <strong>al</strong>l<u>aa</u>ha qad ahlaka min qablihi mina <strong>a</strong>lquruuni man huwa asyaddu minhu quwwatan wa-aktsaru jam\'an wal<u>aa</u> yus-alu \'an <u>dz</u>unuubihimu <strong>a</strong>lmujrimuun<strong>a</strong>', 'Dia (Karun) berkata, “Sesungguhnya aku diberi (harta itu), semata-mata karena ilmu yang ada padaku.” Tidakkah dia tahu, bahwa Allah telah membinasakan umat-umat sebelumnya yang lebih kuat daripadanya, dan lebih banyak mengumpulkan harta? Dan orang-orang yang berdosa itu tidak perlu ditanya tentang dosa-dosa mereka.'),
	(3331, 28, 79, 'فَخَرَجَ عَلٰى قَوْمِهٖ فِيْ زِيْنَتِهٖ ۗقَالَ الَّذِيْنَ يُرِيْدُوْنَ الْحَيٰوةَ الدُّنْيَا يٰلَيْتَ لَنَا مِثْلَ مَآ اُوْتِيَ قَارُوْنُۙ اِنَّهٗ لَذُوْ حَظٍّ عَظِيْمٍ ', 'fakharaja \'al<u>aa</u> qawmihi fii ziinatihi q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina yuriiduuna <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> y<u>aa</u> layta lan<u>aa</u> mitsla m<u>aa</u> uutiya q<u>aa</u>ruunu innahu la<u>dz</u>uu <u>h</u>a<u>zhzh</u>in \'a<u>zh</u>iim<strong>in</strong>', 'Maka keluarlah dia (Karun) kepada kaumnya dengan kemegahannya. Orang-orang yang menginginkan kehidupan dunia berkata, “Mudah-mudahan kita mempunyai harta kekayaan seperti apa yang telah diberikan kepada Karun, sesungguhnya dia benar-benar mempunyai keberuntungan yang besar.”'),
	(3332, 28, 80, 'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِّمَنْ اٰمَنَ وَعَمِلَ صَالِحًا ۚوَلَا يُلَقّٰىهَآ اِلَّا الصّٰبِرُوْنَ ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma waylakum tsaw<u>aa</u>bu <strong>al</strong>l<u>aa</u>hi khayrun liman <u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an wal<u>aa</u> yulaqq<u>aa</u>h<u>aa</u> ill<u>aa</u> <strong>al</strong><u>shshaa</u>biruun<strong>a</strong>', 'Tetapi orang-orang yang dianugerahi ilmu berkata, “Celakalah kamu! Ketahuilah, pahala Allah lebih baik bagi orang-orang yang beriman dan mengerjakan kebajikan, dan (pahala yang besar) itu hanya diperoleh oleh orang-orang yang sabar.”'),
	(3333, 28, 81, 'فَخَسَفْنَا بِهٖ وَبِدَارِهِ الْاَرْضَ ۗفَمَا كَانَ لَهٗ مِنْ فِئَةٍ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ ۖوَمَا كَانَ مِنَ الْمُنْتَصِرِيْنَ ', 'fakhasafn<u>aa</u> bihi wabid<u>aa</u>rihi <strong>a</strong>l-ar<u>dh</u>a fam<u>aa</u> k<u>aa</u>na lahu min fi-atin yan<u>sh</u>uruunahu min duuni <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> k<u>aa</u>na mina <strong>a</strong>lmunta<u>sh</u>iriin<strong>a</strong>', 'Maka Kami benamkan dia (Karun) bersama rumahnya ke dalam bumi. Maka tidak ada baginya satu golongan pun yang akan menolongnya selain Allah, dan dia tidak termasuk orang-orang yang dapat membela diri.'),
	(3334, 28, 82, 'وَاَصْبَحَ الَّذِيْنَ تَمَنَّوْا مَكَانَهٗ بِالْاَمْسِ يَقُوْلُوْنَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُۚ  لَوْلَآ اَنْ مَّنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَا ۗوَيْكَاَنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ ࣖ ', 'wa-a<u>sh</u>ba<u>h</u>a <strong>al</strong>la<u>dz</u>iina tamannaw mak<u>aa</u>nahu bi<strong>a</strong>l-amsi yaquuluuna wayka-anna <strong>al</strong>l<u>aa</u>ha yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lawl<u>aa</u> an manna <strong>al</strong>l<u>aa</u>hu \'alayn<u>aa</u> lakhasafa bin<u>aa</u> wayka-annahu l<u>aa</u> yufli<u>h</u>u <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Dan orang-orang yang kemarin mengangan-angankan kedudukannya (Karun) itu berkata, “Aduhai, benarlah kiranya Allah yang melapangkan rezeki bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya dan membatasi (bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya). Sekiranya Allah tidak melimpahkan karunia-Nya pada kita, tentu Dia telah membenamkan kita pula. Aduhai, benarlah kiranya tidak akan beruntung orang-orang yang mengingkari (nikmat Allah).”'),
	(3335, 28, 83, 'تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذِيْنَ لَا يُرِيْدُوْنَ عُلُوًّا فِى الْاَرْضِ وَلَا فَسَادًا ۗوَالْعَاقِبَةُ لِلْمُتَّقِيْنَ ', 'tilka <strong>al</strong>dd<u>aa</u>ru <strong>a</strong>l-<u>aa</u>khiratu naj\'aluh<u>aa</u> lilla<u>dz</u>iina l<u>aa</u> yuriiduuna \'uluwwan fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> fas<u>aa</u>dan wa<strong>a</strong>l\'<u>aa</u>qibatu lilmuttaqiin<strong>a</strong>', 'Negeri akhirat itu Kami jadikan bagi orang-orang yang tidak menyombongkan diri dan tidak berbuat kerusakan di bumi. Dan kesudahan (yang baik) itu bagi orang-orang yang bertakwa.'),
	(3336, 28, 84, 'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِيْنَ عَمِلُوا السَّيِّاٰتِ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ', 'man j<u>aa</u>-a bi<strong>a</strong>l<u>h</u>asanati falahu khayrun minh<u>aa</u> waman j<u>aa</u>-a bi<strong>al</strong>ssayyi-ati fal<u>aa</u> yujz<u>aa</u> <strong>al</strong>la<u>dz</u>iina \'amiluu <strong>al</strong>ssayyi-<u>aa</u>ti ill<u>aa</u> m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Barangsiapa datang dengan (membawa) kebaikan, maka dia akan mendapat (pahala) yang lebih baik daripada kebaikannya itu; dan barang siapa datang dengan (membawa) kejahatan, maka orang-orang yang telah mengerjakan kejahatan itu hanya diberi balasan (seimbang) dengan apa yang dahulu mereka kerjakan.'),
	(3337, 28, 85, 'اِنَّ الَّذِيْ فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَرَاۤدُّكَ اِلٰى مَعَادٍ ۗقُلْ رَّبِّيْٓ اَعْلَمُ مَنْ جَاۤءَ بِالْهُدٰى وَمَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'inna <strong>al</strong>la<u>dz</u>ii fara<u>dh</u>a \'alayka <strong>a</strong>lqur-<u>aa</u>na lar<u>aa</u>dduka il<u>aa</u> ma\'<u>aa</u>din qul rabbii a\'lamu man j<u>aa</u>-a bi<strong>a</strong>lhud<u>aa</u> waman huwa fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Sesungguhnya (Allah) yang mewajibkan engkau (Muhammad) untuk (melaksanakan hukum-hukum) Al-Qur\'an, benar-benar akan mengembalikanmu ke tempat kembali. Katakanlah (Muhammad), “Tuhanku mengetahui orang yang membawa petunjuk dan orang yang berada dalam kesesatan yang nyata.”'),
	(3338, 28, 86, 'وَمَا كُنْتَ تَرْجُوْٓا اَنْ يُّلْقٰٓى اِلَيْكَ الْكِتٰبُ اِلَّا رَحْمَةً مِّنْ رَّبِّكَ  فَلَا تَكُوْنَنَّ ظَهِيْرًا لِّلْكٰفِرِيْنَ ۖ ', 'wam<u>aa</u> kunta tarjuu an yulq<u>aa</u> ilayka <strong>a</strong>lkit<u>aa</u>bu ill<u>aa</u> ra<u>h</u>matan min rabbika fal<u>aa</u> takuunanna <u>zh</u>ahiiran lilk<u>aa</u>firiin<strong>a</strong>', 'Dan engkau (Muhammad) tidak pernah mengharap agar Kitab (Al-Qur\'an) itu diturunkan kepadamu, tetapi ia (diturunkan) sebagai rahmat dari Tuhanmu, sebab itu janganlah sekali-kali engkau menjadi penolong bagi orang-orang kafir,'),
	(3339, 28, 87, 'وَلَا يَصُدُّنَّكَ عَنْ اٰيٰتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ۚ ', 'wal<u>aa</u> ya<u>sh</u>uddunnaka \'an <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi ba\'da i<u>dz</u> unzilat ilayka wa<strong>u</strong>d\'u il<u>aa</u> rabbika wal<u>aa</u> takuunanna mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'dan jangan sampai mereka menghalang-halangi engkau (Muhammad) untuk (menyampaikan) ayat-ayat Allah, setelah ayat-ayat itu diturunkan kepadamu, dan serulah (manusia) agar (beriman) kepada Tuhanmu, dan janganlah engkau termasuk orang-orang musyrik.'),
	(3340, 28, 88, 'وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۘ  لَآ اِلٰهَ اِلَّا هُوَۗ كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهٗ ۗ لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ࣖ ', 'wal<u>aa</u> tad\'u ma\'a <strong>al</strong>l<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa kullu syay-in h<u>aa</u>likun ill<u>aa</u> wajhahu lahu <strong>a</strong>l<u>h</u>ukmu wa-ilayhi turja\'uun<strong>a</strong>', 'Dan jangan (pula) engkau sembah tuhan yang lain selain Allah. Tidak ada tuhan (yang berhak disembah) selain Dia. Segala sesuatu pasti binasa, kecuali Allah. Segala keputusan menjadi wewenang-Nya, dan hanya kepada-Nya kamu dikembalikan.'),
	(3341, 29, 1, 'الۤمّۤ ۗ ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(3342, 29, 2, 'اَحَسِبَ النَّاسُ اَنْ يُّتْرَكُوْٓا اَنْ يَّقُوْلُوْٓا اٰمَنَّا وَهُمْ لَا يُفْتَنُوْنَ ', 'a<u>h</u>asiba <strong>al</strong>nn<u>aa</u>su an yutrakuu an yaquuluu <u>aa</u>mann<u>aa</u> wahum l<u>aa</u> yuftanuun<strong>a</strong>', 'Apakah manusia mengira bahwa mereka akan dibiarkan hanya dengan mengatakan, “Kami telah beriman,” dan mereka tidak diuji?'),
	(3343, 29, 3, 'وَلَقَدْ فَتَنَّا الَّذِيْنَ مِنْ قَبْلِهِمْ  فَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ صَدَقُوْا وَلَيَعْلَمَنَّ الْكٰذِبِيْنَ ', 'walaqad fatann<u>aa</u> <strong>al</strong>la<u>dz</u>iina min qablihim falaya\'lamanna <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>sh</u>adaquu walaya\'lamanna <strong>a</strong>lk<u>aadz</u>ibiin<strong>a</strong>', 'Dan sungguh, Kami telah menguji orang-orang sebelum mereka, maka Allah pasti mengetahui orang-orang yang benar dan pasti mengetahui orang-orang yang dusta.'),
	(3344, 29, 4, 'اَمْ حَسِبَ الَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِ اَنْ يَّسْبِقُوْنَا ۗسَاۤءَ مَا يَحْكُمُوْنَ ', 'am <u>h</u>asiba <strong>al</strong>la<u>dz</u>iina ya\'maluuna <strong>al</strong>ssayyi-<u>aa</u>ti an yasbiquun<u>aa</u> s<u>aa</u>-a m<u>aa</u> ya<u>h</u>kumuun<strong>a</strong>', 'Ataukah orang-orang yang mengerjakan kejahatan itu mengira bahwa mereka akan luput dari (azab) Kami? Sangatlah buruk apa yang mereka tetapkan itu! '),
	(3345, 29, 5, 'مَنْ كَانَ يَرْجُوْا لِقَاۤءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ', 'man k<u>aa</u>na yarjuu liq<u>aa</u>-a <strong>al</strong>l<u>aa</u>hi fa-inna ajala <strong>al</strong>l<u>aa</u>hi la<u>aa</u>tin wahuwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Barangsiapa mengharap pertemuan dengan Allah, maka sesungguhnya waktu (yang dijanjikan) Allah pasti datang. Dan Dia Yang Maha Mendengar, Maha Mengetahui.'),
	(3346, 29, 6, 'وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِهٖ ۗاِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعٰلَمِيْنَ ', 'waman j<u>aa</u>hada fa-innam<u>aa</u> yuj<u>aa</u>hidu linafsihi inna <strong>al</strong>l<u>aa</u>ha laghaniyyun \'ani <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan barangsiapa berjihad, maka sesungguhnya jihadnya itu untuk dirinya sendiri. Sungguh, Allah Mahakaya (tidak memerlukan sesuatu) dari seluruh alam.'),
	(3347, 29, 7, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lanukaffiranna \'anhum sayyi-<u>aa</u>tihim walanajziyannahum a<u>h</u>sana <strong>al</strong>la<u>dz</u>ii k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan orang-orang yang beriman dan mengerjakan kebajikan, pasti akan Kami hapus kesalahan-kesalahannya dan mereka pasti akan Kami beri balasan yang lebih baik dari apa yang mereka kerjakan.'),
	(3348, 29, 8, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْنًا ۗوَاِنْ جَاهَدٰكَ لِتُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا ۗاِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ', 'wawa<u>shsh</u>ayn<u>aa</u> <strong>a</strong>l-ins<u>aa</u>na biw<u>aa</u>lidayhi <u>h</u>usnan wa-in j<u>aa</u>had<u>aa</u>ka litusyrika bii m<u>aa</u> laysa laka bihi \'ilmun fal<u>aa</u> tu<u>th</u>i\'hum<u>aa</u> ilayya marji\'ukum fa-unabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan Kami wajibkan kepada manusia agar (berbuat) kebaikan kepada kedua orang tuanya. Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang engkau tidak mempunyai ilmu tentang itu, maka janganlah engkau patuhi keduanya. Hanya kepada-Ku tempat kembalimu, dan akan Aku beritakan kepadamu apa yang telah kamu kerjakan.'),
	(3349, 29, 9, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُدْخِلَنَّهُمْ فِى الصّٰلِحِيْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lanudkhilannahum fii <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan orang-orang yang beriman dan mengerjakan kebajikan mereka pasti akan Kami masukkan mereka ke dalam (golongan) orang yang saleh.'),
	(3350, 29, 10, 'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ فَاِذَآ اُوْذِيَ فِى اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِ ۗوَلَىِٕنْ جَاۤءَ نَصْرٌ مِّنْ رَّبِّكَ لَيَقُوْلُنَّ اِنَّا كُنَّا مَعَكُمْۗ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا فِيْ صُدُوْرِ الْع', 'wamina <strong>al</strong>nn<u>aa</u>si man yaquulu <u>aa</u>mann<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi fa-i<u>dzaa</u> uu<u>dz</u>iya fii <strong>al</strong>l<u>aa</u>hi ja\'ala fitnata <strong>al</strong>nn<u>aa</u>si ka\'a<u>dzaa</u>bi <strong>al</strong>l<u>aa</u>hi wala-in j<u>aa</u>-a na<u>sh</u>run min rabbika layaquulunna inn<u>aa</u> kunn<u>aa</u> ma\'akum awa laysa <strong>al</strong>l<u>aa</u>hu bi-a\'lama bim<u>aa</u> fii <u>sh</u>uduuri <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan di antara manusia ada sebagian yang berkata, “Kami beriman kepada Allah,” tetapi apabila dia disakiti (karena dia beriman) kepada Allah, dia menganggap cobaan manusia itu sebagai siksaan Allah. Dan jika datang pertolongan dari Tuhanmu, niscaya mereka akan berkata, “Sesungguhnya kami bersama kamu.” Bukankah Allah lebih mengetahui apa yang ada di dalam dada semua manusia?'),
	(3351, 29, 11, 'وَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَلَيَعْلَمَنَّ الْمُنٰفِقِيْنَ ', 'walaya\'lamanna <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu walaya\'lamanna <strong>a</strong>lmun<u>aa</u>fiqiin<strong>a</strong>', 'Dan Allah pasti mengetahui orang-orang yang beriman dan Dia pasti mengetahui orang-orang yang munafik.'),
	(3352, 29, 12, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوا اتَّبِعُوْا سَبِيْلَنَا وَلْنَحْمِلْ خَطٰيٰكُمْۗ وَمَا هُمْ بِحَامِلِيْنَ مِنْ خَطٰيٰهُمْ مِّنْ شَيْءٍۗ اِنَّهُمْ لَكٰذِبُوْنَ ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lilla<u>dz</u>iina <u>aa</u>manuu ittabi\'uu sabiilan<u>aa</u> walna<u>h</u>mil kha<u>th</u><u>aa</u>y<u>aa</u>kum wam<u>aa</u> hum bi<u>haa</u>miliina min kha<u>th</u><u>aa</u>y<u>aa</u>hum min syay-in innahum lak<u>aadz</u>ibuun<strong>a</strong>', 'Dan orang-orang yang kafir berkata kepada orang-orang yang beriman, “Ikutilah jalan kami, dan kami akan memikul dosa-dosamu,” padahal mereka sedikit pun tidak (sanggup) memikul dosa-dosa mereka sendiri. Sesungguhnya mereka benar-benar pendusta.'),
	(3353, 29, 13, 'وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالًا مَّعَ اَثْقَالِهِمْ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوْا يَفْتَرُوْنَ ࣖ ', 'walaya<u>h</u>milunna atsq<u>aa</u>lahum wa-atsq<u>aa</u>lan ma\'a atsq<u>aa</u>lihim walayus-alunna yawma <strong>a</strong>lqiy<u>aa</u>mati \'amm<u>aa</u> k<u>aa</u>nuu yaftaruun<strong>a</strong>', 'Dan mereka benar-benar akan memikul dosa-dosa mereka sendiri, dan dosa-dosa yang lain bersama dosa mereka, dan pada hari Kiamat mereka pasti akan ditanya tentang kebohongan yang selalu mereka ada-adakan.'),
	(3354, 29, 14, 'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَلَبِثَ فِيْهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْسِيْنَ عَامًا ۗفَاَخَذَهُمُ الطُّوْفَانُ وَهُمْ ظٰلِمُوْنَ ', 'walaqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi falabitsa fiihim <strong>a</strong>lfa sanatin ill<u>aa</u> khamsiina \'<u>aa</u>man fa-akha<u>dz</u>ahumu <strong>al</strong><u>ththh</u>uuf<u>aa</u>nu wahum <u>zhaa</u>limuun<strong>a</strong>', 'Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, maka dia tinggal bersama mereka selama seribu tahun kurang lima puluh tahun. Kemudian mereka dilanda banjir besar, sedangkan mereka adalah orang-orang yang zalim.'),
	(3355, 29, 15, 'فَاَنْجَيْنٰهُ وَاَصْحٰبَ السَّفِيْنَةِ وَجَعَلْنٰهَآ اٰيَةً لِّلْعٰلَمِيْنَ ', 'fa-anjayn<u>aa</u>hu wa-a<u>sh</u>-<u>haa</u>ba <strong>al</strong>ssafiinati waja\'aln<u>aa</u>h<u>aa</u> <u>aa</u>yatan lil\'<u>aa</u>lamiin<strong>a</strong>', 'Maka Kami selamatkan Nuh dan orang-orang yang berada di kapal itu, dan Kami jadikan (peristiwa) itu sebagai pelajaran bagi semua manusia.'),
	(3356, 29, 16, 'وَاِبْرٰهِيْمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ', 'wa-ibr<u>aa</u>hiima i<u>dz</u> q<u>aa</u>la liqawmihi u\'buduu <strong>al</strong>l<u>aa</u>ha wa<strong>i</strong>ttaquuhu <u>dzaa</u>likum khayrun lakum in kuntum ta\'lamuun<strong>a</strong>', 'Dan (ingatlah) Ibrahim, ketika dia berkata kepada kaumnya, “Sembahlah Allah dan bertakwalah kepada-Nya. Yang demikian itu lebih baik bagimu, jika kamu mengetahui.'),
	(3357, 29, 17, 'اِنَّمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ اَوْثَانًا وَّتَخْلُقُوْنَ اِفْكًا ۗاِنَّ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَمْلِكُوْنَ لَكُمْ رِزْقًا فَابْتَغُوْا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوْهُ وَاشْكُرُوْا لَهٗ ۗاِلَيْهِ تُرْجَعُوْنَ ', 'innam<u>aa</u> ta\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi awts<u>aa</u>nan watakhluquuna ifkan inna <strong>al</strong>la<u>dz</u>iina ta\'buduuna min duuni <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yamlikuuna lakum rizqan fa<strong>i</strong>btaghuu \'inda <strong>al</strong>l<u>aa</u>hi <strong>al</strong>rrizqa wa<strong>u</strong>\'buduuhu wa<strong>u</strong>sykuruu lahu ilayhi turja\'uun<strong>a</strong>', 'Sesungguhnya yang kamu sembah selain Allah hanyalah berhala-berhala, dan kamu membuat kebohongan. Sesungguhnya apa yang kamu sembah selain Allah itu tidak mampu memberikan rezeki kepadamu; maka mintalah rezeki dari Allah, dan sembahlah Dia dan bersyukurlah kepada-Nya. Hanya kepada-Nya kamu akan dikembalikan.'),
	(3358, 29, 18, 'وَاِنْ تُكَذِّبُوْا فَقَدْ كَذَّبَ اُمَمٌ مِّنْ قَبْلِكُمْ ۗوَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ', 'wa-in tuka<u>dzdz</u>ibuu faqad ka<u>dzdz</u>aba umamun min qablikum wam<u>aa</u> \'al<u>aa</u> <strong>al</strong>rrasuuli ill<u>aa</u> <strong>a</strong>lbal<u>aa</u>ghu <strong>a</strong>lmubiin<strong>u</strong>', 'Dan jika kamu (orang kafir) mendustakan, maka sungguh, umat sebelum kamu juga telah mendustakan (para rasul). Dan kewajiban rasul itu hanyalah menyampaikan (agama Allah) dengan jelas.”'),
	(3359, 29, 19, 'اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُعِيْدُهٗ ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ', 'awa lam yaraw kayfa yubdi-u <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lkhalqa tsumma yu\'iiduhu inna <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<strong>un</strong>', 'Dan apakah mereka tidak memperhatikan bagaimana Allah memulai penciptaan (makhluk), kemudian Dia mengulanginya (kembali). Sungguh, yang demikian itu mudah bagi Allah.'),
	(3360, 29, 20, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَ ۗاِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۚ', 'qul siiruu fii <strong>a</strong>l-ar<u>dh</u>i fa<strong>u</strong>n<u>zh</u>uruu kayfa bada-a <strong>a</strong>lkhalqa tsumma <strong>al</strong>l<u>aa</u>hu yunsyi-u <strong>al</strong>nnasy-ata <strong>a</strong>l-<u>aa</u>khirata inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Katakanlah, “Berjalanlah di bumi, maka perhatikanlah bagaimana (Allah) memulai penciptaan (makhluk), kemudian Allah menjadikan kejadian yang akhir. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(3361, 29, 21, 'يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَرْحَمُ مَنْ يَّشَاۤءُ ۚوَاِلَيْهِ تُقْلَبُوْنَ ', 'yu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wayar<u>h</u>amu man yasy<u>aa</u>u wa-ilayhi tuqlabuun<strong>a</strong>', 'Dia (Allah) mengazab siapa yang Dia kehendaki dan memberi rahmat kepada siapa yang Dia kehendaki, dan hanya kepada-Nya kamu akan dikembalikan.'),
	(3362, 29, 22, 'وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ ۖوَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ࣖ', 'wam<u>aa</u> antum bimu\'jiziina fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>-i wam<u>aa</u> lakum min duuni <strong>al</strong>l<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir<strong>in</strong>', 'Dan kamu sama sekali tidak dapat melepaskan diri (dari azab Allah) baik di bumi maupun di langit, dan tidak ada pelindung dan penolong bagimu selain Allah.'),
	(3363, 29, 23, 'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ وَلِقَاۤىِٕهٖٓ اُولٰۤىِٕكَ يَىِٕسُوْا مِنْ رَّحْمَتِيْ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi waliq<u>aa</u>-ihi ul<u>aa</u>-ika ya-isuu min ra<u>h</u>matii waul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun <strong>a</strong>liim<strong>un</strong>', 'Dan orang-orang yang mengingkari ayat-ayat Allah dan pertemuan dengan-Nya, mereka berputus asa dari rahmat-Ku, dan mereka itu akan mendapat azab yang pedih.'),
	(3364, 29, 24, 'فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا اقْتُلُوْهُ اَوْ حَرِّقُوْهُ فَاَنْجٰىهُ اللّٰهُ مِنَ النَّارِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ', 'fam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu uqtuluuhu aw <u>h</u>arriquuhu fa-anj<u>aa</u>hu <strong>al</strong>l<u>aa</u>hu mina <strong>al</strong>nn<u>aa</u>ri inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun<strong>a</strong>', 'Maka tidak ada jawaban kaumnya (Ibrahim), selain mengatakan, “Bunuhlah atau bakarlah dia,” lalu Allah menyelamatkannya dari api. Sungguh, pada yang demikian itu pasti terdapat tanda-tanda (kebesaran Allah) bagi orang yang beriman.'),
	(3365, 29, 25, 'وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِّنْ دُوْنِ اللّٰهِ اَوْثَانًاۙ مَّوَدَّةَ بَيْنِكُمْ فِى الْحَيٰوةِ الدُّنْيَا ۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَّيَلْعَنُ بَعْضُكُمْ بَعْضًا ۖوَّمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَۖ ', 'waq<u>aa</u>la innam<u>aa</u> ittakha<u>dz</u>tum min duuni <strong>al</strong>l<u>aa</u>hi awts<u>aa</u>nan mawaddata baynikum fii <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> tsumma yawma <strong>a</strong>lqiy<u>aa</u>mati yakfuru ba\'<u>dh</u>ukum biba\'<u>dh</u>in wayal\'anu ba\'<u>dh</u>ukum ba\'<u>dh</u>an wama-w<u>aa</u>kumu <strong>al</strong>nn<u>aa</u>ru wam<u>aa</u> lakum min n<u>aas</u>iriin<strong>a</strong>', 'Dan dia (Ibrahim) berkata, “Sesungguhnya berhala-berhala yang kamu sembah selain Allah, hanya untuk menciptakan perasaan kasih sayang di antara kamu dalam kehidupan di dunia, kemudian pada hari Kiamat sebagian kamu akan saling mengingkari dan saling mengutuk; dan tempat kembalimu ialah neraka, dan sama sekali tidak ada penolong bagimu.”'),
	(3366, 29, 26, '۞ فَاٰمَنَ لَهٗ لُوْطٌۘ وَقَالَ اِنِّيْ مُهَاجِرٌ اِلٰى رَبِّيْ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الْحَكِيْمُ ', 'fa<u>aa</u>mana lahu luu<u>th</u>un waq<u>aa</u>la innii muh<u>aa</u>jirun il<u>aa</u> rabbii innahu huwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Maka Lut membenarkan (kenabian Ibrahim). Dan dia (Ibrahim) berkata, “Sesungguhnya aku harus berpindah ke (tempat yang diperintahkan) Tuhanku; sungguh, Dialah Yang Mahaperkasa, Mahabijaksana.”'),
	(3367, 29, 27, 'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتٰبَ وَاٰتَيْنٰهُ اَجْرَهٗ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ', 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba waja\'aln<u>aa</u> fii <u>dz</u>urriyyatihi <strong>al</strong>nnubuwwata wa<strong>a</strong>lkit<u>aa</u>ba wa<u>aa</u>tayn<u>aa</u>hu ajrahu fii <strong>al</strong>dduny<u>aa</u> wa-innahu fii <strong>a</strong>l-<u>aa</u>khirati lamina <strong>al</strong><u>shshaa</u>li<u>h</u>iin<strong>a</strong>', 'Dan Kami anugerahkan kepada Ibrahim, Ishak dan Yakub, dan Kami jadikan kenabian dan kitab kepada keturunannya, dan Kami berikan kepadanya balasannya di dunia; dan sesungguhnya dia di akhirat, termasuk orang yang saleh. '),
	(3368, 29, 28, 'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اِنَّكُمْ لَتَأْتُوْنَ الْفَاحِشَةَ ۖمَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ', 'waluu<u>th</u>an i<u>dz</u> q<u>aa</u>la liqawmihi innakum lata/tuuna <strong>a</strong>lf<u>aah</u>isyata m<u>aa</u> sabaqakum bih<u>aa</u> min a<u>h</u>adin mina <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Dan (ingatlah) ketika Lut berkata kepada kaumnya, “Kamu benar-benar melakukan perbuatan yang sangat keji (homoseksual) yang belum pernah dilakukan oleh seorang pun dari umat-umat sebelum kamu. '),
	(3369, 29, 29, 'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ وَتَقْطَعُوْنَ السَّبِيْلَ ەۙ وَتَأْتُوْنَ فِيْ نَادِيْكُمُ الْمُنْكَرَ ۗفَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ', 'a-innakum lata/tuuna <strong>al</strong>rrij<u>aa</u>la wataq<u>th</u>a\'uuna <strong>al</strong>ssabiila wata/tuuna fii n<u>aa</u>diikumu <strong>a</strong>lmunkara fam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu i/tin<u>aa</u> bi\'a<u>dzaa</u>bi <strong>al</strong>l<u>aa</u>hi in kunta mina <strong>al</strong><u>shshaa</u>diqiin<strong>a</strong>', 'Apakah pantas kamu mendatangi laki-laki, menyamun dan mengerjakan kemungkaran di tempat-tempat pertemuanmu?” Maka jawaban kaumnya tidak lain hanya mengatakan, “Datangkanlah kepada kami azab Allah, jika engkau termasuk orang-orang yang benar.” '),
	(3370, 29, 30, 'قَالَ رَبِّ انْصُرْنِيْ عَلَى الْقَوْمِ الْمُفْسِدِيْنَ ࣖ', 'q<u>aa</u>la rabbi un<u>sh</u>urnii \'al<u>aa</u> <strong>a</strong>lqawmi <strong>a</strong>lmufsidiin<strong>a</strong>', 'Dia (Lut) berdoa, “Ya Tuhanku, tolonglah aku (dengan menimpakan azab) atas golongan yang berbuat kerusakan itu.” '),
	(3371, 29, 31, 'وَلَمَّا جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰىۙ قَالُوْٓا اِنَّا مُهْلِكُوْٓا اَهْلِ هٰذِهِ الْقَرْيَةِ ۚاِنَّ اَهْلَهَا كَانُوْا ظٰلِمِيْنَ ۚ', 'walamm<u>aa</u> j<u>aa</u>-at rusulun<u>aa</u> ibr<u>aa</u>hiima bi<strong>a</strong>lbusyr<u>aa</u> q<u>aa</u>luu inn<u>aa</u> muhlikuu ahli h<u>aadz</u>ihi <strong>a</strong>lqaryati inna ahlah<u>aa</u> k<u>aa</u>nuu <u>zhaa</u>limiin<strong>a</strong>', 'Dan ketika utusan Kami (para malaikat) datang kepada Ibrahim dengan membawa kabar gembira, mereka mengatakan, “Sungguh, kami akan membinasakan penduduk kota (Sodom) ini karena penduduknya sungguh orang-orang zalim.” '),
	(3372, 29, 32, 'قَالَ اِنَّ فِيْهَا لُوْطًا ۗقَالُوْا نَحْنُ اَعْلَمُ بِمَنْ فِيْهَا ۖ لَنُنَجِّيَنَّهٗ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ', 'q<u>aa</u>la inna fiih<u>aa</u> luu<u>th</u>an q<u>aa</u>luu na<u>h</u>nu a\'lamu biman fiih<u>aa</u> lanunajjiyannahu wa-ahlahu ill<u>aa</u> imra-atahu k<u>aa</u>nat mina <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'Ibrahim berkata, “Sesungguhnya di kota itu ada Lut.” Mereka (para malaikat) berkata, “Kami lebih mengetahui siapa yang ada di kota itu. Kami pasti akan menyelamatkan dia dan pengikut-pengikutnya kecuali istrinya. Dia termasuk orang-orang yang tertinggal (dibinasakan).” '),
	(3373, 29, 33, 'وَلَمَّآ اَنْ جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالُوْا لَا تَخَفْ وَلَا تَحْزَنْ ۗاِنَّا مُنَجُّوْكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغٰبِرِيْنَ', 'walamm<u>aa</u> an j<u>aa</u>-at rusulun<u>aa</u> luu<u>th</u>an sii-a bihim wa<u>daa</u>qa bihim <u>dz</u>ar\'an waq<u>aa</u>luu l<u>aa</u> takhaf wal<u>aa</u> ta<u>h</u>zan inn<u>aa</u> munajjuuka wa-ahlaka ill<u>aa</u> imra-ataka k<u>aa</u>nat mina <strong>a</strong>lgh<u>aa</u>biriin<strong>a</strong>', 'Dan ketika para utusan Kami (para malaikat) datang kepada Lut, dia merasa bersedih hati karena (kedatangan) mereka, dan (merasa) tidak mempunyai kekuatan untuk melindungi mereka, dan mereka (para utusan) berkata, “Janganlah engkau takut dan jangan (pula) bersedih hati. Sesungguhnya Kami akan menyelamatkanmu dan pengikut-pengikutmu, kecuali istrimu, dia termasuk orang-orang yang tinggal (dibinasakan).” '),
	(3374, 29, 34, 'اِنَّا مُنْزِلُوْنَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ ', 'inn<u>aa</u> munziluuna \'al<u>aa</u> ahli h<u>aadz</u>ihi <strong>a</strong>lqaryati rijzan mina <strong>al</strong>ssam<u>aa</u>-i bim<u>aa</u> k<u>aa</u>nuu yafsuquun<strong>a</strong>', 'Sesungguhnya Kami akan menurunkan azab dari langit kepada penduduk kota ini karena mereka berbuat fasik. '),
	(3375, 29, 35, 'وَلَقَدْ تَّرَكْنَا مِنْهَآ اٰيَةً ۢ بَيِّنَةً لِّقَوْمٍ يَّعْقِلُوْنَ ', 'walaqad tarakn<u>aa</u> minh<u>aa</u> <u>aa</u>yatan bayyinatan liqawmin ya\'qiluun<strong>a</strong>', 'Dan sungguh, tentang itu telah Kami tinggalkan suatu tanda yang nyata bagi orang-orang yang mengerti. '),
	(3376, 29, 36, 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۙ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۖ', 'wa-il<u>aa</u> madyana akh<u>aa</u>hum syu\'ayban faq<u>aa</u>la y<u>aa</u> qawmi u\'buduu <strong>al</strong>l<u>aa</u>ha wa<strong>u</strong>rjuu <strong>a</strong>lyawma <strong>a</strong>l-<u>aa</u>khira wal<u>aa</u> ta\'tsaw fii <strong>a</strong>l-ar<u>dh</u>i mufsidiin<strong>a</strong>', 'Dan kepada penduduk Madyan, (Kami telah mengutus) saudara mereka Syuaib, dia berkata, “Wahai kaumku! Sembahlah Allah, harapkanlah (pahala) hari akhir, dan jangan kamu berkeliaran di bumi berbuat kerusakan.” '),
	(3377, 29, 37, 'فَكَذَّبُوْهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ ۙ', 'faka<u>dzdz</u>abuuhu fa-akha<u>dz</u>at-humu <strong>al</strong>rrajfatu fa-a<u>sh</u>ba<u>h</u>uu fii d<u>aa</u>rihim j<u>aa</u>tsimiin<strong>a</strong>', 'Mereka mendustakannya (Syuaib), maka mereka ditimpa gempa yang dahsyat, lalu jadilah mereka mayat-mayat yang bergelimpangan di tempat-tempat tinggal mereka.'),
	(3378, 29, 38, 'وَعَادًا وَّثَمُوْدَا۟ وَقَدْ تَّبَيَّنَ لَكُمْ مِّنْ مَّسٰكِنِهِمْۗ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ وَكَانُوْا مُسْتَبْصِرِيْنَ ۙ', 'wa\'<u>aa</u>dan watsamuuda waqad tabayyana lakum min mas<u>aa</u>kinihim wazayyana lahumu <strong>al</strong>sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum fa<u>sh</u>addahum \'ani <strong>al</strong>ssabiili wak<u>aa</u>nuu mustab<u>sh</u>iriin<strong>a</strong>', 'Juga (ingatlah) kaum ’Ad dan Samud, sungguh telah nyata bagi kamu (kehancuran mereka) dari (puing-puing) tempat tinggal mereka. Setan telah menjadikan terasa indah bagi mereka perbuatan (buruk) mereka, sehingga menghalangi mereka dari jalan (Allah), sedangkan mereka adalah orang-orang yang berpandangan tajam, '),
	(3379, 29, 39, 'وَقَارُوْنَ وَفِرْعَوْنَ وَهَامٰنَۗ وَلَقَدْ جَاۤءَهُمْ مُّوْسٰى بِالْبَيِّنٰتِ فَاسْتَكْبَرُوْا فِى الْاَرْضِ وَمَا كَانُوْا سَابِقِيْنَ ۚ', 'waq<u>aa</u>ruuna wafir\'awna wah<u>aa</u>m<u>aa</u>na walaqad j<u>aa</u>-ahum muus<u>aa</u> bi<strong>a</strong>lbayyin<u>aa</u>ti fa<strong>i</strong>stakbaruu fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> k<u>aa</u>nuu s<u>aa</u>biqiin<strong>a</strong>', 'dan (juga) Karun, Fir‘aun dan Haman. Sungguh, telah datang kepada mereka Musa dengan (membawa) keterangan-keterangan yang nyata. Tetapi mereka berlaku sombong di bumi, dan mereka orang-orang yang tidak luput (dari azab Allah). '),
	(3380, 29, 40, 'فَكُلًّا اَخَذْنَا بِذَنْۢبِهٖۙ فَمِنْهُمْ مَّنْ اَرْسَلْنَا عَلَيْهِ حَاصِبًا ۚوَمِنْهُمْ مَّنْ اَخَذَتْهُ الصَّيْحَةُ ۚوَمِنْهُمْ مَّنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَّنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُس', 'fakullan akha<u>dz</u>n<u>aa</u> bi<u>dz</u>anbihi faminhum man arsaln<u>aa</u> \'alayhi <u>has</u>iban waminhum man akha<u>dz</u>at-hu <strong>al</strong><u>shsh</u>ay<u>h</u>atu waminhum man khasafn<u>aa</u> bihi <strong>a</strong>l-ar<u>dh</u>a waminhum man aghraqn<u>aa</u> wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liya<u>zh</u>limahum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Maka masing-masing (mereka itu) Kami azab karena dosa-dosanya, di antara mereka ada yang Kami timpakan kepadanya hujan batu kerikil, ada yang ditimpa suara keras yang mengguntur, ada yang Kami benamkan ke dalam bumi, dan ada pula yang Kami tenggelamkan. Allah sama sekali tidak hendak menzalimi mereka, akan tetapi merekalah yang menzalimi diri mereka sendiri. '),
	(3381, 29, 41, 'مَثَلُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَ كَمَثَلِ الْعَنْكَبُوْتِۚ اِتَّخَذَتْ بَيْتًاۗ وَاِنَّ اَوْهَنَ الْبُيُوْتِ لَبَيْتُ الْعَنْكَبُوْتِۘ لَوْ كَانُوْا يَعْلَمُوْنَ ', 'matsalu <strong>al</strong>la<u>dz</u>iina ittakha<u>dz</u>uu min duuni <strong>al</strong>l<u>aa</u>hi awliy<u>aa</u>-a kamatsali <strong>a</strong>l\'ankabuuti ittakha<u>dz</u>at baytan wa-inna awhana <strong>a</strong>lbuyuuti labaytu <strong>a</strong>l\'ankabuuti law k<u>aa</u>nuu ya\'lamuun<strong>a</strong>', 'Perumpamaan orang-orang yang mengambil pelindung selain Allah adalah seperti laba-laba yang membuat rumah. Dan sesungguhnya rumah yang paling lemah ialah rumah laba-laba, sekiranya mereka mengetahui. '),
	(3382, 29, 42, 'اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ مِنْ شَيْءٍۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ', 'inna <strong>al</strong>l<u>aa</u>ha ya\'lamu m<u>aa</u> yad\'uuna min duunihi min syay-in wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Sungguh, Allah mengetahui apa saja yang mereka sembah selain Dia. Dan Dia Mahaperkasa, Mahabijaksana. '),
	(3383, 29, 43, 'وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُهَآ اِلَّا الْعَالِمُوْنَ ', 'watilka <strong>a</strong>l-amts<u>aa</u>lu na<u>dh</u>ribuh<u>aa</u> li<strong>l</strong>nn<u>aa</u>si wam<u>aa</u> ya\'qiluh<u>aa</u> ill<u>aa</u> <strong>a</strong>l\'<u>aa</u>limuun<strong>a</strong>', 'Dan perumpamaan-perumpamaan ini Kami buat untuk manusia; dan tidak ada yang akan memahaminya kecuali mereka yang berilmu. '),
	(3384, 29, 44, 'خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَ ࣖ   ۔', 'khalaqa <strong>al</strong>l<u>aa</u>hu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a bi<strong>a</strong>l<u>h</u>aqqi inna fii <u>dzaa</u>lika la<u>aa</u>yatan lilmu-miniin<strong>a</strong>', 'Allah menciptakan langit dan bumi dengan haq. Sungguh, pada yang demikian itu pasti terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman. '),
	(3385, 29, 45, 'اُتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنَ الْكِتٰبِ وَاَقِمِ الصَّلٰوةَۗ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ ۗوَلَذِكْرُ اللّٰهِ اَكْبَرُ ۗوَاللّٰهُ يَعْلَمُ مَا تَصْنَعُوْنَ', 'utlu m<u>aa</u> uu<u>h</u>iya ilayka mina <strong>a</strong>lkit<u>aa</u>bi wa-aqimi <strong>al</strong><u>shsh</u>al<u>aa</u>ta inna <strong>al</strong><u>shsh</u>al<u>aa</u>ta tanh<u>aa</u> \'ani <strong>a</strong>lfa<u>h</u>sy<u>aa</u>-i wa<strong>a</strong>lmunkari wala<u>dz</u>ikru <strong>al</strong>l<u>aa</u>hi akbaru wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> ta<u>sh</u>na\'uun<strong>a</strong>', 'Bacalah Kitab (Al-Qur\'an) yang telah diwahyukan kepadamu (Muhammad) dan laksanakanlah salat. Sesungguhnya salat itu mencegah dari (perbuatan) keji dan mungkar. Dan (ketahuilah) mengingat Allah (salat) itu lebih besar (keutamaannya dari ibadah yang lain). Allah mengetahui apa yang kamu kerjakan. '),
	(3386, 29, 46, '۞ وَلَا تُجَادِلُوْٓا اَهْلَ الْكِتٰبِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُۖ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ وَقُوْلُوْٓا اٰمَنَّا بِالَّذِيْٓ اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَّنَحْنُ لَهٗ مُسْلِمُوْنَ', 'wal<u>aa</u> tuj<u>aa</u>diluu ahla <strong>a</strong>lkit<u>aa</u>bi ill<u>aa</u> bi<strong>a</strong>llatii hiya a<u>h</u>sanu ill<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu minhum waquuluu <u>aa</u>mann<u>aa</u> bi<strong>a</strong>lla<u>dz</u>ii unzila ilayn<u>aa</u> waunzila ilaykum wa-il<u>aa</u>hun<u>aa</u> wa-il<u>aa</u>hukum w<u>aah</u>idun wana<u>h</u>nu lahu muslimuun<strong>a</strong>', 'Dan janganlah kamu berdebat dengan Ahli Kitab, melainkan dengan cara yang baik, kecuali dengan orang-orang yang zalim di antara mereka, dan katakanlah, ”Kami telah beriman kepada (kitab-kitab) yang diturunkan kepada kami dan yang diturunkan kepadamu; Tuhan kami dan Tuhan kamu satu; dan hanya kepada-Nya kami berserah diri.”'),
	(3387, 29, 47, 'وَكَذٰلِكَ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَۗ فَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يُؤْمِنُوْنَ بِهٖۚ وَمِنْ هٰٓؤُلَاۤءِ مَنْ يُّؤْمِنُ بِهٖۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الْكٰفِرُوْنَ', 'waka<u>dzaa</u>lika anzaln<u>aa</u> ilayka <strong>a</strong>lkit<u>aa</u>ba fa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu <strong>a</strong>lkit<u>aa</u>ba yu/minuuna bihi wamin h<u>aa</u>ul<u>aa</u>-i man yu/minu bihi wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> <strong>a</strong>lk<u>aa</u>firuun<strong>a</strong>', 'Dan demikianlah Kami turunkan Kitab (Al-Qur\'an) kepadamu. Adapun orang-orang yang telah Kami berikan Kitab (Taurat dan Injil) mereka beriman kepadanya (Al-Qur\'an), dan di antara mereka (orang-orang kafir Mekah) ada yang beriman kepadanya. Dan hanya orang-orang kafir yang mengingkari ayat-ayat Kami. '),
	(3388, 29, 48, 'وَمَا كُنْتَ تَتْلُوْا مِنْ قَبْلِهٖ مِنْ كِتٰبٍ وَّلَا تَخُطُّهٗ بِيَمِيْنِكَ اِذًا لَّارْتَابَ الْمُبْطِلُوْنَ', 'wam<u>aa</u> kunta tatluu min qablihi min kit<u>aa</u>bin wal<u>aa</u> takhu<u>ththh</u>uhu biyamiinika i<u>dz</u>an la<strong>i</strong>rt<u>aa</u>ba <strong>a</strong>lmub<u>th</u>iluun<strong>a</strong>', 'Dan engkau (Muhammad) tidak pernah membaca sesuatu kitab sebelum (Al-Qur\'an) dan engkau tidak (pernah) menulis suatu kitab dengan tangan kananmu; sekiranya (engkau pernah membaca dan menulis), niscaya ragu orang-orang yang mengingkarinya. '),
	(3389, 29, 49, 'بَلْ هُوَ اٰيٰتٌۢ بَيِّنٰتٌ فِيْ صُدُوْرِ الَّذِيْنَ اُوْتُوا الْعِلْمَۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الظّٰلِمُوْنَ', 'bal huwa <u>aa</u>y<u>aa</u>tun bayyin<u>aa</u>tun fii <u>sh</u>uduuri <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> <strong>al</strong><u>zhzhaa</u>limuun<strong>a</strong>', 'Sebenarnya, (Al-Qur\'an) itu adalah ayat-ayat yang jelas di dalam dada orang-orang yang berilmu. Hanya orang-orang yang zalim yang mengingkari ayat-ayat Kami. '),
	(3390, 29, 50, 'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيٰتٌ مِّنْ رَّبِّهٖ ۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ ۗوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ', 'waq<u>aa</u>luu lawl<u>aa</u> unzila \'alayhi <u>aa</u>y<u>aa</u>tun min rabbihi qul innam<u>aa</u> <strong>a</strong>l-<u>aa</u>y<u>aa</u>tu \'inda <strong>al</strong>l<u>aa</u>hi wa-innam<u>aa</u> an<u>aa</u> na<u>dz</u>iirun mubiin<strong>un</strong>', 'Dan mereka (orang-orang kafir Mekah) berkata, ”Mengapa tidak diturunkan mukjizat-mukjizat dari Tuhannya?” Katakanlah (Muhammad), ”Mukjizat-mukjizat itu terserah kepada Allah. Aku hanya seorang pemberi peringatan yang jelas.” '),
	(3391, 29, 51, 'اَوَلَمْ يَكْفِهِمْ اَنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ يُتْلٰى عَلَيْهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَرَحْمَةً وَّذِكْرٰى لِقَوْمٍ يُّؤْمِنُوْنَ ࣖ ', 'awa lam yakfihim ann<u>aa</u> anzaln<u>aa</u> \'alayka <strong>a</strong>lkit<u>aa</u>ba yutl<u>aa</u> \'alayhim inna fii <u>dzaa</u>lika lara<u>h</u>matan wa<u>dz</u>ikr<u>aa</u> liqawmin yu/minuun<strong>a</strong>', 'Apakah tidak cukup bagi mereka bahwa Kami telah menurunkan kepadamu Kitab (Al-Qur\'an) yang dibacakan kepada mereka? Sungguh, dalam (Al-Qur\'an) itu terdapat rahmat yang besar dan pelajaran bagi orang-orang yang beriman. '),
	(3392, 29, 52, 'قُلْ كَفٰى بِاللّٰهِ بَيْنِيْ وَبَيْنَكُمْ شَهِيْدًاۚ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ اٰمَنُوْا بِالْبَاطِلِ وَكَفَرُوْا بِاللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ', 'qul kaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi baynii wabaynakum syahiidan ya\'lamu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu bi<strong>a</strong>lb<u>aath</u>ili wakafaruu bi<strong>al</strong>l<u>aa</u>hi ul<u>aa</u>-ika humu <strong>a</strong>lkh<u>aa</u>siruun<strong>a</strong>', 'Katakanlah (Muhammad), ”Cukuplah Allah menjadi saksi antara aku dan kamu. Dia mengetahui apa yang di langit dan di bumi. Dan orang yang percaya kepada yang batil dan ingkar kepada Allah, mereka itulah orang-orang yang rugi.” '),
	(3393, 29, 53, 'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَلَوْلَآ اَجَلٌ مُّسَمًّى لَّجَاۤءَهُمُ الْعَذَابُۗ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ', 'wayasta\'jiluunaka bi<strong>a</strong>l\'a<u>dzaa</u>bi walawl<u>aa</u> ajalun musamman laj<u>aa</u>-ahumu <strong>a</strong>l\'a<u>dzaa</u>bu walaya/tiyannahum baghtatan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Dan mereka meminta kepadamu agar segera diturunkan azab. Kalau bukan karena waktunya yang telah ditetapkan, niscaya datang azab kepada mereka, dan (azab itu) pasti akan datang kepada mereka dengan tiba-tiba, sedang mereka tidak menyadarinya. '),
	(3394, 29, 54, 'يَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَۙ', 'wayasta\'jiluunaka bi<strong>a</strong>l\'a<u>dzaa</u>bi walawl<u>aa</u> ajalun musamman laj<u>aa</u>-ahumu <strong>a</strong>l\'a<u>dzaa</u>bu walaya/tiyannahum baghtatan wahum l<u>aa</u> yasy\'uruun<strong>a</strong>', 'Mereka meminta kepadamu agar segera diturunkan azab. Dan sesungguhnya neraka Jahanam itu pasti meliputi orang-orang kafir, '),
	(3395, 29, 55, 'يَوْمَ يَغْشٰىهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُوْلُ ذُوْقُوْا مَا كُنْتُمْ تَعْمَلُوْنَ', 'yawma yaghsy<u>aa</u>humu <strong>a</strong>l\'a<u>dzaa</u>bu min fawqihim wamin ta<u>h</u>ti arjulihim wayaquulu <u>dz</u>uuquu m<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'pada hari (ketika) azab menutup mereka dari atas dan dari bawah kaki mereka dan (Allah) berkata (kepada mereka), ”Rasakanlah (balasan dari) apa yang telah kamu kerjakan!”'),
	(3396, 29, 56, 'يٰعِبَادِيَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ اَرْضِيْ وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُوْنِ', 'y<u>aa</u> \'ib<u>aa</u>diya <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu inna ar<u>dh</u>ii w<u>aa</u>si\'atun fa-iyy<u>aa</u>ya fa<strong>u</strong>\'buduun<strong>i</strong>', 'Wahai hamba-hamba-Ku yang beriman! Sungguh, bumi-Ku luas, maka sembahlah Aku (saja). '),
	(3397, 29, 57, 'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ ثُمَّ اِلَيْنَا تُرْجَعُوْنَ', 'kullu nafsin <u>dzaa</u>-iqatu <strong>a</strong>lmawti tsumma ilayn<u>aa</u> turja\'uun<strong>a</strong>', 'Setiap yang bernyawa akan merasakan mati. Kemudian hanya kepada Kami kamu dikembalikan. '),
	(3398, 29, 58, 'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُبَوِّئَنَّهُمْ مِّنَ الْجَنَّةِ غُرَفًا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ نِعْمَ اَجْرُ الْعٰمِلِيْنَۖ', 'wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lanubawwi-annahum mina <strong>a</strong>ljannati ghurafan tajrii min ta<u>h</u>tih<u>aa</u> <strong>a</strong>l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> ni\'ma ajru <strong>a</strong>l\'<u>aa</u>miliin<strong>a</strong>', 'Dan orang-orang yang beriman dan mengerjakan kebajikan, sungguh, mereka akan Kami tempatkan pada tempat-tempat yang tinggi (di dalam surga), yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah sebaik-baik balasan bagi orang yang berbuat kebajikan, '),
	(3399, 29, 59, 'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ', '<strong>al</strong>la<u>dz</u>iina <u>sh</u>abaruu wa\'al<u>aa</u> rabbihim yatawakkaluun<strong>a</strong>', '(yaitu) orang-orang yang bersabar dan bertawakal kepada Tuhannya. '),
	(3400, 29, 60, 'وَكَاَيِّنْ مِّنْ دَاۤبَّةٍ لَّا تَحْمِلُ رِزْقَهَاۖ اللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْ وَهُوَ السَّمِيْعُ الْعَلِيْمُ', 'waka-ayyin min d<u>aa</u>bbatin l<u>aa</u> ta<u>h</u>milu rizqah<u>aa</u> <strong>al</strong>l<u>aa</u>hu yarzuquh<u>aa</u> wa-iyy<u>aa</u>kum wahuwa <strong>al</strong>ssamii\'u <strong>a</strong>l\'aliim<strong>u</strong>', 'Dan berapa banyak makhluk bergerak yang bernyawa yang tidak (dapat) membawa (mengurus) rezekinya sendiri. Allah-lah yang memberi rezeki kepadanya dan kepadamu. Dia Maha Mendengar, Maha Mengetahui. '),
	(3401, 29, 61, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُوْلُنَّ اللّٰهُ ۗفَاَنّٰى يُؤْفَكُوْنَ', 'wala-in sa-altahum man khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wasakhkhara <strong>al</strong>sysyamsa wa<strong>a</strong>lqamara layaquulunna <strong>al</strong>l<u>aa</u>hu fa-ann<u>aa</u> yu/fakuun<strong>a</strong>', 'Dan jika engkau bertanya kepada mereka, ”Siapakah yang menciptakan langit dan bumi dan menundukkan matahari dan bulan?” Pasti mereka akan menjawab, ”Allah.” Maka mengapa mereka bisa dipalingkan (dari kebenaran). '),
	(3402, 29, 62, 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ', '<strong>al</strong>l<u>aa</u>hu yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lahu inna <strong>al</strong>l<u>aa</u>ha bikulli syay-in \'aliim<strong>un</strong>', 'Allah melapangkan rezeki bagi orang yang Dia kehendaki di antara hamba-hamba-Nya dan Dia (pula) yang membatasi baginya. Sungguh, Allah Maha Mengetahui segala sesuatu. '),
	(3403, 29, 63, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ نَّزَّلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ مِنْۢ بَعْدِ مَوْتِهَا لَيَقُوْلُنَّ اللّٰهُ ۙقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ ࣖ', 'wala-in sa-altahum man nazzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-a<u>h</u>y<u>aa</u> bihi <strong>a</strong>l-ar<u>dh</u>a min ba\'di mawtih<u>aa</u> layaquulunna <strong>al</strong>l<u>aa</u>hu quli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'qiluun<strong>a</strong>', 'Dan jika kamu bertanya kepada mereka, ”Siapakah yang menurunkan air dari langit lalu dengan (air) itu dihidupkannya bumi yang sudah mati?” Pasti mereka akan menjawab, ”Allah.” Katakanlah, ”Segala puji bagi Allah,” tetapi kebanyakan mereka tidak mengerti. '),
	(3404, 29, 64, 'وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَآ اِلَّا لَهْوٌ وَّلَعِبٌۗ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۘ لَوْ كَانُوْا يَعْلَمُوْنَ', 'wam<u>aa</u> h<u>aadz</u>ihi <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> ill<u>aa</u> lahwun wala\'ibun wa-inna <strong>al</strong>dd<u>aa</u>ra <strong>a</strong>l-<u>aa</u>khirata lahiya <strong>a</strong>l<u>h</u>ayaw<u>aa</u>nu law k<u>aa</u>nuu ya\'lamuun<strong>a</strong>', 'Dan kehidupan dunia ini hanya senda gurau dan permainan. Dan sesungguhnya negeri akhirat itulah kehidupan yang sebenarnya, sekiranya mereka mengetahui. '),
	(3405, 29, 65, 'فَاِذَا رَكِبُوْا فِى الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُوْنَۙ ', 'fa-i<u>dzaa</u> rakibuu fii <strong>a</strong>lfulki da\'awuu <strong>al</strong>l<u>aa</u>ha mukhli<u>sh</u>iina lahu <strong>al</strong>ddiina falamm<u>aa</u> najj<u>aa</u>hum il<u>aa</u> <strong>a</strong>lbarri i<u>dzaa</u> hum yusyrikuun<strong>a</strong>', 'Maka apabila mereka naik kapal, mereka berdoa kepada Allah dengan penuh rasa pengabdian (ikhlas) kepada-Nya, tetapi ketika Allah menyelamatkan mereka sampai ke darat, malah mereka (kembali) mempersekutukan (Allah), '),
	(3406, 29, 66, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۙ وَلِيَتَمَتَّعُوْاۗ فَسَوْفَ يَعْلَمُوْنَ', 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum waliyatamatta\'uu fasawfa ya\'lamuun<strong>a</strong>', 'biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka dan silakan mereka (hidup) bersenang-senang (dalam kekafiran). Maka kelak mereka akan mengetahui (akibat perbuatannya). '),
	(3407, 29, 67, 'اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَمًا اٰمِنًا وَّيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُوْنَ', 'awa lam yaraw ann<u>aa</u> ja\'aln<u>aa</u> <u>h</u>araman <u>aa</u>minan wayutakha<u>ththh</u>afu <strong>al</strong>nn<u>aa</u>su min <u>h</u>awlihim afabi<strong>a</strong>lb<u>aath</u>ili yu/minuuna wabini\'mati <strong>al</strong>l<u>aa</u>hi yakfuruun<strong>a</strong>', 'Tidakkah mereka memperhatikan, bahwa Kami telah menjadikan (negeri mereka) tanah suci yang aman, padahal manusia di sekitarnya saling merampok. Mengapa (setelah nyata kebenaran) mereka masih percaya kepada yang batil dan ingkar kepada nikmat Allah? '),
	(3408, 29, 68, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاۤءَهٗ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ', 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi<strong>a</strong>l<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahu alaysa fii jahannama matswan lilk<u>aa</u>firiin<strong>a</strong>', 'Dan siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan kepada Allah atau orang yang mendustakan yang hak ketika (yang hak) itu datang kepadanya? Bukankah dalam neraka Jahanam ada tempat bagi orang-orang kafir? '),
	(3409, 29, 69, 'وَالَّذِيْنَ جَاهَدُوْا فِيْنَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۗ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِنِيْنَ ࣖ ', 'wa<strong>a</strong>lla<u>dz</u>iina j<u>aa</u>haduu fiin<u>aa</u> lanahdiyannahum subulan<u>aa</u> wa-inna <strong>al</strong>l<u>aa</u>ha lama\'a <strong>a</strong>lmu<u>h</u>siniin<strong>a</strong>', 'Dan orang-orang yang berjihad untuk (mencari keridaan) Kami, Kami akan tunjukkan kepada mereka jalan-jalan Kami. Dan sungguh, Allah beserta orang-orang yang berbuat baik.'),
	(3410, 30, 1, ' الۤمّۤ ۚ  ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(3411, 30, 2, 'غُلِبَتِ الرُّوْمُۙ ', 'ghulibati <strong>al</strong>rruum<strong>u</strong>', 'Bangsa Romawi telah dikalahkan,'),
	(3412, 30, 3, 'فِيْٓ اَدْنَى الْاَرْضِ وَهُمْ مِّنْۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُوْنَۙ ', 'fii adn<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i wahum min ba\'di ghalabihim sayaghlibuun<strong>a</strong>', 'di negeri yang terdekat dan mereka setelah kekalahannya itu akan menang, '),
	(3413, 30, 4, 'فِيْ بِضْعِ سِنِيْنَ ەۗ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْۢ بَعْدُ ۗوَيَوْمَىِٕذٍ يَّفْرَحُ الْمُؤْمِنُوْنَۙ', 'fii bi<u>dh</u>\'i siniina lill<u>aa</u>hi <strong>a</strong>l-amru min qablu wamin ba\'du wayawma-i<u>dz</u>in yafra<u>h</u>u <strong>a</strong>lmu/minuun<strong>a</strong>', 'dalam beberapa tahun (lagi). Bagi Allah-lah urusan sebelum dan setelah (mereka menang). Dan pada hari (kemenangan bangsa Romawi) itu bergembiralah orang-orang yang beriman, '),
	(3414, 30, 5, 'بِنَصْرِ اللّٰهِ ۗيَنْصُرُ مَنْ يَّشَاۤءُۗ وَهُوَ الْعَزِيْزُ الرَّحِيْمُ', 'bina<u>sh</u>ri <strong>al</strong>l<u>aa</u>hi yan<u>sh</u>uru man yasy<u>aa</u>u wahuwa <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'karena pertolongan Allah. Dia menolong siapa yang Dia kehendaki. Dia Mahaperkasa, Maha Penyayang.'),
	(3415, 30, 6, 'وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ وَعْدَهٗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ', 'wa\'da <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yukhlifu <strong>al</strong>l<u>aa</u>hu wa\'dahu wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', '(Itulah) janji Allah. Allah tidak akan menyalahi janji-Nya, tetapi kebanyakan manusia tidak mengetahui. '),
	(3416, 30, 7, 'يَعْلَمُوْنَ ظَاهِرًا مِّنَ الْحَيٰوةِ الدُّنْيَاۖ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غٰفِلُوْنَ', 'ya\'lamuuna <u>zhaa</u>hiran mina <strong>a</strong>l<u>h</u>ay<u>aa</u>ti <strong>al</strong>dduny<u>aa</u> wahum \'ani <strong>a</strong>l-<u>aa</u>khirati hum gh<u>aa</u>filuun<strong>a</strong>', 'Mereka mengetahui yang lahir (tampak) dari kehidupan dunia; sedangkan terhadap (kehidupan) akhirat mereka lalai. '),
	(3417, 30, 8, 'اَوَلَمْ يَتَفَكَّرُوْا فِيْٓ اَنْفُسِهِمْ ۗ مَا خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَاِنَّ كَثِيْرًا مِّنَ النَّاسِ بِلِقَاۤئِ رَبِّهِمْ لَكٰفِرُوْنَ', 'awa lam yatafakkaruu fii anfusihim m<u>aa</u> khalaqa <strong>al</strong>l<u>aa</u>hu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> ill<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wa-ajalin musamman wa-inna katsiiran mina <strong>al</strong>nn<u>aa</u>si biliq<u>aa</u>-i rabbihim lak<u>aa</u>firuun<strong>a</strong>', 'Dan mengapa mereka tidak memikirkan tentang (kejadian) diri mereka? Allah tidak menciptakan langit dan bumi dan apa yang ada di antara keduanya melainkan dengan (tujuan) yang benar dan dalam waktu yang ditentukan. Dan sesungguhnya kebanyakan di antara manusia benar-benar mengingkari pertemuan dengan Tuhannya. '),
	(3418, 30, 9, 'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً وَّاَثَارُوا الْاَرْضَ وَعَمَرُوْهَآ اَكْثَرَ مِمَّا عَمَرُوْهَا وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۗ فَمَا كَانَ', 'awa lam yasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong>la<u>dz</u>iina min qablihim k<u>aa</u>nuu asyadda minhum quwwatan wa-ats<u>aa</u>ruu <strong>a</strong>l-ar<u>dh</u>a wa\'amaruuh<u>aa</u> aktsara mimm<u>aa</u> \'amaruuh<u>aa</u> waj<u>aa</u>-at-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liya<u>zh</u>limahum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun<strong>a</strong>', 'Dan tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul)? Orang-orang itu lebih kuat dari mereka (sendiri) dan mereka telah mengolah bumi (tanah) serta memakmurkannya melebihi apa yang telah mereka makmurkan. Dan telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang jelas. Maka Allah sama sekali tidak berlaku zalim kepada mereka, tetapi merekalah yang berlaku zalim kepada diri mereka sendiri. '),
	(3419, 30, 10, 'ثُمَّ كَانَ عَاقِبَةَ الَّذِيْنَ اَسَاۤءُوا السُّوْۤاٰىٓ اَنْ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ وَكَانُوْا بِهَا يَسْتَهْزِءُوْنَ ࣖ ', 'tsumma k<u>aa</u>na \'<u>aa</u>qibata <strong>al</strong>la<u>dz</u>iina as<u>aa</u>uu <strong>al</strong>ssuu-<u>aa</u> an ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wak<u>aa</u>nuu bih<u>aa</u> yastahzi-uun<strong>a</strong>', 'Kemudian, azab yang lebih buruk adalah kesudahan bagi orang-orang yang mengerjakan kejahatan. Karena mereka mendustakan ayat-ayat Allah dan mereka selalu memperolok-olokkannya. '),
	(3420, 30, 11, 'اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ', '<strong>al</strong>l<u>aa</u>hu yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu tsumma ilayhi turja\'uun<strong>a</strong>', 'Allah yang memulai penciptaan (makhluk), kemudian mengulanginya kembali; kemudian kepada-Nya kamu dikembalikan.'),
	(3421, 30, 12, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُوْنَ', 'wayawma taquumu <strong>al</strong>ss<u>aa</u>\'atu yublisu <strong>a</strong>lmujrimuun<strong>a</strong>', 'Dan pada hari (ketika) terjadi Kiamat, orang-orang yang berdosa (kaum musyrik) terdiam berputus asa. '),
	(3422, 30, 13, 'وَلَمْ يَكُنْ لَّهُمْ مِّنْ شُرَكَاۤىِٕهِمْ شُفَعٰۤؤُا وَكَانُوْا بِشُرَكَاۤىِٕهِمْ كٰفِرِيْنَ', 'walam yakun lahum min syurak<u>aa</u>-ihim syufa\'<u>aa</u>u wak<u>aa</u>nuu bisyurak<u>aa</u>-ihim k<u>aa</u>firiin<strong>a</strong>', 'Dan tidak mungkin ada pemberi syafaat (pertolongan) bagi mereka dari berhala-berhala mereka, sedangkan mereka mengingkari berhala-berhala mereka itu. '),
	(3423, 30, 14, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّتَفَرَّقُوْنَ ', 'wayawma taquumu <strong>al</strong>ss<u>aa</u>\'atu yawma-i<u>dz</u>in yatafarraquun<strong>a</strong>', 'Dan pada hari (ketika) terjadi Kiamat, pada hari itu manusia terpecah-pecah (dalam kelompok). '),
	(3424, 30, 15, 'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَهُمْ فِيْ رَوْضَةٍ يُّحْبَرُوْنَ', 'fa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti fahum fii raw<u>dh</u>atin yu<u>h</u>baruun<strong>a</strong>', 'Maka adapun orang-orang yang beriman dan mengerjakan kebajikan, maka mereka di dalam taman (surga) bergembira. '),
	(3425, 30, 16, 'وَاَمَّا الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤئِ الْاٰخِرَةِ فَاُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waliq<u>aa</u>-i <strong>a</strong>l-<u>aa</u>khirati faul<u>aa</u>-ika fii <strong>a</strong>l\'a<u>dzaa</u>bi mu<u>hd</u>aruun<strong>a</strong>', 'Dan adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami serta (mendustakan) pertemuan hari akhirat, maka mereka tetap berada di dalam azab (neraka). '),
	(3426, 30, 17, 'فَسُبْحٰنَ اللّٰهِ حِيْنَ تُمْسُوْنَ وَحِيْنَ تُصْبِحُوْنَ', 'fasub<u>haa</u>na <strong>al</strong>l<u>aa</u>hi <u>h</u>iina tumsuuna wa<u>h</u>iina tu<u>sh</u>bi<u>h</u>uun<strong>a</strong>', 'Maka bertasbihlah kepada Allah pada petang hari dan pada pagi hari (waktu subuh), '),
	(3427, 30, 18, 'وَلَهُ الْحَمْدُ فِى السَّمٰوٰتِ وَالْاَرْضِ وَعَشِيًّا وَّحِيْنَ تُظْهِرُوْنَ', 'walahu <strong>a</strong>l<u>h</u>amdu fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa\'asyiyyan wa<u>h</u>iina tu<u>zh</u>hiruun<strong>a</strong>', 'dan segala puji bagi-Nya baik di langit, di bumi, pada malam hari dan pada waktu zuhur (tengah hari). '),
	(3428, 30, 19, 'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَا ۗوَكَذٰلِكَ تُخْرَجُوْنَ ࣖ', 'yukhriju <strong>a</strong>l<u>h</u>ayya mina <strong>a</strong>lmayyiti wayukhriju <strong>a</strong>lmayyita mina <strong>a</strong>l<u>h</u>ayyi wayu<u>h</u>yii <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> waka<u>dzaa</u>lika tukhrajuun<strong>a</strong>', 'Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan menghidupkan bumi setelah mati (kering). Dan seperti itulah kamu akan dikeluarkan (dari kubur). '),
	(3429, 30, 20, 'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ اِذَآ اَنْتُمْ بَشَرٌ تَنْتَشِرُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi an khalaqakum min tur<u>aa</u>bin tsumma i<u>dzaa</u> antum basyarun tantasyiruun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya ialah Dia menciptakan kamu dari tanah, kemudian tiba-tiba kamu (menjadi) manusia yang berkembang biak. '),
	(3430, 30, 21, 'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا لِّتَسْكُنُوْٓا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَّوَدَّةً وَّرَحْمَةً ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi an khalaqa lakum min anfusikum azw<u>aa</u>jan litaskunuu ilayh<u>aa</u> waja\'ala baynakum mawaddatan wara<u>h</u>matan inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yatafakkaruun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya ialah Dia menciptakan pasangan-pasangan untukmu dari jenismu sendiri, agar kamu cenderung dan merasa tenteram kepadanya, dan Dia menjadikan di antaramu rasa kasih dan sayang. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi kaum yang berpikir. '),
	(3431, 30, 22, 'وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْعٰلِمِيْنَ ', 'wamin <u>aa</u>y<u>aa</u>tihi khalqu <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>i</strong>khtil<u>aa</u>fu <strong>a</strong>lsinatikum wa-alw<u>aa</u>nikum inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin lil\'<u>aa</u>limiin<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya ialah penciptaan langit dan bumi, perbedaan bahasamu dan warna kulitmu. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi orang-orang yang mengetahui. '),
	(3432, 30, 23, 'وَمِنْ اٰيٰتِهٖ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَاۤؤُكُمْ مِّنْ فَضْلِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi man<u>aa</u>mukum bi<strong>a</strong>llayli wa<strong>al</strong>nnah<u>aa</u>ri wa<strong>i</strong>btigh<u>aa</u>ukum min fa<u>dh</u>lihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yasma\'uun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya ialah tidurmu pada waktu malam dan siang hari dan usahamu mencari sebagian dari karunia-Nya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mendengarkan. '),
	(3433, 30, 24, 'وَمِنْ اٰيٰتِهٖ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنَزِّلُ مِنَ السَّمَاۤءِ مَاۤءً فَيُحْيٖ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi yuriikumu <strong>a</strong>lbarqa khawfan wa<u>th</u>ama\'an wayunazzilu mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fayu<u>h</u>yii bihi <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya, Dia memperlihatkan kilat kepadamu untuk (menimbulkan) ketakutan dan harapan, dan Dia menurunkan air (hujan) dari langit, lalu dengan air itu dihidupkannya bumi setelah mati (kering). Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mengerti. '),
	(3434, 30, 25, 'وَمِنْ اٰيٰتِهٖٓ اَنْ تَقُوْمَ السَّمَاۤءُ وَالْاَرْضُ بِاَمْرِهٖۗ  ثُمَّ اِذَا دَعَاكُمْ دَعْوَةًۖ مِّنَ الْاَرْضِ اِذَآ اَنْتُمْ تَخْرُجُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi an taquuma <strong>al</strong>ssam<u>aa</u>u wa<strong>a</strong>l-ar<u>dh</u>u bi-amrihi tsumma i<u>dzaa</u> da\'<u>aa</u>kum da\'watan mina <strong>a</strong>l-ar<u>dh</u>i i<u>dzaa</u> antum takhrujuun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya ialah berdirinya langit dan bumi dengan kehendak-Nya. Kemudian apabila Dia memanggil kamu sekali panggil dari bumi, seketika itu kamu keluar (dari kubur). '),
	(3435, 30, 26, 'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ', 'walahu man fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i kullun lahu q<u>aa</u>nituun<strong>a</strong>', 'Dan milik-Nya apa yang di langit dan di bumi. Semuanya hanya kepada-Nya tunduk. '),
	(3436, 30, 27, 'وَهُوَ الَّذِيْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَهُوَ اَهْوَنُ عَلَيْهِۗ وَلَهُ الْمَثَلُ الْاَعْلٰى فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ ', 'wahuwa <strong>al</strong>la<u>dz</u>ii yabdau <strong>a</strong>lkhalqa tsumma yu\'iiduhu wahuwa ahwanu \'alayhi walahu <strong>a</strong>lmatsalu <strong>a</strong>l-a\'l<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Dan Dialah yang memulai penciptaan, kemudian mengulanginya kembali, dan itu lebih mudah bagi-Nya. Dia memiliki sifat yang Mahatinggi di langit dan di bumi. Dan Dialah Yang Mahaperkasa, Mahabijaksana. '),
	(3437, 30, 28, 'ضَرَبَ لَكُمْ مَّثَلًا مِّنْ اَنْفُسِكُمْۗ هَلْ لَّكُمْ مِّنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ شُرَكَاۤءَ فِيْ مَا رَزَقْنٰكُمْ فَاَنْتُمْ فِيْهِ سَوَاۤءٌ تَخَافُوْنَهُمْ كَخِيْفَتِكُمْ اَنْفُسَكُمْۗ  كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْقِلُوْنَ', '<u>dh</u>araba lakum matsalan min anfusikum hal lakum mimm<u>aa</u> malakat aym<u>aa</u>nukum min syurak<u>aa</u>-a fii m<u>aa</u> razaqn<u>aa</u>kum fa-antum fiihi saw<u>aa</u>un takh<u>aa</u>fuunahum kakhiifatikum anfusakum ka<u>dzaa</u>lika nufa<u>shsh</u>ilu <strong>a</strong>l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'qiluun<strong>a</strong>', 'Dia membuat perumpamaan bagimu dari dirimu sendiri. Apakah (kamu rela jika) ada di antara hamba sahaya yang kamu miliki, menjadi sekutu bagimu dalam (memiliki) rezeki yang telah Kami berikan kepadamu, sehingga kamu menjadi setara dengan mereka dalam hal ini, lalu kamu takut kepada mereka sebagaimana kamu takut kepada sesamamu. Demikianlah Kami jelaskan ayat-ayat itu bagi kaum yang mengerti.'),
	(3438, 30, 29, 'بَلِ اتَّبَعَ الَّذِيْنَ ظَلَمُوْٓا اَهْوَاۤءَهُمْ بِغَيْرِ عِلْمٍۗ فَمَنْ يَّهْدِيْ مَنْ اَضَلَّ اللّٰهُ ۗوَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ', 'bali ittaba\'a <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu ahw<u>aa</u>-ahum bighayri \'ilmin faman yahdii man a<u>dh</u>alla <strong>al</strong>l<u>aa</u>hu wam<u>aa</u> lahum min n<u>aas</u>iriin<strong>a</strong>', 'Tetapi orang-orang yang zalim, mengikuti keinginannya tanpa ilmu pengetahuan; maka siapakah yang dapat memberi petunjuk kepada orang yang telah disesatkan Allah. Dan tidak ada seorang penolong pun bagi mereka. '),
	(3439, 30, 30, 'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۗ فِطْرَتَ اللّٰهِ الَّتِيْ فَطَرَ النَّاسَ عَلَيْهَاۗ  لَا تَبْدِيْلَ لِخَلْقِ اللّٰهِ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُۙ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ ', 'fa-aqim wajhaka li<strong>l</strong>ddiini <u>h</u>aniifan fi<u>th</u>rata <strong>al</strong>l<u>aa</u>hi <strong>al</strong>latii fa<u>th</u>ara <strong>al</strong>nn<u>aa</u>sa \'alayh<u>aa</u> l<u>aa</u> tabdiila likhalqi <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>lika <strong>al</strong>ddiinu <strong>a</strong>lqayyimu wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Maka hadapkanlah wajahmu dengan lurus kepada agama (Islam); (sesuai) fitrah Allah disebabkan Dia telah menciptakan manusia menurut (fitrah) itu. Tidak ada perubahan pada ciptaan Allah. (Itulah) agama yang lurus, tetapi kebanyakan manusia tidak mengetahui,'),
	(3440, 30, 31, '۞ مُنِيْبِيْنَ اِلَيْهِ وَاتَّقُوْهُ وَاَقِيْمُوا الصَّلٰوةَ وَلَا تَكُوْنُوْا مِنَ الْمُشْرِكِيْنَۙ', 'muniibiina ilayhi wa<strong>i</strong>ttaquuhu wa-aqiimuu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wal<u>aa</u> takuunuu mina <strong>a</strong>lmusyrikiin<strong>a</strong>', 'dengan kembali bertobat kepada-Nya dan bertakwalah kepada-Nya serta laksanakanlah salat dan janganlah kamu termasuk orang-orang yang mempersekutukan  Allah, '),
	(3441, 30, 32, 'مِنَ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا ۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ', 'mina <strong>al</strong>la<u>dz</u>iina farraquu diinahum wak<u>aa</u>nuu syiya\'an kullu <u>h</u>izbin bim<u>aa</u> ladayhim fari<u>h</u>uun<strong>a</strong>', 'yaitu orang-orang yang memecah belah agama mereka dan mereka menjadi beberapa golongan. Setiap golongan merasa bangga dengan apa yang ada pada golongan mereka. '),
	(3442, 30, 33, 'وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُّنِيْبِيْنَ اِلَيْهِ ثُمَّ اِذَآ اَذَاقَهُمْ مِّنْهُ رَحْمَةً اِذَا فَرِيْقٌ مِّنْهُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ', 'wa-i<u>dzaa</u> massa <strong>al</strong>nn<u>aa</u>sa <u>dh</u>urrun da\'aw rabbahum muniibiina ilayhi tsumma i<u>dzaa</u> a<u>dzaa</u>qahum minhu ra<u>h</u>matan i<u>dzaa</u> fariiqun minhum birabbihim yusyrikuun<strong>a</strong>', 'Dan apabila manusia ditimpa oleh suatu bahaya, mereka menyeru Tuhannya dengan kembali (bertobat) kepada-Nya, kemudian apabila Dia memberikan sedikit rahmat-Nya kepada mereka, tiba-tiba sebagian mereka mempersekutukan Allah.'),
	(3443, 30, 34, 'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ', 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum fatamatta\'uu fasawfa ta\'lamuun<strong>a</strong>', 'Biarkan mereka mengingkari rahmat yang telah Kami berikan. Dan bersenang-senanglah kamu, maka kelak kamu akan mengetahui (akibat perbuatanmu). '),
	(3444, 30, 35, 'اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطٰنًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوْا بِهٖ يُشْرِكُوْنَ ', 'am anzaln<u>aa</u> \'alayhim sul<u>th</u><u>aa</u>nan fahuwa yatakallamu bim<u>aa</u> k<u>aa</u>nuu bihi yusyrikuun<strong>a</strong>', 'Atau pernahkah Kami menurunkan kepada mereka keterangan, yang menjelaskan (membenarkan) apa yang (selalu) mereka persekutukan dengan Tuhan? '),
	(3445, 30, 36, 'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوْا بِهَاۗ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ اِذَا هُمْ يَقْنَطُوْنَ', 'wa<strong>i</strong><u>dzaa</u> a<u>dz</u>aqn<u>aa</u> <strong>al</strong>nn<u>aa</u>sa ra<u>h</u>matan fari<u>h</u>uu bih<u>aa</u> wa-in tu<u>sh</u>ibhum sayyi-atun bim<u>aa</u> qaddamat aydiihim i<u>dzaa</u> hum yaqna<u>th</u>uun<strong>a</strong>', 'Dan apabila Kami berikan sesuatu rahmat kepada manusia, niscaya mereka gembira dengan (rahmat) itu. Tetapi apabila mereka ditimpa sesuatu musibah (bahaya) karena kesalahan mereka sendiri, seketika itu mereka berputus asa. '),
	(3446, 30, 37, 'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ', 'awa lam yaraw anna <strong>al</strong>l<u>aa</u>ha yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u wayaqdiru inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun<strong>a</strong>', 'Dan tidakkah mereka memperhatikan bahwa Allah yang melapangkan rezeki bagi siapa yang Dia kehendaki dan Dia (pula) yang membatasi (bagi siapa yang Dia kehendaki). Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi kaum yang beriman. '),
	(3447, 30, 38, 'فَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِۗ ذٰلِكَ خَيْرٌ لِّلَّذِيْنَ يُرِيْدُوْنَ وَجْهَ اللّٰهِ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'fa<u>aa</u>ti <u>dzaa</u> <strong>a</strong>lqurb<u>aa</u> <u>h</u>aqqahu wa<strong>a</strong>lmiskiina wa<strong>i</strong>bna <strong>al</strong>ssabiili <u>dzaa</u>lika khayrun lilla<u>dz</u>iina yuriiduuna wajha <strong>al</strong>l<u>aa</u>hi waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Maka berikanlah haknya kepada kerabat dekat, juga kepada orang miskin dan orang-orang yang dalam perjalanan. Itulah yang lebih baik bagi orang-orang yang mencari keridaan Allah. Dan mereka itulah orang-orang yang beruntung. '),
	(3448, 30, 39, 'وَمَآ اٰتَيْتُمْ مِّنْ رِّبًا لِّيَرْبُوَا۠ فِيْٓ اَمْوَالِ النَّاسِ فَلَا يَرْبُوْا عِنْدَ اللّٰهِ ۚوَمَآ اٰتَيْتُمْ مِّنْ زَكٰوةٍ تُرِيْدُوْنَ وَجْهَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الْمُضْعِفُوْنَ', 'wam<u>aa</u> <u>aa</u>taytum min riban liyarbuwa fii amw<u>aa</u>li <strong>al</strong>nn<u>aa</u>si fal<u>aa</u> yarbuu \'inda <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> <u>aa</u>taytum min zak<u>aa</u>tin turiiduuna wajha <strong>al</strong>l<u>aa</u>hi faul<u>aa</u>-ika humu <strong>a</strong>lmu<u>dh</u>\'ifuun<strong>a</strong>', 'Dan sesuatu riba (tambahan) yang kamu berikan agar harta manusia bertambah, maka tidak bertambah dalam pandangan Allah. Dan apa yang kamu berikan berupa zakat yang kamu maksudkan untuk memperoleh keridaan Allah, maka itulah orang-orang yang melipatgandakan  (pahalanya). '),
	(3449, 30, 40, 'اَللّٰهُ الَّذِيْ خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّفْعَلُ مِنْ ذٰلِكُمْ مِّنْ شَيْءٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ࣖ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqakum tsumma razaqakum tsumma yumiitukum tsumma yu<u>h</u>yiikum hal min syurak<u>aa</u>-ikum man yaf\'alu min <u>dzaa</u>likum min syay-in sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun<strong>a</strong>', 'Allah yang menciptakan kamu, kemudian memberimu rezeki, lalu mematikanmu, kemudian menghidupkanmu (kembali). Adakah di antara mereka yang kamu sekutukan dengan Allah itu yang dapat berbuat sesuatu yang demikian itu? Mahasuci Dia dan Mahatinggi dari apa yang mereka  persekutukan. '),
	(3450, 30, 41, 'ظَهَرَ الْفَسَادُ فِى الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِى النَّاسِ لِيُذِيْقَهُمْ بَعْضَ الَّذِيْ عَمِلُوْا لَعَلَّهُمْ يَرْجِعُوْنَ', '<u>zh</u>ahara <strong>a</strong>lfas<u>aa</u>du fii <strong>a</strong>lbarri wa<strong>a</strong>lba<u>h</u>ri bim<u>aa</u> kasabat aydii <strong>al</strong>nn<u>aa</u>si liyu<u>dz</u>iiqahum ba\'<u>dh</u>a <strong>al</strong>la<u>dz</u>ii \'amiluu la\'allahum yarji\'uun<strong>a</strong>', 'Telah tampak kerusakan di darat dan di laut disebabkan karena perbuatan tangan manusia; Allah menghendaki agar mereka merasakan sebagian dari (akibat) perbuatan mereka, agar mereka kembali (ke jalan yang benar). '),
	(3451, 30, 42, 'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلُۗ  كَانَ اَكْثَرُهُمْ مُّشْرِكِيْنَ', 'qul siiruu fii <strong>a</strong>l-ar<u>dh</u>i fa<strong>u</strong>n<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong>la<u>dz</u>iina min qablu k<u>aa</u>na aktsaruhum musyrikiin<strong>a</strong>', 'Katakanlah (Muhammad), “Bepergianlah di bumi lalu lihatlah bagaimana kesudahan orang-orang dahulu. Kebanyakan dari mereka adalah orang-orang yang mempersekutukan (Allah).”'),
	(3452, 30, 43, 'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ يَوْمَىِٕذٍ يَّصَّدَّعُوْنَ', 'fa-aqim wajhaka li<strong>l</strong>ddiini <strong>a</strong>lqayyimi min qabli an ya/tiya yawmun l<u>aa</u> maradda lahu mina <strong>al</strong>l<u>aa</u>hi yawma-i<u>dz</u>in ya<u>shsh</u>adda\'uun<strong>a</strong>', 'Oleh karena itu, hadapkanlah wajahmu kepada agama yang lurus (Islam) sebelum datang dari Allah suatu hari (Kiamat) yang tidak dapat ditolak, pada hari itu mereka terpisah-pisah.'),
	(3453, 30, 44, 'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۚ وَمَنْ عَمِلَ صَالِحًا فَلِاَنْفُسِهِمْ يَمْهَدُوْنَۙ', 'man kafara fa\'alayhi kufruhu waman \'amila <u>shaa</u>li<u>h</u>an fali-anfusihim yamhaduun<strong>a</strong>', 'Barangsiapa kafir maka dia sendirilah yang menanggung (akibat) kekafirannya itu; dan barangsiapa mengerjakan kebajikan maka mereka menyiapkan untuk diri mereka sendiri (tempat yang menyenangkan), '),
	(3454, 30, 45, 'لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْ فَضْلِهٖۗ  اِنَّهٗ لَا يُحِبُّ الْكٰفِرِيْنَ', 'liyajziya <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti min fa<u>dh</u>lihi innahu l<u>aa</u> yu<u>h</u>ibbu <strong>a</strong>lk<u>aa</u>firiin<strong>a</strong>', 'agar Allah memberi balasan (pahala) kepada orang-orang yang beriman dan mengerjakan kebajikan dari karunia-Nya. Sungguh, Dia tidak menyukai orang-orang yang ingkar (kafir). '),
	(3455, 30, 46, 'وَمِنْ اٰيٰتِهٖٓ اَنْ يُّرْسِلَ الرِّيٰحَ مُبَشِّرٰتٍ وَّلِيُذِيْقَكُمْ مِّنْ رَّحْمَتِهٖ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ', 'wamin <u>aa</u>y<u>aa</u>tihi an yursila <strong>al</strong>rriy<u>aah</u>a mubasysyir<u>aa</u>tin waliyu<u>dz</u>iiqakum min ra<u>h</u>matihi walitajriya <strong>a</strong>lfulku bi-amrihi walitabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun<strong>a</strong>', 'Dan di antara tanda-tanda (kebesaran)-Nya adalah bahwa Dia mengirimkan angin sebagai pembawa berita gembira dan agar kamu merasakan sebagian dari rahmat-Nya dan agar kapal dapat berlayar dengan perintah-Nya dan (juga) agar kamu dapat mencari sebagian dari karunia-Nya, dan agar kamu bersyukur.'),
	(3456, 30, 47, 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَانْتَقَمْنَا مِنَ الَّذِيْنَ اَجْرَمُوْاۗ وَكَانَ حَقًّاۖ عَلَيْنَا نَصْرُ الْمُؤْمِنِيْنَ', 'walaqad arsaln<u>aa</u> min qablika rusulan il<u>aa</u> qawmihim faj<u>aa</u>uuhum bi<strong>a</strong>lbayyin<u>aa</u>ti fa<strong>i</strong>ntaqamn<u>aa</u> mina <strong>al</strong>la<u>dz</u>iina ajramuu wak<u>aa</u>na <u>h</u>aqqan \'alayn<u>aa</u> na<u>sh</u>ru <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan sungguh, Kami telah mengutus sebelum engkau (Muhammad) beberapa orang rasul kepada kaumnya, mereka datang kepadanya dengan membawa keterangan-keterangan (yang cukup), lalu Kami melakukan pembalasan terhadap orang-orang yang berdosa. Dan merupakan hak Kami untuk menolong orang-orang yang beriman. '),
	(3457, 30, 48, 'اَللّٰهُ الَّذِيْ يُرْسِلُ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَيَبْسُطُهٗ فِى السَّمَاۤءِ كَيْفَ يَشَاۤءُ وَيَجْعَلُهٗ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ فَاِذَآ اَصَابَ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَۚ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii yursilu <strong>al</strong>rriy<u>aah</u>a fatutsiiru sa<u>haa</u>ban fayabsu<u>th</u>uhu fii <strong>al</strong>ssam<u>aa</u>-i kayfa yasy<u>aa</u>u wayaj\'aluhu kisafan fatar<u>aa</u> <strong>a</strong>lwadqa yakhruju min khil<u>aa</u>lihi fa-i<u>dzaa</u> a<u>shaa</u>ba bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi i<u>dzaa</u> hum yastabsyiruun<strong>a</strong>', 'Allah-lah yang mengirimkan angin, lalu angin itu menggerakkan awan dan Allah membentangkannya di langit menurut yang Dia kehendaki, dan menjadikannya bergumpal-gumpal, lalu engkau lihat hujan keluar dari celah-celahnya, maka apabila Dia menurunkannya kepada hamba-hamba-Nya yang Dia kehendaki tiba-tiba mereka bergembira. '),
	(3458, 30, 49, 'وَاِنْ كَانُوْا مِنْ قَبْلِ اَنْ يُّنَزَّلَ عَلَيْهِمْ مِّنْ قَبْلِهٖ لَمُبْلِسِيْنَۚ', 'wa-in k<u>aa</u>nuu min qabli an yunazzala \'alayhim min qablihi lamublisiin<strong>a</strong>', 'Padahal walaupun sebelum hujan diturunkan kepada mereka, mereka benar-benar telah berputus asa. '),
	(3459, 30, 50, 'فَانْظُرْ اِلٰٓى اٰثٰرِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ ذٰلِكَ لَمُحْيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ', 'fa<strong>u</strong>n<u>zh</u>ur il<u>aa</u> <u>aa</u>ts<u>aa</u>ri ra<u>h</u>mati <strong>al</strong>l<u>aa</u>hi kayfa yu<u>h</u>yii <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna <u>dzaa</u>lika lamu<u>h</u>yii <strong>a</strong>lmawt<u>aa</u> wahuwa \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Maka perhatikanlah bekas-bekas rahmat Allah, bagaimana Allah menghidupkan bumi setelah mati (kering). Sungguh, itu berarti Dia pasti (berkuasa) menghidupkan yang telah mati. Dan Dia Mahakuasa atas segala sesuatu. '),
	(3460, 30, 51, 'وَلَىِٕنْ اَرْسَلْنَا رِيْحًا فَرَاَوْهُ مُصْفَرًّا لَّظَلُّوْا مِنْۢ بَعْدِهٖ يَكْفُرُوْنَ', 'wala-in arsaln<u>aa</u> rii<u>h</u>an fara-awhu mu<u>sh</u>farran la<u>zh</u>alluu min ba\'dihi yakfuruun<strong>a</strong>', 'Dan sungguh, jika Kami mengirimkan angin lalu mereka melihat (tumbuh-tumbuhan itu) menjadi kuning (kering), niscaya setelah itu mereka tetap ingkar. '),
	(3461, 30, 52, 'فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ', 'fa-innaka l<u>aa</u> tusmi\'u <strong>a</strong>lmawt<u>aa</u> wal<u>aa</u> tusmi\'u <strong>al</strong><u>shsh</u>umma <strong>al</strong>ddu\'<u>aa</u>-a i<u>dzaa</u> wallaw mudbiriin<strong>a</strong>', 'Maka sungguh, engkau tidak akan sanggup menjadikan orang-orang yang mati itu dapat mendengar, dan menjadikan orang-orang yang tuli dapat mendengar seruan, apabila mereka berpaling ke belakang. '),
	(3462, 30, 53, 'وَمَآ اَنْتَ بِهٰدِ الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ ࣖ', 'wam<u>aa</u> anta bih<u>aa</u>di <strong>a</strong>l\'umyi \'an <u>dh</u>al<u>aa</u>latihim in tusmi\'u ill<u>aa</u> man yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum muslimuun<strong>a</strong>', 'Dan engkau tidak akan dapat memberi petunjuk kepada orang-orang yang buta (mata hatinya) dari kesesatannya. Dan engkau tidak dapat memperdengarkan (petunjuk Tuhan) kecuali kepada orang-orang yang beriman dengan ayat-ayat Kami, maka mereka itulah orang-orang yang berserah diri (kepada Kami). '),
	(3463, 30, 54, '۞ اَللّٰهُ الَّذِيْ خَلَقَكُمْ مِّنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْۢ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْۢ بَعْدِ قُوَّةٍ ضَعْفًا وَّشَيْبَةً ۗيَخْلُقُ مَا يَشَاۤءُۚ وَهُوَ الْعَلِيْمُ الْقَدِيْرُ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqakum min <u>dh</u>a\'fin tsumma ja\'ala min ba\'di <u>dh</u>a\'fin quwwatan tsumma ja\'ala min ba\'di quwwatin <u>dh</u>a\'fan wasyaybatan yakhluqu m<u>aa</u> yasy<u>aa</u>u wahuwa <strong>a</strong>l\'aliimu <strong>a</strong>lqadiir<strong>u</strong>', 'Allah-lah yang menciptakan kamu dari keadaan lemah, kemudian Dia menjadikan (kamu) setelah keadaan lemah itu menjadi kuat, kemudian Dia menjadikan (kamu) setelah kuat itu lemah (kembali) dan beruban. Dia menciptakan apa yang Dia kehendaki. Dan Dia Maha Mengetahui, Mahakuasa. '),
	(3464, 30, 55, 'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُوْنَ ەۙ مَا لَبِثُوْا غَيْرَ سَاعَةٍ ۗ كَذٰلِكَ كَانُوْا يُؤْفَكُوْنَ', 'wayawma taquumu <strong>al</strong>ss<u>aa</u>\'atu yuqsimu <strong>a</strong>lmujrimuuna m<u>aa</u> labitsuu ghayra s<u>aa</u>\'atin ka<u>dzaa</u>lika k<u>aa</u>nuu yu/fakuun<strong>a</strong>', 'Dan pada hari (ketika) terjadinya Kiamat, orang-orang yang berdosa bersumpah, bahwa mereka berdiam (dalam kubur) hanya sesaat (saja). Begitulah dahulu mereka dipalingkan (dari kebenaran). '),
	(3465, 30, 56, 'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَالْاِيْمَانَ لَقَدْ لَبِثْتُمْ فِيْ كِتٰبِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۖ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُوْنَ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma wa<strong>a</strong>l-iim<u>aa</u>na laqad labitstum fii kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi il<u>aa</u> yawmi <strong>a</strong>lba\'tsi fah<u>aadzaa</u> yawmu <strong>a</strong>lba\'tsi wal<u>aa</u>kinnakum kuntum l<u>aa</u> ta\'lamuun<strong>a</strong>', 'Dan orang-orang yang diberi ilmu dan keimanan berkata (kepada orang-orang kafir), “Sungguh, kamu telah berdiam (dalam kubur) menurut ketetapan Allah, sampai hari kebangkitan. Maka inilah hari kebangkitan itu, tetapi (dahulu) kamu tidak meyakini(nya).” '),
	(3466, 30, 57, 'فَيَوْمَىِٕذٍ لَّا يَنْفَعُ الَّذِيْنَ ظَلَمُوْا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُوْنَ', 'fayawma-i<u>dz</u>in l<u>aa</u> yanfa\'u <strong>al</strong>la<u>dz</u>iina <u>zh</u>alamuu ma\'<u>dz</u>iratuhum wal<u>aa</u> hum yusta\'tabuun<strong>a</strong>', 'Maka pada hari itu tidak bermanfaat (lagi) permintaan maaf orang-orang yang zalim, dan mereka tidak pula diberi kesempatan bertobat lagi. '),
	(3467, 30, 58, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۗ وَلَىِٕنْ جِئْتَهُمْ بِاٰيَةٍ لَّيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ اَنْتُمْ اِلَّا مُبْطِلُوْنَ', 'walaqad <u>dh</u>arabn<u>aa</u> li<strong>l</strong>nn<u>aa</u>si fii h<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni min kulli matsalin wala-in ji/tahum bi-<u>aa</u>yatin layaquulanna <strong>al</strong>la<u>dz</u>iina kafaruu in antum ill<u>aa</u> mub<u>th</u>iluun<strong>a</strong>', 'Dan sesungguhnya telah Kami jelaskan kepada manusia segala macam perumpamaan dalam Al-Qur\'an ini. Dan jika engkau membawa suatu ayat kepada mereka, pastilah orang-orang kafir itu akan berkata, ”Kamu hanyalah orang-orang yang membuat kepalsuan belaka.” '),
	(3468, 30, 59, 'كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الَّذِيْنَ لَا يَعْلَمُوْنَ', 'ka<u>dzaa</u>lika ya<u>th</u>ba\'u <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> quluubi <strong>al</strong>la<u>dz</u>iina l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Demikianlah Allah mengunci hati orang-orang yang tidak (mau) memahami. '),
	(3469, 30, 60, 'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلَا يَسْتَخِفَّنَّكَ الَّذِيْنَ لَا يُوْقِنُوْنَ ࣖ ', 'fa<strong>i</strong><u>sh</u>bir inna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u> yastakhiffannaka <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yuuqinuun<strong>a</strong>', 'Maka bersabarlah engkau (Muhammad), sungguh, janji Allah itu benar dan sekali-kali jangan sampai orang-orang yang tidak meyakini (kebenaran ayat-ayat Allah) itu menggelisahkan engkau. '),
	(3470, 31, 1, ' الۤمّۤ ۗ ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(3471, 31, 2, 'تِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِۙ ', 'tilka <u>aa</u>y<u>aa</u>tu <strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>l<u>h</u>akiim<strong>i</strong>', 'Inilah ayat-ayat Al-Qur\'an yang mengandung hikmah, '),
	(3472, 31, 3, 'هُدًى وَّرَحْمَةً لِّلْمُحْسِنِيْنَۙ ', 'hudan wara<u>h</u>matan lilmu<u>h</u>siniin<strong>a</strong>', 'sebagai petunjuk dan rahmat bagi orang-orang yang berbuat kebaikan, '),
	(3473, 31, 4, 'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ ', '<strong>al</strong>la<u>dz</u>iina yuqiimuuna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wayu/tuuna <strong>al</strong>zzak<u>aa</u>ta wahum bi<strong>a</strong>l-<u>aa</u>khirati hum yuuqinuun<strong>a</strong>', '(yaitu) orang-orang yang melaksanakan salat, menunaikan zakat dan mereka meyakini adanya akhirat. '),
	(3474, 31, 5, 'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ', 'ul<u>aa</u>-ika \'al<u>aa</u> hudan min rabbihim waul<u>aa</u>-ika humu <strong>a</strong>lmufli<u>h</u>uun<strong>a</strong>', 'Merekalah orang-orang yang tetap mendapat petunjuk dari Tuhannya dan mereka itulah orang-orang yang beruntung. '),
	(3475, 31, 6, 'وَمِنَ النَّاسِ مَنْ يَّشْتَرِيْ لَهْوَ الْحَدِيْثِ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ بِغَيْرِ عِلْمٍۖ وَّيَتَّخِذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ', 'wamina <strong>al</strong>nn<u>aa</u>si man yasytarii lahwa <strong>a</strong>l<u>h</u>adiitsi liyu<u>dh</u>illa \'an sabiili <strong>al</strong>l<u>aa</u>hi bighayri \'ilmin wayattakhi<u>dz</u>ah<u>aa</u> huzuwan ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun muhiin<strong>un</strong>', 'Dan di antara manusia (ada) orang yang mempergunakan percakapan kosong untuk menyesatkan (manusia) dari jalan Allah tanpa ilmu dan menjadikannya olok-olokan. Mereka itu akan memperoleh azab yang menghinakan.'),
	(3476, 31, 7, 'وَاِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا وَلّٰى مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَا كَاَنَّ فِيْٓ اُذُنَيْهِ وَقْرًاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhi <u>aa</u>y<u>aa</u>tun<u>aa</u> wall<u>aa</u> mustakbiran ka-an lam yasma\'h<u>aa</u> ka-anna fii u<u>dz</u>unayhi waqran fabasysyirhu bi\'a<u>dzaa</u>bin <strong>a</strong>liim<strong>in</strong>', 'Dan apabila dibacakan kepadanya ayat-ayat Kami, dia berpaling dengan menyombongkan diri seolah-olah dia belum mendengarnya, seakan-akan ada sumbatan di kedua telinganya, maka gembirakanlah dia dengan azab yang pedih.'),
	(3477, 31, 8, 'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتُ النَّعِيْمِۙ', 'inna <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lahum jann<u>aa</u>tu <strong>al</strong>nna\'iim<strong>i</strong>', 'Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat surga-surga yang penuh kenikmatan, '),
	(3478, 31, 9, 'خٰلِدِيْنَ فِيْهَاۗ وَعْدَ اللّٰهِ حَقًّاۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ', 'kh<u>aa</u>lidiina fiih<u>aa</u> wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqan wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'mereka kekal di dalamnya, sebagai janji Allah yang benar. Dan Dia Mahaperkasa, Mahabijaksana. '),
	(3479, 31, 10, 'خَلَقَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍۗ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ', 'khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti bighayri \'amadin tarawnah<u>aa</u> wa-alq<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bikum wabatstsa fiih<u>aa</u> min kulli d<u>aa</u>bbatin wa-anzaln<u>aa</u> mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-anbatn<u>aa</u> fiih<u>aa</u> min kulli zawjin kariim<strong>in</strong>', 'Dia menciptakan langit tanpa tiang sebagaimana kamu melihatnya, dan Dia meletakkan gunung-gunung (di permukaan) bumi agar ia (bumi) tidak menggoyangkan kamu; dan memperkembangbiakkan segala macam jenis makhluk bergerak yang bernyawa di bumi. Dan Kami turunkan air hujan dari langit, lalu Kami tumbuhkan padanya segala macam tumbuh-tumbuhan yang baik. '),
	(3480, 31, 11, 'هٰذَا خَلْقُ اللّٰهِ فَاَرُوْنِيْ مَاذَا خَلَقَ الَّذِيْنَ مِنْ دُوْنِهٖۗ بَلِ الظّٰلِمُوْنَ فِيْ ضَلٰلٍ مُّبِيْنٍ ࣖ', 'h<u>aadzaa</u> khalqu <strong>al</strong>l<u>aa</u>hi fa-aruunii m<u>aatsaa</u> khalaqa <strong>al</strong>la<u>dz</u>iina min duunihi bali <strong>al</strong><u>zhzhaa</u>limuuna fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Inilah ciptaan Allah, maka perlihatkanlah olehmu kepadaku apa yang telah diciptakan oleh (sesembahanmu) selain Allah. Sebenarnya orang-orang yang zalim itu berada di dalam kesesatan yang nyata. '),
	(3481, 31, 12, 'وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِ ۗوَمَنْ يَّشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ ', 'walaqad <u>aa</u>tayn<u>aa</u> luqm<u>aa</u>na <strong>a</strong>l<u>h</u>ikmata ani usykur lill<u>aa</u>hi waman yasykur fa-innam<u>aa</u> yasykuru linafsihi waman kafara fa-inna <strong>al</strong>l<u>aa</u>ha ghaniyyun <u>h</u>amiid<strong>un</strong>', 'Dan sungguh, telah Kami berikan hikmah kepada Lukman, yaitu,  ”Bersyukurlah kepada Allah! Dan barangsiapa bersyukur (kepada Allah), maka sesungguhnya dia bersyukur untuk dirinya sendiri; dan barangsiapa tidak bersyukur (kufur), maka sesungguhnya Allah Mahakaya, Maha Terpuji.” '),
	(3482, 31, 13, 'وَاِذْ قَالَ لُقْمٰنُ لِابْنِهٖ وَهُوَ يَعِظُهٗ يٰبُنَيَّ لَا تُشْرِكْ بِاللّٰهِ ۗاِنَّ الشِّرْكَ لَظُلْمٌ عَظِيْمٌ ', 'wa-i<u>dz</u> q<u>aa</u>la luqm<u>aa</u>nu li<strong>i</strong>bnihi wahuwa ya\'i<u>zh</u>uhu y<u>aa</u> bunayya l<u>aa</u> tusyrik bi<strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>sysyirka la<u>zh</u>ulmun \'a<u>zh</u>iim<strong>un</strong>', 'Dan (ingatlah) ketika Lukman berkata kepada anaknya, ketika dia memberi pelajaran kepadanya, ”Wahai anakku! Janganlah engkau mempersekutukan Allah, sesungguhnya mempersekutukan (Allah) adalah benar-benar kezaliman yang besar.” '),
	(3483, 31, 14, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهٗ وَهْنًا عَلٰى وَهْنٍ وَّفِصَالُهٗ فِيْ عَامَيْنِ اَنِ اشْكُرْ لِيْ وَلِوَالِدَيْكَۗ اِلَيَّ الْمَصِيْرُ', 'wawa<u>shsh</u>ayn<u>aa</u> <strong>a</strong>l-ins<u>aa</u>na biw<u>aa</u>lidayhi <u>h</u>amalat-hu ummuhu wahnan \'al<u>aa</u> wahnin wafi<u>shaa</u>luhu fii \'<u>aa</u>mayni ani usykur lii waliw<u>aa</u>lidayka ilayya <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan Kami perintahkan kepada manusia (agar berbuat baik) kepada kedua orang tuanya. Ibunya telah mengandungnya dalam keadaan lemah yang bertambah-tambah, dan menyapihnya dalam usia dua tahun. Bersyukurlah kepada-Ku dan kepada kedua orang tuamu. Hanya kepada Aku kembalimu. '),
	(3484, 31, 15, 'وَاِنْ جَاهَدٰكَ عَلٰٓى اَنْ تُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِى الدُّنْيَا مَعْرُوْفًا ۖوَّاتَّبِعْ سَبِيْلَ مَنْ اَنَابَ اِلَيَّۚ  ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'wa-in j<u>aa</u>had<u>aa</u>ka \'al<u>aa</u> an tusyrika bii m<u>aa</u> laysa laka bihi \'ilmun fal<u>aa</u> tu<u>th</u>i\'hum<u>aa</u> wa<u>shaah</u>ibhum<u>aa</u> fii <strong>al</strong>dduny<u>aa</u> ma\'ruufan wa<strong>i</strong>ttabi\' sabiila man an<u>aa</u>ba ilayya tsumma ilayya marji\'ukum fa-unabbi-ukum bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang engkau tidak mempunyai ilmu tentang itu, maka janganlah engkau menaati keduanya, dan pergaulilah keduanya di dunia dengan baik, dan ikutilah jalan orang yang kembali kepada-Ku. Kemudian hanya kepada-Ku tempat kembalimu, maka akan Aku beritahukan kepadamu apa yang telah kamu kerjakan. '),
	(3485, 31, 16, 'يٰبُنَيَّ اِنَّهَآ اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُنْ فِيْ صَخْرَةٍ اَوْ فِى السَّمٰوٰتِ اَوْ فِى الْاَرْضِ يَأْتِ بِهَا اللّٰهُ ۗاِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ', 'y<u>aa</u> bunayya innah<u>aa</u> in taku mitsq<u>aa</u>la <u>h</u>abbatin min khardalin fatakun fii <u>sh</u>akhratin aw fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti aw fii <strong>a</strong>l-ar<u>dh</u>i ya/ti bih<u>aa</u> <strong>al</strong>l<u>aa</u>hu inna <strong>al</strong>l<u>aa</u>ha la<u>th</u>iifun khabiir<strong>un</strong>', '(Lukman berkata), ”Wahai anakku! Sungguh, jika ada (sesuatu perbuatan) seberat biji sawi, dan berada dalam batu atau di langit atau di bumi, niscaya Allah akan memberinya (balasan). Sesungguhnya Allah Mahahalus, Mahateliti. '),
	(3486, 31, 17, 'يٰبُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوْفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَآ اَصَابَكَۗ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ', 'y<u>aa</u> bunayya aqimi <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa/mur bi<strong>a</strong>lma\'ruufi wa<strong>i</strong>nha \'ani <strong>a</strong>lmunkari wa<strong>i</strong><u>sh</u>bir \'al<u>aa</u> m<u>aa</u> a<u>shaa</u>baka inna <u>dzaa</u>lika min \'azmi <strong>a</strong>l-umuur<strong>i</strong>', 'Wahai anakku! Laksanakanlah salat dan suruhlah (manusia) berbuat yang makruf dan cegahlah (mereka) dari yang mungkar dan bersabarlah terhadap apa yang menimpamu, sesungguhnya yang demikian itu termasuk perkara yang penting.'),
	(3487, 31, 18, 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۚ', 'wal<u>aa</u> tu<u>sh</u>a\'\'ir khaddaka li<strong>l</strong>nn<u>aa</u>si wal<u>aa</u> tamsyi fii <strong>a</strong>l-ar<u>dh</u>i mara<u>h</u>an inna <strong>al</strong>l<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu kulla mukht<u>aa</u>lin fakhuur<strong>in</strong>', 'Dan janganlah kamu memalingkan wajah dari manusia (karena sombong) dan janganlah berjalan di bumi dengan angkuh. Sungguh, Allah tidak menyukai orang-orang yang sombong dan membanggakan diri.'),
	(3488, 31, 19, 'وَاقْصِدْ فِيْ مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۗ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَمِيْرِ ࣖ', 'wa<strong>i</strong>q<u>sh</u>id fii masyyika wa<strong>u</strong>gh<u>dh</u>u<u>dh</u> min <u>sh</u>awtika inna ankara <strong>a</strong>l-a<u>sh</u>w<u>aa</u>ti la<u>sh</u>awtu <strong>a</strong>l<u>h</u>amiir<strong>i</strong>', 'Dan sederhanakanlah dalam berjalan dan lunakkanlah suaramu. Sesungguhnya seburuk-buruk suara ialah suara keledai.”'),
	(3489, 31, 20, 'اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهٗ ظَاهِرَةً وَّبَاطِنَةً ۗوَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ', 'alam taraw anna <strong>al</strong>l<u>aa</u>ha sakhkhara lakum m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wa-asbagha \'alaykum ni\'amahu <u>zhaa</u>hiratan wab<u>aath</u>inatan wamina <strong>al</strong>nn<u>aa</u>si man yuj<u>aa</u>dilu fii <strong>al</strong>l<u>aa</u>hi bighayri \'ilmin wal<u>aa</u> hudan wal<u>aa</u> kit<u>aa</u>bin muniir<strong>in</strong>', 'Tidakkah kamu memperhatikan bahwa Allah telah menundukkan apa yang ada di langit dan apa yang ada di bumi untuk (kepentingan)mu dan menyempurnakan nikmat-Nya untukmu lahir dan batin. Tetapi di antara manusia ada yang membantah tentang (keesaan) Allah tanpa ilmu atau petunjuk dan tanpa Kitab yang memberi penerangan.'),
	(3490, 31, 21, 'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَاۗ اَوَلَوْ كَانَ الشَّيْطٰنُ يَدْعُوْهُمْ اِلٰى عَذَابِ السَّعِيْرِ', 'wa-i<u>dzaa</u> qiila lahumu ittabi\'uu m<u>aa</u> anzala <strong>al</strong>l<u>aa</u>hu q<u>aa</u>luu bal nattabi\'u m<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na <strong>al</strong>sysyay<u>th</u><u>aa</u>nu yad\'uuhum il<u>aa</u> \'a<u>dzaa</u>bi <strong>al</strong>ssa\'iir<strong>i</strong>', 'Dan apabila dikatakan kepada mereka, ”Ikutilah apa yang diturunkan Allah!” Mereka menjawab, ”(Tidak), tetapi kami (hanya) mengikuti kebiasaan yang kami dapati dari nenek moyang kami.” Apakah mereka (akan mengikuti nenek moyang mereka) walaupun sebenarnya setan menyeru mereka ke dalam azab api yang menyala-nyala (neraka)?'),
	(3491, 31, 22, '۞ وَمَنْ يُّسْلِمْ وَجْهَهٗٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُوْرِ', 'waman yuslim wajhahu il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wahuwa mu<u>h</u>sinun faqadi istamsaka bi<strong>a</strong>l\'urwati <strong>a</strong>lwutsq<u>aa</u> wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi \'<u>aa</u>qibatu <strong>a</strong>l-umuur<strong>i</strong>', 'Dan barangsiapa berserah diri kepada Allah, sedang dia orang yang berbuat kebaikan, maka sesungguhnya dia telah berpegang kepada buhul (tali) yang kokoh. Hanya kepada Allah kesudahan segala urusan.'),
	(3492, 31, 23, 'وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهٗۗ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اِنَّ اللّٰهَ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ', 'waman kafara fal<u>aa</u> ya<u>h</u>zunka kufruhu ilayn<u>aa</u> marji\'uhum fanunabbi-uhum bim<u>aa</u> \'amiluu inna <strong>al</strong>l<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Dan barangsiapa kafir maka kekafirannya itu janganlah menyedihkanmu (Muhammad). Hanya kepada Kami tempat kembali mereka, lalu Kami beritakan kepada mereka apa yang telah mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala isi hati.'),
	(3493, 31, 24, 'نُمَتِّعُهُمْ قَلِيْلًا ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَلِيْظٍ', 'numatti\'uhum qaliilan tsumma na<u>dth</u>arruhum il<u>aa</u> \'a<u>dzaa</u>bin ghalii<u>zh</u><strong>in</strong>', 'Kami biarkan mereka bersenang-senang sebentar, kemudian Kami paksa mereka (masuk) ke dalam azab yang keras.'),
	(3494, 31, 25, 'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ', 'wala-in sa-altahum man khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a layaquulunna <strong>al</strong>l<u>aa</u>hu quli <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan sungguh, jika engkau (Muhammad) tanyakan kepada mereka, ”Siapakah yang menciptakan langit dan bumi?” Tentu mereka akan menjawab, ”Allah.” Katakanlah, ”Segala puji bagi Allah,” tetapi kebanyakan mereka tidak mengetahui.'),
	(3495, 31, 26, 'لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ', 'lill<u>aa</u>hi m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i inna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>lghaniyyu <strong>a</strong>l<u>h</u>amiid<strong>u</strong>', 'Milik Allah-lah apa yang di langit dan di bumi. Sesungguhnya Allah, Dialah Yang Mahakaya, Maha Terpuji.'),
	(3496, 31, 27, 'وَلَوْ اَنَّ مَا فِى الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَّالْبَحْرُ يَمُدُّهٗ مِنْۢ بَعْدِهٖ سَبْعَةُ اَبْحُرٍ مَّا نَفِدَتْ كَلِمٰتُ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ', 'walaw annam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i min syajaratin aql<u>aa</u>mun wa<strong>a</strong>lba<u>h</u>ru yamudduhu min ba\'dihi sab\'atu ab<u>h</u>urin m<u>aa</u> nafidat kalim<u>aa</u>tu <strong>al</strong>l<u>aa</u>hi inna <strong>al</strong>l<u>aa</u>ha \'aziizun <u>h</u>akiim<strong>un</strong>', 'Dan seandainya pohon-pohon di bumi menjadi pena dan lautan (menjadi tinta), ditambahkan kepadanya tujuh lautan (lagi) setelah (kering)nya, niscaya tidak akan habis-habisnya (dituliskan) kalimat-kalimat Allah. Sesungguhnya Allah Mahaperkasa, Mahabijaksana.'),
	(3497, 31, 28, 'مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَّاحِدَةٍ ۗاِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ', 'm<u>aa</u> khalqukum wal<u>aa</u> ba\'tsukum ill<u>aa</u> kanafsin w<u>aah</u>idatin inna <strong>al</strong>l<u>aa</u>ha samii\'un ba<u>sh</u>iir<strong>un</strong>', 'Menciptakan dan membangkitkan kamu (bagi Allah) hanyalah seperti (menciptakan dan membangkitkan) satu jiwa saja (mudah). Sesungguhnya Allah Maha Mendengar, Maha Melihat.'),
	(3498, 31, 29, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۖ  كُلٌّ يَّجْرِيْٓ اِلٰٓى اَجَلٍ مُّسَمًّى وَّاَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ خَبِيْرٌ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha yuuliju <strong>al</strong>layla fii <strong>al</strong>nnah<u>aa</u>ri wayuuliju <strong>al</strong>nnah<u>aa</u>ra fii <strong>al</strong>layli wasakhkhara <strong>al</strong>sysyamsa wa<strong>a</strong>lqamara kullun yajrii il<u>aa</u> ajalin musamman wa-anna <strong>al</strong>l<u>aa</u>ha bim<u>aa</u> ta\'maluuna khabiir<strong>un</strong>', 'Tidakkah engkau memperhatikan, bahwa Allah memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan Dia menundukkan matahari dan bulan, masing-masing beredar sampai kepada waktu yang ditentukan. Sungguh, Allah Mahateliti apa yang kamu kerjakan.'),
	(3499, 31, 30, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ ࣖ', '<u>dzaa</u>lika bi-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l<u>h</u>aqqu wa-anna m<u>aa</u> yad\'uuna min duunihi <strong>a</strong>lb<u>aath</u>ilu wa-anna <strong>al</strong>l<u>aa</u>ha huwa <strong>a</strong>l\'aliyyu <strong>a</strong>lkabiir<strong>u</strong>', 'Demikianlah, karena sesungguhnya Allah, Dialah (Tuhan) yang sebenarnya dan apa saja yang mereka seru selain Allah adalah batil. Dan sesungguhnya Allah, Dialah Yang Mahatinggi, Mahabesar.'),
	(3500, 31, 31, 'اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِّنْ اٰيٰتِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ', 'alam tara anna <strong>a</strong>lfulka tajrii fii <strong>a</strong>lba<u>h</u>ri bini\'mati <strong>al</strong>l<u>aa</u>hi liyuriyakum min <u>aa</u>y<u>aa</u>tihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur<strong>in</strong>', 'Tidakkah engkau memperhatikan bahwa sesungguhnya kapal itu berlayar di laut dengan nikmat Allah, agar diperlihatkan-Nya kepadamu sebagian dari tanda-tanda (kebesaran)-Nya. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran)-Nya bagi setiap orang yang sangat sabar dan banyak bersyukur.'),
	(3501, 31, 32, 'وَاِذَا غَشِيَهُمْ مَّوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُّقْتَصِدٌۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا كُلُّ خَتَّارٍ كَفُوْرٍ', 'wa-i<u>dzaa</u> ghasyiyahum mawjun ka<strong>al</strong><u>zhzh</u>ulali da\'awuu <strong>al</strong>l<u>aa</u>ha mukhli<u>sh</u>iina lahu <strong>al</strong>ddiina falamm<u>aa</u> najj<u>aa</u>hum il<u>aa</u> <strong>a</strong>lbarri faminhum muqta<u>sh</u>idun wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> kullu khatt<u>aa</u>rin kafuur<strong>in</strong>', 'Dan apabila mereka digulung ombak yang besar seperti gunung, mereka menyeru Allah dengan tulus ikhlas beragama kepada-Nya. Tetapi ketika Allah menyelamatkan mereka sampai di daratan, lalu sebagian mereka tetap menempuh jalan yang lurus. Adapun yang mengingkari ayat-ayat Kami hanyalah pengkhianat yang tidak berterima kasih.'),
	(3502, 31, 33, 'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَّا يَجْزِيْ وَالِدٌ عَنْ وَّلَدِهٖۖ وَلَا مَوْلُوْدٌ هُوَ جَازٍ عَنْ وَّالِدِهٖ شَيْـًٔاۗ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ ا', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su ittaquu rabbakum wa<strong>i</strong>khsyaw yawman l<u>aa</u> yajzii w<u>aa</u>lidun \'an waladihi wal<u>aa</u> mawluudun huwa j<u>aa</u>zin \'an w<u>aa</u>lidihi syay-an inna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun fal<u>aa</u> taghurrannakumu <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> wal<u>aa</u> yaghurrannakum bi<strong>al</strong>l<u>aa</u>hi <strong>a</strong>lgharuur<strong>u</strong>', 'Wahai manusia! Bertakwalah kepada Tuhanmu dan takutlah pada hari yang (ketika itu) seorang bapak tidak dapat menolong anaknya, dan seorang anak tidak dapat (pula) menolong bapaknya sedikit pun. Sungguh, janji Allah pasti benar, maka janganlah sekali-kali kamu teperdaya oleh kehidupan dunia, dan jangan sampai kamu teperdaya oleh penipu dalam (menaati) Allah.'),
	(3503, 31, 34, 'اِنَّ اللّٰهَ عِنْدَهٗ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِى الْاَرْحَامِۗ وَمَا تَدْرِيْ نَفْسٌ مَّاذَا تَكْسِبُ غَدًاۗ وَمَا تَدْرِيْ نَفْسٌۢ بِاَيِّ اَرْضٍ تَمُوْتُۗ اِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ ࣖ', 'inna <strong>al</strong>l<u>aa</u>ha \'indahu \'ilmu <strong>al</strong>ss<u>aa</u>\'ati wayunazzilu <strong>a</strong>lghaytsa waya\'lamu m<u>aa</u> fii <strong>a</strong>l-ar<u>haa</u>mi wam<u>aa</u> tadrii nafsun m<u>aatsaa</u> taksibu ghadan wam<u>aa</u> tadrii nafsun bi-ayyi ar<u>dh</u>in tamuutu inna <strong>al</strong>l<u>aa</u>ha \'aliimun khabiir<strong>un</strong>', 'Sesungguhnya hanya di sisi Allah ilmu tentang hari Kiamat; dan Dia yang menurunkan hujan, dan mengetahui apa yang ada dalam rahim. Dan tidak ada seorang pun yang dapat mengetahui (dengan pasti) apa yang akan dikerjakannya besok. Dan tidak ada seorang pun yang dapat mengetahui di bumi mana dia akan mati. Sungguh, Allah Maha Mengetahui, Maha Mengenal.'),
	(3504, 32, 1, ' الۤمّۤ ۗ ', 'alif-l<u>aa</u>m-miim', 'Alif Lam Mim.'),
	(3505, 32, 2, 'تَنْزِيْلُ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ ', 'tanziilu <strong>a</strong>lkit<u>aa</u>bi l<u>aa</u> rayba fiihi min rabbi <strong>a</strong>l\'<u>aa</u>lamiin<strong>a</strong>', 'Turunnya Al-Qur\'an itu tidak ada keraguan padanya, (yaitu) dari Tuhan seluruh alam. '),
	(3506, 32, 3, 'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۚ بَلْ هُوَ الْحَقُّ مِنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُوْنَ', 'am yaquuluuna iftar<u>aa</u>hu bal huwa <strong>a</strong>l<u>h</u>aqqu min rabbika litun<u>dz</u>ira qawman m<u>aa</u> at<u>aa</u>hum min na<u>dz</u>iirin min qablika la\'allahum yahtaduun<strong>a</strong>', 'Tetapi mengapa mereka (orang kafir) mengatakan, “Dia (Muhammad) telah mengada-adakannya.” Tidak, Al-Qur\'an itu kebenaran (yang datang) dari Tuhanmu, agar engkau memberi peringatan kepada kaum yang belum pernah didatangi orang yang memberi peringatan sebelum engkau; agar mereka mendapat petunjuk.'),
	(3507, 32, 4, 'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ مَا لَكُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّ وَّلَا شَفِيْعٍۗ  اَفَلَا تَتَذَكَّرُوْنَ', '<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii khalaqa <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> <strong>a</strong>l\'arsyi m<u>aa</u> lakum min duunihi min waliyyin wal<u>aa</u> syafii\'in afal<u>aa</u> tata<u>dz</u>akkaruun<strong>a</strong>', 'Allah yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy. Bagimu tidak ada seorang pun penolong maupun pemberi syafaat selain Dia. Maka apakah kamu tidak memperhatikan?'),
	(3508, 32, 5, 'يُدَبِّرُ الْاَمْرَ مِنَ السَّمَاۤءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗٓ اَلْفَ سَنَةٍ مِّمَّا تَعُدُّوْنَ', 'yudabbiru <strong>a</strong>l-amra mina <strong>al</strong>ssam<u>aa</u>-i il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i tsumma ya\'ruju ilayhi fii yawmin k<u>aa</u>na miqd<u>aa</u>ruhu <strong>a</strong>lfa sanatin mimm<u>aa</u> ta\'udduun<strong>a</strong>', 'Dia mengatur segala urusan dari langit ke bumi, kemudian (urusan) itu naik kepada-Nya dalam satu hari yang kadarnya (lamanya) adalah seribu tahun menurut perhitunganmu.'),
	(3509, 32, 6, 'ذٰلِكَ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الرَّحِيْمُۙ ', '<u>dzaa</u>lika \'<u>aa</u>limu <strong>a</strong>lghaybi wa<strong>al</strong>sysyah<u>aa</u>dati <strong>a</strong>l\'aziizu <strong>al</strong>rra<u>h</u>iim<strong>u</strong>', 'Yang demikian itu, ialah Tuhan yang mengetahui yang gaib dan yang nyata, Yang Mahaperkasa, Maha Penyayang.'),
	(3510, 32, 7, 'الَّذِيْٓ اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهٗ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ طِيْنٍ', '<strong>al</strong>la<u>dz</u>ii a<u>h</u>sana kulla syay-in khalaqahu wabada-a khalqa <strong>a</strong>l-ins<u>aa</u>ni min <u>th</u>iin<strong>in</strong>', 'Yang memperindah segala sesuatu yang Dia ciptakan dan yang memulai penciptaan manusia dari tanah,'),
	(3511, 32, 8, 'ثُمَّ جَعَلَ نَسْلَهٗ مِنْ سُلٰلَةٍ مِّنْ مَّاۤءٍ مَّهِيْنٍ ۚ ', 'tsumma ja\'ala naslahu min sul<u>aa</u>latin min m<u>aa</u>-in mahiin<strong>in</strong>', 'kemudian Dia menjadikan keturunannya dari sari pati air yang hina (air mani).'),
	(3512, 32, 9, 'ثُمَّ سَوّٰىهُ وَنَفَخَ فِيْهِ مِنْ رُّوْحِهٖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ', 'tsumma saww<u>aa</u>hu wanafakha fiihi min ruu<u>h</u>ihi waja\'ala lakumu <strong>al</strong>ssam\'a wa<strong>a</strong>l-ab<u>shaa</u>ra wa<strong>a</strong>l-af-idata qaliilan m<u>aa</u> tasykuruun<strong>a</strong>', 'Kemudian Dia menyempurnakannya dan meniupkan roh (ciptaan)-Nya ke dalam (tubuh)nya dan Dia menjadikan pendengaran, penglihatan dan hati bagimu, (tetapi) sedikit sekali kamu bersyukur.'),
	(3513, 32, 10, 'وَقَالُوْٓا ءَاِذَا ضَلَلْنَا فِى الْاَرْضِ ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ بَلْ هُمْ بِلِقَاۤءِ رَبِّهِمْ كٰفِرُوْنَ ', 'waq<u>aa</u>luu a-i<u>dzaa</u> <u>dh</u>al<u>aa</u>ln<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i a-inn<u>aa</u> lafii khalqin jadiidin bal hum biliq<u>aa</u>-i rabbihim k<u>aa</u>firuun<strong>a</strong>', 'Dan mereka berkata, “Apakah apabila kami telah lenyap (hancur) di dalam tanah, kami akan berada dalam ciptaan yang baru?” Bahkan mereka mengingkari pertemuan dengan Tuhannya.'),
	(3514, 32, 11, '۞ قُلْ يَتَوَفّٰىكُمْ مَّلَكُ الْمَوْتِ الَّذِيْ وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ ࣖ ', 'qul yatawaff<u>aa</u>kum malaku <strong>a</strong>lmawti <strong>al</strong>la<u>dz</u>ii wukkila bikum tsumma il<u>aa</u> rabbikum turja\'uun<strong>a</strong>', 'Katakanlah, “Malaikat maut yang diserahi untuk (mencabut nyawa)mu akan mematikan kamu, kemudian kepada Tuhanmu, kamu akan dikembalikan.”'),
	(3515, 32, 12, 'وَلَوْ تَرٰىٓ اِذِ الْمُجْرِمُوْنَ نَاكِسُوْا رُءُوْسِهِمْ عِنْدَ رَبِّهِمْۗ رَبَّنَآ اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا اِنَّا مُوْقِنُوْنَ ', 'walaw tar<u>aa</u> i<u>dz</u>i <strong>a</strong>lmujrimuuna n<u>aa</u>kisuu ruuusihim \'inda rabbihim rabban<u>aa</u> ab<u>sh</u>arn<u>aa</u> wasami\'n<u>aa</u> fa-arji\'n<u>aa</u> na\'mal <u>shaa</u>li<u>h</u>an inn<u>aa</u> muuqinuun<strong>a</strong>', 'Dan (alangkah ngerinya), jika sekiranya kamu melihat orang-orang yang berdosa itu menundukkan kepalanya di hadapan Tuhannya, (mereka berkata), “Ya Tuhan kami, kami telah melihat dan mendengar, maka kembalikanlah kami (ke dunia), niscaya kami akan mengerjakan kebajikan. Sungguh, kami adalah orang-orang yang yakin.”'),
	(3516, 32, 13, 'وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰىهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنِّيْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ ', 'walaw syi-n<u>aa</u> la<u>aa</u>tayn<u>aa</u> kulla nafsin hud<u>aa</u>h<u>aa</u> wal<u>aa</u>kin <u>h</u>aqqa <strong>a</strong>lqawlu minnii la-amla-anna jahannama mina <strong>a</strong>ljinnati wa<strong>al</strong>nn<u>aa</u>si ajma\'iin<strong>a</strong>', 'Dan jika Kami menghendaki niscaya Kami berikan kepada setiap jiwa petunjuk (bagi)nya, tetapi telah ditetapkan perkataan (ketetapan) dari-Ku, “Pasti akan Aku penuhi neraka Jahanam dengan jin dan manusia bersama-sama.'),
	(3517, 32, 14, 'فَذُوْقُوْا بِمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَسِيْنٰكُمْ وَذُوْقُوْا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُوْنَ', 'fa<u>dz</u>uuquu bim<u>aa</u> nasiitum liq<u>aa</u>-a yawmikum h<u>aadzaa</u> inn<u>aa</u> nasiin<u>aa</u>kum wa<u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>a</strong>lkhuldi bim<u>aa</u> kuntum ta\'maluun<strong>a</strong>', 'Maka rasakanlah olehmu (azab ini) disebabkan kamu melalaikan pertemuan dengan harimu ini (hari Kiamat), sesungguhnya Kami pun melalaikan kamu dan rasakanlah azab yang kekal, atas apa yang telah kamu kerjakan.”'),
	(3518, 32, 15, 'اِنَّمَا يُؤْمِنُ بِاٰيٰتِنَا الَّذِيْنَ اِذَا ذُكِّرُوْا بِهَا خَرُّوْا سُجَّدًا وَّسَبَّحُوْا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُوْنَ ۩ ', 'innam<u>aa</u> yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> <strong>al</strong>la<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukkiruu bih<u>aa</u> kharruu sujjadan wasabba<u>h</u>uu bi<u>h</u>amdi rabbihim wahum l<u>aa</u> yastakbiruun<strong>a</strong>', 'Orang-orang yang beriman dengan ayat-ayat Kami, hanyalah orang-orang yang apabila diperingatkan dengannya (ayat-ayat Kami), mereka menyungkur sujud dan bertasbih serta memuji Tuhannya, dan mereka tidak menyombongkan diri.'),
	(3519, 32, 16, 'تَتَجَافٰى جُنُوْبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُوْنَ رَبَّهُمْ خَوْفًا وَّطَمَعًاۖ وَّمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ', 'tataj<u>aa</u>f<u>aa</u> junuubuhum \'ani <strong>a</strong>lma<u>daa</u>ji\'i yad\'uuna rabbahum khawfan wa<u>th</u>ama\'an wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun<strong>a</strong>', 'Lambung mereka jauh dari tempat tidurnya, mereka berdoa kepada Tuhannya dengan rasa takut dan penuh harap, dan mereka menginfakkan sebagian dari rezeki yang Kami berikan kepada mereka.'),
	(3520, 32, 17, 'فَلَا تَعْلَمُ نَفْسٌ مَّآ اُخْفِيَ لَهُمْ مِّنْ قُرَّةِ اَعْيُنٍۚ جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ', 'fal<u>aa</u> ta\'lamu nafsun m<u>aa</u> ukhfiya lahum min qurrati a\'yunin jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Maka tidak seorang pun mengetahui apa yang disembunyikan untuk mereka yaitu (bermacam-macam nikmat) yang menyenangkan hati sebagai balasan terhadap apa yang mereka kerjakan.'),
	(3521, 32, 18, 'اَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًاۗ  لَا يَسْتَوٗنَ', 'afaman k<u>aa</u>na mu/minan kaman k<u>aa</u>na f<u>aa</u>siqan l<u>aa</u> yastawuun<strong>a</strong>', 'Maka apakah orang yang beriman seperti orang yang fasik (kafir)?  Mereka tidak sama.'),
	(3522, 32, 19, 'اَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ جَنّٰتُ الْمَأْوٰىۖ  نُزُلًا ۢبِمَا كَانُوْا يَعْمَلُوْنَ', 'amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti falahum jann<u>aa</u>tu <strong>a</strong>lma/w<u>aa</u> nuzulan bim<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Adapun orang-orang yang beriman dan mengerjakan kebajikan, maka mereka akan mendapat surga-surga tempat kediaman, sebagai pahala atas apa yang telah mereka kerjakan.'),
	(3523, 32, 20, 'وَاَمَّا الَّذِيْنَ فَسَقُوْا فَمَأْوٰىهُمُ النَّارُ كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَآ اُعِيْدُوْا فِيْهَا وَقِيْلَ لَهُمْ ذُوْقُوْا عَذَابَ النَّارِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ', 'wa-amm<u>aa</u> <strong>al</strong>la<u>dz</u>iina fasaquu fama/w<u>aa</u>humu <strong>al</strong>nn<u>aa</u>ru kullam<u>aa</u> ar<u>aa</u>duu an yakhrujuu minh<u>aa</u> u\'iiduu fiih<u>aa</u> waqiila lahum <u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>ri <strong>al</strong>la<u>dz</u>ii kuntum bihi tuka<u>dzdz</u>ibuun<strong>a</strong>', 'Dan adapun orang-orang yang fasik (kafir), maka tempat kediaman mereka adalah neraka. Setiap kali mereka hendak keluar darinya, mereka dikembalikan (lagi) ke dalamnya dan dikatakan kepada mereka, “Rasakanlah azab neraka yang dahulu kamu dustakan.”'),
	(3524, 32, 21, 'وَلَنُذِيْقَنَّهُمْ مِّنَ الْعَذَابِ الْاَدْنٰى دُوْنَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُوْنَ', 'walanu<u>dz</u>iiqannahum mina <strong>a</strong>l\'a<u>dzaa</u>bi <strong>a</strong>l-adn<u>aa</u> duuna <strong>a</strong>l\'a<u>dzaa</u>bi <strong>a</strong>l-akbari la\'allahum yarji\'uun<strong>a</strong>', 'Dan pasti Kami timpakan kepada mereka sebagian siksa yang dekat (di dunia) sebelum azab yang lebih besar (di akhirat); agar mereka kembali (ke jalan yang benar).'),
	(3525, 32, 22, 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ ثُمَّ اَعْرَضَ عَنْهَا ۗاِنَّا مِنَ الْمُجْرِمِيْنَ مُنْتَقِمُوْنَ ࣖ', 'waman a<u>zh</u>lamu mimman <u>dz</u>ukkira bi-<u>aa</u>y<u>aa</u>ti rabbihi tsumma a\'ra<u>dh</u>a \'anh<u>aa</u> inn<u>aa</u> mina <strong>a</strong>lmujrimiina muntaqimuun<strong>a</strong>', 'Dan siapakah yang lebih zalim daripada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, kemudian dia berpaling darinya? Sungguh, Kami akan memberikan balasan kepada orang-orang yang berdosa.'),
	(3526, 32, 23, 'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَلَا تَكُنْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤىِٕهٖ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَۚ', 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba fal<u>aa</u> takun fii miryatin min liq<u>aa</u>-ihi waja\'aln<u>aa</u>hu hudan libanii isr<u>aa</u>-iil<strong>a</strong>', 'Dan sungguh, telah Kami anugerahkan Kitab (Taurat) kepada Musa, maka janganlah engkau (Muhammad) ragu-ragu menerimanya (Al-Qur\'an) dan Kami jadikan Kitab (Taurat) itu petunjuk bagi Bani Israil.'),
	(3527, 32, 24, 'وَجَعَلْنَا مِنْهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا لَمَّا صَبَرُوْاۗ وَكَانُوْا بِاٰيٰتِنَا يُوْقِنُوْنَ', 'waja\'aln<u>aa</u> minhum a-immatan yahduuna bi-amrin<u>aa</u> lamm<u>aa</u> <u>sh</u>abaruu wak<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yuuqinuun<strong>a</strong>', 'Dan Kami jadikan di antara mereka itu pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami selama mereka sabar. Mereka meyakini ayat-ayat Kami.'),
	(3528, 32, 25, 'اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ', 'inna rabbaka huwa yaf<u>sh</u>ilu baynahum yawma <strong>a</strong>lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun<strong>a</strong>', 'Sungguh Tuhanmu, Dia yang memberikan keputusan di antara mereka pada hari Kiamat tentang apa yang dahulu mereka perselisihkan padanya.'),
	(3529, 32, 26, 'اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍۗ اَفَلَا يَسْمَعُوْنَ', 'awa lam yahdi lahum kam ahlakn<u>aa</u> min qablihim mina <strong>a</strong>lquruuni yamsyuuna fii mas<u>aa</u>kinihim inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin afal<u>aa</u> yasma\'uun<strong>a</strong>', 'Dan tidakkah menjadi petunjuk bagi mereka, betapa banyak umat-umat sebelum mereka yang telah Kami binasakan, sedangkan mereka sendiri berjalan di tempat-tempat kediaman mereka itu. Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah). Apakah mereka tidak mendengarkan (memperhatikan)?'),
	(3530, 32, 27, 'اَوَلَمْ يَرَوْا اَنَّا نَسُوْقُ الْمَاۤءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِهٖ زَرْعًا تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۗ اَفَلَا يُبْصِرُوْنَ', 'awa lam yaraw ann<u>aa</u> nasuuqu <strong>a</strong>lm<u>aa</u>-a il<u>aa</u> <strong>a</strong>l-ar<u>dh</u>i <strong>a</strong>ljuruzi fanukhriju bihi zar\'an ta-kulu minhu an\'<u>aa</u>muhum wa-anfusuhum afal<u>aa</u> yub<u>sh</u>iruun<strong>a</strong>', 'Dan tidakkah mereka memperhatikan, bahwa Kami mengarahkan (awan yang mengandung) air ke bumi yang tandus, lalu Kami tumbuhkan (dengan air hujan itu) tanam-tanaman sehingga hewan-hewan ternak mereka dan mereka sendiri dapat makan darinya. Maka mengapa mereka tidak memperhatikan?'),
	(3531, 32, 28, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صٰدِقِيْنَ', 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> <strong>a</strong>lfat<u>h</u>u in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan mereka bertanya, “Kapankah kemenangan itu (datang) jika engkau orang yang benar?”'),
	(3532, 32, 29, 'قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذِيْنَ كَفَرُوْٓا اِيْمَانُهُمْ وَلَا هُمْ يُنْظَرُوْنَ', 'qul yawma <strong>a</strong>lfat<u>h</u>i l<u>aa</u> yanfa\'u <strong>al</strong>la<u>dz</u>iina kafaruu iim<u>aa</u>nuhum wal<u>aa</u> hum yun<u>zh</u>aruun<strong>a</strong>', 'Katakanlah, “Pada hari kemenangan itu, tidak berguna lagi bagi orang-orang kafir keimanan mereka dan mereka tidak diberi penangguhan.”'),
	(3533, 32, 30, 'فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُّنْتَظِرُوْنَ ࣖ', 'fa-a\'ri<u>dh</u> \'anhum wa<strong>i</strong>nta<u>zh</u>ir innahum munta<u>zh</u>iruun<strong>a</strong>', 'Maka berpalinglah engkau dari mereka dan tunggulah, sesungguhnya mereka (juga) menunggu.'),
	(3534, 33, 1, 'يٰٓاَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۙ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu ittaqi <strong>al</strong>l<u>aa</u>ha wal<u>aa</u> tu<u>th</u>i\'i <strong>a</strong>lk<u>aa</u>firiina wa<strong>a</strong>lmun<u>aa</u>fiqiina inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u><strong>n</strong>', 'Wahai Nabi! Bertakwalah kepada Allah dan janganlah engkau menuruti (keinginan) orang-orang kafir dan orang-orang munafik. Sesungguhnya Allah Maha Mengetahui, Mahabijaksana,'),
	(3535, 33, 2, 'وَّاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ مِنْ رَّبِّكَ ۗاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًاۙ ', 'wa<strong>i</strong>ttabi\' m<u>aa</u> yuu<u>haa</u> ilayka min rabbika inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u><strong>n</strong>', 'dan ikutilah apa yang diwahyukan Tuhanmu kepadamu. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan,'),
	(3536, 33, 3, 'وَّتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا', 'watawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wakiil<u>aa</u><strong>n </strong>', 'dan bertawakallah kepada Allah. Dan cukuplah Allah sebagai pemelihara.'),
	(3537, 33, 4, 'مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِّنْ قَلْبَيْنِ فِيْ جَوْفِهٖ ۚوَمَا جَعَلَ اَزْوَاجَكُمُ الّٰـِٕۤيْ تُظٰهِرُوْنَ مِنْهُنَّ اُمَّهٰتِكُمْ ۚوَمَا جَعَلَ اَدْعِيَاۤءَكُمْ اَبْنَاۤءَكُمْۗ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْ ۗوَاللّٰهُ يَقُوْلُ الْحَقَّ وَهُوَ ي', 'm<u>aa</u> ja\'ala <strong>al</strong>l<u>aa</u>hu lirajulin min qalbayni fii jawfihi wam<u>aa</u> ja\'ala azw<u>aa</u>jakumu <strong>al</strong>l<u>aa</u>-ii tu<u>zhaa</u>hiruuna minhunna ummah<u>aa</u>tikum wam<u>aa</u> ja\'ala ad\'iy<u>aa</u>-akum abn<u>aa</u>-akum <u>dzaa</u>likum qawlukum bi-afw<u>aa</u>hikum wa<strong>al</strong>l<u>aa</u>hu yaquulu <strong>a</strong>l<u>h</u>aqqa wahuwa yahdii <strong>al</strong>ssabiil<strong>a</strong>', 'Allah tidak menjadikan bagi seseorang dua hati dalam rongganya; dan Dia tidak menjadikan istri-istrimu yang kamu zihar itu sebagai ibumu, dan Dia tidak menjadikan anak angkatmu sebagai anak kandungmu (sendiri). Yang demikian itu hanyalah perkataan di mulutmu saja. Allah mengatakan yang sebenarnya dan Dia menunjukkan jalan (yang benar).'),
	(3538, 33, 5, 'اُدْعُوْهُمْ لِاٰبَاۤىِٕهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِ ۚ فَاِنْ لَّمْ تَعْلَمُوْٓا اٰبَاۤءَهُمْ فَاِخْوَانُكُمْ فِى الدِّيْنِ وَمَوَالِيْكُمْ ۗوَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيْمَآ اَخْطَأْتُمْ بِهٖ وَلٰكِنْ مَّا تَعَمَّدَتْ قُلُوْبُكُمْ ۗوَكَانَ اللّٰه', 'ud\'uuhum li-<u>aa</u>b<u>aa</u>-ihim huwa aqsa<u>th</u>u \'inda <strong>al</strong>l<u>aa</u>hi fa-in lam ta\'lamuu <u>aa</u>b<u>aa</u>-ahum fa-ikhw<u>aa</u>nukum fii <strong>al</strong>ddiini wamaw<u>aa</u>liikum walaysa \'alaykum jun<u>aah</u>un fiim<u>aa</u> akh<u>th</u>a/tum bihi wal<u>aa</u>kin m<u>aa</u> ta\'ammadat quluubukum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Panggillah mereka (anak angkat itu) dengan (memakai) nama bapak-bapak mereka; itulah yang adil di sisi Allah, dan jika kamu tidak mengetahui bapak mereka, maka (panggillah mereka sebagai) saudara-saudaramu seagama dan maula-maulamu. Dan tidak ada dosa atasmu jika kamu khilaf tentang itu, tetapi (yang ada dosanya) apa yang disengaja oleh hatimu. Allah Maha Pengampun, Maha Penyayang.'),
	(3539, 33, 6, 'اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِنِيْنَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهٗٓ اُمَّهٰتُهُمْ ۗوَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ مِنَ الْمُؤْمِنِيْنَ وَالْمُهٰجِرِيْنَ اِلَّآ اَنْ تَفْعَلُوْٓا اِلٰٓى اَوْلِيَاۤىِٕكُمْ مَّعْرُو', 'a<strong>l</strong>nnabiyyu awl<u>aa</u> bi<strong>a</strong>lmu/miniina min anfusihim wa-azw<u>aa</u>juhu ummah<u>aa</u>tuhum wauluu <strong>a</strong>l-ar<u>haa</u>mi ba\'<u>dh</u>uhum awl<u>aa</u> biba\'<u>dh</u>in fii kit<u>aa</u>bi <strong>al</strong>l<u>aa</u>hi mina <strong>a</strong>lmu/miniina wa<strong>a</strong>lmuh<u>aa</u>jiriina ill<u>aa</u> an taf\'aluu il<u>aa</u> awliy<u>aa</u>-ikum ma\'ruufan k<u>aa</u>na <u>dzaa</u>lika fii <strong>a</strong>lkit<u>aa</u>bi mas<u>th</u>uur<u>aa</u><strong>n</strong>', 'Nabi itu lebih utama bagi orang-orang mukmin dibandingkan diri mereka sendiri dan istri-istrinya adalah ibu-ibu mereka. Orang-orang yang mempunyai hubungan darah satu sama lain lebih berhak (waris-mewarisi) di dalam Kitab Allah daripada orang-orang mukmin dan orang-orang Muhajirin, kecuali kalau kamu hendak berbuat baikkepada saudara-saudaramu  (seagama). Demikianlah telah tertulis dalam Kitab (Allah).'),
	(3540, 33, 7, 'وَاِذْ اَخَذْنَا مِنَ النَّبِيّٖنَ مِيْثَاقَهُمْ وَمِنْكَ وَمِنْ نُّوْحٍ وَّاِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسَى ابْنِ مَرْيَمَ ۖوَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًاۙ', 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> mina <strong>al</strong>nnabiyyiina miits<u>aa</u>qahum waminka wamin nuu<u>h</u>in wa-ibr<u>aa</u>hiima wamuus<u>aa</u> wa\'iis<u>aa</u> ibni maryama wa-akha<u>dz</u>n<u>aa</u> minhum miits<u>aa</u>qan ghalii<u>zhaa</u><strong>n</strong>', 'Dan (ingatlah) ketika Kami mengambil perjanjian dari para nabi dan dari engkau (sendiri), dari Nuh, Ibrahim, Musa dan Isa putra Maryam, dan Kami telah mengambil dari mereka perjanjian yang teguh,'),
	(3541, 33, 8, 'لِّيَسْـَٔلَ الصّٰدِقِيْنَ عَنْ صِدْقِهِمْ ۚوَاَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا اَلِيْمًا ࣖ', 'liyas-ala <strong>al</strong><u>shshaa</u>diqiina \'an <u>sh</u>idqihim wa-a\'adda lilk<u>aa</u>firiina \'a<u>dzaa</u>ban <strong>a</strong>liim<u>aa</u><strong>n</strong>', 'agar Dia menanyakan kepada orang-orang yang benar tentang kebenaran mereka. Dia menyediakan azab yang pedih bagi orang-orang kafir.'),
	(3542, 33, 9, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَاۤءَتْكُمْ جُنُوْدٌ فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا وَّجُنُوْدًا لَّمْ تَرَوْهَا ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًاۚ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu u<u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum i<u>dz</u> j<u>aa</u>-atkum junuudun fa-arsaln<u>aa</u> \'alayhim rii<u>h</u>an wajunuudan lam tarawh<u>aa</u> wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Ingatlah akan nikmat Allah (yang telah dikaruniakan) kepadamu ketika bala tentara datang kepadamu, lalu Kami kirimkan kepada mereka angin topan dan bala tentara yang tidak dapat terlihat olehmu. Allah Maha Melihat apa yang kamu kerjakan.'),
	(3543, 33, 10, 'اِذْ جَاۤءُوْكُمْ مِّنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوْبُ الْحَنَاجِرَ وَتَظُنُّوْنَ بِاللّٰهِ الظُّنُوْنَا۠   ۗ', 'i<u>dz</u> j<u>aa</u>uukum min fawqikum wamin asfala minkum wa-i<u>dz</u> z<u>aa</u>ghati <strong>a</strong>l-ab<u>shaa</u>ru wabalaghati <strong>a</strong>lquluubu <strong>a</strong>l<u>h</u>an<u>aa</u>jira wata<u>zh</u>unnuuna bi<strong>al</strong>l<u>aa</u>hi <strong>al</strong><u>zhzh</u>unuun<u>aa</u>', '(Yaitu) ketika mereka datang kepadamu dari atas dan dari bawahmu, dan ketika penglihatan(mu) terpana dan hatimu menyesak sampai ke tenggorokan dan kamu berprasangka yang bukan-bukan terhadap Allah.'),
	(3544, 33, 11, 'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُوْنَ وَزُلْزِلُوْا زِلْزَالًا شَدِيْدًا', 'hun<u>aa</u>lika ibtuliya <strong>a</strong>lmu-minuuna wazulziluu zilz<u>aa</u>lan syadiid<u>aa</u><strong>n</strong>', 'Di situlah diuji orang-orang mukmin dan digoncangkan (hatinya) dengan goncangan yang dahsyat.'),
	(3545, 33, 12, 'وَاِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ مَّا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗٓ اِلَّا غُرُوْرًا', 'wa-i<u>dz</u> yaquulu <strong>a</strong>lmun<u>aa</u>fiquuna wa<strong>a</strong>lla<u>dz</u>iina fii quluubihim mara<u>dh</u>un m<u>aa</u> wa\'adan<u>aa</u> <strong>al</strong>l<u>aa</u>hu warasuuluhu ill<u>aa</u> ghuruur<u>aa</u><strong>n</strong>', 'Dan (ingatlah) ketika orang-orang munafik dan orang-orang yang hatinya berpenyakit berkata, “Yang dijanjikan Allah dan Rasul-Nya kepada kami hanya tipu daya belaka.”'),
	(3546, 33, 13, 'وَاِذْ قَالَتْ طَّاۤىِٕفَةٌ مِّنْهُمْ يٰٓاَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوْا ۚوَيَسْتَأْذِنُ فَرِيْقٌ مِّنْهُمُ النَّبِيَّ يَقُوْلُوْنَ اِنَّ بُيُوْتَنَا عَوْرَةٌ  ۗوَمَا هِيَ بِعَوْرَةٍ ۗاِنْ يُّرِيْدُوْنَ اِلَّا فِرَارًا', 'wa-i<u>dz</u> q<u>aa</u>lat <u>th</u><u>aa</u>-ifatun minhum y<u>aa</u> ahla yatsriba l<u>aa</u> muq<u>aa</u>ma lakum fa<strong>i</strong>rji\'uu wayasta/<u>dz</u>inu fariiqun minhumu <strong>al</strong>nnabiyya yaquuluuna inna buyuutan<u>aa</u> \'awratun wam<u>aa</u> hiya bi\'awratin in yuriiduuna ill<u>aa</u> fir<u>aa</u>r<u>aa</u><strong>n</strong>', 'Dan (ingatlah) ketika segolongan di antara mereka berkata, “Wahai penduduk Yasrib (Madinah)! Tidak ada tempat bagimu, maka kembalilah kamu.” Dan sebagian dari mereka meminta izin kepada Nabi (untuk kembali pulang) dengan berkata, “Sesungguhnya rumah-rumah kami terbuka (tidak ada penjaga).” Padahal rumah-rumah itu tidak terbuka, mereka hanyalah hendak lari.'),
	(3547, 33, 14, 'وَلَوْ دُخِلَتْ عَلَيْهِمْ مِّنْ اَقْطَارِهَا ثُمَّ سُـِٕلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوْا بِهَآ اِلَّا يَسِيْرًا', 'walaw dukhilat \'alayhim min aq<u>th</u><u>aa</u>rih<u>aa</u> tsumma su-iluu <strong>a</strong>lfitnata la<u>aa</u>tawh<u>aa</u> wam<u>aa</u> talabbatsuu bih<u>aa</u> ill<u>aa</u> yasiir<u>aa</u><strong>n</strong>', 'Dan kalau (Yasrib) diserang dari segala penjuru, dan mereka diminta agar membuat kekacauan, niscaya mereka mengerjakannya; dan hanya sebentar saja mereka menunggu.'),
	(3548, 33, 15, 'وَلَقَدْ كَانُوْا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّوْنَ الْاَدْبَارَ ۗوَكَانَ عَهْدُ اللّٰهِ مَسْـُٔوْلًا', 'walaqad k<u>aa</u>nuu \'<u>aa</u>haduu <strong>al</strong>l<u>aa</u>ha min qablu l<u>aa</u> yuwalluuna <strong>a</strong>l-adb<u>aa</u>ra wak<u>aa</u>na \'ahdu <strong>al</strong>l<u>aa</u>hi mas-uul<u>aa</u><strong>n</strong>', 'Dan sungguh, mereka sebelum itu telah berjanji kepada Allah, tidak akan berbalik ke belakang (mundur). Dan perjanjian dengan Allah akan diminta pertanggungjawabannya.'),
	(3549, 33, 16, 'قُلْ لَّنْ يَّنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِّنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذًا لَّا تُمَتَّعُوْنَ اِلَّا قَلِيْلًا ', 'qul lan yanfa\'akumu <strong>a</strong>lfir<u>aa</u>ru in farartum mina <strong>a</strong>lmawti awi <strong>a</strong>lqatli wa-i<u>dz</u>an l<u>aa</u> tumatta\'uuna ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Katakanlah (Muhammad), “Lari tidaklah berguna bagimu, jika kamu melarikan diri dari kematian atau pembunuhan, dan jika demikian (kamu terhindar dari kematian) kamu hanya akan mengecap kesenangan sebentar saja.”'),
	(3550, 33, 17, 'قُلْ مَنْ ذَا الَّذِيْ يَعْصِمُكُمْ مِّنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُوْۤءًا اَوْ اَرَادَ بِكُمْ رَحْمَةً ۗوَلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا', 'qul man <u>dzaa</u> <strong>al</strong>la<u>dz</u>ii ya\'<u>sh</u>imukum mina <strong>al</strong>l<u>aa</u>hi in ar<u>aa</u>da bikum suu-an aw ar<u>aa</u>da bikum ra<u>h</u>matan wal<u>aa</u> yajiduuna lahum min duuni <strong>al</strong>l<u>aa</u>hi waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Katakanlah, “Siapakah yang dapat melindungi kamu dari (ketentuan) Allah jika Dia menghendaki bencana atasmu atau menghendaki rahmat untuk dirimu?” Mereka itu tidak akan mendapatkan pelindung dan penolong selain Allah.'),
	(3551, 33, 18, '۞ قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّقِيْنَ مِنْكُمْ وَالْقَاۤىِٕلِيْنَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَا ۚوَلَا يَأْتُوْنَ الْبَأْسَ اِلَّا قَلِيْلًاۙ', 'qad ya\'lamu <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmu\'awwiqiina minkum wa<strong>a</strong>lq<u>aa</u>-iliina li-ikhw<u>aa</u>nihim halumma ilayn<u>aa</u> wal<u>aa</u> ya/tuuna <strong>a</strong>lba/sa ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Sungguh, Allah mengetahui orang-orang yang menghalang-halangi di antara kamu dan orang yang berkata kepada saudara-saudaranya, “Marilah bersama kami.” Tetapi mereka datang berperang hanya sebentar,'),
	(3552, 33, 19, 'اَشِحَّةً عَلَيْكُمْ ۖ فَاِذَا جَاۤءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُوْنَ اِلَيْكَ تَدُوْرُ اَعْيُنُهُمْ كَالَّذِيْ يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوْكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۗ اُولٰۤىِٕكَ لَمْ يُؤ', 'asyi<u>hh</u>atan \'alaykum fa-i<u>dzaa</u> j<u>aa</u>-a <strong>a</strong>lkhawfu ra-aytahum yan<u>zh</u>uruuna ilayka taduuru a\'yunuhum ka<strong>a</strong>lla<u>dz</u>ii yughsy<u>aa</u> \'alayhi mina <strong>a</strong>lmawti fa-i<u>dzaa</u> <u>dz</u>ahaba <strong>a</strong>lkhawfu salaquukum bi-alsinatin <u>h</u>id<u>aa</u>din asyi<u>hh</u>atan \'al<u>aa</u> <strong>a</strong>lkhayri ul<u>aa</u>-ika lam yu/minuu fa-a<u>h</u>ba<u>th</u>a <strong>al</strong>l<u>aa</u>hu a\'m<u>aa</u>lahum wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<u>aa</u><strong>n</strong>', 'mereka kikir terhadapmu. Apabila datang ketakutan (bahaya), kamu lihat mereka itu memandang kepadamu dengan mata yang terbalik-balik seperti orang yang pingsan karena akan mati, dan apabila ketakutan telah hilang, mereka mencaci kamu dengan lidah yang tajam, sedang mereka kikir untuk berbuat kebaikan. Mereka itu tidak beriman, maka Allah menghapus amalnya. Dan yang demikian itu mudah bagi Allah.'),
	(3553, 33, 20, 'يَحْسَبُوْنَ الْاَحْزَابَ لَمْ يَذْهَبُوْا ۚوَاِنْ يَّأْتِ الْاَحْزَابُ يَوَدُّوْا لَوْ اَنَّهُمْ بَادُوْنَ فِى الْاَعْرَابِ يَسْاَلُوْنَ عَنْ اَنْۢبَاۤىِٕكُمْ ۖوَلَوْ كَانُوْا فِيْكُمْ مَّا قٰتَلُوْٓا اِلَّا قَلِيْلًا ࣖ', 'ya<u>h</u>sabuuna <strong>a</strong>l-a<u>h</u>z<u>aa</u>ba lam ya<u>dz</u>habuu wa-in ya/ti <strong>a</strong>l-a<u>h</u>z<u>aa</u>bu yawadduu law annahum b<u>aa</u>duuna fii <strong>a</strong>l-a\'r<u>aa</u>bi yas-aluuna \'an anb<u>aa</u>-ikum walaw k<u>aa</u>nuu fiikum m<u>aa</u> q<u>aa</u>taluu ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Mereka mengira (bahwa) golongan-golongan (yang bersekutu) itu belum pergi, dan jika golongan-golongan (yang bersekutu) itu datang kembali, niscaya mereka ingin berada di dusun-dusun bersama-sama orang Arab Badui, sambil menanyakan berita tentang kamu. Dan sekiranya mereka berada bersamamu, mereka tidak akan berperang, melainkan sebentar saja.'),
	(3554, 33, 21, 'لَقَدْ كَانَ لَكُمْ فِيْ رَسُوْلِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَثِيْرًاۗ', 'laqad k<u>aa</u>na lakum fii rasuuli <strong>al</strong>l<u>aa</u>hi uswatun <u>h</u>asanatun liman k<u>aa</u>na yarjuu <strong>al</strong>l<u>aa</u>ha wa<strong>a</strong>lyawma <strong>a</strong>l-<u>aa</u>khira wa<u>dz</u>akara <strong>al</strong>l<u>aa</u>ha katsiir<u>aa</u><strong>n</strong>', 'Sungguh, telah ada pada (diri) Rasulullah itu suri teladan yang baik bagimu (yaitu) bagi orang yang mengharap (rahmat) Allah dan (kedatangan) hari Kiamat dan yang banyak mengingat Allah.'),
	(3555, 33, 22, 'وَلَمَّا رَاَ الْمُؤْمِنُوْنَ الْاَحْزَابَۙ قَالُوْا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗ وَصَدَقَ اللّٰهُ وَرَسُوْلُهٗ  ۖوَمَا زَادَهُمْ اِلَّآ اِيْمَانًا وَّتَسْلِيْمًاۗ ', 'walamm<u>aa</u> ra<u>aa</u> <strong>a</strong>lmu/minuuna <strong>a</strong>l-a<u>h</u>z<u>aa</u>ba q<u>aa</u>luu h<u>aadzaa</u> m<u>aa</u> wa\'adan<u>aa</u> <strong>al</strong>l<u>aa</u>hu warasuuluhu wa<u>sh</u>adaqa <strong>al</strong>l<u>aa</u>hu warasuuluhu wam<u>aa</u> z<u>aa</u>dahum ill<u>aa</u> iim<u>aa</u>nan watasliim<u>aa</u><strong>n</strong>', 'Dan ketika orang-orang mukmin melihat golongan-golongan (yang bersekutu) itu, mereka berkata, “Inilah yang dijanjikan Allah dan Rasul-Nya kepada kita.” Dan benarlah Allah dan Rasul-Nya. Dan yang demikian itu menambah keimanan dan keislaman mereka.'),
	(3556, 33, 23, 'مِنَ الْمُؤْمِنِيْنَ رِجَالٌ صَدَقُوْا مَا عَاهَدُوا اللّٰهَ عَلَيْهِ ۚ فَمِنْهُمْ مَّنْ قَضٰى نَحْبَهٗۙ وَمِنْهُمْ مَّنْ يَّنْتَظِرُ ۖوَمَا بَدَّلُوْا تَبْدِيْلًاۙ ', 'mina <strong>a</strong>lmu/miniina rij<u>aa</u>lun <u>sh</u>adaquu m<u>aa</u> \'<u>aa</u>haduu <strong>al</strong>l<u>aa</u>ha \'alayhi faminhum man qa<u>daa</u> na<u>h</u>bahu waminhum man yanta<u>zh</u>iru wam<u>aa</u> baddaluu tabdiil<u>aa</u><strong>n</strong>', 'Di antara orang-orang mukmin itu ada orang-orang yang menepati apa yang telah mereka janjikan kepada Allah. Dan di antara mereka ada yang gugur, dan di antara mereka ada (pula) yang menunggu-nunggu dan mereka sedikit pun tidak mengubah (janjinya),'),
	(3557, 33, 24, 'لِيَجْزِيَ اللّٰهُ الصّٰدِقِيْنَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنٰفِقِيْنَ اِنْ شَاۤءَ اَوْ يَتُوْبَ عَلَيْهِمْ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ', 'liyajziya <strong>al</strong>l<u>aa</u>hu <strong>al</strong><u>shshaa</u>diqiina bi<u>sh</u>idqihim wayu\'a<u>dzdz</u>iba <strong>a</strong>lmun<u>aa</u>fiqiina in sy<u>aa</u>-a aw yatuuba \'alayhim inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'agar Allah memberikan balasan kepada orang-orang yang benar itu karena kebenarannya, dan mengazab orang munafik jika Dia kehendaki, atau menerima tobat mereka. Sungguh, Allah Maha Pengampun, Maha Penyayang.'),
	(3558, 33, 25, 'وَرَدَّ اللّٰهُ الَّذِيْنَ كَفَرُوْا بِغَيْظِهِمْ لَمْ يَنَالُوْا خَيْرًا ۗوَكَفَى اللّٰهُ الْمُؤْمِنِيْنَ الْقِتَالَ ۗوَكَانَ اللّٰهُ قَوِيًّا عَزِيْزًاۚ ', 'waradda <strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>iina kafaruu bighay<u>zh</u>ihim lam yan<u>aa</u>luu khayran wakaf<u>aa</u> <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmu/miniina <strong>a</strong>lqit<u>aa</u>la wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu qawiyyan \'aziiz<u>aa</u><strong>n</strong>', 'Dan Allah menghalau orang-orang kafir itu yang keadaan mereka penuh kejengkelan, karena mereka (juga) tidak memperoleh keuntungan apa pun. Cukuplah Allah (yang menolong) menghindarkan orang-orang mukmin dalam peperangan. Dan Allah Mahakuat, Mahaperkasa.'),
	(3559, 33, 26, 'وَاَنْزَلَ الَّذِيْنَ ظَاهَرُوْهُمْ مِّنْ اَهْلِ الْكِتٰبِ مِنْ صَيَاصِيْهِمْ وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ فَرِيْقًا تَقْتُلُوْنَ وَتَأْسِرُوْنَ فَرِيْقًاۚ ', 'wa-anzala <strong>al</strong>la<u>dz</u>iina <u>zhaa</u>haruuhum min ahli <strong>a</strong>lkit<u>aa</u>bi min <u>sh</u>ay<u>aas</u>iihim waqa<u>dz</u>afa fii quluubihimu <strong>al</strong>rru\'ba fariiqan taqtuluuna wata/siruuna fariiq<u>aa</u><strong>n</strong>', 'Dan Dia menurunkan orang-orang Ahli Kitab (Bani Quraizah) yang membantu mereka (golongan-golongan yang bersekutu) dari benteng-benteng mereka, dan Dia memasukkan rasa takut ke dalam hati mereka. Sebagian mereka kamu bunuh dan sebagian yang lain kamu tawan.'),
	(3560, 33, 27, 'وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضًا لَّمْ تَطَـُٔوْهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا ࣖ ', 'wa-awratsakum ar<u>dh</u>ahum wadiy<u>aa</u>rahum wa-amw<u>aa</u>lahum wa-ar<u>dh</u>an lam ta<u>th</u>auuh<u>aa</u> wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<u>aa</u><strong>n</strong>', 'Dan Dia mewariskan kepadamu tanah-tanah, rumah-rumah dan harta benda mereka, dan (begitu pula) tanah yang belum kamu injak. Dan Allah Mahakuasa terhadap segala sesuatu.'),
	(3561, 33, 28, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحًا جَمِيْلًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu qul li-azw<u>aa</u>jika in kuntunna turidna <strong>a</strong>l<u>h</u>ay<u>aa</u>ta <strong>al</strong>dduny<u>aa</u> waziinatah<u>aa</u> fata\'<u>aa</u>layna umatti\'kunna wausarri<u>h</u>kunna sar<u>aah</u>an jamiil<u>aa</u><strong>n</strong>', 'Wahai Nabi! Katakanlah kepada istri-istrimu, “Jika kamu menginginkan kehidupan di dunia dan perhiasannya, maka kemarilah agar kuberikan kepadamu mut‘ah dan aku ceraikan kamu dengan cara yang baik.”'),
	(3562, 33, 29, 'وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُوْلَهٗ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنٰتِ مِنْكُنَّ اَجْرًا عَظِيْمًا', 'wa-in kuntunna turidna <strong>al</strong>l<u>aa</u>ha warasuulahu wa<strong>al</strong>dd<u>aa</u>ra <strong>a</strong>l-<u>aa</u>khirata fa-inna <strong>al</strong>l<u>aa</u>ha a\'adda lilmu<u>h</u>sin<u>aa</u>ti minkunna ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Dan jika kamu menginginkan Allah dan Rasul-Nya dan negeri akhirat, maka sesungguhnya Allah menyediakan pahala yang besar bagi siapa yang berbuat baik di antara kamu.'),
	(3563, 33, 30, 'يٰنِسَاۤءَ النَّبِيِّ مَنْ يَّأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُّبَيِّنَةٍ يُّضٰعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا   ۔', 'y<u>aa</u> nis<u>aa</u>-a <strong>al</strong>nnabiyyi man ya/ti minkunna bif<u>aah</u>isyatin mubayyinatin yu<u>daa</u>\'af lah<u>aa</u> <strong>a</strong>l\'a<u>dzaa</u>bu <u>dh</u>i\'fayni wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<u>aa</u><strong>n</strong>', 'Wahai istri-istri Nabi! Barangsiapa di antara kamu yang mengerjakan perbuatan keji yang nyata, niscaya azabnya akan dilipatgandakan dua kali lipat kepadanya. Dan yang demikian itu, mudah bagi Allah.'),
	(3564, 33, 31, '۞ وَمَنْ يَّقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُوْلِهٖ وَتَعْمَلْ صَالِحًا نُّؤْتِهَآ اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقًا كَرِيْمًا ', 'waman yaqnut minkunna lill<u>aa</u>hi warasuulihi wata\'mal <u>shaa</u>li<u>h</u>an nu/tih<u>aa</u> ajrah<u>aa</u> marratayni wa-a\'tadn<u>aa</u> lah<u>aa</u> rizqan kariim<u>aa</u><strong>n</strong>', 'Dan barangsiapa di antara kamu (istri-istri Nabi) tetap taat kepada Allah dan Rasul-Nya dan mengerjakan kebajikan, niscaya Kami berikan pahala kepadanya dua kali lipat dan Kami sediakan rezeki yang mulia baginya.'),
	(3565, 33, 32, 'يٰنِسَاۤءَ النَّبِيِّ لَسْتُنَّ كَاَحَدٍ مِّنَ النِّسَاۤءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِيْ فِيْ قَلْبِهٖ مَرَضٌ وَّقُلْنَ قَوْلًا مَّعْرُوْفًاۚ ', 'y<u>aa</u> nis<u>aa</u>-a <strong>al</strong>nnabiyyi lastunna ka-a<u>h</u>adin mina <strong>al</strong>nnis<u>aa</u>-i ini ittaqaytunna fal<u>aa</u> takh<u>dh</u>a\'na bi<strong>a</strong>lqawli faya<u>th</u>ma\'a <strong>al</strong>la<u>dz</u>ii fii qalbihi mara<u>dh</u>un waqulna qawlan ma\'ruuf<u>aa</u><strong>n</strong>', 'Wahai istri-istri Nabi! Kamu tidak seperti perempuan-perempuan yang lain, jika kamu bertakwa. Maka janganlah kamu tunduk (melemah lembutkan suara) dalam berbicara sehingga bangkit nafsu orang yang ada penyakit dalam hatinya, dan ucapkanlah perkataan yang baik.'),
	(3566, 33, 33, 'وَقَرْنَ فِيْ بُيُوْتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُوْلٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰتِيْنَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِ', 'waqarna fii buyuutikunna wal<u>aa</u> tabarrajna tabarruja <strong>a</strong>lj<u>aa</u>hiliyyati <strong>a</strong>l-uul<u>aa</u> wa-aqimna <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tiina <strong>al</strong>zzak<u>aa</u>ta wa-a<u>th</u>i\'na <strong>al</strong>l<u>aa</u>ha warasuulahu innam<u>aa</u> yuriidu <strong>al</strong>l<u>aa</u>hu liyu<u>dz</u>hiba \'ankumu <strong>al</strong>rrijsa ahla <strong>a</strong>lbayti wayu<u>th</u>ahhirakum ta<u>th</u>hiir<u>aa</u><strong>n</strong>', 'Dan hendaklah kamu tetap di rumahmu dan janganlah kamu berhias dan (bertingkah laku) seperti orang-orang jahiliah dahulu, dan laksanakanlah salat, tunaikanlah zakat dan taatilah Allah dan Rasul-Nya. Sesungguhnya Allah bermaksud hendak menghilangkan dosa dari kamu, wahai ahlulbait dan membersihkan kamu sebersih-bersihnya.'),
	(3567, 33, 34, 'وَاذْكُرْنَ مَا يُتْلٰى فِيْ بُيُوْتِكُنَّ مِنْ اٰيٰتِ اللّٰهِ وَالْحِكْمَةِۗ اِنَّ اللّٰهَ كَانَ لَطِيْفًا خَبِيْرًا ࣖ ', 'wa<strong>u</strong><u>dz</u>kurna m<u>aa</u> yutl<u>aa</u> fii buyuutikunna min <u>aa</u>y<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wa<strong>a</strong>l<u>h</u>ikmati inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na la<u>th</u>iifan khabiir<u>aa</u><strong>n</strong>', 'Dan ingatlah apa yang dibacakan di rumahmu dari ayat-ayat Allah dan hikmah (sunnah Nabimu). Sungguh, Allah Mahalembut, Maha Mengetahui.'),
	(3568, 33, 35, 'اِنَّ الْمُسْلِمِيْنَ وَالْمُسْلِمٰتِ وَالْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ وَالْقٰنِتِيْنَ وَالْقٰنِتٰتِ وَالصّٰدِقِيْنَ وَالصّٰدِقٰتِ وَالصّٰبِرِيْنَ وَالصّٰبِرٰتِ وَالْخٰشِعِيْنَ وَالْخٰشِعٰتِ وَالْمُتَصَدِّقِيْنَ وَالْمُتَصَدِّقٰتِ وَالصَّاۤىِٕمِيْنَ وَال', 'inna <strong>a</strong>lmuslimiina wa<strong>a</strong>lmuslim<u>aa</u>ti wa<strong>a</strong>lmu/miniina wa<strong>a</strong>lmu/min<u>aa</u>ti wa<strong>a</strong>lq<u>aa</u>nitiina wa<strong>a</strong>lq<u>aa</u>nit<u>aa</u>ti wa<strong>al</strong><u>shshaa</u>diqiina wa<strong>al</strong><u>shshaa</u>diq<u>aa</u>ti wa<strong>al</strong><u>shshaa</u>biriina wa<strong>al</strong><u>shshaa</u>bir<u>aa</u>ti wa<strong>a</strong>lkh<u>aa</u>syi\'iina wa<strong>a</strong>lkh<u>aa</u>syi\'<u>aa</u>ti wa<strong>a</strong>lmuta<u>sh</u>addiqiina wa<strong>a</strong>lmuta<u>sh</u>addiq<u>aa</u>ti wa<strong>al</strong><u>shshaa</u>-imiina wa<strong>al</strong><u>shshaa</u>-im<u>aa</u>ti wa<strong>a</strong>l<u>haa</u>fi<u>zh</u>iina furuujahum wa<strong>a</strong>l<u>haa</u>fi<u>zhaa</u>ti wa<strong>al</strong><u>dzdzaa</u>kiriina <strong>al</strong>l<u>aa</u>ha katsiiran wa<strong>al</strong><u>dzdzaa</u>kir<u>aa</u>ti a\'adda <strong>al</strong>l<u>aa</u>hu lahum maghfiratan wa-ajran \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Sungguh, laki-laki dan perempuan muslim, laki-laki dan perempuan mukmin, laki-laki dan perempuan yang tetap dalam ketaatannya, laki-laki dan perempuan yang benar, laki-laki dan perempuan yang sabar, laki-laki dan perempuan yang khusyuk, laki-laki dan perempuan yang bersedekah, laki-laki dan perempuan yang berpuasa, laki-laki dan perempuan yang memelihara kehormatannya, laki-laki dan perempuan yang banyak menyebut (nama) Allah, Allah telah menyediakan untuk mereka ampunan dan pahala yang besar.'),
	(3569, 33, 36, 'وَمَا كَانَ لِمُؤْمِنٍ وَّلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُوْلُهٗٓ اَمْرًا اَنْ يَّكُوْنَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْ ۗوَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ ضَلَّ ضَلٰلًا مُّبِيْنًاۗ  ', 'wam<u>aa</u> k<u>aa</u>na limu/minin wal<u>aa</u> mu/minatin i<u>dzaa</u> qa<u>daa</u> <strong>al</strong>l<u>aa</u>hu warasuuluhu amran an yakuuna lahumu <strong>a</strong>lkhiyaratu min amrihim waman ya\'<u>sh</u>i <strong>al</strong>l<u>aa</u>ha warasuulahu faqad <u>dh</u>alla <u>dh</u>al<u>aa</u>lan mubiin<u>aa</u><strong>n</strong>', 'Dan tidaklah pantas bagi laki-laki yang mukmin dan perempuan yang mukmin, apabila Allah dan Rasul-Nya telah menetapkan suatu ketetapan, akan ada pilihan (yang lain) bagi mereka tentang urusan mereka. Dan barangsiapa mendurhakai Allah dan Rasul-Nya, maka sungguh, dia telah tersesat, dengan kesesatan yang nyata.'),
	(3570, 33, 37, 'وَاِذْ تَقُوْلُ لِلَّذِيْٓ اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْفِيْ فِيْ نَفْسِكَ مَا اللّٰهُ مُبْدِيْهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰىهُ ۗ فَلَمَّا قَضٰى زَيْدٌ مِّنْهَا وَ', 'wa-i<u>dz</u> taquulu lilla<u>dz</u>ii an\'ama <strong>al</strong>l<u>aa</u>hu \'alayhi wa-an\'amta \'alayhi amsik \'alayka zawjaka wa<strong>i</strong>ttaqi <strong>al</strong>l<u>aa</u>ha watukhfii fii nafsika m<u>aa</u> <strong>al</strong>l<u>aa</u>hu mubdiihi watakhsy<u>aa</u> <strong>al</strong>nn<u>aa</u>sa wa<strong>al</strong>l<u>aa</u>hu a<u>h</u>aqqu an takhsy<u>aa</u>hu falamm<u>aa</u> qa<u>daa</u> zaydun minh<u>aa</u> wa<u>th</u>aran zawwajn<u>aa</u>kah<u>aa</u> likay l<u>aa</u> yakuuna \'al<u>aa</u> <strong>a</strong>lmu/miniina <u>h</u>arajun fii azw<u>aa</u>ji ad\'iy<u>aa</u>-ihim i<u>dzaa</u> qa<u>dh</u>aw minhunna wa<u>th</u>aran wak<u>aa</u>na amru <strong>al</strong>l<u>aa</u>hi maf\'uul<u>aa</u><strong>n</strong>', 'Dan (ingatlah), ketika engkau (Muhammad) berkata kepada orang yang telah diberi nikmat oleh Allah dan engkau (juga) telah memberi nikmat kepadanya, “Pertahankanlah terus istrimu dan bertakwalah kepada Allah,” sedang engkau menyembunyikan di dalam hatimu apa yang akan dinyatakan oleh Allah, dan engkau takut kepada manusia, padahal Allah lebih berhak engkau takuti. Maka ketika Zaid telah mengakhiri keperluan terhadap istrinya (menceraikannya), Kami nikahkan engkau dengan dia (Zainab) agar tidak ada keberatan bagi orang mukmin untuk (menikahi) istri-istri anak-anak angkat mereka, apabila anak-anak angkat itu telah menyelesaikan keperluannya terhadap istrinya. Dan ketetapan Allah itu pasti terjadi.'),
	(3571, 33, 38, 'مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيْمَا فَرَضَ اللّٰهُ لَهٗ ۗسُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۗوَكَانَ اَمْرُ اللّٰهِ قَدَرًا مَّقْدُوْرًاۙ  ', 'm<u>aa</u> k<u>aa</u>na \'al<u>aa</u> <strong>al</strong>nnabiyyi min <u>h</u>arajin fiim<u>aa</u> fara<u>dh</u>a <strong>al</strong>l<u>aa</u>hu lahu sunnata <strong>al</strong>l<u>aa</u>hi fii <strong>al</strong>la<u>dz</u>iina khalaw min qablu wak<u>aa</u>na amru <strong>al</strong>l<u>aa</u>hi qadaran maqduur<u>aa</u><strong>n</strong>', 'Tidak ada keberatan apa pun pada Nabi tentang apa yang telah ditetapkan Allah baginya. (Allah telah menetapkan yang demikian) sebagai sunnah Allah pada nabi-nabi yang telah terdahulu. Dan ketetapan Allah itu suatu ketetapan yang pasti berlaku,'),
	(3572, 33, 39, ' ۨالَّذِيْنَ يُبَلِّغُوْنَ رِسٰلٰتِ اللّٰهِ وَيَخْشَوْنَهٗ وَلَا يَخْشَوْنَ اَحَدًا اِلَّا اللّٰهَ ۗوَكَفٰى بِاللّٰهِ حَسِيْبًا ', '<strong>al</strong>la<u>dz</u>iina yuballighuuna ris<u>aa</u>l<u>aa</u>ti <strong>al</strong>l<u>aa</u>hi wayakhsyawnahu wal<u>aa</u> yakhsyawna a<u>h</u>adan ill<u>aa</u> <strong>al</strong>l<u>aa</u>ha wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi <u>h</u>asiib<u>aa</u><strong>n</strong>', '(yaitu) orang-orang yang menyampaikan risalah-risalah Allah, mereka takut kepada-Nya dan tidak merasa takut kepada siapa pun selain kepada Allah. Dan cukuplah Allah sebagai pembuat perhitungan.'),
	(3573, 33, 40, 'مَا كَانَ مُحَمَّدٌ اَبَآ اَحَدٍ مِّنْ رِّجَالِكُمْ وَلٰكِنْ رَّسُوْلَ اللّٰهِ وَخَاتَمَ النَّبِيّٖنَۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا ࣖ', 'm<u>aa</u> k<u>aa</u>na mu<u>h</u>ammadun ab<u>aa</u> a<u>h</u>adin min rij<u>aa</u>likum wal<u>aa</u>kin rasuula <strong>al</strong>l<u>aa</u>hi wakh<u>aa</u>tama <strong>al</strong>nnabiyyiina wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu bikulli syay-in \'aliim<u>aa</u><strong>n</strong>', 'Muhammad itu bukanlah bapak dari seseorang di antara kamu, tetapi dia adalah utusan Allah dan penutup para nabi. Dan Allah Maha Mengetahui segala sesuatu.'),
	(3574, 33, 41, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْرًا كَثِيْرًاۙ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu u<u>dz</u>kuruu <strong>al</strong>l<u>aa</u>ha <u>dz</u>ikran katsiir<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Ingatlah kepada Allah, dengan mengingat (nama-Nya) sebanyak-banyaknya,'),
	(3575, 33, 42, 'وَّسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا ', 'wasabbi<u>h</u>uuhu bukratan wa-a<u>sh</u>iil<u>aa</u><strong>n</strong>', 'dan bertasbihlah kepada-Nya pada waktu pagi dan petang.'),
	(3576, 33, 43, 'هُوَ الَّذِيْ يُصَلِّيْ عَلَيْكُمْ وَمَلٰۤىِٕكَتُهٗ لِيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَكَانَ بِالْمُؤْمِنِيْنَ رَحِيْمًا ', 'huwa <strong>al</strong>la<u>dz</u>ii yu<u>sh</u>allii \'alaykum wamal<u>aa</u>-ikatuhu liyukhrijakum mina <strong>al</strong><u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> <strong>al</strong>nnuuri wak<u>aa</u>na bi<strong>a</strong>lmu/miniina ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Dialah yang memberi rahmat kepadamu dan para malaikat-Nya (memohonkan ampunan untukmu), agar Dia mengeluarkan kamu dari kegelapan kepada cahaya (yang terang). Dan Dia Maha Penyayang kepada orang-orang yang beriman.'),
	(3577, 33, 44, 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهٗ سَلٰمٌ ۚوَاَعَدَّ لَهُمْ اَجْرًا كَرِيْمًا ', 'ta<u>h</u>iyyatuhum yawma yalqawnahu sal<u>aa</u>mun wa-a\'adda lahum ajran kariim<u>aa</u><strong>n</strong>', 'Penghormatan mereka (orang-orang mukmin itu) ketika mereka menemui-Nya ialah, “Salam,” dan Dia menyediakan pahala yang mulia bagi mereka.'),
	(3578, 33, 45, 'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu inn<u>aa</u> arsaln<u>aa</u>ka sy<u>aa</u>hidan wamubasysyiran wana<u>dz</u>iir<u>aa</u><strong>n</strong>', 'Wahai Nabi! Sesungguhnya Kami mengutusmu untuk menjadi saksi, pembawa kabar gembira dan pemberi peringatan,'),
	(3579, 33, 46, 'وَّدَاعِيًا اِلَى اللّٰهِ بِاِذْنِهٖ وَسِرَاجًا مُّنِيْرًا ', 'wad<u>aa</u>\'iyan il<u>aa</u> <strong>al</strong>l<u>aa</u>hi bi-i<u>dz</u>nihi wasir<u>aa</u>jan muniir<u>aa</u><strong>n</strong>', 'dan untuk menjadi penyeru kepada (agama) Allah dengan izin-Nya dan sebagai cahaya yang menerangi.'),
	(3580, 33, 47, 'وَبَشِّرِ الْمُؤْمِنِيْنَ بِاَنَّ لَهُمْ مِّنَ اللّٰهِ فَضْلًا كَبِيْرًا ', 'wabasysyiri <strong>a</strong>lmu/miniina bi-anna lahum mina <strong>al</strong>l<u>aa</u>hi fa<u>dh</u>lan kabiir<u>aa</u><strong>n</strong>', 'Dan sampaikanlah kabar gembira kepada orang-orang mukmin bahwa sesungguhnya bagi mereka karunia yang besar dari Allah.'),
	(3581, 33, 48, 'وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ وَدَعْ اَذٰىهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ', 'wal<u>aa</u> tu<u>th</u>i\'i <strong>a</strong>lk<u>aa</u>firiina wa<strong>a</strong>lmun<u>aa</u>fiqiina wada\' a<u>dzaa</u>hum watawakkal \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wakaf<u>aa</u> bi<strong>al</strong>l<u>aa</u>hi wakiil<u>aa</u><strong>n</strong>', 'Dan janganlah engkau (Muhammad) menuruti orang-orang kafir dan orang-orang munafik itu, janganlah engkau hiraukan gangguan mereka dan bertawakallah kepada Allah. Dan cukuplah Allah sebagai pelindung.'),
	(3582, 33, 49, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَكَحْتُمُ الْمُؤْمِنٰتِ ثُمَّ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّوْنَهَاۚ فَمَتِّعُوْهُنَّ وَسَرِّحُوْهُنَّ سَرَاحًا جَمِيْلًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> naka<u>h</u>tumu <strong>a</strong>lmu/min<u>aa</u>ti tsumma <u>th</u>allaqtumuuhunna min qabli an tamassuuhunna fam<u>aa</u> lakum \'alayhinna min \'iddatin ta\'tadduunah<u>aa</u> famatti\'uuhunna wasarri<u>h</u>uuhunna sar<u>aah</u>an jamiil<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Apabila kamu menikahi perempuan-perempuan mukmin, kemudian kamu ceraikan mereka sebelum kamu mencampurinya maka tidak ada masa idah atas mereka yang perlu kamu perhitungkan. Namun berilah mereka mut’ah dan lepaskanlah mereka itu dengan cara yang sebaik-baiknya.'),
	(3583, 33, 50, 'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰتِيْٓ اٰتَيْتَ اُجُوْرَهُنَّ وَمَا مَلَكَتْ يَمِيْنُكَ مِمَّآ اَفَاۤءَ اللّٰهُ عَلَيْكَ وَبَنٰتِ عَمِّكَ وَبَنٰتِ عَمّٰتِكَ وَبَنٰتِ خَالِكَ وَبَنٰتِ خٰلٰتِكَ الّٰتِيْ هَاجَرْنَ مَعَكَۗ وَام', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu inn<u>aa</u> a<u>h</u>laln<u>aa</u> laka azw<u>aa</u>jaka <strong>al</strong>l<u>aa</u>tii <u>aa</u>tayta ujuurahunna wam<u>aa</u> malakat yamiinuka mimm<u>aa</u> af<u>aa</u>-a <strong>al</strong>l<u>aa</u>hu \'alayka waban<u>aa</u>ti \'ammika waban<u>aa</u>ti \'amm<u>aa</u>tika waban<u>aa</u>ti kh<u>aa</u>lika waban<u>aa</u>ti kh<u>aa</u>l<u>aa</u>tika <strong>al</strong>l<u>aa</u>tii h<u>aa</u>jarna ma\'aka wa<strong>i</strong>mra-atan mu/minatan in wahabat nafsah<u>aa</u> li<strong>l</strong>nnabiyyi in ar<u>aa</u>da <strong>al</strong>nnabiyyu an yastanki<u>h</u>ah<u>aa</u> kh<u>aa</u>li<u>sh</u>atan laka min duuni <strong>a</strong>lmu/miniina qad \'alimn<u>aa</u> m<u>aa</u> fara<u>dh</u>n<u>aa</u> \'alayhim fii azw<u>aa</u>jihim wam<u>aa</u> malakat aym<u>aa</u>nuhum likayl<u>aa</u> yakuuna \'alayka <u>h</u>arajun wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Wahai Nabi! Sesungguhnya Kami telah menghalalkan bagimu istri-istrimu yang telah engkau berikan maskawinnya dan hamba sahaya yang engkau miliki, termasuk apa yang engkau peroleh dalam peperangan yang dikaruniakan Allah untukmu, dan (demikian pula) anak-anak perempuan dari saudara laki-laki bapakmu, anak-anak perempuan dari saudara perempuan bapakmu, anak-anak perempuan dari saudara laki-laki ibumu dan anak-anak perempuan dari saudara perempuan ibumu yang turut hijrah bersamamu, dan perempuan mukmin yang menyerahkan dirinya kepada Nabi kalau Nabi ingin menikahinya, sebagai kekhususan bagimu, bukan untuk semua orang mukmin. Kami telah mengetahui apa yang Kami wajibkan kepada mereka tentang istri-istri mereka dan hamba sahaya yang mereka miliki agar tidak menjadi kesempitan bagimu. Dan Allah Maha Pengampun, Maha Penyayang.'),
	(3584, 33, 51, '۞ تُرْجِيْ مَنْ تَشَاۤءُ مِنْهُنَّ وَتُـْٔوِيْٓ اِلَيْكَ مَنْ تَشَاۤءُۗ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۗ ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ اٰتَيْتَهُنَّ كُلُّهُنَّۗ وَاللّٰهُ يَعْلَمُ', 'turjii man tasy<u>aa</u>u minhunna watu/wii ilayka man tasy<u>aa</u>u wamani ibtaghayta mimman \'azalta fal<u>aa</u> jun<u>aah</u>a \'alayka <u>dzaa</u>lika adn<u>aa</u> an taqarra a\'yunuhunna wal<u>aa</u> ya<u>h</u>zanna wayar<u>dh</u>ayna bim<u>aa</u> <u>aa</u>taytahunna kulluhunna wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> fii quluubikum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>aliim<u>aa</u><strong>n</strong>', 'Engkau boleh menangguhkan (menggauli) siapa yang engkau kehendaki di antara mereka (para istrimu) dan (boleh pula) menggauli siapa (di antara mereka) yang engkau kehendaki. Dan siapa yang engkau ingini untuk menggaulinya kembali dari istri-istrimu yang telah engkau sisihkan, maka tidak ada dosa bagimu. Yang demikian itu lebih dekat untuk ketenangan hati mereka, dan mereka tidak merasa sedih, dan mereka rela dengan apa yang telah engkau berikan kepada mereka semuanya. Dan Allah mengetahui apa yang (tersimpan) dalam hatimu. Dan Allah Maha Mengetahui, Maha Penyantun.'),
	(3585, 33, 52, 'لَا يَحِلُّ لَكَ النِّسَاۤءُ مِنْۢ بَعْدُ وَلَآ اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَّلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَمِيْنُكَۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَّقِيْبًا ࣖ ', 'turjii man tasy<u>aa</u>u minhunna watu/wii ilayka man tasy<u>aa</u>u wamani ibtaghayta mimman \'azalta fal<u>aa</u> jun<u>aah</u>a \'alayka <u>dzaa</u>lika adn<u>aa</u> an taqarra a\'yunuhunna wal<u>aa</u> ya<u>h</u>zanna wayar<u>dh</u>ayna bim<u>aa</u> <u>aa</u>taytahunna kulluhunna wa<strong>al</strong>l<u>aa</u>hu ya\'lamu m<u>aa</u> fii quluubikum wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu \'aliiman <u>h</u>aliim<u>aa</u><strong>n</strong>', 'Tidak halal bagimu (Muhammad) menikahi perempuan-perempuan (lain) setelah itu, dan tidak boleh (pula) mengganti mereka dengan istri-istri (yang lain), meskipun kecantikannya menarik hatimu kecuali perempuan-perempuan (hamba sahaya) yang engkau miliki. Dan Allah Maha Mengawasi segala sesuatu.'),
	(3586, 33, 53, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتَ النَّبِيِّ اِلَّآ اَنْ يُّؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نٰظِرِيْنَ اِنٰىهُ وَلٰكِنْ اِذَا دُعِيْتُمْ فَادْخُلُوْا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوْا وَلَا مُسْتَأْنِسِيْنَ لِحَدِيْثٍۗ اِنَّ ذٰ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tadkhuluu buyuuta <strong>al</strong>nnabiyyi ill<u>aa</u> an yu/<u>dz</u>ana lakum il<u>aa</u> <u>th</u>a\'<u>aa</u>min ghayra n<u>aats</u>iriina in<u>aa</u>hu wal<u>aa</u>kin i<u>dzaa</u> du\'iitum fa<strong>u</strong>dkhuluu fa-i<u>dzaa</u> <u>th</u>a\'imtum fa<strong>i</strong>ntasyiruu wal<u>aa</u> musta/nisiina li<u>h</u>adiitsin inna <u>dzaa</u>likum k<u>aa</u>na yu/<u>dz</u>ii <strong>al</strong>nnabiyya fayasta<u>h</u>yii minkum wa<strong>al</strong>l<u>aa</u>hu l<u>aa</u> yasta<u>h</u>yii mina <strong>a</strong>l<u>h</u>aqqi wa-i<u>dzaa</u> sa-altumuuhunna mat<u>aa</u>\'an fa<strong>i</strong>s-aluuhunna min war<u>aa</u>-i <u>h</u>ij<u>aa</u>bin <u>dzaa</u>likum a<u>th</u>haru liquluubikum waquluubihinna wam<u>aa</u> k<u>aa</u>na lakum an tu/<u>dz</u>uu rasuula <strong>al</strong>l<u>aa</u>hi wal<u>aa</u> an tanki<u>h</u>uu azw<u>aa</u>jahu min ba\'dihi abadan inna <u>dzaa</u>likum k<u>aa</u>na \'inda <strong>al</strong>l<u>aa</u>hi \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu memasuki rumah-rumah Nabi kecuali jika kamu diizinkan untuk makan tanpa menunggu waktu masak (makanannya), tetapi jika kamu dipanggil maka masuklah dan apabila kamu selesai makan, keluarlah kamu tanpa memperpanjang percakapan. Sesungguhnya yang demikian itu adalah mengganggu Nabi sehingga dia (Nabi) malu kepadamu (untuk menyuruhmu keluar), dan Allah tidak malu (menerangkan) yang benar. Apabila kamu meminta sesuatu (keperluan) kepada mereka (istri-istri Nabi), maka mintalah dari belakang tabir. (Cara) yang demikian itu lebih suci bagi hatimu dan hati mereka. Dan tidak boleh kamu menyakiti (hati) Rasulullah dan tidak boleh (pula) menikahi istri-istrinya selama-lamanya setelah (Nabi wafat). Sungguh, yang demikian itu sangat besar (dosanya) di sisi Allah.'),
	(3587, 33, 54, 'اِنْ تُبْدُوْا شَيْـًٔا اَوْ تُخْفُوْهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ', 'in tubduu syay-an aw tukhfuuhu fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bikulli syay-in \'aliim<u>aa</u><strong>n</strong>', 'Jika kamu menyatakan sesuatu atau menyembunyikannya, maka sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
	(3588, 33, 55, 'لَا جُنَاحَ عَلَيْهِنَّ فِيْٓ اٰبَاۤىِٕهِنَّ وَلَآ اَبْنَاۤىِٕهِنَّ وَلَآ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اَخَوٰتِهِنَّ وَلَا نِسَاۤىِٕهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّقِيْنَ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ ع', 'l<u>aa</u> jun<u>aah</u>a \'alayhinna fii <u>aa</u>b<u>aa</u>-ihinna wal<u>aa</u> abn<u>aa</u>-ihinna wal<u>aa</u> ikhw<u>aa</u>nihinna wal<u>aa</u> abn<u>aa</u>-i ikhw<u>aa</u>nihinna wal<u>aa</u> abn<u>aa</u>-i akhaw<u>aa</u>tihinna wal<u>aa</u> nis<u>aa</u>-ihinna wal<u>aa</u> m<u>aa</u> malakat aym<u>aa</u>nuhunna wa<strong>i</strong>ttaqiina <strong>al</strong>l<u>aa</u>ha inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na \'al<u>aa</u> kulli syay-in syahiid<u>aa</u><strong>n</strong>', 'Tidak ada dosa atas istri-istri Nabi (untuk berjumpa tanpa tabir) dengan bapak-bapak mereka, anak laki-laki mereka, saudara laki-laki mereka, anak laki-laki dari saudara laki-laki mereka, anak laki-laki dari saudara perempuan mereka, perempuan-perempuan mereka (yang beriman) dan hamba sahaya yang mereka miliki, dan bertakwalah kamu (istri-istri Nabi) kepada Allah. Sungguh, Allah Maha Menyaksikan segala sesuatu.'),
	(3589, 33, 56, 'اِنَّ اللّٰهَ وَمَلٰۤىِٕكَتَهٗ يُصَلُّوْنَ عَلَى النَّبِيِّۗ  يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا صَلُّوْا عَلَيْهِ وَسَلِّمُوْا تَسْلِيْمًا ', 'inna <strong>al</strong>l<u>aa</u>ha wamal<u>aa</u>-ikatahu yu<u>sh</u>alluuna \'al<u>aa</u> <strong>al</strong>nnabiyyi y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu <u>sh</u>alluu \'alayhi wasallimuu tasliim<u>aa</u><strong>n</strong>', 'Sesungguhnya Allah dan para malaikat-Nya bersalawat untuk Nabi. Wahai orang-orang yang beriman! Bersalawatlah kamu untuk Nabi dan ucapkanlah salam dengan penuh penghormatan kepadanya.'),
	(3590, 33, 57, 'اِنَّ الَّذِيْنَ يُؤْذُوْنَ اللّٰهَ وَرَسُوْلَهٗ لَعَنَهُمُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَابًا مُّهِيْنًا ', 'inna <strong>al</strong>la<u>dz</u>iina yu/<u>dz</u>uuna <strong>al</strong>l<u>aa</u>ha warasuulahu la\'anahumu <strong>al</strong>l<u>aa</u>hu fii <strong>al</strong>dduny<u>aa</u> wa<strong>a</strong>l-<u>aa</u>khirati wa-a\'adda lahum \'a<u>dzaa</u>ban muhiin<u>aa</u><strong>n</strong>', 'Sesungguhnya (terhadap) orang-orang yang menyakiti Allah dan Rasul-Nya, Allah akan melaknatnya di dunia dan di akhirat, dan menyediakan azab yang menghinakan bagi mereka.'),
	(3591, 33, 58, 'وَالَّذِيْنَ يُؤْذُوْنَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ بِغَيْرِ مَا اكْتَسَبُوْا فَقَدِ احْتَمَلُوْا بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ࣖ ', 'wa<strong>a</strong>lla<u>dz</u>iina yu/<u>dz</u>uuna <strong>a</strong>lmu/miniina wa<strong>a</strong>lmu/min<u>aa</u>ti bighayri m<u>aa</u> iktasabuu faqadi i<u>h</u>tamaluu buht<u>aa</u>nan wa-itsman mubiin<u>aa</u><strong>n</strong>', 'Dan orang-orang yang menyakiti orang-orang mukmin laki-laki dan perempuan, tanpa ada kesalahan yang mereka perbuat, maka sungguh, mereka telah memikul kebohongan dan dosa yang nyata.'),
	(3592, 33, 59, 'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ وَبَنٰتِكَ وَنِسَاۤءِ الْمُؤْمِنِيْنَ يُدْنِيْنَ عَلَيْهِنَّ مِنْ جَلَابِيْبِهِنَّۗ ذٰلِكَ اَدْنٰىٓ اَنْ يُّعْرَفْنَ فَلَا يُؤْذَيْنَۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nnabiyyu qul li-azw<u>aa</u>jika waban<u>aa</u>tika wanis<u>aa</u>-i <strong>a</strong>lmu/miniina yudniina \'alayhinna min jal<u>aa</u>biibihinna <u>dzaa</u>lika adn<u>aa</u> an yu\'rafna fal<u>aa</u> yu/<u>dz</u>ayna wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'Wahai Nabi! Katakanlah kepada istri-istrimu, anak-anak perempuanmu dan istri-istri orang mukmin, “Hendaklah mereka menutupkan jilbabnya ke seluruh tubuh mereka.” Yang demikian itu agar mereka lebih mudah untuk dikenali, sehingga mereka tidak diganggu. Dan Allah Maha Pengampun, Maha Penyayang.'),
	(3593, 33, 60, '۞ لَىِٕنْ لَّمْ يَنْتَهِ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْمُرْجِفُوْنَ فِى الْمَدِيْنَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُوْنَكَ فِيْهَآ اِلَّا قَلِيْلًا ', 'la-in lam yantahi <strong>a</strong>lmun<u>aa</u>fiquuna wa<strong>a</strong>lla<u>dz</u>iina fii quluubihim mara<u>dh</u>un wa<strong>a</strong>lmurjifuuna fii <strong>a</strong>lmadiinati lanughriyannaka bihim tsumma l<u>aa</u> yuj<u>aa</u>wiruunaka fiih<u>aa</u> ill<u>aa</u> qaliil<u>aa</u><strong>n</strong>', 'Sungguh, jika orang-orang munafik, orang-orang yang berpenyakit dalam hatinya dan orang-orang yang menyebarkan kabar bohong di Madinah tidak berhenti (dari menyakitimu), niscaya Kami perintahkan engkau (untuk memerangi) mereka, kemudian mereka tidak lagi menjadi tetanggamu (di Madinah) kecuali sebentar,'),
	(3594, 33, 61, 'مَلْعُوْنِيْنَۖ اَيْنَمَا ثُقِفُوْٓا اُخِذُوْا وَقُتِّلُوْا تَقْتِيْلًا ', 'mal\'uuniina ayna m<u>aa</u> tsuqifuu ukhi<u>dz</u>uu waquttiluu taqtiil<u>aa</u><strong>n</strong>', 'dalam keadaan terlaknat. Di mana saja mereka dijumpai, mereka akan ditangkap dan dibunuh tanpa ampun.'),
	(3595, 33, 62, 'سُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۚوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا ', 'sunnata <strong>al</strong>l<u>aa</u>hi fii <strong>al</strong>la<u>dz</u>iina khalaw min qablu walan tajida lisunnati <strong>al</strong>l<u>aa</u>hi tabdiil<u>aa</u><strong>n</strong>', 'Sebagai sunnah Allah yang (berlaku juga) bagi orang-orang yang telah terdahulu sebelum(mu), dan engkau tidak akan mendapati perubahan pada sunnah Allah.'),
	(3596, 33, 63, 'يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ تَكُوْنُ قَرِيْبًا ', 'yas-aluka <strong>al</strong>nn<u>aa</u>su \'ani <strong>al</strong>ss<u>aa</u>\'ati qul innam<u>aa</u> \'ilmuh<u>aa</u> \'inda <strong>al</strong>l<u>aa</u>hi wam<u>aa</u> yudriika la\'alla <strong>al</strong>ss<u>aa</u>\'ata takuunu qariib<u>aa</u><strong>n</strong>', 'Manusia bertanya kepadamu (Muhammad) tentang hari Kiamat. Katakanlah, “Ilmu tentang hari Kiamat itu hanya di sisi Allah.” Dan tahukah engkau, boleh jadi hari Kiamat itu sudah dekat waktunya.'),
	(3597, 33, 64, 'اِنَّ اللّٰهَ لَعَنَ الْكٰفِرِيْنَ وَاَعَدَّ لَهُمْ سَعِيْرًاۙ ', 'inna <strong>al</strong>l<u>aa</u>ha la\'ana <strong>a</strong>lk<u>aa</u>firiina wa-a\'adda lahum sa\'iir<u>aa</u><strong>n</strong>', 'Sungguh, Allah melaknat orang-orang kafir dan menyediakan bagi mereka api yang menyala-nyala (neraka),'),
	(3598, 33, 65, 'خٰلِدِيْنَ فِيْهَآ اَبَدًاۚ  لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ۚ', 'kh<u>aa</u>lidiina fiih<u>aa</u> abadan l<u>aa</u> yajiduuna waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u><strong>n</strong>', 'mereka kekal di dalamnya selama-lamanya; mereka tidak akan mendapatkan pelindung dan penolong.'),
	(3599, 33, 66, 'يَوْمَ تُقَلَّبُ وُجُوْهُهُمْ فِى النَّارِ يَقُوْلُوْنَ يٰلَيْتَنَآ اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُوْلَا۠ ', 'yawma tuqallabu wujuuhuhum fii <strong>al</strong>nn<u>aa</u>ri yaquuluuna y<u>aa</u> laytan<u>aa</u> a<u>th</u>a\'n<u>aa</u> <strong>al</strong>l<u>aa</u>ha wa-a<u>th</u>a\'n<u>aa</u> <strong>al</strong>rrasuul<u>aa</u>', 'Pada hari (ketika) wajah mereka dibolak-balikkan dalam neraka, mereka berkata, “Wahai, kiranya dahulu kami taat kepada Allah dan taat (pula) kepada Rasul.”'),
	(3600, 33, 67, 'وَقَالُوْا رَبَّنَآ اِنَّآ اَطَعْنَا سَادَتَنَا وَكُبَرَاۤءَنَا فَاَضَلُّوْنَا السَّبِيْلَا۠ ', 'waq<u>aa</u>luu rabban<u>aa</u> inn<u>aa</u> a<u>th</u>a\'n<u>aa</u> s<u>aa</u>datan<u>aa</u> wakubar<u>aa</u>-an<u>aa</u> fa-a<u>dh</u>alluun<u>aa</u> <strong>al</strong>ssabiil<u>aa</u>', 'Dan mereka berkata, “Ya Tuhan kami, sesungguhnya kami telah menaati para pemimpin dan para pembesar kami, lalu mereka menyesatkan kami dari jalan (yang benar).'),
	(3601, 33, 68, 'رَبَّنَآ اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيْرًا ࣖ ', 'rabban<u>aa</u> <u>aa</u>tihim <u>dh</u>i\'fayni mina <strong>a</strong>l\'a<u>dzaa</u>bi wa<strong>i</strong>l\'anhum la\'nan kabiir<u>aa</u><strong>n</strong>', 'Ya Tuhan kami, timpakanlah kepada mereka azab dua kali lipat dan laknatlah mereka dengan laknat yang besar.”'),
	(3602, 33, 69, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ اٰذَوْا مُوْسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُوْا ۗوَكَانَ عِنْدَ اللّٰهِ وَجِيْهًا ۗ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> takuunuu ka<strong>a</strong>lla<u>dz</u>iina <u>aadz</u>aw muus<u>aa</u> fabarra-ahu <strong>al</strong>l<u>aa</u>hu mimm<u>aa</u> q<u>aa</u>luu wak<u>aa</u>na \'inda <strong>al</strong>l<u>aa</u>hi wajiih<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Janganlah kamu seperti orang-orang yang menyakiti Musa, maka Allah membersihkannya dari tuduhan-tuduhan yang mereka lontarkan. Dan dia seorang yang mempunyai kedudukan terhormat di sisi Allah.'),
	(3603, 33, 70, 'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُوْلُوْا قَوْلًا سَدِيْدًاۙ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu ittaquu <strong>al</strong>l<u>aa</u>ha waquuluu qawlan sadiid<u>aa</u><strong>n</strong>', 'Wahai orang-orang yang beriman! Bertakwalah kamu kepada Allah dan ucapkanlah perkataan yang benar,'),
	(3604, 33, 71, 'يُّصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ فَازَ فَوْزًا عَظِيْمًا ', 'yu<u>sh</u>li<u>h</u> lakum a\'m<u>aa</u>lakum wayaghfir lakum <u>dz</u>unuubakum waman yu<u>th</u>i\'i <strong>al</strong>l<u>aa</u>ha warasuulahu faqad f<u>aa</u>za fawzan \'a<u>zh</u>iim<u>aa</u><strong>n</strong>', 'niscaya Allah akan memperbaiki amal-amalmu dan mengampuni dosa-dosamu. Dan barangsiapa menaati Allah dan Rasul-Nya, maka sungguh, dia menang dengan kemenangan yang agung.'),
	(3605, 33, 72, 'اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوٰتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَّحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۗ اِنَّهٗ كَانَ ظَلُوْمًا جَهُوْلًاۙ ', 'inn<u>aa</u> \'ara<u>dh</u>n<u>aa</u> <strong>a</strong>l-am<u>aa</u>nata \'al<u>aa</u> <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i wa<strong>a</strong>ljib<u>aa</u>li fa-abayna an ya<u>h</u>milnah<u>aa</u> wa-asyfaqna minh<u>aa</u> wa<u>h</u>amalah<u>aa</u> <strong>a</strong>l-ins<u>aa</u>nu innahu k<u>aa</u>na <u>zh</u>aluuman jahuul<u>aa</u><strong>n</strong>', 'Sesungguhnya Kami telah menawarkan amanat kepada langit, bumi dan gunung-gunung; tetapi semuanya enggan untuk memikul amanat itu dan mereka khawatir tidak akan melaksanakannya (berat), lalu dipikullah amanat itu oleh manusia. Sungguh, manusia itu sangat zalim dan sangat bodoh,'),
	(3606, 33, 73, 'لِّيُعَذِّبَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقَتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ وَيَتُوْبَ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ', 'liyu\'a<u>dzdz</u>iba <strong>al</strong>l<u>aa</u>hu <strong>a</strong>lmun<u>aa</u>fiqiina wa<strong>a</strong>lmun<u>aa</u>fiq<u>aa</u>ti wa<strong>a</strong>lmusyrikiina wa<strong>a</strong>lmusyrik<u>aa</u>ti wayatuuba <strong>al</strong>l<u>aa</u>hu \'al<u>aa</u> <strong>a</strong>lmu/miniina wa<strong>a</strong>lmu/min<u>aa</u>ti wak<u>aa</u>na <strong>al</strong>l<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u><strong>n</strong>', 'sehingga Allah akan mengazab orang-orang munafik laki-laki dan perempuan, orang-orang musyrik, laki-laki dan perempuan; dan Allah akan menerima tobat orang-orang mukmin laki-laki dan perempuan. Dan Allah Maha Pengampun, Maha Penyayang.'),
	(3607, 34, 1, 'اَلْحَمْدُ لِلّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَلَهُ الْحَمْدُ فِى الْاٰخِرَةِۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ', 'al<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii lahu m<u>aa</u> fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i walahu <strong>a</strong>l<u>h</u>amdu fii <strong>a</strong>l-<u>aa</u>khirati wahuwa <strong>a</strong>l<u>h</u>akiimu <strong>a</strong>lkhabiir<strong>u</strong>', 'Segala puji bagi Allah yang memiliki apa yang ada di langit dan apa yang ada di bumi dan segala puji di akhirat bagi Allah. Dan Dialah Yang Mahabijaksana, Mahateliti. '),
	(3608, 34, 2, 'يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ الرَّحِيْمُ الْغَفُوْرُ', 'ya\'lamu m<u>aa</u> yaliju fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> yakhruju minh<u>aa</u> wam<u>aa</u> yanzilu mina <strong>al</strong>ssam<u>aa</u>-i wam<u>aa</u> ya\'ruju fiih<u>aa</u> wahuwa <strong>al</strong>rra<u>h</u>iimu <strong>a</strong>lghafuur<strong>u</strong>', 'Dia mengetahui apa yang masuk ke dalam bumi, apa yang keluar darinya, apa yang turun dari langit dan apa yang naik kepadanya. Dan Dialah Yang Maha Penyayang, Maha Pengampun.'),
	(3609, 34, 3, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَأْتِيْنَا السَّاعَةُ ۗقُلْ بَلٰى وَرَبِّيْ لَتَأْتِيَنَّكُمْۙ عٰلِمِ الْغَيْبِۙ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَلَآ اَصْغَرُ مِنْ ذٰلِكَ وَلَآ اَكْبَرُ اِلَّا فِيْ كِتٰبٍ مُّ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu l<u>aa</u> ta/tiin<u>aa</u> <strong>al</strong>ss<u>aa</u>\'atu qul bal<u>aa</u> warabbii lata/tiyannakum \'<u>aa</u>limi <strong>a</strong>lghaybi l<u>aa</u> ya\'zubu \'anhu mitsq<u>aa</u>lu <u>dz</u>arratin fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wal<u>aa</u> a<u>sh</u>gharu min <u>dzaa</u>lika wal<u>aa</u> akbaru ill<u>aa</u> fii kit<u>aa</u>bin mubiin<strong>in</strong>', 'Dan orang-orang yang kafir berkata, “Hari Kiamat itu tidak akan datang kepada kami.” Katakanlah, “Pasti datang, demi Tuhanku yang mengetahui yang gaib, Kiamat itu pasti akan datang kepadamu. Tidak ada yang tersembunyi bagi-Nya sekalipun seberat zarrah baik yang di langit maupun yang di bumi, yang lebih kecil dari itu atau yang lebih besar, semuanya (tertulis) dalam Kitab yang jelas (Lauh Mahfuzh),”'),
	(3610, 34, 4, 'لِّيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ', 'liyajziya <strong>al</strong>la<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti ul<u>aa</u>-ika lahum maghfiratun warizqun kariim<strong>un</strong>', 'agar Dia (Allah) memberi balasan kepada orang-orang yang beriman dan mengerjakan kebajikan. Mereka memperoleh ampunan dan rezeki yang mulia (surga).'),
	(3611, 34, 5, 'وَالَّذِيْنَ سَعَوْ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ ', 'wa<strong>a</strong>lla<u>dz</u>iina sa\'aw fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun min rijzin <strong>a</strong>liim<strong>in</strong>', 'Dan orang-orang yang berusaha untuk (menentang) ayat-ayat Kami dengan anggapan mereka dapat melemahkan (menggagalkan azab Kami), mereka itu akan memperoleh azab, yaitu azab yang sangat pedih.'),
	(3612, 34, 6, 'وَيَرَى الَّذِيْنَ اُوْتُوا الْعِلْمَ الَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ هُوَ الْحَقَّۙ وَيَهْدِيْٓ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِ ', 'wayar<u>aa</u> <strong>al</strong>la<u>dz</u>iina uutuu <strong>a</strong>l\'ilma <strong>al</strong>la<u>dz</u>ii unzila ilayka min rabbika huwa <strong>a</strong>l<u>h</u>aqqa wayahdii il<u>aa</u> <u>sh</u>ir<u>aath</u>i <strong>a</strong>l\'aziizi <strong>a</strong>l<u>h</u>amiid<strong>i</strong>', 'Dan orang-orang yang diberi ilmu (Ahli Kitab) berpendapat bahwa (wahyu) yang diturunkan kepadamu (Muhammad) dari Tuhanmu itulah yang benar dan memberi petunjuk (bagi manusia) kepada jalan (Allah) Yang Mahaperkasa, Maha Terpuji.'),
	(3613, 34, 7, 'وَقَالَ الَّذِيْنَ كَفَرُوْا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُّنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَفِيْ خَلْقٍ جَدِيْدٍۚ ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu hal nadullukum \'al<u>aa</u> rajulin yunabbi-ukum i<u>dzaa</u> muzziqtum kulla mumazzaqin innakum lafii khalqin jadiid<strong>in</strong>', 'Dan orang-orang kafir berkata (kepada teman-temannya), “Maukah kami tunjukkan kepadamu seorang laki-laki yang memberitakan kepadamu bahwa apabila badanmu telah hancur sehancur-hancurnya, kamu pasti (akan dibangkitkan kembali) dalam ciptaan yang baru.'),
	(3614, 34, 8, 'اَفْتَرٰى عَلَى اللّٰهِ كَذِبًا اَمْ بِهٖ جِنَّةٌ  ۗبَلِ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ فِى الْعَذَابِ وَالضَّلٰلِ الْبَعِيْدِ ', 'aftar<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi ka<u>dz</u>iban am bihi jinnatun bali <strong>al</strong>la<u>dz</u>iina l<u>aa</u> yu/minuuna bi<strong>a</strong>l-<u>aa</u>khirati fii <strong>a</strong>l\'a<u>dzaa</u>bi wa<strong>al</strong><u>dhdh</u>al<u>aa</u>li <strong>a</strong>lba\'iid<strong>i</strong>', 'Apakah dia mengada-adakan kebohongan terhadap Allah atau sakit gila?” (Tidak), tetapi orang-orang yang tidak beriman kepada akhirat itu berada dalam siksaan dan kesesatan yang jauh.'),
	(3615, 34, 9, 'اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ اِنْ نَّشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ السَّمَاۤءِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّكُلِّ عَبْدٍ مُّنِيْبٍ ࣖ ', 'afalam yaraw il<u>aa</u> m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum mina <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i in nasya/ nakhsif bihimu <strong>a</strong>l-ar<u>dh</u>a aw nusqi<u>th</u> \'alayhim kisafan mina <strong>al</strong>ssam<u>aa</u>-i inna fii <u>dzaa</u>lika la<u>aa</u>yatan likulli \'abdin muniib<strong>in</strong>', 'Maka apakah mereka tidak memperhatikan langit dan bumi yang ada di hadapan dan di belakang mereka? Jika Kami menghendaki, niscaya Kami benamkan mereka di bumi atau Kami jatuhkan kepada mereka kepingan-kepingan dari langit. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi setiap hamba yang kembali (kepada-Nya).'),
	(3616, 34, 10, '۞ وَلَقَدْ اٰتَيْنَا دَاوٗدَ مِنَّا فَضْلًاۗ يٰجِبَالُ اَوِّبِيْ مَعَهٗ وَالطَّيْرَ ۚوَاَلَنَّا لَهُ الْحَدِيْدَۙ ', 'walaqad <u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda minn<u>aa</u> fa<u>dh</u>lan y<u>aa</u> jib<u>aa</u>lu awwibii ma\'ahu wa<strong>al</strong><u>ththh</u>ayra wa-alann<u>aa</u> lahu <strong>a</strong>l<u>h</u>adiid<strong>a</strong>', 'Dan sungguh, Telah Kami berikan kepada Dawud karunia dari Kami. (Kami berfirman), “Wahai gunung-gunung dan burung-burung! Bertasbihlah berulang-ulang bersama Dawud,” dan Kami telah melunakkan besi untuknya,'),
	(3617, 34, 11, 'اَنِ اعْمَلْ سٰبِغٰتٍ وَّقَدِّرْ فِى السَّرْدِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ', 'ani i\'mal s<u>aa</u>bigh<u>aa</u>tin waqaddir fii <strong>al</strong>ssardi wa<strong>i</strong>\'maluu <u>shaa</u>li<u>h</u>an innii bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<strong>un</strong>', 'yaitu) buatlah baju besi yang besar-besar dan ukurlah anyamannya; dan kerjakanlah kebajikan. Sungguh, Aku Maha Melihat apa yang kamu kerjakan.'),
	(3618, 34, 12, 'وَلِسُلَيْمٰنَ الرِّيْحَ غُدُوُّهَا شَهْرٌ وَّرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهٗ عَيْنَ الْقِطْرِۗ وَمِنَ الْجِنِّ مَنْ يَّعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّهٖۗ وَمَنْ يَّزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيْرِ ', 'walisulaym<u>aa</u>na <strong>al</strong>rrii<u>h</u>a ghuduwwuh<u>aa</u> syahrun waraw<u>aah</u>uh<u>aa</u> syahrun wa-asaln<u>aa</u> lahu \'ayna <strong>a</strong>lqi<u>th</u>ri wamina <strong>a</strong>ljinni man ya\'malu bayna yadayhi bi-i<u>dz</u>ni rabbihi waman yazigh minhum \'an amrin<u>aa</u> nu<u>dz</u>iqhu min \'a<u>dzaa</u>bi <strong>al</strong>ssa\'iir<strong>i</strong>', 'Dan Kami (tundukkan) angin bagi Sulaiman, yang perjalanannya pada waktu pagi sama dengan perjalanan sebulan dan perjalanannya pada waktu sore sama dengan perjalanan sebulan (pula) dan Kami alirkan cairan tembaga baginya. Dan sebagian dari jin ada yang bekerja di hadapannya (di bawah kekuasaannya) dengan izin Tuhannya. Dan siapa yang menyimpang di antara mereka dari perintah Kami, Kami rasakan kepadanya azab neraka yang apinya menyala-nyala.'),
	(3619, 34, 13, 'يَعْمَلُوْنَ لَهٗ مَا يَشَاۤءُ مِنْ مَّحَارِيْبَ وَتَمَاثِيْلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُوْرٍ رّٰسِيٰتٍۗ اِعْمَلُوْٓا اٰلَ دَاوٗدَ شُكْرًا ۗوَقَلِيْلٌ مِّنْ عِبَادِيَ الشَّكُوْرُ ', 'ya\'maluuna lahu m<u>aa</u> yasy<u>aa</u>u min ma<u>haa</u>riiba watam<u>aa</u>tsiila wajif<u>aa</u>nin ka<strong>a</strong>ljaw<u>aa</u>bi waquduurin r<u>aa</u>siy<u>aa</u>tin i\'maluu <u>aa</u>la d<u>aa</u>wuuda syukran waqaliilun min \'ib<u>aa</u>diya <strong>al</strong>sysyakuur<strong>u</strong>', 'Mereka (para jin itu) bekerja untuk Sulaiman sesuai dengan apa yang dikehendakinya di antaranya (membuat) gedung-gedung yang tinggi, patung-patung, piring-piring yang (besarnya) seperti kolam dan periuk-periuk yang tetap (berada di atas tungku). Bekerjalah wahai keluarga Dawud untuk bersyukur (kepada Allah). Dan sedikit sekali dari hamba-hamba-Ku yang bersyukur.'),
	(3620, 34, 14, 'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِهٖٓ اِلَّا دَاۤبَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهٗ ۚفَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَّوْ كَانُوْا يَعْلَمُوْنَ الْغَيْبَ مَا لَبِثُوْا فِى الْعَذَابِ الْمُهِيْنِۗ ', 'falamm<u>aa</u> qa<u>dh</u>ayn<u>aa</u> \'alayhi <strong>a</strong>lmawta m<u>aa</u> dallahum \'al<u>aa</u> mawtihi ill<u>aa</u> d<u>aa</u>bbatu <strong>a</strong>l-ar<u>dh</u>i ta/kulu minsa-atahu falamm<u>aa</u> kharra tabayyanati <strong>a</strong>ljinnu an law k<u>aa</u>nuu ya\'lamuuna <strong>a</strong>lghayba m<u>aa</u> labitsuu fii <strong>a</strong>l\'a<u>dzaa</u>bi <strong>a</strong>lmuhiin<strong>i</strong>', 'Maka ketika Kami telah menetapkan kematian atasnya (Sulaiman), tidak ada yang menunjukkan kepada mereka kematiannya itu kecuali rayap yang memakan tongkatnya. Maka ketika dia telah tersungkur, tahulah jin itu bahwa sekiranya mereka mengetahui yang gaib tentu mereka tidak tetap dalam siksa yang menghinakan.'),
	(3621, 34, 15, 'لَقَدْ كَانَ لِسَبَاٍ فِيْ مَسْكَنِهِمْ اٰيَةٌ  ۚجَنَّتٰنِ عَنْ يَّمِيْنٍ وَّشِمَالٍ ەۗ كُلُوْا مِنْ رِّزْقِ رَبِّكُمْ وَاشْكُرُوْا لَهٗ  ۗبَلْدَةٌ طَيِّبَةٌ وَّرَبٌّ غَفُوْرٌ', 'laqad k<u>aa</u>na lisaba-in fii maskanihim <u>aa</u>yatun jannat<u>aa</u>ni \'an yamiinin wasyim<u>aa</u>lin kuluu min rizqi rabbikum wa<strong>u</strong>sykuruu lahu baldatun <u>th</u>ayyibatun warabbun ghafuur<strong>un</strong>', 'Sungguh, bagi kaum Saba’ ada tanda (kebesaran Tuhan) di tempat kediaman mereka yaitu dua buah kebun di sebelah kanan dan di sebelah kiri, (kepada mereka dikatakan), “Makanlah olehmu dari rezeki yang (dianugerahkan) Tuhanmu dan bersyukurlah kepada-Nya. (Negerimu) adalah negeri yang baik (nyaman) sedang (Tuhanmu) adalah Tuhan Yang Maha Pengampun.”'),
	(3622, 34, 16, 'فَاَعْرَضُوْا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنٰهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَّاَثْلٍ وَّشَيْءٍ مِّنْ سِدْرٍ قَلِيْلٍ ', 'fa-a\'ra<u>dh</u>uu fa-arsaln<u>aa</u> \'alayhim sayla <strong>a</strong>l\'arimi wabaddaln<u>aa</u>hum bijannatayhim jannatayni <u>dz</u>aw<u>aa</u>tay ukulin kham<u>th</u>in wa-atslin wasyay-in min sidrin qaliil<strong>in</strong>', 'Tetapi mereka berpaling, maka Kami kirim kepada mereka banjir yang besar dan Kami ganti kedua kebun mereka dengan dua kebun yang ditumbuhi (pohon-pohon) yang berbuah pahit, pohon Atsl dan sedikit pohon Sidr.'),
	(3623, 34, 17, 'ذٰلِكَ جَزَيْنٰهُمْ بِمَا كَفَرُوْاۗ وَهَلْ نُجٰزِيْٓ اِلَّا الْكَفُوْرَ ', '<u>dzaa</u>lika jazayn<u>aa</u>hum bim<u>aa</u> kafaruu wahal nuj<u>aa</u>zii ill<u>aa</u> <strong>a</strong>lkafuur<strong>a</strong>', 'Demikianlah Kami memberi balasan kepada mereka karena kekafiran mereka. Dan Kami tidak menjatuhkan azab (yang demikian itu), melainkan hanya kepada orang-orang yang sangat kafir.'),
	(3624, 34, 18, 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِيْ بٰرَكْنَا فِيْهَا قُرًى ظَاهِرَةً وَّقَدَّرْنَا فِيْهَا السَّيْرَۗ سِيْرُوْا فِيْهَا لَيَالِيَ وَاَيَّامًا اٰمِنِيْنَ ', 'waja\'aln<u>aa</u> baynahum wabayna <strong>a</strong>lqur<u>aa</u> <strong>al</strong>latii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> quran <u>zhaa</u>hiratan waqaddarn<u>aa</u> fiih<u>aa</u> <strong>al</strong>ssayra siiruu fiih<u>aa</u> lay<u>aa</u>liya wa-ayy<u>aa</u>man <u>aa</u>miniin<strong>a</strong>', 'Dan Kami jadikan antara mereka (penduduk Saba’) dan negeri-negeri yang Kami berkahi (Syam), beberapa negeri yang berdekatan dan Kami tetapkan antara negeri-negeri itu (jarak-jarak) perjalanan. Berjalanlah kamu di negeri-negeri itu pada malam dan siang hari dengan aman.'),
	(3625, 34, 19, 'فَقَالُوْا رَبَّنَا بٰعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُوْٓا اَنْفُسَهُمْ فَجَعَلْنٰهُمْ اَحَادِيْثَ وَمَزَّقْنٰهُمْ كُلَّ مُمَزَّقٍۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ ', 'faq<u>aa</u>luu rabban<u>aa</u> b<u>aa</u>\'id bayna asf<u>aa</u>rin<u>aa</u> wa<u>zh</u>alamuu anfusahum faja\'aln<u>aa</u>hum a<u>haa</u>diitsa wamazzaqn<u>aa</u>hum kulla mumazzaqin inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur<strong>in</strong>', 'Maka mereka berkata, “Ya Tuhan kami, jauhkanlah jarak perjalanan kami,” dan (berarti mereka) menzalimi diri mereka sendiri; maka Kami jadikan mereka bahan pembicaraan dan Kami hancurkan mereka sehancur-hancurnya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang yang sabar dan bersyukur.'),
	(3626, 34, 20, 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْلِيْسُ ظَنَّهٗ فَاتَّبَعُوْهُ اِلَّا فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ ', 'walaqad <u>sh</u>addaqa \'alayhim ibliisu <u>zh</u>annahu fa<strong>i</strong>ttaba\'uuhu ill<u>aa</u> fariiqan mina <strong>a</strong>lmu/miniin<strong>a</strong>', 'Dan sungguh, Iblis telah dapat meyakinkan terhadap mereka kebenaran sangkaannya, lalu mereka mengikutinya, kecuali sebagian dari orang-orang mukmin.'),
	(3627, 34, 21, 'وَمَا كَانَ لَهٗ عَلَيْهِمْ مِّنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُّؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا فِيْ شَكٍّ ۗوَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ ࣖ ', 'wam<u>aa</u> k<u>aa</u>na lahu \'alayhim min sul<u>th</u><u>aa</u>nin ill<u>aa</u> lina\'lama man yu/minu bi<strong>a</strong>l-<u>aa</u>khirati mimman huwa minh<u>aa</u> fii syakkin warabbuka \'al<u>aa</u> kulli syay-in <u>h</u>afii<u>zh</u><strong>un</strong>', 'Dan tidak ada kekuasaan (Iblis) terhadap mereka, melainkan hanya agar Kami dapat membedakan siapa yang beriman kepada adanya akhirat dan siapa yang masih ragu-ragu tentang  (akhirat) itu. Dan Tuhanmu Maha Memelihara segala sesuatu.'),
	(3628, 34, 22, 'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِ اللّٰهِۚ لَا يَمْلِكُوْنَ مِثْقَالَ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَمَا لَهُمْ فِيْهِمَا مِنْ شِرْكٍ وَّمَا لَهٗ مِنْهُمْ مِّنْ ظَهِيْرٍ ', 'quli ud\'uu <strong>al</strong>la<u>dz</u>iina za\'amtum min duuni <strong>al</strong>l<u>aa</u>hi l<u>aa</u> yamlikuuna mitsq<u>aa</u>la <u>dz</u>arratin fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i wam<u>aa</u> lahum fiihim<u>aa</u> min syirkin wam<u>aa</u> lahu minhum min <u>zh</u>ahiir<strong>in</strong>', 'Katakanlah (Muhammad), “Serulah mereka yang kamu anggap (sebagai tuhan) selain Allah! Mereka tidak memiliki (kekuasaan) seberat zarrah pun di langit dan di bumi, dan mereka sama sekali tidak mempunyai peran serta dalam (penciptaan) langit dan bumi dan tidak ada di antara mereka yang menjadi pembantu bagi-Nya.”'),
	(3629, 34, 23, 'وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهٗٓ اِلَّا لِمَنْ اَذِنَ لَهٗ ۗحَتّٰىٓ اِذَا فُزِّعَ عَنْ قُلُوْبِهِمْ قَالُوْا مَاذَاۙ قَالَ رَبُّكُمْۗ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَبِيْرُ ', 'wal<u>aa</u> tanfa\'u <strong>al</strong>sysyaf<u>aa</u>\'atu \'indahu ill<u>aa</u> liman a<u>dz</u>ina lahu <u>h</u>att<u>aa</u> i<u>dzaa</u> fuzzi\'a \'an quluubihim q<u>aa</u>luu m<u>aatsaa</u> q<u>aa</u>la rabbukum q<u>aa</u>luu <strong>a</strong>l<u>h</u>aqqa wahuwa <strong>a</strong>l\'aliyyu <strong>a</strong>lkabiir<strong>u</strong>', 'Dan syafaat (pertolongan) di sisi-Nya hanya berguna bagi orang yang telah diizinkan-Nya (memperoleh syafaat itu). Sehingga apabila telah dihilangkan ketakutan dari hati mereka, mereka berkata, “Apakah yang telah difirmankan oleh Tuhanmu?” Mereka menjawab, “(Perkataan) yang benar,” dan Dialah Yang Mahatinggi, Mahabesar.'),
	(3630, 34, 24, '۞ قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۙوَاِنَّآ اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ فِيْ ضَلٰلٍ مُّبِيْنٍ ', 'qul man yarzuqukum mina <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i quli <strong>al</strong>l<u>aa</u>hu wa-inn<u>aa</u> aw iyy<u>aa</u>kum la\'al<u>aa</u> hudan aw fii <u>dh</u>al<u>aa</u>lin mubiin<strong>in</strong>', 'Katakanlah (Muhammad), “Siapakah yang memberi rezeki kepadamu dari langit dan dari bumi?” Katakanlah, “Allah,” dan sesungguhnya kami atau kamu (orang-orang musyrik), pasti berada dalam kebenaran atau dalam kesesatan yang nyata.'),
	(3631, 34, 25, 'قُلْ لَّا تُسْـَٔلُوْنَ عَمَّآ اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُوْنَ ', 'qul l<u>aa</u> tus-aluuna \'amm<u>aa</u> ajramn<u>aa</u> wal<u>aa</u> nus-alu \'amm<u>aa</u> ta\'maluun<strong>a</strong>', 'Katakanlah, “Kamu tidak akan dimintai tanggung jawab atas apa yang kami kerjakan dan kami juga tidak akan dimintai tanggung jawab atas apa yang kamu kerjakan.”'),
	(3632, 34, 26, 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۗ وَهُوَ الْفَتَّاحُ الْعَلِيْمُ ', 'qul yajma\'u baynan<u>aa</u> rabbun<u>aa</u> tsumma yafta<u>h</u>u baynan<u>aa</u> bi<strong>a</strong>l<u>h</u>aqqi wahuwa <strong>a</strong>lfatt<u>aah</u>u <strong>a</strong>l\'aliim<strong>u</strong>', 'Katakanlah, “Tuhan kita akan mengumpulkan kita semua, kemudian Dia memberi keputusan antara kita dengan benar. Dan Dia Yang Maha Pemberi keputusan, Maha Mengetahui.”'),
	(3633, 34, 27, 'قُلْ اَرُوْنِيَ الَّذِيْنَ اَلْحَقْتُمْ بِهٖ شُرَكَاۤءَ كَلَّا ۗبَلْ هُوَ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ', 'qul aruuniya <strong>al</strong>la<u>dz</u>iina <strong>a</strong>l<u>h</u>aqtum bihi syurak<u>aa</u>-a kall<u>aa</u> bal huwa <strong>al</strong>l<u>aa</u>hu <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Katakanlah, “Perlihatkanlah kepadaku sembahan-sembahan yang kamu hubungkan dengan Dia sebagai sekutu-sekutu(-Nya), tidak mungkin! Sebenarnya Dialah Allah Yang Mahaperkasa, Mahabijaksana.”'),
	(3634, 34, 28, 'وَمَآ اَرْسَلْنٰكَ اِلَّا كَاۤفَّةً لِّلنَّاسِ بَشِيْرًا وَّنَذِيْرًا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ', 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> k<u>aa</u>ffatan li<strong>l</strong>nn<u>aa</u>si basyiiran wana<u>dz</u>iiran wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Dan Kami tidak mengutus engkau (Muhammad), melainkan kepada semua umat manusia sebagai pembawa berita gembira dan sebagai pemberi peringatan, tetapi kebanyakan manusia tidak mengetahui.'),
	(3635, 34, 29, 'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ', 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> <strong>a</strong>lwa\'du in kuntum <u>shaa</u>diqiin<strong>a</strong>', 'Dan mereka berkata, “Kapankah (datangnya) janji ini, jika kamu orang yang benar?”'),
	(3636, 34, 30, 'قُلْ لَّكُمْ مِّيْعَادُ يَوْمٍ لَّا تَسْتَأْخِرُوْنَ عَنْهُ سَاعَةً وَّلَا تَسْتَقْدِمُوْنَ ࣖ ', 'qul lakum mii\'<u>aa</u>du yawmin l<u>aa</u> tasta/khiruuna \'anhu s<u>aa</u>\'atan wal<u>aa</u> tastaqdimuun<strong>a</strong>', 'Katakanlah, “Bagimu ada hari yang telah dijanjikan (hari Kiamat), kamu tidak dapat meminta penundaan atau percepatannya sesaat pun.”'),
	(3637, 34, 31, 'وَقَالَ الَّذِيْنَ كَفَرُوْا لَنْ نُّؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذِيْ بَيْنَ يَدَيْهِۗ وَلَوْ تَرٰىٓ اِذِ الظّٰلِمُوْنَ مَوْقُوْفُوْنَ عِنْدَ رَبِّهِمْۖ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضِ ِۨالْقَوْلَۚ يَقُوْلُ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْن', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lan nu/mina bih<u>aadzaa</u> <strong>a</strong>lqur-<u>aa</u>ni wal<u>aa</u> bi<strong>a</strong>lla<u>dz</u>ii bayna yadayhi walaw tar<u>aa</u> i<u>dz</u>i <strong>al</strong><u>zhzhaa</u>limuuna mawquufuuna \'inda rabbihim yarji\'u ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in <strong>a</strong>lqawla yaquulu <strong>al</strong>la<u>dz</u>iina istu<u>dh</u>\'ifuu lilla<u>dz</u>iina istakbaruu lawl<u>aa</u> antum lakunn<u>aa</u> mu/miniin<strong>a</strong>', 'Dan orang-orang kafir berkata, “Kami tidak akan beriman kepada Al-Qur\'an ini dan tidak (pula) kepada Kitab yang sebelumnya.” Dan (alangkah mengerikan) kalau kamu melihat ketika orang-orang yang zalim itu dihadapkan kepada Tuhannya, sebagian mereka mengembalikan perkataan kepada sebagian yang lain; orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri, “Kalau tidaklah karena kamu tentulah kami menjadi orang-orang mukmin.”'),
	(3638, 34, 32, 'قَالَ الَّذِيْنَ اسْتَكْبَرُوْا لِلَّذِيْنَ اسْتُضْعِفُوْٓا اَنَحْنُ صَدَدْنٰكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَاۤءَكُمْ بَلْ كُنْتُمْ مُّجْرِمِيْنَ ', 'q<u>aa</u>la <strong>al</strong>la<u>dz</u>iina istakbaruu lilla<u>dz</u>iina istu<u>dh</u>\'ifuu ana<u>h</u>nu <u>sh</u>adadn<u>aa</u>kum \'ani <strong>a</strong>lhud<u>aa</u> ba\'da i<u>dz</u> j<u>aa</u>-akum bal kuntum mujrimiin<strong>a</strong>', 'Orang-orang yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah, “Kamikah yang telah menghalangimu untuk memperoleh petunjuk setelah petunjuk itu datang kepadamu? (Tidak!) Sebenarnya kamu sendirilah orang-orang yang berbuat dosa.”'),
	(3639, 34, 33, 'وَقَالَ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُوْنَنَآ اَنْ نَّكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهٗٓ اَنْدَادًا ۗوَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۗ وَجَعَلْنَا الْاَغْلٰلَ فِيْٓ اَ', 'waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina istu<u>dh</u>\'ifuu lilla<u>dz</u>iina istakbaruu bal makru <strong>al</strong>layli wa<strong>al</strong>nnah<u>aa</u>ri i<u>dz</u> ta/muruunan<u>aa</u> an nakfura bi<strong>al</strong>l<u>aa</u>hi wanaj\'ala lahu and<u>aa</u>dan wa-asarruu <strong>al</strong>nnad<u>aa</u>mata lamm<u>aa</u> ra-awuu <strong>a</strong>l\'a<u>dzaa</u>ba waja\'aln<u>aa</u> <strong>a</strong>l-aghl<u>aa</u>la fii a\'n<u>aa</u>qi <strong>al</strong>la<u>dz</u>iina kafaruu hal yujzawna ill<u>aa</u> m<u>aa</u> k<u>aa</u>nuu ya\'maluun<strong>a</strong>', 'Dan orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri, “(Tidak!) Sebenarnya tipu daya(mu) pada waktu malam dan siang (yang menghalangi kami), ketika kamu menyeru kami agar kami kafir kepada Allah dan menjadikan sekutu-sekutu bagi-Nya.” Mereka menyatakan penyesalan ketika mereka melihat azab. Dan Kami pasangkan belenggu di leher orang-orang yang kafir. Mereka tidak dibalas melainkan sesuai dengan apa yang telah mereka kerjakan.'),
	(3640, 34, 34, 'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍ ِالَّا قَالَ مُتْرَفُوْهَآ  ۙاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ', 'wam<u>aa</u> arsaln<u>aa</u> fii qaryatin min na<u>dz</u>iirin ill<u>aa</u> q<u>aa</u>la mutrafuuh<u>aa</u> inn<u>aa</u> bim<u>aa</u> ursiltum bihi k<u>aa</u>firuun<strong>a</strong>', 'Dan setiap Kami mengutus seorang pemberi peringatan kepada suatu negeri, orang-orang yang hidup mewah (di negeri itu) berkata, “Kami benar-benar mengingkari apa yang kamu sampaikan sebagai utusan.”'),
	(3641, 34, 35, 'وَقَالُوْا نَحْنُ اَكْثَرُ اَمْوَالًا وَّاَوْلَادًاۙ وَّمَا نَحْنُ بِمُعَذَّبِيْنَ ', 'waq<u>aa</u>luu na<u>h</u>nu aktsaru amw<u>aa</u>lan wa-awl<u>aa</u>dan wam<u>aa</u> na<u>h</u>nu bimu\'a<u>dzdz</u>abiin<strong>a</strong>', 'Dan mereka berkata, “Kami memiliki lebih banyak harta dan anak-anak (daripada kamu) dan kami tidak akan diazab.”'),
	(3642, 34, 36, 'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ࣖ', 'qul inna rabbii yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u wayaqdiru wal<u>aa</u>kinna aktsara <strong>al</strong>nn<u>aa</u>si l<u>aa</u> ya\'lamuun<strong>a</strong>', 'Katakanlah, “Sungguh, Tuhanku melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasinya (bagi siapa yang Dia kehendaki), tetapi kebanyakan manusia tidak mengetahui.”'),
	(3643, 34, 37, 'وَمَآ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ بِالَّتِيْ تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰىٓ اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًاۙ فَاُولٰۤىِٕكَ لَهُمْ جَزَاۤءُ الضِّعْفِ بِمَا عَمِلُوْا وَهُمْ فِى الْغُرُفٰتِ اٰمِنُوْنَ ', 'wam<u>aa</u> amw<u>aa</u>lukum wal<u>aa</u> awl<u>aa</u>dukum bi<strong>a</strong>llatii tuqarribukum \'indan<u>aa</u> zulf<u>aa</u> ill<u>aa</u> man <u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika lahum jaz<u>aa</u>u <strong>al</strong><u>dhdh</u>i\'fi bim<u>aa</u> \'amiluu wahum fii <strong>a</strong>lghuruf<u>aa</u>ti <u>aa</u>minuun<strong>a</strong>', 'Dan bukanlah harta atau anak-anakmu yang mendekatkan kamu kepada Kami; melainkan orang-orang yang beriman dan mengerjakan kebajikan, mereka itulah yang memperoleh balasan yang berlipat ganda atas apa yang telah mereka kerjakan; dan mereka aman sentosa di tempat-tempat yang tinggi (dalam surga).'),
	(3644, 34, 38, 'وَالَّذِيْنَ يَسْعَوْنَ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ ', 'wa<strong>a</strong>lla<u>dz</u>iina yas\'awna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika fii <strong>a</strong>l\'a<u>dzaa</u>bi mu<u>hd</u>aruun<strong>a</strong>', 'Dan orang-orang yang berusaha menentang ayat-ayat Kami untuk melemahkan (menggagalkan azab Kami), mereka itu dimasukkan ke dalam azab.'),
	(3645, 34, 39, 'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ  ۗوَمَآ اَنْفَقْتُمْ مِّنْ شَيْءٍ فَهُوَ يُخْلِفُهٗ ۚوَهُوَ خَيْرُ الرّٰزِقِيْنَ ', 'qul inna rabbii yabsu<u>th</u>u <strong>al</strong>rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lahu wam<u>aa</u> anfaqtum min syay-in fahuwa yukhlifuhu wahuwa khayru <strong>al</strong>rr<u>aa</u>ziqiin<strong>a</strong>', 'Katakanlah, “Sungguh, Tuhanku melapangkan rezeki dan membatasinya bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya.” Dan apa saja yang kamu infakkan, Allah akan menggantinya dan Dialah pemberi rezeki yang terbaik.'),
	(3646, 34, 40, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًا ثُمَّ يَقُوْلُ لِلْمَلٰۤىِٕكَةِ اَهٰٓؤُلَاۤءِ اِيَّاكُمْ كَانُوْا يَعْبُدُوْنَ ', 'wayawma ya<u>h</u>syuruhum jamii\'an tsumma yaquulu lilmal<u>aa</u>-ikati ah<u>aa</u>ul<u>aa</u>-i iyy<u>aa</u>kum k<u>aa</u>nuu ya\'buduun<strong>a</strong>', 'Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka semuanya kemudian Dia berfirman kepada para malaikat, “Apakah kepadamu mereka ini dahulu menyembah?”'),
	(3647, 34, 41, 'قَالُوْا سُبْحٰنَكَ اَنْتَ وَلِيُّنَا مِنْ دُوْنِهِمْ ۚبَلْ كَانُوْا يَعْبُدُوْنَ الْجِنَّ اَكْثَرُهُمْ بِهِمْ مُّؤْمِنُوْنَ ', 'q<u>aa</u>luu sub<u>haa</u>naka anta waliyyun<u>aa</u> min duunihim bal k<u>aa</u>nuu ya\'buduuna <strong>a</strong>ljinna aktsaruhum bihim mu/minuun<strong>a</strong>', 'Para malaikat itu menjawab, “Mahasuci Engkau. Engkaulah pelindung kami, bukan mereka; bahkan mereka telah menyembah jin; kebanyakan mereka beriman kepada jin itu.”'),
	(3648, 34, 42, 'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَّلَا ضَرًّا ۗوَنَقُوْلُ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ النَّارِ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ ', 'fa<strong>a</strong>lyawma l<u>aa</u> yamliku ba\'<u>dh</u>ukum liba\'<u>dh</u>in naf\'an wal<u>aa</u> <u>dh</u>arran wanaquulu lilla<u>dz</u>iina <u>zh</u>alamuu <u>dz</u>uuquu \'a<u>dzaa</u>ba <strong>al</strong>nn<u>aa</u>ri <strong>al</strong>latii kuntum bih<u>aa</u> tuka<u>dzdz</u>ibuun<strong>a</strong>', 'Maka pada hari ini sebagian kamu tidak kuasa (mendatangkan) manfaat maupun (menolak) mudarat kepada se-bagian yang lain. Dan Kami katakan kepada orang-orang yang zalim, “Rasakanlah olehmu azab neraka yang dahulu kamu dustakan.”'),
	(3649, 34, 43, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا رَجُلٌ يُّرِيْدُ اَنْ يَّصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُكُمْ ۚوَقَالُوْا مَا هٰذَآ اِلَّآ اِفْكٌ مُّفْتَرًىۗ وَقَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ اِن', 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>luu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> rajulun yuriidu an ya<u>sh</u>uddakum \'amm<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>ukum waq<u>aa</u>luu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> ifkun muftaran waq<u>aa</u>la <strong>al</strong>la<u>dz</u>iina kafaruu lil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin<strong>un</strong>', 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang, mereka berkata, “Orang ini tidak lain hanya ingin menghalang-halangi kamu dari apa yang disembah oleh nenek moyangmu,” dan mereka berkata, “(Al-Qur\'an) ini tidak lain hanyalah kebohongan yang diada-adakan saja.” Dan orang-orang kafir berkata terhadap kebenaran ketika kebenaran (Al-Qur\'an) itu datang kepada mereka, “Ini tidak lain hanyalah sihir yang nyata.”'),
	(3650, 34, 44, 'وَمَآ اٰتَيْنٰهُمْ مِّنْ كُتُبٍ يَّدْرُسُوْنَهَا وَمَآ اَرْسَلْنَآ اِلَيْهِمْ قَبْلَكَ مِنْ نَّذِيْرٍۗ ', 'wam<u>aa</u> <u>aa</u>tayn<u>aa</u>hum min kutubin yadrusuunah<u>aa</u> wam<u>aa</u> arsaln<u>aa</u> ilayhim qablaka min na<u>dz</u>iir<strong>in</strong>', 'Dan Kami tidak pernah memberikan kepada mereka kitab-kitab yang mereka baca dan Kami tidak pernah mengutus seorang pemberi peringatan kepada mereka sebelum engkau (Muhammad).'),
	(3651, 34, 45, 'وَكَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوْا مِعْشَارَ مَآ اٰتَيْنٰهُمْ فَكَذَّبُوْا رُسُلِيْۗ فَكَيْفَ كَانَ نَكِيْرِ ࣖ', 'waka<u>dzdz</u>aba <strong>al</strong>la<u>dz</u>iina min qablihim wam<u>aa</u> balaghuu mi\'sy<u>aa</u>ra m<u>aa</u> <u>aa</u>tayn<u>aa</u>hum faka<u>dzdz</u>abuu rusulii fakayfa k<u>aa</u>na nakiir<strong>i</strong>', 'Dan orang-orang yang sebelum mereka telah mendustakan (para rasul) sedang orang-orang (kafir Mekah) itu belum sampai menerima sepersepuluh dari apa yang telah Kami berikan kepada orang-orang terdahulu itu namun mereka mendustakan para rasul-Ku. Maka (lihatlah) bagaimana dahsyatnya akibat kemurkaan-Ku.'),
	(3652, 34, 46, '۞ قُلْ اِنَّمَآ اَعِظُكُمْ بِوَاحِدَةٍۚ اَنْ تَقُوْمُوْا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوْاۗ مَا بِصَاحِبِكُمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ لَّكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيْدٍ ', 'qul innam<u>aa</u> a\'i<u>zh</u>ukum biw<u>aah</u>idatin an taquumuu lill<u>aa</u>hi matsn<u>aa</u> wafur<u>aa</u>d<u>aa</u> tsumma tatafakkaruu m<u>aa</u> bi<u>shaah</u>ibikum min jinnatin in huwa ill<u>aa</u> na<u>dz</u>iirun lakum bayna yaday \'a<u>dzaa</u>bin syadiid<strong>in</strong>', 'Katakanlah, “Aku hendak memperingatkan kepadamu satu hal saja, yaitu agar kamu menghadap Allah (dengan ikhlas) berdua-dua atau sendiri-sendiri; kemudian agar kamu pikirkan (tentang Muhammad). Kawanmu itu tidak gila sedikit pun. Dia tidak lain hanyalah seorang pemberi peringatan bagi kamu sebelum (menghadapi) azab yang keras.”'),
	(3653, 34, 47, 'قُلْ مَا سَاَلْتُكُمْ مِّنْ اَجْرٍ فَهُوَ لَكُمْۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ', 'qul m<u>aa</u> sa-altukum min ajrin fahuwa lakum in ajriya ill<u>aa</u> \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi wahuwa \'al<u>aa</u> kulli syay-in syahiid<strong>un</strong>', 'Katakanlah (Muhammad), “Imbalan apa pun yang aku minta kepadamu, maka itu untuk kamu. Imbalanku hanyalah dari Allah, dan Dia Maha Mengetahui segala sesuatu.”'),
	(3654, 34, 48, 'قُلْ اِنَّ رَبِّيْ يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوْبِ ', 'qul inna rabbii yaq<u>dz</u>ifu bi<strong>a</strong>l<u>h</u>aqqi \'all<u>aa</u>mu <strong>a</strong>lghuyuub<strong>i</strong>', 'Katakanlah, “Sesungguhnya Tuhanku mewahyukan kebenaran. Dia Maha Mengetahui segala yang gaib.”'),
	(3655, 34, 49, 'قُلْ جَاۤءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيْدُ ', 'qul j<u>aa</u>-a <strong>a</strong>l<u>h</u>aqqu wam<u>aa</u> yubdi-u <strong>a</strong>lb<u>aath</u>ilu wam<u>aa</u> yu\'iid<strong>u</strong>', 'Katakanlah, “Kebenaran telah datang dan yang batil itu tidak akan memulai dan tidak (pula) akan mengulangi.”'),
	(3656, 34, 50, 'قُلْ اِنْ ضَلَلْتُ فَاِنَّمَآ اَضِلُّ عَلٰى نَفْسِيْۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوْحِيْٓ اِلَيَّ رَبِّيْۗ اِنَّهٗ سَمِيْعٌ قَرِيْبٌ ', 'qul in <u>dh</u>alaltu fa-innam<u>aa</u> a<u>dh</u>illu \'al<u>aa</u> nafsii wa-ini ihtadaytu fabim<u>aa</u> yuu<u>h</u>ii ilayya rabbii innahu samii\'un qariib<strong>un</strong>', 'Katakanlah, “Jika aku sesat maka sesungguhnya aku sesat untuk diriku sendiri; dan jika aku mendapat petunjuk maka itu disebabkan apa yang diwahyukan Tuhanku kepadaku. Sungguh, Dia Maha Mendengar, Maha-dekat.”'),
	(3657, 34, 51, 'وَلَوْ تَرٰىٓ اِذْ فَزِعُوْا فَلَا فَوْتَ وَاُخِذُوْا مِنْ مَّكَانٍ قَرِيْبٍۙ ', 'walaw tar<u>aa</u> i<u>dz</u> fazi\'uu fal<u>aa</u> fawta waukhi<u>dz</u>uu min mak<u>aa</u>nin qariib<strong>in</strong>', 'Dan (alangkah mengerikan) sekiranya engkau melihat mereka (orang-orang kafir) ketika terperanjat ketakutan (pada hari Kiamat); lalu mereka tidak dapat melepaskan diri dan mereka ditangkap dari tempat yang dekat (untuk dibawa ke neraka),'),
	(3658, 34, 52, 'وَّقَالُوْٓا اٰمَنَّا بِهٖۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ', 'waq<u>aa</u>luu <u>aa</u>mann<u>aa</u> bihi wa-ann<u>aa</u> lahumu <strong>al</strong>ttan<u>aa</u>wusyu min mak<u>aa</u>nin ba\'iid<strong>in</strong>', 'dan (ketika) mereka berkata, “Kami beriman kepada-Nya.” Namun bagaimana mereka dapat mencapai (keimanan) dari tempat yang jauh?'),
	(3659, 34, 53, 'وَقَدْ كَفَرُوْا بِهٖ مِنْ قَبْلُۚ وَيَقْذِفُوْنَ بِالْغَيْبِ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ', 'waqad kafaruu bihi min qablu wayaq<u>dz</u>ifuuna bi<strong>a</strong>lghaybi min mak<u>aa</u>nin ba\'iid<strong>in</strong>', 'Dan sungguh, mereka telah mengingkari Allah sebelum itu; dan mereka mendustakan tentang yang gaib dari tempat yang jauh.'),
	(3660, 34, 54, 'وَحِيْلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُوْنَۙ  كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا فِيْ شَكٍّ مُّرِيْبٍ ࣖ', 'wa<u>h</u>iila baynahum wabayna m<u>aa</u> yasytahuuna kam<u>aa</u> fu\'ila bi-asyy<u>aa</u>\'ihim min qablu innahum k<u>aa</u>nuu fii syakkin muriib<strong>in</strong>', 'Dan diberi penghalang antara mereka dengan apa yang mereka inginkan sebagaimana yang dilakukan terhadap orang-orang yang sepaham dengan mereka yang terdahulu. Sesungguhnya mereka dahulu (di dunia) dalam keraguan yang mendalam.'),
	(3661, 35, 1, 'اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ جَاعِلِ الْمَلٰۤىِٕكَةِ رُسُلًاۙ اُولِيْٓ اَجْنِحَةٍ مَّثْنٰى وَثُلٰثَ وَرُبٰعَۗ يَزِيْدُ فِى الْخَلْقِ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ', 'al<u>h</u>amdu lill<u>aa</u>hi f<u>aath</u>iri <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i j<u>aa</u>\'ili <strong>a</strong>lmal<u>aa</u>-ikati rusulan ulii ajni<u>h</u>atin matsn<u>aa</u> watsul<u>aa</u>tsa warub<u>aa</u>\'a yaziidu fii <strong>a</strong>lkhalqi m<u>aa</u> yasy<u>aa</u>u inna <strong>al</strong>l<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir<strong>un</strong>', 'Segala puji bagi Allah Pencipta langit dan bumi, yang menjadikan malaikat sebagai utusan-utusan (untuk mengurus berbagai macam urusan) yang mempunyai sayap, masing-masing (ada yang) dua, tiga dan empat. Allah menambahkan pada ciptaan-Nya apa yang Dia kehendaki. Sungguh, Allah Mahakuasa atas segala sesuatu.'),
	(3662, 35, 2, 'مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا ۚوَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهٗ مِنْۢ بَعْدِهٖۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ', 'm<u>aa</u> yafta<u>h</u>i <strong>al</strong>l<u>aa</u>hu li<strong>l</strong>nn<u>aa</u>si min ra<u>h</u>matin fal<u>aa</u> mumsika lah<u>aa</u> wam<u>aa</u> yumsik fal<u>aa</u> mursila lahu min ba\'dihi wahuwa <strong>a</strong>l\'aziizu <strong>a</strong>l<u>h</u>akiim<strong>u</strong>', 'Apa saja di antara rahmat Allah yang dianugerahkan kepada manusia, maka tidak ada yang dapat menahannya; dan apa saja yang ditahan-Nya maka tidak ada yang sanggup untuk melepaskannya setelah itu. Dan Dialah Yang Mahaperkasa, Mahabijaksana.'),
	(3663, 35, 3, 'يٰٓاَيُّهَا النَّاسُ اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْۗ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ  لَآ اِلٰهَ اِلَّا هُوَۖ فَاَنّٰى تُؤْفَكُوْنَ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su u<u>dz</u>kuruu ni\'mata <strong>al</strong>l<u>aa</u>hi \'alaykum hal min kh<u>aa</u>liqin ghayru <strong>al</strong>l<u>aa</u>hi yarzuqukum mina <strong>al</strong>ssam<u>aa</u>-i wa<strong>a</strong>l-ar<u>dh</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fa-ann<u>aa</u> tu/fakuun<strong>a</strong>', 'Wahai manusia! Ingatlah akan nikmat Allah kepadamu. Adakah pencipta selain Allah yang dapat memberikan rezeki kepadamu dari langit dan bumi? Tidak ada tuhan selain Dia; maka mengapa kamu berpaling (dari ketauhidan)?'),
	(3664, 35, 4, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ', 'wa-in yuka<u>dzdz</u>ibuuka faqad kut<u>hts</u>ibat rusulun min qablika wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi turja\'u <strong>a</strong>l-umuur<strong>u</strong>', 'Dan jika mereka mendustakan engkau (setelah engkau beri peringatan), maka sungguh, rasul-rasul sebelum engkau telah didustakan pula. Dan hanya kepada Allah segala urusan dikembalikan.'),
	(3665, 35, 5, 'يٰٓاَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su inna wa\'da <strong>al</strong>l<u>aa</u>hi <u>h</u>aqqun fal<u>aa</u> taghurrannakumu <strong>a</strong>l<u>h</u>ay<u>aa</u>tu <strong>al</strong>dduny<u>aa</u> wal<u>aa</u> yaghurrannakum bi<strong>al</strong>l<u>aa</u>hi <strong>a</strong>lgharuur<strong>u</strong>', 'Wahai manusia! Sungguh, janji Allah itu benar, maka janganlah kehidupan dunia memperdayakan kamu dan janganlah (setan) yang pandai menipu, memperdayakan kamu tentang Allah.'),
	(3666, 35, 6, 'اِنَّ الشَّيْطٰنَ لَكُمْ عَدُوٌّ فَاتَّخِذُوْهُ عَدُوًّاۗ اِنَّمَا يَدْعُوْا حِزْبَهٗ لِيَكُوْنُوْا مِنْ اَصْحٰبِ السَّعِيْرِۗ ', 'inna <strong>al</strong>sysyay<u>th</u><u>aa</u>na lakum \'aduwwun fa<strong>i</strong>ttakhi<u>dz</u>uuhu \'aduwwan innam<u>aa</u> yad\'uu <u>h</u>izbahu liyakuunuu min a<u>sh</u>-<u>haa</u>bi <strong>al</strong>ssa\'iir<strong>i</strong>', 'Sungguh, setan itu musuh bagimu, maka perlakukanlah ia sebagai musuh, karena sesungguhnya setan itu hanya mengajak golongannya agar mereka menjadi penghuni neraka yang menyala-nyala.'),
	(3667, 35, 7, 'اَلَّذِيْنَ كَفَرُوْا لَهُمْ عَذَابٌ شَدِيْدٌ ەۗ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ ࣖ ', '<strong>al</strong>la<u>dz</u>iina kafaruu lahum \'a<u>dzaa</u>bun syadiidun wa<strong>a</strong>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <strong>al</strong><u>shshaa</u>li<u>haa</u>ti lahum maghfiratun wa-ajrun kabiir<strong>un</strong>', 'Orang-orang yang kafir, mereka akan mendapat azab yang sangat keras. Dan orang-orang yang beriman dan mengerjakan kebajikan, mereka memperoleh ampunan dan pahala yang besar.'),
	(3668, 35, 8, 'اَفَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ فَرَاٰهُ حَسَنًاۗ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرٰتٍۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَصْنَعُوْنَ ', 'afaman zuyyina lahu suu-u \'amalihi fara<u>aa</u>hu <u>h</u>asanan fa-inna <strong>al</strong>l<u>aa</u>ha yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u fal<u>aa</u> ta<u>dz</u>hab nafsuka \'alayhim <u>h</u>asar<u>aa</u>tin inna <strong>al</strong>l<u>aa</u>ha \'aliimun bim<u>aa</u> ya<u>sh</u>na\'uun<strong>a</strong>', 'Maka apakah pantas orang yang dijadikan terasa indah perbuatan buruknya, lalu menganggap baik perbuatannya itu? Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan memberi petunjuk kepada siapa yang Dia kehendaki. Maka jangan engkau (Muhammad) biarkan dirimu binasa karena kesedihan terhadap mereka. Sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat.'),
	(3669, 35, 9, 'وَاللّٰهُ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَسُقْنٰهُ اِلٰى بَلَدٍ مَّيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ  كَذٰلِكَ النُّشُوْرُ ', 'wa<strong>al</strong>l<u>aa</u>hu <strong>al</strong>la<u>dz</u>ii arsala <strong>al</strong>rriy<u>aah</u>a fatutsiiru sa<u>haa</u>ban fasuqn<u>aa</u>hu il<u>aa</u> baladin mayyitin fa-a<u>h</u>yayn<u>aa</u> bihi <strong>a</strong>l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> ka<u>dzaa</u>lika <strong>al</strong>nnusyuur<strong>u</strong>', 'Dan Allah-lah yang mengirimkan angin; lalu (angin itu) menggerakkan awan, maka Kami arahkan awan itu ke suatu negeri yang mati (tandus) lalu dengan hujan itu Kami hidupkan bumi setelah mati (kering). Seperti itulah kebangkitan itu.'),
	(3670, 35, 10, 'مَنْ كَانَ يُرِيْدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَمِيْعًاۗ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهٗ ۗوَالَّذِيْنَ يَمْكُرُوْنَ السَّيِّاٰتِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَمَكْرُ اُولٰۤىِٕكَ هُوَ يَبُوْرُ ', 'man k<u>aa</u>na yuriidu <strong>a</strong>l\'izzata falill<u>aa</u>hi <strong>a</strong>l\'izzatu jamii\'an ilayhi ya<u>sh</u>\'adu <strong>a</strong>lkalimu <strong>al</strong><u>ththh</u>ayyibu wa<strong>a</strong>l\'amalu <strong>al</strong><u>shshaa</u>li<u>h</u>u yarfa\'uhu wa<strong>a</strong>lla<u>dz</u>iina yamkuruuna <strong>al</strong>ssayyi-<u>aa</u>ti lahum \'a<u>dzaa</u>bun syadiidun wamakru ul<u>aa</u>-ika huwa yabuur<strong>u</strong>', 'Barangsiapa menghendaki kemuliaan, maka (ketahuilah) kemuliaan itu semuanya milik Allah. Kepada-Nyalah akan naik perkataan-perkataan yang baik, dan amal kebajikan Dia akan mengangkatnya. Adapun orang-orang yang merencanakan kejahatan mereka akan mendapat azab yang sangat keras, dan rencana jahat mereka akan hancur.'),
	(3671, 35, 11, 'وَاللّٰهُ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجًاۗ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖۗ وَمَا يُعَمَّرُ مِنْ مُّعَمَّرٍ وَّلَا يُنْقَصُ مِنْ عُمُرِهٖٓ اِلَّا فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ ', 'wa<strong>al</strong>l<u>aa</u>hu khalaqakum min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma ja\'alakum azw<u>aa</u>jan wam<u>aa</u> ta<u>h</u>milu min unts<u>aa</u> wal<u>aa</u> ta<u>dh</u>a\'u ill<u>aa</u> bi\'ilmihi wam<u>aa</u> yu\'ammaru min mu\'ammarin wal<u>aa</u> yunqa<u>sh</u>u min \'umurihi ill<u>aa</u> fii kit<u>aa</u>bin inna <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi yasiir<strong>un</strong>', 'Dan Allah menciptakan kamu dari tanah kemudian dari air mani, kemudian Dia menjadikan kamu berpasangan (laki-laki dan perempuan). Tidak ada seorang perempuan pun yang mengandung dan melahirkan, melainkan dengan sepengetahuan-Nya. Dan tidak dipanjangkan umur seseorang dan tidak pula dikurangi umurnya, melainkan (sudah ditetapkan) dalam Kitab (Lauh Mahfuzh). Sungguh, yang demikian itu mudah bagi Allah.'),
	(3672, 35, 12, 'وَمَا يَسْتَوِى الْبَحْرٰنِۖ هٰذَا عَذْبٌ فُرَاتٌ سَاۤىِٕغٌ شَرَابُهٗ وَهٰذَا مِلْحٌ اُجَاجٌۗ وَمِنْ كُلٍّ تَأْكُلُوْنَ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْنَ حِلْيَةً تَلْبَسُوْنَهَا ۚوَتَرَى الْفُلْكَ فِيْهِ مَوَاخِرَ لِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّ', 'wam<u>aa</u> yastawii <strong>a</strong>lba<u>h</u>r<u>aa</u>ni h<u>aadzaa</u> \'a<u>dz</u>bun fur<u>aa</u>tun s<u>aa</u>-ighun syar<u>aa</u>buhu wah<u>aadzaa</u> mil<u>h</u>un uj<u>aa</u>jun wamin kullin ta/kuluuna la<u>h</u>man <u>th</u>ariyyan watastakhrijuuna <u>h</u>ilyatan talbasuunah<u>aa</u> watar<u>aa</u> <strong>a</strong>lfulka fiihi maw<u>aa</u>khira litabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun<strong>a</strong>', 'Dan tidak sama (antara) dua lautan; yang ini tawar, segar, sedap diminum dan yang lain asin lagi pahit. Dan dari (masing-masing lautan) itu kamu dapat memakan daging yang segar dan kamu dapat mengeluarkan perhiasan yang kamu pakai, dan di sana kamu melihat kapal-kapal berlayar membelah laut agar kamu dapat mencari karunia-Nya dan agar kamu bersyukur.'),
	(3673, 35, 13, 'يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۚ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ مَا يَمْلِكُوْنَ مِنْ قِطْمِيْرٍۗ ', 'yuuliju <strong>al</strong>layla fii <strong>al</strong>nnah<u>aa</u>ri wayuuliju <strong>al</strong>nnah<u>aa</u>ra fii <strong>al</strong>layli wasakhkhara <strong>al</strong>sysyamsa wa<strong>a</strong>lqamara kullun yajrii li-ajalin musamman <u>dzaa</u>likumu <strong>al</strong>l<u>aa</u>hu rabbukum lahu <strong>a</strong>lmulku wa<strong>a</strong>lla<u>dz</u>iina tad\'uuna min duunihi m<u>aa</u> yamlikuuna min qi<u>th</u>miir<strong>in</strong>', 'Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan menundukkan matahari dan bulan, masing-masing beredar menurut waktu yang ditentukan. Yang (berbuat) demikian itulah Allah Tuhanmu, milik-Nyalah segala kerajaan. Dan orang-orang yang kamu seru (sembah) selain Allah tidak mempunyai apa-apa walaupun setipis kulit ari.'),
	(3674, 35, 14, 'اِنْ تَدْعُوْهُمْ لَا يَسْمَعُوْا دُعَاۤءَكُمْۚ وَلَوْ سَمِعُوْا مَا اسْتَجَابُوْا لَكُمْۗ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُوْنَ بِشِرْكِكُمْۗ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيْرٍ ࣖ', 'in tad\'uuhum l<u>aa</u> yasma\'uu du\'<u>aa</u>-akum walaw sami\'uu m<u>aa</u> istaj<u>aa</u>buu lakum wayawma <strong>a</strong>lqiy<u>aa</u>mati yakfuruuna bisyirkikum wal<u>aa</u> yunabbi-uka mitslu khabiir<strong>in</strong>', 'Jika kamu menyeru mereka, mereka tidak mendengar seruanmu, dan sekiranya mereka mendengar, mereka juga tidak memperkenankan permintaanmu. Dan pada hari Kiamat mereka akan mengingkari kemusyrikanmu dan tidak ada yang dapat memberikan keterangan kepadamu seperti yang diberikan oleh (Allah) Yang Mahateliti.'),
	(3675, 35, 15, '۞ يٰٓاَيُّهَا النَّاسُ اَنْتُمُ الْفُقَرَاۤءُ اِلَى اللّٰهِ ۚوَاللّٰهُ هُوَ الْغَنِيُّ الْحَمِيْدُ ', 'y<u>aa</u> ayyuh<u>aa</u> <strong>al</strong>nn<u>aa</u>su antumu <strong>a</strong>lfuqar<u>aa</u>u il<u>aa</u> <strong>al</strong>l<u>aa</u>hi wa<strong>al</strong>l<u>aa</u>hu huwa <strong>a</strong>lghaniyyu <strong>a</strong>l<u>h</u>amiid<strong>u</strong>', 'Wahai manusia! Kamulah yang memerlukan Allah; dan Allah Dialah Yang Mahakaya (tidak memerlukan sesuatu), Maha Terpuji.'),
	(3676, 35, 16, 'اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۚ ', 'in yasya/ yu<u>dz</u>hibkum waya/ti bikhalqin jadiid<strong>in</strong>', 'Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu).'),
	(3677, 35, 17, 'وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ ', 'wam<u>aa</u> <u>dzaa</u>lika \'al<u>aa</u> <strong>al</strong>l<u>aa</u>hi bi\'aziiz<strong>in</strong>', 'Dan yang demikian itu tidak sulit bagi Allah.'),
	(3678, 35, 18, 'وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰى ۗوَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَّلَوْ كَانَ ذَا قُرْبٰىۗ اِنَّمَا تُنْذِرُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَ ۗوَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَك', 'wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> wa-in tad\'u mutsqalatun il<u>aa</u> <u>h</u>imlih<u>aa</u> l<u>aa</u> yu<u>h</u>mal minhu syay-un walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> innam<u>aa</u> tun<u>dz</u>iru <strong>al</strong>la<u>dz</u>iina yakhsyawna rabbahum bi<strong>a</strong>lghaybi wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta waman tazakk<u>aa</u> fa-innam<u>aa</u> yatazakk<u>aa</u> linafsihi wa-il<u>aa</u> <strong>al</strong>l<u>aa</u>hi <strong>a</strong>lma<u>sh</u>iir<strong>u</strong>', 'Dan orang yang berdosa tidak akan memikul dosa orang lain. Dan jika seseorang yang dibebani berat dosanya memanggil (orang lain) untuk memikul bebannya itu tidak akan dipikulkan sedikit pun, meskipun (yang dipanggilnya itu) kaum kerabatnya. Sesungguhnya yang dapat engkau beri peringatan hanya orang-orang yang takut kepada (azab) Tuhannya (sekalipun) mereka tidak melihat-Nya dan mereka yang melaksanakan salat. Dan barangsiapa menyucikan dirinya, sesungguhnya dia menyucikan diri untuk kebaikan dirinya sendiri. Dan kepada Allah-lah tempat kembali.'),
	(3679, 35, 19, 'وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ۙ ', 'wam<u>aa</u> yastawii <strong>a</strong>l-a\'m<u>aa</u> wa<strong>a</strong>lba<u>sh</u>iir<strong>u</strong>', 'Dan tidaklah sama orang yang buta dengan orang yang melihat,'),
	(3680, 35, 20, 'وَلَا الظُّلُمٰتُ وَلَا النُّوْرُۙ ', 'wal<u>aa</u> <strong>al</strong><u>zhzh</u>ulum<u>aa</u>tu wal<u>aa</u> <strong>al</strong>nnuur<strong>u</strong>', 'dan tidak (pula) sama gelap gulita dengan cahaya,'),
	(3681, 35, 21, 'وَلَا الظِّلُّ وَلَا الْحَرُوْرُۚ ', 'wal<u>aa</u> <strong>al</strong><u>zhzh</u>illu wal<u>aa</u> <strong>a</strong>l<u>h</u>aruur<strong>u</strong>', 'dan tidak (pula) sama yang teduh dengan yang panas,'),
	(3682, 35, 22, 'وَمَا يَسْتَوِى الْاَحْيَاۤءُ وَلَا الْاَمْوَاتُۗ اِنَّ اللّٰهَ يُسْمِعُ مَنْ يَّشَاۤءُ ۚوَمَآ اَنْتَ بِمُسْمِعٍ مَّنْ فِى الْقُبُوْرِ ', 'wam<u>aa</u> yastawii <strong>a</strong>l-a<u>h</u>y<u>aa</u>u wal<u>aa</u> <strong>a</strong>l-amw<u>aa</u>tu inna <strong>al</strong>l<u>aa</u>ha yusmi\'u man yasy<u>aa</u>u wam<u>aa</u> anta bimusmi\'in man fii <strong>a</strong>lqubuur<strong>i</strong>', 'dan tidak (pula) sama orang yang hi-dup dengan orang yang mati. Sungguh, Allah memberikan pendengaran kepada siapa yang Dia kehendaki dan engkau (Muhammad) tidak akan sanggup menjadikan orang yang di dalam kubur dapat mendengar.'),
	(3683, 35, 23, 'اِنْ اَنْتَ اِلَّا نَذِيْرٌ', 'in anta ill<u>aa</u> na<u>dz</u>iir<strong>un</strong>', 'Engkau tidak lain hanyalah seorang pemberi peringatan.'),
	(3684, 35, 24, 'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًا ۗوَاِنْ مِّنْ اُمَّةٍ اِلَّا خَلَا فِيْهَا نَذِيْرٌ ', 'inn<u>aa</u> arsaln<u>aa</u>ka bi<strong>a</strong>l<u>h</u>aqqi basyiiran wana<u>dz</u>iiran wa-in min ummatin ill<u>aa</u> khal<u>aa</u> fiih<u>aa</u> na<u>dz</u>iir<strong>un</strong>', 'Sungguh, Kami mengutus engkau dengan membawa kebenaran sebagai pembawa berita gembira dan sebagai pemberi peringatan. Dan tidak ada satu pun umat melainkan di sana telah datang seorang pemberi peringatan.'),
	(3685, 35, 25, 'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَبِالزُّبُرِ وَبِالْكِتٰبِ الْمُنِيْرِ ', 'wa-in yuka<u>dzdz</u>ibuuka faqad ka<u>dzdz</u>aba <strong>al</strong>la<u>dz</u>iina min qablihim j<u>aa</u>-at-hum rusuluhum bi<strong>a</strong>lbayyin<u>aa</u>ti wabi<strong>al</strong>zzuburi wabi<strong>a</strong>lkit<u>aa</u>bi <strong>a</strong>lmuniir<strong>i</strong>', 'Dan jika mereka mendustakanmu, maka sungguh, orang-orang yang sebelum mereka pun telah mendustakan (rasul-rasul); ketika rasul-rasulnya datang dengan membawa keterangan yang nyata (mukjizat), zubur, dan kitab yang memberi penjelasan yang sempurna.'),
	(3686, 35, 26, 'ثُمَّ اَخَذْتُ الَّذِيْنَ كَفَرُوْا فَكَيْفَ كَانَ نَكِيْرِ ࣖ ', 'tsumma akha<u>dz</u>tu <strong>al</strong>la<u>dz</u>iina kafaruu fakayfa k<u>aa</u>na nakiir<strong>i</strong>', 'Kemudian Aku azab orang-orang yang kafir; maka (lihatlah) bagaimana akibat kemurkaan-Ku.'),
	(3687, 35, 27, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ ثَمَرٰتٍ مُّخْتَلِفًا اَلْوَانُهَا ۗوَمِنَ الْجِبَالِ جُدَدٌ ۢبِيْضٌ وَّحُمْرٌ مُّخْتَلِفٌ اَلْوَانُهَا وَغَرَابِيْبُ سُوْدٌ ', 'alam tara anna <strong>al</strong>l<u>aa</u>ha anzala mina <strong>al</strong>ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi tsamar<u>aa</u>tin mukhtalifan <strong>a</strong>lw<u>aa</u>nuh<u>aa</u> wamina <strong>a</strong>ljib<u>aa</u>li judadun bii<u>dh</u>un wa<u>h</u>umrun mukhtalifun <strong>a</strong>lw<u>aa</u>nuh<u>aa</u> waghar<u>aa</u>biibu suud<strong>un</strong>', 'Tidakkah engkau melihat bahwa Allah menurunkan air dari langit lalu dengan air itu Kami hasilkan buah-buahan yang beraneka macam jenisnya. Dan di antara gunung-gunung itu ada garis-garis putih dan merah yang beraneka macam warnanya dan ada (pula) yang hitam pekat.'),
	(3688, 35, 28, 'وَمِنَ النَّاسِ وَالدَّوَاۤبِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهٗ كَذٰلِكَۗ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰۤؤُاۗ اِنَّ اللّٰهَ عَزِيْزٌ غَفُوْرٌ ', 'wamina <strong>al</strong>nn<u>aa</u>si wa<strong>al</strong>ddaw<u>aa</u>bbi wa<strong>a</strong>l-an\'<u>aa</u>mi mukhtalifun <strong>a</strong>lw<u>aa</u>nuhu ka<u>dzaa</u>lika innam<u>aa</u> yakhsy<u>aa</u> <strong>al</strong>l<u>aa</u>ha min \'ib<u>aa</u>dihi <strong>a</strong>l\'ulam<u>aa</u>u inna <strong>al</strong>l<u>aa</u>ha \'aziizun ghafuur<strong>un</strong>', 'Dan demikian (pula) di antara manusia, makhluk bergerak yang bernyawa dan hewan-hewan ternak ada yang bermacam-macam warnanya (dan jenisnya). Di antara hamba-hamba Allah yang takut kepada-Nya, hanyalah para ulama. Sungguh, Allah Mahaperkasa, Maha Pengampun.'),
	(3689, 35, 29, 'اِنَّ الَّذِيْنَ يَتْلُوْنَ كِتٰبَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً يَّرْجُوْنَ تِجَارَةً لَّنْ تَبُوْرَۙ ', 'inna <strong>al</strong>la<u>dz</u>iina yatluuna kit<u>aa</u>ba <strong>al</strong>l<u>aa</u>hi wa-aq<u>aa</u>muu <strong>al</strong><u>shsh</u>al<u>aa</u>ta wa-anfaquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan yarjuuna tij<u>aa</u>ratan lan tabuur<strong>a</strong>', 'Sesungguhnya orang-orang yang selalu membaca Kitab Allah (Al-Qur\'an) dan melaksanakan salat dan menginfakkan sebagian rezeki yang Kami anugerahkan kepadanya dengan diam-diam dan terang-terangan, mereka itu mengharapkan perdagangan yang tidak akan rugi,'),
	(3690, 35, 30, 'لِيُوَفِّيَهُمْ اُجُوْرَهُمْ وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ اِنَّهٗ غَفُوْرٌ شَكُوْرٌ ', 'liyuwaffiyahum ujuurahum wayaziidahum min fa<u>dh</u>lihi innahu ghafuurun syakuur<strong>un</strong>', 'agar Allah menyempurnakan pahalanya kepada mereka dan menambah karunia-Nya. Sungguh, Allah Maha Pengampun, Maha Mensyukuri.'),
	(3691, 35, 31, 'وَالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ مِنَ الْكِتٰبِ هُوَ الْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِۗ اِنَّ اللّٰهَ بِعِبَادِهٖ لَخَبِيْرٌۢ بَصِيْرٌ ', 'wa<strong>a</strong>lla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka mina <strong>a</strong>lkit<u>aa</u>bi huwa <strong>a</strong>l<u>h</u>aqqu mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi inna <strong>al</strong>l<u>aa</u>ha bi\'ib<u>aa</u>dihi lakhabiirun ba<u>sh</u>iir<strong>un</strong>', 'Dan apa yang telah Kami wahyukan kepadamu (Muhammad) yaitu Kitab (Al-Qur\'an) itulah yang benar, membenarkan kitab-kitab yang sebelumnya. Sungguh, Allah benar-benar Maha Mengetahui, Maha Melihat (keadaan) hamba-hamba-Nya.'),
	(3692, 35, 32, 'ثُمَّ اَوْرَثْنَا الْكِتٰبَ الَّذِيْنَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهٖ ۚوَمِنْهُمْ مُّقْتَصِدٌ ۚوَمِنْهُمْ سَابِقٌۢ بِالْخَيْرٰتِ بِاِذْنِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُۗ ', 'tsumma awratsn<u>aa</u> <strong>a</strong>lkit<u>aa</u>ba <strong>al</strong>la<u>dz</u>iina i<u>sth</u>afayn<u>aa</u> min \'ib<u>aa</u>din<u>aa</u> faminhum <u>zhaa</u>limun linafsihi waminhum muqta<u>sh</u>idun waminhum s<u>aa</u>biqun bi<strong>a</strong>lkhayr<u>aa</u>ti bi-i<u>dz</u>ni <strong>al</strong>l<u>aa</u>hi <u>dzaa</u>lika huwa <strong>a</strong>lfa<u>dh</u>lu <strong>a</strong>lkabiir<strong>u</strong>', 'Kemudian Kitab itu Kami wariskan kepada orang-orang yang Kami pilih di antara hamba-hamba Kami, lalu di antara mereka ada yang menzalimi diri sendiri, ada yang pertengahan dan ada (pula) yang lebih dahulu berbuat kebaikan dengan izin Allah. Yang demikian itu adalah karunia yang besar.'),
	(3693, 35, 33, 'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًا ۚوَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ', 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin walu/lu-an walib<u>aa</u>suhum fiih<u>aa</u> <u>h</u>ariir<strong>un</strong>', '(Mereka akan mendapat) surga ‘Adn, mereka masuk ke dalamnya, di dalamnya mereka diberi perhiasan gelang-gelang dari emas dan mutiara, dan pakaian mereka di dalamnya adalah sutera.'),
	(3694, 35, 34, 'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْٓ اَذْهَبَ عَنَّا الْحَزَنَۗ اِنَّ رَبَّنَا لَغَفُوْرٌ شَكُوْرٌۙ ', 'waq<u>aa</u>luu <strong>a</strong>l<u>h</u>amdu lill<u>aa</u>hi <strong>al</strong>la<u>dz</u>ii a<u>dz</u>haba \'ann<u>aa</u> <strong>a</strong>l<u>h</u>azana inna rabban<u>aa</u> laghafuurun syakuur<strong>un</strong>', 'Dan mereka berkata, “Segala puji bagi Allah yang telah menghilangkan kesedihan dari kami. Sungguh, Tuhan kami benar-benar Maha Pengampun, Maha Mensyukuri,'),
	(3695, 35, 35, ' ۨالَّذِيْٓ اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهٖۚ  لَا يَمَسُّنَا فِيْهَا نَصَبٌ وَّلَا يَمَسُّنَا فِيْهَا لُغُوْبٌ ', '<strong>al</strong>la<u>dz</u>ii a<u>h</u>allan<u>aa</u> d<u>aa</u>ra <strong>a</strong>lmuq<u>aa</u>mati min fa<u>dh</u>lihi l<u>aa</u> yamassun<u>aa</u> fiih<u>aa</u> na<u>sh</u>abun wal<u>aa</u> yamassun<u>aa</u> fiih<u>aa</u> lughuub<strong>un</strong>', 'yang dengan karunia-Nya menempatkan kami dalam tempat yang kekal (surga); di dalamnya kami tidak merasa lelah dan tidak pula merasa lesu.”'),
	(3696, 35, 36, 'وَالَّذِيْنَ كَفَرُوْا لَهُمْ نَارُ جَهَنَّمَۚ  لَا يُقْضٰى عَلَيْهِمْ فَيَمُوْتُوْا وَلَا يُخَفَّفُ عَنْهُمْ مِّنْ عَذَابِهَاۗ  كَذٰلِكَ نَجْزِيْ كُلَّ كَفُوْرٍ ۚ', 'wa<strong>a</strong>lla<u>dz</u>iina kafaruu lahum n<u>aa</u>ru jahannama l<u>aa</u> yuq<u>daa</u> \'alayhim fayamuutuu wal<u>aa</u> yukhaffafu \'anhum min \'a<u>dzaa</u>bih<u>aa</u> ka<u>dzaa</u>lika najzii kulla kafuur<strong>in</strong>', 'Dan orang-orang yang kafir, bagi mereka neraka Jahanam. Mereka tidak dibinasakan hingga mereka mati, dan tidak diringankan dari mereka azabnya. Demikianlah Kami membalas setiap orang yang sangat kafir.'),
	(3697, 35, 37, 'وَهُمْ يَصْطَرِخُوْنَ فِيْهَاۚ رَبَّنَآ اَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ اَوَلَمْ نُعَمِّرْكُمْ مَّا يَتَذَكَّرُ فِيْهِ مَنْ تَذَكَّرَ وَجَاۤءَكُمُ النَّذِيْرُۗ فَذُوْقُوْا فَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ', 'wahum ya<u>sth</u>arikhuuna fiih<u>aa</u> rabban<u>aa</u> akhrijn<u>aa</u> na\'mal <u>shaa</u>li<u>h</u>an ghayra <strong>al</strong>la<u>dz</u>ii kunn<u>aa</u> na\'malu awa lam nu\'ammirkum m<u>aa</u> yata<u>dz</u>akkaru fiihi man ta<u>dz</u>akkara waj<u>aa</u>-akumu <strong>al</strong>nna<u>dz</u>iiru fa<u>dz</u>uuquu fam<u>aa</u> li<strong>l</strong><u>zhzhaa</u>limiina min na<u>sh</u>iir<strong>in</strong>', 'Dan mereka berteriak di dalam neraka itu, “Ya Tuhan kami, keluarkanlah kami (dari neraka), niscaya kami akan mengerjakan kebajikan, yang berlainan dengan yang telah kami kerjakan dahulu.” (Dikatakan kepada mereka), “Bukankah Kami telah memanjangkan umurmu untuk dapat berpikir bagi orang yang mau berpikir, padahal telah datang kepadamu seorang pemberi peringatan? Maka rasakanlah (azab Kami), dan bagi orang-orang zalim tidak ada seorang penolong pun.”'),
	(3698, 35, 38, 'اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ', 'inna <strong>al</strong>l<u>aa</u>ha \'<u>aa</u>limu ghaybi <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>i innahu \'aliimun bi<u>dzaa</u>ti <strong>al</strong><u>shsh</u>uduur<strong>i</strong>', 'Sungguh, Allah mengetahui yang gaib (tersembunyi) di langit dan di bumi. Sungguh, Dia Maha Mengetahui segala isi hati.'),
	(3699, 35, 39, 'هُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِۗ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۗ وَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتًا ۚوَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ اِلَّا خَسَارًا ', 'huwa <strong>al</strong>la<u>dz</u>ii ja\'alakum khal<u>aa</u>-ifa fii <strong>a</strong>l-ar<u>dh</u>i faman kafara fa\'alayhi kufruhu wal<u>aa</u> yaziidu <strong>a</strong>lk<u>aa</u>firiina kufruhum \'inda rabbihim ill<u>aa</u> maqtan wal<u>aa</u> yaziidu <strong>a</strong>lk<u>aa</u>firiina kufruhum ill<u>aa</u> khas<u>aa</u>r<u>aa</u><strong>n</strong>', 'Dialah yang menjadikan kamu sebagai khalifah-khalifah di bumi. Barangsiapa kafir, maka (akibat) kekafirannya akan menimpa dirinya sendiri. Dan kekafiran orang-orang kafir itu hanya akan menambah kemurkaan di sisi Tuhan mereka. Dan kekafiran orang-orang kafir itu hanya akan menambah kerugian mereka belaka.'),
	(3700, 35, 40, 'قُلْ اَرَاَيْتُمْ شُرَكَاۤءَكُمُ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِۚ اَمْ اٰتَيْنٰهُمْ كِتٰبًا فَهُمْ عَلٰى بَيِّنَتٍ مِّنْهُۚ بَلْ اِنْ يَّعِدُ الظّٰلِمُوْنَ بَعْضُهُمْ', 'qul ara-aytum syurak<u>aa</u>-akumu <strong>al</strong>la<u>dz</u>iina tad\'uuna min duuni <strong>al</strong>l<u>aa</u>hi aruunii m<u>aatsaa</u> khalaquu mina <strong>a</strong>l-ar<u>dh</u>i am lahum syirkun fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti am <u>aa</u>tayn<u>aa</u>hum kit<u>aa</u>ban fahum \'al<u>aa</u> bayyinatin minhu bal in ya\'idu <strong>al</strong><u>zhzhaa</u>limuuna ba\'<u>dh</u>uhum ba\'<u>dh</u>an ill<u>aa</u> ghuruur<u>aa</u><strong>n</strong>', 'Katakanlah, “Terangkanlah olehmu tentang sekutu-sekutumu yang kamu seru selain Allah.” Perlihatkanlah kepada-Ku (bagian) manakah dari bumi ini yang telah mereka ciptakan; ataukah mereka mempunyai peran serta dalam (penciptaan) langit; atau adakah Kami memberikan kitab kepada mereka sehingga mereka mendapat keterangan-keterangan yang jelas darinya? Sebenarnya orang-orang zalim itu, sebagian mereka hanya menjanjikan tipuan belaka kepada sebagian yang lain.  '),
	(3701, 35, 41, '۞ اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوٰتِ وَالْاَرْضَ اَنْ تَزُوْلَا ەۚ وَلَىِٕنْ زَالَتَآ اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِّنْۢ بَعْدِهٖ ۗاِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا', 'inna <strong>al</strong>l<u>aa</u>ha yumsiku <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wa<strong>a</strong>l-ar<u>dh</u>a an tazuul<u>aa</u> wala-in z<u>aa</u>lat<u>aa</u> in amsakahum<u>aa</u> min a<u>h</u>adin min ba\'dihi innahu k<u>aa</u>na <u>h</u>aliiman ghafuur<u>aa</u><strong>n</strong>', 'Sungguh, Allah yang menahan langit dan bumi agar tidak lenyap; dan jika keduanya akan lenyap tidak ada seorang pun yang mampu menahannya selain Allah. Sungguh, Dia Maha Penyantun, Maha Pengampun.'),
	(3702, 35, 42, 'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَهُمْ نَذِيْرٌ لَّيَكُوْنُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَاۤءَهُمْ نَذِيْرٌ مَّا زَادَهُمْ اِلَّا نُفُوْرًاۙ ', 'wa-aqsamuu bi<strong>al</strong>l<u>aa</u>hi jahda aym<u>aa</u>nihim la-in j<u>aa</u>-ahum na<u>dz</u>iirun layakuununna ahd<u>aa</u> min i<u>h</u>d<u>aa</u> <strong>a</strong>l-umami falamm<u>aa</u> j<u>aa</u>-ahum na<u>dz</u>iirun m<u>aa</u> z<u>aa</u>dahum ill<u>aa</u> nufuur<u>aa</u><strong>n</strong>', 'Dan mereka bersumpah dengan nama Allah dengan sungguh-sungguh bahwa jika datang kepada mereka seorang pemberi peringatan, niscaya mereka akan lebih mendapat petunjuk dari salah satu umat-umat (yang lain). Tetapi ketika pemberi peringatan datang kepada mereka, tidak menambah (apa-apa) kepada mereka, bahkan semakin jauh mereka dari (kebenaran),'),
	(3703, 35, 43, ' ۨاسْتِكْبَارًا فِى الْاَرْضِ وَمَكْرَ السَّيِّئِۗ وَلَا يَحِيْقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِهٖ ۗفَهَلْ يَنْظُرُوْنَ اِلَّا سُنَّتَ الْاَوَّلِيْنَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْدِيْلًا ەۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْوِيْلًا', 'istikb<u>aa</u>ran fii <strong>a</strong>l-ar<u>dh</u>i wamakra <strong>al</strong>ssayyi-i wal<u>aa</u> ya<u>h</u>iiqu <strong>a</strong>lmakru <strong>al</strong>ssayyi-u ill<u>aa</u> bi-ahlihi fahal yan<u>zh</u>uruuna ill<u>aa</u> sunnata <strong>a</strong>l-awwaliina falan tajida lisunnati <strong>al</strong>l<u>aa</u>hi tabdiilan walan tajida lisunnati <strong>al</strong>l<u>aa</u>hi ta<u>h</u>wiil<u>aa</u><strong>n</strong>', 'karena kesombongan (mereka) di bumi dan karena rencana (mereka) yang jahat. Rencana yang jahat itu hanya akan menimpa orang yang merencanakannya sendiri. Mereka hanyalah menunggu (berlakunya) ketentuan kepada orang-orang yang terdahulu. Maka kamu tidak akan mendapatkan perubahan bagi Allah, dan tidak (pula) akan menemui penyimpangan bagi ketentuan Allah itu.'),
	(3704, 35, 44, 'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ وَكَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً ۗوَمَا كَانَ اللّٰهُ لِيُعْجِزَهٗ مِنْ شَيْءٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ اِنَّهٗ كَانَ عَلِيْمًا قَدِيْرًا ', 'awa lam yasiiruu fii <strong>a</strong>l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu <strong>al</strong>la<u>dz</u>iina min qablihim wak<u>aa</u>nuu asyadda minhum quwwatan wam<u>aa</u> k<u>aa</u>na <strong>al</strong>l<u>aa</u>hu liyu\'jizahu min syay-in fii <strong>al</strong>ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii <strong>a</strong>l-ar<u>dh</u>i innahu k<u>aa</u>na \'aliiman qadiir<u>aa</u><strong>n</strong>', 'Dan tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul), padahal orang-orang itu lebih besar kekuatannya dari mereka? Dan tidak ada sesuatu pun yang dapat melemahkan Allah baik di langit maupun di bumi. Sungguh, Dia Maha Mengetahui, Mahakuasa.'),
	(3705, 35, 45, 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوْا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِهٖ بَصِيْرًا ࣖ ', 'walaw yu-<u>aa</u>khi<u>dz</u>u <strong>al</strong>l<u>aa</u>hu <strong>al</strong>nn<u>aa</u>sa bim<u>aa</u> kasabuu m<u>aa</u> taraka \'al<u>aa</u> <u>zh</u>ahrih<u>aa</u> min d<u>aa</u>bbatin wal<u>aa</u>kin yu-akhkhiruhum il<u>aa</u> ajalin musamman fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum fa-inna <strong>al</strong>l<u>aa</u>ha k<u>aa</u>na bi\'ib<u>aa</u>dihi ba<u>sh</u>iir<u>aa</u><strong>n</strong>', 'Dan sekiranya Allah menghukum manusia disebabkan apa yang telah mereka perbuat, niscaya Dia tidak akan menyisakan satu pun makhluk bergerak yang bernyawa di bumi ini, tetapi Dia menangguhkan (hukuman)nya, sampai waktu yang sudah ditentukan. Nanti apabila ajal mereka tiba, maka Allah Maha Melihat (keadaan) hamba-hamba-Nya.');
INSERT INTO `tbl_master_surah` (`id`, `nomor`, `nama`, `nama_latin`, `jumlah_ayat`, `tempat_turun`, `arti`, `deskripsi`, `audio`) VALUES
	(1, 1, 'الفاتحة', 'Al-Fatihah', 7, 'mekah', 'Pembukaan', 'Surat <i>Al Faatihah</i> (Pembukaan) yang diturunkan di Mekah dan terdiri dari 7 ayat adalah surat yang pertama-tama diturunkan dengan lengkap  diantara surat-surat yang ada dalam Al Quran dan termasuk golongan surat Makkiyyah. Surat ini disebut <i>Al Faatihah</i> (Pembukaan), karena dengan surat inilah dibuka dan dimulainya Al Quran. Dinamakan <i>Ummul Quran</i> (induk Al Quran) atau <i>Ummul Kitaab</i> (induk Al Kitaab) karena dia merupakan induk dari semua isi Al Quran, dan karena itu diwajibkan membacanya pada tiap-tiap sembahyang.<br> Dinamakan pula <i>As Sab\'ul matsaany</i> (tujuh yang berulang-ulang) karena ayatnya tujuh dan dibaca berulang-ulang dalam sholat.', 'https://santrikoding.com/storage/audio/001.mp3'),
	(2, 2, 'البقرة', 'Al-Baqarah', 286, 'madinah', 'Sapi', 'Surat <i>Al Baqarah</i> yang 286 ayat itu turun di Madinah yang sebahagian besar diturunkan pada permulaan tahun Hijrah, kecuali ayat 281 diturunkan di Mina pada Hajji wadaa\' (hajji Nabi Muhammad s.a.w. yang terakhir). Seluruh ayat dari surat Al Baqarah termasuk golongan Madaniyyah, merupakan surat yang terpanjang di antara surat-surat Al Quran yang di dalamnya terdapat pula ayat yang terpancang (ayat 282). Surat ini dinamai <i>Al Baqarah</i> karena di dalamnya disebutkan kisah penyembelihan sapi betina yang diperintahkan Allah kepada Bani Israil (ayat 67 sampai dengan 74), dimana dijelaskan watak orang Yahudi pada umumnya. Dinamai <i>Fusthaatul-Quran</i> (puncak Al Quran) karena memuat beberapa hukum yang tidak disebutkan dalam surat yang lain. Dinamai juga surat  <i>alif-laam-miim</i> karena surat ini dimulai dengan Alif-laam-miim.', 'https://santrikoding.com/storage/audio/002.mp3'),
	(3, 3, 'اٰل عمران', 'Ali \'Imran', 200, 'madinah', 'Keluarga Imran', 'Surat <i>Ali \'Imran</i> yang terdiri dari 200 ayat ini adalah surat Madaniyyah.  Dinamakan <i>Ali \'Imran</i> karena memuat kisah keluarga \'Imran yang di dalam kisah itu disebutkan kelahiran Nabi Isa a.s., persamaan kejadiannya dengan Nabi Adam a. s., kenabian dan beberapa mukjizatnya, serta disebut pula kelahiran Maryam puteri \'Imran, ibu dari Nabi Isa a.s. Surat Al Baqarah dan Ali \'Imran ini dinamakan <i>Az Zahrawaani</i> (dua yang cemerlang), karena kedua surat ini menyingkapkan hal-hal yang disembunyikan oleh para Ahli Kitab, seperti kejadian dan kelahiran Nabi Isa a.s., kedatangan Nabi Muhammad s.a.w. dan sebagainya.', 'https://santrikoding.com/storage/audio/003.mp3'),
	(4, 4, 'النساۤء', 'An-Nisa\'', 176, 'madinah', 'Wanita', 'Surat An Nisaa\' yang terdiri dari 176 ayat itu, adalah surat Madaniyyah yang terpanjang sesudah surat Al Baqarah. Dinamakan <i>An Nisaa\'</i> karena dalam surat ini banyak dibicarakan hal-hal yang berhubungan dengan wanita serta merupakan surat yang paling membicarakan hal itu dibanding dengan surat-surat yang lain. Surat yang lain banyak juga yang membicarakan tentang hal wanita ialah surat Ath Thalaq. Dalam hubungan ini biasa disebut surat An Nisaa\' dengan sebutan: <i>Surat An Nisaa\' Al Kubraa</i> (surat An Nisaa\' yang besar), sedang  surat Ath Thalaq disebut dengan sebutan: <i>Surat An Nisaa\' Ash Shughraa</i> (surat An Nisaa\' yang kecil).', 'https://santrikoding.com/storage/audio/004.mp3'),
	(5, 5, 'الماۤئدة', 'Al-Ma\'idah', 120, 'madinah', 'Hidangan', 'Surat Al Maa\'idah terdiri dari 120 ayat; termasuk golongan surat Madaniyyah. Sekalipun ada ayatnya yang turun di Mekah, namun ayat ini diturunkan sesudah Nabi Muhammad s.a.w. hijrah ke Medinah, yaitu di waktu haji wadaa\'. Surat ini dinamakan <i>Al Maa\'idah</i> (hidangan) karena memuat kisah pengikut-pengikut setia Nabi Isa a.s. meminta kepada Nabi Isa a.s. agar Allah menurunkan untuk mereka Al Maa\'idah (hidangan makanan) dari langit (ayat 112). Dan dinamakan <i>Al Uqud</i> (perjanjian), karena kata itu terdapat pada ayat pertama surat ini, dimana Allah menyuruh agar hamba-hamba-Nya memenuhi janji prasetia terhadap Allah dan perjanjian-perjanjian yang mereka buat sesamanya. Dinamakan juga <i>Al Munqidz</i> (yang menyelamatkan), karena akhir surat ini mengandung kisah tentang Nabi Isa a.s. penyelamat pengikut-pengikut setianya dari azab Allah.', 'https://santrikoding.com/storage/audio/005.mp3'),
	(6, 6, 'الانعام', 'Al-An\'am', 165, 'mekah', 'Binatang Ternak', 'Surat Al An\'aam (binatang ternak: unta, sapi, biri-biri dan kambing) yang terdiri atas 165 ayat, termasuk golongan surat Makkiyah, karena hampur seluruh ayat-ayat-Nya diturunkan di Mekah dekat sebelum hijrah. Dinamakan <i>Al An\'aam</i> karena di dalamnya disebut kata <i>An\'aam</i> dalam hubungan dengan adat-istiadat kaum musyrikin, yang menurut mereka binatang-binatang ternak itu dapat dipergunakan untuk mendekatkan diri kepada tuhan mereka. Juga dalam surat ini disebutkan hukum-hukum yang berkenaan dengan binatang ternak itu.', 'https://santrikoding.com/storage/audio/006.mp3'),
	(7, 7, 'الاعراف', 'Al-A\'raf', 206, 'mekah', 'Tempat Tertinggi', 'Surat Al A\'raaf yang berjumlah 206 ayat termasuk golongan surat Makkiyah, diturunkan sebelum turunnya surat Al An\'aam dan termasuk golongan surat <i>Assab \'uththiwaal</i> (tujuh surat yang panjang). Dinamakan <i>Al A\'raaf</i> karena perkataan Al A\'raaf terdapat dalam ayat 46 yang mengemukakan tentang keadaan orang-orang yang berada di atas Al A\'raaf yaitu: tempat yang tertinggi di batas surga dan neraka.', 'https://santrikoding.com/storage/audio/007.mp3'),
	(8, 8, 'الانفال', 'Al-Anfal', 75, 'madinah', 'Rampasan Perang', 'Surat Al Anfaal terdiri atas 75 ayat dan termasuk golongan surat-surat Madaniyyah, karena seluruh ayat-ayatnya diturunkan di Madinah. Surat ini dinamakan <i>Al Anfaal</i> yang berarti harta rampasan perang berhubung kata <i>Al Anfaal</i> terdapat pada permulaan surat ini dan juga persoalan yang menonjol dalam surat ini ialah tentang harta rampasan perang, hukum perang dan hal-hal yang berhubungan dengan peperangan pada umumnya. Menurut riwayat Ibnu Abbas r.a. surat ini diturunkan berkenaan dengan perang Badar Kubra yang terjadi pada tahun kedua hijrah. Peperangan ini sangat penting artinya, karena dialah yang menentukan jalan sejarah Perkembangan Islam. Pada waktu itu umat Islam dengan berkekuatan kecil untuk pertama kali dapat mengalahkan kaum musyrikin yang berjumlah besar, dan berperlengkapan yang cukup, dan mereka dalam peperangan ini memperoleh harta rampasan perang yang tidak sedikit. Oleh sebab itu timbullah masalah bagaimana membagi harta-harta rampasan perang itu, maka kemudian Allah menurunkan ayat pertama dari surat ini.', 'https://santrikoding.com/storage/audio/008.mp3'),
	(9, 9, 'التوبة', 'At-Taubah', 129, 'madinah', 'Pengampunan', 'Surat At Taubah terdiri atas 129 ayat termasuk golongan surat-surat Madaniyyah. Surat ini dinamakan <i>At Taubah</i> yang berarti pengampunan berhubung kata <i>At Taubah</i> berulang kali disebut dalam surat ini. Dinamakan juga dengan <i>Baraah</i> yang berarti berlepas diri yang di sini maksudnya pernyataan pemutusan perhubungan, disebabkan kebanyakan pokok pembicaraannya tentang pernyataan pemutusan perjanjian damai dengan kaum musyrikin. <br> Di samping kedua nama yang masyhur itu ada lagi beberapa nama yang lain yang merupakan sifat dari surat ini. <br> Berlainan dengan surat-surat yang lain, maka pada permulaan surat ini tidak terdapat basmalah, karena surat ini adalah pernyataan perang dengan arti bahwa segenap kaum muslimin dikerahkan untuk memerangi seluruh kaum musyrikin, sedangkan basmalah bernafaskan perdamaian dan cinta kasih Allah. <br> Surat ini diturunkan sesudah Nabi Muhammad s.a.w. kembali dari peperangan Tabuk yang terjadi pada tahun 9 H. Pengumuman ini disampaikan oleh Saidina \'Ali r.a. pada musim haji tahun itu juga.', 'https://santrikoding.com/storage/audio/009.mp3'),
	(10, 10, 'يونس', 'Yunus', 109, 'mekah', 'Yunus', 'Surat Yunus terdiri atas 109 ayat, termasuk golongan surat-surat Makkiyyah kecuali ayat 40, 94, 95, yang diturunkan pada masa Nabi Muhmmad s.a.w. berada di Madinah. Surat ini dinamai <i>surat Yunus</i> karena dalam surat ini terutama ditampilkan kisah Nabi Yunus a.s. dan pengikut-pengikutnya yang teguh imannya.', 'https://santrikoding.com/storage/audio/010.mp3'),
	(11, 11, 'هود', 'Hud', 123, 'mekah', 'Hud', 'Surat Huud termasuk golongan surat-surat Makkiyyah, terdiri dari 123 ayat diturunkan sesudah surat Yunus. Surat ini dinamai <i>surat Huud</i> karena ada hubungan dengan terdapatnya kisah Nabi Huud a.s. dan kaumnya dalam surat ini terdapat juga kisah-kisah Nabi yang lain, seperti kisah Nuh a.s., Shaleh a.s., Ibrahim a.s., Luth a.s., Syu\'aib a.s. dan Musa a.s.', 'https://santrikoding.com/storage/audio/011.mp3'),
	(12, 12, 'يوسف', 'Yusuf', 111, 'mekah', 'Yusuf', ' Surat Yusuf ini terdiri atas 111 ayat, termasuk golongan surat-surat Makkiyyah karena diturunkan di Mekah sebelum hijrah. Surat ini dinamakan <i>surat Yusuf</i> adalah karena titik berat dari isinya mengenai riwayat Nabi Yusuf a.s.  Riwayat tersebut salah satu di antara cerita-cerita ghaib yang diwahyukan kepada Nabi Muhammad s.a.w. sebagai mukjizat bagi beliau, sedang beliau sebelum diturunkan surat ini tidak mengetahuinya.  Menurut riwayat Al Baihaqi dalam kitab <i>Ad Dalail</i> bahwa segolongan orang Yahudi masuk agama Islam sesudah mereka mendengar cerita Yusuf a.s. ini, karena sesuai dengan cerita-cerita yang mereka ketahui. Dari cerita Yusuf a.s. ini, Nabi Muhammad s.a.w. mengambil pelajaran-pelajaran yang banyak dan merupakan penghibur terhadap beliau dalam menjalankan tugasnya.', 'https://santrikoding.com/storage/audio/012.mp3'),
	(13, 13, 'الرّعد', 'Ar-Ra\'d', 43, 'madinah', 'Guruh', 'Surat Ar Ra\'d ini terdiri atas 43 ayat termasuk golongan surat-surat Makkiyyah. Surat ini dinamakan <i>Ar Ra\'d</i> yang berarti <i>guruh</i> karena dalam ayat 13 Allah berfirman yang artinya <i>Dan guruh itu bertasbih sambil memuji-Nya</i>, menunjukkan sifat kesucian dan kesempurnaan Allah s.w.t.  Dan lagi sesuai dengan sifat Al Quran yang mengandung ancaman dan harapan, maka demikian pulalah halnya bunyi guruh itu menimbulkan kecemasan dan harapan kepada manusia. Isi yang terpenting dari surat ini ialah bahwa bimbingan Allah kepada makhluk-Nya bertalian erat dengan hukum sebab dan akibat. Bagi Allah s.w.t. tidak ada pilih kasih dalam menetapkan hukuman. Balasan atau hukuman adalah akibat dan ketaatan atau keingkaran terhadap hukum Allah.', 'https://santrikoding.com/storage/audio/013.mp3'),
	(14, 14, 'ابرٰهيم', 'Ibrahim', 52, 'mekah', 'Ibrahim', 'Surat Ibrahim ini terdiri atas 52 ayat, termasuk golongan surat-surat Makkiyyah karena diturunkan di Mekah sebelum Hijrah. Dinamakan <i>Ibrahim</i>, karena surat ini mengandung doa Nabi Ibrahim a.s. yaitu ayat 35 sampai dengan 41. Do\'a ini isinya antara lain: permohonan agar keturunannya mendirikan shalat, dijauhkan dari menyembah berhala-berhala dan agar Mekah dan daerah sekitarnya menjadi daerah yang aman dan makmur. Doa Nabi Ibrahim a.s. ini telah diperkenankan oleh Allah s.w.t. sebagaimana telah terbukti keamanannya sejak dahulu sampai sekarang. Do\'a tersebut dipanjatkan beliau ke hadirat Allah s.w.t. sesudah selesai membina Ka\'bah bersama puteranya Ismail a.s., di dataran tanah Mekah yang tandus.', 'https://santrikoding.com/storage/audio/014.mp3'),
	(15, 15, 'الحجر', 'Al-Hijr', 99, 'mekah', 'Hijr', 'Surat ini terdiri atas 99 ayat, termasuk golongan surat-surat Makkiyyah,  karena diturunkan di Mekah sebelum hijrah. Al Hijr  adalah nama  sebuah daerah  pegunungan yang didiami zaman dahulu  oleh kaum  Tsamud terletak  di  pinggir  jalan antara Madinah dan Syam (Syria).<br> Nama surat ini diambil dari nama daerah pegunungan itu, berhubung nasib  penduduknya yaitu  kaum Tsamud  diceritakan pada ayat  80 sampai  dengan 84,  mereka  telah  dimusnahkan Allah  s.w.t.,  karena  mendustakan  Nabi  Shaleh  a.s.  dan berpaling   dari ayat-ayat  Allah. Dalam  surat ini terdapat juga kisah-kisah  kaum yang lain yang telah dibinasakan oleh Allah seperti  kaum Luth  a.s. dan kaum Syu\'aib a.s. Dari ke semua kisah-kisah  itu dapat  diambil pelajaran bahwa orang-orang  yang  menentang  ajaran  rasul-rasul  akan  mengalami kehancuran.', 'https://santrikoding.com/storage/audio/015.mp3'),
	(16, 16, 'النحل', 'An-Nahl', 128, 'mekah', 'Lebah', 'Surat ini terdiri atas 128 ayat, termasuk golongan surat-surat Makkiyyah. Surat ini dinamakan <i>An Nahl</i> yang berarti <i>lebah</i> karena di dalamnya, terdapat firman Allah s.w.t. ayat 68 yang artinya : "Dan Tuhanmu mewahyukan kepada lebah." Lebah adalah makhluk Allah yang banyak memberi manfaat  dan kenikmatan kepada manusia. Ada persamaan antara madu yang dihasilkan oleh lebah dengan Al Quranul Karim. Madu berasal dari bermacam-macam sari bunga dan dia menjadi obat bagi bermacam-macam penyakit manusia (lihat ayat 69). Sedang Al Quran mengandung inti sari dari kitab-kitab yang telah diturunkan kepada Nabi-nabi zaman dahulu ditambah dengan ajaran-ajaran yang diperlukan oleh semua bangsa sepanjang masa untuk mencapai kebahagiaan dunia dan akhirat. (Lihat surat (10) Yunus ayat 57 dan surat (17) Al Isra\' ayat  82). Surat ini dinamakan pula "An Ni\'am" artinya nikmat-nikmat, karena di dalamnya Allah menyebutkan pelbagai macam nikmat untuk hamba-hamba-Nya.', 'https://santrikoding.com/storage/audio/016.mp3'),
	(17, 17, 'الاسراۤء', 'Al-Isra\'', 111, 'mekah', 'Memperjalankan Malam Hari', 'Surat ini terdiri atas 111 ayat, termasuk golongan surat-surat Makkiyyah. Dinamakan dengan <i>Al Israa\'</i> yang berarti <i>memperjalankan di malam hari</i>, berhubung peristiwa Israa\' Nabi Muhammad s.a.w. di Masjidil Haram di Mekah ke Masjidil Aqsha di Baitul Maqdis dicantumkan pada ayat pertama dalam surat ini. Penuturan cerita Israa\' pada permulaan surat ini, mengandung isyarat bahwa Nabi Muhammad s.a.w. beserta umatnya kemudian hari akan mencapai martabat yang tinggi dan akan menjadi umat yang besar.<br> Surat ini dinamakan pula dengan <i>Bani Israil</i> artinya <i>keturunan Israil</i> berhubung dengan permulaan surat ini, yakni pada ayat kedua sampai dengan ayat kedelapan dan kemudian dekat akhir surat yakni pada ayat 101 sampai dengan ayat 104, Allah menyebutkan tentang Bani Israil yang setelah menjadi bangsa yang kuat lagi besar lalu menjadi bangsa yang terhina karena menyimpang dari ajaran Allah s.w.t. Dihubungkannya kisah Israa\' dengan riwayat <i>Bani Israil</i> pada surat ini, memberikan peringatan bahwa umat Islam akan mengalami keruntuhan, sebagaimana halnya Bani Israil, apabila mereka juga meninggalkan ajaran-ajaran agamanya.', 'https://santrikoding.com/storage/audio/017.mp3'),
	(18, 18, 'الكهف', 'Al-Kahf', 110, 'mekah', 'Goa', 'Surat  ini terdiri atas 110 ayat, termasuk  golongan  surat-surat Makkiyyah. Dinamai <i>Al-Kahfi</i> artinya <i>Gua</i> dan <i>Ashhabul Kahfi</i> yang artinya <i>Penghuni-Penghuni Gua</i>. Kedua nama ini diambil dari cerita yang terdapat dalam surat ini pada ayat 9 sampai dengan 26, tentang beberapa orang pemuda yang tidur dalam gua bertahun-tahun lamanya. Selain cerita tersebut, terdapat pula beberapa buah cerita dalam surat ini, yang kesemuanya mengandung i\'tibar dan pelajaran-pelajaran yang amat berguna bagi kehidupan manusia. Banyak hadist-hadist Rasulullah s.a.w. yang menyatakan keutamaan membaca surat ini.', 'https://santrikoding.com/storage/audio/018.mp3'),
	(19, 19, 'مريم', 'Maryam', 98, 'mekah', 'Maryam', 'Surat Maryam terdiri atas 98 ayat, termasuk golongan surat-surat Makkiyyah, karena hampir seluruh ayatnya diturunkan sebelum Nabi Muhammad s.a.w. hijrah ke Madinah, bahkan sebelum sahabat-sahabat beliau hijrah ke negeri Habsyi. Menurut riwayat Ibnu Mas\'ud, Ja\'far bin Abi Thalib membacakan permulaan surat Maryam ini kepada raja Najasyi dan pengikut-pengikutnya di waktu ia ikut hijrah bersama-sama sahabat-sahabat yang lain ke negeri Habsyi.<br>Surat ini dinamai <i>Maryam</i>, karena surat ini mengandung kisah Maryam, ibu Nabi Isa a.s. yang serba ajaib, yaitu melahirkan puteranya lsa a.s., sedang ia sebelumnya belum pernah dikawini atau dicampuri oleh seorang laki-laki pun. Kelahiran Isa a.s. tanpa bapa, merupakan suatu bukti kekuasaan Allah s.w.t.  Pengutaraan kisah Maryam sebagai kejadian yang luar biasa dan ajaib dalam surat ini, diawali dengan kisah kejadian yang luar biasa dan ajaib pula, yaitu dikabulkannya doa Zakaria a.s. oleh Allah s.w.t., agar beliau dianugerahi seorang putera sebagai pewaris dan pelanjut cita-cita dan kepercayaan beliau, sedang usia beliau sudah sangat tua dan isteri beliau seorang yang mandul yang menurut ukuran ilmu biologi tidak mungkin akan terjadi.', 'https://santrikoding.com/storage/audio/019.mp3'),
	(20, 20, 'طٰهٰ', 'Taha', 135, 'mekah', 'Taha', 'Surat Thaahaa terdiri atas 135 ayat, diturunkan sesudah diturunkannya surat Maryam, termasuk golongan surat-surat Makkiyyah. Surat ini dinamai <i>Thaahaa</i>, diambil dari perkataan yang berasal dan ayat pertama surat ini. Sebagaimana yang lazim terdapat pada surat-surat yang memakai huruf-huruf abjad pada permulaannya, di mana huruf tersebut seakan-akan merupakan pemberitahuan Allah kepada orang-orang yang membacanya, bahwa sesudah huruf itu akan dikemukakan hal-hal yang  sangat penting diketahui, maka demikian pula halnya dengan ayat-ayat yang terdapat sesudah huruf <i>thaahaa</i> dalam surat ini. Allah menerangkan bahwa Al Quran merupakan peringatan bagi manusia, wahyu dari Allah, Pencipta semesta alam. Kemudian Allah menerangkan kisah beberapa orang nabi; akibat-akibat yang telah ada akan dialami oleh orang-orang yang percaya kepada Allah dan orang-orang yang mengingkari-Nya, baik di dunia maupun di akhirat. Selain hal-hal tersebut di atas, maka surat ini mengandung pokok-pokok isi sebagai berikut:', 'https://santrikoding.com/storage/audio/020.mp3'),
	(21, 21, 'الانبياۤء', 'Al-Anbiya\'', 112, 'mekah', 'Para Nabi', 'Surat Al Anbiyaa\' yang terdiri atas 112 ayat, termasuk golongan surat Makkiyyah. Dinamai surat ini dengan <i>al anbiyaa\'</i>(nabi-nabi), karena surat ini mengutarakan kisah beberapa orang nabi. Permulaan surat Al Anbiyaa\' menegaskan bahwa manusia lalai dalam menghadapi hari berhisab, kemudian berhubung adanya pengingkaran kaum musyrik Mekah terhadap wahyu yang dibawa Nabi Muhammad s.a.w. maka ditegaskan Allah, kendatipun nabi-nabi itu manusia biasa, akan tetapi masing-masing mereka adalah manusia yang membawa wahyu yang pokok ajarannya adalah tauhid, dan keharusan manusia menyembah Allah Tuhan Penciptanya. Orang yang tidak mau mengakui kekuasaan Allah dan mengingkari ajaran yang dibawa oleh nabi-nabi itu, akan diazab Allah didunia dan di akhirat nanti. Kemudian dikemukakan kisah beberapa orang nabi dengan umatnya. Akhirnya surat itu ditutup dengan seruan agar kaum musyrik Mekah percaya kepada ajaran yang dibawa Muhammad s.a.w supaya tidak mengalami apa yang telah dialami oleh umat-umat yang dahulu.', 'https://santrikoding.com/storage/audio/021.mp3'),
	(22, 22, 'الحج', 'Al-Hajj', 78, 'madinah', 'Haji', 'Surat Al Hajj, termasuk golongan surat-surat Madaniyyah, terdiri atas 78 ayat, sedang menurut pendapat sebahagian ahli tafsir termasuk golongan surat-surat Makkiyah. Sebab perbedaan ini ialah karena sebahagian ayat-ayat surat ini ada yang diturunkan di Mekah dan sebahagian lagi diturunkan di Madinah. <br>Dinamai surat ini <i>Al Hajj</i>, karena surat ini mengemukakan hal-hal yang berhubungan dengan ibadat haji, seperti ihram, thawaf, sa\'i, wuquf di Arafah, mencukur rambut, syi\'ar-syi\'ar Allah, faedah-faedah dan hikmah-hikmah disyari\'atkannya haji. Ditegaskan pula bahwa ibadat haji itu telah disyari\'atkan di masa Nabi Ibrahim a.s., dan Ka\'bah didirikan oleh Nabi Ibrahim a.s. bersama puteranya Ismail a.s.<br>Menurut Al Ghaznawi, surat Al Hajj termasuk di antara surat- surat yang ajaib, diturunkan di malam dan di siang hari, dalam musafir dan dalam keadaan tidak musafir, ada ayat-ayat yang diturunkan di Mekah dan ada pula yang diturunkan di Madinah, isinya ada yang berhubungan dengan peperangan dan ada pula yang berhubungan dengan perdamaian, ada ayat-ayatnya yang muhkam dan ada pula yang mutasyabihaat.', 'https://santrikoding.com/storage/audio/022.mp3'),
	(23, 23, 'المؤمنون', 'Al-Mu\'minun', 118, 'mekah', 'Orang-Orang Mukmin', 'Surat Al Mu\'minuun terdiri atas 118 ayat, termasuk golongan surat-surat Makkiyyah. Dinamai <i>Al Mu\'minuun</i>, karena permulaan ayat ini manerangkan bagaimana seharusnya sifat-sifat orang mukmin yang menyebabkan keberuntungan mereka di akhirat dan ketenteraman jiwa mereka di dunia. Demikian tingginya sifat-sifat itu, hingga ia telah menjadi akhlak bagi Nabi Muhammad s.a.w.', 'https://santrikoding.com/storage/audio/023.mp3'),
	(24, 24, 'النّور', 'An-Nur', 64, 'madinah', 'Cahaya', 'Surat An Nuur terdiri atas 64 ayat, dan termasuk golongan surat-surat Madaniyah. Dinamai <i>An Nuur</i> yang berarti <i>Cahaya</i>, diambil dari kata An Nuur yang terdapat pada ayat ke 35. Dalam ayat ini, Allah s.w.t. menjelaskan tentang Nuur Ilahi, yakni Al Quran yang mengandung petunjuk-petunjuk. Petunjuk-petunjuk Allah itu, merupakan cahaya yang terang benderang menerangi alam semesta. Surat ini sebagian besar isinya memuat petunjuk- petunjuk Allah yang berhubungan dengan soal kemasyarakatan dan rumah tangga.', 'https://santrikoding.com/storage/audio/024.mp3'),
	(25, 25, 'الفرقان', 'Al-Furqan', 77, 'mekah', 'Pembeda', 'Surat ini terdiri atas 77 ayat, termasuk golongan surat-surat Makkiyah. Dinamai <i>Al Furqaan</i> yang artinya <i>pembeda</i>, diambil dari kata <i>Al Furqaan</i> yang terdapat pada ayat pertama surat ini. Yang dimaksud dengan Al Furqaan dalam ayat ini ialah Al Quran.<br> Al Quran dinamakan Al Furqaan karena dia membedakan antara yang haq dengan yang batil. MAka pada surat ini pun terdapat ayat-ayat yang membedakan antara kebenaran ke-esaan Allah s.w.t. dengan kebatilan kepercayaan syirik.', 'https://santrikoding.com/storage/audio/025.mp3'),
	(26, 26, 'الشعراۤء', 'Asy-Syu\'ara\'', 227, 'mekah', 'Para Penyair', 'Surat ini terdiri dari 227 ayat termasuk golongan surat-surat Makkiyyah. Dinamakan <i>Asy Syu\'araa\'</i> (kata jamak dari <i>Asy Syaa\'ir</i> yang berarti penyair) diambil dari kata <i>Asy Syuaraa\'</i> yang terdapat pada ayat 224, yaitu pada bagian terakhir surat ini, di kala Allah s.w.t. secara khusus menyebutkan kedudukan penyair- penyair. Para penyair-penyair itu mempunyai sifat-sifat yang jauh berbeda dengan para rasul-rasul; mereka diikuti oleh orang-orang yang sesat dan mereka suka memutar balikkan lidah dan mereka tidak mempunyai pendirian, perbuatan mereka tidak sesuai dengan tidak mempunyai pendirian, perbuatan mereka tidak sesuai dengan apa yang mereka ucapkan. Sifat-sifat yang demikian tidaklah sekali-kali terdapat pada rasul-rasul. Oleh karena demikian tidak patut bila Nabi Muhammad s.a.w. dituduh sebagai penyair, dan Al Quran dituduh sebagai syair, Al Quran adalah wahyu Allah, bukan buatan manusia.', 'https://santrikoding.com/storage/audio/026.mp3'),
	(27, 27, 'النمل', 'An-Naml', 93, 'mekah', 'Semut-semut', 'Surat An Naml terdiri atas 98 ayat, termasuk golongan surat- surat Makkiyyah dan diturunkan sesudah surat Asy Syu\'araa\'. Dinamai dengan <i>An Naml</i>, karena pada ayat 18 dan 19 terdapat perkataan <i>An Naml</i> (semut), di mana raja semut mengatakan kepada anak buahnya agar masuk sarangnya masing-masing, supaya jangan terpijak oleh Nabi Sulaiman a.s. dan tentaranya yang akan lalu di tempat itu. Mendengar perintah raja semut kepada anak buahnya itu, Nabi Sulaiman tersenyum dan ta\'jub atas keteraturan kerajaan semut itu dan beliau mengucapkan syukur kepada Tuhan Yang Maba Kuasa yang telah melimpahkan nikmat kepadanya, berupa kerajaan, kekayaan, memahami ucapan-ucapan binatang, mempunyai tentara yang terdiri atas jin, manusia, burung dan sebagainya. Nabi Sulaiman a.s. yang telah diberi Allah nikmat yang besar itu tidak merasa takabur dan sombong dan sebagai seorang hamba Allah mohon agar Allah memasukkannya ke dalam golongan orang-orang yang saleh. Allah s.w.t. menyebut binatang semut dalam surat ini agar manusia mengambil pelajaran dari kehidupan semut itu. Semut adalah binatang yang hidup berkelompok di dalam tanah, membuat liang dan ruang yang bertingkat-tingkat sebagai rumah dan gudang tempat menyimpan makanan musim dingin. Kerapian dan kedisiplinan yang terdapat dalam kerajaan semut ini, dinyatakan Allah dalam ayat ini dengan bagaimana rakyat semut mencari perlindungan segera agar jangan terpijak oleh Nabi Sulaiman a.s dan tentaranya, setelah menerima peringatan dari rajanya. Secara tidak langsung Allah mengingatkan juga kepada manusia agar dalam berusaha untuk mencukupkan kebutuhan sehari-hari, mementingkan pula kemaslahatan bersama dan sebagainya, rakyat semut mempunyai organisasi dan kerja sama yang baik pula. Dengan mengisahkan kisah Nabi Sulaiman a.s. dalam surat ini Allah mengisyaratkan hari depan dan kebesaran Nabi Muhammad s.a.w. Nabi Sulaiman a.s. sebagai seorang nabi, rasul dan raja yang dianugerahi kekayaan yang melimpah ruah, begitu pula Nabi Muhammad s.a.w. sebagai seorang nabi, rasul dan seoramg kepala negara yang ummi\' dan miskin akan berhasil membawa dan memimpin umatnya ke jalan Allah.', 'https://santrikoding.com/storage/audio/027.mp3'),
	(28, 28, 'القصص', 'Al-Qasas', 88, 'mekah', 'Kisah-Kisah', 'Surat Al Qashash terdiri atas 88 ayat termasuk golongan surat-surat Makkiyyah. Dinamai dengan <i>Al Qashash</i>, karena pada ayat 25 surat ini terdapat kata <i>Al Qashash</i> yang berarti <i>cerita</i>. Ayat ini menerangkan bahwa setelah Nabi Musa a.s. bertemu dengan Nabi Syua\'ib a.s. ia menceritakan cerita yang berhubungan  dengan dirinya sendiri, yakni pengalamannya dengan Fir\'aun, sampai waktu ia diburu oleh Fir\'aun karena membunuh seseorang dari bangsa Qibthi tanpa disengaja, Syua\'ib a.s. menjawab bahwa Musa a.s. telah selamat dari pengejaran  orang-orang zalim. Turunnya ayat 25 surat ini amat besar artinya bagi Nabi Muhammad s.a.w. dan bagi sahabat-sahabat yang melakukan hijrah ke Madinah, yang menambah keyakinan mereka, bahwa akhirnya orang-orang Islamlah yang menang, sebab ayat ini menunjukkan bahwa barangsiapa yang berhijrah dari tempat musuh untuk mempertahankan keimanan, pasti akan berhasil dalam perjuangannya menghadapi musuh-musuh agama. Kepastian kemenangan bagi kaum muslimin itu, ditegaskan pada bagian akhir surat ini yang mengandung bahwa setelah hijrah ke Madinah kaum muslimin akan kembali ke Mekah sebagai pemenang dan penegak agama Allah. Surat Al Qashash ini adalah surat yang paling lengkap memuat cerita Nabi Musa a.s. sehingga menurut suatu riwayat, surat ini dinamai juga dengan surat Musa.', 'https://santrikoding.com/storage/audio/028.mp3'),
	(29, 29, 'العنكبوت', 'Al-\'Ankabut', 69, 'mekah', 'Laba-Laba', 'Surat Al \'Ankabuut terdiri atas 69 ayat, termasuk golongan surat-surrat Makkiyah. Dinamai <i>Al \'Ankabuut</i> berhubung terdapatnya perkataan <i>Al \'Ankabuut</i> yang berarti <i>laba-laba</i> pada ayat 41 surat ini, dimana Allah mengumpamakan penyembah-penyembah berhala-berhala itu, dengan laba-laba yang percaya kepada kekuatan rumahnya sebagai tempat ia berlindung dan tempat ia menjerat mangsanya, padahal kalau dihembus angin atau ditimpa oleh suatu barang yang kecil saja, rumah itu akan hancur. Begitu pula halnya dengan kaum musyrikin yang percaya kepada kekuatan sembahan-sembahan mereka sebagai tempat berlindung dan tempat meminta sesuatu yang mereka ingini, padahal sembahan-sembahan mereka itu tidak mampu sedikit juga menolong mereka dari azab Allah waktu di dunia, seperti yang terjadi pada kaum Nuh, kaum Ibrahim, kaum Luth, kaum Syu\'aib, kaum Saleh, dan lain-lain. Apalagi menghadapi azab Allah di akhirat nanti, sembahan-sembahan mereka itu lebih tidak mampu menghindarkan dan melindungi mereka.', 'https://santrikoding.com/storage/audio/029.mp3'),
	(30, 30, 'الرّوم', 'Ar-Rum', 60, 'mekah', 'Romawi', 'Surat Ar Ruum terdiri atas 60 ayat, termasuk golongan surat-surat Makkiyah diturunkan sesudah ayat Al Insyiqaq. Dinamakan <i>Ar Ruum</i> karena pada permulaan surat ini, yaitu ayat 2, 3 dan 4 terdapat pemberitaan bangsa Rumawi yang pada mulanya dikalahkan oleh bangsa Persia, tetapi setelah beberapa tahun kemudian kerajaan Ruum dapat menuntut balas dan mengalahkan kerajaan Persia kembali. Ini adalah suatu mukjizat Al Quran, yaitu memberitakan hal-hal yang akan terjadi di masa yang akan datang. Dan juga suatu isyarat bahwa kaum muslimin yang demikian lemahnya di waktu itu akan menang dan dapat menghancurkan kaum musyrikin. Isyarat ini terbukti pertama kali pada perang Badar.', 'https://santrikoding.com/storage/audio/030.mp3'),
	(31, 31, 'لقمٰن', 'Luqman', 34, 'mekah', 'Luqman', 'Surat  Luqman  terdiri   dari   34   ayat,   termasuk   golongan   surat-surat Makkiyyah, diturunkan sesudah surat Ash Shaffaat. <br>Dinamai <i>Luqman</i> karena pada  ayat  12   disebutkan   bahwa   <i>Luqman</i>   telah diberi   oleh   Allah   nikmat   dan   ilmu   pengetahuan,  oleh sebab itu dia bersyukur kepadaNya atas nikmat yang  diberikan  itu.   Dan   pada   ayat   13 sampai 19 terdapat nasihat-nasihat Luqman kepada anaknya.<br>Ini adalah sebagai  isyarat   daripada   Allah   supaya   setiap   ibu   bapak melaksanakan  pula terhadap anak-anak mereka sebagai yang telah dilakukan oleh Luqman.', 'https://santrikoding.com/storage/audio/031.mp3'),
	(32, 32, 'السّجدة', 'As-Sajdah', 30, 'mekah', 'Sajdah', 'Surat As Sajdah terdiri atas 30 ayat termasuk golongan surat Makkiyah diturunkan sesudah surat Al Mu\'minuun. Dinamakan <i>As Sajdah</i> berhubung pada surat ini terdapat ayat sajdah, yaitu ayat yang kelima belas.', 'https://santrikoding.com/storage/audio/032.mp3'),
	(33, 33, 'الاحزاب', 'Al-Ahzab', 73, 'madinah', 'Golongan Yang Bersekutu', 'Surat Al Ahzab terdiri atas 73 ayat, termasuk golongan surat-surat Madaniyah, diturunkan sesudah surat Ali\'Imran. Dinamai <i>Al Ahzab</i> yang berarti <i>golongan-golongan yang bersekutu</i> karena dalam surat ini terdapat beberapa ayat, yaitu ayat 9 sampai dengan ayat 27 yang berhubungan dengan peperangan Al Ahzab, yaitu peperangan yang dilancarkan oleh orang-orang Yahudi, kaum munafik dan orang-orang musyrik terhadap orang-orang mukmin di Medinah. Mereka telah mengepung rapat orang- orang mukmin sehingga sebahagian dari mereka telah berputus asa dan menyangka bahwa mereka akan dihancurkan oleh musuh-musuh mereka itu. Ini adalah suatu ujian yang berat dari Allah untuk menguji sampai dimana teguhnya keimanan mereka. Akhirnya Allah mengirimkan bantuan berupa tentara yang tidak kelihatan dan angin topan, sehingga musuh-musuh itu menjadi kacau balau dan melarikan diri.', 'https://santrikoding.com/storage/audio/033.mp3'),
	(34, 34, 'سبأ', 'Saba\'', 54, 'mekah', 'Saba\'', 'Surat Saba\' terdiri atas 54 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Luqman. Dinamakan <i>Saba\'</i> karena didalamnya terdapat kisah kaum Saba\'. Saba\' adalah nama suatu kabilah dari kabilah-kabilah Arab yang tinggal di daerah Yaman sekarang ini. Mereka mendirikan kerajaan yang terkenal dengan nama kerajaan Sabaiyyah, ibukotanya Ma\'rib; telah dapat membangun suatu bendungan raksasa, yang bernama <i>Bendungan Ma\'rib</i>, sehingga negeri meka subur dan makmur. Kemewahan dan kemakmuran ini menyebabkan kaum Saba\' lupa dan ingkar kepada Allah yang telah melimpahkan nikmatnya kepada mereka, serta mereka mengingkari pula seruan para rasul. Karena keingkaran mereka ini, Allah menimpahkan kepada mereka azab berupa <i>sailul \'arim</i> (banjir yang besar) yang ditimbulkan oleh bobolnya bendungan Ma\'rib. Setelah bendungan ma\'rib bobol negeri Saba\' menjadi kering dan kerajaan mereka hancur.', 'https://santrikoding.com/storage/audio/034.mp3'),
	(35, 35, 'فاطر', 'Fatir', 45, 'mekah', 'Maha Pencipta', 'Surat Faathir terdiri atas 45 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Furqaan dan merupakan surat akhir dari urutan surat-surat dalam Al Quran yang dimulai dengan <i>Alhamdulillah</i>. Dinamakan <i>Faathir</i> (pencipta) ada hubungannya dengan perkataan <i>Faathir</i> yang terdapat pada ayat pertama pada surat ini. Pada ayat tersebut diterangkan bahwa Allah adalah Pencipta langit dan bumi, Pencipta malaikat-malaikat, Pencipta semesta alam yang semuanya itu adalah sebagai bukti atas kekuasaan dan kebesaran-Nya. Surat ini dinamai juga dengan <i>surat Malaikat</i> karena pada ayat pertama disebutkan bahwa Allah telah menjadikan malaikat-malaikat sebagai utusan-Nya  yang mempunyai beberapa sayap.', 'https://santrikoding.com/storage/audio/035.mp3'),
	(36, 36, 'يٰسۤ', 'Yasin', 83, 'mekah', 'Yasin', 'Surat <i>Yaasiin</i> terdiri atas 83 ayat, termasuk golongan surat-surat Makkiyyah,  diturunkan sesudah surat Jin. Dinamai <i>Yaasiin</i> karena dimulai dengan huruf <i>Yaasiin</i>. Sebagaimana halnya arti huruf-huruf abjad yang terletak pada permulaan beberapa surat Al Quran, maka demikian pula arti <i>Yaasiin</i> yang terdapat pada ayat permulaan surat ini, yaitu Allah mengisyaratkan bahwa sesudah huruf tersebut akan dikemukakan hal-hal yang penting antara lain: Allah bersumpah dengan Al Quran bahwa Muhammad s.a.w. benar-benar seorang rasul yang diutus-Nya kepada kaum yang belum pernah diutus kepada mereka rasul-rasul.', 'https://santrikoding.com/storage/audio/036.mp3'),
	(37, 37, 'الصّٰۤفّٰت', 'As-Saffat', 182, 'mekah', 'Barisan-Barisan', 'Surat Ash Shaaffaat terdiri atas 182 ayat termasuk golongan surat Makkiyyah diturunkan sesudah surat Al An\'aam. Dinamai dengan <i>Ash Shaaffaat</i> (yang bershaf-shaf) ada hubungannya dengan perkataan <i>Ash Shaaffaat</i> yang terletak pada ayat permulaan surat ini yang mengemukakan bagaimana para malaikat yang berbaris di hadapan Tuhannya yang bersih jiwanya, tidak dapat digoda oleh syaitan. Hal ini hendaklah menjadi i\'tibar bagi manusia dalam menghambakan dirinya kepada Allah.', 'https://santrikoding.com/storage/audio/037.mp3'),
	(38, 38, 'ص', 'Sad', 88, 'mekah', 'Sad', 'Surat Shaad  terdiri atas  88 ayat  termasuk golongan  surat Makkiyyah, diturunkan sesudah surat Al Qamar. Dinamai  dengan  <i>Shaad</i>  karena surat  ini  dimulai  dengan <i>Shaad</i> (selanjutnya lihat no. <a href="s002a001.htm">[10)</a>). Dalam surat  ini Allah  bersumpah dengan  Al Quran,   untuk menunjukkan bahwa Al Quran itu suatu  kitab yang  agung dan bahwa siapa saja yang  mengikutinya  akan mendapat  kebahagiaan  dunia  dan akhirat dan untuk menunjukkan bahwa Al Quran ini adalah mukjizat Nabi Muhammad s.a.w. yang  menyatakan kebenarannya dan ketinggian akhlaknya.', 'https://santrikoding.com/storage/audio/038.mp3'),
	(39, 39, 'الزمر', 'Az-Zumar', 75, 'mekah', 'Rombongan', 'Surat Az Zumar terdiri ataz 75 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Saba\'. Dinamakan <i>Az Zumar</i> (Rombongan-rombongan) karena perkataan <i>Az Zumar</i> yang terdapat pada ayat 71 dan 73 ini. Dalam ayat-ayat tersebut diterangkan keadaan manusia di hari kiamat setelah mereka dihisab, di waktu itu mereka terbagi atas dua rombongan; satu rombongan dibawa ke neraka dan satu rombongan lagi dibawa ke syurga. Masing- masing rombongan memperoleh balasan dari apa yang mereka kerjakan di dunia dahulu. Surat ini dinamakan juga <i>Al Ghuraf</i> (kamar-kamar) berhubung perkataan <i>ghuraf</i> yang terdapat pada ayat 20, dimana diterangkan keadaan kamar-kamar dalam syurga yang diperoleh orang-orang yang bertakwa.', 'https://santrikoding.com/storage/audio/039.mp3'),
	(40, 40, 'غافر', 'Gafir', 85, 'mekah', 'Maha Pengampun', 'Surat Al Mu\'min terdiri atas 85 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Az Zumar. Dinamai <i>Al Mu\'min</i> (Orang yang beriman), berhubung dengan perkataan <i>mukmin</i> yang terdapat pada ayat 28 surat ini. Pada ayat 28 diterangkan bahwa salah seorang dari kaum Fir\'aun telah beriman kepada Nabi Musa a.s. dengan menyembunyikan imannya kepada kaumnya, setelah mendengar keterangan dan melihat mukjizat yang dikemukakan oleh Nabi Musa a.s. Hati kecil orang ini mencela Fir\'aun dan kaumnya yang tidak mau beriman kepada Nabi Musa a.s., sekalipun telah dikemukakan keterangan dan mukjizat yang diminta mereka.<br>Dinamakan pula <i>Ghafir</i> (yang mengampuni), karena ada hubungannya dengan kalimat <i>Ghafir</i> yang terdapat pada ayat 3 surat ini. Ayat ini mengingatkan bahwa <i>Maha Pengampun</i> dan <i>Maha Penerima Taubat</i> adalah sebagian dari sifat-sifat Allah, karena itu hamba-hamba Allah tidak usah khawatir terhadap  perbuatan-perbuatan dosa yang telah terlanjur mereka lakukan, semuanya itu akan diampuni Allah asal benar-benar memohon ampun dan bertaubat kepada-Nya dan berjanji tidak akan mengerjakan  perbuatan-perbuatan dosa itu lagi. Dan surat ini dinamai <i>Dzit Thaul</i> (Yang Mempunyai Kurnia) karena perkataan tersebut terdapat pada ayat 3.', 'https://santrikoding.com/storage/audio/040.mp3'),
	(41, 41, 'فصّلت', 'Fussilat', 54, 'mekah', 'Yang Dijelaskan', 'Surat Fushshilat terdiri atas 54 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Mu\'min. Dinamai <i>Fushshilat</i> (yang dijelaskan) karena ada hubungannya dengan perkataan <i>Fushshilat</i> yang terdapat pada permulaan surat ini yang berarti <i>yang dijelaskan</i>. Maksudnya ayat-ayatnya diperinci dengan jelas tentang hukum-hukum, keimanan, janji dan ancaman, budi pekerti, kisah, dan sebagainya. Dinamai juga dengan <i>Haa Miim dan As Sajdah</i> karena surat ini dimulai dengan <i>Haa Miim</i> dan dalam surat ini terdapat ayat Sajdah.', 'https://santrikoding.com/storage/audio/041.mp3'),
	(42, 42, 'الشورى', 'Asy-Syura', 53, 'mekah', 'Musyawarah', 'Surat Asy Syuura terdiri atas 53 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Fushshilat. Dinamai dengan <i>Asy Syuura</i> (musyawarat) diambil dari perkataan <i>Syuura</i> yang terdapat pada ayat 38 surat ini. Dalam ayat tersebut diletakkan salah satu dari dasar-dasar pemerintahan Islam ialah musyawarat. Dinamai juga <i>Haa Miim \'Ain Siin Qaaf</i> karena surat ini dimulai dengan huruf-huruf hijaiyah itu.', 'https://santrikoding.com/storage/audio/042.mp3'),
	(43, 43, 'الزخرف', 'Az-Zukhruf', 89, 'mekah', 'Perhiasan', 'Surat Az Zukhruf terdiri atas 89 ayat, termasuk golongan  surat-surat Makkiyyah, diturunkan sesudah surat Asy Syuura. <br>Dinamai <i>Az Zukhruf</i> (Perhiasan) diambil dari perkataan <i>Az Zukhruf</i> yang terdapat pada ayat 35 surat ini.  Orang-orang musyrik mengukur tinggi rendahnya derajat seseorang tergantung kepada perhiasan dan harta benda yang ia punyai, karena Muhammad s.a.w. adalah seorang anak yatim lagi miskin, ia tidak pantas diangkat Allah sebagai seorang rasul dan nabi.  Pangkat rasul dan nabi harus diberikan kepada orang yang kaya.  Ayat ini menegaskan bahwa harta tidak dapat dijadikan dasar untuk mengukur tinggi rendahnya derajat seseorang, karena harta itu merupakan hiasan kehidupan duniawi, bukan berarti kesenangan akhirat.', 'https://santrikoding.com/storage/audio/043.mp3'),
	(44, 44, 'الدخان', 'Ad-Dukhan', 59, 'mekah', 'Kabut', 'Surat Ad Dukhaan terdiri atas 59 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah Az Zukhruf. <br>Dinamai <i>Ad Dukhaan</i> (kabut), diambil dari perkataan <i>Dukhaan</i> yang terdapat pada ayat 10 surat ini.<br>Menurut riwayat Bukhari secara ringkas dapat diterangkan sebagai berikut: Orang-orang kafir Mekah dalam menghalang-halangi agama Islam dan menyakiti serta mendurhakai Nabi Muhammad s.a.w. sudah melewati batas, karena itu Nabi mendoa kepada Allah agar diturunkan azab sebagaimana yang telah diturunkan kepada orang-orang yang durhaka kepada Nabi Yusuf yaitu musim kemarau yang panjang.  Do\'a Nabi itu dikabulkan Allah sampai orang-orang kafir memakan tulang dan bangkai, karena kelaparan.  Mereka selalu menengadah ke langit mengharap pertolongan Allah.  Tetapi tidak satupun yang mereka lihat kecuali kabut yang menutupi pandangan mereka.<br>Akhirnya mereka datang kepada Nabi agar Nabi memohon kepada Allah supaya hujan diturunkan.  Setelah Allah mengabulkan doa Nabi, dan hujan di turunkan, mereka kembali kafir seperti semula; karena itu Allah menyatakan bahwa nanti mereka akan diazab dengan azab yang pedih.', 'https://santrikoding.com/storage/audio/044.mp3'),
	(45, 45, 'الجاثية', 'Al-Jasiyah', 37, 'mekah', 'Berlutut', 'Surat Al Jaatsiyah terdiri atas 37 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Ad Dukhaan. Dinamai dengan <i>Al Jaatsiyah</i> (yang berlutut) diambil dari perkataan <i>Jaatsiyah</i> yang terdapat pada ayat 28 surat ini. Ayat tersebut menerangkan tentang keadaan manusia pada hari kiamat, yaitu semua manusia dikumpulkan ke hadapan mahkamah Allah Yang Maha Tinggi yang memberikan keputusan terhadap perbuatan yang telah mereka lakukan di dunia. Pada hari itu semua manusia berlutut di hadapan Allah. Dinamai juga dengan <i>Asy Syari\'ah</i> diambil dari perkataan <i>Syari\'ah</i> (Syari\'at) yang terdapat pada ayat 18 surat ini.', 'https://santrikoding.com/storage/audio/045.mp3'),
	(46, 46, 'الاحقاف', 'Al-Ahqaf', 35, 'mekah', 'Bukit Pasir', 'Surat Al Ahqaaf terdiri dari 35 ayat termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Jaatsiyah. Dinamai <i>Al Ahqaaf</i>  (bukit-bukit pasir) dari perkataan <i>Al Ahqaaf</i> yang terdapat pada ayat 21 surat ini.<br>Dalam ayat tersebut dan ayat-ayat sesudahnya diterangkan bahwa Nabi Hud a.s. telah menyampaikan risalahnya kepada kaumnya di <i>Al Ahqaaf</i> yang sekarang dikenal dengan Ar Rab\'ul Khaali, tetapi kaumnya tetap ingkar sekalipun mereka telah diberi peringatan pula oleh rasul-rasul yang sebelumnya.  Akhirnya Allah menghancurkan mereka dengan tiupan angin kencang.  Hal ini adalah sebagai isyarat dari Allah kepada kaum musyrikin Quraisy bahwa mereka akan dihancurkan bila mereka tidak mengindahkan seruan Rasul.', 'https://santrikoding.com/storage/audio/046.mp3'),
	(47, 47, 'محمّد', 'Muhammad', 38, 'madinah', 'Muhammad', 'Surat Muhammad terdiri atas 38 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Hadiid. Nama <i>Muhammad</i> sebagai nama surat ini diambil dari perkataan Muhammad yang terdapat pada ayat 2 surat ini. Pada ayat 1, 2, dan 3 surat ini, Allah membandingkan antara hasil yang diperoleh oleh orang-orang yang tidak percaya kepada apa yang diturunkan kepada Nabi Muhammad s.a.w dan hasil yang diperoleh oleh orang-orang yang tidak percaya kepadanya. Orang-orang yang percaya kepada apa yang dibawa oleh Muhammad s.a.w merekalah orang-orang yang beriman dan mengikuti yang hak, diterima Allah semua amalnya, diampuni segala kesalahannya. Adapun orang-orang yang tidak percaya kepada Muhammad s.a.w adalah orang-orang yang mengikuti kebatilan, amalnya tidak diterima, dosa mereka tidak diampuni, kepada mereka dijanjikan azab di dunia dan di akhirat.<br>Dinamai juga dengan <i>Al Qital</i> (peperangan), karena sebahagian besar surat ini mengutarakan tentang peperangan dan pokok-pokok hukumnya, serta bagaimana seharusnya sikap orang-orang mukmin terhadap orang-orang kafir.', 'https://santrikoding.com/storage/audio/047.mp3'),
	(48, 48, 'الفتح', 'Al-Fath', 29, 'madinah', 'Kemenangan', 'Surat Al Fath terdiri atas 29 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Jum\'ah. Dinamakan <i>Al Fath (kemenangan)</i> diambil dari perkataan <i>Fat-han</i> yang terdapat pada ayat pertama surat ini. Sebagian besar dari ayat-ayat surat ini menerangkan hal-hal yang berhubungan dengan kemenangan yang dicapai Nabi Muhammad s.a.w. dalam peperangan-peperangannya. <br>Nabi Muhammad s.a.w. sangat gembira dengan turunnya ayat pertama surat ini. Kegembiraan ini dinyatakan dalam sabda beliau yang diriwayatkan Bukhari; <i>Sesungguhnya telah diturunkan kepadaku satu surat, yang surat itu benar-benar lebih aku cintai dari seluruh apa yang disinari matahari</i>. Kegembiraan Nabi Muhammad s.a.w. itu ialah karena ayat-ayatnya menerangkan tentang kemenangan yang akan diperoleh Muhammad s.a.w. dalam perjuangannya dan tentang kesempurnaan nikmat Allah kepadanya.', 'https://santrikoding.com/storage/audio/048.mp3'),
	(49, 49, 'الحجرٰت', 'Al-Hujurat', 18, 'madinah', 'Kamar-Kamar', 'Surat Al Hujuraat terdiri atas 18 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Mujaadalah. Dinamai <i>Al Hujuraat</i> diambil dari perkataan <i>Al Hujuraat</i> yang terdapat pada ayat 4 surat ini. Ayat tersebut mencela para sahabat yang memanggil Nabi Muhammad SAW yang sedang berada di dalam kamar rumahnya bersama isterinya. Memanggil Nabi Muhammad SAW dengan cara dan dalam keadaan yang demikian menunjukkan sifat kurang hormat kepada beliau dan mengganggu ketenteraman beliau.', 'https://santrikoding.com/storage/audio/049.mp3'),
	(50, 50, 'ق', 'Qaf', 45, 'mekah', 'Qaf', 'Surat Qaaf terdiri atas 45 ayat, termasuk golongan surat-surat Makkiyah diturunkan sesudah surat Al Murssalaat. Dinamai <i>Qaaf</i> karena surat ini dimulai dengan huruf <i>Qaaf</i>. Menurut hadits yang diriwayatkan Imam Muslim, bahwa Rasulullah SAW senang membaca surat ini pada rakaat pertama sembahyang subuh dan pada shalat hari raya. Sedang menurut riwayat Abu Daud, Al Baihaqy dan Ibnu Majah bahwa Rasulullah SAW membaca surat ini pada tiap-tiap membaca Khutbah pada hari Jum\'at. Kedua riwayat ini menunjukkan bahwa surat QAAF sering dibaca Nabi Muhammad SAW di tempat-tempat umum, untuk memperingatkan manusia tentang kejadian mereka dan nikmat-nikmat yang diberikan kepadanya, begitu pula tentang hari berbangkit, hari berhisab, syurga, neraka, pahala, dosa, dsb. Surat ini dinamai juga <i>Al Baasiqaat</i>, diambil dari perkataan <i>Al- Baasiqaat</i> yang terdapat pada ayat 10 surat ini.', 'https://santrikoding.com/storage/audio/050.mp3'),
	(51, 51, 'الذّٰريٰت', 'Az-Zariyat', 60, 'mekah', 'Angin yang Menerbangkan', 'Surat Adz Dzaariyaat terdiri atas 60 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Ahqaaf. Dinamai <i>Adz Dzaariyaat</i> (angin yang menerbangkan), diambil dari perkataan <i>Adz Dzaariyaat</i> yang terdapat pada ayat pertama surat ini. Allah bersumpah dengan angin, mega, bahtera, dan malaikat yang menjadi sumber kesejahteraan dan pembawa kemakmuran. Hal ini meng- isyaratkan inayat Allah kepada hamba-hamba-Nya.', 'https://santrikoding.com/storage/audio/051.mp3'),
	(52, 52, 'الطور', 'At-Tur', 49, 'mekah', 'Bukit Tursina', 'Surat Ath Thuur terdiri atas 49 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat As Sajdah. Dinamai <i>Ath Thuur</i> (Bukit) diambil dari perkataan <i>Ath Thuur</i> yang terdapat pada ayat pertama surat ini. Yang dimaksud dengan <i>bukit</i> di sini ialah bukit Thursina yang terletak di semenanjung Sinai, tempat Nabi Musa menerima wahyu dari Tuhannya.', 'https://santrikoding.com/storage/audio/052.mp3'),
	(53, 53, 'النجم', 'An-Najm', 62, 'mekah', 'Bintang', 'Surat An Najm terdiri atas 62 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Ikhlash. Nama <i>An Najm</i> (bintang), diambil dari perkataan  <i>An Najm</i> yang terdapat pada ayat pertama surat ini. Allah bersumpah dengan <i>An Najm</i> (bintang) adalah karena bintang-bintang yang timbul dan tenggelam, amat besar manfaatnya bagi manusia, sebagai pedoman bagi manusia dalam melakukan pelayaran di lautan, dalam perjalanan di padang pasir, untuk menentukan peredaran musim dan sebagainya.', 'https://santrikoding.com/storage/audio/053.mp3'),
	(54, 54, 'القمر', 'Al-Qamar', 55, 'mekah', 'Bulan', 'Surat Al Qamar terdiri atas 55 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesedah surat Ath Thaariq. Nama <i>Al Qamar</i> (bulan) diambil dari perkataan <i>Al Qamar</i> yang terdapat pada ayat pertama surat ini. Pada ayat ini diterangkan tentang terbelahnya bulan sebagai mukjizat Nabi Muhammad s.a.w.', 'https://santrikoding.com/storage/audio/054.mp3'),
	(55, 55, 'الرحمن', 'Ar-Rahman', 78, 'madinah', 'Maha Pengasih', 'Surat Ar Rahmaan terdiri atas 78 ayat, termasuk golongan surat- surat Makkiyyah, diturunkan sesudah surat Ar Ra\'du. Dinamai <i>Ar Rahmaan</i> (Yang Maha Pemurah), diambil dari perkataan <i>Ar Rahmaan</i> yang terdapat pada ayat pertama surat ini. Ar Rahmaan adalah salah satu dari nama-nama Allah. Sebagian besar dari surat ini menerangkan kepemurahan Allah s.w.t. kepada hamba-hamba-Nya, yaitu dengan memberikan nikmat-nikmat yang tidak terhingga baik di dunia maupun di akhirat nanti.', 'https://santrikoding.com/storage/audio/055.mp3'),
	(56, 56, 'الواقعة', 'Al-Waqi\'ah', 96, 'mekah', 'Hari Kiamat', 'Surat Al Waaqi\'ah terdiri atas 96 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Thaa Haa. 	Dinamai dengan <i>Al Waaqi\'ah</i> (Hari Kiamat), diambil dari perkataan <i>Al Waaqi\'ah</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/056.mp3'),
	(57, 57, 'الحديد', 'Al-Hadid', 29, 'madinah', 'Besi', 'Surat Al Hadiid terdiri atas 29 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Az Zalzalah. Dinamai <i>Al Hadiid</i> (Besi), diambil dari perkataan <i>Al Hadiid</i> yang terdapat pada ayat 25 surat ini.', 'https://santrikoding.com/storage/audio/057.mp3'),
	(58, 58, 'المجادلة', 'Al-Mujadalah', 22, 'madinah', 'Gugatan', 'Surat Al Mujaadilah terdiri atas 22 ayat, termasuk golongan surat Madaniyyah, diturunkan sesudah surat Al Munaafiquun. Surat ini dinamai dengan <i>Al Mujaadilah</i> (wanita yang mengajukan gugatan) karena pada awal surat ini disebutkan bantahan seorang perempuan, menurut riwayat bernama Khaulah binti Tsa\'labah terhadap sikap suaminya yang telah menzhiharnya. Hal ini diadukan kepada Rasulullah s.a.w. dan ia menuntut supaya beliau memberikan putusan yang adil dalam persoalan itu. Dinamai juga <i>Al Mujaadalah</i> yang berarti <i>perbantahan</i>.', 'https://santrikoding.com/storage/audio/058.mp3'),
	(59, 59, 'الحشر', 'Al-Hasyr', 24, 'madinah', 'Pengusiran', 'Surat Al Hasyr terdiri atas 24 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Bayyinah.<br> 	Dinamai surat <i>Al Hasyr</i> (pengusiran) diambil dari perkataan <i>Al-Hasyr</i> yang terdapat pada ayat 2 surat ini. Di dalam surat ini disebutkan  kisah pengusiran suatu suku Yahudi yang bernama Bani Nadhir yang berdiam  di sekitar kota Madinah.', 'https://santrikoding.com/storage/audio/059.mp3'),
	(60, 60, 'الممتحنة', 'Al-Mumtahanah', 13, 'madinah', 'Wanita Yang Diuji', 'Surat Al Mumtahanah terdiri atas 13 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Ahzab. Dinamai <i>Al Mumtahanah</i> (wanita yang diuji), diambil dari kata "Famtahinuuhunna" yang berarti <i>maka ujilah mereka</i>, yang terdapat pada ayat 10 surat ini.', 'https://santrikoding.com/storage/audio/060.mp3'),
	(61, 61, 'الصّفّ', 'As-Saff', 14, 'madinah', 'Barisan', 'Surat Ash Shaff terdiri atas 14 ayat termasuk golongan surat-surat Madaniyyah. Dinamai dengan <i>Ash Shaff</i>, karena pada ayat 4 surat ini terdapat kata <i>Shaffan</i> yang berarti <i>satu barisan</i>. Ayat ini menerangkan apa yang diridhai Allah sesudah menerangkan apa yang dimurkai-Nya. Pada ayat 3 diterangkan bahwa Allah murka kepada orang yang hanya pandai berkata saja tetapi tidak melaksanakan apa yang diucapkannya. Dan pada ayat 4 diterangkan bahwa Allah menyukai orang yang mempraktekkan apa yang diucapkannya yaitu orang-orang yang berperang pada jalan Allah dalam satu barisan.', 'https://santrikoding.com/storage/audio/061.mp3'),
	(62, 62, 'الجمعة', 'Al-Jumu\'ah', 11, 'madinah', 'Jumat', 'Surat Al Jumu\'ah ini terdiri atas 11 ayat, termasuk golongan-golongan surat-surat Madaniyyah dan diturunkan sesudah surat Ash Shaf. Nama surat Al Jumu\'ah diambil dari kata <i>Al Jumu\'ah</i> yang terdapat pada ayat 9 surat ini yang artinya: <i>hari Jum\'at</i>.', 'https://santrikoding.com/storage/audio/062.mp3'),
	(63, 63, 'المنٰفقون', 'Al-Munafiqun', 11, 'madinah', 'Orang-Orang Munafik', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Hajj. Surat ini dinamai <i>Al-Munaafiquun</i>  yang artinya orang-orang munafik, karena surat ini mengungkapkan  sifat-sifat orang-orang munafik.', 'https://santrikoding.com/storage/audio/063.mp3'),
	(64, 64, 'التغابن', 'At-Tagabun', 18, 'madinah', 'Pengungkapan Kesalahan', 'Surat ini terdiri atas 18 ayat, termasuk golongan surat-surat Madaniyyah dan diturunkan sesudah surat At Tahrim. Nama <i>At Taghaabun</i> diambil dari kata <i>At Taghaabun</i> yang terdapat pada ayat ke 9 yang artinya <i>hari dinampakkan kesalahan-kesalahan</i>.', 'https://santrikoding.com/storage/audio/064.mp3'),
	(65, 65, 'الطلاق', 'At-Talaq', 12, 'madinah', 'Talak', 'Surat ini terdiri atas 12 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Insaan. Dinamai surat <i>Ath Thalaaq</i> karena kebanyakan ayat-ayatnya mengenai masalah talak dan yang berhubungan dengan masalah itu.', 'https://santrikoding.com/storage/audio/065.mp3'),
	(66, 66, 'التحريم', 'At-Tahrim', 12, 'madinah', 'Pengharaman', 'Surat ini terdiri atas 12 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Al Hujuraat. Dinamai surat <i>At Tahrim</i> karena pada awal surat ini terdapat kata <i>tuharrim</i> yang kata asalnya adalah Attahrim yang berarti <i>mengharamkan</i>.', 'https://santrikoding.com/storage/audio/066.mp3'),
	(67, 67, 'الملك', 'Al-Mulk', 30, 'mekah', 'Kerajaan', 'Surat ini terdiri atas 30 ayat, termasuk golongan surat-surat  Makkiyah, diturunkan sesudah Ath Thuur.<br> Nama <i>Al Mulk</i> diambil dari kata <i>Al Mulk</i> yang terdapat pada ayat pertama surat ini yang artinya kerajaan atau kekuasaan. Dinamai pula surat ini dengan <i>At Tabaarak</i> (Maha Suci).', 'https://santrikoding.com/storage/audio/067.mp3'),
	(68, 68, 'القلم', 'Al-Qalam', 52, 'mekah', 'Pena', 'Surat ini terdiri atas 52 ayat,termasuk golongan surat-surat Makkiyah,diturunkan sesudah surat Al Alaq. <br>Nama <i>Al Qalam</i> diambil dari kata Al Qalam yang terdapat pada ayat pertama surat iniyang artinya <i>pena</i>. Surat ini dinamai pula dengan surat <i>Nun</i> (huruf <i>nun</i>).', 'https://santrikoding.com/storage/audio/068.mp3'),
	(69, 69, 'الحاۤقّة', 'Al-Haqqah', 52, 'mekah', 'Hari Kiamat', 'Surat ini terdiri atas 52 ayat,termasuk golongan surat-surat Makkiyah,diturunkan sesudah surat Al Mulk. <br> Nama <i>Al Haaqqah</i> diambil dari kata <i>Al Haaqqah</i> yang terdapat pada ayat pertama surat ini yang artinya <i>hari kiamat</i>', 'https://santrikoding.com/storage/audio/069.mp3'),
	(70, 70, 'المعارج', 'Al-Ma\'arij', 44, 'mekah', 'Tempat Naik', 'Surat ini terdiri atas 44 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Haaqqah.<br>Perkataan <i>Al Ma\'arij</i> yang menjadi nama bagi surat ini adalah kata jamak dari <i>Mi\'raj</i>, diambil dari perkataan Al Ma\'arij yang terdapat pada ayat 3, yang artinya menurut bahasa <i>tempat naik</i>. Sedang para ahli  tafsir memberi arti bermacam-macam, di antaranya <i>langit</i>, nikmat karunia dan derajat atau tingkatan yang diberikan Allah s.w.t kepada ahli surga.', 'https://santrikoding.com/storage/audio/070.mp3'),
	(71, 71, 'نوح', 'Nuh', 28, 'mekah', 'Nuh', 'Surat ini terdiri atas 28 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat An Nahl. <br>Dinamakan dengan surat <i>Nuh</i> karena surat ini seluruhnya menjelaskan da\'wah dan doa Nabi Nuh a.s.', 'https://santrikoding.com/storage/audio/071.mp3'),
	(72, 72, 'الجن', 'Al-Jinn', 28, 'mekah', 'Jin', 'Surat Al Jin terdiri atas 28 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al A\'raaf. Dinamai <i>Al Jin</i> diambil dari perkataan <i>Al Jin</i> yang terdapat  pada ayat pertama surat ini. Pada ayat tersebut dan ayat-ayat berikutnya  diterangkan bahwa Jin sebagai makhluk halus telah mendengar pembacaan  Al Quran dan mereka mengikuti ajaran Al Quran tersebut.', 'https://santrikoding.com/storage/audio/072.mp3'),
	(73, 73, 'المزّمّل', 'Al-Muzzammil', 20, 'mekah', 'Orang Yang Berselimut', 'Surat Al Muzzammil terdiri atas 20 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Qalam.<br>Dinamai <i>Al Muzzammil</i> (orang yang berselimut) diambil dari perkataan <i>Al Muzzammil</i> yang terdapat pada ayat pertama surat ini. Yang dimaksud dengan <i>orang yang berkemul</i> ialah Nabi Muhammad s.a.w.', 'https://santrikoding.com/storage/audio/073.mp3'),
	(74, 74, 'المدّثّر', 'Al-Muddassir', 56, 'mekah', 'Orang Yang Berkemul', 'Surat Al Muddatstsir terdiri atas 56 ayat, termasuk golongan  surat-surat Makkiyah, diturunkan sesudah surat Al Muzzammil. 	Dinamai <i>Al Muddatstsir</i> (orang yang berkemul) diambil dari perkataan <i>Al Muddatstsir</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/074.mp3'),
	(75, 75, 'القيٰمة', 'Al-Qiyamah', 40, 'mekah', 'Hari Kiamat', 'Surat Al Qiyaamah terdiri atas 40 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Qaari\'ah. Dinamai <i>Al Qiyaamah</i> (hari kiamat) diambil dari perkataan <i>Al Qiyaamah</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/075.mp3'),
	(76, 76, 'الانسان', 'Al-Insan', 31, 'madinah', 'Manusia', 'Surat Al Insaan terdiri atas 31 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Ar Rahmaan. Dinamai <i>al Insaan</i> (manusia) diambil dari perkataan <i>Al Insaan</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/076.mp3'),
	(77, 77, 'المرسلٰت', 'Al-Mursalat', 50, 'mekah', 'Malaikat Yang Diutus', 'Surat Al Mursalaat terdiri atas 50 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Humazah. Dinamai <i>Al Mursalaat</i> (Malaikat-Malaikat yang diutus), diambil dari perkataan <i>Al Mursalaat</i> yang terdapat pada ayat pertama surat ini. Dinamai juga <i>Amma yatasaa aluun</i> diambil dari perkataan <i>Amma yatasaa aluun</i> yang terdapat pada ayat 1 surat ini.', 'https://santrikoding.com/storage/audio/077.mp3'),
	(78, 78, 'النبأ', 'An-Naba\'', 40, 'mekah', 'Berita Besar', 'Surat An NabaÂ´ terdiri atas 40 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al MaÂ´aarij. <br>Dinamai <i>An NabaÂ´</i> (berita besar) diambil dari perkataan <i>An NabaÂ´</i> yang terdapat pada ayat 2 surat ini. Dinamai juga <i>Amma yatasaa aluun</i> diambil dari perkataan <i>Amma yatasaa aluun</i> yang terdapat pada ayat 1 surat ini.', 'https://santrikoding.com/storage/audio/078.mp3'),
	(79, 79, 'النّٰزعٰت', 'An-Nazi\'at', 46, 'mekah', 'Malaikat Yang Mencabut', 'Surat An NaaziÂ´aat terdiri atas 46 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat An NabaÂ´. Dinamai <i>An NaaziÂ´aat</i> diambil dari perkataan <i>An NaaziÂ´aat</i> yang terdapat pada ayat pertama surat ini. Dinamai pula <i>as Saahirah</i> yang diambil dari ayat 14, dinamai juga <i>Ath Thaammah</i> diambil dari ayat 34.', 'https://santrikoding.com/storage/audio/079.mp3'),
	(80, 80, 'عبس', '\'Abasa', 42, 'mekah', 'Bermuka Masam', 'Surat \'Abasa terdiri atas 42 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat An Najm. Dinamai <i>\'Abasa</i>  diambil dari perkataan <i>\'Abasa</i> yang terdapat pada ayat pertama surat ini.<br> Menurut riwayat, pada suatu ketika Rasulullah s.a.w. menerima dan berbicara dengan pemuka-pemuka Quraisy yang beliau harapkan agar mereka masuk Islam. Dalam pada itu datanglah Ibnu Ummi Maktum, seorang sahabat yang buta yang mengharap agar Rasulullah s.a.w. membacakan kepadanya ayat- ayat Al Quran yang telah diturunkan Allah. tetapi Rasulullah s.a.w. bermuka masam dan memalingkan muka dari Ibnu Ummi Maktum yang buta itu, lalu Allah menurunkan surat ini sebagai teguran atas sikap Rasulullah terhadap ibnu Ummi Maktum itu.', 'https://santrikoding.com/storage/audio/080.mp3'),
	(81, 81, 'التكوير', 'At-Takwir', 29, 'mekah', 'Penggulungan', 'Surat At Takwir terdiri atas 29 ayat dan termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Masadd. Kata <i>At Takwir</i> (terbelah) yang menjadi nama bagi surat ini adalah dari kata asal (mashdar) dari kata kerja <i>kuwwirat</i> (digulung) yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/081.mp3'),
	(82, 82, 'الانفطار', 'Al-Infitar', 19, 'mekah', 'Terbelah', 'Surat ini terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyah dan diturunkan sesudah surat An Naazi\'aat. Al Infithaar yang dijadikan  nama untuk surat ini adalah kata asal dari kata <i>Infatharat</i> (terbelah)  yang terdapat pada ayat pertama.', 'https://santrikoding.com/storage/audio/082.mp3'),
	(83, 83, 'المطفّفين', 'Al-Mutaffifin', 36, 'mekah', 'Orang-Orang Curang', 'Surat ini terdiri atas 36 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al \'Ankabuut dan merupakan  surat yang terakhir di Mekkah sebelum hijrah. <i>Al Muthaffifiin</i>  yang dijadikan nama bagi surat ini diambil dari kata  <i>Al Muthaffifiin</i> yang terdapat pada ayat pertama.', 'https://santrikoding.com/storage/audio/083.mp3'),
	(84, 84, 'الانشقاق', 'Al-Insyiqaq', 25, 'mekah', 'Terbelah', 'Surat Al Insyiqaaq, terdiri atas 25 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Infithaarr. Dinamai <i>Al Insyiqaaq</i> (terbelah), diambil dari perkataan <i>Insyaqqat</i> yang terdapat pada permulaan surat ini, yang pokok katanya ialah <i>insyiqaaq</i>.', 'https://santrikoding.com/storage/audio/084.mp3'),
	(85, 85, 'البروج', 'Al-Buruj', 22, 'mekah', 'Gugusan Bintang', 'Surat Al Buruuj terdiri atas 22 ayat, termasuk golongan surat-surat Makkiyyah diturunkan sesudah surat Asy-Syams.<br>Dinamai <i>Al Buruuj</i> (gugusan bintang) diambil dari perkataan <i>Al Buruuj</i> yang terdapat pada ayat 1 surat ini.', 'https://santrikoding.com/storage/audio/085.mp3'),
	(86, 86, 'الطارق', 'At-Tariq', 17, 'mekah', 'Yang Datang Di Malam Hari', 'Surat Ath Thaariq terdiri atas 17 ayat, termasuk golongan surat-surat Makkiyah,  diturunkan sesudah surat Al Balad.  Dinamai <i>Ath Thaariq</i> (yang datang di malam hari) diambil dari  perkataan <i>Ath Thaariq</i> yang terdapat pada ayat 1 surat ini.', 'https://santrikoding.com/storage/audio/086.mp3'),
	(87, 87, 'الاعلى', 'Al-A\'la', 19, 'mekah', 'Maha Tinggi', 'Surat ini terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyyah, dan diturunkan sesudah surat At Takwiir. Nama <i>Al AÂ´laa</i> diambil dari kata <i>Al AÂ´laa</i> yang terdapat pada ayat pertama, berarti <i>Yang Paling Tinggi</i>. Muslim meriwayatkan dalam kitab Al Jumu\'ah, dan diriwayatkan pula oleh Ashhaabus Sunan, dari Nu\'man ibnu Basyir bahwa Rasulullah s.a.w. pada shalat dua hari Raya (Fitri dan Adha) dan shalat Jum\'at membaca surat Al AÂ´laa pada rakaat pertama, dan surat Al Ghaasyiyah pada rakaat kedua.', 'https://santrikoding.com/storage/audio/087.mp3'),
	(88, 88, 'الغاشية', 'Al-Gasyiyah', 26, 'mekah', 'Hari Kiamat', 'Surat ini terdiri atas 26 ayat, termasuk surat-surat Makkiyah, diturunkan sesudah surat Adz Dzaariat. Nama <i>Ghaasyiyah</i> diambil dari kata <i>Al Ghaasyiyah</i> yang terdapat pada ayat pertama surat ini yang  artinya peristiwa yang dahsyat, tapi yang dimaksud adalah hari kiamat. Surat ini adalah surat yang kerap kali dibaca Nabi pada rakaat kedua  pada shalat hari-hari Raya dan shalat Jum\'at', 'https://santrikoding.com/storage/audio/088.mp3'),
	(89, 89, 'الفجر', 'Al-Fajr', 30, 'mekah', 'Fajar', 'Surat ini terdiri atas 30 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Lail. Nama <i>Al Fajr</i> diambil dari kata <i>Al Fajr</i> yang terdapat pada ayat pertama surat ini yang artinya <i>fajar</i>.', 'https://santrikoding.com/storage/audio/089.mp3'),
	(90, 90, 'البلد', 'Al-Balad', 20, 'mekah', 'Negeri', 'Surat Al Balad terdiri atas 20 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Qaaf. Dinamai <i>Al Balad</i>, diambil dari perkataan <i>Al Balad</i> yang terdapat  pada ayat pertama surat ini. Yang dimaksud dengan kota di sini ialah kota Mekah.', 'https://santrikoding.com/storage/audio/090.mp3'),
	(91, 91, 'الشمس', 'Asy-Syams', 15, 'mekah', 'Matahari', 'Surat Asy Syams terdiri atas 15 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Qadar.  Dinamai <i>Asy Syams</i> (matahari) diambil dari perkataan <i>Asy Syams</i> yang terdapat pada ayat permulaan surat ini.', 'https://santrikoding.com/storage/audio/091.mp3'),
	(92, 92, 'الّيل', 'Al-Lail', 21, 'mekah', 'Malam', 'Surat ini terdiri atas 21 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al A\'laa. Surat ini dinamai <i>Al Lail</i> (malam), diambil dari perkataan <i>Al Lail</i> yang terdapat pada ayat pertama surat ini', 'https://santrikoding.com/storage/audio/092.mp3'),
	(93, 93, 'الضحى', 'Ad-Duha', 11, 'mekah', 'Duha', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat Makiyyah dan diturunkan sesudah surat Al Fajr. Nama <i>Adh Dhuhaa</i> diambil dari kata yang terdapat pada ayat pertama, artinya : waktu matahari sepenggalahan naik.', 'https://santrikoding.com/storage/audio/093.mp3'),
	(94, 94, 'الشرح', 'Asy-Syarh', 8, 'mekah', 'Lapang', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyah dan diturunkan sesudah surat Adh Dhuhaa. Nama <i>Alam Nasyrah</i> diambil dari kata <i>Alam Nasyrah</i> yang terdapat pada ayat pertama, yang berarti: bukankah Kami telah melapangkan.', 'https://santrikoding.com/storage/audio/094.mp3'),
	(95, 95, 'التين', 'At-Tin', 8, 'mekah', 'Buah Tin', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Buruuj. Nama At Tiin diambil dari kata <i>At Tiin</i> yang terdapat pada ayat pertama surat ini yang artinya buah Tin.', 'https://santrikoding.com/storage/audio/095.mp3'),
	(96, 96, 'العلق', 'Al-\'Alaq', 19, 'mekah', 'Segumpal Darah', 'Surat Al \'Alaq terdiri atas 19 ayat, termasuk golongan surat-surat Makkiyah. Ayat 1 sampai dengan 5 dari surat ini adalah ayat-ayat Al Quran yang pertama sekali diturunkan, yaitu di waktu Nabi Muhammad s.a.w. berkhalwat di gua Hira\'. Surat ini dinamai <i>Al \'Alaq</i> (segumpal darah), diambil dari perkataan <i>Alaq</i> yang terdapat pada ayat 2 surat ini. Surat ini dinamai juga dengan <i>Iqra</i> atau <i>Al Qalam</i>.', 'https://santrikoding.com/storage/audio/096.mp3'),
	(97, 97, 'القدر', 'Al-Qadr', 5, 'mekah', 'Kemuliaan', 'Surat Al Qadr terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat \'Abasa. Surat ini dinamai <i>Al Qadr</i> (kemuliaan), diambil dari perkataan <i>Al Qadr</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/097.mp3'),
	(98, 98, 'البيّنة', 'Al-Bayyinah', 8, 'madinah', 'Bukti Nyata', 'Surat Al Bayyinah terdiri atas 8 ayat, termasuk golongan surat-surat Madaniyyah, diturunkan sesudah surat Ath Thalaq. Dinamai <i>Al Bayyinah</i> (bukti yang nyata) diambil dari perkataan <i>Al Bayyinah</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/098.mp3'),
	(99, 99, 'الزلزلة', 'Az-Zalzalah', 8, 'madinah', 'Guncangan', 'Surat ini terdiri atas 8 ayat, termasuk golongan surat-surat Madaniyyah diturunkan sesudah surat An Nisaa\'. Nama <i>Al Zalzalah</i> diambil dari kata: <i>Zilzaal</i> yang terdapat pada ayat pertama surat ini yang berarti goncangan.', 'https://santrikoding.com/storage/audio/099.mp3'),
	(100, 100, 'العٰديٰت', 'Al-\'Adiyat', 11, 'mekah', 'Kuda Yang Berlari Kencang', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al\'Ashr. Nama <i>Al \'Aadiyaat</i> diambil dari kata <i>Al \'Aadiyaat</i> yang terdapat pada ayat pertama surat ini, artinya yang berlari kencang.', 'https://santrikoding.com/storage/audio/100.mp3'),
	(101, 101, 'القارعة', 'Al-Qari\'ah', 11, 'mekah', 'Hari Kiamat', 'Surat ini terdiri atas 11 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Quraisy. Nama <i>Al Qaari\'ah</i> diambil dari kata <i>Al Qaari\'ah</i> yang terdapat pada ayat pertama, artinya mengetok dengan keras, kemudian kata ini dipakai untuk nama hari kiamat.', 'https://santrikoding.com/storage/audio/101.mp3'),
	(102, 102, 'التكاثر', 'At-Takasur', 8, 'mekah', 'Bermegah-Megahan', 'Surat At Takaatsur terdiri atas 8 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Kautsar. Dinamai <i>At Takaatsur</i> (bermegah-megahan) diambil dari perkataan <i>At Takaatsur</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/102.mp3'),
	(103, 103, 'العصر', 'Al-\'Asr', 3, 'mekah', 'Asar', 'Surat Al \'Ashr terdiri atas 3 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Alam Nasyrah. Dinamai <i>Al \'Ashr</i> (masa) diambil dari perkataan <i>Al \'Ashr</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/103.mp3'),
	(104, 104, 'الهمزة', 'Al-Humazah', 9, 'mekah', 'Pengumpat', 'Surat Al Humazah terdiri atas 9 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Qiyaamah. Dinamai <i>Al Humazah</i> (pengumpat) diambil dari perkataan <i>Humazah</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/104.mp3'),
	(105, 105, 'الفيل', 'Al-Fil', 5, 'mekah', 'Gajah', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat Al Kaafirun. Nama <i>Al Fiil</i> diambil dari kata <i>Al Fiil</i> yang terdapat pada ayat pertama surat ini, artinya <i>gajah</i>. Surat Al Fiil mengemukakan cerita pasukan bergajah dari Yaman yang dipimpin oleh Abrahah yang ingin meruntuhkan Ka\'bah di Mekah. Peristiwa ini terjadi pada tahun Nabi Muhammad s.a.w. dilahirkan.', 'https://santrikoding.com/storage/audio/105.mp3'),
	(106, 106, 'قريش', 'Quraisy', 4, 'mekah', 'Quraisy', 'Surat ini terdiri atas 4 ayat, termasuk golongan surat-surat Makkiyyah dan diturunkan sesudah surat At Tiin. Nama <i>Quraisy</i> diambil dari kata <i>Quraisy</i> yang terdapat pada ayat pertama, artinya suku Quraisy. Suku Quraisy adalah suku yang mendapat  kehormatan untuk memelihara Ka\'bah.', 'https://santrikoding.com/storage/audio/106.mp3'),
	(107, 107, 'الماعون', 'Al-Ma\'un', 7, 'mekah', 'Barang Yang Berguna', 'Surat ini terdiri atas 7 ayat, termasuk golongan surat-surat Makkiyyah, diturunkan sesudah surat At Taakatsur. Nama <i>Al Maa\'uun</i> diambil dari kata <i>Al Maa\'uun</i> yang terdapat pada ayat 7, artinya barang-barang yang berguna.', 'https://santrikoding.com/storage/audio/107.mp3'),
	(108, 108, 'الكوثر', 'Al-Kausar', 3, 'mekah', 'Pemberian Yang Banyak', 'Surat Al Kautsar terdiri atas 3 ayat, termasuk golongan surat-surat  Makkiyyah diturunkan sesudah surat Al \'Aadiyaat. Dinamai <i>Al Kautsar</i> (nikmat yang banyak) diambil dari perkataan <i>Al Kautsar</i> yang terdapat pada ayat pertama surat ini.<br>Surat ini sebagai penghibur hati Nabi Muhammad s.a.w.', 'https://santrikoding.com/storage/audio/108.mp3'),
	(109, 109, 'الكٰفرون', 'Al-Kafirun', 6, 'mekah', 'Orang-Orang kafir', 'Surat Al Kaafiruun terdiri atas 6 ayat, termasuk golongan surat-surat  Makkiyyah, diturunkan sesudah surat Al Maa\'uun. Dinamai <i>Al Kaafiruun</i> (orang-orang kafir), diambil dari perkataan  <i>Al Kaafiruun</i> yang terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/109.mp3'),
	(110, 110, 'النصر', 'An-Nasr', 3, 'madinah', 'Pertolongan', 'Surat An Nashr terdiri atas 3 ayat, termasuk golongan surat-surat  Madaniyyah yang diturunkan di Mekah sesudah surat At Taubah.  Dinamai <i>An Nashr</i> (pertolongan) diambil dari perkataan <i>Nashr</i> yang  terdapat pada ayat pertama surat ini.', 'https://santrikoding.com/storage/audio/110.mp3'),
	(111, 111, 'اللهب', 'Al-Lahab', 5, 'mekah', 'Api Yang Bergejolak', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyyah,  diturunkan sesudah surat Al Fath. Nama <i>Al Lahab</i> diambil dari kata  <i>Al Lahab</i> yang terdapat pada ayat ketiga surat ini yang artinya gejolak  api. Surat ini juga dinamakan surat <i>Al Masad</i>.', 'https://santrikoding.com/storage/audio/111.mp3'),
	(112, 112, 'الاخلاص', 'Al-Ikhlas', 4, 'mekah', 'Ikhlas', 'Surat ini terdiri atas 4 ayat, termasuk golongan surat-surat  Makkiyyah, diturunkan sesudah sesudah surat An Naas. Dinamakan <i>Al Ikhlas</i> karena surat ini sepenuhnya menegaskan kemurnian keesaan Allah s.w.t.', 'https://santrikoding.com/storage/audio/112.mp3'),
	(113, 113, 'الفلق', 'Al-Falaq', 5, 'mekah', 'Subuh', 'Surat ini terdiri atas 5 ayat, termasuk golongan surat-surat Makkiyah, diturunkan sesudah surat Al Fiil. Nama <i>Al Falaq</i> diambil dari kata <i>Al Falaq</i> yang terdapat pada ayat pertama surat ini yang artinya waktu subuh. Diriwayatkan oleh Abu Daud, At Tirmizi dan An Nasa-i dari \'Uqbah bin \'Aamir bahwa Rasulullah s.a.w. bersembahyang dengan membaca surat Al Falaq  dan surat An Naas dalam perjalanan.', 'https://santrikoding.com/storage/audio/113.mp3'),
	(114, 114, 'الناس', 'An-Nas', 6, 'mekah', 'Manusia', 'Surat ini terdiri atas 6 ayat, termasuk golongan surat-surat Makkiyah,  diturunkan sesudah surat Al Falaq. Nama <i>An Naas</i> diambil dari <i>An Naas</i> yang berulang kali disebut dalam surat ini yang artinya manusia.', 'https://santrikoding.com/storage/audio/114.mp3');
